INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(55, 114, 6231, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قُلۡ أَعُوذُ بِرَبِّ ٱلنَّاسِ\n', NULL, 0, '2023-05-26 16:48:29', '2023-05-27 11:05:43'),
(56, 114, 6232, 2, 'مَلِكِ ٱلنَّاسِ\n', NULL, 0, '2023-05-26 16:48:29', '2023-05-27 11:05:43'),
(57, 114, 6233, 3, 'إِلَـٰهِ ٱلنَّاسِ\n', NULL, 0, '2023-05-26 16:48:29', '2023-05-27 11:05:43'),
(58, 114, 6234, 4, 'مِن شَرِّ ٱلۡوَسۡوَاسِ ٱلۡخَنَّاسِ\n', NULL, 0, '2023-05-26 16:48:29', '2023-05-27 11:05:43'),
(59, 114, 6235, 5, 'ٱلَّذِی یُوَسۡوِسُ فِی صُدُورِ ٱلنَّاسِ\n', NULL, 0, '2023-05-26 16:48:29', '2023-05-27 11:05:43'),
(60, 114, 6236, 6, 'مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ', NULL, 0, '2023-05-26 16:48:29', '2023-05-27 11:05:43'),
(61, 113, 6226, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قُلۡ أَعُوذُ بِرَبِّ ٱلۡفَلَقِ\n', NULL, 0, '2023-05-26 16:48:30', '2023-05-27 11:05:44'),
(62, 113, 6227, 2, 'مِن شَرِّ مَا خَلَقَ\n', NULL, 0, '2023-05-26 16:48:30', '2023-05-27 11:05:44'),
(63, 113, 6228, 3, 'وَمِن شَرِّ غَاسِقٍ إِذَا وَقَبَ\n', NULL, 0, '2023-05-26 16:48:30', '2023-05-27 11:05:44'),
(64, 113, 6229, 4, 'وَمِن شَرِّ ٱلنَّفَّـٰثَـٰتِ فِی ٱلۡعُقَدِ\n', NULL, 0, '2023-05-26 16:48:30', '2023-05-27 11:05:44'),
(65, 113, 6230, 5, 'وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ\n', NULL, 0, '2023-05-26 16:48:30', '2023-05-27 11:05:44'),
(66, 112, 6222, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قُلۡ هُوَ ٱللَّهُ أَحَدٌ\n', NULL, 0, '2023-05-26 16:48:31', '2023-05-27 11:05:44'),
(67, 112, 6223, 2, 'ٱللَّهُ ٱلصَّمَدُ\n', NULL, 0, '2023-05-26 16:48:31', '2023-05-27 11:05:45'),
(68, 112, 6224, 3, 'لَمۡ یَلِدۡ وَلَمۡ یُولَدۡ\n', NULL, 0, '2023-05-26 16:48:31', '2023-05-27 11:05:45'),
(69, 112, 6225, 4, 'وَلَمۡ یَكُن لَّهُۥ كُفُوًا أَحَدُۢ\n', NULL, 0, '2023-05-26 16:48:31', '2023-05-27 11:05:45'),
(70, 111, 6217, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ تَبَّتۡ یَدَاۤ أَبِی لَهَبࣲ وَتَبَّ\n', NULL, 0, '2023-05-26 16:48:33', '2023-05-27 11:05:45'),
(71, 111, 6218, 2, 'مَاۤ أَغۡنَىٰ عَنۡهُ مَالُهُۥ وَمَا كَسَبَ\n', NULL, 0, '2023-05-26 16:48:33', '2023-05-27 11:05:45'),
(72, 111, 6219, 3, 'سَیَصۡلَىٰ نَارࣰا ذَاتَ لَهَبࣲ\n', NULL, 0, '2023-05-26 16:48:33', '2023-05-27 11:05:45'),
(73, 111, 6220, 4, 'وَٱمۡرَأَتُهُۥ حَمَّالَةَ ٱلۡحَطَبِ\n', NULL, 0, '2023-05-26 16:48:33', '2023-05-27 11:05:45'),
(74, 111, 6221, 5, 'فِی جِیدِهَا حَبۡلࣱ مِّن مَّسَدِۭ\n', NULL, 0, '2023-05-26 16:48:33', '2023-05-27 11:05:46'),
(75, 110, 6214, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِذَا جَاۤءَ نَصۡرُ ٱللَّهِ وَٱلۡفَتۡحُ\n', NULL, 0, '2023-05-26 16:48:34', '2023-05-27 11:05:46'),
(76, 110, 6215, 2, 'وَرَأَیۡتَ ٱلنَّاسَ یَدۡخُلُونَ فِی دِینِ ٱللَّهِ أَفۡوَاجࣰا\n', NULL, 0, '2023-05-26 16:48:34', '2023-05-27 11:05:46'),
(77, 110, 6216, 3, 'فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَٱسۡتَغۡفِرۡهُۚ إِنَّهُۥ كَانَ تَوَّابَۢا\n', NULL, 0, '2023-05-26 16:48:34', '2023-05-27 11:05:46'),
(78, 109, 6208, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قُلۡ یَـٰۤأَیُّهَا ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 16:51:31', '2023-05-27 11:05:56'),
(79, 109, 6209, 2, 'لَاۤ أَعۡبُدُ مَا تَعۡبُدُونَ\n', NULL, 0, '2023-05-26 16:51:31', '2023-05-27 11:05:56'),
(80, 109, 6210, 3, 'وَلَاۤ أَنتُمۡ عَـٰبِدُونَ مَاۤ أَعۡبُدُ\n', NULL, 0, '2023-05-26 16:51:31', '2023-05-27 11:05:56'),
(81, 109, 6211, 4, 'وَلَاۤ أَنَا۠ عَابِدࣱ مَّا عَبَدتُّمۡ\n', NULL, 0, '2023-05-26 16:51:31', '2023-05-27 11:05:56'),
(82, 109, 6212, 5, 'وَلَاۤ أَنتُمۡ عَـٰبِدُونَ مَاۤ أَعۡبُدُ\n', NULL, 0, '2023-05-26 16:51:31', '2023-05-27 11:05:56'),
(83, 109, 6213, 6, 'لَكُمۡ دِینُكُمۡ وَلِیَ دِینِ\n', NULL, 0, '2023-05-26 16:51:31', '2023-05-27 11:05:56'),
(84, 108, 6205, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِنَّاۤ أَعۡطَیۡنَـٰكَ ٱلۡكَوۡثَرَ\n', NULL, 0, '2023-05-26 16:51:32', '2023-05-27 11:05:56'),
(85, 108, 6206, 2, 'فَصَلِّ لِرَبِّكَ وَٱنۡحَرۡ\n', NULL, 0, '2023-05-26 16:51:32', '2023-05-27 11:05:56'),
(86, 108, 6207, 3, 'إِنَّ شَانِئَكَ هُوَ ٱلۡأَبۡتَرُ\n', NULL, 0, '2023-05-26 16:51:32', '2023-05-27 11:05:56'),
(87, 107, 6198, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ أَرَءَیۡتَ ٱلَّذِی یُكَذِّبُ بِٱلدِّینِ\n', NULL, 0, '2023-05-26 16:51:33', '2023-05-27 11:05:57'),
(88, 107, 6199, 2, 'فَذَ ٰ⁠لِكَ ٱلَّذِی یَدُعُّ ٱلۡیَتِیمَ\n', NULL, 0, '2023-05-26 16:51:33', '2023-05-27 11:05:57'),
(89, 107, 6200, 3, 'وَلَا یَحُضُّ عَلَىٰ طَعَامِ ٱلۡمِسۡكِینِ\n', NULL, 0, '2023-05-26 16:51:33', '2023-05-27 11:05:57'),
(90, 107, 6201, 4, 'فَوَیۡلࣱ لِّلۡمُصَلِّینَ\n', NULL, 0, '2023-05-26 16:51:33', '2023-05-27 11:05:57'),
(91, 107, 6202, 5, 'ٱلَّذِینَ هُمۡ عَن صَلَاتِهِمۡ سَاهُونَ\n', NULL, 0, '2023-05-26 16:51:33', '2023-05-27 11:05:57'),
(92, 107, 6203, 6, 'ٱلَّذِینَ هُمۡ یُرَاۤءُونَ\n', NULL, 0, '2023-05-26 16:51:33', '2023-05-27 11:05:57'),
(93, 107, 6204, 7, 'وَیَمۡنَعُونَ ٱلۡمَاعُونَ\n', NULL, 0, '2023-05-26 16:51:33', '2023-05-27 11:05:57'),
(94, 106, 6194, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ لِإِیلَـٰفِ قُرَیۡشٍ\n', NULL, 0, '2023-05-26 16:51:34', '2023-05-27 11:05:58'),
(95, 106, 6195, 2, 'إِۦلَـٰفِهِمۡ رِحۡلَةَ ٱلشِّتَاۤءِ وَٱلصَّیۡفِ\n', NULL, 0, '2023-05-26 16:51:34', '2023-05-27 11:05:58'),
(96, 106, 6196, 3, 'فَلۡیَعۡبُدُوا۟ رَبَّ هَـٰذَا ٱلۡبَیۡتِ\n', NULL, 0, '2023-05-26 16:51:34', '2023-05-27 11:05:58'),
(97, 106, 6197, 4, 'ٱلَّذِیۤ أَطۡعَمَهُم مِّن جُوعࣲ وَءَامَنَهُم مِّنۡ خَوۡفِۭ\n', NULL, 0, '2023-05-26 16:51:34', '2023-05-27 11:05:58'),
(98, 105, 6189, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ أَلَمۡ تَرَ كَیۡفَ فَعَلَ رَبُّكَ بِأَصۡحَـٰبِ ٱلۡفِیلِ\n', NULL, 0, '2023-05-26 16:51:35', '2023-05-27 11:05:59'),
(99, 105, 6190, 2, 'أَلَمۡ یَجۡعَلۡ كَیۡدَهُمۡ فِی تَضۡلِیلࣲ\n', NULL, 0, '2023-05-26 16:51:35', '2023-05-27 11:05:59'),
(100, 105, 6191, 3, 'وَأَرۡسَلَ عَلَیۡهِمۡ طَیۡرًا أَبَابِیلَ\n', NULL, 0, '2023-05-26 16:51:35', '2023-05-27 11:05:59'),
(101, 105, 6192, 4, 'تَرۡمِیهِم بِحِجَارَةࣲ مِّن سِجِّیلࣲ\n', NULL, 0, '2023-05-26 16:51:35', '2023-05-27 11:05:59'),
(102, 105, 6193, 5, 'فَجَعَلَهُمۡ كَعَصۡفࣲ مَّأۡكُولِۭ\n', NULL, 0, '2023-05-26 16:51:35', '2023-05-27 11:05:59'),
(103, 104, 6180, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَیۡلࣱ لِّكُلِّ هُمَزَةࣲ لُّمَزَةٍ\n', NULL, 0, '2023-05-26 16:55:21', '2023-05-27 11:06:05'),
(104, 104, 6181, 2, 'ٱلَّذِی جَمَعَ مَالࣰا وَعَدَّدَهُۥ\n', NULL, 0, '2023-05-26 16:55:21', '2023-05-27 11:06:05'),
(105, 104, 6182, 3, 'یَحۡسَبُ أَنَّ مَالَهُۥۤ أَخۡلَدَهُۥ\n', NULL, 0, '2023-05-26 16:55:21', '2023-05-27 11:06:06'),
(106, 104, 6183, 4, 'كَلَّاۖ لَیُنۢبَذَنَّ فِی ٱلۡحُطَمَةِ\n', NULL, 0, '2023-05-26 16:55:22', '2023-05-27 11:06:06'),
(107, 104, 6184, 5, 'وَمَاۤ أَدۡرَىٰكَ مَا ٱلۡحُطَمَةُ\n', NULL, 0, '2023-05-26 16:55:22', '2023-05-27 11:06:06'),
(108, 104, 6185, 6, 'نَارُ ٱللَّهِ ٱلۡمُوقَدَةُ\n', NULL, 0, '2023-05-26 16:55:22', '2023-05-27 11:06:06'),
(109, 104, 6186, 7, 'ٱلَّتِی تَطَّلِعُ عَلَى ٱلۡأَفۡـِٔدَةِ\n', NULL, 0, '2023-05-26 16:55:22', '2023-05-27 11:06:06'),
(110, 104, 6187, 8, 'إِنَّهَا عَلَیۡهِم مُّؤۡصَدَةࣱ\n', NULL, 0, '2023-05-26 16:55:22', '2023-05-27 11:06:06'),
(111, 104, 6188, 9, 'فِی عَمَدࣲ مُّمَدَّدَةِۭ\n', NULL, 0, '2023-05-26 16:55:22', '2023-05-27 11:06:06'),
(112, 103, 6177, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَٱلۡعَصۡرِ\n', NULL, 0, '2023-05-26 16:55:23', '2023-05-27 11:06:07'),
(113, 103, 6178, 2, 'إِنَّ ٱلۡإِنسَـٰنَ لَفِی خُسۡرٍ\n', NULL, 0, '2023-05-26 16:55:23', '2023-05-27 11:06:07'),
(114, 103, 6179, 3, 'إِلَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَتَوَاصَوۡا۟ بِٱلۡحَقِّ وَتَوَاصَوۡا۟ بِٱلصَّبۡرِ\n', NULL, 0, '2023-05-26 16:55:23', '2023-05-27 11:06:07'),
(115, 102, 6169, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ أَلۡهَىٰكُمُ ٱلتَّكَاثُرُ\n', NULL, 0, '2023-05-26 16:55:25', '2023-05-27 11:06:07'),
(116, 102, 6170, 2, 'حَتَّىٰ زُرۡتُمُ ٱلۡمَقَابِرَ\n', NULL, 0, '2023-05-26 16:55:25', '2023-05-27 11:06:07'),
(117, 102, 6171, 3, 'كَلَّا سَوۡفَ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 16:55:26', '2023-05-27 11:06:07'),
(118, 102, 6172, 4, 'ثُمَّ كَلَّا سَوۡفَ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 16:55:26', '2023-05-27 11:06:08'),
(119, 102, 6173, 5, 'كَلَّا لَوۡ تَعۡلَمُونَ عِلۡمَ ٱلۡیَقِینِ\n', NULL, 0, '2023-05-26 16:55:26', '2023-05-27 11:06:08'),
(120, 102, 6174, 6, 'لَتَرَوُنَّ ٱلۡجَحِیمَ\n', NULL, 0, '2023-05-26 16:55:26', '2023-05-27 11:06:08'),
(121, 102, 6175, 7, 'ثُمَّ لَتَرَوُنَّهَا عَیۡنَ ٱلۡیَقِینِ\n', NULL, 0, '2023-05-26 16:55:26', '2023-05-27 11:06:08'),
(122, 102, 6176, 8, 'ثُمَّ لَتُسۡـَٔلُنَّ یَوۡمَىِٕذٍ عَنِ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 16:55:26', '2023-05-27 11:06:08'),
(123, 101, 6158, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱلۡقَارِعَةُ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(124, 101, 6159, 2, 'مَا ٱلۡقَارِعَةُ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(125, 101, 6160, 3, 'وَمَاۤ أَدۡرَىٰكَ مَا ٱلۡقَارِعَةُ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(126, 101, 6161, 4, 'یَوۡمَ یَكُونُ ٱلنَّاسُ كَٱلۡفَرَاشِ ٱلۡمَبۡثُوثِ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(127, 101, 6162, 5, 'وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ ٱلۡمَنفُوشِ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(128, 101, 6163, 6, 'فَأَمَّا مَن ثَقُلَتۡ مَوَ ٰ⁠زِینُهُۥ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(129, 101, 6164, 7, 'فَهُوَ فِی عِیشَةࣲ رَّاضِیَةࣲ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(130, 101, 6165, 8, 'وَأَمَّا مَنۡ خَفَّتۡ مَوَ ٰ⁠زِینُهُۥ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:08'),
(131, 101, 6166, 9, 'فَأُمُّهُۥ هَاوِیَةࣱ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:09'),
(132, 101, 6167, 10, 'وَمَاۤ أَدۡرَىٰكَ مَا هِیَهۡ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:09'),
(133, 101, 6168, 11, 'نَارٌ حَامِیَةُۢ\n', NULL, 0, '2023-05-26 16:55:27', '2023-05-27 11:06:09'),
(134, 100, 6147, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَٱلۡعَـٰدِیَـٰتِ ضَبۡحࣰا\n', NULL, 0, '2023-05-26 16:55:28', '2023-05-27 11:06:09'),
(135, 100, 6148, 2, 'فَٱلۡمُورِیَـٰتِ قَدۡحࣰا\n', NULL, 0, '2023-05-26 16:55:28', '2023-05-27 11:06:09'),
(136, 100, 6149, 3, 'فَٱلۡمُغِیرَ ٰ⁠تِ صُبۡحࣰا\n', NULL, 0, '2023-05-26 16:55:28', '2023-05-27 11:06:09'),
(137, 100, 6150, 4, 'فَأَثَرۡنَ بِهِۦ نَقۡعࣰا\n', NULL, 0, '2023-05-26 16:55:28', '2023-05-27 11:06:09'),
(138, 100, 6151, 5, 'فَوَسَطۡنَ بِهِۦ جَمۡعًا\n', NULL, 0, '2023-05-26 16:55:28', '2023-05-27 11:06:09'),
