(420, 84, 5892, 8, 'فَسَوۡفَ یُحَاسَبُ حِسَابࣰا یَسِیرࣰا\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:10'),
(421, 84, 5893, 9, 'وَیَنقَلِبُ إِلَىٰۤ أَهۡلِهِۦ مَسۡرُورࣰا\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:10'),
(422, 84, 5894, 10, 'وَأَمَّا مَنۡ أُوتِیَ كِتَـٰبَهُۥ وَرَاۤءَ ظَهۡرِهِۦ\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:10'),
(423, 84, 5895, 11, 'فَسَوۡفَ یَدۡعُوا۟ ثُبُورࣰا\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:11'),
(424, 84, 5896, 12, 'وَیَصۡلَىٰ سَعِیرًا\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:11'),
(425, 84, 5897, 13, 'إِنَّهُۥ كَانَ فِیۤ أَهۡلِهِۦ مَسۡرُورًا\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:11'),
(426, 84, 5898, 14, 'إِنَّهُۥ ظَنَّ أَن لَّن یَحُورَ\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:11'),
(427, 84, 5899, 15, 'بَلَىٰۤۚ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِیرࣰا\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:11'),
(428, 84, 5900, 16, 'فَلَاۤ أُقۡسِمُ بِٱلشَّفَقِ\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:11'),
(429, 84, 5901, 17, 'وَٱلَّیۡلِ وَمَا وَسَقَ\n', NULL, 0, '2023-05-26 17:19:04', '2023-05-27 11:07:11'),
(430, 84, 5902, 18, 'وَٱلۡقَمَرِ إِذَا ٱتَّسَقَ\n', NULL, 0, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(431, 84, 5903, 19, 'لَتَرۡكَبُنَّ طَبَقًا عَن طَبَقࣲ\n', NULL, 0, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(432, 84, 5904, 20, 'فَمَا لَهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(433, 84, 5905, 21, 'وَإِذَا قُرِئَ عَلَیۡهِمُ ٱلۡقُرۡءَانُ لَا یَسۡجُدُونَ ۩\n', NULL, 1, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(434, 84, 5906, 22, 'بَلِ ٱلَّذِینَ كَفَرُوا۟ یُكَذِّبُونَ\n', NULL, 0, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(435, 84, 5907, 23, 'وَٱللَّهُ أَعۡلَمُ بِمَا یُوعُونَ\n', NULL, 0, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(436, 84, 5908, 24, 'فَبَشِّرۡهُم بِعَذَابٍ أَلِیمٍ\n', NULL, 0, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(437, 84, 5909, 25, 'إِلَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُمۡ أَجۡرٌ غَیۡرُ مَمۡنُونِۭ\n', NULL, 0, '2023-05-26 17:19:05', '2023-05-27 11:07:11'),
(438, 83, 5849, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَیۡلࣱ لِّلۡمُطَفِّفِینَ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:12'),
(439, 83, 5850, 2, 'ٱلَّذِینَ إِذَا ٱكۡتَالُوا۟ عَلَى ٱلنَّاسِ یَسۡتَوۡفُونَ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:12'),
(440, 83, 5851, 3, 'وَإِذَا كَالُوهُمۡ أَو وَّزَنُوهُمۡ یُخۡسِرُونَ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:12'),
(441, 83, 5852, 4, 'أَلَا یَظُنُّ أُو۟لَـٰۤىِٕكَ أَنَّهُم مَّبۡعُوثُونَ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:12'),
(442, 83, 5853, 5, 'لِیَوۡمٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:12'),
(443, 83, 5854, 6, 'یَوۡمَ یَقُومُ ٱلنَّاسُ لِرَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:13'),
(444, 83, 5855, 7, 'كَلَّاۤ إِنَّ كِتَـٰبَ ٱلۡفُجَّارِ لَفِی سِجِّینࣲ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:13'),
(445, 83, 5856, 8, 'وَمَاۤ أَدۡرَىٰكَ مَا سِجِّینࣱ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:13'),
(446, 83, 5857, 9, 'كِتَـٰبࣱ مَّرۡقُومࣱ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:13'),
(447, 83, 5858, 10, 'وَیۡلࣱ یَوۡمَىِٕذࣲ لِّلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:19:06', '2023-05-27 11:07:13'),
(448, 83, 5859, 11, 'ٱلَّذِینَ یُكَذِّبُونَ بِیَوۡمِ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(449, 83, 5860, 12, 'وَمَا یُكَذِّبُ بِهِۦۤ إِلَّا كُلُّ مُعۡتَدٍ أَثِیمٍ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(450, 83, 5861, 13, 'إِذَا تُتۡلَىٰ عَلَیۡهِ ءَایَـٰتُنَا قَالَ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(451, 83, 5862, 14, 'كَلَّاۖ بَلۡۜ رَانَ عَلَىٰ قُلُوبِهِم مَّا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(452, 83, 5863, 15, 'كَلَّاۤ إِنَّهُمۡ عَن رَّبِّهِمۡ یَوۡمَىِٕذࣲ لَّمَحۡجُوبُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(453, 83, 5864, 16, 'ثُمَّ إِنَّهُمۡ لَصَالُوا۟ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(454, 83, 5865, 17, 'ثُمَّ یُقَالُ هَـٰذَا ٱلَّذِی كُنتُم بِهِۦ تُكَذِّبُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(455, 83, 5866, 18, 'كَلَّاۤ إِنَّ كِتَـٰبَ ٱلۡأَبۡرَارِ لَفِی عِلِّیِّینَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(456, 83, 5867, 19, 'وَمَاۤ أَدۡرَىٰكَ مَا عِلِّیُّونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(457, 83, 5868, 20, 'كِتَـٰبࣱ مَّرۡقُومࣱ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(458, 83, 5869, 21, 'یَشۡهَدُهُ ٱلۡمُقَرَّبُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(459, 83, 5870, 22, 'إِنَّ ٱلۡأَبۡرَارَ لَفِی نَعِیمٍ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(460, 83, 5871, 23, 'عَلَى ٱلۡأَرَاۤىِٕكِ یَنظُرُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(461, 83, 5872, 24, 'تَعۡرِفُ فِی وُجُوهِهِمۡ نَضۡرَةَ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(462, 83, 5873, 25, 'یُسۡقَوۡنَ مِن رَّحِیقࣲ مَّخۡتُومٍ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(463, 83, 5874, 26, 'خِتَـٰمُهُۥ مِسۡكࣱۚ وَفِی ذَ ٰ⁠لِكَ فَلۡیَتَنَافَسِ ٱلۡمُتَنَـٰفِسُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(464, 83, 5875, 27, 'وَمِزَاجُهُۥ مِن تَسۡنِیمٍ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:13'),
(465, 83, 5876, 28, 'عَیۡنࣰا یَشۡرَبُ بِهَا ٱلۡمُقَرَّبُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:14'),
(466, 83, 5877, 29, 'إِنَّ ٱلَّذِینَ أَجۡرَمُوا۟ كَانُوا۟ مِنَ ٱلَّذِینَ ءَامَنُوا۟ یَضۡحَكُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:14'),
(467, 83, 5878, 30, 'وَإِذَا مَرُّوا۟ بِهِمۡ یَتَغَامَزُونَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:14'),
(468, 83, 5879, 31, 'وَإِذَا ٱنقَلَبُوۤا۟ إِلَىٰۤ أَهۡلِهِمُ ٱنقَلَبُوا۟ فَكِهِینَ\n', NULL, 0, '2023-05-26 17:19:07', '2023-05-27 11:07:14'),
(469, 83, 5880, 32, 'وَإِذَا رَأَوۡهُمۡ قَالُوۤا۟ إِنَّ هَـٰۤؤُلَاۤءِ لَضَاۤلُّونَ\n', NULL, 0, '2023-05-26 17:19:08', '2023-05-27 11:07:14'),
(470, 83, 5881, 33, 'وَمَاۤ أُرۡسِلُوا۟ عَلَیۡهِمۡ حَـٰفِظِینَ\n', NULL, 0, '2023-05-26 17:19:08', '2023-05-27 11:07:14'),
(471, 83, 5882, 34, 'فَٱلۡیَوۡمَ ٱلَّذِینَ ءَامَنُوا۟ مِنَ ٱلۡكُفَّارِ یَضۡحَكُونَ\n', NULL, 0, '2023-05-26 17:19:08', '2023-05-27 11:07:14'),
(472, 83, 5883, 35, 'عَلَى ٱلۡأَرَاۤىِٕكِ یَنظُرُونَ\n', NULL, 0, '2023-05-26 17:19:08', '2023-05-27 11:07:14'),
(473, 83, 5884, 36, 'هَلۡ ثُوِّبَ ٱلۡكُفَّارُ مَا كَانُوا۟ یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:19:08', '2023-05-27 11:07:14'),
(474, 82, 5830, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِذَا ٱلسَّمَاۤءُ ٱنفَطَرَتۡ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(475, 82, 5831, 2, 'وَإِذَا ٱلۡكَوَاكِبُ ٱنتَثَرَتۡ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(476, 82, 5832, 3, 'وَإِذَا ٱلۡبِحَارُ فُجِّرَتۡ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(477, 82, 5833, 4, 'وَإِذَا ٱلۡقُبُورُ بُعۡثِرَتۡ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(478, 82, 5834, 5, 'عَلِمَتۡ نَفۡسࣱ مَّا قَدَّمَتۡ وَأَخَّرَتۡ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(479, 82, 5835, 6, 'یَـٰۤأَیُّهَا ٱلۡإِنسَـٰنُ مَا غَرَّكَ بِرَبِّكَ ٱلۡكَرِیمِ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(480, 82, 5836, 7, 'ٱلَّذِی خَلَقَكَ فَسَوَّىٰكَ فَعَدَلَكَ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(481, 82, 5837, 8, 'فِیۤ أَیِّ صُورَةࣲ مَّا شَاۤءَ رَكَّبَكَ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(482, 82, 5838, 9, 'كَلَّا بَلۡ تُكَذِّبُونَ بِٱلدِّینِ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(483, 82, 5839, 10, 'وَإِنَّ عَلَیۡكُمۡ لَحَـٰفِظِینَ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(484, 82, 5840, 11, 'كِرَامࣰا كَـٰتِبِینَ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(485, 82, 5841, 12, 'یَعۡلَمُونَ مَا تَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(486, 82, 5842, 13, 'إِنَّ ٱلۡأَبۡرَارَ لَفِی نَعِیمࣲ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(487, 82, 5843, 14, 'وَإِنَّ ٱلۡفُجَّارَ لَفِی جَحِیمࣲ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(488, 82, 5844, 15, 'یَصۡلَوۡنَهَا یَوۡمَ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(489, 82, 5845, 16, 'وَمَا هُمۡ عَنۡهَا بِغَاۤىِٕبِینَ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(490, 82, 5846, 17, 'وَمَاۤ أَدۡرَىٰكَ مَا یَوۡمُ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(491, 82, 5847, 18, 'ثُمَّ مَاۤ أَدۡرَىٰكَ مَا یَوۡمُ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(492, 82, 5848, 19, 'یَوۡمَ لَا تَمۡلِكُ نَفۡسࣱ لِّنَفۡسࣲ شَیۡـࣰٔاۖ وَٱلۡأَمۡرُ یَوۡمَىِٕذࣲ لِّلَّهِ\n', NULL, 0, '2023-05-26 17:19:09', '2023-05-27 11:07:15'),
(493, 81, 5801, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِذَا ٱلشَّمۡسُ كُوِّرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(494, 81, 5802, 2, 'وَإِذَا ٱلنُّجُومُ ٱنكَدَرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(495, 81, 5803, 3, 'وَإِذَا ٱلۡجِبَالُ سُیِّرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(496, 81, 5804, 4, 'وَإِذَا ٱلۡعِشَارُ عُطِّلَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(497, 81, 5805, 5, 'وَإِذَا ٱلۡوُحُوشُ حُشِرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(498, 81, 5806, 6, 'وَإِذَا ٱلۡبِحَارُ سُجِّرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(499, 81, 5807, 7, 'وَإِذَا ٱلنُّفُوسُ زُوِّجَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(500, 81, 5808, 8, 'وَإِذَا ٱلۡمَوۡءُۥدَةُ سُىِٕلَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(501, 81, 5809, 9, 'بِأَیِّ ذَنۢبࣲ قُتِلَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(502, 81, 5810, 10, 'وَإِذَا ٱلصُّحُفُ نُشِرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:16'),
(503, 81, 5811, 11, 'وَإِذَا ٱلسَّمَاۤءُ كُشِطَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:17'),
(504, 81, 5812, 12, 'وَإِذَا ٱلۡجَحِیمُ سُعِّرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:17'),
(505, 81, 5813, 13, 'وَإِذَا ٱلۡجَنَّةُ أُزۡلِفَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:17'),
(506, 81, 5814, 14, 'عَلِمَتۡ نَفۡسࣱ مَّاۤ أَحۡضَرَتۡ\n', NULL, 0, '2023-05-26 17:19:12', '2023-05-27 11:07:17'),
(507, 81, 5815, 15, 'فَلَاۤ أُقۡسِمُ بِٱلۡخُنَّسِ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(508, 81, 5816, 16, 'ٱلۡجَوَارِ ٱلۡكُنَّسِ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(509, 81, 5817, 17, 'وَٱلَّیۡلِ إِذَا عَسۡعَسَ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(510, 81, 5818, 18, 'وَٱلصُّبۡحِ إِذَا تَنَفَّسَ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(511, 81, 5819, 19, 'إِنَّهُۥ لَقَوۡلُ رَسُولࣲ كَرِیمࣲ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(512, 81, 5820, 20, 'ذِی قُوَّةٍ عِندَ ذِی ٱلۡعَرۡشِ مَكِینࣲ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(513, 81, 5821, 21, 'مُّطَاعࣲ ثَمَّ أَمِینࣲ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(514, 81, 5822, 22, 'وَمَا صَاحِبُكُم بِمَجۡنُونࣲ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(515, 81, 5823, 23, 'وَلَقَدۡ رَءَاهُ بِٱلۡأُفُقِ ٱلۡمُبِینِ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(516, 81, 5824, 24, 'وَمَا هُوَ عَلَى ٱلۡغَیۡبِ بِضَنِینࣲ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(517, 81, 5825, 25, 'وَمَا هُوَ بِقَوۡلِ شَیۡطَـٰنࣲ رَّجِیمࣲ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(518, 81, 5826, 26, 'فَأَیۡنَ تَذۡهَبُونَ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(519, 81, 5827, 27, 'إِنۡ هُوَ إِلَّا ذِكۡرࣱ لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(520, 81, 5828, 28, 'لِمَن شَاۤءَ مِنكُمۡ أَن یَسۡتَقِیمَ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(521, 81, 5829, 29, 'وَمَا تَشَاۤءُونَ إِلَّاۤ أَن یَشَاۤءَ ٱللَّهُ رَبُّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:19:13', '2023-05-27 11:07:17'),
(522, 80, 5759, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ عَبَسَ وَتَوَلَّىٰۤ\n', NULL, 0, '2023-05-26 17:19:14', '2023-05-27 11:07:18'),
(523, 80, 5760, 2, 'أَن جَاۤءَهُ ٱلۡأَعۡمَىٰ\n', NULL, 0, '2023-05-26 17:19:14', '2023-05-27 11:07:18'),
(524, 80, 5761, 3, 'وَمَا یُدۡرِیكَ لَعَلَّهُۥ یَزَّكَّىٰۤ\n', NULL, 0, '2023-05-26 17:19:14', '2023-05-27 11:07:18'),
(525, 80, 5762, 4, 'أَوۡ یَذَّكَّرُ فَتَنفَعَهُ ٱلذِّكۡرَىٰۤ\n', NULL, 0, '2023-05-26 17:19:14', '2023-05-27 11:07:18'),
(526, 80, 5763, 5, 'أَمَّا مَنِ ٱسۡتَغۡنَىٰ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(527, 80, 5764, 6, 'فَأَنتَ لَهُۥ تَصَدَّىٰ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(528, 80, 5765, 7, 'وَمَا عَلَیۡكَ أَلَّا یَزَّكَّىٰ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(529, 80, 5766, 8, 'وَأَمَّا مَن جَاۤءَكَ یَسۡعَىٰ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(530, 80, 5767, 9, 'وَهُوَ یَخۡشَىٰ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(531, 80, 5768, 10, 'فَأَنتَ عَنۡهُ تَلَهَّىٰ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(532, 80, 5769, 11, 'كَلَّاۤ إِنَّهَا تَذۡكِرَةࣱ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(533, 80, 5770, 12, 'فَمَن شَاۤءَ ذَكَرَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(534, 80, 5771, 13, 'فِی صُحُفࣲ مُّكَرَّمَةࣲ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(535, 80, 5772, 14, 'مَّرۡفُوعَةࣲ مُّطَهَّرَةِۭ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:18'),
(536, 80, 5773, 15, 'بِأَیۡدِی سَفَرَةࣲ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(537, 80, 5774, 16, 'كِرَامِۭ بَرَرَةࣲ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(538, 80, 5775, 17, 'قُتِلَ ٱلۡإِنسَـٰنُ مَاۤ أَكۡفَرَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(539, 80, 5776, 18, 'مِنۡ أَیِّ شَیۡءٍ خَلَقَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(540, 80, 5777, 19, 'مِن نُّطۡفَةٍ خَلَقَهُۥ فَقَدَّرَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(541, 80, 5778, 20, 'ثُمَّ ٱلسَّبِیلَ یَسَّرَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(542, 80, 5779, 21, 'ثُمَّ أَمَاتَهُۥ فَأَقۡبَرَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(543, 80, 5780, 22, 'ثُمَّ إِذَا شَاۤءَ أَنشَرَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(544, 80, 5781, 23, 'كَلَّا لَمَّا یَقۡضِ مَاۤ أَمَرَهُۥ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(545, 80, 5782, 24, 'فَلۡیَنظُرِ ٱلۡإِنسَـٰنُ إِلَىٰ طَعَامِهِۦۤ\n', NULL, 0, '2023-05-26 17:19:15', '2023-05-27 11:07:19'),
(546, 80, 5783, 25, 'أَنَّا صَبَبۡنَا ٱلۡمَاۤءَ صَبࣰّا\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(547, 80, 5784, 26, 'ثُمَّ شَقَقۡنَا ٱلۡأَرۡضَ شَقࣰّا\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(548, 80, 5785, 27, 'فَأَنۢبَتۡنَا فِیهَا حَبࣰّا\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(549, 80, 5786, 28, 'وَعِنَبࣰا وَقَضۡبࣰا\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(550, 80, 5787, 29, 'وَزَیۡتُونࣰا وَنَخۡلࣰا\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(551, 80, 5788, 30, 'وَحَدَاۤىِٕقَ غُلۡبࣰا\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(552, 80, 5789, 31, 'وَفَـٰكِهَةࣰ وَأَبࣰّا\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(553, 80, 5790, 32, 'مَّتَـٰعࣰا لَّكُمۡ وَلِأَنۡعَـٰمِكُمۡ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(554, 80, 5791, 33, 'فَإِذَا جَاۤءَتِ ٱلصَّاۤخَّةُ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:19'),
(555, 80, 5792, 34, 'یَوۡمَ یَفِرُّ ٱلۡمَرۡءُ مِنۡ أَخِیهِ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(556, 80, 5793, 35, 'وَأُمِّهِۦ وَأَبِیهِ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(557, 80, 5794, 36, 'وَصَـٰحِبَتِهِۦ وَبَنِیهِ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(558, 80, 5795, 37, 'لِكُلِّ ٱمۡرِئࣲ مِّنۡهُمۡ یَوۡمَىِٕذࣲ شَأۡنࣱ یُغۡنِیهِ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(559, 80, 5796, 38, 'وُجُوهࣱ یَوۡمَىِٕذࣲ مُّسۡفِرَةࣱ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(560, 80, 5797, 39, 'ضَاحِكَةࣱ مُّسۡتَبۡشِرَةࣱ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(561, 80, 5798, 40, 'وَوُجُوهࣱ یَوۡمَىِٕذٍ عَلَیۡهَا غَبَرَةࣱ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(562, 80, 5799, 41, 'تَرۡهَقُهَا قَتَرَةٌ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(563, 80, 5800, 42, 'أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡكَفَرَةُ ٱلۡفَجَرَةُ\n', NULL, 0, '2023-05-26 17:19:16', '2023-05-27 11:07:20'),
(564, 79, 5713, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَٱلنَّـٰزِعَـٰتِ غَرۡقࣰا\n', NULL, 0, '2023-05-26 17:19:25', '2023-05-27 11:07:28'),
(565, 79, 5714, 2, 'وَٱلنَّـٰشِطَـٰتِ نَشۡطࣰا\n', NULL, 0, '2023-05-26 17:19:25', '2023-05-27 11:07:28'),
(566, 79, 5715, 3, 'وَٱلسَّـٰبِحَـٰتِ سَبۡحࣰا\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(567, 79, 5716, 4, 'فَٱلسَّـٰبِقَـٰتِ سَبۡقࣰا\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(568, 79, 5717, 5, 'فَٱلۡمُدَبِّرَ ٰ⁠تِ أَمۡرࣰا\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(569, 79, 5718, 6, 'یَوۡمَ تَرۡجُفُ ٱلرَّاجِفَةُ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(570, 79, 5719, 7, 'تَتۡبَعُهَا ٱلرَّادِفَةُ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(571, 79, 5720, 8, 'قُلُوبࣱ یَوۡمَىِٕذࣲ وَاجِفَةٌ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(572, 79, 5721, 9, 'أَبۡصَـٰرُهَا خَـٰشِعَةࣱ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(573, 79, 5722, 10, 'یَقُولُونَ أَءِنَّا لَمَرۡدُودُونَ فِی ٱلۡحَافِرَةِ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(574, 79, 5723, 11, 'أَءِذَا كُنَّا عِظَـٰمࣰا نَّخِرَةࣰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(575, 79, 5724, 12, 'قَالُوا۟ تِلۡكَ إِذࣰا كَرَّةٌ خَاسِرَةࣱ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(576, 79, 5725, 13, 'فَإِنَّمَا هِیَ زَجۡرَةࣱ وَ ٰ⁠حِدَةࣱ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(577, 79, 5726, 14, 'فَإِذَا هُم بِٱلسَّاهِرَةِ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(578, 79, 5727, 15, 'هَلۡ أَتَىٰكَ حَدِیثُ مُوسَىٰۤ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(579, 79, 5728, 16, 'إِذۡ نَادَىٰهُ رَبُّهُۥ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوًى\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(580, 79, 5729, 17, 'ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:28'),
(581, 79, 5730, 18, 'فَقُلۡ هَل لَّكَ إِلَىٰۤ أَن تَزَكَّىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(582, 79, 5731, 19, 'وَأَهۡدِیَكَ إِلَىٰ رَبِّكَ فَتَخۡشَىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(583, 79, 5732, 20, 'فَأَرَىٰهُ ٱلۡـَٔایَةَ ٱلۡكُبۡرَىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(584, 79, 5733, 21, 'فَكَذَّبَ وَعَصَىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(585, 79, 5734, 22, 'ثُمَّ أَدۡبَرَ یَسۡعَىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(586, 79, 5735, 23, 'فَحَشَرَ فَنَادَىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(587, 79, 5736, 24, 'فَقَالَ أَنَا۠ رَبُّكُمُ ٱلۡأَعۡلَىٰ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(588, 79, 5737, 25, 'فَأَخَذَهُ ٱللَّهُ نَكَالَ ٱلۡـَٔاخِرَةِ وَٱلۡأُولَىٰۤ\n', NULL, 0, '2023-05-26 17:19:26', '2023-05-27 11:07:29'),
(589, 79, 5738, 26, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَعِبۡرَةࣰ لِّمَن یَخۡشَىٰۤ\n', NULL, 0, '2023-05-26 17:19:27', '2023-05-27 11:07:29'),
(590, 79, 5739, 27, 'ءَأَنتُمۡ أَشَدُّ خَلۡقًا أَمِ ٱلسَّمَاۤءُۚ بَنَىٰهَا\n', NULL, 0, '2023-05-26 17:19:27', '2023-05-27 11:07:29'),
(591, 79, 5740, 28, 'رَفَعَ سَمۡكَهَا فَسَوَّىٰهَا\n', NULL, 0, '2023-05-26 17:19:27', '2023-05-27 11:07:29'),
(592, 79, 5741, 29, 'وَأَغۡطَشَ لَیۡلَهَا وَأَخۡرَجَ ضُحَىٰهَا\n', NULL, 0, '2023-05-26 17:19:27', '2023-05-27 11:07:29'),
(593, 79, 5742, 30, 'وَٱلۡأَرۡضَ بَعۡدَ ذَ ٰ⁠لِكَ دَحَىٰهَاۤ\n', NULL, 0, '2023-05-26 17:19:27', '2023-05-27 11:07:29'),
