(681, 77, 5654, 32, 'إِنَّهَا تَرۡمِی بِشَرَرࣲ كَٱلۡقَصۡرِ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(682, 77, 5655, 33, 'كَأَنَّهُۥ جِمَـٰلَتࣱ صُفۡرࣱ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(683, 77, 5656, 34, 'وَیۡلࣱ یَوۡمَىِٕذࣲ لِّلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(684, 77, 5657, 35, 'هَـٰذَا یَوۡمُ لَا یَنطِقُونَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(685, 77, 5658, 36, 'وَلَا یُؤۡذَنُ لَهُمۡ فَیَعۡتَذِرُونَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(686, 77, 5659, 37, 'وَیۡلࣱ یَوۡمَىِٕذࣲ لِّلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(687, 77, 5660, 38, 'هَـٰذَا یَوۡمُ ٱلۡفَصۡلِۖ جَمَعۡنَـٰكُمۡ وَٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(688, 77, 5661, 39, 'فَإِن كَانَ لَكُمۡ كَیۡدࣱ فَكِیدُونِ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(689, 77, 5662, 40, 'وَیۡلࣱ یَوۡمَىِٕذࣲ لِّلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:35'),
(690, 77, 5663, 41, 'إِنَّ ٱلۡمُتَّقِینَ فِی ظِلَـٰلࣲ وَعُیُونࣲ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:36'),
(691, 77, 5664, 42, 'وَفَوَ ٰ⁠كِهَ مِمَّا یَشۡتَهُونَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:36'),
(692, 77, 5665, 43, 'كُلُوا۟ وَٱشۡرَبُوا۟ هَنِیۤـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:36'),
(693, 77, 5666, 44, 'إِنَّا كَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:19:39', '2023-05-27 11:07:36'),
(694, 77, 5667, 45, 'وَیۡلࣱ یَوۡمَىِٕذࣲ لِّلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:19:40', '2023-05-27 11:07:36'),
(695, 77, 5668, 46, 'كُلُوا۟ وَتَمَتَّعُوا۟ قَلِیلًا إِنَّكُم مُّجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:19:40', '2023-05-27 11:07:36'),
(696, 77, 5669, 47, 'وَیۡلࣱ یَوۡمَىِٕذࣲ لِّلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:19:40', '2023-05-27 11:07:36'),
(697, 77, 5670, 48, 'وَإِذَا قِیلَ لَهُمُ ٱرۡكَعُوا۟ لَا یَرۡكَعُونَ\n', NULL, 0, '2023-05-26 17:19:40', '2023-05-27 11:07:36'),
(698, 77, 5671, 49, 'وَیۡلࣱ یَوۡمَىِٕذࣲ لِّلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:19:40', '2023-05-27 11:07:36'),
(699, 77, 5672, 50, 'فَبِأَیِّ حَدِیثِۭ بَعۡدَهُۥ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:19:40', '2023-05-27 11:07:36'),
(700, 76, 5592, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ هَلۡ أَتَىٰ عَلَى ٱلۡإِنسَـٰنِ حِینࣱ مِّنَ ٱلدَّهۡرِ لَمۡ یَكُن شَیۡـࣰٔا مَّذۡكُورًا\n', NULL, 0, '2023-05-26 17:19:44', '2023-05-27 11:07:37'),
(701, 76, 5593, 2, 'إِنَّا خَلَقۡنَا ٱلۡإِنسَـٰنَ مِن نُّطۡفَةٍ أَمۡشَاجࣲ نَّبۡتَلِیهِ فَجَعَلۡنَـٰهُ سَمِیعَۢا بَصِیرًا\n', NULL, 0, '2023-05-26 17:19:44', '2023-05-27 11:07:37'),
(702, 76, 5594, 3, 'إِنَّا هَدَیۡنَـٰهُ ٱلسَّبِیلَ إِمَّا شَاكِرࣰا وَإِمَّا كَفُورًا\n', NULL, 0, '2023-05-26 17:19:44', '2023-05-27 11:07:37'),
(703, 76, 5595, 4, 'إِنَّاۤ أَعۡتَدۡنَا لِلۡكَـٰفِرِینَ سَلَـٰسِلَا۟ وَأَغۡلَـٰلࣰا وَسَعِیرًا\n', NULL, 0, '2023-05-26 17:19:44', '2023-05-27 11:07:37'),
(704, 76, 5596, 5, 'إِنَّ ٱلۡأَبۡرَارَ یَشۡرَبُونَ مِن كَأۡسࣲ كَانَ مِزَاجُهَا كَافُورًا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(705, 76, 5597, 6, 'عَیۡنࣰا یَشۡرَبُ بِهَا عِبَادُ ٱللَّهِ یُفَجِّرُونَهَا تَفۡجِیرࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(706, 76, 5598, 7, 'یُوفُونَ بِٱلنَّذۡرِ وَیَخَافُونَ یَوۡمࣰا كَانَ شَرُّهُۥ مُسۡتَطِیرࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(707, 76, 5599, 8, 'وَیُطۡعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسۡكِینࣰا وَیَتِیمࣰا وَأَسِیرًا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(708, 76, 5600, 9, 'إِنَّمَا نُطۡعِمُكُمۡ لِوَجۡهِ ٱللَّهِ لَا نُرِیدُ مِنكُمۡ جَزَاۤءࣰ وَلَا شُكُورًا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(709, 76, 5601, 10, 'إِنَّا نَخَافُ مِن رَّبِّنَا یَوۡمًا عَبُوسࣰا قَمۡطَرِیرࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(710, 76, 5602, 11, 'فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَ ٰ⁠لِكَ ٱلۡیَوۡمِ وَلَقَّىٰهُمۡ نَضۡرَةࣰ وَسُرُورࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(711, 76, 5603, 12, 'وَجَزَىٰهُم بِمَا صَبَرُوا۟ جَنَّةࣰ وَحَرِیرࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(712, 76, 5604, 13, 'مُّتَّكِـِٔینَ فِیهَا عَلَى ٱلۡأَرَاۤىِٕكِۖ لَا یَرَوۡنَ فِیهَا شَمۡسࣰا وَلَا زَمۡهَرِیرࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(713, 76, 5605, 14, 'وَدَانِیَةً عَلَیۡهِمۡ ظِلَـٰلُهَا وَذُلِّلَتۡ قُطُوفُهَا تَذۡلِیلࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(714, 76, 5606, 15, 'وَیُطَافُ عَلَیۡهِم بِـَٔانِیَةࣲ مِّن فِضَّةࣲ وَأَكۡوَابࣲ كَانَتۡ قَوَارِیرَا۠\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(715, 76, 5607, 16, 'قَوَارِیرَا۟ مِن فِضَّةࣲ قَدَّرُوهَا تَقۡدِیرࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(716, 76, 5608, 17, 'وَیُسۡقَوۡنَ فِیهَا كَأۡسࣰا كَانَ مِزَاجُهَا زَنجَبِیلًا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:37'),
(717, 76, 5609, 18, 'عَیۡنࣰا فِیهَا تُسَمَّىٰ سَلۡسَبِیلࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(718, 76, 5610, 19, '۞ وَیَطُوفُ عَلَیۡهِمۡ وِلۡدَ ٰ⁠نࣱ مُّخَلَّدُونَ إِذَا رَأَیۡتَهُمۡ حَسِبۡتَهُمۡ لُؤۡلُؤࣰا مَّنثُورࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(719, 76, 5611, 20, 'وَإِذَا رَأَیۡتَ ثَمَّ رَأَیۡتَ نَعِیمࣰا وَمُلۡكࣰا كَبِیرًا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(720, 76, 5612, 21, 'عَـٰلِیَهُمۡ ثِیَابُ سُندُسٍ خُضۡرࣱ وَإِسۡتَبۡرَقࣱۖ وَحُلُّوۤا۟ أَسَاوِرَ مِن فِضَّةࣲ وَسَقَىٰهُمۡ رَبُّهُمۡ شَرَابࣰا طَهُورًا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(721, 76, 5613, 22, 'إِنَّ هَـٰذَا كَانَ لَكُمۡ جَزَاۤءࣰ وَكَانَ سَعۡیُكُم مَّشۡكُورًا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(722, 76, 5614, 23, 'إِنَّا نَحۡنُ نَزَّلۡنَا عَلَیۡكَ ٱلۡقُرۡءَانَ تَنزِیلࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(723, 76, 5615, 24, 'فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تُطِعۡ مِنۡهُمۡ ءَاثِمًا أَوۡ كَفُورࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(724, 76, 5616, 25, 'وَٱذۡكُرِ ٱسۡمَ رَبِّكَ بُكۡرَةࣰ وَأَصِیلࣰا\n', NULL, 0, '2023-05-26 17:19:45', '2023-05-27 11:07:38'),
(725, 76, 5617, 26, 'وَمِنَ ٱلَّیۡلِ فَٱسۡجُدۡ لَهُۥ وَسَبِّحۡهُ لَیۡلࣰا طَوِیلًا\n', NULL, 0, '2023-05-26 17:19:46', '2023-05-27 11:07:38'),
(726, 76, 5618, 27, 'إِنَّ هَـٰۤؤُلَاۤءِ یُحِبُّونَ ٱلۡعَاجِلَةَ وَیَذَرُونَ وَرَاۤءَهُمۡ یَوۡمࣰا ثَقِیلࣰا\n', NULL, 0, '2023-05-26 17:19:46', '2023-05-27 11:07:38'),
(727, 76, 5619, 28, 'نَّحۡنُ خَلَقۡنَـٰهُمۡ وَشَدَدۡنَاۤ أَسۡرَهُمۡۖ وَإِذَا شِئۡنَا بَدَّلۡنَاۤ أَمۡثَـٰلَهُمۡ تَبۡدِیلًا\n', NULL, 0, '2023-05-26 17:19:46', '2023-05-27 11:07:38'),
(728, 76, 5620, 29, 'إِنَّ هَـٰذِهِۦ تَذۡكِرَةࣱۖ فَمَن شَاۤءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:19:46', '2023-05-27 11:07:38'),
(729, 76, 5621, 30, 'وَمَا تَشَاۤءُونَ إِلَّاۤ أَن یَشَاۤءَ ٱللَّهُۚ إِنَّ ٱللَّهَ كَانَ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:19:46', '2023-05-27 11:07:38'),
(730, 76, 5622, 31, 'یُدۡخِلُ مَن یَشَاۤءُ فِی رَحۡمَتِهِۦۚ وَٱلظَّـٰلِمِینَ أَعَدَّ لَهُمۡ عَذَابًا أَلِیمَۢا\n', NULL, 0, '2023-05-26 17:19:46', '2023-05-27 11:07:38'),
(731, 75, 5552, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ لَاۤ أُقۡسِمُ بِیَوۡمِ ٱلۡقِیَـٰمَةِ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(732, 75, 5553, 2, 'وَلَاۤ أُقۡسِمُ بِٱلنَّفۡسِ ٱللَّوَّامَةِ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(733, 75, 5554, 3, 'أَیَحۡسَبُ ٱلۡإِنسَـٰنُ أَلَّن نَّجۡمَعَ عِظَامَهُۥ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(734, 75, 5555, 4, 'بَلَىٰ قَـٰدِرِینَ عَلَىٰۤ أَن نُّسَوِّیَ بَنَانَهُۥ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(735, 75, 5556, 5, 'بَلۡ یُرِیدُ ٱلۡإِنسَـٰنُ لِیَفۡجُرَ أَمَامَهُۥ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(736, 75, 5557, 6, 'یَسۡـَٔلُ أَیَّانَ یَوۡمُ ٱلۡقِیَـٰمَةِ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(737, 75, 5558, 7, 'فَإِذَا بَرِقَ ٱلۡبَصَرُ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(738, 75, 5559, 8, 'وَخَسَفَ ٱلۡقَمَرُ\n', NULL, 0, '2023-05-26 17:19:47', '2023-05-27 11:07:39'),
(739, 75, 5560, 9, 'وَجُمِعَ ٱلشَّمۡسُ وَٱلۡقَمَرُ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:39'),
(740, 75, 5561, 10, 'یَقُولُ ٱلۡإِنسَـٰنُ یَوۡمَىِٕذٍ أَیۡنَ ٱلۡمَفَرُّ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:39'),
(741, 75, 5562, 11, 'كَلَّا لَا وَزَرَ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:39'),
(742, 75, 5563, 12, 'إِلَىٰ رَبِّكَ یَوۡمَىِٕذٍ ٱلۡمُسۡتَقَرُّ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:40'),
(743, 75, 5564, 13, 'یُنَبَّؤُا۟ ٱلۡإِنسَـٰنُ یَوۡمَىِٕذِۭ بِمَا قَدَّمَ وَأَخَّرَ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:40'),
(744, 75, 5565, 14, 'بَلِ ٱلۡإِنسَـٰنُ عَلَىٰ نَفۡسِهِۦ بَصِیرَةࣱ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:40'),
(745, 75, 5566, 15, 'وَلَوۡ أَلۡقَىٰ مَعَاذِیرَهُۥ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:40'),
(746, 75, 5567, 16, 'لَا تُحَرِّكۡ بِهِۦ لِسَانَكَ لِتَعۡجَلَ بِهِۦۤ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:40'),
(747, 75, 5568, 17, 'إِنَّ عَلَیۡنَا جَمۡعَهُۥ وَقُرۡءَانَهُۥ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:40'),
(748, 75, 5569, 18, 'فَإِذَا قَرَأۡنَـٰهُ فَٱتَّبِعۡ قُرۡءَانَهُۥ\n', NULL, 0, '2023-05-26 17:19:48', '2023-05-27 11:07:40'),
