(833, 73, 5482, 7, 'إِنَّ لَكَ فِی ٱلنَّهَارِ سَبۡحࣰا طَوِیلࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:58'),
(834, 73, 5483, 8, 'وَٱذۡكُرِ ٱسۡمَ رَبِّكَ وَتَبَتَّلۡ إِلَیۡهِ تَبۡتِیلࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:58'),
(835, 73, 5484, 9, 'رَّبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ لَاۤ إِلَـٰهَ إِلَّا هُوَ فَٱتَّخِذۡهُ وَكِیلࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:58'),
(836, 73, 5485, 10, 'وَٱصۡبِرۡ عَلَىٰ مَا یَقُولُونَ وَٱهۡجُرۡهُمۡ هَجۡرࣰا جَمِیلࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:58'),
(837, 73, 5486, 11, 'وَذَرۡنِی وَٱلۡمُكَذِّبِینَ أُو۟لِی ٱلنَّعۡمَةِ وَمَهِّلۡهُمۡ قَلِیلًا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:58'),
(838, 73, 5487, 12, 'إِنَّ لَدَیۡنَاۤ أَنكَالࣰا وَجَحِیمࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:58'),
(839, 73, 5488, 13, 'وَطَعَامࣰا ذَا غُصَّةࣲ وَعَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:59'),
(840, 73, 5489, 14, 'یَوۡمَ تَرۡجُفُ ٱلۡأَرۡضُ وَٱلۡجِبَالُ وَكَانَتِ ٱلۡجِبَالُ كَثِیبࣰا مَّهِیلًا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:59'),
(841, 73, 5490, 15, 'إِنَّاۤ أَرۡسَلۡنَاۤ إِلَیۡكُمۡ رَسُولࣰا شَـٰهِدًا عَلَیۡكُمۡ كَمَاۤ أَرۡسَلۡنَاۤ إِلَىٰ فِرۡعَوۡنَ رَسُولࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:59'),
(842, 73, 5491, 16, 'فَعَصَىٰ فِرۡعَوۡنُ ٱلرَّسُولَ فَأَخَذۡنَـٰهُ أَخۡذࣰا وَبِیلࣰا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:59'),
(843, 73, 5492, 17, 'فَكَیۡفَ تَتَّقُونَ إِن كَفَرۡتُمۡ یَوۡمࣰا یَجۡعَلُ ٱلۡوِلۡدَ ٰ⁠نَ شِیبًا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:59'),
(844, 73, 5493, 18, 'ٱلسَّمَاۤءُ مُنفَطِرُۢ بِهِۦۚ كَانَ وَعۡدُهُۥ مَفۡعُولًا\n', NULL, 0, '2023-05-26 17:20:09', '2023-05-27 11:07:59'),
(845, 73, 5494, 19, 'إِنَّ هَـٰذِهِۦ تَذۡكِرَةࣱۖ فَمَن شَاۤءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِیلًا\n', NULL, 0, '2023-05-26 17:20:10', '2023-05-27 11:07:59'),
(846, 73, 5495, 20, '۞ إِنَّ رَبَّكَ یَعۡلَمُ أَنَّكَ تَقُومُ أَدۡنَىٰ مِن ثُلُثَیِ ٱلَّیۡلِ وَنِصۡفَهُۥ وَثُلُثَهُۥ وَطَاۤىِٕفَةࣱ مِّنَ ٱلَّذِینَ مَعَكَۚ وَٱللَّهُ یُقَدِّرُ ٱلَّیۡلَ وَٱلنَّهَارَۚ عَلِمَ أَن لَّن تُحۡصُوهُ فَتَابَ عَلَیۡكُمۡۖ فَٱقۡرَءُوا۟ مَا تَیَسَّرَ مِنَ ٱلۡقُرۡءَانِۚ عَلِمَ أَن سَیَكُونُ مِنكُم مَّرۡضَىٰ وَءَاخَرُونَ یَضۡرِبُونَ فِی ٱلۡأَرۡضِ یَبۡتَغُونَ مِن فَضۡلِ ٱللَّهِ وَءَاخَرُونَ یُقَـٰتِلُونَ فِی سَبِیلِ ٱللَّهِۖ فَٱقۡرَءُوا۟ مَا تَیَسَّرَ مِنۡهُۚ وَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَقۡرِضُوا۟ ٱللَّهَ قَرۡضًا حَسَنࣰاۚ وَمَا تُقَدِّمُوا۟ لِأَنفُسِكُم مِّنۡ خَیۡرࣲ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَیۡرࣰا وَأَعۡظَمَ أَجۡرࣰاۚ وَٱسۡتَغۡفِرُوا۟ ٱللَّهَۖ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمُۢ\n', NULL, 0, '2023-05-26 17:20:10', '2023-05-27 11:07:59'),
(847, 72, 5448, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قُلۡ أُوحِیَ إِلَیَّ أَنَّهُ ٱسۡتَمَعَ نَفَرࣱ مِّنَ ٱلۡجِنِّ فَقَالُوۤا۟ إِنَّا سَمِعۡنَا قُرۡءَانًا عَجَبࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(848, 72, 5449, 2, 'یَهۡدِیۤ إِلَى ٱلرُّشۡدِ فَـَٔامَنَّا بِهِۦۖ وَلَن نُّشۡرِكَ بِرَبِّنَاۤ أَحَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(849, 72, 5450, 3, 'وَأَنَّهُۥ تَعَـٰلَىٰ جَدُّ رَبِّنَا مَا ٱتَّخَذَ صَـٰحِبَةࣰ وَلَا وَلَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(850, 72, 5451, 4, 'وَأَنَّهُۥ كَانَ یَقُولُ سَفِیهُنَا عَلَى ٱللَّهِ شَطَطࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(851, 72, 5452, 5, 'وَأَنَّا ظَنَنَّاۤ أَن لَّن تَقُولَ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَى ٱللَّهِ كَذِبࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(852, 72, 5453, 6, 'وَأَنَّهُۥ كَانَ رِجَالࣱ مِّنَ ٱلۡإِنسِ یَعُوذُونَ بِرِجَالࣲ مِّنَ ٱلۡجِنِّ فَزَادُوهُمۡ رَهَقࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(853, 72, 5454, 7, 'وَأَنَّهُمۡ ظَنُّوا۟ كَمَا ظَنَنتُمۡ أَن لَّن یَبۡعَثَ ٱللَّهُ أَحَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(854, 72, 5455, 8, 'وَأَنَّا لَمَسۡنَا ٱلسَّمَاۤءَ فَوَجَدۡنَـٰهَا مُلِئَتۡ حَرَسࣰا شَدِیدࣰا وَشُهُبࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(855, 72, 5456, 9, 'وَأَنَّا كُنَّا نَقۡعُدُ مِنۡهَا مَقَـٰعِدَ لِلسَّمۡعِۖ فَمَن یَسۡتَمِعِ ٱلۡـَٔانَ یَجِدۡ لَهُۥ شِهَابࣰا رَّصَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(856, 72, 5457, 10, 'وَأَنَّا لَا نَدۡرِیۤ أَشَرٌّ أُرِیدَ بِمَن فِی ٱلۡأَرۡضِ أَمۡ أَرَادَ بِهِمۡ رَبُّهُمۡ رَشَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(857, 72, 5458, 11, 'وَأَنَّا مِنَّا ٱلصَّـٰلِحُونَ وَمِنَّا دُونَ ذَ ٰ⁠لِكَۖ كُنَّا طَرَاۤىِٕقَ قِدَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(858, 72, 5459, 12, 'وَأَنَّا ظَنَنَّاۤ أَن لَّن نُّعۡجِزَ ٱللَّهَ فِی ٱلۡأَرۡضِ وَلَن نُّعۡجِزَهُۥ هَرَبࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(859, 72, 5460, 13, 'وَأَنَّا لَمَّا سَمِعۡنَا ٱلۡهُدَىٰۤ ءَامَنَّا بِهِۦۖ فَمَن یُؤۡمِنۢ بِرَبِّهِۦ فَلَا یَخَافُ بَخۡسࣰا وَلَا رَهَقࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(860, 72, 5461, 14, 'وَأَنَّا مِنَّا ٱلۡمُسۡلِمُونَ وَمِنَّا ٱلۡقَـٰسِطُونَۖ فَمَنۡ أَسۡلَمَ فَأُو۟لَـٰۤىِٕكَ تَحَرَّوۡا۟ رَشَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(861, 72, 5462, 15, 'وَأَمَّا ٱلۡقَـٰسِطُونَ فَكَانُوا۟ لِجَهَنَّمَ حَطَبࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(862, 72, 5463, 16, 'وَأَلَّوِ ٱسۡتَقَـٰمُوا۟ عَلَى ٱلطَّرِیقَةِ لَأَسۡقَیۡنَـٰهُم مَّاۤءً غَدَقࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(863, 72, 5464, 17, 'لِّنَفۡتِنَهُمۡ فِیهِۚ وَمَن یُعۡرِضۡ عَن ذِكۡرِ رَبِّهِۦ یَسۡلُكۡهُ عَذَابࣰا صَعَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(864, 72, 5465, 18, 'وَأَنَّ ٱلۡمَسَـٰجِدَ لِلَّهِ فَلَا تَدۡعُوا۟ مَعَ ٱللَّهِ أَحَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(865, 72, 5466, 19, 'وَأَنَّهُۥ لَمَّا قَامَ عَبۡدُ ٱللَّهِ یَدۡعُوهُ كَادُوا۟ یَكُونُونَ عَلَیۡهِ لِبَدࣰا\n', NULL, 0, '2023-05-26 17:20:12', '2023-05-27 11:08:00'),
(866, 72, 5467, 20, 'قُلۡ إِنَّمَاۤ أَدۡعُوا۟ رَبِّی وَلَاۤ أُشۡرِكُ بِهِۦۤ أَحَدࣰا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:00'),
(867, 72, 5468, 21, 'قُلۡ إِنِّی لَاۤ أَمۡلِكُ لَكُمۡ ضَرࣰّا وَلَا رَشَدࣰا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(868, 72, 5469, 22, 'قُلۡ إِنِّی لَن یُجِیرَنِی مِنَ ٱللَّهِ أَحَدࣱ وَلَنۡ أَجِدَ مِن دُونِهِۦ مُلۡتَحَدًا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(869, 72, 5470, 23, 'إِلَّا بَلَـٰغࣰا مِّنَ ٱللَّهِ وَرِسَـٰلَـٰتِهِۦۚ وَمَن یَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ لَهُۥ نَارَ جَهَنَّمَ خَـٰلِدِینَ فِیهَاۤ أَبَدًا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(870, 72, 5471, 24, 'حَتَّىٰۤ إِذَا رَأَوۡا۟ مَا یُوعَدُونَ فَسَیَعۡلَمُونَ مَنۡ أَضۡعَفُ نَاصِرࣰا وَأَقَلُّ عَدَدࣰا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(871, 72, 5472, 25, 'قُلۡ إِنۡ أَدۡرِیۤ أَقَرِیبࣱ مَّا تُوعَدُونَ أَمۡ یَجۡعَلُ لَهُۥ رَبِّیۤ أَمَدًا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(872, 72, 5473, 26, 'عَـٰلِمُ ٱلۡغَیۡبِ فَلَا یُظۡهِرُ عَلَىٰ غَیۡبِهِۦۤ أَحَدًا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(873, 72, 5474, 27, 'إِلَّا مَنِ ٱرۡتَضَىٰ مِن رَّسُولࣲ فَإِنَّهُۥ یَسۡلُكُ مِنۢ بَیۡنِ یَدَیۡهِ وَمِنۡ خَلۡفِهِۦ رَصَدࣰا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(874, 72, 5475, 28, 'لِّیَعۡلَمَ أَن قَدۡ أَبۡلَغُوا۟ رِسَـٰلَـٰتِ رَبِّهِمۡ وَأَحَاطَ بِمَا لَدَیۡهِمۡ وَأَحۡصَىٰ كُلَّ شَیۡءٍ عَدَدَۢا\n', NULL, 0, '2023-05-26 17:20:13', '2023-05-27 11:08:01'),
(875, 71, 5420, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِنَّاۤ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦۤ أَنۡ أَنذِرۡ قَوۡمَكَ مِن قَبۡلِ أَن یَأۡتِیَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:20:14', '2023-05-27 11:08:02'),
(876, 71, 5421, 2, 'قَالَ یَـٰقَوۡمِ إِنِّی لَكُمۡ نَذِیرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:20:14', '2023-05-27 11:08:02'),
(877, 71, 5422, 3, 'أَنِ ٱعۡبُدُوا۟ ٱللَّهَ وَٱتَّقُوهُ وَأَطِیعُونِ\n', NULL, 0, '2023-05-26 17:20:14', '2023-05-27 11:08:02'),
(878, 71, 5423, 4, 'یَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَیُؤَخِّرۡكُمۡ إِلَىٰۤ أَجَلࣲ مُّسَمًّىۚ إِنَّ أَجَلَ ٱللَّهِ إِذَا جَاۤءَ لَا یُؤَخَّرُۚ لَوۡ كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(879, 71, 5424, 5, 'قَالَ رَبِّ إِنِّی دَعَوۡتُ قَوۡمِی لَیۡلࣰا وَنَهَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(880, 71, 5425, 6, 'فَلَمۡ یَزِدۡهُمۡ دُعَاۤءِیۤ إِلَّا فِرَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(881, 71, 5426, 7, 'وَإِنِّی كُلَّمَا دَعَوۡتُهُمۡ لِتَغۡفِرَ لَهُمۡ جَعَلُوۤا۟ أَصَـٰبِعَهُمۡ فِیۤ ءَاذَانِهِمۡ وَٱسۡتَغۡشَوۡا۟ ثِیَابَهُمۡ وَأَصَرُّوا۟ وَٱسۡتَكۡبَرُوا۟ ٱسۡتِكۡبَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(882, 71, 5427, 8, 'ثُمَّ إِنِّی دَعَوۡتُهُمۡ جِهَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(883, 71, 5428, 9, 'ثُمَّ إِنِّیۤ أَعۡلَنتُ لَهُمۡ وَأَسۡرَرۡتُ لَهُمۡ إِسۡرَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(884, 71, 5429, 10, 'فَقُلۡتُ ٱسۡتَغۡفِرُوا۟ رَبَّكُمۡ إِنَّهُۥ كَانَ غَفَّارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(885, 71, 5430, 11, 'یُرۡسِلِ ٱلسَّمَاۤءَ عَلَیۡكُم مِّدۡرَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(886, 71, 5431, 12, 'وَیُمۡدِدۡكُم بِأَمۡوَ ٰ⁠لࣲ وَبَنِینَ وَیَجۡعَل لَّكُمۡ جَنَّـٰتࣲ وَیَجۡعَل لَّكُمۡ أَنۡهَـٰرࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(887, 71, 5432, 13, 'مَّا لَكُمۡ لَا تَرۡجُونَ لِلَّهِ وَقَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(888, 71, 5433, 14, 'وَقَدۡ خَلَقَكُمۡ أَطۡوَارًا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(889, 71, 5434, 15, 'أَلَمۡ تَرَوۡا۟ كَیۡفَ خَلَقَ ٱللَّهُ سَبۡعَ سَمَـٰوَ ٰ⁠تࣲ طِبَاقࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(890, 71, 5435, 16, 'وَجَعَلَ ٱلۡقَمَرَ فِیهِنَّ نُورࣰا وَجَعَلَ ٱلشَّمۡسَ سِرَاجࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02'),
(891, 71, 5436, 17, 'وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلۡأَرۡضِ نَبَاتࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:02');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(892, 71, 5437, 18, 'ثُمَّ یُعِیدُكُمۡ فِیهَا وَیُخۡرِجُكُمۡ إِخۡرَاجࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:03'),
(893, 71, 5438, 19, 'وَٱللَّهُ جَعَلَ لَكُمُ ٱلۡأَرۡضَ بِسَاطࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:03'),
(894, 71, 5439, 20, 'لِّتَسۡلُكُوا۟ مِنۡهَا سُبُلࣰا فِجَاجࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:03'),
(895, 71, 5440, 21, 'قَالَ نُوحࣱ رَّبِّ إِنَّهُمۡ عَصَوۡنِی وَٱتَّبَعُوا۟ مَن لَّمۡ یَزِدۡهُ مَالُهُۥ وَوَلَدُهُۥۤ إِلَّا خَسَارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:03'),
(896, 71, 5441, 22, 'وَمَكَرُوا۟ مَكۡرࣰا كُبَّارࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:03'),
(897, 71, 5442, 23, 'وَقَالُوا۟ لَا تَذَرُنَّ ءَالِهَتَكُمۡ وَلَا تَذَرُنَّ وَدࣰّا وَلَا سُوَاعࣰا وَلَا یَغُوثَ وَیَعُوقَ وَنَسۡرࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:03'),
(898, 71, 5443, 24, 'وَقَدۡ أَضَلُّوا۟ كَثِیرࣰاۖ وَلَا تَزِدِ ٱلظَّـٰلِمِینَ إِلَّا ضَلَـٰلࣰا\n', NULL, 0, '2023-05-26 17:20:15', '2023-05-27 11:08:03'),
(899, 71, 5444, 25, 'مِّمَّا خَطِیۤـَٔـٰتِهِمۡ أُغۡرِقُوا۟ فَأُدۡخِلُوا۟ نَارࣰا فَلَمۡ یَجِدُوا۟ لَهُم مِّن دُونِ ٱللَّهِ أَنصَارࣰا\n', NULL, 0, '2023-05-26 17:20:16', '2023-05-27 11:08:03'),
(900, 71, 5445, 26, 'وَقَالَ نُوحࣱ رَّبِّ لَا تَذَرۡ عَلَى ٱلۡأَرۡضِ مِنَ ٱلۡكَـٰفِرِینَ دَیَّارًا\n', NULL, 0, '2023-05-26 17:20:16', '2023-05-27 11:08:03'),
(901, 71, 5446, 27, 'إِنَّكَ إِن تَذَرۡهُمۡ یُضِلُّوا۟ عِبَادَكَ وَلَا یَلِدُوۤا۟ إِلَّا فَاجِرࣰا كَفَّارࣰا\n', NULL, 0, '2023-05-26 17:20:16', '2023-05-27 11:08:03'),
(902, 71, 5447, 28, 'رَّبِّ ٱغۡفِرۡ لِی وَلِوَ ٰ⁠لِدَیَّ وَلِمَن دَخَلَ بَیۡتِیَ مُؤۡمِنࣰا وَلِلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِۖ وَلَا تَزِدِ ٱلظَّـٰلِمِینَ إِلَّا تَبَارَۢا\n', NULL, 0, '2023-05-26 17:20:16', '2023-05-27 11:08:03'),
(903, 70, 5376, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ سَأَلَ سَاۤىِٕلُۢ بِعَذَابࣲ وَاقِعࣲ\n', NULL, 0, '2023-05-26 17:20:17', '2023-05-27 11:08:04'),
(904, 70, 5377, 2, 'لِّلۡكَـٰفِرِینَ لَیۡسَ لَهُۥ دَافِعࣱ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(905, 70, 5378, 3, 'مِّنَ ٱللَّهِ ذِی ٱلۡمَعَارِجِ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(906, 70, 5379, 4, 'تَعۡرُجُ ٱلۡمَلَـٰۤىِٕكَةُ وَٱلرُّوحُ إِلَیۡهِ فِی یَوۡمࣲ كَانَ مِقۡدَارُهُۥ خَمۡسِینَ أَلۡفَ سَنَةࣲ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(907, 70, 5380, 5, 'فَٱصۡبِرۡ صَبۡرࣰا جَمِیلًا\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(908, 70, 5381, 6, 'إِنَّهُمۡ یَرَوۡنَهُۥ بَعِیدࣰا\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(909, 70, 5382, 7, 'وَنَرَىٰهُ قَرِیبࣰا\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(910, 70, 5383, 8, 'یَوۡمَ تَكُونُ ٱلسَّمَاۤءُ كَٱلۡمُهۡلِ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(911, 70, 5384, 9, 'وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(912, 70, 5385, 10, 'وَلَا یَسۡـَٔلُ حَمِیمٌ حَمِیمࣰا\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(913, 70, 5386, 11, 'یُبَصَّرُونَهُمۡۚ یَوَدُّ ٱلۡمُجۡرِمُ لَوۡ یَفۡتَدِی مِنۡ عَذَابِ یَوۡمِىِٕذِۭ بِبَنِیهِ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(914, 70, 5387, 12, 'وَصَـٰحِبَتِهِۦ وَأَخِیهِ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(915, 70, 5388, 13, 'وَفَصِیلَتِهِ ٱلَّتِی تُـٔۡوِیهِ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(916, 70, 5389, 14, 'وَمَن فِی ٱلۡأَرۡضِ جَمِیعࣰا ثُمَّ یُنجِیهِ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:04'),
(917, 70, 5390, 15, 'كَلَّاۤۖ إِنَّهَا لَظَىٰ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:05'),
(918, 70, 5391, 16, 'نَزَّاعَةࣰ لِّلشَّوَىٰ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:05'),
(919, 70, 5392, 17, 'تَدۡعُوا۟ مَنۡ أَدۡبَرَ وَتَوَلَّىٰ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:05'),
(920, 70, 5393, 18, 'وَجَمَعَ فَأَوۡعَىٰۤ\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:05'),
(921, 70, 5394, 19, '۞ إِنَّ ٱلۡإِنسَـٰنَ خُلِقَ هَلُوعًا\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:05'),
(922, 70, 5395, 20, 'إِذَا مَسَّهُ ٱلشَّرُّ جَزُوعࣰا\n', NULL, 0, '2023-05-26 17:20:18', '2023-05-27 11:08:05'),
(923, 70, 5396, 21, 'وَإِذَا مَسَّهُ ٱلۡخَیۡرُ مَنُوعًا\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(924, 70, 5397, 22, 'إِلَّا ٱلۡمُصَلِّینَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(925, 70, 5398, 23, 'ٱلَّذِینَ هُمۡ عَلَىٰ صَلَاتِهِمۡ دَاۤىِٕمُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(926, 70, 5399, 24, 'وَٱلَّذِینَ فِیۤ أَمۡوَ ٰ⁠لِهِمۡ حَقࣱّ مَّعۡلُومࣱ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(927, 70, 5400, 25, 'لِّلسَّاۤىِٕلِ وَٱلۡمَحۡرُومِ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(928, 70, 5401, 26, 'وَٱلَّذِینَ یُصَدِّقُونَ بِیَوۡمِ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(929, 70, 5402, 27, 'وَٱلَّذِینَ هُم مِّنۡ عَذَابِ رَبِّهِم مُّشۡفِقُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(930, 70, 5403, 28, 'إِنَّ عَذَابَ رَبِّهِمۡ غَیۡرُ مَأۡمُونࣲ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(931, 70, 5404, 29, 'وَٱلَّذِینَ هُمۡ لِفُرُوجِهِمۡ حَـٰفِظُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(932, 70, 5405, 30, 'إِلَّا عَلَىٰۤ أَزۡوَ ٰ⁠جِهِمۡ أَوۡ مَا مَلَكَتۡ أَیۡمَـٰنُهُمۡ فَإِنَّهُمۡ غَیۡرُ مَلُومِینَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(933, 70, 5406, 31, 'فَمَنِ ٱبۡتَغَىٰ وَرَاۤءَ ذَ ٰ⁠لِكَ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡعَادُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(934, 70, 5407, 32, 'وَٱلَّذِینَ هُمۡ لِأَمَـٰنَـٰتِهِمۡ وَعَهۡدِهِمۡ رَ ٰ⁠عُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(935, 70, 5408, 33, 'وَٱلَّذِینَ هُم بِشَهَـٰدَ ٰ⁠تِهِمۡ قَاۤىِٕمُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:05'),
(936, 70, 5409, 34, 'وَٱلَّذِینَ هُمۡ عَلَىٰ صَلَاتِهِمۡ یُحَافِظُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:06'),
(937, 70, 5410, 35, 'أُو۟لَـٰۤىِٕكَ فِی جَنَّـٰتࣲ مُّكۡرَمُونَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:06'),
(938, 70, 5411, 36, 'فَمَالِ ٱلَّذِینَ كَفَرُوا۟ قِبَلَكَ مُهۡطِعِینَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:06'),
(939, 70, 5412, 37, 'عَنِ ٱلۡیَمِینِ وَعَنِ ٱلشِّمَالِ عِزِینَ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:06'),
(940, 70, 5413, 38, 'أَیَطۡمَعُ كُلُّ ٱمۡرِئࣲ مِّنۡهُمۡ أَن یُدۡخَلَ جَنَّةَ نَعِیمࣲ\n', NULL, 0, '2023-05-26 17:20:19', '2023-05-27 11:08:06'),
(941, 70, 5414, 39, 'كَلَّاۤۖ إِنَّا خَلَقۡنَـٰهُم مِّمَّا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:20:20', '2023-05-27 11:08:06'),
(942, 70, 5415, 40, 'فَلَاۤ أُقۡسِمُ بِرَبِّ ٱلۡمَشَـٰرِقِ وَٱلۡمَغَـٰرِبِ إِنَّا لَقَـٰدِرُونَ\n', NULL, 0, '2023-05-26 17:20:20', '2023-05-27 11:08:06'),
(943, 70, 5416, 41, 'عَلَىٰۤ أَن نُّبَدِّلَ خَیۡرࣰا مِّنۡهُمۡ وَمَا نَحۡنُ بِمَسۡبُوقِینَ\n', NULL, 0, '2023-05-26 17:20:20', '2023-05-27 11:08:06'),
(944, 70, 5417, 42, 'فَذَرۡهُمۡ یَخُوضُوا۟ وَیَلۡعَبُوا۟ حَتَّىٰ یُلَـٰقُوا۟ یَوۡمَهُمُ ٱلَّذِی یُوعَدُونَ\n', NULL, 0, '2023-05-26 17:20:20', '2023-05-27 11:08:06'),
(945, 70, 5418, 43, 'یَوۡمَ یَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ سِرَاعࣰا كَأَنَّهُمۡ إِلَىٰ نُصُبࣲ یُوفِضُونَ\n', NULL, 0, '2023-05-26 17:20:20', '2023-05-27 11:08:06'),
(946, 70, 5419, 44, 'خَـٰشِعَةً أَبۡصَـٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةࣱۚ ذَ ٰ⁠لِكَ ٱلۡیَوۡمُ ٱلَّذِی كَانُوا۟ یُوعَدُونَ\n', NULL, 0, '2023-05-26 17:20:20', '2023-05-27 11:08:06'),
(947, 69, 5324, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱلۡحَاۤقَّةُ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
(948, 69, 5325, 2, 'مَا ٱلۡحَاۤقَّةُ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
(949, 69, 5326, 3, 'وَمَاۤ أَدۡرَىٰكَ مَا ٱلۡحَاۤقَّةُ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
(950, 69, 5327, 4, 'كَذَّبَتۡ ثَمُودُ وَعَادُۢ بِٱلۡقَارِعَةِ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
(951, 69, 5328, 5, 'فَأَمَّا ثَمُودُ فَأُهۡلِكُوا۟ بِٱلطَّاغِیَةِ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
(952, 69, 5329, 6, 'وَأَمَّا عَادࣱ فَأُهۡلِكُوا۟ بِرِیحࣲ صَرۡصَرٍ عَاتِیَةࣲ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
(953, 69, 5330, 7, 'سَخَّرَهَا عَلَیۡهِمۡ سَبۡعَ لَیَالࣲ وَثَمَـٰنِیَةَ أَیَّامٍ حُسُومࣰاۖ فَتَرَى ٱلۡقَوۡمَ فِیهَا صَرۡعَىٰ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلٍ خَاوِیَةࣲ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
(954, 69, 5331, 8, 'فَهَلۡ تَرَىٰ لَهُم مِّنۢ بَاقِیَةࣲ\n', NULL, 0, '2023-05-26 17:20:33', '2023-05-27 11:08:16'),
