(1030, 68, 5303, 32, 'عَسَىٰ رَبُّنَاۤ أَن یُبۡدِلَنَا خَیۡرࣰا مِّنۡهَاۤ إِنَّاۤ إِلَىٰ رَبِّنَا رَ ٰ⁠غِبُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1031, 68, 5304, 33, 'كَذَ ٰ⁠لِكَ ٱلۡعَذَابُۖ وَلَعَذَابُ ٱلۡـَٔاخِرَةِ أَكۡبَرُۚ لَوۡ كَانُوا۟ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1032, 68, 5305, 34, 'إِنَّ لِلۡمُتَّقِینَ عِندَ رَبِّهِمۡ جَنَّـٰتِ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1033, 68, 5306, 35, 'أَفَنَجۡعَلُ ٱلۡمُسۡلِمِینَ كَٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1034, 68, 5307, 36, 'مَا لَكُمۡ كَیۡفَ تَحۡكُمُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1035, 68, 5308, 37, 'أَمۡ لَكُمۡ كِتَـٰبࣱ فِیهِ تَدۡرُسُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1036, 68, 5309, 38, 'إِنَّ لَكُمۡ فِیهِ لَمَا تَخَیَّرُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1037, 68, 5310, 39, 'أَمۡ لَكُمۡ أَیۡمَـٰنٌ عَلَیۡنَا بَـٰلِغَةٌ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ إِنَّ لَكُمۡ لَمَا تَحۡكُمُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1038, 68, 5311, 40, 'سَلۡهُمۡ أَیُّهُم بِذَ ٰ⁠لِكَ زَعِیمٌ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1039, 68, 5312, 41, 'أَمۡ لَهُمۡ شُرَكَاۤءُ فَلۡیَأۡتُوا۟ بِشُرَكَاۤىِٕهِمۡ إِن كَانُوا۟ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1040, 68, 5313, 42, 'یَوۡمَ یُكۡشَفُ عَن سَاقࣲ وَیُدۡعَوۡنَ إِلَى ٱلسُّجُودِ فَلَا یَسۡتَطِیعُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1041, 68, 5314, 43, 'خَـٰشِعَةً أَبۡصَـٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةࣱۖ وَقَدۡ كَانُوا۟ یُدۡعَوۡنَ إِلَى ٱلسُّجُودِ وَهُمۡ سَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1042, 68, 5315, 44, 'فَذَرۡنِی وَمَن یُكَذِّبُ بِهَـٰذَا ٱلۡحَدِیثِۖ سَنَسۡتَدۡرِجُهُم مِّنۡ حَیۡثُ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1043, 68, 5316, 45, 'وَأُمۡلِی لَهُمۡۚ إِنَّ كَیۡدِی مَتِینٌ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1044, 68, 5317, 46, 'أَمۡ تَسۡـَٔلُهُمۡ أَجۡرࣰا فَهُم مِّن مَّغۡرَمࣲ مُّثۡقَلُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1045, 68, 5318, 47, 'أَمۡ عِندَهُمُ ٱلۡغَیۡبُ فَهُمۡ یَكۡتُبُونَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1046, 68, 5319, 48, 'فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تَكُن كَصَاحِبِ ٱلۡحُوتِ إِذۡ نَادَىٰ وَهُوَ مَكۡظُومࣱ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:21'),
(1047, 68, 5320, 49, 'لَّوۡلَاۤ أَن تَدَ ٰ⁠رَكَهُۥ نِعۡمَةࣱ مِّن رَّبِّهِۦ لَنُبِذَ بِٱلۡعَرَاۤءِ وَهُوَ مَذۡمُومࣱ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:22'),
(1048, 68, 5321, 50, 'فَٱجۡتَبَـٰهُ رَبُّهُۥ فَجَعَلَهُۥ مِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:20:39', '2023-05-27 11:08:22'),
(1049, 68, 5322, 51, 'وَإِن یَكَادُ ٱلَّذِینَ كَفَرُوا۟ لَیُزۡلِقُونَكَ بِأَبۡصَـٰرِهِمۡ لَمَّا سَمِعُوا۟ ٱلذِّكۡرَ وَیَقُولُونَ إِنَّهُۥ لَمَجۡنُونࣱ\n', NULL, 0, '2023-05-26 17:20:40', '2023-05-27 11:08:22'),
(1050, 68, 5323, 52, 'وَمَا هُوَ إِلَّا ذِكۡرࣱ لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:20:40', '2023-05-27 11:08:22'),
(1051, 67, 5242, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ تَبَـٰرَكَ ٱلَّذِی بِیَدِهِ ٱلۡمُلۡكُ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:20:41', '2023-05-27 11:08:22'),
(1052, 67, 5243, 2, 'ٱلَّذِی خَلَقَ ٱلۡمَوۡتَ وَٱلۡحَیَوٰةَ لِیَبۡلُوَكُمۡ أَیُّكُمۡ أَحۡسَنُ عَمَلࣰاۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡغَفُورُ\n', NULL, 0, '2023-05-26 17:20:41', '2023-05-27 11:08:23'),
(1053, 67, 5244, 3, 'ٱلَّذِی خَلَقَ سَبۡعَ سَمَـٰوَ ٰ⁠تࣲ طِبَاقࣰاۖ مَّا تَرَىٰ فِی خَلۡقِ ٱلرَّحۡمَـٰنِ مِن تَفَـٰوُتࣲۖ فَٱرۡجِعِ ٱلۡبَصَرَ هَلۡ تَرَىٰ مِن فُطُورࣲ\n', NULL, 0, '2023-05-26 17:20:41', '2023-05-27 11:08:23'),
(1054, 67, 5245, 4, 'ثُمَّ ٱرۡجِعِ ٱلۡبَصَرَ كَرَّتَیۡنِ یَنقَلِبۡ إِلَیۡكَ ٱلۡبَصَرُ خَاسِئࣰا وَهُوَ حَسِیرࣱ\n', NULL, 0, '2023-05-26 17:20:41', '2023-05-27 11:08:23'),
(1055, 67, 5246, 5, 'وَلَقَدۡ زَیَّنَّا ٱلسَّمَاۤءَ ٱلدُّنۡیَا بِمَصَـٰبِیحَ وَجَعَلۡنَـٰهَا رُجُومࣰا لِّلشَّیَـٰطِینِۖ وَأَعۡتَدۡنَا لَهُمۡ عَذَابَ ٱلسَّعِیرِ\n', NULL, 0, '2023-05-26 17:20:41', '2023-05-27 11:08:23'),
(1056, 67, 5247, 6, 'وَلِلَّذِینَ كَفَرُوا۟ بِرَبِّهِمۡ عَذَابُ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:20:41', '2023-05-27 11:08:23'),
(1057, 67, 5248, 7, 'إِذَاۤ أُلۡقُوا۟ فِیهَا سَمِعُوا۟ لَهَا شَهِیقࣰا وَهِیَ تَفُورُ\n', NULL, 0, '2023-05-26 17:20:41', '2023-05-27 11:08:23'),
(1058, 67, 5249, 8, 'تَكَادُ تَمَیَّزُ مِنَ ٱلۡغَیۡظِۖ كُلَّمَاۤ أُلۡقِیَ فِیهَا فَوۡجࣱ سَأَلَهُمۡ خَزَنَتُهَاۤ أَلَمۡ یَأۡتِكُمۡ نَذِیرࣱ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1059, 67, 5250, 9, 'قَالُوا۟ بَلَىٰ قَدۡ جَاۤءَنَا نَذِیرࣱ فَكَذَّبۡنَا وَقُلۡنَا مَا نَزَّلَ ٱللَّهُ مِن شَیۡءٍ إِنۡ أَنتُمۡ إِلَّا فِی ضَلَـٰلࣲ كَبِیرࣲ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1060, 67, 5251, 10, 'وَقَالُوا۟ لَوۡ كُنَّا نَسۡمَعُ أَوۡ نَعۡقِلُ مَا كُنَّا فِیۤ أَصۡحَـٰبِ ٱلسَّعِیرِ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1061, 67, 5252, 11, 'فَٱعۡتَرَفُوا۟ بِذَنۢبِهِمۡ فَسُحۡقࣰا لِّأَصۡحَـٰبِ ٱلسَّعِیرِ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1062, 67, 5253, 12, 'إِنَّ ٱلَّذِینَ یَخۡشَوۡنَ رَبَّهُم بِٱلۡغَیۡبِ لَهُم مَّغۡفِرَةࣱ وَأَجۡرࣱ كَبِیرࣱ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1063, 67, 5254, 13, 'وَأَسِرُّوا۟ قَوۡلَكُمۡ أَوِ ٱجۡهَرُوا۟ بِهِۦۤۖ إِنَّهُۥ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1064, 67, 5255, 14, 'أَلَا یَعۡلَمُ مَنۡ خَلَقَ وَهُوَ ٱللَّطِیفُ ٱلۡخَبِیرُ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1065, 67, 5256, 15, 'هُوَ ٱلَّذِی جَعَلَ لَكُمُ ٱلۡأَرۡضَ ذَلُولࣰا فَٱمۡشُوا۟ فِی مَنَاكِبِهَا وَكُلُوا۟ مِن رِّزۡقِهِۦۖ وَإِلَیۡهِ ٱلنُّشُورُ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1066, 67, 5257, 16, 'ءَأَمِنتُم مَّن فِی ٱلسَّمَاۤءِ أَن یَخۡسِفَ بِكُمُ ٱلۡأَرۡضَ فَإِذَا هِیَ تَمُورُ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1067, 67, 5258, 17, 'أَمۡ أَمِنتُم مَّن فِی ٱلسَّمَاۤءِ أَن یُرۡسِلَ عَلَیۡكُمۡ حَاصِبࣰاۖ فَسَتَعۡلَمُونَ كَیۡفَ نَذِیرِ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1068, 67, 5259, 18, 'وَلَقَدۡ كَذَّبَ ٱلَّذِینَ مِن قَبۡلِهِمۡ فَكَیۡفَ كَانَ نَكِیرِ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:23'),
(1069, 67, 5260, 19, 'أَوَلَمۡ یَرَوۡا۟ إِلَى ٱلطَّیۡرِ فَوۡقَهُمۡ صَـٰۤفَّـٰتࣲ وَیَقۡبِضۡنَۚ مَا یُمۡسِكُهُنَّ إِلَّا ٱلرَّحۡمَـٰنُۚ إِنَّهُۥ بِكُلِّ شَیۡءِۭ بَصِیرٌ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:24'),
(1070, 67, 5261, 20, 'أَمَّنۡ هَـٰذَا ٱلَّذِی هُوَ جُندࣱ لَّكُمۡ یَنصُرُكُم مِّن دُونِ ٱلرَّحۡمَـٰنِۚ إِنِ ٱلۡكَـٰفِرُونَ إِلَّا فِی غُرُورٍ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:24'),
(1071, 67, 5262, 21, 'أَمَّنۡ هَـٰذَا ٱلَّذِی یَرۡزُقُكُمۡ إِنۡ أَمۡسَكَ رِزۡقَهُۥۚ بَل لَّجُّوا۟ فِی عُتُوࣲّ وَنُفُورٍ\n', NULL, 0, '2023-05-26 17:20:42', '2023-05-27 11:08:24'),
(1072, 67, 5263, 22, 'أَفَمَن یَمۡشِی مُكِبًّا عَلَىٰ وَجۡهِهِۦۤ أَهۡدَىٰۤ أَمَّن یَمۡشِی سَوِیًّا عَلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1073, 67, 5264, 23, 'قُلۡ هُوَ ٱلَّذِیۤ أَنشَأَكُمۡ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَـٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِیلࣰا مَّا تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1074, 67, 5265, 24, 'قُلۡ هُوَ ٱلَّذِی ذَرَأَكُمۡ فِی ٱلۡأَرۡضِ وَإِلَیۡهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1075, 67, 5266, 25, 'وَیَقُولُونَ مَتَىٰ هَـٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1076, 67, 5267, 26, 'قُلۡ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَإِنَّمَاۤ أَنَا۠ نَذِیرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1077, 67, 5268, 27, 'فَلَمَّا رَأَوۡهُ زُلۡفَةࣰ سِیۤـَٔتۡ وُجُوهُ ٱلَّذِینَ كَفَرُوا۟ وَقِیلَ هَـٰذَا ٱلَّذِی كُنتُم بِهِۦ تَدَّعُونَ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1078, 67, 5269, 28, 'قُلۡ أَرَءَیۡتُمۡ إِنۡ أَهۡلَكَنِیَ ٱللَّهُ وَمَن مَّعِیَ أَوۡ رَحِمَنَا فَمَن یُجِیرُ ٱلۡكَـٰفِرِینَ مِنۡ عَذَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1079, 67, 5270, 29, 'قُلۡ هُوَ ٱلرَّحۡمَـٰنُ ءَامَنَّا بِهِۦ وَعَلَیۡهِ تَوَكَّلۡنَاۖ فَسَتَعۡلَمُونَ مَنۡ هُوَ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1080, 67, 5271, 30, 'قُلۡ أَرَءَیۡتُمۡ إِنۡ أَصۡبَحَ مَاۤؤُكُمۡ غَوۡرࣰا فَمَن یَأۡتِیكُم بِمَاۤءࣲ مَّعِینِۭ\n', NULL, 0, '2023-05-26 17:20:43', '2023-05-27 11:08:24'),
(1081, 66, 5230, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلنَّبِیُّ لِمَ تُحَرِّمُ مَاۤ أَحَلَّ ٱللَّهُ لَكَۖ تَبۡتَغِی مَرۡضَاتَ أَزۡوَ ٰ⁠جِكَۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1082, 66, 5231, 2, 'قَدۡ فَرَضَ ٱللَّهُ لَكُمۡ تَحِلَّةَ أَیۡمَـٰنِكُمۡۚ وَٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ ٱلۡعَلِیمُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1083, 66, 5232, 3, 'وَإِذۡ أَسَرَّ ٱلنَّبِیُّ إِلَىٰ بَعۡضِ أَزۡوَ ٰ⁠جِهِۦ حَدِیثࣰا فَلَمَّا نَبَّأَتۡ بِهِۦ وَأَظۡهَرَهُ ٱللَّهُ عَلَیۡهِ عَرَّفَ بَعۡضَهُۥ وَأَعۡرَضَ عَنۢ بَعۡضࣲۖ فَلَمَّا نَبَّأَهَا بِهِۦ قَالَتۡ مَنۡ أَنۢبَأَكَ هَـٰذَاۖ قَالَ نَبَّأَنِیَ ٱلۡعَلِیمُ ٱلۡخَبِیرُ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1084, 66, 5233, 4, 'إِن تَتُوبَاۤ إِلَى ٱللَّهِ فَقَدۡ صَغَتۡ قُلُوبُكُمَاۖ وَإِن تَظَـٰهَرَا عَلَیۡهِ فَإِنَّ ٱللَّهَ هُوَ مَوۡلَىٰهُ وَجِبۡرِیلُ وَصَـٰلِحُ ٱلۡمُؤۡمِنِینَۖ وَٱلۡمَلَـٰۤىِٕكَةُ بَعۡدَ ذَ ٰ⁠لِكَ ظَهِیرٌ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1085, 66, 5234, 5, 'عَسَىٰ رَبُّهُۥۤ إِن طَلَّقَكُنَّ أَن یُبۡدِلَهُۥۤ أَزۡوَ ٰ⁠جًا خَیۡرࣰا مِّنكُنَّ مُسۡلِمَـٰتࣲ مُّؤۡمِنَـٰتࣲ قَـٰنِتَـٰتࣲ تَـٰۤىِٕبَـٰتٍ عَـٰبِدَ ٰ⁠تࣲ سَـٰۤىِٕحَـٰتࣲ ثَیِّبَـٰتࣲ وَأَبۡكَارࣰا\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1086, 66, 5235, 6, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ قُوۤا۟ أَنفُسَكُمۡ وَأَهۡلِیكُمۡ نَارࣰا وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُ عَلَیۡهَا مَلَـٰۤىِٕكَةٌ غِلَاظࣱ شِدَادࣱ لَّا یَعۡصُونَ ٱللَّهَ مَاۤ أَمَرَهُمۡ وَیَفۡعَلُونَ مَا یُؤۡمَرُونَ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1087, 66, 5236, 7, 'یَـٰۤأَیُّهَا ٱلَّذِینَ كَفَرُوا۟ لَا تَعۡتَذِرُوا۟ ٱلۡیَوۡمَۖ إِنَّمَا تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1088, 66, 5237, 8, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ تُوبُوۤا۟ إِلَى ٱللَّهِ تَوۡبَةࣰ نَّصُوحًا عَسَىٰ رَبُّكُمۡ أَن یُكَفِّرَ عَنكُمۡ سَیِّـَٔاتِكُمۡ وَیُدۡخِلَكُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ یَوۡمَ لَا یُخۡزِی ٱللَّهُ ٱلنَّبِیَّ وَٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥۖ نُورُهُمۡ یَسۡعَىٰ بَیۡنَ أَیۡدِیهِمۡ وَبِأَیۡمَـٰنِهِمۡ یَقُولُونَ رَبَّنَاۤ أَتۡمِمۡ لَنَا نُورَنَا وَٱغۡفِرۡ لَنَاۤۖ إِنَّكَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1089, 66, 5238, 9, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ جَـٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَـٰفِقِینَ وَٱغۡلُظۡ عَلَیۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1090, 66, 5239, 10, 'ضَرَبَ ٱللَّهُ مَثَلࣰا لِّلَّذِینَ كَفَرُوا۟ ٱمۡرَأَتَ نُوحࣲ وَٱمۡرَأَتَ لُوطࣲۖ كَانَتَا تَحۡتَ عَبۡدَیۡنِ مِنۡ عِبَادِنَا صَـٰلِحَیۡنِ فَخَانَتَاهُمَا فَلَمۡ یُغۡنِیَا عَنۡهُمَا مِنَ ٱللَّهِ شَیۡـࣰٔا وَقِیلَ ٱدۡخُلَا ٱلنَّارَ مَعَ ٱلدَّ ٰ⁠خِلِینَ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1091, 66, 5240, 11, 'وَضَرَبَ ٱللَّهُ مَثَلࣰا لِّلَّذِینَ ءَامَنُوا۟ ٱمۡرَأَتَ فِرۡعَوۡنَ إِذۡ قَالَتۡ رَبِّ ٱبۡنِ لِی عِندَكَ بَیۡتࣰا فِی ٱلۡجَنَّةِ وَنَجِّنِی مِن فِرۡعَوۡنَ وَعَمَلِهِۦ وَنَجِّنِی مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1092, 66, 5241, 12, 'وَمَرۡیَمَ ٱبۡنَتَ عِمۡرَ ٰ⁠نَ ٱلَّتِیۤ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِیهِ مِن رُّوحِنَا وَصَدَّقَتۡ بِكَلِمَـٰتِ رَبِّهَا وَكُتُبِهِۦ وَكَانَتۡ مِنَ ٱلۡقَـٰنِتِینَ\n', NULL, 0, '2023-05-26 17:20:45', '2023-05-27 11:08:25'),
(1093, 65, 5218, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلنَّبِیُّ إِذَا طَلَّقۡتُمُ ٱلنِّسَاۤءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحۡصُوا۟ ٱلۡعِدَّةَۖ وَٱتَّقُوا۟ ٱللَّهَ رَبَّكُمۡۖ لَا تُخۡرِجُوهُنَّ مِنۢ بُیُوتِهِنَّ وَلَا یَخۡرُجۡنَ إِلَّاۤ أَن یَأۡتِینَ بِفَـٰحِشَةࣲ مُّبَیِّنَةࣲۚ وَتِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن یَتَعَدَّ حُدُودَ ٱللَّهِ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ لَا تَدۡرِی لَعَلَّ ٱللَّهَ یُحۡدِثُ بَعۡدَ ذَ ٰ⁠لِكَ أَمۡرࣰا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:26'),
(1094, 65, 5219, 2, 'فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ فَارِقُوهُنَّ بِمَعۡرُوفࣲ وَأَشۡهِدُوا۟ ذَوَیۡ عَدۡلࣲ مِّنكُمۡ وَأَقِیمُوا۟ ٱلشَّهَـٰدَةَ لِلَّهِۚ ذَ ٰ⁠لِكُمۡ یُوعَظُ بِهِۦ مَن كَانَ یُؤۡمِنُ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِۚ وَمَن یَتَّقِ ٱللَّهَ یَجۡعَل لَّهُۥ مَخۡرَجࣰا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:26'),
(1095, 65, 5220, 3, 'وَیَرۡزُقۡهُ مِنۡ حَیۡثُ لَا یَحۡتَسِبُۚ وَمَن یَتَوَكَّلۡ عَلَى ٱللَّهِ فَهُوَ حَسۡبُهُۥۤۚ إِنَّ ٱللَّهَ بَـٰلِغُ أَمۡرِهِۦۚ قَدۡ جَعَلَ ٱللَّهُ لِكُلِّ شَیۡءࣲ قَدۡرࣰا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:26'),
(1096, 65, 5221, 4, 'وَٱلَّـٰۤـِٔی یَىِٕسۡنَ مِنَ ٱلۡمَحِیضِ مِن نِّسَاۤىِٕكُمۡ إِنِ ٱرۡتَبۡتُمۡ فَعِدَّتُهُنَّ ثَلَـٰثَةُ أَشۡهُرࣲ وَٱلَّـٰۤـِٔی لَمۡ یَحِضۡنَۚ وَأُو۟لَـٰتُ ٱلۡأَحۡمَالِ أَجَلُهُنَّ أَن یَضَعۡنَ حَمۡلَهُنَّۚ وَمَن یَتَّقِ ٱللَّهَ یَجۡعَل لَّهُۥ مِنۡ أَمۡرِهِۦ یُسۡرࣰا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1097, 65, 5222, 5, 'ذَ ٰ⁠لِكَ أَمۡرُ ٱللَّهِ أَنزَلَهُۥۤ إِلَیۡكُمۡۚ وَمَن یَتَّقِ ٱللَّهَ یُكَفِّرۡ عَنۡهُ سَیِّـَٔاتِهِۦ وَیُعۡظِمۡ لَهُۥۤ أَجۡرًا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1098, 65, 5223, 6, 'أَسۡكِنُوهُنَّ مِنۡ حَیۡثُ سَكَنتُم مِّن وُجۡدِكُمۡ وَلَا تُضَاۤرُّوهُنَّ لِتُضَیِّقُوا۟ عَلَیۡهِنَّۚ وَإِن كُنَّ أُو۟لَـٰتِ حَمۡلࣲ فَأَنفِقُوا۟ عَلَیۡهِنَّ حَتَّىٰ یَضَعۡنَ حَمۡلَهُنَّۚ فَإِنۡ أَرۡضَعۡنَ لَكُمۡ فَـَٔاتُوهُنَّ أُجُورَهُنَّ وَأۡتَمِرُوا۟ بَیۡنَكُم بِمَعۡرُوفࣲۖ وَإِن تَعَاسَرۡتُمۡ فَسَتُرۡضِعُ لَهُۥۤ أُخۡرَىٰ\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1099, 65, 5224, 7, 'لِیُنفِقۡ ذُو سَعَةࣲ مِّن سَعَتِهِۦۖ وَمَن قُدِرَ عَلَیۡهِ رِزۡقُهُۥ فَلۡیُنفِقۡ مِمَّاۤ ءَاتَىٰهُ ٱللَّهُۚ لَا یُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا مَاۤ ءَاتَىٰهَاۚ سَیَجۡعَلُ ٱللَّهُ بَعۡدَ عُسۡرࣲ یُسۡرࣰا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1100, 65, 5225, 8, 'وَكَأَیِّن مِّن قَرۡیَةٍ عَتَتۡ عَنۡ أَمۡرِ رَبِّهَا وَرُسُلِهِۦ فَحَاسَبۡنَـٰهَا حِسَابࣰا شَدِیدࣰا وَعَذَّبۡنَـٰهَا عَذَابࣰا نُّكۡرࣰا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1101, 65, 5226, 9, 'فَذَاقَتۡ وَبَالَ أَمۡرِهَا وَكَانَ عَـٰقِبَةُ أَمۡرِهَا خُسۡرًا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1102, 65, 5227, 10, 'أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابࣰا شَدِیدࣰاۖ فَٱتَّقُوا۟ ٱللَّهَ یَـٰۤأُو۟لِی ٱلۡأَلۡبَـٰبِ ٱلَّذِینَ ءَامَنُوا۟ۚ قَدۡ أَنزَلَ ٱللَّهُ إِلَیۡكُمۡ ذِكۡرࣰا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1103, 65, 5228, 11, 'رَّسُولࣰا یَتۡلُوا۟ عَلَیۡكُمۡ ءَایَـٰتِ ٱللَّهِ مُبَیِّنَـٰتࣲ لِّیُخۡرِجَ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ مِنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِۚ وَمَن یُؤۡمِنۢ بِٱللَّهِ وَیَعۡمَلۡ صَـٰلِحࣰا یُدۡخِلۡهُ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۖ قَدۡ أَحۡسَنَ ٱللَّهُ لَهُۥ رِزۡقًا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1104, 65, 5229, 12, 'ٱللَّهُ ٱلَّذِی خَلَقَ سَبۡعَ سَمَـٰوَ ٰ⁠تࣲ وَمِنَ ٱلۡأَرۡضِ مِثۡلَهُنَّۖ یَتَنَزَّلُ ٱلۡأَمۡرُ بَیۡنَهُنَّ لِتَعۡلَمُوۤا۟ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ وَأَنَّ ٱللَّهَ قَدۡ أَحَاطَ بِكُلِّ شَیۡءٍ عِلۡمَۢا\n', NULL, 0, '2023-05-26 17:20:47', '2023-05-27 11:08:27'),
(1105, 64, 5200, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یُسَبِّحُ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۖ لَهُ ٱلۡمُلۡكُ وَلَهُ ٱلۡحَمۡدُۖ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:21:19', '2023-05-27 11:08:35'),
(1106, 64, 5201, 2, 'هُوَ ٱلَّذِی خَلَقَكُمۡ فَمِنكُمۡ كَافِرࣱ وَمِنكُم مُّؤۡمِنࣱۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرٌ\n', NULL, 0, '2023-05-26 17:21:19', '2023-05-27 11:08:36'),
(1107, 64, 5202, 3, 'خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡۖ وَإِلَیۡهِ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:21:19', '2023-05-27 11:08:36'),
(1108, 64, 5203, 4, 'یَعۡلَمُ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَیَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَۚ وَٱللَّهُ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:21:19', '2023-05-27 11:08:36'),
(1109, 64, 5204, 5, 'أَلَمۡ یَأۡتِكُمۡ نَبَؤُا۟ ٱلَّذِینَ كَفَرُوا۟ مِن قَبۡلُ فَذَاقُوا۟ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:21:19', '2023-05-27 11:08:36'),
(1110, 64, 5205, 6, 'ذَ ٰ⁠لِكَ بِأَنَّهُۥ كَانَت تَّأۡتِیهِمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِ فَقَالُوۤا۟ أَبَشَرࣱ یَهۡدُونَنَا فَكَفَرُوا۟ وَتَوَلَّوا۟ۖ وَّٱسۡتَغۡنَى ٱللَّهُۚ وَٱللَّهُ غَنِیٌّ حَمِیدࣱ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1111, 64, 5206, 7, 'زَعَمَ ٱلَّذِینَ كَفَرُوۤا۟ أَن لَّن یُبۡعَثُوا۟ۚ قُلۡ بَلَىٰ وَرَبِّی لَتُبۡعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلۡتُمۡۚ وَذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣱ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1112, 64, 5207, 8, 'فَـَٔامِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَٱلنُّورِ ٱلَّذِیۤ أَنزَلۡنَاۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1113, 64, 5208, 9, 'یَوۡمَ یَجۡمَعُكُمۡ لِیَوۡمِ ٱلۡجَمۡعِۖ ذَ ٰ⁠لِكَ یَوۡمُ ٱلتَّغَابُنِۗ وَمَن یُؤۡمِنۢ بِٱللَّهِ وَیَعۡمَلۡ صَـٰلِحࣰا یُكَفِّرۡ عَنۡهُ سَیِّـَٔاتِهِۦ وَیُدۡخِلۡهُ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۚ ذَ ٰ⁠لِكَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1114, 64, 5209, 10, 'وَٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔایَـٰتِنَاۤ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِ خَـٰلِدِینَ فِیهَاۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1115, 64, 5210, 11, 'مَاۤ أَصَابَ مِن مُّصِیبَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ وَمَن یُؤۡمِنۢ بِٱللَّهِ یَهۡدِ قَلۡبَهُۥۚ وَٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1116, 64, 5211, 12, 'وَأَطِیعُوا۟ ٱللَّهَ وَأَطِیعُوا۟ ٱلرَّسُولَۚ فَإِن تَوَلَّیۡتُمۡ فَإِنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَـٰغُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1117, 64, 5212, 13, 'ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَۚ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1118, 64, 5213, 14, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِنَّ مِنۡ أَزۡوَ ٰ⁠جِكُمۡ وَأَوۡلَـٰدِكُمۡ عَدُوࣰّا لَّكُمۡ فَٱحۡذَرُوهُمۡۚ وَإِن تَعۡفُوا۟ وَتَصۡفَحُوا۟ وَتَغۡفِرُوا۟ فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1119, 64, 5214, 15, 'إِنَّمَاۤ أَمۡوَ ٰ⁠لُكُمۡ وَأَوۡلَـٰدُكُمۡ فِتۡنَةࣱۚ وَٱللَّهُ عِندَهُۥۤ أَجۡرٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1120, 64, 5215, 16, 'فَٱتَّقُوا۟ ٱللَّهَ مَا ٱسۡتَطَعۡتُمۡ وَٱسۡمَعُوا۟ وَأَطِیعُوا۟ وَأَنفِقُوا۟ خَیۡرࣰا لِّأَنفُسِكُمۡۗ وَمَن یُوقَ شُحَّ نَفۡسِهِۦ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1121, 64, 5216, 17, 'إِن تُقۡرِضُوا۟ ٱللَّهَ قَرۡضًا حَسَنࣰا یُضَـٰعِفۡهُ لَكُمۡ وَیَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ شَكُورٌ حَلِیمٌ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1122, 64, 5217, 18, 'عَـٰلِمُ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:21:20', '2023-05-27 11:08:36'),
(1123, 63, 5189, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِذَا جَاۤءَكَ ٱلۡمُنَـٰفِقُونَ قَالُوا۟ نَشۡهَدُ إِنَّكَ لَرَسُولُ ٱللَّهِۗ وَٱللَّهُ یَعۡلَمُ إِنَّكَ لَرَسُولُهُۥ وَٱللَّهُ یَشۡهَدُ إِنَّ ٱلۡمُنَـٰفِقِینَ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:21:21', '2023-05-27 11:08:37'),
(1124, 63, 5190, 2, 'ٱتَّخَذُوۤا۟ أَیۡمَـٰنَهُمۡ جُنَّةࣰ فَصَدُّوا۟ عَن سَبِیلِ ٱللَّهِۚ إِنَّهُمۡ سَاۤءَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:21:21', '2023-05-27 11:08:37'),
(1125, 63, 5191, 3, 'ذَ ٰ⁠لِكَ بِأَنَّهُمۡ ءَامَنُوا۟ ثُمَّ كَفَرُوا۟ فَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:21:21', '2023-05-27 11:08:37'),
(1126, 63, 5192, 4, '۞ وَإِذَا رَأَیۡتَهُمۡ تُعۡجِبُكَ أَجۡسَامُهُمۡۖ وَإِن یَقُولُوا۟ تَسۡمَعۡ لِقَوۡلِهِمۡۖ كَأَنَّهُمۡ خُشُبࣱ مُّسَنَّدَةࣱۖ یَحۡسَبُونَ كُلَّ صَیۡحَةٍ عَلَیۡهِمۡۚ هُمُ ٱلۡعَدُوُّ فَٱحۡذَرۡهُمۡۚ قَـٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ یُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:21:21', '2023-05-27 11:08:37'),
(1127, 63, 5193, 5, 'وَإِذَا قِیلَ لَهُمۡ تَعَالَوۡا۟ یَسۡتَغۡفِرۡ لَكُمۡ رَسُولُ ٱللَّهِ لَوَّوۡا۟ رُءُوسَهُمۡ وَرَأَیۡتَهُمۡ یَصُدُّونَ وَهُم مُّسۡتَكۡبِرُونَ\n', NULL, 0, '2023-05-26 17:21:22', '2023-05-27 11:08:37'),
(1128, 63, 5194, 6, 'سَوَاۤءٌ عَلَیۡهِمۡ أَسۡتَغۡفَرۡتَ لَهُمۡ أَمۡ لَمۡ تَسۡتَغۡفِرۡ لَهُمۡ لَن یَغۡفِرَ ٱللَّهُ لَهُمۡۚ إِنَّ ٱللَّهَ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:21:22', '2023-05-27 11:08:37'),
(1129, 63, 5195, 7, 'هُمُ ٱلَّذِینَ یَقُولُونَ لَا تُنفِقُوا۟ عَلَىٰ مَنۡ عِندَ رَسُولِ ٱللَّهِ حَتَّىٰ یَنفَضُّوا۟ۗ وَلِلَّهِ خَزَاۤىِٕنُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَلَـٰكِنَّ ٱلۡمُنَـٰفِقِینَ لَا یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:21:22', '2023-05-27 11:08:37'),
(1130, 63, 5196, 8, 'یَقُولُونَ لَىِٕن رَّجَعۡنَاۤ إِلَى ٱلۡمَدِینَةِ لَیُخۡرِجَنَّ ٱلۡأَعَزُّ مِنۡهَا ٱلۡأَذَلَّۚ وَلِلَّهِ ٱلۡعِزَّةُ وَلِرَسُولِهِۦ وَلِلۡمُؤۡمِنِینَ وَلَـٰكِنَّ ٱلۡمُنَـٰفِقِینَ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:21:22', '2023-05-27 11:08:37'),
(1131, 63, 5197, 9, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تُلۡهِكُمۡ أَمۡوَ ٰ⁠لُكُمۡ وَلَاۤ أَوۡلَـٰدُكُمۡ عَن ذِكۡرِ ٱللَّهِۚ وَمَن یَفۡعَلۡ ذَ ٰ⁠لِكَ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:21:22', '2023-05-27 11:08:38'),
(1132, 63, 5198, 10, 'وَأَنفِقُوا۟ مِن مَّا رَزَقۡنَـٰكُم مِّن قَبۡلِ أَن یَأۡتِیَ أَحَدَكُمُ ٱلۡمَوۡتُ فَیَقُولَ رَبِّ لَوۡلَاۤ أَخَّرۡتَنِیۤ إِلَىٰۤ أَجَلࣲ قَرِیبࣲ فَأَصَّدَّقَ وَأَكُن مِّنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:21:22', '2023-05-27 11:08:38'),
(1133, 63, 5199, 11, 'وَلَن یُؤَخِّرَ ٱللَّهُ نَفۡسًا إِذَا جَاۤءَ أَجَلُهَاۚ وَٱللَّهُ خَبِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:21:22', '2023-05-27 11:08:38'),
(1134, 62, 5178, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یُسَبِّحُ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِ ٱلۡمَلِكِ ٱلۡقُدُّوسِ ٱلۡعَزِیزِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:21:24', '2023-05-27 11:08:38'),
(1135, 62, 5179, 2, 'هُوَ ٱلَّذِی بَعَثَ فِی ٱلۡأُمِّیِّـۧنَ رَسُولࣰا مِّنۡهُمۡ یَتۡلُوا۟ عَلَیۡهِمۡ ءَایَـٰتِهِۦ وَیُزَكِّیهِمۡ وَیُعَلِّمُهُمُ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُوا۟ مِن قَبۡلُ لَفِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:21:24', '2023-05-27 11:08:38'),
(1136, 62, 5180, 3, 'وَءَاخَرِینَ مِنۡهُمۡ لَمَّا یَلۡحَقُوا۟ بِهِمۡۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:21:24', '2023-05-27 11:08:38'),
(1137, 62, 5181, 4, 'ذَ ٰ⁠لِكَ فَضۡلُ ٱللَّهِ یُؤۡتِیهِ مَن یَشَاۤءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:21:24', '2023-05-27 11:08:38'),
(1138, 62, 5182, 5, 'مَثَلُ ٱلَّذِینَ حُمِّلُوا۟ ٱلتَّوۡرَىٰةَ ثُمَّ لَمۡ یَحۡمِلُوهَا كَمَثَلِ ٱلۡحِمَارِ یَحۡمِلُ أَسۡفَارَۢاۚ بِئۡسَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِ ٱللَّهِۚ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:21:24', '2023-05-27 11:08:38'),
(1139, 62, 5183, 6, 'قُلۡ یَـٰۤأَیُّهَا ٱلَّذِینَ هَادُوۤا۟ إِن زَعَمۡتُمۡ أَنَّكُمۡ أَوۡلِیَاۤءُ لِلَّهِ مِن دُونِ ٱلنَّاسِ فَتَمَنَّوُا۟ ٱلۡمَوۡتَ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:21:24', '2023-05-27 11:08:38'),
(1140, 62, 5184, 7, 'وَلَا یَتَمَنَّوۡنَهُۥۤ أَبَدَۢا بِمَا قَدَّمَتۡ أَیۡدِیهِمۡۚ وَٱللَّهُ عَلِیمُۢ بِٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:21:24', '2023-05-27 11:08:38'),
(1141, 62, 5185, 8, 'قُلۡ إِنَّ ٱلۡمَوۡتَ ٱلَّذِی تَفِرُّونَ مِنۡهُ فَإِنَّهُۥ مُلَـٰقِیكُمۡۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَـٰلِمِ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ فَیُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:21:25', '2023-05-27 11:08:39'),
(1142, 62, 5186, 9, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا نُودِیَ لِلصَّلَوٰةِ مِن یَوۡمِ ٱلۡجُمُعَةِ فَٱسۡعَوۡا۟ إِلَىٰ ذِكۡرِ ٱللَّهِ وَذَرُوا۟ ٱلۡبَیۡعَۚ ذَ ٰ⁠لِكُمۡ خَیۡرࣱ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:21:25', '2023-05-27 11:08:39'),
(1143, 62, 5187, 10, 'فَإِذَا قُضِیَتِ ٱلصَّلَوٰةُ فَٱنتَشِرُوا۟ فِی ٱلۡأَرۡضِ وَٱبۡتَغُوا۟ مِن فَضۡلِ ٱللَّهِ وَٱذۡكُرُوا۟ ٱللَّهَ كَثِیرࣰا لَّعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:21:25', '2023-05-27 11:08:39'),
(1144, 62, 5188, 11, 'وَإِذَا رَأَوۡا۟ تِجَـٰرَةً أَوۡ لَهۡوًا ٱنفَضُّوۤا۟ إِلَیۡهَا وَتَرَكُوكَ قَاۤىِٕمࣰاۚ قُلۡ مَا عِندَ ٱللَّهِ خَیۡرࣱ مِّنَ ٱللَّهۡوِ وَمِنَ ٱلتِّجَـٰرَةِۚ وَٱللَّهُ خَیۡرُ ٱلرَّ ٰ⁠زِقِینَ\n', NULL, 0, '2023-05-26 17:21:25', '2023-05-27 11:08:39'),
(1145, 61, 5164, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ سَبَّحَ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:21:26', '2023-05-27 11:08:40'),
(1146, 61, 5165, 2, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لِمَ تَقُولُونَ مَا لَا تَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1147, 61, 5166, 3, 'كَبُرَ مَقۡتًا عِندَ ٱللَّهِ أَن تَقُولُوا۟ مَا لَا تَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1148, 61, 5167, 4, 'إِنَّ ٱللَّهَ یُحِبُّ ٱلَّذِینَ یُقَـٰتِلُونَ فِی سَبِیلِهِۦ صَفࣰّا كَأَنَّهُم بُنۡیَـٰنࣱ مَّرۡصُوصࣱ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1149, 61, 5168, 5, 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ یَـٰقَوۡمِ لِمَ تُؤۡذُونَنِی وَقَد تَّعۡلَمُونَ أَنِّی رَسُولُ ٱللَّهِ إِلَیۡكُمۡۖ فَلَمَّا زَاغُوۤا۟ أَزَاغَ ٱللَّهُ قُلُوبَهُمۡۚ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1150, 61, 5169, 6, 'وَإِذۡ قَالَ عِیسَى ٱبۡنُ مَرۡیَمَ یَـٰبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ إِنِّی رَسُولُ ٱللَّهِ إِلَیۡكُم مُّصَدِّقࣰا لِّمَا بَیۡنَ یَدَیَّ مِنَ ٱلتَّوۡرَىٰةِ وَمُبَشِّرَۢا بِرَسُولࣲ یَأۡتِی مِنۢ بَعۡدِی ٱسۡمُهُۥۤ أَحۡمَدُۖ فَلَمَّا جَاۤءَهُم بِٱلۡبَیِّنَـٰتِ قَالُوا۟ هَـٰذَا سِحۡرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1151, 61, 5170, 7, 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُوَ یُدۡعَىٰۤ إِلَى ٱلۡإِسۡلَـٰمِۚ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1152, 61, 5171, 8, 'یُرِیدُونَ لِیُطۡفِـُٔوا۟ نُورَ ٱللَّهِ بِأَفۡوَ ٰ⁠هِهِمۡ وَٱللَّهُ مُتِمُّ نُورِهِۦ وَلَوۡ كَرِهَ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1153, 61, 5172, 9, 'هُوَ ٱلَّذِیۤ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِینِ ٱلۡحَقِّ لِیُظۡهِرَهُۥ عَلَى ٱلدِّینِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1154, 61, 5173, 10, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ هَلۡ أَدُلُّكُمۡ عَلَىٰ تِجَـٰرَةࣲ تُنجِیكُم مِّنۡ عَذَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1155, 61, 5174, 11, 'تُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦ وَتُجَـٰهِدُونَ فِی سَبِیلِ ٱللَّهِ بِأَمۡوَ ٰ⁠لِكُمۡ وَأَنفُسِكُمۡۚ ذَ ٰ⁠لِكُمۡ خَیۡرࣱ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1156, 61, 5175, 12, 'یَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡ وَیُدۡخِلۡكُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ وَمَسَـٰكِنَ طَیِّبَةࣰ فِی جَنَّـٰتِ عَدۡنࣲۚ ذَ ٰ⁠لِكَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1157, 61, 5176, 13, 'وَأُخۡرَىٰ تُحِبُّونَهَاۖ نَصۡرࣱ مِّنَ ٱللَّهِ وَفَتۡحࣱ قَرِیبࣱۗ وَبَشِّرِ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1158, 61, 5177, 14, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ كُونُوۤا۟ أَنصَارَ ٱللَّهِ كَمَا قَالَ عِیسَى ٱبۡنُ مَرۡیَمَ لِلۡحَوَارِیِّـۧنَ مَنۡ أَنصَارِیۤ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِیُّونَ نَحۡنُ أَنصَارُ ٱللَّهِۖ فَـَٔامَنَت طَّاۤىِٕفَةࣱ مِّنۢ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ وَكَفَرَت طَّاۤىِٕفَةࣱۖ فَأَیَّدۡنَا ٱلَّذِینَ ءَامَنُوا۟ عَلَىٰ عَدُوِّهِمۡ فَأَصۡبَحُوا۟ ظَـٰهِرِینَ\n', NULL, 0, '2023-05-26 17:21:27', '2023-05-27 11:08:40'),
(1159, 60, 5151, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَّخِذُوا۟ عَدُوِّی وَعَدُوَّكُمۡ أَوۡلِیَاۤءَ تُلۡقُونَ إِلَیۡهِم بِٱلۡمَوَدَّةِ وَقَدۡ كَفَرُوا۟ بِمَا جَاۤءَكُم مِّنَ ٱلۡحَقِّ یُخۡرِجُونَ ٱلرَّسُولَ وَإِیَّاكُمۡ أَن تُؤۡمِنُوا۟ بِٱللَّهِ رَبِّكُمۡ إِن كُنتُمۡ خَرَجۡتُمۡ جِهَـٰدࣰا فِی سَبِیلِی وَٱبۡتِغَاۤءَ مَرۡضَاتِیۚ تُسِرُّونَ إِلَیۡهِم بِٱلۡمَوَدَّةِ وَأَنَا۠ أَعۡلَمُ بِمَاۤ أَخۡفَیۡتُمۡ وَمَاۤ أَعۡلَنتُمۡۚ وَمَن یَفۡعَلۡهُ مِنكُمۡ فَقَدۡ ضَلَّ سَوَاۤءَ ٱلسَّبِیلِ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:41'),
(1160, 60, 5152, 2, 'إِن یَثۡقَفُوكُمۡ یَكُونُوا۟ لَكُمۡ أَعۡدَاۤءࣰ وَیَبۡسُطُوۤا۟ إِلَیۡكُمۡ أَیۡدِیَهُمۡ وَأَلۡسِنَتَهُم بِٱلسُّوۤءِ وَوَدُّوا۟ لَوۡ تَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:41'),
(1161, 60, 5153, 3, 'لَن تَنفَعَكُمۡ أَرۡحَامُكُمۡ وَلَاۤ أَوۡلَـٰدُكُمۡۚ یَوۡمَ ٱلۡقِیَـٰمَةِ یَفۡصِلُ بَیۡنَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:41'),
(1162, 60, 5154, 4, 'قَدۡ كَانَتۡ لَكُمۡ أُسۡوَةٌ حَسَنَةࣱ فِیۤ إِبۡرَ ٰ⁠هِیمَ وَٱلَّذِینَ مَعَهُۥۤ إِذۡ قَالُوا۟ لِقَوۡمِهِمۡ إِنَّا بُرَءَ ٰۤ⁠ ؤُا۟ مِنكُمۡ وَمِمَّا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ كَفَرۡنَا بِكُمۡ وَبَدَا بَیۡنَنَا وَبَیۡنَكُمُ ٱلۡعَدَ ٰ⁠وَةُ وَٱلۡبَغۡضَاۤءُ أَبَدًا حَتَّىٰ تُؤۡمِنُوا۟ بِٱللَّهِ وَحۡدَهُۥۤ إِلَّا قَوۡلَ إِبۡرَ ٰ⁠هِیمَ لِأَبِیهِ لَأَسۡتَغۡفِرَنَّ لَكَ وَمَاۤ أَمۡلِكُ لَكَ مِنَ ٱللَّهِ مِن شَیۡءࣲۖ رَّبَّنَا عَلَیۡكَ تَوَكَّلۡنَا وَإِلَیۡكَ أَنَبۡنَا وَإِلَیۡكَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:41'),
(1163, 60, 5155, 5, 'رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةࣰ لِّلَّذِینَ كَفَرُوا۟ وَٱغۡفِرۡ لَنَا رَبَّنَاۤۖ إِنَّكَ أَنتَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:41'),
(1164, 60, 5156, 6, 'لَقَدۡ كَانَ لَكُمۡ فِیهِمۡ أُسۡوَةٌ حَسَنَةࣱ لِّمَن كَانَ یَرۡجُوا۟ ٱللَّهَ وَٱلۡیَوۡمَ ٱلۡـَٔاخِرَۚ وَمَن یَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِیُّ ٱلۡحَمِیدُ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:41'),
(1165, 60, 5157, 7, '۞ عَسَى ٱللَّهُ أَن یَجۡعَلَ بَیۡنَكُمۡ وَبَیۡنَ ٱلَّذِینَ عَادَیۡتُم مِّنۡهُم مَّوَدَّةࣰۚ وَٱللَّهُ قَدِیرࣱۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:41'),
(1166, 60, 5158, 8, 'لَّا یَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِینَ لَمۡ یُقَـٰتِلُوكُمۡ فِی ٱلدِّینِ وَلَمۡ یُخۡرِجُوكُم مِّن دِیَـٰرِكُمۡ أَن تَبَرُّوهُمۡ وَتُقۡسِطُوۤا۟ إِلَیۡهِمۡۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُقۡسِطِینَ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:42'),
(1167, 60, 5159, 9, 'إِنَّمَا یَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِینَ قَـٰتَلُوكُمۡ فِی ٱلدِّینِ وَأَخۡرَجُوكُم مِّن دِیَـٰرِكُمۡ وَظَـٰهَرُوا۟ عَلَىٰۤ إِخۡرَاجِكُمۡ أَن تَوَلَّوۡهُمۡۚ وَمَن یَتَوَلَّهُمۡ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:42'),
(1168, 60, 5160, 10, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا جَاۤءَكُمُ ٱلۡمُؤۡمِنَـٰتُ مُهَـٰجِرَ ٰ⁠تࣲ فَٱمۡتَحِنُوهُنَّۖ ٱللَّهُ أَعۡلَمُ بِإِیمَـٰنِهِنَّۖ فَإِنۡ عَلِمۡتُمُوهُنَّ مُؤۡمِنَـٰتࣲ فَلَا تَرۡجِعُوهُنَّ إِلَى ٱلۡكُفَّارِۖ لَا هُنَّ حِلࣱّ لَّهُمۡ وَلَا هُمۡ یَحِلُّونَ لَهُنَّۖ وَءَاتُوهُم مَّاۤ أَنفَقُوا۟ۚ وَلَا جُنَاحَ عَلَیۡكُمۡ أَن تَنكِحُوهُنَّ إِذَاۤ ءَاتَیۡتُمُوهُنَّ أُجُورَهُنَّۚ وَلَا تُمۡسِكُوا۟ بِعِصَمِ ٱلۡكَوَافِرِ وَسۡـَٔلُوا۟ مَاۤ أَنفَقۡتُمۡ وَلۡیَسۡـَٔلُوا۟ مَاۤ أَنفَقُوا۟ۚ ذَ ٰ⁠لِكُمۡ حُكۡمُ ٱللَّهِ یَحۡكُمُ بَیۡنَكُمۡۖ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:42'),
(1169, 60, 5161, 11, 'وَإِن فَاتَكُمۡ شَیۡءࣱ مِّنۡ أَزۡوَ ٰ⁠جِكُمۡ إِلَى ٱلۡكُفَّارِ فَعَاقَبۡتُمۡ فَـَٔاتُوا۟ ٱلَّذِینَ ذَهَبَتۡ أَزۡوَ ٰ⁠جُهُم مِّثۡلَ مَاۤ أَنفَقُوا۟ۚ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِیۤ أَنتُم بِهِۦ مُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:42'),
(1170, 60, 5162, 12, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ إِذَا جَاۤءَكَ ٱلۡمُؤۡمِنَـٰتُ یُبَایِعۡنَكَ عَلَىٰۤ أَن لَّا یُشۡرِكۡنَ بِٱللَّهِ شَیۡـࣰٔا وَلَا یَسۡرِقۡنَ وَلَا یَزۡنِینَ وَلَا یَقۡتُلۡنَ أَوۡلَـٰدَهُنَّ وَلَا یَأۡتِینَ بِبُهۡتَـٰنࣲ یَفۡتَرِینَهُۥ بَیۡنَ أَیۡدِیهِنَّ وَأَرۡجُلِهِنَّ وَلَا یَعۡصِینَكَ فِی مَعۡرُوفࣲ فَبَایِعۡهُنَّ وَٱسۡتَغۡفِرۡ لَهُنَّ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:42');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(1171, 60, 5163, 13, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَوَلَّوۡا۟ قَوۡمًا غَضِبَ ٱللَّهُ عَلَیۡهِمۡ قَدۡ یَىِٕسُوا۟ مِنَ ٱلۡـَٔاخِرَةِ كَمَا یَىِٕسَ ٱلۡكُفَّارُ مِنۡ أَصۡحَـٰبِ ٱلۡقُبُورِ\n', NULL, 0, '2023-05-26 17:21:33', '2023-05-27 11:08:42'),
(1172, 59, 5127, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ سَبَّحَ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1173, 59, 5128, 2, 'هُوَ ٱلَّذِیۤ أَخۡرَجَ ٱلَّذِینَ كَفَرُوا۟ مِنۡ أَهۡلِ ٱلۡكِتَـٰبِ مِن دِیَـٰرِهِمۡ لِأَوَّلِ ٱلۡحَشۡرِۚ مَا ظَنَنتُمۡ أَن یَخۡرُجُوا۟ۖ وَظَنُّوۤا۟ أَنَّهُم مَّانِعَتُهُمۡ حُصُونُهُم مِّنَ ٱللَّهِ فَأَتَىٰهُمُ ٱللَّهُ مِنۡ حَیۡثُ لَمۡ یَحۡتَسِبُوا۟ۖ وَقَذَفَ فِی قُلُوبِهِمُ ٱلرُّعۡبَۚ یُخۡرِبُونَ بُیُوتَهُم بِأَیۡدِیهِمۡ وَأَیۡدِی ٱلۡمُؤۡمِنِینَ فَٱعۡتَبِرُوا۟ یَـٰۤأُو۟لِی ٱلۡأَبۡصَـٰرِ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1174, 59, 5129, 3, 'وَلَوۡلَاۤ أَن كَتَبَ ٱللَّهُ عَلَیۡهِمُ ٱلۡجَلَاۤءَ لَعَذَّبَهُمۡ فِی ٱلدُّنۡیَاۖ وَلَهُمۡ فِی ٱلۡـَٔاخِرَةِ عَذَابُ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1175, 59, 5130, 4, 'ذَ ٰ⁠لِكَ بِأَنَّهُمۡ شَاۤقُّوا۟ ٱللَّهَ وَرَسُولَهُۥۖ وَمَن یُشَاۤقِّ ٱللَّهَ فَإِنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1176, 59, 5131, 5, 'مَا قَطَعۡتُم مِّن لِّینَةٍ أَوۡ تَرَكۡتُمُوهَا قَاۤىِٕمَةً عَلَىٰۤ أُصُولِهَا فَبِإِذۡنِ ٱللَّهِ وَلِیُخۡزِیَ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1177, 59, 5132, 6, 'وَمَاۤ أَفَاۤءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡهُمۡ فَمَاۤ أَوۡجَفۡتُمۡ عَلَیۡهِ مِنۡ خَیۡلࣲ وَلَا رِكَابࣲ وَلَـٰكِنَّ ٱللَّهَ یُسَلِّطُ رُسُلَهُۥ عَلَىٰ مَن یَشَاۤءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1178, 59, 5133, 7, 'مَّاۤ أَفَاۤءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡ أَهۡلِ ٱلۡقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِی ٱلۡقُرۡبَىٰ وَٱلۡیَتَـٰمَىٰ وَٱلۡمَسَـٰكِینِ وَٱبۡنِ ٱلسَّبِیلِ كَیۡ لَا یَكُونَ دُولَةَۢ بَیۡنَ ٱلۡأَغۡنِیَاۤءِ مِنكُمۡۚ وَمَاۤ ءَاتَىٰكُمُ ٱلرَّسُولُ فَخُذُوهُ وَمَا نَهَىٰكُمۡ عَنۡهُ فَٱنتَهُوا۟ۚ وَٱتَّقُوا۟ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1179, 59, 5134, 8, 'لِلۡفُقَرَاۤءِ ٱلۡمُهَـٰجِرِینَ ٱلَّذِینَ أُخۡرِجُوا۟ مِن دِیَـٰرِهِمۡ وَأَمۡوَ ٰ⁠لِهِمۡ یَبۡتَغُونَ فَضۡلࣰا مِّنَ ٱللَّهِ وَرِضۡوَ ٰ⁠نࣰا وَیَنصُرُونَ ٱللَّهَ وَرَسُولَهُۥۤۚ أُو۟لَـٰۤىِٕكَ هُمُ ٱلصَّـٰدِقُونَ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1180, 59, 5135, 9, 'وَٱلَّذِینَ تَبَوَّءُو ٱلدَّارَ وَٱلۡإِیمَـٰنَ مِن قَبۡلِهِمۡ یُحِبُّونَ مَنۡ هَاجَرَ إِلَیۡهِمۡ وَلَا یَجِدُونَ فِی صُدُورِهِمۡ حَاجَةࣰ مِّمَّاۤ أُوتُوا۟ وَیُؤۡثِرُونَ عَلَىٰۤ أَنفُسِهِمۡ وَلَوۡ كَانَ بِهِمۡ خَصَاصَةࣱۚ وَمَن یُوقَ شُحَّ نَفۡسِهِۦ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:08:59'),
(1181, 59, 5136, 10, 'وَٱلَّذِینَ جَاۤءُو مِنۢ بَعۡدِهِمۡ یَقُولُونَ رَبَّنَا ٱغۡفِرۡ لَنَا وَلِإِخۡوَ ٰ⁠نِنَا ٱلَّذِینَ سَبَقُونَا بِٱلۡإِیمَـٰنِ وَلَا تَجۡعَلۡ فِی قُلُوبِنَا غِلࣰّا لِّلَّذِینَ ءَامَنُوا۟ رَبَّنَاۤ إِنَّكَ رَءُوفࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:09:00'),
(1182, 59, 5137, 11, '۞ أَلَمۡ تَرَ إِلَى ٱلَّذِینَ نَافَقُوا۟ یَقُولُونَ لِإِخۡوَ ٰ⁠نِهِمُ ٱلَّذِینَ كَفَرُوا۟ مِنۡ أَهۡلِ ٱلۡكِتَـٰبِ لَىِٕنۡ أُخۡرِجۡتُمۡ لَنَخۡرُجَنَّ مَعَكُمۡ وَلَا نُطِیعُ فِیكُمۡ أَحَدًا أَبَدࣰا وَإِن قُوتِلۡتُمۡ لَنَنصُرَنَّكُمۡ وَٱللَّهُ یَشۡهَدُ إِنَّهُمۡ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:09:00'),
(1183, 59, 5138, 12, 'لَىِٕنۡ أُخۡرِجُوا۟ لَا یَخۡرُجُونَ مَعَهُمۡ وَلَىِٕن قُوتِلُوا۟ لَا یَنصُرُونَهُمۡ وَلَىِٕن نَّصَرُوهُمۡ لَیُوَلُّنَّ ٱلۡأَدۡبَـٰرَ ثُمَّ لَا یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:21:42', '2023-05-27 11:09:00'),
(1184, 59, 5139, 13, 'لَأَنتُمۡ أَشَدُّ رَهۡبَةࣰ فِی صُدُورِهِم مِّنَ ٱللَّهِۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَوۡمࣱ لَّا یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1185, 59, 5140, 14, 'لَا یُقَـٰتِلُونَكُمۡ جَمِیعًا إِلَّا فِی قُرࣰى مُّحَصَّنَةٍ أَوۡ مِن وَرَاۤءِ جُدُرِۭۚ بَأۡسُهُم بَیۡنَهُمۡ شَدِیدࣱۚ تَحۡسَبُهُمۡ جَمِیعࣰا وَقُلُوبُهُمۡ شَتَّىٰۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَوۡمࣱ لَّا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1186, 59, 5141, 15, 'كَمَثَلِ ٱلَّذِینَ مِن قَبۡلِهِمۡ قَرِیبࣰاۖ ذَاقُوا۟ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1187, 59, 5142, 16, 'كَمَثَلِ ٱلشَّیۡطَـٰنِ إِذۡ قَالَ لِلۡإِنسَـٰنِ ٱكۡفُرۡ فَلَمَّا كَفَرَ قَالَ إِنِّی بَرِیۤءࣱ مِّنكَ إِنِّیۤ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1188, 59, 5143, 17, 'فَكَانَ عَـٰقِبَتَهُمَاۤ أَنَّهُمَا فِی ٱلنَّارِ خَـٰلِدَیۡنِ فِیهَاۚ وَذَ ٰ⁠لِكَ جَزَ ٰ⁠ۤؤُا۟ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1189, 59, 5144, 18, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَلۡتَنظُرۡ نَفۡسࣱ مَّا قَدَّمَتۡ لِغَدࣲۖ وَٱتَّقُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1190, 59, 5145, 19, 'وَلَا تَكُونُوا۟ كَٱلَّذِینَ نَسُوا۟ ٱللَّهَ فَأَنسَىٰهُمۡ أَنفُسَهُمۡۚ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1191, 59, 5146, 20, 'لَا یَسۡتَوِیۤ أَصۡحَـٰبُ ٱلنَّارِ وَأَصۡحَـٰبُ ٱلۡجَنَّةِۚ أَصۡحَـٰبُ ٱلۡجَنَّةِ هُمُ ٱلۡفَاۤىِٕزُونَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1192, 59, 5147, 21, 'لَوۡ أَنزَلۡنَا هَـٰذَا ٱلۡقُرۡءَانَ عَلَىٰ جَبَلࣲ لَّرَأَیۡتَهُۥ خَـٰشِعࣰا مُّتَصَدِّعࣰا مِّنۡ خَشۡیَةِ ٱللَّهِۚ وَتِلۡكَ ٱلۡأَمۡثَـٰلُ نَضۡرِبُهَا لِلنَّاسِ لَعَلَّهُمۡ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1193, 59, 5148, 22, 'هُوَ ٱللَّهُ ٱلَّذِی لَاۤ إِلَـٰهَ إِلَّا هُوَۖ عَـٰلِمُ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِۖ هُوَ ٱلرَّحۡمَـٰنُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1194, 59, 5149, 23, 'هُوَ ٱللَّهُ ٱلَّذِی لَاۤ إِلَـٰهَ إِلَّا هُوَ ٱلۡمَلِكُ ٱلۡقُدُّوسُ ٱلسَّلَـٰمُ ٱلۡمُؤۡمِنُ ٱلۡمُهَیۡمِنُ ٱلۡعَزِیزُ ٱلۡجَبَّارُ ٱلۡمُتَكَبِّرُۚ سُبۡحَـٰنَ ٱللَّهِ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1195, 59, 5150, 24, 'هُوَ ٱللَّهُ ٱلۡخَـٰلِقُ ٱلۡبَارِئُ ٱلۡمُصَوِّرُۖ لَهُ ٱلۡأَسۡمَاۤءُ ٱلۡحُسۡنَىٰۚ یُسَبِّحُ لَهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:21:43', '2023-05-27 11:09:00'),
(1196, 58, 5105, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّتِی تُجَـٰدِلُكَ فِی زَوۡجِهَا وَتَشۡتَكِیۤ إِلَى ٱللَّهِ وَٱللَّهُ یَسۡمَعُ تَحَاوُرَكُمَاۤۚ إِنَّ ٱللَّهَ سَمِیعُۢ بَصِیرٌ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1197, 58, 5106, 2, 'ٱلَّذِینَ یُظَـٰهِرُونَ مِنكُم مِّن نِّسَاۤىِٕهِم مَّا هُنَّ أُمَّهَـٰتِهِمۡۖ إِنۡ أُمَّهَـٰتُهُمۡ إِلَّا ٱلَّـٰۤـِٔی وَلَدۡنَهُمۡۚ وَإِنَّهُمۡ لَیَقُولُونَ مُنكَرࣰا مِّنَ ٱلۡقَوۡلِ وَزُورࣰاۚ وَإِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورࣱ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1198, 58, 5107, 3, 'وَٱلَّذِینَ یُظَـٰهِرُونَ مِن نِّسَاۤىِٕهِمۡ ثُمَّ یَعُودُونَ لِمَا قَالُوا۟ فَتَحۡرِیرُ رَقَبَةࣲ مِّن قَبۡلِ أَن یَتَمَاۤسَّاۚ ذَ ٰ⁠لِكُمۡ تُوعَظُونَ بِهِۦۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1199, 58, 5108, 4, 'فَمَن لَّمۡ یَجِدۡ فَصِیَامُ شَهۡرَیۡنِ مُتَتَابِعَیۡنِ مِن قَبۡلِ أَن یَتَمَاۤسَّاۖ فَمَن لَّمۡ یَسۡتَطِعۡ فَإِطۡعَامُ سِتِّینَ مِسۡكِینࣰاۚ ذَ ٰ⁠لِكَ لِتُؤۡمِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦۚ وَتِلۡكَ حُدُودُ ٱللَّهِۗ وَلِلۡكَـٰفِرِینَ عَذَابٌ أَلِیمٌ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1200, 58, 5109, 5, 'إِنَّ ٱلَّذِینَ یُحَاۤدُّونَ ٱللَّهَ وَرَسُولَهُۥ كُبِتُوا۟ كَمَا كُبِتَ ٱلَّذِینَ مِن قَبۡلِهِمۡۚ وَقَدۡ أَنزَلۡنَاۤ ءَایَـٰتِۭ بَیِّنَـٰتࣲۚ وَلِلۡكَـٰفِرِینَ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1201, 58, 5110, 6, 'یَوۡمَ یَبۡعَثُهُمُ ٱللَّهُ جَمِیعࣰا فَیُنَبِّئُهُم بِمَا عَمِلُوۤا۟ۚ أَحۡصَىٰهُ ٱللَّهُ وَنَسُوهُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ شَهِیدٌ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1202, 58, 5111, 7, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ یَعۡلَمُ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۖ مَا یَكُونُ مِن نَّجۡوَىٰ ثَلَـٰثَةٍ إِلَّا هُوَ رَابِعُهُمۡ وَلَا خَمۡسَةٍ إِلَّا هُوَ سَادِسُهُمۡ وَلَاۤ أَدۡنَىٰ مِن ذَ ٰ⁠لِكَ وَلَاۤ أَكۡثَرَ إِلَّا هُوَ مَعَهُمۡ أَیۡنَ مَا كَانُوا۟ۖ ثُمَّ یُنَبِّئُهُم بِمَا عَمِلُوا۟ یَوۡمَ ٱلۡقِیَـٰمَةِۚ إِنَّ ٱللَّهَ بِكُلِّ شَیۡءٍ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1203, 58, 5112, 8, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ نُهُوا۟ عَنِ ٱلنَّجۡوَىٰ ثُمَّ یَعُودُونَ لِمَا نُهُوا۟ عَنۡهُ وَیَتَنَـٰجَوۡنَ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَ ٰ⁠نِ وَمَعۡصِیَتِ ٱلرَّسُولِۖ وَإِذَا جَاۤءُوكَ حَیَّوۡكَ بِمَا لَمۡ یُحَیِّكَ بِهِ ٱللَّهُ وَیَقُولُونَ فِیۤ أَنفُسِهِمۡ لَوۡلَا یُعَذِّبُنَا ٱللَّهُ بِمَا نَقُولُۚ حَسۡبُهُمۡ جَهَنَّمُ یَصۡلَوۡنَهَاۖ فَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:21:45', '2023-05-27 11:09:01'),
(1204, 58, 5113, 9, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا تَنَـٰجَیۡتُمۡ فَلَا تَتَنَـٰجَوۡا۟ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَ ٰ⁠نِ وَمَعۡصِیَتِ ٱلرَّسُولِ وَتَنَـٰجَوۡا۟ بِٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِیۤ إِلَیۡهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:01'),
(1205, 58, 5114, 10, 'إِنَّمَا ٱلنَّجۡوَىٰ مِنَ ٱلشَّیۡطَـٰنِ لِیَحۡزُنَ ٱلَّذِینَ ءَامَنُوا۟ وَلَیۡسَ بِضَاۤرِّهِمۡ شَیۡـًٔا إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:01'),
(1206, 58, 5115, 11, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا قِیلَ لَكُمۡ تَفَسَّحُوا۟ فِی ٱلۡمَجَـٰلِسِ فَٱفۡسَحُوا۟ یَفۡسَحِ ٱللَّهُ لَكُمۡۖ وَإِذَا قِیلَ ٱنشُزُوا۟ فَٱنشُزُوا۟ یَرۡفَعِ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ مِنكُمۡ وَٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ دَرَجَـٰتࣲۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1207, 58, 5116, 12, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا نَـٰجَیۡتُمُ ٱلرَّسُولَ فَقَدِّمُوا۟ بَیۡنَ یَدَیۡ نَجۡوَىٰكُمۡ صَدَقَةࣰۚ ذَ ٰ⁠لِكَ خَیۡرࣱ لَّكُمۡ وَأَطۡهَرُۚ فَإِن لَّمۡ تَجِدُوا۟ فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1208, 58, 5117, 13, 'ءَأَشۡفَقۡتُمۡ أَن تُقَدِّمُوا۟ بَیۡنَ یَدَیۡ نَجۡوَىٰكُمۡ صَدَقَـٰتࣲۚ فَإِذۡ لَمۡ تَفۡعَلُوا۟ وَتَابَ ٱللَّهُ عَلَیۡكُمۡ فَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَطِیعُوا۟ ٱللَّهَ وَرَسُولَهُۥۚ وَٱللَّهُ خَبِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1209, 58, 5118, 14, '۞ أَلَمۡ تَرَ إِلَى ٱلَّذِینَ تَوَلَّوۡا۟ قَوۡمًا غَضِبَ ٱللَّهُ عَلَیۡهِم مَّا هُم مِّنكُمۡ وَلَا مِنۡهُمۡ وَیَحۡلِفُونَ عَلَى ٱلۡكَذِبِ وَهُمۡ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1210, 58, 5119, 15, 'أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابࣰا شَدِیدًاۖ إِنَّهُمۡ سَاۤءَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1211, 58, 5120, 16, 'ٱتَّخَذُوۤا۟ أَیۡمَـٰنَهُمۡ جُنَّةࣰ فَصَدُّوا۟ عَن سَبِیلِ ٱللَّهِ فَلَهُمۡ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1212, 58, 5121, 17, 'لَّن تُغۡنِیَ عَنۡهُمۡ أَمۡوَ ٰ⁠لُهُمۡ وَلَاۤ أَوۡلَـٰدُهُم مِّنَ ٱللَّهِ شَیۡـًٔاۚ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1213, 58, 5122, 18, 'یَوۡمَ یَبۡعَثُهُمُ ٱللَّهُ جَمِیعࣰا فَیَحۡلِفُونَ لَهُۥ كَمَا یَحۡلِفُونَ لَكُمۡ وَیَحۡسَبُونَ أَنَّهُمۡ عَلَىٰ شَیۡءٍۚ أَلَاۤ إِنَّهُمۡ هُمُ ٱلۡكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1214, 58, 5123, 19, 'ٱسۡتَحۡوَذَ عَلَیۡهِمُ ٱلشَّیۡطَـٰنُ فَأَنسَىٰهُمۡ ذِكۡرَ ٱللَّهِۚ أُو۟لَـٰۤىِٕكَ حِزۡبُ ٱلشَّیۡطَـٰنِۚ أَلَاۤ إِنَّ حِزۡبَ ٱلشَّیۡطَـٰنِ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1215, 58, 5124, 20, 'إِنَّ ٱلَّذِینَ یُحَاۤدُّونَ ٱللَّهَ وَرَسُولَهُۥۤ أُو۟لَـٰۤىِٕكَ فِی ٱلۡأَذَلِّینَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1216, 58, 5125, 21, 'كَتَبَ ٱللَّهُ لَأَغۡلِبَنَّ أَنَا۠ وَرُسُلِیۤۚ إِنَّ ٱللَّهَ قَوِیٌّ عَزِیزࣱ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1217, 58, 5126, 22, 'لَّا تَجِدُ قَوۡمࣰا یُؤۡمِنُونَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ یُوَاۤدُّونَ مَنۡ حَاۤدَّ ٱللَّهَ وَرَسُولَهُۥ وَلَوۡ كَانُوۤا۟ ءَابَاۤءَهُمۡ أَوۡ أَبۡنَاۤءَهُمۡ أَوۡ إِخۡوَ ٰ⁠نَهُمۡ أَوۡ عَشِیرَتَهُمۡۚ أُو۟لَـٰۤىِٕكَ كَتَبَ فِی قُلُوبِهِمُ ٱلۡإِیمَـٰنَ وَأَیَّدَهُم بِرُوحࣲ مِّنۡهُۖ وَیُدۡخِلُهُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ رَضِیَ ٱللَّهُ عَنۡهُمۡ وَرَضُوا۟ عَنۡهُۚ أُو۟لَـٰۤىِٕكَ حِزۡبُ ٱللَّهِۚ أَلَاۤ إِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:21:46', '2023-05-27 11:09:02'),
(1218, 57, 5076, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ سَبَّحَ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:21:48', '2023-05-27 11:09:03'),
(1219, 57, 5077, 2, 'لَهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ یُحۡیِۦ وَیُمِیتُۖ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:21:48', '2023-05-27 11:09:03'),
(1220, 57, 5078, 3, 'هُوَ ٱلۡأَوَّلُ وَٱلۡـَٔاخِرُ وَٱلظَّـٰهِرُ وَٱلۡبَاطِنُۖ وَهُوَ بِكُلِّ شَیۡءٍ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:21:48', '2023-05-27 11:09:03'),
(1221, 57, 5079, 4, 'هُوَ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ فِی سِتَّةِ أَیَّامࣲ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ یَعۡلَمُ مَا یَلِجُ فِی ٱلۡأَرۡضِ وَمَا یَخۡرُجُ مِنۡهَا وَمَا یَنزِلُ مِنَ ٱلسَّمَاۤءِ وَمَا یَعۡرُجُ فِیهَاۖ وَهُوَ مَعَكُمۡ أَیۡنَ مَا كُنتُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:21:48', '2023-05-27 11:09:03'),
(1222, 57, 5080, 5, 'لَّهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:03'),
(1223, 57, 5081, 6, 'یُولِجُ ٱلَّیۡلَ فِی ٱلنَّهَارِ وَیُولِجُ ٱلنَّهَارَ فِی ٱلَّیۡلِۚ وَهُوَ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:03'),
(1224, 57, 5082, 7, 'ءَامِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَأَنفِقُوا۟ مِمَّا جَعَلَكُم مُّسۡتَخۡلَفِینَ فِیهِۖ فَٱلَّذِینَ ءَامَنُوا۟ مِنكُمۡ وَأَنفَقُوا۟ لَهُمۡ أَجۡرࣱ كَبِیرࣱ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:03'),
(1225, 57, 5083, 8, 'وَمَا لَكُمۡ لَا تُؤۡمِنُونَ بِٱللَّهِ وَٱلرَّسُولُ یَدۡعُوكُمۡ لِتُؤۡمِنُوا۟ بِرَبِّكُمۡ وَقَدۡ أَخَذَ مِیثَـٰقَكُمۡ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:03'),
(1226, 57, 5084, 9, 'هُوَ ٱلَّذِی یُنَزِّلُ عَلَىٰ عَبۡدِهِۦۤ ءَایَـٰتِۭ بَیِّنَـٰتࣲ لِّیُخۡرِجَكُم مِّنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِۚ وَإِنَّ ٱللَّهَ بِكُمۡ لَرَءُوفࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:03'),
(1227, 57, 5085, 10, 'وَمَا لَكُمۡ أَلَّا تُنفِقُوا۟ فِی سَبِیلِ ٱللَّهِ وَلِلَّهِ مِیرَ ٰ⁠ثُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ لَا یَسۡتَوِی مِنكُم مَّنۡ أَنفَقَ مِن قَبۡلِ ٱلۡفَتۡحِ وَقَـٰتَلَۚ أُو۟لَـٰۤىِٕكَ أَعۡظَمُ دَرَجَةࣰ مِّنَ ٱلَّذِینَ أَنفَقُوا۟ مِنۢ بَعۡدُ وَقَـٰتَلُوا۟ۚ وَكُلࣰّا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1228, 57, 5086, 11, 'مَّن ذَا ٱلَّذِی یُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنࣰا فَیُضَـٰعِفَهُۥ لَهُۥ وَلَهُۥۤ أَجۡرࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1229, 57, 5087, 12, 'یَوۡمَ تَرَى ٱلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِ یَسۡعَىٰ نُورُهُم بَیۡنَ أَیۡدِیهِمۡ وَبِأَیۡمَـٰنِهِمۖ بُشۡرَىٰكُمُ ٱلۡیَوۡمَ جَنَّـٰتࣱ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ ذَ ٰ⁠لِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1230, 57, 5088, 13, 'یَوۡمَ یَقُولُ ٱلۡمُنَـٰفِقُونَ وَٱلۡمُنَـٰفِقَـٰتُ لِلَّذِینَ ءَامَنُوا۟ ٱنظُرُونَا نَقۡتَبِسۡ مِن نُّورِكُمۡ قِیلَ ٱرۡجِعُوا۟ وَرَاۤءَكُمۡ فَٱلۡتَمِسُوا۟ نُورࣰاۖ فَضُرِبَ بَیۡنَهُم بِسُورࣲ لَّهُۥ بَابُۢ بَاطِنُهُۥ فِیهِ ٱلرَّحۡمَةُ وَظَـٰهِرُهُۥ مِن قِبَلِهِ ٱلۡعَذَابُ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1231, 57, 5089, 14, 'یُنَادُونَهُمۡ أَلَمۡ نَكُن مَّعَكُمۡۖ قَالُوا۟ بَلَىٰ وَلَـٰكِنَّكُمۡ فَتَنتُمۡ أَنفُسَكُمۡ وَتَرَبَّصۡتُمۡ وَٱرۡتَبۡتُمۡ وَغَرَّتۡكُمُ ٱلۡأَمَانِیُّ حَتَّىٰ جَاۤءَ أَمۡرُ ٱللَّهِ وَغَرَّكُم بِٱللَّهِ ٱلۡغَرُورُ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1232, 57, 5090, 15, 'فَٱلۡیَوۡمَ لَا یُؤۡخَذُ مِنكُمۡ فِدۡیَةࣱ وَلَا مِنَ ٱلَّذِینَ كَفَرُوا۟ۚ مَأۡوَىٰكُمُ ٱلنَّارُۖ هِیَ مَوۡلَىٰكُمۡۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1233, 57, 5091, 16, '۞ أَلَمۡ یَأۡنِ لِلَّذِینَ ءَامَنُوۤا۟ أَن تَخۡشَعَ قُلُوبُهُمۡ لِذِكۡرِ ٱللَّهِ وَمَا نَزَلَ مِنَ ٱلۡحَقِّ وَلَا یَكُونُوا۟ كَٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ مِن قَبۡلُ فَطَالَ عَلَیۡهِمُ ٱلۡأَمَدُ فَقَسَتۡ قُلُوبُهُمۡۖ وَكَثِیرࣱ مِّنۡهُمۡ فَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1234, 57, 5092, 17, 'ٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ یُحۡیِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ قَدۡ بَیَّنَّا لَكُمُ ٱلۡـَٔایَـٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1235, 57, 5093, 18, 'إِنَّ ٱلۡمُصَّدِّقِینَ وَٱلۡمُصَّدِّقَـٰتِ وَأَقۡرَضُوا۟ ٱللَّهَ قَرۡضًا حَسَنࣰا یُضَـٰعَفُ لَهُمۡ وَلَهُمۡ أَجۡرࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1236, 57, 5094, 19, 'وَٱلَّذِینَ ءَامَنُوا۟ بِٱللَّهِ وَرُسُلِهِۦۤ أُو۟لَـٰۤىِٕكَ هُمُ ٱلصِّدِّیقُونَۖ وَٱلشُّهَدَاۤءُ عِندَ رَبِّهِمۡ لَهُمۡ أَجۡرُهُمۡ وَنُورُهُمۡۖ وَٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔایَـٰتِنَاۤ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1237, 57, 5095, 20, 'ٱعۡلَمُوۤا۟ أَنَّمَا ٱلۡحَیَوٰةُ ٱلدُّنۡیَا لَعِبࣱ وَلَهۡوࣱ وَزِینَةࣱ وَتَفَاخُرُۢ بَیۡنَكُمۡ وَتَكَاثُرࣱ فِی ٱلۡأَمۡوَ ٰ⁠لِ وَٱلۡأَوۡلَـٰدِۖ كَمَثَلِ غَیۡثٍ أَعۡجَبَ ٱلۡكُفَّارَ نَبَاتُهُۥ ثُمَّ یَهِیجُ فَتَرَىٰهُ مُصۡفَرࣰّا ثُمَّ یَكُونُ حُطَـٰمࣰاۖ وَفِی ٱلۡـَٔاخِرَةِ عَذَابࣱ شَدِیدࣱ وَمَغۡفِرَةࣱ مِّنَ ٱللَّهِ وَرِضۡوَ ٰ⁠نࣱۚ وَمَا ٱلۡحَیَوٰةُ ٱلدُّنۡیَاۤ إِلَّا مَتَـٰعُ ٱلۡغُرُورِ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1238, 57, 5096, 21, 'سَابِقُوۤا۟ إِلَىٰ مَغۡفِرَةࣲ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا كَعَرۡضِ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِ أُعِدَّتۡ لِلَّذِینَ ءَامَنُوا۟ بِٱللَّهِ وَرُسُلِهِۦۚ ذَ ٰ⁠لِكَ فَضۡلُ ٱللَّهِ یُؤۡتِیهِ مَن یَشَاۤءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1239, 57, 5097, 22, 'مَاۤ أَصَابَ مِن مُّصِیبَةࣲ فِی ٱلۡأَرۡضِ وَلَا فِیۤ أَنفُسِكُمۡ إِلَّا فِی كِتَـٰبࣲ مِّن قَبۡلِ أَن نَّبۡرَأَهَاۤۚ إِنَّ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣱ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1240, 57, 5098, 23, 'لِّكَیۡلَا تَأۡسَوۡا۟ عَلَىٰ مَا فَاتَكُمۡ وَلَا تَفۡرَحُوا۟ بِمَاۤ ءَاتَىٰكُمۡۗ وَٱللَّهُ لَا یُحِبُّ كُلَّ مُخۡتَالࣲ فَخُورٍ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1241, 57, 5099, 24, 'ٱلَّذِینَ یَبۡخَلُونَ وَیَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِۗ وَمَن یَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِیُّ ٱلۡحَمِیدُ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1242, 57, 5100, 25, 'لَقَدۡ أَرۡسَلۡنَا رُسُلَنَا بِٱلۡبَیِّنَـٰتِ وَأَنزَلۡنَا مَعَهُمُ ٱلۡكِتَـٰبَ وَٱلۡمِیزَانَ لِیَقُومَ ٱلنَّاسُ بِٱلۡقِسۡطِۖ وَأَنزَلۡنَا ٱلۡحَدِیدَ فِیهِ بَأۡسࣱ شَدِیدࣱ وَمَنَـٰفِعُ لِلنَّاسِ وَلِیَعۡلَمَ ٱللَّهُ مَن یَنصُرُهُۥ وَرُسُلَهُۥ بِٱلۡغَیۡبِۚ إِنَّ ٱللَّهَ قَوِیٌّ عَزِیزࣱ\n', NULL, 0, '2023-05-26 17:21:49', '2023-05-27 11:09:04'),
(1243, 57, 5101, 26, 'وَلَقَدۡ أَرۡسَلۡنَا نُوحࣰا وَإِبۡرَ ٰ⁠هِیمَ وَجَعَلۡنَا فِی ذُرِّیَّتِهِمَا ٱلنُّبُوَّةَ وَٱلۡكِتَـٰبَۖ فَمِنۡهُم مُّهۡتَدࣲۖ وَكَثِیرࣱ مِّنۡهُمۡ فَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:21:50', '2023-05-27 11:09:04'),
(1244, 57, 5102, 27, 'ثُمَّ قَفَّیۡنَا عَلَىٰۤ ءَاثَـٰرِهِم بِرُسُلِنَا وَقَفَّیۡنَا بِعِیسَى ٱبۡنِ مَرۡیَمَ وَءَاتَیۡنَـٰهُ ٱلۡإِنجِیلَۖ وَجَعَلۡنَا فِی قُلُوبِ ٱلَّذِینَ ٱتَّبَعُوهُ رَأۡفَةࣰ وَرَحۡمَةࣰۚ وَرَهۡبَانِیَّةً ٱبۡتَدَعُوهَا مَا كَتَبۡنَـٰهَا عَلَیۡهِمۡ إِلَّا ٱبۡتِغَاۤءَ رِضۡوَ ٰ⁠نِ ٱللَّهِ فَمَا رَعَوۡهَا حَقَّ رِعَایَتِهَاۖ فَـَٔاتَیۡنَا ٱلَّذِینَ ءَامَنُوا۟ مِنۡهُمۡ أَجۡرَهُمۡۖ وَكَثِیرࣱ مِّنۡهُمۡ فَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:21:50', '2023-05-27 11:09:04'),
(1245, 57, 5103, 28, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَءَامِنُوا۟ بِرَسُولِهِۦ یُؤۡتِكُمۡ كِفۡلَیۡنِ مِن رَّحۡمَتِهِۦ وَیَجۡعَل لَّكُمۡ نُورࣰا تَمۡشُونَ بِهِۦ وَیَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:21:50', '2023-05-27 11:09:04'),
(1246, 57, 5104, 29, 'لِّئَلَّا یَعۡلَمَ أَهۡلُ ٱلۡكِتَـٰبِ أَلَّا یَقۡدِرُونَ عَلَىٰ شَیۡءࣲ مِّن فَضۡلِ ٱللَّهِ وَأَنَّ ٱلۡفَضۡلَ بِیَدِ ٱللَّهِ یُؤۡتِیهِ مَن یَشَاۤءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:21:50', '2023-05-27 11:09:05'),
(1247, 56, 4980, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِذَا وَقَعَتِ ٱلۡوَاقِعَةُ\n', NULL, 0, '2023-05-26 17:21:53', '2023-05-27 11:09:05'),
(1248, 56, 4981, 2, 'لَیۡسَ لِوَقۡعَتِهَا كَاذِبَةٌ\n', NULL, 0, '2023-05-26 17:21:53', '2023-05-27 11:09:05'),
(1249, 56, 4982, 3, 'خَافِضَةࣱ رَّافِعَةٌ\n', NULL, 0, '2023-05-26 17:21:53', '2023-05-27 11:09:05'),
