(1413, 55, 4972, 71, 'فَبِأَیِّ ءَالَاۤءِ رَبِّكُمَا تُكَذِّبَانِ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1414, 55, 4973, 72, 'حُورࣱ مَّقۡصُورَ ٰ⁠تࣱ فِی ٱلۡخِیَامِ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1415, 55, 4974, 73, 'فَبِأَیِّ ءَالَاۤءِ رَبِّكُمَا تُكَذِّبَانِ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1416, 55, 4975, 74, 'لَمۡ یَطۡمِثۡهُنَّ إِنسࣱ قَبۡلَهُمۡ وَلَا جَاۤنࣱّ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1417, 55, 4976, 75, 'فَبِأَیِّ ءَالَاۤءِ رَبِّكُمَا تُكَذِّبَانِ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1418, 55, 4977, 76, 'مُتَّكِـِٔینَ عَلَىٰ رَفۡرَفٍ خُضۡرࣲ وَعَبۡقَرِیٍّ حِسَانࣲ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1419, 55, 4978, 77, 'فَبِأَیِّ ءَالَاۤءِ رَبِّكُمَا تُكَذِّبَانِ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1420, 55, 4979, 78, 'تَبَـٰرَكَ ٱسۡمُ رَبِّكَ ذِی ٱلۡجَلَـٰلِ وَٱلۡإِكۡرَامِ\n', NULL, 0, '2023-05-26 17:22:05', '2023-05-27 11:09:17'),
(1421, 54, 4847, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱقۡتَرَبَتِ ٱلسَّاعَةُ وَٱنشَقَّ ٱلۡقَمَرُ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:23'),
(1422, 54, 4848, 2, 'وَإِن یَرَوۡا۟ ءَایَةࣰ یُعۡرِضُوا۟ وَیَقُولُوا۟ سِحۡرࣱ مُّسۡتَمِرࣱّ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:23'),
(1423, 54, 4849, 3, 'وَكَذَّبُوا۟ وَٱتَّبَعُوۤا۟ أَهۡوَاۤءَهُمۡۚ وَكُلُّ أَمۡرࣲ مُّسۡتَقِرࣱّ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:23'),
(1424, 54, 4850, 4, 'وَلَقَدۡ جَاۤءَهُم مِّنَ ٱلۡأَنۢبَاۤءِ مَا فِیهِ مُزۡدَجَرٌ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:23'),
(1425, 54, 4851, 5, 'حِكۡمَةُۢ بَـٰلِغَةࣱۖ فَمَا تُغۡنِ ٱلنُّذُرُ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1426, 54, 4852, 6, 'فَتَوَلَّ عَنۡهُمۡۘ یَوۡمَ یَدۡعُ ٱلدَّاعِ إِلَىٰ شَیۡءࣲ نُّكُرٍ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1427, 54, 4853, 7, 'خُشَّعًا أَبۡصَـٰرُهُمۡ یَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ كَأَنَّهُمۡ جَرَادࣱ مُّنتَشِرࣱ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1428, 54, 4854, 8, 'مُّهۡطِعِینَ إِلَى ٱلدَّاعِۖ یَقُولُ ٱلۡكَـٰفِرُونَ هَـٰذَا یَوۡمٌ عَسِرࣱ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1429, 54, 4855, 9, '۞ كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحࣲ فَكَذَّبُوا۟ عَبۡدَنَا وَقَالُوا۟ مَجۡنُونࣱ وَٱزۡدُجِرَ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1430, 54, 4856, 10, 'فَدَعَا رَبَّهُۥۤ أَنِّی مَغۡلُوبࣱ فَٱنتَصِرۡ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1431, 54, 4857, 11, 'فَفَتَحۡنَاۤ أَبۡوَ ٰ⁠بَ ٱلسَّمَاۤءِ بِمَاۤءࣲ مُّنۡهَمِرࣲ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1432, 54, 4858, 12, 'وَفَجَّرۡنَا ٱلۡأَرۡضَ عُیُونࣰا فَٱلۡتَقَى ٱلۡمَاۤءُ عَلَىٰۤ أَمۡرࣲ قَدۡ قُدِرَ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1433, 54, 4859, 13, 'وَحَمَلۡنَـٰهُ عَلَىٰ ذَاتِ أَلۡوَ ٰ⁠حࣲ وَدُسُرࣲ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1434, 54, 4860, 14, 'تَجۡرِی بِأَعۡیُنِنَا جَزَاۤءࣰ لِّمَن كَانَ كُفِرَ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1435, 54, 4861, 15, 'وَلَقَد تَّرَكۡنَـٰهَاۤ ءَایَةࣰ فَهَلۡ مِن مُّدَّكِرࣲ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1436, 54, 4862, 16, 'فَكَیۡفَ كَانَ عَذَابِی وَنُذُرِ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1437, 54, 4863, 17, 'وَلَقَدۡ یَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرࣲ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1438, 54, 4864, 18, 'كَذَّبَتۡ عَادࣱ فَكَیۡفَ كَانَ عَذَابِی وَنُذُرِ\n', NULL, 0, '2023-05-26 17:22:19', '2023-05-27 11:09:24'),
(1439, 54, 4865, 19, 'إِنَّاۤ أَرۡسَلۡنَا عَلَیۡهِمۡ رِیحࣰا صَرۡصَرࣰا فِی یَوۡمِ نَحۡسࣲ مُّسۡتَمِرࣲّ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:24'),
(1440, 54, 4866, 20, 'تَنزِعُ ٱلنَّاسَ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلࣲ مُّنقَعِرࣲ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:24'),
(1441, 54, 4867, 21, 'فَكَیۡفَ كَانَ عَذَابِی وَنُذُرِ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:24'),
(1442, 54, 4868, 22, 'وَلَقَدۡ یَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرࣲ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:24'),
(1443, 54, 4869, 23, 'كَذَّبَتۡ ثَمُودُ بِٱلنُّذُرِ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:24'),
(1444, 54, 4870, 24, 'فَقَالُوۤا۟ أَبَشَرࣰا مِّنَّا وَ ٰ⁠حِدࣰا نَّتَّبِعُهُۥۤ إِنَّاۤ إِذࣰا لَّفِی ضَلَـٰلࣲ وَسُعُرٍ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:24'),
(1445, 54, 4871, 25, 'أَءُلۡقِیَ ٱلذِّكۡرُ عَلَیۡهِ مِنۢ بَیۡنِنَا بَلۡ هُوَ كَذَّابٌ أَشِرࣱ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:24'),
(1446, 54, 4872, 26, 'سَیَعۡلَمُونَ غَدࣰا مَّنِ ٱلۡكَذَّابُ ٱلۡأَشِرُ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1447, 54, 4873, 27, 'إِنَّا مُرۡسِلُوا۟ ٱلنَّاقَةِ فِتۡنَةࣰ لَّهُمۡ فَٱرۡتَقِبۡهُمۡ وَٱصۡطَبِرۡ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1448, 54, 4874, 28, 'وَنَبِّئۡهُمۡ أَنَّ ٱلۡمَاۤءَ قِسۡمَةُۢ بَیۡنَهُمۡۖ كُلُّ شِرۡبࣲ مُّحۡتَضَرࣱ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1449, 54, 4875, 29, 'فَنَادَوۡا۟ صَاحِبَهُمۡ فَتَعَاطَىٰ فَعَقَرَ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1450, 54, 4876, 30, 'فَكَیۡفَ كَانَ عَذَابِی وَنُذُرِ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1451, 54, 4877, 31, 'إِنَّاۤ أَرۡسَلۡنَا عَلَیۡهِمۡ صَیۡحَةࣰ وَ ٰ⁠حِدَةࣰ فَكَانُوا۟ كَهَشِیمِ ٱلۡمُحۡتَظِرِ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1452, 54, 4878, 32, 'وَلَقَدۡ یَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرࣲ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1453, 54, 4879, 33, 'كَذَّبَتۡ قَوۡمُ لُوطِۭ بِٱلنُّذُرِ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1454, 54, 4880, 34, 'إِنَّاۤ أَرۡسَلۡنَا عَلَیۡهِمۡ حَاصِبًا إِلَّاۤ ءَالَ لُوطࣲۖ نَّجَّیۡنَـٰهُم بِسَحَرࣲ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1455, 54, 4881, 35, 'نِّعۡمَةࣰ مِّنۡ عِندِنَاۚ كَذَ ٰ⁠لِكَ نَجۡزِی مَن شَكَرَ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1456, 54, 4882, 36, 'وَلَقَدۡ أَنذَرَهُم بَطۡشَتَنَا فَتَمَارَوۡا۟ بِٱلنُّذُرِ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1457, 54, 4883, 37, 'وَلَقَدۡ رَ ٰ⁠وَدُوهُ عَن ضَیۡفِهِۦ فَطَمَسۡنَاۤ أَعۡیُنَهُمۡ فَذُوقُوا۟ عَذَابِی وَنُذُرِ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1458, 54, 4884, 38, 'وَلَقَدۡ صَبَّحَهُم بُكۡرَةً عَذَابࣱ مُّسۡتَقِرࣱّ\n', NULL, 0, '2023-05-26 17:22:20', '2023-05-27 11:09:25'),
(1459, 54, 4885, 39, 'فَذُوقُوا۟ عَذَابِی وَنُذُرِ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:25'),
(1460, 54, 4886, 40, 'وَلَقَدۡ یَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرࣲ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:25'),
(1461, 54, 4887, 41, 'وَلَقَدۡ جَاۤءَ ءَالَ فِرۡعَوۡنَ ٱلنُّذُرُ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:25'),
(1462, 54, 4888, 42, 'كَذَّبُوا۟ بِـَٔایَـٰتِنَا كُلِّهَا فَأَخَذۡنَـٰهُمۡ أَخۡذَ عَزِیزࣲ مُّقۡتَدِرٍ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:25'),
(1463, 54, 4889, 43, 'أَكُفَّارُكُمۡ خَیۡرࣱ مِّنۡ أُو۟لَـٰۤىِٕكُمۡ أَمۡ لَكُم بَرَاۤءَةࣱ فِی ٱلزُّبُرِ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:25'),
(1464, 54, 4890, 44, 'أَمۡ یَقُولُونَ نَحۡنُ جَمِیعࣱ مُّنتَصِرࣱ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1465, 54, 4891, 45, 'سَیُهۡزَمُ ٱلۡجَمۡعُ وَیُوَلُّونَ ٱلدُّبُرَ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1466, 54, 4892, 46, 'بَلِ ٱلسَّاعَةُ مَوۡعِدُهُمۡ وَٱلسَّاعَةُ أَدۡهَىٰ وَأَمَرُّ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1467, 54, 4893, 47, 'إِنَّ ٱلۡمُجۡرِمِینَ فِی ضَلَـٰلࣲ وَسُعُرࣲ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1468, 54, 4894, 48, 'یَوۡمَ یُسۡحَبُونَ فِی ٱلنَّارِ عَلَىٰ وُجُوهِهِمۡ ذُوقُوا۟ مَسَّ سَقَرَ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1469, 54, 4895, 49, 'إِنَّا كُلَّ شَیۡءٍ خَلَقۡنَـٰهُ بِقَدَرࣲ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1470, 54, 4896, 50, 'وَمَاۤ أَمۡرُنَاۤ إِلَّا وَ ٰ⁠حِدَةࣱ كَلَمۡحِۭ بِٱلۡبَصَرِ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1471, 54, 4897, 51, 'وَلَقَدۡ أَهۡلَكۡنَاۤ أَشۡیَاعَكُمۡ فَهَلۡ مِن مُّدَّكِرࣲ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1472, 54, 4898, 52, 'وَكُلُّ شَیۡءࣲ فَعَلُوهُ فِی ٱلزُّبُرِ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1473, 54, 4899, 53, 'وَكُلُّ صَغِیرࣲ وَكَبِیرࣲ مُّسۡتَطَرٌ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1474, 54, 4900, 54, 'إِنَّ ٱلۡمُتَّقِینَ فِی جَنَّـٰتࣲ وَنَهَرࣲ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1475, 54, 4901, 55, 'فِی مَقۡعَدِ صِدۡقٍ عِندَ مَلِیكࣲ مُّقۡتَدِرِۭ\n', NULL, 0, '2023-05-26 17:22:21', '2023-05-27 11:09:26'),
(1476, 53, 4785, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَٱلنَّجۡمِ إِذَا هَوَىٰ\n', NULL, 0, '2023-05-26 17:22:26', '2023-05-27 11:09:27'),
(1477, 53, 4786, 2, 'مَا ضَلَّ صَاحِبُكُمۡ وَمَا غَوَىٰ\n', NULL, 0, '2023-05-26 17:22:26', '2023-05-27 11:09:27'),
(1478, 53, 4787, 3, 'وَمَا یَنطِقُ عَنِ ٱلۡهَوَىٰۤ\n', NULL, 0, '2023-05-26 17:22:26', '2023-05-27 11:09:27');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(1479, 53, 4788, 4, 'إِنۡ هُوَ إِلَّا وَحۡیࣱ یُوحَىٰ\n', NULL, 0, '2023-05-26 17:22:26', '2023-05-27 11:09:27'),
