(1556, 52, 4754, 19, 'كُلُوا۟ وَٱشۡرَبُوا۟ هَنِیۤـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1557, 52, 4755, 20, 'مُتَّكِـِٔینَ عَلَىٰ سُرُرࣲ مَّصۡفُوفَةࣲۖ وَزَوَّجۡنَـٰهُم بِحُورٍ عِینࣲ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1558, 52, 4756, 21, 'وَٱلَّذِینَ ءَامَنُوا۟ وَٱتَّبَعَتۡهُمۡ ذُرِّیَّتُهُم بِإِیمَـٰنٍ أَلۡحَقۡنَا بِهِمۡ ذُرِّیَّتَهُمۡ وَمَاۤ أَلَتۡنَـٰهُم مِّنۡ عَمَلِهِم مِّن شَیۡءࣲۚ كُلُّ ٱمۡرِىِٕۭ بِمَا كَسَبَ رَهِینࣱ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1559, 52, 4757, 22, 'وَأَمۡدَدۡنَـٰهُم بِفَـٰكِهَةࣲ وَلَحۡمࣲ مِّمَّا یَشۡتَهُونَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1560, 52, 4758, 23, 'یَتَنَـٰزَعُونَ فِیهَا كَأۡسࣰا لَّا لَغۡوࣱ فِیهَا وَلَا تَأۡثِیمࣱ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1561, 52, 4759, 24, '۞ وَیَطُوفُ عَلَیۡهِمۡ غِلۡمَانࣱ لَّهُمۡ كَأَنَّهُمۡ لُؤۡلُؤࣱ مَّكۡنُونࣱ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1562, 52, 4760, 25, 'وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضࣲ یَتَسَاۤءَلُونَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1563, 52, 4761, 26, 'قَالُوۤا۟ إِنَّا كُنَّا قَبۡلُ فِیۤ أَهۡلِنَا مُشۡفِقِینَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1564, 52, 4762, 27, 'فَمَنَّ ٱللَّهُ عَلَیۡنَا وَوَقَىٰنَا عَذَابَ ٱلسَّمُومِ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1565, 52, 4763, 28, 'إِنَّا كُنَّا مِن قَبۡلُ نَدۡعُوهُۖ إِنَّهُۥ هُوَ ٱلۡبَرُّ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1566, 52, 4764, 29, 'فَذَكِّرۡ فَمَاۤ أَنتَ بِنِعۡمَتِ رَبِّكَ بِكَاهِنࣲ وَلَا مَجۡنُونٍ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1567, 52, 4765, 30, 'أَمۡ یَقُولُونَ شَاعِرࣱ نَّتَرَبَّصُ بِهِۦ رَیۡبَ ٱلۡمَنُونِ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1568, 52, 4766, 31, 'قُلۡ تَرَبَّصُوا۟ فَإِنِّی مَعَكُم مِّنَ ٱلۡمُتَرَبِّصِینَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:32'),
(1569, 52, 4767, 32, 'أَمۡ تَأۡمُرُهُمۡ أَحۡلَـٰمُهُم بِهَـٰذَاۤۚ أَمۡ هُمۡ قَوۡمࣱ طَاغُونَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:33'),
(1570, 52, 4768, 33, 'أَمۡ یَقُولُونَ تَقَوَّلَهُۥۚ بَل لَّا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:33'),
(1571, 52, 4769, 34, 'فَلۡیَأۡتُوا۟ بِحَدِیثࣲ مِّثۡلِهِۦۤ إِن كَانُوا۟ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:33'),
(1572, 52, 4770, 35, 'أَمۡ خُلِقُوا۟ مِنۡ غَیۡرِ شَیۡءٍ أَمۡ هُمُ ٱلۡخَـٰلِقُونَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:33'),
(1573, 52, 4771, 36, 'أَمۡ خَلَقُوا۟ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَۚ بَل لَّا یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:22:32', '2023-05-27 11:09:33'),
(1574, 52, 4772, 37, 'أَمۡ عِندَهُمۡ خَزَاۤىِٕنُ رَبِّكَ أَمۡ هُمُ ٱلۡمُصَۣیۡطِرُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1575, 52, 4773, 38, 'أَمۡ لَهُمۡ سُلَّمࣱ یَسۡتَمِعُونَ فِیهِۖ فَلۡیَأۡتِ مُسۡتَمِعُهُم بِسُلۡطَـٰنࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1576, 52, 4774, 39, 'أَمۡ لَهُ ٱلۡبَنَـٰتُ وَلَكُمُ ٱلۡبَنُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1577, 52, 4775, 40, 'أَمۡ تَسۡـَٔلُهُمۡ أَجۡرࣰا فَهُم مِّن مَّغۡرَمࣲ مُّثۡقَلُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1578, 52, 4776, 41, 'أَمۡ عِندَهُمُ ٱلۡغَیۡبُ فَهُمۡ یَكۡتُبُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1579, 52, 4777, 42, 'أَمۡ یُرِیدُونَ كَیۡدࣰاۖ فَٱلَّذِینَ كَفَرُوا۟ هُمُ ٱلۡمَكِیدُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1580, 52, 4778, 43, 'أَمۡ لَهُمۡ إِلَـٰهٌ غَیۡرُ ٱللَّهِۚ سُبۡحَـٰنَ ٱللَّهِ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1581, 52, 4779, 44, 'وَإِن یَرَوۡا۟ كِسۡفࣰا مِّنَ ٱلسَّمَاۤءِ سَاقِطࣰا یَقُولُوا۟ سَحَابࣱ مَّرۡكُومࣱ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1582, 52, 4780, 45, 'فَذَرۡهُمۡ حَتَّىٰ یُلَـٰقُوا۟ یَوۡمَهُمُ ٱلَّذِی فِیهِ یُصۡعَقُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1583, 52, 4781, 46, 'یَوۡمَ لَا یُغۡنِی عَنۡهُمۡ كَیۡدُهُمۡ شَیۡـࣰٔا وَلَا هُمۡ یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1584, 52, 4782, 47, 'وَإِنَّ لِلَّذِینَ ظَلَمُوا۟ عَذَابࣰا دُونَ ذَ ٰ⁠لِكَ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1585, 52, 4783, 48, 'وَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ فَإِنَّكَ بِأَعۡیُنِنَاۖ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ حِینَ تَقُومُ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1586, 52, 4784, 49, 'وَمِنَ ٱلَّیۡلِ فَسَبِّحۡهُ وَإِدۡبَـٰرَ ٱلنُّجُومِ\n', NULL, 0, '2023-05-26 17:22:33', '2023-05-27 11:09:33'),
(1587, 51, 4676, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَٱلذَّ ٰ⁠رِیَـٰتِ ذَرۡوࣰا\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:34'),
(1588, 51, 4677, 2, 'فَٱلۡحَـٰمِلَـٰتِ وِقۡرࣰا\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:34'),
(1589, 51, 4678, 3, 'فَٱلۡجَـٰرِیَـٰتِ یُسۡرࣰا\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:34'),
(1590, 51, 4679, 4, 'فَٱلۡمُقَسِّمَـٰتِ أَمۡرًا\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1591, 51, 4680, 5, 'إِنَّمَا تُوعَدُونَ لَصَادِقࣱ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1592, 51, 4681, 6, 'وَإِنَّ ٱلدِّینَ لَوَ ٰ⁠قِعࣱ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1593, 51, 4682, 7, 'وَٱلسَّمَاۤءِ ذَاتِ ٱلۡحُبُكِ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1594, 51, 4683, 8, 'إِنَّكُمۡ لَفِی قَوۡلࣲ مُّخۡتَلِفࣲ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1595, 51, 4684, 9, 'یُؤۡفَكُ عَنۡهُ مَنۡ أُفِكَ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1596, 51, 4685, 10, 'قُتِلَ ٱلۡخَرَّ ٰ⁠صُونَ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1597, 51, 4686, 11, 'ٱلَّذِینَ هُمۡ فِی غَمۡرَةࣲ سَاهُونَ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1598, 51, 4687, 12, 'یَسۡـَٔلُونَ أَیَّانَ یَوۡمُ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1599, 51, 4688, 13, 'یَوۡمَ هُمۡ عَلَى ٱلنَّارِ یُفۡتَنُونَ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1600, 51, 4689, 14, 'ذُوقُوا۟ فِتۡنَتَكُمۡ هَـٰذَا ٱلَّذِی كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1601, 51, 4690, 15, 'إِنَّ ٱلۡمُتَّقِینَ فِی جَنَّـٰتࣲ وَعُیُونٍ\n', NULL, 0, '2023-05-26 17:22:35', '2023-05-27 11:09:35'),
(1602, 51, 4691, 16, 'ءَاخِذِینَ مَاۤ ءَاتَىٰهُمۡ رَبُّهُمۡۚ إِنَّهُمۡ كَانُوا۟ قَبۡلَ ذَ ٰ⁠لِكَ مُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1603, 51, 4692, 17, 'كَانُوا۟ قَلِیلࣰا مِّنَ ٱلَّیۡلِ مَا یَهۡجَعُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1604, 51, 4693, 18, 'وَبِٱلۡأَسۡحَارِ هُمۡ یَسۡتَغۡفِرُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1605, 51, 4694, 19, 'وَفِیۤ أَمۡوَ ٰ⁠لِهِمۡ حَقࣱّ لِّلسَّاۤىِٕلِ وَٱلۡمَحۡرُومِ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1606, 51, 4695, 20, 'وَفِی ٱلۡأَرۡضِ ءَایَـٰتࣱ لِّلۡمُوقِنِینَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1607, 51, 4696, 21, 'وَفِیۤ أَنفُسِكُمۡۚ أَفَلَا تُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1608, 51, 4697, 22, 'وَفِی ٱلسَّمَاۤءِ رِزۡقُكُمۡ وَمَا تُوعَدُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1609, 51, 4698, 23, 'فَوَرَبِّ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِ إِنَّهُۥ لَحَقࣱّ مِّثۡلَ مَاۤ أَنَّكُمۡ تَنطِقُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:35'),
(1610, 51, 4699, 24, 'هَلۡ أَتَىٰكَ حَدِیثُ ضَیۡفِ إِبۡرَ ٰ⁠هِیمَ ٱلۡمُكۡرَمِینَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1611, 51, 4700, 25, 'إِذۡ دَخَلُوا۟ عَلَیۡهِ فَقَالُوا۟ سَلَـٰمࣰاۖ قَالَ سَلَـٰمࣱ قَوۡمࣱ مُّنكَرُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1612, 51, 4701, 26, 'فَرَاغَ إِلَىٰۤ أَهۡلِهِۦ فَجَاۤءَ بِعِجۡلࣲ سَمِینࣲ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1613, 51, 4702, 27, 'فَقَرَّبَهُۥۤ إِلَیۡهِمۡ قَالَ أَلَا تَأۡكُلُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1614, 51, 4703, 28, 'فَأَوۡجَسَ مِنۡهُمۡ خِیفَةࣰۖ قَالُوا۟ لَا تَخَفۡۖ وَبَشَّرُوهُ بِغُلَـٰمٍ عَلِیمࣲ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1615, 51, 4704, 29, 'فَأَقۡبَلَتِ ٱمۡرَأَتُهُۥ فِی صَرَّةࣲ فَصَكَّتۡ وَجۡهَهَا وَقَالَتۡ عَجُوزٌ عَقِیمࣱ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1616, 51, 4705, 30, 'قَالُوا۟ كَذَ ٰ⁠لِكِ قَالَ رَبُّكِۖ إِنَّهُۥ هُوَ ٱلۡحَكِیمُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1617, 51, 4706, 31, '۞ قَالَ فَمَا خَطۡبُكُمۡ أَیُّهَا ٱلۡمُرۡسَلُونَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1618, 51, 4707, 32, 'قَالُوۤا۟ إِنَّاۤ أُرۡسِلۡنَاۤ إِلَىٰ قَوۡمࣲ مُّجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:22:36', '2023-05-27 11:09:36'),
(1619, 51, 4708, 33, 'لِنُرۡسِلَ عَلَیۡهِمۡ حِجَارَةࣰ مِّن طِینࣲ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1620, 51, 4709, 34, 'مُّسَوَّمَةً عِندَ رَبِّكَ لِلۡمُسۡرِفِینَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1621, 51, 4710, 35, 'فَأَخۡرَجۡنَا مَن كَانَ فِیهَا مِنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1622, 51, 4711, 36, 'فَمَا وَجَدۡنَا فِیهَا غَیۡرَ بَیۡتࣲ مِّنَ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1623, 51, 4712, 37, 'وَتَرَكۡنَا فِیهَاۤ ءَایَةࣰ لِّلَّذِینَ یَخَافُونَ ٱلۡعَذَابَ ٱلۡأَلِیمَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1624, 51, 4713, 38, 'وَفِی مُوسَىٰۤ إِذۡ أَرۡسَلۡنَـٰهُ إِلَىٰ فِرۡعَوۡنَ بِسُلۡطَـٰنࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1625, 51, 4714, 39, 'فَتَوَلَّىٰ بِرُكۡنِهِۦ وَقَالَ سَـٰحِرٌ أَوۡ مَجۡنُونࣱ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1626, 51, 4715, 40, 'فَأَخَذۡنَـٰهُ وَجُنُودَهُۥ فَنَبَذۡنَـٰهُمۡ فِی ٱلۡیَمِّ وَهُوَ مُلِیمࣱ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:36'),
(1627, 51, 4716, 41, 'وَفِی عَادٍ إِذۡ أَرۡسَلۡنَا عَلَیۡهِمُ ٱلرِّیحَ ٱلۡعَقِیمَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1628, 51, 4717, 42, 'مَا تَذَرُ مِن شَیۡءٍ أَتَتۡ عَلَیۡهِ إِلَّا جَعَلَتۡهُ كَٱلرَّمِیمِ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1629, 51, 4718, 43, 'وَفِی ثَمُودَ إِذۡ قِیلَ لَهُمۡ تَمَتَّعُوا۟ حَتَّىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1630, 51, 4719, 44, 'فَعَتَوۡا۟ عَنۡ أَمۡرِ رَبِّهِمۡ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ وَهُمۡ یَنظُرُونَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1631, 51, 4720, 45, 'فَمَا ٱسۡتَطَـٰعُوا۟ مِن قِیَامࣲ وَمَا كَانُوا۟ مُنتَصِرِینَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1632, 51, 4721, 46, 'وَقَوۡمَ نُوحࣲ مِّن قَبۡلُۖ إِنَّهُمۡ كَانُوا۟ قَوۡمࣰا فَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1633, 51, 4722, 47, 'وَٱلسَّمَاۤءَ بَنَیۡنَـٰهَا بِأَیۡی۟دࣲ وَإِنَّا لَمُوسِعُونَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1634, 51, 4723, 48, 'وَٱلۡأَرۡضَ فَرَشۡنَـٰهَا فَنِعۡمَ ٱلۡمَـٰهِدُونَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1635, 51, 4724, 49, 'وَمِن كُلِّ شَیۡءٍ خَلَقۡنَا زَوۡجَیۡنِ لَعَلَّكُمۡ تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1636, 51, 4725, 50, 'فَفِرُّوۤا۟ إِلَى ٱللَّهِۖ إِنِّی لَكُم مِّنۡهُ نَذِیرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:22:37', '2023-05-27 11:09:37'),
(1637, 51, 4726, 51, 'وَلَا تَجۡعَلُوا۟ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَۖ إِنِّی لَكُم مِّنۡهُ نَذِیرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1638, 51, 4727, 52, 'كَذَ ٰ⁠لِكَ مَاۤ أَتَى ٱلَّذِینَ مِن قَبۡلِهِم مِّن رَّسُولٍ إِلَّا قَالُوا۟ سَاحِرٌ أَوۡ مَجۡنُونٌ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1639, 51, 4728, 53, 'أَتَوَاصَوۡا۟ بِهِۦۚ بَلۡ هُمۡ قَوۡمࣱ طَاغُونَ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1640, 51, 4729, 54, 'فَتَوَلَّ عَنۡهُمۡ فَمَاۤ أَنتَ بِمَلُومࣲ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1641, 51, 4730, 55, 'وَذَكِّرۡ فَإِنَّ ٱلذِّكۡرَىٰ تَنفَعُ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1642, 51, 4731, 56, 'وَمَا خَلَقۡتُ ٱلۡجِنَّ وَٱلۡإِنسَ إِلَّا لِیَعۡبُدُونِ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1643, 51, 4732, 57, 'مَاۤ أُرِیدُ مِنۡهُم مِّن رِّزۡقࣲ وَمَاۤ أُرِیدُ أَن یُطۡعِمُونِ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1644, 51, 4733, 58, 'إِنَّ ٱللَّهَ هُوَ ٱلرَّزَّاقُ ذُو ٱلۡقُوَّةِ ٱلۡمَتِینُ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1645, 51, 4734, 59, 'فَإِنَّ لِلَّذِینَ ظَلَمُوا۟ ذَنُوبࣰا مِّثۡلَ ذَنُوبِ أَصۡحَـٰبِهِمۡ فَلَا یَسۡتَعۡجِلُونِ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:37'),
(1646, 51, 4735, 60, 'فَوَیۡلࣱ لِّلَّذِینَ كَفَرُوا۟ مِن یَوۡمِهِمُ ٱلَّذِی یُوعَدُونَ\n', NULL, 0, '2023-05-26 17:22:38', '2023-05-27 11:09:38'),
(1647, 50, 4631, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قۤۚ وَٱلۡقُرۡءَانِ ٱلۡمَجِیدِ\n', NULL, 0, '2023-05-26 17:22:42', '2023-05-27 11:09:38'),
(1648, 50, 4632, 2, 'بَلۡ عَجِبُوۤا۟ أَن جَاۤءَهُم مُّنذِرࣱ مِّنۡهُمۡ فَقَالَ ٱلۡكَـٰفِرُونَ هَـٰذَا شَیۡءٌ عَجِیبٌ\n', NULL, 0, '2023-05-26 17:22:42', '2023-05-27 11:09:39'),
(1649, 50, 4633, 3, 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابࣰاۖ ذَ ٰ⁠لِكَ رَجۡعُۢ بَعِیدࣱ\n', NULL, 0, '2023-05-26 17:22:42', '2023-05-27 11:09:39'),
(1650, 50, 4634, 4, 'قَدۡ عَلِمۡنَا مَا تَنقُصُ ٱلۡأَرۡضُ مِنۡهُمۡۖ وَعِندَنَا كِتَـٰبٌ حَفِیظُۢ\n', NULL, 0, '2023-05-26 17:22:42', '2023-05-27 11:09:39'),
(1651, 50, 4635, 5, 'بَلۡ كَذَّبُوا۟ بِٱلۡحَقِّ لَمَّا جَاۤءَهُمۡ فَهُمۡ فِیۤ أَمۡرࣲ مَّرِیجٍ\n', NULL, 0, '2023-05-26 17:22:42', '2023-05-27 11:09:39'),
(1652, 50, 4636, 6, 'أَفَلَمۡ یَنظُرُوۤا۟ إِلَى ٱلسَّمَاۤءِ فَوۡقَهُمۡ كَیۡفَ بَنَیۡنَـٰهَا وَزَیَّنَّـٰهَا وَمَا لَهَا مِن فُرُوجࣲ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1653, 50, 4637, 7, 'وَٱلۡأَرۡضَ مَدَدۡنَـٰهَا وَأَلۡقَیۡنَا فِیهَا رَوَ ٰ⁠سِیَ وَأَنۢبَتۡنَا فِیهَا مِن كُلِّ زَوۡجِۭ بَهِیجࣲ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1654, 50, 4638, 8, 'تَبۡصِرَةࣰ وَذِكۡرَىٰ لِكُلِّ عَبۡدࣲ مُّنِیبࣲ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1655, 50, 4639, 9, 'وَنَزَّلۡنَا مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ مُّبَـٰرَكࣰا فَأَنۢبَتۡنَا بِهِۦ جَنَّـٰتࣲ وَحَبَّ ٱلۡحَصِیدِ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1656, 50, 4640, 10, 'وَٱلنَّخۡلَ بَاسِقَـٰتࣲ لَّهَا طَلۡعࣱ نَّضِیدࣱ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1657, 50, 4641, 11, 'رِّزۡقࣰا لِّلۡعِبَادِۖ وَأَحۡیَیۡنَا بِهِۦ بَلۡدَةࣰ مَّیۡتࣰاۚ كَذَ ٰ⁠لِكَ ٱلۡخُرُوجُ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1658, 50, 4642, 12, 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحࣲ وَأَصۡحَـٰبُ ٱلرَّسِّ وَثَمُودُ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1659, 50, 4643, 13, 'وَعَادࣱ وَفِرۡعَوۡنُ وَإِخۡوَ ٰ⁠نُ لُوطࣲ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1660, 50, 4644, 14, 'وَأَصۡحَـٰبُ ٱلۡأَیۡكَةِ وَقَوۡمُ تُبَّعࣲۚ كُلࣱّ كَذَّبَ ٱلرُّسُلَ فَحَقَّ وَعِیدِ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1661, 50, 4645, 15, 'أَفَعَیِینَا بِٱلۡخَلۡقِ ٱلۡأَوَّلِۚ بَلۡ هُمۡ فِی لَبۡسࣲ مِّنۡ خَلۡقࣲ جَدِیدࣲ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1662, 50, 4646, 16, 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَـٰنَ وَنَعۡلَمُ مَا تُوَسۡوِسُ بِهِۦ نَفۡسُهُۥۖ وَنَحۡنُ أَقۡرَبُ إِلَیۡهِ مِنۡ حَبۡلِ ٱلۡوَرِیدِ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:39'),
(1663, 50, 4647, 17, 'إِذۡ یَتَلَقَّى ٱلۡمُتَلَقِّیَانِ عَنِ ٱلۡیَمِینِ وَعَنِ ٱلشِّمَالِ قَعِیدࣱ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1664, 50, 4648, 18, 'مَّا یَلۡفِظُ مِن قَوۡلٍ إِلَّا لَدَیۡهِ رَقِیبٌ عَتِیدࣱ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1665, 50, 4649, 19, 'وَجَاۤءَتۡ سَكۡرَةُ ٱلۡمَوۡتِ بِٱلۡحَقِّۖ ذَ ٰ⁠لِكَ مَا كُنتَ مِنۡهُ تَحِیدُ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1666, 50, 4650, 20, 'وَنُفِخَ فِی ٱلصُّورِۚ ذَ ٰ⁠لِكَ یَوۡمُ ٱلۡوَعِیدِ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1667, 50, 4651, 21, 'وَجَاۤءَتۡ كُلُّ نَفۡسࣲ مَّعَهَا سَاۤىِٕقࣱ وَشَهِیدࣱ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1668, 50, 4652, 22, 'لَّقَدۡ كُنتَ فِی غَفۡلَةࣲ مِّنۡ هَـٰذَا فَكَشَفۡنَا عَنكَ غِطَاۤءَكَ فَبَصَرُكَ ٱلۡیَوۡمَ حَدِیدࣱ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1669, 50, 4653, 23, 'وَقَالَ قَرِینُهُۥ هَـٰذَا مَا لَدَیَّ عَتِیدٌ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1670, 50, 4654, 24, 'أَلۡقِیَا فِی جَهَنَّمَ كُلَّ كَفَّارٍ عَنِیدࣲ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1671, 50, 4655, 25, 'مَّنَّاعࣲ لِّلۡخَیۡرِ مُعۡتَدࣲ مُّرِیبٍ\n', NULL, 0, '2023-05-26 17:22:43', '2023-05-27 11:09:40'),
(1672, 50, 4656, 26, 'ٱلَّذِی جَعَلَ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ فَأَلۡقِیَاهُ فِی ٱلۡعَذَابِ ٱلشَّدِیدِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1673, 50, 4657, 27, '۞ قَالَ قَرِینُهُۥ رَبَّنَا مَاۤ أَطۡغَیۡتُهُۥ وَلَـٰكِن كَانَ فِی ضَلَـٰلِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1674, 50, 4658, 28, 'قَالَ لَا تَخۡتَصِمُوا۟ لَدَیَّ وَقَدۡ قَدَّمۡتُ إِلَیۡكُم بِٱلۡوَعِیدِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1675, 50, 4659, 29, 'مَا یُبَدَّلُ ٱلۡقَوۡلُ لَدَیَّ وَمَاۤ أَنَا۠ بِظَلَّـٰمࣲ لِّلۡعَبِیدِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1676, 50, 4660, 30, 'یَوۡمَ نَقُولُ لِجَهَنَّمَ هَلِ ٱمۡتَلَأۡتِ وَتَقُولُ هَلۡ مِن مَّزِیدࣲ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1677, 50, 4661, 31, 'وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِینَ غَیۡرَ بَعِیدٍ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1678, 50, 4662, 32, 'هَـٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِیظࣲ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1679, 50, 4663, 33, 'مَّنۡ خَشِیَ ٱلرَّحۡمَـٰنَ بِٱلۡغَیۡبِ وَجَاۤءَ بِقَلۡبࣲ مُّنِیبٍ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1680, 50, 4664, 34, 'ٱدۡخُلُوهَا بِسَلَـٰمࣲۖ ذَ ٰ⁠لِكَ یَوۡمُ ٱلۡخُلُودِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:40'),
(1681, 50, 4665, 35, 'لَهُم مَّا یَشَاۤءُونَ فِیهَا وَلَدَیۡنَا مَزِیدࣱ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1682, 50, 4666, 36, 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَشَدُّ مِنۡهُم بَطۡشࣰا فَنَقَّبُوا۟ فِی ٱلۡبِلَـٰدِ هَلۡ مِن مَّحِیصٍ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1683, 50, 4667, 37, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَذِكۡرَىٰ لِمَن كَانَ لَهُۥ قَلۡبٌ أَوۡ أَلۡقَى ٱلسَّمۡعَ وَهُوَ شَهِیدࣱ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1684, 50, 4668, 38, 'وَلَقَدۡ خَلَقۡنَا ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَا فِی سِتَّةِ أَیَّامࣲ وَمَا مَسَّنَا مِن لُّغُوبࣲ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1685, 50, 4669, 39, 'فَٱصۡبِرۡ عَلَىٰ مَا یَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ ٱلۡغُرُوبِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1686, 50, 4670, 40, 'وَمِنَ ٱلَّیۡلِ فَسَبِّحۡهُ وَأَدۡبَـٰرَ ٱلسُّجُودِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1687, 50, 4671, 41, 'وَٱسۡتَمِعۡ یَوۡمَ یُنَادِ ٱلۡمُنَادِ مِن مَّكَانࣲ قَرِیبࣲ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1688, 50, 4672, 42, 'یَوۡمَ یَسۡمَعُونَ ٱلصَّیۡحَةَ بِٱلۡحَقِّۚ ذَ ٰ⁠لِكَ یَوۡمُ ٱلۡخُرُوجِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1689, 50, 4673, 43, 'إِنَّا نَحۡنُ نُحۡیِۦ وَنُمِیتُ وَإِلَیۡنَا ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1690, 50, 4674, 44, 'یَوۡمَ تَشَقَّقُ ٱلۡأَرۡضُ عَنۡهُمۡ سِرَاعࣰاۚ ذَ ٰ⁠لِكَ حَشۡرٌ عَلَیۡنَا یَسِیرࣱ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1691, 50, 4675, 45, 'نَّحۡنُ أَعۡلَمُ بِمَا یَقُولُونَۖ وَمَاۤ أَنتَ عَلَیۡهِم بِجَبَّارࣲۖ فَذَكِّرۡ بِٱلۡقُرۡءَانِ مَن یَخَافُ وَعِیدِ\n', NULL, 0, '2023-05-26 17:22:44', '2023-05-27 11:09:41'),
(1692, 49, 4613, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تُقَدِّمُوا۟ بَیۡنَ یَدَیِ ٱللَّهِ وَرَسُولِهِۦۖ وَٱتَّقُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:49'),
(1693, 49, 4614, 2, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَرۡفَعُوۤا۟ أَصۡوَ ٰ⁠تَكُمۡ فَوۡقَ صَوۡتِ ٱلنَّبِیِّ وَلَا تَجۡهَرُوا۟ لَهُۥ بِٱلۡقَوۡلِ كَجَهۡرِ بَعۡضِكُمۡ لِبَعۡضٍ أَن تَحۡبَطَ أَعۡمَـٰلُكُمۡ وَأَنتُمۡ لَا تَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:49'),
(1694, 49, 4615, 3, 'إِنَّ ٱلَّذِینَ یَغُضُّونَ أَصۡوَ ٰ⁠تَهُمۡ عِندَ رَسُولِ ٱللَّهِ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ ٱمۡتَحَنَ ٱللَّهُ قُلُوبَهُمۡ لِلتَّقۡوَىٰۚ لَهُم مَّغۡفِرَةࣱ وَأَجۡرٌ عَظِیمٌ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:49'),
(1695, 49, 4616, 4, 'إِنَّ ٱلَّذِینَ یُنَادُونَكَ مِن وَرَاۤءِ ٱلۡحُجُرَ ٰ⁠تِ أَكۡثَرُهُمۡ لَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:49'),
(1696, 49, 4617, 5, 'وَلَوۡ أَنَّهُمۡ صَبَرُوا۟ حَتَّىٰ تَخۡرُجَ إِلَیۡهِمۡ لَكَانَ خَیۡرࣰا لَّهُمۡۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1697, 49, 4618, 6, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِن جَاۤءَكُمۡ فَاسِقُۢ بِنَبَإࣲ فَتَبَیَّنُوۤا۟ أَن تُصِیبُوا۟ قَوۡمَۢا بِجَهَـٰلَةࣲ فَتُصۡبِحُوا۟ عَلَىٰ مَا فَعَلۡتُمۡ نَـٰدِمِینَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1698, 49, 4619, 7, 'وَٱعۡلَمُوۤا۟ أَنَّ فِیكُمۡ رَسُولَ ٱللَّهِۚ لَوۡ یُطِیعُكُمۡ فِی كَثِیرࣲ مِّنَ ٱلۡأَمۡرِ لَعَنِتُّمۡ وَلَـٰكِنَّ ٱللَّهَ حَبَّبَ إِلَیۡكُمُ ٱلۡإِیمَـٰنَ وَزَیَّنَهُۥ فِی قُلُوبِكُمۡ وَكَرَّهَ إِلَیۡكُمُ ٱلۡكُفۡرَ وَٱلۡفُسُوقَ وَٱلۡعِصۡیَانَۚ أُو۟لَـٰۤىِٕكَ هُمُ ٱلرَّ ٰ⁠شِدُونَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1699, 49, 4620, 8, 'فَضۡلࣰا مِّنَ ٱللَّهِ وَنِعۡمَةࣰۚ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1700, 49, 4621, 9, 'وَإِن طَاۤىِٕفَتَانِ مِنَ ٱلۡمُؤۡمِنِینَ ٱقۡتَتَلُوا۟ فَأَصۡلِحُوا۟ بَیۡنَهُمَاۖ فَإِنۢ بَغَتۡ إِحۡدَىٰهُمَا عَلَى ٱلۡأُخۡرَىٰ فَقَـٰتِلُوا۟ ٱلَّتِی تَبۡغِی حَتَّىٰ تَفِیۤءَ إِلَىٰۤ أَمۡرِ ٱللَّهِۚ فَإِن فَاۤءَتۡ فَأَصۡلِحُوا۟ بَیۡنَهُمَا بِٱلۡعَدۡلِ وَأَقۡسِطُوۤا۟ۖ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُقۡسِطِینَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1701, 49, 4622, 10, 'إِنَّمَا ٱلۡمُؤۡمِنُونَ إِخۡوَةࣱ فَأَصۡلِحُوا۟ بَیۡنَ أَخَوَیۡكُمۡۚ وَٱتَّقُوا۟ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1702, 49, 4623, 11, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا یَسۡخَرۡ قَوۡمࣱ مِّن قَوۡمٍ عَسَىٰۤ أَن یَكُونُوا۟ خَیۡرࣰا مِّنۡهُمۡ وَلَا نِسَاۤءࣱ مِّن نِّسَاۤءٍ عَسَىٰۤ أَن یَكُنَّ خَیۡرࣰا مِّنۡهُنَّۖ وَلَا تَلۡمِزُوۤا۟ أَنفُسَكُمۡ وَلَا تَنَابَزُوا۟ بِٱلۡأَلۡقَـٰبِۖ بِئۡسَ ٱلِٱسۡمُ ٱلۡفُسُوقُ بَعۡدَ ٱلۡإِیمَـٰنِۚ وَمَن لَّمۡ یَتُبۡ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1703, 49, 4624, 12, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱجۡتَنِبُوا۟ كَثِیرࣰا مِّنَ ٱلظَّنِّ إِنَّ بَعۡضَ ٱلظَّنِّ إِثۡمࣱۖ وَلَا تَجَسَّسُوا۟ وَلَا یَغۡتَب بَّعۡضُكُم بَعۡضًاۚ أَیُحِبُّ أَحَدُكُمۡ أَن یَأۡكُلَ لَحۡمَ أَخِیهِ مَیۡتࣰا فَكَرِهۡتُمُوهُۚ وَٱتَّقُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ تَوَّابࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1704, 49, 4625, 13, 'یَـٰۤأَیُّهَا ٱلنَّاسُ إِنَّا خَلَقۡنَـٰكُم مِّن ذَكَرࣲ وَأُنثَىٰ وَجَعَلۡنَـٰكُمۡ شُعُوبࣰا وَقَبَاۤىِٕلَ لِتَعَارَفُوۤا۟ۚ إِنَّ أَكۡرَمَكُمۡ عِندَ ٱللَّهِ أَتۡقَىٰكُمۡۚ إِنَّ ٱللَّهَ عَلِیمٌ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1705, 49, 4626, 14, '۞ قَالَتِ ٱلۡأَعۡرَابُ ءَامَنَّاۖ قُل لَّمۡ تُؤۡمِنُوا۟ وَلَـٰكِن قُولُوۤا۟ أَسۡلَمۡنَا وَلَمَّا یَدۡخُلِ ٱلۡإِیمَـٰنُ فِی قُلُوبِكُمۡۖ وَإِن تُطِیعُوا۟ ٱللَّهَ وَرَسُولَهُۥ لَا یَلِتۡكُم مِّنۡ أَعۡمَـٰلِكُمۡ شَیۡـًٔاۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1706, 49, 4627, 15, 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِینَ ءَامَنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ ثُمَّ لَمۡ یَرۡتَابُوا۟ وَجَـٰهَدُوا۟ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡ فِی سَبِیلِ ٱللَّهِۚ أُو۟لَـٰۤىِٕكَ هُمُ ٱلصَّـٰدِقُونَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1707, 49, 4628, 16, 'قُلۡ أَتُعَلِّمُونَ ٱللَّهَ بِدِینِكُمۡ وَٱللَّهُ یَعۡلَمُ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ وَٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1708, 49, 4629, 17, 'یَمُنُّونَ عَلَیۡكَ أَنۡ أَسۡلَمُوا۟ۖ قُل لَّا تَمُنُّوا۟ عَلَیَّ إِسۡلَـٰمَكُمۖ بَلِ ٱللَّهُ یَمُنُّ عَلَیۡكُمۡ أَنۡ هَدَىٰكُمۡ لِلۡإِیمَـٰنِ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1709, 49, 4630, 18, 'إِنَّ ٱللَّهَ یَعۡلَمُ غَیۡبَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَٱللَّهُ بَصِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:22:54', '2023-05-27 11:09:50'),
(1710, 48, 4584, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ إِنَّا فَتَحۡنَا لَكَ فَتۡحࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:53'),
(1711, 48, 4585, 2, 'لِّیَغۡفِرَ لَكَ ٱللَّهُ مَا تَقَدَّمَ مِن ذَنۢبِكَ وَمَا تَأَخَّرَ وَیُتِمَّ نِعۡمَتَهُۥ عَلَیۡكَ وَیَهۡدِیَكَ صِرَ ٰ⁠طࣰا مُّسۡتَقِیمࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:53'),
(1712, 48, 4586, 3, 'وَیَنصُرَكَ ٱللَّهُ نَصۡرًا عَزِیزًا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:53'),
(1713, 48, 4587, 4, 'هُوَ ٱلَّذِیۤ أَنزَلَ ٱلسَّكِینَةَ فِی قُلُوبِ ٱلۡمُؤۡمِنِینَ لِیَزۡدَادُوۤا۟ إِیمَـٰنࣰا مَّعَ إِیمَـٰنِهِمۡۗ وَلِلَّهِ جُنُودُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:53'),
(1714, 48, 4588, 5, 'لِّیُدۡخِلَ ٱلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَا وَیُكَفِّرَ عَنۡهُمۡ سَیِّـَٔاتِهِمۡۚ وَكَانَ ذَ ٰ⁠لِكَ عِندَ ٱللَّهِ فَوۡزًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1715, 48, 4589, 6, 'وَیُعَذِّبَ ٱلۡمُنَـٰفِقِینَ وَٱلۡمُنَـٰفِقَـٰتِ وَٱلۡمُشۡرِكِینَ وَٱلۡمُشۡرِكَـٰتِ ٱلظَّاۤنِّینَ بِٱللَّهِ ظَنَّ ٱلسَّوۡءِۚ عَلَیۡهِمۡ دَاۤىِٕرَةُ ٱلسَّوۡءِۖ وَغَضِبَ ٱللَّهُ عَلَیۡهِمۡ وَلَعَنَهُمۡ وَأَعَدَّ لَهُمۡ جَهَنَّمَۖ وَسَاۤءَتۡ مَصِیرࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1716, 48, 4590, 7, 'وَلِلَّهِ جُنُودُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَزِیزًا حَكِیمًا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1717, 48, 4591, 8, 'إِنَّاۤ أَرۡسَلۡنَـٰكَ شَـٰهِدࣰا وَمُبَشِّرࣰا وَنَذِیرࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1718, 48, 4592, 9, 'لِّتُؤۡمِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُۚ وَتُسَبِّحُوهُ بُكۡرَةࣰ وَأَصِیلًا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1719, 48, 4593, 10, 'إِنَّ ٱلَّذِینَ یُبَایِعُونَكَ إِنَّمَا یُبَایِعُونَ ٱللَّهَ یَدُ ٱللَّهِ فَوۡقَ أَیۡدِیهِمۡۚ فَمَن نَّكَثَ فَإِنَّمَا یَنكُثُ عَلَىٰ نَفۡسِهِۦۖ وَمَنۡ أَوۡفَىٰ بِمَا عَـٰهَدَ عَلَیۡهُ ٱللَّهَ فَسَیُؤۡتِیهِ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1720, 48, 4594, 11, 'سَیَقُولُ لَكَ ٱلۡمُخَلَّفُونَ مِنَ ٱلۡأَعۡرَابِ شَغَلَتۡنَاۤ أَمۡوَ ٰ⁠لُنَا وَأَهۡلُونَا فَٱسۡتَغۡفِرۡ لَنَاۚ یَقُولُونَ بِأَلۡسِنَتِهِم مَّا لَیۡسَ فِی قُلُوبِهِمۡۚ قُلۡ فَمَن یَمۡلِكُ لَكُم مِّنَ ٱللَّهِ شَیۡـًٔا إِنۡ أَرَادَ بِكُمۡ ضَرًّا أَوۡ أَرَادَ بِكُمۡ نَفۡعَۢاۚ بَلۡ كَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرَۢا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1721, 48, 4595, 12, 'بَلۡ ظَنَنتُمۡ أَن لَّن یَنقَلِبَ ٱلرَّسُولُ وَٱلۡمُؤۡمِنُونَ إِلَىٰۤ أَهۡلِیهِمۡ أَبَدࣰا وَزُیِّنَ ذَ ٰ⁠لِكَ فِی قُلُوبِكُمۡ وَظَنَنتُمۡ ظَنَّ ٱلسَّوۡءِ وَكُنتُمۡ قَوۡمَۢا بُورࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1722, 48, 4596, 13, 'وَمَن لَّمۡ یُؤۡمِنۢ بِٱللَّهِ وَرَسُولِهِۦ فَإِنَّاۤ أَعۡتَدۡنَا لِلۡكَـٰفِرِینَ سَعِیرࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1723, 48, 4597, 14, 'وَلِلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ یَغۡفِرُ لِمَن یَشَاۤءُ وَیُعَذِّبُ مَن یَشَاۤءُۚ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:23:02', '2023-05-27 11:09:54'),
(1724, 48, 4598, 15, 'سَیَقُولُ ٱلۡمُخَلَّفُونَ إِذَا ٱنطَلَقۡتُمۡ إِلَىٰ مَغَانِمَ لِتَأۡخُذُوهَا ذَرُونَا نَتَّبِعۡكُمۡۖ یُرِیدُونَ أَن یُبَدِّلُوا۟ كَلَـٰمَ ٱللَّهِۚ قُل لَّن تَتَّبِعُونَا كَذَ ٰ⁠لِكُمۡ قَالَ ٱللَّهُ مِن قَبۡلُۖ فَسَیَقُولُونَ بَلۡ تَحۡسُدُونَنَاۚ بَلۡ كَانُوا۟ لَا یَفۡقَهُونَ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1725, 48, 4599, 16, 'قُل لِّلۡمُخَلَّفِینَ مِنَ ٱلۡأَعۡرَابِ سَتُدۡعَوۡنَ إِلَىٰ قَوۡمٍ أُو۟لِی بَأۡسࣲ شَدِیدࣲ تُقَـٰتِلُونَهُمۡ أَوۡ یُسۡلِمُونَۖ فَإِن تُطِیعُوا۟ یُؤۡتِكُمُ ٱللَّهُ أَجۡرًا حَسَنࣰاۖ وَإِن تَتَوَلَّوۡا۟ كَمَا تَوَلَّیۡتُم مِّن قَبۡلُ یُعَذِّبۡكُمۡ عَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1726, 48, 4600, 17, 'لَّیۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجࣱ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجࣱ وَلَا عَلَى ٱلۡمَرِیضِ حَرَجࣱۗ وَمَن یُطِعِ ٱللَّهَ وَرَسُولَهُۥ یُدۡخِلۡهُ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۖ وَمَن یَتَوَلَّ یُعَذِّبۡهُ عَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1727, 48, 4601, 18, '۞ لَّقَدۡ رَضِیَ ٱللَّهُ عَنِ ٱلۡمُؤۡمِنِینَ إِذۡ یُبَایِعُونَكَ تَحۡتَ ٱلشَّجَرَةِ فَعَلِمَ مَا فِی قُلُوبِهِمۡ فَأَنزَلَ ٱلسَّكِینَةَ عَلَیۡهِمۡ وَأَثَـٰبَهُمۡ فَتۡحࣰا قَرِیبࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1728, 48, 4602, 19, 'وَمَغَانِمَ كَثِیرَةࣰ یَأۡخُذُونَهَاۗ وَكَانَ ٱللَّهُ عَزِیزًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1729, 48, 4603, 20, 'وَعَدَكُمُ ٱللَّهُ مَغَانِمَ كَثِیرَةࣰ تَأۡخُذُونَهَا فَعَجَّلَ لَكُمۡ هَـٰذِهِۦ وَكَفَّ أَیۡدِیَ ٱلنَّاسِ عَنكُمۡ وَلِتَكُونَ ءَایَةࣰ لِّلۡمُؤۡمِنِینَ وَیَهۡدِیَكُمۡ صِرَ ٰ⁠طࣰا مُّسۡتَقِیمࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1730, 48, 4604, 21, 'وَأُخۡرَىٰ لَمۡ تَقۡدِرُوا۟ عَلَیۡهَا قَدۡ أَحَاطَ ٱللَّهُ بِهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1731, 48, 4605, 22, 'وَلَوۡ قَـٰتَلَكُمُ ٱلَّذِینَ كَفَرُوا۟ لَوَلَّوُا۟ ٱلۡأَدۡبَـٰرَ ثُمَّ لَا یَجِدُونَ وَلِیࣰّا وَلَا نَصِیرࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1732, 48, 4606, 23, 'سُنَّةَ ٱللَّهِ ٱلَّتِی قَدۡ خَلَتۡ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِیلࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1733, 48, 4607, 24, 'وَهُوَ ٱلَّذِی كَفَّ أَیۡدِیَهُمۡ عَنكُمۡ وَأَیۡدِیَكُمۡ عَنۡهُم بِبَطۡنِ مَكَّةَ مِنۢ بَعۡدِ أَنۡ أَظۡفَرَكُمۡ عَلَیۡهِمۡۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرًا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1734, 48, 4608, 25, 'هُمُ ٱلَّذِینَ كَفَرُوا۟ وَصَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَٱلۡهَدۡیَ مَعۡكُوفًا أَن یَبۡلُغَ مَحِلَّهُۥۚ وَلَوۡلَا رِجَالࣱ مُّؤۡمِنُونَ وَنِسَاۤءࣱ مُّؤۡمِنَـٰتࣱ لَّمۡ تَعۡلَمُوهُمۡ أَن تَطَـُٔوهُمۡ فَتُصِیبَكُم مِّنۡهُم مَّعَرَّةُۢ بِغَیۡرِ عِلۡمࣲۖ لِّیُدۡخِلَ ٱللَّهُ فِی رَحۡمَتِهِۦ مَن یَشَاۤءُۚ لَوۡ تَزَیَّلُوا۟ لَعَذَّبۡنَا ٱلَّذِینَ كَفَرُوا۟ مِنۡهُمۡ عَذَابًا أَلِیمًا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:54'),
(1735, 48, 4609, 26, 'إِذۡ جَعَلَ ٱلَّذِینَ كَفَرُوا۟ فِی قُلُوبِهِمُ ٱلۡحَمِیَّةَ حَمِیَّةَ ٱلۡجَـٰهِلِیَّةِ فَأَنزَلَ ٱللَّهُ سَكِینَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِینَ وَأَلۡزَمَهُمۡ كَلِمَةَ ٱلتَّقۡوَىٰ وَكَانُوۤا۟ أَحَقَّ بِهَا وَأَهۡلَهَاۚ وَكَانَ ٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:55'),
(1736, 48, 4610, 27, 'لَّقَدۡ صَدَقَ ٱللَّهُ رَسُولَهُ ٱلرُّءۡیَا بِٱلۡحَقِّۖ لَتَدۡخُلُنَّ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ إِن شَاۤءَ ٱللَّهُ ءَامِنِینَ مُحَلِّقِینَ رُءُوسَكُمۡ وَمُقَصِّرِینَ لَا تَخَافُونَۖ فَعَلِمَ مَا لَمۡ تَعۡلَمُوا۟ فَجَعَلَ مِن دُونِ ذَ ٰ⁠لِكَ فَتۡحࣰا قَرِیبًا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:55'),
(1737, 48, 4611, 28, 'هُوَ ٱلَّذِیۤ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِینِ ٱلۡحَقِّ لِیُظۡهِرَهُۥ عَلَى ٱلدِّینِ كُلِّهِۦۚ وَكَفَىٰ بِٱللَّهِ شَهِیدࣰا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:55'),
(1738, 48, 4612, 29, 'مُّحَمَّدࣱ رَّسُولُ ٱللَّهِۚ وَٱلَّذِینَ مَعَهُۥۤ أَشِدَّاۤءُ عَلَى ٱلۡكُفَّارِ رُحَمَاۤءُ بَیۡنَهُمۡۖ تَرَىٰهُمۡ رُكَّعࣰا سُجَّدࣰا یَبۡتَغُونَ فَضۡلࣰا مِّنَ ٱللَّهِ وَرِضۡوَ ٰ⁠نࣰاۖ سِیمَاهُمۡ فِی وُجُوهِهِم مِّنۡ أَثَرِ ٱلسُّجُودِۚ ذَ ٰ⁠لِكَ مَثَلُهُمۡ فِی ٱلتَّوۡرَىٰةِۚ وَمَثَلُهُمۡ فِی ٱلۡإِنجِیلِ كَزَرۡعٍ أَخۡرَجَ شَطۡـَٔهُۥ فَـَٔازَرَهُۥ فَٱسۡتَغۡلَظَ فَٱسۡتَوَىٰ عَلَىٰ سُوقِهِۦ یُعۡجِبُ ٱلزُّرَّاعَ لِیَغِیظَ بِهِمُ ٱلۡكُفَّارَۗ وَعَدَ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ مِنۡهُم مَّغۡفِرَةࣰ وَأَجۡرًا عَظِیمَۢا\n', NULL, 0, '2023-05-26 17:23:03', '2023-05-27 11:09:55'),
(1739, 47, 4546, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱلَّذِینَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِیلِ ٱللَّهِ أَضَلَّ أَعۡمَـٰلَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1740, 47, 4547, 2, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَءَامَنُوا۟ بِمَا نُزِّلَ عَلَىٰ مُحَمَّدࣲ وَهُوَ ٱلۡحَقُّ مِن رَّبِّهِمۡ كَفَّرَ عَنۡهُمۡ سَیِّـَٔاتِهِمۡ وَأَصۡلَحَ بَالَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1741, 47, 4548, 3, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱلَّذِینَ كَفَرُوا۟ ٱتَّبَعُوا۟ ٱلۡبَـٰطِلَ وَأَنَّ ٱلَّذِینَ ءَامَنُوا۟ ٱتَّبَعُوا۟ ٱلۡحَقَّ مِن رَّبِّهِمۡۚ كَذَ ٰ⁠لِكَ یَضۡرِبُ ٱللَّهُ لِلنَّاسِ أَمۡثَـٰلَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1742, 47, 4549, 4, 'فَإِذَا لَقِیتُمُ ٱلَّذِینَ كَفَرُوا۟ فَضَرۡبَ ٱلرِّقَابِ حَتَّىٰۤ إِذَاۤ أَثۡخَنتُمُوهُمۡ فَشُدُّوا۟ ٱلۡوَثَاقَ فَإِمَّا مَنَّۢا بَعۡدُ وَإِمَّا فِدَاۤءً حَتَّىٰ تَضَعَ ٱلۡحَرۡبُ أَوۡزَارَهَاۚ ذَ ٰ⁠لِكَۖ وَلَوۡ یَشَاۤءُ ٱللَّهُ لَٱنتَصَرَ مِنۡهُمۡ وَلَـٰكِن لِّیَبۡلُوَا۟ بَعۡضَكُم بِبَعۡضࣲۗ وَٱلَّذِینَ قُتِلُوا۟ فِی سَبِیلِ ٱللَّهِ فَلَن یُضِلَّ أَعۡمَـٰلَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1743, 47, 4550, 5, 'سَیَهۡدِیهِمۡ وَیُصۡلِحُ بَالَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1744, 47, 4551, 6, 'وَیُدۡخِلُهُمُ ٱلۡجَنَّةَ عَرَّفَهَا لَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1745, 47, 4552, 7, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِن تَنصُرُوا۟ ٱللَّهَ یَنصُرۡكُمۡ وَیُثَبِّتۡ أَقۡدَامَكُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1746, 47, 4553, 8, 'وَٱلَّذِینَ كَفَرُوا۟ فَتَعۡسࣰا لَّهُمۡ وَأَضَلَّ أَعۡمَـٰلَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:56'),
(1747, 47, 4554, 9, 'ذَ ٰ⁠لِكَ بِأَنَّهُمۡ كَرِهُوا۟ مَاۤ أَنزَلَ ٱللَّهُ فَأَحۡبَطَ أَعۡمَـٰلَهُمۡ\n', NULL, 0, '2023-05-26 17:23:07', '2023-05-27 11:09:57'),
(1748, 47, 4555, 10, '۞ أَفَلَمۡ یَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَیَنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلَّذِینَ مِن قَبۡلِهِمۡۖ دَمَّرَ ٱللَّهُ عَلَیۡهِمۡۖ وَلِلۡكَـٰفِرِینَ أَمۡثَـٰلُهَا\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1749, 47, 4556, 11, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱللَّهَ مَوۡلَى ٱلَّذِینَ ءَامَنُوا۟ وَأَنَّ ٱلۡكَـٰفِرِینَ لَا مَوۡلَىٰ لَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1750, 47, 4557, 12, 'إِنَّ ٱللَّهَ یُدۡخِلُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۖ وَٱلَّذِینَ كَفَرُوا۟ یَتَمَتَّعُونَ وَیَأۡكُلُونَ كَمَا تَأۡكُلُ ٱلۡأَنۡعَـٰمُ وَٱلنَّارُ مَثۡوࣰى لَّهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1751, 47, 4558, 13, 'وَكَأَیِّن مِّن قَرۡیَةٍ هِیَ أَشَدُّ قُوَّةࣰ مِّن قَرۡیَتِكَ ٱلَّتِیۤ أَخۡرَجَتۡكَ أَهۡلَكۡنَـٰهُمۡ فَلَا نَاصِرَ لَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1752, 47, 4559, 14, 'أَفَمَن كَانَ عَلَىٰ بَیِّنَةࣲ مِّن رَّبِّهِۦ كَمَن زُیِّنَ لَهُۥ سُوۤءُ عَمَلِهِۦ وَٱتَّبَعُوۤا۟ أَهۡوَاۤءَهُم\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1753, 47, 4560, 15, 'مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِی وُعِدَ ٱلۡمُتَّقُونَۖ فِیهَاۤ أَنۡهَـٰرࣱ مِّن مَّاۤءٍ غَیۡرِ ءَاسِنࣲ وَأَنۡهَـٰرࣱ مِّن لَّبَنࣲ لَّمۡ یَتَغَیَّرۡ طَعۡمُهُۥ وَأَنۡهَـٰرࣱ مِّنۡ خَمۡرࣲ لَّذَّةࣲ لِّلشَّـٰرِبِینَ وَأَنۡهَـٰرࣱ مِّنۡ عَسَلࣲ مُّصَفࣰّىۖ وَلَهُمۡ فِیهَا مِن كُلِّ ٱلثَّمَرَ ٰ⁠تِ وَمَغۡفِرَةࣱ مِّن رَّبِّهِمۡۖ كَمَنۡ هُوَ خَـٰلِدࣱ فِی ٱلنَّارِ وَسُقُوا۟ مَاۤءً حَمِیمࣰا فَقَطَّعَ أَمۡعَاۤءَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1754, 47, 4561, 16, 'وَمِنۡهُم مَّن یَسۡتَمِعُ إِلَیۡكَ حَتَّىٰۤ إِذَا خَرَجُوا۟ مِنۡ عِندِكَ قَالُوا۟ لِلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ مَاذَا قَالَ ءَانِفًاۚ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَٱتَّبَعُوۤا۟ أَهۡوَاۤءَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1755, 47, 4562, 17, 'وَٱلَّذِینَ ٱهۡتَدَوۡا۟ زَادَهُمۡ هُدࣰى وَءَاتَىٰهُمۡ تَقۡوَىٰهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1756, 47, 4563, 18, 'فَهَلۡ یَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِیَهُم بَغۡتَةࣰۖ فَقَدۡ جَاۤءَ أَشۡرَاطُهَاۚ فَأَنَّىٰ لَهُمۡ إِذَا جَاۤءَتۡهُمۡ ذِكۡرَىٰهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1757, 47, 4564, 19, 'فَٱعۡلَمۡ أَنَّهُۥ لَاۤ إِلَـٰهَ إِلَّا ٱللَّهُ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَلِلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِۗ وَٱللَّهُ یَعۡلَمُ مُتَقَلَّبَكُمۡ وَمَثۡوَىٰكُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1758, 47, 4565, 20, 'وَیَقُولُ ٱلَّذِینَ ءَامَنُوا۟ لَوۡلَا نُزِّلَتۡ سُورَةࣱۖ فَإِذَاۤ أُنزِلَتۡ سُورَةࣱ مُّحۡكَمَةࣱ وَذُكِرَ فِیهَا ٱلۡقِتَالُ رَأَیۡتَ ٱلَّذِینَ فِی قُلُوبِهِم مَّرَضࣱ یَنظُرُونَ إِلَیۡكَ نَظَرَ ٱلۡمَغۡشِیِّ عَلَیۡهِ مِنَ ٱلۡمَوۡتِۖ فَأَوۡلَىٰ لَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1759, 47, 4566, 21, 'طَاعَةࣱ وَقَوۡلࣱ مَّعۡرُوفࣱۚ فَإِذَا عَزَمَ ٱلۡأَمۡرُ فَلَوۡ صَدَقُوا۟ ٱللَّهَ لَكَانَ خَیۡرࣰا لَّهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1760, 47, 4567, 22, 'فَهَلۡ عَسَیۡتُمۡ إِن تَوَلَّیۡتُمۡ أَن تُفۡسِدُوا۟ فِی ٱلۡأَرۡضِ وَتُقَطِّعُوۤا۟ أَرۡحَامَكُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1761, 47, 4568, 23, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ لَعَنَهُمُ ٱللَّهُ فَأَصَمَّهُمۡ وَأَعۡمَىٰۤ أَبۡصَـٰرَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1762, 47, 4569, 24, 'أَفَلَا یَتَدَبَّرُونَ ٱلۡقُرۡءَانَ أَمۡ عَلَىٰ قُلُوبٍ أَقۡفَالُهَاۤ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1763, 47, 4570, 25, 'إِنَّ ٱلَّذِینَ ٱرۡتَدُّوا۟ عَلَىٰۤ أَدۡبَـٰرِهِم مِّنۢ بَعۡدِ مَا تَبَیَّنَ لَهُمُ ٱلۡهُدَى ٱلشَّیۡطَـٰنُ سَوَّلَ لَهُمۡ وَأَمۡلَىٰ لَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1764, 47, 4571, 26, 'ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَالُوا۟ لِلَّذِینَ كَرِهُوا۟ مَا نَزَّلَ ٱللَّهُ سَنُطِیعُكُمۡ فِی بَعۡضِ ٱلۡأَمۡرِۖ وَٱللَّهُ یَعۡلَمُ إِسۡرَارَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:57'),
(1765, 47, 4572, 27, 'فَكَیۡفَ إِذَا تَوَفَّتۡهُمُ ٱلۡمَلَـٰۤىِٕكَةُ یَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَـٰرَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:58'),
(1766, 47, 4573, 28, 'ذَ ٰ⁠لِكَ بِأَنَّهُمُ ٱتَّبَعُوا۟ مَاۤ أَسۡخَطَ ٱللَّهَ وَكَرِهُوا۟ رِضۡوَ ٰ⁠نَهُۥ فَأَحۡبَطَ أَعۡمَـٰلَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:58'),
(1767, 47, 4574, 29, 'أَمۡ حَسِبَ ٱلَّذِینَ فِی قُلُوبِهِم مَّرَضٌ أَن لَّن یُخۡرِجَ ٱللَّهُ أَضۡغَـٰنَهُمۡ\n', NULL, 0, '2023-05-26 17:23:08', '2023-05-27 11:09:58'),
(1768, 47, 4575, 30, 'وَلَوۡ نَشَاۤءُ لَأَرَیۡنَـٰكَهُمۡ فَلَعَرَفۡتَهُم بِسِیمَـٰهُمۡۚ وَلَتَعۡرِفَنَّهُمۡ فِی لَحۡنِ ٱلۡقَوۡلِۚ وَٱللَّهُ یَعۡلَمُ أَعۡمَـٰلَكُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1769, 47, 4576, 31, 'وَلَنَبۡلُوَنَّكُمۡ حَتَّىٰ نَعۡلَمَ ٱلۡمُجَـٰهِدِینَ مِنكُمۡ وَٱلصَّـٰبِرِینَ وَنَبۡلُوَا۟ أَخۡبَارَكُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1770, 47, 4577, 32, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِیلِ ٱللَّهِ وَشَاۤقُّوا۟ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَیَّنَ لَهُمُ ٱلۡهُدَىٰ لَن یَضُرُّوا۟ ٱللَّهَ شَیۡـࣰٔا وَسَیُحۡبِطُ أَعۡمَـٰلَهُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1771, 47, 4578, 33, '۞ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ أَطِیعُوا۟ ٱللَّهَ وَأَطِیعُوا۟ ٱلرَّسُولَ وَلَا تُبۡطِلُوۤا۟ أَعۡمَـٰلَكُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1772, 47, 4579, 34, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِیلِ ٱللَّهِ ثُمَّ مَاتُوا۟ وَهُمۡ كُفَّارࣱ فَلَن یَغۡفِرَ ٱللَّهُ لَهُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1773, 47, 4580, 35, 'فَلَا تَهِنُوا۟ وَتَدۡعُوۤا۟ إِلَى ٱلسَّلۡمِ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ وَٱللَّهُ مَعَكُمۡ وَلَن یَتِرَكُمۡ أَعۡمَـٰلَكُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(1774, 47, 4581, 36, 'إِنَّمَا ٱلۡحَیَوٰةُ ٱلدُّنۡیَا لَعِبࣱ وَلَهۡوࣱۚ وَإِن تُؤۡمِنُوا۟ وَتَتَّقُوا۟ یُؤۡتِكُمۡ أُجُورَكُمۡ وَلَا یَسۡـَٔلۡكُمۡ أَمۡوَ ٰ⁠لَكُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1775, 47, 4582, 37, 'إِن یَسۡـَٔلۡكُمُوهَا فَیُحۡفِكُمۡ تَبۡخَلُوا۟ وَیُخۡرِجۡ أَضۡغَـٰنَكُمۡ\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1776, 47, 4583, 38, 'هَـٰۤأَنتُمۡ هَـٰۤؤُلَاۤءِ تُدۡعَوۡنَ لِتُنفِقُوا۟ فِی سَبِیلِ ٱللَّهِ فَمِنكُم مَّن یَبۡخَلُۖ وَمَن یَبۡخَلۡ فَإِنَّمَا یَبۡخَلُ عَن نَّفۡسِهِۦۚ وَٱللَّهُ ٱلۡغَنِیُّ وَأَنتُمُ ٱلۡفُقَرَاۤءُۚ وَإِن تَتَوَلَّوۡا۟ یَسۡتَبۡدِلۡ قَوۡمًا غَیۡرَكُمۡ ثُمَّ لَا یَكُونُوۤا۟ أَمۡثَـٰلَكُم\n', NULL, 0, '2023-05-26 17:23:09', '2023-05-27 11:09:58'),
(1777, 46, 4511, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ حمۤ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:09:59'),
(1778, 46, 4512, 2, 'تَنزِیلُ ٱلۡكِتَـٰبِ مِنَ ٱللَّهِ ٱلۡعَزِیزِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:09:59'),
(1779, 46, 4513, 3, 'مَا خَلَقۡنَا ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَاۤ إِلَّا بِٱلۡحَقِّ وَأَجَلࣲ مُّسَمࣰّىۚ وَٱلَّذِینَ كَفَرُوا۟ عَمَّاۤ أُنذِرُوا۟ مُعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:09:59'),
(1780, 46, 4514, 4, 'قُلۡ أَرَءَیۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِی مَاذَا خَلَقُوا۟ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكࣱ فِی ٱلسَّمَـٰوَ ٰ⁠تِۖ ٱئۡتُونِی بِكِتَـٰبࣲ مِّن قَبۡلِ هَـٰذَاۤ أَوۡ أَثَـٰرَةࣲ مِّنۡ عِلۡمٍ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:10:00'),
(1781, 46, 4515, 5, 'وَمَنۡ أَضَلُّ مِمَّن یَدۡعُوا۟ مِن دُونِ ٱللَّهِ مَن لَّا یَسۡتَجِیبُ لَهُۥۤ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ وَهُمۡ عَن دُعَاۤىِٕهِمۡ غَـٰفِلُونَ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:10:00'),
(1782, 46, 4516, 6, 'وَإِذَا حُشِرَ ٱلنَّاسُ كَانُوا۟ لَهُمۡ أَعۡدَاۤءࣰ وَكَانُوا۟ بِعِبَادَتِهِمۡ كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:10:00'),
(1783, 46, 4517, 7, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَـٰتُنَا بَیِّنَـٰتࣲ قَالَ ٱلَّذِینَ كَفَرُوا۟ لِلۡحَقِّ لَمَّا جَاۤءَهُمۡ هَـٰذَا سِحۡرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:10:00'),
(1784, 46, 4518, 8, 'أَمۡ یَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَیۡتُهُۥ فَلَا تَمۡلِكُونَ لِی مِنَ ٱللَّهِ شَیۡـًٔاۖ هُوَ أَعۡلَمُ بِمَا تُفِیضُونَ فِیهِۚ كَفَىٰ بِهِۦ شَهِیدَۢا بَیۡنِی وَبَیۡنَكُمۡۖ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:10:00'),
(1785, 46, 4519, 9, 'قُلۡ مَا كُنتُ بِدۡعࣰا مِّنَ ٱلرُّسُلِ وَمَاۤ أَدۡرِی مَا یُفۡعَلُ بِی وَلَا بِكُمۡۖ إِنۡ أَتَّبِعُ إِلَّا مَا یُوحَىٰۤ إِلَیَّ وَمَاۤ أَنَا۠ إِلَّا نَذِیرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:23:19', '2023-05-27 11:10:00'),
(1786, 46, 4520, 10, 'قُلۡ أَرَءَیۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ وَكَفَرۡتُم بِهِۦ وَشَهِدَ شَاهِدࣱ مِّنۢ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ عَلَىٰ مِثۡلِهِۦ فَـَٔامَنَ وَٱسۡتَكۡبَرۡتُمۡۚ إِنَّ ٱللَّهَ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1787, 46, 4521, 11, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لِلَّذِینَ ءَامَنُوا۟ لَوۡ كَانَ خَیۡرࣰا مَّا سَبَقُونَاۤ إِلَیۡهِۚ وَإِذۡ لَمۡ یَهۡتَدُوا۟ بِهِۦ فَسَیَقُولُونَ هَـٰذَاۤ إِفۡكࣱ قَدِیمࣱ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1788, 46, 4522, 12, 'وَمِن قَبۡلِهِۦ كِتَـٰبُ مُوسَىٰۤ إِمَامࣰا وَرَحۡمَةࣰۚ وَهَـٰذَا كِتَـٰبࣱ مُّصَدِّقࣱ لِّسَانًا عَرَبِیࣰّا لِّیُنذِرَ ٱلَّذِینَ ظَلَمُوا۟ وَبُشۡرَىٰ لِلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1789, 46, 4523, 13, 'إِنَّ ٱلَّذِینَ قَالُوا۟ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَـٰمُوا۟ فَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1790, 46, 4524, 14, 'أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَنَّةِ خَـٰلِدِینَ فِیهَا جَزَاۤءَۢ بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1791, 46, 4525, 15, 'وَوَصَّیۡنَا ٱلۡإِنسَـٰنَ بِوَ ٰ⁠لِدَیۡهِ إِحۡسَـٰنًاۖ حَمَلَتۡهُ أُمُّهُۥ كُرۡهࣰا وَوَضَعَتۡهُ كُرۡهࣰاۖ وَحَمۡلُهُۥ وَفِصَـٰلُهُۥ ثَلَـٰثُونَ شَهۡرًاۚ حَتَّىٰۤ إِذَا بَلَغَ أَشُدَّهُۥ وَبَلَغَ أَرۡبَعِینَ سَنَةࣰ قَالَ رَبِّ أَوۡزِعۡنِیۤ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِیۤ أَنۡعَمۡتَ عَلَیَّ وَعَلَىٰ وَ ٰ⁠لِدَیَّ وَأَنۡ أَعۡمَلَ صَـٰلِحࣰا تَرۡضَىٰهُ وَأَصۡلِحۡ لِی فِی ذُرِّیَّتِیۤۖ إِنِّی تُبۡتُ إِلَیۡكَ وَإِنِّی مِنَ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1792, 46, 4526, 16, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ نَتَقَبَّلُ عَنۡهُمۡ أَحۡسَنَ مَا عَمِلُوا۟ وَنَتَجَاوَزُ عَن سَیِّـَٔاتِهِمۡ فِیۤ أَصۡحَـٰبِ ٱلۡجَنَّةِۖ وَعۡدَ ٱلصِّدۡقِ ٱلَّذِی كَانُوا۟ یُوعَدُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1793, 46, 4527, 17, 'وَٱلَّذِی قَالَ لِوَ ٰ⁠لِدَیۡهِ أُفࣲّ لَّكُمَاۤ أَتَعِدَانِنِیۤ أَنۡ أُخۡرَجَ وَقَدۡ خَلَتِ ٱلۡقُرُونُ مِن قَبۡلِی وَهُمَا یَسۡتَغِیثَانِ ٱللَّهَ وَیۡلَكَ ءَامِنۡ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّ فَیَقُولُ مَا هَـٰذَاۤ إِلَّاۤ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1794, 46, 4528, 18, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ حَقَّ عَلَیۡهِمُ ٱلۡقَوۡلُ فِیۤ أُمَمࣲ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُوا۟ خَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1795, 46, 4529, 19, 'وَلِكُلࣲّ دَرَجَـٰتࣱ مِّمَّا عَمِلُوا۟ۖ وَلِیُوَفِّیَهُمۡ أَعۡمَـٰلَهُمۡ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1796, 46, 4530, 20, 'وَیَوۡمَ یُعۡرَضُ ٱلَّذِینَ كَفَرُوا۟ عَلَى ٱلنَّارِ أَذۡهَبۡتُمۡ طَیِّبَـٰتِكُمۡ فِی حَیَاتِكُمُ ٱلدُّنۡیَا وَٱسۡتَمۡتَعۡتُم بِهَا فَٱلۡیَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَسۡتَكۡبِرُونَ فِی ٱلۡأَرۡضِ بِغَیۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَفۡسُقُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1797, 46, 4531, 21, '۞ وَٱذۡكُرۡ أَخَا عَادٍ إِذۡ أَنذَرَ قَوۡمَهُۥ بِٱلۡأَحۡقَافِ وَقَدۡ خَلَتِ ٱلنُّذُرُ مِنۢ بَیۡنِ یَدَیۡهِ وَمِنۡ خَلۡفِهِۦۤ أَلَّا تَعۡبُدُوۤا۟ إِلَّا ٱللَّهَ إِنِّیۤ أَخَافُ عَلَیۡكُمۡ عَذَابَ یَوۡمٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1798, 46, 4532, 22, 'قَالُوۤا۟ أَجِئۡتَنَا لِتَأۡفِكَنَا عَنۡ ءَالِهَتِنَا فَأۡتِنَا بِمَا تَعِدُنَاۤ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1799, 46, 4533, 23, 'قَالَ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَأُبَلِّغُكُم مَّاۤ أُرۡسِلۡتُ بِهِۦ وَلَـٰكِنِّیۤ أَرَىٰكُمۡ قَوۡمࣰا تَجۡهَلُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:00'),
(1800, 46, 4534, 24, 'فَلَمَّا رَأَوۡهُ عَارِضࣰا مُّسۡتَقۡبِلَ أَوۡدِیَتِهِمۡ قَالُوا۟ هَـٰذَا عَارِضࣱ مُّمۡطِرُنَاۚ بَلۡ هُوَ مَا ٱسۡتَعۡجَلۡتُم بِهِۦۖ رِیحࣱ فِیهَا عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:01'),
(1801, 46, 4535, 25, 'تُدَمِّرُ كُلَّ شَیۡءِۭ بِأَمۡرِ رَبِّهَا فَأَصۡبَحُوا۟ لَا یُرَىٰۤ إِلَّا مَسَـٰكِنُهُمۡۚ كَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡقَوۡمَ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:01'),
(1802, 46, 4536, 26, 'وَلَقَدۡ مَكَّنَّـٰهُمۡ فِیمَاۤ إِن مَّكَّنَّـٰكُمۡ فِیهِ وَجَعَلۡنَا لَهُمۡ سَمۡعࣰا وَأَبۡصَـٰرࣰا وَأَفۡـِٔدَةࣰ فَمَاۤ أَغۡنَىٰ عَنۡهُمۡ سَمۡعُهُمۡ وَلَاۤ أَبۡصَـٰرُهُمۡ وَلَاۤ أَفۡـِٔدَتُهُم مِّن شَیۡءٍ إِذۡ كَانُوا۟ یَجۡحَدُونَ بِـَٔایَـٰتِ ٱللَّهِ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:01'),
(1803, 46, 4537, 27, 'وَلَقَدۡ أَهۡلَكۡنَا مَا حَوۡلَكُم مِّنَ ٱلۡقُرَىٰ وَصَرَّفۡنَا ٱلۡـَٔایَـٰتِ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:01'),
(1804, 46, 4538, 28, 'فَلَوۡلَا نَصَرَهُمُ ٱلَّذِینَ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ قُرۡبَانًا ءَالِهَةَۢۖ بَلۡ ضَلُّوا۟ عَنۡهُمۡۚ وَذَ ٰ⁠لِكَ إِفۡكُهُمۡ وَمَا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:01'),
(1805, 46, 4539, 29, 'وَإِذۡ صَرَفۡنَاۤ إِلَیۡكَ نَفَرࣰا مِّنَ ٱلۡجِنِّ یَسۡتَمِعُونَ ٱلۡقُرۡءَانَ فَلَمَّا حَضَرُوهُ قَالُوۤا۟ أَنصِتُوا۟ۖ فَلَمَّا قُضِیَ وَلَّوۡا۟ إِلَىٰ قَوۡمِهِم مُّنذِرِینَ\n', NULL, 0, '2023-05-26 17:23:20', '2023-05-27 11:10:01'),
(1806, 46, 4540, 30, 'قَالُوا۟ یَـٰقَوۡمَنَاۤ إِنَّا سَمِعۡنَا كِتَـٰبًا أُنزِلَ مِنۢ بَعۡدِ مُوسَىٰ مُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیۡهِ یَهۡدِیۤ إِلَى ٱلۡحَقِّ وَإِلَىٰ طَرِیقࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:23:21', '2023-05-27 11:10:01'),
(1807, 46, 4541, 31, 'یَـٰقَوۡمَنَاۤ أَجِیبُوا۟ دَاعِیَ ٱللَّهِ وَءَامِنُوا۟ بِهِۦ یَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَیُجِرۡكُم مِّنۡ عَذَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:23:21', '2023-05-27 11:10:01'),
(1808, 46, 4542, 32, 'وَمَن لَّا یُجِبۡ دَاعِیَ ٱللَّهِ فَلَیۡسَ بِمُعۡجِزࣲ فِی ٱلۡأَرۡضِ وَلَیۡسَ لَهُۥ مِن دُونِهِۦۤ أَوۡلِیَاۤءُۚ أُو۟لَـٰۤىِٕكَ فِی ضَلَـٰلࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:23:21', '2023-05-27 11:10:01'),
(1809, 46, 4543, 33, 'أَوَلَمۡ یَرَوۡا۟ أَنَّ ٱللَّهَ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَلَمۡ یَعۡیَ بِخَلۡقِهِنَّ بِقَـٰدِرٍ عَلَىٰۤ أَن یُحۡـِۧیَ ٱلۡمَوۡتَىٰۚ بَلَىٰۤۚ إِنَّهُۥ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:23:21', '2023-05-27 11:10:01'),
(1810, 46, 4544, 34, 'وَیَوۡمَ یُعۡرَضُ ٱلَّذِینَ كَفَرُوا۟ عَلَى ٱلنَّارِ أَلَیۡسَ هَـٰذَا بِٱلۡحَقِّۖ قَالُوا۟ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُوا۟ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:23:21', '2023-05-27 11:10:01'),
(1811, 46, 4545, 35, 'فَٱصۡبِرۡ كَمَا صَبَرَ أُو۟لُوا۟ ٱلۡعَزۡمِ مِنَ ٱلرُّسُلِ وَلَا تَسۡتَعۡجِل لَّهُمۡۚ كَأَنَّهُمۡ یَوۡمَ یَرَوۡنَ مَا یُوعَدُونَ لَمۡ یَلۡبَثُوۤا۟ إِلَّا سَاعَةࣰ مِّن نَّهَارِۭۚ بَلَـٰغࣱۚ فَهَلۡ یُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:23:21', '2023-05-27 11:10:01'),
(1812, 45, 4474, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ حمۤ\n', NULL, 0, '2023-05-26 17:23:26', '2023-05-27 11:10:02'),
(1813, 45, 4475, 2, 'تَنزِیلُ ٱلۡكِتَـٰبِ مِنَ ٱللَّهِ ٱلۡعَزِیزِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:23:26', '2023-05-27 11:10:02'),
(1814, 45, 4476, 3, 'إِنَّ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ لَـَٔایَـٰتࣲ لِّلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:23:26', '2023-05-27 11:10:02'),
(1815, 45, 4477, 4, 'وَفِی خَلۡقِكُمۡ وَمَا یَبُثُّ مِن دَاۤبَّةٍ ءَایَـٰتࣱ لِّقَوۡمࣲ یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:23:26', '2023-05-27 11:10:03'),
(1816, 45, 4478, 5, 'وَٱخۡتِلَـٰفِ ٱلَّیۡلِ وَٱلنَّهَارِ وَمَاۤ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَاۤءِ مِن رِّزۡقࣲ فَأَحۡیَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَتَصۡرِیفِ ٱلرِّیَـٰحِ ءَایَـٰتࣱ لِّقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:23:26', '2023-05-27 11:10:03'),
(1817, 45, 4479, 6, 'تِلۡكَ ءَایَـٰتُ ٱللَّهِ نَتۡلُوهَا عَلَیۡكَ بِٱلۡحَقِّۖ فَبِأَیِّ حَدِیثِۭ بَعۡدَ ٱللَّهِ وَءَایَـٰتِهِۦ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:23:26', '2023-05-27 11:10:03'),
(1818, 45, 4480, 7, 'وَیۡلࣱ لِّكُلِّ أَفَّاكٍ أَثِیمࣲ\n', NULL, 0, '2023-05-26 17:23:26', '2023-05-27 11:10:03'),
(1819, 45, 4481, 8, 'یَسۡمَعُ ءَایَـٰتِ ٱللَّهِ تُتۡلَىٰ عَلَیۡهِ ثُمَّ یُصِرُّ مُسۡتَكۡبِرࣰا كَأَن لَّمۡ یَسۡمَعۡهَاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1820, 45, 4482, 9, 'وَإِذَا عَلِمَ مِنۡ ءَایَـٰتِنَا شَیۡـًٔا ٱتَّخَذَهَا هُزُوًاۚ أُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1821, 45, 4483, 10, 'مِّن وَرَاۤىِٕهِمۡ جَهَنَّمُۖ وَلَا یُغۡنِی عَنۡهُم مَّا كَسَبُوا۟ شَیۡـࣰٔا وَلَا مَا ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ أَوۡلِیَاۤءَۖ وَلَهُمۡ عَذَابٌ عَظِیمٌ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1822, 45, 4484, 11, 'هَـٰذَا هُدࣰىۖ وَٱلَّذِینَ كَفَرُوا۟ بِـَٔایَـٰتِ رَبِّهِمۡ لَهُمۡ عَذَابࣱ مِّن رِّجۡزٍ أَلِیمٌ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1823, 45, 4485, 12, '۞ ٱللَّهُ ٱلَّذِی سَخَّرَ لَكُمُ ٱلۡبَحۡرَ لِتَجۡرِیَ ٱلۡفُلۡكُ فِیهِ بِأَمۡرِهِۦ وَلِتَبۡتَغُوا۟ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1824, 45, 4486, 13, 'وَسَخَّرَ لَكُم مَّا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِ جَمِیعࣰا مِّنۡهُۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1825, 45, 4487, 14, 'قُل لِّلَّذِینَ ءَامَنُوا۟ یَغۡفِرُوا۟ لِلَّذِینَ لَا یَرۡجُونَ أَیَّامَ ٱللَّهِ لِیَجۡزِیَ قَوۡمَۢا بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1826, 45, 4488, 15, 'مَنۡ عَمِلَ صَـٰلِحࣰا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَاۤءَ فَعَلَیۡهَاۖ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1827, 45, 4489, 16, 'وَلَقَدۡ ءَاتَیۡنَا بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱلۡكِتَـٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ وَرَزَقۡنَـٰهُم مِّنَ ٱلطَّیِّبَـٰتِ وَفَضَّلۡنَـٰهُمۡ عَلَى ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1828, 45, 4490, 17, 'وَءَاتَیۡنَـٰهُم بَیِّنَـٰتࣲ مِّنَ ٱلۡأَمۡرِۖ فَمَا ٱخۡتَلَفُوۤا۟ إِلَّا مِنۢ بَعۡدِ مَا جَاۤءَهُمُ ٱلۡعِلۡمُ بَغۡیَۢا بَیۡنَهُمۡۚ إِنَّ رَبَّكَ یَقۡضِی بَیۡنَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ فِیمَا كَانُوا۟ فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1829, 45, 4491, 18, 'ثُمَّ جَعَلۡنَـٰكَ عَلَىٰ شَرِیعَةࣲ مِّنَ ٱلۡأَمۡرِ فَٱتَّبِعۡهَا وَلَا تَتَّبِعۡ أَهۡوَاۤءَ ٱلَّذِینَ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1830, 45, 4492, 19, 'إِنَّهُمۡ لَن یُغۡنُوا۟ عَنكَ مِنَ ٱللَّهِ شَیۡـࣰٔاۚ وَإِنَّ ٱلظَّـٰلِمِینَ بَعۡضُهُمۡ أَوۡلِیَاۤءُ بَعۡضࣲۖ وَٱللَّهُ وَلِیُّ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1831, 45, 4493, 20, 'هَـٰذَا بَصَـٰۤىِٕرُ لِلنَّاسِ وَهُدࣰى وَرَحۡمَةࣱ لِّقَوۡمࣲ یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1832, 45, 4494, 21, 'أَمۡ حَسِبَ ٱلَّذِینَ ٱجۡتَرَحُوا۟ ٱلسَّیِّـَٔاتِ أَن نَّجۡعَلَهُمۡ كَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ سَوَاۤءࣰ مَّحۡیَاهُمۡ وَمَمَاتُهُمۡۚ سَاۤءَ مَا یَحۡكُمُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:03'),
(1833, 45, 4495, 22, 'وَخَلَقَ ٱللَّهُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَلِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:04'),
(1834, 45, 4496, 23, 'أَفَرَءَیۡتَ مَنِ ٱتَّخَذَ إِلَـٰهَهُۥ هَوَىٰهُ وَأَضَلَّهُ ٱللَّهُ عَلَىٰ عِلۡمࣲ وَخَتَمَ عَلَىٰ سَمۡعِهِۦ وَقَلۡبِهِۦ وَجَعَلَ عَلَىٰ بَصَرِهِۦ غِشَـٰوَةࣰ فَمَن یَهۡدِیهِ مِنۢ بَعۡدِ ٱللَّهِۚ أَفَلَا تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:04'),
(1835, 45, 4497, 24, 'وَقَالُوا۟ مَا هِیَ إِلَّا حَیَاتُنَا ٱلدُّنۡیَا نَمُوتُ وَنَحۡیَا وَمَا یُهۡلِكُنَاۤ إِلَّا ٱلدَّهۡرُۚ وَمَا لَهُم بِذَ ٰ⁠لِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا یَظُنُّونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:04'),
(1836, 45, 4498, 25, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَـٰتُنَا بَیِّنَـٰتࣲ مَّا كَانَ حُجَّتَهُمۡ إِلَّاۤ أَن قَالُوا۟ ٱئۡتُوا۟ بِـَٔابَاۤىِٕنَاۤ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:04'),
(1837, 45, 4499, 26, 'قُلِ ٱللَّهُ یُحۡیِیكُمۡ ثُمَّ یُمِیتُكُمۡ ثُمَّ یَجۡمَعُكُمۡ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ لَا رَیۡبَ فِیهِ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:04'),
(1838, 45, 4500, 27, 'وَلِلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَیَوۡمَ تَقُومُ ٱلسَّاعَةُ یَوۡمَىِٕذࣲ یَخۡسَرُ ٱلۡمُبۡطِلُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:04'),
(1839, 45, 4501, 28, 'وَتَرَىٰ كُلَّ أُمَّةࣲ جَاثِیَةࣰۚ كُلُّ أُمَّةࣲ تُدۡعَىٰۤ إِلَىٰ كِتَـٰبِهَا ٱلۡیَوۡمَ تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:23:27', '2023-05-27 11:10:04'),
(1840, 45, 4502, 29, 'هَـٰذَا كِتَـٰبُنَا یَنطِقُ عَلَیۡكُم بِٱلۡحَقِّۚ إِنَّا كُنَّا نَسۡتَنسِخُ مَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1841, 45, 4503, 30, 'فَأَمَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَیُدۡخِلُهُمۡ رَبُّهُمۡ فِی رَحۡمَتِهِۦۚ ذَ ٰ⁠لِكَ هُوَ ٱلۡفَوۡزُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1842, 45, 4504, 31, 'وَأَمَّا ٱلَّذِینَ كَفَرُوۤا۟ أَفَلَمۡ تَكُنۡ ءَایَـٰتِی تُتۡلَىٰ عَلَیۡكُمۡ فَٱسۡتَكۡبَرۡتُمۡ وَكُنتُمۡ قَوۡمࣰا مُّجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1843, 45, 4505, 32, 'وَإِذَا قِیلَ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّ وَٱلسَّاعَةُ لَا رَیۡبَ فِیهَا قُلۡتُم مَّا نَدۡرِی مَا ٱلسَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنࣰّا وَمَا نَحۡنُ بِمُسۡتَیۡقِنِینَ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1844, 45, 4506, 33, 'وَبَدَا لَهُمۡ سَیِّـَٔاتُ مَا عَمِلُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1845, 45, 4507, 34, 'وَقِیلَ ٱلۡیَوۡمَ نَنسَىٰكُمۡ كَمَا نَسِیتُمۡ لِقَاۤءَ یَوۡمِكُمۡ هَـٰذَا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِینَ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1846, 45, 4508, 35, 'ذَ ٰ⁠لِكُم بِأَنَّكُمُ ٱتَّخَذۡتُمۡ ءَایَـٰتِ ٱللَّهِ هُزُوࣰا وَغَرَّتۡكُمُ ٱلۡحَیَوٰةُ ٱلدُّنۡیَاۚ فَٱلۡیَوۡمَ لَا یُخۡرَجُونَ مِنۡهَا وَلَا هُمۡ یُسۡتَعۡتَبُونَ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1847, 45, 4509, 36, 'فَلِلَّهِ ٱلۡحَمۡدُ رَبِّ ٱلسَّمَـٰوَ ٰ⁠تِ وَرَبِّ ٱلۡأَرۡضِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1848, 45, 4510, 37, 'وَلَهُ ٱلۡكِبۡرِیَاۤءُ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:23:28', '2023-05-27 11:10:04'),
(1849, 44, 4415, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ حمۤ\n', NULL, 0, '2023-05-26 17:23:44', '2023-05-27 11:10:23'),
(1850, 44, 4416, 2, 'وَٱلۡكِتَـٰبِ ٱلۡمُبِینِ\n', NULL, 0, '2023-05-26 17:23:44', '2023-05-27 11:10:23'),
(1851, 44, 4417, 3, 'إِنَّاۤ أَنزَلۡنَـٰهُ فِی لَیۡلَةࣲ مُّبَـٰرَكَةٍۚ إِنَّا كُنَّا مُنذِرِینَ\n', NULL, 0, '2023-05-26 17:23:44', '2023-05-27 11:10:23'),
(1852, 44, 4418, 4, 'فِیهَا یُفۡرَقُ كُلُّ أَمۡرٍ حَكِیمٍ\n', NULL, 0, '2023-05-26 17:23:44', '2023-05-27 11:10:23'),
(1853, 44, 4419, 5, 'أَمۡرࣰا مِّنۡ عِندِنَاۤۚ إِنَّا كُنَّا مُرۡسِلِینَ\n', NULL, 0, '2023-05-26 17:23:44', '2023-05-27 11:10:23'),
(1854, 44, 4420, 6, 'رَحۡمَةࣰ مِّن رَّبِّكَۚ إِنَّهُۥ هُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:23:44', '2023-05-27 11:10:23'),
(1855, 44, 4421, 7, 'رَبِّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَاۤۖ إِن كُنتُم مُّوقِنِینَ\n', NULL, 0, '2023-05-26 17:23:44', '2023-05-27 11:10:23'),
