(1920, 43, 4338, 13, 'لِتَسۡتَوُۥا۟ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذۡكُرُوا۟ نِعۡمَةَ رَبِّكُمۡ إِذَا ٱسۡتَوَیۡتُمۡ عَلَیۡهِ وَتَقُولُوا۟ سُبۡحَـٰنَ ٱلَّذِی سَخَّرَ لَنَا هَـٰذَا وَمَا كُنَّا لَهُۥ مُقۡرِنِینَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1921, 43, 4339, 14, 'وَإِنَّاۤ إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1922, 43, 4340, 15, 'وَجَعَلُوا۟ لَهُۥ مِنۡ عِبَادِهِۦ جُزۡءًاۚ إِنَّ ٱلۡإِنسَـٰنَ لَكَفُورࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1923, 43, 4341, 16, 'أَمِ ٱتَّخَذَ مِمَّا یَخۡلُقُ بَنَاتࣲ وَأَصۡفَىٰكُم بِٱلۡبَنِینَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1924, 43, 4342, 17, 'وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحۡمَـٰنِ مَثَلࣰا ظَلَّ وَجۡهُهُۥ مُسۡوَدࣰّا وَهُوَ كَظِیمٌ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1925, 43, 4343, 18, 'أَوَمَن یُنَشَّؤُا۟ فِی ٱلۡحِلۡیَةِ وَهُوَ فِی ٱلۡخِصَامِ غَیۡرُ مُبِینࣲ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1926, 43, 4344, 19, 'وَجَعَلُوا۟ ٱلۡمَلَـٰۤىِٕكَةَ ٱلَّذِینَ هُمۡ عِبَـٰدُ ٱلرَّحۡمَـٰنِ إِنَـٰثًاۚ أَشَهِدُوا۟ خَلۡقَهُمۡۚ سَتُكۡتَبُ شَهَـٰدَتُهُمۡ وَیُسۡـَٔلُونَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1927, 43, 4345, 20, 'وَقَالُوا۟ لَوۡ شَاۤءَ ٱلرَّحۡمَـٰنُ مَا عَبَدۡنَـٰهُمۗ مَّا لَهُم بِذَ ٰ⁠لِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا یَخۡرُصُونَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1928, 43, 4346, 21, 'أَمۡ ءَاتَیۡنَـٰهُمۡ كِتَـٰبࣰا مِّن قَبۡلِهِۦ فَهُم بِهِۦ مُسۡتَمۡسِكُونَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1929, 43, 4347, 22, 'بَلۡ قَالُوۤا۟ إِنَّا وَجَدۡنَاۤ ءَابَاۤءَنَا عَلَىٰۤ أُمَّةࣲ وَإِنَّا عَلَىٰۤ ءَاثَـٰرِهِم مُّهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1930, 43, 4348, 23, 'وَكَذَ ٰ⁠لِكَ مَاۤ أَرۡسَلۡنَا مِن قَبۡلِكَ فِی قَرۡیَةࣲ مِّن نَّذِیرٍ إِلَّا قَالَ مُتۡرَفُوهَاۤ إِنَّا وَجَدۡنَاۤ ءَابَاۤءَنَا عَلَىٰۤ أُمَّةࣲ وَإِنَّا عَلَىٰۤ ءَاثَـٰرِهِم مُّقۡتَدُونَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1931, 43, 4349, 24, '۞ قَـٰلَ أَوَلَوۡ جِئۡتُكُم بِأَهۡدَىٰ مِمَّا وَجَدتُّمۡ عَلَیۡهِ ءَابَاۤءَكُمۡۖ قَالُوۤا۟ إِنَّا بِمَاۤ أُرۡسِلۡتُم بِهِۦ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:24:24', '2023-05-27 11:10:29'),
(1932, 43, 4350, 25, 'فَٱنتَقَمۡنَا مِنۡهُمۡۖ فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1933, 43, 4351, 26, 'وَإِذۡ قَالَ إِبۡرَ ٰ⁠هِیمُ لِأَبِیهِ وَقَوۡمِهِۦۤ إِنَّنِی بَرَاۤءࣱ مِّمَّا تَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1934, 43, 4352, 27, 'إِلَّا ٱلَّذِی فَطَرَنِی فَإِنَّهُۥ سَیَهۡدِینِ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1935, 43, 4353, 28, 'وَجَعَلَهَا كَلِمَةَۢ بَاقِیَةࣰ فِی عَقِبِهِۦ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1936, 43, 4354, 29, 'بَلۡ مَتَّعۡتُ هَـٰۤؤُلَاۤءِ وَءَابَاۤءَهُمۡ حَتَّىٰ جَاۤءَهُمُ ٱلۡحَقُّ وَرَسُولࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1937, 43, 4355, 30, 'وَلَمَّا جَاۤءَهُمُ ٱلۡحَقُّ قَالُوا۟ هَـٰذَا سِحۡرࣱ وَإِنَّا بِهِۦ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1938, 43, 4356, 31, 'وَقَالُوا۟ لَوۡلَا نُزِّلَ هَـٰذَا ٱلۡقُرۡءَانُ عَلَىٰ رَجُلࣲ مِّنَ ٱلۡقَرۡیَتَیۡنِ عَظِیمٍ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1939, 43, 4357, 32, 'أَهُمۡ یَقۡسِمُونَ رَحۡمَتَ رَبِّكَۚ نَحۡنُ قَسَمۡنَا بَیۡنَهُم مَّعِیشَتَهُمۡ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۚ وَرَفَعۡنَا بَعۡضَهُمۡ فَوۡقَ بَعۡضࣲ دَرَجَـٰتࣲ لِّیَتَّخِذَ بَعۡضُهُم بَعۡضࣰا سُخۡرِیࣰّاۗ وَرَحۡمَتُ رَبِّكَ خَیۡرࣱ مِّمَّا یَجۡمَعُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1940, 43, 4358, 33, 'وَلَوۡلَاۤ أَن یَكُونَ ٱلنَّاسُ أُمَّةࣰ وَ ٰ⁠حِدَةࣰ لَّجَعَلۡنَا لِمَن یَكۡفُرُ بِٱلرَّحۡمَـٰنِ لِبُیُوتِهِمۡ سُقُفࣰا مِّن فِضَّةࣲ وَمَعَارِجَ عَلَیۡهَا یَظۡهَرُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1941, 43, 4359, 34, 'وَلِبُیُوتِهِمۡ أَبۡوَ ٰ⁠بࣰا وَسُرُرًا عَلَیۡهَا یَتَّكِـُٔونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1942, 43, 4360, 35, 'وَزُخۡرُفࣰاۚ وَإِن كُلُّ ذَ ٰ⁠لِكَ لَمَّا مَتَـٰعُ ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۚ وَٱلۡـَٔاخِرَةُ عِندَ رَبِّكَ لِلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1943, 43, 4361, 36, 'وَمَن یَعۡشُ عَن ذِكۡرِ ٱلرَّحۡمَـٰنِ نُقَیِّضۡ لَهُۥ شَیۡطَـٰنࣰا فَهُوَ لَهُۥ قَرِینࣱ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1944, 43, 4362, 37, 'وَإِنَّهُمۡ لَیَصُدُّونَهُمۡ عَنِ ٱلسَّبِیلِ وَیَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1945, 43, 4363, 38, 'حَتَّىٰۤ إِذَا جَاۤءَنَا قَالَ یَـٰلَیۡتَ بَیۡنِی وَبَیۡنَكَ بُعۡدَ ٱلۡمَشۡرِقَیۡنِ فَبِئۡسَ ٱلۡقَرِینُ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1946, 43, 4364, 39, 'وَلَن یَنفَعَكُمُ ٱلۡیَوۡمَ إِذ ظَّلَمۡتُمۡ أَنَّكُمۡ فِی ٱلۡعَذَابِ مُشۡتَرِكُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1947, 43, 4365, 40, 'أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ أَوۡ تَهۡدِی ٱلۡعُمۡیَ وَمَن كَانَ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1948, 43, 4366, 41, 'فَإِمَّا نَذۡهَبَنَّ بِكَ فَإِنَّا مِنۡهُم مُّنتَقِمُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1949, 43, 4367, 42, 'أَوۡ نُرِیَنَّكَ ٱلَّذِی وَعَدۡنَـٰهُمۡ فَإِنَّا عَلَیۡهِم مُّقۡتَدِرُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1950, 43, 4368, 43, 'فَٱسۡتَمۡسِكۡ بِٱلَّذِیۤ أُوحِیَ إِلَیۡكَۖ إِنَّكَ عَلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:30'),
(1951, 43, 4369, 44, 'وَإِنَّهُۥ لَذِكۡرࣱ لَّكَ وَلِقَوۡمِكَۖ وَسَوۡفَ تُسۡـَٔلُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:31'),
(1952, 43, 4370, 45, 'وَسۡـَٔلۡ مَنۡ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رُّسُلِنَاۤ أَجَعَلۡنَا مِن دُونِ ٱلرَّحۡمَـٰنِ ءَالِهَةࣰ یُعۡبَدُونَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:31'),
(1953, 43, 4371, 46, 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔایَـٰتِنَاۤ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِی۟هِۦ فَقَالَ إِنِّی رَسُولُ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:24:25', '2023-05-27 11:10:31'),
(1954, 43, 4372, 47, 'فَلَمَّا جَاۤءَهُم بِـَٔایَـٰتِنَاۤ إِذَا هُم مِّنۡهَا یَضۡحَكُونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1955, 43, 4373, 48, 'وَمَا نُرِیهِم مِّنۡ ءَایَةٍ إِلَّا هِیَ أَكۡبَرُ مِنۡ أُخۡتِهَاۖ وَأَخَذۡنَـٰهُم بِٱلۡعَذَابِ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1956, 43, 4374, 49, 'وَقَالُوا۟ یَـٰۤأَیُّهَ ٱلسَّاحِرُ ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1957, 43, 4375, 50, 'فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِذَا هُمۡ یَنكُثُونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1958, 43, 4376, 51, 'وَنَادَىٰ فِرۡعَوۡنُ فِی قَوۡمِهِۦ قَالَ یَـٰقَوۡمِ أَلَیۡسَ لِی مُلۡكُ مِصۡرَ وَهَـٰذِهِ ٱلۡأَنۡهَـٰرُ تَجۡرِی مِن تَحۡتِیۤۚ أَفَلَا تُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1959, 43, 4377, 52, 'أَمۡ أَنَا۠ خَیۡرࣱ مِّنۡ هَـٰذَا ٱلَّذِی هُوَ مَهِینࣱ وَلَا یَكَادُ یُبِینُ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1960, 43, 4378, 53, 'فَلَوۡلَاۤ أُلۡقِیَ عَلَیۡهِ أَسۡوِرَةࣱ مِّن ذَهَبٍ أَوۡ جَاۤءَ مَعَهُ ٱلۡمَلَـٰۤىِٕكَةُ مُقۡتَرِنِینَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1961, 43, 4379, 54, 'فَٱسۡتَخَفَّ قَوۡمَهُۥ فَأَطَاعُوهُۚ إِنَّهُمۡ كَانُوا۟ قَوۡمࣰا فَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1962, 43, 4380, 55, 'فَلَمَّاۤ ءَاسَفُونَا ٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَـٰهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1963, 43, 4381, 56, 'فَجَعَلۡنَـٰهُمۡ سَلَفࣰا وَمَثَلࣰا لِّلۡـَٔاخِرِینَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1964, 43, 4382, 57, '۞ وَلَمَّا ضُرِبَ ٱبۡنُ مَرۡیَمَ مَثَلًا إِذَا قَوۡمُكَ مِنۡهُ یَصِدُّونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1965, 43, 4383, 58, 'وَقَالُوۤا۟ ءَأَ ٰ⁠لِهَتُنَا خَیۡرٌ أَمۡ هُوَۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلَۢاۚ بَلۡ هُمۡ قَوۡمٌ خَصِمُونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1966, 43, 4384, 59, 'إِنۡ هُوَ إِلَّا عَبۡدٌ أَنۡعَمۡنَا عَلَیۡهِ وَجَعَلۡنَـٰهُ مَثَلࣰا لِّبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
(1967, 43, 4385, 60, 'وَلَوۡ نَشَاۤءُ لَجَعَلۡنَا مِنكُم مَّلَـٰۤىِٕكَةࣰ فِی ٱلۡأَرۡضِ یَخۡلُفُونَ\n', NULL, 0, '2023-05-26 17:24:26', '2023-05-27 11:10:31'),
