(2011, 42, 4287, 15, 'فَلِذَ ٰ⁠لِكَ فَٱدۡعُۖ وَٱسۡتَقِمۡ كَمَاۤ أُمِرۡتَۖ وَلَا تَتَّبِعۡ أَهۡوَاۤءَهُمۡۖ وَقُلۡ ءَامَنتُ بِمَاۤ أَنزَلَ ٱللَّهُ مِن كِتَـٰبࣲۖ وَأُمِرۡتُ لِأَعۡدِلَ بَیۡنَكُمُۖ ٱللَّهُ رَبُّنَا وَرَبُّكُمۡۖ لَنَاۤ أَعۡمَـٰلُنَا وَلَكُمۡ أَعۡمَـٰلُكُمۡۖ لَا حُجَّةَ بَیۡنَنَا وَبَیۡنَكُمُۖ ٱللَّهُ یَجۡمَعُ بَیۡنَنَاۖ وَإِلَیۡهِ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:35'),
(2012, 42, 4288, 16, 'وَٱلَّذِینَ یُحَاۤجُّونَ فِی ٱللَّهِ مِنۢ بَعۡدِ مَا ٱسۡتُجِیبَ لَهُۥ حُجَّتُهُمۡ دَاحِضَةٌ عِندَ رَبِّهِمۡ وَعَلَیۡهِمۡ غَضَبࣱ وَلَهُمۡ عَذَابࣱ شَدِیدٌ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:35'),
(2013, 42, 4289, 17, 'ٱللَّهُ ٱلَّذِیۤ أَنزَلَ ٱلۡكِتَـٰبَ بِٱلۡحَقِّ وَٱلۡمِیزَانَۗ وَمَا یُدۡرِیكَ لَعَلَّ ٱلسَّاعَةَ قَرِیبࣱ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:35'),
(2014, 42, 4290, 18, 'یَسۡتَعۡجِلُ بِهَا ٱلَّذِینَ لَا یُؤۡمِنُونَ بِهَاۖ وَٱلَّذِینَ ءَامَنُوا۟ مُشۡفِقُونَ مِنۡهَا وَیَعۡلَمُونَ أَنَّهَا ٱلۡحَقُّۗ أَلَاۤ إِنَّ ٱلَّذِینَ یُمَارُونَ فِی ٱلسَّاعَةِ لَفِی ضَلَـٰلِۭ بَعِیدٍ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2015, 42, 4291, 19, 'ٱللَّهُ لَطِیفُۢ بِعِبَادِهِۦ یَرۡزُقُ مَن یَشَاۤءُۖ وَهُوَ ٱلۡقَوِیُّ ٱلۡعَزِیزُ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2016, 42, 4292, 20, 'مَن كَانَ یُرِیدُ حَرۡثَ ٱلۡـَٔاخِرَةِ نَزِدۡ لَهُۥ فِی حَرۡثِهِۦۖ وَمَن كَانَ یُرِیدُ حَرۡثَ ٱلدُّنۡیَا نُؤۡتِهِۦ مِنۡهَا وَمَا لَهُۥ فِی ٱلۡـَٔاخِرَةِ مِن نَّصِیبٍ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2017, 42, 4293, 21, 'أَمۡ لَهُمۡ شُرَكَـٰۤؤُا۟ شَرَعُوا۟ لَهُم مِّنَ ٱلدِّینِ مَا لَمۡ یَأۡذَنۢ بِهِ ٱللَّهُۚ وَلَوۡلَا كَلِمَةُ ٱلۡفَصۡلِ لَقُضِیَ بَیۡنَهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِینَ لَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2018, 42, 4294, 22, 'تَرَى ٱلظَّـٰلِمِینَ مُشۡفِقِینَ مِمَّا كَسَبُوا۟ وَهُوَ وَاقِعُۢ بِهِمۡۗ وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فِی رَوۡضَاتِ ٱلۡجَنَّاتِۖ لَهُم مَّا یَشَاۤءُونَ عِندَ رَبِّهِمۡۚ ذَ ٰ⁠لِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِیرُ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2019, 42, 4295, 23, 'ذَ ٰ⁠لِكَ ٱلَّذِی یُبَشِّرُ ٱللَّهُ عِبَادَهُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِۗ قُل لَّاۤ أَسۡـَٔلُكُمۡ عَلَیۡهِ أَجۡرًا إِلَّا ٱلۡمَوَدَّةَ فِی ٱلۡقُرۡبَىٰۗ وَمَن یَقۡتَرِفۡ حَسَنَةࣰ نَّزِدۡ لَهُۥ فِیهَا حُسۡنًاۚ إِنَّ ٱللَّهَ غَفُورࣱ شَكُورٌ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2020, 42, 4296, 24, 'أَمۡ یَقُولُونَ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبࣰاۖ فَإِن یَشَإِ ٱللَّهُ یَخۡتِمۡ عَلَىٰ قَلۡبِكَۗ وَیَمۡحُ ٱللَّهُ ٱلۡبَـٰطِلَ وَیُحِقُّ ٱلۡحَقَّ بِكَلِمَـٰتِهِۦۤۚ إِنَّهُۥ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2021, 42, 4297, 25, 'وَهُوَ ٱلَّذِی یَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَیَعۡفُوا۟ عَنِ ٱلسَّیِّـَٔاتِ وَیَعۡلَمُ مَا تَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2022, 42, 4298, 26, 'وَیَسۡتَجِیبُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَیَزِیدُهُم مِّن فَضۡلِهِۦۚ وَٱلۡكَـٰفِرُونَ لَهُمۡ عَذَابࣱ شَدِیدࣱ\n', NULL, 0, '2023-05-26 17:24:31', '2023-05-27 11:10:36'),
(2023, 42, 4299, 27, '۞ وَلَوۡ بَسَطَ ٱللَّهُ ٱلرِّزۡقَ لِعِبَادِهِۦ لَبَغَوۡا۟ فِی ٱلۡأَرۡضِ وَلَـٰكِن یُنَزِّلُ بِقَدَرࣲ مَّا یَشَاۤءُۚ إِنَّهُۥ بِعِبَادِهِۦ خَبِیرُۢ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2024, 42, 4300, 28, 'وَهُوَ ٱلَّذِی یُنَزِّلُ ٱلۡغَیۡثَ مِنۢ بَعۡدِ مَا قَنَطُوا۟ وَیَنشُرُ رَحۡمَتَهُۥۚ وَهُوَ ٱلۡوَلِیُّ ٱلۡحَمِیدُ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2025, 42, 4301, 29, 'وَمِنۡ ءَایَـٰتِهِۦ خَلۡقُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَثَّ فِیهِمَا مِن دَاۤبَّةࣲۚ وَهُوَ عَلَىٰ جَمۡعِهِمۡ إِذَا یَشَاۤءُ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2026, 42, 4302, 30, 'وَمَاۤ أَصَـٰبَكُم مِّن مُّصِیبَةࣲ فَبِمَا كَسَبَتۡ أَیۡدِیكُمۡ وَیَعۡفُوا۟ عَن كَثِیرࣲ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2027, 42, 4303, 31, 'وَمَاۤ أَنتُم بِمُعۡجِزِینَ فِی ٱلۡأَرۡضِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِیࣲّ وَلَا نَصِیرࣲ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2028, 42, 4304, 32, 'وَمِنۡ ءَایَـٰتِهِ ٱلۡجَوَارِ فِی ٱلۡبَحۡرِ كَٱلۡأَعۡلَـٰمِ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2029, 42, 4305, 33, 'إِن یَشَأۡ یُسۡكِنِ ٱلرِّیحَ فَیَظۡلَلۡنَ رَوَاكِدَ عَلَىٰ ظَهۡرِهِۦۤۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّكُلِّ صَبَّارࣲ شَكُورٍ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2030, 42, 4306, 34, 'أَوۡ یُوبِقۡهُنَّ بِمَا كَسَبُوا۟ وَیَعۡفُ عَن كَثِیرࣲ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2031, 42, 4307, 35, 'وَیَعۡلَمَ ٱلَّذِینَ یُجَـٰدِلُونَ فِیۤ ءَایَـٰتِنَا مَا لَهُم مِّن مَّحِیصࣲ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2032, 42, 4308, 36, 'فَمَاۤ أُوتِیتُم مِّن شَیۡءࣲ فَمَتَـٰعُ ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۚ وَمَا عِندَ ٱللَّهِ خَیۡرࣱ وَأَبۡقَىٰ لِلَّذِینَ ءَامَنُوا۟ وَعَلَىٰ رَبِّهِمۡ یَتَوَكَّلُونَ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2033, 42, 4309, 37, 'وَٱلَّذِینَ یَجۡتَنِبُونَ كَبَـٰۤىِٕرَ ٱلۡإِثۡمِ وَٱلۡفَوَ ٰ⁠حِشَ وَإِذَا مَا غَضِبُوا۟ هُمۡ یَغۡفِرُونَ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2034, 42, 4310, 38, 'وَٱلَّذِینَ ٱسۡتَجَابُوا۟ لِرَبِّهِمۡ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَمۡرُهُمۡ شُورَىٰ بَیۡنَهُمۡ وَمِمَّا رَزَقۡنَـٰهُمۡ یُنفِقُونَ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:36'),
(2035, 42, 4311, 39, 'وَٱلَّذِینَ إِذَاۤ أَصَابَهُمُ ٱلۡبَغۡیُ هُمۡ یَنتَصِرُونَ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2036, 42, 4312, 40, 'وَجَزَ ٰ⁠ۤؤُا۟ سَیِّئَةࣲ سَیِّئَةࣱ مِّثۡلُهَاۖ فَمَنۡ عَفَا وَأَصۡلَحَ فَأَجۡرُهُۥ عَلَى ٱللَّهِۚ إِنَّهُۥ لَا یُحِبُّ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2037, 42, 4313, 41, 'وَلَمَنِ ٱنتَصَرَ بَعۡدَ ظُلۡمِهِۦ فَأُو۟لَـٰۤىِٕكَ مَا عَلَیۡهِم مِّن سَبِیلٍ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2038, 42, 4314, 42, 'إِنَّمَا ٱلسَّبِیلُ عَلَى ٱلَّذِینَ یَظۡلِمُونَ ٱلنَّاسَ وَیَبۡغُونَ فِی ٱلۡأَرۡضِ بِغَیۡرِ ٱلۡحَقِّۚ أُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2039, 42, 4315, 43, 'وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَ ٰ⁠لِكَ لَمِنۡ عَزۡمِ ٱلۡأُمُورِ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2040, 42, 4316, 44, 'وَمَن یُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِیࣲّ مِّنۢ بَعۡدِهِۦۗ وَتَرَى ٱلظَّـٰلِمِینَ لَمَّا رَأَوُا۟ ٱلۡعَذَابَ یَقُولُونَ هَلۡ إِلَىٰ مَرَدࣲّ مِّن سَبِیلࣲ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2041, 42, 4317, 45, 'وَتَرَىٰهُمۡ یُعۡرَضُونَ عَلَیۡهَا خَـٰشِعِینَ مِنَ ٱلذُّلِّ یَنظُرُونَ مِن طَرۡفٍ خَفِیࣲّۗ وَقَالَ ٱلَّذِینَ ءَامَنُوۤا۟ إِنَّ ٱلۡخَـٰسِرِینَ ٱلَّذِینَ خَسِرُوۤا۟ أَنفُسَهُمۡ وَأَهۡلِیهِمۡ یَوۡمَ ٱلۡقِیَـٰمَةِۗ أَلَاۤ إِنَّ ٱلظَّـٰلِمِینَ فِی عَذَابࣲ مُّقِیمࣲ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(2042, 42, 4318, 46, 'وَمَا كَانَ لَهُم مِّنۡ أَوۡلِیَاۤءَ یَنصُرُونَهُم مِّن دُونِ ٱللَّهِۗ وَمَن یُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِیلٍ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2043, 42, 4319, 47, 'ٱسۡتَجِیبُوا۟ لِرَبِّكُم مِّن قَبۡلِ أَن یَأۡتِیَ یَوۡمࣱ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۚ مَا لَكُم مِّن مَّلۡجَإࣲ یَوۡمَىِٕذࣲ وَمَا لَكُم مِّن نَّكِیرࣲ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2044, 42, 4320, 48, 'فَإِنۡ أَعۡرَضُوا۟ فَمَاۤ أَرۡسَلۡنَـٰكَ عَلَیۡهِمۡ حَفِیظًاۖ إِنۡ عَلَیۡكَ إِلَّا ٱلۡبَلَـٰغُۗ وَإِنَّاۤ إِذَاۤ أَذَقۡنَا ٱلۡإِنسَـٰنَ مِنَّا رَحۡمَةࣰ فَرِحَ بِهَاۖ وَإِن تُصِبۡهُمۡ سَیِّئَةُۢ بِمَا قَدَّمَتۡ أَیۡدِیهِمۡ فَإِنَّ ٱلۡإِنسَـٰنَ كَفُورࣱ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2045, 42, 4321, 49, 'لِّلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ یَخۡلُقُ مَا یَشَاۤءُۚ یَهَبُ لِمَن یَشَاۤءُ إِنَـٰثࣰا وَیَهَبُ لِمَن یَشَاۤءُ ٱلذُّكُورَ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2046, 42, 4322, 50, 'أَوۡ یُزَوِّجُهُمۡ ذُكۡرَانࣰا وَإِنَـٰثࣰاۖ وَیَجۡعَلُ مَن یَشَاۤءُ عَقِیمًاۚ إِنَّهُۥ عَلِیمࣱ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2047, 42, 4323, 51, '۞ وَمَا كَانَ لِبَشَرٍ أَن یُكَلِّمَهُ ٱللَّهُ إِلَّا وَحۡیًا أَوۡ مِن وَرَاۤىِٕ حِجَابٍ أَوۡ یُرۡسِلَ رَسُولࣰا فَیُوحِیَ بِإِذۡنِهِۦ مَا یَشَاۤءُۚ إِنَّهُۥ عَلِیٌّ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:24:32', '2023-05-27 11:10:37'),
(2048, 42, 4324, 52, 'وَكَذَ ٰ⁠لِكَ أَوۡحَیۡنَاۤ إِلَیۡكَ رُوحࣰا مِّنۡ أَمۡرِنَاۚ مَا كُنتَ تَدۡرِی مَا ٱلۡكِتَـٰبُ وَلَا ٱلۡإِیمَـٰنُ وَلَـٰكِن جَعَلۡنَـٰهُ نُورࣰا نَّهۡدِی بِهِۦ مَن نَّشَاۤءُ مِنۡ عِبَادِنَاۚ وَإِنَّكَ لَتَهۡدِیۤ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:24:33', '2023-05-27 11:10:37'),
(2049, 42, 4325, 53, 'صِرَ ٰ⁠طِ ٱللَّهِ ٱلَّذِی لَهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۗ أَلَاۤ إِلَى ٱللَّهِ تَصِیرُ ٱلۡأُمُورُ\n', NULL, 0, '2023-05-26 17:24:33', '2023-05-27 11:10:37'),
(2050, 41, 4219, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ حمۤ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:39'),
(2051, 41, 4220, 2, 'تَنزِیلࣱ مِّنَ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:39'),
(2052, 41, 4221, 3, 'كِتَـٰبࣱ فُصِّلَتۡ ءَایَـٰتُهُۥ قُرۡءَانًا عَرَبِیࣰّا لِّقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:39'),
(2053, 41, 4222, 4, 'بَشِیرࣰا وَنَذِیرࣰا فَأَعۡرَضَ أَكۡثَرُهُمۡ فَهُمۡ لَا یَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:39'),
(2054, 41, 4223, 5, 'وَقَالُوا۟ قُلُوبُنَا فِیۤ أَكِنَّةࣲ مِّمَّا تَدۡعُونَاۤ إِلَیۡهِ وَفِیۤ ءَاذَانِنَا وَقۡرࣱ وَمِنۢ بَیۡنِنَا وَبَیۡنِكَ حِجَابࣱ فَٱعۡمَلۡ إِنَّنَا عَـٰمِلُونَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:39'),
(2055, 41, 4224, 6, 'قُلۡ إِنَّمَاۤ أَنَا۠ بَشَرࣱ مِّثۡلُكُمۡ یُوحَىٰۤ إِلَیَّ أَنَّمَاۤ إِلَـٰهُكُمۡ إِلَـٰهࣱ وَ ٰ⁠حِدࣱ فَٱسۡتَقِیمُوۤا۟ إِلَیۡهِ وَٱسۡتَغۡفِرُوهُۗ وَوَیۡلࣱ لِّلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:39'),
(2056, 41, 4225, 7, 'ٱلَّذِینَ لَا یُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡـَٔاخِرَةِ هُمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2057, 41, 4226, 8, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُمۡ أَجۡرٌ غَیۡرُ مَمۡنُونࣲ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2058, 41, 4227, 9, '۞ قُلۡ أَىِٕنَّكُمۡ لَتَكۡفُرُونَ بِٱلَّذِی خَلَقَ ٱلۡأَرۡضَ فِی یَوۡمَیۡنِ وَتَجۡعَلُونَ لَهُۥۤ أَندَادࣰاۚ ذَ ٰ⁠لِكَ رَبُّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2059, 41, 4228, 10, 'وَجَعَلَ فِیهَا رَوَ ٰ⁠سِیَ مِن فَوۡقِهَا وَبَـٰرَكَ فِیهَا وَقَدَّرَ فِیهَاۤ أَقۡوَ ٰ⁠تَهَا فِیۤ أَرۡبَعَةِ أَیَّامࣲ سَوَاۤءࣰ لِّلسَّاۤىِٕلِینَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2060, 41, 4229, 11, 'ثُمَّ ٱسۡتَوَىٰۤ إِلَى ٱلسَّمَاۤءِ وَهِیَ دُخَانࣱ فَقَالَ لَهَا وَلِلۡأَرۡضِ ٱئۡتِیَا طَوۡعًا أَوۡ كَرۡهࣰا قَالَتَاۤ أَتَیۡنَا طَاۤىِٕعِینَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2061, 41, 4230, 12, 'فَقَضَىٰهُنَّ سَبۡعَ سَمَـٰوَاتࣲ فِی یَوۡمَیۡنِ وَأَوۡحَىٰ فِی كُلِّ سَمَاۤءٍ أَمۡرَهَاۚ وَزَیَّنَّا ٱلسَّمَاۤءَ ٱلدُّنۡیَا بِمَصَـٰبِیحَ وَحِفۡظࣰاۚ ذَ ٰ⁠لِكَ تَقۡدِیرُ ٱلۡعَزِیزِ ٱلۡعَلِیمِ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2062, 41, 4231, 13, 'فَإِنۡ أَعۡرَضُوا۟ فَقُلۡ أَنذَرۡتُكُمۡ صَـٰعِقَةࣰ مِّثۡلَ صَـٰعِقَةِ عَادࣲ وَثَمُودَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2063, 41, 4232, 14, 'إِذۡ جَاۤءَتۡهُمُ ٱلرُّسُلُ مِنۢ بَیۡنِ أَیۡدِیهِمۡ وَمِنۡ خَلۡفِهِمۡ أَلَّا تَعۡبُدُوۤا۟ إِلَّا ٱللَّهَۖ قَالُوا۟ لَوۡ شَاۤءَ رَبُّنَا لَأَنزَلَ مَلَـٰۤىِٕكَةࣰ فَإِنَّا بِمَاۤ أُرۡسِلۡتُم بِهِۦ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2064, 41, 4233, 15, 'فَأَمَّا عَادࣱ فَٱسۡتَكۡبَرُوا۟ فِی ٱلۡأَرۡضِ بِغَیۡرِ ٱلۡحَقِّ وَقَالُوا۟ مَنۡ أَشَدُّ مِنَّا قُوَّةًۖ أَوَلَمۡ یَرَوۡا۟ أَنَّ ٱللَّهَ ٱلَّذِی خَلَقَهُمۡ هُوَ أَشَدُّ مِنۡهُمۡ قُوَّةࣰۖ وَكَانُوا۟ بِـَٔایَـٰتِنَا یَجۡحَدُونَ\n', NULL, 0, '2023-05-26 17:24:37', '2023-05-27 11:10:40'),
(2065, 41, 4234, 16, 'فَأَرۡسَلۡنَا عَلَیۡهِمۡ رِیحࣰا صَرۡصَرࣰا فِیۤ أَیَّامࣲ نَّحِسَاتࣲ لِّنُذِیقَهُمۡ عَذَابَ ٱلۡخِزۡیِ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ وَلَعَذَابُ ٱلۡـَٔاخِرَةِ أَخۡزَىٰۖ وَهُمۡ لَا یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2066, 41, 4235, 17, 'وَأَمَّا ثَمُودُ فَهَدَیۡنَـٰهُمۡ فَٱسۡتَحَبُّوا۟ ٱلۡعَمَىٰ عَلَى ٱلۡهُدَىٰ فَأَخَذَتۡهُمۡ صَـٰعِقَةُ ٱلۡعَذَابِ ٱلۡهُونِ بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2067, 41, 4236, 18, 'وَنَجَّیۡنَا ٱلَّذِینَ ءَامَنُوا۟ وَكَانُوا۟ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2068, 41, 4237, 19, 'وَیَوۡمَ یُحۡشَرُ أَعۡدَاۤءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمۡ یُوزَعُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2069, 41, 4238, 20, 'حَتَّىٰۤ إِذَا مَا جَاۤءُوهَا شَهِدَ عَلَیۡهِمۡ سَمۡعُهُمۡ وَأَبۡصَـٰرُهُمۡ وَجُلُودُهُم بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2070, 41, 4239, 21, 'وَقَالُوا۟ لِجُلُودِهِمۡ لِمَ شَهِدتُّمۡ عَلَیۡنَاۖ قَالُوۤا۟ أَنطَقَنَا ٱللَّهُ ٱلَّذِیۤ أَنطَقَ كُلَّ شَیۡءࣲۚ وَهُوَ خَلَقَكُمۡ أَوَّلَ مَرَّةࣲ وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2071, 41, 4240, 22, 'وَمَا كُنتُمۡ تَسۡتَتِرُونَ أَن یَشۡهَدَ عَلَیۡكُمۡ سَمۡعُكُمۡ وَلَاۤ أَبۡصَـٰرُكُمۡ وَلَا جُلُودُكُمۡ وَلَـٰكِن ظَنَنتُمۡ أَنَّ ٱللَّهَ لَا یَعۡلَمُ كَثِیرࣰا مِّمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2072, 41, 4241, 23, 'وَذَ ٰ⁠لِكُمۡ ظَنُّكُمُ ٱلَّذِی ظَنَنتُم بِرَبِّكُمۡ أَرۡدَىٰكُمۡ فَأَصۡبَحۡتُم مِّنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2073, 41, 4242, 24, 'فَإِن یَصۡبِرُوا۟ فَٱلنَّارُ مَثۡوࣰى لَّهُمۡۖ وَإِن یَسۡتَعۡتِبُوا۟ فَمَا هُم مِّنَ ٱلۡمُعۡتَبِینَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2074, 41, 4243, 25, '۞ وَقَیَّضۡنَا لَهُمۡ قُرَنَاۤءَ فَزَیَّنُوا۟ لَهُم مَّا بَیۡنَ أَیۡدِیهِمۡ وَمَا خَلۡفَهُمۡ وَحَقَّ عَلَیۡهِمُ ٱلۡقَوۡلُ فِیۤ أُمَمࣲ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُوا۟ خَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2075, 41, 4244, 26, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لَا تَسۡمَعُوا۟ لِهَـٰذَا ٱلۡقُرۡءَانِ وَٱلۡغَوۡا۟ فِیهِ لَعَلَّكُمۡ تَغۡلِبُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:40'),
(2076, 41, 4245, 27, 'فَلَنُذِیقَنَّ ٱلَّذِینَ كَفَرُوا۟ عَذَابࣰا شَدِیدࣰا وَلَنَجۡزِیَنَّهُمۡ أَسۡوَأَ ٱلَّذِی كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2077, 41, 4246, 28, 'ذَ ٰ⁠لِكَ جَزَاۤءُ أَعۡدَاۤءِ ٱللَّهِ ٱلنَّارُۖ لَهُمۡ فِیهَا دَارُ ٱلۡخُلۡدِ جَزَاۤءَۢ بِمَا كَانُوا۟ بِـَٔایَـٰتِنَا یَجۡحَدُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2078, 41, 4247, 29, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ رَبَّنَاۤ أَرِنَا ٱلَّذَیۡنِ أَضَلَّانَا مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ نَجۡعَلۡهُمَا تَحۡتَ أَقۡدَامِنَا لِیَكُونَا مِنَ ٱلۡأَسۡفَلِینَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2079, 41, 4248, 30, 'إِنَّ ٱلَّذِینَ قَالُوا۟ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَـٰمُوا۟ تَتَنَزَّلُ عَلَیۡهِمُ ٱلۡمَلَـٰۤىِٕكَةُ أَلَّا تَخَافُوا۟ وَلَا تَحۡزَنُوا۟ وَأَبۡشِرُوا۟ بِٱلۡجَنَّةِ ٱلَّتِی كُنتُمۡ تُوعَدُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2080, 41, 4249, 31, 'نَحۡنُ أَوۡلِیَاۤؤُكُمۡ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَفِی ٱلۡـَٔاخِرَةِۖ وَلَكُمۡ فِیهَا مَا تَشۡتَهِیۤ أَنفُسُكُمۡ وَلَكُمۡ فِیهَا مَا تَدَّعُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2081, 41, 4250, 32, 'نُزُلࣰا مِّنۡ غَفُورࣲ رَّحِیمࣲ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2082, 41, 4251, 33, 'وَمَنۡ أَحۡسَنُ قَوۡلࣰا مِّمَّن دَعَاۤ إِلَى ٱللَّهِ وَعَمِلَ صَـٰلِحࣰا وَقَالَ إِنَّنِی مِنَ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2083, 41, 4252, 34, 'وَلَا تَسۡتَوِی ٱلۡحَسَنَةُ وَلَا ٱلسَّیِّئَةُۚ ٱدۡفَعۡ بِٱلَّتِی هِیَ أَحۡسَنُ فَإِذَا ٱلَّذِی بَیۡنَكَ وَبَیۡنَهُۥ عَدَ ٰ⁠وَةࣱ كَأَنَّهُۥ وَلِیٌّ حَمِیمࣱ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2084, 41, 4253, 35, 'وَمَا یُلَقَّىٰهَاۤ إِلَّا ٱلَّذِینَ صَبَرُوا۟ وَمَا یُلَقَّىٰهَاۤ إِلَّا ذُو حَظٍّ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2085, 41, 4254, 36, 'وَإِمَّا یَنزَغَنَّكَ مِنَ ٱلشَّیۡطَـٰنِ نَزۡغࣱ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2086, 41, 4255, 37, 'وَمِنۡ ءَایَـٰتِهِ ٱلَّیۡلُ وَٱلنَّهَارُ وَٱلشَّمۡسُ وَٱلۡقَمَرُۚ لَا تَسۡجُدُوا۟ لِلشَّمۡسِ وَلَا لِلۡقَمَرِ وَٱسۡجُدُوا۟ لِلَّهِ ٱلَّذِی خَلَقَهُنَّ إِن كُنتُمۡ إِیَّاهُ تَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:24:38', '2023-05-27 11:10:41'),
(2087, 41, 4256, 38, 'فَإِنِ ٱسۡتَكۡبَرُوا۟ فَٱلَّذِینَ عِندَ رَبِّكَ یُسَبِّحُونَ لَهُۥ بِٱلَّیۡلِ وَٱلنَّهَارِ وَهُمۡ لَا یَسۡـَٔمُونَ ۩\n', NULL, 1, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2088, 41, 4257, 39, 'وَمِنۡ ءَایَـٰتِهِۦۤ أَنَّكَ تَرَى ٱلۡأَرۡضَ خَـٰشِعَةࣰ فَإِذَاۤ أَنزَلۡنَا عَلَیۡهَا ٱلۡمَاۤءَ ٱهۡتَزَّتۡ وَرَبَتۡۚ إِنَّ ٱلَّذِیۤ أَحۡیَاهَا لَمُحۡیِ ٱلۡمَوۡتَىٰۤۚ إِنَّهُۥ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2089, 41, 4258, 40, 'إِنَّ ٱلَّذِینَ یُلۡحِدُونَ فِیۤ ءَایَـٰتِنَا لَا یَخۡفَوۡنَ عَلَیۡنَاۤۗ أَفَمَن یُلۡقَىٰ فِی ٱلنَّارِ خَیۡرٌ أَم مَّن یَأۡتِیۤ ءَامِنࣰا یَوۡمَ ٱلۡقِیَـٰمَةِۚ ٱعۡمَلُوا۟ مَا شِئۡتُمۡ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِیرٌ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2090, 41, 4259, 41, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ بِٱلذِّكۡرِ لَمَّا جَاۤءَهُمۡۖ وَإِنَّهُۥ لَكِتَـٰبٌ عَزِیزࣱ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2091, 41, 4260, 42, 'لَّا یَأۡتِیهِ ٱلۡبَـٰطِلُ مِنۢ بَیۡنِ یَدَیۡهِ وَلَا مِنۡ خَلۡفِهِۦۖ تَنزِیلࣱ مِّنۡ حَكِیمٍ حَمِیدࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2092, 41, 4261, 43, 'مَّا یُقَالُ لَكَ إِلَّا مَا قَدۡ قِیلَ لِلرُّسُلِ مِن قَبۡلِكَۚ إِنَّ رَبَّكَ لَذُو مَغۡفِرَةࣲ وَذُو عِقَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2093, 41, 4262, 44, 'وَلَوۡ جَعَلۡنَـٰهُ قُرۡءَانًا أَعۡجَمِیࣰّا لَّقَالُوا۟ لَوۡلَا فُصِّلَتۡ ءَایَـٰتُهُۥۤۖ ءَا۬عۡجَمِیࣱّ وَعَرَبِیࣱّۗ قُلۡ هُوَ لِلَّذِینَ ءَامَنُوا۟ هُدࣰى وَشِفَاۤءࣱۚ وَٱلَّذِینَ لَا یُؤۡمِنُونَ فِیۤ ءَاذَانِهِمۡ وَقۡرࣱ وَهُوَ عَلَیۡهِمۡ عَمًىۚ أُو۟لَـٰۤىِٕكَ یُنَادَوۡنَ مِن مَّكَانِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2094, 41, 4263, 45, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ فَٱخۡتُلِفَ فِیهِۚ وَلَوۡلَا كَلِمَةࣱ سَبَقَتۡ مِن رَّبِّكَ لَقُضِیَ بَیۡنَهُمۡۚ وَإِنَّهُمۡ لَفِی شَكࣲّ مِّنۡهُ مُرِیبࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2095, 41, 4264, 46, 'مَّنۡ عَمِلَ صَـٰلِحࣰا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَاۤءَ فَعَلَیۡهَاۗ وَمَا رَبُّكَ بِظَلَّـٰمࣲ لِّلۡعَبِیدِ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:41'),
(2096, 41, 4265, 47, '۞ إِلَیۡهِ یُرَدُّ عِلۡمُ ٱلسَّاعَةِۚ وَمَا تَخۡرُجُ مِن ثَمَرَ ٰ⁠تࣲ مِّنۡ أَكۡمَامِهَا وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَیَوۡمَ یُنَادِیهِمۡ أَیۡنَ شُرَكَاۤءِی قَالُوۤا۟ ءَاذَنَّـٰكَ مَا مِنَّا مِن شَهِیدࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2097, 41, 4266, 48, 'وَضَلَّ عَنۡهُم مَّا كَانُوا۟ یَدۡعُونَ مِن قَبۡلُۖ وَظَنُّوا۟ مَا لَهُم مِّن مَّحِیصࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2098, 41, 4267, 49, 'لَّا یَسۡـَٔمُ ٱلۡإِنسَـٰنُ مِن دُعَاۤءِ ٱلۡخَیۡرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَیَـُٔوسࣱ قَنُوطࣱ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2099, 41, 4268, 50, 'وَلَىِٕنۡ أَذَقۡنَـٰهُ رَحۡمَةࣰ مِّنَّا مِنۢ بَعۡدِ ضَرَّاۤءَ مَسَّتۡهُ لَیَقُولَنَّ هَـٰذَا لِی وَمَاۤ أَظُنُّ ٱلسَّاعَةَ قَاۤىِٕمَةࣰ وَلَىِٕن رُّجِعۡتُ إِلَىٰ رَبِّیۤ إِنَّ لِی عِندَهُۥ لَلۡحُسۡنَىٰۚ فَلَنُنَبِّئَنَّ ٱلَّذِینَ كَفَرُوا۟ بِمَا عَمِلُوا۟ وَلَنُذِیقَنَّهُم مِّنۡ عَذَابٍ غَلِیظࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2100, 41, 4269, 51, 'وَإِذَاۤ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَـٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَاۤءٍ عَرِیضࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2101, 41, 4270, 52, 'قُلۡ أَرَءَیۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ ثُمَّ كَفَرۡتُم بِهِۦ مَنۡ أَضَلُّ مِمَّنۡ هُوَ فِی شِقَاقِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2102, 41, 4271, 53, 'سَنُرِیهِمۡ ءَایَـٰتِنَا فِی ٱلۡـَٔافَاقِ وَفِیۤ أَنفُسِهِمۡ حَتَّىٰ یَتَبَیَّنَ لَهُمۡ أَنَّهُ ٱلۡحَقُّۗ أَوَلَمۡ یَكۡفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَیۡءࣲ شَهِیدٌ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2103, 41, 4272, 54, 'أَلَاۤ إِنَّهُمۡ فِی مِرۡیَةࣲ مِّن لِّقَاۤءِ رَبِّهِمۡۗ أَلَاۤ إِنَّهُۥ بِكُلِّ شَیۡءࣲ مُّحِیطُۢ\n', NULL, 0, '2023-05-26 17:24:39', '2023-05-27 11:10:42'),
(2104, 40, 4134, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ حمۤ\n', NULL, 0, '2023-05-26 17:24:42', '2023-05-27 11:10:44'),
(2105, 40, 4135, 2, 'تَنزِیلُ ٱلۡكِتَـٰبِ مِنَ ٱللَّهِ ٱلۡعَزِیزِ ٱلۡعَلِیمِ\n', NULL, 0, '2023-05-26 17:24:42', '2023-05-27 11:10:44'),
(2106, 40, 4136, 3, 'غَافِرِ ٱلذَّنۢبِ وَقَابِلِ ٱلتَّوۡبِ شَدِیدِ ٱلۡعِقَابِ ذِی ٱلطَّوۡلِۖ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ إِلَیۡهِ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:44'),
(2107, 40, 4137, 4, 'مَا یُجَـٰدِلُ فِیۤ ءَایَـٰتِ ٱللَّهِ إِلَّا ٱلَّذِینَ كَفَرُوا۟ فَلَا یَغۡرُرۡكَ تَقَلُّبُهُمۡ فِی ٱلۡبِلَـٰدِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:44'),
(2108, 40, 4138, 5, 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحࣲ وَٱلۡأَحۡزَابُ مِنۢ بَعۡدِهِمۡۖ وَهَمَّتۡ كُلُّ أُمَّةِۭ بِرَسُولِهِمۡ لِیَأۡخُذُوهُۖ وَجَـٰدَلُوا۟ بِٱلۡبَـٰطِلِ لِیُدۡحِضُوا۟ بِهِ ٱلۡحَقَّ فَأَخَذۡتُهُمۡۖ فَكَیۡفَ كَانَ عِقَابِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2109, 40, 4139, 6, 'وَكَذَ ٰ⁠لِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِینَ كَفَرُوۤا۟ أَنَّهُمۡ أَصۡحَـٰبُ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2110, 40, 4140, 7, 'ٱلَّذِینَ یَحۡمِلُونَ ٱلۡعَرۡشَ وَمَنۡ حَوۡلَهُۥ یُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَیُؤۡمِنُونَ بِهِۦ وَیَسۡتَغۡفِرُونَ لِلَّذِینَ ءَامَنُوا۟ۖ رَبَّنَا وَسِعۡتَ كُلَّ شَیۡءࣲ رَّحۡمَةࣰ وَعِلۡمࣰا فَٱغۡفِرۡ لِلَّذِینَ تَابُوا۟ وَٱتَّبَعُوا۟ سَبِیلَكَ وَقِهِمۡ عَذَابَ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2111, 40, 4141, 8, 'رَبَّنَا وَأَدۡخِلۡهُمۡ جَنَّـٰتِ عَدۡنٍ ٱلَّتِی وَعَدتَّهُمۡ وَمَن صَلَحَ مِنۡ ءَابَاۤىِٕهِمۡ وَأَزۡوَ ٰ⁠جِهِمۡ وَذُرِّیَّـٰتِهِمۡۚ إِنَّكَ أَنتَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2112, 40, 4142, 9, 'وَقِهِمُ ٱلسَّیِّـَٔاتِۚ وَمَن تَقِ ٱلسَّیِّـَٔاتِ یَوۡمَىِٕذࣲ فَقَدۡ رَحِمۡتَهُۥۚ وَذَ ٰ⁠لِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2113, 40, 4143, 10, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ یُنَادَوۡنَ لَمَقۡتُ ٱللَّهِ أَكۡبَرُ مِن مَّقۡتِكُمۡ أَنفُسَكُمۡ إِذۡ تُدۡعَوۡنَ إِلَى ٱلۡإِیمَـٰنِ فَتَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2114, 40, 4144, 11, 'قَالُوا۟ رَبَّنَاۤ أَمَتَّنَا ٱثۡنَتَیۡنِ وَأَحۡیَیۡتَنَا ٱثۡنَتَیۡنِ فَٱعۡتَرَفۡنَا بِذُنُوبِنَا فَهَلۡ إِلَىٰ خُرُوجࣲ مِّن سَبِیلࣲ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2115, 40, 4145, 12, 'ذَ ٰ⁠لِكُم بِأَنَّهُۥۤ إِذَا دُعِیَ ٱللَّهُ وَحۡدَهُۥ كَفَرۡتُمۡ وَإِن یُشۡرَكۡ بِهِۦ تُؤۡمِنُوا۟ۚ فَٱلۡحُكۡمُ لِلَّهِ ٱلۡعَلِیِّ ٱلۡكَبِیرِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2116, 40, 4146, 13, 'هُوَ ٱلَّذِی یُرِیكُمۡ ءَایَـٰتِهِۦ وَیُنَزِّلُ لَكُم مِّنَ ٱلسَّمَاۤءِ رِزۡقࣰاۚ وَمَا یَتَذَكَّرُ إِلَّا مَن یُنِیبُ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2117, 40, 4147, 14, 'فَٱدۡعُوا۟ ٱللَّهَ مُخۡلِصِینَ لَهُ ٱلدِّینَ وَلَوۡ كَرِهَ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2118, 40, 4148, 15, 'رَفِیعُ ٱلدَّرَجَـٰتِ ذُو ٱلۡعَرۡشِ یُلۡقِی ٱلرُّوحَ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن یَشَاۤءُ مِنۡ عِبَادِهِۦ لِیُنذِرَ یَوۡمَ ٱلتَّلَاقِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2119, 40, 4149, 16, 'یَوۡمَ هُم بَـٰرِزُونَۖ لَا یَخۡفَىٰ عَلَى ٱللَّهِ مِنۡهُمۡ شَیۡءࣱۚ لِّمَنِ ٱلۡمُلۡكُ ٱلۡیَوۡمَۖ لِلَّهِ ٱلۡوَ ٰ⁠حِدِ ٱلۡقَهَّارِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2120, 40, 4150, 17, 'ٱلۡیَوۡمَ تُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡۚ لَا ظُلۡمَ ٱلۡیَوۡمَۚ إِنَّ ٱللَّهَ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2121, 40, 4151, 18, 'وَأَنذِرۡهُمۡ یَوۡمَ ٱلۡـَٔازِفَةِ إِذِ ٱلۡقُلُوبُ لَدَى ٱلۡحَنَاجِرِ كَـٰظِمِینَۚ مَا لِلظَّـٰلِمِینَ مِنۡ حَمِیمࣲ وَلَا شَفِیعࣲ یُطَاعُ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2122, 40, 4152, 19, 'یَعۡلَمُ خَاۤىِٕنَةَ ٱلۡأَعۡیُنِ وَمَا تُخۡفِی ٱلصُّدُورُ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2123, 40, 4153, 20, 'وَٱللَّهُ یَقۡضِی بِٱلۡحَقِّۖ وَٱلَّذِینَ یَدۡعُونَ مِن دُونِهِۦ لَا یَقۡضُونَ بِشَیۡءٍۗ إِنَّ ٱللَّهَ هُوَ ٱلسَّمِیعُ ٱلۡبَصِیرُ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2124, 40, 4154, 21, '۞ أَوَلَمۡ یَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَیَنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلَّذِینَ كَانُوا۟ مِن قَبۡلِهِمۡۚ كَانُوا۟ هُمۡ أَشَدَّ مِنۡهُمۡ قُوَّةࣰ وَءَاثَارࣰا فِی ٱلۡأَرۡضِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡ وَمَا كَانَ لَهُم مِّنَ ٱللَّهِ مِن وَاقࣲ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2125, 40, 4155, 22, 'ذَ ٰ⁠لِكَ بِأَنَّهُمۡ كَانَت تَّأۡتِیهِمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِ فَكَفَرُوا۟ فَأَخَذَهُمُ ٱللَّهُۚ إِنَّهُۥ قَوِیࣱّ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2126, 40, 4156, 23, 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔایَـٰتِنَا وَسُلۡطَـٰنࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:45'),
(2127, 40, 4157, 24, 'إِلَىٰ فِرۡعَوۡنَ وَهَـٰمَـٰنَ وَقَـٰرُونَ فَقَالُوا۟ سَـٰحِرࣱ كَذَّابࣱ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:46'),
(2128, 40, 4158, 25, 'فَلَمَّا جَاۤءَهُم بِٱلۡحَقِّ مِنۡ عِندِنَا قَالُوا۟ ٱقۡتُلُوۤا۟ أَبۡنَاۤءَ ٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥ وَٱسۡتَحۡیُوا۟ نِسَاۤءَهُمۡۚ وَمَا كَیۡدُ ٱلۡكَـٰفِرِینَ إِلَّا فِی ضَلَـٰلࣲ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:46'),
(2129, 40, 4159, 26, 'وَقَالَ فِرۡعَوۡنُ ذَرُونِیۤ أَقۡتُلۡ مُوسَىٰ وَلۡیَدۡعُ رَبَّهُۥۤۖ إِنِّیۤ أَخَافُ أَن یُبَدِّلَ دِینَكُمۡ أَوۡ أَن یُظۡهِرَ فِی ٱلۡأَرۡضِ ٱلۡفَسَادَ\n', NULL, 0, '2023-05-26 17:24:43', '2023-05-27 11:10:46'),
(2130, 40, 4160, 27, 'وَقَالَ مُوسَىٰۤ إِنِّی عُذۡتُ بِرَبِّی وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرࣲ لَّا یُؤۡمِنُ بِیَوۡمِ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2131, 40, 4161, 28, 'وَقَالَ رَجُلࣱ مُّؤۡمِنࣱ مِّنۡ ءَالِ فِرۡعَوۡنَ یَكۡتُمُ إِیمَـٰنَهُۥۤ أَتَقۡتُلُونَ رَجُلًا أَن یَقُولَ رَبِّیَ ٱللَّهُ وَقَدۡ جَاۤءَكُم بِٱلۡبَیِّنَـٰتِ مِن رَّبِّكُمۡۖ وَإِن یَكُ كَـٰذِبࣰا فَعَلَیۡهِ كَذِبُهُۥۖ وَإِن یَكُ صَادِقࣰا یُصِبۡكُم بَعۡضُ ٱلَّذِی یَعِدُكُمۡۖ إِنَّ ٱللَّهَ لَا یَهۡدِی مَنۡ هُوَ مُسۡرِفࣱ كَذَّابࣱ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2132, 40, 4162, 29, 'یَـٰقَوۡمِ لَكُمُ ٱلۡمُلۡكُ ٱلۡیَوۡمَ ظَـٰهِرِینَ فِی ٱلۡأَرۡضِ فَمَن یَنصُرُنَا مِنۢ بَأۡسِ ٱللَّهِ إِن جَاۤءَنَاۚ قَالَ فِرۡعَوۡنُ مَاۤ أُرِیكُمۡ إِلَّا مَاۤ أَرَىٰ وَمَاۤ أَهۡدِیكُمۡ إِلَّا سَبِیلَ ٱلرَّشَادِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2133, 40, 4163, 30, 'وَقَالَ ٱلَّذِیۤ ءَامَنَ یَـٰقَوۡمِ إِنِّیۤ أَخَافُ عَلَیۡكُم مِّثۡلَ یَوۡمِ ٱلۡأَحۡزَابِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2134, 40, 4164, 31, 'مِثۡلَ دَأۡبِ قَوۡمِ نُوحࣲ وَعَادࣲ وَثَمُودَ وَٱلَّذِینَ مِنۢ بَعۡدِهِمۡۚ وَمَا ٱللَّهُ یُرِیدُ ظُلۡمࣰا لِّلۡعِبَادِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2135, 40, 4165, 32, 'وَیَـٰقَوۡمِ إِنِّیۤ أَخَافُ عَلَیۡكُمۡ یَوۡمَ ٱلتَّنَادِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2136, 40, 4166, 33, 'یَوۡمَ تُوَلُّونَ مُدۡبِرِینَ مَا لَكُم مِّنَ ٱللَّهِ مِنۡ عَاصِمࣲۗ وَمَن یُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادࣲ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2137, 40, 4167, 34, 'وَلَقَدۡ جَاۤءَكُمۡ یُوسُفُ مِن قَبۡلُ بِٱلۡبَیِّنَـٰتِ فَمَا زِلۡتُمۡ فِی شَكࣲّ مِّمَّا جَاۤءَكُم بِهِۦۖ حَتَّىٰۤ إِذَا هَلَكَ قُلۡتُمۡ لَن یَبۡعَثَ ٱللَّهُ مِنۢ بَعۡدِهِۦ رَسُولࣰاۚ كَذَ ٰ⁠لِكَ یُضِلُّ ٱللَّهُ مَنۡ هُوَ مُسۡرِفࣱ مُّرۡتَابٌ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2138, 40, 4168, 35, 'ٱلَّذِینَ یُجَـٰدِلُونَ فِیۤ ءَایَـٰتِ ٱللَّهِ بِغَیۡرِ سُلۡطَـٰنٍ أَتَىٰهُمۡۖ كَبُرَ مَقۡتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِینَ ءَامَنُوا۟ۚ كَذَ ٰ⁠لِكَ یَطۡبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلۡبِ مُتَكَبِّرࣲ جَبَّارࣲ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2139, 40, 4169, 36, 'وَقَالَ فِرۡعَوۡنُ یَـٰهَـٰمَـٰنُ ٱبۡنِ لِی صَرۡحࣰا لَّعَلِّیۤ أَبۡلُغُ ٱلۡأَسۡبَـٰبَ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2140, 40, 4170, 37, 'أَسۡبَـٰبَ ٱلسَّمَـٰوَ ٰ⁠تِ فَأَطَّلِعَ إِلَىٰۤ إِلَـٰهِ مُوسَىٰ وَإِنِّی لَأَظُنُّهُۥ كَـٰذِبࣰاۚ وَكَذَ ٰ⁠لِكَ زُیِّنَ لِفِرۡعَوۡنَ سُوۤءُ عَمَلِهِۦ وَصُدَّ عَنِ ٱلسَّبِیلِۚ وَمَا كَیۡدُ فِرۡعَوۡنَ إِلَّا فِی تَبَابࣲ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2141, 40, 4171, 38, 'وَقَالَ ٱلَّذِیۤ ءَامَنَ یَـٰقَوۡمِ ٱتَّبِعُونِ أَهۡدِكُمۡ سَبِیلَ ٱلرَّشَادِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:46'),
(2142, 40, 4172, 39, 'یَـٰقَوۡمِ إِنَّمَا هَـٰذِهِ ٱلۡحَیَوٰةُ ٱلدُّنۡیَا مَتَـٰعࣱ وَإِنَّ ٱلۡـَٔاخِرَةَ هِیَ دَارُ ٱلۡقَرَارِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2143, 40, 4173, 40, 'مَنۡ عَمِلَ سَیِّئَةࣰ فَلَا یُجۡزَىٰۤ إِلَّا مِثۡلَهَاۖ وَمَنۡ عَمِلَ صَـٰلِحࣰا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنࣱ فَأُو۟لَـٰۤىِٕكَ یَدۡخُلُونَ ٱلۡجَنَّةَ یُرۡزَقُونَ فِیهَا بِغَیۡرِ حِسَابࣲ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2144, 40, 4174, 41, '۞ وَیَـٰقَوۡمِ مَا لِیۤ أَدۡعُوكُمۡ إِلَى ٱلنَّجَوٰةِ وَتَدۡعُونَنِیۤ إِلَى ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2145, 40, 4175, 42, 'تَدۡعُونَنِی لِأَكۡفُرَ بِٱللَّهِ وَأُشۡرِكَ بِهِۦ مَا لَیۡسَ لِی بِهِۦ عِلۡمࣱ وَأَنَا۠ أَدۡعُوكُمۡ إِلَى ٱلۡعَزِیزِ ٱلۡغَفَّـٰرِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2146, 40, 4176, 43, 'لَا جَرَمَ أَنَّمَا تَدۡعُونَنِیۤ إِلَیۡهِ لَیۡسَ لَهُۥ دَعۡوَةࣱ فِی ٱلدُّنۡیَا وَلَا فِی ٱلۡـَٔاخِرَةِ وَأَنَّ مَرَدَّنَاۤ إِلَى ٱللَّهِ وَأَنَّ ٱلۡمُسۡرِفِینَ هُمۡ أَصۡحَـٰبُ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2147, 40, 4177, 44, 'فَسَتَذۡكُرُونَ مَاۤ أَقُولُ لَكُمۡۚ وَأُفَوِّضُ أَمۡرِیۤ إِلَى ٱللَّهِۚ إِنَّ ٱللَّهَ بَصِیرُۢ بِٱلۡعِبَادِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2148, 40, 4178, 45, 'فَوَقَىٰهُ ٱللَّهُ سَیِّـَٔاتِ مَا مَكَرُوا۟ۖ وَحَاقَ بِـَٔالِ فِرۡعَوۡنَ سُوۤءُ ٱلۡعَذَابِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2149, 40, 4179, 46, 'ٱلنَّارُ یُعۡرَضُونَ عَلَیۡهَا غُدُوࣰّا وَعَشِیࣰّاۚ وَیَوۡمَ تَقُومُ ٱلسَّاعَةُ أَدۡخِلُوۤا۟ ءَالَ فِرۡعَوۡنَ أَشَدَّ ٱلۡعَذَابِ\n', NULL, 0, '2023-05-26 17:24:44', '2023-05-27 11:10:47'),
(2150, 40, 4180, 47, 'وَإِذۡ یَتَحَاۤجُّونَ فِی ٱلنَّارِ فَیَقُولُ ٱلضُّعَفَـٰۤؤُا۟ لِلَّذِینَ ٱسۡتَكۡبَرُوۤا۟ إِنَّا كُنَّا لَكُمۡ تَبَعࣰا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا نَصِیبࣰا مِّنَ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2151, 40, 4181, 48, 'قَالَ ٱلَّذِینَ ٱسۡتَكۡبَرُوۤا۟ إِنَّا كُلࣱّ فِیهَاۤ إِنَّ ٱللَّهَ قَدۡ حَكَمَ بَیۡنَ ٱلۡعِبَادِ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2152, 40, 4182, 49, 'وَقَالَ ٱلَّذِینَ فِی ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدۡعُوا۟ رَبَّكُمۡ یُخَفِّفۡ عَنَّا یَوۡمࣰا مِّنَ ٱلۡعَذَابِ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2153, 40, 4183, 50, 'قَالُوۤا۟ أَوَلَمۡ تَكُ تَأۡتِیكُمۡ رُسُلُكُم بِٱلۡبَیِّنَـٰتِۖ قَالُوا۟ بَلَىٰۚ قَالُوا۟ فَٱدۡعُوا۟ۗ وَمَا دُعَـٰۤؤُا۟ ٱلۡكَـٰفِرِینَ إِلَّا فِی ضَلَـٰلٍ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2154, 40, 4184, 51, 'إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِینَ ءَامَنُوا۟ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَیَوۡمَ یَقُومُ ٱلۡأَشۡهَـٰدُ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2155, 40, 4185, 52, 'یَوۡمَ لَا یَنفَعُ ٱلظَّـٰلِمِینَ مَعۡذِرَتُهُمۡۖ وَلَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوۤءُ ٱلدَّارِ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2156, 40, 4186, 53, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡهُدَىٰ وَأَوۡرَثۡنَا بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱلۡكِتَـٰبَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2157, 40, 4187, 54, 'هُدࣰى وَذِكۡرَىٰ لِأُو۟لِی ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2158, 40, 4188, 55, 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ بِٱلۡعَشِیِّ وَٱلۡإِبۡكَـٰرِ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2159, 40, 4189, 56, 'إِنَّ ٱلَّذِینَ یُجَـٰدِلُونَ فِیۤ ءَایَـٰتِ ٱللَّهِ بِغَیۡرِ سُلۡطَـٰنٍ أَتَىٰهُمۡ إِن فِی صُدُورِهِمۡ إِلَّا كِبۡرࣱ مَّا هُم بِبَـٰلِغِیهِۚ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِیعُ ٱلۡبَصِیرُ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:47'),
(2160, 40, 4190, 57, 'لَخَلۡقُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ أَكۡبَرُ مِنۡ خَلۡقِ ٱلنَّاسِ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2161, 40, 4191, 58, 'وَمَا یَسۡتَوِی ٱلۡأَعۡمَىٰ وَٱلۡبَصِیرُ وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَلَا ٱلۡمُسِیۤءُۚ قَلِیلࣰا مَّا تَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2162, 40, 4192, 59, 'إِنَّ ٱلسَّاعَةَ لَـَٔاتِیَةࣱ لَّا رَیۡبَ فِیهَا وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2163, 40, 4193, 60, 'وَقَالَ رَبُّكُمُ ٱدۡعُونِیۤ أَسۡتَجِبۡ لَكُمۡۚ إِنَّ ٱلَّذِینَ یَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِی سَیَدۡخُلُونَ جَهَنَّمَ دَاخِرِینَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2164, 40, 4194, 61, 'ٱللَّهُ ٱلَّذِی جَعَلَ لَكُمُ ٱلَّیۡلَ لِتَسۡكُنُوا۟ فِیهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2165, 40, 4195, 62, 'ذَ ٰ⁠لِكُمُ ٱللَّهُ رَبُّكُمۡ خَـٰلِقُ كُلِّ شَیۡءࣲ لَّاۤ إِلَـٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2166, 40, 4196, 63, 'كَذَ ٰ⁠لِكَ یُؤۡفَكُ ٱلَّذِینَ كَانُوا۟ بِـَٔایَـٰتِ ٱللَّهِ یَجۡحَدُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2167, 40, 4197, 64, 'ٱللَّهُ ٱلَّذِی جَعَلَ لَكُمُ ٱلۡأَرۡضَ قَرَارࣰا وَٱلسَّمَاۤءَ بِنَاۤءࣰ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡ وَرَزَقَكُم مِّنَ ٱلطَّیِّبَـٰتِۚ ذَ ٰ⁠لِكُمُ ٱللَّهُ رَبُّكُمۡۖ فَتَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2168, 40, 4198, 65, 'هُوَ ٱلۡحَیُّ لَاۤ إِلَـٰهَ إِلَّا هُوَ فَٱدۡعُوهُ مُخۡلِصِینَ لَهُ ٱلدِّینَۗ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2169, 40, 4199, 66, '۞ قُلۡ إِنِّی نُهِیتُ أَنۡ أَعۡبُدَ ٱلَّذِینَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَمَّا جَاۤءَنِیَ ٱلۡبَیِّنَـٰتُ مِن رَّبِّی وَأُمِرۡتُ أَنۡ أُسۡلِمَ لِرَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2170, 40, 4200, 67, 'هُوَ ٱلَّذِی خَلَقَكُم مِّن تُرَابࣲ ثُمَّ مِن نُّطۡفَةࣲ ثُمَّ مِنۡ عَلَقَةࣲ ثُمَّ یُخۡرِجُكُمۡ طِفۡلࣰا ثُمَّ لِتَبۡلُغُوۤا۟ أَشُدَّكُمۡ ثُمَّ لِتَكُونُوا۟ شُیُوخࣰاۚ وَمِنكُم مَّن یُتَوَفَّىٰ مِن قَبۡلُۖ وَلِتَبۡلُغُوۤا۟ أَجَلࣰا مُّسَمࣰّى وَلَعَلَّكُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2171, 40, 4201, 68, 'هُوَ ٱلَّذِی یُحۡیِۦ وَیُمِیتُۖ فَإِذَا قَضَىٰۤ أَمۡرࣰا فَإِنَّمَا یَقُولُ لَهُۥ كُن فَیَكُونُ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2172, 40, 4202, 69, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ یُجَـٰدِلُونَ فِیۤ ءَایَـٰتِ ٱللَّهِ أَنَّىٰ یُصۡرَفُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2173, 40, 4203, 70, 'ٱلَّذِینَ كَذَّبُوا۟ بِٱلۡكِتَـٰبِ وَبِمَاۤ أَرۡسَلۡنَا بِهِۦ رُسُلَنَاۖ فَسَوۡفَ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:24:45', '2023-05-27 11:10:48'),
(2174, 40, 4204, 71, 'إِذِ ٱلۡأَغۡلَـٰلُ فِیۤ أَعۡنَـٰقِهِمۡ وَٱلسَّلَـٰسِلُ یُسۡحَبُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:48'),
(2175, 40, 4205, 72, 'فِی ٱلۡحَمِیمِ ثُمَّ فِی ٱلنَّارِ یُسۡجَرُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:48'),
(2176, 40, 4206, 73, 'ثُمَّ قِیلَ لَهُمۡ أَیۡنَ مَا كُنتُمۡ تُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:48'),
(2177, 40, 4207, 74, 'مِن دُونِ ٱللَّهِۖ قَالُوا۟ ضَلُّوا۟ عَنَّا بَل لَّمۡ نَكُن نَّدۡعُوا۟ مِن قَبۡلُ شَیۡـࣰٔاۚ كَذَ ٰ⁠لِكَ یُضِلُّ ٱللَّهُ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2178, 40, 4208, 75, 'ذَ ٰ⁠لِكُم بِمَا كُنتُمۡ تَفۡرَحُونَ فِی ٱلۡأَرۡضِ بِغَیۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَمۡرَحُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2179, 40, 4209, 76, 'ٱدۡخُلُوۤا۟ أَبۡوَ ٰ⁠بَ جَهَنَّمَ خَـٰلِدِینَ فِیهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِینَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2180, 40, 4210, 77, 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّۚ فَإِمَّا نُرِیَنَّكَ بَعۡضَ ٱلَّذِی نَعِدُهُمۡ أَوۡ نَتَوَفَّیَنَّكَ فَإِلَیۡنَا یُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2181, 40, 4211, 78, 'وَلَقَدۡ أَرۡسَلۡنَا رُسُلࣰا مِّن قَبۡلِكَ مِنۡهُم مَّن قَصَصۡنَا عَلَیۡكَ وَمِنۡهُم مَّن لَّمۡ نَقۡصُصۡ عَلَیۡكَۗ وَمَا كَانَ لِرَسُولٍ أَن یَأۡتِیَ بِـَٔایَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ فَإِذَا جَاۤءَ أَمۡرُ ٱللَّهِ قُضِیَ بِٱلۡحَقِّ وَخَسِرَ هُنَالِكَ ٱلۡمُبۡطِلُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2182, 40, 4212, 79, 'ٱللَّهُ ٱلَّذِی جَعَلَ لَكُمُ ٱلۡأَنۡعَـٰمَ لِتَرۡكَبُوا۟ مِنۡهَا وَمِنۡهَا تَأۡكُلُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2183, 40, 4213, 80, 'وَلَكُمۡ فِیهَا مَنَـٰفِعُ وَلِتَبۡلُغُوا۟ عَلَیۡهَا حَاجَةࣰ فِی صُدُورِكُمۡ وَعَلَیۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2184, 40, 4214, 81, 'وَیُرِیكُمۡ ءَایَـٰتِهِۦ فَأَیَّ ءَایَـٰتِ ٱللَّهِ تُنكِرُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2185, 40, 4215, 82, 'أَفَلَمۡ یَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَیَنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلَّذِینَ مِن قَبۡلِهِمۡۚ كَانُوۤا۟ أَكۡثَرَ مِنۡهُمۡ وَأَشَدَّ قُوَّةࣰ وَءَاثَارࣰا فِی ٱلۡأَرۡضِ فَمَاۤ أَغۡنَىٰ عَنۡهُم مَّا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2186, 40, 4216, 83, 'فَلَمَّا جَاۤءَتۡهُمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِ فَرِحُوا۟ بِمَا عِندَهُم مِّنَ ٱلۡعِلۡمِ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2187, 40, 4217, 84, 'فَلَمَّا رَأَوۡا۟ بَأۡسَنَا قَالُوۤا۟ ءَامَنَّا بِٱللَّهِ وَحۡدَهُۥ وَكَفَرۡنَا بِمَا كُنَّا بِهِۦ مُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2188, 40, 4218, 85, 'فَلَمۡ یَكُ یَنفَعُهُمۡ إِیمَـٰنُهُمۡ لَمَّا رَأَوۡا۟ بَأۡسَنَاۖ سُنَّتَ ٱللَّهِ ٱلَّتِی قَدۡ خَلَتۡ فِی عِبَادِهِۦۖ وَخَسِرَ هُنَالِكَ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:24:46', '2023-05-27 11:10:49'),
(2189, 39, 4059, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ تَنزِیلُ ٱلۡكِتَـٰبِ مِنَ ٱللَّهِ ٱلۡعَزِیزِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:24:58', '2023-05-27 11:10:58'),
(2190, 39, 4060, 2, 'إِنَّاۤ أَنزَلۡنَاۤ إِلَیۡكَ ٱلۡكِتَـٰبَ بِٱلۡحَقِّ فَٱعۡبُدِ ٱللَّهَ مُخۡلِصࣰا لَّهُ ٱلدِّینَ\n', NULL, 0, '2023-05-26 17:24:58', '2023-05-27 11:10:58'),
(2191, 39, 4061, 3, 'أَلَا لِلَّهِ ٱلدِّینُ ٱلۡخَالِصُۚ وَٱلَّذِینَ ٱتَّخَذُوا۟ مِن دُونِهِۦۤ أَوۡلِیَاۤءَ مَا نَعۡبُدُهُمۡ إِلَّا لِیُقَرِّبُونَاۤ إِلَى ٱللَّهِ زُلۡفَىٰۤ إِنَّ ٱللَّهَ یَحۡكُمُ بَیۡنَهُمۡ فِی مَا هُمۡ فِیهِ یَخۡتَلِفُونَۗ إِنَّ ٱللَّهَ لَا یَهۡدِی مَنۡ هُوَ كَـٰذِبࣱ كَفَّارࣱ\n', NULL, 0, '2023-05-26 17:24:58', '2023-05-27 11:10:58'),
(2192, 39, 4062, 4, 'لَّوۡ أَرَادَ ٱللَّهُ أَن یَتَّخِذَ وَلَدࣰا لَّٱصۡطَفَىٰ مِمَّا یَخۡلُقُ مَا یَشَاۤءُۚ سُبۡحَـٰنَهُۥۖ هُوَ ٱللَّهُ ٱلۡوَ ٰ⁠حِدُ ٱلۡقَهَّارُ\n', NULL, 0, '2023-05-26 17:24:58', '2023-05-27 11:10:58'),
(2193, 39, 4063, 5, 'خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ یُكَوِّرُ ٱلَّیۡلَ عَلَى ٱلنَّهَارِ وَیُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّیۡلِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلࣱّ یَجۡرِی لِأَجَلࣲ مُّسَمًّىۗ أَلَا هُوَ ٱلۡعَزِیزُ ٱلۡغَفَّـٰرُ\n', NULL, 0, '2023-05-26 17:24:58', '2023-05-27 11:10:58'),
(2194, 39, 4064, 6, 'خَلَقَكُم مِّن نَّفۡسࣲ وَ ٰ⁠حِدَةࣲ ثُمَّ جَعَلَ مِنۡهَا زَوۡجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلۡأَنۡعَـٰمِ ثَمَـٰنِیَةَ أَزۡوَ ٰ⁠جࣲۚ یَخۡلُقُكُمۡ فِی بُطُونِ أُمَّهَـٰتِكُمۡ خَلۡقࣰا مِّنۢ بَعۡدِ خَلۡقࣲ فِی ظُلُمَـٰتࣲ ثَلَـٰثࣲۚ ذَ ٰ⁠لِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۖ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُصۡرَفُونَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2195, 39, 4065, 7, 'إِن تَكۡفُرُوا۟ فَإِنَّ ٱللَّهَ غَنِیٌّ عَنكُمۡۖ وَلَا یَرۡضَىٰ لِعِبَادِهِ ٱلۡكُفۡرَۖ وَإِن تَشۡكُرُوا۟ یَرۡضَهُ لَكُمۡۗ وَلَا تَزِرُ وَازِرَةࣱ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَیُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَۚ إِنَّهُۥ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2196, 39, 4066, 8, '۞ وَإِذَا مَسَّ ٱلۡإِنسَـٰنَ ضُرࣱّ دَعَا رَبَّهُۥ مُنِیبًا إِلَیۡهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعۡمَةࣰ مِّنۡهُ نَسِیَ مَا كَانَ یَدۡعُوۤا۟ إِلَیۡهِ مِن قَبۡلُ وَجَعَلَ لِلَّهِ أَندَادࣰا لِّیُضِلَّ عَن سَبِیلِهِۦۚ قُلۡ تَمَتَّعۡ بِكُفۡرِكَ قَلِیلًا إِنَّكَ مِنۡ أَصۡحَـٰبِ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2197, 39, 4067, 9, 'أَمَّنۡ هُوَ قَـٰنِتٌ ءَانَاۤءَ ٱلَّیۡلِ سَاجِدࣰا وَقَاۤىِٕمࣰا یَحۡذَرُ ٱلۡـَٔاخِرَةَ وَیَرۡجُوا۟ رَحۡمَةَ رَبِّهِۦۗ قُلۡ هَلۡ یَسۡتَوِی ٱلَّذِینَ یَعۡلَمُونَ وَٱلَّذِینَ لَا یَعۡلَمُونَۗ إِنَّمَا یَتَذَكَّرُ أُو۟لُوا۟ ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2198, 39, 4068, 10, 'قُلۡ یَـٰعِبَادِ ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ رَبَّكُمۡۚ لِلَّذِینَ أَحۡسَنُوا۟ فِی هَـٰذِهِ ٱلدُّنۡیَا حَسَنَةࣱۗ وَأَرۡضُ ٱللَّهِ وَ ٰ⁠سِعَةٌۗ إِنَّمَا یُوَفَّى ٱلصَّـٰبِرُونَ أَجۡرَهُم بِغَیۡرِ حِسَابࣲ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2199, 39, 4069, 11, 'قُلۡ إِنِّیۤ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ مُخۡلِصࣰا لَّهُ ٱلدِّینَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2200, 39, 4070, 12, 'وَأُمِرۡتُ لِأَنۡ أَكُونَ أَوَّلَ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2201, 39, 4071, 13, 'قُلۡ إِنِّیۤ أَخَافُ إِنۡ عَصَیۡتُ رَبِّی عَذَابَ یَوۡمٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2202, 39, 4072, 14, 'قُلِ ٱللَّهَ أَعۡبُدُ مُخۡلِصࣰا لَّهُۥ دِینِی\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2203, 39, 4073, 15, 'فَٱعۡبُدُوا۟ مَا شِئۡتُم مِّن دُونِهِۦۗ قُلۡ إِنَّ ٱلۡخَـٰسِرِینَ ٱلَّذِینَ خَسِرُوۤا۟ أَنفُسَهُمۡ وَأَهۡلِیهِمۡ یَوۡمَ ٱلۡقِیَـٰمَةِۗ أَلَا ذَ ٰ⁠لِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2204, 39, 4074, 16, 'لَهُم مِّن فَوۡقِهِمۡ ظُلَلࣱ مِّنَ ٱلنَّارِ وَمِن تَحۡتِهِمۡ ظُلَلࣱۚ ذَ ٰ⁠لِكَ یُخَوِّفُ ٱللَّهُ بِهِۦ عِبَادَهُۥۚ یَـٰعِبَادِ فَٱتَّقُونِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2205, 39, 4075, 17, 'وَٱلَّذِینَ ٱجۡتَنَبُوا۟ ٱلطَّـٰغُوتَ أَن یَعۡبُدُوهَا وَأَنَابُوۤا۟ إِلَى ٱللَّهِ لَهُمُ ٱلۡبُشۡرَىٰۚ فَبَشِّرۡ عِبَادِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2206, 39, 4076, 18, 'ٱلَّذِینَ یَسۡتَمِعُونَ ٱلۡقَوۡلَ فَیَتَّبِعُونَ أَحۡسَنَهُۥۤۚ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ هَدَىٰهُمُ ٱللَّهُۖ وَأُو۟لَـٰۤىِٕكَ هُمۡ أُو۟لُوا۟ ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2207, 39, 4077, 19, 'أَفَمَنۡ حَقَّ عَلَیۡهِ كَلِمَةُ ٱلۡعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِی ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:58'),
(2208, 39, 4078, 20, 'لَـٰكِنِ ٱلَّذِینَ ٱتَّقَوۡا۟ رَبَّهُمۡ لَهُمۡ غُرَفࣱ مِّن فَوۡقِهَا غُرَفࣱ مَّبۡنِیَّةࣱ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۖ وَعۡدَ ٱللَّهِ لَا یُخۡلِفُ ٱللَّهُ ٱلۡمِیعَادَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2209, 39, 4079, 21, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَسَلَكَهُۥ یَنَـٰبِیعَ فِی ٱلۡأَرۡضِ ثُمَّ یُخۡرِجُ بِهِۦ زَرۡعࣰا مُّخۡتَلِفًا أَلۡوَ ٰ⁠نُهُۥ ثُمَّ یَهِیجُ فَتَرَىٰهُ مُصۡفَرࣰّا ثُمَّ یَجۡعَلُهُۥ حُطَـٰمًاۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَذِكۡرَىٰ لِأُو۟لِی ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2210, 39, 4080, 22, 'أَفَمَن شَرَحَ ٱللَّهُ صَدۡرَهُۥ لِلۡإِسۡلَـٰمِ فَهُوَ عَلَىٰ نُورࣲ مِّن رَّبِّهِۦۚ فَوَیۡلࣱ لِّلۡقَـٰسِیَةِ قُلُوبُهُم مِّن ذِكۡرِ ٱللَّهِۚ أُو۟لَـٰۤىِٕكَ فِی ضَلَـٰلࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2211, 39, 4081, 23, 'ٱللَّهُ نَزَّلَ أَحۡسَنَ ٱلۡحَدِیثِ كِتَـٰبࣰا مُّتَشَـٰبِهࣰا مَّثَانِیَ تَقۡشَعِرُّ مِنۡهُ جُلُودُ ٱلَّذِینَ یَخۡشَوۡنَ رَبَّهُمۡ ثُمَّ تَلِینُ جُلُودُهُمۡ وَقُلُوبُهُمۡ إِلَىٰ ذِكۡرِ ٱللَّهِۚ ذَ ٰ⁠لِكَ هُدَى ٱللَّهِ یَهۡدِی بِهِۦ مَن یَشَاۤءُۚ وَمَن یُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٍ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2212, 39, 4082, 24, 'أَفَمَن یَتَّقِی بِوَجۡهِهِۦ سُوۤءَ ٱلۡعَذَابِ یَوۡمَ ٱلۡقِیَـٰمَةِۚ وَقِیلَ لِلظَّـٰلِمِینَ ذُوقُوا۟ مَا كُنتُمۡ تَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2213, 39, 4083, 25, 'كَذَّبَ ٱلَّذِینَ مِن قَبۡلِهِمۡ فَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَیۡثُ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2214, 39, 4084, 26, 'فَأَذَاقَهُمُ ٱللَّهُ ٱلۡخِزۡیَ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ وَلَعَذَابُ ٱلۡـَٔاخِرَةِ أَكۡبَرُۚ لَوۡ كَانُوا۟ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2215, 39, 4085, 27, 'وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِی هَـٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلࣲ لَّعَلَّهُمۡ یَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2216, 39, 4086, 28, 'قُرۡءَانًا عَرَبِیًّا غَیۡرَ ذِی عِوَجࣲ لَّعَلَّهُمۡ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2217, 39, 4087, 29, 'ضَرَبَ ٱللَّهُ مَثَلࣰا رَّجُلࣰا فِیهِ شُرَكَاۤءُ مُتَشَـٰكِسُونَ وَرَجُلࣰا سَلَمࣰا لِّرَجُلٍ هَلۡ یَسۡتَوِیَانِ مَثَلًاۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:24:59', '2023-05-27 11:10:59'),
(2218, 39, 4088, 30, 'إِنَّكَ مَیِّتࣱ وَإِنَّهُم مَّیِّتُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2219, 39, 4089, 31, 'ثُمَّ إِنَّكُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ عِندَ رَبِّكُمۡ تَخۡتَصِمُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2220, 39, 4090, 32, '۞ فَمَنۡ أَظۡلَمُ مِمَّن كَذَبَ عَلَى ٱللَّهِ وَكَذَّبَ بِٱلصِّدۡقِ إِذۡ جَاۤءَهُۥۤۚ أَلَیۡسَ فِی جَهَنَّمَ مَثۡوࣰى لِّلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2221, 39, 4091, 33, 'وَٱلَّذِی جَاۤءَ بِٱلصِّدۡقِ وَصَدَّقَ بِهِۦۤ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُتَّقُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2222, 39, 4092, 34, 'لَهُم مَّا یَشَاۤءُونَ عِندَ رَبِّهِمۡۚ ذَ ٰ⁠لِكَ جَزَاۤءُ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2223, 39, 4093, 35, 'لِیُكَفِّرَ ٱللَّهُ عَنۡهُمۡ أَسۡوَأَ ٱلَّذِی عَمِلُوا۟ وَیَجۡزِیَهُمۡ أَجۡرَهُم بِأَحۡسَنِ ٱلَّذِی كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2224, 39, 4094, 36, 'أَلَیۡسَ ٱللَّهُ بِكَافٍ عَبۡدَهُۥۖ وَیُخَوِّفُونَكَ بِٱلَّذِینَ مِن دُونِهِۦۚ وَمَن یُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادࣲ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2225, 39, 4095, 37, 'وَمَن یَهۡدِ ٱللَّهُ فَمَا لَهُۥ مِن مُّضِلٍّۗ أَلَیۡسَ ٱللَّهُ بِعَزِیزࣲ ذِی ٱنتِقَامࣲ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2226, 39, 4096, 38, 'وَلَىِٕن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ لَیَقُولُنَّ ٱللَّهُۚ قُلۡ أَفَرَءَیۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ إِنۡ أَرَادَنِیَ ٱللَّهُ بِضُرٍّ هَلۡ هُنَّ كَـٰشِفَـٰتُ ضُرِّهِۦۤ أَوۡ أَرَادَنِی بِرَحۡمَةٍ هَلۡ هُنَّ مُمۡسِكَـٰتُ رَحۡمَتِهِۦۚ قُلۡ حَسۡبِیَ ٱللَّهُۖ عَلَیۡهِ یَتَوَكَّلُ ٱلۡمُتَوَكِّلُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2227, 39, 4097, 39, 'قُلۡ یَـٰقَوۡمِ ٱعۡمَلُوا۟ عَلَىٰ مَكَانَتِكُمۡ إِنِّی عَـٰمِلࣱۖ فَسَوۡفَ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2228, 39, 4098, 40, 'مَن یَأۡتِیهِ عَذَابࣱ یُخۡزِیهِ وَیَحِلُّ عَلَیۡهِ عَذَابࣱ مُّقِیمٌ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:10:59'),
(2229, 39, 4099, 41, 'إِنَّاۤ أَنزَلۡنَا عَلَیۡكَ ٱلۡكِتَـٰبَ لِلنَّاسِ بِٱلۡحَقِّۖ فَمَنِ ٱهۡتَدَىٰ فَلِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا یَضِلُّ عَلَیۡهَاۖ وَمَاۤ أَنتَ عَلَیۡهِم بِوَكِیلٍ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2230, 39, 4100, 42, 'ٱللَّهُ یَتَوَفَّى ٱلۡأَنفُسَ حِینَ مَوۡتِهَا وَٱلَّتِی لَمۡ تَمُتۡ فِی مَنَامِهَاۖ فَیُمۡسِكُ ٱلَّتِی قَضَىٰ عَلَیۡهَا ٱلۡمَوۡتَ وَیُرۡسِلُ ٱلۡأُخۡرَىٰۤ إِلَىٰۤ أَجَلࣲ مُّسَمًّىۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2231, 39, 4101, 43, 'أَمِ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ شُفَعَاۤءَۚ قُلۡ أَوَلَوۡ كَانُوا۟ لَا یَمۡلِكُونَ شَیۡـࣰٔا وَلَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2232, 39, 4102, 44, 'قُل لِّلَّهِ ٱلشَّفَـٰعَةُ جَمِیعࣰاۖ لَّهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ ثُمَّ إِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2233, 39, 4103, 45, 'وَإِذَا ذُكِرَ ٱللَّهُ وَحۡدَهُ ٱشۡمَأَزَّتۡ قُلُوبُ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِۖ وَإِذَا ذُكِرَ ٱلَّذِینَ مِن دُونِهِۦۤ إِذَا هُمۡ یَسۡتَبۡشِرُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2234, 39, 4104, 46, 'قُلِ ٱللَّهُمَّ فَاطِرَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ عَـٰلِمَ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ أَنتَ تَحۡكُمُ بَیۡنَ عِبَادِكَ فِی مَا كَانُوا۟ فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2235, 39, 4105, 47, 'وَلَوۡ أَنَّ لِلَّذِینَ ظَلَمُوا۟ مَا فِی ٱلۡأَرۡضِ جَمِیعࣰا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡا۟ بِهِۦ مِن سُوۤءِ ٱلۡعَذَابِ یَوۡمَ ٱلۡقِیَـٰمَةِۚ وَبَدَا لَهُم مِّنَ ٱللَّهِ مَا لَمۡ یَكُونُوا۟ یَحۡتَسِبُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2236, 39, 4106, 48, 'وَبَدَا لَهُمۡ سَیِّـَٔاتُ مَا كَسَبُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2237, 39, 4107, 49, 'فَإِذَا مَسَّ ٱلۡإِنسَـٰنَ ضُرࣱّ دَعَانَا ثُمَّ إِذَا خَوَّلۡنَـٰهُ نِعۡمَةࣰ مِّنَّا قَالَ إِنَّمَاۤ أُوتِیتُهُۥ عَلَىٰ عِلۡمِۭۚ بَلۡ هِیَ فِتۡنَةࣱ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2238, 39, 4108, 50, 'قَدۡ قَالَهَا ٱلَّذِینَ مِن قَبۡلِهِمۡ فَمَاۤ أَغۡنَىٰ عَنۡهُم مَّا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2239, 39, 4109, 51, 'فَأَصَابَهُمۡ سَیِّـَٔاتُ مَا كَسَبُوا۟ۚ وَٱلَّذِینَ ظَلَمُوا۟ مِنۡ هَـٰۤؤُلَاۤءِ سَیُصِیبُهُمۡ سَیِّـَٔاتُ مَا كَسَبُوا۟ وَمَا هُم بِمُعۡجِزِینَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2240, 39, 4110, 52, 'أَوَلَمۡ یَعۡلَمُوۤا۟ أَنَّ ٱللَّهَ یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ وَیَقۡدِرُۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2241, 39, 4111, 53, '۞ قُلۡ یَـٰعِبَادِیَ ٱلَّذِینَ أَسۡرَفُوا۟ عَلَىٰۤ أَنفُسِهِمۡ لَا تَقۡنَطُوا۟ مِن رَّحۡمَةِ ٱللَّهِۚ إِنَّ ٱللَّهَ یَغۡفِرُ ٱلذُّنُوبَ جَمِیعًاۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2242, 39, 4112, 54, 'وَأَنِیبُوۤا۟ إِلَىٰ رَبِّكُمۡ وَأَسۡلِمُوا۟ لَهُۥ مِن قَبۡلِ أَن یَأۡتِیَكُمُ ٱلۡعَذَابُ ثُمَّ لَا تُنصَرُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2243, 39, 4113, 55, 'وَٱتَّبِعُوۤا۟ أَحۡسَنَ مَاۤ أُنزِلَ إِلَیۡكُم مِّن رَّبِّكُم مِّن قَبۡلِ أَن یَأۡتِیَكُمُ ٱلۡعَذَابُ بَغۡتَةࣰ وَأَنتُمۡ لَا تَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2244, 39, 4114, 56, 'أَن تَقُولَ نَفۡسࣱ یَـٰحَسۡرَتَىٰ عَلَىٰ مَا فَرَّطتُ فِی جَنۢبِ ٱللَّهِ وَإِن كُنتُ لَمِنَ ٱلسَّـٰخِرِینَ\n', NULL, 0, '2023-05-26 17:25:00', '2023-05-27 11:11:00'),
(2245, 39, 4115, 57, 'أَوۡ تَقُولَ لَوۡ أَنَّ ٱللَّهَ هَدَىٰنِی لَكُنتُ مِنَ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:00'),
(2246, 39, 4116, 58, 'أَوۡ تَقُولَ حِینَ تَرَى ٱلۡعَذَابَ لَوۡ أَنَّ لِی كَرَّةࣰ فَأَكُونَ مِنَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2247, 39, 4117, 59, 'بَلَىٰ قَدۡ جَاۤءَتۡكَ ءَایَـٰتِی فَكَذَّبۡتَ بِهَا وَٱسۡتَكۡبَرۡتَ وَكُنتَ مِنَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2248, 39, 4118, 60, 'وَیَوۡمَ ٱلۡقِیَـٰمَةِ تَرَى ٱلَّذِینَ كَذَبُوا۟ عَلَى ٱللَّهِ وُجُوهُهُم مُّسۡوَدَّةٌۚ أَلَیۡسَ فِی جَهَنَّمَ مَثۡوࣰى لِّلۡمُتَكَبِّرِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2249, 39, 4119, 61, 'وَیُنَجِّی ٱللَّهُ ٱلَّذِینَ ٱتَّقَوۡا۟ بِمَفَازَتِهِمۡ لَا یَمَسُّهُمُ ٱلسُّوۤءُ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2250, 39, 4120, 62, 'ٱللَّهُ خَـٰلِقُ كُلِّ شَیۡءࣲۖ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ وَكِیلࣱ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2251, 39, 4121, 63, 'لَّهُۥ مَقَالِیدُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۗ وَٱلَّذِینَ كَفَرُوا۟ بِـَٔایَـٰتِ ٱللَّهِ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2252, 39, 4122, 64, 'قُلۡ أَفَغَیۡرَ ٱللَّهِ تَأۡمُرُوۤنِّیۤ أَعۡبُدُ أَیُّهَا ٱلۡجَـٰهِلُونَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2253, 39, 4123, 65, 'وَلَقَدۡ أُوحِیَ إِلَیۡكَ وَإِلَى ٱلَّذِینَ مِن قَبۡلِكَ لَىِٕنۡ أَشۡرَكۡتَ لَیَحۡبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2254, 39, 4124, 66, 'بَلِ ٱللَّهَ فَٱعۡبُدۡ وَكُن مِّنَ ٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2255, 39, 4125, 67, 'وَمَا قَدَرُوا۟ ٱللَّهَ حَقَّ قَدۡرِهِۦ وَٱلۡأَرۡضُ جَمِیعࣰا قَبۡضَتُهُۥ یَوۡمَ ٱلۡقِیَـٰمَةِ وَٱلسَّمَـٰوَ ٰ⁠تُ مَطۡوِیَّـٰتُۢ بِیَمِینِهِۦۚ سُبۡحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2256, 39, 4126, 68, 'وَنُفِخَ فِی ٱلصُّورِ فَصَعِقَ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَن فِی ٱلۡأَرۡضِ إِلَّا مَن شَاۤءَ ٱللَّهُۖ ثُمَّ نُفِخَ فِیهِ أُخۡرَىٰ فَإِذَا هُمۡ قِیَامࣱ یَنظُرُونَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2257, 39, 4127, 69, 'وَأَشۡرَقَتِ ٱلۡأَرۡضُ بِنُورِ رَبِّهَا وَوُضِعَ ٱلۡكِتَـٰبُ وَجِا۟یۤءَ بِٱلنَّبِیِّـۧنَ وَٱلشُّهَدَاۤءِ وَقُضِیَ بَیۡنَهُم بِٱلۡحَقِّ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2258, 39, 4128, 70, 'وَوُفِّیَتۡ كُلُّ نَفۡسࣲ مَّا عَمِلَتۡ وَهُوَ أَعۡلَمُ بِمَا یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2259, 39, 4129, 71, 'وَسِیقَ ٱلَّذِینَ كَفَرُوۤا۟ إِلَىٰ جَهَنَّمَ زُمَرًاۖ حَتَّىٰۤ إِذَا جَاۤءُوهَا فُتِحَتۡ أَبۡوَ ٰ⁠بُهَا وَقَالَ لَهُمۡ خَزَنَتُهَاۤ أَلَمۡ یَأۡتِكُمۡ رُسُلࣱ مِّنكُمۡ یَتۡلُونَ عَلَیۡكُمۡ ءَایَـٰتِ رَبِّكُمۡ وَیُنذِرُونَكُمۡ لِقَاۤءَ یَوۡمِكُمۡ هَـٰذَاۚ قَالُوا۟ بَلَىٰ وَلَـٰكِنۡ حَقَّتۡ كَلِمَةُ ٱلۡعَذَابِ عَلَى ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2260, 39, 4130, 72, 'قِیلَ ٱدۡخُلُوۤا۟ أَبۡوَ ٰ⁠بَ جَهَنَّمَ خَـٰلِدِینَ فِیهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2261, 39, 4131, 73, 'وَسِیقَ ٱلَّذِینَ ٱتَّقَوۡا۟ رَبَّهُمۡ إِلَى ٱلۡجَنَّةِ زُمَرًاۖ حَتَّىٰۤ إِذَا جَاۤءُوهَا وَفُتِحَتۡ أَبۡوَ ٰ⁠بُهَا وَقَالَ لَهُمۡ خَزَنَتُهَا سَلَـٰمٌ عَلَیۡكُمۡ طِبۡتُمۡ فَٱدۡخُلُوهَا خَـٰلِدِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2262, 39, 4132, 74, 'وَقَالُوا۟ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی صَدَقَنَا وَعۡدَهُۥ وَأَوۡرَثَنَا ٱلۡأَرۡضَ نَتَبَوَّأُ مِنَ ٱلۡجَنَّةِ حَیۡثُ نَشَاۤءُۖ فَنِعۡمَ أَجۡرُ ٱلۡعَـٰمِلِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2263, 39, 4133, 75, 'وَتَرَى ٱلۡمَلَـٰۤىِٕكَةَ حَاۤفِّینَ مِنۡ حَوۡلِ ٱلۡعَرۡشِ یُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡۚ وَقُضِیَ بَیۡنَهُم بِٱلۡحَقِّۚ وَقِیلَ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:25:01', '2023-05-27 11:11:01'),
(2264, 38, 3971, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ صۤۚ وَٱلۡقُرۡءَانِ ذِی ٱلذِّكۡرِ\n', NULL, 0, '2023-05-26 17:25:20', '2023-05-27 11:11:04'),
(2265, 38, 3972, 2, 'بَلِ ٱلَّذِینَ كَفَرُوا۟ فِی عِزَّةࣲ وَشِقَاقࣲ\n', NULL, 0, '2023-05-26 17:25:20', '2023-05-27 11:11:04'),
(2266, 38, 3973, 3, 'كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنࣲ فَنَادَوا۟ وَّلَاتَ حِینَ مَنَاصࣲ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:04'),
(2267, 38, 3974, 4, 'وَعَجِبُوۤا۟ أَن جَاۤءَهُم مُّنذِرࣱ مِّنۡهُمۡۖ وَقَالَ ٱلۡكَـٰفِرُونَ هَـٰذَا سَـٰحِرࣱ كَذَّابٌ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:04'),
(2268, 38, 3975, 5, 'أَجَعَلَ ٱلۡـَٔالِهَةَ إِلَـٰهࣰا وَ ٰ⁠حِدًاۖ إِنَّ هَـٰذَا لَشَیۡءٌ عُجَابࣱ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:04'),
(2269, 38, 3976, 6, 'وَٱنطَلَقَ ٱلۡمَلَأُ مِنۡهُمۡ أَنِ ٱمۡشُوا۟ وَٱصۡبِرُوا۟ عَلَىٰۤ ءَالِهَتِكُمۡۖ إِنَّ هَـٰذَا لَشَیۡءࣱ یُرَادُ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:04'),
(2270, 38, 3977, 7, 'مَا سَمِعۡنَا بِهَـٰذَا فِی ٱلۡمِلَّةِ ٱلۡـَٔاخِرَةِ إِنۡ هَـٰذَاۤ إِلَّا ٱخۡتِلَـٰقٌ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:04'),
(2271, 38, 3978, 8, 'أَءُنزِلَ عَلَیۡهِ ٱلذِّكۡرُ مِنۢ بَیۡنِنَاۚ بَلۡ هُمۡ فِی شَكࣲّ مِّن ذِكۡرِیۚ بَل لَّمَّا یَذُوقُوا۟ عَذَابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:04'),
(2272, 38, 3979, 9, 'أَمۡ عِندَهُمۡ خَزَاۤىِٕنُ رَحۡمَةِ رَبِّكَ ٱلۡعَزِیزِ ٱلۡوَهَّابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:04'),
(2273, 38, 3980, 10, 'أَمۡ لَهُم مُّلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَاۖ فَلۡیَرۡتَقُوا۟ فِی ٱلۡأَسۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2274, 38, 3981, 11, 'جُندࣱ مَّا هُنَالِكَ مَهۡزُومࣱ مِّنَ ٱلۡأَحۡزَابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(2275, 38, 3982, 12, 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحࣲ وَعَادࣱ وَفِرۡعَوۡنُ ذُو ٱلۡأَوۡتَادِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2276, 38, 3983, 13, 'وَثَمُودُ وَقَوۡمُ لُوطࣲ وَأَصۡحَـٰبُ لۡـَٔیۡكَةِۚ أُو۟لَـٰۤىِٕكَ ٱلۡأَحۡزَابُ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2277, 38, 3984, 14, 'إِن كُلٌّ إِلَّا كَذَّبَ ٱلرُّسُلَ فَحَقَّ عِقَابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2278, 38, 3985, 15, 'وَمَا یَنظُرُ هَـٰۤؤُلَاۤءِ إِلَّا صَیۡحَةࣰ وَ ٰ⁠حِدَةࣰ مَّا لَهَا مِن فَوَاقࣲ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2279, 38, 3986, 16, 'وَقَالُوا۟ رَبَّنَا عَجِّل لَّنَا قِطَّنَا قَبۡلَ یَوۡمِ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2280, 38, 3987, 17, 'ٱصۡبِرۡ عَلَىٰ مَا یَقُولُونَ وَٱذۡكُرۡ عَبۡدَنَا دَاوُۥدَ ذَا ٱلۡأَیۡدِۖ إِنَّهُۥۤ أَوَّابٌ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2281, 38, 3988, 18, 'إِنَّا سَخَّرۡنَا ٱلۡجِبَالَ مَعَهُۥ یُسَبِّحۡنَ بِٱلۡعَشِیِّ وَٱلۡإِشۡرَاقِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2282, 38, 3989, 19, 'وَٱلطَّیۡرَ مَحۡشُورَةࣰۖ كُلࣱّ لَّهُۥۤ أَوَّابࣱ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2283, 38, 3990, 20, 'وَشَدَدۡنَا مُلۡكَهُۥ وَءَاتَیۡنَـٰهُ ٱلۡحِكۡمَةَ وَفَصۡلَ ٱلۡخِطَابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2284, 38, 3991, 21, '۞ وَهَلۡ أَتَىٰكَ نَبَؤُا۟ ٱلۡخَصۡمِ إِذۡ تَسَوَّرُوا۟ ٱلۡمِحۡرَابَ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2285, 38, 3992, 22, 'إِذۡ دَخَلُوا۟ عَلَىٰ دَاوُۥدَ فَفَزِعَ مِنۡهُمۡۖ قَالُوا۟ لَا تَخَفۡۖ خَصۡمَانِ بَغَىٰ بَعۡضُنَا عَلَىٰ بَعۡضࣲ فَٱحۡكُم بَیۡنَنَا بِٱلۡحَقِّ وَلَا تُشۡطِطۡ وَٱهۡدِنَاۤ إِلَىٰ سَوَاۤءِ ٱلصِّرَ ٰ⁠طِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2286, 38, 3993, 23, 'إِنَّ هَـٰذَاۤ أَخِی لَهُۥ تِسۡعࣱ وَتِسۡعُونَ نَعۡجَةࣰ وَلِیَ نَعۡجَةࣱ وَ ٰ⁠حِدَةࣱ فَقَالَ أَكۡفِلۡنِیهَا وَعَزَّنِی فِی ٱلۡخِطَابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2287, 38, 3994, 24, 'قَالَ لَقَدۡ ظَلَمَكَ بِسُؤَالِ نَعۡجَتِكَ إِلَىٰ نِعَاجِهِۦۖ وَإِنَّ كَثِیرࣰا مِّنَ ٱلۡخُلَطَاۤءِ لَیَبۡغِی بَعۡضُهُمۡ عَلَىٰ بَعۡضٍ إِلَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَقَلِیلࣱ مَّا هُمۡۗ وَظَنَّ دَاوُۥدُ أَنَّمَا فَتَنَّـٰهُ فَٱسۡتَغۡفَرَ رَبَّهُۥ وَخَرَّ رَاكِعࣰا وَأَنَابَ ۩\n', NULL, 1, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2288, 38, 3995, 25, 'فَغَفَرۡنَا لَهُۥ ذَ ٰ⁠لِكَۖ وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابࣲ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2289, 38, 3996, 26, 'یَـٰدَاوُۥدُ إِنَّا جَعَلۡنَـٰكَ خَلِیفَةࣰ فِی ٱلۡأَرۡضِ فَٱحۡكُم بَیۡنَ ٱلنَّاسِ بِٱلۡحَقِّ وَلَا تَتَّبِعِ ٱلۡهَوَىٰ فَیُضِلَّكَ عَن سَبِیلِ ٱللَّهِۚ إِنَّ ٱلَّذِینَ یَضِلُّونَ عَن سَبِیلِ ٱللَّهِ لَهُمۡ عَذَابࣱ شَدِیدُۢ بِمَا نَسُوا۟ یَوۡمَ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:05'),
(2290, 38, 3997, 27, 'وَمَا خَلَقۡنَا ٱلسَّمَاۤءَ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَا بَـٰطِلࣰاۚ ذَ ٰ⁠لِكَ ظَنُّ ٱلَّذِینَ كَفَرُوا۟ۚ فَوَیۡلࣱ لِّلَّذِینَ كَفَرُوا۟ مِنَ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:06'),
(2291, 38, 3998, 28, 'أَمۡ نَجۡعَلُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ كَٱلۡمُفۡسِدِینَ فِی ٱلۡأَرۡضِ أَمۡ نَجۡعَلُ ٱلۡمُتَّقِینَ كَٱلۡفُجَّارِ\n', NULL, 0, '2023-05-26 17:25:21', '2023-05-27 11:11:06'),
(2292, 38, 3999, 29, 'كِتَـٰبٌ أَنزَلۡنَـٰهُ إِلَیۡكَ مُبَـٰرَكࣱ لِّیَدَّبَّرُوۤا۟ ءَایَـٰتِهِۦ وَلِیَتَذَكَّرَ أُو۟لُوا۟ ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2293, 38, 4000, 30, 'وَوَهَبۡنَا لِدَاوُۥدَ سُلَیۡمَـٰنَۚ نِعۡمَ ٱلۡعَبۡدُ إِنَّهُۥۤ أَوَّابٌ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2294, 38, 4001, 31, 'إِذۡ عُرِضَ عَلَیۡهِ بِٱلۡعَشِیِّ ٱلصَّـٰفِنَـٰتُ ٱلۡجِیَادُ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2295, 38, 4002, 32, 'فَقَالَ إِنِّیۤ أَحۡبَبۡتُ حُبَّ ٱلۡخَیۡرِ عَن ذِكۡرِ رَبِّی حَتَّىٰ تَوَارَتۡ بِٱلۡحِجَابِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2296, 38, 4003, 33, 'رُدُّوهَا عَلَیَّۖ فَطَفِقَ مَسۡحَۢا بِٱلسُّوقِ وَٱلۡأَعۡنَاقِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2297, 38, 4004, 34, 'وَلَقَدۡ فَتَنَّا سُلَیۡمَـٰنَ وَأَلۡقَیۡنَا عَلَىٰ كُرۡسِیِّهِۦ جَسَدࣰا ثُمَّ أَنَابَ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2298, 38, 4005, 35, 'قَالَ رَبِّ ٱغۡفِرۡ لِی وَهَبۡ لِی مُلۡكࣰا لَّا یَنۢبَغِی لِأَحَدࣲ مِّنۢ بَعۡدِیۤۖ إِنَّكَ أَنتَ ٱلۡوَهَّابُ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2299, 38, 4006, 36, 'فَسَخَّرۡنَا لَهُ ٱلرِّیحَ تَجۡرِی بِأَمۡرِهِۦ رُخَاۤءً حَیۡثُ أَصَابَ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2300, 38, 4007, 37, 'وَٱلشَّیَـٰطِینَ كُلَّ بَنَّاۤءࣲ وَغَوَّاصࣲ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2301, 38, 4008, 38, 'وَءَاخَرِینَ مُقَرَّنِینَ فِی ٱلۡأَصۡفَادِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2302, 38, 4009, 39, 'هَـٰذَا عَطَاۤؤُنَا فَٱمۡنُنۡ أَوۡ أَمۡسِكۡ بِغَیۡرِ حِسَابࣲ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2303, 38, 4010, 40, 'وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابࣲ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2304, 38, 4011, 41, 'وَٱذۡكُرۡ عَبۡدَنَاۤ أَیُّوبَ إِذۡ نَادَىٰ رَبَّهُۥۤ أَنِّی مَسَّنِیَ ٱلشَّیۡطَـٰنُ بِنُصۡبࣲ وَعَذَابٍ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2305, 38, 4012, 42, 'ٱرۡكُضۡ بِرِجۡلِكَۖ هَـٰذَا مُغۡتَسَلُۢ بَارِدࣱ وَشَرَابࣱ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2306, 38, 4013, 43, 'وَوَهَبۡنَا لَهُۥۤ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةࣰ مِّنَّا وَذِكۡرَىٰ لِأُو۟لِی ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2307, 38, 4014, 44, 'وَخُذۡ بِیَدِكَ ضِغۡثࣰا فَٱضۡرِب بِّهِۦ وَلَا تَحۡنَثۡۗ إِنَّا وَجَدۡنَـٰهُ صَابِرࣰاۚ نِّعۡمَ ٱلۡعَبۡدُ إِنَّهُۥۤ أَوَّابࣱ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2308, 38, 4015, 45, 'وَٱذۡكُرۡ عِبَـٰدَنَاۤ إِبۡرَ ٰ⁠هِیمَ وَإِسۡحَـٰقَ وَیَعۡقُوبَ أُو۟لِی ٱلۡأَیۡدِی وَٱلۡأَبۡصَـٰرِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:06'),
(2309, 38, 4016, 46, 'إِنَّاۤ أَخۡلَصۡنَـٰهُم بِخَالِصَةࣲ ذِكۡرَى ٱلدَّارِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:07'),
(2310, 38, 4017, 47, 'وَإِنَّهُمۡ عِندَنَا لَمِنَ ٱلۡمُصۡطَفَیۡنَ ٱلۡأَخۡیَارِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:07'),
(2311, 38, 4018, 48, 'وَٱذۡكُرۡ إِسۡمَـٰعِیلَ وَٱلۡیَسَعَ وَذَا ٱلۡكِفۡلِۖ وَكُلࣱّ مِّنَ ٱلۡأَخۡیَارِ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:07'),
(2312, 38, 4019, 49, 'هَـٰذَا ذِكۡرࣱۚ وَإِنَّ لِلۡمُتَّقِینَ لَحُسۡنَ مَـَٔابࣲ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:07'),
(2313, 38, 4020, 50, 'جَنَّـٰتِ عَدۡنࣲ مُّفَتَّحَةࣰ لَّهُمُ ٱلۡأَبۡوَ ٰ⁠بُ\n', NULL, 0, '2023-05-26 17:25:22', '2023-05-27 11:11:07'),
(2314, 38, 4021, 51, 'مُتَّكِـِٔینَ فِیهَا یَدۡعُونَ فِیهَا بِفَـٰكِهَةࣲ كَثِیرَةࣲ وَشَرَابࣲ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2315, 38, 4022, 52, '۞ وَعِندَهُمۡ قَـٰصِرَ ٰ⁠تُ ٱلطَّرۡفِ أَتۡرَابٌ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2316, 38, 4023, 53, 'هَـٰذَا مَا تُوعَدُونَ لِیَوۡمِ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2317, 38, 4024, 54, 'إِنَّ هَـٰذَا لَرِزۡقُنَا مَا لَهُۥ مِن نَّفَادٍ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2318, 38, 4025, 55, 'هَـٰذَاۚ وَإِنَّ لِلطَّـٰغِینَ لَشَرَّ مَـَٔابࣲ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2319, 38, 4026, 56, 'جَهَنَّمَ یَصۡلَوۡنَهَا فَبِئۡسَ ٱلۡمِهَادُ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2320, 38, 4027, 57, 'هَـٰذَا فَلۡیَذُوقُوهُ حَمِیمࣱ وَغَسَّاقࣱ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2321, 38, 4028, 58, 'وَءَاخَرُ مِن شَكۡلِهِۦۤ أَزۡوَ ٰ⁠جٌ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2322, 38, 4029, 59, 'هَـٰذَا فَوۡجࣱ مُّقۡتَحِمࣱ مَّعَكُمۡ لَا مَرۡحَبَۢا بِهِمۡۚ إِنَّهُمۡ صَالُوا۟ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2323, 38, 4030, 60, 'قَالُوا۟ بَلۡ أَنتُمۡ لَا مَرۡحَبَۢا بِكُمۡۖ أَنتُمۡ قَدَّمۡتُمُوهُ لَنَاۖ فَبِئۡسَ ٱلۡقَرَارُ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2324, 38, 4031, 61, 'قَالُوا۟ رَبَّنَا مَن قَدَّمَ لَنَا هَـٰذَا فَزِدۡهُ عَذَابࣰا ضِعۡفࣰا فِی ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2325, 38, 4032, 62, 'وَقَالُوا۟ مَا لَنَا لَا نَرَىٰ رِجَالࣰا كُنَّا نَعُدُّهُم مِّنَ ٱلۡأَشۡرَارِ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2326, 38, 4033, 63, 'أَتَّخَذۡنَـٰهُمۡ سِخۡرِیًّا أَمۡ زَاغَتۡ عَنۡهُمُ ٱلۡأَبۡصَـٰرُ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:07'),
(2327, 38, 4034, 64, 'إِنَّ ذَ ٰ⁠لِكَ لَحَقࣱّ تَخَاصُمُ أَهۡلِ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:08'),
(2328, 38, 4035, 65, 'قُلۡ إِنَّمَاۤ أَنَا۠ مُنذِرࣱۖ وَمَا مِنۡ إِلَـٰهٍ إِلَّا ٱللَّهُ ٱلۡوَ ٰ⁠حِدُ ٱلۡقَهَّارُ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:08'),
(2329, 38, 4036, 66, 'رَبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَا ٱلۡعَزِیزُ ٱلۡغَفَّـٰرُ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:08'),
(2330, 38, 4037, 67, 'قُلۡ هُوَ نَبَؤٌا۟ عَظِیمٌ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:08'),
(2331, 38, 4038, 68, 'أَنتُمۡ عَنۡهُ مُعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:25:23', '2023-05-27 11:11:08'),
(2332, 38, 4039, 69, 'مَا كَانَ لِیَ مِنۡ عِلۡمِۭ بِٱلۡمَلَإِ ٱلۡأَعۡلَىٰۤ إِذۡ یَخۡتَصِمُونَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2333, 38, 4040, 70, 'إِن یُوحَىٰۤ إِلَیَّ إِلَّاۤ أَنَّمَاۤ أَنَا۠ نَذِیرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2334, 38, 4041, 71, 'إِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰۤىِٕكَةِ إِنِّی خَـٰلِقُۢ بَشَرࣰا مِّن طِینࣲ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2335, 38, 4042, 72, 'فَإِذَا سَوَّیۡتُهُۥ وَنَفَخۡتُ فِیهِ مِن رُّوحِی فَقَعُوا۟ لَهُۥ سَـٰجِدِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2336, 38, 4043, 73, 'فَسَجَدَ ٱلۡمَلَـٰۤىِٕكَةُ كُلُّهُمۡ أَجۡمَعُونَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2337, 38, 4044, 74, 'إِلَّاۤ إِبۡلِیسَ ٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2338, 38, 4045, 75, 'قَالَ یَـٰۤإِبۡلِیسُ مَا مَنَعَكَ أَن تَسۡجُدَ لِمَا خَلَقۡتُ بِیَدَیَّۖ أَسۡتَكۡبَرۡتَ أَمۡ كُنتَ مِنَ ٱلۡعَالِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2339, 38, 4046, 76, 'قَالَ أَنَا۠ خَیۡرࣱ مِّنۡهُ خَلَقۡتَنِی مِن نَّارࣲ وَخَلَقۡتَهُۥ مِن طِینࣲ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2340, 38, 4047, 77, 'قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِیمࣱ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2341, 38, 4048, 78, 'وَإِنَّ عَلَیۡكَ لَعۡنَتِیۤ إِلَىٰ یَوۡمِ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2342, 38, 4049, 79, 'قَالَ رَبِّ فَأَنظِرۡنِیۤ إِلَىٰ یَوۡمِ یُبۡعَثُونَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2343, 38, 4050, 80, 'قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2344, 38, 4051, 81, 'إِلَىٰ یَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2345, 38, 4052, 82, 'قَالَ فَبِعِزَّتِكَ لَأُغۡوِیَنَّهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:08'),
(2346, 38, 4053, 83, 'إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:09'),
(2347, 38, 4054, 84, 'قَالَ فَٱلۡحَقُّ وَٱلۡحَقَّ أَقُولُ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:09'),
(2348, 38, 4055, 85, 'لَأَمۡلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنۡهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:09'),
(2349, 38, 4056, 86, 'قُلۡ مَاۤ أَسۡـَٔلُكُمۡ عَلَیۡهِ مِنۡ أَجۡرࣲ وَمَاۤ أَنَا۠ مِنَ ٱلۡمُتَكَلِّفِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:09'),
(2350, 38, 4057, 87, 'إِنۡ هُوَ إِلَّا ذِكۡرࣱ لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:09'),
(2351, 38, 4058, 88, 'وَلَتَعۡلَمُنَّ نَبَأَهُۥ بَعۡدَ حِینِۭ\n', NULL, 0, '2023-05-26 17:25:24', '2023-05-27 11:11:09'),
(2352, 37, 3789, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ وَٱلصَّـٰۤفَّـٰتِ صَفࣰّا\n', NULL, 0, '2023-05-26 17:25:46', '2023-05-27 11:11:11'),
(2353, 37, 3790, 2, 'فَٱلزَّ ٰ⁠جِرَ ٰ⁠تِ زَجۡرࣰا\n', NULL, 0, '2023-05-26 17:25:46', '2023-05-27 11:11:11'),
(2354, 37, 3791, 3, 'فَٱلتَّـٰلِیَـٰتِ ذِكۡرًا\n', NULL, 0, '2023-05-26 17:25:46', '2023-05-27 11:11:11'),
(2355, 37, 3792, 4, 'إِنَّ إِلَـٰهَكُمۡ لَوَ ٰ⁠حِدࣱ\n', NULL, 0, '2023-05-26 17:25:46', '2023-05-27 11:11:11'),
(2356, 37, 3793, 5, 'رَّبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَا وَرَبُّ ٱلۡمَشَـٰرِقِ\n', NULL, 0, '2023-05-26 17:25:46', '2023-05-27 11:11:12'),
(2357, 37, 3794, 6, 'إِنَّا زَیَّنَّا ٱلسَّمَاۤءَ ٱلدُّنۡیَا بِزِینَةٍ ٱلۡكَوَاكِبِ\n', NULL, 0, '2023-05-26 17:25:46', '2023-05-27 11:11:12'),
(2358, 37, 3795, 7, 'وَحِفۡظࣰا مِّن كُلِّ شَیۡطَـٰنࣲ مَّارِدࣲ\n', NULL, 0, '2023-05-26 17:25:46', '2023-05-27 11:11:12'),
(2359, 37, 3796, 8, 'لَّا یَسَّمَّعُونَ إِلَى ٱلۡمَلَإِ ٱلۡأَعۡلَىٰ وَیُقۡذَفُونَ مِن كُلِّ جَانِبࣲ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2360, 37, 3797, 9, 'دُحُورࣰاۖ وَلَهُمۡ عَذَابࣱ وَاصِبٌ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2361, 37, 3798, 10, 'إِلَّا مَنۡ خَطِفَ ٱلۡخَطۡفَةَ فَأَتۡبَعَهُۥ شِهَابࣱ ثَاقِبࣱ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2362, 37, 3799, 11, 'فَٱسۡتَفۡتِهِمۡ أَهُمۡ أَشَدُّ خَلۡقًا أَم مَّنۡ خَلَقۡنَاۤۚ إِنَّا خَلَقۡنَـٰهُم مِّن طِینࣲ لَّازِبِۭ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2363, 37, 3800, 12, 'بَلۡ عَجِبۡتَ وَیَسۡخَرُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2364, 37, 3801, 13, 'وَإِذَا ذُكِّرُوا۟ لَا یَذۡكُرُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2365, 37, 3802, 14, 'وَإِذَا رَأَوۡا۟ ءَایَةࣰ یَسۡتَسۡخِرُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2366, 37, 3803, 15, 'وَقَالُوۤا۟ إِنۡ هَـٰذَاۤ إِلَّا سِحۡرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2367, 37, 3804, 16, 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابࣰا وَعِظَـٰمًا أَءِنَّا لَمَبۡعُوثُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2368, 37, 3805, 17, 'أَوَءَابَاۤؤُنَا ٱلۡأَوَّلُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2369, 37, 3806, 18, 'قُلۡ نَعَمۡ وَأَنتُمۡ دَ ٰ⁠خِرُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2370, 37, 3807, 19, 'فَإِنَّمَا هِیَ زَجۡرَةࣱ وَ ٰ⁠حِدَةࣱ فَإِذَا هُمۡ یَنظُرُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2371, 37, 3808, 20, 'وَقَالُوا۟ یَـٰوَیۡلَنَا هَـٰذَا یَوۡمُ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2372, 37, 3809, 21, 'هَـٰذَا یَوۡمُ ٱلۡفَصۡلِ ٱلَّذِی كُنتُم بِهِۦ تُكَذِّبُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2373, 37, 3810, 22, '۞ ٱحۡشُرُوا۟ ٱلَّذِینَ ظَلَمُوا۟ وَأَزۡوَ ٰ⁠جَهُمۡ وَمَا كَانُوا۟ یَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2374, 37, 3811, 23, 'مِن دُونِ ٱللَّهِ فَٱهۡدُوهُمۡ إِلَىٰ صِرَ ٰ⁠طِ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:12'),
(2375, 37, 3812, 24, 'وَقِفُوهُمۡۖ إِنَّهُم مَّسۡـُٔولُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2376, 37, 3813, 25, 'مَا لَكُمۡ لَا تَنَاصَرُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2377, 37, 3814, 26, 'بَلۡ هُمُ ٱلۡیَوۡمَ مُسۡتَسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2378, 37, 3815, 27, 'وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضࣲ یَتَسَاۤءَلُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2379, 37, 3816, 28, 'قَالُوۤا۟ إِنَّكُمۡ كُنتُمۡ تَأۡتُونَنَا عَنِ ٱلۡیَمِینِ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2380, 37, 3817, 29, 'قَالُوا۟ بَل لَّمۡ تَكُونُوا۟ مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2381, 37, 3818, 30, 'وَمَا كَانَ لَنَا عَلَیۡكُم مِّن سُلۡطَـٰنِۭۖ بَلۡ كُنتُمۡ قَوۡمࣰا طَـٰغِینَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2382, 37, 3819, 31, 'فَحَقَّ عَلَیۡنَا قَوۡلُ رَبِّنَاۤۖ إِنَّا لَذَاۤىِٕقُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2383, 37, 3820, 32, 'فَأَغۡوَیۡنَـٰكُمۡ إِنَّا كُنَّا غَـٰوِینَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2384, 37, 3821, 33, 'فَإِنَّهُمۡ یَوۡمَىِٕذࣲ فِی ٱلۡعَذَابِ مُشۡتَرِكُونَ\n', NULL, 0, '2023-05-26 17:25:47', '2023-05-27 11:11:13'),
(2385, 37, 3822, 34, 'إِنَّا كَذَ ٰ⁠لِكَ نَفۡعَلُ بِٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:25:48', '2023-05-27 11:11:13'),
