(2542, 36, 3714, 9, 'وَجَعَلۡنَا مِنۢ بَیۡنِ أَیۡدِیهِمۡ سَدࣰّا وَمِنۡ خَلۡفِهِمۡ سَدࣰّا فَأَغۡشَیۡنَـٰهُمۡ فَهُمۡ لَا یُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:25:57', '2023-05-27 11:11:24'),
(2543, 36, 3715, 10, 'وَسَوَاۤءٌ عَلَیۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:25:57', '2023-05-27 11:11:24'),
(2544, 36, 3716, 11, 'إِنَّمَا تُنذِرُ مَنِ ٱتَّبَعَ ٱلذِّكۡرَ وَخَشِیَ ٱلرَّحۡمَـٰنَ بِٱلۡغَیۡبِۖ فَبَشِّرۡهُ بِمَغۡفِرَةࣲ وَأَجۡرࣲ كَرِیمٍ\n', NULL, 0, '2023-05-26 17:25:57', '2023-05-27 11:11:24'),
(2545, 36, 3717, 12, 'إِنَّا نَحۡنُ نُحۡیِ ٱلۡمَوۡتَىٰ وَنَكۡتُبُ مَا قَدَّمُوا۟ وَءَاثَـٰرَهُمۡۚ وَكُلَّ شَیۡءٍ أَحۡصَیۡنَـٰهُ فِیۤ إِمَامࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:25:57', '2023-05-27 11:11:24'),
(2546, 36, 3718, 13, 'وَٱضۡرِبۡ لَهُم مَّثَلًا أَصۡحَـٰبَ ٱلۡقَرۡیَةِ إِذۡ جَاۤءَهَا ٱلۡمُرۡسَلُونَ\n', NULL, 0, '2023-05-26 17:25:57', '2023-05-27 11:11:24'),
(2547, 36, 3719, 14, 'إِذۡ أَرۡسَلۡنَاۤ إِلَیۡهِمُ ٱثۡنَیۡنِ فَكَذَّبُوهُمَا فَعَزَّزۡنَا بِثَالِثࣲ فَقَالُوۤا۟ إِنَّاۤ إِلَیۡكُم مُّرۡسَلُونَ\n', NULL, 0, '2023-05-26 17:25:57', '2023-05-27 11:11:24'),
(2548, 36, 3720, 15, 'قَالُوا۟ مَاۤ أَنتُمۡ إِلَّا بَشَرࣱ مِّثۡلُنَا وَمَاۤ أَنزَلَ ٱلرَّحۡمَـٰنُ مِن شَیۡءٍ إِنۡ أَنتُمۡ إِلَّا تَكۡذِبُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:24'),
(2549, 36, 3721, 16, 'قَالُوا۟ رَبُّنَا یَعۡلَمُ إِنَّاۤ إِلَیۡكُمۡ لَمُرۡسَلُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:24'),
(2550, 36, 3722, 17, 'وَمَا عَلَیۡنَاۤ إِلَّا ٱلۡبَلَـٰغُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:24'),
(2551, 36, 3723, 18, 'قَالُوۤا۟ إِنَّا تَطَیَّرۡنَا بِكُمۡۖ لَىِٕن لَّمۡ تَنتَهُوا۟ لَنَرۡجُمَنَّكُمۡ وَلَیَمَسَّنَّكُم مِّنَّا عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:24'),
(2552, 36, 3724, 19, 'قَالُوا۟ طَـٰۤىِٕرُكُم مَّعَكُمۡ أَىِٕن ذُكِّرۡتُمۚ بَلۡ أَنتُمۡ قَوۡمࣱ مُّسۡرِفُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:24'),
(2553, 36, 3725, 20, 'وَجَاۤءَ مِنۡ أَقۡصَا ٱلۡمَدِینَةِ رَجُلࣱ یَسۡعَىٰ قَالَ یَـٰقَوۡمِ ٱتَّبِعُوا۟ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2554, 36, 3726, 21, 'ٱتَّبِعُوا۟ مَن لَّا یَسۡـَٔلُكُمۡ أَجۡرࣰا وَهُم مُّهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2555, 36, 3727, 22, 'وَمَا لِیَ لَاۤ أَعۡبُدُ ٱلَّذِی فَطَرَنِی وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2556, 36, 3728, 23, 'ءَأَتَّخِذُ مِن دُونِهِۦۤ ءَالِهَةً إِن یُرِدۡنِ ٱلرَّحۡمَـٰنُ بِضُرࣲّ لَّا تُغۡنِ عَنِّی شَفَـٰعَتُهُمۡ شَیۡـࣰٔا وَلَا یُنقِذُونِ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2557, 36, 3729, 24, 'إِنِّیۤ إِذࣰا لَّفِی ضَلَـٰلࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2558, 36, 3730, 25, 'إِنِّیۤ ءَامَنتُ بِرَبِّكُمۡ فَٱسۡمَعُونِ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2559, 36, 3731, 26, 'قِیلَ ٱدۡخُلِ ٱلۡجَنَّةَۖ قَالَ یَـٰلَیۡتَ قَوۡمِی یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2560, 36, 3732, 27, 'بِمَا غَفَرَ لِی رَبِّی وَجَعَلَنِی مِنَ ٱلۡمُكۡرَمِینَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2561, 36, 3733, 28, '۞ وَمَاۤ أَنزَلۡنَا عَلَىٰ قَوۡمِهِۦ مِنۢ بَعۡدِهِۦ مِن جُندࣲ مِّنَ ٱلسَّمَاۤءِ وَمَا كُنَّا مُنزِلِینَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2562, 36, 3734, 29, 'إِن كَانَتۡ إِلَّا صَیۡحَةࣰ وَ ٰ⁠حِدَةࣰ فَإِذَا هُمۡ خَـٰمِدُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2563, 36, 3735, 30, 'یَـٰحَسۡرَةً عَلَى ٱلۡعِبَادِۚ مَا یَأۡتِیهِم مِّن رَّسُولٍ إِلَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2564, 36, 3736, 31, 'أَلَمۡ یَرَوۡا۟ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ أَنَّهُمۡ إِلَیۡهِمۡ لَا یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2565, 36, 3737, 32, 'وَإِن كُلࣱّ لَّمَّا جَمِیعࣱ لَّدَیۡنَا مُحۡضَرُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2566, 36, 3738, 33, 'وَءَایَةࣱ لَّهُمُ ٱلۡأَرۡضُ ٱلۡمَیۡتَةُ أَحۡیَیۡنَـٰهَا وَأَخۡرَجۡنَا مِنۡهَا حَبࣰّا فَمِنۡهُ یَأۡكُلُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2567, 36, 3739, 34, 'وَجَعَلۡنَا فِیهَا جَنَّـٰتࣲ مِّن نَّخِیلࣲ وَأَعۡنَـٰبࣲ وَفَجَّرۡنَا فِیهَا مِنَ ٱلۡعُیُونِ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2568, 36, 3740, 35, 'لِیَأۡكُلُوا۟ مِن ثَمَرِهِۦ وَمَا عَمِلَتۡهُ أَیۡدِیهِمۡۚ أَفَلَا یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:25:58', '2023-05-27 11:11:25'),
(2569, 36, 3741, 36, 'سُبۡحَـٰنَ ٱلَّذِی خَلَقَ ٱلۡأَزۡوَ ٰ⁠جَ كُلَّهَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ وَمِنۡ أَنفُسِهِمۡ وَمِمَّا لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:25'),
(2570, 36, 3742, 37, 'وَءَایَةࣱ لَّهُمُ ٱلَّیۡلُ نَسۡلَخُ مِنۡهُ ٱلنَّهَارَ فَإِذَا هُم مُّظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:25'),
(2571, 36, 3743, 38, 'وَٱلشَّمۡسُ تَجۡرِی لِمُسۡتَقَرࣲّ لَّهَاۚ ذَ ٰ⁠لِكَ تَقۡدِیرُ ٱلۡعَزِیزِ ٱلۡعَلِیمِ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:25'),
(2572, 36, 3744, 39, 'وَٱلۡقَمَرَ قَدَّرۡنَـٰهُ مَنَازِلَ حَتَّىٰ عَادَ كَٱلۡعُرۡجُونِ ٱلۡقَدِیمِ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:25'),
(2573, 36, 3745, 40, 'لَا ٱلشَّمۡسُ یَنۢبَغِی لَهَاۤ أَن تُدۡرِكَ ٱلۡقَمَرَ وَلَا ٱلَّیۡلُ سَابِقُ ٱلنَّهَارِۚ وَكُلࣱّ فِی فَلَكࣲ یَسۡبَحُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2574, 36, 3746, 41, 'وَءَایَةࣱ لَّهُمۡ أَنَّا حَمَلۡنَا ذُرِّیَّتَهُمۡ فِی ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2575, 36, 3747, 42, 'وَخَلَقۡنَا لَهُم مِّن مِّثۡلِهِۦ مَا یَرۡكَبُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2576, 36, 3748, 43, 'وَإِن نَّشَأۡ نُغۡرِقۡهُمۡ فَلَا صَرِیخَ لَهُمۡ وَلَا هُمۡ یُنقَذُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2577, 36, 3749, 44, 'إِلَّا رَحۡمَةࣰ مِّنَّا وَمَتَـٰعًا إِلَىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2578, 36, 3750, 45, 'وَإِذَا قِیلَ لَهُمُ ٱتَّقُوا۟ مَا بَیۡنَ أَیۡدِیكُمۡ وَمَا خَلۡفَكُمۡ لَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2579, 36, 3751, 46, 'وَمَا تَأۡتِیهِم مِّنۡ ءَایَةࣲ مِّنۡ ءَایَـٰتِ رَبِّهِمۡ إِلَّا كَانُوا۟ عَنۡهَا مُعۡرِضِینَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2580, 36, 3752, 47, 'وَإِذَا قِیلَ لَهُمۡ أَنفِقُوا۟ مِمَّا رَزَقَكُمُ ٱللَّهُ قَالَ ٱلَّذِینَ كَفَرُوا۟ لِلَّذِینَ ءَامَنُوۤا۟ أَنُطۡعِمُ مَن لَّوۡ یَشَاۤءُ ٱللَّهُ أَطۡعَمَهُۥۤ إِنۡ أَنتُمۡ إِلَّا فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2581, 36, 3753, 48, 'وَیَقُولُونَ مَتَىٰ هَـٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2582, 36, 3754, 49, 'مَا یَنظُرُونَ إِلَّا صَیۡحَةࣰ وَ ٰ⁠حِدَةࣰ تَأۡخُذُهُمۡ وَهُمۡ یَخِصِّمُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2583, 36, 3755, 50, 'فَلَا یَسۡتَطِیعُونَ تَوۡصِیَةࣰ وَلَاۤ إِلَىٰۤ أَهۡلِهِمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2584, 36, 3756, 51, 'وَنُفِخَ فِی ٱلصُّورِ فَإِذَا هُم مِّنَ ٱلۡأَجۡدَاثِ إِلَىٰ رَبِّهِمۡ یَنسِلُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2585, 36, 3757, 52, 'قَالُوا۟ یَـٰوَیۡلَنَا مَنۢ بَعَثَنَا مِن مَّرۡقَدِنَاۜۗ هَـٰذَا مَا وَعَدَ ٱلرَّحۡمَـٰنُ وَصَدَقَ ٱلۡمُرۡسَلُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2586, 36, 3758, 53, 'إِن كَانَتۡ إِلَّا صَیۡحَةࣰ وَ ٰ⁠حِدَةࣰ فَإِذَا هُمۡ جَمِیعࣱ لَّدَیۡنَا مُحۡضَرُونَ\n', NULL, 0, '2023-05-26 17:25:59', '2023-05-27 11:11:26'),
(2587, 36, 3759, 54, 'فَٱلۡیَوۡمَ لَا تُظۡلَمُ نَفۡسࣱ شَیۡـࣰٔا وَلَا تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:26'),
(2588, 36, 3760, 55, 'إِنَّ أَصۡحَـٰبَ ٱلۡجَنَّةِ ٱلۡیَوۡمَ فِی شُغُلࣲ فَـٰكِهُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:26'),
(2589, 36, 3761, 56, 'هُمۡ وَأَزۡوَ ٰ⁠جُهُمۡ فِی ظِلَـٰلٍ عَلَى ٱلۡأَرَاۤىِٕكِ مُتَّكِـُٔونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:26'),
(2590, 36, 3762, 57, 'لَهُمۡ فِیهَا فَـٰكِهَةࣱ وَلَهُم مَّا یَدَّعُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:26'),
(2591, 36, 3763, 58, 'سَلَـٰمࣱ قَوۡلࣰا مِّن رَّبࣲّ رَّحِیمࣲ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:26'),
(2592, 36, 3764, 59, 'وَٱمۡتَـٰزُوا۟ ٱلۡیَوۡمَ أَیُّهَا ٱلۡمُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:26'),
(2593, 36, 3765, 60, '۞ أَلَمۡ أَعۡهَدۡ إِلَیۡكُمۡ یَـٰبَنِیۤ ءَادَمَ أَن لَّا تَعۡبُدُوا۟ ٱلشَّیۡطَـٰنَۖ إِنَّهُۥ لَكُمۡ عَدُوࣱّ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2594, 36, 3766, 61, 'وَأَنِ ٱعۡبُدُونِیۚ هَـٰذَا صِرَ ٰ⁠طࣱ مُّسۡتَقِیمࣱ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2595, 36, 3767, 62, 'وَلَقَدۡ أَضَلَّ مِنكُمۡ جِبِلࣰّا كَثِیرًاۖ أَفَلَمۡ تَكُونُوا۟ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2596, 36, 3768, 63, 'هَـٰذِهِۦ جَهَنَّمُ ٱلَّتِی كُنتُمۡ تُوعَدُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2597, 36, 3769, 64, 'ٱصۡلَوۡهَا ٱلۡیَوۡمَ بِمَا كُنتُمۡ تَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2598, 36, 3770, 65, 'ٱلۡیَوۡمَ نَخۡتِمُ عَلَىٰۤ أَفۡوَ ٰ⁠هِهِمۡ وَتُكَلِّمُنَاۤ أَیۡدِیهِمۡ وَتَشۡهَدُ أَرۡجُلُهُم بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2599, 36, 3771, 66, 'وَلَوۡ نَشَاۤءُ لَطَمَسۡنَا عَلَىٰۤ أَعۡیُنِهِمۡ فَٱسۡتَبَقُوا۟ ٱلصِّرَ ٰ⁠طَ فَأَنَّىٰ یُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2600, 36, 3772, 67, 'وَلَوۡ نَشَاۤءُ لَمَسَخۡنَـٰهُمۡ عَلَىٰ مَكَانَتِهِمۡ فَمَا ٱسۡتَطَـٰعُوا۟ مُضِیࣰّا وَلَا یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2601, 36, 3773, 68, 'وَمَن نُّعَمِّرۡهُ نُنَكِّسۡهُ فِی ٱلۡخَلۡقِۚ أَفَلَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2602, 36, 3774, 69, 'وَمَا عَلَّمۡنَـٰهُ ٱلشِّعۡرَ وَمَا یَنۢبَغِی لَهُۥۤۚ إِنۡ هُوَ إِلَّا ذِكۡرࣱ وَقُرۡءَانࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2603, 36, 3775, 70, 'لِّیُنذِرَ مَن كَانَ حَیࣰّا وَیَحِقَّ ٱلۡقَوۡلُ عَلَى ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2604, 36, 3776, 71, 'أَوَلَمۡ یَرَوۡا۟ أَنَّا خَلَقۡنَا لَهُم مِّمَّا عَمِلَتۡ أَیۡدِینَاۤ أَنۡعَـٰمࣰا فَهُمۡ لَهَا مَـٰلِكُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2605, 36, 3777, 72, 'وَذَلَّلۡنَـٰهَا لَهُمۡ فَمِنۡهَا رَكُوبُهُمۡ وَمِنۡهَا یَأۡكُلُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2606, 36, 3778, 73, 'وَلَهُمۡ فِیهَا مَنَـٰفِعُ وَمَشَارِبُۚ أَفَلَا یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:26:00', '2023-05-27 11:11:27'),
(2607, 36, 3779, 74, 'وَٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ ءَالِهَةࣰ لَّعَلَّهُمۡ یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:27'),
(2608, 36, 3780, 75, 'لَا یَسۡتَطِیعُونَ نَصۡرَهُمۡ وَهُمۡ لَهُمۡ جُندࣱ مُّحۡضَرُونَ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:27'),
(2609, 36, 3781, 76, 'فَلَا یَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّا نَعۡلَمُ مَا یُسِرُّونَ وَمَا یُعۡلِنُونَ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:27'),
(2610, 36, 3782, 77, 'أَوَلَمۡ یَرَ ٱلۡإِنسَـٰنُ أَنَّا خَلَقۡنَـٰهُ مِن نُّطۡفَةࣲ فَإِذَا هُوَ خَصِیمࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:27'),
(2611, 36, 3783, 78, 'وَضَرَبَ لَنَا مَثَلࣰا وَنَسِیَ خَلۡقَهُۥۖ قَالَ مَن یُحۡیِ ٱلۡعِظَـٰمَ وَهِیَ رَمِیمࣱ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:27'),
(2612, 36, 3784, 79, 'قُلۡ یُحۡیِیهَا ٱلَّذِیۤ أَنشَأَهَاۤ أَوَّلَ مَرَّةࣲۖ وَهُوَ بِكُلِّ خَلۡقٍ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:27'),
(2613, 36, 3785, 80, 'ٱلَّذِی جَعَلَ لَكُم مِّنَ ٱلشَّجَرِ ٱلۡأَخۡضَرِ نَارࣰا فَإِذَاۤ أَنتُم مِّنۡهُ تُوقِدُونَ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:27'),
(2614, 36, 3786, 81, 'أَوَلَیۡسَ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِقَـٰدِرٍ عَلَىٰۤ أَن یَخۡلُقَ مِثۡلَهُمۚ بَلَىٰ وَهُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:28'),
(2615, 36, 3787, 82, 'إِنَّمَاۤ أَمۡرُهُۥۤ إِذَاۤ أَرَادَ شَیۡـًٔا أَن یَقُولَ لَهُۥ كُن فَیَكُونُ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:28'),
(2616, 36, 3788, 83, 'فَسُبۡحَـٰنَ ٱلَّذِی بِیَدِهِۦ مَلَكُوتُ كُلِّ شَیۡءࣲ وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:26:01', '2023-05-27 11:11:28'),
(2617, 35, 3661, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱلۡحَمۡدُ لِلَّهِ فَاطِرِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ جَاعِلِ ٱلۡمَلَـٰۤىِٕكَةِ رُسُلًا أُو۟لِیۤ أَجۡنِحَةࣲ مَّثۡنَىٰ وَثُلَـٰثَ وَرُبَـٰعَۚ یَزِیدُ فِی ٱلۡخَلۡقِ مَا یَشَاۤءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:29'),
(2618, 35, 3662, 2, 'مَّا یَفۡتَحِ ٱللَّهُ لِلنَّاسِ مِن رَّحۡمَةࣲ فَلَا مُمۡسِكَ لَهَاۖ وَمَا یُمۡسِكۡ فَلَا مُرۡسِلَ لَهُۥ مِنۢ بَعۡدِهِۦۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:29'),
(2619, 35, 3663, 3, 'یَـٰۤأَیُّهَا ٱلنَّاسُ ٱذۡكُرُوا۟ نِعۡمَتَ ٱللَّهِ عَلَیۡكُمۡۚ هَلۡ مِنۡ خَـٰلِقٍ غَیۡرُ ٱللَّهِ یَرۡزُقُكُم مِّنَ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِۚ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:29'),
(2620, 35, 3664, 4, 'وَإِن یُكَذِّبُوكَ فَقَدۡ كُذِّبَتۡ رُسُلࣱ مِّن قَبۡلِكَۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2621, 35, 3665, 5, 'یَـٰۤأَیُّهَا ٱلنَّاسُ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَیَوٰةُ ٱلدُّنۡیَا وَلَا یَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2622, 35, 3666, 6, 'إِنَّ ٱلشَّیۡطَـٰنَ لَكُمۡ عَدُوࣱّ فَٱتَّخِذُوهُ عَدُوًّاۚ إِنَّمَا یَدۡعُوا۟ حِزۡبَهُۥ لِیَكُونُوا۟ مِنۡ أَصۡحَـٰبِ ٱلسَّعِیرِ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(2623, 35, 3667, 7, 'ٱلَّذِینَ كَفَرُوا۟ لَهُمۡ عَذَابࣱ شَدِیدࣱۖ وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُم مَّغۡفِرَةࣱ وَأَجۡرࣱ كَبِیرٌ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2624, 35, 3668, 8, 'أَفَمَن زُیِّنَ لَهُۥ سُوۤءُ عَمَلِهِۦ فَرَءَاهُ حَسَنࣰاۖ فَإِنَّ ٱللَّهَ یُضِلُّ مَن یَشَاۤءُ وَیَهۡدِی مَن یَشَاۤءُۖ فَلَا تَذۡهَبۡ نَفۡسُكَ عَلَیۡهِمۡ حَسَرَ ٰ⁠تٍۚ إِنَّ ٱللَّهَ عَلِیمُۢ بِمَا یَصۡنَعُونَ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2625, 35, 3669, 9, 'وَٱللَّهُ ٱلَّذِیۤ أَرۡسَلَ ٱلرِّیَـٰحَ فَتُثِیرُ سَحَابࣰا فَسُقۡنَـٰهُ إِلَىٰ بَلَدࣲ مَّیِّتࣲ فَأَحۡیَیۡنَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ كَذَ ٰ⁠لِكَ ٱلنُّشُورُ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2626, 35, 3670, 10, 'مَن كَانَ یُرِیدُ ٱلۡعِزَّةَ فَلِلَّهِ ٱلۡعِزَّةُ جَمِیعًاۚ إِلَیۡهِ یَصۡعَدُ ٱلۡكَلِمُ ٱلطَّیِّبُ وَٱلۡعَمَلُ ٱلصَّـٰلِحُ یَرۡفَعُهُۥۚ وَٱلَّذِینَ یَمۡكُرُونَ ٱلسَّیِّـَٔاتِ لَهُمۡ عَذَابࣱ شَدِیدࣱۖ وَمَكۡرُ أُو۟لَـٰۤىِٕكَ هُوَ یَبُورُ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2627, 35, 3671, 11, 'وَٱللَّهُ خَلَقَكُم مِّن تُرَابࣲ ثُمَّ مِن نُّطۡفَةࣲ ثُمَّ جَعَلَكُمۡ أَزۡوَ ٰ⁠جࣰاۚ وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَمَا یُعَمَّرُ مِن مُّعَمَّرࣲ وَلَا یُنقَصُ مِنۡ عُمُرِهِۦۤ إِلَّا فِی كِتَـٰبٍۚ إِنَّ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣱ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2628, 35, 3672, 12, 'وَمَا یَسۡتَوِی ٱلۡبَحۡرَانِ هَـٰذَا عَذۡبࣱ فُرَاتࣱ سَاۤىِٕغࣱ شَرَابُهُۥ وَهَـٰذَا مِلۡحٌ أُجَاجࣱۖ وَمِن كُلࣲّ تَأۡكُلُونَ لَحۡمࣰا طَرِیࣰّا وَتَسۡتَخۡرِجُونَ حِلۡیَةࣰ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ فِیهِ مَوَاخِرَ لِتَبۡتَغُوا۟ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2629, 35, 3673, 13, 'یُولِجُ ٱلَّیۡلَ فِی ٱلنَّهَارِ وَیُولِجُ ٱلنَّهَارَ فِی ٱلَّیۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلࣱّ یَجۡرِی لِأَجَلࣲ مُّسَمࣰّىۚ ذَ ٰ⁠لِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۚ وَٱلَّذِینَ تَدۡعُونَ مِن دُونِهِۦ مَا یَمۡلِكُونَ مِن قِطۡمِیرٍ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2630, 35, 3674, 14, 'إِن تَدۡعُوهُمۡ لَا یَسۡمَعُوا۟ دُعَاۤءَكُمۡ وَلَوۡ سَمِعُوا۟ مَا ٱسۡتَجَابُوا۟ لَكُمۡۖ وَیَوۡمَ ٱلۡقِیَـٰمَةِ یَكۡفُرُونَ بِشِرۡكِكُمۡۚ وَلَا یُنَبِّئُكَ مِثۡلُ خَبِیرࣲ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2631, 35, 3675, 15, '۞ یَـٰۤأَیُّهَا ٱلنَّاسُ أَنتُمُ ٱلۡفُقَرَاۤءُ إِلَى ٱللَّهِۖ وَٱللَّهُ هُوَ ٱلۡغَنِیُّ ٱلۡحَمِیدُ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2632, 35, 3676, 16, 'إِن یَشَأۡ یُذۡهِبۡكُمۡ وَیَأۡتِ بِخَلۡقࣲ جَدِیدࣲ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2633, 35, 3677, 17, 'وَمَا ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ بِعَزِیزࣲ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2634, 35, 3678, 18, 'وَلَا تَزِرُ وَازِرَةࣱ وِزۡرَ أُخۡرَىٰۚ وَإِن تَدۡعُ مُثۡقَلَةٌ إِلَىٰ حِمۡلِهَا لَا یُحۡمَلۡ مِنۡهُ شَیۡءࣱ وَلَوۡ كَانَ ذَا قُرۡبَىٰۤۗ إِنَّمَا تُنذِرُ ٱلَّذِینَ یَخۡشَوۡنَ رَبَّهُم بِٱلۡغَیۡبِ وَأَقَامُوا۟ ٱلصَّلَوٰةَۚ وَمَن تَزَكَّىٰ فَإِنَّمَا یَتَزَكَّىٰ لِنَفۡسِهِۦۚ وَإِلَى ٱللَّهِ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:26:03', '2023-05-27 11:11:30'),
(2635, 35, 3679, 19, 'وَمَا یَسۡتَوِی ٱلۡأَعۡمَىٰ وَٱلۡبَصِیرُ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:30'),
(2636, 35, 3680, 20, 'وَلَا ٱلظُّلُمَـٰتُ وَلَا ٱلنُّورُ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:30'),
(2637, 35, 3681, 21, 'وَلَا ٱلظِّلُّ وَلَا ٱلۡحَرُورُ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:30'),
(2638, 35, 3682, 22, 'وَمَا یَسۡتَوِی ٱلۡأَحۡیَاۤءُ وَلَا ٱلۡأَمۡوَ ٰ⁠تُۚ إِنَّ ٱللَّهَ یُسۡمِعُ مَن یَشَاۤءُۖ وَمَاۤ أَنتَ بِمُسۡمِعࣲ مَّن فِی ٱلۡقُبُورِ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2639, 35, 3683, 23, 'إِنۡ أَنتَ إِلَّا نَذِیرٌ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2640, 35, 3684, 24, 'إِنَّاۤ أَرۡسَلۡنَـٰكَ بِٱلۡحَقِّ بَشِیرࣰا وَنَذِیرࣰاۚ وَإِن مِّنۡ أُمَّةٍ إِلَّا خَلَا فِیهَا نَذِیرࣱ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2641, 35, 3685, 25, 'وَإِن یُكَذِّبُوكَ فَقَدۡ كَذَّبَ ٱلَّذِینَ مِن قَبۡلِهِمۡ جَاۤءَتۡهُمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِ وَبِٱلزُّبُرِ وَبِٱلۡكِتَـٰبِ ٱلۡمُنِیرِ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2642, 35, 3686, 26, 'ثُمَّ أَخَذۡتُ ٱلَّذِینَ كَفَرُوا۟ۖ فَكَیۡفَ كَانَ نَكِیرِ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2643, 35, 3687, 27, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَخۡرَجۡنَا بِهِۦ ثَمَرَ ٰ⁠تࣲ مُّخۡتَلِفًا أَلۡوَ ٰ⁠نُهَاۚ وَمِنَ ٱلۡجِبَالِ جُدَدُۢ بِیضࣱ وَحُمۡرࣱ مُّخۡتَلِفٌ أَلۡوَ ٰ⁠نُهَا وَغَرَابِیبُ سُودࣱ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2644, 35, 3688, 28, 'وَمِنَ ٱلنَّاسِ وَٱلدَّوَاۤبِّ وَٱلۡأَنۡعَـٰمِ مُخۡتَلِفٌ أَلۡوَ ٰ⁠نُهُۥ كَذَ ٰ⁠لِكَۗ إِنَّمَا یَخۡشَى ٱللَّهَ مِنۡ عِبَادِهِ ٱلۡعُلَمَـٰۤؤُا۟ۗ إِنَّ ٱللَّهَ عَزِیزٌ غَفُورٌ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2645, 35, 3689, 29, 'إِنَّ ٱلَّذِینَ یَتۡلُونَ كِتَـٰبَ ٱللَّهِ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَنفَقُوا۟ مِمَّا رَزَقۡنَـٰهُمۡ سِرࣰّا وَعَلَانِیَةࣰ یَرۡجُونَ تِجَـٰرَةࣰ لَّن تَبُورَ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2646, 35, 3690, 30, 'لِیُوَفِّیَهُمۡ أُجُورَهُمۡ وَیَزِیدَهُم مِّن فَضۡلِهِۦۤۚ إِنَّهُۥ غَفُورࣱ شَكُورࣱ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2647, 35, 3691, 31, 'وَٱلَّذِیۤ أَوۡحَیۡنَاۤ إِلَیۡكَ مِنَ ٱلۡكِتَـٰبِ هُوَ ٱلۡحَقُّ مُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیۡهِۗ إِنَّ ٱللَّهَ بِعِبَادِهِۦ لَخَبِیرُۢ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2648, 35, 3692, 32, 'ثُمَّ أَوۡرَثۡنَا ٱلۡكِتَـٰبَ ٱلَّذِینَ ٱصۡطَفَیۡنَا مِنۡ عِبَادِنَاۖ فَمِنۡهُمۡ ظَالِمࣱ لِّنَفۡسِهِۦ وَمِنۡهُم مُّقۡتَصِدࣱ وَمِنۡهُمۡ سَابِقُۢ بِٱلۡخَیۡرَ ٰ⁠تِ بِإِذۡنِ ٱللَّهِۚ ذَ ٰ⁠لِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِیرُ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2649, 35, 3693, 33, 'جَنَّـٰتُ عَدۡنࣲ یَدۡخُلُونَهَا یُحَلَّوۡنَ فِیهَا مِنۡ أَسَاوِرَ مِن ذَهَبࣲ وَلُؤۡلُؤࣰاۖ وَلِبَاسُهُمۡ فِیهَا حَرِیرࣱ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2650, 35, 3694, 34, 'وَقَالُوا۟ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِیۤ أَذۡهَبَ عَنَّا ٱلۡحَزَنَۖ إِنَّ رَبَّنَا لَغَفُورࣱ شَكُورٌ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2651, 35, 3695, 35, 'ٱلَّذِیۤ أَحَلَّنَا دَارَ ٱلۡمُقَامَةِ مِن فَضۡلِهِۦ لَا یَمَسُّنَا فِیهَا نَصَبࣱ وَلَا یَمَسُّنَا فِیهَا لُغُوبࣱ\n', NULL, 0, '2023-05-26 17:26:04', '2023-05-27 11:11:31'),
(2652, 35, 3696, 36, 'وَٱلَّذِینَ كَفَرُوا۟ لَهُمۡ نَارُ جَهَنَّمَ لَا یُقۡضَىٰ عَلَیۡهِمۡ فَیَمُوتُوا۟ وَلَا یُخَفَّفُ عَنۡهُم مِّنۡ عَذَابِهَاۚ كَذَ ٰ⁠لِكَ نَجۡزِی كُلَّ كَفُورࣲ\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:31'),
(2653, 35, 3697, 37, 'وَهُمۡ یَصۡطَرِخُونَ فِیهَا رَبَّنَاۤ أَخۡرِجۡنَا نَعۡمَلۡ صَـٰلِحًا غَیۡرَ ٱلَّذِی كُنَّا نَعۡمَلُۚ أَوَلَمۡ نُعَمِّرۡكُم مَّا یَتَذَكَّرُ فِیهِ مَن تَذَكَّرَ وَجَاۤءَكُمُ ٱلنَّذِیرُۖ فَذُوقُوا۟ فَمَا لِلظَّـٰلِمِینَ مِن نَّصِیرٍ\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:31'),
(2654, 35, 3698, 38, 'إِنَّ ٱللَّهَ عَـٰلِمُ غَیۡبِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ إِنَّهُۥ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:31'),
(2655, 35, 3699, 39, 'هُوَ ٱلَّذِی جَعَلَكُمۡ خَلَـٰۤىِٕفَ فِی ٱلۡأَرۡضِۚ فَمَن كَفَرَ فَعَلَیۡهِ كُفۡرُهُۥۖ وَلَا یَزِیدُ ٱلۡكَـٰفِرِینَ كُفۡرُهُمۡ عِندَ رَبِّهِمۡ إِلَّا مَقۡتࣰاۖ وَلَا یَزِیدُ ٱلۡكَـٰفِرِینَ كُفۡرُهُمۡ إِلَّا خَسَارࣰا\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:31'),
(2656, 35, 3700, 40, 'قُلۡ أَرَءَیۡتُمۡ شُرَكَاۤءَكُمُ ٱلَّذِینَ تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِی مَاذَا خَلَقُوا۟ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكࣱ فِی ٱلسَّمَـٰوَ ٰ⁠تِ أَمۡ ءَاتَیۡنَـٰهُمۡ كِتَـٰبࣰا فَهُمۡ عَلَىٰ بَیِّنَتࣲ مِّنۡهُۚ بَلۡ إِن یَعِدُ ٱلظَّـٰلِمُونَ بَعۡضُهُم بَعۡضًا إِلَّا غُرُورًا\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:32'),
(2657, 35, 3701, 41, '۞ إِنَّ ٱللَّهَ یُمۡسِكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ أَن تَزُولَاۚ وَلَىِٕن زَالَتَاۤ إِنۡ أَمۡسَكَهُمَا مِنۡ أَحَدࣲ مِّنۢ بَعۡدِهِۦۤۚ إِنَّهُۥ كَانَ حَلِیمًا غَفُورࣰا\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:32'),
(2658, 35, 3702, 42, 'وَأَقۡسَمُوا۟ بِٱللَّهِ جَهۡدَ أَیۡمَـٰنِهِمۡ لَىِٕن جَاۤءَهُمۡ نَذِیرࣱ لَّیَكُونُنَّ أَهۡدَىٰ مِنۡ إِحۡدَى ٱلۡأُمَمِۖ فَلَمَّا جَاۤءَهُمۡ نَذِیرࣱ مَّا زَادَهُمۡ إِلَّا نُفُورًا\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:32'),
(2659, 35, 3703, 43, 'ٱسۡتِكۡبَارࣰا فِی ٱلۡأَرۡضِ وَمَكۡرَ ٱلسَّیِّىِٕۚ وَلَا یَحِیقُ ٱلۡمَكۡرُ ٱلسَّیِّئُ إِلَّا بِأَهۡلِهِۦۚ فَهَلۡ یَنظُرُونَ إِلَّا سُنَّتَ ٱلۡأَوَّلِینَۚ فَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَبۡدِیلࣰاۖ وَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَحۡوِیلًا\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:32'),
(2660, 35, 3704, 44, 'أَوَلَمۡ یَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَیَنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلَّذِینَ مِن قَبۡلِهِمۡ وَكَانُوۤا۟ أَشَدَّ مِنۡهُمۡ قُوَّةࣰۚ وَمَا كَانَ ٱللَّهُ لِیُعۡجِزَهُۥ مِن شَیۡءࣲ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَلَا فِی ٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ عَلِیمࣰا قَدِیرࣰا\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:32'),
(2661, 35, 3705, 45, 'وَلَوۡ یُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِمَا كَسَبُوا۟ مَا تَرَكَ عَلَىٰ ظَهۡرِهَا مِن دَاۤبَّةࣲ وَلَـٰكِن یُؤَخِّرُهُمۡ إِلَىٰۤ أَجَلࣲ مُّسَمࣰّىۖ فَإِذَا جَاۤءَ أَجَلُهُمۡ فَإِنَّ ٱللَّهَ كَانَ بِعِبَادِهِۦ بَصِیرَۢا\n', NULL, 0, '2023-05-26 17:26:05', '2023-05-27 11:11:32'),
(2662, 34, 3607, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی لَهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِ وَلَهُ ٱلۡحَمۡدُ فِی ٱلۡـَٔاخِرَةِۚ وَهُوَ ٱلۡحَكِیمُ ٱلۡخَبِیرُ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:38'),
(2663, 34, 3608, 2, 'یَعۡلَمُ مَا یَلِجُ فِی ٱلۡأَرۡضِ وَمَا یَخۡرُجُ مِنۡهَا وَمَا یَنزِلُ مِنَ ٱلسَّمَاۤءِ وَمَا یَعۡرُجُ فِیهَاۚ وَهُوَ ٱلرَّحِیمُ ٱلۡغَفُورُ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2664, 34, 3609, 3, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لَا تَأۡتِینَا ٱلسَّاعَةُۖ قُلۡ بَلَىٰ وَرَبِّی لَتَأۡتِیَنَّكُمۡ عَـٰلِمِ ٱلۡغَیۡبِۖ لَا یَعۡزُبُ عَنۡهُ مِثۡقَالُ ذَرَّةࣲ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَلَا فِی ٱلۡأَرۡضِ وَلَاۤ أَصۡغَرُ مِن ذَ ٰ⁠لِكَ وَلَاۤ أَكۡبَرُ إِلَّا فِی كِتَـٰبࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2665, 34, 3610, 4, 'لِّیَجۡزِیَ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِۚ أُو۟لَـٰۤىِٕكَ لَهُم مَّغۡفِرَةࣱ وَرِزۡقࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2666, 34, 3611, 5, 'وَٱلَّذِینَ سَعَوۡ فِیۤ ءَایَـٰتِنَا مُعَـٰجِزِینَ أُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابࣱ مِّن رِّجۡزٍ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2667, 34, 3612, 6, 'وَیَرَى ٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ ٱلَّذِیۤ أُنزِلَ إِلَیۡكَ مِن رَّبِّكَ هُوَ ٱلۡحَقَّ وَیَهۡدِیۤ إِلَىٰ صِرَ ٰ⁠طِ ٱلۡعَزِیزِ ٱلۡحَمِیدِ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2668, 34, 3613, 7, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ هَلۡ نَدُلُّكُمۡ عَلَىٰ رَجُلࣲ یُنَبِّئُكُمۡ إِذَا مُزِّقۡتُمۡ كُلَّ مُمَزَّقٍ إِنَّكُمۡ لَفِی خَلۡقࣲ جَدِیدٍ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2669, 34, 3614, 8, 'أَفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَم بِهِۦ جِنَّةُۢۗ بَلِ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ فِی ٱلۡعَذَابِ وَٱلضَّلَـٰلِ ٱلۡبَعِیدِ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2670, 34, 3615, 9, 'أَفَلَمۡ یَرَوۡا۟ إِلَىٰ مَا بَیۡنَ أَیۡدِیهِمۡ وَمَا خَلۡفَهُم مِّنَ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِۚ إِن نَّشَأۡ نَخۡسِفۡ بِهِمُ ٱلۡأَرۡضَ أَوۡ نُسۡقِطۡ عَلَیۡهِمۡ كِسَفࣰا مِّنَ ٱلسَّمَاۤءِۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّكُلِّ عَبۡدࣲ مُّنِیبࣲ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2671, 34, 3616, 10, '۞ وَلَقَدۡ ءَاتَیۡنَا دَاوُۥدَ مِنَّا فَضۡلࣰاۖ یَـٰجِبَالُ أَوِّبِی مَعَهُۥ وَٱلطَّیۡرَۖ وَأَلَنَّا لَهُ ٱلۡحَدِیدَ\n', NULL, 0, '2023-05-26 17:26:17', '2023-05-27 11:11:39'),
(2672, 34, 3617, 11, 'أَنِ ٱعۡمَلۡ سَـٰبِغَـٰتࣲ وَقَدِّرۡ فِی ٱلسَّرۡدِۖ وَٱعۡمَلُوا۟ صَـٰلِحًاۖ إِنِّی بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2673, 34, 3618, 12, 'وَلِسُلَیۡمَـٰنَ ٱلرِّیحَ غُدُوُّهَا شَهۡرࣱ وَرَوَاحُهَا شَهۡرࣱۖ وَأَسَلۡنَا لَهُۥ عَیۡنَ ٱلۡقِطۡرِۖ وَمِنَ ٱلۡجِنِّ مَن یَعۡمَلُ بَیۡنَ یَدَیۡهِ بِإِذۡنِ رَبِّهِۦۖ وَمَن یَزِغۡ مِنۡهُمۡ عَنۡ أَمۡرِنَا نُذِقۡهُ مِنۡ عَذَابِ ٱلسَّعِیرِ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2674, 34, 3619, 13, 'یَعۡمَلُونَ لَهُۥ مَا یَشَاۤءُ مِن مَّحَـٰرِیبَ وَتَمَـٰثِیلَ وَجِفَانࣲ كَٱلۡجَوَابِ وَقُدُورࣲ رَّاسِیَـٰتٍۚ ٱعۡمَلُوۤا۟ ءَالَ دَاوُۥدَ شُكۡرࣰاۚ وَقَلِیلࣱ مِّنۡ عِبَادِیَ ٱلشَّكُورُ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2675, 34, 3620, 14, 'فَلَمَّا قَضَیۡنَا عَلَیۡهِ ٱلۡمَوۡتَ مَا دَلَّهُمۡ عَلَىٰ مَوۡتِهِۦۤ إِلَّا دَاۤبَّةُ ٱلۡأَرۡضِ تَأۡكُلُ مِنسَأَتَهُۥۖ فَلَمَّا خَرَّ تَبَیَّنَتِ ٱلۡجِنُّ أَن لَّوۡ كَانُوا۟ یَعۡلَمُونَ ٱلۡغَیۡبَ مَا لَبِثُوا۟ فِی ٱلۡعَذَابِ ٱلۡمُهِینِ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2676, 34, 3621, 15, 'لَقَدۡ كَانَ لِسَبَإࣲ فِی مَسۡكَنِهِمۡ ءَایَةࣱۖ جَنَّتَانِ عَن یَمِینࣲ وَشِمَالࣲۖ كُلُوا۟ مِن رِّزۡقِ رَبِّكُمۡ وَٱشۡكُرُوا۟ لَهُۥۚ بَلۡدَةࣱ طَیِّبَةࣱ وَرَبٌّ غَفُورࣱ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2677, 34, 3622, 16, 'فَأَعۡرَضُوا۟ فَأَرۡسَلۡنَا عَلَیۡهِمۡ سَیۡلَ ٱلۡعَرِمِ وَبَدَّلۡنَـٰهُم بِجَنَّتَیۡهِمۡ جَنَّتَیۡنِ ذَوَاتَیۡ أُكُلٍ خَمۡطࣲ وَأَثۡلࣲ وَشَیۡءࣲ مِّن سِدۡرࣲ قَلِیلࣲ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2678, 34, 3623, 17, 'ذَ ٰ⁠لِكَ جَزَیۡنَـٰهُم بِمَا كَفَرُوا۟ۖ وَهَلۡ نُجَـٰزِیۤ إِلَّا ٱلۡكَفُورَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2679, 34, 3624, 18, 'وَجَعَلۡنَا بَیۡنَهُمۡ وَبَیۡنَ ٱلۡقُرَى ٱلَّتِی بَـٰرَكۡنَا فِیهَا قُرࣰى ظَـٰهِرَةࣰ وَقَدَّرۡنَا فِیهَا ٱلسَّیۡرَۖ سِیرُوا۟ فِیهَا لَیَالِیَ وَأَیَّامًا ءَامِنِینَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:39'),
(2680, 34, 3625, 19, 'فَقَالُوا۟ رَبَّنَا بَـٰعِدۡ بَیۡنَ أَسۡفَارِنَا وَظَلَمُوۤا۟ أَنفُسَهُمۡ فَجَعَلۡنَـٰهُمۡ أَحَادِیثَ وَمَزَّقۡنَـٰهُمۡ كُلَّ مُمَزَّقٍۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّكُلِّ صَبَّارࣲ شَكُورࣲ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2681, 34, 3626, 20, 'وَلَقَدۡ صَدَّقَ عَلَیۡهِمۡ إِبۡلِیسُ ظَنَّهُۥ فَٱتَّبَعُوهُ إِلَّا فَرِیقࣰا مِّنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2682, 34, 3627, 21, 'وَمَا كَانَ لَهُۥ عَلَیۡهِم مِّن سُلۡطَـٰنٍ إِلَّا لِنَعۡلَمَ مَن یُؤۡمِنُ بِٱلۡـَٔاخِرَةِ مِمَّنۡ هُوَ مِنۡهَا فِی شَكࣲّۗ وَرَبُّكَ عَلَىٰ كُلِّ شَیۡءٍ حَفِیظࣱ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2683, 34, 3628, 22, 'قُلِ ٱدۡعُوا۟ ٱلَّذِینَ زَعَمۡتُم مِّن دُونِ ٱللَّهِ لَا یَمۡلِكُونَ مِثۡقَالَ ذَرَّةࣲ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَلَا فِی ٱلۡأَرۡضِ وَمَا لَهُمۡ فِیهِمَا مِن شِرۡكࣲ وَمَا لَهُۥ مِنۡهُم مِّن ظَهِیرࣲ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2684, 34, 3629, 23, 'وَلَا تَنفَعُ ٱلشَّفَـٰعَةُ عِندَهُۥۤ إِلَّا لِمَنۡ أَذِنَ لَهُۥۚ حَتَّىٰۤ إِذَا فُزِّعَ عَن قُلُوبِهِمۡ قَالُوا۟ مَاذَا قَالَ رَبُّكُمۡۖ قَالُوا۟ ٱلۡحَقَّۖ وَهُوَ ٱلۡعَلِیُّ ٱلۡكَبِیرُ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2685, 34, 3630, 24, '۞ قُلۡ مَن یَرۡزُقُكُم مِّنَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ قُلِ ٱللَّهُۖ وَإِنَّاۤ أَوۡ إِیَّاكُمۡ لَعَلَىٰ هُدًى أَوۡ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2686, 34, 3631, 25, 'قُل لَّا تُسۡـَٔلُونَ عَمَّاۤ أَجۡرَمۡنَا وَلَا نُسۡـَٔلُ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2687, 34, 3632, 26, 'قُلۡ یَجۡمَعُ بَیۡنَنَا رَبُّنَا ثُمَّ یَفۡتَحُ بَیۡنَنَا بِٱلۡحَقِّ وَهُوَ ٱلۡفَتَّاحُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2688, 34, 3633, 27, 'قُلۡ أَرُونِیَ ٱلَّذِینَ أَلۡحَقۡتُم بِهِۦ شُرَكَاۤءَۖ كَلَّاۚ بَلۡ هُوَ ٱللَّهُ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2689, 34, 3634, 28, 'وَمَاۤ أَرۡسَلۡنَـٰكَ إِلَّا كَاۤفَّةࣰ لِّلنَّاسِ بَشِیرࣰا وَنَذِیرࣰا وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2690, 34, 3635, 29, 'وَیَقُولُونَ مَتَىٰ هَـٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2691, 34, 3636, 30, 'قُل لَّكُم مِّیعَادُ یَوۡمࣲ لَّا تَسۡتَـٔۡخِرُونَ عَنۡهُ سَاعَةࣰ وَلَا تَسۡتَقۡدِمُونَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2692, 34, 3637, 31, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لَن نُّؤۡمِنَ بِهَـٰذَا ٱلۡقُرۡءَانِ وَلَا بِٱلَّذِی بَیۡنَ یَدَیۡهِۗ وَلَوۡ تَرَىٰۤ إِذِ ٱلظَّـٰلِمُونَ مَوۡقُوفُونَ عِندَ رَبِّهِمۡ یَرۡجِعُ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ ٱلۡقَوۡلَ یَقُولُ ٱلَّذِینَ ٱسۡتُضۡعِفُوا۟ لِلَّذِینَ ٱسۡتَكۡبَرُوا۟ لَوۡلَاۤ أَنتُمۡ لَكُنَّا مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2693, 34, 3638, 32, 'قَالَ ٱلَّذِینَ ٱسۡتَكۡبَرُوا۟ لِلَّذِینَ ٱسۡتُضۡعِفُوۤا۟ أَنَحۡنُ صَدَدۡنَـٰكُمۡ عَنِ ٱلۡهُدَىٰ بَعۡدَ إِذۡ جَاۤءَكُمۖ بَلۡ كُنتُم مُّجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2694, 34, 3639, 33, 'وَقَالَ ٱلَّذِینَ ٱسۡتُضۡعِفُوا۟ لِلَّذِینَ ٱسۡتَكۡبَرُوا۟ بَلۡ مَكۡرُ ٱلَّیۡلِ وَٱلنَّهَارِ إِذۡ تَأۡمُرُونَنَاۤ أَن نَّكۡفُرَ بِٱللَّهِ وَنَجۡعَلَ لَهُۥۤ أَندَادࣰاۚ وَأَسَرُّوا۟ ٱلنَّدَامَةَ لَمَّا رَأَوُا۟ ٱلۡعَذَابَۚ وَجَعَلۡنَا ٱلۡأَغۡلَـٰلَ فِیۤ أَعۡنَاقِ ٱلَّذِینَ كَفَرُوا۟ۖ هَلۡ یُجۡزَوۡنَ إِلَّا مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:26:18', '2023-05-27 11:11:40'),
(2695, 34, 3640, 34, 'وَمَاۤ أَرۡسَلۡنَا فِی قَرۡیَةࣲ مِّن نَّذِیرٍ إِلَّا قَالَ مُتۡرَفُوهَاۤ إِنَّا بِمَاۤ أُرۡسِلۡتُم بِهِۦ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:40'),
(2696, 34, 3641, 35, 'وَقَالُوا۟ نَحۡنُ أَكۡثَرُ أَمۡوَ ٰ⁠لࣰا وَأَوۡلَـٰدࣰا وَمَا نَحۡنُ بِمُعَذَّبِینَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:40'),
(2697, 34, 3642, 36, 'قُلۡ إِنَّ رَبِّی یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ وَیَقۡدِرُ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:40'),
(2698, 34, 3643, 37, 'وَمَاۤ أَمۡوَ ٰ⁠لُكُمۡ وَلَاۤ أَوۡلَـٰدُكُم بِٱلَّتِی تُقَرِّبُكُمۡ عِندَنَا زُلۡفَىٰۤ إِلَّا مَنۡ ءَامَنَ وَعَمِلَ صَـٰلِحࣰا فَأُو۟لَـٰۤىِٕكَ لَهُمۡ جَزَاۤءُ ٱلضِّعۡفِ بِمَا عَمِلُوا۟ وَهُمۡ فِی ٱلۡغُرُفَـٰتِ ءَامِنُونَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:40'),
(2699, 34, 3644, 38, 'وَٱلَّذِینَ یَسۡعَوۡنَ فِیۤ ءَایَـٰتِنَا مُعَـٰجِزِینَ أُو۟لَـٰۤىِٕكَ فِی ٱلۡعَذَابِ مُحۡضَرُونَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2700, 34, 3645, 39, 'قُلۡ إِنَّ رَبِّی یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ مِنۡ عِبَادِهِۦ وَیَقۡدِرُ لَهُۥۚ وَمَاۤ أَنفَقۡتُم مِّن شَیۡءࣲ فَهُوَ یُخۡلِفُهُۥۖ وَهُوَ خَیۡرُ ٱلرَّ ٰ⁠زِقِینَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2701, 34, 3646, 40, 'وَیَوۡمَ یَحۡشُرُهُمۡ جَمِیعࣰا ثُمَّ یَقُولُ لِلۡمَلَـٰۤىِٕكَةِ أَهَـٰۤؤُلَاۤءِ إِیَّاكُمۡ كَانُوا۟ یَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2702, 34, 3647, 41, 'قَالُوا۟ سُبۡحَـٰنَكَ أَنتَ وَلِیُّنَا مِن دُونِهِمۖ بَلۡ كَانُوا۟ یَعۡبُدُونَ ٱلۡجِنَّۖ أَكۡثَرُهُم بِهِم مُّؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2703, 34, 3648, 42, 'فَٱلۡیَوۡمَ لَا یَمۡلِكُ بَعۡضُكُمۡ لِبَعۡضࣲ نَّفۡعࣰا وَلَا ضَرࣰّا وَنَقُولُ لِلَّذِینَ ظَلَمُوا۟ ذُوقُوا۟ عَذَابَ ٱلنَّارِ ٱلَّتِی كُنتُم بِهَا تُكَذِّبُونَ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2704, 34, 3649, 43, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَـٰتُنَا بَیِّنَـٰتࣲ قَالُوا۟ مَا هَـٰذَاۤ إِلَّا رَجُلࣱ یُرِیدُ أَن یَصُدَّكُمۡ عَمَّا كَانَ یَعۡبُدُ ءَابَاۤؤُكُمۡ وَقَالُوا۟ مَا هَـٰذَاۤ إِلَّاۤ إِفۡكࣱ مُّفۡتَرࣰىۚ وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لِلۡحَقِّ لَمَّا جَاۤءَهُمۡ إِنۡ هَـٰذَاۤ إِلَّا سِحۡرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2705, 34, 3650, 44, 'وَمَاۤ ءَاتَیۡنَـٰهُم مِّن كُتُبࣲ یَدۡرُسُونَهَاۖ وَمَاۤ أَرۡسَلۡنَاۤ إِلَیۡهِمۡ قَبۡلَكَ مِن نَّذِیرࣲ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2706, 34, 3651, 45, 'وَكَذَّبَ ٱلَّذِینَ مِن قَبۡلِهِمۡ وَمَا بَلَغُوا۟ مِعۡشَارَ مَاۤ ءَاتَیۡنَـٰهُمۡ فَكَذَّبُوا۟ رُسُلِیۖ فَكَیۡفَ كَانَ نَكِیرِ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2707, 34, 3652, 46, '۞ قُلۡ إِنَّمَاۤ أَعِظُكُم بِوَ ٰ⁠حِدَةٍۖ أَن تَقُومُوا۟ لِلَّهِ مَثۡنَىٰ وَفُرَ ٰ⁠دَىٰ ثُمَّ تَتَفَكَّرُوا۟ۚ مَا بِصَاحِبِكُم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِیرࣱ لَّكُم بَیۡنَ یَدَیۡ عَذَابࣲ شَدِیدࣲ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2708, 34, 3653, 47, 'قُلۡ مَا سَأَلۡتُكُم مِّنۡ أَجۡرࣲ فَهُوَ لَكُمۡۖ إِنۡ أَجۡرِیَ إِلَّا عَلَى ٱللَّهِۖ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ شَهِیدࣱ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2709, 34, 3654, 48, 'قُلۡ إِنَّ رَبِّی یَقۡذِفُ بِٱلۡحَقِّ عَلَّـٰمُ ٱلۡغُیُوبِ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2710, 34, 3655, 49, 'قُلۡ جَاۤءَ ٱلۡحَقُّ وَمَا یُبۡدِئُ ٱلۡبَـٰطِلُ وَمَا یُعِیدُ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2711, 34, 3656, 50, 'قُلۡ إِن ضَلَلۡتُ فَإِنَّمَاۤ أَضِلُّ عَلَىٰ نَفۡسِیۖ وَإِنِ ٱهۡتَدَیۡتُ فَبِمَا یُوحِیۤ إِلَیَّ رَبِّیۤۚ إِنَّهُۥ سَمِیعࣱ قَرِیبࣱ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2712, 34, 3657, 51, 'وَلَوۡ تَرَىٰۤ إِذۡ فَزِعُوا۟ فَلَا فَوۡتَ وَأُخِذُوا۟ مِن مَّكَانࣲ قَرِیبࣲ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2713, 34, 3658, 52, 'وَقَالُوۤا۟ ءَامَنَّا بِهِۦ وَأَنَّىٰ لَهُمُ ٱلتَّنَاوُشُ مِن مَّكَانِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:26:19', '2023-05-27 11:11:41'),
(2714, 34, 3659, 53, 'وَقَدۡ كَفَرُوا۟ بِهِۦ مِن قَبۡلُۖ وَیَقۡذِفُونَ بِٱلۡغَیۡبِ مِن مَّكَانِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:26:20', '2023-05-27 11:11:41'),
(2715, 34, 3660, 54, 'وَحِیلَ بَیۡنَهُمۡ وَبَیۡنَ مَا یَشۡتَهُونَ كَمَا فُعِلَ بِأَشۡیَاعِهِم مِّن قَبۡلُۚ إِنَّهُمۡ كَانُوا۟ فِی شَكࣲّ مُّرِیبِۭ\n', NULL, 0, '2023-05-26 17:26:20', '2023-05-27 11:11:41'),
(2716, 33, 3534, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلنَّبِیُّ ٱتَّقِ ٱللَّهۚ وَلَا تُطِعِ ٱلۡكَـٰفِرِینَ وَٱلۡمُنَـٰفِقِینَۗ إِنَّ ٱللَّهَ كَانَ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:44'),
(2717, 33, 3535, 2, 'وَٱتَّبِعۡ مَا یُوحَىٰۤ إِلَیۡكَ مِن رَّبِّكَۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِیرࣰا\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:44'),
(2718, 33, 3536, 3, 'وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِیلࣰا\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:45'),
(2719, 33, 3537, 4, 'مَّا جَعَلَ ٱللَّهُ لِرَجُلࣲ مِّن قَلۡبَیۡنِ فِی جَوۡفِهِۦۚ وَمَا جَعَلَ أَزۡوَ ٰ⁠جَكُمُ ٱلَّـٰۤـِٔی تُظَـٰهِرُونَ مِنۡهُنَّ أُمَّهَـٰتِكُمۡۚ وَمَا جَعَلَ أَدۡعِیَاۤءَكُمۡ أَبۡنَاۤءَكُمۡۚ ذَ ٰ⁠لِكُمۡ قَوۡلُكُم بِأَفۡوَ ٰ⁠هِكُمۡۖ وَٱللَّهُ یَقُولُ ٱلۡحَقَّ وَهُوَ یَهۡدِی ٱلسَّبِیلَ\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:45'),
(2720, 33, 3538, 5, 'ٱدۡعُوهُمۡ لِـَٔابَاۤىِٕهِمۡ هُوَ أَقۡسَطُ عِندَ ٱللَّهِۚ فَإِن لَّمۡ تَعۡلَمُوۤا۟ ءَابَاۤءَهُمۡ فَإِخۡوَ ٰ⁠نُكُمۡ فِی ٱلدِّینِ وَمَوَ ٰ⁠لِیكُمۡۚ وَلَیۡسَ عَلَیۡكُمۡ جُنَاحࣱ فِیمَاۤ أَخۡطَأۡتُم بِهِۦ وَلَـٰكِن مَّا تَعَمَّدَتۡ قُلُوبُكُمۡۚ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمًا\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:45'),
(2721, 33, 3539, 6, 'ٱلنَّبِیُّ أَوۡلَىٰ بِٱلۡمُؤۡمِنِینَ مِنۡ أَنفُسِهِمۡۖ وَأَزۡوَ ٰ⁠جُهُۥۤ أُمَّهَـٰتُهُمۡۗ وَأُو۟لُوا۟ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضࣲ فِی كِتَـٰبِ ٱللَّهِ مِنَ ٱلۡمُؤۡمِنِینَ وَٱلۡمُهَـٰجِرِینَ إِلَّاۤ أَن تَفۡعَلُوۤا۟ إِلَىٰۤ أَوۡلِیَاۤىِٕكُم مَّعۡرُوفࣰاۚ كَانَ ذَ ٰ⁠لِكَ فِی ٱلۡكِتَـٰبِ مَسۡطُورࣰا\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:45'),
(2722, 33, 3540, 7, 'وَإِذۡ أَخَذۡنَا مِنَ ٱلنَّبِیِّـۧنَ مِیثَـٰقَهُمۡ وَمِنكَ وَمِن نُّوحࣲ وَإِبۡرَ ٰ⁠هِیمَ وَمُوسَىٰ وَعِیسَى ٱبۡنِ مَرۡیَمَۖ وَأَخَذۡنَا مِنۡهُم مِّیثَـٰقًا غَلِیظࣰا\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:45'),
(2723, 33, 3541, 8, 'لِّیَسۡـَٔلَ ٱلصَّـٰدِقِینَ عَن صِدۡقِهِمۡۚ وَأَعَدَّ لِلۡكَـٰفِرِینَ عَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-26 17:26:27', '2023-05-27 11:11:45'),
(2724, 33, 3542, 9, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱذۡكُرُوا۟ نِعۡمَةَ ٱللَّهِ عَلَیۡكُمۡ إِذۡ جَاۤءَتۡكُمۡ جُنُودࣱ فَأَرۡسَلۡنَا عَلَیۡهِمۡ رِیحࣰا وَجُنُودࣰا لَّمۡ تَرَوۡهَاۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرًا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2725, 33, 3543, 10, 'إِذۡ جَاۤءُوكُم مِّن فَوۡقِكُمۡ وَمِنۡ أَسۡفَلَ مِنكُمۡ وَإِذۡ زَاغَتِ ٱلۡأَبۡصَـٰرُ وَبَلَغَتِ ٱلۡقُلُوبُ ٱلۡحَنَاجِرَ وَتَظُنُّونَ بِٱللَّهِ ٱلظُّنُونَا۠\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2726, 33, 3544, 11, 'هُنَالِكَ ٱبۡتُلِیَ ٱلۡمُؤۡمِنُونَ وَزُلۡزِلُوا۟ زِلۡزَالࣰا شَدِیدࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2727, 33, 3545, 12, 'وَإِذۡ یَقُولُ ٱلۡمُنَـٰفِقُونَ وَٱلَّذِینَ فِی قُلُوبِهِم مَّرَضࣱ مَّا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥۤ إِلَّا غُرُورࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2728, 33, 3546, 13, 'وَإِذۡ قَالَت طَّاۤىِٕفَةࣱ مِّنۡهُمۡ یَـٰۤأَهۡلَ یَثۡرِبَ لَا مُقَامَ لَكُمۡ فَٱرۡجِعُوا۟ۚ وَیَسۡتَـٔۡذِنُ فَرِیقࣱ مِّنۡهُمُ ٱلنَّبِیَّ یَقُولُونَ إِنَّ بُیُوتَنَا عَوۡرَةࣱ وَمَا هِیَ بِعَوۡرَةٍۖ إِن یُرِیدُونَ إِلَّا فِرَارࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2729, 33, 3547, 14, 'وَلَوۡ دُخِلَتۡ عَلَیۡهِم مِّنۡ أَقۡطَارِهَا ثُمَّ سُىِٕلُوا۟ ٱلۡفِتۡنَةَ لَـَٔاتَوۡهَا وَمَا تَلَبَّثُوا۟ بِهَاۤ إِلَّا یَسِیرࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2730, 33, 3548, 15, 'وَلَقَدۡ كَانُوا۟ عَـٰهَدُوا۟ ٱللَّهَ مِن قَبۡلُ لَا یُوَلُّونَ ٱلۡأَدۡبَـٰرَۚ وَكَانَ عَهۡدُ ٱللَّهِ مَسۡـُٔولࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2731, 33, 3549, 16, 'قُل لَّن یَنفَعَكُمُ ٱلۡفِرَارُ إِن فَرَرۡتُم مِّنَ ٱلۡمَوۡتِ أَوِ ٱلۡقَتۡلِ وَإِذࣰا لَّا تُمَتَّعُونَ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2732, 33, 3550, 17, 'قُلۡ مَن ذَا ٱلَّذِی یَعۡصِمُكُم مِّنَ ٱللَّهِ إِنۡ أَرَادَ بِكُمۡ سُوۤءًا أَوۡ أَرَادَ بِكُمۡ رَحۡمَةࣰۚ وَلَا یَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِیࣰّا وَلَا نَصِیرࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2733, 33, 3551, 18, '۞ قَدۡ یَعۡلَمُ ٱللَّهُ ٱلۡمُعَوِّقِینَ مِنكُمۡ وَٱلۡقَاۤىِٕلِینَ لِإِخۡوَ ٰ⁠نِهِمۡ هَلُمَّ إِلَیۡنَاۖ وَلَا یَأۡتُونَ ٱلۡبَأۡسَ إِلَّا قَلِیلًا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2734, 33, 3552, 19, 'أَشِحَّةً عَلَیۡكُمۡۖ فَإِذَا جَاۤءَ ٱلۡخَوۡفُ رَأَیۡتَهُمۡ یَنظُرُونَ إِلَیۡكَ تَدُورُ أَعۡیُنُهُمۡ كَٱلَّذِی یُغۡشَىٰ عَلَیۡهِ مِنَ ٱلۡمَوۡتِۖ فَإِذَا ذَهَبَ ٱلۡخَوۡفُ سَلَقُوكُم بِأَلۡسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى ٱلۡخَیۡرِۚ أُو۟لَـٰۤىِٕكَ لَمۡ یُؤۡمِنُوا۟ فَأَحۡبَطَ ٱللَّهُ أَعۡمَـٰلَهُمۡۚ وَكَانَ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2735, 33, 3553, 20, 'یَحۡسَبُونَ ٱلۡأَحۡزَابَ لَمۡ یَذۡهَبُوا۟ۖ وَإِن یَأۡتِ ٱلۡأَحۡزَابُ یَوَدُّوا۟ لَوۡ أَنَّهُم بَادُونَ فِی ٱلۡأَعۡرَابِ یَسۡـَٔلُونَ عَنۡ أَنۢبَاۤىِٕكُمۡۖ وَلَوۡ كَانُوا۟ فِیكُم مَّا قَـٰتَلُوۤا۟ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:45'),
(2736, 33, 3554, 21, 'لَّقَدۡ كَانَ لَكُمۡ فِی رَسُولِ ٱللَّهِ أُسۡوَةٌ حَسَنَةࣱ لِّمَن كَانَ یَرۡجُوا۟ ٱللَّهَ وَٱلۡیَوۡمَ ٱلۡـَٔاخِرَ وَذَكَرَ ٱللَّهَ كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2737, 33, 3555, 22, 'وَلَمَّا رَءَا ٱلۡمُؤۡمِنُونَ ٱلۡأَحۡزَابَ قَالُوا۟ هَـٰذَا مَا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥ وَصَدَقَ ٱللَّهُ وَرَسُولُهُۥۚ وَمَا زَادَهُمۡ إِلَّاۤ إِیمَـٰنࣰا وَتَسۡلِیمࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2738, 33, 3556, 23, 'مِّنَ ٱلۡمُؤۡمِنِینَ رِجَالࣱ صَدَقُوا۟ مَا عَـٰهَدُوا۟ ٱللَّهَ عَلَیۡهِۖ فَمِنۡهُم مَّن قَضَىٰ نَحۡبَهُۥ وَمِنۡهُم مَّن یَنتَظِرُۖ وَمَا بَدَّلُوا۟ تَبۡدِیلࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2739, 33, 3557, 24, 'لِّیَجۡزِیَ ٱللَّهُ ٱلصَّـٰدِقِینَ بِصِدۡقِهِمۡ وَیُعَذِّبَ ٱلۡمُنَـٰفِقِینَ إِن شَاۤءَ أَوۡ یَتُوبَ عَلَیۡهِمۡۚ إِنَّ ٱللَّهَ كَانَ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2740, 33, 3558, 25, 'وَرَدَّ ٱللَّهُ ٱلَّذِینَ كَفَرُوا۟ بِغَیۡظِهِمۡ لَمۡ یَنَالُوا۟ خَیۡرࣰاۚ وَكَفَى ٱللَّهُ ٱلۡمُؤۡمِنِینَ ٱلۡقِتَالَۚ وَكَانَ ٱللَّهُ قَوِیًّا عَزِیزࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2741, 33, 3559, 26, 'وَأَنزَلَ ٱلَّذِینَ ظَـٰهَرُوهُم مِّنۡ أَهۡلِ ٱلۡكِتَـٰبِ مِن صَیَاصِیهِمۡ وَقَذَفَ فِی قُلُوبِهِمُ ٱلرُّعۡبَ فَرِیقࣰا تَقۡتُلُونَ وَتَأۡسِرُونَ فَرِیقࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2742, 33, 3560, 27, 'وَأَوۡرَثَكُمۡ أَرۡضَهُمۡ وَدِیَـٰرَهُمۡ وَأَمۡوَ ٰ⁠لَهُمۡ وَأَرۡضࣰا لَّمۡ تَطَـُٔوهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2743, 33, 3561, 28, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ قُل لِّأَزۡوَ ٰ⁠جِكَ إِن كُنتُنَّ تُرِدۡنَ ٱلۡحَیَوٰةَ ٱلدُّنۡیَا وَزِینَتَهَا فَتَعَالَیۡنَ أُمَتِّعۡكُنَّ وَأُسَرِّحۡكُنَّ سَرَاحࣰا جَمِیلࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2744, 33, 3562, 29, 'وَإِن كُنتُنَّ تُرِدۡنَ ٱللَّهَ وَرَسُولَهُۥ وَٱلدَّارَ ٱلۡـَٔاخِرَةَ فَإِنَّ ٱللَّهَ أَعَدَّ لِلۡمُحۡسِنَـٰتِ مِنكُنَّ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2745, 33, 3563, 30, 'یَـٰنِسَاۤءَ ٱلنَّبِیِّ مَن یَأۡتِ مِنكُنَّ بِفَـٰحِشَةࣲ مُّبَیِّنَةࣲ یُضَـٰعَفۡ لَهَا ٱلۡعَذَابُ ضِعۡفَیۡنِۚ وَكَانَ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2746, 33, 3564, 31, '۞ وَمَن یَقۡنُتۡ مِنكُنَّ لِلَّهِ وَرَسُولِهِۦ وَتَعۡمَلۡ صَـٰلِحࣰا نُّؤۡتِهَاۤ أَجۡرَهَا مَرَّتَیۡنِ وَأَعۡتَدۡنَا لَهَا رِزۡقࣰا كَرِیمࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2747, 33, 3565, 32, 'یَـٰنِسَاۤءَ ٱلنَّبِیِّ لَسۡتُنَّ كَأَحَدࣲ مِّنَ ٱلنِّسَاۤءِ إِنِ ٱتَّقَیۡتُنَّۚ فَلَا تَخۡضَعۡنَ بِٱلۡقَوۡلِ فَیَطۡمَعَ ٱلَّذِی فِی قَلۡبِهِۦ مَرَضࣱ وَقُلۡنَ قَوۡلࣰا مَّعۡرُوفࣰا\n', NULL, 0, '2023-05-26 17:26:28', '2023-05-27 11:11:46'),
(2748, 33, 3566, 33, 'وَقَرۡنَ فِی بُیُوتِكُنَّ وَلَا تَبَرَّجۡنَ تَبَرُّجَ ٱلۡجَـٰهِلِیَّةِ ٱلۡأُولَىٰۖ وَأَقِمۡنَ ٱلصَّلَوٰةَ وَءَاتِینَ ٱلزَّكَوٰةَ وَأَطِعۡنَ ٱللَّهَ وَرَسُولَهُۥۤۚ إِنَّمَا یُرِیدُ ٱللَّهُ لِیُذۡهِبَ عَنكُمُ ٱلرِّجۡسَ أَهۡلَ ٱلۡبَیۡتِ وَیُطَهِّرَكُمۡ تَطۡهِیرࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:46'),
(2749, 33, 3567, 34, 'وَٱذۡكُرۡنَ مَا یُتۡلَىٰ فِی بُیُوتِكُنَّ مِنۡ ءَایَـٰتِ ٱللَّهِ وَٱلۡحِكۡمَةِۚ إِنَّ ٱللَّهَ كَانَ لَطِیفًا خَبِیرًا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:46'),
(2750, 33, 3568, 35, 'إِنَّ ٱلۡمُسۡلِمِینَ وَٱلۡمُسۡلِمَـٰتِ وَٱلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِ وَٱلۡقَـٰنِتِینَ وَٱلۡقَـٰنِتَـٰتِ وَٱلصَّـٰدِقِینَ وَٱلصَّـٰدِقَـٰتِ وَٱلصَّـٰبِرِینَ وَٱلصَّـٰبِرَ ٰ⁠تِ وَٱلۡخَـٰشِعِینَ وَٱلۡخَـٰشِعَـٰتِ وَٱلۡمُتَصَدِّقِینَ وَٱلۡمُتَصَدِّقَـٰتِ وَٱلصَّـٰۤىِٕمِینَ وَٱلصَّـٰۤىِٕمَـٰتِ وَٱلۡحَـٰفِظِینَ فُرُوجَهُمۡ وَٱلۡحَـٰفِظَـٰتِ وَٱلذَّ ٰ⁠كِرِینَ ٱللَّهَ كَثِیرࣰا وَٱلذَّ ٰ⁠كِرَ ٰ⁠تِ أَعَدَّ ٱللَّهُ لَهُم مَّغۡفِرَةࣰ وَأَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:46'),
(2751, 33, 3569, 36, 'وَمَا كَانَ لِمُؤۡمِنࣲ وَلَا مُؤۡمِنَةٍ إِذَا قَضَى ٱللَّهُ وَرَسُولُهُۥۤ أَمۡرًا أَن یَكُونَ لَهُمُ ٱلۡخِیَرَةُ مِنۡ أَمۡرِهِمۡۗ وَمَن یَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ ضَلَّ ضَلَـٰلࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:46'),
(2752, 33, 3570, 37, 'وَإِذۡ تَقُولُ لِلَّذِیۤ أَنۡعَمَ ٱللَّهُ عَلَیۡهِ وَأَنۡعَمۡتَ عَلَیۡهِ أَمۡسِكۡ عَلَیۡكَ زَوۡجَكَ وَٱتَّقِ ٱللَّهَ وَتُخۡفِی فِی نَفۡسِكَ مَا ٱللَّهُ مُبۡدِیهِ وَتَخۡشَى ٱلنَّاسَ وَٱللَّهُ أَحَقُّ أَن تَخۡشَىٰهُۖ فَلَمَّا قَضَىٰ زَیۡدࣱ مِّنۡهَا وَطَرࣰا زَوَّجۡنَـٰكَهَا لِكَیۡ لَا یَكُونَ عَلَى ٱلۡمُؤۡمِنِینَ حَرَجࣱ فِیۤ أَزۡوَ ٰ⁠جِ أَدۡعِیَاۤىِٕهِمۡ إِذَا قَضَوۡا۟ مِنۡهُنَّ وَطَرࣰاۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:46'),
(2753, 33, 3571, 38, 'مَّا كَانَ عَلَى ٱلنَّبِیِّ مِنۡ حَرَجࣲ فِیمَا فَرَضَ ٱللَّهُ لَهُۥۖ سُنَّةَ ٱللَّهِ فِی ٱلَّذِینَ خَلَوۡا۟ مِن قَبۡلُۚ وَكَانَ أَمۡرُ ٱللَّهِ قَدَرࣰا مَّقۡدُورًا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:46'),
(2754, 33, 3572, 39, 'ٱلَّذِینَ یُبَلِّغُونَ رِسَـٰلَـٰتِ ٱللَّهِ وَیَخۡشَوۡنَهُۥ وَلَا یَخۡشَوۡنَ أَحَدًا إِلَّا ٱللَّهَۗ وَكَفَىٰ بِٱللَّهِ حَسِیبࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:46'),
(2755, 33, 3573, 40, 'مَّا كَانَ مُحَمَّدٌ أَبَاۤ أَحَدࣲ مِّن رِّجَالِكُمۡ وَلَـٰكِن رَّسُولَ ٱللَّهِ وَخَاتَمَ ٱلنَّبِیِّـۧنَۗ وَكَانَ ٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2756, 33, 3574, 41, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱذۡكُرُوا۟ ٱللَّهَ ذِكۡرࣰا كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2757, 33, 3575, 42, 'وَسَبِّحُوهُ بُكۡرَةࣰ وَأَصِیلًا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2758, 33, 3576, 43, 'هُوَ ٱلَّذِی یُصَلِّی عَلَیۡكُمۡ وَمَلَـٰۤىِٕكَتُهُۥ لِیُخۡرِجَكُم مِّنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِۚ وَكَانَ بِٱلۡمُؤۡمِنِینَ رَحِیمࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2759, 33, 3577, 44, 'تَحِیَّتُهُمۡ یَوۡمَ یَلۡقَوۡنَهُۥ سَلَـٰمࣱۚ وَأَعَدَّ لَهُمۡ أَجۡرࣰا كَرِیمࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2760, 33, 3578, 45, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ إِنَّاۤ أَرۡسَلۡنَـٰكَ شَـٰهِدࣰا وَمُبَشِّرࣰا وَنَذِیرࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2761, 33, 3579, 46, 'وَدَاعِیًا إِلَى ٱللَّهِ بِإِذۡنِهِۦ وَسِرَاجࣰا مُّنِیرࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2762, 33, 3580, 47, 'وَبَشِّرِ ٱلۡمُؤۡمِنِینَ بِأَنَّ لَهُم مِّنَ ٱللَّهِ فَضۡلࣰا كَبِیرࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2763, 33, 3581, 48, 'وَلَا تُطِعِ ٱلۡكَـٰفِرِینَ وَٱلۡمُنَـٰفِقِینَ وَدَعۡ أَذَىٰهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِیلࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2764, 33, 3582, 49, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا نَكَحۡتُمُ ٱلۡمُؤۡمِنَـٰتِ ثُمَّ طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمۡ عَلَیۡهِنَّ مِنۡ عِدَّةࣲ تَعۡتَدُّونَهَاۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحࣰا جَمِیلࣰا\n', NULL, 0, '2023-05-26 17:26:29', '2023-05-27 11:11:47'),
(2765, 33, 3583, 50, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ إِنَّاۤ أَحۡلَلۡنَا لَكَ أَزۡوَ ٰ⁠جَكَ ٱلَّـٰتِیۤ ءَاتَیۡتَ أُجُورَهُنَّ وَمَا مَلَكَتۡ یَمِینُكَ مِمَّاۤ أَفَاۤءَ ٱللَّهُ عَلَیۡكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّـٰتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَـٰلَـٰتِكَ ٱلَّـٰتِی هَاجَرۡنَ مَعَكَ وَٱمۡرَأَةࣰ مُّؤۡمِنَةً إِن وَهَبَتۡ نَفۡسَهَا لِلنَّبِیِّ إِنۡ أَرَادَ ٱلنَّبِیُّ أَن یَسۡتَنكِحَهَا خَالِصَةࣰ لَّكَ مِن دُونِ ٱلۡمُؤۡمِنِینَۗ قَدۡ عَلِمۡنَا مَا فَرَضۡنَا عَلَیۡهِمۡ فِیۤ أَزۡوَ ٰ⁠جِهِمۡ وَمَا مَلَكَتۡ أَیۡمَـٰنُهُمۡ لِكَیۡلَا یَكُونَ عَلَیۡكَ حَرَجࣱۗ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2766, 33, 3584, 51, '۞ تُرۡجِی مَن تَشَاۤءُ مِنۡهُنَّ وَتُـٔۡوِیۤ إِلَیۡكَ مَن تَشَاۤءُۖ وَمَنِ ٱبۡتَغَیۡتَ مِمَّنۡ عَزَلۡتَ فَلَا جُنَاحَ عَلَیۡكَۚ ذَ ٰ⁠لِكَ أَدۡنَىٰۤ أَن تَقَرَّ أَعۡیُنُهُنَّ وَلَا یَحۡزَنَّ وَیَرۡضَیۡنَ بِمَاۤ ءَاتَیۡتَهُنَّ كُلُّهُنَّۚ وَٱللَّهُ یَعۡلَمُ مَا فِی قُلُوبِكُمۡۚ وَكَانَ ٱللَّهُ عَلِیمًا حَلِیمࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2767, 33, 3585, 52, 'لَّا یَحِلُّ لَكَ ٱلنِّسَاۤءُ مِنۢ بَعۡدُ وَلَاۤ أَن تَبَدَّلَ بِهِنَّ مِنۡ أَزۡوَ ٰ⁠جࣲ وَلَوۡ أَعۡجَبَكَ حُسۡنُهُنَّ إِلَّا مَا مَلَكَتۡ یَمِینُكَۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ رَّقِیبࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2768, 33, 3586, 53, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَدۡخُلُوا۟ بُیُوتَ ٱلنَّبِیِّ إِلَّاۤ أَن یُؤۡذَنَ لَكُمۡ إِلَىٰ طَعَامٍ غَیۡرَ نَـٰظِرِینَ إِنَىٰهُ وَلَـٰكِنۡ إِذَا دُعِیتُمۡ فَٱدۡخُلُوا۟ فَإِذَا طَعِمۡتُمۡ فَٱنتَشِرُوا۟ وَلَا مُسۡتَـٔۡنِسِینَ لِحَدِیثٍۚ إِنَّ ذَ ٰ⁠لِكُمۡ كَانَ یُؤۡذِی ٱلنَّبِیَّ فَیَسۡتَحۡیِۦ مِنكُمۡۖ وَٱللَّهُ لَا یَسۡتَحۡیِۦ مِنَ ٱلۡحَقِّۚ وَإِذَا سَأَلۡتُمُوهُنَّ مَتَـٰعࣰا فَسۡـَٔلُوهُنَّ مِن وَرَاۤءِ حِجَابࣲۚ ذَ ٰ⁠لِكُمۡ أَطۡهَرُ لِقُلُوبِكُمۡ وَقُلُوبِهِنَّۚ وَمَا كَانَ لَكُمۡ أَن تُؤۡذُوا۟ رَسُولَ ٱللَّهِ وَلَاۤ أَن تَنكِحُوۤا۟ أَزۡوَ ٰ⁠جَهُۥ مِنۢ بَعۡدِهِۦۤ أَبَدًاۚ إِنَّ ذَ ٰ⁠لِكُمۡ كَانَ عِندَ ٱللَّهِ عَظِیمًا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2769, 33, 3587, 54, 'إِن تُبۡدُوا۟ شَیۡـًٔا أَوۡ تُخۡفُوهُ فَإِنَّ ٱللَّهَ كَانَ بِكُلِّ شَیۡءٍ عَلِیمࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2770, 33, 3588, 55, 'لَّا جُنَاحَ عَلَیۡهِنَّ فِیۤ ءَابَاۤىِٕهِنَّ وَلَاۤ أَبۡنَاۤىِٕهِنَّ وَلَاۤ إِخۡوَ ٰ⁠نِهِنَّ وَلَاۤ أَبۡنَاۤءِ إِخۡوَ ٰ⁠نِهِنَّ وَلَاۤ أَبۡنَاۤءِ أَخَوَ ٰ⁠تِهِنَّ وَلَا نِسَاۤىِٕهِنَّ وَلَا مَا مَلَكَتۡ أَیۡمَـٰنُهُنَّۗ وَٱتَّقِینَ ٱللَّهَۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَیۡءࣲ شَهِیدًا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2771, 33, 3589, 56, 'إِنَّ ٱللَّهَ وَمَلَـٰۤىِٕكَتَهُۥ یُصَلُّونَ عَلَى ٱلنَّبِیِّۚ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ صَلُّوا۟ عَلَیۡهِ وَسَلِّمُوا۟ تَسۡلِیمًا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2772, 33, 3590, 57, 'إِنَّ ٱلَّذِینَ یُؤۡذُونَ ٱللَّهَ وَرَسُولَهُۥ لَعَنَهُمُ ٱللَّهُ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِ وَأَعَدَّ لَهُمۡ عَذَابࣰا مُّهِینࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:47'),
(2773, 33, 3591, 58, 'وَٱلَّذِینَ یُؤۡذُونَ ٱلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِ بِغَیۡرِ مَا ٱكۡتَسَبُوا۟ فَقَدِ ٱحۡتَمَلُوا۟ بُهۡتَـٰنࣰا وَإِثۡمࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2774, 33, 3592, 59, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ قُل لِّأَزۡوَ ٰ⁠جِكَ وَبَنَاتِكَ وَنِسَاۤءِ ٱلۡمُؤۡمِنِینَ یُدۡنِینَ عَلَیۡهِنَّ مِن جَلَـٰبِیبِهِنَّۚ ذَ ٰ⁠لِكَ أَدۡنَىٰۤ أَن یُعۡرَفۡنَ فَلَا یُؤۡذَیۡنَۗ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2775, 33, 3593, 60, '۞ لَّىِٕن لَّمۡ یَنتَهِ ٱلۡمُنَـٰفِقُونَ وَٱلَّذِینَ فِی قُلُوبِهِم مَّرَضࣱ وَٱلۡمُرۡجِفُونَ فِی ٱلۡمَدِینَةِ لَنُغۡرِیَنَّكَ بِهِمۡ ثُمَّ لَا یُجَاوِرُونَكَ فِیهَاۤ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2776, 33, 3594, 61, 'مَّلۡعُونِینَۖ أَیۡنَمَا ثُقِفُوۤا۟ أُخِذُوا۟ وَقُتِّلُوا۟ تَقۡتِیلࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2777, 33, 3595, 62, 'سُنَّةَ ٱللَّهِ فِی ٱلَّذِینَ خَلَوۡا۟ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِیلࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2778, 33, 3596, 63, 'یَسۡـَٔلُكَ ٱلنَّاسُ عَنِ ٱلسَّاعَةِۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِۚ وَمَا یُدۡرِیكَ لَعَلَّ ٱلسَّاعَةَ تَكُونُ قَرِیبًا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2779, 33, 3597, 64, 'إِنَّ ٱللَّهَ لَعَنَ ٱلۡكَـٰفِرِینَ وَأَعَدَّ لَهُمۡ سَعِیرًا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2780, 33, 3598, 65, 'خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۖ لَّا یَجِدُونَ وَلِیࣰّا وَلَا نَصِیرࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2781, 33, 3599, 66, 'یَوۡمَ تُقَلَّبُ وُجُوهُهُمۡ فِی ٱلنَّارِ یَقُولُونَ یَـٰلَیۡتَنَاۤ أَطَعۡنَا ٱللَّهَ وَأَطَعۡنَا ٱلرَّسُولَا۠\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2782, 33, 3600, 67, 'وَقَالُوا۟ رَبَّنَاۤ إِنَّاۤ أَطَعۡنَا سَادَتَنَا وَكُبَرَاۤءَنَا فَأَضَلُّونَا ٱلسَّبِیلَا۠\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2783, 33, 3601, 68, 'رَبَّنَاۤ ءَاتِهِمۡ ضِعۡفَیۡنِ مِنَ ٱلۡعَذَابِ وَٱلۡعَنۡهُمۡ لَعۡنࣰا كَبِیرࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2784, 33, 3602, 69, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَكُونُوا۟ كَٱلَّذِینَ ءَاذَوۡا۟ مُوسَىٰ فَبَرَّأَهُ ٱللَّهُ مِمَّا قَالُوا۟ۚ وَكَانَ عِندَ ٱللَّهِ وَجِیهࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2785, 33, 3603, 70, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَقُولُوا۟ قَوۡلࣰا سَدِیدࣰا\n', NULL, 0, '2023-05-26 17:26:30', '2023-05-27 11:11:48'),
(2786, 33, 3604, 71, 'یُصۡلِحۡ لَكُمۡ أَعۡمَـٰلَكُمۡ وَیَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۗ وَمَن یُطِعِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ فَازَ فَوۡزًا عَظِیمًا\n', NULL, 0, '2023-05-26 17:26:31', '2023-05-27 11:11:48'),
(2787, 33, 3605, 72, 'إِنَّا عَرَضۡنَا ٱلۡأَمَانَةَ عَلَى ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَٱلۡجِبَالِ فَأَبَیۡنَ أَن یَحۡمِلۡنَهَا وَأَشۡفَقۡنَ مِنۡهَا وَحَمَلَهَا ٱلۡإِنسَـٰنُۖ إِنَّهُۥ كَانَ ظَلُومࣰا جَهُولࣰا\n', NULL, 0, '2023-05-26 17:26:31', '2023-05-27 11:11:48'),
(2788, 33, 3606, 73, 'لِّیُعَذِّبَ ٱللَّهُ ٱلۡمُنَـٰفِقِینَ وَٱلۡمُنَـٰفِقَـٰتِ وَٱلۡمُشۡرِكِینَ وَٱلۡمُشۡرِكَـٰتِ وَیَتُوبَ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِۗ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمَۢا\n', NULL, 0, '2023-05-26 17:26:31', '2023-05-27 11:11:48'),
(2789, 32, 3504, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2790, 32, 3505, 2, 'تَنزِیلُ ٱلۡكِتَـٰبِ لَا رَیۡبَ فِیهِ مِن رَّبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2791, 32, 3506, 3, 'أَمۡ یَقُولُونَ ٱفۡتَرَىٰهُۚ بَلۡ هُوَ ٱلۡحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوۡمࣰا مَّاۤ أَتَىٰهُم مِّن نَّذِیرࣲ مِّن قَبۡلِكَ لَعَلَّهُمۡ یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2792, 32, 3507, 4, 'ٱللَّهُ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَا فِی سِتَّةِ أَیَّامࣲ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ مَا لَكُم مِّن دُونِهِۦ مِن وَلِیࣲّ وَلَا شَفِیعٍۚ أَفَلَا تَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2793, 32, 3508, 5, 'یُدَبِّرُ ٱلۡأَمۡرَ مِنَ ٱلسَّمَاۤءِ إِلَى ٱلۡأَرۡضِ ثُمَّ یَعۡرُجُ إِلَیۡهِ فِی یَوۡمࣲ كَانَ مِقۡدَارُهُۥۤ أَلۡفَ سَنَةࣲ مِّمَّا تَعُدُّونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2794, 32, 3509, 6, 'ذَ ٰ⁠لِكَ عَـٰلِمُ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ ٱلۡعَزِیزُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2795, 32, 3510, 7, 'ٱلَّذِیۤ أَحۡسَنَ كُلَّ شَیۡءٍ خَلَقَهُۥۖ وَبَدَأَ خَلۡقَ ٱلۡإِنسَـٰنِ مِن طِینࣲ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2796, 32, 3511, 8, 'ثُمَّ جَعَلَ نَسۡلَهُۥ مِن سُلَـٰلَةࣲ مِّن مَّاۤءࣲ مَّهِینࣲ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2797, 32, 3512, 9, 'ثُمَّ سَوَّىٰهُ وَنَفَخَ فِیهِ مِن رُّوحِهِۦۖ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَـٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِیلࣰا مَّا تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2798, 32, 3513, 10, 'وَقَالُوۤا۟ أَءِذَا ضَلَلۡنَا فِی ٱلۡأَرۡضِ أَءِنَّا لَفِی خَلۡقࣲ جَدِیدِۭۚ بَلۡ هُم بِلِقَاۤءِ رَبِّهِمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2799, 32, 3514, 11, '۞ قُلۡ یَتَوَفَّىٰكُم مَّلَكُ ٱلۡمَوۡتِ ٱلَّذِی وُكِّلَ بِكُمۡ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2800, 32, 3515, 12, 'وَلَوۡ تَرَىٰۤ إِذِ ٱلۡمُجۡرِمُونَ نَاكِسُوا۟ رُءُوسِهِمۡ عِندَ رَبِّهِمۡ رَبَّنَاۤ أَبۡصَرۡنَا وَسَمِعۡنَا فَٱرۡجِعۡنَا نَعۡمَلۡ صَـٰلِحًا إِنَّا مُوقِنُونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2801, 32, 3516, 13, 'وَلَوۡ شِئۡنَا لَـَٔاتَیۡنَا كُلَّ نَفۡسٍ هُدَىٰهَا وَلَـٰكِنۡ حَقَّ ٱلۡقَوۡلُ مِنِّی لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2802, 32, 3517, 14, 'فَذُوقُوا۟ بِمَا نَسِیتُمۡ لِقَاۤءَ یَوۡمِكُمۡ هَـٰذَاۤ إِنَّا نَسِینَـٰكُمۡۖ وَذُوقُوا۟ عَذَابَ ٱلۡخُلۡدِ بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:26:32', '2023-05-27 11:11:51'),
(2803, 32, 3518, 15, 'إِنَّمَا یُؤۡمِنُ بِـَٔایَـٰتِنَا ٱلَّذِینَ إِذَا ذُكِّرُوا۟ بِهَا خَرُّوا۟ سُجَّدࣰا وَسَبَّحُوا۟ بِحَمۡدِ رَبِّهِمۡ وَهُمۡ لَا یَسۡتَكۡبِرُونَ ۩\n', NULL, 1, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2804, 32, 3519, 16, 'تَتَجَافَىٰ جُنُوبُهُمۡ عَنِ ٱلۡمَضَاجِعِ یَدۡعُونَ رَبَّهُمۡ خَوۡفࣰا وَطَمَعࣰا وَمِمَّا رَزَقۡنَـٰهُمۡ یُنفِقُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2805, 32, 3520, 17, 'فَلَا تَعۡلَمُ نَفۡسࣱ مَّاۤ أُخۡفِیَ لَهُم مِّن قُرَّةِ أَعۡیُنࣲ جَزَاۤءَۢ بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2806, 32, 3521, 18, 'أَفَمَن كَانَ مُؤۡمِنࣰا كَمَن كَانَ فَاسِقࣰاۚ لَّا یَسۡتَوُۥنَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2807, 32, 3522, 19, 'أَمَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَلَهُمۡ جَنَّـٰتُ ٱلۡمَأۡوَىٰ نُزُلَۢا بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2808, 32, 3523, 20, 'وَأَمَّا ٱلَّذِینَ فَسَقُوا۟ فَمَأۡوَىٰهُمُ ٱلنَّارُۖ كُلَّمَاۤ أَرَادُوۤا۟ أَن یَخۡرُجُوا۟ مِنۡهَاۤ أُعِیدُوا۟ فِیهَا وَقِیلَ لَهُمۡ ذُوقُوا۟ عَذَابَ ٱلنَّارِ ٱلَّذِی كُنتُم بِهِۦ تُكَذِّبُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2809, 32, 3524, 21, 'وَلَنُذِیقَنَّهُم مِّنَ ٱلۡعَذَابِ ٱلۡأَدۡنَىٰ دُونَ ٱلۡعَذَابِ ٱلۡأَكۡبَرِ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2810, 32, 3525, 22, 'وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔایَـٰتِ رَبِّهِۦ ثُمَّ أَعۡرَضَ عَنۡهَاۤۚ إِنَّا مِنَ ٱلۡمُجۡرِمِینَ مُنتَقِمُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2811, 32, 3526, 23, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ فَلَا تَكُن فِی مِرۡیَةࣲ مِّن لِّقَاۤىِٕهِۦۖ وَجَعَلۡنَـٰهُ هُدࣰى لِّبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2812, 32, 3527, 24, 'وَجَعَلۡنَا مِنۡهُمۡ أَىِٕمَّةࣰ یَهۡدُونَ بِأَمۡرِنَا لَمَّا صَبَرُوا۟ۖ وَكَانُوا۟ بِـَٔایَـٰتِنَا یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2813, 32, 3528, 25, 'إِنَّ رَبَّكَ هُوَ یَفۡصِلُ بَیۡنَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ فِیمَا كَانُوا۟ فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2814, 32, 3529, 26, 'أَوَلَمۡ یَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّنَ ٱلۡقُرُونِ یَمۡشُونَ فِی مَسَـٰكِنِهِمۡۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتٍۚ أَفَلَا یَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2815, 32, 3530, 27, 'أَوَلَمۡ یَرَوۡا۟ أَنَّا نَسُوقُ ٱلۡمَاۤءَ إِلَى ٱلۡأَرۡضِ ٱلۡجُرُزِ فَنُخۡرِجُ بِهِۦ زَرۡعࣰا تَأۡكُلُ مِنۡهُ أَنۡعَـٰمُهُمۡ وَأَنفُسُهُمۡۚ أَفَلَا یُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2816, 32, 3531, 28, 'وَیَقُولُونَ مَتَىٰ هَـٰذَا ٱلۡفَتۡحُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2817, 32, 3532, 29, 'قُلۡ یَوۡمَ ٱلۡفَتۡحِ لَا یَنفَعُ ٱلَّذِینَ كَفَرُوۤا۟ إِیمَـٰنُهُمۡ وَلَا هُمۡ یُنظَرُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2818, 32, 3533, 30, 'فَأَعۡرِضۡ عَنۡهُمۡ وَٱنتَظِرۡ إِنَّهُم مُّنتَظِرُونَ\n', NULL, 0, '2023-05-26 17:26:33', '2023-05-27 11:11:52'),
(2819, 31, 3470, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:54'),
(2820, 31, 3471, 2, 'تِلۡكَ ءَایَـٰتُ ٱلۡكِتَـٰبِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:54'),
(2821, 31, 3472, 3, 'هُدࣰى وَرَحۡمَةࣰ لِّلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:54'),
(2822, 31, 3473, 4, 'ٱلَّذِینَ یُقِیمُونَ ٱلصَّلَوٰةَ وَیُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡـَٔاخِرَةِ هُمۡ یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:54'),
(2823, 31, 3474, 5, 'أُو۟لَـٰۤىِٕكَ عَلَىٰ هُدࣰى مِّن رَّبِّهِمۡۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:54'),
(2824, 31, 3475, 6, 'وَمِنَ ٱلنَّاسِ مَن یَشۡتَرِی لَهۡوَ ٱلۡحَدِیثِ لِیُضِلَّ عَن سَبِیلِ ٱللَّهِ بِغَیۡرِ عِلۡمࣲ وَیَتَّخِذَهَا هُزُوًاۚ أُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:54'),
(2825, 31, 3476, 7, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِ ءَایَـٰتُنَا وَلَّىٰ مُسۡتَكۡبِرࣰا كَأَن لَّمۡ یَسۡمَعۡهَا كَأَنَّ فِیۤ أُذُنَیۡهِ وَقۡرࣰاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِیمٍ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:54'),
(2826, 31, 3477, 8, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُمۡ جَنَّـٰتُ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 17:26:40', '2023-05-27 11:11:55'),
(2827, 31, 3478, 9, 'خَـٰلِدِینَ فِیهَاۖ وَعۡدَ ٱللَّهِ حَقࣰّاۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2828, 31, 3479, 10, 'خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ بِغَیۡرِ عَمَدࣲ تَرَوۡنَهَاۖ وَأَلۡقَىٰ فِی ٱلۡأَرۡضِ رَوَ ٰ⁠سِیَ أَن تَمِیدَ بِكُمۡ وَبَثَّ فِیهَا مِن كُلِّ دَاۤبَّةࣲۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَنۢبَتۡنَا فِیهَا مِن كُلِّ زَوۡجࣲ كَرِیمٍ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2829, 31, 3480, 11, 'هَـٰذَا خَلۡقُ ٱللَّهِ فَأَرُونِی مَاذَا خَلَقَ ٱلَّذِینَ مِن دُونِهِۦۚ بَلِ ٱلظَّـٰلِمُونَ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2830, 31, 3481, 12, 'وَلَقَدۡ ءَاتَیۡنَا لُقۡمَـٰنَ ٱلۡحِكۡمَةَ أَنِ ٱشۡكُرۡ لِلَّهِۚ وَمَن یَشۡكُرۡ فَإِنَّمَا یَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِیٌّ حَمِیدࣱ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2831, 31, 3482, 13, 'وَإِذۡ قَالَ لُقۡمَـٰنُ لِٱبۡنِهِۦ وَهُوَ یَعِظُهُۥ یَـٰبُنَیَّ لَا تُشۡرِكۡ بِٱللَّهِۖ إِنَّ ٱلشِّرۡكَ لَظُلۡمٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2832, 31, 3483, 14, 'وَوَصَّیۡنَا ٱلۡإِنسَـٰنَ بِوَ ٰ⁠لِدَیۡهِ حَمَلَتۡهُ أُمُّهُۥ وَهۡنًا عَلَىٰ وَهۡنࣲ وَفِصَـٰلُهُۥ فِی عَامَیۡنِ أَنِ ٱشۡكُرۡ لِی وَلِوَ ٰ⁠لِدَیۡكَ إِلَیَّ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2833, 31, 3484, 15, 'وَإِن جَـٰهَدَاكَ عَلَىٰۤ أَن تُشۡرِكَ بِی مَا لَیۡسَ لَكَ بِهِۦ عِلۡمࣱ فَلَا تُطِعۡهُمَاۖ وَصَاحِبۡهُمَا فِی ٱلدُّنۡیَا مَعۡرُوفࣰاۖ وَٱتَّبِعۡ سَبِیلَ مَنۡ أَنَابَ إِلَیَّۚ ثُمَّ إِلَیَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2834, 31, 3485, 16, 'یَـٰبُنَیَّ إِنَّهَاۤ إِن تَكُ مِثۡقَالَ حَبَّةࣲ مِّنۡ خَرۡدَلࣲ فَتَكُن فِی صَخۡرَةٍ أَوۡ فِی ٱلسَّمَـٰوَ ٰ⁠تِ أَوۡ فِی ٱلۡأَرۡضِ یَأۡتِ بِهَا ٱللَّهُۚ إِنَّ ٱللَّهَ لَطِیفٌ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2835, 31, 3486, 17, 'یَـٰبُنَیَّ أَقِمِ ٱلصَّلَوٰةَ وَأۡمُرۡ بِٱلۡمَعۡرُوفِ وَٱنۡهَ عَنِ ٱلۡمُنكَرِ وَٱصۡبِرۡ عَلَىٰ مَاۤ أَصَابَكَۖ إِنَّ ذَ ٰ⁠لِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2836, 31, 3487, 18, 'وَلَا تُصَعِّرۡ خَدَّكَ لِلنَّاسِ وَلَا تَمۡشِ فِی ٱلۡأَرۡضِ مَرَحًاۖ إِنَّ ٱللَّهَ لَا یُحِبُّ كُلَّ مُخۡتَالࣲ فَخُورࣲ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2837, 31, 3488, 19, 'وَٱقۡصِدۡ فِی مَشۡیِكَ وَٱغۡضُضۡ مِن صَوۡتِكَۚ إِنَّ أَنكَرَ ٱلۡأَصۡوَ ٰ⁠تِ لَصَوۡتُ ٱلۡحَمِیرِ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2838, 31, 3489, 20, 'أَلَمۡ تَرَوۡا۟ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِ وَأَسۡبَغَ عَلَیۡكُمۡ نِعَمَهُۥ ظَـٰهِرَةࣰ وَبَاطِنَةࣰۗ وَمِنَ ٱلنَّاسِ مَن یُجَـٰدِلُ فِی ٱللَّهِ بِغَیۡرِ عِلۡمࣲ وَلَا هُدࣰى وَلَا كِتَـٰبࣲ مُّنِیرࣲ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2839, 31, 3490, 21, 'وَإِذَا قِیلَ لَهُمُ ٱتَّبِعُوا۟ مَاۤ أَنزَلَ ٱللَّهُ قَالُوا۟ بَلۡ نَتَّبِعُ مَا وَجَدۡنَا عَلَیۡهِ ءَابَاۤءَنَاۤۚ أَوَلَوۡ كَانَ ٱلشَّیۡطَـٰنُ یَدۡعُوهُمۡ إِلَىٰ عَذَابِ ٱلسَّعِیرِ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(2840, 31, 3491, 22, '۞ وَمَن یُسۡلِمۡ وَجۡهَهُۥۤ إِلَى ٱللَّهِ وَهُوَ مُحۡسِنࣱ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰۗ وَإِلَى ٱللَّهِ عَـٰقِبَةُ ٱلۡأُمُورِ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2841, 31, 3492, 23, 'وَمَن كَفَرَ فَلَا یَحۡزُنكَ كُفۡرُهُۥۤۚ إِلَیۡنَا مَرۡجِعُهُمۡ فَنُنَبِّئُهُم بِمَا عَمِلُوۤا۟ۚ إِنَّ ٱللَّهَ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2842, 31, 3493, 24, 'نُمَتِّعُهُمۡ قَلِیلࣰا ثُمَّ نَضۡطَرُّهُمۡ إِلَىٰ عَذَابٍ غَلِیظࣲ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2843, 31, 3494, 25, 'وَلَىِٕن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ لَیَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:41', '2023-05-27 11:11:55'),
(2844, 31, 3495, 26, 'لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ إِنَّ ٱللَّهَ هُوَ ٱلۡغَنِیُّ ٱلۡحَمِیدُ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2845, 31, 3496, 27, 'وَلَوۡ أَنَّمَا فِی ٱلۡأَرۡضِ مِن شَجَرَةٍ أَقۡلَـٰمࣱ وَٱلۡبَحۡرُ یَمُدُّهُۥ مِنۢ بَعۡدِهِۦ سَبۡعَةُ أَبۡحُرࣲ مَّا نَفِدَتۡ كَلِمَـٰتُ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2846, 31, 3497, 28, 'مَّا خَلۡقُكُمۡ وَلَا بَعۡثُكُمۡ إِلَّا كَنَفۡسࣲ وَ ٰ⁠حِدَةٍۚ إِنَّ ٱللَّهَ سَمِیعُۢ بَصِیرٌ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2847, 31, 3498, 29, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ یُولِجُ ٱلَّیۡلَ فِی ٱلنَّهَارِ وَیُولِجُ ٱلنَّهَارَ فِی ٱلَّیۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلࣱّ یَجۡرِیۤ إِلَىٰۤ أَجَلࣲ مُّسَمࣰّى وَأَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2848, 31, 3499, 30, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا یَدۡعُونَ مِن دُونِهِ ٱلۡبَـٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِیُّ ٱلۡكَبِیرُ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2849, 31, 3500, 31, 'أَلَمۡ تَرَ أَنَّ ٱلۡفُلۡكَ تَجۡرِی فِی ٱلۡبَحۡرِ بِنِعۡمَتِ ٱللَّهِ لِیُرِیَكُم مِّنۡ ءَایَـٰتِهِۦۤۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّكُلِّ صَبَّارࣲ شَكُورࣲ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2850, 31, 3501, 32, 'وَإِذَا غَشِیَهُم مَّوۡجࣱ كَٱلظُّلَلِ دَعَوُا۟ ٱللَّهَ مُخۡلِصِینَ لَهُ ٱلدِّینَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ فَمِنۡهُم مُّقۡتَصِدࣱۚ وَمَا یَجۡحَدُ بِـَٔایَـٰتِنَاۤ إِلَّا كُلُّ خَتَّارࣲ كَفُورࣲ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2851, 31, 3502, 33, 'یَـٰۤأَیُّهَا ٱلنَّاسُ ٱتَّقُوا۟ رَبَّكُمۡ وَٱخۡشَوۡا۟ یَوۡمࣰا لَّا یَجۡزِی وَالِدٌ عَن وَلَدِهِۦ وَلَا مَوۡلُودٌ هُوَ جَازٍ عَن وَالِدِهِۦ شَیۡـًٔاۚ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَیَوٰةُ ٱلدُّنۡیَا وَلَا یَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2852, 31, 3503, 34, 'إِنَّ ٱللَّهَ عِندَهُۥ عِلۡمُ ٱلسَّاعَةِ وَیُنَزِّلُ ٱلۡغَیۡثَ وَیَعۡلَمُ مَا فِی ٱلۡأَرۡحَامِۖ وَمَا تَدۡرِی نَفۡسࣱ مَّاذَا تَكۡسِبُ غَدࣰاۖ وَمَا تَدۡرِی نَفۡسُۢ بِأَیِّ أَرۡضࣲ تَمُوتُۚ إِنَّ ٱللَّهَ عَلِیمٌ خَبِیرُۢ\n', NULL, 0, '2023-05-26 17:26:42', '2023-05-27 11:11:56'),
(2853, 30, 3410, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤ\n', NULL, 0, '2023-05-26 17:26:50', '2023-05-27 11:12:00'),
(2854, 30, 3411, 2, 'غُلِبَتِ ٱلرُّومُ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2855, 30, 3412, 3, 'فِیۤ أَدۡنَى ٱلۡأَرۡضِ وَهُم مِّنۢ بَعۡدِ غَلَبِهِمۡ سَیَغۡلِبُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2856, 30, 3413, 4, 'فِی بِضۡعِ سِنِینَۗ لِلَّهِ ٱلۡأَمۡرُ مِن قَبۡلُ وَمِنۢ بَعۡدُۚ وَیَوۡمَىِٕذࣲ یَفۡرَحُ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2857, 30, 3414, 5, 'بِنَصۡرِ ٱللَّهِۚ یَنصُرُ مَن یَشَاۤءُۖ وَهُوَ ٱلۡعَزِیزُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2858, 30, 3415, 6, 'وَعۡدَ ٱللَّهِۖ لَا یُخۡلِفُ ٱللَّهُ وَعۡدَهُۥ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2859, 30, 3416, 7, 'یَعۡلَمُونَ ظَـٰهِرࣰا مِّنَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَهُمۡ عَنِ ٱلۡـَٔاخِرَةِ هُمۡ غَـٰفِلُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2860, 30, 3417, 8, 'أَوَلَمۡ یَتَفَكَّرُوا۟ فِیۤ أَنفُسِهِمۗ مَّا خَلَقَ ٱللَّهُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَاۤ إِلَّا بِٱلۡحَقِّ وَأَجَلࣲ مُّسَمࣰّىۗ وَإِنَّ كَثِیرࣰا مِّنَ ٱلنَّاسِ بِلِقَاۤىِٕ رَبِّهِمۡ لَكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2861, 30, 3418, 9, 'أَوَلَمۡ یَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَیَنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلَّذِینَ مِن قَبۡلِهِمۡۚ كَانُوۤا۟ أَشَدَّ مِنۡهُمۡ قُوَّةࣰ وَأَثَارُوا۟ ٱلۡأَرۡضَ وَعَمَرُوهَاۤ أَكۡثَرَ مِمَّا عَمَرُوهَا وَجَاۤءَتۡهُمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِۖ فَمَا كَانَ ٱللَّهُ لِیَظۡلِمَهُمۡ وَلَـٰكِن كَانُوۤا۟ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2862, 30, 3419, 10, 'ثُمَّ كَانَ عَـٰقِبَةَ ٱلَّذِینَ أَسَـٰۤـُٔوا۟ ٱلسُّوۤأَىٰۤ أَن كَذَّبُوا۟ بِـَٔایَـٰتِ ٱللَّهِ وَكَانُوا۟ بِهَا یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:00'),
(2863, 30, 3420, 11, 'ٱللَّهُ یَبۡدَؤُا۟ ٱلۡخَلۡقَ ثُمَّ یُعِیدُهُۥ ثُمَّ إِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2864, 30, 3421, 12, 'وَیَوۡمَ تَقُومُ ٱلسَّاعَةُ یُبۡلِسُ ٱلۡمُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2865, 30, 3422, 13, 'وَلَمۡ یَكُن لَّهُم مِّن شُرَكَاۤىِٕهِمۡ شُفَعَـٰۤؤُا۟ وَكَانُوا۟ بِشُرَكَاۤىِٕهِمۡ كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2866, 30, 3423, 14, 'وَیَوۡمَ تَقُومُ ٱلسَّاعَةُ یَوۡمَىِٕذࣲ یَتَفَرَّقُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2867, 30, 3424, 15, 'فَأَمَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَهُمۡ فِی رَوۡضَةࣲ یُحۡبَرُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2868, 30, 3425, 16, 'وَأَمَّا ٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔایَـٰتِنَا وَلِقَاۤىِٕ ٱلۡـَٔاخِرَةِ فَأُو۟لَـٰۤىِٕكَ فِی ٱلۡعَذَابِ مُحۡضَرُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2869, 30, 3426, 17, 'فَسُبۡحَـٰنَ ٱللَّهِ حِینَ تُمۡسُونَ وَحِینَ تُصۡبِحُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2870, 30, 3427, 18, 'وَلَهُ ٱلۡحَمۡدُ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَعَشِیࣰّا وَحِینَ تُظۡهِرُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2871, 30, 3428, 19, 'یُخۡرِجُ ٱلۡحَیَّ مِنَ ٱلۡمَیِّتِ وَیُخۡرِجُ ٱلۡمَیِّتَ مِنَ ٱلۡحَیِّ وَیُحۡیِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ وَكَذَ ٰ⁠لِكَ تُخۡرَجُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2872, 30, 3429, 20, 'وَمِنۡ ءَایَـٰتِهِۦۤ أَنۡ خَلَقَكُم مِّن تُرَابࣲ ثُمَّ إِذَاۤ أَنتُم بَشَرࣱ تَنتَشِرُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2873, 30, 3430, 21, 'وَمِنۡ ءَایَـٰتِهِۦۤ أَنۡ خَلَقَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَ ٰ⁠جࣰا لِّتَسۡكُنُوۤا۟ إِلَیۡهَا وَجَعَلَ بَیۡنَكُم مَّوَدَّةࣰ وَرَحۡمَةًۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2874, 30, 3431, 22, 'وَمِنۡ ءَایَـٰتِهِۦ خَلۡقُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَٱخۡتِلَـٰفُ أَلۡسِنَتِكُمۡ وَأَلۡوَ ٰ⁠نِكُمۡۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّلۡعَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2875, 30, 3432, 23, 'وَمِنۡ ءَایَـٰتِهِۦ مَنَامُكُم بِٱلَّیۡلِ وَٱلنَّهَارِ وَٱبۡتِغَاۤؤُكُم مِّن فَضۡلِهِۦۤۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2876, 30, 3433, 24, 'وَمِنۡ ءَایَـٰتِهِۦ یُرِیكُمُ ٱلۡبَرۡقَ خَوۡفࣰا وَطَمَعࣰا وَیُنَزِّلُ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَیُحۡیِۦ بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۤۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:26:51', '2023-05-27 11:12:01'),
(2877, 30, 3434, 25, 'وَمِنۡ ءَایَـٰتِهِۦۤ أَن تَقُومَ ٱلسَّمَاۤءُ وَٱلۡأَرۡضُ بِأَمۡرِهِۦۚ ثُمَّ إِذَا دَعَاكُمۡ دَعۡوَةࣰ مِّنَ ٱلۡأَرۡضِ إِذَاۤ أَنتُمۡ تَخۡرُجُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:01'),
(2878, 30, 3435, 26, 'وَلَهُۥ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ كُلࣱّ لَّهُۥ قَـٰنِتُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:01'),
(2879, 30, 3436, 27, 'وَهُوَ ٱلَّذِی یَبۡدَؤُا۟ ٱلۡخَلۡقَ ثُمَّ یُعِیدُهُۥ وَهُوَ أَهۡوَنُ عَلَیۡهِۚ وَلَهُ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:01'),
(2880, 30, 3437, 28, 'ضَرَبَ لَكُم مَّثَلࣰا مِّنۡ أَنفُسِكُمۡۖ هَل لَّكُم مِّن مَّا مَلَكَتۡ أَیۡمَـٰنُكُم مِّن شُرَكَاۤءَ فِی مَا رَزَقۡنَـٰكُمۡ فَأَنتُمۡ فِیهِ سَوَاۤءࣱ تَخَافُونَهُمۡ كَخِیفَتِكُمۡ أَنفُسَكُمۡۚ كَذَ ٰ⁠لِكَ نُفَصِّلُ ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:01'),
(2881, 30, 3438, 29, 'بَلِ ٱتَّبَعَ ٱلَّذِینَ ظَلَمُوۤا۟ أَهۡوَاۤءَهُم بِغَیۡرِ عِلۡمࣲۖ فَمَن یَهۡدِی مَنۡ أَضَلَّ ٱللَّهُۖ وَمَا لَهُم مِّن نَّـٰصِرِینَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2882, 30, 3439, 30, 'فَأَقِمۡ وَجۡهَكَ لِلدِّینِ حَنِیفࣰاۚ فِطۡرَتَ ٱللَّهِ ٱلَّتِی فَطَرَ ٱلنَّاسَ عَلَیۡهَاۚ لَا تَبۡدِیلَ لِخَلۡقِ ٱللَّهِۚ ذَ ٰ⁠لِكَ ٱلدِّینُ ٱلۡقَیِّمُ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2883, 30, 3440, 31, '۞ مُنِیبِینَ إِلَیۡهِ وَٱتَّقُوهُ وَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَلَا تَكُونُوا۟ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2884, 30, 3441, 32, 'مِنَ ٱلَّذِینَ فَرَّقُوا۟ دِینَهُمۡ وَكَانُوا۟ شِیَعࣰاۖ كُلُّ حِزۡبِۭ بِمَا لَدَیۡهِمۡ فَرِحُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2885, 30, 3442, 33, 'وَإِذَا مَسَّ ٱلنَّاسَ ضُرࣱّ دَعَوۡا۟ رَبَّهُم مُّنِیبِینَ إِلَیۡهِ ثُمَّ إِذَاۤ أَذَاقَهُم مِّنۡهُ رَحۡمَةً إِذَا فَرِیقࣱ مِّنۡهُم بِرَبِّهِمۡ یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2886, 30, 3443, 34, 'لِیَكۡفُرُوا۟ بِمَاۤ ءَاتَیۡنَـٰهُمۡۚ فَتَمَتَّعُوا۟ فَسَوۡفَ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2887, 30, 3444, 35, 'أَمۡ أَنزَلۡنَا عَلَیۡهِمۡ سُلۡطَـٰنࣰا فَهُوَ یَتَكَلَّمُ بِمَا كَانُوا۟ بِهِۦ یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2888, 30, 3445, 36, 'وَإِذَاۤ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةࣰ فَرِحُوا۟ بِهَاۖ وَإِن تُصِبۡهُمۡ سَیِّئَةُۢ بِمَا قَدَّمَتۡ أَیۡدِیهِمۡ إِذَا هُمۡ یَقۡنَطُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2889, 30, 3446, 37, 'أَوَلَمۡ یَرَوۡا۟ أَنَّ ٱللَّهَ یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ وَیَقۡدِرُۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2890, 30, 3447, 38, 'فَـَٔاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِینَ وَٱبۡنَ ٱلسَّبِیلِۚ ذَ ٰ⁠لِكَ خَیۡرࣱ لِّلَّذِینَ یُرِیدُونَ وَجۡهَ ٱللَّهِۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2891, 30, 3448, 39, 'وَمَاۤ ءَاتَیۡتُم مِّن رِّبࣰا لِّیَرۡبُوَا۟ فِیۤ أَمۡوَ ٰ⁠لِ ٱلنَّاسِ فَلَا یَرۡبُوا۟ عِندَ ٱللَّهِۖ وَمَاۤ ءَاتَیۡتُم مِّن زَكَوٰةࣲ تُرِیدُونَ وَجۡهَ ٱللَّهِ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُضۡعِفُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2892, 30, 3449, 40, 'ٱللَّهُ ٱلَّذِی خَلَقَكُمۡ ثُمَّ رَزَقَكُمۡ ثُمَّ یُمِیتُكُمۡ ثُمَّ یُحۡیِیكُمۡۖ هَلۡ مِن شُرَكَاۤىِٕكُم مَّن یَفۡعَلُ مِن ذَ ٰ⁠لِكُم مِّن شَیۡءࣲۚ سُبۡحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2893, 30, 3450, 41, 'ظَهَرَ ٱلۡفَسَادُ فِی ٱلۡبَرِّ وَٱلۡبَحۡرِ بِمَا كَسَبَتۡ أَیۡدِی ٱلنَّاسِ لِیُذِیقَهُم بَعۡضَ ٱلَّذِی عَمِلُوا۟ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2894, 30, 3451, 42, 'قُلۡ سِیرُوا۟ فِی ٱلۡأَرۡضِ فَٱنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلَّذِینَ مِن قَبۡلُۚ كَانَ أَكۡثَرُهُم مُّشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2895, 30, 3452, 43, 'فَأَقِمۡ وَجۡهَكَ لِلدِّینِ ٱلۡقَیِّمِ مِن قَبۡلِ أَن یَأۡتِیَ یَوۡمࣱ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۖ یَوۡمَىِٕذࣲ یَصَّدَّعُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2896, 30, 3453, 44, 'مَن كَفَرَ فَعَلَیۡهِ كُفۡرُهُۥۖ وَمَنۡ عَمِلَ صَـٰلِحࣰا فَلِأَنفُسِهِمۡ یَمۡهَدُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2897, 30, 3454, 45, 'لِیَجۡزِیَ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ مِن فَضۡلِهِۦۤۚ إِنَّهُۥ لَا یُحِبُّ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:02'),
(2898, 30, 3455, 46, 'وَمِنۡ ءَایَـٰتِهِۦۤ أَن یُرۡسِلَ ٱلرِّیَاحَ مُبَشِّرَ ٰ⁠تࣲ وَلِیُذِیقَكُم مِّن رَّحۡمَتِهِۦ وَلِتَجۡرِیَ ٱلۡفُلۡكُ بِأَمۡرِهِۦ وَلِتَبۡتَغُوا۟ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:03'),
(2899, 30, 3456, 47, 'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَاۤءُوهُم بِٱلۡبَیِّنَـٰتِ فَٱنتَقَمۡنَا مِنَ ٱلَّذِینَ أَجۡرَمُوا۟ۖ وَكَانَ حَقًّا عَلَیۡنَا نَصۡرُ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:26:52', '2023-05-27 11:12:03'),
(2900, 30, 3457, 48, 'ٱللَّهُ ٱلَّذِی یُرۡسِلُ ٱلرِّیَـٰحَ فَتُثِیرُ سَحَابࣰا فَیَبۡسُطُهُۥ فِی ٱلسَّمَاۤءِ كَیۡفَ یَشَاۤءُ وَیَجۡعَلُهُۥ كِسَفࣰا فَتَرَى ٱلۡوَدۡقَ یَخۡرُجُ مِنۡ خِلَـٰلِهِۦۖ فَإِذَاۤ أَصَابَ بِهِۦ مَن یَشَاۤءُ مِنۡ عِبَادِهِۦۤ إِذَا هُمۡ یَسۡتَبۡشِرُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2901, 30, 3458, 49, 'وَإِن كَانُوا۟ مِن قَبۡلِ أَن یُنَزَّلَ عَلَیۡهِم مِّن قَبۡلِهِۦ لَمُبۡلِسِینَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2902, 30, 3459, 50, 'فَٱنظُرۡ إِلَىٰۤ ءَاثَـٰرِ رَحۡمَتِ ٱللَّهِ كَیۡفَ یُحۡیِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۤۚ إِنَّ ذَ ٰ⁠لِكَ لَمُحۡیِ ٱلۡمَوۡتَىٰۖ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2903, 30, 3460, 51, 'وَلَىِٕنۡ أَرۡسَلۡنَا رِیحࣰا فَرَأَوۡهُ مُصۡفَرࣰّا لَّظَلُّوا۟ مِنۢ بَعۡدِهِۦ یَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2904, 30, 3461, 52, 'فَإِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَاۤءَ إِذَا وَلَّوۡا۟ مُدۡبِرِینَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2905, 30, 3462, 53, 'وَمَاۤ أَنتَ بِهَـٰدِ ٱلۡعُمۡیِ عَن ضَلَـٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن یُؤۡمِنُ بِـَٔایَـٰتِنَا فَهُم مُّسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2906, 30, 3463, 54, '۞ ٱللَّهُ ٱلَّذِی خَلَقَكُم مِّن ضَعۡفࣲ ثُمَّ جَعَلَ مِنۢ بَعۡدِ ضَعۡفࣲ قُوَّةࣰ ثُمَّ جَعَلَ مِنۢ بَعۡدِ قُوَّةࣲ ضَعۡفࣰا وَشَیۡبَةࣰۚ یَخۡلُقُ مَا یَشَاۤءُۚ وَهُوَ ٱلۡعَلِیمُ ٱلۡقَدِیرُ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2907, 30, 3464, 55, 'وَیَوۡمَ تَقُومُ ٱلسَّاعَةُ یُقۡسِمُ ٱلۡمُجۡرِمُونَ مَا لَبِثُوا۟ غَیۡرَ سَاعَةࣲۚ كَذَ ٰ⁠لِكَ كَانُوا۟ یُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2908, 30, 3465, 56, 'وَقَالَ ٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ وَٱلۡإِیمَـٰنَ لَقَدۡ لَبِثۡتُمۡ فِی كِتَـٰبِ ٱللَّهِ إِلَىٰ یَوۡمِ ٱلۡبَعۡثِۖ فَهَـٰذَا یَوۡمُ ٱلۡبَعۡثِ وَلَـٰكِنَّكُمۡ كُنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2909, 30, 3466, 57, 'فَیَوۡمَىِٕذࣲ لَّا یَنفَعُ ٱلَّذِینَ ظَلَمُوا۟ مَعۡذِرَتُهُمۡ وَلَا هُمۡ یُسۡتَعۡتَبُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2910, 30, 3467, 58, 'وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِی هَـٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلࣲۚ وَلَىِٕن جِئۡتَهُم بِـَٔایَةࣲ لَّیَقُولَنَّ ٱلَّذِینَ كَفَرُوۤا۟ إِنۡ أَنتُمۡ إِلَّا مُبۡطِلُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2911, 30, 3468, 59, 'كَذَ ٰ⁠لِكَ یَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلَّذِینَ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2912, 30, 3469, 60, 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّۖ وَلَا یَسۡتَخِفَّنَّكَ ٱلَّذِینَ لَا یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:26:53', '2023-05-27 11:12:03'),
(2913, 29, 3341, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2914, 29, 3342, 2, 'أَحَسِبَ ٱلنَّاسُ أَن یُتۡرَكُوۤا۟ أَن یَقُولُوۤا۟ ءَامَنَّا وَهُمۡ لَا یُفۡتَنُونَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2915, 29, 3343, 3, 'وَلَقَدۡ فَتَنَّا ٱلَّذِینَ مِن قَبۡلِهِمۡۖ فَلَیَعۡلَمَنَّ ٱللَّهُ ٱلَّذِینَ صَدَقُوا۟ وَلَیَعۡلَمَنَّ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2916, 29, 3344, 4, 'أَمۡ حَسِبَ ٱلَّذِینَ یَعۡمَلُونَ ٱلسَّیِّـَٔاتِ أَن یَسۡبِقُونَاۚ سَاۤءَ مَا یَحۡكُمُونَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2917, 29, 3345, 5, 'مَن كَانَ یَرۡجُوا۟ لِقَاۤءَ ٱللَّهِ فَإِنَّ أَجَلَ ٱللَّهِ لَـَٔاتࣲۚ وَهُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2918, 29, 3346, 6, 'وَمَن جَـٰهَدَ فَإِنَّمَا یُجَـٰهِدُ لِنَفۡسِهِۦۤۚ إِنَّ ٱللَّهَ لَغَنِیٌّ عَنِ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2919, 29, 3347, 7, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَنُكَفِّرَنَّ عَنۡهُمۡ سَیِّـَٔاتِهِمۡ وَلَنَجۡزِیَنَّهُمۡ أَحۡسَنَ ٱلَّذِی كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2920, 29, 3348, 8, 'وَوَصَّیۡنَا ٱلۡإِنسَـٰنَ بِوَ ٰ⁠لِدَیۡهِ حُسۡنࣰاۖ وَإِن جَـٰهَدَاكَ لِتُشۡرِكَ بِی مَا لَیۡسَ لَكَ بِهِۦ عِلۡمࣱ فَلَا تُطِعۡهُمَاۤۚ إِلَیَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2921, 29, 3349, 9, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَنُدۡخِلَنَّهُمۡ فِی ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2922, 29, 3350, 10, 'وَمِنَ ٱلنَّاسِ مَن یَقُولُ ءَامَنَّا بِٱللَّهِ فَإِذَاۤ أُوذِیَ فِی ٱللَّهِ جَعَلَ فِتۡنَةَ ٱلنَّاسِ كَعَذَابِ ٱللَّهِۖ وَلَىِٕن جَاۤءَ نَصۡرࣱ مِّن رَّبِّكَ لَیَقُولُنَّ إِنَّا كُنَّا مَعَكُمۡۚ أَوَلَیۡسَ ٱللَّهُ بِأَعۡلَمَ بِمَا فِی صُدُورِ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2923, 29, 3351, 11, 'وَلَیَعۡلَمَنَّ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ وَلَیَعۡلَمَنَّ ٱلۡمُنَـٰفِقِینَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2924, 29, 3352, 12, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لِلَّذِینَ ءَامَنُوا۟ ٱتَّبِعُوا۟ سَبِیلَنَا وَلۡنَحۡمِلۡ خَطَـٰیَـٰكُمۡ وَمَا هُم بِحَـٰمِلِینَ مِنۡ خَطَـٰیَـٰهُم مِّن شَیۡءٍۖ إِنَّهُمۡ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2925, 29, 3353, 13, 'وَلَیَحۡمِلُنَّ أَثۡقَالَهُمۡ وَأَثۡقَالࣰا مَّعَ أَثۡقَالِهِمۡۖ وَلَیُسۡـَٔلُنَّ یَوۡمَ ٱلۡقِیَـٰمَةِ عَمَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:27:05', '2023-05-27 11:12:11'),
(2926, 29, 3354, 14, 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَلَبِثَ فِیهِمۡ أَلۡفَ سَنَةٍ إِلَّا خَمۡسِینَ عَامࣰا فَأَخَذَهُمُ ٱلطُّوفَانُ وَهُمۡ ظَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:11'),
(2927, 29, 3355, 15, 'فَأَنجَیۡنَـٰهُ وَأَصۡحَـٰبَ ٱلسَّفِینَةِ وَجَعَلۡنَـٰهَاۤ ءَایَةࣰ لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:11'),
(2928, 29, 3356, 16, 'وَإِبۡرَ ٰ⁠هِیمَ إِذۡ قَالَ لِقَوۡمِهِ ٱعۡبُدُوا۟ ٱللَّهَ وَٱتَّقُوهُۖ ذَ ٰ⁠لِكُمۡ خَیۡرࣱ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:11'),
(2929, 29, 3357, 17, 'إِنَّمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ أَوۡثَـٰنࣰا وَتَخۡلُقُونَ إِفۡكًاۚ إِنَّ ٱلَّذِینَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ لَا یَمۡلِكُونَ لَكُمۡ رِزۡقࣰا فَٱبۡتَغُوا۟ عِندَ ٱللَّهِ ٱلرِّزۡقَ وَٱعۡبُدُوهُ وَٱشۡكُرُوا۟ لَهُۥۤۖ إِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:11'),
(2930, 29, 3358, 18, 'وَإِن تُكَذِّبُوا۟ فَقَدۡ كَذَّبَ أُمَمࣱ مِّن قَبۡلِكُمۡۖ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَـٰغُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:11'),
(2931, 29, 3359, 19, 'أَوَلَمۡ یَرَوۡا۟ كَیۡفَ یُبۡدِئُ ٱللَّهُ ٱلۡخَلۡقَ ثُمَّ یُعِیدُهُۥۤۚ إِنَّ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣱ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2932, 29, 3360, 20, 'قُلۡ سِیرُوا۟ فِی ٱلۡأَرۡضِ فَٱنظُرُوا۟ كَیۡفَ بَدَأَ ٱلۡخَلۡقَۚ ثُمَّ ٱللَّهُ یُنشِئُ ٱلنَّشۡأَةَ ٱلۡـَٔاخِرَةَۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2933, 29, 3361, 21, 'یُعَذِّبُ مَن یَشَاۤءُ وَیَرۡحَمُ مَن یَشَاۤءُۖ وَإِلَیۡهِ تُقۡلَبُونَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2934, 29, 3362, 22, 'وَمَاۤ أَنتُم بِمُعۡجِزِینَ فِی ٱلۡأَرۡضِ وَلَا فِی ٱلسَّمَاۤءِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِیࣲّ وَلَا نَصِیرࣲ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2935, 29, 3363, 23, 'وَٱلَّذِینَ كَفَرُوا۟ بِـَٔایَـٰتِ ٱللَّهِ وَلِقَاۤىِٕهِۦۤ أُو۟لَـٰۤىِٕكَ یَىِٕسُوا۟ مِن رَّحۡمَتِی وَأُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2936, 29, 3364, 24, 'فَمَا كَانَ جَوَابَ قَوۡمِهِۦۤ إِلَّاۤ أَن قَالُوا۟ ٱقۡتُلُوهُ أَوۡ حَرِّقُوهُ فَأَنجَىٰهُ ٱللَّهُ مِنَ ٱلنَّارِۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2937, 29, 3365, 25, 'وَقَالَ إِنَّمَا ٱتَّخَذۡتُم مِّن دُونِ ٱللَّهِ أَوۡثَـٰنࣰا مَّوَدَّةَ بَیۡنِكُمۡ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ ثُمَّ یَوۡمَ ٱلۡقِیَـٰمَةِ یَكۡفُرُ بَعۡضُكُم بِبَعۡضࣲ وَیَلۡعَنُ بَعۡضُكُم بَعۡضࣰا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2938, 29, 3366, 26, '۞ فَـَٔامَنَ لَهُۥ لُوطࣱۘ وَقَالَ إِنِّی مُهَاجِرٌ إِلَىٰ رَبِّیۤۖ إِنَّهُۥ هُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2939, 29, 3367, 27, 'وَوَهَبۡنَا لَهُۥۤ إِسۡحَـٰقَ وَیَعۡقُوبَ وَجَعَلۡنَا فِی ذُرِّیَّتِهِ ٱلنُّبُوَّةَ وَٱلۡكِتَـٰبَ وَءَاتَیۡنَـٰهُ أَجۡرَهُۥ فِی ٱلدُّنۡیَاۖ وَإِنَّهُۥ فِی ٱلۡـَٔاخِرَةِ لَمِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2940, 29, 3368, 28, 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦۤ إِنَّكُمۡ لَتَأۡتُونَ ٱلۡفَـٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدࣲ مِّنَ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2941, 29, 3369, 29, 'أَىِٕنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ وَتَقۡطَعُونَ ٱلسَّبِیلَ وَتَأۡتُونَ فِی نَادِیكُمُ ٱلۡمُنكَرَۖ فَمَا كَانَ جَوَابَ قَوۡمِهِۦۤ إِلَّاۤ أَن قَالُوا۟ ٱئۡتِنَا بِعَذَابِ ٱللَّهِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2942, 29, 3370, 30, 'قَالَ رَبِّ ٱنصُرۡنِی عَلَى ٱلۡقَوۡمِ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2943, 29, 3371, 31, 'وَلَمَّا جَاۤءَتۡ رُسُلُنَاۤ إِبۡرَ ٰ⁠هِیمَ بِٱلۡبُشۡرَىٰ قَالُوۤا۟ إِنَّا مُهۡلِكُوۤا۟ أَهۡلِ هَـٰذِهِ ٱلۡقَرۡیَةِۖ إِنَّ أَهۡلَهَا كَانُوا۟ ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2944, 29, 3372, 32, 'قَالَ إِنَّ فِیهَا لُوطࣰاۚ قَالُوا۟ نَحۡنُ أَعۡلَمُ بِمَن فِیهَاۖ لَنُنَجِّیَنَّهُۥ وَأَهۡلَهُۥۤ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:27:06', '2023-05-27 11:12:12'),
(2945, 29, 3373, 33, 'وَلَمَّاۤ أَن جَاۤءَتۡ رُسُلُنَا لُوطࣰا سِیۤءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعࣰاۖ وَقَالُوا۟ لَا تَخَفۡ وَلَا تَحۡزَنۡ إِنَّا مُنَجُّوكَ وَأَهۡلَكَ إِلَّا ٱمۡرَأَتَكَ كَانَتۡ مِنَ ٱلۡغَـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:12'),
(2946, 29, 3374, 34, 'إِنَّا مُنزِلُونَ عَلَىٰۤ أَهۡلِ هَـٰذِهِ ٱلۡقَرۡیَةِ رِجۡزࣰا مِّنَ ٱلسَّمَاۤءِ بِمَا كَانُوا۟ یَفۡسُقُونَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:12'),
(2947, 29, 3375, 35, 'وَلَقَد تَّرَكۡنَا مِنۡهَاۤ ءَایَةَۢ بَیِّنَةࣰ لِّقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:12'),
(2948, 29, 3376, 36, 'وَإِلَىٰ مَدۡیَنَ أَخَاهُمۡ شُعَیۡبࣰا فَقَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ وَٱرۡجُوا۟ ٱلۡیَوۡمَ ٱلۡـَٔاخِرَ وَلَا تَعۡثَوۡا۟ فِی ٱلۡأَرۡضِ مُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:12'),
(2949, 29, 3377, 37, 'فَكَذَّبُوهُ فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُوا۟ فِی دَارِهِمۡ جَـٰثِمِینَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2950, 29, 3378, 38, 'وَعَادࣰا وَثَمُودَا۟ وَقَد تَّبَیَّنَ لَكُم مِّن مَّسَـٰكِنِهِمۡۖ وَزَیَّنَ لَهُمُ ٱلشَّیۡطَـٰنُ أَعۡمَـٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِیلِ وَكَانُوا۟ مُسۡتَبۡصِرِینَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2951, 29, 3379, 39, 'وَقَـٰرُونَ وَفِرۡعَوۡنَ وَهَـٰمَـٰنَۖ وَلَقَدۡ جَاۤءَهُم مُّوسَىٰ بِٱلۡبَیِّنَـٰتِ فَٱسۡتَكۡبَرُوا۟ فِی ٱلۡأَرۡضِ وَمَا كَانُوا۟ سَـٰبِقِینَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2952, 29, 3380, 40, 'فَكُلًّا أَخَذۡنَا بِذَنۢبِهِۦۖ فَمِنۡهُم مَّنۡ أَرۡسَلۡنَا عَلَیۡهِ حَاصِبࣰا وَمِنۡهُم مَّنۡ أَخَذَتۡهُ ٱلصَّیۡحَةُ وَمِنۡهُم مَّنۡ خَسَفۡنَا بِهِ ٱلۡأَرۡضَ وَمِنۡهُم مَّنۡ أَغۡرَقۡنَاۚ وَمَا كَانَ ٱللَّهُ لِیَظۡلِمَهُمۡ وَلَـٰكِن كَانُوۤا۟ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2953, 29, 3381, 41, 'مَثَلُ ٱلَّذِینَ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ أَوۡلِیَاۤءَ كَمَثَلِ ٱلۡعَنكَبُوتِ ٱتَّخَذَتۡ بَیۡتࣰاۖ وَإِنَّ أَوۡهَنَ ٱلۡبُیُوتِ لَبَیۡتُ ٱلۡعَنكَبُوتِۚ لَوۡ كَانُوا۟ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2954, 29, 3382, 42, 'إِنَّ ٱللَّهَ یَعۡلَمُ مَا یَدۡعُونَ مِن دُونِهِۦ مِن شَیۡءࣲۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2955, 29, 3383, 43, 'وَتِلۡكَ ٱلۡأَمۡثَـٰلُ نَضۡرِبُهَا لِلنَّاسِۖ وَمَا یَعۡقِلُهَاۤ إِلَّا ٱلۡعَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2956, 29, 3384, 44, 'خَلَقَ ٱللَّهُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2957, 29, 3385, 45, 'ٱتۡلُ مَاۤ أُوحِیَ إِلَیۡكَ مِنَ ٱلۡكِتَـٰبِ وَأَقِمِ ٱلصَّلَوٰةَۖ إِنَّ ٱلصَّلَوٰةَ تَنۡهَىٰ عَنِ ٱلۡفَحۡشَاۤءِ وَٱلۡمُنكَرِۗ وَلَذِكۡرُ ٱللَّهِ أَكۡبَرُۗ وَٱللَّهُ یَعۡلَمُ مَا تَصۡنَعُونَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2958, 29, 3386, 46, '۞ وَلَا تُجَـٰدِلُوۤا۟ أَهۡلَ ٱلۡكِتَـٰبِ إِلَّا بِٱلَّتِی هِیَ أَحۡسَنُ إِلَّا ٱلَّذِینَ ظَلَمُوا۟ مِنۡهُمۡۖ وَقُولُوۤا۟ ءَامَنَّا بِٱلَّذِیۤ أُنزِلَ إِلَیۡنَا وَأُنزِلَ إِلَیۡكُمۡ وَإِلَـٰهُنَا وَإِلَـٰهُكُمۡ وَ ٰ⁠حِدࣱ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:27:07', '2023-05-27 11:12:13'),
(2959, 29, 3387, 47, 'وَكَذَ ٰ⁠لِكَ أَنزَلۡنَاۤ إِلَیۡكَ ٱلۡكِتَـٰبَۚ فَٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ یُؤۡمِنُونَ بِهِۦۖ وَمِنۡ هَـٰۤؤُلَاۤءِ مَن یُؤۡمِنُ بِهِۦۚ وَمَا یَجۡحَدُ بِـَٔایَـٰتِنَاۤ إِلَّا ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2960, 29, 3388, 48, 'وَمَا كُنتَ تَتۡلُوا۟ مِن قَبۡلِهِۦ مِن كِتَـٰبࣲ وَلَا تَخُطُّهُۥ بِیَمِینِكَۖ إِذࣰا لَّٱرۡتَابَ ٱلۡمُبۡطِلُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2961, 29, 3389, 49, 'بَلۡ هُوَ ءَایَـٰتُۢ بَیِّنَـٰتࣱ فِی صُدُورِ ٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَۚ وَمَا یَجۡحَدُ بِـَٔایَـٰتِنَاۤ إِلَّا ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2962, 29, 3390, 50, 'وَقَالُوا۟ لَوۡلَاۤ أُنزِلَ عَلَیۡهِ ءَایَـٰتࣱ مِّن رَّبِّهِۦۚ قُلۡ إِنَّمَا ٱلۡـَٔایَـٰتُ عِندَ ٱللَّهِ وَإِنَّمَاۤ أَنَا۠ نَذِیرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2963, 29, 3391, 51, 'أَوَلَمۡ یَكۡفِهِمۡ أَنَّاۤ أَنزَلۡنَا عَلَیۡكَ ٱلۡكِتَـٰبَ یُتۡلَىٰ عَلَیۡهِمۡۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَرَحۡمَةࣰ وَذِكۡرَىٰ لِقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2964, 29, 3392, 52, 'قُلۡ كَفَىٰ بِٱللَّهِ بَیۡنِی وَبَیۡنَكُمۡ شَهِیدࣰاۖ یَعۡلَمُ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۗ وَٱلَّذِینَ ءَامَنُوا۟ بِٱلۡبَـٰطِلِ وَكَفَرُوا۟ بِٱللَّهِ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2965, 29, 3393, 53, 'وَیَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَوۡلَاۤ أَجَلࣱ مُّسَمࣰّى لَّجَاۤءَهُمُ ٱلۡعَذَابُۚ وَلَیَأۡتِیَنَّهُم بَغۡتَةࣰ وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2966, 29, 3394, 54, 'یَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِیطَةُۢ بِٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2967, 29, 3395, 55, 'یَوۡمَ یَغۡشَىٰهُمُ ٱلۡعَذَابُ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۡ وَیَقُولُ ذُوقُوا۟ مَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:13'),
(2968, 29, 3396, 56, 'یَـٰعِبَادِیَ ٱلَّذِینَ ءَامَنُوۤا۟ إِنَّ أَرۡضِی وَ ٰ⁠سِعَةࣱ فَإِیَّـٰیَ فَٱعۡبُدُونِ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2969, 29, 3397, 57, 'كُلُّ نَفۡسࣲ ذَاۤىِٕقَةُ ٱلۡمَوۡتِۖ ثُمَّ إِلَیۡنَا تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2970, 29, 3398, 58, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَنُبَوِّئَنَّهُم مِّنَ ٱلۡجَنَّةِ غُرَفࣰا تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ نِعۡمَ أَجۡرُ ٱلۡعَـٰمِلِینَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2971, 29, 3399, 59, 'ٱلَّذِینَ صَبَرُوا۟ وَعَلَىٰ رَبِّهِمۡ یَتَوَكَّلُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2972, 29, 3400, 60, 'وَكَأَیِّن مِّن دَاۤبَّةࣲ لَّا تَحۡمِلُ رِزۡقَهَا ٱللَّهُ یَرۡزُقُهَا وَإِیَّاكُمۡۚ وَهُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2973, 29, 3401, 61, 'وَلَىِٕن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَ لَیَقُولُنَّ ٱللَّهُۖ فَأَنَّىٰ یُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2974, 29, 3402, 62, 'ٱللَّهُ یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ مِنۡ عِبَادِهِۦ وَیَقۡدِرُ لَهُۥۤۚ إِنَّ ٱللَّهَ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2975, 29, 3403, 63, 'وَلَىِٕن سَأَلۡتَهُم مَّن نَّزَّلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَحۡیَا بِهِ ٱلۡأَرۡضَ مِنۢ بَعۡدِ مَوۡتِهَا لَیَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2976, 29, 3404, 64, 'وَمَا هَـٰذِهِ ٱلۡحَیَوٰةُ ٱلدُّنۡیَاۤ إِلَّا لَهۡوࣱ وَلَعِبࣱۚ وَإِنَّ ٱلدَّارَ ٱلۡـَٔاخِرَةَ لَهِیَ ٱلۡحَیَوَانُۚ لَوۡ كَانُوا۟ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2977, 29, 3405, 65, 'فَإِذَا رَكِبُوا۟ فِی ٱلۡفُلۡكِ دَعَوُا۟ ٱللَّهَ مُخۡلِصِینَ لَهُ ٱلدِّینَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ إِذَا هُمۡ یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2978, 29, 3406, 66, 'لِیَكۡفُرُوا۟ بِمَاۤ ءَاتَیۡنَـٰهُمۡ وَلِیَتَمَتَّعُوا۟ۚ فَسَوۡفَ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:08', '2023-05-27 11:12:14'),
(2979, 29, 3407, 67, 'أَوَلَمۡ یَرَوۡا۟ أَنَّا جَعَلۡنَا حَرَمًا ءَامِنࣰا وَیُتَخَطَّفُ ٱلنَّاسُ مِنۡ حَوۡلِهِمۡۚ أَفَبِٱلۡبَـٰطِلِ یُؤۡمِنُونَ وَبِنِعۡمَةِ ٱللَّهِ یَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:27:09', '2023-05-27 11:12:14'),
(2980, 29, 3408, 68, 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِٱلۡحَقِّ لَمَّا جَاۤءَهُۥۤۚ أَلَیۡسَ فِی جَهَنَّمَ مَثۡوࣰى لِّلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:27:09', '2023-05-27 11:12:14'),
(2981, 29, 3409, 69, 'وَٱلَّذِینَ جَـٰهَدُوا۟ فِینَا لَنَهۡدِیَنَّهُمۡ سُبُلَنَاۚ وَإِنَّ ٱللَّهَ لَمَعَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:27:09', '2023-05-27 11:12:14'),
(2982, 28, 3253, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ طسۤمۤ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:18'),
(2983, 28, 3254, 2, 'تِلۡكَ ءَایَـٰتُ ٱلۡكِتَـٰبِ ٱلۡمُبِینِ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:18'),
(2984, 28, 3255, 3, 'نَتۡلُوا۟ عَلَیۡكَ مِن نَّبَإِ مُوسَىٰ وَفِرۡعَوۡنَ بِٱلۡحَقِّ لِقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:18'),
(2985, 28, 3256, 4, 'إِنَّ فِرۡعَوۡنَ عَلَا فِی ٱلۡأَرۡضِ وَجَعَلَ أَهۡلَهَا شِیَعࣰا یَسۡتَضۡعِفُ طَاۤىِٕفَةࣰ مِّنۡهُمۡ یُذَبِّحُ أَبۡنَاۤءَهُمۡ وَیَسۡتَحۡیِۦ نِسَاۤءَهُمۡۚ إِنَّهُۥ كَانَ مِنَ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:19'),
(2986, 28, 3257, 5, 'وَنُرِیدُ أَن نَّمُنَّ عَلَى ٱلَّذِینَ ٱسۡتُضۡعِفُوا۟ فِی ٱلۡأَرۡضِ وَنَجۡعَلَهُمۡ أَىِٕمَّةࣰ وَنَجۡعَلَهُمُ ٱلۡوَ ٰ⁠رِثِینَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:19'),
(2987, 28, 3258, 6, 'وَنُمَكِّنَ لَهُمۡ فِی ٱلۡأَرۡضِ وَنُرِیَ فِرۡعَوۡنَ وَهَـٰمَـٰنَ وَجُنُودَهُمَا مِنۡهُم مَّا كَانُوا۟ یَحۡذَرُونَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:19'),
(2988, 28, 3259, 7, 'وَأَوۡحَیۡنَاۤ إِلَىٰۤ أُمِّ مُوسَىٰۤ أَنۡ أَرۡضِعِیهِۖ فَإِذَا خِفۡتِ عَلَیۡهِ فَأَلۡقِیهِ فِی ٱلۡیَمِّ وَلَا تَخَافِی وَلَا تَحۡزَنِیۤۖ إِنَّا رَاۤدُّوهُ إِلَیۡكِ وَجَاعِلُوهُ مِنَ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:19'),
(2989, 28, 3260, 8, 'فَٱلۡتَقَطَهُۥۤ ءَالُ فِرۡعَوۡنَ لِیَكُونَ لَهُمۡ عَدُوࣰّا وَحَزَنًاۗ إِنَّ فِرۡعَوۡنَ وَهَـٰمَـٰنَ وَجُنُودَهُمَا كَانُوا۟ خَـٰطِـِٔینَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:19'),
(2990, 28, 3261, 9, 'وَقَالَتِ ٱمۡرَأَتُ فِرۡعَوۡنَ قُرَّتُ عَیۡنࣲ لِّی وَلَكَۖ لَا تَقۡتُلُوهُ عَسَىٰۤ أَن یَنفَعَنَاۤ أَوۡ نَتَّخِذَهُۥ وَلَدࣰا وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:19'),
(2991, 28, 3262, 10, 'وَأَصۡبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَـٰرِغًاۖ إِن كَادَتۡ لَتُبۡدِی بِهِۦ لَوۡلَاۤ أَن رَّبَطۡنَا عَلَىٰ قَلۡبِهَا لِتَكُونَ مِنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:15', '2023-05-27 11:12:19'),
(2992, 28, 3263, 11, 'وَقَالَتۡ لِأُخۡتِهِۦ قُصِّیهِۖ فَبَصُرَتۡ بِهِۦ عَن جُنُبࣲ وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(2993, 28, 3264, 12, '۞ وَحَرَّمۡنَا عَلَیۡهِ ٱلۡمَرَاضِعَ مِن قَبۡلُ فَقَالَتۡ هَلۡ أَدُلُّكُمۡ عَلَىٰۤ أَهۡلِ بَیۡتࣲ یَكۡفُلُونَهُۥ لَكُمۡ وَهُمۡ لَهُۥ نَـٰصِحُونَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(2994, 28, 3265, 13, 'فَرَدَدۡنَـٰهُ إِلَىٰۤ أُمِّهِۦ كَیۡ تَقَرَّ عَیۡنُهَا وَلَا تَحۡزَنَ وَلِتَعۡلَمَ أَنَّ وَعۡدَ ٱللَّهِ حَقࣱّ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(2995, 28, 3266, 14, 'وَلَمَّا بَلَغَ أَشُدَّهُۥ وَٱسۡتَوَىٰۤ ءَاتَیۡنَـٰهُ حُكۡمࣰا وَعِلۡمࣰاۚ وَكَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(2996, 28, 3267, 15, 'وَدَخَلَ ٱلۡمَدِینَةَ عَلَىٰ حِینِ غَفۡلَةࣲ مِّنۡ أَهۡلِهَا فَوَجَدَ فِیهَا رَجُلَیۡنِ یَقۡتَتِلَانِ هَـٰذَا مِن شِیعَتِهِۦ وَهَـٰذَا مِنۡ عَدُوِّهِۦۖ فَٱسۡتَغَـٰثَهُ ٱلَّذِی مِن شِیعَتِهِۦ عَلَى ٱلَّذِی مِنۡ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسَىٰ فَقَضَىٰ عَلَیۡهِۖ قَالَ هَـٰذَا مِنۡ عَمَلِ ٱلشَّیۡطَـٰنِۖ إِنَّهُۥ عَدُوࣱّ مُّضِلࣱّ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(2997, 28, 3268, 16, 'قَالَ رَبِّ إِنِّی ظَلَمۡتُ نَفۡسِی فَٱغۡفِرۡ لِی فَغَفَرَ لَهُۥۤۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(2998, 28, 3269, 17, 'قَالَ رَبِّ بِمَاۤ أَنۡعَمۡتَ عَلَیَّ فَلَنۡ أَكُونَ ظَهِیرࣰا لِّلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(2999, 28, 3270, 18, 'فَأَصۡبَحَ فِی ٱلۡمَدِینَةِ خَاۤىِٕفࣰا یَتَرَقَّبُ فَإِذَا ٱلَّذِی ٱسۡتَنصَرَهُۥ بِٱلۡأَمۡسِ یَسۡتَصۡرِخُهُۥۚ قَالَ لَهُۥ مُوسَىٰۤ إِنَّكَ لَغَوِیࣱّ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(3000, 28, 3271, 19, 'فَلَمَّاۤ أَنۡ أَرَادَ أَن یَبۡطِشَ بِٱلَّذِی هُوَ عَدُوࣱّ لَّهُمَا قَالَ یَـٰمُوسَىٰۤ أَتُرِیدُ أَن تَقۡتُلَنِی كَمَا قَتَلۡتَ نَفۡسَۢا بِٱلۡأَمۡسِۖ إِن تُرِیدُ إِلَّاۤ أَن تَكُونَ جَبَّارࣰا فِی ٱلۡأَرۡضِ وَمَا تُرِیدُ أَن تَكُونَ مِنَ ٱلۡمُصۡلِحِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(3001, 28, 3272, 20, 'وَجَاۤءَ رَجُلࣱ مِّنۡ أَقۡصَا ٱلۡمَدِینَةِ یَسۡعَىٰ قَالَ یَـٰمُوسَىٰۤ إِنَّ ٱلۡمَلَأَ یَأۡتَمِرُونَ بِكَ لِیَقۡتُلُوكَ فَٱخۡرُجۡ إِنِّی لَكَ مِنَ ٱلنَّـٰصِحِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(3002, 28, 3273, 21, 'فَخَرَجَ مِنۡهَا خَاۤىِٕفࣰا یَتَرَقَّبُۖ قَالَ رَبِّ نَجِّنِی مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(3003, 28, 3274, 22, 'وَلَمَّا تَوَجَّهَ تِلۡقَاۤءَ مَدۡیَنَ قَالَ عَسَىٰ رَبِّیۤ أَن یَهۡدِیَنِی سَوَاۤءَ ٱلسَّبِیلِ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:19'),
(3004, 28, 3275, 23, 'وَلَمَّا وَرَدَ مَاۤءَ مَدۡیَنَ وَجَدَ عَلَیۡهِ أُمَّةࣰ مِّنَ ٱلنَّاسِ یَسۡقُونَ وَوَجَدَ مِن دُونِهِمُ ٱمۡرَأَتَیۡنِ تَذُودَانِۖ قَالَ مَا خَطۡبُكُمَاۖ قَالَتَا لَا نَسۡقِی حَتَّىٰ یُصۡدِرَ ٱلرِّعَاۤءُۖ وَأَبُونَا شَیۡخࣱ كَبِیرࣱ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3005, 28, 3276, 24, 'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰۤ إِلَى ٱلظِّلِّ فَقَالَ رَبِّ إِنِّی لِمَاۤ أَنزَلۡتَ إِلَیَّ مِنۡ خَیۡرࣲ فَقِیرࣱ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3006, 28, 3277, 25, 'فَجَاۤءَتۡهُ إِحۡدَىٰهُمَا تَمۡشِی عَلَى ٱسۡتِحۡیَاۤءࣲ قَالَتۡ إِنَّ أَبِی یَدۡعُوكَ لِیَجۡزِیَكَ أَجۡرَ مَا سَقَیۡتَ لَنَاۚ فَلَمَّا جَاۤءَهُۥ وَقَصَّ عَلَیۡهِ ٱلۡقَصَصَ قَالَ لَا تَخَفۡۖ نَجَوۡتَ مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3007, 28, 3278, 26, 'قَالَتۡ إِحۡدَىٰهُمَا یَـٰۤأَبَتِ ٱسۡتَـٔۡجِرۡهُۖ إِنَّ خَیۡرَ مَنِ ٱسۡتَـٔۡجَرۡتَ ٱلۡقَوِیُّ ٱلۡأَمِینُ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3008, 28, 3279, 27, 'قَالَ إِنِّیۤ أُرِیدُ أَنۡ أُنكِحَكَ إِحۡدَى ٱبۡنَتَیَّ هَـٰتَیۡنِ عَلَىٰۤ أَن تَأۡجُرَنِی ثَمَـٰنِیَ حِجَجࣲۖ فَإِنۡ أَتۡمَمۡتَ عَشۡرࣰا فَمِنۡ عِندِكَۖ وَمَاۤ أُرِیدُ أَنۡ أَشُقَّ عَلَیۡكَۚ سَتَجِدُنِیۤ إِن شَاۤءَ ٱللَّهُ مِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3009, 28, 3280, 28, 'قَالَ ذَ ٰ⁠لِكَ بَیۡنِی وَبَیۡنَكَۖ أَیَّمَا ٱلۡأَجَلَیۡنِ قَضَیۡتُ فَلَا عُدۡوَ ٰ⁠نَ عَلَیَّۖ وَٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِیلࣱ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3010, 28, 3281, 29, '۞ فَلَمَّا قَضَىٰ مُوسَى ٱلۡأَجَلَ وَسَارَ بِأَهۡلِهِۦۤ ءَانَسَ مِن جَانِبِ ٱلطُّورِ نَارࣰاۖ قَالَ لِأَهۡلِهِ ٱمۡكُثُوۤا۟ إِنِّیۤ ءَانَسۡتُ نَارࣰا لَّعَلِّیۤ ءَاتِیكُم مِّنۡهَا بِخَبَرٍ أَوۡ جَذۡوَةࣲ مِّنَ ٱلنَّارِ لَعَلَّكُمۡ تَصۡطَلُونَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3011, 28, 3282, 30, 'فَلَمَّاۤ أَتَىٰهَا نُودِیَ مِن شَـٰطِىِٕ ٱلۡوَادِ ٱلۡأَیۡمَنِ فِی ٱلۡبُقۡعَةِ ٱلۡمُبَـٰرَكَةِ مِنَ ٱلشَّجَرَةِ أَن یَـٰمُوسَىٰۤ إِنِّیۤ أَنَا ٱللَّهُ رَبُّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3012, 28, 3283, 31, 'وَأَنۡ أَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَاۤنࣱّ وَلَّىٰ مُدۡبِرࣰا وَلَمۡ یُعَقِّبۡۚ یَـٰمُوسَىٰۤ أَقۡبِلۡ وَلَا تَخَفۡۖ إِنَّكَ مِنَ ٱلۡـَٔامِنِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3013, 28, 3284, 32, 'ٱسۡلُكۡ یَدَكَ فِی جَیۡبِكَ تَخۡرُجۡ بَیۡضَاۤءَ مِنۡ غَیۡرِ سُوۤءࣲ وَٱضۡمُمۡ إِلَیۡكَ جَنَاحَكَ مِنَ ٱلرَّهۡبِۖ فَذَ ٰ⁠نِكَ بُرۡهَـٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِی۟هِۦۤۚ إِنَّهُمۡ كَانُوا۟ قَوۡمࣰا فَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3014, 28, 3285, 33, 'قَالَ رَبِّ إِنِّی قَتَلۡتُ مِنۡهُمۡ نَفۡسࣰا فَأَخَافُ أَن یَقۡتُلُونِ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3015, 28, 3286, 34, 'وَأَخِی هَـٰرُونُ هُوَ أَفۡصَحُ مِنِّی لِسَانࣰا فَأَرۡسِلۡهُ مَعِیَ رِدۡءࣰا یُصَدِّقُنِیۤۖ إِنِّیۤ أَخَافُ أَن یُكَذِّبُونِ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3016, 28, 3287, 35, 'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِیكَ وَنَجۡعَلُ لَكُمَا سُلۡطَـٰنࣰا فَلَا یَصِلُونَ إِلَیۡكُمَا بِـَٔایَـٰتِنَاۤۚ أَنتُمَا وَمَنِ ٱتَّبَعَكُمَا ٱلۡغَـٰلِبُونَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3017, 28, 3288, 36, 'فَلَمَّا جَاۤءَهُم مُّوسَىٰ بِـَٔایَـٰتِنَا بَیِّنَـٰتࣲ قَالُوا۟ مَا هَـٰذَاۤ إِلَّا سِحۡرࣱ مُّفۡتَرࣰى وَمَا سَمِعۡنَا بِهَـٰذَا فِیۤ ءَابَاۤىِٕنَا ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:27:16', '2023-05-27 11:12:20'),
(3018, 28, 3289, 37, 'وَقَالَ مُوسَىٰ رَبِّیۤ أَعۡلَمُ بِمَن جَاۤءَ بِٱلۡهُدَىٰ مِنۡ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَـٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا یُفۡلِحُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:20'),
(3019, 28, 3290, 38, 'وَقَالَ فِرۡعَوۡنُ یَـٰۤأَیُّهَا ٱلۡمَلَأُ مَا عَلِمۡتُ لَكُم مِّنۡ إِلَـٰهٍ غَیۡرِی فَأَوۡقِدۡ لِی یَـٰهَـٰمَـٰنُ عَلَى ٱلطِّینِ فَٱجۡعَل لِّی صَرۡحࣰا لَّعَلِّیۤ أَطَّلِعُ إِلَىٰۤ إِلَـٰهِ مُوسَىٰ وَإِنِّی لَأَظُنُّهُۥ مِنَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:20'),
(3020, 28, 3291, 39, 'وَٱسۡتَكۡبَرَ هُوَ وَجُنُودُهُۥ فِی ٱلۡأَرۡضِ بِغَیۡرِ ٱلۡحَقِّ وَظَنُّوۤا۟ أَنَّهُمۡ إِلَیۡنَا لَا یُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:20'),
(3021, 28, 3292, 40, 'فَأَخَذۡنَـٰهُ وَجُنُودَهُۥ فَنَبَذۡنَـٰهُمۡ فِی ٱلۡیَمِّۖ فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:20'),
(3022, 28, 3293, 41, 'وَجَعَلۡنَـٰهُمۡ أَىِٕمَّةࣰ یَدۡعُونَ إِلَى ٱلنَّارِۖ وَیَوۡمَ ٱلۡقِیَـٰمَةِ لَا یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3023, 28, 3294, 42, 'وَأَتۡبَعۡنَـٰهُمۡ فِی هَـٰذِهِ ٱلدُّنۡیَا لَعۡنَةࣰۖ وَیَوۡمَ ٱلۡقِیَـٰمَةِ هُم مِّنَ ٱلۡمَقۡبُوحِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3024, 28, 3295, 43, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ مِنۢ بَعۡدِ مَاۤ أَهۡلَكۡنَا ٱلۡقُرُونَ ٱلۡأُولَىٰ بَصَاۤىِٕرَ لِلنَّاسِ وَهُدࣰى وَرَحۡمَةࣰ لَّعَلَّهُمۡ یَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3025, 28, 3296, 44, 'وَمَا كُنتَ بِجَانِبِ ٱلۡغَرۡبِیِّ إِذۡ قَضَیۡنَاۤ إِلَىٰ مُوسَى ٱلۡأَمۡرَ وَمَا كُنتَ مِنَ ٱلشَّـٰهِدِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3026, 28, 3297, 45, 'وَلَـٰكِنَّاۤ أَنشَأۡنَا قُرُونࣰا فَتَطَاوَلَ عَلَیۡهِمُ ٱلۡعُمُرُۚ وَمَا كُنتَ ثَاوِیࣰا فِیۤ أَهۡلِ مَدۡیَنَ تَتۡلُوا۟ عَلَیۡهِمۡ ءَایَـٰتِنَا وَلَـٰكِنَّا كُنَّا مُرۡسِلِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3027, 28, 3298, 46, 'وَمَا كُنتَ بِجَانِبِ ٱلطُّورِ إِذۡ نَادَیۡنَا وَلَـٰكِن رَّحۡمَةࣰ مِّن رَّبِّكَ لِتُنذِرَ قَوۡمࣰا مَّاۤ أَتَىٰهُم مِّن نَّذِیرࣲ مِّن قَبۡلِكَ لَعَلَّهُمۡ یَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3028, 28, 3299, 47, 'وَلَوۡلَاۤ أَن تُصِیبَهُم مُّصِیبَةُۢ بِمَا قَدَّمَتۡ أَیۡدِیهِمۡ فَیَقُولُوا۟ رَبَّنَا لَوۡلَاۤ أَرۡسَلۡتَ إِلَیۡنَا رَسُولࣰا فَنَتَّبِعَ ءَایَـٰتِكَ وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3029, 28, 3300, 48, 'فَلَمَّا جَاۤءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُوا۟ لَوۡلَاۤ أُوتِیَ مِثۡلَ مَاۤ أُوتِیَ مُوسَىٰۤۚ أَوَلَمۡ یَكۡفُرُوا۟ بِمَاۤ أُوتِیَ مُوسَىٰ مِن قَبۡلُۖ قَالُوا۟ سِحۡرَانِ تَظَـٰهَرَا وَقَالُوۤا۟ إِنَّا بِكُلࣲّ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3030, 28, 3301, 49, 'قُلۡ فَأۡتُوا۟ بِكِتَـٰبࣲ مِّنۡ عِندِ ٱللَّهِ هُوَ أَهۡدَىٰ مِنۡهُمَاۤ أَتَّبِعۡهُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3031, 28, 3302, 50, 'فَإِن لَّمۡ یَسۡتَجِیبُوا۟ لَكَ فَٱعۡلَمۡ أَنَّمَا یَتَّبِعُونَ أَهۡوَاۤءَهُمۡۚ وَمَنۡ أَضَلُّ مِمَّنِ ٱتَّبَعَ هَوَىٰهُ بِغَیۡرِ هُدࣰى مِّنَ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3032, 28, 3303, 51, '۞ وَلَقَدۡ وَصَّلۡنَا لَهُمُ ٱلۡقَوۡلَ لَعَلَّهُمۡ یَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3033, 28, 3304, 52, 'ٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ مِن قَبۡلِهِۦ هُم بِهِۦ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3034, 28, 3305, 53, 'وَإِذَا یُتۡلَىٰ عَلَیۡهِمۡ قَالُوۤا۟ ءَامَنَّا بِهِۦۤ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّنَاۤ إِنَّا كُنَّا مِن قَبۡلِهِۦ مُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3035, 28, 3306, 54, 'أُو۟لَـٰۤىِٕكَ یُؤۡتَوۡنَ أَجۡرَهُم مَّرَّتَیۡنِ بِمَا صَبَرُوا۟ وَیَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّیِّئَةَ وَمِمَّا رَزَقۡنَـٰهُمۡ یُنفِقُونَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3036, 28, 3307, 55, 'وَإِذَا سَمِعُوا۟ ٱللَّغۡوَ أَعۡرَضُوا۟ عَنۡهُ وَقَالُوا۟ لَنَاۤ أَعۡمَـٰلُنَا وَلَكُمۡ أَعۡمَـٰلُكُمۡ سَلَـٰمٌ عَلَیۡكُمۡ لَا نَبۡتَغِی ٱلۡجَـٰهِلِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3037, 28, 3308, 56, 'إِنَّكَ لَا تَهۡدِی مَنۡ أَحۡبَبۡتَ وَلَـٰكِنَّ ٱللَّهَ یَهۡدِی مَن یَشَاۤءُۚ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِینَ\n', NULL, 0, '2023-05-26 17:27:17', '2023-05-27 11:12:21'),
(3038, 28, 3309, 57, 'وَقَالُوۤا۟ إِن نَّتَّبِعِ ٱلۡهُدَىٰ مَعَكَ نُتَخَطَّفۡ مِنۡ أَرۡضِنَاۤۚ أَوَلَمۡ نُمَكِّن لَّهُمۡ حَرَمًا ءَامِنࣰا یُجۡبَىٰۤ إِلَیۡهِ ثَمَرَ ٰ⁠تُ كُلِّ شَیۡءࣲ رِّزۡقࣰا مِّن لَّدُنَّا وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:21'),
(3039, 28, 3310, 58, 'وَكَمۡ أَهۡلَكۡنَا مِن قَرۡیَةِۭ بَطِرَتۡ مَعِیشَتَهَاۖ فَتِلۡكَ مَسَـٰكِنُهُمۡ لَمۡ تُسۡكَن مِّنۢ بَعۡدِهِمۡ إِلَّا قَلِیلࣰاۖ وَكُنَّا نَحۡنُ ٱلۡوَ ٰ⁠رِثِینَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:21'),
(3040, 28, 3311, 59, 'وَمَا كَانَ رَبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ حَتَّىٰ یَبۡعَثَ فِیۤ أُمِّهَا رَسُولࣰا یَتۡلُوا۟ عَلَیۡهِمۡ ءَایَـٰتِنَاۚ وَمَا كُنَّا مُهۡلِكِی ٱلۡقُرَىٰۤ إِلَّا وَأَهۡلُهَا ظَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:21'),
(3041, 28, 3312, 60, 'وَمَاۤ أُوتِیتُم مِّن شَیۡءࣲ فَمَتَـٰعُ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَزِینَتُهَاۚ وَمَا عِندَ ٱللَّهِ خَیۡرࣱ وَأَبۡقَىٰۤۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:21'),
(3042, 28, 3313, 61, 'أَفَمَن وَعَدۡنَـٰهُ وَعۡدًا حَسَنࣰا فَهُوَ لَـٰقِیهِ كَمَن مَّتَّعۡنَـٰهُ مَتَـٰعَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا ثُمَّ هُوَ یَوۡمَ ٱلۡقِیَـٰمَةِ مِنَ ٱلۡمُحۡضَرِینَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3043, 28, 3314, 62, 'وَیَوۡمَ یُنَادِیهِمۡ فَیَقُولُ أَیۡنَ شُرَكَاۤءِیَ ٱلَّذِینَ كُنتُمۡ تَزۡعُمُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3044, 28, 3315, 63, 'قَالَ ٱلَّذِینَ حَقَّ عَلَیۡهِمُ ٱلۡقَوۡلُ رَبَّنَا هَـٰۤؤُلَاۤءِ ٱلَّذِینَ أَغۡوَیۡنَاۤ أَغۡوَیۡنَـٰهُمۡ كَمَا غَوَیۡنَاۖ تَبَرَّأۡنَاۤ إِلَیۡكَۖ مَا كَانُوۤا۟ إِیَّانَا یَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3045, 28, 3316, 64, 'وَقِیلَ ٱدۡعُوا۟ شُرَكَاۤءَكُمۡ فَدَعَوۡهُمۡ فَلَمۡ یَسۡتَجِیبُوا۟ لَهُمۡ وَرَأَوُا۟ ٱلۡعَذَابَۚ لَوۡ أَنَّهُمۡ كَانُوا۟ یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3046, 28, 3317, 65, 'وَیَوۡمَ یُنَادِیهِمۡ فَیَقُولُ مَاذَاۤ أَجَبۡتُمُ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3047, 28, 3318, 66, 'فَعَمِیَتۡ عَلَیۡهِمُ ٱلۡأَنۢبَاۤءُ یَوۡمَىِٕذࣲ فَهُمۡ لَا یَتَسَاۤءَلُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3048, 28, 3319, 67, 'فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَـٰلِحࣰا فَعَسَىٰۤ أَن یَكُونَ مِنَ ٱلۡمُفۡلِحِینَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3049, 28, 3320, 68, 'وَرَبُّكَ یَخۡلُقُ مَا یَشَاۤءُ وَیَخۡتَارُۗ مَا كَانَ لَهُمُ ٱلۡخِیَرَةُۚ سُبۡحَـٰنَ ٱللَّهِ وَتَعَـٰلَىٰ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3050, 28, 3321, 69, 'وَرَبُّكَ یَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا یُعۡلِنُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3051, 28, 3322, 70, 'وَهُوَ ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ لَهُ ٱلۡحَمۡدُ فِی ٱلۡأُولَىٰ وَٱلۡـَٔاخِرَةِۖ وَلَهُ ٱلۡحُكۡمُ وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3052, 28, 3323, 71, 'قُلۡ أَرَءَیۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَیۡكُمُ ٱلَّیۡلَ سَرۡمَدًا إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ مَنۡ إِلَـٰهٌ غَیۡرُ ٱللَّهِ یَأۡتِیكُم بِضِیَاۤءٍۚ أَفَلَا تَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3053, 28, 3324, 72, 'قُلۡ أَرَءَیۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَیۡكُمُ ٱلنَّهَارَ سَرۡمَدًا إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ مَنۡ إِلَـٰهٌ غَیۡرُ ٱللَّهِ یَأۡتِیكُم بِلَیۡلࣲ تَسۡكُنُونَ فِیهِۚ أَفَلَا تُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:27:18', '2023-05-27 11:12:22'),
(3054, 28, 3325, 73, 'وَمِن رَّحۡمَتِهِۦ جَعَلَ لَكُمُ ٱلَّیۡلَ وَٱلنَّهَارَ لِتَسۡكُنُوا۟ فِیهِ وَلِتَبۡتَغُوا۟ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:22'),
(3055, 28, 3326, 74, 'وَیَوۡمَ یُنَادِیهِمۡ فَیَقُولُ أَیۡنَ شُرَكَاۤءِیَ ٱلَّذِینَ كُنتُمۡ تَزۡعُمُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:22'),
(3056, 28, 3327, 75, 'وَنَزَعۡنَا مِن كُلِّ أُمَّةࣲ شَهِیدࣰا فَقُلۡنَا هَاتُوا۟ بُرۡهَـٰنَكُمۡ فَعَلِمُوۤا۟ أَنَّ ٱلۡحَقَّ لِلَّهِ وَضَلَّ عَنۡهُم مَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:22'),
(3057, 28, 3328, 76, '۞ إِنَّ قَـٰرُونَ كَانَ مِن قَوۡمِ مُوسَىٰ فَبَغَىٰ عَلَیۡهِمۡۖ وَءَاتَیۡنَـٰهُ مِنَ ٱلۡكُنُوزِ مَاۤ إِنَّ مَفَاتِحَهُۥ لَتَنُوۤأُ بِٱلۡعُصۡبَةِ أُو۟لِی ٱلۡقُوَّةِ إِذۡ قَالَ لَهُۥ قَوۡمُهُۥ لَا تَفۡرَحۡۖ إِنَّ ٱللَّهَ لَا یُحِبُّ ٱلۡفَرِحِینَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:22'),
(3058, 28, 3329, 77, 'وَٱبۡتَغِ فِیمَاۤ ءَاتَىٰكَ ٱللَّهُ ٱلدَّارَ ٱلۡـَٔاخِرَةَۖ وَلَا تَنسَ نَصِیبَكَ مِنَ ٱلدُّنۡیَاۖ وَأَحۡسِن كَمَاۤ أَحۡسَنَ ٱللَّهُ إِلَیۡكَۖ وَلَا تَبۡغِ ٱلۡفَسَادَ فِی ٱلۡأَرۡضِۖ إِنَّ ٱللَّهَ لَا یُحِبُّ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:22'),
(3059, 28, 3330, 78, 'قَالَ إِنَّمَاۤ أُوتِیتُهُۥ عَلَىٰ عِلۡمٍ عِندِیۤۚ أَوَلَمۡ یَعۡلَمۡ أَنَّ ٱللَّهَ قَدۡ أَهۡلَكَ مِن قَبۡلِهِۦ مِنَ ٱلۡقُرُونِ مَنۡ هُوَ أَشَدُّ مِنۡهُ قُوَّةࣰ وَأَكۡثَرُ جَمۡعࣰاۚ وَلَا یُسۡـَٔلُ عَن ذُنُوبِهِمُ ٱلۡمُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:22'),
(3060, 28, 3331, 79, 'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ فِی زِینَتِهِۦۖ قَالَ ٱلَّذِینَ یُرِیدُونَ ٱلۡحَیَوٰةَ ٱلدُّنۡیَا یَـٰلَیۡتَ لَنَا مِثۡلَ مَاۤ أُوتِیَ قَـٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:22'),
(3061, 28, 3332, 80, 'وَقَالَ ٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ وَیۡلَكُمۡ ثَوَابُ ٱللَّهِ خَیۡرࣱ لِّمَنۡ ءَامَنَ وَعَمِلَ صَـٰلِحࣰاۚ وَلَا یُلَقَّىٰهَاۤ إِلَّا ٱلصَّـٰبِرُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3062, 28, 3333, 81, 'فَخَسَفۡنَا بِهِۦ وَبِدَارِهِ ٱلۡأَرۡضَ فَمَا كَانَ لَهُۥ مِن فِئَةࣲ یَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مِنَ ٱلۡمُنتَصِرِینَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3063, 28, 3334, 82, 'وَأَصۡبَحَ ٱلَّذِینَ تَمَنَّوۡا۟ مَكَانَهُۥ بِٱلۡأَمۡسِ یَقُولُونَ وَیۡكَأَنَّ ٱللَّهَ یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ مِنۡ عِبَادِهِۦ وَیَقۡدِرُۖ لَوۡلَاۤ أَن مَّنَّ ٱللَّهُ عَلَیۡنَا لَخَسَفَ بِنَاۖ وَیۡكَأَنَّهُۥ لَا یُفۡلِحُ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3064, 28, 3335, 83, 'تِلۡكَ ٱلدَّارُ ٱلۡـَٔاخِرَةُ نَجۡعَلُهَا لِلَّذِینَ لَا یُرِیدُونَ عُلُوࣰّا فِی ٱلۡأَرۡضِ وَلَا فَسَادࣰاۚ وَٱلۡعَـٰقِبَةُ لِلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3065, 28, 3336, 84, 'مَن جَاۤءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَیۡرࣱ مِّنۡهَاۖ وَمَن جَاۤءَ بِٱلسَّیِّئَةِ فَلَا یُجۡزَى ٱلَّذِینَ عَمِلُوا۟ ٱلسَّیِّـَٔاتِ إِلَّا مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3066, 28, 3337, 85, 'إِنَّ ٱلَّذِی فَرَضَ عَلَیۡكَ ٱلۡقُرۡءَانَ لَرَاۤدُّكَ إِلَىٰ مَعَادࣲۚ قُل رَّبِّیۤ أَعۡلَمُ مَن جَاۤءَ بِٱلۡهُدَىٰ وَمَنۡ هُوَ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3067, 28, 3338, 86, 'وَمَا كُنتَ تَرۡجُوۤا۟ أَن یُلۡقَىٰۤ إِلَیۡكَ ٱلۡكِتَـٰبُ إِلَّا رَحۡمَةࣰ مِّن رَّبِّكَۖ فَلَا تَكُونَنَّ ظَهِیرࣰا لِّلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(3068, 28, 3339, 87, 'وَلَا یَصُدُّنَّكَ عَنۡ ءَایَـٰتِ ٱللَّهِ بَعۡدَ إِذۡ أُنزِلَتۡ إِلَیۡكَۖ وَٱدۡعُ إِلَىٰ رَبِّكَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3069, 28, 3340, 88, 'وَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَۘ لَاۤ إِلَـٰهَ إِلَّا هُوَۚ كُلُّ شَیۡءٍ هَالِكٌ إِلَّا وَجۡهَهُۥۚ لَهُ ٱلۡحُكۡمُ وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:27:19', '2023-05-27 11:12:23'),
(3070, 27, 3160, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ طسۤۚ تِلۡكَ ءَایَـٰتُ ٱلۡقُرۡءَانِ وَكِتَابࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3071, 27, 3161, 2, 'هُدࣰى وَبُشۡرَىٰ لِلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3072, 27, 3162, 3, 'ٱلَّذِینَ یُقِیمُونَ ٱلصَّلَوٰةَ وَیُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡـَٔاخِرَةِ هُمۡ یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3073, 27, 3163, 4, 'إِنَّ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ زَیَّنَّا لَهُمۡ أَعۡمَـٰلَهُمۡ فَهُمۡ یَعۡمَهُونَ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3074, 27, 3164, 5, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ لَهُمۡ سُوۤءُ ٱلۡعَذَابِ وَهُمۡ فِی ٱلۡـَٔاخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3075, 27, 3165, 6, 'وَإِنَّكَ لَتُلَقَّى ٱلۡقُرۡءَانَ مِن لَّدُنۡ حَكِیمٍ عَلِیمٍ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3076, 27, 3166, 7, 'إِذۡ قَالَ مُوسَىٰ لِأَهۡلِهِۦۤ إِنِّیۤ ءَانَسۡتُ نَارࣰا سَـَٔاتِیكُم مِّنۡهَا بِخَبَرٍ أَوۡ ءَاتِیكُم بِشِهَابࣲ قَبَسࣲ لَّعَلَّكُمۡ تَصۡطَلُونَ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3077, 27, 3167, 8, 'فَلَمَّا جَاۤءَهَا نُودِیَ أَنۢ بُورِكَ مَن فِی ٱلنَّارِ وَمَنۡ حَوۡلَهَا وَسُبۡحَـٰنَ ٱللَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:28', '2023-05-27 11:12:26'),
(3078, 27, 3168, 9, 'یَـٰمُوسَىٰۤ إِنَّهُۥۤ أَنَا ٱللَّهُ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:26'),
(3079, 27, 3169, 10, 'وَأَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَاۤنࣱّ وَلَّىٰ مُدۡبِرࣰا وَلَمۡ یُعَقِّبۡۚ یَـٰمُوسَىٰ لَا تَخَفۡ إِنِّی لَا یَخَافُ لَدَیَّ ٱلۡمُرۡسَلُونَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:26'),
(3080, 27, 3170, 11, 'إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسۡنَۢا بَعۡدَ سُوۤءࣲ فَإِنِّی غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:26'),
(3081, 27, 3171, 12, 'وَأَدۡخِلۡ یَدَكَ فِی جَیۡبِكَ تَخۡرُجۡ بَیۡضَاۤءَ مِنۡ غَیۡرِ سُوۤءࣲۖ فِی تِسۡعِ ءَایَـٰتٍ إِلَىٰ فِرۡعَوۡنَ وَقَوۡمِهِۦۤۚ إِنَّهُمۡ كَانُوا۟ قَوۡمࣰا فَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:26'),
(3082, 27, 3172, 13, 'فَلَمَّا جَاۤءَتۡهُمۡ ءَایَـٰتُنَا مُبۡصِرَةࣰ قَالُوا۟ هَـٰذَا سِحۡرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:26'),
(3083, 27, 3173, 14, 'وَجَحَدُوا۟ بِهَا وَٱسۡتَیۡقَنَتۡهَاۤ أَنفُسُهُمۡ ظُلۡمࣰا وَعُلُوࣰّاۚ فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:26'),
(3084, 27, 3174, 15, 'وَلَقَدۡ ءَاتَیۡنَا دَاوُۥدَ وَسُلَیۡمَـٰنَ عِلۡمࣰاۖ وَقَالَا ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی فَضَّلَنَا عَلَىٰ كَثِیرࣲ مِّنۡ عِبَادِهِ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:26'),
(3085, 27, 3175, 16, 'وَوَرِثَ سُلَیۡمَـٰنُ دَاوُۥدَۖ وَقَالَ یَـٰۤأَیُّهَا ٱلنَّاسُ عُلِّمۡنَا مَنطِقَ ٱلطَّیۡرِ وَأُوتِینَا مِن كُلِّ شَیۡءٍۖ إِنَّ هَـٰذَا لَهُوَ ٱلۡفَضۡلُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3086, 27, 3176, 17, 'وَحُشِرَ لِسُلَیۡمَـٰنَ جُنُودُهُۥ مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ وَٱلطَّیۡرِ فَهُمۡ یُوزَعُونَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3087, 27, 3177, 18, 'حَتَّىٰۤ إِذَاۤ أَتَوۡا۟ عَلَىٰ وَادِ ٱلنَّمۡلِ قَالَتۡ نَمۡلَةࣱ یَـٰۤأَیُّهَا ٱلنَّمۡلُ ٱدۡخُلُوا۟ مَسَـٰكِنَكُمۡ لَا یَحۡطِمَنَّكُمۡ سُلَیۡمَـٰنُ وَجُنُودُهُۥ وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3088, 27, 3178, 19, 'فَتَبَسَّمَ ضَاحِكࣰا مِّن قَوۡلِهَا وَقَالَ رَبِّ أَوۡزِعۡنِیۤ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِیۤ أَنۡعَمۡتَ عَلَیَّ وَعَلَىٰ وَ ٰ⁠لِدَیَّ وَأَنۡ أَعۡمَلَ صَـٰلِحࣰا تَرۡضَىٰهُ وَأَدۡخِلۡنِی بِرَحۡمَتِكَ فِی عِبَادِكَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3089, 27, 3179, 20, 'وَتَفَقَّدَ ٱلطَّیۡرَ فَقَالَ مَا لِیَ لَاۤ أَرَى ٱلۡهُدۡهُدَ أَمۡ كَانَ مِنَ ٱلۡغَاۤىِٕبِینَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3090, 27, 3180, 21, 'لَأُعَذِّبَنَّهُۥ عَذَابࣰا شَدِیدًا أَوۡ لَأَا۟ذۡبَحَنَّهُۥۤ أَوۡ لَیَأۡتِیَنِّی بِسُلۡطَـٰنࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3091, 27, 3181, 22, 'فَمَكَثَ غَیۡرَ بَعِیدࣲ فَقَالَ أَحَطتُ بِمَا لَمۡ تُحِطۡ بِهِۦ وَجِئۡتُكَ مِن سَبَإِۭ بِنَبَإࣲ یَقِینٍ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3092, 27, 3182, 23, 'إِنِّی وَجَدتُّ ٱمۡرَأَةࣰ تَمۡلِكُهُمۡ وَأُوتِیَتۡ مِن كُلِّ شَیۡءࣲ وَلَهَا عَرۡشٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3093, 27, 3183, 24, 'وَجَدتُّهَا وَقَوۡمَهَا یَسۡجُدُونَ لِلشَّمۡسِ مِن دُونِ ٱللَّهِ وَزَیَّنَ لَهُمُ ٱلشَّیۡطَـٰنُ أَعۡمَـٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِیلِ فَهُمۡ لَا یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3094, 27, 3184, 25, 'أَلَّا یَسۡجُدُوا۟ لِلَّهِ ٱلَّذِی یُخۡرِجُ ٱلۡخَبۡءَ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَیَعۡلَمُ مَا تُخۡفُونَ وَمَا تُعۡلِنُونَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3095, 27, 3185, 26, 'ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِیمِ ۩\n', NULL, 1, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3096, 27, 3186, 27, '۞ قَالَ سَنَنظُرُ أَصَدَقۡتَ أَمۡ كُنتَ مِنَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:27:29', '2023-05-27 11:12:27'),
(3097, 27, 3187, 28, 'ٱذۡهَب بِّكِتَـٰبِی هَـٰذَا فَأَلۡقِهۡ إِلَیۡهِمۡ ثُمَّ تَوَلَّ عَنۡهُمۡ فَٱنظُرۡ مَاذَا یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:27'),
(3098, 27, 3188, 29, 'قَالَتۡ یَـٰۤأَیُّهَا ٱلۡمَلَؤُا۟ إِنِّیۤ أُلۡقِیَ إِلَیَّ كِتَـٰبࣱ كَرِیمٌ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:27'),
(3099, 27, 3189, 30, 'إِنَّهُۥ مِن سُلَیۡمَـٰنَ وَإِنَّهُۥ بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:27'),
(3100, 27, 3190, 31, 'أَلَّا تَعۡلُوا۟ عَلَیَّ وَأۡتُونِی مُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:27'),
(3101, 27, 3191, 32, 'قَالَتۡ یَـٰۤأَیُّهَا ٱلۡمَلَؤُا۟ أَفۡتُونِی فِیۤ أَمۡرِی مَا كُنتُ قَاطِعَةً أَمۡرًا حَتَّىٰ تَشۡهَدُونِ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:27'),
(3102, 27, 3192, 33, 'قَالُوا۟ نَحۡنُ أُو۟لُوا۟ قُوَّةࣲ وَأُو۟لُوا۟ بَأۡسࣲ شَدِیدࣲ وَٱلۡأَمۡرُ إِلَیۡكِ فَٱنظُرِی مَاذَا تَأۡمُرِینَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:27'),
(3103, 27, 3193, 34, 'قَالَتۡ إِنَّ ٱلۡمُلُوكَ إِذَا دَخَلُوا۟ قَرۡیَةً أَفۡسَدُوهَا وَجَعَلُوۤا۟ أَعِزَّةَ أَهۡلِهَاۤ أَذِلَّةࣰۚ وَكَذَ ٰ⁠لِكَ یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3104, 27, 3194, 35, 'وَإِنِّی مُرۡسِلَةٌ إِلَیۡهِم بِهَدِیَّةࣲ فَنَاظِرَةُۢ بِمَ یَرۡجِعُ ٱلۡمُرۡسَلُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3105, 27, 3195, 36, 'فَلَمَّا جَاۤءَ سُلَیۡمَـٰنَ قَالَ أَتُمِدُّونَنِ بِمَالࣲ فَمَاۤ ءَاتَىٰنِۦَ ٱللَّهُ خَیۡرࣱ مِّمَّاۤ ءَاتَىٰكُمۚ بَلۡ أَنتُم بِهَدِیَّتِكُمۡ تَفۡرَحُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3106, 27, 3196, 37, 'ٱرۡجِعۡ إِلَیۡهِمۡ فَلَنَأۡتِیَنَّهُم بِجُنُودࣲ لَّا قِبَلَ لَهُم بِهَا وَلَنُخۡرِجَنَّهُم مِّنۡهَاۤ أَذِلَّةࣰ وَهُمۡ صَـٰغِرُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3107, 27, 3197, 38, 'قَالَ یَـٰۤأَیُّهَا ٱلۡمَلَؤُا۟ أَیُّكُمۡ یَأۡتِینِی بِعَرۡشِهَا قَبۡلَ أَن یَأۡتُونِی مُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3108, 27, 3198, 39, 'قَالَ عِفۡرِیتࣱ مِّنَ ٱلۡجِنِّ أَنَا۠ ءَاتِیكَ بِهِۦ قَبۡلَ أَن تَقُومَ مِن مَّقَامِكَۖ وَإِنِّی عَلَیۡهِ لَقَوِیٌّ أَمِینࣱ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3109, 27, 3199, 40, 'قَالَ ٱلَّذِی عِندَهُۥ عِلۡمࣱ مِّنَ ٱلۡكِتَـٰبِ أَنَا۠ ءَاتِیكَ بِهِۦ قَبۡلَ أَن یَرۡتَدَّ إِلَیۡكَ طَرۡفُكَۚ فَلَمَّا رَءَاهُ مُسۡتَقِرًّا عِندَهُۥ قَالَ هَـٰذَا مِن فَضۡلِ رَبِّی لِیَبۡلُوَنِیۤ ءَأَشۡكُرُ أَمۡ أَكۡفُرُۖ وَمَن شَكَرَ فَإِنَّمَا یَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ رَبِّی غَنِیࣱّ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3110, 27, 3200, 41, 'قَالَ نَكِّرُوا۟ لَهَا عَرۡشَهَا نَنظُرۡ أَتَهۡتَدِیۤ أَمۡ تَكُونُ مِنَ ٱلَّذِینَ لَا یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3111, 27, 3201, 42, 'فَلَمَّا جَاۤءَتۡ قِیلَ أَهَـٰكَذَا عَرۡشُكِۖ قَالَتۡ كَأَنَّهُۥ هُوَۚ وَأُوتِینَا ٱلۡعِلۡمَ مِن قَبۡلِهَا وَكُنَّا مُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3112, 27, 3202, 43, 'وَصَدَّهَا مَا كَانَت تَّعۡبُدُ مِن دُونِ ٱللَّهِۖ إِنَّهَا كَانَتۡ مِن قَوۡمࣲ كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3113, 27, 3203, 44, 'قِیلَ لَهَا ٱدۡخُلِی ٱلصَّرۡحَۖ فَلَمَّا رَأَتۡهُ حَسِبَتۡهُ لُجَّةࣰ وَكَشَفَتۡ عَن سَاقَیۡهَاۚ قَالَ إِنَّهُۥ صَرۡحࣱ مُّمَرَّدࣱ مِّن قَوَارِیرَۗ قَالَتۡ رَبِّ إِنِّی ظَلَمۡتُ نَفۡسِی وَأَسۡلَمۡتُ مَعَ سُلَیۡمَـٰنَ لِلَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3114, 27, 3204, 45, 'وَلَقَدۡ أَرۡسَلۡنَاۤ إِلَىٰ ثَمُودَ أَخَاهُمۡ صَـٰلِحًا أَنِ ٱعۡبُدُوا۟ ٱللَّهَ فَإِذَا هُمۡ فَرِیقَانِ یَخۡتَصِمُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3115, 27, 3205, 46, 'قَالَ یَـٰقَوۡمِ لِمَ تَسۡتَعۡجِلُونَ بِٱلسَّیِّئَةِ قَبۡلَ ٱلۡحَسَنَةِۖ لَوۡلَا تَسۡتَغۡفِرُونَ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:27:30', '2023-05-27 11:12:28'),
(3116, 27, 3206, 47, 'قَالُوا۟ ٱطَّیَّرۡنَا بِكَ وَبِمَن مَّعَكَۚ قَالَ طَـٰۤىِٕرُكُمۡ عِندَ ٱللَّهِۖ بَلۡ أَنتُمۡ قَوۡمࣱ تُفۡتَنُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:28'),
(3117, 27, 3207, 48, 'وَكَانَ فِی ٱلۡمَدِینَةِ تِسۡعَةُ رَهۡطࣲ یُفۡسِدُونَ فِی ٱلۡأَرۡضِ وَلَا یُصۡلِحُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:28'),
(3118, 27, 3208, 49, 'قَالُوا۟ تَقَاسَمُوا۟ بِٱللَّهِ لَنُبَیِّتَنَّهُۥ وَأَهۡلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِیِّهِۦ مَا شَهِدۡنَا مَهۡلِكَ أَهۡلِهِۦ وَإِنَّا لَصَـٰدِقُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:28'),
(3119, 27, 3209, 50, 'وَمَكَرُوا۟ مَكۡرࣰا وَمَكَرۡنَا مَكۡرࣰا وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:28'),
(3120, 27, 3210, 51, 'فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ مَكۡرِهِمۡ أَنَّا دَمَّرۡنَـٰهُمۡ وَقَوۡمَهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:28'),
(3121, 27, 3211, 52, 'فَتِلۡكَ بُیُوتُهُمۡ خَاوِیَةَۢ بِمَا ظَلَمُوۤا۟ۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3122, 27, 3212, 53, 'وَأَنجَیۡنَا ٱلَّذِینَ ءَامَنُوا۟ وَكَانُوا۟ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3123, 27, 3213, 54, 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦۤ أَتَأۡتُونَ ٱلۡفَـٰحِشَةَ وَأَنتُمۡ تُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3124, 27, 3214, 55, 'أَىِٕنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةࣰ مِّن دُونِ ٱلنِّسَاۤءِۚ بَلۡ أَنتُمۡ قَوۡمࣱ تَجۡهَلُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3125, 27, 3215, 56, '۞ فَمَا كَانَ جَوَابَ قَوۡمِهِۦۤ إِلَّاۤ أَن قَالُوۤا۟ أَخۡرِجُوۤا۟ ءَالَ لُوطࣲ مِّن قَرۡیَتِكُمۡۖ إِنَّهُمۡ أُنَاسࣱ یَتَطَهَّرُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3126, 27, 3216, 57, 'فَأَنجَیۡنَـٰهُ وَأَهۡلَهُۥۤ إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَـٰهَا مِنَ ٱلۡغَـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3127, 27, 3217, 58, 'وَأَمۡطَرۡنَا عَلَیۡهِم مَّطَرࣰاۖ فَسَاۤءَ مَطَرُ ٱلۡمُنذَرِینَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3128, 27, 3218, 59, 'قُلِ ٱلۡحَمۡدُ لِلَّهِ وَسَلَـٰمٌ عَلَىٰ عِبَادِهِ ٱلَّذِینَ ٱصۡطَفَىٰۤۗ ءَاۤللَّهُ خَیۡرٌ أَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3129, 27, 3219, 60, 'أَمَّنۡ خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَأَنزَلَ لَكُم مِّنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَنۢبَتۡنَا بِهِۦ حَدَاۤىِٕقَ ذَاتَ بَهۡجَةࣲ مَّا كَانَ لَكُمۡ أَن تُنۢبِتُوا۟ شَجَرَهَاۤۗ أَءِلَـٰهࣱ مَّعَ ٱللَّهِۚ بَلۡ هُمۡ قَوۡمࣱ یَعۡدِلُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3130, 27, 3220, 61, 'أَمَّن جَعَلَ ٱلۡأَرۡضَ قَرَارࣰا وَجَعَلَ خِلَـٰلَهَاۤ أَنۡهَـٰرࣰا وَجَعَلَ لَهَا رَوَ ٰ⁠سِیَ وَجَعَلَ بَیۡنَ ٱلۡبَحۡرَیۡنِ حَاجِزًاۗ أَءِلَـٰهࣱ مَّعَ ٱللَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3131, 27, 3221, 62, 'أَمَّن یُجِیبُ ٱلۡمُضۡطَرَّ إِذَا دَعَاهُ وَیَكۡشِفُ ٱلسُّوۤءَ وَیَجۡعَلُكُمۡ خُلَفَاۤءَ ٱلۡأَرۡضِۗ أَءِلَـٰهࣱ مَّعَ ٱللَّهِۚ قَلِیلࣰا مَّا تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3132, 27, 3222, 63, 'أَمَّن یَهۡدِیكُمۡ فِی ظُلُمَـٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ وَمَن یُرۡسِلُ ٱلرِّیَـٰحَ بُشۡرَۢا بَیۡنَ یَدَیۡ رَحۡمَتِهِۦۤۗ أَءِلَـٰهࣱ مَّعَ ٱللَّهِۚ تَعَـٰلَى ٱللَّهُ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3133, 27, 3223, 64, 'أَمَّن یَبۡدَؤُا۟ ٱلۡخَلۡقَ ثُمَّ یُعِیدُهُۥ وَمَن یَرۡزُقُكُم مِّنَ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِۗ أَءِلَـٰهࣱ مَّعَ ٱللَّهِۚ قُلۡ هَاتُوا۟ بُرۡهَـٰنَكُمۡ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3134, 27, 3224, 65, 'قُل لَّا یَعۡلَمُ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ ٱلۡغَیۡبَ إِلَّا ٱللَّهُۚ وَمَا یَشۡعُرُونَ أَیَّانَ یُبۡعَثُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3135, 27, 3225, 66, 'بَلِ ٱدَّ ٰ⁠رَكَ عِلۡمُهُمۡ فِی ٱلۡـَٔاخِرَةِۚ بَلۡ هُمۡ فِی شَكࣲّ مِّنۡهَاۖ بَلۡ هُم مِّنۡهَا عَمُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3136, 27, 3226, 67, 'وَقَالَ ٱلَّذِینَ كَفَرُوۤا۟ أَءِذَا كُنَّا تُرَ ٰ⁠بࣰا وَءَابَاۤؤُنَاۤ أَىِٕنَّا لَمُخۡرَجُونَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3137, 27, 3227, 68, 'لَقَدۡ وُعِدۡنَا هَـٰذَا نَحۡنُ وَءَابَاۤؤُنَا مِن قَبۡلُ إِنۡ هَـٰذَاۤ إِلَّاۤ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3138, 27, 3228, 69, 'قُلۡ سِیرُوا۟ فِی ٱلۡأَرۡضِ فَٱنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:27:31', '2023-05-27 11:12:29'),
(3139, 27, 3229, 70, 'وَلَا تَحۡزَنۡ عَلَیۡهِمۡ وَلَا تَكُن فِی ضَیۡقࣲ مِّمَّا یَمۡكُرُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3140, 27, 3230, 71, 'وَیَقُولُونَ مَتَىٰ هَـٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3141, 27, 3231, 72, 'قُلۡ عَسَىٰۤ أَن یَكُونَ رَدِفَ لَكُم بَعۡضُ ٱلَّذِی تَسۡتَعۡجِلُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3142, 27, 3232, 73, 'وَإِنَّ رَبَّكَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3143, 27, 3233, 74, 'وَإِنَّ رَبَّكَ لَیَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا یُعۡلِنُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3144, 27, 3234, 75, 'وَمَا مِنۡ غَاۤىِٕبَةࣲ فِی ٱلسَّمَاۤءِ وَٱلۡأَرۡضِ إِلَّا فِی كِتَـٰبࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3145, 27, 3235, 76, 'إِنَّ هَـٰذَا ٱلۡقُرۡءَانَ یَقُصُّ عَلَىٰ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ أَكۡثَرَ ٱلَّذِی هُمۡ فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3146, 27, 3236, 77, 'وَإِنَّهُۥ لَهُدࣰى وَرَحۡمَةࣱ لِّلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3147, 27, 3237, 78, 'إِنَّ رَبَّكَ یَقۡضِی بَیۡنَهُم بِحُكۡمِهِۦۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3148, 27, 3238, 79, 'فَتَوَكَّلۡ عَلَى ٱللَّهِۖ إِنَّكَ عَلَى ٱلۡحَقِّ ٱلۡمُبِینِ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3149, 27, 3239, 80, 'إِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَاۤءَ إِذَا وَلَّوۡا۟ مُدۡبِرِینَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3150, 27, 3240, 81, 'وَمَاۤ أَنتَ بِهَـٰدِی ٱلۡعُمۡیِ عَن ضَلَـٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن یُؤۡمِنُ بِـَٔایَـٰتِنَا فَهُم مُّسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3151, 27, 3241, 82, '۞ وَإِذَا وَقَعَ ٱلۡقَوۡلُ عَلَیۡهِمۡ أَخۡرَجۡنَا لَهُمۡ دَاۤبَّةࣰ مِّنَ ٱلۡأَرۡضِ تُكَلِّمُهُمۡ أَنَّ ٱلنَّاسَ كَانُوا۟ بِـَٔایَـٰتِنَا لَا یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3152, 27, 3242, 83, 'وَیَوۡمَ نَحۡشُرُ مِن كُلِّ أُمَّةࣲ فَوۡجࣰا مِّمَّن یُكَذِّبُ بِـَٔایَـٰتِنَا فَهُمۡ یُوزَعُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3153, 27, 3243, 84, 'حَتَّىٰۤ إِذَا جَاۤءُو قَالَ أَكَذَّبۡتُم بِـَٔایَـٰتِی وَلَمۡ تُحِیطُوا۟ بِهَا عِلۡمًا أَمَّاذَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3154, 27, 3244, 85, 'وَوَقَعَ ٱلۡقَوۡلُ عَلَیۡهِم بِمَا ظَلَمُوا۟ فَهُمۡ لَا یَنطِقُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3155, 27, 3245, 86, 'أَلَمۡ یَرَوۡا۟ أَنَّا جَعَلۡنَا ٱلَّیۡلَ لِیَسۡكُنُوا۟ فِیهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:30'),
(3156, 27, 3246, 87, 'وَیَوۡمَ یُنفَخُ فِی ٱلصُّورِ فَفَزِعَ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَن فِی ٱلۡأَرۡضِ إِلَّا مَن شَاۤءَ ٱللَّهُۚ وَكُلٌّ أَتَوۡهُ دَ ٰ⁠خِرِینَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:31'),
(3157, 27, 3247, 88, 'وَتَرَى ٱلۡجِبَالَ تَحۡسَبُهَا جَامِدَةࣰ وَهِیَ تَمُرُّ مَرَّ ٱلسَّحَابِۚ صُنۡعَ ٱللَّهِ ٱلَّذِیۤ أَتۡقَنَ كُلَّ شَیۡءٍۚ إِنَّهُۥ خَبِیرُۢ بِمَا تَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:27:32', '2023-05-27 11:12:31'),
(3158, 27, 3248, 89, 'مَن جَاۤءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَیۡرࣱ مِّنۡهَا وَهُم مِّن فَزَعࣲ یَوۡمَىِٕذٍ ءَامِنُونَ\n', NULL, 0, '2023-05-26 17:27:33', '2023-05-27 11:12:31'),
(3159, 27, 3249, 90, 'وَمَن جَاۤءَ بِٱلسَّیِّئَةِ فَكُبَّتۡ وُجُوهُهُمۡ فِی ٱلنَّارِ هَلۡ تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:27:33', '2023-05-27 11:12:31'),
(3160, 27, 3250, 91, 'إِنَّمَاۤ أُمِرۡتُ أَنۡ أَعۡبُدَ رَبَّ هَـٰذِهِ ٱلۡبَلۡدَةِ ٱلَّذِی حَرَّمَهَا وَلَهُۥ كُلُّ شَیۡءࣲۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:27:33', '2023-05-27 11:12:31'),
(3161, 27, 3251, 92, 'وَأَنۡ أَتۡلُوَا۟ ٱلۡقُرۡءَانَۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا یَهۡتَدِی لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَقُلۡ إِنَّمَاۤ أَنَا۠ مِنَ ٱلۡمُنذِرِینَ\n', NULL, 0, '2023-05-26 17:27:33', '2023-05-27 11:12:31'),
(3162, 27, 3252, 93, 'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ سَیُرِیكُمۡ ءَایَـٰتِهِۦ فَتَعۡرِفُونَهَاۚ وَمَا رَبُّكَ بِغَـٰفِلٍ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:27:33', '2023-05-27 11:12:31'),
(3163, 26, 2933, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ طسۤمۤ\n', NULL, 0, '2023-05-26 17:27:57', '2023-05-27 11:12:34'),
(3164, 26, 2934, 2, 'تِلۡكَ ءَایَـٰتُ ٱلۡكِتَـٰبِ ٱلۡمُبِینِ\n', NULL, 0, '2023-05-26 17:27:57', '2023-05-27 11:12:34'),
(3165, 26, 2935, 3, 'لَعَلَّكَ بَـٰخِعࣱ نَّفۡسَكَ أَلَّا یَكُونُوا۟ مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:57', '2023-05-27 11:12:34'),
(3166, 26, 2936, 4, 'إِن نَّشَأۡ نُنَزِّلۡ عَلَیۡهِم مِّنَ ٱلسَّمَاۤءِ ءَایَةࣰ فَظَلَّتۡ أَعۡنَـٰقُهُمۡ لَهَا خَـٰضِعِینَ\n', NULL, 0, '2023-05-26 17:27:57', '2023-05-27 11:12:34'),
(3167, 26, 2937, 5, 'وَمَا یَأۡتِیهِم مِّن ذِكۡرࣲ مِّنَ ٱلرَّحۡمَـٰنِ مُحۡدَثٍ إِلَّا كَانُوا۟ عَنۡهُ مُعۡرِضِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3168, 26, 2938, 6, 'فَقَدۡ كَذَّبُوا۟ فَسَیَأۡتِیهِمۡ أَنۢبَـٰۤؤُا۟ مَا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3169, 26, 2939, 7, 'أَوَلَمۡ یَرَوۡا۟ إِلَى ٱلۡأَرۡضِ كَمۡ أَنۢبَتۡنَا فِیهَا مِن كُلِّ زَوۡجࣲ كَرِیمٍ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3170, 26, 2940, 8, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3171, 26, 2941, 9, 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِیزُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3172, 26, 2942, 10, 'وَإِذۡ نَادَىٰ رَبُّكَ مُوسَىٰۤ أَنِ ٱئۡتِ ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3173, 26, 2943, 11, 'قَوۡمَ فِرۡعَوۡنَۚ أَلَا یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3174, 26, 2944, 12, 'قَالَ رَبِّ إِنِّیۤ أَخَافُ أَن یُكَذِّبُونِ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3175, 26, 2945, 13, 'وَیَضِیقُ صَدۡرِی وَلَا یَنطَلِقُ لِسَانِی فَأَرۡسِلۡ إِلَىٰ هَـٰرُونَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3176, 26, 2946, 14, 'وَلَهُمۡ عَلَیَّ ذَنۢبࣱ فَأَخَافُ أَن یَقۡتُلُونِ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3177, 26, 2947, 15, 'قَالَ كَلَّاۖ فَٱذۡهَبَا بِـَٔایَـٰتِنَاۤۖ إِنَّا مَعَكُم مُّسۡتَمِعُونَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3178, 26, 2948, 16, 'فَأۡتِیَا فِرۡعَوۡنَ فَقُولَاۤ إِنَّا رَسُولُ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3179, 26, 2949, 17, 'أَنۡ أَرۡسِلۡ مَعَنَا بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3180, 26, 2950, 18, 'قَالَ أَلَمۡ نُرَبِّكَ فِینَا وَلِیدࣰا وَلَبِثۡتَ فِینَا مِنۡ عُمُرِكَ سِنِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:34'),
(3181, 26, 2951, 19, 'وَفَعَلۡتَ فَعۡلَتَكَ ٱلَّتِی فَعَلۡتَ وَأَنتَ مِنَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3182, 26, 2952, 20, 'قَالَ فَعَلۡتُهَاۤ إِذࣰا وَأَنَا۠ مِنَ ٱلضَّاۤلِّینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3183, 26, 2953, 21, 'فَفَرَرۡتُ مِنكُمۡ لَمَّا خِفۡتُكُمۡ فَوَهَبَ لِی رَبِّی حُكۡمࣰا وَجَعَلَنِی مِنَ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3184, 26, 2954, 22, 'وَتِلۡكَ نِعۡمَةࣱ تَمُنُّهَا عَلَیَّ أَنۡ عَبَّدتَّ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3185, 26, 2955, 23, 'قَالَ فِرۡعَوۡنُ وَمَا رَبُّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3186, 26, 2956, 24, 'قَالَ رَبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَاۤۖ إِن كُنتُم مُّوقِنِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3187, 26, 2957, 25, 'قَالَ لِمَنۡ حَوۡلَهُۥۤ أَلَا تَسۡتَمِعُونَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3188, 26, 2958, 26, 'قَالَ رَبُّكُمۡ وَرَبُّ ءَابَاۤىِٕكُمُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:27:58', '2023-05-27 11:12:35'),
(3189, 26, 2959, 27, 'قَالَ إِنَّ رَسُولَكُمُ ٱلَّذِیۤ أُرۡسِلَ إِلَیۡكُمۡ لَمَجۡنُونࣱ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3190, 26, 2960, 28, 'قَالَ رَبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَمَا بَیۡنَهُمَاۤۖ إِن كُنتُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3191, 26, 2961, 29, 'قَالَ لَىِٕنِ ٱتَّخَذۡتَ إِلَـٰهًا غَیۡرِی لَأَجۡعَلَنَّكَ مِنَ ٱلۡمَسۡجُونِینَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3192, 26, 2962, 30, 'قَالَ أَوَلَوۡ جِئۡتُكَ بِشَیۡءࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3193, 26, 2963, 31, 'قَالَ فَأۡتِ بِهِۦۤ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3194, 26, 2964, 32, 'فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِیَ ثُعۡبَانࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3195, 26, 2965, 33, 'وَنَزَعَ یَدَهُۥ فَإِذَا هِیَ بَیۡضَاۤءُ لِلنَّـٰظِرِینَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3196, 26, 2966, 34, 'قَالَ لِلۡمَلَإِ حَوۡلَهُۥۤ إِنَّ هَـٰذَا لَسَـٰحِرٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3197, 26, 2967, 35, 'یُرِیدُ أَن یُخۡرِجَكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِۦ فَمَاذَا تَأۡمُرُونَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3198, 26, 2968, 36, 'قَالُوۤا۟ أَرۡجِهۡ وَأَخَاهُ وَٱبۡعَثۡ فِی ٱلۡمَدَاۤىِٕنِ حَـٰشِرِینَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:35'),
(3199, 26, 2969, 37, 'یَأۡتُوكَ بِكُلِّ سَحَّارٍ عَلِیمࣲ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3200, 26, 2970, 38, 'فَجُمِعَ ٱلسَّحَرَةُ لِمِیقَـٰتِ یَوۡمࣲ مَّعۡلُومࣲ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3201, 26, 2971, 39, 'وَقِیلَ لِلنَّاسِ هَلۡ أَنتُم مُّجۡتَمِعُونَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3202, 26, 2972, 40, 'لَعَلَّنَا نَتَّبِعُ ٱلسَّحَرَةَ إِن كَانُوا۟ هُمُ ٱلۡغَـٰلِبِینَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3203, 26, 2973, 41, 'فَلَمَّا جَاۤءَ ٱلسَّحَرَةُ قَالُوا۟ لِفِرۡعَوۡنَ أَىِٕنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَـٰلِبِینَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3204, 26, 2974, 42, 'قَالَ نَعَمۡ وَإِنَّكُمۡ إِذࣰا لَّمِنَ ٱلۡمُقَرَّبِینَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3205, 26, 2975, 43, 'قَالَ لَهُم مُّوسَىٰۤ أَلۡقُوا۟ مَاۤ أَنتُم مُّلۡقُونَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3206, 26, 2976, 44, 'فَأَلۡقَوۡا۟ حِبَالَهُمۡ وَعِصِیَّهُمۡ وَقَالُوا۟ بِعِزَّةِ فِرۡعَوۡنَ إِنَّا لَنَحۡنُ ٱلۡغَـٰلِبُونَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3207, 26, 2977, 45, 'فَأَلۡقَىٰ مُوسَىٰ عَصَاهُ فَإِذَا هِیَ تَلۡقَفُ مَا یَأۡفِكُونَ\n', NULL, 0, '2023-05-26 17:27:59', '2023-05-27 11:12:36'),
(3208, 26, 2978, 46, 'فَأُلۡقِیَ ٱلسَّحَرَةُ سَـٰجِدِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:36'),
(3209, 26, 2979, 47, 'قَالُوۤا۟ ءَامَنَّا بِرَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:36'),
(3210, 26, 2980, 48, 'رَبِّ مُوسَىٰ وَهَـٰرُونَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:36'),
(3211, 26, 2981, 49, 'قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِیرُكُمُ ٱلَّذِی عَلَّمَكُمُ ٱلسِّحۡرَ فَلَسَوۡفَ تَعۡلَمُونَۚ لَأُقَطِّعَنَّ أَیۡدِیَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَـٰفࣲ وَلَأُصَلِّبَنَّكُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:36'),
(3212, 26, 2982, 50, 'قَالُوا۟ لَا ضَیۡرَۖ إِنَّاۤ إِلَىٰ رَبِّنَا مُنقَلِبُونَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:36'),
(3213, 26, 2983, 51, 'إِنَّا نَطۡمَعُ أَن یَغۡفِرَ لَنَا رَبُّنَا خَطَـٰیَـٰنَاۤ أَن كُنَّاۤ أَوَّلَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:36'),
(3214, 26, 2984, 52, '۞ وَأَوۡحَیۡنَاۤ إِلَىٰ مُوسَىٰۤ أَنۡ أَسۡرِ بِعِبَادِیۤ إِنَّكُم مُّتَّبَعُونَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3215, 26, 2985, 53, 'فَأَرۡسَلَ فِرۡعَوۡنُ فِی ٱلۡمَدَاۤىِٕنِ حَـٰشِرِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3216, 26, 2986, 54, 'إِنَّ هَـٰۤؤُلَاۤءِ لَشِرۡذِمَةࣱ قَلِیلُونَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3217, 26, 2987, 55, 'وَإِنَّهُمۡ لَنَا لَغَاۤىِٕظُونَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3218, 26, 2988, 56, 'وَإِنَّا لَجَمِیعٌ حَـٰذِرُونَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3219, 26, 2989, 57, 'فَأَخۡرَجۡنَـٰهُم مِّن جَنَّـٰتࣲ وَعُیُونࣲ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3220, 26, 2990, 58, 'وَكُنُوزࣲ وَمَقَامࣲ كَرِیمࣲ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3221, 26, 2991, 59, 'كَذَ ٰ⁠لِكَۖ وَأَوۡرَثۡنَـٰهَا بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3222, 26, 2992, 60, 'فَأَتۡبَعُوهُم مُّشۡرِقِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3223, 26, 2993, 61, 'فَلَمَّا تَرَ ٰ⁠ۤءَا ٱلۡجَمۡعَانِ قَالَ أَصۡحَـٰبُ مُوسَىٰۤ إِنَّا لَمُدۡرَكُونَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3224, 26, 2994, 62, 'قَالَ كَلَّاۤۖ إِنَّ مَعِیَ رَبِّی سَیَهۡدِینِ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3225, 26, 2995, 63, 'فَأَوۡحَیۡنَاۤ إِلَىٰ مُوسَىٰۤ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡبَحۡرَۖ فَٱنفَلَقَ فَكَانَ كُلُّ فِرۡقࣲ كَٱلطَّوۡدِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3226, 26, 2996, 64, 'وَأَزۡلَفۡنَا ثَمَّ ٱلۡـَٔاخَرِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3227, 26, 2997, 65, 'وَأَنجَیۡنَا مُوسَىٰ وَمَن مَّعَهُۥۤ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3228, 26, 2998, 66, 'ثُمَّ أَغۡرَقۡنَا ٱلۡـَٔاخَرِینَ\n', NULL, 0, '2023-05-26 17:28:00', '2023-05-27 11:12:37'),
(3229, 26, 2999, 67, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:37'),
(3230, 26, 3000, 68, 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِیزُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:37'),
(3231, 26, 3001, 69, 'وَٱتۡلُ عَلَیۡهِمۡ نَبَأَ إِبۡرَ ٰ⁠هِیمَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:37'),
(3232, 26, 3002, 70, 'إِذۡ قَالَ لِأَبِیهِ وَقَوۡمِهِۦ مَا تَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3233, 26, 3003, 71, 'قَالُوا۟ نَعۡبُدُ أَصۡنَامࣰا فَنَظَلُّ لَهَا عَـٰكِفِینَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3234, 26, 3004, 72, 'قَالَ هَلۡ یَسۡمَعُونَكُمۡ إِذۡ تَدۡعُونَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3235, 26, 3005, 73, 'أَوۡ یَنفَعُونَكُمۡ أَوۡ یَضُرُّونَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3236, 26, 3006, 74, 'قَالُوا۟ بَلۡ وَجَدۡنَاۤ ءَابَاۤءَنَا كَذَ ٰ⁠لِكَ یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3237, 26, 3007, 75, 'قَالَ أَفَرَءَیۡتُم مَّا كُنتُمۡ تَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3238, 26, 3008, 76, 'أَنتُمۡ وَءَابَاۤؤُكُمُ ٱلۡأَقۡدَمُونَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3239, 26, 3009, 77, 'فَإِنَّهُمۡ عَدُوࣱّ لِّیۤ إِلَّا رَبَّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3240, 26, 3010, 78, 'ٱلَّذِی خَلَقَنِی فَهُوَ یَهۡدِینِ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3241, 26, 3011, 79, 'وَٱلَّذِی هُوَ یُطۡعِمُنِی وَیَسۡقِینِ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3242, 26, 3012, 80, 'وَإِذَا مَرِضۡتُ فَهُوَ یَشۡفِینِ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3243, 26, 3013, 81, 'وَٱلَّذِی یُمِیتُنِی ثُمَّ یُحۡیِینِ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3244, 26, 3014, 82, 'وَٱلَّذِیۤ أَطۡمَعُ أَن یَغۡفِرَ لِی خَطِیۤـَٔتِی یَوۡمَ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3245, 26, 3015, 83, 'رَبِّ هَبۡ لِی حُكۡمࣰا وَأَلۡحِقۡنِی بِٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3246, 26, 3016, 84, 'وَٱجۡعَل لِّی لِسَانَ صِدۡقࣲ فِی ٱلۡـَٔاخِرِینَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3247, 26, 3017, 85, 'وَٱجۡعَلۡنِی مِن وَرَثَةِ جَنَّةِ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3248, 26, 3018, 86, 'وَٱغۡفِرۡ لِأَبِیۤ إِنَّهُۥ كَانَ مِنَ ٱلضَّاۤلِّینَ\n', NULL, 0, '2023-05-26 17:28:01', '2023-05-27 11:12:38'),
(3249, 26, 3019, 87, 'وَلَا تُخۡزِنِی یَوۡمَ یُبۡعَثُونَ\n', NULL, 0, '2023-05-26 17:28:02', '2023-05-27 11:12:38'),
(3250, 26, 3020, 88, 'یَوۡمَ لَا یَنفَعُ مَالࣱ وَلَا بَنُونَ\n', NULL, 0, '2023-05-26 17:28:02', '2023-05-27 11:12:39'),
(3251, 26, 3021, 89, 'إِلَّا مَنۡ أَتَى ٱللَّهَ بِقَلۡبࣲ سَلِیمࣲ\n', NULL, 0, '2023-05-26 17:28:02', '2023-05-27 11:12:39'),
(3252, 26, 3022, 90, 'وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:28:02', '2023-05-27 11:12:39'),
