(3329, 26, 3099, 167, 'قَالُوا۟ لَىِٕن لَّمۡ تَنتَهِ یَـٰلُوطُ لَتَكُونَنَّ مِنَ ٱلۡمُخۡرَجِینَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:43'),
(3330, 26, 3100, 168, 'قَالَ إِنِّی لِعَمَلِكُم مِّنَ ٱلۡقَالِینَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:43'),
(3331, 26, 3101, 169, 'رَبِّ نَجِّنِی وَأَهۡلِی مِمَّا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:43'),
(3332, 26, 3102, 170, 'فَنَجَّیۡنَـٰهُ وَأَهۡلَهُۥۤ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:43'),
(3333, 26, 3103, 171, 'إِلَّا عَجُوزࣰا فِی ٱلۡغَـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:44'),
(3334, 26, 3104, 172, 'ثُمَّ دَمَّرۡنَا ٱلۡـَٔاخَرِینَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:44'),
(3335, 26, 3105, 173, 'وَأَمۡطَرۡنَا عَلَیۡهِم مَّطَرࣰاۖ فَسَاۤءَ مَطَرُ ٱلۡمُنذَرِینَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:44'),
(3336, 26, 3106, 174, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:44'),
(3337, 26, 3107, 175, 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِیزُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:28:06', '2023-05-27 11:12:44'),
(3338, 26, 3108, 176, 'كَذَّبَ أَصۡحَـٰبُ لۡـَٔیۡكَةِ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3339, 26, 3109, 177, 'إِذۡ قَالَ لَهُمۡ شُعَیۡبٌ أَلَا تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3340, 26, 3110, 178, 'إِنِّی لَكُمۡ رَسُولٌ أَمِینࣱ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3341, 26, 3111, 179, 'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِیعُونِ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3342, 26, 3112, 180, 'وَمَاۤ أَسۡـَٔلُكُمۡ عَلَیۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِیَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3343, 26, 3113, 181, '۞ أَوۡفُوا۟ ٱلۡكَیۡلَ وَلَا تَكُونُوا۟ مِنَ ٱلۡمُخۡسِرِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3344, 26, 3114, 182, 'وَزِنُوا۟ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِیمِ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3345, 26, 3115, 183, 'وَلَا تَبۡخَسُوا۟ ٱلنَّاسَ أَشۡیَاۤءَهُمۡ وَلَا تَعۡثَوۡا۟ فِی ٱلۡأَرۡضِ مُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3346, 26, 3116, 184, 'وَٱتَّقُوا۟ ٱلَّذِی خَلَقَكُمۡ وَٱلۡجِبِلَّةَ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3347, 26, 3117, 185, 'قَالُوۤا۟ إِنَّمَاۤ أَنتَ مِنَ ٱلۡمُسَحَّرِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3348, 26, 3118, 186, 'وَمَاۤ أَنتَ إِلَّا بَشَرࣱ مِّثۡلُنَا وَإِن نَّظُنُّكَ لَمِنَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3349, 26, 3119, 187, 'فَأَسۡقِطۡ عَلَیۡنَا كِسَفࣰا مِّنَ ٱلسَّمَاۤءِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3350, 26, 3120, 188, 'قَالَ رَبِّیۤ أَعۡلَمُ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:44'),
(3351, 26, 3121, 189, 'فَكَذَّبُوهُ فَأَخَذَهُمۡ عَذَابُ یَوۡمِ ٱلظُّلَّةِۚ إِنَّهُۥ كَانَ عَذَابَ یَوۡمٍ عَظِیمٍ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:45'),
(3352, 26, 3122, 190, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:45'),
(3353, 26, 3123, 191, 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِیزُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:45'),
(3354, 26, 3124, 192, 'وَإِنَّهُۥ لَتَنزِیلُ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:45'),
(3355, 26, 3125, 193, 'نَزَلَ بِهِ ٱلرُّوحُ ٱلۡأَمِینُ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:45'),
(3356, 26, 3126, 194, 'عَلَىٰ قَلۡبِكَ لِتَكُونَ مِنَ ٱلۡمُنذِرِینَ\n', NULL, 0, '2023-05-26 17:28:07', '2023-05-27 11:12:45'),
(3357, 26, 3127, 195, 'بِلِسَانٍ عَرَبِیࣲّ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3358, 26, 3128, 196, 'وَإِنَّهُۥ لَفِی زُبُرِ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3359, 26, 3129, 197, 'أَوَلَمۡ یَكُن لَّهُمۡ ءَایَةً أَن یَعۡلَمَهُۥ عُلَمَـٰۤؤُا۟ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3360, 26, 3130, 198, 'وَلَوۡ نَزَّلۡنَـٰهُ عَلَىٰ بَعۡضِ ٱلۡأَعۡجَمِینَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3361, 26, 3131, 199, 'فَقَرَأَهُۥ عَلَیۡهِم مَّا كَانُوا۟ بِهِۦ مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3362, 26, 3132, 200, 'كَذَ ٰ⁠لِكَ سَلَكۡنَـٰهُ فِی قُلُوبِ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3363, 26, 3133, 201, 'لَا یُؤۡمِنُونَ بِهِۦ حَتَّىٰ یَرَوُا۟ ٱلۡعَذَابَ ٱلۡأَلِیمَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3364, 26, 3134, 202, 'فَیَأۡتِیَهُم بَغۡتَةࣰ وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3365, 26, 3135, 203, 'فَیَقُولُوا۟ هَلۡ نَحۡنُ مُنظَرُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3366, 26, 3136, 204, 'أَفَبِعَذَابِنَا یَسۡتَعۡجِلُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3367, 26, 3137, 205, 'أَفَرَءَیۡتَ إِن مَّتَّعۡنَـٰهُمۡ سِنِینَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3368, 26, 3138, 206, 'ثُمَّ جَاۤءَهُم مَّا كَانُوا۟ یُوعَدُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:45'),
(3369, 26, 3139, 207, 'مَاۤ أَغۡنَىٰ عَنۡهُم مَّا كَانُوا۟ یُمَتَّعُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:46'),
(3370, 26, 3140, 208, 'وَمَاۤ أَهۡلَكۡنَا مِن قَرۡیَةٍ إِلَّا لَهَا مُنذِرُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:46'),
(3371, 26, 3141, 209, 'ذِكۡرَىٰ وَمَا كُنَّا ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:46'),
(3372, 26, 3142, 210, 'وَمَا تَنَزَّلَتۡ بِهِ ٱلشَّیَـٰطِینُ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:46'),
(3373, 26, 3143, 211, 'وَمَا یَنۢبَغِی لَهُمۡ وَمَا یَسۡتَطِیعُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:46'),
(3374, 26, 3144, 212, 'إِنَّهُمۡ عَنِ ٱلسَّمۡعِ لَمَعۡزُولُونَ\n', NULL, 0, '2023-05-26 17:28:08', '2023-05-27 11:12:46'),
(3375, 26, 3145, 213, 'فَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ فَتَكُونَ مِنَ ٱلۡمُعَذَّبِینَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3376, 26, 3146, 214, 'وَأَنذِرۡ عَشِیرَتَكَ ٱلۡأَقۡرَبِینَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3377, 26, 3147, 215, 'وَٱخۡفِضۡ جَنَاحَكَ لِمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3378, 26, 3148, 216, 'فَإِنۡ عَصَوۡكَ فَقُلۡ إِنِّی بَرِیۤءࣱ مِّمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3379, 26, 3149, 217, 'وَتَوَكَّلۡ عَلَى ٱلۡعَزِیزِ ٱلرَّحِیمِ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3380, 26, 3150, 218, 'ٱلَّذِی یَرَىٰكَ حِینَ تَقُومُ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3381, 26, 3151, 219, 'وَتَقَلُّبَكَ فِی ٱلسَّـٰجِدِینَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3382, 26, 3152, 220, 'إِنَّهُۥ هُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3383, 26, 3153, 221, 'هَلۡ أُنَبِّئُكُمۡ عَلَىٰ مَن تَنَزَّلُ ٱلشَّیَـٰطِینُ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3384, 26, 3154, 222, 'تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِیمࣲ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:46'),
(3385, 26, 3155, 223, 'یُلۡقُونَ ٱلسَّمۡعَ وَأَكۡثَرُهُمۡ كَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:47'),
(3386, 26, 3156, 224, 'وَٱلشُّعَرَاۤءُ یَتَّبِعُهُمُ ٱلۡغَاوُۥنَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:47'),
(3387, 26, 3157, 225, 'أَلَمۡ تَرَ أَنَّهُمۡ فِی كُلِّ وَادࣲ یَهِیمُونَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:47'),
(3388, 26, 3158, 226, 'وَأَنَّهُمۡ یَقُولُونَ مَا لَا یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:47'),
(3389, 26, 3159, 227, 'إِلَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَذَكَرُوا۟ ٱللَّهَ كَثِیرࣰا وَٱنتَصَرُوا۟ مِنۢ بَعۡدِ مَا ظُلِمُوا۟ۗ وَسَیَعۡلَمُ ٱلَّذِینَ ظَلَمُوۤا۟ أَیَّ مُنقَلَبࣲ یَنقَلِبُونَ\n', NULL, 0, '2023-05-26 17:28:09', '2023-05-27 11:12:47'),
(3390, 25, 2856, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ تَبَارَكَ ٱلَّذِی نَزَّلَ ٱلۡفُرۡقَانَ عَلَىٰ عَبۡدِهِۦ لِیَكُونَ لِلۡعَـٰلَمِینَ نَذِیرًا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:50'),
(3391, 25, 2857, 2, 'ٱلَّذِی لَهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَلَمۡ یَتَّخِذۡ وَلَدࣰا وَلَمۡ یَكُن لَّهُۥ شَرِیكࣱ فِی ٱلۡمُلۡكِ وَخَلَقَ كُلَّ شَیۡءࣲ فَقَدَّرَهُۥ تَقۡدِیرࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:50'),
(3392, 25, 2858, 3, 'وَٱتَّخَذُوا۟ مِن دُونِهِۦۤ ءَالِهَةࣰ لَّا یَخۡلُقُونَ شَیۡـࣰٔا وَهُمۡ یُخۡلَقُونَ وَلَا یَمۡلِكُونَ لِأَنفُسِهِمۡ ضَرࣰّا وَلَا نَفۡعࣰا وَلَا یَمۡلِكُونَ مَوۡتࣰا وَلَا حَیَوٰةࣰ وَلَا نُشُورࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:50'),
(3393, 25, 2859, 4, 'وَقَالَ ٱلَّذِینَ كَفَرُوۤا۟ إِنۡ هَـٰذَاۤ إِلَّاۤ إِفۡكٌ ٱفۡتَرَىٰهُ وَأَعَانَهُۥ عَلَیۡهِ قَوۡمٌ ءَاخَرُونَۖ فَقَدۡ جَاۤءُو ظُلۡمࣰا وَزُورࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:50'),
(3394, 25, 2860, 5, 'وَقَالُوۤا۟ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ ٱكۡتَتَبَهَا فَهِیَ تُمۡلَىٰ عَلَیۡهِ بُكۡرَةࣰ وَأَصِیلࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:50');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(3395, 25, 2861, 6, 'قُلۡ أَنزَلَهُ ٱلَّذِی یَعۡلَمُ ٱلسِّرَّ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3396, 25, 2862, 7, 'وَقَالُوا۟ مَالِ هَـٰذَا ٱلرَّسُولِ یَأۡكُلُ ٱلطَّعَامَ وَیَمۡشِی فِی ٱلۡأَسۡوَاقِ لَوۡلَاۤ أُنزِلَ إِلَیۡهِ مَلَكࣱ فَیَكُونَ مَعَهُۥ نَذِیرًا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3397, 25, 2863, 8, 'أَوۡ یُلۡقَىٰۤ إِلَیۡهِ كَنزٌ أَوۡ تَكُونُ لَهُۥ جَنَّةࣱ یَأۡكُلُ مِنۡهَاۚ وَقَالَ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلࣰا مَّسۡحُورًا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3398, 25, 2864, 9, 'ٱنظُرۡ كَیۡفَ ضَرَبُوا۟ لَكَ ٱلۡأَمۡثَـٰلَ فَضَلُّوا۟ فَلَا یَسۡتَطِیعُونَ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3399, 25, 2865, 10, 'تَبَارَكَ ٱلَّذِیۤ إِن شَاۤءَ جَعَلَ لَكَ خَیۡرࣰا مِّن ذَ ٰ⁠لِكَ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ وَیَجۡعَل لَّكَ قُصُورَۢا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3400, 25, 2866, 11, 'بَلۡ كَذَّبُوا۟ بِٱلسَّاعَةِۖ وَأَعۡتَدۡنَا لِمَن كَذَّبَ بِٱلسَّاعَةِ سَعِیرًا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3401, 25, 2867, 12, 'إِذَا رَأَتۡهُم مِّن مَّكَانِۭ بَعِیدࣲ سَمِعُوا۟ لَهَا تَغَیُّظࣰا وَزَفِیرࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3402, 25, 2868, 13, 'وَإِذَاۤ أُلۡقُوا۟ مِنۡهَا مَكَانࣰا ضَیِّقࣰا مُّقَرَّنِینَ دَعَوۡا۟ هُنَالِكَ ثُبُورࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3403, 25, 2869, 14, 'لَّا تَدۡعُوا۟ ٱلۡیَوۡمَ ثُبُورࣰا وَ ٰ⁠حِدࣰا وَٱدۡعُوا۟ ثُبُورࣰا كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3404, 25, 2870, 15, 'قُلۡ أَذَ ٰ⁠لِكَ خَیۡرٌ أَمۡ جَنَّةُ ٱلۡخُلۡدِ ٱلَّتِی وُعِدَ ٱلۡمُتَّقُونَۚ كَانَتۡ لَهُمۡ جَزَاۤءࣰ وَمَصِیرࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3405, 25, 2871, 16, 'لَّهُمۡ فِیهَا مَا یَشَاۤءُونَ خَـٰلِدِینَۚ كَانَ عَلَىٰ رَبِّكَ وَعۡدࣰا مَّسۡـُٔولࣰا\n', NULL, 0, '2023-05-26 17:28:11', '2023-05-27 11:12:51'),
(3406, 25, 2872, 17, 'وَیَوۡمَ یَحۡشُرُهُمۡ وَمَا یَعۡبُدُونَ مِن دُونِ ٱللَّهِ فَیَقُولُ ءَأَنتُمۡ أَضۡلَلۡتُمۡ عِبَادِی هَـٰۤؤُلَاۤءِ أَمۡ هُمۡ ضَلُّوا۟ ٱلسَّبِیلَ\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:51'),
(3407, 25, 2873, 18, 'قَالُوا۟ سُبۡحَـٰنَكَ مَا كَانَ یَنۢبَغِی لَنَاۤ أَن نَّتَّخِذَ مِن دُونِكَ مِنۡ أَوۡلِیَاۤءَ وَلَـٰكِن مَّتَّعۡتَهُمۡ وَءَابَاۤءَهُمۡ حَتَّىٰ نَسُوا۟ ٱلذِّكۡرَ وَكَانُوا۟ قَوۡمَۢا بُورࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:51'),
(3408, 25, 2874, 19, 'فَقَدۡ كَذَّبُوكُم بِمَا تَقُولُونَ فَمَا تَسۡتَطِیعُونَ صَرۡفࣰا وَلَا نَصۡرࣰاۚ وَمَن یَظۡلِم مِّنكُمۡ نُذِقۡهُ عَذَابࣰا كَبِیرࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:51'),
(3409, 25, 2875, 20, 'وَمَاۤ أَرۡسَلۡنَا قَبۡلَكَ مِنَ ٱلۡمُرۡسَلِینَ إِلَّاۤ إِنَّهُمۡ لَیَأۡكُلُونَ ٱلطَّعَامَ وَیَمۡشُونَ فِی ٱلۡأَسۡوَاقِۗ وَجَعَلۡنَا بَعۡضَكُمۡ لِبَعۡضࣲ فِتۡنَةً أَتَصۡبِرُونَۗ وَكَانَ رَبُّكَ بَصِیرࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:51'),
(3410, 25, 2876, 21, '۞ وَقَالَ ٱلَّذِینَ لَا یَرۡجُونَ لِقَاۤءَنَا لَوۡلَاۤ أُنزِلَ عَلَیۡنَا ٱلۡمَلَـٰۤىِٕكَةُ أَوۡ نَرَىٰ رَبَّنَاۗ لَقَدِ ٱسۡتَكۡبَرُوا۟ فِیۤ أَنفُسِهِمۡ وَعَتَوۡ عُتُوࣰّا كَبِیرࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:51'),
(3411, 25, 2877, 22, 'یَوۡمَ یَرَوۡنَ ٱلۡمَلَـٰۤىِٕكَةَ لَا بُشۡرَىٰ یَوۡمَىِٕذࣲ لِّلۡمُجۡرِمِینَ وَیَقُولُونَ حِجۡرࣰا مَّحۡجُورࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:51'),
(3412, 25, 2878, 23, 'وَقَدِمۡنَاۤ إِلَىٰ مَا عَمِلُوا۟ مِنۡ عَمَلࣲ فَجَعَلۡنَـٰهُ هَبَاۤءࣰ مَّنثُورًا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:51'),
(3413, 25, 2879, 24, 'أَصۡحَـٰبُ ٱلۡجَنَّةِ یَوۡمَىِٕذٍ خَیۡرࣱ مُّسۡتَقَرࣰّا وَأَحۡسَنُ مَقِیلࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3414, 25, 2880, 25, 'وَیَوۡمَ تَشَقَّقُ ٱلسَّمَاۤءُ بِٱلۡغَمَـٰمِ وَنُزِّلَ ٱلۡمَلَـٰۤىِٕكَةُ تَنزِیلًا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3415, 25, 2881, 26, 'ٱلۡمُلۡكُ یَوۡمَىِٕذٍ ٱلۡحَقُّ لِلرَّحۡمَـٰنِۚ وَكَانَ یَوۡمًا عَلَى ٱلۡكَـٰفِرِینَ عَسِیرࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3416, 25, 2882, 27, 'وَیَوۡمَ یَعَضُّ ٱلظَّالِمُ عَلَىٰ یَدَیۡهِ یَقُولُ یَـٰلَیۡتَنِی ٱتَّخَذۡتُ مَعَ ٱلرَّسُولِ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3417, 25, 2883, 28, 'یَـٰوَیۡلَتَىٰ لَیۡتَنِی لَمۡ أَتَّخِذۡ فُلَانًا خَلِیلࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3418, 25, 2884, 29, 'لَّقَدۡ أَضَلَّنِی عَنِ ٱلذِّكۡرِ بَعۡدَ إِذۡ جَاۤءَنِیۗ وَكَانَ ٱلشَّیۡطَـٰنُ لِلۡإِنسَـٰنِ خَذُولࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3419, 25, 2885, 30, 'وَقَالَ ٱلرَّسُولُ یَـٰرَبِّ إِنَّ قَوۡمِی ٱتَّخَذُوا۟ هَـٰذَا ٱلۡقُرۡءَانَ مَهۡجُورࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3420, 25, 2886, 31, 'وَكَذَ ٰ⁠لِكَ جَعَلۡنَا لِكُلِّ نَبِیٍّ عَدُوࣰّا مِّنَ ٱلۡمُجۡرِمِینَۗ وَكَفَىٰ بِرَبِّكَ هَادِیࣰا وَنَصِیرࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3421, 25, 2887, 32, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لَوۡلَا نُزِّلَ عَلَیۡهِ ٱلۡقُرۡءَانُ جُمۡلَةࣰ وَ ٰ⁠حِدَةࣰۚ كَذَ ٰ⁠لِكَ لِنُثَبِّتَ بِهِۦ فُؤَادَكَۖ وَرَتَّلۡنَـٰهُ تَرۡتِیلࣰا\n', NULL, 0, '2023-05-26 17:28:12', '2023-05-27 11:12:52'),
(3422, 25, 2888, 33, 'وَلَا یَأۡتُونَكَ بِمَثَلٍ إِلَّا جِئۡنَـٰكَ بِٱلۡحَقِّ وَأَحۡسَنَ تَفۡسِیرًا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3423, 25, 2889, 34, 'ٱلَّذِینَ یُحۡشَرُونَ عَلَىٰ وُجُوهِهِمۡ إِلَىٰ جَهَنَّمَ أُو۟لَـٰۤىِٕكَ شَرࣱّ مَّكَانࣰا وَأَضَلُّ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3424, 25, 2890, 35, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ وَجَعَلۡنَا مَعَهُۥۤ أَخَاهُ هَـٰرُونَ وَزِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3425, 25, 2891, 36, 'فَقُلۡنَا ٱذۡهَبَاۤ إِلَى ٱلۡقَوۡمِ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا فَدَمَّرۡنَـٰهُمۡ تَدۡمِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3426, 25, 2892, 37, 'وَقَوۡمَ نُوحࣲ لَّمَّا كَذَّبُوا۟ ٱلرُّسُلَ أَغۡرَقۡنَـٰهُمۡ وَجَعَلۡنَـٰهُمۡ لِلنَّاسِ ءَایَةࣰۖ وَأَعۡتَدۡنَا لِلظَّـٰلِمِینَ عَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3427, 25, 2893, 38, 'وَعَادࣰا وَثَمُودَا۟ وَأَصۡحَـٰبَ ٱلرَّسِّ وَقُرُونَۢا بَیۡنَ ذَ ٰ⁠لِكَ كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3428, 25, 2894, 39, 'وَكُلࣰّا ضَرَبۡنَا لَهُ ٱلۡأَمۡثَـٰلَۖ وَكُلࣰّا تَبَّرۡنَا تَتۡبِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3429, 25, 2895, 40, 'وَلَقَدۡ أَتَوۡا۟ عَلَى ٱلۡقَرۡیَةِ ٱلَّتِیۤ أُمۡطِرَتۡ مَطَرَ ٱلسَّوۡءِۚ أَفَلَمۡ یَكُونُوا۟ یَرَوۡنَهَاۚ بَلۡ كَانُوا۟ لَا یَرۡجُونَ نُشُورࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3430, 25, 2896, 41, 'وَإِذَا رَأَوۡكَ إِن یَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَـٰذَا ٱلَّذِی بَعَثَ ٱللَّهُ رَسُولًا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:52'),
(3431, 25, 2897, 42, 'إِن كَادَ لَیُضِلُّنَا عَنۡ ءَالِهَتِنَا لَوۡلَاۤ أَن صَبَرۡنَا عَلَیۡهَاۚ وَسَوۡفَ یَعۡلَمُونَ حِینَ یَرَوۡنَ ٱلۡعَذَابَ مَنۡ أَضَلُّ سَبِیلًا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3432, 25, 2898, 43, 'أَرَءَیۡتَ مَنِ ٱتَّخَذَ إِلَـٰهَهُۥ هَوَىٰهُ أَفَأَنتَ تَكُونُ عَلَیۡهِ وَكِیلًا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3433, 25, 2899, 44, 'أَمۡ تَحۡسَبُ أَنَّ أَكۡثَرَهُمۡ یَسۡمَعُونَ أَوۡ یَعۡقِلُونَۚ إِنۡ هُمۡ إِلَّا كَٱلۡأَنۡعَـٰمِ بَلۡ هُمۡ أَضَلُّ سَبِیلًا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3434, 25, 2900, 45, 'أَلَمۡ تَرَ إِلَىٰ رَبِّكَ كَیۡفَ مَدَّ ٱلظِّلَّ وَلَوۡ شَاۤءَ لَجَعَلَهُۥ سَاكِنࣰا ثُمَّ جَعَلۡنَا ٱلشَّمۡسَ عَلَیۡهِ دَلِیلࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3435, 25, 2901, 46, 'ثُمَّ قَبَضۡنَـٰهُ إِلَیۡنَا قَبۡضࣰا یَسِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3436, 25, 2902, 47, 'وَهُوَ ٱلَّذِی جَعَلَ لَكُمُ ٱلَّیۡلَ لِبَاسࣰا وَٱلنَّوۡمَ سُبَاتࣰا وَجَعَلَ ٱلنَّهَارَ نُشُورࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3437, 25, 2903, 48, 'وَهُوَ ٱلَّذِیۤ أَرۡسَلَ ٱلرِّیَـٰحَ بُشۡرَۢا بَیۡنَ یَدَیۡ رَحۡمَتِهِۦۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ طَهُورࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3438, 25, 2904, 49, 'لِّنُحۡـِۧیَ بِهِۦ بَلۡدَةࣰ مَّیۡتࣰا وَنُسۡقِیَهُۥ مِمَّا خَلَقۡنَاۤ أَنۡعَـٰمࣰا وَأَنَاسِیَّ كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3439, 25, 2905, 50, 'وَلَقَدۡ صَرَّفۡنَـٰهُ بَیۡنَهُمۡ لِیَذَّكَّرُوا۟ فَأَبَىٰۤ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3440, 25, 2906, 51, 'وَلَوۡ شِئۡنَا لَبَعَثۡنَا فِی كُلِّ قَرۡیَةࣲ نَّذِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3441, 25, 2907, 52, 'فَلَا تُطِعِ ٱلۡكَـٰفِرِینَ وَجَـٰهِدۡهُم بِهِۦ جِهَادࣰا كَبِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3442, 25, 2908, 53, '۞ وَهُوَ ٱلَّذِی مَرَجَ ٱلۡبَحۡرَیۡنِ هَـٰذَا عَذۡبࣱ فُرَاتࣱ وَهَـٰذَا مِلۡحٌ أُجَاجࣱ وَجَعَلَ بَیۡنَهُمَا بَرۡزَخࣰا وَحِجۡرࣰا مَّحۡجُورࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3443, 25, 2909, 54, 'وَهُوَ ٱلَّذِی خَلَقَ مِنَ ٱلۡمَاۤءِ بَشَرࣰا فَجَعَلَهُۥ نَسَبࣰا وَصِهۡرࣰاۗ وَكَانَ رَبُّكَ قَدِیرࣰا\n', NULL, 0, '2023-05-26 17:28:13', '2023-05-27 11:12:53'),
(3444, 25, 2910, 55, 'وَیَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا یَنفَعُهُمۡ وَلَا یَضُرُّهُمۡۗ وَكَانَ ٱلۡكَافِرُ عَلَىٰ رَبِّهِۦ ظَهِیرࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:53'),
(3445, 25, 2911, 56, 'وَمَاۤ أَرۡسَلۡنَـٰكَ إِلَّا مُبَشِّرࣰا وَنَذِیرࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:53'),
(3446, 25, 2912, 57, 'قُلۡ مَاۤ أَسۡـَٔلُكُمۡ عَلَیۡهِ مِنۡ أَجۡرٍ إِلَّا مَن شَاۤءَ أَن یَتَّخِذَ إِلَىٰ رَبِّهِۦ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:53'),
(3447, 25, 2913, 58, 'وَتَوَكَّلۡ عَلَى ٱلۡحَیِّ ٱلَّذِی لَا یَمُوتُ وَسَبِّحۡ بِحَمۡدِهِۦۚ وَكَفَىٰ بِهِۦ بِذُنُوبِ عِبَادِهِۦ خَبِیرًا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:53'),
(3448, 25, 2914, 59, 'ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَا فِی سِتَّةِ أَیَّامࣲ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ ٱلرَّحۡمَـٰنُ فَسۡـَٔلۡ بِهِۦ خَبِیرࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3449, 25, 2915, 60, 'وَإِذَا قِیلَ لَهُمُ ٱسۡجُدُوا۟ لِلرَّحۡمَـٰنِ قَالُوا۟ وَمَا ٱلرَّحۡمَـٰنُ أَنَسۡجُدُ لِمَا تَأۡمُرُنَا وَزَادَهُمۡ نُفُورࣰا ۩\n', NULL, 1, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3450, 25, 2916, 61, 'تَبَارَكَ ٱلَّذِی جَعَلَ فِی ٱلسَّمَاۤءِ بُرُوجࣰا وَجَعَلَ فِیهَا سِرَ ٰ⁠جࣰا وَقَمَرࣰا مُّنِیرࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3451, 25, 2917, 62, 'وَهُوَ ٱلَّذِی جَعَلَ ٱلَّیۡلَ وَٱلنَّهَارَ خِلۡفَةࣰ لِّمَنۡ أَرَادَ أَن یَذَّكَّرَ أَوۡ أَرَادَ شُكُورࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3452, 25, 2918, 63, 'وَعِبَادُ ٱلرَّحۡمَـٰنِ ٱلَّذِینَ یَمۡشُونَ عَلَى ٱلۡأَرۡضِ هَوۡنࣰا وَإِذَا خَاطَبَهُمُ ٱلۡجَـٰهِلُونَ قَالُوا۟ سَلَـٰمࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3453, 25, 2919, 64, 'وَٱلَّذِینَ یَبِیتُونَ لِرَبِّهِمۡ سُجَّدࣰا وَقِیَـٰمࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3454, 25, 2920, 65, 'وَٱلَّذِینَ یَقُولُونَ رَبَّنَا ٱصۡرِفۡ عَنَّا عَذَابَ جَهَنَّمَۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3455, 25, 2921, 66, 'إِنَّهَا سَاۤءَتۡ مُسۡتَقَرࣰّا وَمُقَامࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3456, 25, 2922, 67, 'وَٱلَّذِینَ إِذَاۤ أَنفَقُوا۟ لَمۡ یُسۡرِفُوا۟ وَلَمۡ یَقۡتُرُوا۟ وَكَانَ بَیۡنَ ذَ ٰ⁠لِكَ قَوَامࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3457, 25, 2923, 68, 'وَٱلَّذِینَ لَا یَدۡعُونَ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ وَلَا یَقۡتُلُونَ ٱلنَّفۡسَ ٱلَّتِی حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّ وَلَا یَزۡنُونَۚ وَمَن یَفۡعَلۡ ذَ ٰ⁠لِكَ یَلۡقَ أَثَامࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3458, 25, 2924, 69, 'یُضَـٰعَفۡ لَهُ ٱلۡعَذَابُ یَوۡمَ ٱلۡقِیَـٰمَةِ وَیَخۡلُدۡ فِیهِۦ مُهَانًا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3459, 25, 2925, 70, 'إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ عَمَلࣰا صَـٰلِحࣰا فَأُو۟لَـٰۤىِٕكَ یُبَدِّلُ ٱللَّهُ سَیِّـَٔاتِهِمۡ حَسَنَـٰتࣲۗ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3460, 25, 2926, 71, 'وَمَن تَابَ وَعَمِلَ صَـٰلِحࣰا فَإِنَّهُۥ یَتُوبُ إِلَى ٱللَّهِ مَتَابࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3461, 25, 2927, 72, 'وَٱلَّذِینَ لَا یَشۡهَدُونَ ٱلزُّورَ وَإِذَا مَرُّوا۟ بِٱللَّغۡوِ مَرُّوا۟ كِرَامࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3462, 25, 2928, 73, 'وَٱلَّذِینَ إِذَا ذُكِّرُوا۟ بِـَٔایَـٰتِ رَبِّهِمۡ لَمۡ یَخِرُّوا۟ عَلَیۡهَا صُمࣰّا وَعُمۡیَانࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3463, 25, 2929, 74, 'وَٱلَّذِینَ یَقُولُونَ رَبَّنَا هَبۡ لَنَا مِنۡ أَزۡوَ ٰ⁠جِنَا وَذُرِّیَّـٰتِنَا قُرَّةَ أَعۡیُنࣲ وَٱجۡعَلۡنَا لِلۡمُتَّقِینَ إِمَامًا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3464, 25, 2930, 75, 'أُو۟لَـٰۤىِٕكَ یُجۡزَوۡنَ ٱلۡغُرۡفَةَ بِمَا صَبَرُوا۟ وَیُلَقَّوۡنَ فِیهَا تَحِیَّةࣰ وَسَلَـٰمًا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3465, 25, 2931, 76, 'خَـٰلِدِینَ فِیهَاۚ حَسُنَتۡ مُسۡتَقَرࣰّا وَمُقَامࣰا\n', NULL, 0, '2023-05-26 17:28:14', '2023-05-27 11:12:54'),
(3466, 25, 2932, 77, 'قُلۡ مَا یَعۡبَؤُا۟ بِكُمۡ رَبِّی لَوۡلَا دُعَاۤؤُكُمۡۖ فَقَدۡ كَذَّبۡتُمۡ فَسَوۡفَ یَكُونُ لِزَامَۢا\n', NULL, 0, '2023-05-26 17:28:15', '2023-05-27 11:12:55'),
(3467, 24, 2792, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ سُورَةٌ أَنزَلۡنَـٰهَا وَفَرَضۡنَـٰهَا وَأَنزَلۡنَا فِیهَاۤ ءَایَـٰتِۭ بَیِّنَـٰتࣲ لَّعَلَّكُمۡ تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3468, 24, 2793, 2, 'ٱلزَّانِیَةُ وَٱلزَّانِی فَٱجۡلِدُوا۟ كُلَّ وَ ٰ⁠حِدࣲ مِّنۡهُمَا مِا۟ئَةَ جَلۡدَةࣲۖ وَلَا تَأۡخُذۡكُم بِهِمَا رَأۡفَةࣱ فِی دِینِ ٱللَّهِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِۖ وَلۡیَشۡهَدۡ عَذَابَهُمَا طَاۤىِٕفَةࣱ مِّنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3469, 24, 2794, 3, 'ٱلزَّانِی لَا یَنكِحُ إِلَّا زَانِیَةً أَوۡ مُشۡرِكَةࣰ وَٱلزَّانِیَةُ لَا یَنكِحُهَاۤ إِلَّا زَانٍ أَوۡ مُشۡرِكࣱۚ وَحُرِّمَ ذَ ٰ⁠لِكَ عَلَى ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3470, 24, 2795, 4, 'وَٱلَّذِینَ یَرۡمُونَ ٱلۡمُحۡصَنَـٰتِ ثُمَّ لَمۡ یَأۡتُوا۟ بِأَرۡبَعَةِ شُهَدَاۤءَ فَٱجۡلِدُوهُمۡ ثَمَـٰنِینَ جَلۡدَةࣰ وَلَا تَقۡبَلُوا۟ لَهُمۡ شَهَـٰدَةً أَبَدࣰاۚ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3471, 24, 2796, 5, 'إِلَّا ٱلَّذِینَ تَابُوا۟ مِنۢ بَعۡدِ ذَ ٰ⁠لِكَ وَأَصۡلَحُوا۟ فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3472, 24, 2797, 6, 'وَٱلَّذِینَ یَرۡمُونَ أَزۡوَ ٰ⁠جَهُمۡ وَلَمۡ یَكُن لَّهُمۡ شُهَدَاۤءُ إِلَّاۤ أَنفُسُهُمۡ فَشَهَـٰدَةُ أَحَدِهِمۡ أَرۡبَعُ شَهَـٰدَ ٰ⁠تِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3473, 24, 2798, 7, 'وَٱلۡخَـٰمِسَةُ أَنَّ لَعۡنَتَ ٱللَّهِ عَلَیۡهِ إِن كَانَ مِنَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3474, 24, 2799, 8, 'وَیَدۡرَؤُا۟ عَنۡهَا ٱلۡعَذَابَ أَن تَشۡهَدَ أَرۡبَعَ شَهَـٰدَ ٰ⁠تِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3475, 24, 2800, 9, 'وَٱلۡخَـٰمِسَةَ أَنَّ غَضَبَ ٱللَّهِ عَلَیۡهَاۤ إِن كَانَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:28:32', '2023-05-27 11:13:06'),
(3476, 24, 2801, 10, 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَیۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ تَوَّابٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:06'),
(3477, 24, 2802, 11, 'إِنَّ ٱلَّذِینَ جَاۤءُو بِٱلۡإِفۡكِ عُصۡبَةࣱ مِّنكُمۡۚ لَا تَحۡسَبُوهُ شَرࣰّا لَّكُمۖ بَلۡ هُوَ خَیۡرࣱ لَّكُمۡۚ لِكُلِّ ٱمۡرِئࣲ مِّنۡهُم مَّا ٱكۡتَسَبَ مِنَ ٱلۡإِثۡمِۚ وَٱلَّذِی تَوَلَّىٰ كِبۡرَهُۥ مِنۡهُمۡ لَهُۥ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:06'),
(3478, 24, 2803, 12, 'لَّوۡلَاۤ إِذۡ سَمِعۡتُمُوهُ ظَنَّ ٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَـٰتُ بِأَنفُسِهِمۡ خَیۡرࣰا وَقَالُوا۟ هَـٰذَاۤ إِفۡكࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:06'),
(3479, 24, 2804, 13, 'لَّوۡلَا جَاۤءُو عَلَیۡهِ بِأَرۡبَعَةِ شُهَدَاۤءَۚ فَإِذۡ لَمۡ یَأۡتُوا۟ بِٱلشُّهَدَاۤءِ فَأُو۟لَـٰۤىِٕكَ عِندَ ٱللَّهِ هُمُ ٱلۡكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3480, 24, 2805, 14, 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَیۡكُمۡ وَرَحۡمَتُهُۥ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِ لَمَسَّكُمۡ فِی مَاۤ أَفَضۡتُمۡ فِیهِ عَذَابٌ عَظِیمٌ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3481, 24, 2806, 15, 'إِذۡ تَلَقَّوۡنَهُۥ بِأَلۡسِنَتِكُمۡ وَتَقُولُونَ بِأَفۡوَاهِكُم مَّا لَیۡسَ لَكُم بِهِۦ عِلۡمࣱ وَتَحۡسَبُونَهُۥ هَیِّنࣰا وَهُوَ عِندَ ٱللَّهِ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3482, 24, 2807, 16, 'وَلَوۡلَاۤ إِذۡ سَمِعۡتُمُوهُ قُلۡتُم مَّا یَكُونُ لَنَاۤ أَن نَّتَكَلَّمَ بِهَـٰذَا سُبۡحَـٰنَكَ هَـٰذَا بُهۡتَـٰنٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3483, 24, 2808, 17, 'یَعِظُكُمُ ٱللَّهُ أَن تَعُودُوا۟ لِمِثۡلِهِۦۤ أَبَدًا إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3484, 24, 2809, 18, 'وَیُبَیِّنُ ٱللَّهُ لَكُمُ ٱلۡـَٔایَـٰتِۚ وَٱللَّهُ عَلِیمٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3485, 24, 2810, 19, 'إِنَّ ٱلَّذِینَ یُحِبُّونَ أَن تَشِیعَ ٱلۡفَـٰحِشَةُ فِی ٱلَّذِینَ ءَامَنُوا۟ لَهُمۡ عَذَابٌ أَلِیمࣱ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِۚ وَٱللَّهُ یَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3486, 24, 2811, 20, 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَیۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ رَءُوفࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3487, 24, 2812, 21, '۞ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَّبِعُوا۟ خُطُوَ ٰ⁠تِ ٱلشَّیۡطَـٰنِۚ وَمَن یَتَّبِعۡ خُطُوَ ٰ⁠تِ ٱلشَّیۡطَـٰنِ فَإِنَّهُۥ یَأۡمُرُ بِٱلۡفَحۡشَاۤءِ وَٱلۡمُنكَرِۚ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَیۡكُمۡ وَرَحۡمَتُهُۥ مَا زَكَىٰ مِنكُم مِّنۡ أَحَدٍ أَبَدࣰا وَلَـٰكِنَّ ٱللَّهَ یُزَكِّی مَن یَشَاۤءُۗ وَٱللَّهُ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3488, 24, 2813, 22, 'وَلَا یَأۡتَلِ أُو۟لُوا۟ ٱلۡفَضۡلِ مِنكُمۡ وَٱلسَّعَةِ أَن یُؤۡتُوۤا۟ أُو۟لِی ٱلۡقُرۡبَىٰ وَٱلۡمَسَـٰكِینَ وَٱلۡمُهَـٰجِرِینَ فِی سَبِیلِ ٱللَّهِۖ وَلۡیَعۡفُوا۟ وَلۡیَصۡفَحُوۤا۟ۗ أَلَا تُحِبُّونَ أَن یَغۡفِرَ ٱللَّهُ لَكُمۡۚ وَٱللَّهُ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3489, 24, 2814, 23, 'إِنَّ ٱلَّذِینَ یَرۡمُونَ ٱلۡمُحۡصَنَـٰتِ ٱلۡغَـٰفِلَـٰتِ ٱلۡمُؤۡمِنَـٰتِ لُعِنُوا۟ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِ وَلَهُمۡ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3490, 24, 2815, 24, 'یَوۡمَ تَشۡهَدُ عَلَیۡهِمۡ أَلۡسِنَتُهُمۡ وَأَیۡدِیهِمۡ وَأَرۡجُلُهُم بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3491, 24, 2816, 25, 'یَوۡمَىِٕذࣲ یُوَفِّیهِمُ ٱللَّهُ دِینَهُمُ ٱلۡحَقَّ وَیَعۡلَمُونَ أَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3492, 24, 2817, 26, 'ٱلۡخَبِیثَـٰتُ لِلۡخَبِیثِینَ وَٱلۡخَبِیثُونَ لِلۡخَبِیثَـٰتِۖ وَٱلطَّیِّبَـٰتُ لِلطَّیِّبِینَ وَٱلطَّیِّبُونَ لِلطَّیِّبَـٰتِۚ أُو۟لَـٰۤىِٕكَ مُبَرَّءُونَ مِمَّا یَقُولُونَۖ لَهُم مَّغۡفِرَةࣱ وَرِزۡقࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3493, 24, 2818, 27, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَدۡخُلُوا۟ بُیُوتًا غَیۡرَ بُیُوتِكُمۡ حَتَّىٰ تَسۡتَأۡنِسُوا۟ وَتُسَلِّمُوا۟ عَلَىٰۤ أَهۡلِهَاۚ ذَ ٰ⁠لِكُمۡ خَیۡرࣱ لَّكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3494, 24, 2819, 28, 'فَإِن لَّمۡ تَجِدُوا۟ فِیهَاۤ أَحَدࣰا فَلَا تَدۡخُلُوهَا حَتَّىٰ یُؤۡذَنَ لَكُمۡۖ وَإِن قِیلَ لَكُمُ ٱرۡجِعُوا۟ فَٱرۡجِعُوا۟ۖ هُوَ أَزۡكَىٰ لَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3495, 24, 2820, 29, 'لَّیۡسَ عَلَیۡكُمۡ جُنَاحٌ أَن تَدۡخُلُوا۟ بُیُوتًا غَیۡرَ مَسۡكُونَةࣲ فِیهَا مَتَـٰعࣱ لَّكُمۡۚ وَٱللَّهُ یَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3496, 24, 2821, 30, 'قُل لِّلۡمُؤۡمِنِینَ یَغُضُّوا۟ مِنۡ أَبۡصَـٰرِهِمۡ وَیَحۡفَظُوا۟ فُرُوجَهُمۡۚ ذَ ٰ⁠لِكَ أَزۡكَىٰ لَهُمۡۚ إِنَّ ٱللَّهَ خَبِیرُۢ بِمَا یَصۡنَعُونَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3497, 24, 2822, 31, 'وَقُل لِّلۡمُؤۡمِنَـٰتِ یَغۡضُضۡنَ مِنۡ أَبۡصَـٰرِهِنَّ وَیَحۡفَظۡنَ فُرُوجَهُنَّ وَلَا یُبۡدِینَ زِینَتَهُنَّ إِلَّا مَا ظَهَرَ مِنۡهَاۖ وَلۡیَضۡرِبۡنَ بِخُمُرِهِنَّ عَلَىٰ جُیُوبِهِنَّۖ وَلَا یُبۡدِینَ زِینَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوۡ ءَابَاۤىِٕهِنَّ أَوۡ ءَابَاۤءِ بُعُولَتِهِنَّ أَوۡ أَبۡنَاۤىِٕهِنَّ أَوۡ أَبۡنَاۤءِ بُعُولَتِهِنَّ أَوۡ إِخۡوَ ٰ⁠نِهِنَّ أَوۡ بَنِیۤ إِخۡوَ ٰ⁠نِهِنَّ أَوۡ بَنِیۤ أَخَوَ ٰ⁠تِهِنَّ أَوۡ نِسَاۤىِٕهِنَّ أَوۡ مَا مَلَكَتۡ أَیۡمَـٰنُهُنَّ أَوِ ٱلتَّـٰبِعِینَ غَیۡرِ أُو۟لِی ٱلۡإِرۡبَةِ مِنَ ٱلرِّجَالِ أَوِ ٱلطِّفۡلِ ٱلَّذِینَ لَمۡ یَظۡهَرُوا۟ عَلَىٰ عَوۡرَ ٰ⁠تِ ٱلنِّسَاۤءِۖ وَلَا یَضۡرِبۡنَ بِأَرۡجُلِهِنَّ لِیُعۡلَمَ مَا یُخۡفِینَ مِن زِینَتِهِنَّۚ وَتُوبُوۤا۟ إِلَى ٱللَّهِ جَمِیعًا أَیُّهَ ٱلۡمُؤۡمِنُونَ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3498, 24, 2823, 32, 'وَأَنكِحُوا۟ ٱلۡأَیَـٰمَىٰ مِنكُمۡ وَٱلصَّـٰلِحِینَ مِنۡ عِبَادِكُمۡ وَإِمَاۤىِٕكُمۡۚ إِن یَكُونُوا۟ فُقَرَاۤءَ یُغۡنِهِمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱللَّهُ وَ ٰ⁠سِعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:28:33', '2023-05-27 11:13:07'),
(3499, 24, 2824, 33, 'وَلۡیَسۡتَعۡفِفِ ٱلَّذِینَ لَا یَجِدُونَ نِكَاحًا حَتَّىٰ یُغۡنِیَهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱلَّذِینَ یَبۡتَغُونَ ٱلۡكِتَـٰبَ مِمَّا مَلَكَتۡ أَیۡمَـٰنُكُمۡ فَكَاتِبُوهُمۡ إِنۡ عَلِمۡتُمۡ فِیهِمۡ خَیۡرࣰاۖ وَءَاتُوهُم مِّن مَّالِ ٱللَّهِ ٱلَّذِیۤ ءَاتَىٰكُمۡۚ وَلَا تُكۡرِهُوا۟ فَتَیَـٰتِكُمۡ عَلَى ٱلۡبِغَاۤءِ إِنۡ أَرَدۡنَ تَحَصُّنࣰا لِّتَبۡتَغُوا۟ عَرَضَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۚ وَمَن یُكۡرِههُّنَّ فَإِنَّ ٱللَّهَ مِنۢ بَعۡدِ إِكۡرَ ٰ⁠هِهِنَّ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3500, 24, 2825, 34, 'وَلَقَدۡ أَنزَلۡنَاۤ إِلَیۡكُمۡ ءَایَـٰتࣲ مُّبَیِّنَـٰتࣲ وَمَثَلࣰا مِّنَ ٱلَّذِینَ خَلَوۡا۟ مِن قَبۡلِكُمۡ وَمَوۡعِظَةࣰ لِّلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3501, 24, 2826, 35, '۞ ٱللَّهُ نُورُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ مَثَلُ نُورِهِۦ كَمِشۡكَوٰةࣲ فِیهَا مِصۡبَاحٌۖ ٱلۡمِصۡبَاحُ فِی زُجَاجَةٍۖ ٱلزُّجَاجَةُ كَأَنَّهَا كَوۡكَبࣱ دُرِّیࣱّ یُوقَدُ مِن شَجَرَةࣲ مُّبَـٰرَكَةࣲ زَیۡتُونَةࣲ لَّا شَرۡقِیَّةࣲ وَلَا غَرۡبِیَّةࣲ یَكَادُ زَیۡتُهَا یُضِیۤءُ وَلَوۡ لَمۡ تَمۡسَسۡهُ نَارࣱۚ نُّورٌ عَلَىٰ نُورࣲۚ یَهۡدِی ٱللَّهُ لِنُورِهِۦ مَن یَشَاۤءُۚ وَیَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَـٰلَ لِلنَّاسِۗ وَٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3502, 24, 2827, 36, 'فِی بُیُوتٍ أَذِنَ ٱللَّهُ أَن تُرۡفَعَ وَیُذۡكَرَ فِیهَا ٱسۡمُهُۥ یُسَبِّحُ لَهُۥ فِیهَا بِٱلۡغُدُوِّ وَٱلۡـَٔاصَالِ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3503, 24, 2828, 37, 'رِجَالࣱ لَّا تُلۡهِیهِمۡ تِجَـٰرَةࣱ وَلَا بَیۡعٌ عَن ذِكۡرِ ٱللَّهِ وَإِقَامِ ٱلصَّلَوٰةِ وَإِیتَاۤءِ ٱلزَّكَوٰةِ یَخَافُونَ یَوۡمࣰا تَتَقَلَّبُ فِیهِ ٱلۡقُلُوبُ وَٱلۡأَبۡصَـٰرُ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3504, 24, 2829, 38, 'لِیَجۡزِیَهُمُ ٱللَّهُ أَحۡسَنَ مَا عَمِلُوا۟ وَیَزِیدَهُم مِّن فَضۡلِهِۦۗ وَٱللَّهُ یَرۡزُقُ مَن یَشَاۤءُ بِغَیۡرِ حِسَابࣲ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3505, 24, 2830, 39, 'وَٱلَّذِینَ كَفَرُوۤا۟ أَعۡمَـٰلُهُمۡ كَسَرَابِۭ بِقِیعَةࣲ یَحۡسَبُهُ ٱلظَّمۡـَٔانُ مَاۤءً حَتَّىٰۤ إِذَا جَاۤءَهُۥ لَمۡ یَجِدۡهُ شَیۡـࣰٔا وَوَجَدَ ٱللَّهَ عِندَهُۥ فَوَفَّىٰهُ حِسَابَهُۥۗ وَٱللَّهُ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3506, 24, 2831, 40, 'أَوۡ كَظُلُمَـٰتࣲ فِی بَحۡرࣲ لُّجِّیࣲّ یَغۡشَىٰهُ مَوۡجࣱ مِّن فَوۡقِهِۦ مَوۡجࣱ مِّن فَوۡقِهِۦ سَحَابࣱۚ ظُلُمَـٰتُۢ بَعۡضُهَا فَوۡقَ بَعۡضٍ إِذَاۤ أَخۡرَجَ یَدَهُۥ لَمۡ یَكَدۡ یَرَىٰهَاۗ وَمَن لَّمۡ یَجۡعَلِ ٱللَّهُ لَهُۥ نُورࣰا فَمَا لَهُۥ مِن نُّورٍ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3507, 24, 2832, 41, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ یُسَبِّحُ لَهُۥ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَٱلطَّیۡرُ صَـٰۤفَّـٰتࣲۖ كُلࣱّ قَدۡ عَلِمَ صَلَاتَهُۥ وَتَسۡبِیحَهُۥۗ وَٱللَّهُ عَلِیمُۢ بِمَا یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3508, 24, 2833, 42, 'وَلِلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ وَإِلَى ٱللَّهِ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3509, 24, 2834, 43, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ یُزۡجِی سَحَابࣰا ثُمَّ یُؤَلِّفُ بَیۡنَهُۥ ثُمَّ یَجۡعَلُهُۥ رُكَامࣰا فَتَرَى ٱلۡوَدۡقَ یَخۡرُجُ مِنۡ خِلَـٰلِهِۦ وَیُنَزِّلُ مِنَ ٱلسَّمَاۤءِ مِن جِبَالࣲ فِیهَا مِنۢ بَرَدࣲ فَیُصِیبُ بِهِۦ مَن یَشَاۤءُ وَیَصۡرِفُهُۥ عَن مَّن یَشَاۤءُۖ یَكَادُ سَنَا بَرۡقِهِۦ یَذۡهَبُ بِٱلۡأَبۡصَـٰرِ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3510, 24, 2835, 44, 'یُقَلِّبُ ٱللَّهُ ٱلَّیۡلَ وَٱلنَّهَارَۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَعِبۡرَةࣰ لِّأُو۟لِی ٱلۡأَبۡصَـٰرِ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3511, 24, 2836, 45, 'وَٱللَّهُ خَلَقَ كُلَّ دَاۤبَّةࣲ مِّن مَّاۤءࣲۖ فَمِنۡهُم مَّن یَمۡشِی عَلَىٰ بَطۡنِهِۦ وَمِنۡهُم مَّن یَمۡشِی عَلَىٰ رِجۡلَیۡنِ وَمِنۡهُم مَّن یَمۡشِی عَلَىٰۤ أَرۡبَعࣲۚ یَخۡلُقُ ٱللَّهُ مَا یَشَاۤءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3512, 24, 2837, 46, 'لَّقَدۡ أَنزَلۡنَاۤ ءَایَـٰتࣲ مُّبَیِّنَـٰتࣲۚ وَٱللَّهُ یَهۡدِی مَن یَشَاۤءُ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3513, 24, 2838, 47, 'وَیَقُولُونَ ءَامَنَّا بِٱللَّهِ وَبِٱلرَّسُولِ وَأَطَعۡنَا ثُمَّ یَتَوَلَّىٰ فَرِیقࣱ مِّنۡهُم مِّنۢ بَعۡدِ ذَ ٰ⁠لِكَۚ وَمَاۤ أُو۟لَـٰۤىِٕكَ بِٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3514, 24, 2839, 48, 'وَإِذَا دُعُوۤا۟ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِیَحۡكُمَ بَیۡنَهُمۡ إِذَا فَرِیقࣱ مِّنۡهُم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3515, 24, 2840, 49, 'وَإِن یَكُن لَّهُمُ ٱلۡحَقُّ یَأۡتُوۤا۟ إِلَیۡهِ مُذۡعِنِینَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3516, 24, 2841, 50, 'أَفِی قُلُوبِهِم مَّرَضٌ أَمِ ٱرۡتَابُوۤا۟ أَمۡ یَخَافُونَ أَن یَحِیفَ ٱللَّهُ عَلَیۡهِمۡ وَرَسُولُهُۥۚ بَلۡ أُو۟لَـٰۤىِٕكَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3517, 24, 2842, 51, 'إِنَّمَا كَانَ قَوۡلَ ٱلۡمُؤۡمِنِینَ إِذَا دُعُوۤا۟ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِیَحۡكُمَ بَیۡنَهُمۡ أَن یَقُولُوا۟ سَمِعۡنَا وَأَطَعۡنَاۚ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:08'),
(3518, 24, 2843, 52, 'وَمَن یُطِعِ ٱللَّهَ وَرَسُولَهُۥ وَیَخۡشَ ٱللَّهَ وَیَتَّقۡهِ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡفَاۤىِٕزُونَ\n', NULL, 0, '2023-05-26 17:28:34', '2023-05-27 11:13:09'),
(3519, 24, 2844, 53, '۞ وَأَقۡسَمُوا۟ بِٱللَّهِ جَهۡدَ أَیۡمَـٰنِهِمۡ لَىِٕنۡ أَمَرۡتَهُمۡ لَیَخۡرُجُنَّۖ قُل لَّا تُقۡسِمُوا۟ۖ طَاعَةࣱ مَّعۡرُوفَةٌۚ إِنَّ ٱللَّهَ خَبِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3520, 24, 2845, 54, 'قُلۡ أَطِیعُوا۟ ٱللَّهَ وَأَطِیعُوا۟ ٱلرَّسُولَۖ فَإِن تَوَلَّوۡا۟ فَإِنَّمَا عَلَیۡهِ مَا حُمِّلَ وَعَلَیۡكُم مَّا حُمِّلۡتُمۡۖ وَإِن تُطِیعُوهُ تَهۡتَدُوا۟ۚ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَـٰغُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3521, 24, 2846, 55, 'وَعَدَ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ مِنكُمۡ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَیَسۡتَخۡلِفَنَّهُمۡ فِی ٱلۡأَرۡضِ كَمَا ٱسۡتَخۡلَفَ ٱلَّذِینَ مِن قَبۡلِهِمۡ وَلَیُمَكِّنَنَّ لَهُمۡ دِینَهُمُ ٱلَّذِی ٱرۡتَضَىٰ لَهُمۡ وَلَیُبَدِّلَنَّهُم مِّنۢ بَعۡدِ خَوۡفِهِمۡ أَمۡنࣰاۚ یَعۡبُدُونَنِی لَا یُشۡرِكُونَ بِی شَیۡـࣰٔاۚ وَمَن كَفَرَ بَعۡدَ ذَ ٰ⁠لِكَ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3522, 24, 2847, 56, 'وَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَطِیعُوا۟ ٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3523, 24, 2848, 57, 'لَا تَحۡسَبَنَّ ٱلَّذِینَ كَفَرُوا۟ مُعۡجِزِینَ فِی ٱلۡأَرۡضِۚ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَلَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3524, 24, 2849, 58, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لِیَسۡتَـٔۡذِنكُمُ ٱلَّذِینَ مَلَكَتۡ أَیۡمَـٰنُكُمۡ وَٱلَّذِینَ لَمۡ یَبۡلُغُوا۟ ٱلۡحُلُمَ مِنكُمۡ ثَلَـٰثَ مَرَّ ٰ⁠تࣲۚ مِّن قَبۡلِ صَلَوٰةِ ٱلۡفَجۡرِ وَحِینَ تَضَعُونَ ثِیَابَكُم مِّنَ ٱلظَّهِیرَةِ وَمِنۢ بَعۡدِ صَلَوٰةِ ٱلۡعِشَاۤءِۚ ثَلَـٰثُ عَوۡرَ ٰ⁠تࣲ لَّكُمۡۚ لَیۡسَ عَلَیۡكُمۡ وَلَا عَلَیۡهِمۡ جُنَاحُۢ بَعۡدَهُنَّۚ طَوَّ ٰ⁠فُونَ عَلَیۡكُم بَعۡضُكُمۡ عَلَىٰ بَعۡضࣲۚ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمُ ٱلۡـَٔایَـٰتِۗ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3525, 24, 2850, 59, 'وَإِذَا بَلَغَ ٱلۡأَطۡفَـٰلُ مِنكُمُ ٱلۡحُلُمَ فَلۡیَسۡتَـٔۡذِنُوا۟ كَمَا ٱسۡتَـٔۡذَنَ ٱلَّذِینَ مِن قَبۡلِهِمۡۚ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمۡ ءَایَـٰتِهِۦۗ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3526, 24, 2851, 60, 'وَٱلۡقَوَ ٰ⁠عِدُ مِنَ ٱلنِّسَاۤءِ ٱلَّـٰتِی لَا یَرۡجُونَ نِكَاحࣰا فَلَیۡسَ عَلَیۡهِنَّ جُنَاحٌ أَن یَضَعۡنَ ثِیَابَهُنَّ غَیۡرَ مُتَبَرِّجَـٰتِۭ بِزِینَةࣲۖ وَأَن یَسۡتَعۡفِفۡنَ خَیۡرࣱ لَّهُنَّۗ وَٱللَّهُ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3527, 24, 2852, 61, 'لَّیۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجࣱ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجࣱ وَلَا عَلَى ٱلۡمَرِیضِ حَرَجࣱ وَلَا عَلَىٰۤ أَنفُسِكُمۡ أَن تَأۡكُلُوا۟ مِنۢ بُیُوتِكُمۡ أَوۡ بُیُوتِ ءَابَاۤىِٕكُمۡ أَوۡ بُیُوتِ أُمَّهَـٰتِكُمۡ أَوۡ بُیُوتِ إِخۡوَ ٰ⁠نِكُمۡ أَوۡ بُیُوتِ أَخَوَ ٰ⁠تِكُمۡ أَوۡ بُیُوتِ أَعۡمَـٰمِكُمۡ أَوۡ بُیُوتِ عَمَّـٰتِكُمۡ أَوۡ بُیُوتِ أَخۡوَ ٰ⁠لِكُمۡ أَوۡ بُیُوتِ خَـٰلَـٰتِكُمۡ أَوۡ مَا مَلَكۡتُم مَّفَاتِحَهُۥۤ أَوۡ صَدِیقِكُمۡۚ لَیۡسَ عَلَیۡكُمۡ جُنَاحٌ أَن تَأۡكُلُوا۟ جَمِیعًا أَوۡ أَشۡتَاتࣰاۚ فَإِذَا دَخَلۡتُم بُیُوتࣰا فَسَلِّمُوا۟ عَلَىٰۤ أَنفُسِكُمۡ تَحِیَّةࣰ مِّنۡ عِندِ ٱللَّهِ مُبَـٰرَكَةࣰ طَیِّبَةࣰۚ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمُ ٱلۡـَٔایَـٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3528, 24, 2853, 62, 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِینَ ءَامَنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَإِذَا كَانُوا۟ مَعَهُۥ عَلَىٰۤ أَمۡرࣲ جَامِعࣲ لَّمۡ یَذۡهَبُوا۟ حَتَّىٰ یَسۡتَـٔۡذِنُوهُۚ إِنَّ ٱلَّذِینَ یَسۡتَـٔۡذِنُونَكَ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ یُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦۚ فَإِذَا ٱسۡتَـٔۡذَنُوكَ لِبَعۡضِ شَأۡنِهِمۡ فَأۡذَن لِّمَن شِئۡتَ مِنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمُ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3529, 24, 2854, 63, 'لَّا تَجۡعَلُوا۟ دُعَاۤءَ ٱلرَّسُولِ بَیۡنَكُمۡ كَدُعَاۤءِ بَعۡضِكُم بَعۡضࣰاۚ قَدۡ یَعۡلَمُ ٱللَّهُ ٱلَّذِینَ یَتَسَلَّلُونَ مِنكُمۡ لِوَاذࣰاۚ فَلۡیَحۡذَرِ ٱلَّذِینَ یُخَالِفُونَ عَنۡ أَمۡرِهِۦۤ أَن تُصِیبَهُمۡ فِتۡنَةٌ أَوۡ یُصِیبَهُمۡ عَذَابٌ أَلِیمٌ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3530, 24, 2855, 64, 'أَلَاۤ إِنَّ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ قَدۡ یَعۡلَمُ مَاۤ أَنتُمۡ عَلَیۡهِ وَیَوۡمَ یُرۡجَعُونَ إِلَیۡهِ فَیُنَبِّئُهُم بِمَا عَمِلُوا۟ۗ وَٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمُۢ\n', NULL, 0, '2023-05-26 17:28:35', '2023-05-27 11:13:09'),
(3531, 23, 2674, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ قَدۡ أَفۡلَحَ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3532, 23, 2675, 2, 'ٱلَّذِینَ هُمۡ فِی صَلَاتِهِمۡ خَـٰشِعُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3533, 23, 2676, 3, 'وَٱلَّذِینَ هُمۡ عَنِ ٱللَّغۡوِ مُعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3534, 23, 2677, 4, 'وَٱلَّذِینَ هُمۡ لِلزَّكَوٰةِ فَـٰعِلُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3535, 23, 2678, 5, 'وَٱلَّذِینَ هُمۡ لِفُرُوجِهِمۡ حَـٰفِظُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3536, 23, 2679, 6, 'إِلَّا عَلَىٰۤ أَزۡوَ ٰ⁠جِهِمۡ أَوۡ مَا مَلَكَتۡ أَیۡمَـٰنُهُمۡ فَإِنَّهُمۡ غَیۡرُ مَلُومِینَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3537, 23, 2680, 7, 'فَمَنِ ٱبۡتَغَىٰ وَرَاۤءَ ذَ ٰ⁠لِكَ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡعَادُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3538, 23, 2681, 8, 'وَٱلَّذِینَ هُمۡ لِأَمَـٰنَـٰتِهِمۡ وَعَهۡدِهِمۡ رَ ٰ⁠عُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3539, 23, 2682, 9, 'وَٱلَّذِینَ هُمۡ عَلَىٰ صَلَوَ ٰ⁠تِهِمۡ یُحَافِظُونَ\n', NULL, 0, '2023-05-26 17:28:37', '2023-05-27 11:13:12'),
(3540, 23, 2683, 10, 'أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡوَ ٰ⁠رِثُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3541, 23, 2684, 11, 'ٱلَّذِینَ یَرِثُونَ ٱلۡفِرۡدَوۡسَ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3542, 23, 2685, 12, 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَـٰنَ مِن سُلَـٰلَةࣲ مِّن طِینࣲ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3543, 23, 2686, 13, 'ثُمَّ جَعَلۡنَـٰهُ نُطۡفَةࣰ فِی قَرَارࣲ مَّكِینࣲ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3544, 23, 2687, 14, 'ثُمَّ خَلَقۡنَا ٱلنُّطۡفَةَ عَلَقَةࣰ فَخَلَقۡنَا ٱلۡعَلَقَةَ مُضۡغَةࣰ فَخَلَقۡنَا ٱلۡمُضۡغَةَ عِظَـٰمࣰا فَكَسَوۡنَا ٱلۡعِظَـٰمَ لَحۡمࣰا ثُمَّ أَنشَأۡنَـٰهُ خَلۡقًا ءَاخَرَۚ فَتَبَارَكَ ٱللَّهُ أَحۡسَنُ ٱلۡخَـٰلِقِینَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3545, 23, 2688, 15, 'ثُمَّ إِنَّكُم بَعۡدَ ذَ ٰ⁠لِكَ لَمَیِّتُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3546, 23, 2689, 16, 'ثُمَّ إِنَّكُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ تُبۡعَثُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3547, 23, 2690, 17, 'وَلَقَدۡ خَلَقۡنَا فَوۡقَكُمۡ سَبۡعَ طَرَاۤىِٕقَ وَمَا كُنَّا عَنِ ٱلۡخَلۡقِ غَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3548, 23, 2691, 18, 'وَأَنزَلۡنَا مِنَ ٱلسَّمَاۤءِ مَاۤءَۢ بِقَدَرࣲ فَأَسۡكَنَّـٰهُ فِی ٱلۡأَرۡضِۖ وَإِنَّا عَلَىٰ ذَهَابِۭ بِهِۦ لَقَـٰدِرُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:12'),
(3549, 23, 2692, 19, 'فَأَنشَأۡنَا لَكُم بِهِۦ جَنَّـٰتࣲ مِّن نَّخِیلࣲ وَأَعۡنَـٰبࣲ لَّكُمۡ فِیهَا فَوَ ٰ⁠كِهُ كَثِیرَةࣱ وَمِنۡهَا تَأۡكُلُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3550, 23, 2693, 20, 'وَشَجَرَةࣰ تَخۡرُجُ مِن طُورِ سَیۡنَاۤءَ تَنۢبُتُ بِٱلدُّهۡنِ وَصِبۡغࣲ لِّلۡـَٔاكِلِینَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3551, 23, 2694, 21, 'وَإِنَّ لَكُمۡ فِی ٱلۡأَنۡعَـٰمِ لَعِبۡرَةࣰۖ نُّسۡقِیكُم مِّمَّا فِی بُطُونِهَا وَلَكُمۡ فِیهَا مَنَـٰفِعُ كَثِیرَةࣱ وَمِنۡهَا تَأۡكُلُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3552, 23, 2695, 22, 'وَعَلَیۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3553, 23, 2696, 23, 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۤۚ أَفَلَا تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3554, 23, 2697, 24, 'فَقَالَ ٱلۡمَلَؤُا۟ ٱلَّذِینَ كَفَرُوا۟ مِن قَوۡمِهِۦ مَا هَـٰذَاۤ إِلَّا بَشَرࣱ مِّثۡلُكُمۡ یُرِیدُ أَن یَتَفَضَّلَ عَلَیۡكُمۡ وَلَوۡ شَاۤءَ ٱللَّهُ لَأَنزَلَ مَلَـٰۤىِٕكَةࣰ مَّا سَمِعۡنَا بِهَـٰذَا فِیۤ ءَابَاۤىِٕنَا ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3555, 23, 2698, 25, 'إِنۡ هُوَ إِلَّا رَجُلُۢ بِهِۦ جِنَّةࣱ فَتَرَبَّصُوا۟ بِهِۦ حَتَّىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3556, 23, 2699, 26, 'قَالَ رَبِّ ٱنصُرۡنِی بِمَا كَذَّبُونِ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3557, 23, 2700, 27, 'فَأَوۡحَیۡنَاۤ إِلَیۡهِ أَنِ ٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡیُنِنَا وَوَحۡیِنَا فَإِذَا جَاۤءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ فَٱسۡلُكۡ فِیهَا مِن كُلࣲّ زَوۡجَیۡنِ ٱثۡنَیۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَیۡهِ ٱلۡقَوۡلُ مِنۡهُمۡۖ وَلَا تُخَـٰطِبۡنِی فِی ٱلَّذِینَ ظَلَمُوۤا۟ إِنَّهُم مُّغۡرَقُونَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3558, 23, 2701, 28, 'فَإِذَا ٱسۡتَوَیۡتَ أَنتَ وَمَن مَّعَكَ عَلَى ٱلۡفُلۡكِ فَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی نَجَّىٰنَا مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:28:38', '2023-05-27 11:13:13'),
(3559, 23, 2702, 29, 'وَقُل رَّبِّ أَنزِلۡنِی مُنزَلࣰا مُّبَارَكࣰا وَأَنتَ خَیۡرُ ٱلۡمُنزِلِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:13'),
(3560, 23, 2703, 30, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ وَإِن كُنَّا لَمُبۡتَلِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:13'),
(3561, 23, 2704, 31, 'ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:13'),
(3562, 23, 2705, 32, 'فَأَرۡسَلۡنَا فِیهِمۡ رَسُولࣰا مِّنۡهُمۡ أَنِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۤۚ أَفَلَا تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:13'),
(3563, 23, 2706, 33, 'وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِهِ ٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِلِقَاۤءِ ٱلۡـَٔاخِرَةِ وَأَتۡرَفۡنَـٰهُمۡ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا مَا هَـٰذَاۤ إِلَّا بَشَرࣱ مِّثۡلُكُمۡ یَأۡكُلُ مِمَّا تَأۡكُلُونَ مِنۡهُ وَیَشۡرَبُ مِمَّا تَشۡرَبُونَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:13'),
(3564, 23, 2707, 34, 'وَلَىِٕنۡ أَطَعۡتُم بَشَرࣰا مِّثۡلَكُمۡ إِنَّكُمۡ إِذࣰا لَّخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:13'),
(3565, 23, 2708, 35, 'أَیَعِدُكُمۡ أَنَّكُمۡ إِذَا مِتُّمۡ وَكُنتُمۡ تُرَابࣰا وَعِظَـٰمًا أَنَّكُم مُّخۡرَجُونَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:13'),
(3566, 23, 2709, 36, '۞ هَیۡهَاتَ هَیۡهَاتَ لِمَا تُوعَدُونَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3567, 23, 2710, 37, 'إِنۡ هِیَ إِلَّا حَیَاتُنَا ٱلدُّنۡیَا نَمُوتُ وَنَحۡیَا وَمَا نَحۡنُ بِمَبۡعُوثِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3568, 23, 2711, 38, 'إِنۡ هُوَ إِلَّا رَجُلٌ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبࣰا وَمَا نَحۡنُ لَهُۥ بِمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3569, 23, 2712, 39, 'قَالَ رَبِّ ٱنصُرۡنِی بِمَا كَذَّبُونِ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3570, 23, 2713, 40, 'قَالَ عَمَّا قَلِیلࣲ لَّیُصۡبِحُنَّ نَـٰدِمِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3571, 23, 2714, 41, 'فَأَخَذَتۡهُمُ ٱلصَّیۡحَةُ بِٱلۡحَقِّ فَجَعَلۡنَـٰهُمۡ غُثَاۤءࣰۚ فَبُعۡدࣰا لِّلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3572, 23, 2715, 42, 'ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قُرُونًا ءَاخَرِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3573, 23, 2716, 43, 'مَا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا یَسۡتَـٔۡخِرُونَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3574, 23, 2717, 44, 'ثُمَّ أَرۡسَلۡنَا رُسُلَنَا تَتۡرَاۖ كُلَّ مَا جَاۤءَ أُمَّةࣰ رَّسُولُهَا كَذَّبُوهُۖ فَأَتۡبَعۡنَا بَعۡضَهُم بَعۡضࣰا وَجَعَلۡنَـٰهُمۡ أَحَادِیثَۚ فَبُعۡدࣰا لِّقَوۡمࣲ لَّا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3575, 23, 2718, 45, 'ثُمَّ أَرۡسَلۡنَا مُوسَىٰ وَأَخَاهُ هَـٰرُونَ بِـَٔایَـٰتِنَا وَسُلۡطَـٰنࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3576, 23, 2719, 46, 'إِلَىٰ فِرۡعَوۡنَ وَمَلَإِی۟هِۦ فَٱسۡتَكۡبَرُوا۟ وَكَانُوا۟ قَوۡمًا عَالِینَ\n', NULL, 0, '2023-05-26 17:28:39', '2023-05-27 11:13:14'),
(3577, 23, 2720, 47, 'فَقَالُوۤا۟ أَنُؤۡمِنُ لِبَشَرَیۡنِ مِثۡلِنَا وَقَوۡمُهُمَا لَنَا عَـٰبِدُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:14'),
(3578, 23, 2721, 48, 'فَكَذَّبُوهُمَا فَكَانُوا۟ مِنَ ٱلۡمُهۡلَكِینَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:14'),
(3579, 23, 2722, 49, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ لَعَلَّهُمۡ یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:14'),
(3580, 23, 2723, 50, 'وَجَعَلۡنَا ٱبۡنَ مَرۡیَمَ وَأُمَّهُۥۤ ءَایَةࣰ وَءَاوَیۡنَـٰهُمَاۤ إِلَىٰ رَبۡوَةࣲ ذَاتِ قَرَارࣲ وَمَعِینࣲ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:14'),
(3581, 23, 2724, 51, 'یَـٰۤأَیُّهَا ٱلرُّسُلُ كُلُوا۟ مِنَ ٱلطَّیِّبَـٰتِ وَٱعۡمَلُوا۟ صَـٰلِحًاۖ إِنِّی بِمَا تَعۡمَلُونَ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:14'),
(3582, 23, 2725, 52, 'وَإِنَّ هَـٰذِهِۦۤ أُمَّتُكُمۡ أُمَّةࣰ وَ ٰ⁠حِدَةࣰ وَأَنَا۠ رَبُّكُمۡ فَٱتَّقُونِ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:14'),
(3583, 23, 2726, 53, 'فَتَقَطَّعُوۤا۟ أَمۡرَهُم بَیۡنَهُمۡ زُبُرࣰاۖ كُلُّ حِزۡبِۭ بِمَا لَدَیۡهِمۡ فَرِحُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:14'),
(3584, 23, 2727, 54, 'فَذَرۡهُمۡ فِی غَمۡرَتِهِمۡ حَتَّىٰ حِینٍ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:15'),
(3585, 23, 2728, 55, 'أَیَحۡسَبُونَ أَنَّمَا نُمِدُّهُم بِهِۦ مِن مَّالࣲ وَبَنِینَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:15'),
(3586, 23, 2729, 56, 'نُسَارِعُ لَهُمۡ فِی ٱلۡخَیۡرَ ٰ⁠تِۚ بَل لَّا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:15'),
(3587, 23, 2730, 57, 'إِنَّ ٱلَّذِینَ هُم مِّنۡ خَشۡیَةِ رَبِّهِم مُّشۡفِقُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:15'),
(3588, 23, 2731, 58, 'وَٱلَّذِینَ هُم بِـَٔایَـٰتِ رَبِّهِمۡ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:15'),
(3589, 23, 2732, 59, 'وَٱلَّذِینَ هُم بِرَبِّهِمۡ لَا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:15'),
(3590, 23, 2733, 60, 'وَٱلَّذِینَ یُؤۡتُونَ مَاۤ ءَاتَوا۟ وَّقُلُوبُهُمۡ وَجِلَةٌ أَنَّهُمۡ إِلَىٰ رَبِّهِمۡ رَ ٰ⁠جِعُونَ\n', NULL, 0, '2023-05-26 17:28:40', '2023-05-27 11:13:15'),
(3591, 23, 2734, 61, 'أُو۟لَـٰۤىِٕكَ یُسَـٰرِعُونَ فِی ٱلۡخَیۡرَ ٰ⁠تِ وَهُمۡ لَهَا سَـٰبِقُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3592, 23, 2735, 62, 'وَلَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۚ وَلَدَیۡنَا كِتَـٰبࣱ یَنطِقُ بِٱلۡحَقِّ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3593, 23, 2736, 63, 'بَلۡ قُلُوبُهُمۡ فِی غَمۡرَةࣲ مِّنۡ هَـٰذَا وَلَهُمۡ أَعۡمَـٰلࣱ مِّن دُونِ ذَ ٰ⁠لِكَ هُمۡ لَهَا عَـٰمِلُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3594, 23, 2737, 64, 'حَتَّىٰۤ إِذَاۤ أَخَذۡنَا مُتۡرَفِیهِم بِٱلۡعَذَابِ إِذَا هُمۡ یَجۡـَٔرُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3595, 23, 2738, 65, 'لَا تَجۡـَٔرُوا۟ ٱلۡیَوۡمَۖ إِنَّكُم مِّنَّا لَا تُنصَرُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3596, 23, 2739, 66, 'قَدۡ كَانَتۡ ءَایَـٰتِی تُتۡلَىٰ عَلَیۡكُمۡ فَكُنتُمۡ عَلَىٰۤ أَعۡقَـٰبِكُمۡ تَنكِصُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3597, 23, 2740, 67, 'مُسۡتَكۡبِرِینَ بِهِۦ سَـٰمِرࣰا تَهۡجُرُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3598, 23, 2741, 68, 'أَفَلَمۡ یَدَّبَّرُوا۟ ٱلۡقَوۡلَ أَمۡ جَاۤءَهُم مَّا لَمۡ یَأۡتِ ءَابَاۤءَهُمُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3599, 23, 2742, 69, 'أَمۡ لَمۡ یَعۡرِفُوا۟ رَسُولَهُمۡ فَهُمۡ لَهُۥ مُنكِرُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3600, 23, 2743, 70, 'أَمۡ یَقُولُونَ بِهِۦ جِنَّةُۢۚ بَلۡ جَاۤءَهُم بِٱلۡحَقِّ وَأَكۡثَرُهُمۡ لِلۡحَقِّ كَـٰرِهُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3601, 23, 2744, 71, 'وَلَوِ ٱتَّبَعَ ٱلۡحَقُّ أَهۡوَاۤءَهُمۡ لَفَسَدَتِ ٱلسَّمَـٰوَ ٰ⁠تُ وَٱلۡأَرۡضُ وَمَن فِیهِنَّۚ بَلۡ أَتَیۡنَـٰهُم بِذِكۡرِهِمۡ فَهُمۡ عَن ذِكۡرِهِم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:15'),
(3602, 23, 2745, 72, 'أَمۡ تَسۡـَٔلُهُمۡ خَرۡجࣰا فَخَرَاجُ رَبِّكَ خَیۡرࣱۖ وَهُوَ خَیۡرُ ٱلرَّ ٰ⁠زِقِینَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3603, 23, 2746, 73, 'وَإِنَّكَ لَتَدۡعُوهُمۡ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3604, 23, 2747, 74, 'وَإِنَّ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ عَنِ ٱلصِّرَ ٰ⁠طِ لَنَـٰكِبُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3605, 23, 2748, 75, '۞ وَلَوۡ رَحِمۡنَـٰهُمۡ وَكَشَفۡنَا مَا بِهِم مِّن ضُرࣲّ لَّلَجُّوا۟ فِی طُغۡیَـٰنِهِمۡ یَعۡمَهُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3606, 23, 2749, 76, 'وَلَقَدۡ أَخَذۡنَـٰهُم بِٱلۡعَذَابِ فَمَا ٱسۡتَكَانُوا۟ لِرَبِّهِمۡ وَمَا یَتَضَرَّعُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3607, 23, 2750, 77, 'حَتَّىٰۤ إِذَا فَتَحۡنَا عَلَیۡهِم بَابࣰا ذَا عَذَابࣲ شَدِیدٍ إِذَا هُمۡ فِیهِ مُبۡلِسُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3608, 23, 2751, 78, 'وَهُوَ ٱلَّذِیۤ أَنشَأَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَـٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِیلࣰا مَّا تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3609, 23, 2752, 79, 'وَهُوَ ٱلَّذِی ذَرَأَكُمۡ فِی ٱلۡأَرۡضِ وَإِلَیۡهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3610, 23, 2753, 80, 'وَهُوَ ٱلَّذِی یُحۡیِۦ وَیُمِیتُ وَلَهُ ٱخۡتِلَـٰفُ ٱلَّیۡلِ وَٱلنَّهَارِۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3611, 23, 2754, 81, 'بَلۡ قَالُوا۟ مِثۡلَ مَا قَالَ ٱلۡأَوَّلُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3612, 23, 2755, 82, 'قَالُوۤا۟ أَءِذَا مِتۡنَا وَكُنَّا تُرَابࣰا وَعِظَـٰمًا أَءِنَّا لَمَبۡعُوثُونَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3613, 23, 2756, 83, 'لَقَدۡ وُعِدۡنَا نَحۡنُ وَءَابَاۤؤُنَا هَـٰذَا مِن قَبۡلُ إِنۡ هَـٰذَاۤ إِلَّاۤ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:28:41', '2023-05-27 11:13:16'),
(3614, 23, 2757, 84, 'قُل لِّمَنِ ٱلۡأَرۡضُ وَمَن فِیهَاۤ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:16'),
(3615, 23, 2758, 85, 'سَیَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:16'),
(3616, 23, 2759, 86, 'قُلۡ مَن رَّبُّ ٱلسَّمَـٰوَ ٰ⁠تِ ٱلسَّبۡعِ وَرَبُّ ٱلۡعَرۡشِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:16'),
(3617, 23, 2760, 87, 'سَیَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:16'),
(3618, 23, 2761, 88, 'قُلۡ مَنۢ بِیَدِهِۦ مَلَكُوتُ كُلِّ شَیۡءࣲ وَهُوَ یُجِیرُ وَلَا یُجَارُ عَلَیۡهِ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:16'),
(3619, 23, 2762, 89, 'سَیَقُولُونَ لِلَّهِۚ قُلۡ فَأَنَّىٰ تُسۡحَرُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:16'),
(3620, 23, 2763, 90, 'بَلۡ أَتَیۡنَـٰهُم بِٱلۡحَقِّ وَإِنَّهُمۡ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:17'),
(3621, 23, 2764, 91, 'مَا ٱتَّخَذَ ٱللَّهُ مِن وَلَدࣲ وَمَا كَانَ مَعَهُۥ مِنۡ إِلَـٰهٍۚ إِذࣰا لَّذَهَبَ كُلُّ إِلَـٰهِۭ بِمَا خَلَقَ وَلَعَلَا بَعۡضُهُمۡ عَلَىٰ بَعۡضࣲۚ سُبۡحَـٰنَ ٱللَّهِ عَمَّا یَصِفُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:17'),
(3622, 23, 2765, 92, 'عَـٰلِمِ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ فَتَعَـٰلَىٰ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:17'),
(3623, 23, 2766, 93, 'قُل رَّبِّ إِمَّا تُرِیَنِّی مَا یُوعَدُونَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:17'),
(3624, 23, 2767, 94, 'رَبِّ فَلَا تَجۡعَلۡنِی فِی ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:28:42', '2023-05-27 11:13:17'),
(3625, 23, 2768, 95, 'وَإِنَّا عَلَىٰۤ أَن نُّرِیَكَ مَا نَعِدُهُمۡ لَقَـٰدِرُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3626, 23, 2769, 96, 'ٱدۡفَعۡ بِٱلَّتِی هِیَ أَحۡسَنُ ٱلسَّیِّئَةَۚ نَحۡنُ أَعۡلَمُ بِمَا یَصِفُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3627, 23, 2770, 97, 'وَقُل رَّبِّ أَعُوذُ بِكَ مِنۡ هَمَزَ ٰ⁠تِ ٱلشَّیَـٰطِینِ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3628, 23, 2771, 98, 'وَأَعُوذُ بِكَ رَبِّ أَن یَحۡضُرُونِ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3629, 23, 2772, 99, 'حَتَّىٰۤ إِذَا جَاۤءَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ رَبِّ ٱرۡجِعُونِ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3630, 23, 2773, 100, 'لَعَلِّیۤ أَعۡمَلُ صَـٰلِحࣰا فِیمَا تَرَكۡتُۚ كَلَّاۤۚ إِنَّهَا كَلِمَةٌ هُوَ قَاۤىِٕلُهَاۖ وَمِن وَرَاۤىِٕهِم بَرۡزَخٌ إِلَىٰ یَوۡمِ یُبۡعَثُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3631, 23, 2774, 101, 'فَإِذَا نُفِخَ فِی ٱلصُّورِ فَلَاۤ أَنسَابَ بَیۡنَهُمۡ یَوۡمَىِٕذࣲ وَلَا یَتَسَاۤءَلُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3632, 23, 2775, 102, 'فَمَن ثَقُلَتۡ مَوَ ٰ⁠زِینُهُۥ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3633, 23, 2776, 103, 'وَمَنۡ خَفَّتۡ مَوَ ٰ⁠زِینُهُۥ فَأُو۟لَـٰۤىِٕكَ ٱلَّذِینَ خَسِرُوۤا۟ أَنفُسَهُمۡ فِی جَهَنَّمَ خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3634, 23, 2777, 104, 'تَلۡفَحُ وُجُوهَهُمُ ٱلنَّارُ وَهُمۡ فِیهَا كَـٰلِحُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3635, 23, 2778, 105, 'أَلَمۡ تَكُنۡ ءَایَـٰتِی تُتۡلَىٰ عَلَیۡكُمۡ فَكُنتُم بِهَا تُكَذِّبُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3636, 23, 2779, 106, 'قَالُوا۟ رَبَّنَا غَلَبَتۡ عَلَیۡنَا شِقۡوَتُنَا وَكُنَّا قَوۡمࣰا ضَاۤلِّینَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3637, 23, 2780, 107, 'رَبَّنَاۤ أَخۡرِجۡنَا مِنۡهَا فَإِنۡ عُدۡنَا فَإِنَّا ظَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:17'),
(3638, 23, 2781, 108, 'قَالَ ٱخۡسَـُٔوا۟ فِیهَا وَلَا تُكَلِّمُونِ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:18'),
(3639, 23, 2782, 109, 'إِنَّهُۥ كَانَ فَرِیقࣱ مِّنۡ عِبَادِی یَقُولُونَ رَبَّنَاۤ ءَامَنَّا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَا وَأَنتَ خَیۡرُ ٱلرَّ ٰ⁠حِمِینَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:18'),
(3640, 23, 2783, 110, 'فَٱتَّخَذۡتُمُوهُمۡ سِخۡرِیًّا حَتَّىٰۤ أَنسَوۡكُمۡ ذِكۡرِی وَكُنتُم مِّنۡهُمۡ تَضۡحَكُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:18'),
(3641, 23, 2784, 111, 'إِنِّی جَزَیۡتُهُمُ ٱلۡیَوۡمَ بِمَا صَبَرُوۤا۟ أَنَّهُمۡ هُمُ ٱلۡفَاۤىِٕزُونَ\n', NULL, 0, '2023-05-26 17:28:43', '2023-05-27 11:13:18'),
(3642, 23, 2785, 112, 'قَـٰلَ كَمۡ لَبِثۡتُمۡ فِی ٱلۡأَرۡضِ عَدَدَ سِنِینَ\n', NULL, 0, '2023-05-26 17:28:44', '2023-05-27 11:13:18'),
(3643, 23, 2786, 113, 'قَالُوا۟ لَبِثۡنَا یَوۡمًا أَوۡ بَعۡضَ یَوۡمࣲ فَسۡـَٔلِ ٱلۡعَاۤدِّینَ\n', NULL, 0, '2023-05-26 17:28:44', '2023-05-27 11:13:18'),
(3644, 23, 2787, 114, 'قَـٰلَ إِن لَّبِثۡتُمۡ إِلَّا قَلِیلࣰاۖ لَّوۡ أَنَّكُمۡ كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:28:44', '2023-05-27 11:13:18'),
(3645, 23, 2788, 115, 'أَفَحَسِبۡتُمۡ أَنَّمَا خَلَقۡنَـٰكُمۡ عَبَثࣰا وَأَنَّكُمۡ إِلَیۡنَا لَا تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:28:44', '2023-05-27 11:13:18'),
(3646, 23, 2789, 116, 'فَتَعَـٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۖ لَاۤ إِلَـٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡكَرِیمِ\n', NULL, 0, '2023-05-26 17:28:44', '2023-05-27 11:13:18');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(3647, 23, 2790, 117, 'وَمَن یَدۡعُ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ لَا بُرۡهَـٰنَ لَهُۥ بِهِۦ فَإِنَّمَا حِسَابُهُۥ عِندَ رَبِّهِۦۤۚ إِنَّهُۥ لَا یُفۡلِحُ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:28:44', '2023-05-27 11:13:18'),
(3648, 23, 2791, 118, 'وَقُل رَّبِّ ٱغۡفِرۡ وَٱرۡحَمۡ وَأَنتَ خَیۡرُ ٱلرَّ ٰ⁠حِمِینَ\n', NULL, 0, '2023-05-26 17:28:44', '2023-05-27 11:13:18'),
(3649, 22, 2596, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلنَّاسُ ٱتَّقُوا۟ رَبَّكُمۡۚ إِنَّ زَلۡزَلَةَ ٱلسَّاعَةِ شَیۡءٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3650, 22, 2597, 2, 'یَوۡمَ تَرَوۡنَهَا تَذۡهَلُ كُلُّ مُرۡضِعَةٍ عَمَّاۤ أَرۡضَعَتۡ وَتَضَعُ كُلُّ ذَاتِ حَمۡلٍ حَمۡلَهَا وَتَرَى ٱلنَّاسَ سُكَـٰرَىٰ وَمَا هُم بِسُكَـٰرَىٰ وَلَـٰكِنَّ عَذَابَ ٱللَّهِ شَدِیدࣱ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3651, 22, 2598, 3, 'وَمِنَ ٱلنَّاسِ مَن یُجَـٰدِلُ فِی ٱللَّهِ بِغَیۡرِ عِلۡمࣲ وَیَتَّبِعُ كُلَّ شَیۡطَـٰنࣲ مَّرِیدࣲ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3652, 22, 2599, 4, 'كُتِبَ عَلَیۡهِ أَنَّهُۥ مَن تَوَلَّاهُ فَأَنَّهُۥ یُضِلُّهُۥ وَیَهۡدِیهِ إِلَىٰ عَذَابِ ٱلسَّعِیرِ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3653, 22, 2600, 5, 'یَـٰۤأَیُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِی رَیۡبࣲ مِّنَ ٱلۡبَعۡثِ فَإِنَّا خَلَقۡنَـٰكُم مِّن تُرَابࣲ ثُمَّ مِن نُّطۡفَةࣲ ثُمَّ مِنۡ عَلَقَةࣲ ثُمَّ مِن مُّضۡغَةࣲ مُّخَلَّقَةࣲ وَغَیۡرِ مُخَلَّقَةࣲ لِّنُبَیِّنَ لَكُمۡۚ وَنُقِرُّ فِی ٱلۡأَرۡحَامِ مَا نَشَاۤءُ إِلَىٰۤ أَجَلࣲ مُّسَمࣰّى ثُمَّ نُخۡرِجُكُمۡ طِفۡلࣰا ثُمَّ لِتَبۡلُغُوۤا۟ أَشُدَّكُمۡۖ وَمِنكُم مَّن یُتَوَفَّىٰ وَمِنكُم مَّن یُرَدُّ إِلَىٰۤ أَرۡذَلِ ٱلۡعُمُرِ لِكَیۡلَا یَعۡلَمَ مِنۢ بَعۡدِ عِلۡمࣲ شَیۡـࣰٔاۚ وَتَرَى ٱلۡأَرۡضَ هَامِدَةࣰ فَإِذَاۤ أَنزَلۡنَا عَلَیۡهَا ٱلۡمَاۤءَ ٱهۡتَزَّتۡ وَرَبَتۡ وَأَنۢبَتَتۡ مِن كُلِّ زَوۡجِۭ بَهِیجࣲ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3654, 22, 2601, 6, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّهُۥ یُحۡیِ ٱلۡمَوۡتَىٰ وَأَنَّهُۥ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3655, 22, 2602, 7, 'وَأَنَّ ٱلسَّاعَةَ ءَاتِیَةࣱ لَّا رَیۡبَ فِیهَا وَأَنَّ ٱللَّهَ یَبۡعَثُ مَن فِی ٱلۡقُبُورِ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3656, 22, 2603, 8, 'وَمِنَ ٱلنَّاسِ مَن یُجَـٰدِلُ فِی ٱللَّهِ بِغَیۡرِ عِلۡمࣲ وَلَا هُدࣰى وَلَا كِتَـٰبࣲ مُّنِیرࣲ\n', NULL, 0, '2023-05-26 17:28:56', '2023-05-27 11:13:23'),
(3657, 22, 2604, 9, 'ثَانِیَ عِطۡفِهِۦ لِیُضِلَّ عَن سَبِیلِ ٱللَّهِۖ لَهُۥ فِی ٱلدُّنۡیَا خِزۡیࣱۖ وَنُذِیقُهُۥ یَوۡمَ ٱلۡقِیَـٰمَةِ عَذَابَ ٱلۡحَرِیقِ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:23'),
(3658, 22, 2605, 10, 'ذَ ٰ⁠لِكَ بِمَا قَدَّمَتۡ یَدَاكَ وَأَنَّ ٱللَّهَ لَیۡسَ بِظَلَّـٰمࣲ لِّلۡعَبِیدِ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:23'),
(3659, 22, 2606, 11, 'وَمِنَ ٱلنَّاسِ مَن یَعۡبُدُ ٱللَّهَ عَلَىٰ حَرۡفࣲۖ فَإِنۡ أَصَابَهُۥ خَیۡرٌ ٱطۡمَأَنَّ بِهِۦۖ وَإِنۡ أَصَابَتۡهُ فِتۡنَةٌ ٱنقَلَبَ عَلَىٰ وَجۡهِهِۦ خَسِرَ ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةَۚ ذَ ٰ⁠لِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:23'),
(3660, 22, 2607, 12, 'یَدۡعُوا۟ مِن دُونِ ٱللَّهِ مَا لَا یَضُرُّهُۥ وَمَا لَا یَنفَعُهُۥۚ ذَ ٰ⁠لِكَ هُوَ ٱلضَّلَـٰلُ ٱلۡبَعِیدُ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:23'),
(3661, 22, 2608, 13, 'یَدۡعُوا۟ لَمَن ضَرُّهُۥۤ أَقۡرَبُ مِن نَّفۡعِهِۦۚ لَبِئۡسَ ٱلۡمَوۡلَىٰ وَلَبِئۡسَ ٱلۡعَشِیرُ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:23'),
(3662, 22, 2609, 14, 'إِنَّ ٱللَّهَ یُدۡخِلُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۚ إِنَّ ٱللَّهَ یَفۡعَلُ مَا یُرِیدُ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:23'),
(3663, 22, 2610, 15, 'مَن كَانَ یَظُنُّ أَن لَّن یَنصُرَهُ ٱللَّهُ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِ فَلۡیَمۡدُدۡ بِسَبَبٍ إِلَى ٱلسَّمَاۤءِ ثُمَّ لۡیَقۡطَعۡ فَلۡیَنظُرۡ هَلۡ یُذۡهِبَنَّ كَیۡدُهُۥ مَا یَغِیظُ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:24'),
(3664, 22, 2611, 16, 'وَكَذَ ٰ⁠لِكَ أَنزَلۡنَـٰهُ ءَایَـٰتِۭ بَیِّنَـٰتࣲ وَأَنَّ ٱللَّهَ یَهۡدِی مَن یُرِیدُ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:24'),
(3665, 22, 2612, 17, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَٱلَّذِینَ هَادُوا۟ وَٱلصَّـٰبِـِٔینَ وَٱلنَّصَـٰرَىٰ وَٱلۡمَجُوسَ وَٱلَّذِینَ أَشۡرَكُوۤا۟ إِنَّ ٱللَّهَ یَفۡصِلُ بَیۡنَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ شَهِیدٌ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:24'),
(3666, 22, 2613, 18, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ یَسۡجُدُ لَهُۥ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَن فِی ٱلۡأَرۡضِ وَٱلشَّمۡسُ وَٱلۡقَمَرُ وَٱلنُّجُومُ وَٱلۡجِبَالُ وَٱلشَّجَرُ وَٱلدَّوَاۤبُّ وَكَثِیرࣱ مِّنَ ٱلنَّاسِۖ وَكَثِیرٌ حَقَّ عَلَیۡهِ ٱلۡعَذَابُۗ وَمَن یُهِنِ ٱللَّهُ فَمَا لَهُۥ مِن مُّكۡرِمٍۚ إِنَّ ٱللَّهَ یَفۡعَلُ مَا یَشَاۤءُ ۩\n', NULL, 1, '2023-05-26 17:28:57', '2023-05-27 11:13:24'),
(3667, 22, 2614, 19, '۞ هَـٰذَانِ خَصۡمَانِ ٱخۡتَصَمُوا۟ فِی رَبِّهِمۡۖ فَٱلَّذِینَ كَفَرُوا۟ قُطِّعَتۡ لَهُمۡ ثِیَابࣱ مِّن نَّارࣲ یُصَبُّ مِن فَوۡقِ رُءُوسِهِمُ ٱلۡحَمِیمُ\n', NULL, 0, '2023-05-26 17:28:57', '2023-05-27 11:13:24'),
(3668, 22, 2615, 20, 'یُصۡهَرُ بِهِۦ مَا فِی بُطُونِهِمۡ وَٱلۡجُلُودُ\n', NULL, 0, '2023-05-26 17:28:58', '2023-05-27 11:13:24'),
(3669, 22, 2616, 21, 'وَلَهُم مَّقَـٰمِعُ مِنۡ حَدِیدࣲ\n', NULL, 0, '2023-05-26 17:28:58', '2023-05-27 11:13:24'),
(3670, 22, 2617, 22, 'كُلَّمَاۤ أَرَادُوۤا۟ أَن یَخۡرُجُوا۟ مِنۡهَا مِنۡ غَمٍّ أُعِیدُوا۟ فِیهَا وَذُوقُوا۟ عَذَابَ ٱلۡحَرِیقِ\n', NULL, 0, '2023-05-26 17:28:58', '2023-05-27 11:13:24'),
(3671, 22, 2618, 23, 'إِنَّ ٱللَّهَ یُدۡخِلُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ یُحَلَّوۡنَ فِیهَا مِنۡ أَسَاوِرَ مِن ذَهَبࣲ وَلُؤۡلُؤࣰاۖ وَلِبَاسُهُمۡ فِیهَا حَرِیرࣱ\n', NULL, 0, '2023-05-26 17:28:58', '2023-05-27 11:13:24'),
(3672, 22, 2619, 24, 'وَهُدُوۤا۟ إِلَى ٱلطَّیِّبِ مِنَ ٱلۡقَوۡلِ وَهُدُوۤا۟ إِلَىٰ صِرَ ٰ⁠طِ ٱلۡحَمِیدِ\n', NULL, 0, '2023-05-26 17:28:58', '2023-05-27 11:13:24'),
(3673, 22, 2620, 25, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ وَیَصُدُّونَ عَن سَبِیلِ ٱللَّهِ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ ٱلَّذِی جَعَلۡنَـٰهُ لِلنَّاسِ سَوَاۤءً ٱلۡعَـٰكِفُ فِیهِ وَٱلۡبَادِۚ وَمَن یُرِدۡ فِیهِ بِإِلۡحَادِۭ بِظُلۡمࣲ نُّذِقۡهُ مِنۡ عَذَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:28:58', '2023-05-27 11:13:24'),
(3674, 22, 2621, 26, 'وَإِذۡ بَوَّأۡنَا لِإِبۡرَ ٰ⁠هِیمَ مَكَانَ ٱلۡبَیۡتِ أَن لَّا تُشۡرِكۡ بِی شَیۡـࣰٔا وَطَهِّرۡ بَیۡتِیَ لِلطَّاۤىِٕفِینَ وَٱلۡقَاۤىِٕمِینَ وَٱلرُّكَّعِ ٱلسُّجُودِ\n', NULL, 0, '2023-05-26 17:28:58', '2023-05-27 11:13:24'),
(3675, 22, 2622, 27, 'وَأَذِّن فِی ٱلنَّاسِ بِٱلۡحَجِّ یَأۡتُوكَ رِجَالࣰا وَعَلَىٰ كُلِّ ضَامِرࣲ یَأۡتِینَ مِن كُلِّ فَجٍّ عَمِیقࣲ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3676, 22, 2623, 28, 'لِّیَشۡهَدُوا۟ مَنَـٰفِعَ لَهُمۡ وَیَذۡكُرُوا۟ ٱسۡمَ ٱللَّهِ فِیۤ أَیَّامࣲ مَّعۡلُومَـٰتٍ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِیمَةِ ٱلۡأَنۡعَـٰمِۖ فَكُلُوا۟ مِنۡهَا وَأَطۡعِمُوا۟ ٱلۡبَاۤىِٕسَ ٱلۡفَقِیرَ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3677, 22, 2624, 29, 'ثُمَّ لۡیَقۡضُوا۟ تَفَثَهُمۡ وَلۡیُوفُوا۟ نُذُورَهُمۡ وَلۡیَطَّوَّفُوا۟ بِٱلۡبَیۡتِ ٱلۡعَتِیقِ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3678, 22, 2625, 30, 'ذَ ٰ⁠لِكَۖ وَمَن یُعَظِّمۡ حُرُمَـٰتِ ٱللَّهِ فَهُوَ خَیۡرࣱ لَّهُۥ عِندَ رَبِّهِۦۗ وَأُحِلَّتۡ لَكُمُ ٱلۡأَنۡعَـٰمُ إِلَّا مَا یُتۡلَىٰ عَلَیۡكُمۡۖ فَٱجۡتَنِبُوا۟ ٱلرِّجۡسَ مِنَ ٱلۡأَوۡثَـٰنِ وَٱجۡتَنِبُوا۟ قَوۡلَ ٱلزُّورِ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3679, 22, 2626, 31, 'حُنَفَاۤءَ لِلَّهِ غَیۡرَ مُشۡرِكِینَ بِهِۦۚ وَمَن یُشۡرِكۡ بِٱللَّهِ فَكَأَنَّمَا خَرَّ مِنَ ٱلسَّمَاۤءِ فَتَخۡطَفُهُ ٱلطَّیۡرُ أَوۡ تَهۡوِی بِهِ ٱلرِّیحُ فِی مَكَانࣲ سَحِیقࣲ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3680, 22, 2627, 32, 'ذَ ٰ⁠لِكَۖ وَمَن یُعَظِّمۡ شَعَـٰۤىِٕرَ ٱللَّهِ فَإِنَّهَا مِن تَقۡوَى ٱلۡقُلُوبِ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3681, 22, 2628, 33, 'لَكُمۡ فِیهَا مَنَـٰفِعُ إِلَىٰۤ أَجَلࣲ مُّسَمࣰّى ثُمَّ مَحِلُّهَاۤ إِلَى ٱلۡبَیۡتِ ٱلۡعَتِیقِ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3682, 22, 2629, 34, 'وَلِكُلِّ أُمَّةࣲ جَعَلۡنَا مَنسَكࣰا لِّیَذۡكُرُوا۟ ٱسۡمَ ٱللَّهِ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِیمَةِ ٱلۡأَنۡعَـٰمِۗ فَإِلَـٰهُكُمۡ إِلَـٰهࣱ وَ ٰ⁠حِدࣱ فَلَهُۥۤ أَسۡلِمُوا۟ۗ وَبَشِّرِ ٱلۡمُخۡبِتِینَ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:24'),
(3683, 22, 2630, 35, 'ٱلَّذِینَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَٱلصَّـٰبِرِینَ عَلَىٰ مَاۤ أَصَابَهُمۡ وَٱلۡمُقِیمِی ٱلصَّلَوٰةِ وَمِمَّا رَزَقۡنَـٰهُمۡ یُنفِقُونَ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:25'),
(3684, 22, 2631, 36, 'وَٱلۡبُدۡنَ جَعَلۡنَـٰهَا لَكُم مِّن شَعَـٰۤىِٕرِ ٱللَّهِ لَكُمۡ فِیهَا خَیۡرࣱۖ فَٱذۡكُرُوا۟ ٱسۡمَ ٱللَّهِ عَلَیۡهَا صَوَاۤفَّۖ فَإِذَا وَجَبَتۡ جُنُوبُهَا فَكُلُوا۟ مِنۡهَا وَأَطۡعِمُوا۟ ٱلۡقَانِعَ وَٱلۡمُعۡتَرَّۚ كَذَ ٰ⁠لِكَ سَخَّرۡنَـٰهَا لَكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:25'),
(3685, 22, 2632, 37, 'لَن یَنَالَ ٱللَّهَ لُحُومُهَا وَلَا دِمَاۤؤُهَا وَلَـٰكِن یَنَالُهُ ٱلتَّقۡوَىٰ مِنكُمۡۚ كَذَ ٰ⁠لِكَ سَخَّرَهَا لَكُمۡ لِتُكَبِّرُوا۟ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡۗ وَبَشِّرِ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:28:59', '2023-05-27 11:13:25'),
(3686, 22, 2633, 38, '۞ إِنَّ ٱللَّهَ یُدَ ٰ⁠فِعُ عَنِ ٱلَّذِینَ ءَامَنُوۤا۟ۗ إِنَّ ٱللَّهَ لَا یُحِبُّ كُلَّ خَوَّانࣲ كَفُورٍ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3687, 22, 2634, 39, 'أُذِنَ لِلَّذِینَ یُقَـٰتَلُونَ بِأَنَّهُمۡ ظُلِمُوا۟ۚ وَإِنَّ ٱللَّهَ عَلَىٰ نَصۡرِهِمۡ لَقَدِیرٌ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3688, 22, 2635, 40, 'ٱلَّذِینَ أُخۡرِجُوا۟ مِن دِیَـٰرِهِم بِغَیۡرِ حَقٍّ إِلَّاۤ أَن یَقُولُوا۟ رَبُّنَا ٱللَّهُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضࣲ لَّهُدِّمَتۡ صَوَ ٰ⁠مِعُ وَبِیَعࣱ وَصَلَوَ ٰ⁠تࣱ وَمَسَـٰجِدُ یُذۡكَرُ فِیهَا ٱسۡمُ ٱللَّهِ كَثِیرࣰاۗ وَلَیَنصُرَنَّ ٱللَّهُ مَن یَنصُرُهُۥۤۚ إِنَّ ٱللَّهَ لَقَوِیٌّ عَزِیزٌ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3689, 22, 2636, 41, 'ٱلَّذِینَ إِن مَّكَّنَّـٰهُمۡ فِی ٱلۡأَرۡضِ أَقَامُوا۟ ٱلصَّلَوٰةَ وَءَاتَوُا۟ ٱلزَّكَوٰةَ وَأَمَرُوا۟ بِٱلۡمَعۡرُوفِ وَنَهَوۡا۟ عَنِ ٱلۡمُنكَرِۗ وَلِلَّهِ عَـٰقِبَةُ ٱلۡأُمُورِ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3690, 22, 2637, 42, 'وَإِن یُكَذِّبُوكَ فَقَدۡ كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحࣲ وَعَادࣱ وَثَمُودُ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3691, 22, 2638, 43, 'وَقَوۡمُ إِبۡرَ ٰ⁠هِیمَ وَقَوۡمُ لُوطࣲ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3692, 22, 2639, 44, 'وَأَصۡحَـٰبُ مَدۡیَنَۖ وَكُذِّبَ مُوسَىٰۖ فَأَمۡلَیۡتُ لِلۡكَـٰفِرِینَ ثُمَّ أَخَذۡتُهُمۡۖ فَكَیۡفَ كَانَ نَكِیرِ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3693, 22, 2640, 45, 'فَكَأَیِّن مِّن قَرۡیَةٍ أَهۡلَكۡنَـٰهَا وَهِیَ ظَالِمَةࣱ فَهِیَ خَاوِیَةٌ عَلَىٰ عُرُوشِهَا وَبِئۡرࣲ مُّعَطَّلَةࣲ وَقَصۡرࣲ مَّشِیدٍ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3694, 22, 2641, 46, 'أَفَلَمۡ یَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَتَكُونَ لَهُمۡ قُلُوبࣱ یَعۡقِلُونَ بِهَاۤ أَوۡ ءَاذَانࣱ یَسۡمَعُونَ بِهَاۖ فَإِنَّهَا لَا تَعۡمَى ٱلۡأَبۡصَـٰرُ وَلَـٰكِن تَعۡمَى ٱلۡقُلُوبُ ٱلَّتِی فِی ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3695, 22, 2642, 47, 'وَیَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَن یُخۡلِفَ ٱللَّهُ وَعۡدَهُۥۚ وَإِنَّ یَوۡمًا عِندَ رَبِّكَ كَأَلۡفِ سَنَةࣲ مِّمَّا تَعُدُّونَ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3696, 22, 2643, 48, 'وَكَأَیِّن مِّن قَرۡیَةٍ أَمۡلَیۡتُ لَهَا وَهِیَ ظَالِمَةࣱ ثُمَّ أَخَذۡتُهَا وَإِلَیَّ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:29:00', '2023-05-27 11:13:25'),
(3697, 22, 2644, 49, 'قُلۡ یَـٰۤأَیُّهَا ٱلنَّاسُ إِنَّمَاۤ أَنَا۠ لَكُمۡ نَذِیرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:25'),
(3698, 22, 2645, 50, 'فَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُم مَّغۡفِرَةࣱ وَرِزۡقࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3699, 22, 2646, 51, 'وَٱلَّذِینَ سَعَوۡا۟ فِیۤ ءَایَـٰتِنَا مُعَـٰجِزِینَ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3700, 22, 2647, 52, 'وَمَاۤ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولࣲ وَلَا نَبِیٍّ إِلَّاۤ إِذَا تَمَنَّىٰۤ أَلۡقَى ٱلشَّیۡطَـٰنُ فِیۤ أُمۡنِیَّتِهِۦ فَیَنسَخُ ٱللَّهُ مَا یُلۡقِی ٱلشَّیۡطَـٰنُ ثُمَّ یُحۡكِمُ ٱللَّهُ ءَایَـٰتِهِۦۗ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3701, 22, 2648, 53, 'لِّیَجۡعَلَ مَا یُلۡقِی ٱلشَّیۡطَـٰنُ فِتۡنَةࣰ لِّلَّذِینَ فِی قُلُوبِهِم مَّرَضࣱ وَٱلۡقَاسِیَةِ قُلُوبُهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِینَ لَفِی شِقَاقِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3702, 22, 2649, 54, 'وَلِیَعۡلَمَ ٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ فَیُؤۡمِنُوا۟ بِهِۦ فَتُخۡبِتَ لَهُۥ قُلُوبُهُمۡۗ وَإِنَّ ٱللَّهَ لَهَادِ ٱلَّذِینَ ءَامَنُوۤا۟ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3703, 22, 2650, 55, 'وَلَا یَزَالُ ٱلَّذِینَ كَفَرُوا۟ فِی مِرۡیَةࣲ مِّنۡهُ حَتَّىٰ تَأۡتِیَهُمُ ٱلسَّاعَةُ بَغۡتَةً أَوۡ یَأۡتِیَهُمۡ عَذَابُ یَوۡمٍ عَقِیمٍ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3704, 22, 2651, 56, 'ٱلۡمُلۡكُ یَوۡمَىِٕذࣲ لِّلَّهِ یَحۡكُمُ بَیۡنَهُمۡۚ فَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فِی جَنَّـٰتِ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3705, 22, 2652, 57, 'وَٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔایَـٰتِنَا فَأُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:29:01', '2023-05-27 11:13:26'),
(3706, 22, 2653, 58, 'وَٱلَّذِینَ هَاجَرُوا۟ فِی سَبِیلِ ٱللَّهِ ثُمَّ قُتِلُوۤا۟ أَوۡ مَاتُوا۟ لَیَرۡزُقَنَّهُمُ ٱللَّهُ رِزۡقًا حَسَنࣰاۚ وَإِنَّ ٱللَّهَ لَهُوَ خَیۡرُ ٱلرَّ ٰ⁠زِقِینَ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3707, 22, 2654, 59, 'لَیُدۡخِلَنَّهُم مُّدۡخَلࣰا یَرۡضَوۡنَهُۥۚ وَإِنَّ ٱللَّهَ لَعَلِیمٌ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3708, 22, 2655, 60, '۞ ذَ ٰ⁠لِكَۖ وَمَنۡ عَاقَبَ بِمِثۡلِ مَا عُوقِبَ بِهِۦ ثُمَّ بُغِیَ عَلَیۡهِ لَیَنصُرَنَّهُ ٱللَّهُۚ إِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورࣱ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3709, 22, 2656, 61, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱللَّهَ یُولِجُ ٱلَّیۡلَ فِی ٱلنَّهَارِ وَیُولِجُ ٱلنَّهَارَ فِی ٱلَّیۡلِ وَأَنَّ ٱللَّهَ سَمِیعُۢ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3710, 22, 2657, 62, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا یَدۡعُونَ مِن دُونِهِۦ هُوَ ٱلۡبَـٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِیُّ ٱلۡكَبِیرُ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3711, 22, 2658, 63, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَتُصۡبِحُ ٱلۡأَرۡضُ مُخۡضَرَّةًۚ إِنَّ ٱللَّهَ لَطِیفٌ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3712, 22, 2659, 64, 'لَّهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡغَنِیُّ ٱلۡحَمِیدُ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3713, 22, 2660, 65, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِی ٱلۡأَرۡضِ وَٱلۡفُلۡكَ تَجۡرِی فِی ٱلۡبَحۡرِ بِأَمۡرِهِۦ وَیُمۡسِكُ ٱلسَّمَاۤءَ أَن تَقَعَ عَلَى ٱلۡأَرۡضِ إِلَّا بِإِذۡنِهِۦۤۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3714, 22, 2661, 66, 'وَهُوَ ٱلَّذِیۤ أَحۡیَاكُمۡ ثُمَّ یُمِیتُكُمۡ ثُمَّ یُحۡیِیكُمۡۗ إِنَّ ٱلۡإِنسَـٰنَ لَكَفُورࣱ\n', NULL, 0, '2023-05-26 17:29:02', '2023-05-27 11:13:26'),
(3715, 22, 2662, 67, 'لِّكُلِّ أُمَّةࣲ جَعَلۡنَا مَنسَكًا هُمۡ نَاسِكُوهُۖ فَلَا یُنَـٰزِعُنَّكَ فِی ٱلۡأَمۡرِۚ وَٱدۡعُ إِلَىٰ رَبِّكَۖ إِنَّكَ لَعَلَىٰ هُدࣰى مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:26'),
(3716, 22, 2663, 68, 'وَإِن جَـٰدَلُوكَ فَقُلِ ٱللَّهُ أَعۡلَمُ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:26'),
(3717, 22, 2664, 69, 'ٱللَّهُ یَحۡكُمُ بَیۡنَكُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ فِیمَا كُنتُمۡ فِیهِ تَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3718, 22, 2665, 70, 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ یَعۡلَمُ مَا فِی ٱلسَّمَاۤءِ وَٱلۡأَرۡضِۚ إِنَّ ذَ ٰ⁠لِكَ فِی كِتَـٰبٍۚ إِنَّ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣱ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3719, 22, 2666, 71, 'وَیَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَمۡ یُنَزِّلۡ بِهِۦ سُلۡطَـٰنࣰا وَمَا لَیۡسَ لَهُم بِهِۦ عِلۡمࣱۗ وَمَا لِلظَّـٰلِمِینَ مِن نَّصِیرࣲ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3720, 22, 2667, 72, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَـٰتُنَا بَیِّنَـٰتࣲ تَعۡرِفُ فِی وُجُوهِ ٱلَّذِینَ كَفَرُوا۟ ٱلۡمُنكَرَۖ یَكَادُونَ یَسۡطُونَ بِٱلَّذِینَ یَتۡلُونَ عَلَیۡهِمۡ ءَایَـٰتِنَاۗ قُلۡ أَفَأُنَبِّئُكُم بِشَرࣲّ مِّن ذَ ٰ⁠لِكُمُۚ ٱلنَّارُ وَعَدَهَا ٱللَّهُ ٱلَّذِینَ كَفَرُوا۟ۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3721, 22, 2668, 73, 'یَـٰۤأَیُّهَا ٱلنَّاسُ ضُرِبَ مَثَلࣱ فَٱسۡتَمِعُوا۟ لَهُۥۤۚ إِنَّ ٱلَّذِینَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَن یَخۡلُقُوا۟ ذُبَابࣰا وَلَوِ ٱجۡتَمَعُوا۟ لَهُۥۖ وَإِن یَسۡلُبۡهُمُ ٱلذُّبَابُ شَیۡـࣰٔا لَّا یَسۡتَنقِذُوهُ مِنۡهُۚ ضَعُفَ ٱلطَّالِبُ وَٱلۡمَطۡلُوبُ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3722, 22, 2669, 74, 'مَا قَدَرُوا۟ ٱللَّهَ حَقَّ قَدۡرِهِۦۤۚ إِنَّ ٱللَّهَ لَقَوِیٌّ عَزِیزٌ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3723, 22, 2670, 75, 'ٱللَّهُ یَصۡطَفِی مِنَ ٱلۡمَلَـٰۤىِٕكَةِ رُسُلࣰا وَمِنَ ٱلنَّاسِۚ إِنَّ ٱللَّهَ سَمِیعُۢ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3724, 22, 2671, 76, 'یَعۡلَمُ مَا بَیۡنَ أَیۡدِیهِمۡ وَمَا خَلۡفَهُمۡۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n', NULL, 0, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3725, 22, 2672, 77, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱرۡكَعُوا۟ وَٱسۡجُدُوا۟ وَٱعۡبُدُوا۟ رَبَّكُمۡ وَٱفۡعَلُوا۟ ٱلۡخَیۡرَ لَعَلَّكُمۡ تُفۡلِحُونَ ۩\n', NULL, 1, '2023-05-26 17:29:03', '2023-05-27 11:13:27'),
(3726, 22, 2673, 78, 'وَجَـٰهِدُوا۟ فِی ٱللَّهِ حَقَّ جِهَادِهِۦۚ هُوَ ٱجۡتَبَىٰكُمۡ وَمَا جَعَلَ عَلَیۡكُمۡ فِی ٱلدِّینِ مِنۡ حَرَجࣲۚ مِّلَّةَ أَبِیكُمۡ إِبۡرَ ٰ⁠هِیمَۚ هُوَ سَمَّىٰكُمُ ٱلۡمُسۡلِمِینَ مِن قَبۡلُ وَفِی هَـٰذَا لِیَكُونَ ٱلرَّسُولُ شَهِیدًا عَلَیۡكُمۡ وَتَكُونُوا۟ شُهَدَاۤءَ عَلَى ٱلنَّاسِۚ فَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَٱعۡتَصِمُوا۟ بِٱللَّهِ هُوَ مَوۡلَىٰكُمۡۖ فَنِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِیرُ\n', NULL, 0, '2023-05-26 17:29:04', '2023-05-27 11:13:27'),
(3727, 21, 2484, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱقۡتَرَبَ لِلنَّاسِ حِسَابُهُمۡ وَهُمۡ فِی غَفۡلَةࣲ مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:29'),
(3728, 21, 2485, 2, 'مَا یَأۡتِیهِم مِّن ذِكۡرࣲ مِّن رَّبِّهِم مُّحۡدَثٍ إِلَّا ٱسۡتَمَعُوهُ وَهُمۡ یَلۡعَبُونَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:29'),
(3729, 21, 2486, 3, 'لَاهِیَةࣰ قُلُوبُهُمۡۗ وَأَسَرُّوا۟ ٱلنَّجۡوَى ٱلَّذِینَ ظَلَمُوا۟ هَلۡ هَـٰذَاۤ إِلَّا بَشَرࣱ مِّثۡلُكُمۡۖ أَفَتَأۡتُونَ ٱلسِّحۡرَ وَأَنتُمۡ تُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:30'),
(3730, 21, 2487, 4, 'قَالَ رَبِّی یَعۡلَمُ ٱلۡقَوۡلَ فِی ٱلسَّمَاۤءِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:30'),
(3731, 21, 2488, 5, 'بَلۡ قَالُوۤا۟ أَضۡغَـٰثُ أَحۡلَـٰمِۭ بَلِ ٱفۡتَرَىٰهُ بَلۡ هُوَ شَاعِرࣱ فَلۡیَأۡتِنَا بِـَٔایَةࣲ كَمَاۤ أُرۡسِلَ ٱلۡأَوَّلُونَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:30'),
(3732, 21, 2489, 6, 'مَاۤ ءَامَنَتۡ قَبۡلَهُم مِّن قَرۡیَةٍ أَهۡلَكۡنَـٰهَاۤۖ أَفَهُمۡ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:30'),
(3733, 21, 2490, 7, 'وَمَاۤ أَرۡسَلۡنَا قَبۡلَكَ إِلَّا رِجَالࣰا نُّوحِیۤ إِلَیۡهِمۡۖ فَسۡـَٔلُوۤا۟ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:30'),
(3734, 21, 2491, 8, 'وَمَا جَعَلۡنَـٰهُمۡ جَسَدࣰا لَّا یَأۡكُلُونَ ٱلطَّعَامَ وَمَا كَانُوا۟ خَـٰلِدِینَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:30'),
(3735, 21, 2492, 9, 'ثُمَّ صَدَقۡنَـٰهُمُ ٱلۡوَعۡدَ فَأَنجَیۡنَـٰهُمۡ وَمَن نَّشَاۤءُ وَأَهۡلَكۡنَا ٱلۡمُسۡرِفِینَ\n', NULL, 0, '2023-05-26 17:29:11', '2023-05-27 11:13:30'),
(3736, 21, 2493, 10, 'لَقَدۡ أَنزَلۡنَاۤ إِلَیۡكُمۡ كِتَـٰبࣰا فِیهِ ذِكۡرُكُمۡۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3737, 21, 2494, 11, 'وَكَمۡ قَصَمۡنَا مِن قَرۡیَةࣲ كَانَتۡ ظَالِمَةࣰ وَأَنشَأۡنَا بَعۡدَهَا قَوۡمًا ءَاخَرِینَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3738, 21, 2495, 12, 'فَلَمَّاۤ أَحَسُّوا۟ بَأۡسَنَاۤ إِذَا هُم مِّنۡهَا یَرۡكُضُونَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3739, 21, 2496, 13, 'لَا تَرۡكُضُوا۟ وَٱرۡجِعُوۤا۟ إِلَىٰ مَاۤ أُتۡرِفۡتُمۡ فِیهِ وَمَسَـٰكِنِكُمۡ لَعَلَّكُمۡ تُسۡـَٔلُونَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3740, 21, 2497, 14, 'قَالُوا۟ یَـٰوَیۡلَنَاۤ إِنَّا كُنَّا ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3741, 21, 2498, 15, 'فَمَا زَالَت تِّلۡكَ دَعۡوَىٰهُمۡ حَتَّىٰ جَعَلۡنَـٰهُمۡ حَصِیدًا خَـٰمِدِینَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3742, 21, 2499, 16, 'وَمَا خَلَقۡنَا ٱلسَّمَاۤءَ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَا لَـٰعِبِینَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3743, 21, 2500, 17, 'لَوۡ أَرَدۡنَاۤ أَن نَّتَّخِذَ لَهۡوࣰا لَّٱتَّخَذۡنَـٰهُ مِن لَّدُنَّاۤ إِن كُنَّا فَـٰعِلِینَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3744, 21, 2501, 18, 'بَلۡ نَقۡذِفُ بِٱلۡحَقِّ عَلَى ٱلۡبَـٰطِلِ فَیَدۡمَغُهُۥ فَإِذَا هُوَ زَاهِقࣱۚ وَلَكُمُ ٱلۡوَیۡلُ مِمَّا تَصِفُونَ\n', NULL, 0, '2023-05-26 17:29:12', '2023-05-27 11:13:30'),
(3745, 21, 2502, 19, 'وَلَهُۥ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَمَنۡ عِندَهُۥ لَا یَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَلَا یَسۡتَحۡسِرُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:30'),
(3746, 21, 2503, 20, 'یُسَبِّحُونَ ٱلَّیۡلَ وَٱلنَّهَارَ لَا یَفۡتُرُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:30'),
(3747, 21, 2504, 21, 'أَمِ ٱتَّخَذُوۤا۟ ءَالِهَةࣰ مِّنَ ٱلۡأَرۡضِ هُمۡ یُنشِرُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:30'),
(3748, 21, 2505, 22, 'لَوۡ كَانَ فِیهِمَاۤ ءَالِهَةٌ إِلَّا ٱللَّهُ لَفَسَدَتَاۚ فَسُبۡحَـٰنَ ٱللَّهِ رَبِّ ٱلۡعَرۡشِ عَمَّا یَصِفُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3749, 21, 2506, 23, 'لَا یُسۡـَٔلُ عَمَّا یَفۡعَلُ وَهُمۡ یُسۡـَٔلُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3750, 21, 2507, 24, 'أَمِ ٱتَّخَذُوا۟ مِن دُونِهِۦۤ ءَالِهَةࣰۖ قُلۡ هَاتُوا۟ بُرۡهَـٰنَكُمۡۖ هَـٰذَا ذِكۡرُ مَن مَّعِیَ وَذِكۡرُ مَن قَبۡلِیۚ بَلۡ أَكۡثَرُهُمۡ لَا یَعۡلَمُونَ ٱلۡحَقَّۖ فَهُم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3751, 21, 2508, 25, 'وَمَاۤ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولٍ إِلَّا نُوحِیۤ إِلَیۡهِ أَنَّهُۥ لَاۤ إِلَـٰهَ إِلَّاۤ أَنَا۠ فَٱعۡبُدُونِ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3752, 21, 2509, 26, 'وَقَالُوا۟ ٱتَّخَذَ ٱلرَّحۡمَـٰنُ وَلَدࣰاۗ سُبۡحَـٰنَهُۥۚ بَلۡ عِبَادࣱ مُّكۡرَمُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3753, 21, 2510, 27, 'لَا یَسۡبِقُونَهُۥ بِٱلۡقَوۡلِ وَهُم بِأَمۡرِهِۦ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3754, 21, 2511, 28, 'یَعۡلَمُ مَا بَیۡنَ أَیۡدِیهِمۡ وَمَا خَلۡفَهُمۡ وَلَا یَشۡفَعُونَ إِلَّا لِمَنِ ٱرۡتَضَىٰ وَهُم مِّنۡ خَشۡیَتِهِۦ مُشۡفِقُونَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3755, 21, 2512, 29, '۞ وَمَن یَقُلۡ مِنۡهُمۡ إِنِّیۤ إِلَـٰهࣱ مِّن دُونِهِۦ فَذَ ٰ⁠لِكَ نَجۡزِیهِ جَهَنَّمَۚ كَذَ ٰ⁠لِكَ نَجۡزِی ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:13', '2023-05-27 11:13:31'),
(3756, 21, 2513, 30, 'أَوَلَمۡ یَرَ ٱلَّذِینَ كَفَرُوۤا۟ أَنَّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ كَانَتَا رَتۡقࣰا فَفَتَقۡنَـٰهُمَاۖ وَجَعَلۡنَا مِنَ ٱلۡمَاۤءِ كُلَّ شَیۡءٍ حَیٍّۚ أَفَلَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3757, 21, 2514, 31, 'وَجَعَلۡنَا فِی ٱلۡأَرۡضِ رَوَ ٰ⁠سِیَ أَن تَمِیدَ بِهِمۡ وَجَعَلۡنَا فِیهَا فِجَاجࣰا سُبُلࣰا لَّعَلَّهُمۡ یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3758, 21, 2515, 32, 'وَجَعَلۡنَا ٱلسَّمَاۤءَ سَقۡفࣰا مَّحۡفُوظࣰاۖ وَهُمۡ عَنۡ ءَایَـٰتِهَا مُعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3759, 21, 2516, 33, 'وَهُوَ ٱلَّذِی خَلَقَ ٱلَّیۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلࣱّ فِی فَلَكࣲ یَسۡبَحُونَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3760, 21, 2517, 34, 'وَمَا جَعَلۡنَا لِبَشَرࣲ مِّن قَبۡلِكَ ٱلۡخُلۡدَۖ أَفَإِی۟ن مِّتَّ فَهُمُ ٱلۡخَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3761, 21, 2518, 35, 'كُلُّ نَفۡسࣲ ذَاۤىِٕقَةُ ٱلۡمَوۡتِۗ وَنَبۡلُوكُم بِٱلشَّرِّ وَٱلۡخَیۡرِ فِتۡنَةࣰۖ وَإِلَیۡنَا تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3762, 21, 2519, 36, 'وَإِذَا رَءَاكَ ٱلَّذِینَ كَفَرُوۤا۟ إِن یَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَـٰذَا ٱلَّذِی یَذۡكُرُ ءَالِهَتَكُمۡ وَهُم بِذِكۡرِ ٱلرَّحۡمَـٰنِ هُمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3763, 21, 2520, 37, 'خُلِقَ ٱلۡإِنسَـٰنُ مِنۡ عَجَلࣲۚ سَأُو۟رِیكُمۡ ءَایَـٰتِی فَلَا تَسۡتَعۡجِلُونِ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3764, 21, 2521, 38, 'وَیَقُولُونَ مَتَىٰ هَـٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:29:14', '2023-05-27 11:13:31'),
(3765, 21, 2522, 39, 'لَوۡ یَعۡلَمُ ٱلَّذِینَ كَفَرُوا۟ حِینَ لَا یَكُفُّونَ عَن وُجُوهِهِمُ ٱلنَّارَ وَلَا عَن ظُهُورِهِمۡ وَلَا هُمۡ یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:31'),
(3766, 21, 2523, 40, 'بَلۡ تَأۡتِیهِم بَغۡتَةࣰ فَتَبۡهَتُهُمۡ فَلَا یَسۡتَطِیعُونَ رَدَّهَا وَلَا هُمۡ یُنظَرُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3767, 21, 2524, 41, 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلࣲ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِینَ سَخِرُوا۟ مِنۡهُم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3768, 21, 2525, 42, 'قُلۡ مَن یَكۡلَؤُكُم بِٱلَّیۡلِ وَٱلنَّهَارِ مِنَ ٱلرَّحۡمَـٰنِۚ بَلۡ هُمۡ عَن ذِكۡرِ رَبِّهِم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3769, 21, 2526, 43, 'أَمۡ لَهُمۡ ءَالِهَةࣱ تَمۡنَعُهُم مِّن دُونِنَاۚ لَا یَسۡتَطِیعُونَ نَصۡرَ أَنفُسِهِمۡ وَلَا هُم مِّنَّا یُصۡحَبُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3770, 21, 2527, 44, 'بَلۡ مَتَّعۡنَا هَـٰۤؤُلَاۤءِ وَءَابَاۤءَهُمۡ حَتَّىٰ طَالَ عَلَیۡهِمُ ٱلۡعُمُرُۗ أَفَلَا یَرَوۡنَ أَنَّا نَأۡتِی ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَاۤۚ أَفَهُمُ ٱلۡغَـٰلِبُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3771, 21, 2528, 45, 'قُلۡ إِنَّمَاۤ أُنذِرُكُم بِٱلۡوَحۡیِۚ وَلَا یَسۡمَعُ ٱلصُّمُّ ٱلدُّعَاۤءَ إِذَا مَا یُنذَرُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3772, 21, 2529, 46, 'وَلَىِٕن مَّسَّتۡهُمۡ نَفۡحَةࣱ مِّنۡ عَذَابِ رَبِّكَ لَیَقُولُنَّ یَـٰوَیۡلَنَاۤ إِنَّا كُنَّا ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3773, 21, 2530, 47, 'وَنَضَعُ ٱلۡمَوَ ٰ⁠زِینَ ٱلۡقِسۡطَ لِیَوۡمِ ٱلۡقِیَـٰمَةِ فَلَا تُظۡلَمُ نَفۡسࣱ شَیۡـࣰٔاۖ وَإِن كَانَ مِثۡقَالَ حَبَّةࣲ مِّنۡ خَرۡدَلٍ أَتَیۡنَا بِهَاۗ وَكَفَىٰ بِنَا حَـٰسِبِینَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3774, 21, 2531, 48, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَىٰ وَهَـٰرُونَ ٱلۡفُرۡقَانَ وَضِیَاۤءࣰ وَذِكۡرࣰا لِّلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3775, 21, 2532, 49, 'ٱلَّذِینَ یَخۡشَوۡنَ رَبَّهُم بِٱلۡغَیۡبِ وَهُم مِّنَ ٱلسَّاعَةِ مُشۡفِقُونَ\n', NULL, 0, '2023-05-26 17:29:15', '2023-05-27 11:13:32'),
(3776, 21, 2533, 50, 'وَهَـٰذَا ذِكۡرࣱ مُّبَارَكٌ أَنزَلۡنَـٰهُۚ أَفَأَنتُمۡ لَهُۥ مُنكِرُونَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3777, 21, 2534, 51, '۞ وَلَقَدۡ ءَاتَیۡنَاۤ إِبۡرَ ٰ⁠هِیمَ رُشۡدَهُۥ مِن قَبۡلُ وَكُنَّا بِهِۦ عَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3778, 21, 2535, 52, 'إِذۡ قَالَ لِأَبِیهِ وَقَوۡمِهِۦ مَا هَـٰذِهِ ٱلتَّمَاثِیلُ ٱلَّتِیۤ أَنتُمۡ لَهَا عَـٰكِفُونَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3779, 21, 2536, 53, 'قَالُوا۟ وَجَدۡنَاۤ ءَابَاۤءَنَا لَهَا عَـٰبِدِینَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3780, 21, 2537, 54, 'قَالَ لَقَدۡ كُنتُمۡ أَنتُمۡ وَءَابَاۤؤُكُمۡ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3781, 21, 2538, 55, 'قَالُوۤا۟ أَجِئۡتَنَا بِٱلۡحَقِّ أَمۡ أَنتَ مِنَ ٱللَّـٰعِبِینَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3782, 21, 2539, 56, 'قَالَ بَل رَّبُّكُمۡ رَبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ ٱلَّذِی فَطَرَهُنَّ وَأَنَا۠ عَلَىٰ ذَ ٰ⁠لِكُم مِّنَ ٱلشَّـٰهِدِینَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3783, 21, 2540, 57, 'وَتَٱللَّهِ لَأَكِیدَنَّ أَصۡنَـٰمَكُم بَعۡدَ أَن تُوَلُّوا۟ مُدۡبِرِینَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3784, 21, 2541, 58, 'فَجَعَلَهُمۡ جُذَ ٰ⁠ذًا إِلَّا كَبِیرࣰا لَّهُمۡ لَعَلَّهُمۡ إِلَیۡهِ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:32'),
(3785, 21, 2542, 59, 'قَالُوا۟ مَن فَعَلَ هَـٰذَا بِـَٔالِهَتِنَاۤ إِنَّهُۥ لَمِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:33'),
(3786, 21, 2543, 60, 'قَالُوا۟ سَمِعۡنَا فَتࣰى یَذۡكُرُهُمۡ یُقَالُ لَهُۥۤ إِبۡرَ ٰ⁠هِیمُ\n', NULL, 0, '2023-05-26 17:29:16', '2023-05-27 11:13:33'),
(3787, 21, 2544, 61, 'قَالُوا۟ فَأۡتُوا۟ بِهِۦ عَلَىٰۤ أَعۡیُنِ ٱلنَّاسِ لَعَلَّهُمۡ یَشۡهَدُونَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3788, 21, 2545, 62, 'قَالُوۤا۟ ءَأَنتَ فَعَلۡتَ هَـٰذَا بِـَٔالِهَتِنَا یَـٰۤإِبۡرَ ٰ⁠هِیمُ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3789, 21, 2546, 63, 'قَالَ بَلۡ فَعَلَهُۥ كَبِیرُهُمۡ هَـٰذَا فَسۡـَٔلُوهُمۡ إِن كَانُوا۟ یَنطِقُونَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3790, 21, 2547, 64, 'فَرَجَعُوۤا۟ إِلَىٰۤ أَنفُسِهِمۡ فَقَالُوۤا۟ إِنَّكُمۡ أَنتُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3791, 21, 2548, 65, 'ثُمَّ نُكِسُوا۟ عَلَىٰ رُءُوسِهِمۡ لَقَدۡ عَلِمۡتَ مَا هَـٰۤؤُلَاۤءِ یَنطِقُونَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3792, 21, 2549, 66, 'قَالَ أَفَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا یَنفَعُكُمۡ شَیۡـࣰٔا وَلَا یَضُرُّكُمۡ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3793, 21, 2550, 67, 'أُفࣲّ لَّكُمۡ وَلِمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3794, 21, 2551, 68, 'قَالُوا۟ حَرِّقُوهُ وَٱنصُرُوۤا۟ ءَالِهَتَكُمۡ إِن كُنتُمۡ فَـٰعِلِینَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3795, 21, 2552, 69, 'قُلۡنَا یَـٰنَارُ كُونِی بَرۡدࣰا وَسَلَـٰمًا عَلَىٰۤ إِبۡرَ ٰ⁠هِیمَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3796, 21, 2553, 70, 'وَأَرَادُوا۟ بِهِۦ كَیۡدࣰا فَجَعَلۡنَـٰهُمُ ٱلۡأَخۡسَرِینَ\n', NULL, 0, '2023-05-26 17:29:17', '2023-05-27 11:13:33'),
(3797, 21, 2554, 71, 'وَنَجَّیۡنَـٰهُ وَلُوطًا إِلَى ٱلۡأَرۡضِ ٱلَّتِی بَـٰرَكۡنَا فِیهَا لِلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:33'),
(3798, 21, 2555, 72, 'وَوَهَبۡنَا لَهُۥۤ إِسۡحَـٰقَ وَیَعۡقُوبَ نَافِلَةࣰۖ وَكُلࣰّا جَعَلۡنَا صَـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:33'),
(3799, 21, 2556, 73, 'وَجَعَلۡنَـٰهُمۡ أَىِٕمَّةࣰ یَهۡدُونَ بِأَمۡرِنَا وَأَوۡحَیۡنَاۤ إِلَیۡهِمۡ فِعۡلَ ٱلۡخَیۡرَ ٰ⁠تِ وَإِقَامَ ٱلصَّلَوٰةِ وَإِیتَاۤءَ ٱلزَّكَوٰةِۖ وَكَانُوا۟ لَنَا عَـٰبِدِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:33'),
(3800, 21, 2557, 74, 'وَلُوطًا ءَاتَیۡنَـٰهُ حُكۡمࣰا وَعِلۡمࣰا وَنَجَّیۡنَـٰهُ مِنَ ٱلۡقَرۡیَةِ ٱلَّتِی كَانَت تَّعۡمَلُ ٱلۡخَبَـٰۤىِٕثَۚ إِنَّهُمۡ كَانُوا۟ قَوۡمَ سَوۡءࣲ فَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:33'),
(3801, 21, 2558, 75, 'وَأَدۡخَلۡنَـٰهُ فِی رَحۡمَتِنَاۤۖ إِنَّهُۥ مِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:33'),
(3802, 21, 2559, 76, 'وَنُوحًا إِذۡ نَادَىٰ مِن قَبۡلُ فَٱسۡتَجَبۡنَا لَهُۥ فَنَجَّیۡنَـٰهُ وَأَهۡلَهُۥ مِنَ ٱلۡكَرۡبِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:33'),
(3803, 21, 2560, 77, 'وَنَصَرۡنَـٰهُ مِنَ ٱلۡقَوۡمِ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَاۤۚ إِنَّهُمۡ كَانُوا۟ قَوۡمَ سَوۡءࣲ فَأَغۡرَقۡنَـٰهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:34'),
(3804, 21, 2561, 78, 'وَدَاوُۥدَ وَسُلَیۡمَـٰنَ إِذۡ یَحۡكُمَانِ فِی ٱلۡحَرۡثِ إِذۡ نَفَشَتۡ فِیهِ غَنَمُ ٱلۡقَوۡمِ وَكُنَّا لِحُكۡمِهِمۡ شَـٰهِدِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:34'),
(3805, 21, 2562, 79, 'فَفَهَّمۡنَـٰهَا سُلَیۡمَـٰنَۚ وَكُلًّا ءَاتَیۡنَا حُكۡمࣰا وَعِلۡمࣰاۚ وَسَخَّرۡنَا مَعَ دَاوُۥدَ ٱلۡجِبَالَ یُسَبِّحۡنَ وَٱلطَّیۡرَۚ وَكُنَّا فَـٰعِلِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:34'),
(3806, 21, 2563, 80, 'وَعَلَّمۡنَـٰهُ صَنۡعَةَ لَبُوسࣲ لَّكُمۡ لِتُحۡصِنَكُم مِّنۢ بَأۡسِكُمۡۖ فَهَلۡ أَنتُمۡ شَـٰكِرُونَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:34'),
(3807, 21, 2564, 81, 'وَلِسُلَیۡمَـٰنَ ٱلرِّیحَ عَاصِفَةࣰ تَجۡرِی بِأَمۡرِهِۦۤ إِلَى ٱلۡأَرۡضِ ٱلَّتِی بَـٰرَكۡنَا فِیهَاۚ وَكُنَّا بِكُلِّ شَیۡءٍ عَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:18', '2023-05-27 11:13:34'),
(3808, 21, 2565, 82, 'وَمِنَ ٱلشَّیَـٰطِینِ مَن یَغُوصُونَ لَهُۥ وَیَعۡمَلُونَ عَمَلࣰا دُونَ ذَ ٰ⁠لِكَۖ وَكُنَّا لَهُمۡ حَـٰفِظِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3809, 21, 2566, 83, '۞ وَأَیُّوبَ إِذۡ نَادَىٰ رَبَّهُۥۤ أَنِّی مَسَّنِیَ ٱلضُّرُّ وَأَنتَ أَرۡحَمُ ٱلرَّ ٰ⁠حِمِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3810, 21, 2567, 84, 'فَٱسۡتَجَبۡنَا لَهُۥ فَكَشَفۡنَا مَا بِهِۦ مِن ضُرࣲّۖ وَءَاتَیۡنَـٰهُ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةࣰ مِّنۡ عِندِنَا وَذِكۡرَىٰ لِلۡعَـٰبِدِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3811, 21, 2568, 85, 'وَإِسۡمَـٰعِیلَ وَإِدۡرِیسَ وَذَا ٱلۡكِفۡلِۖ كُلࣱّ مِّنَ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3812, 21, 2569, 86, 'وَأَدۡخَلۡنَـٰهُمۡ فِی رَحۡمَتِنَاۤۖ إِنَّهُم مِّنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3813, 21, 2570, 87, 'وَذَا ٱلنُّونِ إِذ ذَّهَبَ مُغَـٰضِبࣰا فَظَنَّ أَن لَّن نَّقۡدِرَ عَلَیۡهِ فَنَادَىٰ فِی ٱلظُّلُمَـٰتِ أَن لَّاۤ إِلَـٰهَ إِلَّاۤ أَنتَ سُبۡحَـٰنَكَ إِنِّی كُنتُ مِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3814, 21, 2571, 88, 'فَٱسۡتَجَبۡنَا لَهُۥ وَنَجَّیۡنَـٰهُ مِنَ ٱلۡغَمِّۚ وَكَذَ ٰ⁠لِكَ نُـۨجِی ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3815, 21, 2572, 89, 'وَزَكَرِیَّاۤ إِذۡ نَادَىٰ رَبَّهُۥ رَبِّ لَا تَذَرۡنِی فَرۡدࣰا وَأَنتَ خَیۡرُ ٱلۡوَ ٰ⁠رِثِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3816, 21, 2573, 90, 'فَٱسۡتَجَبۡنَا لَهُۥ وَوَهَبۡنَا لَهُۥ یَحۡیَىٰ وَأَصۡلَحۡنَا لَهُۥ زَوۡجَهُۥۤۚ إِنَّهُمۡ كَانُوا۟ یُسَـٰرِعُونَ فِی ٱلۡخَیۡرَ ٰ⁠تِ وَیَدۡعُونَنَا رَغَبࣰا وَرَهَبࣰاۖ وَكَانُوا۟ لَنَا خَـٰشِعِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3817, 21, 2574, 91, 'وَٱلَّتِیۤ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِیهَا مِن رُّوحِنَا وَجَعَلۡنَـٰهَا وَٱبۡنَهَاۤ ءَایَةࣰ لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3818, 21, 2575, 92, 'إِنَّ هَـٰذِهِۦۤ أُمَّتُكُمۡ أُمَّةࣰ وَ ٰ⁠حِدَةࣰ وَأَنَا۠ رَبُّكُمۡ فَٱعۡبُدُونِ\n', NULL, 0, '2023-05-26 17:29:19', '2023-05-27 11:13:34'),
(3819, 21, 2576, 93, 'وَتَقَطَّعُوۤا۟ أَمۡرَهُم بَیۡنَهُمۡۖ كُلٌّ إِلَیۡنَا رَ ٰ⁠جِعُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:34'),
(3820, 21, 2577, 94, 'فَمَن یَعۡمَلۡ مِنَ ٱلصَّـٰلِحَـٰتِ وَهُوَ مُؤۡمِنࣱ فَلَا كُفۡرَانَ لِسَعۡیِهِۦ وَإِنَّا لَهُۥ كَـٰتِبُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:34'),
(3821, 21, 2578, 95, 'وَحَرَ ٰ⁠مٌ عَلَىٰ قَرۡیَةٍ أَهۡلَكۡنَـٰهَاۤ أَنَّهُمۡ لَا یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:34'),
(3822, 21, 2579, 96, 'حَتَّىٰۤ إِذَا فُتِحَتۡ یَأۡجُوجُ وَمَأۡجُوجُ وَهُم مِّن كُلِّ حَدَبࣲ یَنسِلُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3823, 21, 2580, 97, 'وَٱقۡتَرَبَ ٱلۡوَعۡدُ ٱلۡحَقُّ فَإِذَا هِیَ شَـٰخِصَةٌ أَبۡصَـٰرُ ٱلَّذِینَ كَفَرُوا۟ یَـٰوَیۡلَنَا قَدۡ كُنَّا فِی غَفۡلَةࣲ مِّنۡ هَـٰذَا بَلۡ كُنَّا ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3824, 21, 2581, 98, 'إِنَّكُمۡ وَمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ حَصَبُ جَهَنَّمَ أَنتُمۡ لَهَا وَ ٰ⁠رِدُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3825, 21, 2582, 99, 'لَوۡ كَانَ هَـٰۤؤُلَاۤءِ ءَالِهَةࣰ مَّا وَرَدُوهَاۖ وَكُلࣱّ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3826, 21, 2583, 100, 'لَهُمۡ فِیهَا زَفِیرࣱ وَهُمۡ فِیهَا لَا یَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3827, 21, 2584, 101, 'إِنَّ ٱلَّذِینَ سَبَقَتۡ لَهُم مِّنَّا ٱلۡحُسۡنَىٰۤ أُو۟لَـٰۤىِٕكَ عَنۡهَا مُبۡعَدُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3828, 21, 2585, 102, 'لَا یَسۡمَعُونَ حَسِیسَهَاۖ وَهُمۡ فِی مَا ٱشۡتَهَتۡ أَنفُسُهُمۡ خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3829, 21, 2586, 103, 'لَا یَحۡزُنُهُمُ ٱلۡفَزَعُ ٱلۡأَكۡبَرُ وَتَتَلَقَّىٰهُمُ ٱلۡمَلَـٰۤىِٕكَةُ هَـٰذَا یَوۡمُكُمُ ٱلَّذِی كُنتُمۡ تُوعَدُونَ\n', NULL, 0, '2023-05-26 17:29:20', '2023-05-27 11:13:35'),
(3830, 21, 2587, 104, 'یَوۡمَ نَطۡوِی ٱلسَّمَاۤءَ كَطَیِّ ٱلسِّجِلِّ لِلۡكُتُبِۚ كَمَا بَدَأۡنَاۤ أَوَّلَ خَلۡقࣲ نُّعِیدُهُۥۚ وَعۡدًا عَلَیۡنَاۤۚ إِنَّا كُنَّا فَـٰعِلِینَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3831, 21, 2588, 105, 'وَلَقَدۡ كَتَبۡنَا فِی ٱلزَّبُورِ مِنۢ بَعۡدِ ٱلذِّكۡرِ أَنَّ ٱلۡأَرۡضَ یَرِثُهَا عِبَادِیَ ٱلصَّـٰلِحُونَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3832, 21, 2589, 106, 'إِنَّ فِی هَـٰذَا لَبَلَـٰغࣰا لِّقَوۡمٍ عَـٰبِدِینَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3833, 21, 2590, 107, 'وَمَاۤ أَرۡسَلۡنَـٰكَ إِلَّا رَحۡمَةࣰ لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3834, 21, 2591, 108, 'قُلۡ إِنَّمَا یُوحَىٰۤ إِلَیَّ أَنَّمَاۤ إِلَـٰهُكُمۡ إِلَـٰهࣱ وَ ٰ⁠حِدࣱۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3835, 21, 2592, 109, 'فَإِن تَوَلَّوۡا۟ فَقُلۡ ءَاذَنتُكُمۡ عَلَىٰ سَوَاۤءࣲۖ وَإِنۡ أَدۡرِیۤ أَقَرِیبٌ أَم بَعِیدࣱ مَّا تُوعَدُونَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3836, 21, 2593, 110, 'إِنَّهُۥ یَعۡلَمُ ٱلۡجَهۡرَ مِنَ ٱلۡقَوۡلِ وَیَعۡلَمُ مَا تَكۡتُمُونَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3837, 21, 2594, 111, 'وَإِنۡ أَدۡرِی لَعَلَّهُۥ فِتۡنَةࣱ لَّكُمۡ وَمَتَـٰعٌ إِلَىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3838, 21, 2595, 112, 'قَـٰلَ رَبِّ ٱحۡكُم بِٱلۡحَقِّۗ وَرَبُّنَا ٱلرَّحۡمَـٰنُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ\n', NULL, 0, '2023-05-26 17:29:21', '2023-05-27 11:13:35'),
(3839, 20, 2349, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ طه\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3840, 20, 2350, 2, 'مَاۤ أَنزَلۡنَا عَلَیۡكَ ٱلۡقُرۡءَانَ لِتَشۡقَىٰۤ\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3841, 20, 2351, 3, 'إِلَّا تَذۡكِرَةࣰ لِّمَن یَخۡشَىٰ\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3842, 20, 2352, 4, 'تَنزِیلࣰا مِّمَّنۡ خَلَقَ ٱلۡأَرۡضَ وَٱلسَّمَـٰوَ ٰ⁠تِ ٱلۡعُلَى\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3843, 20, 2353, 5, 'ٱلرَّحۡمَـٰنُ عَلَى ٱلۡعَرۡشِ ٱسۡتَوَىٰ\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3844, 20, 2354, 6, 'لَهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِ وَمَا بَیۡنَهُمَا وَمَا تَحۡتَ ٱلثَّرَىٰ\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3845, 20, 2355, 7, 'وَإِن تَجۡهَرۡ بِٱلۡقَوۡلِ فَإِنَّهُۥ یَعۡلَمُ ٱلسِّرَّ وَأَخۡفَى\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3846, 20, 2356, 8, 'ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ لَهُ ٱلۡأَسۡمَاۤءُ ٱلۡحُسۡنَىٰ\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3847, 20, 2357, 9, 'وَهَلۡ أَتَىٰكَ حَدِیثُ مُوسَىٰۤ\n', NULL, 0, '2023-05-26 17:29:28', '2023-05-27 11:13:38'),
(3848, 20, 2358, 10, 'إِذۡ رَءَا نَارࣰا فَقَالَ لِأَهۡلِهِ ٱمۡكُثُوۤا۟ إِنِّیۤ ءَانَسۡتُ نَارࣰا لَّعَلِّیۤ ءَاتِیكُم مِّنۡهَا بِقَبَسٍ أَوۡ أَجِدُ عَلَى ٱلنَّارِ هُدࣰى\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:38'),
(3849, 20, 2359, 11, 'فَلَمَّاۤ أَتَىٰهَا نُودِیَ یَـٰمُوسَىٰۤ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:38'),
(3850, 20, 2360, 12, 'إِنِّیۤ أَنَا۠ رَبُّكَ فَٱخۡلَعۡ نَعۡلَیۡكَ إِنَّكَ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوࣰى\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:38'),
(3851, 20, 2361, 13, 'وَأَنَا ٱخۡتَرۡتُكَ فَٱسۡتَمِعۡ لِمَا یُوحَىٰۤ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:38'),
(3852, 20, 2362, 14, 'إِنَّنِیۤ أَنَا ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّاۤ أَنَا۠ فَٱعۡبُدۡنِی وَأَقِمِ ٱلصَّلَوٰةَ لِذِكۡرِیۤ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:38'),
(3853, 20, 2363, 15, 'إِنَّ ٱلسَّاعَةَ ءَاتِیَةٌ أَكَادُ أُخۡفِیهَا لِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا تَسۡعَىٰ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:38'),
(3854, 20, 2364, 16, 'فَلَا یَصُدَّنَّكَ عَنۡهَا مَن لَّا یُؤۡمِنُ بِهَا وَٱتَّبَعَ هَوَىٰهُ فَتَرۡدَىٰ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:38'),
(3855, 20, 2365, 17, 'وَمَا تِلۡكَ بِیَمِینِكَ یَـٰمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:39'),
(3856, 20, 2366, 18, 'قَالَ هِیَ عَصَایَ أَتَوَكَّؤُا۟ عَلَیۡهَا وَأَهُشُّ بِهَا عَلَىٰ غَنَمِی وَلِیَ فِیهَا مَـَٔارِبُ أُخۡرَىٰ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:39'),
(3857, 20, 2367, 19, 'قَالَ أَلۡقِهَا یَـٰمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:29', '2023-05-27 11:13:39'),
(3858, 20, 2368, 20, 'فَأَلۡقَىٰهَا فَإِذَا هِیَ حَیَّةࣱ تَسۡعَىٰ\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3859, 20, 2369, 21, 'قَالَ خُذۡهَا وَلَا تَخَفۡۖ سَنُعِیدُهَا سِیرَتَهَا ٱلۡأُولَىٰ\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3860, 20, 2370, 22, 'وَٱضۡمُمۡ یَدَكَ إِلَىٰ جَنَاحِكَ تَخۡرُجۡ بَیۡضَاۤءَ مِنۡ غَیۡرِ سُوۤءٍ ءَایَةً أُخۡرَىٰ\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3861, 20, 2371, 23, 'لِنُرِیَكَ مِنۡ ءَایَـٰتِنَا ٱلۡكُبۡرَى\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3862, 20, 2372, 24, 'ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3863, 20, 2373, 25, 'قَالَ رَبِّ ٱشۡرَحۡ لِی صَدۡرِی\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3864, 20, 2374, 26, 'وَیَسِّرۡ لِیۤ أَمۡرِی\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3865, 20, 2375, 27, 'وَٱحۡلُلۡ عُقۡدَةࣰ مِّن لِّسَانِی\n', NULL, 0, '2023-05-26 17:29:30', '2023-05-27 11:13:39'),
(3866, 20, 2376, 28, 'یَفۡقَهُوا۟ قَوۡلِی\n', NULL, 0, '2023-05-26 17:29:31', '2023-05-27 11:13:39'),
(3867, 20, 2377, 29, 'وَٱجۡعَل لِّی وَزِیرࣰا مِّنۡ أَهۡلِی\n', NULL, 0, '2023-05-26 17:29:31', '2023-05-27 11:13:39'),
(3868, 20, 2378, 30, 'هَـٰرُونَ أَخِی\n', NULL, 0, '2023-05-26 17:29:31', '2023-05-27 11:13:39'),
(3869, 20, 2379, 31, 'ٱشۡدُدۡ بِهِۦۤ أَزۡرِی\n', NULL, 0, '2023-05-26 17:29:31', '2023-05-27 11:13:39'),
(3870, 20, 2380, 32, 'وَأَشۡرِكۡهُ فِیۤ أَمۡرِی\n', NULL, 0, '2023-05-26 17:29:31', '2023-05-27 11:13:39'),
(3871, 20, 2381, 33, 'كَیۡ نُسَبِّحَكَ كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:29:31', '2023-05-27 11:13:40'),
(3872, 20, 2382, 34, 'وَنَذۡكُرَكَ كَثِیرًا\n', NULL, 0, '2023-05-26 17:29:31', '2023-05-27 11:13:40'),
(3873, 20, 2383, 35, 'إِنَّكَ كُنتَ بِنَا بَصِیرࣰا\n', NULL, 0, '2023-05-26 17:29:32', '2023-05-27 11:13:40'),
(3874, 20, 2384, 36, 'قَالَ قَدۡ أُوتِیتَ سُؤۡلَكَ یَـٰمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:32', '2023-05-27 11:13:40'),
(3875, 20, 2385, 37, 'وَلَقَدۡ مَنَنَّا عَلَیۡكَ مَرَّةً أُخۡرَىٰۤ\n', NULL, 0, '2023-05-26 17:29:32', '2023-05-27 11:13:40'),
(3876, 20, 2386, 38, 'إِذۡ أَوۡحَیۡنَاۤ إِلَىٰۤ أُمِّكَ مَا یُوحَىٰۤ\n', NULL, 0, '2023-05-26 17:29:32', '2023-05-27 11:13:40'),
(3877, 20, 2387, 39, 'أَنِ ٱقۡذِفِیهِ فِی ٱلتَّابُوتِ فَٱقۡذِفِیهِ فِی ٱلۡیَمِّ فَلۡیُلۡقِهِ ٱلۡیَمُّ بِٱلسَّاحِلِ یَأۡخُذۡهُ عَدُوࣱّ لِّی وَعَدُوࣱّ لَّهُۥۚ وَأَلۡقَیۡتُ عَلَیۡكَ مَحَبَّةࣰ مِّنِّی وَلِتُصۡنَعَ عَلَىٰ عَیۡنِیۤ\n', NULL, 0, '2023-05-26 17:29:32', '2023-05-27 11:13:40'),
(3878, 20, 2388, 40, 'إِذۡ تَمۡشِیۤ أُخۡتُكَ فَتَقُولُ هَلۡ أَدُلُّكُمۡ عَلَىٰ مَن یَكۡفُلُهُۥۖ فَرَجَعۡنَـٰكَ إِلَىٰۤ أُمِّكَ كَیۡ تَقَرَّ عَیۡنُهَا وَلَا تَحۡزَنَۚ وَقَتَلۡتَ نَفۡسࣰا فَنَجَّیۡنَـٰكَ مِنَ ٱلۡغَمِّ وَفَتَنَّـٰكَ فُتُونࣰاۚ فَلَبِثۡتَ سِنِینَ فِیۤ أَهۡلِ مَدۡیَنَ ثُمَّ جِئۡتَ عَلَىٰ قَدَرࣲ یَـٰمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:32', '2023-05-27 11:13:40'),
(3879, 20, 2389, 41, 'وَٱصۡطَنَعۡتُكَ لِنَفۡسِی\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3880, 20, 2390, 42, 'ٱذۡهَبۡ أَنتَ وَأَخُوكَ بِـَٔایَـٰتِی وَلَا تَنِیَا فِی ذِكۡرِی\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3881, 20, 2391, 43, 'ٱذۡهَبَاۤ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3882, 20, 2392, 44, 'فَقُولَا لَهُۥ قَوۡلࣰا لَّیِّنࣰا لَّعَلَّهُۥ یَتَذَكَّرُ أَوۡ یَخۡشَىٰ\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3883, 20, 2393, 45, 'قَالَا رَبَّنَاۤ إِنَّنَا نَخَافُ أَن یَفۡرُطَ عَلَیۡنَاۤ أَوۡ أَن یَطۡغَىٰ\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3884, 20, 2394, 46, 'قَالَ لَا تَخَافَاۤۖ إِنَّنِی مَعَكُمَاۤ أَسۡمَعُ وَأَرَىٰ\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3885, 20, 2395, 47, 'فَأۡتِیَاهُ فَقُولَاۤ إِنَّا رَسُولَا رَبِّكَ فَأَرۡسِلۡ مَعَنَا بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ وَلَا تُعَذِّبۡهُمۡۖ قَدۡ جِئۡنَـٰكَ بِـَٔایَةࣲ مِّن رَّبِّكَۖ وَٱلسَّلَـٰمُ عَلَىٰ مَنِ ٱتَّبَعَ ٱلۡهُدَىٰۤ\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3886, 20, 2396, 48, 'إِنَّا قَدۡ أُوحِیَ إِلَیۡنَاۤ أَنَّ ٱلۡعَذَابَ عَلَىٰ مَن كَذَّبَ وَتَوَلَّىٰ\n', NULL, 0, '2023-05-26 17:29:33', '2023-05-27 11:13:40'),
(3887, 20, 2397, 49, 'قَالَ فَمَن رَّبُّكُمَا یَـٰمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3888, 20, 2398, 50, 'قَالَ رَبُّنَا ٱلَّذِیۤ أَعۡطَىٰ كُلَّ شَیۡءٍ خَلۡقَهُۥ ثُمَّ هَدَىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3889, 20, 2399, 51, 'قَالَ فَمَا بَالُ ٱلۡقُرُونِ ٱلۡأُولَىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3890, 20, 2400, 52, 'قَالَ عِلۡمُهَا عِندَ رَبِّی فِی كِتَـٰبࣲۖ لَّا یَضِلُّ رَبِّی وَلَا یَنسَى\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3891, 20, 2401, 53, 'ٱلَّذِی جَعَلَ لَكُمُ ٱلۡأَرۡضَ مَهۡدࣰا وَسَلَكَ لَكُمۡ فِیهَا سُبُلࣰا وَأَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَخۡرَجۡنَا بِهِۦۤ أَزۡوَ ٰ⁠جࣰا مِّن نَّبَاتࣲ شَتَّىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3892, 20, 2402, 54, 'كُلُوا۟ وَٱرۡعَوۡا۟ أَنۡعَـٰمَكُمۡۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّأُو۟لِی ٱلنُّهَىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3893, 20, 2403, 55, '۞ مِنۡهَا خَلَقۡنَـٰكُمۡ وَفِیهَا نُعِیدُكُمۡ وَمِنۡهَا نُخۡرِجُكُمۡ تَارَةً أُخۡرَىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3894, 20, 2404, 56, 'وَلَقَدۡ أَرَیۡنَـٰهُ ءَایَـٰتِنَا كُلَّهَا فَكَذَّبَ وَأَبَىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3895, 20, 2405, 57, 'قَالَ أَجِئۡتَنَا لِتُخۡرِجَنَا مِنۡ أَرۡضِنَا بِسِحۡرِكَ یَـٰمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3896, 20, 2406, 58, 'فَلَنَأۡتِیَنَّكَ بِسِحۡرࣲ مِّثۡلِهِۦ فَٱجۡعَلۡ بَیۡنَنَا وَبَیۡنَكَ مَوۡعِدࣰا لَّا نُخۡلِفُهُۥ نَحۡنُ وَلَاۤ أَنتَ مَكَانࣰا سُوࣰى\n', NULL, 0, '2023-05-26 17:29:34', '2023-05-27 11:13:41'),
(3897, 20, 2407, 59, 'قَالَ مَوۡعِدُكُمۡ یَوۡمُ ٱلزِّینَةِ وَأَن یُحۡشَرَ ٱلنَّاسُ ضُحࣰى\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3898, 20, 2408, 60, 'فَتَوَلَّىٰ فِرۡعَوۡنُ فَجَمَعَ كَیۡدَهُۥ ثُمَّ أَتَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3899, 20, 2409, 61, 'قَالَ لَهُم مُّوسَىٰ وَیۡلَكُمۡ لَا تَفۡتَرُوا۟ عَلَى ٱللَّهِ كَذِبࣰا فَیُسۡحِتَكُم بِعَذَابࣲۖ وَقَدۡ خَابَ مَنِ ٱفۡتَرَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3900, 20, 2410, 62, 'فَتَنَـٰزَعُوۤا۟ أَمۡرَهُم بَیۡنَهُمۡ وَأَسَرُّوا۟ ٱلنَّجۡوَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3901, 20, 2411, 63, 'قَالُوۤا۟ إِنۡ هَـٰذَ ٰ⁠نِ لَسَـٰحِرَ ٰ⁠نِ یُرِیدَانِ أَن یُخۡرِجَاكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِمَا وَیَذۡهَبَا بِطَرِیقَتِكُمُ ٱلۡمُثۡلَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3902, 20, 2412, 64, 'فَأَجۡمِعُوا۟ كَیۡدَكُمۡ ثُمَّ ٱئۡتُوا۟ صَفࣰّاۚ وَقَدۡ أَفۡلَحَ ٱلۡیَوۡمَ مَنِ ٱسۡتَعۡلَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3903, 20, 2413, 65, 'قَالُوا۟ یَـٰمُوسَىٰۤ إِمَّاۤ أَن تُلۡقِیَ وَإِمَّاۤ أَن نَّكُونَ أَوَّلَ مَنۡ أَلۡقَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3904, 20, 2414, 66, 'قَالَ بَلۡ أَلۡقُوا۟ۖ فَإِذَا حِبَالُهُمۡ وَعِصِیُّهُمۡ یُخَیَّلُ إِلَیۡهِ مِن سِحۡرِهِمۡ أَنَّهَا تَسۡعَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:41'),
(3905, 20, 2415, 67, 'فَأَوۡجَسَ فِی نَفۡسِهِۦ خِیفَةࣰ مُّوسَىٰ\n', NULL, 0, '2023-05-26 17:29:35', '2023-05-27 11:13:42'),
(3906, 20, 2416, 68, 'قُلۡنَا لَا تَخَفۡ إِنَّكَ أَنتَ ٱلۡأَعۡلَىٰ\n', NULL, 0, '2023-05-26 17:29:36', '2023-05-27 11:13:42'),
(3907, 20, 2417, 69, 'وَأَلۡقِ مَا فِی یَمِینِكَ تَلۡقَفۡ مَا صَنَعُوۤا۟ۖ إِنَّمَا صَنَعُوا۟ كَیۡدُ سَـٰحِرࣲۖ وَلَا یُفۡلِحُ ٱلسَّاحِرُ حَیۡثُ أَتَىٰ\n', NULL, 0, '2023-05-26 17:29:36', '2023-05-27 11:13:42'),
(3908, 20, 2418, 70, 'فَأُلۡقِیَ ٱلسَّحَرَةُ سُجَّدࣰا قَالُوۤا۟ ءَامَنَّا بِرَبِّ هَـٰرُونَ وَمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:36', '2023-05-27 11:13:42'),
(3909, 20, 2419, 71, 'قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِیرُكُمُ ٱلَّذِی عَلَّمَكُمُ ٱلسِّحۡرَۖ فَلَأُقَطِّعَنَّ أَیۡدِیَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَـٰفࣲ وَلَأُصَلِّبَنَّكُمۡ فِی جُذُوعِ ٱلنَّخۡلِ وَلَتَعۡلَمُنَّ أَیُّنَاۤ أَشَدُّ عَذَابࣰا وَأَبۡقَىٰ\n', NULL, 0, '2023-05-26 17:29:36', '2023-05-27 11:13:42'),
(3910, 20, 2420, 72, 'قَالُوا۟ لَن نُّؤۡثِرَكَ عَلَىٰ مَا جَاۤءَنَا مِنَ ٱلۡبَیِّنَـٰتِ وَٱلَّذِی فَطَرَنَاۖ فَٱقۡضِ مَاۤ أَنتَ قَاضٍۖ إِنَّمَا تَقۡضِی هَـٰذِهِ ٱلۡحَیَوٰةَ ٱلدُّنۡیَاۤ\n', NULL, 0, '2023-05-26 17:29:36', '2023-05-27 11:13:42'),
(3911, 20, 2421, 73, 'إِنَّاۤ ءَامَنَّا بِرَبِّنَا لِیَغۡفِرَ لَنَا خَطَـٰیَـٰنَا وَمَاۤ أَكۡرَهۡتَنَا عَلَیۡهِ مِنَ ٱلسِّحۡرِۗ وَٱللَّهُ خَیۡرࣱ وَأَبۡقَىٰۤ\n', NULL, 0, '2023-05-26 17:29:36', '2023-05-27 11:13:42'),
(3912, 20, 2422, 74, 'إِنَّهُۥ مَن یَأۡتِ رَبَّهُۥ مُجۡرِمࣰا فَإِنَّ لَهُۥ جَهَنَّمَ لَا یَمُوتُ فِیهَا وَلَا یَحۡیَىٰ\n', NULL, 0, '2023-05-26 17:29:37', '2023-05-27 11:13:42'),
(3913, 20, 2423, 75, 'وَمَن یَأۡتِهِۦ مُؤۡمِنࣰا قَدۡ عَمِلَ ٱلصَّـٰلِحَـٰتِ فَأُو۟لَـٰۤىِٕكَ لَهُمُ ٱلدَّرَجَـٰتُ ٱلۡعُلَىٰ\n', NULL, 0, '2023-05-26 17:29:37', '2023-05-27 11:13:42');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(3914, 20, 2424, 76, 'جَنَّـٰتُ عَدۡنࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ وَذَ ٰ⁠لِكَ جَزَاۤءُ مَن تَزَكَّىٰ\n', NULL, 0, '2023-05-26 17:29:37', '2023-05-27 11:13:42'),
(3915, 20, 2425, 77, 'وَلَقَدۡ أَوۡحَیۡنَاۤ إِلَىٰ مُوسَىٰۤ أَنۡ أَسۡرِ بِعِبَادِی فَٱضۡرِبۡ لَهُمۡ طَرِیقࣰا فِی ٱلۡبَحۡرِ یَبَسࣰا لَّا تَخَـٰفُ دَرَكࣰا وَلَا تَخۡشَىٰ\n', NULL, 0, '2023-05-26 17:29:37', '2023-05-27 11:13:42'),
(3916, 20, 2426, 78, 'فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ بِجُنُودِهِۦ فَغَشِیَهُم مِّنَ ٱلۡیَمِّ مَا غَشِیَهُمۡ\n', NULL, 0, '2023-05-26 17:29:37', '2023-05-27 11:13:42'),
(3917, 20, 2427, 79, 'وَأَضَلَّ فِرۡعَوۡنُ قَوۡمَهُۥ وَمَا هَدَىٰ\n', NULL, 0, '2023-05-26 17:29:37', '2023-05-27 11:13:42'),
(3918, 20, 2428, 80, 'یَـٰبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ قَدۡ أَنجَیۡنَـٰكُم مِّنۡ عَدُوِّكُمۡ وَوَ ٰ⁠عَدۡنَـٰكُمۡ جَانِبَ ٱلطُّورِ ٱلۡأَیۡمَنَ وَنَزَّلۡنَا عَلَیۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰ\n', NULL, 0, '2023-05-26 17:29:37', '2023-05-27 11:13:42'),
(3919, 20, 2429, 81, 'كُلُوا۟ مِن طَیِّبَـٰتِ مَا رَزَقۡنَـٰكُمۡ وَلَا تَطۡغَوۡا۟ فِیهِ فَیَحِلَّ عَلَیۡكُمۡ غَضَبِیۖ وَمَن یَحۡلِلۡ عَلَیۡهِ غَضَبِی فَقَدۡ هَوَىٰ\n', NULL, 0, '2023-05-26 17:29:38', '2023-05-27 11:13:42'),
(3920, 20, 2430, 82, 'وَإِنِّی لَغَفَّارࣱ لِّمَن تَابَ وَءَامَنَ وَعَمِلَ صَـٰلِحࣰا ثُمَّ ٱهۡتَدَىٰ\n', NULL, 0, '2023-05-26 17:29:38', '2023-05-27 11:13:42'),
(3921, 20, 2431, 83, '۞ وَمَاۤ أَعۡجَلَكَ عَن قَوۡمِكَ یَـٰمُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:38', '2023-05-27 11:13:42'),
(3922, 20, 2432, 84, 'قَالَ هُمۡ أُو۟لَاۤءِ عَلَىٰۤ أَثَرِی وَعَجِلۡتُ إِلَیۡكَ رَبِّ لِتَرۡضَىٰ\n', NULL, 0, '2023-05-26 17:29:38', '2023-05-27 11:13:42'),
(3923, 20, 2433, 85, 'قَالَ فَإِنَّا قَدۡ فَتَنَّا قَوۡمَكَ مِنۢ بَعۡدِكَ وَأَضَلَّهُمُ ٱلسَّامِرِیُّ\n', NULL, 0, '2023-05-26 17:29:38', '2023-05-27 11:13:43'),
(3924, 20, 2434, 86, 'فَرَجَعَ مُوسَىٰۤ إِلَىٰ قَوۡمِهِۦ غَضۡبَـٰنَ أَسِفࣰاۚ قَالَ یَـٰقَوۡمِ أَلَمۡ یَعِدۡكُمۡ رَبُّكُمۡ وَعۡدًا حَسَنًاۚ أَفَطَالَ عَلَیۡكُمُ ٱلۡعَهۡدُ أَمۡ أَرَدتُّمۡ أَن یَحِلَّ عَلَیۡكُمۡ غَضَبࣱ مِّن رَّبِّكُمۡ فَأَخۡلَفۡتُم مَّوۡعِدِی\n', NULL, 0, '2023-05-26 17:29:38', '2023-05-27 11:13:43'),
(3925, 20, 2435, 87, 'قَالُوا۟ مَاۤ أَخۡلَفۡنَا مَوۡعِدَكَ بِمَلۡكِنَا وَلَـٰكِنَّا حُمِّلۡنَاۤ أَوۡزَارࣰا مِّن زِینَةِ ٱلۡقَوۡمِ فَقَذَفۡنَـٰهَا فَكَذَ ٰ⁠لِكَ أَلۡقَى ٱلسَّامِرِیُّ\n', NULL, 0, '2023-05-26 17:29:38', '2023-05-27 11:13:43'),
(3926, 20, 2436, 88, 'فَأَخۡرَجَ لَهُمۡ عِجۡلࣰا جَسَدࣰا لَّهُۥ خُوَارࣱ فَقَالُوا۟ هَـٰذَاۤ إِلَـٰهُكُمۡ وَإِلَـٰهُ مُوسَىٰ فَنَسِیَ\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3927, 20, 2437, 89, 'أَفَلَا یَرَوۡنَ أَلَّا یَرۡجِعُ إِلَیۡهِمۡ قَوۡلࣰا وَلَا یَمۡلِكُ لَهُمۡ ضَرࣰّا وَلَا نَفۡعࣰا\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3928, 20, 2438, 90, 'وَلَقَدۡ قَالَ لَهُمۡ هَـٰرُونُ مِن قَبۡلُ یَـٰقَوۡمِ إِنَّمَا فُتِنتُم بِهِۦۖ وَإِنَّ رَبَّكُمُ ٱلرَّحۡمَـٰنُ فَٱتَّبِعُونِی وَأَطِیعُوۤا۟ أَمۡرِی\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3929, 20, 2439, 91, 'قَالُوا۟ لَن نَّبۡرَحَ عَلَیۡهِ عَـٰكِفِینَ حَتَّىٰ یَرۡجِعَ إِلَیۡنَا مُوسَىٰ\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3930, 20, 2440, 92, 'قَالَ یَـٰهَـٰرُونُ مَا مَنَعَكَ إِذۡ رَأَیۡتَهُمۡ ضَلُّوۤا۟\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3931, 20, 2441, 93, 'أَلَّا تَتَّبِعَنِۖ أَفَعَصَیۡتَ أَمۡرِی\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3932, 20, 2442, 94, 'قَالَ یَبۡنَؤُمَّ لَا تَأۡخُذۡ بِلِحۡیَتِی وَلَا بِرَأۡسِیۤۖ إِنِّی خَشِیتُ أَن تَقُولَ فَرَّقۡتَ بَیۡنَ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ وَلَمۡ تَرۡقُبۡ قَوۡلِی\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3933, 20, 2443, 95, 'قَالَ فَمَا خَطۡبُكَ یَـٰسَـٰمِرِیُّ\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3934, 20, 2444, 96, 'قَالَ بَصُرۡتُ بِمَا لَمۡ یَبۡصُرُوا۟ بِهِۦ فَقَبَضۡتُ قَبۡضَةࣰ مِّنۡ أَثَرِ ٱلرَّسُولِ فَنَبَذۡتُهَا وَكَذَ ٰ⁠لِكَ سَوَّلَتۡ لِی نَفۡسِی\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3935, 20, 2445, 97, 'قَالَ فَٱذۡهَبۡ فَإِنَّ لَكَ فِی ٱلۡحَیَوٰةِ أَن تَقُولَ لَا مِسَاسَۖ وَإِنَّ لَكَ مَوۡعِدࣰا لَّن تُخۡلَفَهُۥۖ وَٱنظُرۡ إِلَىٰۤ إِلَـٰهِكَ ٱلَّذِی ظَلۡتَ عَلَیۡهِ عَاكِفࣰاۖ لَّنُحَرِّقَنَّهُۥ ثُمَّ لَنَنسِفَنَّهُۥ فِی ٱلۡیَمِّ نَسۡفًا\n', NULL, 0, '2023-05-26 17:29:39', '2023-05-27 11:13:43'),
(3936, 20, 2446, 98, 'إِنَّمَاۤ إِلَـٰهُكُمُ ٱللَّهُ ٱلَّذِی لَاۤ إِلَـٰهَ إِلَّا هُوَۚ وَسِعَ كُلَّ شَیۡءٍ عِلۡمࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:43'),
(3937, 20, 2447, 99, 'كَذَ ٰ⁠لِكَ نَقُصُّ عَلَیۡكَ مِنۡ أَنۢبَاۤءِ مَا قَدۡ سَبَقَۚ وَقَدۡ ءَاتَیۡنَـٰكَ مِن لَّدُنَّا ذِكۡرࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:43'),
(3938, 20, 2448, 100, 'مَّنۡ أَعۡرَضَ عَنۡهُ فَإِنَّهُۥ یَحۡمِلُ یَوۡمَ ٱلۡقِیَـٰمَةِ وِزۡرًا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:43'),
(3939, 20, 2449, 101, 'خَـٰلِدِینَ فِیهِۖ وَسَاۤءَ لَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ حِمۡلࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:43'),
(3940, 20, 2450, 102, 'یَوۡمَ یُنفَخُ فِی ٱلصُّورِۚ وَنَحۡشُرُ ٱلۡمُجۡرِمِینَ یَوۡمَىِٕذࣲ زُرۡقࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:43'),
(3941, 20, 2451, 103, 'یَتَخَـٰفَتُونَ بَیۡنَهُمۡ إِن لَّبِثۡتُمۡ إِلَّا عَشۡرࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:44'),
(3942, 20, 2452, 104, 'نَّحۡنُ أَعۡلَمُ بِمَا یَقُولُونَ إِذۡ یَقُولُ أَمۡثَلُهُمۡ طَرِیقَةً إِن لَّبِثۡتُمۡ إِلَّا یَوۡمࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:44'),
(3943, 20, 2453, 105, 'وَیَسۡـَٔلُونَكَ عَنِ ٱلۡجِبَالِ فَقُلۡ یَنسِفُهَا رَبِّی نَسۡفࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:44'),
(3944, 20, 2454, 106, 'فَیَذَرُهَا قَاعࣰا صَفۡصَفࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:44'),
(3945, 20, 2455, 107, 'لَّا تَرَىٰ فِیهَا عِوَجࣰا وَلَاۤ أَمۡتࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:44'),
(3946, 20, 2456, 108, 'یَوۡمَىِٕذࣲ یَتَّبِعُونَ ٱلدَّاعِیَ لَا عِوَجَ لَهُۥۖ وَخَشَعَتِ ٱلۡأَصۡوَاتُ لِلرَّحۡمَـٰنِ فَلَا تَسۡمَعُ إِلَّا هَمۡسࣰا\n', NULL, 0, '2023-05-26 17:29:40', '2023-05-27 11:13:44'),
(3947, 20, 2457, 109, 'یَوۡمَىِٕذࣲ لَّا تَنفَعُ ٱلشَّفَـٰعَةُ إِلَّا مَنۡ أَذِنَ لَهُ ٱلرَّحۡمَـٰنُ وَرَضِیَ لَهُۥ قَوۡلࣰا\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3948, 20, 2458, 110, 'یَعۡلَمُ مَا بَیۡنَ أَیۡدِیهِمۡ وَمَا خَلۡفَهُمۡ وَلَا یُحِیطُونَ بِهِۦ عِلۡمࣰا\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3949, 20, 2459, 111, '۞ وَعَنَتِ ٱلۡوُجُوهُ لِلۡحَیِّ ٱلۡقَیُّومِۖ وَقَدۡ خَابَ مَنۡ حَمَلَ ظُلۡمࣰا\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3950, 20, 2460, 112, 'وَمَن یَعۡمَلۡ مِنَ ٱلصَّـٰلِحَـٰتِ وَهُوَ مُؤۡمِنࣱ فَلَا یَخَافُ ظُلۡمࣰا وَلَا هَضۡمࣰا\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3951, 20, 2461, 113, 'وَكَذَ ٰ⁠لِكَ أَنزَلۡنَـٰهُ قُرۡءَانًا عَرَبِیࣰّا وَصَرَّفۡنَا فِیهِ مِنَ ٱلۡوَعِیدِ لَعَلَّهُمۡ یَتَّقُونَ أَوۡ یُحۡدِثُ لَهُمۡ ذِكۡرࣰا\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3952, 20, 2462, 114, 'فَتَعَـٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۗ وَلَا تَعۡجَلۡ بِٱلۡقُرۡءَانِ مِن قَبۡلِ أَن یُقۡضَىٰۤ إِلَیۡكَ وَحۡیُهُۥۖ وَقُل رَّبِّ زِدۡنِی عِلۡمࣰا\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3953, 20, 2463, 115, 'وَلَقَدۡ عَهِدۡنَاۤ إِلَىٰۤ ءَادَمَ مِن قَبۡلُ فَنَسِیَ وَلَمۡ نَجِدۡ لَهُۥ عَزۡمࣰا\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3954, 20, 2464, 116, 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰۤىِٕكَةِ ٱسۡجُدُوا۟ لِـَٔادَمَ فَسَجَدُوۤا۟ إِلَّاۤ إِبۡلِیسَ أَبَىٰ\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3955, 20, 2465, 117, 'فَقُلۡنَا یَـٰۤـَٔادَمُ إِنَّ هَـٰذَا عَدُوࣱّ لَّكَ وَلِزَوۡجِكَ فَلَا یُخۡرِجَنَّكُمَا مِنَ ٱلۡجَنَّةِ فَتَشۡقَىٰۤ\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3956, 20, 2466, 118, 'إِنَّ لَكَ أَلَّا تَجُوعَ فِیهَا وَلَا تَعۡرَىٰ\n', NULL, 0, '2023-05-26 17:29:41', '2023-05-27 11:13:44'),
(3957, 20, 2467, 119, 'وَأَنَّكَ لَا تَظۡمَؤُا۟ فِیهَا وَلَا تَضۡحَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:44'),
(3958, 20, 2468, 120, 'فَوَسۡوَسَ إِلَیۡهِ ٱلشَّیۡطَـٰنُ قَالَ یَـٰۤـَٔادَمُ هَلۡ أَدُلُّكَ عَلَىٰ شَجَرَةِ ٱلۡخُلۡدِ وَمُلۡكࣲ لَّا یَبۡلَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3959, 20, 2469, 121, 'فَأَكَلَا مِنۡهَا فَبَدَتۡ لَهُمَا سَوۡءَ ٰ⁠ تُهُمَا وَطَفِقَا یَخۡصِفَانِ عَلَیۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۚ وَعَصَىٰۤ ءَادَمُ رَبَّهُۥ فَغَوَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3960, 20, 2470, 122, 'ثُمَّ ٱجۡتَبَـٰهُ رَبُّهُۥ فَتَابَ عَلَیۡهِ وَهَدَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3961, 20, 2471, 123, 'قَالَ ٱهۡبِطَا مِنۡهَا جَمِیعَۢاۖ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوࣱّۖ فَإِمَّا یَأۡتِیَنَّكُم مِّنِّی هُدࣰى فَمَنِ ٱتَّبَعَ هُدَایَ فَلَا یَضِلُّ وَلَا یَشۡقَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3962, 20, 2472, 124, 'وَمَنۡ أَعۡرَضَ عَن ذِكۡرِی فَإِنَّ لَهُۥ مَعِیشَةࣰ ضَنكࣰا وَنَحۡشُرُهُۥ یَوۡمَ ٱلۡقِیَـٰمَةِ أَعۡمَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3963, 20, 2473, 125, 'قَالَ رَبِّ لِمَ حَشَرۡتَنِیۤ أَعۡمَىٰ وَقَدۡ كُنتُ بَصِیرࣰا\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3964, 20, 2474, 126, 'قَالَ كَذَ ٰ⁠لِكَ أَتَتۡكَ ءَایَـٰتُنَا فَنَسِیتَهَاۖ وَكَذَ ٰ⁠لِكَ ٱلۡیَوۡمَ تُنسَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3965, 20, 2475, 127, 'وَكَذَ ٰ⁠لِكَ نَجۡزِی مَنۡ أَسۡرَفَ وَلَمۡ یُؤۡمِنۢ بِـَٔایَـٰتِ رَبِّهِۦۚ وَلَعَذَابُ ٱلۡـَٔاخِرَةِ أَشَدُّ وَأَبۡقَىٰۤ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3966, 20, 2476, 128, 'أَفَلَمۡ یَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ یَمۡشُونَ فِی مَسَـٰكِنِهِمۡۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّأُو۟لِی ٱلنُّهَىٰ\n', NULL, 0, '2023-05-26 17:29:42', '2023-05-27 11:13:45'),
(3967, 20, 2477, 129, 'وَلَوۡلَا كَلِمَةࣱ سَبَقَتۡ مِن رَّبِّكَ لَكَانَ لِزَامࣰا وَأَجَلࣱ مُّسَمࣰّى\n', NULL, 0, '2023-05-26 17:29:43', '2023-05-27 11:13:45'),
(3968, 20, 2478, 130, 'فَٱصۡبِرۡ عَلَىٰ مَا یَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ غُرُوبِهَاۖ وَمِنۡ ءَانَاۤىِٕ ٱلَّیۡلِ فَسَبِّحۡ وَأَطۡرَافَ ٱلنَّهَارِ لَعَلَّكَ تَرۡضَىٰ\n', NULL, 0, '2023-05-26 17:29:43', '2023-05-27 11:13:45'),
(3969, 20, 2479, 131, 'وَلَا تَمُدَّنَّ عَیۡنَیۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦۤ أَزۡوَ ٰ⁠جࣰا مِّنۡهُمۡ زَهۡرَةَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا لِنَفۡتِنَهُمۡ فِیهِۚ وَرِزۡقُ رَبِّكَ خَیۡرࣱ وَأَبۡقَىٰ\n', NULL, 0, '2023-05-26 17:29:43', '2023-05-27 11:13:45'),
(3970, 20, 2480, 132, 'وَأۡمُرۡ أَهۡلَكَ بِٱلصَّلَوٰةِ وَٱصۡطَبِرۡ عَلَیۡهَاۖ لَا نَسۡـَٔلُكَ رِزۡقࣰاۖ نَّحۡنُ نَرۡزُقُكَۗ وَٱلۡعَـٰقِبَةُ لِلتَّقۡوَىٰ\n', NULL, 0, '2023-05-26 17:29:43', '2023-05-27 11:13:45'),
(3971, 20, 2481, 133, 'وَقَالُوا۟ لَوۡلَا یَأۡتِینَا بِـَٔایَةࣲ مِّن رَّبِّهِۦۤۚ أَوَلَمۡ تَأۡتِهِم بَیِّنَةُ مَا فِی ٱلصُّحُفِ ٱلۡأُولَىٰ\n', NULL, 0, '2023-05-26 17:29:43', '2023-05-27 11:13:45'),
(3972, 20, 2482, 134, 'وَلَوۡ أَنَّاۤ أَهۡلَكۡنَـٰهُم بِعَذَابࣲ مِّن قَبۡلِهِۦ لَقَالُوا۟ رَبَّنَا لَوۡلَاۤ أَرۡسَلۡتَ إِلَیۡنَا رَسُولࣰا فَنَتَّبِعَ ءَایَـٰتِكَ مِن قَبۡلِ أَن نَّذِلَّ وَنَخۡزَىٰ\n', NULL, 0, '2023-05-26 17:29:43', '2023-05-27 11:13:45'),
(3973, 20, 2483, 135, 'قُلۡ كُلࣱّ مُّتَرَبِّصࣱ فَتَرَبَّصُوا۟ۖ فَسَتَعۡلَمُونَ مَنۡ أَصۡحَـٰبُ ٱلصِّرَ ٰ⁠طِ ٱلسَّوِیِّ وَمَنِ ٱهۡتَدَىٰ\n', NULL, 0, '2023-05-26 17:29:43', '2023-05-27 11:13:45'),
(3974, 14, 1751, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤرۚ كِتَـٰبٌ أَنزَلۡنَـٰهُ إِلَیۡكَ لِتُخۡرِجَ ٱلنَّاسَ مِنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِ بِإِذۡنِ رَبِّهِمۡ إِلَىٰ صِرَ ٰ⁠طِ ٱلۡعَزِیزِ ٱلۡحَمِیدِ\n', NULL, 0, '2023-05-26 17:30:20', '2023-05-27 11:17:25'),
(3975, 14, 1752, 2, 'ٱللَّهِ ٱلَّذِی لَهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۗ وَوَیۡلࣱ لِّلۡكَـٰفِرِینَ مِنۡ عَذَابࣲ شَدِیدٍ\n', NULL, 0, '2023-05-26 17:30:20', '2023-05-27 11:17:25'),
(3976, 14, 1753, 3, 'ٱلَّذِینَ یَسۡتَحِبُّونَ ٱلۡحَیَوٰةَ ٱلدُّنۡیَا عَلَى ٱلۡـَٔاخِرَةِ وَیَصُدُّونَ عَن سَبِیلِ ٱللَّهِ وَیَبۡغُونَهَا عِوَجًاۚ أُو۟لَـٰۤىِٕكَ فِی ضَلَـٰلِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:30:20', '2023-05-27 11:17:25'),
(3977, 14, 1754, 4, 'وَمَاۤ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا بِلِسَانِ قَوۡمِهِۦ لِیُبَیِّنَ لَهُمۡۖ فَیُضِلُّ ٱللَّهُ مَن یَشَاۤءُ وَیَهۡدِی مَن یَشَاۤءُۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:30:20', '2023-05-27 11:17:25'),
(3978, 14, 1755, 5, 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔایَـٰتِنَاۤ أَنۡ أَخۡرِجۡ قَوۡمَكَ مِنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِ وَذَكِّرۡهُم بِأَیَّىٰمِ ٱللَّهِۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّكُلِّ صَبَّارࣲ شَكُورࣲ\n', NULL, 0, '2023-05-26 17:30:20', '2023-05-27 11:17:25'),
(3979, 14, 1756, 6, 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِ ٱذۡكُرُوا۟ نِعۡمَةَ ٱللَّهِ عَلَیۡكُمۡ إِذۡ أَنجَىٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ یَسُومُونَكُمۡ سُوۤءَ ٱلۡعَذَابِ وَیُذَبِّحُونَ أَبۡنَاۤءَكُمۡ وَیَسۡتَحۡیُونَ نِسَاۤءَكُمۡۚ وَفِی ذَ ٰ⁠لِكُم بَلَاۤءࣱ مِّن رَّبِّكُمۡ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:30:20', '2023-05-27 11:17:25'),
(3980, 14, 1757, 7, 'وَإِذۡ تَأَذَّنَ رَبُّكُمۡ لَىِٕن شَكَرۡتُمۡ لَأَزِیدَنَّكُمۡۖ وَلَىِٕن كَفَرۡتُمۡ إِنَّ عَذَابِی لَشَدِیدࣱ\n', NULL, 0, '2023-05-26 17:30:20', '2023-05-27 11:17:25'),
(3981, 14, 1758, 8, 'وَقَالَ مُوسَىٰۤ إِن تَكۡفُرُوۤا۟ أَنتُمۡ وَمَن فِی ٱلۡأَرۡضِ جَمِیعࣰا فَإِنَّ ٱللَّهَ لَغَنِیٌّ حَمِیدٌ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3982, 14, 1759, 9, 'أَلَمۡ یَأۡتِكُمۡ نَبَؤُا۟ ٱلَّذِینَ مِن قَبۡلِكُمۡ قَوۡمِ نُوحࣲ وَعَادࣲ وَثَمُودَ وَٱلَّذِینَ مِنۢ بَعۡدِهِمۡ لَا یَعۡلَمُهُمۡ إِلَّا ٱللَّهُۚ جَاۤءَتۡهُمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِ فَرَدُّوۤا۟ أَیۡدِیَهُمۡ فِیۤ أَفۡوَ ٰ⁠هِهِمۡ وَقَالُوۤا۟ إِنَّا كَفَرۡنَا بِمَاۤ أُرۡسِلۡتُم بِهِۦ وَإِنَّا لَفِی شَكࣲّ مِّمَّا تَدۡعُونَنَاۤ إِلَیۡهِ مُرِیبࣲ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3983, 14, 1760, 10, '۞ قَالَتۡ رُسُلُهُمۡ أَفِی ٱللَّهِ شَكࣱّ فَاطِرِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ یَدۡعُوكُمۡ لِیَغۡفِرَ لَكُم مِّن ذُنُوبِكُمۡ وَیُؤَخِّرَكُمۡ إِلَىٰۤ أَجَلࣲ مُّسَمࣰّىۚ قَالُوۤا۟ إِنۡ أَنتُمۡ إِلَّا بَشَرࣱ مِّثۡلُنَا تُرِیدُونَ أَن تَصُدُّونَا عَمَّا كَانَ یَعۡبُدُ ءَابَاۤؤُنَا فَأۡتُونَا بِسُلۡطَـٰنࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3984, 14, 1761, 11, 'قَالَتۡ لَهُمۡ رُسُلُهُمۡ إِن نَّحۡنُ إِلَّا بَشَرࣱ مِّثۡلُكُمۡ وَلَـٰكِنَّ ٱللَّهَ یَمُنُّ عَلَىٰ مَن یَشَاۤءُ مِنۡ عِبَادِهِۦۖ وَمَا كَانَ لَنَاۤ أَن نَّأۡتِیَكُم بِسُلۡطَـٰنٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3985, 14, 1762, 12, 'وَمَا لَنَاۤ أَلَّا نَتَوَكَّلَ عَلَى ٱللَّهِ وَقَدۡ هَدَىٰنَا سُبُلَنَاۚ وَلَنَصۡبِرَنَّ عَلَىٰ مَاۤ ءَاذَیۡتُمُونَاۚ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3986, 14, 1763, 13, 'وَقَالَ ٱلَّذِینَ كَفَرُوا۟ لِرُسُلِهِمۡ لَنُخۡرِجَنَّكُم مِّنۡ أَرۡضِنَاۤ أَوۡ لَتَعُودُنَّ فِی مِلَّتِنَاۖ فَأَوۡحَىٰۤ إِلَیۡهِمۡ رَبُّهُمۡ لَنُهۡلِكَنَّ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3987, 14, 1764, 14, 'وَلَنُسۡكِنَنَّكُمُ ٱلۡأَرۡضَ مِنۢ بَعۡدِهِمۡۚ ذَ ٰ⁠لِكَ لِمَنۡ خَافَ مَقَامِی وَخَافَ وَعِیدِ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3988, 14, 1765, 15, 'وَٱسۡتَفۡتَحُوا۟ وَخَابَ كُلُّ جَبَّارٍ عَنِیدࣲ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3989, 14, 1766, 16, 'مِّن وَرَاۤىِٕهِۦ جَهَنَّمُ وَیُسۡقَىٰ مِن مَّاۤءࣲ صَدِیدࣲ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3990, 14, 1767, 17, 'یَتَجَرَّعُهُۥ وَلَا یَكَادُ یُسِیغُهُۥ وَیَأۡتِیهِ ٱلۡمَوۡتُ مِن كُلِّ مَكَانࣲ وَمَا هُوَ بِمَیِّتࣲۖ وَمِن وَرَاۤىِٕهِۦ عَذَابٌ غَلِیظࣱ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3991, 14, 1768, 18, 'مَّثَلُ ٱلَّذِینَ كَفَرُوا۟ بِرَبِّهِمۡۖ أَعۡمَـٰلُهُمۡ كَرَمَادٍ ٱشۡتَدَّتۡ بِهِ ٱلرِّیحُ فِی یَوۡمٍ عَاصِفࣲۖ لَّا یَقۡدِرُونَ مِمَّا كَسَبُوا۟ عَلَىٰ شَیۡءࣲۚ ذَ ٰ⁠لِكَ هُوَ ٱلضَّلَـٰلُ ٱلۡبَعِیدُ\n', NULL, 0, '2023-05-26 17:30:21', '2023-05-27 11:17:26'),
(3992, 14, 1769, 19, 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِن یَشَأۡ یُذۡهِبۡكُمۡ وَیَأۡتِ بِخَلۡقࣲ جَدِیدࣲ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:26'),
(3993, 14, 1770, 20, 'وَمَا ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ بِعَزِیزࣲ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:26'),
(3994, 14, 1771, 21, 'وَبَرَزُوا۟ لِلَّهِ جَمِیعࣰا فَقَالَ ٱلضُّعَفَـٰۤؤُا۟ لِلَّذِینَ ٱسۡتَكۡبَرُوۤا۟ إِنَّا كُنَّا لَكُمۡ تَبَعࣰا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا مِنۡ عَذَابِ ٱللَّهِ مِن شَیۡءࣲۚ قَالُوا۟ لَوۡ هَدَىٰنَا ٱللَّهُ لَهَدَیۡنَـٰكُمۡۖ سَوَاۤءٌ عَلَیۡنَاۤ أَجَزِعۡنَاۤ أَمۡ صَبَرۡنَا مَا لَنَا مِن مَّحِیصࣲ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:26'),
(3995, 14, 1772, 22, 'وَقَالَ ٱلشَّیۡطَـٰنُ لَمَّا قُضِیَ ٱلۡأَمۡرُ إِنَّ ٱللَّهَ وَعَدَكُمۡ وَعۡدَ ٱلۡحَقِّ وَوَعَدتُّكُمۡ فَأَخۡلَفۡتُكُمۡۖ وَمَا كَانَ لِیَ عَلَیۡكُم مِّن سُلۡطَـٰنٍ إِلَّاۤ أَن دَعَوۡتُكُمۡ فَٱسۡتَجَبۡتُمۡ لِیۖ فَلَا تَلُومُونِی وَلُومُوۤا۟ أَنفُسَكُمۖ مَّاۤ أَنَا۠ بِمُصۡرِخِكُمۡ وَمَاۤ أَنتُم بِمُصۡرِخِیَّ إِنِّی كَفَرۡتُ بِمَاۤ أَشۡرَكۡتُمُونِ مِن قَبۡلُۗ إِنَّ ٱلظَّـٰلِمِینَ لَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:26'),
(3996, 14, 1773, 23, 'وَأُدۡخِلَ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَا بِإِذۡنِ رَبِّهِمۡۖ تَحِیَّتُهُمۡ فِیهَا سَلَـٰمٌ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:26'),
(3997, 14, 1774, 24, 'أَلَمۡ تَرَ كَیۡفَ ضَرَبَ ٱللَّهُ مَثَلࣰا كَلِمَةࣰ طَیِّبَةࣰ كَشَجَرَةࣲ طَیِّبَةٍ أَصۡلُهَا ثَابِتࣱ وَفَرۡعُهَا فِی ٱلسَّمَاۤءِ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:26'),
(3998, 14, 1775, 25, 'تُؤۡتِیۤ أُكُلَهَا كُلَّ حِینِۭ بِإِذۡنِ رَبِّهَاۗ وَیَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ لِلنَّاسِ لَعَلَّهُمۡ یَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:26'),
(3999, 14, 1776, 26, 'وَمَثَلُ كَلِمَةٍ خَبِیثَةࣲ كَشَجَرَةٍ خَبِیثَةٍ ٱجۡتُثَّتۡ مِن فَوۡقِ ٱلۡأَرۡضِ مَا لَهَا مِن قَرَارࣲ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:27'),
(4000, 14, 1777, 27, 'یُثَبِّتُ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ بِٱلۡقَوۡلِ ٱلثَّابِتِ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَفِی ٱلۡـَٔاخِرَةِۖ وَیُضِلُّ ٱللَّهُ ٱلظَّـٰلِمِینَۚ وَیَفۡعَلُ ٱللَّهُ مَا یَشَاۤءُ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:27'),
(4001, 14, 1778, 28, '۞ أَلَمۡ تَرَ إِلَى ٱلَّذِینَ بَدَّلُوا۟ نِعۡمَتَ ٱللَّهِ كُفۡرࣰا وَأَحَلُّوا۟ قَوۡمَهُمۡ دَارَ ٱلۡبَوَارِ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:27'),
(4002, 14, 1779, 29, 'جَهَنَّمَ یَصۡلَوۡنَهَاۖ وَبِئۡسَ ٱلۡقَرَارُ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:27'),
(4003, 14, 1780, 30, 'وَجَعَلُوا۟ لِلَّهِ أَندَادࣰا لِّیُضِلُّوا۟ عَن سَبِیلِهِۦۗ قُلۡ تَمَتَّعُوا۟ فَإِنَّ مَصِیرَكُمۡ إِلَى ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:30:22', '2023-05-27 11:17:27'),
(4004, 14, 1781, 31, 'قُل لِّعِبَادِیَ ٱلَّذِینَ ءَامَنُوا۟ یُقِیمُوا۟ ٱلصَّلَوٰةَ وَیُنفِقُوا۟ مِمَّا رَزَقۡنَـٰهُمۡ سِرࣰّا وَعَلَانِیَةࣰ مِّن قَبۡلِ أَن یَأۡتِیَ یَوۡمࣱ لَّا بَیۡعࣱ فِیهِ وَلَا خِلَـٰلٌ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4005, 14, 1782, 32, 'ٱللَّهُ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَأَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَ ٰ⁠تِ رِزۡقࣰا لَّكُمۡۖ وَسَخَّرَ لَكُمُ ٱلۡفُلۡكَ لِتَجۡرِیَ فِی ٱلۡبَحۡرِ بِأَمۡرِهِۦۖ وَسَخَّرَ لَكُمُ ٱلۡأَنۡهَـٰرَ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4006, 14, 1783, 33, 'وَسَخَّرَ لَكُمُ ٱلشَّمۡسَ وَٱلۡقَمَرَ دَاۤىِٕبَیۡنِۖ وَسَخَّرَ لَكُمُ ٱلَّیۡلَ وَٱلنَّهَارَ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4007, 14, 1784, 34, 'وَءَاتَىٰكُم مِّن كُلِّ مَا سَأَلۡتُمُوهُۚ وَإِن تَعُدُّوا۟ نِعۡمَتَ ٱللَّهِ لَا تُحۡصُوهَاۤۗ إِنَّ ٱلۡإِنسَـٰنَ لَظَلُومࣱ كَفَّارࣱ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4008, 14, 1785, 35, 'وَإِذۡ قَالَ إِبۡرَ ٰ⁠هِیمُ رَبِّ ٱجۡعَلۡ هَـٰذَا ٱلۡبَلَدَ ءَامِنࣰا وَٱجۡنُبۡنِی وَبَنِیَّ أَن نَّعۡبُدَ ٱلۡأَصۡنَامَ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4009, 14, 1786, 36, 'رَبِّ إِنَّهُنَّ أَضۡلَلۡنَ كَثِیرࣰا مِّنَ ٱلنَّاسِۖ فَمَن تَبِعَنِی فَإِنَّهُۥ مِنِّیۖ وَمَنۡ عَصَانِی فَإِنَّكَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4010, 14, 1787, 37, 'رَّبَّنَاۤ إِنِّیۤ أَسۡكَنتُ مِن ذُرِّیَّتِی بِوَادٍ غَیۡرِ ذِی زَرۡعٍ عِندَ بَیۡتِكَ ٱلۡمُحَرَّمِ رَبَّنَا لِیُقِیمُوا۟ ٱلصَّلَوٰةَ فَٱجۡعَلۡ أَفۡـِٔدَةࣰ مِّنَ ٱلنَّاسِ تَهۡوِیۤ إِلَیۡهِمۡ وَٱرۡزُقۡهُم مِّنَ ٱلثَّمَرَ ٰ⁠تِ لَعَلَّهُمۡ یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4011, 14, 1788, 38, 'رَبَّنَاۤ إِنَّكَ تَعۡلَمُ مَا نُخۡفِی وَمَا نُعۡلِنُۗ وَمَا یَخۡفَىٰ عَلَى ٱللَّهِ مِن شَیۡءࣲ فِی ٱلۡأَرۡضِ وَلَا فِی ٱلسَّمَاۤءِ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4012, 14, 1789, 39, 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی وَهَبَ لِی عَلَى ٱلۡكِبَرِ إِسۡمَـٰعِیلَ وَإِسۡحَـٰقَۚ إِنَّ رَبِّی لَسَمِیعُ ٱلدُّعَاۤءِ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4013, 14, 1790, 40, 'رَبِّ ٱجۡعَلۡنِی مُقِیمَ ٱلصَّلَوٰةِ وَمِن ذُرِّیَّتِیۚ رَبَّنَا وَتَقَبَّلۡ دُعَاۤءِ\n', NULL, 0, '2023-05-26 17:30:23', '2023-05-27 11:17:27'),
(4014, 14, 1791, 41, 'رَبَّنَا ٱغۡفِرۡ لِی وَلِوَ ٰ⁠لِدَیَّ وَلِلۡمُؤۡمِنِینَ یَوۡمَ یَقُومُ ٱلۡحِسَابُ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:27'),
(4015, 14, 1792, 42, 'وَلَا تَحۡسَبَنَّ ٱللَّهَ غَـٰفِلًا عَمَّا یَعۡمَلُ ٱلظَّـٰلِمُونَۚ إِنَّمَا یُؤَخِّرُهُمۡ لِیَوۡمࣲ تَشۡخَصُ فِیهِ ٱلۡأَبۡصَـٰرُ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:27'),
(4016, 14, 1793, 43, 'مُهۡطِعِینَ مُقۡنِعِی رُءُوسِهِمۡ لَا یَرۡتَدُّ إِلَیۡهِمۡ طَرۡفُهُمۡۖ وَأَفۡـِٔدَتُهُمۡ هَوَاۤءࣱ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:27'),
(4017, 14, 1794, 44, 'وَأَنذِرِ ٱلنَّاسَ یَوۡمَ یَأۡتِیهِمُ ٱلۡعَذَابُ فَیَقُولُ ٱلَّذِینَ ظَلَمُوا۟ رَبَّنَاۤ أَخِّرۡنَاۤ إِلَىٰۤ أَجَلࣲ قَرِیبࣲ نُّجِبۡ دَعۡوَتَكَ وَنَتَّبِعِ ٱلرُّسُلَۗ أَوَلَمۡ تَكُونُوۤا۟ أَقۡسَمۡتُم مِّن قَبۡلُ مَا لَكُم مِّن زَوَالࣲ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:28'),
(4018, 14, 1795, 45, 'وَسَكَنتُمۡ فِی مَسَـٰكِنِ ٱلَّذِینَ ظَلَمُوۤا۟ أَنفُسَهُمۡ وَتَبَیَّنَ لَكُمۡ كَیۡفَ فَعَلۡنَا بِهِمۡ وَضَرَبۡنَا لَكُمُ ٱلۡأَمۡثَالَ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:28'),
(4019, 14, 1796, 46, 'وَقَدۡ مَكَرُوا۟ مَكۡرَهُمۡ وَعِندَ ٱللَّهِ مَكۡرُهُمۡ وَإِن كَانَ مَكۡرُهُمۡ لِتَزُولَ مِنۡهُ ٱلۡجِبَالُ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:28'),
(4020, 14, 1797, 47, 'فَلَا تَحۡسَبَنَّ ٱللَّهَ مُخۡلِفَ وَعۡدِهِۦ رُسُلَهُۥۤۚ إِنَّ ٱللَّهَ عَزِیزࣱ ذُو ٱنتِقَامࣲ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:28'),
(4021, 14, 1798, 48, 'یَوۡمَ تُبَدَّلُ ٱلۡأَرۡضُ غَیۡرَ ٱلۡأَرۡضِ وَٱلسَّمَـٰوَ ٰ⁠تُۖ وَبَرَزُوا۟ لِلَّهِ ٱلۡوَ ٰ⁠حِدِ ٱلۡقَهَّارِ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:28'),
(4022, 14, 1799, 49, 'وَتَرَى ٱلۡمُجۡرِمِینَ یَوۡمَىِٕذࣲ مُّقَرَّنِینَ فِی ٱلۡأَصۡفَادِ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:28'),
(4023, 14, 1800, 50, 'سَرَابِیلُهُم مِّن قَطِرَانࣲ وَتَغۡشَىٰ وُجُوهَهُمُ ٱلنَّارُ\n', NULL, 0, '2023-05-26 17:30:24', '2023-05-27 11:17:28'),
(4024, 14, 1801, 51, 'لِیَجۡزِیَ ٱللَّهُ كُلَّ نَفۡسࣲ مَّا كَسَبَتۡۚ إِنَّ ٱللَّهَ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:30:25', '2023-05-27 11:17:28'),
(4025, 14, 1802, 52, 'هَـٰذَا بَلَـٰغࣱ لِّلنَّاسِ وَلِیُنذَرُوا۟ بِهِۦ وَلِیَعۡلَمُوۤا۟ أَنَّمَا هُوَ إِلَـٰهࣱ وَ ٰ⁠حِدࣱ وَلِیَذَّكَّرَ أُو۟لُوا۟ ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:30:25', '2023-05-27 11:17:28'),
(4026, 13, 1708, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤرۚ تِلۡكَ ءَایَـٰتُ ٱلۡكِتَـٰبِۗ وَٱلَّذِیۤ أُنزِلَ إِلَیۡكَ مِن رَّبِّكَ ٱلۡحَقُّ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:30:27', '2023-05-27 11:17:29'),
(4027, 13, 1709, 2, 'ٱللَّهُ ٱلَّذِی رَفَعَ ٱلسَّمَـٰوَ ٰ⁠تِ بِغَیۡرِ عَمَدࣲ تَرَوۡنَهَاۖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلࣱّ یَجۡرِی لِأَجَلࣲ مُّسَمࣰّىۚ یُدَبِّرُ ٱلۡأَمۡرَ یُفَصِّلُ ٱلۡـَٔایَـٰتِ لَعَلَّكُم بِلِقَاۤءِ رَبِّكُمۡ تُوقِنُونَ\n', NULL, 0, '2023-05-26 17:30:27', '2023-05-27 11:17:30'),
(4028, 13, 1710, 3, 'وَهُوَ ٱلَّذِی مَدَّ ٱلۡأَرۡضَ وَجَعَلَ فِیهَا رَوَ ٰ⁠سِیَ وَأَنۡهَـٰرࣰاۖ وَمِن كُلِّ ٱلثَّمَرَ ٰ⁠تِ جَعَلَ فِیهَا زَوۡجَیۡنِ ٱثۡنَیۡنِۖ یُغۡشِی ٱلَّیۡلَ ٱلنَّهَارَۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:30:27', '2023-05-27 11:17:30'),
(4029, 13, 1711, 4, 'وَفِی ٱلۡأَرۡضِ قِطَعࣱ مُّتَجَـٰوِرَ ٰ⁠تࣱ وَجَنَّـٰتࣱ مِّنۡ أَعۡنَـٰبࣲ وَزَرۡعࣱ وَنَخِیلࣱ صِنۡوَانࣱ وَغَیۡرُ صِنۡوَانࣲ یُسۡقَىٰ بِمَاۤءࣲ وَ ٰ⁠حِدࣲ وَنُفَضِّلُ بَعۡضَهَا عَلَىٰ بَعۡضࣲ فِی ٱلۡأُكُلِۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:30:27', '2023-05-27 11:17:30'),
(4030, 13, 1712, 5, '۞ وَإِن تَعۡجَبۡ فَعَجَبࣱ قَوۡلُهُمۡ أَءِذَا كُنَّا تُرَ ٰ⁠بًا أَءِنَّا لَفِی خَلۡقࣲ جَدِیدٍۗ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ كَفَرُوا۟ بِرَبِّهِمۡۖ وَأُو۟لَـٰۤىِٕكَ ٱلۡأَغۡلَـٰلُ فِیۤ أَعۡنَاقِهِمۡۖ وَأُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4031, 13, 1713, 6, 'وَیَسۡتَعۡجِلُونَكَ بِٱلسَّیِّئَةِ قَبۡلَ ٱلۡحَسَنَةِ وَقَدۡ خَلَتۡ مِن قَبۡلِهِمُ ٱلۡمَثُلَـٰتُۗ وَإِنَّ رَبَّكَ لَذُو مَغۡفِرَةࣲ لِّلنَّاسِ عَلَىٰ ظُلۡمِهِمۡۖ وَإِنَّ رَبَّكَ لَشَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4032, 13, 1714, 7, 'وَیَقُولُ ٱلَّذِینَ كَفَرُوا۟ لَوۡلَاۤ أُنزِلَ عَلَیۡهِ ءَایَةࣱ مِّن رَّبِّهِۦۤۗ إِنَّمَاۤ أَنتَ مُنذِرࣱۖ وَلِكُلِّ قَوۡمٍ هَادٍ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4033, 13, 1715, 8, 'ٱللَّهُ یَعۡلَمُ مَا تَحۡمِلُ كُلُّ أُنثَىٰ وَمَا تَغِیضُ ٱلۡأَرۡحَامُ وَمَا تَزۡدَادُۚ وَكُلُّ شَیۡءٍ عِندَهُۥ بِمِقۡدَارٍ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4034, 13, 1716, 9, 'عَـٰلِمُ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ ٱلۡكَبِیرُ ٱلۡمُتَعَالِ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4035, 13, 1717, 10, 'سَوَاۤءࣱ مِّنكُم مَّنۡ أَسَرَّ ٱلۡقَوۡلَ وَمَن جَهَرَ بِهِۦ وَمَنۡ هُوَ مُسۡتَخۡفِۭ بِٱلَّیۡلِ وَسَارِبُۢ بِٱلنَّهَارِ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4036, 13, 1718, 11, 'لَهُۥ مُعَقِّبَـٰتࣱ مِّنۢ بَیۡنِ یَدَیۡهِ وَمِنۡ خَلۡفِهِۦ یَحۡفَظُونَهُۥ مِنۡ أَمۡرِ ٱللَّهِۗ إِنَّ ٱللَّهَ لَا یُغَیِّرُ مَا بِقَوۡمٍ حَتَّىٰ یُغَیِّرُوا۟ مَا بِأَنفُسِهِمۡۗ وَإِذَاۤ أَرَادَ ٱللَّهُ بِقَوۡمࣲ سُوۤءࣰا فَلَا مَرَدَّ لَهُۥۚ وَمَا لَهُم مِّن دُونِهِۦ مِن وَالٍ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4037, 13, 1719, 12, 'هُوَ ٱلَّذِی یُرِیكُمُ ٱلۡبَرۡقَ خَوۡفࣰا وَطَمَعࣰا وَیُنشِئُ ٱلسَّحَابَ ٱلثِّقَالَ\n', NULL, 0, '2023-05-26 17:30:28', '2023-05-27 11:17:30'),
(4038, 13, 1720, 13, 'وَیُسَبِّحُ ٱلرَّعۡدُ بِحَمۡدِهِۦ وَٱلۡمَلَـٰۤىِٕكَةُ مِنۡ خِیفَتِهِۦ وَیُرۡسِلُ ٱلصَّوَ ٰ⁠عِقَ فَیُصِیبُ بِهَا مَن یَشَاۤءُ وَهُمۡ یُجَـٰدِلُونَ فِی ٱللَّهِ وَهُوَ شَدِیدُ ٱلۡمِحَالِ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:30'),
(4039, 13, 1721, 14, 'لَهُۥ دَعۡوَةُ ٱلۡحَقِّۚ وَٱلَّذِینَ یَدۡعُونَ مِن دُونِهِۦ لَا یَسۡتَجِیبُونَ لَهُم بِشَیۡءٍ إِلَّا كَبَـٰسِطِ كَفَّیۡهِ إِلَى ٱلۡمَاۤءِ لِیَبۡلُغَ فَاهُ وَمَا هُوَ بِبَـٰلِغِهِۦۚ وَمَا دُعَاۤءُ ٱلۡكَـٰفِرِینَ إِلَّا فِی ضَلَـٰلࣲ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:30'),
(4040, 13, 1722, 15, 'وَلِلَّهِ یَسۡجُدُ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ طَوۡعࣰا وَكَرۡهࣰا وَظِلَـٰلُهُم بِٱلۡغُدُوِّ وَٱلۡـَٔاصَالِ ۩\n', NULL, 1, '2023-05-26 17:30:29', '2023-05-27 11:17:30'),
(4041, 13, 1723, 16, 'قُلۡ مَن رَّبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ قُلِ ٱللَّهُۚ قُلۡ أَفَٱتَّخَذۡتُم مِّن دُونِهِۦۤ أَوۡلِیَاۤءَ لَا یَمۡلِكُونَ لِأَنفُسِهِمۡ نَفۡعࣰا وَلَا ضَرࣰّاۚ قُلۡ هَلۡ یَسۡتَوِی ٱلۡأَعۡمَىٰ وَٱلۡبَصِیرُ أَمۡ هَلۡ تَسۡتَوِی ٱلظُّلُمَـٰتُ وَٱلنُّورُۗ أَمۡ جَعَلُوا۟ لِلَّهِ شُرَكَاۤءَ خَلَقُوا۟ كَخَلۡقِهِۦ فَتَشَـٰبَهَ ٱلۡخَلۡقُ عَلَیۡهِمۡۚ قُلِ ٱللَّهُ خَـٰلِقُ كُلِّ شَیۡءࣲ وَهُوَ ٱلۡوَ ٰ⁠حِدُ ٱلۡقَهَّـٰرُ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:30'),
(4042, 13, 1724, 17, 'أَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَسَالَتۡ أَوۡدِیَةُۢ بِقَدَرِهَا فَٱحۡتَمَلَ ٱلسَّیۡلُ زَبَدࣰا رَّابِیࣰاۖ وَمِمَّا یُوقِدُونَ عَلَیۡهِ فِی ٱلنَّارِ ٱبۡتِغَاۤءَ حِلۡیَةٍ أَوۡ مَتَـٰعࣲ زَبَدࣱ مِّثۡلُهُۥۚ كَذَ ٰ⁠لِكَ یَضۡرِبُ ٱللَّهُ ٱلۡحَقَّ وَٱلۡبَـٰطِلَۚ فَأَمَّا ٱلزَّبَدُ فَیَذۡهَبُ جُفَاۤءࣰۖ وَأَمَّا مَا یَنفَعُ ٱلنَّاسَ فَیَمۡكُثُ فِی ٱلۡأَرۡضِۚ كَذَ ٰ⁠لِكَ یَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:30'),
(4043, 13, 1725, 18, 'لِلَّذِینَ ٱسۡتَجَابُوا۟ لِرَبِّهِمُ ٱلۡحُسۡنَىٰۚ وَٱلَّذِینَ لَمۡ یَسۡتَجِیبُوا۟ لَهُۥ لَوۡ أَنَّ لَهُم مَّا فِی ٱلۡأَرۡضِ جَمِیعࣰا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡا۟ بِهِۦۤۚ أُو۟لَـٰۤىِٕكَ لَهُمۡ سُوۤءُ ٱلۡحِسَابِ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:30'),
(4044, 13, 1726, 19, '۞ أَفَمَن یَعۡلَمُ أَنَّمَاۤ أُنزِلَ إِلَیۡكَ مِن رَّبِّكَ ٱلۡحَقُّ كَمَنۡ هُوَ أَعۡمَىٰۤۚ إِنَّمَا یَتَذَكَّرُ أُو۟لُوا۟ ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:31'),
(4045, 13, 1727, 20, 'ٱلَّذِینَ یُوفُونَ بِعَهۡدِ ٱللَّهِ وَلَا یَنقُضُونَ ٱلۡمِیثَـٰقَ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:31'),
(4046, 13, 1728, 21, 'وَٱلَّذِینَ یَصِلُونَ مَاۤ أَمَرَ ٱللَّهُ بِهِۦۤ أَن یُوصَلَ وَیَخۡشَوۡنَ رَبَّهُمۡ وَیَخَافُونَ سُوۤءَ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:30:29', '2023-05-27 11:17:31'),
(4047, 13, 1729, 22, 'وَٱلَّذِینَ صَبَرُوا۟ ٱبۡتِغَاۤءَ وَجۡهِ رَبِّهِمۡ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَنفَقُوا۟ مِمَّا رَزَقۡنَـٰهُمۡ سِرࣰّا وَعَلَانِیَةࣰ وَیَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّیِّئَةَ أُو۟لَـٰۤىِٕكَ لَهُمۡ عُقۡبَى ٱلدَّارِ\n', NULL, 0, '2023-05-26 17:30:30', '2023-05-27 11:17:31'),
(4048, 13, 1730, 23, 'جَنَّـٰتُ عَدۡنࣲ یَدۡخُلُونَهَا وَمَن صَلَحَ مِنۡ ءَابَاۤىِٕهِمۡ وَأَزۡوَ ٰ⁠جِهِمۡ وَذُرِّیَّـٰتِهِمۡۖ وَٱلۡمَلَـٰۤىِٕكَةُ یَدۡخُلُونَ عَلَیۡهِم مِّن كُلِّ بَابࣲ\n', NULL, 0, '2023-05-26 17:30:30', '2023-05-27 11:17:31'),
(4049, 13, 1731, 24, 'سَلَـٰمٌ عَلَیۡكُم بِمَا صَبَرۡتُمۡۚ فَنِعۡمَ عُقۡبَى ٱلدَّارِ\n', NULL, 0, '2023-05-26 17:30:30', '2023-05-27 11:17:31'),
(4050, 13, 1732, 25, 'وَٱلَّذِینَ یَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِیثَـٰقِهِۦ وَیَقۡطَعُونَ مَاۤ أَمَرَ ٱللَّهُ بِهِۦۤ أَن یُوصَلَ وَیُفۡسِدُونَ فِی ٱلۡأَرۡضِ أُو۟لَـٰۤىِٕكَ لَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوۤءُ ٱلدَّارِ\n', NULL, 0, '2023-05-26 17:30:30', '2023-05-27 11:17:31'),
(4051, 13, 1733, 26, 'ٱللَّهُ یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ وَیَقۡدِرُۚ وَفَرِحُوا۟ بِٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَمَا ٱلۡحَیَوٰةُ ٱلدُّنۡیَا فِی ٱلۡـَٔاخِرَةِ إِلَّا مَتَـٰعࣱ\n', NULL, 0, '2023-05-26 17:30:30', '2023-05-27 11:17:31'),
(4052, 13, 1734, 27, 'وَیَقُولُ ٱلَّذِینَ كَفَرُوا۟ لَوۡلَاۤ أُنزِلَ عَلَیۡهِ ءَایَةࣱ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ یُضِلُّ مَن یَشَاۤءُ وَیَهۡدِیۤ إِلَیۡهِ مَنۡ أَنَابَ\n', NULL, 0, '2023-05-26 17:30:30', '2023-05-27 11:17:31'),
(4053, 13, 1735, 28, 'ٱلَّذِینَ ءَامَنُوا۟ وَتَطۡمَىِٕنُّ قُلُوبُهُم بِذِكۡرِ ٱللَّهِۗ أَلَا بِذِكۡرِ ٱللَّهِ تَطۡمَىِٕنُّ ٱلۡقُلُوبُ\n', NULL, 0, '2023-05-26 17:30:30', '2023-05-27 11:17:31'),
(4054, 13, 1736, 29, 'ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ طُوبَىٰ لَهُمۡ وَحُسۡنُ مَـَٔابࣲ\n', NULL, 0, '2023-05-26 17:30:31', '2023-05-27 11:17:31'),
(4055, 13, 1737, 30, 'كَذَ ٰ⁠لِكَ أَرۡسَلۡنَـٰكَ فِیۤ أُمَّةࣲ قَدۡ خَلَتۡ مِن قَبۡلِهَاۤ أُمَمࣱ لِّتَتۡلُوَا۟ عَلَیۡهِمُ ٱلَّذِیۤ أَوۡحَیۡنَاۤ إِلَیۡكَ وَهُمۡ یَكۡفُرُونَ بِٱلرَّحۡمَـٰنِۚ قُلۡ هُوَ رَبِّی لَاۤ إِلَـٰهَ إِلَّا هُوَ عَلَیۡهِ تَوَكَّلۡتُ وَإِلَیۡهِ مَتَابِ\n', NULL, 0, '2023-05-26 17:30:31', '2023-05-27 11:17:31'),
(4056, 13, 1738, 31, 'وَلَوۡ أَنَّ قُرۡءَانࣰا سُیِّرَتۡ بِهِ ٱلۡجِبَالُ أَوۡ قُطِّعَتۡ بِهِ ٱلۡأَرۡضُ أَوۡ كُلِّمَ بِهِ ٱلۡمَوۡتَىٰۗ بَل لِّلَّهِ ٱلۡأَمۡرُ جَمِیعًاۗ أَفَلَمۡ یَا۟یۡـَٔسِ ٱلَّذِینَ ءَامَنُوۤا۟ أَن لَّوۡ یَشَاۤءُ ٱللَّهُ لَهَدَى ٱلنَّاسَ جَمِیعࣰاۗ وَلَا یَزَالُ ٱلَّذِینَ كَفَرُوا۟ تُصِیبُهُم بِمَا صَنَعُوا۟ قَارِعَةٌ أَوۡ تَحُلُّ قَرِیبࣰا مِّن دَارِهِمۡ حَتَّىٰ یَأۡتِیَ وَعۡدُ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا یُخۡلِفُ ٱلۡمِیعَادَ\n', NULL, 0, '2023-05-26 17:30:31', '2023-05-27 11:17:31'),
(4057, 13, 1739, 32, 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلࣲ مِّن قَبۡلِكَ فَأَمۡلَیۡتُ لِلَّذِینَ كَفَرُوا۟ ثُمَّ أَخَذۡتُهُمۡۖ فَكَیۡفَ كَانَ عِقَابِ\n', NULL, 0, '2023-05-26 17:30:31', '2023-05-27 11:17:31'),
(4058, 13, 1740, 33, 'أَفَمَنۡ هُوَ قَاۤىِٕمٌ عَلَىٰ كُلِّ نَفۡسِۭ بِمَا كَسَبَتۡۗ وَجَعَلُوا۟ لِلَّهِ شُرَكَاۤءَ قُلۡ سَمُّوهُمۡۚ أَمۡ تُنَبِّـُٔونَهُۥ بِمَا لَا یَعۡلَمُ فِی ٱلۡأَرۡضِ أَم بِظَـٰهِرࣲ مِّنَ ٱلۡقَوۡلِۗ بَلۡ زُیِّنَ لِلَّذِینَ كَفَرُوا۟ مَكۡرُهُمۡ وَصُدُّوا۟ عَنِ ٱلسَّبِیلِۗ وَمَن یُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادࣲ\n', NULL, 0, '2023-05-26 17:30:31', '2023-05-27 11:17:31'),
(4059, 13, 1741, 34, 'لَّهُمۡ عَذَابࣱ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ وَلَعَذَابُ ٱلۡـَٔاخِرَةِ أَشَقُّۖ وَمَا لَهُم مِّنَ ٱللَّهِ مِن وَاقࣲ\n', NULL, 0, '2023-05-26 17:30:31', '2023-05-27 11:17:31'),
(4060, 13, 1742, 35, '۞ مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِی وُعِدَ ٱلۡمُتَّقُونَۖ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۖ أُكُلُهَا دَاۤىِٕمࣱ وَظِلُّهَاۚ تِلۡكَ عُقۡبَى ٱلَّذِینَ ٱتَّقَوا۟ۚ وَّعُقۡبَى ٱلۡكَـٰفِرِینَ ٱلنَّارُ\n', NULL, 0, '2023-05-26 17:30:31', '2023-05-27 11:17:32'),
(4061, 13, 1743, 36, 'وَٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ یَفۡرَحُونَ بِمَاۤ أُنزِلَ إِلَیۡكَۖ وَمِنَ ٱلۡأَحۡزَابِ مَن یُنكِرُ بَعۡضَهُۥۚ قُلۡ إِنَّمَاۤ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ وَلَاۤ أُشۡرِكَ بِهِۦۤۚ إِلَیۡهِ أَدۡعُوا۟ وَإِلَیۡهِ مَـَٔابِ\n', NULL, 0, '2023-05-26 17:30:32', '2023-05-27 11:17:32'),
(4062, 13, 1744, 37, 'وَكَذَ ٰ⁠لِكَ أَنزَلۡنَـٰهُ حُكۡمًا عَرَبِیࣰّاۚ وَلَىِٕنِ ٱتَّبَعۡتَ أَهۡوَاۤءَهُم بَعۡدَ مَا جَاۤءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِیࣲّ وَلَا وَاقࣲ\n', NULL, 0, '2023-05-26 17:30:32', '2023-05-27 11:17:32'),
(4063, 13, 1745, 38, 'وَلَقَدۡ أَرۡسَلۡنَا رُسُلࣰا مِّن قَبۡلِكَ وَجَعَلۡنَا لَهُمۡ أَزۡوَ ٰ⁠جࣰا وَذُرِّیَّةࣰۚ وَمَا كَانَ لِرَسُولٍ أَن یَأۡتِیَ بِـَٔایَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ لِكُلِّ أَجَلࣲ كِتَابࣱ\n', NULL, 0, '2023-05-26 17:30:32', '2023-05-27 11:17:32'),
(4064, 13, 1746, 39, 'یَمۡحُوا۟ ٱللَّهُ مَا یَشَاۤءُ وَیُثۡبِتُۖ وَعِندَهُۥۤ أُمُّ ٱلۡكِتَـٰبِ\n', NULL, 0, '2023-05-26 17:30:32', '2023-05-27 11:17:32'),
(4065, 13, 1747, 40, 'وَإِن مَّا نُرِیَنَّكَ بَعۡضَ ٱلَّذِی نَعِدُهُمۡ أَوۡ نَتَوَفَّیَنَّكَ فَإِنَّمَا عَلَیۡكَ ٱلۡبَلَـٰغُ وَعَلَیۡنَا ٱلۡحِسَابُ\n', NULL, 0, '2023-05-26 17:30:32', '2023-05-27 11:17:32'),
(4066, 13, 1748, 41, 'أَوَلَمۡ یَرَوۡا۟ أَنَّا نَأۡتِی ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَاۚ وَٱللَّهُ یَحۡكُمُ لَا مُعَقِّبَ لِحُكۡمِهِۦۚ وَهُوَ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:30:32', '2023-05-27 11:17:32'),
(4067, 13, 1749, 42, 'وَقَدۡ مَكَرَ ٱلَّذِینَ مِن قَبۡلِهِمۡ فَلِلَّهِ ٱلۡمَكۡرُ جَمِیعࣰاۖ یَعۡلَمُ مَا تَكۡسِبُ كُلُّ نَفۡسࣲۗ وَسَیَعۡلَمُ ٱلۡكُفَّـٰرُ لِمَنۡ عُقۡبَى ٱلدَّارِ\n', NULL, 0, '2023-05-26 17:30:32', '2023-05-27 11:17:32'),
(4068, 13, 1750, 43, 'وَیَقُولُ ٱلَّذِینَ كَفَرُوا۟ لَسۡتَ مُرۡسَلࣰاۚ قُلۡ كَفَىٰ بِٱللَّهِ شَهِیدَۢا بَیۡنِی وَبَیۡنَكُمۡ وَمَنۡ عِندَهُۥ عِلۡمُ ٱلۡكِتَـٰبِ\n', NULL, 0, '2023-05-26 17:30:33', '2023-05-27 11:17:32'),
(4069, 12, 1597, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤرۚ تِلۡكَ ءَایَـٰتُ ٱلۡكِتَـٰبِ ٱلۡمُبِینِ\n', NULL, 0, '2023-05-26 17:30:46', '2023-05-27 11:17:34'),
(4070, 12, 1598, 2, 'إِنَّاۤ أَنزَلۡنَـٰهُ قُرۡءَ ٰ⁠ نًا عَرَبِیࣰّا لَّعَلَّكُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:30:46', '2023-05-27 11:17:34'),
(4071, 12, 1599, 3, 'نَحۡنُ نَقُصُّ عَلَیۡكَ أَحۡسَنَ ٱلۡقَصَصِ بِمَاۤ أَوۡحَیۡنَاۤ إِلَیۡكَ هَـٰذَا ٱلۡقُرۡءَانَ وَإِن كُنتَ مِن قَبۡلِهِۦ لَمِنَ ٱلۡغَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4072, 12, 1600, 4, 'إِذۡ قَالَ یُوسُفُ لِأَبِیهِ یَـٰۤأَبَتِ إِنِّی رَأَیۡتُ أَحَدَ عَشَرَ كَوۡكَبࣰا وَٱلشَّمۡسَ وَٱلۡقَمَرَ رَأَیۡتُهُمۡ لِی سَـٰجِدِینَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4073, 12, 1601, 5, 'قَالَ یَـٰبُنَیَّ لَا تَقۡصُصۡ رُءۡیَاكَ عَلَىٰۤ إِخۡوَتِكَ فَیَكِیدُوا۟ لَكَ كَیۡدًاۖ إِنَّ ٱلشَّیۡطَـٰنَ لِلۡإِنسَـٰنِ عَدُوࣱّ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4074, 12, 1602, 6, 'وَكَذَ ٰ⁠لِكَ یَجۡتَبِیكَ رَبُّكَ وَیُعَلِّمُكَ مِن تَأۡوِیلِ ٱلۡأَحَادِیثِ وَیُتِمُّ نِعۡمَتَهُۥ عَلَیۡكَ وَعَلَىٰۤ ءَالِ یَعۡقُوبَ كَمَاۤ أَتَمَّهَا عَلَىٰۤ أَبَوَیۡكَ مِن قَبۡلُ إِبۡرَ ٰ⁠هِیمَ وَإِسۡحَـٰقَۚ إِنَّ رَبَّكَ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4075, 12, 1603, 7, '۞ لَّقَدۡ كَانَ فِی یُوسُفَ وَإِخۡوَتِهِۦۤ ءَایَـٰتࣱ لِّلسَّاۤىِٕلِینَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4076, 12, 1604, 8, 'إِذۡ قَالُوا۟ لَیُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰۤ أَبِینَا مِنَّا وَنَحۡنُ عُصۡبَةٌ إِنَّ أَبَانَا لَفِی ضَلَـٰلࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4077, 12, 1605, 9, 'ٱقۡتُلُوا۟ یُوسُفَ أَوِ ٱطۡرَحُوهُ أَرۡضࣰا یَخۡلُ لَكُمۡ وَجۡهُ أَبِیكُمۡ وَتَكُونُوا۟ مِنۢ بَعۡدِهِۦ قَوۡمࣰا صَـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4078, 12, 1606, 10, 'قَالَ قَاۤىِٕلࣱ مِّنۡهُمۡ لَا تَقۡتُلُوا۟ یُوسُفَ وَأَلۡقُوهُ فِی غَیَـٰبَتِ ٱلۡجُبِّ یَلۡتَقِطۡهُ بَعۡضُ ٱلسَّیَّارَةِ إِن كُنتُمۡ فَـٰعِلِینَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:34'),
(4079, 12, 1607, 11, 'قَالُوا۟ یَـٰۤأَبَانَا مَا لَكَ لَا تَأۡمَ۬نَّا عَلَىٰ یُوسُفَ وَإِنَّا لَهُۥ لَنَـٰصِحُونَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:35'),
(4080, 12, 1608, 12, 'أَرۡسِلۡهُ مَعَنَا غَدࣰا یَرۡتَعۡ وَیَلۡعَبۡ وَإِنَّا لَهُۥ لَحَـٰفِظُونَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:35'),
(4081, 12, 1609, 13, 'قَالَ إِنِّی لَیَحۡزُنُنِیۤ أَن تَذۡهَبُوا۟ بِهِۦ وَأَخَافُ أَن یَأۡكُلَهُ ٱلذِّئۡبُ وَأَنتُمۡ عَنۡهُ غَـٰفِلُونَ\n', NULL, 0, '2023-05-26 17:30:47', '2023-05-27 11:17:35'),
(4082, 12, 1610, 14, 'قَالُوا۟ لَىِٕنۡ أَكَلَهُ ٱلذِّئۡبُ وَنَحۡنُ عُصۡبَةٌ إِنَّاۤ إِذࣰا لَّخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4083, 12, 1611, 15, 'فَلَمَّا ذَهَبُوا۟ بِهِۦ وَأَجۡمَعُوۤا۟ أَن یَجۡعَلُوهُ فِی غَیَـٰبَتِ ٱلۡجُبِّۚ وَأَوۡحَیۡنَاۤ إِلَیۡهِ لَتُنَبِّئَنَّهُم بِأَمۡرِهِمۡ هَـٰذَا وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4084, 12, 1612, 16, 'وَجَاۤءُوۤ أَبَاهُمۡ عِشَاۤءࣰ یَبۡكُونَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4085, 12, 1613, 17, 'قَالُوا۟ یَـٰۤأَبَانَاۤ إِنَّا ذَهَبۡنَا نَسۡتَبِقُ وَتَرَكۡنَا یُوسُفَ عِندَ مَتَـٰعِنَا فَأَكَلَهُ ٱلذِّئۡبُۖ وَمَاۤ أَنتَ بِمُؤۡمِنࣲ لَّنَا وَلَوۡ كُنَّا صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4086, 12, 1614, 18, 'وَجَاۤءُو عَلَىٰ قَمِیصِهِۦ بِدَمࣲ كَذِبࣲۚ قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرࣰاۖ فَصَبۡرࣱ جَمِیلࣱۖ وَٱللَّهُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4087, 12, 1615, 19, 'وَجَاۤءَتۡ سَیَّارَةࣱ فَأَرۡسَلُوا۟ وَارِدَهُمۡ فَأَدۡلَىٰ دَلۡوَهُۥۖ قَالَ یَـٰبُشۡرَىٰ هَـٰذَا غُلَـٰمࣱۚ وَأَسَرُّوهُ بِضَـٰعَةࣰۚ وَٱللَّهُ عَلِیمُۢ بِمَا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4088, 12, 1616, 20, 'وَشَرَوۡهُ بِثَمَنِۭ بَخۡسࣲ دَرَ ٰ⁠هِمَ مَعۡدُودَةࣲ وَكَانُوا۟ فِیهِ مِنَ ٱلزَّ ٰ⁠هِدِینَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4089, 12, 1617, 21, 'وَقَالَ ٱلَّذِی ٱشۡتَرَىٰهُ مِن مِّصۡرَ لِٱمۡرَأَتِهِۦۤ أَكۡرِمِی مَثۡوَىٰهُ عَسَىٰۤ أَن یَنفَعَنَاۤ أَوۡ نَتَّخِذَهُۥ وَلَدࣰاۚ وَكَذَ ٰ⁠لِكَ مَكَّنَّا لِیُوسُفَ فِی ٱلۡأَرۡضِ وَلِنُعَلِّمَهُۥ مِن تَأۡوِیلِ ٱلۡأَحَادِیثِۚ وَٱللَّهُ غَالِبٌ عَلَىٰۤ أَمۡرِهِۦ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4090, 12, 1618, 22, 'وَلَمَّا بَلَغَ أَشُدَّهُۥۤ ءَاتَیۡنَـٰهُ حُكۡمࣰا وَعِلۡمࣰاۚ وَكَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4091, 12, 1619, 23, 'وَرَ ٰ⁠وَدَتۡهُ ٱلَّتِی هُوَ فِی بَیۡتِهَا عَن نَّفۡسِهِۦ وَغَلَّقَتِ ٱلۡأَبۡوَ ٰ⁠بَ وَقَالَتۡ هَیۡتَ لَكَۚ قَالَ مَعَاذَ ٱللَّهِۖ إِنَّهُۥ رَبِّیۤ أَحۡسَنَ مَثۡوَایَۖ إِنَّهُۥ لَا یُفۡلِحُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4092, 12, 1620, 24, 'وَلَقَدۡ هَمَّتۡ بِهِۦۖ وَهَمَّ بِهَا لَوۡلَاۤ أَن رَّءَا بُرۡهَـٰنَ رَبِّهِۦۚ كَذَ ٰ⁠لِكَ لِنَصۡرِفَ عَنۡهُ ٱلسُّوۤءَ وَٱلۡفَحۡشَاۤءَۚ إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُخۡلَصِینَ\n', NULL, 0, '2023-05-26 17:30:48', '2023-05-27 11:17:35'),
(4093, 12, 1621, 25, 'وَٱسۡتَبَقَا ٱلۡبَابَ وَقَدَّتۡ قَمِیصَهُۥ مِن دُبُرࣲ وَأَلۡفَیَا سَیِّدَهَا لَدَا ٱلۡبَابِۚ قَالَتۡ مَا جَزَاۤءُ مَنۡ أَرَادَ بِأَهۡلِكَ سُوۤءًا إِلَّاۤ أَن یُسۡجَنَ أَوۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:35'),
(4094, 12, 1622, 26, 'قَالَ هِیَ رَ ٰ⁠وَدَتۡنِی عَن نَّفۡسِیۚ وَشَهِدَ شَاهِدࣱ مِّنۡ أَهۡلِهَاۤ إِن كَانَ قَمِیصُهُۥ قُدَّ مِن قُبُلࣲ فَصَدَقَتۡ وَهُوَ مِنَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:35'),
(4095, 12, 1623, 27, 'وَإِن كَانَ قَمِیصُهُۥ قُدَّ مِن دُبُرࣲ فَكَذَبَتۡ وَهُوَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:35'),
(4096, 12, 1624, 28, 'فَلَمَّا رَءَا قَمِیصَهُۥ قُدَّ مِن دُبُرࣲ قَالَ إِنَّهُۥ مِن كَیۡدِكُنَّۖ إِنَّ كَیۡدَكُنَّ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4097, 12, 1625, 29, 'یُوسُفُ أَعۡرِضۡ عَنۡ هَـٰذَاۚ وَٱسۡتَغۡفِرِی لِذَنۢبِكِۖ إِنَّكِ كُنتِ مِنَ ٱلۡخَاطِـِٔینَ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4098, 12, 1626, 30, '۞ وَقَالَ نِسۡوَةࣱ فِی ٱلۡمَدِینَةِ ٱمۡرَأَتُ ٱلۡعَزِیزِ تُرَ ٰ⁠وِدُ فَتَىٰهَا عَن نَّفۡسِهِۦۖ قَدۡ شَغَفَهَا حُبًّاۖ إِنَّا لَنَرَىٰهَا فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4099, 12, 1627, 31, 'فَلَمَّا سَمِعَتۡ بِمَكۡرِهِنَّ أَرۡسَلَتۡ إِلَیۡهِنَّ وَأَعۡتَدَتۡ لَهُنَّ مُتَّكَـࣰٔا وَءَاتَتۡ كُلَّ وَ ٰ⁠حِدَةࣲ مِّنۡهُنَّ سِكِّینࣰا وَقَالَتِ ٱخۡرُجۡ عَلَیۡهِنَّۖ فَلَمَّا رَأَیۡنَهُۥۤ أَكۡبَرۡنَهُۥ وَقَطَّعۡنَ أَیۡدِیَهُنَّ وَقُلۡنَ حَـٰشَ لِلَّهِ مَا هَـٰذَا بَشَرًا إِنۡ هَـٰذَاۤ إِلَّا مَلَكࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4100, 12, 1628, 32, 'قَالَتۡ فَذَ ٰ⁠لِكُنَّ ٱلَّذِی لُمۡتُنَّنِی فِیهِۖ وَلَقَدۡ رَ ٰ⁠وَدتُّهُۥ عَن نَّفۡسِهِۦ فَٱسۡتَعۡصَمَۖ وَلَىِٕن لَّمۡ یَفۡعَلۡ مَاۤ ءَامُرُهُۥ لَیُسۡجَنَنَّ وَلَیَكُونࣰا مِّنَ ٱلصَّـٰغِرِینَ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4101, 12, 1629, 33, 'قَالَ رَبِّ ٱلسِّجۡنُ أَحَبُّ إِلَیَّ مِمَّا یَدۡعُونَنِیۤ إِلَیۡهِۖ وَإِلَّا تَصۡرِفۡ عَنِّی كَیۡدَهُنَّ أَصۡبُ إِلَیۡهِنَّ وَأَكُن مِّنَ ٱلۡجَـٰهِلِینَ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4102, 12, 1630, 34, 'فَٱسۡتَجَابَ لَهُۥ رَبُّهُۥ فَصَرَفَ عَنۡهُ كَیۡدَهُنَّۚ إِنَّهُۥ هُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4103, 12, 1631, 35, 'ثُمَّ بَدَا لَهُم مِّنۢ بَعۡدِ مَا رَأَوُا۟ ٱلۡـَٔایَـٰتِ لَیَسۡجُنُنَّهُۥ حَتَّىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:30:49', '2023-05-27 11:17:36'),
(4104, 12, 1632, 36, 'وَدَخَلَ مَعَهُ ٱلسِّجۡنَ فَتَیَانِۖ قَالَ أَحَدُهُمَاۤ إِنِّیۤ أَرَىٰنِیۤ أَعۡصِرُ خَمۡرࣰاۖ وَقَالَ ٱلۡـَٔاخَرُ إِنِّیۤ أَرَىٰنِیۤ أَحۡمِلُ فَوۡقَ رَأۡسِی خُبۡزࣰا تَأۡكُلُ ٱلطَّیۡرُ مِنۡهُۖ نَبِّئۡنَا بِتَأۡوِیلِهِۦۤۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4105, 12, 1633, 37, 'قَالَ لَا یَأۡتِیكُمَا طَعَامࣱ تُرۡزَقَانِهِۦۤ إِلَّا نَبَّأۡتُكُمَا بِتَأۡوِیلِهِۦ قَبۡلَ أَن یَأۡتِیَكُمَاۚ ذَ ٰ⁠لِكُمَا مِمَّا عَلَّمَنِی رَبِّیۤۚ إِنِّی تَرَكۡتُ مِلَّةَ قَوۡمࣲ لَّا یُؤۡمِنُونَ بِٱللَّهِ وَهُم بِٱلۡـَٔاخِرَةِ هُمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4106, 12, 1634, 38, 'وَٱتَّبَعۡتُ مِلَّةَ ءَابَاۤءِیۤ إِبۡرَ ٰ⁠هِیمَ وَإِسۡحَـٰقَ وَیَعۡقُوبَۚ مَا كَانَ لَنَاۤ أَن نُّشۡرِكَ بِٱللَّهِ مِن شَیۡءࣲۚ ذَ ٰ⁠لِكَ مِن فَضۡلِ ٱللَّهِ عَلَیۡنَا وَعَلَى ٱلنَّاسِ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4107, 12, 1635, 39, 'یَـٰصَـٰحِبَیِ ٱلسِّجۡنِ ءَأَرۡبَابࣱ مُّتَفَرِّقُونَ خَیۡرٌ أَمِ ٱللَّهُ ٱلۡوَ ٰ⁠حِدُ ٱلۡقَهَّارُ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4108, 12, 1636, 40, 'مَا تَعۡبُدُونَ مِن دُونِهِۦۤ إِلَّاۤ أَسۡمَاۤءࣰ سَمَّیۡتُمُوهَاۤ أَنتُمۡ وَءَابَاۤؤُكُم مَّاۤ أَنزَلَ ٱللَّهُ بِهَا مِن سُلۡطَـٰنٍۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِ أَمَرَ أَلَّا تَعۡبُدُوۤا۟ إِلَّاۤ إِیَّاهُۚ ذَ ٰ⁠لِكَ ٱلدِّینُ ٱلۡقَیِّمُ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4109, 12, 1637, 41, 'یَـٰصَـٰحِبَیِ ٱلسِّجۡنِ أَمَّاۤ أَحَدُكُمَا فَیَسۡقِی رَبَّهُۥ خَمۡرࣰاۖ وَأَمَّا ٱلۡـَٔاخَرُ فَیُصۡلَبُ فَتَأۡكُلُ ٱلطَّیۡرُ مِن رَّأۡسِهِۦۚ قُضِیَ ٱلۡأَمۡرُ ٱلَّذِی فِیهِ تَسۡتَفۡتِیَانِ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4110, 12, 1638, 42, 'وَقَالَ لِلَّذِی ظَنَّ أَنَّهُۥ نَاجࣲ مِّنۡهُمَا ٱذۡكُرۡنِی عِندَ رَبِّكَ فَأَنسَىٰهُ ٱلشَّیۡطَـٰنُ ذِكۡرَ رَبِّهِۦ فَلَبِثَ فِی ٱلسِّجۡنِ بِضۡعَ سِنِینَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4111, 12, 1639, 43, 'وَقَالَ ٱلۡمَلِكُ إِنِّیۤ أَرَىٰ سَبۡعَ بَقَرَ ٰ⁠تࣲ سِمَانࣲ یَأۡكُلُهُنَّ سَبۡعٌ عِجَافࣱ وَسَبۡعَ سُنۢبُلَـٰتٍ خُضۡرࣲ وَأُخَرَ یَابِسَـٰتࣲۖ یَـٰۤأَیُّهَا ٱلۡمَلَأُ أَفۡتُونِی فِی رُءۡیَـٰیَ إِن كُنتُمۡ لِلرُّءۡیَا تَعۡبُرُونَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4112, 12, 1640, 44, 'قَالُوۤا۟ أَضۡغَـٰثُ أَحۡلَـٰمࣲۖ وَمَا نَحۡنُ بِتَأۡوِیلِ ٱلۡأَحۡلَـٰمِ بِعَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4113, 12, 1641, 45, 'وَقَالَ ٱلَّذِی نَجَا مِنۡهُمَا وَٱدَّكَرَ بَعۡدَ أُمَّةٍ أَنَا۠ أُنَبِّئُكُم بِتَأۡوِیلِهِۦ فَأَرۡسِلُونِ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:36'),
(4114, 12, 1642, 46, 'یُوسُفُ أَیُّهَا ٱلصِّدِّیقُ أَفۡتِنَا فِی سَبۡعِ بَقَرَ ٰ⁠تࣲ سِمَانࣲ یَأۡكُلُهُنَّ سَبۡعٌ عِجَافࣱ وَسَبۡعِ سُنۢبُلَـٰتٍ خُضۡرࣲ وَأُخَرَ یَابِسَـٰتࣲ لَّعَلِّیۤ أَرۡجِعُ إِلَى ٱلنَّاسِ لَعَلَّهُمۡ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:30:50', '2023-05-27 11:17:37'),
(4115, 12, 1643, 47, 'قَالَ تَزۡرَعُونَ سَبۡعَ سِنِینَ دَأَبࣰا فَمَا حَصَدتُّمۡ فَذَرُوهُ فِی سُنۢبُلِهِۦۤ إِلَّا قَلِیلࣰا مِّمَّا تَأۡكُلُونَ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4116, 12, 1644, 48, 'ثُمَّ یَأۡتِی مِنۢ بَعۡدِ ذَ ٰ⁠لِكَ سَبۡعࣱ شِدَادࣱ یَأۡكُلۡنَ مَا قَدَّمۡتُمۡ لَهُنَّ إِلَّا قَلِیلࣰا مِّمَّا تُحۡصِنُونَ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4117, 12, 1645, 49, 'ثُمَّ یَأۡتِی مِنۢ بَعۡدِ ذَ ٰ⁠لِكَ عَامࣱ فِیهِ یُغَاثُ ٱلنَّاسُ وَفِیهِ یَعۡصِرُونَ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4118, 12, 1646, 50, 'وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِی بِهِۦۖ فَلَمَّا جَاۤءَهُ ٱلرَّسُولُ قَالَ ٱرۡجِعۡ إِلَىٰ رَبِّكَ فَسۡـَٔلۡهُ مَا بَالُ ٱلنِّسۡوَةِ ٱلَّـٰتِی قَطَّعۡنَ أَیۡدِیَهُنَّۚ إِنَّ رَبِّی بِكَیۡدِهِنَّ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4119, 12, 1647, 51, 'قَالَ مَا خَطۡبُكُنَّ إِذۡ رَ ٰ⁠وَدتُّنَّ یُوسُفَ عَن نَّفۡسِهِۦۚ قُلۡنَ حَـٰشَ لِلَّهِ مَا عَلِمۡنَا عَلَیۡهِ مِن سُوۤءࣲۚ قَالَتِ ٱمۡرَأَتُ ٱلۡعَزِیزِ ٱلۡـَٔـٰنَ حَصۡحَصَ ٱلۡحَقُّ أَنَا۠ رَ ٰ⁠وَدتُّهُۥ عَن نَّفۡسِهِۦ وَإِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4120, 12, 1648, 52, 'ذَ ٰ⁠لِكَ لِیَعۡلَمَ أَنِّی لَمۡ أَخُنۡهُ بِٱلۡغَیۡبِ وَأَنَّ ٱللَّهَ لَا یَهۡدِی كَیۡدَ ٱلۡخَاۤىِٕنِینَ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4121, 12, 1649, 53, '۞ وَمَاۤ أُبَرِّئُ نَفۡسِیۤۚ إِنَّ ٱلنَّفۡسَ لَأَمَّارَةُۢ بِٱلسُّوۤءِ إِلَّا مَا رَحِمَ رَبِّیۤۚ إِنَّ رَبِّی غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4122, 12, 1650, 54, 'وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِی بِهِۦۤ أَسۡتَخۡلِصۡهُ لِنَفۡسِیۖ فَلَمَّا كَلَّمَهُۥ قَالَ إِنَّكَ ٱلۡیَوۡمَ لَدَیۡنَا مَكِینٌ أَمِینࣱ\n', NULL, 0, '2023-05-26 17:30:51', '2023-05-27 11:17:37'),
(4123, 12, 1651, 55, 'قَالَ ٱجۡعَلۡنِی عَلَىٰ خَزَاۤىِٕنِ ٱلۡأَرۡضِۖ إِنِّی حَفِیظٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4124, 12, 1652, 56, 'وَكَذَ ٰ⁠لِكَ مَكَّنَّا لِیُوسُفَ فِی ٱلۡأَرۡضِ یَتَبَوَّأُ مِنۡهَا حَیۡثُ یَشَاۤءُۚ نُصِیبُ بِرَحۡمَتِنَا مَن نَّشَاۤءُۖ وَلَا نُضِیعُ أَجۡرَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4125, 12, 1653, 57, 'وَلَأَجۡرُ ٱلۡـَٔاخِرَةِ خَیۡرࣱ لِّلَّذِینَ ءَامَنُوا۟ وَكَانُوا۟ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4126, 12, 1654, 58, 'وَجَاۤءَ إِخۡوَةُ یُوسُفَ فَدَخَلُوا۟ عَلَیۡهِ فَعَرَفَهُمۡ وَهُمۡ لَهُۥ مُنكِرُونَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4127, 12, 1655, 59, 'وَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ قَالَ ٱئۡتُونِی بِأَخࣲ لَّكُم مِّنۡ أَبِیكُمۡۚ أَلَا تَرَوۡنَ أَنِّیۤ أُوفِی ٱلۡكَیۡلَ وَأَنَا۠ خَیۡرُ ٱلۡمُنزِلِینَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4128, 12, 1656, 60, 'فَإِن لَّمۡ تَأۡتُونِی بِهِۦ فَلَا كَیۡلَ لَكُمۡ عِندِی وَلَا تَقۡرَبُونِ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4129, 12, 1657, 61, 'قَالُوا۟ سَنُرَ ٰ⁠وِدُ عَنۡهُ أَبَاهُ وَإِنَّا لَفَـٰعِلُونَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4130, 12, 1658, 62, 'وَقَالَ لِفِتۡیَـٰنِهِ ٱجۡعَلُوا۟ بِضَـٰعَتَهُمۡ فِی رِحَالِهِمۡ لَعَلَّهُمۡ یَعۡرِفُونَهَاۤ إِذَا ٱنقَلَبُوۤا۟ إِلَىٰۤ أَهۡلِهِمۡ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4131, 12, 1659, 63, 'فَلَمَّا رَجَعُوۤا۟ إِلَىٰۤ أَبِیهِمۡ قَالُوا۟ یَـٰۤأَبَانَا مُنِعَ مِنَّا ٱلۡكَیۡلُ فَأَرۡسِلۡ مَعَنَاۤ أَخَانَا نَكۡتَلۡ وَإِنَّا لَهُۥ لَحَـٰفِظُونَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4132, 12, 1660, 64, 'قَالَ هَلۡ ءَامَنُكُمۡ عَلَیۡهِ إِلَّا كَمَاۤ أَمِنتُكُمۡ عَلَىٰۤ أَخِیهِ مِن قَبۡلُ فَٱللَّهُ خَیۡرٌ حَـٰفِظࣰاۖ وَهُوَ أَرۡحَمُ ٱلرَّ ٰ⁠حِمِینَ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:37'),
(4133, 12, 1661, 65, 'وَلَمَّا فَتَحُوا۟ مَتَـٰعَهُمۡ وَجَدُوا۟ بِضَـٰعَتَهُمۡ رُدَّتۡ إِلَیۡهِمۡۖ قَالُوا۟ یَـٰۤأَبَانَا مَا نَبۡغِیۖ هَـٰذِهِۦ بِضَـٰعَتُنَا رُدَّتۡ إِلَیۡنَاۖ وَنَمِیرُ أَهۡلَنَا وَنَحۡفَظُ أَخَانَا وَنَزۡدَادُ كَیۡلَ بَعِیرࣲۖ ذَ ٰ⁠لِكَ كَیۡلࣱ یَسِیرࣱ\n', NULL, 0, '2023-05-26 17:30:52', '2023-05-27 11:17:38'),
(4134, 12, 1662, 66, 'قَالَ لَنۡ أُرۡسِلَهُۥ مَعَكُمۡ حَتَّىٰ تُؤۡتُونِ مَوۡثِقࣰا مِّنَ ٱللَّهِ لَتَأۡتُنَّنِی بِهِۦۤ إِلَّاۤ أَن یُحَاطَ بِكُمۡۖ فَلَمَّاۤ ءَاتَوۡهُ مَوۡثِقَهُمۡ قَالَ ٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِیلࣱ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4135, 12, 1663, 67, 'وَقَالَ یَـٰبَنِیَّ لَا تَدۡخُلُوا۟ مِنۢ بَابࣲ وَ ٰ⁠حِدࣲ وَٱدۡخُلُوا۟ مِنۡ أَبۡوَ ٰ⁠بࣲ مُّتَفَرِّقَةࣲۖ وَمَاۤ أُغۡنِی عَنكُم مِّنَ ٱللَّهِ مِن شَیۡءٍۖ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ عَلَیۡهِ تَوَكَّلۡتُۖ وَعَلَیۡهِ فَلۡیَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4136, 12, 1664, 68, 'وَلَمَّا دَخَلُوا۟ مِنۡ حَیۡثُ أَمَرَهُمۡ أَبُوهُم مَّا كَانَ یُغۡنِی عَنۡهُم مِّنَ ٱللَّهِ مِن شَیۡءٍ إِلَّا حَاجَةࣰ فِی نَفۡسِ یَعۡقُوبَ قَضَىٰهَاۚ وَإِنَّهُۥ لَذُو عِلۡمࣲ لِّمَا عَلَّمۡنَـٰهُ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(4137, 12, 1665, 69, 'وَلَمَّا دَخَلُوا۟ عَلَىٰ یُوسُفَ ءَاوَىٰۤ إِلَیۡهِ أَخَاهُۖ قَالَ إِنِّیۤ أَنَا۠ أَخُوكَ فَلَا تَبۡتَىِٕسۡ بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4138, 12, 1666, 70, 'فَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ جَعَلَ ٱلسِّقَایَةَ فِی رَحۡلِ أَخِیهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَیَّتُهَا ٱلۡعِیرُ إِنَّكُمۡ لَسَـٰرِقُونَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4139, 12, 1667, 71, 'قَالُوا۟ وَأَقۡبَلُوا۟ عَلَیۡهِم مَّاذَا تَفۡقِدُونَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4140, 12, 1668, 72, 'قَالُوا۟ نَفۡقِدُ صُوَاعَ ٱلۡمَلِكِ وَلِمَن جَاۤءَ بِهِۦ حِمۡلُ بَعِیرࣲ وَأَنَا۠ بِهِۦ زَعِیمࣱ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4141, 12, 1669, 73, 'قَالُوا۟ تَٱللَّهِ لَقَدۡ عَلِمۡتُم مَّا جِئۡنَا لِنُفۡسِدَ فِی ٱلۡأَرۡضِ وَمَا كُنَّا سَـٰرِقِینَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4142, 12, 1670, 74, 'قَالُوا۟ فَمَا جَزَ ٰ⁠ۤؤُهُۥۤ إِن كُنتُمۡ كَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4143, 12, 1671, 75, 'قَالُوا۟ جَزَ ٰ⁠ۤؤُهُۥ مَن وُجِدَ فِی رَحۡلِهِۦ فَهُوَ جَزَ ٰ⁠ۤؤُهُۥۚ كَذَ ٰ⁠لِكَ نَجۡزِی ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4144, 12, 1672, 76, 'فَبَدَأَ بِأَوۡعِیَتِهِمۡ قَبۡلَ وِعَاۤءِ أَخِیهِ ثُمَّ ٱسۡتَخۡرَجَهَا مِن وِعَاۤءِ أَخِیهِۚ كَذَ ٰ⁠لِكَ كِدۡنَا لِیُوسُفَۖ مَا كَانَ لِیَأۡخُذَ أَخَاهُ فِی دِینِ ٱلۡمَلِكِ إِلَّاۤ أَن یَشَاۤءَ ٱللَّهُۚ نَرۡفَعُ دَرَجَـٰتࣲ مَّن نَّشَاۤءُۗ وَفَوۡقَ كُلِّ ذِی عِلۡمٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4145, 12, 1673, 77, '۞ قَالُوۤا۟ إِن یَسۡرِقۡ فَقَدۡ سَرَقَ أَخࣱ لَّهُۥ مِن قَبۡلُۚ فَأَسَرَّهَا یُوسُفُ فِی نَفۡسِهِۦ وَلَمۡ یُبۡدِهَا لَهُمۡۚ قَالَ أَنتُمۡ شَرࣱّ مَّكَانࣰاۖ وَٱللَّهُ أَعۡلَمُ بِمَا تَصِفُونَ\n', NULL, 0, '2023-05-26 17:30:53', '2023-05-27 11:17:38'),
(4146, 12, 1674, 78, 'قَالُوا۟ یَـٰۤأَیُّهَا ٱلۡعَزِیزُ إِنَّ لَهُۥۤ أَبࣰا شَیۡخࣰا كَبِیرࣰا فَخُذۡ أَحَدَنَا مَكَانَهُۥۤۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:30:54', '2023-05-27 11:17:38'),
(4147, 12, 1675, 79, 'قَالَ مَعَاذَ ٱللَّهِ أَن نَّأۡخُذَ إِلَّا مَن وَجَدۡنَا مَتَـٰعَنَا عِندَهُۥۤ إِنَّاۤ إِذࣰا لَّظَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:30:54', '2023-05-27 11:17:38'),
(4148, 12, 1676, 80, 'فَلَمَّا ٱسۡتَیۡـَٔسُوا۟ مِنۡهُ خَلَصُوا۟ نَجِیࣰّاۖ قَالَ كَبِیرُهُمۡ أَلَمۡ تَعۡلَمُوۤا۟ أَنَّ أَبَاكُمۡ قَدۡ أَخَذَ عَلَیۡكُم مَّوۡثِقࣰا مِّنَ ٱللَّهِ وَمِن قَبۡلُ مَا فَرَّطتُمۡ فِی یُوسُفَۖ فَلَنۡ أَبۡرَحَ ٱلۡأَرۡضَ حَتَّىٰ یَأۡذَنَ لِیۤ أَبِیۤ أَوۡ یَحۡكُمَ ٱللَّهُ لِیۖ وَهُوَ خَیۡرُ ٱلۡحَـٰكِمِینَ\n', NULL, 0, '2023-05-26 17:30:54', '2023-05-27 11:17:38'),
(4149, 12, 1677, 81, 'ٱرۡجِعُوۤا۟ إِلَىٰۤ أَبِیكُمۡ فَقُولُوا۟ یَـٰۤأَبَانَاۤ إِنَّ ٱبۡنَكَ سَرَقَ وَمَا شَهِدۡنَاۤ إِلَّا بِمَا عَلِمۡنَا وَمَا كُنَّا لِلۡغَیۡبِ حَـٰفِظِینَ\n', NULL, 0, '2023-05-26 17:30:54', '2023-05-27 11:17:38'),
(4150, 12, 1678, 82, 'وَسۡـَٔلِ ٱلۡقَرۡیَةَ ٱلَّتِی كُنَّا فِیهَا وَٱلۡعِیرَ ٱلَّتِیۤ أَقۡبَلۡنَا فِیهَاۖ وَإِنَّا لَصَـٰدِقُونَ\n', NULL, 0, '2023-05-26 17:30:54', '2023-05-27 11:17:38'),
(4151, 12, 1679, 83, 'قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرࣰاۖ فَصَبۡرࣱ جَمِیلٌۖ عَسَى ٱللَّهُ أَن یَأۡتِیَنِی بِهِمۡ جَمِیعًاۚ إِنَّهُۥ هُوَ ٱلۡعَلِیمُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:30:54', '2023-05-27 11:17:38'),
(4152, 12, 1680, 84, 'وَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ یَـٰۤأَسَفَىٰ عَلَىٰ یُوسُفَ وَٱبۡیَضَّتۡ عَیۡنَاهُ مِنَ ٱلۡحُزۡنِ فَهُوَ كَظِیمࣱ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:38'),
(4153, 12, 1681, 85, 'قَالُوا۟ تَٱللَّهِ تَفۡتَؤُا۟ تَذۡكُرُ یُوسُفَ حَتَّىٰ تَكُونَ حَرَضًا أَوۡ تَكُونَ مِنَ ٱلۡهَـٰلِكِینَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4154, 12, 1682, 86, 'قَالَ إِنَّمَاۤ أَشۡكُوا۟ بَثِّی وَحُزۡنِیۤ إِلَى ٱللَّهِ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4155, 12, 1683, 87, 'یَـٰبَنِیَّ ٱذۡهَبُوا۟ فَتَحَسَّسُوا۟ مِن یُوسُفَ وَأَخِیهِ وَلَا تَا۟یۡـَٔسُوا۟ مِن رَّوۡحِ ٱللَّهِۖ إِنَّهُۥ لَا یَا۟یۡـَٔسُ مِن رَّوۡحِ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4156, 12, 1684, 88, 'فَلَمَّا دَخَلُوا۟ عَلَیۡهِ قَالُوا۟ یَـٰۤأَیُّهَا ٱلۡعَزِیزُ مَسَّنَا وَأَهۡلَنَا ٱلضُّرُّ وَجِئۡنَا بِبِضَـٰعَةࣲ مُّزۡجَىٰةࣲ فَأَوۡفِ لَنَا ٱلۡكَیۡلَ وَتَصَدَّقۡ عَلَیۡنَاۤۖ إِنَّ ٱللَّهَ یَجۡزِی ٱلۡمُتَصَدِّقِینَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4157, 12, 1685, 89, 'قَالَ هَلۡ عَلِمۡتُم مَّا فَعَلۡتُم بِیُوسُفَ وَأَخِیهِ إِذۡ أَنتُمۡ جَـٰهِلُونَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4158, 12, 1686, 90, 'قَالُوۤا۟ أَءِنَّكَ لَأَنتَ یُوسُفُۖ قَالَ أَنَا۠ یُوسُفُ وَهَـٰذَاۤ أَخِیۖ قَدۡ مَنَّ ٱللَّهُ عَلَیۡنَاۤۖ إِنَّهُۥ مَن یَتَّقِ وَیَصۡبِرۡ فَإِنَّ ٱللَّهَ لَا یُضِیعُ أَجۡرَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4159, 12, 1687, 91, 'قَالُوا۟ تَٱللَّهِ لَقَدۡ ءَاثَرَكَ ٱللَّهُ عَلَیۡنَا وَإِن كُنَّا لَخَـٰطِـِٔینَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4160, 12, 1688, 92, 'قَالَ لَا تَثۡرِیبَ عَلَیۡكُمُ ٱلۡیَوۡمَۖ یَغۡفِرُ ٱللَّهُ لَكُمۡۖ وَهُوَ أَرۡحَمُ ٱلرَّ ٰ⁠حِمِینَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4161, 12, 1689, 93, 'ٱذۡهَبُوا۟ بِقَمِیصِی هَـٰذَا فَأَلۡقُوهُ عَلَىٰ وَجۡهِ أَبِی یَأۡتِ بَصِیرࣰا وَأۡتُونِی بِأَهۡلِكُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:30:55', '2023-05-27 11:17:39'),
(4162, 12, 1690, 94, 'وَلَمَّا فَصَلَتِ ٱلۡعِیرُ قَالَ أَبُوهُمۡ إِنِّی لَأَجِدُ رِیحَ یُوسُفَۖ لَوۡلَاۤ أَن تُفَنِّدُونِ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4163, 12, 1691, 95, 'قَالُوا۟ تَٱللَّهِ إِنَّكَ لَفِی ضَلَـٰلِكَ ٱلۡقَدِیمِ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4164, 12, 1692, 96, 'فَلَمَّاۤ أَن جَاۤءَ ٱلۡبَشِیرُ أَلۡقَىٰهُ عَلَىٰ وَجۡهِهِۦ فَٱرۡتَدَّ بَصِیرࣰاۖ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّیۤ أَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4165, 12, 1693, 97, 'قَالُوا۟ یَـٰۤأَبَانَا ٱسۡتَغۡفِرۡ لَنَا ذُنُوبَنَاۤ إِنَّا كُنَّا خَـٰطِـِٔینَ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4166, 12, 1694, 98, 'قَالَ سَوۡفَ أَسۡتَغۡفِرُ لَكُمۡ رَبِّیۤۖ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4167, 12, 1695, 99, 'فَلَمَّا دَخَلُوا۟ عَلَىٰ یُوسُفَ ءَاوَىٰۤ إِلَیۡهِ أَبَوَیۡهِ وَقَالَ ٱدۡخُلُوا۟ مِصۡرَ إِن شَاۤءَ ٱللَّهُ ءَامِنِینَ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4168, 12, 1696, 100, 'وَرَفَعَ أَبَوَیۡهِ عَلَى ٱلۡعَرۡشِ وَخَرُّوا۟ لَهُۥ سُجَّدࣰاۖ وَقَالَ یَـٰۤأَبَتِ هَـٰذَا تَأۡوِیلُ رُءۡیَـٰیَ مِن قَبۡلُ قَدۡ جَعَلَهَا رَبِّی حَقࣰّاۖ وَقَدۡ أَحۡسَنَ بِیۤ إِذۡ أَخۡرَجَنِی مِنَ ٱلسِّجۡنِ وَجَاۤءَ بِكُم مِّنَ ٱلۡبَدۡوِ مِنۢ بَعۡدِ أَن نَّزَغَ ٱلشَّیۡطَـٰنُ بَیۡنِی وَبَیۡنَ إِخۡوَتِیۤۚ إِنَّ رَبِّی لَطِیفࣱ لِّمَا یَشَاۤءُۚ إِنَّهُۥ هُوَ ٱلۡعَلِیمُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4169, 12, 1697, 101, '۞ رَبِّ قَدۡ ءَاتَیۡتَنِی مِنَ ٱلۡمُلۡكِ وَعَلَّمۡتَنِی مِن تَأۡوِیلِ ٱلۡأَحَادِیثِۚ فَاطِرَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ أَنتَ وَلِیِّۦ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِۖ تَوَفَّنِی مُسۡلِمࣰا وَأَلۡحِقۡنِی بِٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4170, 12, 1698, 102, 'ذَ ٰ⁠لِكَ مِنۡ أَنۢبَاۤءِ ٱلۡغَیۡبِ نُوحِیهِ إِلَیۡكَۖ وَمَا كُنتَ لَدَیۡهِمۡ إِذۡ أَجۡمَعُوۤا۟ أَمۡرَهُمۡ وَهُمۡ یَمۡكُرُونَ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:39'),
(4171, 12, 1699, 103, 'وَمَاۤ أَكۡثَرُ ٱلنَّاسِ وَلَوۡ حَرَصۡتَ بِمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:40'),
(4172, 12, 1700, 104, 'وَمَا تَسۡـَٔلُهُمۡ عَلَیۡهِ مِنۡ أَجۡرٍۚ إِنۡ هُوَ إِلَّا ذِكۡرࣱ لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:30:56', '2023-05-27 11:17:40'),
(4173, 12, 1701, 105, 'وَكَأَیِّن مِّنۡ ءَایَةࣲ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ یَمُرُّونَ عَلَیۡهَا وَهُمۡ عَنۡهَا مُعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:30:57', '2023-05-27 11:17:40'),
(4174, 12, 1702, 106, 'وَمَا یُؤۡمِنُ أَكۡثَرُهُم بِٱللَّهِ إِلَّا وَهُم مُّشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:30:57', '2023-05-27 11:17:40'),
(4175, 12, 1703, 107, 'أَفَأَمِنُوۤا۟ أَن تَأۡتِیَهُمۡ غَـٰشِیَةࣱ مِّنۡ عَذَابِ ٱللَّهِ أَوۡ تَأۡتِیَهُمُ ٱلسَّاعَةُ بَغۡتَةࣰ وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:30:57', '2023-05-27 11:17:40'),
(4176, 12, 1704, 108, 'قُلۡ هَـٰذِهِۦ سَبِیلِیۤ أَدۡعُوۤا۟ إِلَى ٱللَّهِۚ عَلَىٰ بَصِیرَةٍ أَنَا۠ وَمَنِ ٱتَّبَعَنِیۖ وَسُبۡحَـٰنَ ٱللَّهِ وَمَاۤ أَنَا۠ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:30:57', '2023-05-27 11:17:40'),
(4177, 12, 1705, 109, 'وَمَاۤ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالࣰا نُّوحِیۤ إِلَیۡهِم مِّنۡ أَهۡلِ ٱلۡقُرَىٰۤۗ أَفَلَمۡ یَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَیَنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلَّذِینَ مِن قَبۡلِهِمۡۗ وَلَدَارُ ٱلۡـَٔاخِرَةِ خَیۡرࣱ لِّلَّذِینَ ٱتَّقَوۡا۟ۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:30:57', '2023-05-27 11:17:40'),
(4178, 12, 1706, 110, 'حَتَّىٰۤ إِذَا ٱسۡتَیۡـَٔسَ ٱلرُّسُلُ وَظَنُّوۤا۟ أَنَّهُمۡ قَدۡ كُذِبُوا۟ جَاۤءَهُمۡ نَصۡرُنَا فَنُجِّیَ مَن نَّشَاۤءُۖ وَلَا یُرَدُّ بَأۡسُنَا عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:30:57', '2023-05-27 11:17:40'),
(4179, 12, 1707, 111, 'لَقَدۡ كَانَ فِی قَصَصِهِمۡ عِبۡرَةࣱ لِّأُو۟لِی ٱلۡأَلۡبَـٰبِۗ مَا كَانَ حَدِیثࣰا یُفۡتَرَىٰ وَلَـٰكِن تَصۡدِیقَ ٱلَّذِی بَیۡنَ یَدَیۡهِ وَتَفۡصِیلَ كُلِّ شَیۡءࣲ وَهُدࣰى وَرَحۡمَةࣰ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:30:57', '2023-05-27 11:17:40'),
(4180, 11, 1474, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤرۚ كِتَـٰبٌ أُحۡكِمَتۡ ءَایَـٰتُهُۥ ثُمَّ فُصِّلَتۡ مِن لَّدُنۡ حَكِیمٍ خَبِیرٍ\n', NULL, 0, '2023-05-26 17:31:02', '2023-05-27 11:17:42'),
(4181, 11, 1475, 2, 'أَلَّا تَعۡبُدُوۤا۟ إِلَّا ٱللَّهَۚ إِنَّنِی لَكُم مِّنۡهُ نَذِیرࣱ وَبَشِیرࣱ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4182, 11, 1476, 3, 'وَأَنِ ٱسۡتَغۡفِرُوا۟ رَبَّكُمۡ ثُمَّ تُوبُوۤا۟ إِلَیۡهِ یُمَتِّعۡكُم مَّتَـٰعًا حَسَنًا إِلَىٰۤ أَجَلࣲ مُّسَمࣰّى وَیُؤۡتِ كُلَّ ذِی فَضۡلࣲ فَضۡلَهُۥۖ وَإِن تَوَلَّوۡا۟ فَإِنِّیۤ أَخَافُ عَلَیۡكُمۡ عَذَابَ یَوۡمࣲ كَبِیرٍ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4183, 11, 1477, 4, 'إِلَى ٱللَّهِ مَرۡجِعُكُمۡۖ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4184, 11, 1478, 5, 'أَلَاۤ إِنَّهُمۡ یَثۡنُونَ صُدُورَهُمۡ لِیَسۡتَخۡفُوا۟ مِنۡهُۚ أَلَا حِینَ یَسۡتَغۡشُونَ ثِیَابَهُمۡ یَعۡلَمُ مَا یُسِرُّونَ وَمَا یُعۡلِنُونَۚ إِنَّهُۥ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4185, 11, 1479, 6, '۞ وَمَا مِن دَاۤبَّةࣲ فِی ٱلۡأَرۡضِ إِلَّا عَلَى ٱللَّهِ رِزۡقُهَا وَیَعۡلَمُ مُسۡتَقَرَّهَا وَمُسۡتَوۡدَعَهَاۚ كُلࣱّ فِی كِتَـٰبࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4186, 11, 1480, 7, 'وَهُوَ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ فِی سِتَّةِ أَیَّامࣲ وَكَانَ عَرۡشُهُۥ عَلَى ٱلۡمَاۤءِ لِیَبۡلُوَكُمۡ أَیُّكُمۡ أَحۡسَنُ عَمَلࣰاۗ وَلَىِٕن قُلۡتَ إِنَّكُم مَّبۡعُوثُونَ مِنۢ بَعۡدِ ٱلۡمَوۡتِ لَیَقُولَنَّ ٱلَّذِینَ كَفَرُوۤا۟ إِنۡ هَـٰذَاۤ إِلَّا سِحۡرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4187, 11, 1481, 8, 'وَلَىِٕنۡ أَخَّرۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِلَىٰۤ أُمَّةࣲ مَّعۡدُودَةࣲ لَّیَقُولُنَّ مَا یَحۡبِسُهُۥۤۗ أَلَا یَوۡمَ یَأۡتِیهِمۡ لَیۡسَ مَصۡرُوفًا عَنۡهُمۡ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4188, 11, 1482, 9, 'وَلَىِٕنۡ أَذَقۡنَا ٱلۡإِنسَـٰنَ مِنَّا رَحۡمَةࣰ ثُمَّ نَزَعۡنَـٰهَا مِنۡهُ إِنَّهُۥ لَیَـُٔوسࣱ كَفُورࣱ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4189, 11, 1483, 10, 'وَلَىِٕنۡ أَذَقۡنَـٰهُ نَعۡمَاۤءَ بَعۡدَ ضَرَّاۤءَ مَسَّتۡهُ لَیَقُولَنَّ ذَهَبَ ٱلسَّیِّـَٔاتُ عَنِّیۤۚ إِنَّهُۥ لَفَرِحࣱ فَخُورٌ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4190, 11, 1484, 11, 'إِلَّا ٱلَّذِینَ صَبَرُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ أُو۟لَـٰۤىِٕكَ لَهُم مَّغۡفِرَةࣱ وَأَجۡرࣱ كَبِیرࣱ\n', NULL, 0, '2023-05-26 17:31:03', '2023-05-27 11:17:42'),
(4191, 11, 1485, 12, 'فَلَعَلَّكَ تَارِكُۢ بَعۡضَ مَا یُوحَىٰۤ إِلَیۡكَ وَضَاۤىِٕقُۢ بِهِۦ صَدۡرُكَ أَن یَقُولُوا۟ لَوۡلَاۤ أُنزِلَ عَلَیۡهِ كَنزٌ أَوۡ جَاۤءَ مَعَهُۥ مَلَكٌۚ إِنَّمَاۤ أَنتَ نَذِیرࣱۚ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ وَكِیلٌ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:42'),
(4192, 11, 1486, 13, 'أَمۡ یَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُوا۟ بِعَشۡرِ سُوَرࣲ مِّثۡلِهِۦ مُفۡتَرَیَـٰتࣲ وَٱدۡعُوا۟ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:42'),
(4193, 11, 1487, 14, 'فَإِلَّمۡ یَسۡتَجِیبُوا۟ لَكُمۡ فَٱعۡلَمُوۤا۟ أَنَّمَاۤ أُنزِلَ بِعِلۡمِ ٱللَّهِ وَأَن لَّاۤ إِلَـٰهَ إِلَّا هُوَۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:43'),
(4194, 11, 1488, 15, 'مَن كَانَ یُرِیدُ ٱلۡحَیَوٰةَ ٱلدُّنۡیَا وَزِینَتَهَا نُوَفِّ إِلَیۡهِمۡ أَعۡمَـٰلَهُمۡ فِیهَا وَهُمۡ فِیهَا لَا یُبۡخَسُونَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:43'),
(4195, 11, 1489, 16, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ لَیۡسَ لَهُمۡ فِی ٱلۡـَٔاخِرَةِ إِلَّا ٱلنَّارُۖ وَحَبِطَ مَا صَنَعُوا۟ فِیهَا وَبَـٰطِلࣱ مَّا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:43'),
(4196, 11, 1490, 17, 'أَفَمَن كَانَ عَلَىٰ بَیِّنَةࣲ مِّن رَّبِّهِۦ وَیَتۡلُوهُ شَاهِدࣱ مِّنۡهُ وَمِن قَبۡلِهِۦ كِتَـٰبُ مُوسَىٰۤ إِمَامࣰا وَرَحۡمَةًۚ أُو۟لَـٰۤىِٕكَ یُؤۡمِنُونَ بِهِۦۚ وَمَن یَكۡفُرۡ بِهِۦ مِنَ ٱلۡأَحۡزَابِ فَٱلنَّارُ مَوۡعِدُهُۥۚ فَلَا تَكُ فِی مِرۡیَةࣲ مِّنۡهُۚ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:43'),
(4197, 11, 1491, 18, 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًاۚ أُو۟لَـٰۤىِٕكَ یُعۡرَضُونَ عَلَىٰ رَبِّهِمۡ وَیَقُولُ ٱلۡأَشۡهَـٰدُ هَـٰۤؤُلَاۤءِ ٱلَّذِینَ كَذَبُوا۟ عَلَىٰ رَبِّهِمۡۚ أَلَا لَعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:43'),
(4198, 11, 1492, 19, 'ٱلَّذِینَ یَصُدُّونَ عَن سَبِیلِ ٱللَّهِ وَیَبۡغُونَهَا عِوَجࣰا وَهُم بِٱلۡـَٔاخِرَةِ هُمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:43'),
(4199, 11, 1493, 20, 'أُو۟لَـٰۤىِٕكَ لَمۡ یَكُونُوا۟ مُعۡجِزِینَ فِی ٱلۡأَرۡضِ وَمَا كَانَ لَهُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِیَاۤءَۘ یُضَـٰعَفُ لَهُمُ ٱلۡعَذَابُۚ مَا كَانُوا۟ یَسۡتَطِیعُونَ ٱلسَّمۡعَ وَمَا كَانُوا۟ یُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:31:04', '2023-05-27 11:17:43'),
(4200, 11, 1494, 21, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ خَسِرُوۤا۟ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4201, 11, 1495, 22, 'لَا جَرَمَ أَنَّهُمۡ فِی ٱلۡـَٔاخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4202, 11, 1496, 23, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَأَخۡبَتُوۤا۟ إِلَىٰ رَبِّهِمۡ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَنَّةِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4203, 11, 1497, 24, '۞ مَثَلُ ٱلۡفَرِیقَیۡنِ كَٱلۡأَعۡمَىٰ وَٱلۡأَصَمِّ وَٱلۡبَصِیرِ وَٱلسَّمِیعِۚ هَلۡ یَسۡتَوِیَانِ مَثَلًاۚ أَفَلَا تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4204, 11, 1498, 25, 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦۤ إِنِّی لَكُمۡ نَذِیرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4205, 11, 1499, 26, 'أَن لَّا تَعۡبُدُوۤا۟ إِلَّا ٱللَّهَۖ إِنِّیۤ أَخَافُ عَلَیۡكُمۡ عَذَابَ یَوۡمٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4206, 11, 1500, 27, 'فَقَالَ ٱلۡمَلَأُ ٱلَّذِینَ كَفَرُوا۟ مِن قَوۡمِهِۦ مَا نَرَىٰكَ إِلَّا بَشَرࣰا مِّثۡلَنَا وَمَا نَرَىٰكَ ٱتَّبَعَكَ إِلَّا ٱلَّذِینَ هُمۡ أَرَاذِلُنَا بَادِیَ ٱلرَّأۡیِ وَمَا نَرَىٰ لَكُمۡ عَلَیۡنَا مِن فَضۡلِۭ بَلۡ نَظُنُّكُمۡ كَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4207, 11, 1501, 28, 'قَالَ یَـٰقَوۡمِ أَرَءَیۡتُمۡ إِن كُنتُ عَلَىٰ بَیِّنَةࣲ مِّن رَّبِّی وَءَاتَىٰنِی رَحۡمَةࣰ مِّنۡ عِندِهِۦ فَعُمِّیَتۡ عَلَیۡكُمۡ أَنُلۡزِمُكُمُوهَا وَأَنتُمۡ لَهَا كَـٰرِهُونَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4208, 11, 1502, 29, 'وَیَـٰقَوۡمِ لَاۤ أَسۡـَٔلُكُمۡ عَلَیۡهِ مَالًاۖ إِنۡ أَجۡرِیَ إِلَّا عَلَى ٱللَّهِۚ وَمَاۤ أَنَا۠ بِطَارِدِ ٱلَّذِینَ ءَامَنُوۤا۟ۚ إِنَّهُم مُّلَـٰقُوا۟ رَبِّهِمۡ وَلَـٰكِنِّیۤ أَرَىٰكُمۡ قَوۡمࣰا تَجۡهَلُونَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4209, 11, 1503, 30, 'وَیَـٰقَوۡمِ مَن یَنصُرُنِی مِنَ ٱللَّهِ إِن طَرَدتُّهُمۡۚ أَفَلَا تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:31:05', '2023-05-27 11:17:43'),
(4210, 11, 1504, 31, 'وَلَاۤ أَقُولُ لَكُمۡ عِندِی خَزَاۤىِٕنُ ٱللَّهِ وَلَاۤ أَعۡلَمُ ٱلۡغَیۡبَ وَلَاۤ أَقُولُ إِنِّی مَلَكࣱ وَلَاۤ أَقُولُ لِلَّذِینَ تَزۡدَرِیۤ أَعۡیُنُكُمۡ لَن یُؤۡتِیَهُمُ ٱللَّهُ خَیۡرًاۖ ٱللَّهُ أَعۡلَمُ بِمَا فِیۤ أَنفُسِهِمۡ إِنِّیۤ إِذࣰا لَّمِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:43'),
(4211, 11, 1505, 32, 'قَالُوا۟ یَـٰنُوحُ قَدۡ جَـٰدَلۡتَنَا فَأَكۡثَرۡتَ جِدَ ٰ⁠لَنَا فَأۡتِنَا بِمَا تَعِدُنَاۤ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4212, 11, 1506, 33, 'قَالَ إِنَّمَا یَأۡتِیكُم بِهِ ٱللَّهُ إِن شَاۤءَ وَمَاۤ أَنتُم بِمُعۡجِزِینَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4213, 11, 1507, 34, 'وَلَا یَنفَعُكُمۡ نُصۡحِیۤ إِنۡ أَرَدتُّ أَنۡ أَنصَحَ لَكُمۡ إِن كَانَ ٱللَّهُ یُرِیدُ أَن یُغۡوِیَكُمۡۚ هُوَ رَبُّكُمۡ وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4214, 11, 1508, 35, 'أَمۡ یَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَیۡتُهُۥ فَعَلَیَّ إِجۡرَامِی وَأَنَا۠ بَرِیۤءࣱ مِّمَّا تُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4215, 11, 1509, 36, 'وَأُوحِیَ إِلَىٰ نُوحٍ أَنَّهُۥ لَن یُؤۡمِنَ مِن قَوۡمِكَ إِلَّا مَن قَدۡ ءَامَنَ فَلَا تَبۡتَىِٕسۡ بِمَا كَانُوا۟ یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4216, 11, 1510, 37, 'وَٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡیُنِنَا وَوَحۡیِنَا وَلَا تُخَـٰطِبۡنِی فِی ٱلَّذِینَ ظَلَمُوۤا۟ إِنَّهُم مُّغۡرَقُونَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4217, 11, 1511, 38, 'وَیَصۡنَعُ ٱلۡفُلۡكَ وَكُلَّمَا مَرَّ عَلَیۡهِ مَلَأࣱ مِّن قَوۡمِهِۦ سَخِرُوا۟ مِنۡهُۚ قَالَ إِن تَسۡخَرُوا۟ مِنَّا فَإِنَّا نَسۡخَرُ مِنكُمۡ كَمَا تَسۡخَرُونَ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4218, 11, 1512, 39, 'فَسَوۡفَ تَعۡلَمُونَ مَن یَأۡتِیهِ عَذَابࣱ یُخۡزِیهِ وَیَحِلُّ عَلَیۡهِ عَذَابࣱ مُّقِیمٌ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4219, 11, 1513, 40, 'حَتَّىٰۤ إِذَا جَاۤءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ قُلۡنَا ٱحۡمِلۡ فِیهَا مِن كُلࣲّ زَوۡجَیۡنِ ٱثۡنَیۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَیۡهِ ٱلۡقَوۡلُ وَمَنۡ ءَامَنَۚ وَمَاۤ ءَامَنَ مَعَهُۥۤ إِلَّا قَلِیلࣱ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4220, 11, 1514, 41, '۞ وَقَالَ ٱرۡكَبُوا۟ فِیهَا بِسۡمِ ٱللَّهِ مَجۡرٜىٰهَا وَمُرۡسَىٰهَاۤۚ إِنَّ رَبِّی لَغَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:31:06', '2023-05-27 11:17:44'),
(4221, 11, 1515, 42, 'وَهِیَ تَجۡرِی بِهِمۡ فِی مَوۡجࣲ كَٱلۡجِبَالِ وَنَادَىٰ نُوحٌ ٱبۡنَهُۥ وَكَانَ فِی مَعۡزِلࣲ یَـٰبُنَیَّ ٱرۡكَب مَّعَنَا وَلَا تَكُن مَّعَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4222, 11, 1516, 43, 'قَالَ سَـَٔاوِیۤ إِلَىٰ جَبَلࣲ یَعۡصِمُنِی مِنَ ٱلۡمَاۤءِۚ قَالَ لَا عَاصِمَ ٱلۡیَوۡمَ مِنۡ أَمۡرِ ٱللَّهِ إِلَّا مَن رَّحِمَۚ وَحَالَ بَیۡنَهُمَا ٱلۡمَوۡجُ فَكَانَ مِنَ ٱلۡمُغۡرَقِینَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4223, 11, 1517, 44, 'وَقِیلَ یَـٰۤأَرۡضُ ٱبۡلَعِی مَاۤءَكِ وَیَـٰسَمَاۤءُ أَقۡلِعِی وَغِیضَ ٱلۡمَاۤءُ وَقُضِیَ ٱلۡأَمۡرُ وَٱسۡتَوَتۡ عَلَى ٱلۡجُودِیِّۖ وَقِیلَ بُعۡدࣰا لِّلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4224, 11, 1518, 45, 'وَنَادَىٰ نُوحࣱ رَّبَّهُۥ فَقَالَ رَبِّ إِنَّ ٱبۡنِی مِنۡ أَهۡلِی وَإِنَّ وَعۡدَكَ ٱلۡحَقُّ وَأَنتَ أَحۡكَمُ ٱلۡحَـٰكِمِینَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4225, 11, 1519, 46, 'قَالَ یَـٰنُوحُ إِنَّهُۥ لَیۡسَ مِنۡ أَهۡلِكَۖ إِنَّهُۥ عَمَلٌ غَیۡرُ صَـٰلِحࣲۖ فَلَا تَسۡـَٔلۡنِ مَا لَیۡسَ لَكَ بِهِۦ عِلۡمٌۖ إِنِّیۤ أَعِظُكَ أَن تَكُونَ مِنَ ٱلۡجَـٰهِلِینَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4226, 11, 1520, 47, 'قَالَ رَبِّ إِنِّیۤ أَعُوذُ بِكَ أَنۡ أَسۡـَٔلَكَ مَا لَیۡسَ لِی بِهِۦ عِلۡمࣱۖ وَإِلَّا تَغۡفِرۡ لِی وَتَرۡحَمۡنِیۤ أَكُن مِّنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4227, 11, 1521, 48, 'قِیلَ یَـٰنُوحُ ٱهۡبِطۡ بِسَلَـٰمࣲ مِّنَّا وَبَرَكَـٰتٍ عَلَیۡكَ وَعَلَىٰۤ أُمَمࣲ مِّمَّن مَّعَكَۚ وَأُمَمࣱ سَنُمَتِّعُهُمۡ ثُمَّ یَمَسُّهُم مِّنَّا عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4228, 11, 1522, 49, 'تِلۡكَ مِنۡ أَنۢبَاۤءِ ٱلۡغَیۡبِ نُوحِیهَاۤ إِلَیۡكَۖ مَا كُنتَ تَعۡلَمُهَاۤ أَنتَ وَلَا قَوۡمُكَ مِن قَبۡلِ هَـٰذَاۖ فَٱصۡبِرۡۖ إِنَّ ٱلۡعَـٰقِبَةَ لِلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4229, 11, 1523, 50, 'وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودࣰاۚ قَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۤۖ إِنۡ أَنتُمۡ إِلَّا مُفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:31:07', '2023-05-27 11:17:44'),
(4230, 11, 1524, 51, 'یَـٰقَوۡمِ لَاۤ أَسۡـَٔلُكُمۡ عَلَیۡهِ أَجۡرًاۖ إِنۡ أَجۡرِیَ إِلَّا عَلَى ٱلَّذِی فَطَرَنِیۤۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4231, 11, 1525, 52, 'وَیَـٰقَوۡمِ ٱسۡتَغۡفِرُوا۟ رَبَّكُمۡ ثُمَّ تُوبُوۤا۟ إِلَیۡهِ یُرۡسِلِ ٱلسَّمَاۤءَ عَلَیۡكُم مِّدۡرَارࣰا وَیَزِدۡكُمۡ قُوَّةً إِلَىٰ قُوَّتِكُمۡ وَلَا تَتَوَلَّوۡا۟ مُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4232, 11, 1526, 53, 'قَالُوا۟ یَـٰهُودُ مَا جِئۡتَنَا بِبَیِّنَةࣲ وَمَا نَحۡنُ بِتَارِكِیۤ ءَالِهَتِنَا عَن قَوۡلِكَ وَمَا نَحۡنُ لَكَ بِمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4233, 11, 1527, 54, 'إِن نَّقُولُ إِلَّا ٱعۡتَرَىٰكَ بَعۡضُ ءَالِهَتِنَا بِسُوۤءࣲۗ قَالَ إِنِّیۤ أُشۡهِدُ ٱللَّهَ وَٱشۡهَدُوۤا۟ أَنِّی بَرِیۤءࣱ مِّمَّا تُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4234, 11, 1528, 55, 'مِن دُونِهِۦۖ فَكِیدُونِی جَمِیعࣰا ثُمَّ لَا تُنظِرُونِ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4235, 11, 1529, 56, 'إِنِّی تَوَكَّلۡتُ عَلَى ٱللَّهِ رَبِّی وَرَبِّكُمۚ مَّا مِن دَاۤبَّةٍ إِلَّا هُوَ ءَاخِذُۢ بِنَاصِیَتِهَاۤۚ إِنَّ رَبِّی عَلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4236, 11, 1530, 57, 'فَإِن تَوَلَّوۡا۟ فَقَدۡ أَبۡلَغۡتُكُم مَّاۤ أُرۡسِلۡتُ بِهِۦۤ إِلَیۡكُمۡۚ وَیَسۡتَخۡلِفُ رَبِّی قَوۡمًا غَیۡرَكُمۡ وَلَا تَضُرُّونَهُۥ شَیۡـًٔاۚ إِنَّ رَبِّی عَلَىٰ كُلِّ شَیۡءٍ حَفِیظࣱ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4237, 11, 1531, 58, 'وَلَمَّا جَاۤءَ أَمۡرُنَا نَجَّیۡنَا هُودࣰا وَٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥ بِرَحۡمَةࣲ مِّنَّا وَنَجَّیۡنَـٰهُم مِّنۡ عَذَابٍ غَلِیظࣲ\n', NULL, 0, '2023-05-26 17:31:08', '2023-05-27 11:17:45'),
(4238, 11, 1532, 59, 'وَتِلۡكَ عَادࣱۖ جَحَدُوا۟ بِـَٔایَـٰتِ رَبِّهِمۡ وَعَصَوۡا۟ رُسُلَهُۥ وَٱتَّبَعُوۤا۟ أَمۡرَ كُلِّ جَبَّارٍ عَنِیدࣲ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4239, 11, 1533, 60, 'وَأُتۡبِعُوا۟ فِی هَـٰذِهِ ٱلدُّنۡیَا لَعۡنَةࣰ وَیَوۡمَ ٱلۡقِیَـٰمَةِۗ أَلَاۤ إِنَّ عَادࣰا كَفَرُوا۟ رَبَّهُمۡۗ أَلَا بُعۡدࣰا لِّعَادࣲ قَوۡمِ هُودࣲ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4240, 11, 1534, 61, '۞ وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَـٰلِحࣰاۚ قَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۖ هُوَ أَنشَأَكُم مِّنَ ٱلۡأَرۡضِ وَٱسۡتَعۡمَرَكُمۡ فِیهَا فَٱسۡتَغۡفِرُوهُ ثُمَّ تُوبُوۤا۟ إِلَیۡهِۚ إِنَّ رَبِّی قَرِیبࣱ مُّجِیبࣱ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4241, 11, 1535, 62, 'قَالُوا۟ یَـٰصَـٰلِحُ قَدۡ كُنتَ فِینَا مَرۡجُوࣰّا قَبۡلَ هَـٰذَاۤۖ أَتَنۡهَىٰنَاۤ أَن نَّعۡبُدَ مَا یَعۡبُدُ ءَابَاۤؤُنَا وَإِنَّنَا لَفِی شَكࣲّ مِّمَّا تَدۡعُونَاۤ إِلَیۡهِ مُرِیبࣲ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4242, 11, 1536, 63, 'قَالَ یَـٰقَوۡمِ أَرَءَیۡتُمۡ إِن كُنتُ عَلَىٰ بَیِّنَةࣲ مِّن رَّبِّی وَءَاتَىٰنِی مِنۡهُ رَحۡمَةࣰ فَمَن یَنصُرُنِی مِنَ ٱللَّهِ إِنۡ عَصَیۡتُهُۥۖ فَمَا تَزِیدُونَنِی غَیۡرَ تَخۡسِیرࣲ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4243, 11, 1537, 64, 'وَیَـٰقَوۡمِ هَـٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَایَةࣰۖ فَذَرُوهَا تَأۡكُلۡ فِیۤ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوۤءࣲ فَیَأۡخُذَكُمۡ عَذَابࣱ قَرِیبࣱ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4244, 11, 1538, 65, 'فَعَقَرُوهَا فَقَالَ تَمَتَّعُوا۟ فِی دَارِكُمۡ ثَلَـٰثَةَ أَیَّامࣲۖ ذَ ٰ⁠لِكَ وَعۡدٌ غَیۡرُ مَكۡذُوبࣲ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4245, 11, 1539, 66, 'فَلَمَّا جَاۤءَ أَمۡرُنَا نَجَّیۡنَا صَـٰلِحࣰا وَٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥ بِرَحۡمَةࣲ مِّنَّا وَمِنۡ خِزۡیِ یَوۡمِىِٕذٍۚ إِنَّ رَبَّكَ هُوَ ٱلۡقَوِیُّ ٱلۡعَزِیزُ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4246, 11, 1540, 67, 'وَأَخَذَ ٱلَّذِینَ ظَلَمُوا۟ ٱلصَّیۡحَةُ فَأَصۡبَحُوا۟ فِی دِیَـٰرِهِمۡ جَـٰثِمِینَ\n', NULL, 0, '2023-05-26 17:31:09', '2023-05-27 11:17:45'),
(4247, 11, 1541, 68, 'كَأَن لَّمۡ یَغۡنَوۡا۟ فِیهَاۤۗ أَلَاۤ إِنَّ ثَمُودَا۟ كَفَرُوا۟ رَبَّهُمۡۗ أَلَا بُعۡدࣰا لِّثَمُودَ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:45'),
(4248, 11, 1542, 69, 'وَلَقَدۡ جَاۤءَتۡ رُسُلُنَاۤ إِبۡرَ ٰ⁠هِیمَ بِٱلۡبُشۡرَىٰ قَالُوا۟ سَلَـٰمࣰاۖ قَالَ سَلَـٰمࣱۖ فَمَا لَبِثَ أَن جَاۤءَ بِعِجۡلٍ حَنِیذࣲ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4249, 11, 1543, 70, 'فَلَمَّا رَءَاۤ أَیۡدِیَهُمۡ لَا تَصِلُ إِلَیۡهِ نَكِرَهُمۡ وَأَوۡجَسَ مِنۡهُمۡ خِیفَةࣰۚ قَالُوا۟ لَا تَخَفۡ إِنَّاۤ أُرۡسِلۡنَاۤ إِلَىٰ قَوۡمِ لُوطࣲ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4250, 11, 1544, 71, 'وَٱمۡرَأَتُهُۥ قَاۤىِٕمَةࣱ فَضَحِكَتۡ فَبَشَّرۡنَـٰهَا بِإِسۡحَـٰقَ وَمِن وَرَاۤءِ إِسۡحَـٰقَ یَعۡقُوبَ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4251, 11, 1545, 72, 'قَالَتۡ یَـٰوَیۡلَتَىٰۤ ءَأَلِدُ وَأَنَا۠ عَجُوزࣱ وَهَـٰذَا بَعۡلِی شَیۡخًاۖ إِنَّ هَـٰذَا لَشَیۡءٌ عَجِیبࣱ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4252, 11, 1546, 73, 'قَالُوۤا۟ أَتَعۡجَبِینَ مِنۡ أَمۡرِ ٱللَّهِۖ رَحۡمَتُ ٱللَّهِ وَبَرَكَـٰتُهُۥ عَلَیۡكُمۡ أَهۡلَ ٱلۡبَیۡتِۚ إِنَّهُۥ حَمِیدࣱ مَّجِیدࣱ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4253, 11, 1547, 74, 'فَلَمَّا ذَهَبَ عَنۡ إِبۡرَ ٰ⁠هِیمَ ٱلرَّوۡعُ وَجَاۤءَتۡهُ ٱلۡبُشۡرَىٰ یُجَـٰدِلُنَا فِی قَوۡمِ لُوطٍ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4254, 11, 1548, 75, 'إِنَّ إِبۡرَ ٰ⁠هِیمَ لَحَلِیمٌ أَوَّ ٰ⁠هࣱ مُّنِیبࣱ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4255, 11, 1549, 76, 'یَـٰۤإِبۡرَ ٰ⁠هِیمُ أَعۡرِضۡ عَنۡ هَـٰذَاۤۖ إِنَّهُۥ قَدۡ جَاۤءَ أَمۡرُ رَبِّكَۖ وَإِنَّهُمۡ ءَاتِیهِمۡ عَذَابٌ غَیۡرُ مَرۡدُودࣲ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4256, 11, 1550, 77, 'وَلَمَّا جَاۤءَتۡ رُسُلُنَا لُوطࣰا سِیۤءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعࣰا وَقَالَ هَـٰذَا یَوۡمٌ عَصِیبࣱ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4257, 11, 1551, 78, 'وَجَاۤءَهُۥ قَوۡمُهُۥ یُهۡرَعُونَ إِلَیۡهِ وَمِن قَبۡلُ كَانُوا۟ یَعۡمَلُونَ ٱلسَّیِّـَٔاتِۚ قَالَ یَـٰقَوۡمِ هَـٰۤؤُلَاۤءِ بَنَاتِی هُنَّ أَطۡهَرُ لَكُمۡۖ فَٱتَّقُوا۟ ٱللَّهَ وَلَا تُخۡزُونِ فِی ضَیۡفِیۤۖ أَلَیۡسَ مِنكُمۡ رَجُلࣱ رَّشِیدࣱ\n', NULL, 0, '2023-05-26 17:31:10', '2023-05-27 11:17:46'),
(4258, 11, 1552, 79, 'قَالُوا۟ لَقَدۡ عَلِمۡتَ مَا لَنَا فِی بَنَاتِكَ مِنۡ حَقࣲّ وَإِنَّكَ لَتَعۡلَمُ مَا نُرِیدُ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:46'),
(4259, 11, 1553, 80, 'قَالَ لَوۡ أَنَّ لِی بِكُمۡ قُوَّةً أَوۡ ءَاوِیۤ إِلَىٰ رُكۡنࣲ شَدِیدࣲ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:46'),
(4260, 11, 1554, 81, 'قَالُوا۟ یَـٰلُوطُ إِنَّا رُسُلُ رَبِّكَ لَن یَصِلُوۤا۟ إِلَیۡكَۖ فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعࣲ مِّنَ ٱلَّیۡلِ وَلَا یَلۡتَفِتۡ مِنكُمۡ أَحَدٌ إِلَّا ٱمۡرَأَتَكَۖ إِنَّهُۥ مُصِیبُهَا مَاۤ أَصَابَهُمۡۚ إِنَّ مَوۡعِدَهُمُ ٱلصُّبۡحُۚ أَلَیۡسَ ٱلصُّبۡحُ بِقَرِیبࣲ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:46'),
(4261, 11, 1555, 82, 'فَلَمَّا جَاۤءَ أَمۡرُنَا جَعَلۡنَا عَـٰلِیَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَیۡهَا حِجَارَةࣰ مِّن سِجِّیلࣲ مَّنضُودࣲ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:46'),
(4262, 11, 1556, 83, 'مُّسَوَّمَةً عِندَ رَبِّكَۖ وَمَا هِیَ مِنَ ٱلظَّـٰلِمِینَ بِبَعِیدࣲ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:46'),
(4263, 11, 1557, 84, '۞ وَإِلَىٰ مَدۡیَنَ أَخَاهُمۡ شُعَیۡبࣰاۚ قَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۖ وَلَا تَنقُصُوا۟ ٱلۡمِكۡیَالَ وَٱلۡمِیزَانَۖ إِنِّیۤ أَرَىٰكُم بِخَیۡرࣲ وَإِنِّیۤ أَخَافُ عَلَیۡكُمۡ عَذَابَ یَوۡمࣲ مُّحِیطࣲ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:46'),
(4264, 11, 1558, 85, 'وَیَـٰقَوۡمِ أَوۡفُوا۟ ٱلۡمِكۡیَالَ وَٱلۡمِیزَانَ بِٱلۡقِسۡطِۖ وَلَا تَبۡخَسُوا۟ ٱلنَّاسَ أَشۡیَاۤءَهُمۡ وَلَا تَعۡثَوۡا۟ فِی ٱلۡأَرۡضِ مُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:46'),
(4265, 11, 1559, 86, 'بَقِیَّتُ ٱللَّهِ خَیۡرࣱ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِینَۚ وَمَاۤ أَنَا۠ عَلَیۡكُم بِحَفِیظࣲ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:47'),
(4266, 11, 1560, 87, 'قَالُوا۟ یَـٰشُعَیۡبُ أَصَلَوٰتُكَ تَأۡمُرُكَ أَن نَّتۡرُكَ مَا یَعۡبُدُ ءَابَاۤؤُنَاۤ أَوۡ أَن نَّفۡعَلَ فِیۤ أَمۡوَ ٰ⁠لِنَا مَا نَشَـٰۤؤُا۟ۖ إِنَّكَ لَأَنتَ ٱلۡحَلِیمُ ٱلرَّشِیدُ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:47'),
(4267, 11, 1561, 88, 'قَالَ یَـٰقَوۡمِ أَرَءَیۡتُمۡ إِن كُنتُ عَلَىٰ بَیِّنَةࣲ مِّن رَّبِّی وَرَزَقَنِی مِنۡهُ رِزۡقًا حَسَنࣰاۚ وَمَاۤ أُرِیدُ أَنۡ أُخَالِفَكُمۡ إِلَىٰ مَاۤ أَنۡهَىٰكُمۡ عَنۡهُۚ إِنۡ أُرِیدُ إِلَّا ٱلۡإِصۡلَـٰحَ مَا ٱسۡتَطَعۡتُۚ وَمَا تَوۡفِیقِیۤ إِلَّا بِٱللَّهِۚ عَلَیۡهِ تَوَكَّلۡتُ وَإِلَیۡهِ أُنِیبُ\n', NULL, 0, '2023-05-26 17:31:11', '2023-05-27 11:17:47'),
(4268, 11, 1562, 89, 'وَیَـٰقَوۡمِ لَا یَجۡرِمَنَّكُمۡ شِقَاقِیۤ أَن یُصِیبَكُم مِّثۡلُ مَاۤ أَصَابَ قَوۡمَ نُوحٍ أَوۡ قَوۡمَ هُودٍ أَوۡ قَوۡمَ صَـٰلِحࣲۚ وَمَا قَوۡمُ لُوطࣲ مِّنكُم بِبَعِیدࣲ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4269, 11, 1563, 90, 'وَٱسۡتَغۡفِرُوا۟ رَبَّكُمۡ ثُمَّ تُوبُوۤا۟ إِلَیۡهِۚ إِنَّ رَبِّی رَحِیمࣱ وَدُودࣱ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4270, 11, 1564, 91, 'قَالُوا۟ یَـٰشُعَیۡبُ مَا نَفۡقَهُ كَثِیرࣰا مِّمَّا تَقُولُ وَإِنَّا لَنَرَىٰكَ فِینَا ضَعِیفࣰاۖ وَلَوۡلَا رَهۡطُكَ لَرَجَمۡنَـٰكَۖ وَمَاۤ أَنتَ عَلَیۡنَا بِعَزِیزࣲ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4271, 11, 1565, 92, 'قَالَ یَـٰقَوۡمِ أَرَهۡطِیۤ أَعَزُّ عَلَیۡكُم مِّنَ ٱللَّهِ وَٱتَّخَذۡتُمُوهُ وَرَاۤءَكُمۡ ظِهۡرِیًّاۖ إِنَّ رَبِّی بِمَا تَعۡمَلُونَ مُحِیطࣱ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4272, 11, 1566, 93, 'وَیَـٰقَوۡمِ ٱعۡمَلُوا۟ عَلَىٰ مَكَانَتِكُمۡ إِنِّی عَـٰمِلࣱۖ سَوۡفَ تَعۡلَمُونَ مَن یَأۡتِیهِ عَذَابࣱ یُخۡزِیهِ وَمَنۡ هُوَ كَـٰذِبࣱۖ وَٱرۡتَقِبُوۤا۟ إِنِّی مَعَكُمۡ رَقِیبࣱ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4273, 11, 1567, 94, 'وَلَمَّا جَاۤءَ أَمۡرُنَا نَجَّیۡنَا شُعَیۡبࣰا وَٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥ بِرَحۡمَةࣲ مِّنَّا وَأَخَذَتِ ٱلَّذِینَ ظَلَمُوا۟ ٱلصَّیۡحَةُ فَأَصۡبَحُوا۟ فِی دِیَـٰرِهِمۡ جَـٰثِمِینَ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4274, 11, 1568, 95, 'كَأَن لَّمۡ یَغۡنَوۡا۟ فِیهَاۤۗ أَلَا بُعۡدࣰا لِّمَدۡیَنَ كَمَا بَعِدَتۡ ثَمُودُ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4275, 11, 1569, 96, 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔایَـٰتِنَا وَسُلۡطَـٰنࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4276, 11, 1570, 97, 'إِلَىٰ فِرۡعَوۡنَ وَمَلَإِی۟هِۦ فَٱتَّبَعُوۤا۟ أَمۡرَ فِرۡعَوۡنَۖ وَمَاۤ أَمۡرُ فِرۡعَوۡنَ بِرَشِیدࣲ\n', NULL, 0, '2023-05-26 17:31:12', '2023-05-27 11:17:47'),
(4277, 11, 1571, 98, 'یَقۡدُمُ قَوۡمَهُۥ یَوۡمَ ٱلۡقِیَـٰمَةِ فَأَوۡرَدَهُمُ ٱلنَّارَۖ وَبِئۡسَ ٱلۡوِرۡدُ ٱلۡمَوۡرُودُ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:47'),
(4278, 11, 1572, 99, 'وَأُتۡبِعُوا۟ فِی هَـٰذِهِۦ لَعۡنَةࣰ وَیَوۡمَ ٱلۡقِیَـٰمَةِۚ بِئۡسَ ٱلرِّفۡدُ ٱلۡمَرۡفُودُ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:47'),
(4279, 11, 1573, 100, 'ذَ ٰ⁠لِكَ مِنۡ أَنۢبَاۤءِ ٱلۡقُرَىٰ نَقُصُّهُۥ عَلَیۡكَۖ مِنۡهَا قَاۤىِٕمࣱ وَحَصِیدࣱ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4280, 11, 1574, 101, 'وَمَا ظَلَمۡنَـٰهُمۡ وَلَـٰكِن ظَلَمُوۤا۟ أَنفُسَهُمۡۖ فَمَاۤ أَغۡنَتۡ عَنۡهُمۡ ءَالِهَتُهُمُ ٱلَّتِی یَدۡعُونَ مِن دُونِ ٱللَّهِ مِن شَیۡءࣲ لَّمَّا جَاۤءَ أَمۡرُ رَبِّكَۖ وَمَا زَادُوهُمۡ غَیۡرَ تَتۡبِیبࣲ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4281, 11, 1575, 102, 'وَكَذَ ٰ⁠لِكَ أَخۡذُ رَبِّكَ إِذَاۤ أَخَذَ ٱلۡقُرَىٰ وَهِیَ ظَـٰلِمَةٌۚ إِنَّ أَخۡذَهُۥۤ أَلِیمࣱ شَدِیدٌ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4282, 11, 1576, 103, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّمَنۡ خَافَ عَذَابَ ٱلۡـَٔاخِرَةِۚ ذَ ٰ⁠لِكَ یَوۡمࣱ مَّجۡمُوعࣱ لَّهُ ٱلنَّاسُ وَذَ ٰ⁠لِكَ یَوۡمࣱ مَّشۡهُودࣱ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4283, 11, 1577, 104, 'وَمَا نُؤَخِّرُهُۥۤ إِلَّا لِأَجَلࣲ مَّعۡدُودࣲ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4284, 11, 1578, 105, 'یَوۡمَ یَأۡتِ لَا تَكَلَّمُ نَفۡسٌ إِلَّا بِإِذۡنِهِۦۚ فَمِنۡهُمۡ شَقِیࣱّ وَسَعِیدࣱ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4285, 11, 1579, 106, 'فَأَمَّا ٱلَّذِینَ شَقُوا۟ فَفِی ٱلنَّارِ لَهُمۡ فِیهَا زَفِیرࣱ وَشَهِیقٌ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4286, 11, 1580, 107, 'خَـٰلِدِینَ فِیهَا مَا دَامَتِ ٱلسَّمَـٰوَ ٰ⁠تُ وَٱلۡأَرۡضُ إِلَّا مَا شَاۤءَ رَبُّكَۚ إِنَّ رَبَّكَ فَعَّالࣱ لِّمَا یُرِیدُ\n', NULL, 0, '2023-05-26 17:31:13', '2023-05-27 11:17:48'),
(4287, 11, 1581, 108, '۞ وَأَمَّا ٱلَّذِینَ سُعِدُوا۟ فَفِی ٱلۡجَنَّةِ خَـٰلِدِینَ فِیهَا مَا دَامَتِ ٱلسَّمَـٰوَ ٰ⁠تُ وَٱلۡأَرۡضُ إِلَّا مَا شَاۤءَ رَبُّكَۖ عَطَاۤءً غَیۡرَ مَجۡذُوذࣲ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:48'),
(4288, 11, 1582, 109, 'فَلَا تَكُ فِی مِرۡیَةࣲ مِّمَّا یَعۡبُدُ هَـٰۤؤُلَاۤءِۚ مَا یَعۡبُدُونَ إِلَّا كَمَا یَعۡبُدُ ءَابَاۤؤُهُم مِّن قَبۡلُۚ وَإِنَّا لَمُوَفُّوهُمۡ نَصِیبَهُمۡ غَیۡرَ مَنقُوصࣲ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:48'),
(4289, 11, 1583, 110, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ فَٱخۡتُلِفَ فِیهِۚ وَلَوۡلَا كَلِمَةࣱ سَبَقَتۡ مِن رَّبِّكَ لَقُضِیَ بَیۡنَهُمۡۚ وَإِنَّهُمۡ لَفِی شَكࣲّ مِّنۡهُ مُرِیبࣲ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:48'),
(4290, 11, 1584, 111, 'وَإِنَّ كُلࣰّا لَّمَّا لَیُوَفِّیَنَّهُمۡ رَبُّكَ أَعۡمَـٰلَهُمۡۚ إِنَّهُۥ بِمَا یَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:48'),
(4291, 11, 1585, 112, 'فَٱسۡتَقِمۡ كَمَاۤ أُمِرۡتَ وَمَن تَابَ مَعَكَ وَلَا تَطۡغَوۡا۟ۚ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:48'),
(4292, 11, 1586, 113, 'وَلَا تَرۡكَنُوۤا۟ إِلَى ٱلَّذِینَ ظَلَمُوا۟ فَتَمَسَّكُمُ ٱلنَّارُ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِیَاۤءَ ثُمَّ لَا تُنصَرُونَ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:48'),
(4293, 11, 1587, 114, 'وَأَقِمِ ٱلصَّلَوٰةَ طَرَفَیِ ٱلنَّهَارِ وَزُلَفࣰا مِّنَ ٱلَّیۡلِۚ إِنَّ ٱلۡحَسَنَـٰتِ یُذۡهِبۡنَ ٱلسَّیِّـَٔاتِۚ ذَ ٰ⁠لِكَ ذِكۡرَىٰ لِلذَّ ٰ⁠كِرِینَ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:48'),
(4294, 11, 1588, 115, 'وَٱصۡبِرۡ فَإِنَّ ٱللَّهَ لَا یُضِیعُ أَجۡرَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:49'),
(4295, 11, 1589, 116, 'فَلَوۡلَا كَانَ مِنَ ٱلۡقُرُونِ مِن قَبۡلِكُمۡ أُو۟لُوا۟ بَقِیَّةࣲ یَنۡهَوۡنَ عَنِ ٱلۡفَسَادِ فِی ٱلۡأَرۡضِ إِلَّا قَلِیلࣰا مِّمَّنۡ أَنجَیۡنَا مِنۡهُمۡۗ وَٱتَّبَعَ ٱلَّذِینَ ظَلَمُوا۟ مَاۤ أُتۡرِفُوا۟ فِیهِ وَكَانُوا۟ مُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:31:14', '2023-05-27 11:17:49'),
(4296, 11, 1590, 117, 'وَمَا كَانَ رَبُّكَ لِیُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمࣲ وَأَهۡلُهَا مُصۡلِحُونَ\n', NULL, 0, '2023-05-26 17:31:15', '2023-05-27 11:17:49'),
(4297, 11, 1591, 118, 'وَلَوۡ شَاۤءَ رَبُّكَ لَجَعَلَ ٱلنَّاسَ أُمَّةࣰ وَ ٰ⁠حِدَةࣰۖ وَلَا یَزَالُونَ مُخۡتَلِفِینَ\n', NULL, 0, '2023-05-26 17:31:15', '2023-05-27 11:17:49'),
(4298, 11, 1592, 119, 'إِلَّا مَن رَّحِمَ رَبُّكَۚ وَلِذَ ٰ⁠لِكَ خَلَقَهُمۡۗ وَتَمَّتۡ كَلِمَةُ رَبِّكَ لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:31:15', '2023-05-27 11:17:49'),
(4299, 11, 1593, 120, 'وَكُلࣰّا نَّقُصُّ عَلَیۡكَ مِنۡ أَنۢبَاۤءِ ٱلرُّسُلِ مَا نُثَبِّتُ بِهِۦ فُؤَادَكَۚ وَجَاۤءَكَ فِی هَـٰذِهِ ٱلۡحَقُّ وَمَوۡعِظَةࣱ وَذِكۡرَىٰ لِلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:15', '2023-05-27 11:17:49'),
(4300, 11, 1594, 121, 'وَقُل لِّلَّذِینَ لَا یُؤۡمِنُونَ ٱعۡمَلُوا۟ عَلَىٰ مَكَانَتِكُمۡ إِنَّا عَـٰمِلُونَ\n', NULL, 0, '2023-05-26 17:31:15', '2023-05-27 11:17:49'),
(4301, 11, 1595, 122, 'وَٱنتَظِرُوۤا۟ إِنَّا مُنتَظِرُونَ\n', NULL, 0, '2023-05-26 17:31:15', '2023-05-27 11:17:49'),
(4302, 11, 1596, 123, 'وَلِلَّهِ غَیۡبُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَإِلَیۡهِ یُرۡجَعُ ٱلۡأَمۡرُ كُلُّهُۥ فَٱعۡبُدۡهُ وَتَوَكَّلۡ عَلَیۡهِۚ وَمَا رَبُّكَ بِغَـٰفِلٍ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:15', '2023-05-27 11:17:49'),
(4303, 10, 1365, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤرۚ تِلۡكَ ءَایَـٰتُ ٱلۡكِتَـٰبِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:31:18', '2023-05-27 11:17:51'),
(4304, 10, 1366, 2, 'أَكَانَ لِلنَّاسِ عَجَبًا أَنۡ أَوۡحَیۡنَاۤ إِلَىٰ رَجُلࣲ مِّنۡهُمۡ أَنۡ أَنذِرِ ٱلنَّاسَ وَبَشِّرِ ٱلَّذِینَ ءَامَنُوۤا۟ أَنَّ لَهُمۡ قَدَمَ صِدۡقٍ عِندَ رَبِّهِمۡۗ قَالَ ٱلۡكَـٰفِرُونَ إِنَّ هَـٰذَا لَسَـٰحِرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:31:18', '2023-05-27 11:17:51'),
(4305, 10, 1367, 3, 'إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ فِی سِتَّةِ أَیَّامࣲ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ یُدَبِّرُ ٱلۡأَمۡرَۖ مَا مِن شَفِیعٍ إِلَّا مِنۢ بَعۡدِ إِذۡنِهِۦۚ ذَ ٰ⁠لِكُمُ ٱللَّهُ رَبُّكُمۡ فَٱعۡبُدُوهُۚ أَفَلَا تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:31:18', '2023-05-27 11:17:51'),
(4306, 10, 1368, 4, 'إِلَیۡهِ مَرۡجِعُكُمۡ جَمِیعࣰاۖ وَعۡدَ ٱللَّهِ حَقًّاۚ إِنَّهُۥ یَبۡدَؤُا۟ ٱلۡخَلۡقَ ثُمَّ یُعِیدُهُۥ لِیَجۡزِیَ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ بِٱلۡقِسۡطِۚ وَٱلَّذِینَ كَفَرُوا۟ لَهُمۡ شَرَابࣱ مِّنۡ حَمِیمࣲ وَعَذَابٌ أَلِیمُۢ بِمَا كَانُوا۟ یَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4307, 10, 1369, 5, 'هُوَ ٱلَّذِی جَعَلَ ٱلشَّمۡسَ ضِیَاۤءࣰ وَٱلۡقَمَرَ نُورࣰا وَقَدَّرَهُۥ مَنَازِلَ لِتَعۡلَمُوا۟ عَدَدَ ٱلسِّنِینَ وَٱلۡحِسَابَۚ مَا خَلَقَ ٱللَّهُ ذَ ٰ⁠لِكَ إِلَّا بِٱلۡحَقِّۚ یُفَصِّلُ ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4308, 10, 1370, 6, 'إِنَّ فِی ٱخۡتِلَـٰفِ ٱلَّیۡلِ وَٱلنَّهَارِ وَمَا خَلَقَ ٱللَّهُ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4309, 10, 1371, 7, 'إِنَّ ٱلَّذِینَ لَا یَرۡجُونَ لِقَاۤءَنَا وَرَضُوا۟ بِٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَٱطۡمَأَنُّوا۟ بِهَا وَٱلَّذِینَ هُمۡ عَنۡ ءَایَـٰتِنَا غَـٰفِلُونَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4310, 10, 1372, 8, 'أُو۟لَـٰۤىِٕكَ مَأۡوَىٰهُمُ ٱلنَّارُ بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4311, 10, 1373, 9, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ یَهۡدِیهِمۡ رَبُّهُم بِإِیمَـٰنِهِمۡۖ تَجۡرِی مِن تَحۡتِهِمُ ٱلۡأَنۡهَـٰرُ فِی جَنَّـٰتِ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4312, 10, 1374, 10, 'دَعۡوَىٰهُمۡ فِیهَا سُبۡحَـٰنَكَ ٱللَّهُمَّ وَتَحِیَّتُهُمۡ فِیهَا سَلَـٰمࣱۚ وَءَاخِرُ دَعۡوَىٰهُمۡ أَنِ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4313, 10, 1375, 11, '۞ وَلَوۡ یُعَجِّلُ ٱللَّهُ لِلنَّاسِ ٱلشَّرَّ ٱسۡتِعۡجَالَهُم بِٱلۡخَیۡرِ لَقُضِیَ إِلَیۡهِمۡ أَجَلُهُمۡۖ فَنَذَرُ ٱلَّذِینَ لَا یَرۡجُونَ لِقَاۤءَنَا فِی طُغۡیَـٰنِهِمۡ یَعۡمَهُونَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4314, 10, 1376, 12, 'وَإِذَا مَسَّ ٱلۡإِنسَـٰنَ ٱلضُّرُّ دَعَانَا لِجَنۢبِهِۦۤ أَوۡ قَاعِدًا أَوۡ قَاۤىِٕمࣰا فَلَمَّا كَشَفۡنَا عَنۡهُ ضُرَّهُۥ مَرَّ كَأَن لَّمۡ یَدۡعُنَاۤ إِلَىٰ ضُرࣲّ مَّسَّهُۥۚ كَذَ ٰ⁠لِكَ زُیِّنَ لِلۡمُسۡرِفِینَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:19', '2023-05-27 11:17:51'),
(4315, 10, 1377, 13, 'وَلَقَدۡ أَهۡلَكۡنَا ٱلۡقُرُونَ مِن قَبۡلِكُمۡ لَمَّا ظَلَمُوا۟ وَجَاۤءَتۡهُمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِ وَمَا كَانُوا۟ لِیُؤۡمِنُوا۟ۚ كَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡقَوۡمَ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:51'),
(4316, 10, 1378, 14, 'ثُمَّ جَعَلۡنَـٰكُمۡ خَلَـٰۤىِٕفَ فِی ٱلۡأَرۡضِ مِنۢ بَعۡدِهِمۡ لِنَنظُرَ كَیۡفَ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:51'),
(4317, 10, 1379, 15, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَاتُنَا بَیِّنَـٰتࣲ قَالَ ٱلَّذِینَ لَا یَرۡجُونَ لِقَاۤءَنَا ٱئۡتِ بِقُرۡءَانٍ غَیۡرِ هَـٰذَاۤ أَوۡ بَدِّلۡهُۚ قُلۡ مَا یَكُونُ لِیۤ أَنۡ أُبَدِّلَهُۥ مِن تِلۡقَاۤىِٕ نَفۡسِیۤۖ إِنۡ أَتَّبِعُ إِلَّا مَا یُوحَىٰۤ إِلَیَّۖ إِنِّیۤ أَخَافُ إِنۡ عَصَیۡتُ رَبِّی عَذَابَ یَوۡمٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:51'),
(4318, 10, 1380, 16, 'قُل لَّوۡ شَاۤءَ ٱللَّهُ مَا تَلَوۡتُهُۥ عَلَیۡكُمۡ وَلَاۤ أَدۡرَىٰكُم بِهِۦۖ فَقَدۡ لَبِثۡتُ فِیكُمۡ عُمُرࣰا مِّن قَبۡلِهِۦۤۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:52'),
(4319, 10, 1381, 17, 'فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔایَـٰتِهِۦۤۚ إِنَّهُۥ لَا یُفۡلِحُ ٱلۡمُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:52'),
(4320, 10, 1382, 18, 'وَیَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا یَضُرُّهُمۡ وَلَا یَنفَعُهُمۡ وَیَقُولُونَ هَـٰۤؤُلَاۤءِ شُفَعَـٰۤؤُنَا عِندَ ٱللَّهِۚ قُلۡ أَتُنَبِّـُٔونَ ٱللَّهَ بِمَا لَا یَعۡلَمُ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَلَا فِی ٱلۡأَرۡضِۚ سُبۡحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:52'),
(4321, 10, 1383, 19, 'وَمَا كَانَ ٱلنَّاسُ إِلَّاۤ أُمَّةࣰ وَ ٰ⁠حِدَةࣰ فَٱخۡتَلَفُوا۟ۚ وَلَوۡلَا كَلِمَةࣱ سَبَقَتۡ مِن رَّبِّكَ لَقُضِیَ بَیۡنَهُمۡ فِیمَا فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:52'),
(4322, 10, 1384, 20, 'وَیَقُولُونَ لَوۡلَاۤ أُنزِلَ عَلَیۡهِ ءَایَةࣱ مِّن رَّبِّهِۦۖ فَقُلۡ إِنَّمَا ٱلۡغَیۡبُ لِلَّهِ فَٱنتَظِرُوۤا۟ إِنِّی مَعَكُم مِّنَ ٱلۡمُنتَظِرِینَ\n', NULL, 0, '2023-05-26 17:31:20', '2023-05-27 11:17:52'),
(4323, 10, 1385, 21, 'وَإِذَاۤ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةࣰ مِّنۢ بَعۡدِ ضَرَّاۤءَ مَسَّتۡهُمۡ إِذَا لَهُم مَّكۡرࣱ فِیۤ ءَایَاتِنَاۚ قُلِ ٱللَّهُ أَسۡرَعُ مَكۡرًاۚ إِنَّ رُسُلَنَا یَكۡتُبُونَ مَا تَمۡكُرُونَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4324, 10, 1386, 22, 'هُوَ ٱلَّذِی یُسَیِّرُكُمۡ فِی ٱلۡبَرِّ وَٱلۡبَحۡرِۖ حَتَّىٰۤ إِذَا كُنتُمۡ فِی ٱلۡفُلۡكِ وَجَرَیۡنَ بِهِم بِرِیحࣲ طَیِّبَةࣲ وَفَرِحُوا۟ بِهَا جَاۤءَتۡهَا رِیحٌ عَاصِفࣱ وَجَاۤءَهُمُ ٱلۡمَوۡجُ مِن كُلِّ مَكَانࣲ وَظَنُّوۤا۟ أَنَّهُمۡ أُحِیطَ بِهِمۡ دَعَوُا۟ ٱللَّهَ مُخۡلِصِینَ لَهُ ٱلدِّینَ لَىِٕنۡ أَنجَیۡتَنَا مِنۡ هَـٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4325, 10, 1387, 23, 'فَلَمَّاۤ أَنجَىٰهُمۡ إِذَا هُمۡ یَبۡغُونَ فِی ٱلۡأَرۡضِ بِغَیۡرِ ٱلۡحَقِّۗ یَـٰۤأَیُّهَا ٱلنَّاسُ إِنَّمَا بَغۡیُكُمۡ عَلَىٰۤ أَنفُسِكُمۖ مَّتَـٰعَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ ثُمَّ إِلَیۡنَا مَرۡجِعُكُمۡ فَنُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4326, 10, 1388, 24, 'إِنَّمَا مَثَلُ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا كَمَاۤءٍ أَنزَلۡنَـٰهُ مِنَ ٱلسَّمَاۤءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ مِمَّا یَأۡكُلُ ٱلنَّاسُ وَٱلۡأَنۡعَـٰمُ حَتَّىٰۤ إِذَاۤ أَخَذَتِ ٱلۡأَرۡضُ زُخۡرُفَهَا وَٱزَّیَّنَتۡ وَظَنَّ أَهۡلُهَاۤ أَنَّهُمۡ قَـٰدِرُونَ عَلَیۡهَاۤ أَتَىٰهَاۤ أَمۡرُنَا لَیۡلًا أَوۡ نَهَارࣰا فَجَعَلۡنَـٰهَا حَصِیدࣰا كَأَن لَّمۡ تَغۡنَ بِٱلۡأَمۡسِۚ كَذَ ٰ⁠لِكَ نُفَصِّلُ ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4327, 10, 1389, 25, 'وَٱللَّهُ یَدۡعُوۤا۟ إِلَىٰ دَارِ ٱلسَّلَـٰمِ وَیَهۡدِی مَن یَشَاۤءُ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4328, 10, 1390, 26, '۞ لِّلَّذِینَ أَحۡسَنُوا۟ ٱلۡحُسۡنَىٰ وَزِیَادَةࣱۖ وَلَا یَرۡهَقُ وُجُوهَهُمۡ قَتَرࣱ وَلَا ذِلَّةٌۚ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَنَّةِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4329, 10, 1391, 27, 'وَٱلَّذِینَ كَسَبُوا۟ ٱلسَّیِّـَٔاتِ جَزَاۤءُ سَیِّئَةِۭ بِمِثۡلِهَا وَتَرۡهَقُهُمۡ ذِلَّةࣱۖ مَّا لَهُم مِّنَ ٱللَّهِ مِنۡ عَاصِمࣲۖ كَأَنَّمَاۤ أُغۡشِیَتۡ وُجُوهُهُمۡ قِطَعࣰا مِّنَ ٱلَّیۡلِ مُظۡلِمًاۚ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4330, 10, 1392, 28, 'وَیَوۡمَ نَحۡشُرُهُمۡ جَمِیعࣰا ثُمَّ نَقُولُ لِلَّذِینَ أَشۡرَكُوا۟ مَكَانَكُمۡ أَنتُمۡ وَشُرَكَاۤؤُكُمۡۚ فَزَیَّلۡنَا بَیۡنَهُمۡۖ وَقَالَ شُرَكَاۤؤُهُم مَّا كُنتُمۡ إِیَّانَا تَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4331, 10, 1393, 29, 'فَكَفَىٰ بِٱللَّهِ شَهِیدَۢا بَیۡنَنَا وَبَیۡنَكُمۡ إِن كُنَّا عَنۡ عِبَادَتِكُمۡ لَغَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:31:21', '2023-05-27 11:17:52'),
(4332, 10, 1394, 30, 'هُنَالِكَ تَبۡلُوا۟ كُلُّ نَفۡسࣲ مَّاۤ أَسۡلَفَتۡۚ وَرُدُّوۤا۟ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۖ وَضَلَّ عَنۡهُم مَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:52'),
(4333, 10, 1395, 31, 'قُلۡ مَن یَرۡزُقُكُم مِّنَ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِ أَمَّن یَمۡلِكُ ٱلسَّمۡعَ وَٱلۡأَبۡصَـٰرَ وَمَن یُخۡرِجُ ٱلۡحَیَّ مِنَ ٱلۡمَیِّتِ وَیُخۡرِجُ ٱلۡمَیِّتَ مِنَ ٱلۡحَیِّ وَمَن یُدَبِّرُ ٱلۡأَمۡرَۚ فَسَیَقُولُونَ ٱللَّهُۚ فَقُلۡ أَفَلَا تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4334, 10, 1396, 32, 'فَذَ ٰ⁠لِكُمُ ٱللَّهُ رَبُّكُمُ ٱلۡحَقُّۖ فَمَاذَا بَعۡدَ ٱلۡحَقِّ إِلَّا ٱلضَّلَـٰلُۖ فَأَنَّىٰ تُصۡرَفُونَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4335, 10, 1397, 33, 'كَذَ ٰ⁠لِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِینَ فَسَقُوۤا۟ أَنَّهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4336, 10, 1398, 34, 'قُلۡ هَلۡ مِن شُرَكَاۤىِٕكُم مَّن یَبۡدَؤُا۟ ٱلۡخَلۡقَ ثُمَّ یُعِیدُهُۥۚ قُلِ ٱللَّهُ یَبۡدَؤُا۟ ٱلۡخَلۡقَ ثُمَّ یُعِیدُهُۥۖ فَأَنَّىٰ تُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4337, 10, 1399, 35, 'قُلۡ هَلۡ مِن شُرَكَاۤىِٕكُم مَّن یَهۡدِیۤ إِلَى ٱلۡحَقِّۚ قُلِ ٱللَّهُ یَهۡدِی لِلۡحَقِّۗ أَفَمَن یَهۡدِیۤ إِلَى ٱلۡحَقِّ أَحَقُّ أَن یُتَّبَعَ أَمَّن لَّا یَهِدِّیۤ إِلَّاۤ أَن یُهۡدَىٰۖ فَمَا لَكُمۡ كَیۡفَ تَحۡكُمُونَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4338, 10, 1400, 36, 'وَمَا یَتَّبِعُ أَكۡثَرُهُمۡ إِلَّا ظَنًّاۚ إِنَّ ٱلظَّنَّ لَا یُغۡنِی مِنَ ٱلۡحَقِّ شَیۡـًٔاۚ إِنَّ ٱللَّهَ عَلِیمُۢ بِمَا یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4339, 10, 1401, 37, 'وَمَا كَانَ هَـٰذَا ٱلۡقُرۡءَانُ أَن یُفۡتَرَىٰ مِن دُونِ ٱللَّهِ وَلَـٰكِن تَصۡدِیقَ ٱلَّذِی بَیۡنَ یَدَیۡهِ وَتَفۡصِیلَ ٱلۡكِتَـٰبِ لَا رَیۡبَ فِیهِ مِن رَّبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4340, 10, 1402, 38, 'أَمۡ یَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُوا۟ بِسُورَةࣲ مِّثۡلِهِۦ وَٱدۡعُوا۟ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4341, 10, 1403, 39, 'بَلۡ كَذَّبُوا۟ بِمَا لَمۡ یُحِیطُوا۟ بِعِلۡمِهِۦ وَلَمَّا یَأۡتِهِمۡ تَأۡوِیلُهُۥۚ كَذَ ٰ⁠لِكَ كَذَّبَ ٱلَّذِینَ مِن قَبۡلِهِمۡۖ فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:22', '2023-05-27 11:17:53'),
(4342, 10, 1404, 40, 'وَمِنۡهُم مَّن یُؤۡمِنُ بِهِۦ وَمِنۡهُم مَّن لَّا یُؤۡمِنُ بِهِۦۚ وَرَبُّكَ أَعۡلَمُ بِٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:53'),
(4343, 10, 1405, 41, 'وَإِن كَذَّبُوكَ فَقُل لِّی عَمَلِی وَلَكُمۡ عَمَلُكُمۡۖ أَنتُم بَرِیۤـُٔونَ مِمَّاۤ أَعۡمَلُ وَأَنَا۠ بَرِیۤءࣱ مِّمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:53'),
(4344, 10, 1406, 42, 'وَمِنۡهُم مَّن یَسۡتَمِعُونَ إِلَیۡكَۚ أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ وَلَوۡ كَانُوا۟ لَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:53'),
(4345, 10, 1407, 43, 'وَمِنۡهُم مَّن یَنظُرُ إِلَیۡكَۚ أَفَأَنتَ تَهۡدِی ٱلۡعُمۡیَ وَلَوۡ كَانُوا۟ لَا یُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:53'),
(4346, 10, 1408, 44, 'إِنَّ ٱللَّهَ لَا یَظۡلِمُ ٱلنَّاسَ شَیۡـࣰٔا وَلَـٰكِنَّ ٱلنَّاسَ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:53'),
(4347, 10, 1409, 45, 'وَیَوۡمَ یَحۡشُرُهُمۡ كَأَن لَّمۡ یَلۡبَثُوۤا۟ إِلَّا سَاعَةࣰ مِّنَ ٱلنَّهَارِ یَتَعَارَفُونَ بَیۡنَهُمۡۚ قَدۡ خَسِرَ ٱلَّذِینَ كَذَّبُوا۟ بِلِقَاۤءِ ٱللَّهِ وَمَا كَانُوا۟ مُهۡتَدِینَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:53'),
(4348, 10, 1410, 46, 'وَإِمَّا نُرِیَنَّكَ بَعۡضَ ٱلَّذِی نَعِدُهُمۡ أَوۡ نَتَوَفَّیَنَّكَ فَإِلَیۡنَا مَرۡجِعُهُمۡ ثُمَّ ٱللَّهُ شَهِیدٌ عَلَىٰ مَا یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:53'),
(4349, 10, 1411, 47, 'وَلِكُلِّ أُمَّةࣲ رَّسُولࣱۖ فَإِذَا جَاۤءَ رَسُولُهُمۡ قُضِیَ بَیۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:54'),
(4350, 10, 1412, 48, 'وَیَقُولُونَ مَتَىٰ هَـٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:54'),
(4351, 10, 1413, 49, 'قُل لَّاۤ أَمۡلِكُ لِنَفۡسِی ضَرࣰّا وَلَا نَفۡعًا إِلَّا مَا شَاۤءَ ٱللَّهُۗ لِكُلِّ أُمَّةٍ أَجَلٌۚ إِذَا جَاۤءَ أَجَلُهُمۡ فَلَا یَسۡتَـٔۡخِرُونَ سَاعَةࣰ وَلَا یَسۡتَقۡدِمُونَ\n', NULL, 0, '2023-05-26 17:31:23', '2023-05-27 11:17:54'),
(4352, 10, 1414, 50, 'قُلۡ أَرَءَیۡتُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُهُۥ بَیَـٰتًا أَوۡ نَهَارࣰا مَّاذَا یَسۡتَعۡجِلُ مِنۡهُ ٱلۡمُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4353, 10, 1415, 51, 'أَثُمَّ إِذَا مَا وَقَعَ ءَامَنتُم بِهِۦۤۚ ءَاۤلۡـَٔـٰنَ وَقَدۡ كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4354, 10, 1416, 52, 'ثُمَّ قِیلَ لِلَّذِینَ ظَلَمُوا۟ ذُوقُوا۟ عَذَابَ ٱلۡخُلۡدِ هَلۡ تُجۡزَوۡنَ إِلَّا بِمَا كُنتُمۡ تَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4355, 10, 1417, 53, '۞ وَیَسۡتَنۢبِـُٔونَكَ أَحَقٌّ هُوَۖ قُلۡ إِی وَرَبِّیۤ إِنَّهُۥ لَحَقࣱّۖ وَمَاۤ أَنتُم بِمُعۡجِزِینَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4356, 10, 1418, 54, 'وَلَوۡ أَنَّ لِكُلِّ نَفۡسࣲ ظَلَمَتۡ مَا فِی ٱلۡأَرۡضِ لَٱفۡتَدَتۡ بِهِۦۗ وَأَسَرُّوا۟ ٱلنَّدَامَةَ لَمَّا رَأَوُا۟ ٱلۡعَذَابَۖ وَقُضِیَ بَیۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4357, 10, 1419, 55, 'أَلَاۤ إِنَّ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۗ أَلَاۤ إِنَّ وَعۡدَ ٱللَّهِ حَقࣱّ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(4358, 10, 1420, 56, 'هُوَ یُحۡیِۦ وَیُمِیتُ وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4359, 10, 1421, 57, 'یَـٰۤأَیُّهَا ٱلنَّاسُ قَدۡ جَاۤءَتۡكُم مَّوۡعِظَةࣱ مِّن رَّبِّكُمۡ وَشِفَاۤءࣱ لِّمَا فِی ٱلصُّدُورِ وَهُدࣰى وَرَحۡمَةࣱ لِّلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4360, 10, 1422, 58, 'قُلۡ بِفَضۡلِ ٱللَّهِ وَبِرَحۡمَتِهِۦ فَبِذَ ٰ⁠لِكَ فَلۡیَفۡرَحُوا۟ هُوَ خَیۡرࣱ مِّمَّا یَجۡمَعُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4361, 10, 1423, 59, 'قُلۡ أَرَءَیۡتُم مَّاۤ أَنزَلَ ٱللَّهُ لَكُم مِّن رِّزۡقࣲ فَجَعَلۡتُم مِّنۡهُ حَرَامࣰا وَحَلَـٰلࣰا قُلۡ ءَاۤللَّهُ أَذِنَ لَكُمۡۖ أَمۡ عَلَى ٱللَّهِ تَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:31:24', '2023-05-27 11:17:54'),
(4362, 10, 1424, 60, 'وَمَا ظَنُّ ٱلَّذِینَ یَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ یَوۡمَ ٱلۡقِیَـٰمَةِۗ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:54'),
(4363, 10, 1425, 61, 'وَمَا تَكُونُ فِی شَأۡنࣲ وَمَا تَتۡلُوا۟ مِنۡهُ مِن قُرۡءَانࣲ وَلَا تَعۡمَلُونَ مِنۡ عَمَلٍ إِلَّا كُنَّا عَلَیۡكُمۡ شُهُودًا إِذۡ تُفِیضُونَ فِیهِۚ وَمَا یَعۡزُبُ عَن رَّبِّكَ مِن مِّثۡقَالِ ذَرَّةࣲ فِی ٱلۡأَرۡضِ وَلَا فِی ٱلسَّمَاۤءِ وَلَاۤ أَصۡغَرَ مِن ذَ ٰ⁠لِكَ وَلَاۤ أَكۡبَرَ إِلَّا فِی كِتَـٰبࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:54'),
(4364, 10, 1426, 62, 'أَلَاۤ إِنَّ أَوۡلِیَاۤءَ ٱللَّهِ لَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:54'),
(4365, 10, 1427, 63, 'ٱلَّذِینَ ءَامَنُوا۟ وَكَانُوا۟ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4366, 10, 1428, 64, 'لَهُمُ ٱلۡبُشۡرَىٰ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَفِی ٱلۡـَٔاخِرَةِۚ لَا تَبۡدِیلَ لِكَلِمَـٰتِ ٱللَّهِۚ ذَ ٰ⁠لِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4367, 10, 1429, 65, 'وَلَا یَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِیعًاۚ هُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4368, 10, 1430, 66, 'أَلَاۤ إِنَّ لِلَّهِ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَن فِی ٱلۡأَرۡضِۗ وَمَا یَتَّبِعُ ٱلَّذِینَ یَدۡعُونَ مِن دُونِ ٱللَّهِ شُرَكَاۤءَۚ إِن یَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا یَخۡرُصُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4369, 10, 1431, 67, 'هُوَ ٱلَّذِی جَعَلَ لَكُمُ ٱلَّیۡلَ لِتَسۡكُنُوا۟ فِیهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4370, 10, 1432, 68, 'قَالُوا۟ ٱتَّخَذَ ٱللَّهُ وَلَدࣰاۗ سُبۡحَـٰنَهُۥۖ هُوَ ٱلۡغَنِیُّۖ لَهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ إِنۡ عِندَكُم مِّن سُلۡطَـٰنِۭ بِهَـٰذَاۤۚ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4371, 10, 1433, 69, 'قُلۡ إِنَّ ٱلَّذِینَ یَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا یُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4372, 10, 1434, 70, 'مَتَـٰعࣱ فِی ٱلدُّنۡیَا ثُمَّ إِلَیۡنَا مَرۡجِعُهُمۡ ثُمَّ نُذِیقُهُمُ ٱلۡعَذَابَ ٱلشَّدِیدَ بِمَا كَانُوا۟ یَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:31:25', '2023-05-27 11:17:55'),
(4373, 10, 1435, 71, '۞ وَٱتۡلُ عَلَیۡهِمۡ نَبَأَ نُوحٍ إِذۡ قَالَ لِقَوۡمِهِۦ یَـٰقَوۡمِ إِن كَانَ كَبُرَ عَلَیۡكُم مَّقَامِی وَتَذۡكِیرِی بِـَٔایَـٰتِ ٱللَّهِ فَعَلَى ٱللَّهِ تَوَكَّلۡتُ فَأَجۡمِعُوۤا۟ أَمۡرَكُمۡ وَشُرَكَاۤءَكُمۡ ثُمَّ لَا یَكُنۡ أَمۡرُكُمۡ عَلَیۡكُمۡ غُمَّةࣰ ثُمَّ ٱقۡضُوۤا۟ إِلَیَّ وَلَا تُنظِرُونِ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4374, 10, 1436, 72, 'فَإِن تَوَلَّیۡتُمۡ فَمَا سَأَلۡتُكُم مِّنۡ أَجۡرٍۖ إِنۡ أَجۡرِیَ إِلَّا عَلَى ٱللَّهِۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4375, 10, 1437, 73, 'فَكَذَّبُوهُ فَنَجَّیۡنَـٰهُ وَمَن مَّعَهُۥ فِی ٱلۡفُلۡكِ وَجَعَلۡنَـٰهُمۡ خَلَـٰۤىِٕفَ وَأَغۡرَقۡنَا ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَاۖ فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُنذَرِینَ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4376, 10, 1438, 74, 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِۦ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَاۤءُوهُم بِٱلۡبَیِّنَـٰتِ فَمَا كَانُوا۟ لِیُؤۡمِنُوا۟ بِمَا كَذَّبُوا۟ بِهِۦ مِن قَبۡلُۚ كَذَ ٰ⁠لِكَ نَطۡبَعُ عَلَىٰ قُلُوبِ ٱلۡمُعۡتَدِینَ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4377, 10, 1439, 75, 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ وَهَـٰرُونَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِی۟هِۦ بِـَٔایَـٰتِنَا فَٱسۡتَكۡبَرُوا۟ وَكَانُوا۟ قَوۡمࣰا مُّجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4378, 10, 1440, 76, 'فَلَمَّا جَاۤءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُوۤا۟ إِنَّ هَـٰذَا لَسِحۡرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4379, 10, 1441, 77, 'قَالَ مُوسَىٰۤ أَتَقُولُونَ لِلۡحَقِّ لَمَّا جَاۤءَكُمۡۖ أَسِحۡرٌ هَـٰذَا وَلَا یُفۡلِحُ ٱلسَّـٰحِرُونَ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4380, 10, 1442, 78, 'قَالُوۤا۟ أَجِئۡتَنَا لِتَلۡفِتَنَا عَمَّا وَجَدۡنَا عَلَیۡهِ ءَابَاۤءَنَا وَتَكُونَ لَكُمَا ٱلۡكِبۡرِیَاۤءُ فِی ٱلۡأَرۡضِ وَمَا نَحۡنُ لَكُمَا بِمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:55'),
(4381, 10, 1443, 79, 'وَقَالَ فِرۡعَوۡنُ ٱئۡتُونِی بِكُلِّ سَـٰحِرٍ عَلِیمࣲ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:56'),
(4382, 10, 1444, 80, 'فَلَمَّا جَاۤءَ ٱلسَّحَرَةُ قَالَ لَهُم مُّوسَىٰۤ أَلۡقُوا۟ مَاۤ أَنتُم مُّلۡقُونَ\n', NULL, 0, '2023-05-26 17:31:26', '2023-05-27 11:17:56'),
(4383, 10, 1445, 81, 'فَلَمَّاۤ أَلۡقَوۡا۟ قَالَ مُوسَىٰ مَا جِئۡتُم بِهِ ٱلسِّحۡرُۖ إِنَّ ٱللَّهَ سَیُبۡطِلُهُۥۤ إِنَّ ٱللَّهَ لَا یُصۡلِحُ عَمَلَ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4384, 10, 1446, 82, 'وَیُحِقُّ ٱللَّهُ ٱلۡحَقَّ بِكَلِمَـٰتِهِۦ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4385, 10, 1447, 83, 'فَمَاۤ ءَامَنَ لِمُوسَىٰۤ إِلَّا ذُرِّیَّةࣱ مِّن قَوۡمِهِۦ عَلَىٰ خَوۡفࣲ مِّن فِرۡعَوۡنَ وَمَلَإِی۟هِمۡ أَن یَفۡتِنَهُمۡۚ وَإِنَّ فِرۡعَوۡنَ لَعَالࣲ فِی ٱلۡأَرۡضِ وَإِنَّهُۥ لَمِنَ ٱلۡمُسۡرِفِینَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4386, 10, 1448, 84, 'وَقَالَ مُوسَىٰ یَـٰقَوۡمِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ فَعَلَیۡهِ تَوَكَّلُوۤا۟ إِن كُنتُم مُّسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4387, 10, 1449, 85, 'فَقَالُوا۟ عَلَى ٱللَّهِ تَوَكَّلۡنَا رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةࣰ لِّلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4388, 10, 1450, 86, 'وَنَجِّنَا بِرَحۡمَتِكَ مِنَ ٱلۡقَوۡمِ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4389, 10, 1451, 87, 'وَأَوۡحَیۡنَاۤ إِلَىٰ مُوسَىٰ وَأَخِیهِ أَن تَبَوَّءَا لِقَوۡمِكُمَا بِمِصۡرَ بُیُوتࣰا وَٱجۡعَلُوا۟ بُیُوتَكُمۡ قِبۡلَةࣰ وَأَقِیمُوا۟ ٱلصَّلَوٰةَۗ وَبَشِّرِ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4390, 10, 1452, 88, 'وَقَالَ مُوسَىٰ رَبَّنَاۤ إِنَّكَ ءَاتَیۡتَ فِرۡعَوۡنَ وَمَلَأَهُۥ زِینَةࣰ وَأَمۡوَ ٰ⁠لࣰا فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا رَبَّنَا لِیُضِلُّوا۟ عَن سَبِیلِكَۖ رَبَّنَا ٱطۡمِسۡ عَلَىٰۤ أَمۡوَ ٰ⁠لِهِمۡ وَٱشۡدُدۡ عَلَىٰ قُلُوبِهِمۡ فَلَا یُؤۡمِنُوا۟ حَتَّىٰ یَرَوُا۟ ٱلۡعَذَابَ ٱلۡأَلِیمَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4391, 10, 1453, 89, 'قَالَ قَدۡ أُجِیبَت دَّعۡوَتُكُمَا فَٱسۡتَقِیمَا وَلَا تَتَّبِعَاۤنِّ سَبِیلَ ٱلَّذِینَ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4392, 10, 1454, 90, '۞ وَجَـٰوَزۡنَا بِبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱلۡبَحۡرَ فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ وَجُنُودُهُۥ بَغۡیࣰا وَعَدۡوًاۖ حَتَّىٰۤ إِذَاۤ أَدۡرَكَهُ ٱلۡغَرَقُ قَالَ ءَامَنتُ أَنَّهُۥ لَاۤ إِلَـٰهَ إِلَّا ٱلَّذِیۤ ءَامَنَتۡ بِهِۦ بَنُوۤا۟ إِسۡرَ ٰ⁠ۤءِیلَ وَأَنَا۠ مِنَ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:31:27', '2023-05-27 11:17:56'),
(4393, 10, 1455, 91, 'ءَاۤلۡـَٔـٰنَ وَقَدۡ عَصَیۡتَ قَبۡلُ وَكُنتَ مِنَ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:56'),
(4394, 10, 1456, 92, 'فَٱلۡیَوۡمَ نُنَجِّیكَ بِبَدَنِكَ لِتَكُونَ لِمَنۡ خَلۡفَكَ ءَایَةࣰۚ وَإِنَّ كَثِیرࣰا مِّنَ ٱلنَّاسِ عَنۡ ءَایَـٰتِنَا لَغَـٰفِلُونَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:56'),
(4395, 10, 1457, 93, 'وَلَقَدۡ بَوَّأۡنَا بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ مُبَوَّأَ صِدۡقࣲ وَرَزَقۡنَـٰهُم مِّنَ ٱلطَّیِّبَـٰتِ فَمَا ٱخۡتَلَفُوا۟ حَتَّىٰ جَاۤءَهُمُ ٱلۡعِلۡمُۚ إِنَّ رَبَّكَ یَقۡضِی بَیۡنَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ فِیمَا كَانُوا۟ فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:56'),
(4396, 10, 1458, 94, 'فَإِن كُنتَ فِی شَكࣲّ مِّمَّاۤ أَنزَلۡنَاۤ إِلَیۡكَ فَسۡـَٔلِ ٱلَّذِینَ یَقۡرَءُونَ ٱلۡكِتَـٰبَ مِن قَبۡلِكَۚ لَقَدۡ جَاۤءَكَ ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِینَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:56'),
(4397, 10, 1459, 95, 'وَلَا تَكُونَنَّ مِنَ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِ ٱللَّهِ فَتَكُونَ مِنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:57'),
(4398, 10, 1460, 96, 'إِنَّ ٱلَّذِینَ حَقَّتۡ عَلَیۡهِمۡ كَلِمَتُ رَبِّكَ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:57'),
(4399, 10, 1461, 97, 'وَلَوۡ جَاۤءَتۡهُمۡ كُلُّ ءَایَةٍ حَتَّىٰ یَرَوُا۟ ٱلۡعَذَابَ ٱلۡأَلِیمَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:57'),
(4400, 10, 1462, 98, 'فَلَوۡلَا كَانَتۡ قَرۡیَةٌ ءَامَنَتۡ فَنَفَعَهَاۤ إِیمَـٰنُهَاۤ إِلَّا قَوۡمَ یُونُسَ لَمَّاۤ ءَامَنُوا۟ كَشَفۡنَا عَنۡهُمۡ عَذَابَ ٱلۡخِزۡیِ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَمَتَّعۡنَـٰهُمۡ إِلَىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:57'),
(4401, 10, 1463, 99, 'وَلَوۡ شَاۤءَ رَبُّكَ لَـَٔامَنَ مَن فِی ٱلۡأَرۡضِ كُلُّهُمۡ جَمِیعًاۚ أَفَأَنتَ تُكۡرِهُ ٱلنَّاسَ حَتَّىٰ یَكُونُوا۟ مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:28', '2023-05-27 11:17:57'),
(4402, 10, 1464, 100, 'وَمَا كَانَ لِنَفۡسٍ أَن تُؤۡمِنَ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَیَجۡعَلُ ٱلرِّجۡسَ عَلَى ٱلَّذِینَ لَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4403, 10, 1465, 101, 'قُلِ ٱنظُرُوا۟ مَاذَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَمَا تُغۡنِی ٱلۡـَٔایَـٰتُ وَٱلنُّذُرُ عَن قَوۡمࣲ لَّا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4404, 10, 1466, 102, 'فَهَلۡ یَنتَظِرُونَ إِلَّا مِثۡلَ أَیَّامِ ٱلَّذِینَ خَلَوۡا۟ مِن قَبۡلِهِمۡۚ قُلۡ فَٱنتَظِرُوۤا۟ إِنِّی مَعَكُم مِّنَ ٱلۡمُنتَظِرِینَ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4405, 10, 1467, 103, 'ثُمَّ نُنَجِّی رُسُلَنَا وَٱلَّذِینَ ءَامَنُوا۟ۚ كَذَ ٰ⁠لِكَ حَقًّا عَلَیۡنَا نُنجِ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4406, 10, 1468, 104, 'قُلۡ یَـٰۤأَیُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِی شَكࣲّ مِّن دِینِی فَلَاۤ أَعۡبُدُ ٱلَّذِینَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَلَـٰكِنۡ أَعۡبُدُ ٱللَّهَ ٱلَّذِی یَتَوَفَّىٰكُمۡۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4407, 10, 1469, 105, 'وَأَنۡ أَقِمۡ وَجۡهَكَ لِلدِّینِ حَنِیفࣰا وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4408, 10, 1470, 106, 'وَلَا تَدۡعُ مِن دُونِ ٱللَّهِ مَا لَا یَنفَعُكَ وَلَا یَضُرُّكَۖ فَإِن فَعَلۡتَ فَإِنَّكَ إِذࣰا مِّنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4409, 10, 1471, 107, 'وَإِن یَمۡسَسۡكَ ٱللَّهُ بِضُرࣲّ فَلَا كَاشِفَ لَهُۥۤ إِلَّا هُوَۖ وَإِن یُرِدۡكَ بِخَیۡرࣲ فَلَا رَاۤدَّ لِفَضۡلِهِۦۚ یُصِیبُ بِهِۦ مَن یَشَاۤءُ مِنۡ عِبَادِهِۦۚ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4410, 10, 1472, 108, 'قُلۡ یَـٰۤأَیُّهَا ٱلنَّاسُ قَدۡ جَاۤءَكُمُ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا یَهۡتَدِی لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا یَضِلُّ عَلَیۡهَاۖ وَمَاۤ أَنَا۠ عَلَیۡكُم بِوَكِیلࣲ\n', NULL, 0, '2023-05-26 17:31:29', '2023-05-27 11:17:57'),
(4411, 10, 1473, 109, 'وَٱتَّبِعۡ مَا یُوحَىٰۤ إِلَیۡكَ وَٱصۡبِرۡ حَتَّىٰ یَحۡكُمَ ٱللَّهُۚ وَهُوَ خَیۡرُ ٱلۡحَـٰكِمِینَ\n', NULL, 0, '2023-05-26 17:31:30', '2023-05-27 11:17:57'),
(4412, 9, 1236, 1, 'بَرَاۤءَةࣱ مِّنَ ٱللَّهِ وَرَسُولِهِۦۤ إِلَى ٱلَّذِینَ عَـٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4413, 9, 1237, 2, 'فَسِیحُوا۟ فِی ٱلۡأَرۡضِ أَرۡبَعَةَ أَشۡهُرࣲ وَٱعۡلَمُوۤا۟ أَنَّكُمۡ غَیۡرُ مُعۡجِزِی ٱللَّهِ وَأَنَّ ٱللَّهَ مُخۡزِی ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4414, 9, 1238, 3, 'وَأَذَ ٰ⁠نࣱ مِّنَ ٱللَّهِ وَرَسُولِهِۦۤ إِلَى ٱلنَّاسِ یَوۡمَ ٱلۡحَجِّ ٱلۡأَكۡبَرِ أَنَّ ٱللَّهَ بَرِیۤءࣱ مِّنَ ٱلۡمُشۡرِكِینَ وَرَسُولُهُۥۚ فَإِن تُبۡتُمۡ فَهُوَ خَیۡرࣱ لَّكُمۡۖ وَإِن تَوَلَّیۡتُمۡ فَٱعۡلَمُوۤا۟ أَنَّكُمۡ غَیۡرُ مُعۡجِزِی ٱللَّهِۗ وَبَشِّرِ ٱلَّذِینَ كَفَرُوا۟ بِعَذَابٍ أَلِیمٍ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4415, 9, 1239, 4, 'إِلَّا ٱلَّذِینَ عَـٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِینَ ثُمَّ لَمۡ یَنقُصُوكُمۡ شَیۡـࣰٔا وَلَمۡ یُظَـٰهِرُوا۟ عَلَیۡكُمۡ أَحَدࣰا فَأَتِمُّوۤا۟ إِلَیۡهِمۡ عَهۡدَهُمۡ إِلَىٰ مُدَّتِهِمۡۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4416, 9, 1240, 5, 'فَإِذَا ٱنسَلَخَ ٱلۡأَشۡهُرُ ٱلۡحُرُمُ فَٱقۡتُلُوا۟ ٱلۡمُشۡرِكِینَ حَیۡثُ وَجَدتُّمُوهُمۡ وَخُذُوهُمۡ وَٱحۡصُرُوهُمۡ وَٱقۡعُدُوا۟ لَهُمۡ كُلَّ مَرۡصَدࣲۚ فَإِن تَابُوا۟ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَءَاتَوُا۟ ٱلزَّكَوٰةَ فَخَلُّوا۟ سَبِیلَهُمۡۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4417, 9, 1241, 6, 'وَإِنۡ أَحَدࣱ مِّنَ ٱلۡمُشۡرِكِینَ ٱسۡتَجَارَكَ فَأَجِرۡهُ حَتَّىٰ یَسۡمَعَ كَلَـٰمَ ٱللَّهِ ثُمَّ أَبۡلِغۡهُ مَأۡمَنَهُۥۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَوۡمࣱ لَّا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4418, 9, 1242, 7, 'كَیۡفَ یَكُونُ لِلۡمُشۡرِكِینَ عَهۡدٌ عِندَ ٱللَّهِ وَعِندَ رَسُولِهِۦۤ إِلَّا ٱلَّذِینَ عَـٰهَدتُّمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ فَمَا ٱسۡتَقَـٰمُوا۟ لَكُمۡ فَٱسۡتَقِیمُوا۟ لَهُمۡۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4419, 9, 1243, 8, 'كَیۡفَ وَإِن یَظۡهَرُوا۟ عَلَیۡكُمۡ لَا یَرۡقُبُوا۟ فِیكُمۡ إِلࣰّا وَلَا ذِمَّةࣰۚ یُرۡضُونَكُم بِأَفۡوَ ٰ⁠هِهِمۡ وَتَأۡبَىٰ قُلُوبُهُمۡ وَأَكۡثَرُهُمۡ فَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4420, 9, 1244, 9, 'ٱشۡتَرَوۡا۟ بِـَٔایَـٰتِ ٱللَّهِ ثَمَنࣰا قَلِیلࣰا فَصَدُّوا۟ عَن سَبِیلِهِۦۤۚ إِنَّهُمۡ سَاۤءَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:55', '2023-05-27 11:18:07'),
(4421, 9, 1245, 10, 'لَا یَرۡقُبُونَ فِی مُؤۡمِنٍ إِلࣰّا وَلَا ذِمَّةࣰۚ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُعۡتَدُونَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:07'),
(4422, 9, 1246, 11, 'فَإِن تَابُوا۟ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَءَاتَوُا۟ ٱلزَّكَوٰةَ فَإِخۡوَ ٰ⁠نُكُمۡ فِی ٱلدِّینِۗ وَنُفَصِّلُ ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:07'),
(4423, 9, 1247, 12, 'وَإِن نَّكَثُوۤا۟ أَیۡمَـٰنَهُم مِّنۢ بَعۡدِ عَهۡدِهِمۡ وَطَعَنُوا۟ فِی دِینِكُمۡ فَقَـٰتِلُوۤا۟ أَىِٕمَّةَ ٱلۡكُفۡرِ إِنَّهُمۡ لَاۤ أَیۡمَـٰنَ لَهُمۡ لَعَلَّهُمۡ یَنتَهُونَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:07'),
(4424, 9, 1248, 13, 'أَلَا تُقَـٰتِلُونَ قَوۡمࣰا نَّكَثُوۤا۟ أَیۡمَـٰنَهُمۡ وَهَمُّوا۟ بِإِخۡرَاجِ ٱلرَّسُولِ وَهُم بَدَءُوكُمۡ أَوَّلَ مَرَّةٍۚ أَتَخۡشَوۡنَهُمۡۚ فَٱللَّهُ أَحَقُّ أَن تَخۡشَوۡهُ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:07'),
(4425, 9, 1249, 14, 'قَـٰتِلُوهُمۡ یُعَذِّبۡهُمُ ٱللَّهُ بِأَیۡدِیكُمۡ وَیُخۡزِهِمۡ وَیَنصُرۡكُمۡ عَلَیۡهِمۡ وَیَشۡفِ صُدُورَ قَوۡمࣲ مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:07'),
(4426, 9, 1250, 15, 'وَیُذۡهِبۡ غَیۡظَ قُلُوبِهِمۡۗ وَیَتُوبُ ٱللَّهُ عَلَىٰ مَن یَشَاۤءُۗ وَٱللَّهُ عَلِیمٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:07'),
(4427, 9, 1251, 16, 'أَمۡ حَسِبۡتُمۡ أَن تُتۡرَكُوا۟ وَلَمَّا یَعۡلَمِ ٱللَّهُ ٱلَّذِینَ جَـٰهَدُوا۟ مِنكُمۡ وَلَمۡ یَتَّخِذُوا۟ مِن دُونِ ٱللَّهِ وَلَا رَسُولِهِۦ وَلَا ٱلۡمُؤۡمِنِینَ وَلِیجَةࣰۚ وَٱللَّهُ خَبِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:08'),
(4428, 9, 1252, 17, 'مَا كَانَ لِلۡمُشۡرِكِینَ أَن یَعۡمُرُوا۟ مَسَـٰجِدَ ٱللَّهِ شَـٰهِدِینَ عَلَىٰۤ أَنفُسِهِم بِٱلۡكُفۡرِۚ أُو۟لَـٰۤىِٕكَ حَبِطَتۡ أَعۡمَـٰلُهُمۡ وَفِی ٱلنَّارِ هُمۡ خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:08'),
(4429, 9, 1253, 18, 'إِنَّمَا یَعۡمُرُ مَسَـٰجِدَ ٱللَّهِ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَلَمۡ یَخۡشَ إِلَّا ٱللَّهَۖ فَعَسَىٰۤ أُو۟لَـٰۤىِٕكَ أَن یَكُونُوا۟ مِنَ ٱلۡمُهۡتَدِینَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:08'),
(4430, 9, 1254, 19, '۞ أَجَعَلۡتُمۡ سِقَایَةَ ٱلۡحَاۤجِّ وَعِمَارَةَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ كَمَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَجَـٰهَدَ فِی سَبِیلِ ٱللَّهِۚ لَا یَسۡتَوُۥنَ عِندَ ٱللَّهِۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:56', '2023-05-27 11:18:08'),
(4431, 9, 1255, 20, 'ٱلَّذِینَ ءَامَنُوا۟ وَهَاجَرُوا۟ وَجَـٰهَدُوا۟ فِی سَبِیلِ ٱللَّهِ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡ أَعۡظَمُ دَرَجَةً عِندَ ٱللَّهِۚ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡفَاۤىِٕزُونَ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4432, 9, 1256, 21, 'یُبَشِّرُهُمۡ رَبُّهُم بِرَحۡمَةࣲ مِّنۡهُ وَرِضۡوَ ٰ⁠نࣲ وَجَنَّـٰتࣲ لَّهُمۡ فِیهَا نَعِیمࣱ مُّقِیمٌ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4433, 9, 1257, 22, 'خَـٰلِدِینَ فِیهَاۤ أَبَدًاۚ إِنَّ ٱللَّهَ عِندَهُۥۤ أَجۡرٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4434, 9, 1258, 23, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَّخِذُوۤا۟ ءَابَاۤءَكُمۡ وَإِخۡوَ ٰ⁠نَكُمۡ أَوۡلِیَاۤءَ إِنِ ٱسۡتَحَبُّوا۟ ٱلۡكُفۡرَ عَلَى ٱلۡإِیمَـٰنِۚ وَمَن یَتَوَلَّهُم مِّنكُمۡ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4435, 9, 1259, 24, 'قُلۡ إِن كَانَ ءَابَاۤؤُكُمۡ وَأَبۡنَاۤؤُكُمۡ وَإِخۡوَ ٰ⁠نُكُمۡ وَأَزۡوَ ٰ⁠جُكُمۡ وَعَشِیرَتُكُمۡ وَأَمۡوَ ٰ⁠لٌ ٱقۡتَرَفۡتُمُوهَا وَتِجَـٰرَةࣱ تَخۡشَوۡنَ كَسَادَهَا وَمَسَـٰكِنُ تَرۡضَوۡنَهَاۤ أَحَبَّ إِلَیۡكُم مِّنَ ٱللَّهِ وَرَسُولِهِۦ وَجِهَادࣲ فِی سَبِیلِهِۦ فَتَرَبَّصُوا۟ حَتَّىٰ یَأۡتِیَ ٱللَّهُ بِأَمۡرِهِۦۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4436, 9, 1260, 25, 'لَقَدۡ نَصَرَكُمُ ٱللَّهُ فِی مَوَاطِنَ كَثِیرَةࣲ وَیَوۡمَ حُنَیۡنٍ إِذۡ أَعۡجَبَتۡكُمۡ كَثۡرَتُكُمۡ فَلَمۡ تُغۡنِ عَنكُمۡ شَیۡـࣰٔا وَضَاقَتۡ عَلَیۡكُمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ ثُمَّ وَلَّیۡتُم مُّدۡبِرِینَ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4437, 9, 1261, 26, 'ثُمَّ أَنزَلَ ٱللَّهُ سَكِینَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِینَ وَأَنزَلَ جُنُودࣰا لَّمۡ تَرَوۡهَا وَعَذَّبَ ٱلَّذِینَ كَفَرُوا۟ۚ وَذَ ٰ⁠لِكَ جَزَاۤءُ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4438, 9, 1262, 27, 'ثُمَّ یَتُوبُ ٱللَّهُ مِنۢ بَعۡدِ ذَ ٰ⁠لِكَ عَلَىٰ مَن یَشَاۤءُۗ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4439, 9, 1263, 28, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِنَّمَا ٱلۡمُشۡرِكُونَ نَجَسࣱ فَلَا یَقۡرَبُوا۟ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ بَعۡدَ عَامِهِمۡ هَـٰذَاۚ وَإِنۡ خِفۡتُمۡ عَیۡلَةࣰ فَسَوۡفَ یُغۡنِیكُمُ ٱللَّهُ مِن فَضۡلِهِۦۤ إِن شَاۤءَۚ إِنَّ ٱللَّهَ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4440, 9, 1264, 29, 'قَـٰتِلُوا۟ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَلَا یُحَرِّمُونَ مَا حَرَّمَ ٱللَّهُ وَرَسُولُهُۥ وَلَا یَدِینُونَ دِینَ ٱلۡحَقِّ مِنَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ حَتَّىٰ یُعۡطُوا۟ ٱلۡجِزۡیَةَ عَن یَدࣲ وَهُمۡ صَـٰغِرُونَ\n', NULL, 0, '2023-05-26 17:31:57', '2023-05-27 11:18:08'),
(4441, 9, 1265, 30, 'وَقَالَتِ ٱلۡیَهُودُ عُزَیۡرٌ ٱبۡنُ ٱللَّهِ وَقَالَتِ ٱلنَّصَـٰرَى ٱلۡمَسِیحُ ٱبۡنُ ٱللَّهِۖ ذَ ٰ⁠لِكَ قَوۡلُهُم بِأَفۡوَ ٰ⁠هِهِمۡۖ یُضَـٰهِـُٔونَ قَوۡلَ ٱلَّذِینَ كَفَرُوا۟ مِن قَبۡلُۚ قَـٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ یُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:08'),
(4442, 9, 1266, 31, 'ٱتَّخَذُوۤا۟ أَحۡبَارَهُمۡ وَرُهۡبَـٰنَهُمۡ أَرۡبَابࣰا مِّن دُونِ ٱللَّهِ وَٱلۡمَسِیحَ ٱبۡنَ مَرۡیَمَ وَمَاۤ أُمِرُوۤا۟ إِلَّا لِیَعۡبُدُوۤا۟ إِلَـٰهࣰا وَ ٰ⁠حِدࣰاۖ لَّاۤ إِلَـٰهَ إِلَّا هُوَۚ سُبۡحَـٰنَهُۥ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:08'),
(4443, 9, 1267, 32, 'یُرِیدُونَ أَن یُطۡفِـُٔوا۟ نُورَ ٱللَّهِ بِأَفۡوَ ٰ⁠هِهِمۡ وَیَأۡبَى ٱللَّهُ إِلَّاۤ أَن یُتِمَّ نُورَهُۥ وَلَوۡ كَرِهَ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:08'),
(4444, 9, 1268, 33, 'هُوَ ٱلَّذِیۤ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِینِ ٱلۡحَقِّ لِیُظۡهِرَهُۥ عَلَى ٱلدِّینِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:08'),
(4445, 9, 1269, 34, '۞ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِنَّ كَثِیرࣰا مِّنَ ٱلۡأَحۡبَارِ وَٱلرُّهۡبَانِ لَیَأۡكُلُونَ أَمۡوَ ٰ⁠لَ ٱلنَّاسِ بِٱلۡبَـٰطِلِ وَیَصُدُّونَ عَن سَبِیلِ ٱللَّهِۗ وَٱلَّذِینَ یَكۡنِزُونَ ٱلذَّهَبَ وَٱلۡفِضَّةَ وَلَا یُنفِقُونَهَا فِی سَبِیلِ ٱللَّهِ فَبَشِّرۡهُم بِعَذَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:09'),
(4446, 9, 1270, 35, 'یَوۡمَ یُحۡمَىٰ عَلَیۡهَا فِی نَارِ جَهَنَّمَ فَتُكۡوَىٰ بِهَا جِبَاهُهُمۡ وَجُنُوبُهُمۡ وَظُهُورُهُمۡۖ هَـٰذَا مَا كَنَزۡتُمۡ لِأَنفُسِكُمۡ فَذُوقُوا۟ مَا كُنتُمۡ تَكۡنِزُونَ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:09'),
(4447, 9, 1271, 36, 'إِنَّ عِدَّةَ ٱلشُّهُورِ عِندَ ٱللَّهِ ٱثۡنَا عَشَرَ شَهۡرࣰا فِی كِتَـٰبِ ٱللَّهِ یَوۡمَ خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ مِنۡهَاۤ أَرۡبَعَةٌ حُرُمࣱۚ ذَ ٰ⁠لِكَ ٱلدِّینُ ٱلۡقَیِّمُۚ فَلَا تَظۡلِمُوا۟ فِیهِنَّ أَنفُسَكُمۡۚ وَقَـٰتِلُوا۟ ٱلۡمُشۡرِكِینَ كَاۤفَّةࣰ كَمَا یُقَـٰتِلُونَكُمۡ كَاۤفَّةࣰۚ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:09'),
(4448, 9, 1272, 37, 'إِنَّمَا ٱلنَّسِیۤءُ زِیَادَةࣱ فِی ٱلۡكُفۡرِۖ یُضَلُّ بِهِ ٱلَّذِینَ كَفَرُوا۟ یُحِلُّونَهُۥ عَامࣰا وَیُحَرِّمُونَهُۥ عَامࣰا لِّیُوَاطِـُٔوا۟ عِدَّةَ مَا حَرَّمَ ٱللَّهُ فَیُحِلُّوا۟ مَا حَرَّمَ ٱللَّهُۚ زُیِّنَ لَهُمۡ سُوۤءُ أَعۡمَـٰلِهِمۡۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:09'),
(4449, 9, 1273, 38, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ مَا لَكُمۡ إِذَا قِیلَ لَكُمُ ٱنفِرُوا۟ فِی سَبِیلِ ٱللَّهِ ٱثَّاقَلۡتُمۡ إِلَى ٱلۡأَرۡضِۚ أَرَضِیتُم بِٱلۡحَیَوٰةِ ٱلدُّنۡیَا مِنَ ٱلۡـَٔاخِرَةِۚ فَمَا مَتَـٰعُ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا فِی ٱلۡـَٔاخِرَةِ إِلَّا قَلِیلٌ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:09'),
(4450, 9, 1274, 39, 'إِلَّا تَنفِرُوا۟ یُعَذِّبۡكُمۡ عَذَابًا أَلِیمࣰا وَیَسۡتَبۡدِلۡ قَوۡمًا غَیۡرَكُمۡ وَلَا تَضُرُّوهُ شَیۡـࣰٔاۗ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:31:58', '2023-05-27 11:18:09'),
(4451, 9, 1275, 40, 'إِلَّا تَنصُرُوهُ فَقَدۡ نَصَرَهُ ٱللَّهُ إِذۡ أَخۡرَجَهُ ٱلَّذِینَ كَفَرُوا۟ ثَانِیَ ٱثۡنَیۡنِ إِذۡ هُمَا فِی ٱلۡغَارِ إِذۡ یَقُولُ لِصَـٰحِبِهِۦ لَا تَحۡزَنۡ إِنَّ ٱللَّهَ مَعَنَاۖ فَأَنزَلَ ٱللَّهُ سَكِینَتَهُۥ عَلَیۡهِ وَأَیَّدَهُۥ بِجُنُودࣲ لَّمۡ تَرَوۡهَا وَجَعَلَ كَلِمَةَ ٱلَّذِینَ كَفَرُوا۟ ٱلسُّفۡلَىٰۗ وَكَلِمَةُ ٱللَّهِ هِیَ ٱلۡعُلۡیَاۗ وَٱللَّهُ عَزِیزٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4452, 9, 1276, 41, 'ٱنفِرُوا۟ خِفَافࣰا وَثِقَالࣰا وَجَـٰهِدُوا۟ بِأَمۡوَ ٰ⁠لِكُمۡ وَأَنفُسِكُمۡ فِی سَبِیلِ ٱللَّهِۚ ذَ ٰ⁠لِكُمۡ خَیۡرࣱ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4453, 9, 1277, 42, 'لَوۡ كَانَ عَرَضࣰا قَرِیبࣰا وَسَفَرࣰا قَاصِدࣰا لَّٱتَّبَعُوكَ وَلَـٰكِنۢ بَعُدَتۡ عَلَیۡهِمُ ٱلشُّقَّةُۚ وَسَیَحۡلِفُونَ بِٱللَّهِ لَوِ ٱسۡتَطَعۡنَا لَخَرَجۡنَا مَعَكُمۡ یُهۡلِكُونَ أَنفُسَهُمۡ وَٱللَّهُ یَعۡلَمُ إِنَّهُمۡ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4454, 9, 1278, 43, 'عَفَا ٱللَّهُ عَنكَ لِمَ أَذِنتَ لَهُمۡ حَتَّىٰ یَتَبَیَّنَ لَكَ ٱلَّذِینَ صَدَقُوا۟ وَتَعۡلَمَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4455, 9, 1279, 44, 'لَا یَسۡتَـٔۡذِنُكَ ٱلَّذِینَ یُؤۡمِنُونَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ أَن یُجَـٰهِدُوا۟ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡۗ وَٱللَّهُ عَلِیمُۢ بِٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4456, 9, 1280, 45, 'إِنَّمَا یَسۡتَـٔۡذِنُكَ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَٱرۡتَابَتۡ قُلُوبُهُمۡ فَهُمۡ فِی رَیۡبِهِمۡ یَتَرَدَّدُونَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4457, 9, 1281, 46, '۞ وَلَوۡ أَرَادُوا۟ ٱلۡخُرُوجَ لَأَعَدُّوا۟ لَهُۥ عُدَّةࣰ وَلَـٰكِن كَرِهَ ٱللَّهُ ٱنۢبِعَاثَهُمۡ فَثَبَّطَهُمۡ وَقِیلَ ٱقۡعُدُوا۟ مَعَ ٱلۡقَـٰعِدِینَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4458, 9, 1282, 47, 'لَوۡ خَرَجُوا۟ فِیكُم مَّا زَادُوكُمۡ إِلَّا خَبَالࣰا وَلَأَوۡضَعُوا۟ خِلَـٰلَكُمۡ یَبۡغُونَكُمُ ٱلۡفِتۡنَةَ وَفِیكُمۡ سَمَّـٰعُونَ لَهُمۡۗ وَٱللَّهُ عَلِیمُۢ بِٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4459, 9, 1283, 48, 'لَقَدِ ٱبۡتَغَوُا۟ ٱلۡفِتۡنَةَ مِن قَبۡلُ وَقَلَّبُوا۟ لَكَ ٱلۡأُمُورَ حَتَّىٰ جَاۤءَ ٱلۡحَقُّ وَظَهَرَ أَمۡرُ ٱللَّهِ وَهُمۡ كَـٰرِهُونَ\n', NULL, 0, '2023-05-26 17:31:59', '2023-05-27 11:18:09'),
(4460, 9, 1284, 49, 'وَمِنۡهُم مَّن یَقُولُ ٱئۡذَن لِّی وَلَا تَفۡتِنِّیۤۚ أَلَا فِی ٱلۡفِتۡنَةِ سَقَطُوا۟ۗ وَإِنَّ جَهَنَّمَ لَمُحِیطَةُۢ بِٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:09'),
(4461, 9, 1285, 50, 'إِن تُصِبۡكَ حَسَنَةࣱ تَسُؤۡهُمۡۖ وَإِن تُصِبۡكَ مُصِیبَةࣱ یَقُولُوا۟ قَدۡ أَخَذۡنَاۤ أَمۡرَنَا مِن قَبۡلُ وَیَتَوَلَّوا۟ وَّهُمۡ فَرِحُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4462, 9, 1286, 51, 'قُل لَّن یُصِیبَنَاۤ إِلَّا مَا كَتَبَ ٱللَّهُ لَنَا هُوَ مَوۡلَىٰنَاۚ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4463, 9, 1287, 52, 'قُلۡ هَلۡ تَرَبَّصُونَ بِنَاۤ إِلَّاۤ إِحۡدَى ٱلۡحُسۡنَیَیۡنِۖ وَنَحۡنُ نَتَرَبَّصُ بِكُمۡ أَن یُصِیبَكُمُ ٱللَّهُ بِعَذَابࣲ مِّنۡ عِندِهِۦۤ أَوۡ بِأَیۡدِینَاۖ فَتَرَبَّصُوۤا۟ إِنَّا مَعَكُم مُّتَرَبِّصُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4464, 9, 1288, 53, 'قُلۡ أَنفِقُوا۟ طَوۡعًا أَوۡ كَرۡهࣰا لَّن یُتَقَبَّلَ مِنكُمۡ إِنَّكُمۡ كُنتُمۡ قَوۡمࣰا فَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4465, 9, 1289, 54, 'وَمَا مَنَعَهُمۡ أَن تُقۡبَلَ مِنۡهُمۡ نَفَقَـٰتُهُمۡ إِلَّاۤ أَنَّهُمۡ كَفَرُوا۟ بِٱللَّهِ وَبِرَسُولِهِۦ وَلَا یَأۡتُونَ ٱلصَّلَوٰةَ إِلَّا وَهُمۡ كُسَالَىٰ وَلَا یُنفِقُونَ إِلَّا وَهُمۡ كَـٰرِهُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4466, 9, 1290, 55, 'فَلَا تُعۡجِبۡكَ أَمۡوَ ٰ⁠لُهُمۡ وَلَاۤ أَوۡلَـٰدُهُمۡۚ إِنَّمَا یُرِیدُ ٱللَّهُ لِیُعَذِّبَهُم بِهَا فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4467, 9, 1291, 56, 'وَیَحۡلِفُونَ بِٱللَّهِ إِنَّهُمۡ لَمِنكُمۡ وَمَا هُم مِّنكُمۡ وَلَـٰكِنَّهُمۡ قَوۡمࣱ یَفۡرَقُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4468, 9, 1292, 57, 'لَوۡ یَجِدُونَ مَلۡجَـًٔا أَوۡ مَغَـٰرَ ٰ⁠تٍ أَوۡ مُدَّخَلࣰا لَّوَلَّوۡا۟ إِلَیۡهِ وَهُمۡ یَجۡمَحُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4469, 9, 1293, 58, 'وَمِنۡهُم مَّن یَلۡمِزُكَ فِی ٱلصَّدَقَـٰتِ فَإِنۡ أُعۡطُوا۟ مِنۡهَا رَضُوا۟ وَإِن لَّمۡ یُعۡطَوۡا۟ مِنۡهَاۤ إِذَا هُمۡ یَسۡخَطُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4470, 9, 1294, 59, 'وَلَوۡ أَنَّهُمۡ رَضُوا۟ مَاۤ ءَاتَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ وَقَالُوا۟ حَسۡبُنَا ٱللَّهُ سَیُؤۡتِینَا ٱللَّهُ مِن فَضۡلِهِۦ وَرَسُولُهُۥۤ إِنَّاۤ إِلَى ٱللَّهِ رَ ٰ⁠غِبُونَ\n', NULL, 0, '2023-05-26 17:32:00', '2023-05-27 11:18:10'),
(4471, 9, 1295, 60, '۞ إِنَّمَا ٱلصَّدَقَـٰتُ لِلۡفُقَرَاۤءِ وَٱلۡمَسَـٰكِینِ وَٱلۡعَـٰمِلِینَ عَلَیۡهَا وَٱلۡمُؤَلَّفَةِ قُلُوبُهُمۡ وَفِی ٱلرِّقَابِ وَٱلۡغَـٰرِمِینَ وَفِی سَبِیلِ ٱللَّهِ وَٱبۡنِ ٱلسَّبِیلِۖ فَرِیضَةࣰ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:10'),
(4472, 9, 1296, 61, 'وَمِنۡهُمُ ٱلَّذِینَ یُؤۡذُونَ ٱلنَّبِیَّ وَیَقُولُونَ هُوَ أُذُنࣱۚ قُلۡ أُذُنُ خَیۡرࣲ لَّكُمۡ یُؤۡمِنُ بِٱللَّهِ وَیُؤۡمِنُ لِلۡمُؤۡمِنِینَ وَرَحۡمَةࣱ لِّلَّذِینَ ءَامَنُوا۟ مِنكُمۡۚ وَٱلَّذِینَ یُؤۡذُونَ رَسُولَ ٱللَّهِ لَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:10'),
(4473, 9, 1297, 62, 'یَحۡلِفُونَ بِٱللَّهِ لَكُمۡ لِیُرۡضُوكُمۡ وَٱللَّهُ وَرَسُولُهُۥۤ أَحَقُّ أَن یُرۡضُوهُ إِن كَانُوا۟ مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:10'),
(4474, 9, 1298, 63, 'أَلَمۡ یَعۡلَمُوۤا۟ أَنَّهُۥ مَن یُحَادِدِ ٱللَّهَ وَرَسُولَهُۥ فَأَنَّ لَهُۥ نَارَ جَهَنَّمَ خَـٰلِدࣰا فِیهَاۚ ذَ ٰ⁠لِكَ ٱلۡخِزۡیُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:10'),
(4475, 9, 1299, 64, 'یَحۡذَرُ ٱلۡمُنَـٰفِقُونَ أَن تُنَزَّلَ عَلَیۡهِمۡ سُورَةࣱ تُنَبِّئُهُم بِمَا فِی قُلُوبِهِمۡۚ قُلِ ٱسۡتَهۡزِءُوۤا۟ إِنَّ ٱللَّهَ مُخۡرِجࣱ مَّا تَحۡذَرُونَ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:10'),
(4476, 9, 1300, 65, 'وَلَىِٕن سَأَلۡتَهُمۡ لَیَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلۡعَبُۚ قُلۡ أَبِٱللَّهِ وَءَایَـٰتِهِۦ وَرَسُولِهِۦ كُنتُمۡ تَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:11'),
(4477, 9, 1301, 66, 'لَا تَعۡتَذِرُوا۟ قَدۡ كَفَرۡتُم بَعۡدَ إِیمَـٰنِكُمۡۚ إِن نَّعۡفُ عَن طَاۤىِٕفَةࣲ مِّنكُمۡ نُعَذِّبۡ طَاۤىِٕفَةَۢ بِأَنَّهُمۡ كَانُوا۟ مُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:11'),
(4478, 9, 1302, 67, 'ٱلۡمُنَـٰفِقُونَ وَٱلۡمُنَـٰفِقَـٰتُ بَعۡضُهُم مِّنۢ بَعۡضࣲۚ یَأۡمُرُونَ بِٱلۡمُنكَرِ وَیَنۡهَوۡنَ عَنِ ٱلۡمَعۡرُوفِ وَیَقۡبِضُونَ أَیۡدِیَهُمۡۚ نَسُوا۟ ٱللَّهَ فَنَسِیَهُمۡۚ إِنَّ ٱلۡمُنَـٰفِقِینَ هُمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:11'),
(4479, 9, 1303, 68, 'وَعَدَ ٱللَّهُ ٱلۡمُنَـٰفِقِینَ وَٱلۡمُنَـٰفِقَـٰتِ وَٱلۡكُفَّارَ نَارَ جَهَنَّمَ خَـٰلِدِینَ فِیهَاۚ هِیَ حَسۡبُهُمۡۚ وَلَعَنَهُمُ ٱللَّهُۖ وَلَهُمۡ عَذَابࣱ مُّقِیمࣱ\n', NULL, 0, '2023-05-26 17:32:01', '2023-05-27 11:18:11'),
(4480, 9, 1304, 69, 'كَٱلَّذِینَ مِن قَبۡلِكُمۡ كَانُوۤا۟ أَشَدَّ مِنكُمۡ قُوَّةࣰ وَأَكۡثَرَ أَمۡوَ ٰ⁠لࣰا وَأَوۡلَـٰدࣰا فَٱسۡتَمۡتَعُوا۟ بِخَلَـٰقِهِمۡ فَٱسۡتَمۡتَعۡتُم بِخَلَـٰقِكُمۡ كَمَا ٱسۡتَمۡتَعَ ٱلَّذِینَ مِن قَبۡلِكُم بِخَلَـٰقِهِمۡ وَخُضۡتُمۡ كَٱلَّذِی خَاضُوۤا۟ۚ أُو۟لَـٰۤىِٕكَ حَبِطَتۡ أَعۡمَـٰلُهُمۡ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4481, 9, 1305, 70, 'أَلَمۡ یَأۡتِهِمۡ نَبَأُ ٱلَّذِینَ مِن قَبۡلِهِمۡ قَوۡمِ نُوحࣲ وَعَادࣲ وَثَمُودَ وَقَوۡمِ إِبۡرَ ٰ⁠هِیمَ وَأَصۡحَـٰبِ مَدۡیَنَ وَٱلۡمُؤۡتَفِكَـٰتِۚ أَتَتۡهُمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِۖ فَمَا كَانَ ٱللَّهُ لِیَظۡلِمَهُمۡ وَلَـٰكِن كَانُوۤا۟ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4482, 9, 1306, 71, 'وَٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَـٰتُ بَعۡضُهُمۡ أَوۡلِیَاۤءُ بَعۡضࣲۚ یَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَیَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَیُقِیمُونَ ٱلصَّلَوٰةَ وَیُؤۡتُونَ ٱلزَّكَوٰةَ وَیُطِیعُونَ ٱللَّهَ وَرَسُولَهُۥۤۚ أُو۟لَـٰۤىِٕكَ سَیَرۡحَمُهُمُ ٱللَّهُۗ إِنَّ ٱللَّهَ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4483, 9, 1307, 72, 'وَعَدَ ٱللَّهُ ٱلۡمُؤۡمِنِینَ وَٱلۡمُؤۡمِنَـٰتِ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَا وَمَسَـٰكِنَ طَیِّبَةࣰ فِی جَنَّـٰتِ عَدۡنࣲۚ وَرِضۡوَ ٰ⁠نࣱ مِّنَ ٱللَّهِ أَكۡبَرُۚ ذَ ٰ⁠لِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4484, 9, 1308, 73, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ جَـٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَـٰفِقِینَ وَٱغۡلُظۡ عَلَیۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4485, 9, 1309, 74, 'یَحۡلِفُونَ بِٱللَّهِ مَا قَالُوا۟ وَلَقَدۡ قَالُوا۟ كَلِمَةَ ٱلۡكُفۡرِ وَكَفَرُوا۟ بَعۡدَ إِسۡلَـٰمِهِمۡ وَهَمُّوا۟ بِمَا لَمۡ یَنَالُوا۟ۚ وَمَا نَقَمُوۤا۟ إِلَّاۤ أَنۡ أَغۡنَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ مِن فَضۡلِهِۦۚ فَإِن یَتُوبُوا۟ یَكُ خَیۡرࣰا لَّهُمۡۖ وَإِن یَتَوَلَّوۡا۟ یُعَذِّبۡهُمُ ٱللَّهُ عَذَابًا أَلِیمࣰا فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِۚ وَمَا لَهُمۡ فِی ٱلۡأَرۡضِ مِن وَلِیࣲّ وَلَا نَصِیرࣲ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4486, 9, 1310, 75, '۞ وَمِنۡهُم مَّنۡ عَـٰهَدَ ٱللَّهَ لَىِٕنۡ ءَاتَىٰنَا مِن فَضۡلِهِۦ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4487, 9, 1311, 76, 'فَلَمَّاۤ ءَاتَىٰهُم مِّن فَضۡلِهِۦ بَخِلُوا۟ بِهِۦ وَتَوَلَّوا۟ وَّهُم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4488, 9, 1312, 77, 'فَأَعۡقَبَهُمۡ نِفَاقࣰا فِی قُلُوبِهِمۡ إِلَىٰ یَوۡمِ یَلۡقَوۡنَهُۥ بِمَاۤ أَخۡلَفُوا۟ ٱللَّهَ مَا وَعَدُوهُ وَبِمَا كَانُوا۟ یَكۡذِبُونَ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4489, 9, 1313, 78, 'أَلَمۡ یَعۡلَمُوۤا۟ أَنَّ ٱللَّهَ یَعۡلَمُ سِرَّهُمۡ وَنَجۡوَىٰهُمۡ وَأَنَّ ٱللَّهَ عَلَّـٰمُ ٱلۡغُیُوبِ\n', NULL, 0, '2023-05-26 17:32:02', '2023-05-27 11:18:11'),
(4490, 9, 1314, 79, 'ٱلَّذِینَ یَلۡمِزُونَ ٱلۡمُطَّوِّعِینَ مِنَ ٱلۡمُؤۡمِنِینَ فِی ٱلصَّدَقَـٰتِ وَٱلَّذِینَ لَا یَجِدُونَ إِلَّا جُهۡدَهُمۡ فَیَسۡخَرُونَ مِنۡهُمۡ سَخِرَ ٱللَّهُ مِنۡهُمۡ وَلَهُمۡ عَذَابٌ أَلِیمٌ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:11'),
(4491, 9, 1315, 80, 'ٱسۡتَغۡفِرۡ لَهُمۡ أَوۡ لَا تَسۡتَغۡفِرۡ لَهُمۡ إِن تَسۡتَغۡفِرۡ لَهُمۡ سَبۡعِینَ مَرَّةࣰ فَلَن یَغۡفِرَ ٱللَّهُ لَهُمۡۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ كَفَرُوا۟ بِٱللَّهِ وَرَسُولِهِۦۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:11'),
(4492, 9, 1316, 81, 'فَرِحَ ٱلۡمُخَلَّفُونَ بِمَقۡعَدِهِمۡ خِلَـٰفَ رَسُولِ ٱللَّهِ وَكَرِهُوۤا۟ أَن یُجَـٰهِدُوا۟ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡ فِی سَبِیلِ ٱللَّهِ وَقَالُوا۟ لَا تَنفِرُوا۟ فِی ٱلۡحَرِّۗ قُلۡ نَارُ جَهَنَّمَ أَشَدُّ حَرࣰّاۚ لَّوۡ كَانُوا۟ یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:11'),
(4493, 9, 1317, 82, 'فَلۡیَضۡحَكُوا۟ قَلِیلࣰا وَلۡیَبۡكُوا۟ كَثِیرࣰا جَزَاۤءَۢ بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:11'),
(4494, 9, 1318, 83, 'فَإِن رَّجَعَكَ ٱللَّهُ إِلَىٰ طَاۤىِٕفَةࣲ مِّنۡهُمۡ فَٱسۡتَـٔۡذَنُوكَ لِلۡخُرُوجِ فَقُل لَّن تَخۡرُجُوا۟ مَعِیَ أَبَدࣰا وَلَن تُقَـٰتِلُوا۟ مَعِیَ عَدُوًّاۖ إِنَّكُمۡ رَضِیتُم بِٱلۡقُعُودِ أَوَّلَ مَرَّةࣲ فَٱقۡعُدُوا۟ مَعَ ٱلۡخَـٰلِفِینَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:11'),
(4495, 9, 1319, 84, 'وَلَا تُصَلِّ عَلَىٰۤ أَحَدࣲ مِّنۡهُم مَّاتَ أَبَدࣰا وَلَا تَقُمۡ عَلَىٰ قَبۡرِهِۦۤۖ إِنَّهُمۡ كَفَرُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَمَاتُوا۟ وَهُمۡ فَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:12'),
(4496, 9, 1320, 85, 'وَلَا تُعۡجِبۡكَ أَمۡوَ ٰ⁠لُهُمۡ وَأَوۡلَـٰدُهُمۡۚ إِنَّمَا یُرِیدُ ٱللَّهُ أَن یُعَذِّبَهُم بِهَا فِی ٱلدُّنۡیَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:12'),
(4497, 9, 1321, 86, 'وَإِذَاۤ أُنزِلَتۡ سُورَةٌ أَنۡ ءَامِنُوا۟ بِٱللَّهِ وَجَـٰهِدُوا۟ مَعَ رَسُولِهِ ٱسۡتَـٔۡذَنَكَ أُو۟لُوا۟ ٱلطَّوۡلِ مِنۡهُمۡ وَقَالُوا۟ ذَرۡنَا نَكُن مَّعَ ٱلۡقَـٰعِدِینَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:12'),
(4498, 9, 1322, 87, 'رَضُوا۟ بِأَن یَكُونُوا۟ مَعَ ٱلۡخَوَالِفِ وَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:12'),
(4499, 9, 1323, 88, 'لَـٰكِنِ ٱلرَّسُولُ وَٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥ جَـٰهَدُوا۟ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡۚ وَأُو۟لَـٰۤىِٕكَ لَهُمُ ٱلۡخَیۡرَ ٰ⁠تُۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:32:03', '2023-05-27 11:18:12'),
(4500, 9, 1324, 89, 'أَعَدَّ ٱللَّهُ لَهُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ ذَ ٰ⁠لِكَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4501, 9, 1325, 90, 'وَجَاۤءَ ٱلۡمُعَذِّرُونَ مِنَ ٱلۡأَعۡرَابِ لِیُؤۡذَنَ لَهُمۡ وَقَعَدَ ٱلَّذِینَ كَذَبُوا۟ ٱللَّهَ وَرَسُولَهُۥۚ سَیُصِیبُ ٱلَّذِینَ كَفَرُوا۟ مِنۡهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4502, 9, 1326, 91, 'لَّیۡسَ عَلَى ٱلضُّعَفَاۤءِ وَلَا عَلَى ٱلۡمَرۡضَىٰ وَلَا عَلَى ٱلَّذِینَ لَا یَجِدُونَ مَا یُنفِقُونَ حَرَجٌ إِذَا نَصَحُوا۟ لِلَّهِ وَرَسُولِهِۦۚ مَا عَلَى ٱلۡمُحۡسِنِینَ مِن سَبِیلࣲۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4503, 9, 1327, 92, 'وَلَا عَلَى ٱلَّذِینَ إِذَا مَاۤ أَتَوۡكَ لِتَحۡمِلَهُمۡ قُلۡتَ لَاۤ أَجِدُ مَاۤ أَحۡمِلُكُمۡ عَلَیۡهِ تَوَلَّوا۟ وَّأَعۡیُنُهُمۡ تَفِیضُ مِنَ ٱلدَّمۡعِ حَزَنًا أَلَّا یَجِدُوا۟ مَا یُنفِقُونَ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4504, 9, 1328, 93, '۞ إِنَّمَا ٱلسَّبِیلُ عَلَى ٱلَّذِینَ یَسۡتَـٔۡذِنُونَكَ وَهُمۡ أَغۡنِیَاۤءُۚ رَضُوا۟ بِأَن یَكُونُوا۟ مَعَ ٱلۡخَوَالِفِ وَطَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4505, 9, 1329, 94, 'یَعۡتَذِرُونَ إِلَیۡكُمۡ إِذَا رَجَعۡتُمۡ إِلَیۡهِمۡۚ قُل لَّا تَعۡتَذِرُوا۟ لَن نُّؤۡمِنَ لَكُمۡ قَدۡ نَبَّأَنَا ٱللَّهُ مِنۡ أَخۡبَارِكُمۡۚ وَسَیَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ ثُمَّ تُرَدُّونَ إِلَىٰ عَـٰلِمِ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ فَیُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4506, 9, 1330, 95, 'سَیَحۡلِفُونَ بِٱللَّهِ لَكُمۡ إِذَا ٱنقَلَبۡتُمۡ إِلَیۡهِمۡ لِتُعۡرِضُوا۟ عَنۡهُمۡۖ فَأَعۡرِضُوا۟ عَنۡهُمۡۖ إِنَّهُمۡ رِجۡسࣱۖ وَمَأۡوَىٰهُمۡ جَهَنَّمُ جَزَاۤءَۢ بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4507, 9, 1331, 96, 'یَحۡلِفُونَ لَكُمۡ لِتَرۡضَوۡا۟ عَنۡهُمۡۖ فَإِن تَرۡضَوۡا۟ عَنۡهُمۡ فَإِنَّ ٱللَّهَ لَا یَرۡضَىٰ عَنِ ٱلۡقَوۡمِ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4508, 9, 1332, 97, 'ٱلۡأَعۡرَابُ أَشَدُّ كُفۡرࣰا وَنِفَاقࣰا وَأَجۡدَرُ أَلَّا یَعۡلَمُوا۟ حُدُودَ مَاۤ أَنزَلَ ٱللَّهُ عَلَىٰ رَسُولِهِۦۗ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:32:04', '2023-05-27 11:18:12'),
(4509, 9, 1333, 98, 'وَمِنَ ٱلۡأَعۡرَابِ مَن یَتَّخِذُ مَا یُنفِقُ مَغۡرَمࣰا وَیَتَرَبَّصُ بِكُمُ ٱلدَّوَاۤىِٕرَۚ عَلَیۡهِمۡ دَاۤىِٕرَةُ ٱلسَّوۡءِۗ وَٱللَّهُ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:12'),
(4510, 9, 1334, 99, 'وَمِنَ ٱلۡأَعۡرَابِ مَن یُؤۡمِنُ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَیَتَّخِذُ مَا یُنفِقُ قُرُبَـٰتٍ عِندَ ٱللَّهِ وَصَلَوَ ٰ⁠تِ ٱلرَّسُولِۚ أَلَاۤ إِنَّهَا قُرۡبَةࣱ لَّهُمۡۚ سَیُدۡخِلُهُمُ ٱللَّهُ فِی رَحۡمَتِهِۦۤۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:12'),
(4511, 9, 1335, 100, 'وَٱلسَّـٰبِقُونَ ٱلۡأَوَّلُونَ مِنَ ٱلۡمُهَـٰجِرِینَ وَٱلۡأَنصَارِ وَٱلَّذِینَ ٱتَّبَعُوهُم بِإِحۡسَـٰنࣲ رَّضِیَ ٱللَّهُ عَنۡهُمۡ وَرَضُوا۟ عَنۡهُ وَأَعَدَّ لَهُمۡ جَنَّـٰتࣲ تَجۡرِی تَحۡتَهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۚ ذَ ٰ⁠لِكَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4512, 9, 1336, 101, 'وَمِمَّنۡ حَوۡلَكُم مِّنَ ٱلۡأَعۡرَابِ مُنَـٰفِقُونَۖ وَمِنۡ أَهۡلِ ٱلۡمَدِینَةِ مَرَدُوا۟ عَلَى ٱلنِّفَاقِ لَا تَعۡلَمُهُمۡۖ نَحۡنُ نَعۡلَمُهُمۡۚ سَنُعَذِّبُهُم مَّرَّتَیۡنِ ثُمَّ یُرَدُّونَ إِلَىٰ عَذَابٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4513, 9, 1337, 102, 'وَءَاخَرُونَ ٱعۡتَرَفُوا۟ بِذُنُوبِهِمۡ خَلَطُوا۟ عَمَلࣰا صَـٰلِحࣰا وَءَاخَرَ سَیِّئًا عَسَى ٱللَّهُ أَن یَتُوبَ عَلَیۡهِمۡۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4514, 9, 1338, 103, 'خُذۡ مِنۡ أَمۡوَ ٰ⁠لِهِمۡ صَدَقَةࣰ تُطَهِّرُهُمۡ وَتُزَكِّیهِم بِهَا وَصَلِّ عَلَیۡهِمۡۖ إِنَّ صَلَوٰتَكَ سَكَنࣱ لَّهُمۡۗ وَٱللَّهُ سَمِیعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4515, 9, 1339, 104, 'أَلَمۡ یَعۡلَمُوۤا۟ أَنَّ ٱللَّهَ هُوَ یَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَیَأۡخُذُ ٱلصَّدَقَـٰتِ وَأَنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4516, 9, 1340, 105, 'وَقُلِ ٱعۡمَلُوا۟ فَسَیَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ وَٱلۡمُؤۡمِنُونَۖ وَسَتُرَدُّونَ إِلَىٰ عَـٰلِمِ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِ فَیُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4517, 9, 1341, 106, 'وَءَاخَرُونَ مُرۡجَوۡنَ لِأَمۡرِ ٱللَّهِ إِمَّا یُعَذِّبُهُمۡ وَإِمَّا یَتُوبُ عَلَیۡهِمۡۗ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4518, 9, 1342, 107, 'وَٱلَّذِینَ ٱتَّخَذُوا۟ مَسۡجِدࣰا ضِرَارࣰا وَكُفۡرࣰا وَتَفۡرِیقَۢا بَیۡنَ ٱلۡمُؤۡمِنِینَ وَإِرۡصَادࣰا لِّمَنۡ حَارَبَ ٱللَّهَ وَرَسُولَهُۥ مِن قَبۡلُۚ وَلَیَحۡلِفُنَّ إِنۡ أَرَدۡنَاۤ إِلَّا ٱلۡحُسۡنَىٰۖ وَٱللَّهُ یَشۡهَدُ إِنَّهُمۡ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:32:05', '2023-05-27 11:18:13'),
(4519, 9, 1343, 108, 'لَا تَقُمۡ فِیهِ أَبَدࣰاۚ لَّمَسۡجِدٌ أُسِّسَ عَلَى ٱلتَّقۡوَىٰ مِنۡ أَوَّلِ یَوۡمٍ أَحَقُّ أَن تَقُومَ فِیهِۚ فِیهِ رِجَالࣱ یُحِبُّونَ أَن یَتَطَهَّرُوا۟ۚ وَٱللَّهُ یُحِبُّ ٱلۡمُطَّهِّرِینَ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4520, 9, 1344, 109, 'أَفَمَنۡ أَسَّسَ بُنۡیَـٰنَهُۥ عَلَىٰ تَقۡوَىٰ مِنَ ٱللَّهِ وَرِضۡوَ ٰ⁠نٍ خَیۡرٌ أَم مَّنۡ أَسَّسَ بُنۡیَـٰنَهُۥ عَلَىٰ شَفَا جُرُفٍ هَارࣲ فَٱنۡهَارَ بِهِۦ فِی نَارِ جَهَنَّمَۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4521, 9, 1345, 110, 'لَا یَزَالُ بُنۡیَـٰنُهُمُ ٱلَّذِی بَنَوۡا۟ رِیبَةࣰ فِی قُلُوبِهِمۡ إِلَّاۤ أَن تَقَطَّعَ قُلُوبُهُمۡۗ وَٱللَّهُ عَلِیمٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4522, 9, 1346, 111, '۞ إِنَّ ٱللَّهَ ٱشۡتَرَىٰ مِنَ ٱلۡمُؤۡمِنِینَ أَنفُسَهُمۡ وَأَمۡوَ ٰ⁠لَهُم بِأَنَّ لَهُمُ ٱلۡجَنَّةَۚ یُقَـٰتِلُونَ فِی سَبِیلِ ٱللَّهِ فَیَقۡتُلُونَ وَیُقۡتَلُونَۖ وَعۡدًا عَلَیۡهِ حَقࣰّا فِی ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِیلِ وَٱلۡقُرۡءَانِۚ وَمَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ مِنَ ٱللَّهِۚ فَٱسۡتَبۡشِرُوا۟ بِبَیۡعِكُمُ ٱلَّذِی بَایَعۡتُم بِهِۦۚ وَذَ ٰ⁠لِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4523, 9, 1347, 112, 'ٱلتَّـٰۤىِٕبُونَ ٱلۡعَـٰبِدُونَ ٱلۡحَـٰمِدُونَ ٱلسَّـٰۤىِٕحُونَ ٱلرَّ ٰ⁠كِعُونَ ٱلسَّـٰجِدُونَ ٱلۡـَٔامِرُونَ بِٱلۡمَعۡرُوفِ وَٱلنَّاهُونَ عَنِ ٱلۡمُنكَرِ وَٱلۡحَـٰفِظُونَ لِحُدُودِ ٱللَّهِۗ وَبَشِّرِ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4524, 9, 1348, 113, 'مَا كَانَ لِلنَّبِیِّ وَٱلَّذِینَ ءَامَنُوۤا۟ أَن یَسۡتَغۡفِرُوا۟ لِلۡمُشۡرِكِینَ وَلَوۡ كَانُوۤا۟ أُو۟لِی قُرۡبَىٰ مِنۢ بَعۡدِ مَا تَبَیَّنَ لَهُمۡ أَنَّهُمۡ أَصۡحَـٰبُ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4525, 9, 1349, 114, 'وَمَا كَانَ ٱسۡتِغۡفَارُ إِبۡرَ ٰ⁠هِیمَ لِأَبِیهِ إِلَّا عَن مَّوۡعِدَةࣲ وَعَدَهَاۤ إِیَّاهُ فَلَمَّا تَبَیَّنَ لَهُۥۤ أَنَّهُۥ عَدُوࣱّ لِّلَّهِ تَبَرَّأَ مِنۡهُۚ إِنَّ إِبۡرَ ٰ⁠هِیمَ لَأَوَّ ٰ⁠هٌ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4526, 9, 1350, 115, 'وَمَا كَانَ ٱللَّهُ لِیُضِلَّ قَوۡمَۢا بَعۡدَ إِذۡ هَدَىٰهُمۡ حَتَّىٰ یُبَیِّنَ لَهُم مَّا یَتَّقُونَۚ إِنَّ ٱللَّهَ بِكُلِّ شَیۡءٍ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4527, 9, 1351, 116, 'إِنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ یُحۡیِۦ وَیُمِیتُۚ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِیࣲّ وَلَا نَصِیرࣲ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:13'),
(4528, 9, 1352, 117, 'لَّقَد تَّابَ ٱللَّهُ عَلَى ٱلنَّبِیِّ وَٱلۡمُهَـٰجِرِینَ وَٱلۡأَنصَارِ ٱلَّذِینَ ٱتَّبَعُوهُ فِی سَاعَةِ ٱلۡعُسۡرَةِ مِنۢ بَعۡدِ مَا كَادَ یَزِیغُ قُلُوبُ فَرِیقࣲ مِّنۡهُمۡ ثُمَّ تَابَ عَلَیۡهِمۡۚ إِنَّهُۥ بِهِمۡ رَءُوفࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:14'),
(4529, 9, 1353, 118, 'وَعَلَى ٱلثَّلَـٰثَةِ ٱلَّذِینَ خُلِّفُوا۟ حَتَّىٰۤ إِذَا ضَاقَتۡ عَلَیۡهِمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ وَضَاقَتۡ عَلَیۡهِمۡ أَنفُسُهُمۡ وَظَنُّوۤا۟ أَن لَّا مَلۡجَأَ مِنَ ٱللَّهِ إِلَّاۤ إِلَیۡهِ ثُمَّ تَابَ عَلَیۡهِمۡ لِیَتُوبُوۤا۟ۚ إِنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:32:06', '2023-05-27 11:18:14'),
(4530, 9, 1354, 119, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَكُونُوا۟ مَعَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4531, 9, 1355, 120, 'مَا كَانَ لِأَهۡلِ ٱلۡمَدِینَةِ وَمَنۡ حَوۡلَهُم مِّنَ ٱلۡأَعۡرَابِ أَن یَتَخَلَّفُوا۟ عَن رَّسُولِ ٱللَّهِ وَلَا یَرۡغَبُوا۟ بِأَنفُسِهِمۡ عَن نَّفۡسِهِۦۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ لَا یُصِیبُهُمۡ ظَمَأࣱ وَلَا نَصَبࣱ وَلَا مَخۡمَصَةࣱ فِی سَبِیلِ ٱللَّهِ وَلَا یَطَـُٔونَ مَوۡطِئࣰا یَغِیظُ ٱلۡكُفَّارَ وَلَا یَنَالُونَ مِنۡ عَدُوࣲّ نَّیۡلًا إِلَّا كُتِبَ لَهُم بِهِۦ عَمَلࣱ صَـٰلِحٌۚ إِنَّ ٱللَّهَ لَا یُضِیعُ أَجۡرَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4532, 9, 1356, 121, 'وَلَا یُنفِقُونَ نَفَقَةࣰ صَغِیرَةࣰ وَلَا كَبِیرَةࣰ وَلَا یَقۡطَعُونَ وَادِیًا إِلَّا كُتِبَ لَهُمۡ لِیَجۡزِیَهُمُ ٱللَّهُ أَحۡسَنَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4533, 9, 1357, 122, '۞ وَمَا كَانَ ٱلۡمُؤۡمِنُونَ لِیَنفِرُوا۟ كَاۤفَّةࣰۚ فَلَوۡلَا نَفَرَ مِن كُلِّ فِرۡقَةࣲ مِّنۡهُمۡ طَاۤىِٕفَةࣱ لِّیَتَفَقَّهُوا۟ فِی ٱلدِّینِ وَلِیُنذِرُوا۟ قَوۡمَهُمۡ إِذَا رَجَعُوۤا۟ إِلَیۡهِمۡ لَعَلَّهُمۡ یَحۡذَرُونَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4534, 9, 1358, 123, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ قَـٰتِلُوا۟ ٱلَّذِینَ یَلُونَكُم مِّنَ ٱلۡكُفَّارِ وَلۡیَجِدُوا۟ فِیكُمۡ غِلۡظَةࣰۚ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4535, 9, 1359, 124, 'وَإِذَا مَاۤ أُنزِلَتۡ سُورَةࣱ فَمِنۡهُم مَّن یَقُولُ أَیُّكُمۡ زَادَتۡهُ هَـٰذِهِۦۤ إِیمَـٰنࣰاۚ فَأَمَّا ٱلَّذِینَ ءَامَنُوا۟ فَزَادَتۡهُمۡ إِیمَـٰنࣰا وَهُمۡ یَسۡتَبۡشِرُونَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4536, 9, 1360, 125, 'وَأَمَّا ٱلَّذِینَ فِی قُلُوبِهِم مَّرَضࣱ فَزَادَتۡهُمۡ رِجۡسًا إِلَىٰ رِجۡسِهِمۡ وَمَاتُوا۟ وَهُمۡ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4537, 9, 1361, 126, 'أَوَلَا یَرَوۡنَ أَنَّهُمۡ یُفۡتَنُونَ فِی كُلِّ عَامࣲ مَّرَّةً أَوۡ مَرَّتَیۡنِ ثُمَّ لَا یَتُوبُونَ وَلَا هُمۡ یَذَّكَّرُونَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4538, 9, 1362, 127, 'وَإِذَا مَاۤ أُنزِلَتۡ سُورَةࣱ نَّظَرَ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ هَلۡ یَرَىٰكُم مِّنۡ أَحَدࣲ ثُمَّ ٱنصَرَفُوا۟ۚ صَرَفَ ٱللَّهُ قُلُوبَهُم بِأَنَّهُمۡ قَوۡمࣱ لَّا یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:32:07', '2023-05-27 11:18:14'),
(4539, 9, 1363, 128, 'لَقَدۡ جَاۤءَكُمۡ رَسُولࣱ مِّنۡ أَنفُسِكُمۡ عَزِیزٌ عَلَیۡهِ مَا عَنِتُّمۡ حَرِیصٌ عَلَیۡكُم بِٱلۡمُؤۡمِنِینَ رَءُوفࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:32:08', '2023-05-27 11:18:14'),
(4540, 9, 1364, 129, 'فَإِن تَوَلَّوۡا۟ فَقُلۡ حَسۡبِیَ ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ عَلَیۡهِ تَوَكَّلۡتُۖ وَهُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:32:08', '2023-05-27 11:18:14'),
(4541, 8, 1161, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَسۡـَٔلُونَكَ عَنِ ٱلۡأَنفَالِۖ قُلِ ٱلۡأَنفَالُ لِلَّهِ وَٱلرَّسُولِۖ فَٱتَّقُوا۟ ٱللَّهَ وَأَصۡلِحُوا۟ ذَاتَ بَیۡنِكُمۡۖ وَأَطِیعُوا۟ ٱللَّهَ وَرَسُولَهُۥۤ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:12', '2023-05-27 11:18:16'),
(4542, 8, 1162, 2, 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِینَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَإِذَا تُلِیَتۡ عَلَیۡهِمۡ ءَایَـٰتُهُۥ زَادَتۡهُمۡ إِیمَـٰنࣰا وَعَلَىٰ رَبِّهِمۡ یَتَوَكَّلُونَ\n', NULL, 0, '2023-05-26 17:32:12', '2023-05-27 11:18:16'),
(4543, 8, 1163, 3, 'ٱلَّذِینَ یُقِیمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَـٰهُمۡ یُنفِقُونَ\n', NULL, 0, '2023-05-26 17:32:12', '2023-05-27 11:18:16'),
(4544, 8, 1164, 4, 'أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقࣰّاۚ لَّهُمۡ دَرَجَـٰتٌ عِندَ رَبِّهِمۡ وَمَغۡفِرَةࣱ وَرِزۡقࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:32:12', '2023-05-27 11:18:16'),
(4545, 8, 1165, 5, 'كَمَاۤ أَخۡرَجَكَ رَبُّكَ مِنۢ بَیۡتِكَ بِٱلۡحَقِّ وَإِنَّ فَرِیقࣰا مِّنَ ٱلۡمُؤۡمِنِینَ لَكَـٰرِهُونَ\n', NULL, 0, '2023-05-26 17:32:12', '2023-05-27 11:18:16'),
(4546, 8, 1166, 6, 'یُجَـٰدِلُونَكَ فِی ٱلۡحَقِّ بَعۡدَ مَا تَبَیَّنَ كَأَنَّمَا یُسَاقُونَ إِلَى ٱلۡمَوۡتِ وَهُمۡ یَنظُرُونَ\n', NULL, 0, '2023-05-26 17:32:12', '2023-05-27 11:18:16'),
(4547, 8, 1167, 7, 'وَإِذۡ یَعِدُكُمُ ٱللَّهُ إِحۡدَى ٱلطَّاۤىِٕفَتَیۡنِ أَنَّهَا لَكُمۡ وَتَوَدُّونَ أَنَّ غَیۡرَ ذَاتِ ٱلشَّوۡكَةِ تَكُونُ لَكُمۡ وَیُرِیدُ ٱللَّهُ أَن یُحِقَّ ٱلۡحَقَّ بِكَلِمَـٰتِهِۦ وَیَقۡطَعَ دَابِرَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4548, 8, 1168, 8, 'لِیُحِقَّ ٱلۡحَقَّ وَیُبۡطِلَ ٱلۡبَـٰطِلَ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4549, 8, 1169, 9, 'إِذۡ تَسۡتَغِیثُونَ رَبَّكُمۡ فَٱسۡتَجَابَ لَكُمۡ أَنِّی مُمِدُّكُم بِأَلۡفࣲ مِّنَ ٱلۡمَلَـٰۤىِٕكَةِ مُرۡدِفِینَ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4550, 8, 1170, 10, 'وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ وَلِتَطۡمَىِٕنَّ بِهِۦ قُلُوبُكُمۡۚ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِیزٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4551, 8, 1171, 11, 'إِذۡ یُغَشِّیكُمُ ٱلنُّعَاسَ أَمَنَةࣰ مِّنۡهُ وَیُنَزِّلُ عَلَیۡكُم مِّنَ ٱلسَّمَاۤءِ مَاۤءࣰ لِّیُطَهِّرَكُم بِهِۦ وَیُذۡهِبَ عَنكُمۡ رِجۡزَ ٱلشَّیۡطَـٰنِ وَلِیَرۡبِطَ عَلَىٰ قُلُوبِكُمۡ وَیُثَبِّتَ بِهِ ٱلۡأَقۡدَامَ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4552, 8, 1172, 12, 'إِذۡ یُوحِی رَبُّكَ إِلَى ٱلۡمَلَـٰۤىِٕكَةِ أَنِّی مَعَكُمۡ فَثَبِّتُوا۟ ٱلَّذِینَ ءَامَنُوا۟ۚ سَأُلۡقِی فِی قُلُوبِ ٱلَّذِینَ كَفَرُوا۟ ٱلرُّعۡبَ فَٱضۡرِبُوا۟ فَوۡقَ ٱلۡأَعۡنَاقِ وَٱضۡرِبُوا۟ مِنۡهُمۡ كُلَّ بَنَانࣲ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4553, 8, 1173, 13, 'ذَ ٰ⁠لِكَ بِأَنَّهُمۡ شَاۤقُّوا۟ ٱللَّهَ وَرَسُولَهُۥۚ وَمَن یُشَاقِقِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4554, 8, 1174, 14, 'ذَ ٰ⁠لِكُمۡ فَذُوقُوهُ وَأَنَّ لِلۡكَـٰفِرِینَ عَذَابَ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4555, 8, 1175, 15, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا لَقِیتُمُ ٱلَّذِینَ كَفَرُوا۟ زَحۡفࣰا فَلَا تُوَلُّوهُمُ ٱلۡأَدۡبَارَ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:16'),
(4556, 8, 1176, 16, 'وَمَن یُوَلِّهِمۡ یَوۡمَىِٕذࣲ دُبُرَهُۥۤ إِلَّا مُتَحَرِّفࣰا لِّقِتَالٍ أَوۡ مُتَحَیِّزًا إِلَىٰ فِئَةࣲ فَقَدۡ بَاۤءَ بِغَضَبࣲ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:32:13', '2023-05-27 11:18:17'),
(4557, 8, 1177, 17, 'فَلَمۡ تَقۡتُلُوهُمۡ وَلَـٰكِنَّ ٱللَّهَ قَتَلَهُمۡۚ وَمَا رَمَیۡتَ إِذۡ رَمَیۡتَ وَلَـٰكِنَّ ٱللَّهَ رَمَىٰ وَلِیُبۡلِیَ ٱلۡمُؤۡمِنِینَ مِنۡهُ بَلَاۤءً حَسَنًاۚ إِنَّ ٱللَّهَ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17'),
(4558, 8, 1178, 18, 'ذَ ٰ⁠لِكُمۡ وَأَنَّ ٱللَّهَ مُوهِنُ كَیۡدِ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(4559, 8, 1179, 19, 'إِن تَسۡتَفۡتِحُوا۟ فَقَدۡ جَاۤءَكُمُ ٱلۡفَتۡحُۖ وَإِن تَنتَهُوا۟ فَهُوَ خَیۡرࣱ لَّكُمۡۖ وَإِن تَعُودُوا۟ نَعُدۡ وَلَن تُغۡنِیَ عَنكُمۡ فِئَتُكُمۡ شَیۡـࣰٔا وَلَوۡ كَثُرَتۡ وَأَنَّ ٱللَّهَ مَعَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17'),
(4560, 8, 1180, 20, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ أَطِیعُوا۟ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَوَلَّوۡا۟ عَنۡهُ وَأَنتُمۡ تَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17'),
(4561, 8, 1181, 21, 'وَلَا تَكُونُوا۟ كَٱلَّذِینَ قَالُوا۟ سَمِعۡنَا وَهُمۡ لَا یَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17'),
(4562, 8, 1182, 22, '۞ إِنَّ شَرَّ ٱلدَّوَاۤبِّ عِندَ ٱللَّهِ ٱلصُّمُّ ٱلۡبُكۡمُ ٱلَّذِینَ لَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17'),
(4563, 8, 1183, 23, 'وَلَوۡ عَلِمَ ٱللَّهُ فِیهِمۡ خَیۡرࣰا لَّأَسۡمَعَهُمۡۖ وَلَوۡ أَسۡمَعَهُمۡ لَتَوَلَّوا۟ وَّهُم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17'),
(4564, 8, 1184, 24, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱسۡتَجِیبُوا۟ لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمۡ لِمَا یُحۡیِیكُمۡۖ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ یَحُولُ بَیۡنَ ٱلۡمَرۡءِ وَقَلۡبِهِۦ وَأَنَّهُۥۤ إِلَیۡهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:32:14', '2023-05-27 11:18:17'),
(4565, 8, 1185, 25, 'وَٱتَّقُوا۟ فِتۡنَةࣰ لَّا تُصِیبَنَّ ٱلَّذِینَ ظَلَمُوا۟ مِنكُمۡ خَاۤصَّةࣰۖ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:17'),
(4566, 8, 1186, 26, 'وَٱذۡكُرُوۤا۟ إِذۡ أَنتُمۡ قَلِیلࣱ مُّسۡتَضۡعَفُونَ فِی ٱلۡأَرۡضِ تَخَافُونَ أَن یَتَخَطَّفَكُمُ ٱلنَّاسُ فَـَٔاوَىٰكُمۡ وَأَیَّدَكُم بِنَصۡرِهِۦ وَرَزَقَكُم مِّنَ ٱلطَّیِّبَـٰتِ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:17'),
(4567, 8, 1187, 27, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَخُونُوا۟ ٱللَّهَ وَٱلرَّسُولَ وَتَخُونُوۤا۟ أَمَـٰنَـٰتِكُمۡ وَأَنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:17'),
(4568, 8, 1188, 28, 'وَٱعۡلَمُوۤا۟ أَنَّمَاۤ أَمۡوَ ٰ⁠لُكُمۡ وَأَوۡلَـٰدُكُمۡ فِتۡنَةࣱ وَأَنَّ ٱللَّهَ عِندَهُۥۤ أَجۡرٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:17'),
(4569, 8, 1189, 29, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِن تَتَّقُوا۟ ٱللَّهَ یَجۡعَل لَّكُمۡ فُرۡقَانࣰا وَیُكَفِّرۡ عَنكُمۡ سَیِّـَٔاتِكُمۡ وَیَغۡفِرۡ لَكُمۡۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:17'),
(4570, 8, 1190, 30, 'وَإِذۡ یَمۡكُرُ بِكَ ٱلَّذِینَ كَفَرُوا۟ لِیُثۡبِتُوكَ أَوۡ یَقۡتُلُوكَ أَوۡ یُخۡرِجُوكَۚ وَیَمۡكُرُونَ وَیَمۡكُرُ ٱللَّهُۖ وَٱللَّهُ خَیۡرُ ٱلۡمَـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:17'),
(4571, 8, 1191, 31, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَـٰتُنَا قَالُوا۟ قَدۡ سَمِعۡنَا لَوۡ نَشَاۤءُ لَقُلۡنَا مِثۡلَ هَـٰذَاۤ إِنۡ هَـٰذَاۤ إِلَّاۤ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:17'),
(4572, 8, 1192, 32, 'وَإِذۡ قَالُوا۟ ٱللَّهُمَّ إِن كَانَ هَـٰذَا هُوَ ٱلۡحَقَّ مِنۡ عِندِكَ فَأَمۡطِرۡ عَلَیۡنَا حِجَارَةࣰ مِّنَ ٱلسَّمَاۤءِ أَوِ ٱئۡتِنَا بِعَذَابٍ أَلِیمࣲ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:18'),
(4573, 8, 1193, 33, 'وَمَا كَانَ ٱللَّهُ لِیُعَذِّبَهُمۡ وَأَنتَ فِیهِمۡۚ وَمَا كَانَ ٱللَّهُ مُعَذِّبَهُمۡ وَهُمۡ یَسۡتَغۡفِرُونَ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:18'),
(4574, 8, 1194, 34, 'وَمَا لَهُمۡ أَلَّا یُعَذِّبَهُمُ ٱللَّهُ وَهُمۡ یَصُدُّونَ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَمَا كَانُوۤا۟ أَوۡلِیَاۤءَهُۥۤۚ إِنۡ أَوۡلِیَاۤؤُهُۥۤ إِلَّا ٱلۡمُتَّقُونَ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:15', '2023-05-27 11:18:18'),
(4575, 8, 1195, 35, 'وَمَا كَانَ صَلَاتُهُمۡ عِندَ ٱلۡبَیۡتِ إِلَّا مُكَاۤءࣰ وَتَصۡدِیَةࣰۚ فَذُوقُوا۟ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4576, 8, 1196, 36, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ یُنفِقُونَ أَمۡوَ ٰ⁠لَهُمۡ لِیَصُدُّوا۟ عَن سَبِیلِ ٱللَّهِۚ فَسَیُنفِقُونَهَا ثُمَّ تَكُونُ عَلَیۡهِمۡ حَسۡرَةࣰ ثُمَّ یُغۡلَبُونَۗ وَٱلَّذِینَ كَفَرُوۤا۟ إِلَىٰ جَهَنَّمَ یُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4577, 8, 1197, 37, 'لِیَمِیزَ ٱللَّهُ ٱلۡخَبِیثَ مِنَ ٱلطَّیِّبِ وَیَجۡعَلَ ٱلۡخَبِیثَ بَعۡضَهُۥ عَلَىٰ بَعۡضࣲ فَیَرۡكُمَهُۥ جَمِیعࣰا فَیَجۡعَلَهُۥ فِی جَهَنَّمَۚ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4578, 8, 1198, 38, 'قُل لِّلَّذِینَ كَفَرُوۤا۟ إِن یَنتَهُوا۟ یُغۡفَرۡ لَهُم مَّا قَدۡ سَلَفَ وَإِن یَعُودُوا۟ فَقَدۡ مَضَتۡ سُنَّتُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4579, 8, 1199, 39, 'وَقَـٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةࣱ وَیَكُونَ ٱلدِّینُ كُلُّهُۥ لِلَّهِۚ فَإِنِ ٱنتَهَوۡا۟ فَإِنَّ ٱللَّهَ بِمَا یَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4580, 8, 1200, 40, 'وَإِن تَوَلَّوۡا۟ فَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ مَوۡلَىٰكُمۡۚ نِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِیرُ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4581, 8, 1201, 41, '۞ وَٱعۡلَمُوۤا۟ أَنَّمَا غَنِمۡتُم مِّن شَیۡءࣲ فَأَنَّ لِلَّهِ خُمُسَهُۥ وَلِلرَّسُولِ وَلِذِی ٱلۡقُرۡبَىٰ وَٱلۡیَتَـٰمَىٰ وَٱلۡمَسَـٰكِینِ وَٱبۡنِ ٱلسَّبِیلِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ وَمَاۤ أَنزَلۡنَا عَلَىٰ عَبۡدِنَا یَوۡمَ ٱلۡفُرۡقَانِ یَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4582, 8, 1202, 42, 'إِذۡ أَنتُم بِٱلۡعُدۡوَةِ ٱلدُّنۡیَا وَهُم بِٱلۡعُدۡوَةِ ٱلۡقُصۡوَىٰ وَٱلرَّكۡبُ أَسۡفَلَ مِنكُمۡۚ وَلَوۡ تَوَاعَدتُّمۡ لَٱخۡتَلَفۡتُمۡ فِی ٱلۡمِیعَـٰدِ وَلَـٰكِن لِّیَقۡضِیَ ٱللَّهُ أَمۡرࣰا كَانَ مَفۡعُولࣰا لِّیَهۡلِكَ مَنۡ هَلَكَ عَنۢ بَیِّنَةࣲ وَیَحۡیَىٰ مَنۡ حَیَّ عَنۢ بَیِّنَةࣲۗ وَإِنَّ ٱللَّهَ لَسَمِیعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4583, 8, 1203, 43, 'إِذۡ یُرِیكَهُمُ ٱللَّهُ فِی مَنَامِكَ قَلِیلࣰاۖ وَلَوۡ أَرَىٰكَهُمۡ كَثِیرࣰا لَّفَشِلۡتُمۡ وَلَتَنَـٰزَعۡتُمۡ فِی ٱلۡأَمۡرِ وَلَـٰكِنَّ ٱللَّهَ سَلَّمَۚ إِنَّهُۥ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4584, 8, 1204, 44, 'وَإِذۡ یُرِیكُمُوهُمۡ إِذِ ٱلۡتَقَیۡتُمۡ فِیۤ أَعۡیُنِكُمۡ قَلِیلࣰا وَیُقَلِّلُكُمۡ فِیۤ أَعۡیُنِهِمۡ لِیَقۡضِیَ ٱللَّهُ أَمۡرࣰا كَانَ مَفۡعُولࣰاۗ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n', NULL, 0, '2023-05-26 17:32:16', '2023-05-27 11:18:18'),
(4585, 8, 1205, 45, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا لَقِیتُمۡ فِئَةࣰ فَٱثۡبُتُوا۟ وَٱذۡكُرُوا۟ ٱللَّهَ كَثِیرࣰا لَّعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:18'),
(4586, 8, 1206, 46, 'وَأَطِیعُوا۟ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَنَـٰزَعُوا۟ فَتَفۡشَلُوا۟ وَتَذۡهَبَ رِیحُكُمۡۖ وَٱصۡبِرُوۤا۟ۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:18'),
(4587, 8, 1207, 47, 'وَلَا تَكُونُوا۟ كَٱلَّذِینَ خَرَجُوا۟ مِن دِیَـٰرِهِم بَطَرࣰا وَرِئَاۤءَ ٱلنَّاسِ وَیَصُدُّونَ عَن سَبِیلِ ٱللَّهِۚ وَٱللَّهُ بِمَا یَعۡمَلُونَ مُحِیطࣱ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:18'),
(4588, 8, 1208, 48, 'وَإِذۡ زَیَّنَ لَهُمُ ٱلشَّیۡطَـٰنُ أَعۡمَـٰلَهُمۡ وَقَالَ لَا غَالِبَ لَكُمُ ٱلۡیَوۡمَ مِنَ ٱلنَّاسِ وَإِنِّی جَارࣱ لَّكُمۡۖ فَلَمَّا تَرَاۤءَتِ ٱلۡفِئَتَانِ نَكَصَ عَلَىٰ عَقِبَیۡهِ وَقَالَ إِنِّی بَرِیۤءࣱ مِّنكُمۡ إِنِّیۤ أَرَىٰ مَا لَا تَرَوۡنَ إِنِّیۤ أَخَافُ ٱللَّهَۚ وَٱللَّهُ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:19'),
(4589, 8, 1209, 49, 'إِذۡ یَقُولُ ٱلۡمُنَـٰفِقُونَ وَٱلَّذِینَ فِی قُلُوبِهِم مَّرَضٌ غَرَّ هَـٰۤؤُلَاۤءِ دِینُهُمۡۗ وَمَن یَتَوَكَّلۡ عَلَى ٱللَّهِ فَإِنَّ ٱللَّهَ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:19'),
(4590, 8, 1210, 50, 'وَلَوۡ تَرَىٰۤ إِذۡ یَتَوَفَّى ٱلَّذِینَ كَفَرُوا۟ ٱلۡمَلَـٰۤىِٕكَةُ یَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَـٰرَهُمۡ وَذُوقُوا۟ عَذَابَ ٱلۡحَرِیقِ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:19'),
(4591, 8, 1211, 51, 'ذَ ٰ⁠لِكَ بِمَا قَدَّمَتۡ أَیۡدِیكُمۡ وَأَنَّ ٱللَّهَ لَیۡسَ بِظَلَّـٰمࣲ لِّلۡعَبِیدِ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:19'),
(4592, 8, 1212, 52, 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِینَ مِن قَبۡلِهِمۡۚ كَفَرُوا۟ بِـَٔایَـٰتِ ٱللَّهِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۚ إِنَّ ٱللَّهَ قَوِیࣱّ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:19'),
(4593, 8, 1213, 53, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱللَّهَ لَمۡ یَكُ مُغَیِّرࣰا نِّعۡمَةً أَنۡعَمَهَا عَلَىٰ قَوۡمٍ حَتَّىٰ یُغَیِّرُوا۟ مَا بِأَنفُسِهِمۡ وَأَنَّ ٱللَّهَ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:17', '2023-05-27 11:18:19'),
(4594, 8, 1214, 54, 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِینَ مِن قَبۡلِهِمۡۚ كَذَّبُوا۟ بِـَٔایَـٰتِ رَبِّهِمۡ فَأَهۡلَكۡنَـٰهُم بِذُنُوبِهِمۡ وَأَغۡرَقۡنَاۤ ءَالَ فِرۡعَوۡنَۚ وَكُلࣱّ كَانُوا۟ ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4595, 8, 1215, 55, 'إِنَّ شَرَّ ٱلدَّوَاۤبِّ عِندَ ٱللَّهِ ٱلَّذِینَ كَفَرُوا۟ فَهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4596, 8, 1216, 56, 'ٱلَّذِینَ عَـٰهَدتَّ مِنۡهُمۡ ثُمَّ یَنقُضُونَ عَهۡدَهُمۡ فِی كُلِّ مَرَّةࣲ وَهُمۡ لَا یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4597, 8, 1217, 57, 'فَإِمَّا تَثۡقَفَنَّهُمۡ فِی ٱلۡحَرۡبِ فَشَرِّدۡ بِهِم مَّنۡ خَلۡفَهُمۡ لَعَلَّهُمۡ یَذَّكَّرُونَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4598, 8, 1218, 58, 'وَإِمَّا تَخَافَنَّ مِن قَوۡمٍ خِیَانَةࣰ فَٱنۢبِذۡ إِلَیۡهِمۡ عَلَىٰ سَوَاۤءٍۚ إِنَّ ٱللَّهَ لَا یُحِبُّ ٱلۡخَاۤىِٕنِینَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4599, 8, 1219, 59, 'وَلَا یَحۡسَبَنَّ ٱلَّذِینَ كَفَرُوا۟ سَبَقُوۤا۟ۚ إِنَّهُمۡ لَا یُعۡجِزُونَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4600, 8, 1220, 60, 'وَأَعِدُّوا۟ لَهُم مَّا ٱسۡتَطَعۡتُم مِّن قُوَّةࣲ وَمِن رِّبَاطِ ٱلۡخَیۡلِ تُرۡهِبُونَ بِهِۦ عَدُوَّ ٱللَّهِ وَعَدُوَّكُمۡ وَءَاخَرِینَ مِن دُونِهِمۡ لَا تَعۡلَمُونَهُمُ ٱللَّهُ یَعۡلَمُهُمۡۚ وَمَا تُنفِقُوا۟ مِن شَیۡءࣲ فِی سَبِیلِ ٱللَّهِ یُوَفَّ إِلَیۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4601, 8, 1221, 61, '۞ وَإِن جَنَحُوا۟ لِلسَّلۡمِ فَٱجۡنَحۡ لَهَا وَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّهُۥ هُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4602, 8, 1222, 62, 'وَإِن یُرِیدُوۤا۟ أَن یَخۡدَعُوكَ فَإِنَّ حَسۡبَكَ ٱللَّهُۚ هُوَ ٱلَّذِیۤ أَیَّدَكَ بِنَصۡرِهِۦ وَبِٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4603, 8, 1223, 63, 'وَأَلَّفَ بَیۡنَ قُلُوبِهِمۡۚ لَوۡ أَنفَقۡتَ مَا فِی ٱلۡأَرۡضِ جَمِیعࣰا مَّاۤ أَلَّفۡتَ بَیۡنَ قُلُوبِهِمۡ وَلَـٰكِنَّ ٱللَّهَ أَلَّفَ بَیۡنَهُمۡۚ إِنَّهُۥ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4604, 8, 1224, 64, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ حَسۡبُكَ ٱللَّهُ وَمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:18', '2023-05-27 11:18:19'),
(4605, 8, 1225, 65, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ حَرِّضِ ٱلۡمُؤۡمِنِینَ عَلَى ٱلۡقِتَالِۚ إِن یَكُن مِّنكُمۡ عِشۡرُونَ صَـٰبِرُونَ یَغۡلِبُوا۟ مِا۟ئَتَیۡنِۚ وَإِن یَكُن مِّنكُم مِّا۟ئَةࣱ یَغۡلِبُوۤا۟ أَلۡفࣰا مِّنَ ٱلَّذِینَ كَفَرُوا۟ بِأَنَّهُمۡ قَوۡمࣱ لَّا یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4606, 8, 1226, 66, 'ٱلۡـَٔـٰنَ خَفَّفَ ٱللَّهُ عَنكُمۡ وَعَلِمَ أَنَّ فِیكُمۡ ضَعۡفࣰاۚ فَإِن یَكُن مِّنكُم مِّا۟ئَةࣱ صَابِرَةࣱ یَغۡلِبُوا۟ مِا۟ئَتَیۡنِۚ وَإِن یَكُن مِّنكُمۡ أَلۡفࣱ یَغۡلِبُوۤا۟ أَلۡفَیۡنِ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4607, 8, 1227, 67, 'مَا كَانَ لِنَبِیٍّ أَن یَكُونَ لَهُۥۤ أَسۡرَىٰ حَتَّىٰ یُثۡخِنَ فِی ٱلۡأَرۡضِۚ تُرِیدُونَ عَرَضَ ٱلدُّنۡیَا وَٱللَّهُ یُرِیدُ ٱلۡـَٔاخِرَةَۗ وَٱللَّهُ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4608, 8, 1228, 68, 'لَّوۡلَا كِتَـٰبࣱ مِّنَ ٱللَّهِ سَبَقَ لَمَسَّكُمۡ فِیمَاۤ أَخَذۡتُمۡ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4609, 8, 1229, 69, 'فَكُلُوا۟ مِمَّا غَنِمۡتُمۡ حَلَـٰلࣰا طَیِّبࣰاۚ وَٱتَّقُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4610, 8, 1230, 70, 'یَـٰۤأَیُّهَا ٱلنَّبِیُّ قُل لِّمَن فِیۤ أَیۡدِیكُم مِّنَ ٱلۡأَسۡرَىٰۤ إِن یَعۡلَمِ ٱللَّهُ فِی قُلُوبِكُمۡ خَیۡرࣰا یُؤۡتِكُمۡ خَیۡرࣰا مِّمَّاۤ أُخِذَ مِنكُمۡ وَیَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4611, 8, 1231, 71, 'وَإِن یُرِیدُوا۟ خِیَانَتَكَ فَقَدۡ خَانُوا۟ ٱللَّهَ مِن قَبۡلُ فَأَمۡكَنَ مِنۡهُمۡۗ وَٱللَّهُ عَلِیمٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4612, 8, 1232, 72, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَهَاجَرُوا۟ وَجَـٰهَدُوا۟ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡ فِی سَبِیلِ ٱللَّهِ وَٱلَّذِینَ ءَاوَوا۟ وَّنَصَرُوۤا۟ أُو۟لَـٰۤىِٕكَ بَعۡضُهُمۡ أَوۡلِیَاۤءُ بَعۡضࣲۚ وَٱلَّذِینَ ءَامَنُوا۟ وَلَمۡ یُهَاجِرُوا۟ مَا لَكُم مِّن وَلَـٰیَتِهِم مِّن شَیۡءٍ حَتَّىٰ یُهَاجِرُوا۟ۚ وَإِنِ ٱسۡتَنصَرُوكُمۡ فِی ٱلدِّینِ فَعَلَیۡكُمُ ٱلنَّصۡرُ إِلَّا عَلَىٰ قَوۡمِۭ بَیۡنَكُمۡ وَبَیۡنَهُم مِّیثَـٰقࣱۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4613, 8, 1233, 73, 'وَٱلَّذِینَ كَفَرُوا۟ بَعۡضُهُمۡ أَوۡلِیَاۤءُ بَعۡضٍۚ إِلَّا تَفۡعَلُوهُ تَكُن فِتۡنَةࣱ فِی ٱلۡأَرۡضِ وَفَسَادࣱ كَبِیرࣱ\n', NULL, 0, '2023-05-26 17:32:19', '2023-05-27 11:18:20'),
(4614, 8, 1234, 74, 'وَٱلَّذِینَ ءَامَنُوا۟ وَهَاجَرُوا۟ وَجَـٰهَدُوا۟ فِی سَبِیلِ ٱللَّهِ وَٱلَّذِینَ ءَاوَوا۟ وَّنَصَرُوۤا۟ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقࣰّاۚ لَّهُم مَّغۡفِرَةࣱ وَرِزۡقࣱ كَرِیمࣱ\n', NULL, 0, '2023-05-26 17:32:20', '2023-05-27 11:18:20'),
(4615, 8, 1235, 75, 'وَٱلَّذِینَ ءَامَنُوا۟ مِنۢ بَعۡدُ وَهَاجَرُوا۟ وَجَـٰهَدُوا۟ مَعَكُمۡ فَأُو۟لَـٰۤىِٕكَ مِنكُمۡۚ وَأُو۟لُوا۟ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضࣲ فِی كِتَـٰبِ ٱللَّهِۚ إِنَّ ٱللَّهَ بِكُلِّ شَیۡءٍ عَلِیمُۢ\n', NULL, 0, '2023-05-26 17:32:20', '2023-05-27 11:18:20'),
(4616, 7, 955, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤصۤ\n', NULL, 0, '2023-05-26 17:32:43', '2023-05-27 11:18:22'),
(4617, 7, 956, 2, 'كِتَـٰبٌ أُنزِلَ إِلَیۡكَ فَلَا یَكُن فِی صَدۡرِكَ حَرَجࣱ مِّنۡهُ لِتُنذِرَ بِهِۦ وَذِكۡرَىٰ لِلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:43', '2023-05-27 11:18:22'),
(4618, 7, 957, 3, 'ٱتَّبِعُوا۟ مَاۤ أُنزِلَ إِلَیۡكُم مِّن رَّبِّكُمۡ وَلَا تَتَّبِعُوا۟ مِن دُونِهِۦۤ أَوۡلِیَاۤءَۗ قَلِیلࣰا مَّا تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:32:43', '2023-05-27 11:18:23'),
(4619, 7, 958, 4, 'وَكَم مِّن قَرۡیَةٍ أَهۡلَكۡنَـٰهَا فَجَاۤءَهَا بَأۡسُنَا بَیَـٰتًا أَوۡ هُمۡ قَاۤىِٕلُونَ\n', NULL, 0, '2023-05-26 17:32:43', '2023-05-27 11:18:23'),
(4620, 7, 959, 5, 'فَمَا كَانَ دَعۡوَىٰهُمۡ إِذۡ جَاۤءَهُم بَأۡسُنَاۤ إِلَّاۤ أَن قَالُوۤا۟ إِنَّا كُنَّا ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:43', '2023-05-27 11:18:23'),
(4621, 7, 960, 6, 'فَلَنَسۡـَٔلَنَّ ٱلَّذِینَ أُرۡسِلَ إِلَیۡهِمۡ وَلَنَسۡـَٔلَنَّ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:32:43', '2023-05-27 11:18:23'),
(4622, 7, 961, 7, 'فَلَنَقُصَّنَّ عَلَیۡهِم بِعِلۡمࣲۖ وَمَا كُنَّا غَاۤىِٕبِینَ\n', NULL, 0, '2023-05-26 17:32:43', '2023-05-27 11:18:23'),
(4623, 7, 962, 8, 'وَٱلۡوَزۡنُ یَوۡمَىِٕذٍ ٱلۡحَقُّۚ فَمَن ثَقُلَتۡ مَوَ ٰ⁠زِینُهُۥ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4624, 7, 963, 9, 'وَمَنۡ خَفَّتۡ مَوَ ٰ⁠زِینُهُۥ فَأُو۟لَـٰۤىِٕكَ ٱلَّذِینَ خَسِرُوۤا۟ أَنفُسَهُم بِمَا كَانُوا۟ بِـَٔایَـٰتِنَا یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4625, 7, 964, 10, 'وَلَقَدۡ مَكَّنَّـٰكُمۡ فِی ٱلۡأَرۡضِ وَجَعَلۡنَا لَكُمۡ فِیهَا مَعَـٰیِشَۗ قَلِیلࣰا مَّا تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4626, 7, 965, 11, 'وَلَقَدۡ خَلَقۡنَـٰكُمۡ ثُمَّ صَوَّرۡنَـٰكُمۡ ثُمَّ قُلۡنَا لِلۡمَلَـٰۤىِٕكَةِ ٱسۡجُدُوا۟ لِـَٔادَمَ فَسَجَدُوۤا۟ إِلَّاۤ إِبۡلِیسَ لَمۡ یَكُن مِّنَ ٱلسَّـٰجِدِینَ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4627, 7, 966, 12, 'قَالَ مَا مَنَعَكَ أَلَّا تَسۡجُدَ إِذۡ أَمَرۡتُكَۖ قَالَ أَنَا۠ خَیۡرࣱ مِّنۡهُ خَلَقۡتَنِی مِن نَّارࣲ وَخَلَقۡتَهُۥ مِن طِینࣲ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4628, 7, 967, 13, 'قَالَ فَٱهۡبِطۡ مِنۡهَا فَمَا یَكُونُ لَكَ أَن تَتَكَبَّرَ فِیهَا فَٱخۡرُجۡ إِنَّكَ مِنَ ٱلصَّـٰغِرِینَ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4629, 7, 968, 14, 'قَالَ أَنظِرۡنِیۤ إِلَىٰ یَوۡمِ یُبۡعَثُونَ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4630, 7, 969, 15, 'قَالَ إِنَّكَ مِنَ ٱلۡمُنظَرِینَ\n', NULL, 0, '2023-05-26 17:32:44', '2023-05-27 11:18:23'),
(4631, 7, 970, 16, 'قَالَ فَبِمَاۤ أَغۡوَیۡتَنِی لَأَقۡعُدَنَّ لَهُمۡ صِرَ ٰ⁠طَكَ ٱلۡمُسۡتَقِیمَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:23'),
(4632, 7, 971, 17, 'ثُمَّ لَـَٔاتِیَنَّهُم مِّنۢ بَیۡنِ أَیۡدِیهِمۡ وَمِنۡ خَلۡفِهِمۡ وَعَنۡ أَیۡمَـٰنِهِمۡ وَعَن شَمَاۤىِٕلِهِمۡۖ وَلَا تَجِدُ أَكۡثَرَهُمۡ شَـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:23'),
(4633, 7, 972, 18, 'قَالَ ٱخۡرُجۡ مِنۡهَا مَذۡءُومࣰا مَّدۡحُورࣰاۖ لَّمَن تَبِعَكَ مِنۡهُمۡ لَأَمۡلَأَنَّ جَهَنَّمَ مِنكُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:23'),
(4634, 7, 973, 19, 'وَیَـٰۤـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ فَكُلَا مِنۡ حَیۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَـٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:24'),
(4635, 7, 974, 20, 'فَوَسۡوَسَ لَهُمَا ٱلشَّیۡطَـٰنُ لِیُبۡدِیَ لَهُمَا مَا وُۥرِیَ عَنۡهُمَا مِن سَوۡءَ ٰ⁠ تِهِمَا وَقَالَ مَا نَهَىٰكُمَا رَبُّكُمَا عَنۡ هَـٰذِهِ ٱلشَّجَرَةِ إِلَّاۤ أَن تَكُونَا مَلَكَیۡنِ أَوۡ تَكُونَا مِنَ ٱلۡخَـٰلِدِینَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:24'),
(4636, 7, 975, 21, 'وَقَاسَمَهُمَاۤ إِنِّی لَكُمَا لَمِنَ ٱلنَّـٰصِحِینَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:24'),
(4637, 7, 976, 22, 'فَدَلَّىٰهُمَا بِغُرُورࣲۚ فَلَمَّا ذَاقَا ٱلشَّجَرَةَ بَدَتۡ لَهُمَا سَوۡءَ ٰ⁠ تُهُمَا وَطَفِقَا یَخۡصِفَانِ عَلَیۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۖ وَنَادَىٰهُمَا رَبُّهُمَاۤ أَلَمۡ أَنۡهَكُمَا عَن تِلۡكُمَا ٱلشَّجَرَةِ وَأَقُل لَّكُمَاۤ إِنَّ ٱلشَّیۡطَـٰنَ لَكُمَا عَدُوࣱّ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:24'),
(4638, 7, 977, 23, 'قَالَا رَبَّنَا ظَلَمۡنَاۤ أَنفُسَنَا وَإِن لَّمۡ تَغۡفِرۡ لَنَا وَتَرۡحَمۡنَا لَنَكُونَنَّ مِنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:24'),
(4639, 7, 978, 24, 'قَالَ ٱهۡبِطُوا۟ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوࣱّۖ وَلَكُمۡ فِی ٱلۡأَرۡضِ مُسۡتَقَرࣱّ وَمَتَـٰعٌ إِلَىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:24'),
(4640, 7, 979, 25, 'قَالَ فِیهَا تَحۡیَوۡنَ وَفِیهَا تَمُوتُونَ وَمِنۡهَا تُخۡرَجُونَ\n', NULL, 0, '2023-05-26 17:32:45', '2023-05-27 11:18:24'),
(4641, 7, 980, 26, 'یَـٰبَنِیۤ ءَادَمَ قَدۡ أَنزَلۡنَا عَلَیۡكُمۡ لِبَاسࣰا یُوَ ٰ⁠رِی سَوۡءَ ٰ⁠ تِكُمۡ وَرِیشࣰاۖ وَلِبَاسُ ٱلتَّقۡوَىٰ ذَ ٰ⁠لِكَ خَیۡرࣱۚ ذَ ٰ⁠لِكَ مِنۡ ءَایَـٰتِ ٱللَّهِ لَعَلَّهُمۡ یَذَّكَّرُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4642, 7, 981, 27, 'یَـٰبَنِیۤ ءَادَمَ لَا یَفۡتِنَنَّكُمُ ٱلشَّیۡطَـٰنُ كَمَاۤ أَخۡرَجَ أَبَوَیۡكُم مِّنَ ٱلۡجَنَّةِ یَنزِعُ عَنۡهُمَا لِبَاسَهُمَا لِیُرِیَهُمَا سَوۡءَ ٰ⁠ تِهِمَاۤۚ إِنَّهُۥ یَرَىٰكُمۡ هُوَ وَقَبِیلُهُۥ مِنۡ حَیۡثُ لَا تَرَوۡنَهُمۡۗ إِنَّا جَعَلۡنَا ٱلشَّیَـٰطِینَ أَوۡلِیَاۤءَ لِلَّذِینَ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4643, 7, 982, 28, 'وَإِذَا فَعَلُوا۟ فَـٰحِشَةࣰ قَالُوا۟ وَجَدۡنَا عَلَیۡهَاۤ ءَابَاۤءَنَا وَٱللَّهُ أَمَرَنَا بِهَاۗ قُلۡ إِنَّ ٱللَّهَ لَا یَأۡمُرُ بِٱلۡفَحۡشَاۤءِۖ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4644, 7, 983, 29, 'قُلۡ أَمَرَ رَبِّی بِٱلۡقِسۡطِۖ وَأَقِیمُوا۟ وُجُوهَكُمۡ عِندَ كُلِّ مَسۡجِدࣲ وَٱدۡعُوهُ مُخۡلِصِینَ لَهُ ٱلدِّینَۚ كَمَا بَدَأَكُمۡ تَعُودُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4645, 7, 984, 30, 'فَرِیقًا هَدَىٰ وَفَرِیقًا حَقَّ عَلَیۡهِمُ ٱلضَّلَـٰلَةُۚ إِنَّهُمُ ٱتَّخَذُوا۟ ٱلشَّیَـٰطِینَ أَوۡلِیَاۤءَ مِن دُونِ ٱللَّهِ وَیَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4646, 7, 985, 31, '۞ یَـٰبَنِیۤ ءَادَمَ خُذُوا۟ زِینَتَكُمۡ عِندَ كُلِّ مَسۡجِدࣲ وَكُلُوا۟ وَٱشۡرَبُوا۟ وَلَا تُسۡرِفُوۤا۟ۚ إِنَّهُۥ لَا یُحِبُّ ٱلۡمُسۡرِفِینَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4647, 7, 986, 32, 'قُلۡ مَنۡ حَرَّمَ زِینَةَ ٱللَّهِ ٱلَّتِیۤ أَخۡرَجَ لِعِبَادِهِۦ وَٱلطَّیِّبَـٰتِ مِنَ ٱلرِّزۡقِۚ قُلۡ هِیَ لِلَّذِینَ ءَامَنُوا۟ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا خَالِصَةࣰ یَوۡمَ ٱلۡقِیَـٰمَةِۗ كَذَ ٰ⁠لِكَ نُفَصِّلُ ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4648, 7, 987, 33, 'قُلۡ إِنَّمَا حَرَّمَ رَبِّیَ ٱلۡفَوَ ٰ⁠حِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَ وَٱلۡإِثۡمَ وَٱلۡبَغۡیَ بِغَیۡرِ ٱلۡحَقِّ وَأَن تُشۡرِكُوا۟ بِٱللَّهِ مَا لَمۡ یُنَزِّلۡ بِهِۦ سُلۡطَـٰنࣰا وَأَن تَقُولُوا۟ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4649, 7, 988, 34, 'وَلِكُلِّ أُمَّةٍ أَجَلࣱۖ فَإِذَا جَاۤءَ أَجَلُهُمۡ لَا یَسۡتَأۡخِرُونَ سَاعَةࣰ وَلَا یَسۡتَقۡدِمُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4650, 7, 989, 35, 'یَـٰبَنِیۤ ءَادَمَ إِمَّا یَأۡتِیَنَّكُمۡ رُسُلࣱ مِّنكُمۡ یَقُصُّونَ عَلَیۡكُمۡ ءَایَـٰتِی فَمَنِ ٱتَّقَىٰ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:24'),
(4651, 7, 990, 36, 'وَٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا وَٱسۡتَكۡبَرُوا۟ عَنۡهَاۤ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:25'),
(4652, 7, 991, 37, 'فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔایَـٰتِهِۦۤۚ أُو۟لَـٰۤىِٕكَ یَنَالُهُمۡ نَصِیبُهُم مِّنَ ٱلۡكِتَـٰبِۖ حَتَّىٰۤ إِذَا جَاۤءَتۡهُمۡ رُسُلُنَا یَتَوَفَّوۡنَهُمۡ قَالُوۤا۟ أَیۡنَ مَا كُنتُمۡ تَدۡعُونَ مِن دُونِ ٱللَّهِۖ قَالُوا۟ ضَلُّوا۟ عَنَّا وَشَهِدُوا۟ عَلَىٰۤ أَنفُسِهِمۡ أَنَّهُمۡ كَانُوا۟ كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:46', '2023-05-27 11:18:25'),
(4653, 7, 992, 38, 'قَالَ ٱدۡخُلُوا۟ فِیۤ أُمَمࣲ قَدۡ خَلَتۡ مِن قَبۡلِكُم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِ فِی ٱلنَّارِۖ كُلَّمَا دَخَلَتۡ أُمَّةࣱ لَّعَنَتۡ أُخۡتَهَاۖ حَتَّىٰۤ إِذَا ٱدَّارَكُوا۟ فِیهَا جَمِیعࣰا قَالَتۡ أُخۡرَىٰهُمۡ لِأُولَىٰهُمۡ رَبَّنَا هَـٰۤؤُلَاۤءِ أَضَلُّونَا فَـَٔاتِهِمۡ عَذَابࣰا ضِعۡفࣰا مِّنَ ٱلنَّارِۖ قَالَ لِكُلࣲّ ضِعۡفࣱ وَلَـٰكِن لَّا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4654, 7, 993, 39, 'وَقَالَتۡ أُولَىٰهُمۡ لِأُخۡرَىٰهُمۡ فَمَا كَانَ لَكُمۡ عَلَیۡنَا مِن فَضۡلࣲ فَذُوقُوا۟ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4655, 7, 994, 40, 'إِنَّ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا وَٱسۡتَكۡبَرُوا۟ عَنۡهَا لَا تُفَتَّحُ لَهُمۡ أَبۡوَ ٰ⁠بُ ٱلسَّمَاۤءِ وَلَا یَدۡخُلُونَ ٱلۡجَنَّةَ حَتَّىٰ یَلِجَ ٱلۡجَمَلُ فِی سَمِّ ٱلۡخِیَاطِۚ وَكَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4656, 7, 995, 41, 'لَهُم مِّن جَهَنَّمَ مِهَادࣱ وَمِن فَوۡقِهِمۡ غَوَاشࣲۚ وَكَذَ ٰ⁠لِكَ نَجۡزِی ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4657, 7, 996, 42, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۤ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَنَّةِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4658, 7, 997, 43, 'وَنَزَعۡنَا مَا فِی صُدُورِهِم مِّنۡ غِلࣲّ تَجۡرِی مِن تَحۡتِهِمُ ٱلۡأَنۡهَـٰرُۖ وَقَالُوا۟ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی هَدَىٰنَا لِهَـٰذَا وَمَا كُنَّا لِنَهۡتَدِیَ لَوۡلَاۤ أَنۡ هَدَىٰنَا ٱللَّهُۖ لَقَدۡ جَاۤءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّۖ وَنُودُوۤا۟ أَن تِلۡكُمُ ٱلۡجَنَّةُ أُورِثۡتُمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4659, 7, 998, 44, 'وَنَادَىٰۤ أَصۡحَـٰبُ ٱلۡجَنَّةِ أَصۡحَـٰبَ ٱلنَّارِ أَن قَدۡ وَجَدۡنَا مَا وَعَدَنَا رَبُّنَا حَقࣰّا فَهَلۡ وَجَدتُّم مَّا وَعَدَ رَبُّكُمۡ حَقࣰّاۖ قَالُوا۟ نَعَمۡۚ فَأَذَّنَ مُؤَذِّنُۢ بَیۡنَهُمۡ أَن لَّعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4660, 7, 999, 45, 'ٱلَّذِینَ یَصُدُّونَ عَن سَبِیلِ ٱللَّهِ وَیَبۡغُونَهَا عِوَجࣰا وَهُم بِٱلۡـَٔاخِرَةِ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:32:47', '2023-05-27 11:18:25'),
(4661, 7, 1000, 46, 'وَبَیۡنَهُمَا حِجَابࣱۚ وَعَلَى ٱلۡأَعۡرَافِ رِجَالࣱ یَعۡرِفُونَ كُلَّۢا بِسِیمَىٰهُمۡۚ وَنَادَوۡا۟ أَصۡحَـٰبَ ٱلۡجَنَّةِ أَن سَلَـٰمٌ عَلَیۡكُمۡۚ لَمۡ یَدۡخُلُوهَا وَهُمۡ یَطۡمَعُونَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4662, 7, 1001, 47, '۞ وَإِذَا صُرِفَتۡ أَبۡصَـٰرُهُمۡ تِلۡقَاۤءَ أَصۡحَـٰبِ ٱلنَّارِ قَالُوا۟ رَبَّنَا لَا تَجۡعَلۡنَا مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4663, 7, 1002, 48, 'وَنَادَىٰۤ أَصۡحَـٰبُ ٱلۡأَعۡرَافِ رِجَالࣰا یَعۡرِفُونَهُم بِسِیمَىٰهُمۡ قَالُوا۟ مَاۤ أَغۡنَىٰ عَنكُمۡ جَمۡعُكُمۡ وَمَا كُنتُمۡ تَسۡتَكۡبِرُونَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4664, 7, 1003, 49, 'أَهَـٰۤؤُلَاۤءِ ٱلَّذِینَ أَقۡسَمۡتُمۡ لَا یَنَالُهُمُ ٱللَّهُ بِرَحۡمَةٍۚ ٱدۡخُلُوا۟ ٱلۡجَنَّةَ لَا خَوۡفٌ عَلَیۡكُمۡ وَلَاۤ أَنتُمۡ تَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4665, 7, 1004, 50, 'وَنَادَىٰۤ أَصۡحَـٰبُ ٱلنَّارِ أَصۡحَـٰبَ ٱلۡجَنَّةِ أَنۡ أَفِیضُوا۟ عَلَیۡنَا مِنَ ٱلۡمَاۤءِ أَوۡ مِمَّا رَزَقَكُمُ ٱللَّهُۚ قَالُوۤا۟ إِنَّ ٱللَّهَ حَرَّمَهُمَا عَلَى ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4666, 7, 1005, 51, 'ٱلَّذِینَ ٱتَّخَذُوا۟ دِینَهُمۡ لَهۡوࣰا وَلَعِبࣰا وَغَرَّتۡهُمُ ٱلۡحَیَوٰةُ ٱلدُّنۡیَاۚ فَٱلۡیَوۡمَ نَنسَىٰهُمۡ كَمَا نَسُوا۟ لِقَاۤءَ یَوۡمِهِمۡ هَـٰذَا وَمَا كَانُوا۟ بِـَٔایَـٰتِنَا یَجۡحَدُونَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4667, 7, 1006, 52, 'وَلَقَدۡ جِئۡنَـٰهُم بِكِتَـٰبࣲ فَصَّلۡنَـٰهُ عَلَىٰ عِلۡمٍ هُدࣰى وَرَحۡمَةࣰ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4668, 7, 1007, 53, 'هَلۡ یَنظُرُونَ إِلَّا تَأۡوِیلَهُۥۚ یَوۡمَ یَأۡتِی تَأۡوِیلُهُۥ یَقُولُ ٱلَّذِینَ نَسُوهُ مِن قَبۡلُ قَدۡ جَاۤءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّ فَهَل لَّنَا مِن شُفَعَاۤءَ فَیَشۡفَعُوا۟ لَنَاۤ أَوۡ نُرَدُّ فَنَعۡمَلَ غَیۡرَ ٱلَّذِی كُنَّا نَعۡمَلُۚ قَدۡ خَسِرُوۤا۟ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:25'),
(4669, 7, 1008, 54, 'إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ فِی سِتَّةِ أَیَّامࣲ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ یُغۡشِی ٱلَّیۡلَ ٱلنَّهَارَ یَطۡلُبُهُۥ حَثِیثࣰا وَٱلشَّمۡسَ وَٱلۡقَمَرَ وَٱلنُّجُومَ مُسَخَّرَ ٰ⁠تِۭ بِأَمۡرِهِۦۤۗ أَلَا لَهُ ٱلۡخَلۡقُ وَٱلۡأَمۡرُۗ تَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:32:48', '2023-05-27 11:18:26'),
(4670, 7, 1009, 55, 'ٱدۡعُوا۟ رَبَّكُمۡ تَضَرُّعࣰا وَخُفۡیَةًۚ إِنَّهُۥ لَا یُحِبُّ ٱلۡمُعۡتَدِینَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4671, 7, 1010, 56, 'وَلَا تُفۡسِدُوا۟ فِی ٱلۡأَرۡضِ بَعۡدَ إِصۡلَـٰحِهَا وَٱدۡعُوهُ خَوۡفࣰا وَطَمَعًاۚ إِنَّ رَحۡمَتَ ٱللَّهِ قَرِیبࣱ مِّنَ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4672, 7, 1011, 57, 'وَهُوَ ٱلَّذِی یُرۡسِلُ ٱلرِّیَـٰحَ بُشۡرَۢا بَیۡنَ یَدَیۡ رَحۡمَتِهِۦۖ حَتَّىٰۤ إِذَاۤ أَقَلَّتۡ سَحَابࣰا ثِقَالࣰا سُقۡنَـٰهُ لِبَلَدࣲ مَّیِّتࣲ فَأَنزَلۡنَا بِهِ ٱلۡمَاۤءَ فَأَخۡرَجۡنَا بِهِۦ مِن كُلِّ ٱلثَّمَرَ ٰ⁠تِۚ كَذَ ٰ⁠لِكَ نُخۡرِجُ ٱلۡمَوۡتَىٰ لَعَلَّكُمۡ تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4673, 7, 1012, 58, 'وَٱلۡبَلَدُ ٱلطَّیِّبُ یَخۡرُجُ نَبَاتُهُۥ بِإِذۡنِ رَبِّهِۦۖ وَٱلَّذِی خَبُثَ لَا یَخۡرُجُ إِلَّا نَكِدࣰاۚ كَذَ ٰ⁠لِكَ نُصَرِّفُ ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4674, 7, 1013, 59, 'لَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۤ إِنِّیۤ أَخَافُ عَلَیۡكُمۡ عَذَابَ یَوۡمٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4675, 7, 1014, 60, 'قَالَ ٱلۡمَلَأُ مِن قَوۡمِهِۦۤ إِنَّا لَنَرَىٰكَ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4676, 7, 1015, 61, 'قَالَ یَـٰقَوۡمِ لَیۡسَ بِی ضَلَـٰلَةࣱ وَلَـٰكِنِّی رَسُولࣱ مِّن رَّبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4677, 7, 1016, 62, 'أُبَلِّغُكُمۡ رِسَـٰلَـٰتِ رَبِّی وَأَنصَحُ لَكُمۡ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4678, 7, 1017, 63, 'أَوَعَجِبۡتُمۡ أَن جَاۤءَكُمۡ ذِكۡرࣱ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلࣲ مِّنكُمۡ لِیُنذِرَكُمۡ وَلِتَتَّقُوا۟ وَلَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4679, 7, 1018, 64, 'فَكَذَّبُوهُ فَأَنجَیۡنَـٰهُ وَٱلَّذِینَ مَعَهُۥ فِی ٱلۡفُلۡكِ وَأَغۡرَقۡنَا ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَاۤۚ إِنَّهُمۡ كَانُوا۟ قَوۡمًا عَمِینَ\n', NULL, 0, '2023-05-26 17:32:49', '2023-05-27 11:18:26'),
(4680, 7, 1019, 65, '۞ وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودࣰاۚ قَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۤۚ أَفَلَا تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:26'),
(4681, 7, 1020, 66, 'قَالَ ٱلۡمَلَأُ ٱلَّذِینَ كَفَرُوا۟ مِن قَوۡمِهِۦۤ إِنَّا لَنَرَىٰكَ فِی سَفَاهَةࣲ وَإِنَّا لَنَظُنُّكَ مِنَ ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:26'),
(4682, 7, 1021, 67, 'قَالَ یَـٰقَوۡمِ لَیۡسَ بِی سَفَاهَةࣱ وَلَـٰكِنِّی رَسُولࣱ مِّن رَّبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:26'),
(4683, 7, 1022, 68, 'أُبَلِّغُكُمۡ رِسَـٰلَـٰتِ رَبِّی وَأَنَا۠ لَكُمۡ نَاصِحٌ أَمِینٌ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:26'),
(4684, 7, 1023, 69, 'أَوَعَجِبۡتُمۡ أَن جَاۤءَكُمۡ ذِكۡرࣱ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلࣲ مِّنكُمۡ لِیُنذِرَكُمۡۚ وَٱذۡكُرُوۤا۟ إِذۡ جَعَلَكُمۡ خُلَفَاۤءَ مِنۢ بَعۡدِ قَوۡمِ نُوحࣲ وَزَادَكُمۡ فِی ٱلۡخَلۡقِ بَصۜۡطَةࣰۖ فَٱذۡكُرُوۤا۟ ءَالَاۤءَ ٱللَّهِ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:26'),
(4685, 7, 1024, 70, 'قَالُوۤا۟ أَجِئۡتَنَا لِنَعۡبُدَ ٱللَّهَ وَحۡدَهُۥ وَنَذَرَ مَا كَانَ یَعۡبُدُ ءَابَاۤؤُنَا فَأۡتِنَا بِمَا تَعِدُنَاۤ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:27'),
(4686, 7, 1025, 71, 'قَالَ قَدۡ وَقَعَ عَلَیۡكُم مِّن رَّبِّكُمۡ رِجۡسࣱ وَغَضَبٌۖ أَتُجَـٰدِلُونَنِی فِیۤ أَسۡمَاۤءࣲ سَمَّیۡتُمُوهَاۤ أَنتُمۡ وَءَابَاۤؤُكُم مَّا نَزَّلَ ٱللَّهُ بِهَا مِن سُلۡطَـٰنࣲۚ فَٱنتَظِرُوۤا۟ إِنِّی مَعَكُم مِّنَ ٱلۡمُنتَظِرِینَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:27'),
(4687, 7, 1026, 72, 'فَأَنجَیۡنَـٰهُ وَٱلَّذِینَ مَعَهُۥ بِرَحۡمَةࣲ مِّنَّا وَقَطَعۡنَا دَابِرَ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَاۖ وَمَا كَانُوا۟ مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:27'),
(4688, 7, 1027, 73, 'وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَـٰلِحࣰاۚ قَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۖ قَدۡ جَاۤءَتۡكُم بَیِّنَةࣱ مِّن رَّبِّكُمۡۖ هَـٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَایَةࣰۖ فَذَرُوهَا تَأۡكُلۡ فِیۤ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوۤءࣲ فَیَأۡخُذَكُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:27'),
(4689, 7, 1028, 74, 'وَٱذۡكُرُوۤا۟ إِذۡ جَعَلَكُمۡ خُلَفَاۤءَ مِنۢ بَعۡدِ عَادࣲ وَبَوَّأَكُمۡ فِی ٱلۡأَرۡضِ تَتَّخِذُونَ مِن سُهُولِهَا قُصُورࣰا وَتَنۡحِتُونَ ٱلۡجِبَالَ بُیُوتࣰاۖ فَٱذۡكُرُوۤا۟ ءَالَاۤءَ ٱللَّهِ وَلَا تَعۡثَوۡا۟ فِی ٱلۡأَرۡضِ مُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:32:50', '2023-05-27 11:18:27'),
(4690, 7, 1029, 75, 'قَالَ ٱلۡمَلَأُ ٱلَّذِینَ ٱسۡتَكۡبَرُوا۟ مِن قَوۡمِهِۦ لِلَّذِینَ ٱسۡتُضۡعِفُوا۟ لِمَنۡ ءَامَنَ مِنۡهُمۡ أَتَعۡلَمُونَ أَنَّ صَـٰلِحࣰا مُّرۡسَلࣱ مِّن رَّبِّهِۦۚ قَالُوۤا۟ إِنَّا بِمَاۤ أُرۡسِلَ بِهِۦ مُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4691, 7, 1030, 76, 'قَالَ ٱلَّذِینَ ٱسۡتَكۡبَرُوۤا۟ إِنَّا بِٱلَّذِیۤ ءَامَنتُم بِهِۦ كَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4692, 7, 1031, 77, 'فَعَقَرُوا۟ ٱلنَّاقَةَ وَعَتَوۡا۟ عَنۡ أَمۡرِ رَبِّهِمۡ وَقَالُوا۟ یَـٰصَـٰلِحُ ٱئۡتِنَا بِمَا تَعِدُنَاۤ إِن كُنتَ مِنَ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4693, 7, 1032, 78, 'فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُوا۟ فِی دَارِهِمۡ جَـٰثِمِینَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4694, 7, 1033, 79, 'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ یَـٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَالَةَ رَبِّی وَنَصَحۡتُ لَكُمۡ وَلَـٰكِن لَّا تُحِبُّونَ ٱلنَّـٰصِحِینَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4695, 7, 1034, 80, 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦۤ أَتَأۡتُونَ ٱلۡفَـٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدࣲ مِّنَ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4696, 7, 1035, 81, 'إِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةࣰ مِّن دُونِ ٱلنِّسَاۤءِۚ بَلۡ أَنتُمۡ قَوۡمࣱ مُّسۡرِفُونَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4697, 7, 1036, 82, 'وَمَا كَانَ جَوَابَ قَوۡمِهِۦۤ إِلَّاۤ أَن قَالُوۤا۟ أَخۡرِجُوهُم مِّن قَرۡیَتِكُمۡۖ إِنَّهُمۡ أُنَاسࣱ یَتَطَهَّرُونَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4698, 7, 1037, 83, 'فَأَنجَیۡنَـٰهُ وَأَهۡلَهُۥۤ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4699, 7, 1038, 84, 'وَأَمۡطَرۡنَا عَلَیۡهِم مَّطَرࣰاۖ فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:32:51', '2023-05-27 11:18:27'),
(4700, 7, 1039, 85, 'وَإِلَىٰ مَدۡیَنَ أَخَاهُمۡ شُعَیۡبࣰاۚ قَالَ یَـٰقَوۡمِ ٱعۡبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنۡ إِلَـٰهٍ غَیۡرُهُۥۖ قَدۡ جَاۤءَتۡكُم بَیِّنَةࣱ مِّن رَّبِّكُمۡۖ فَأَوۡفُوا۟ ٱلۡكَیۡلَ وَٱلۡمِیزَانَ وَلَا تَبۡخَسُوا۟ ٱلنَّاسَ أَشۡیَاۤءَهُمۡ وَلَا تُفۡسِدُوا۟ فِی ٱلۡأَرۡضِ بَعۡدَ إِصۡلَـٰحِهَاۚ ذَ ٰ⁠لِكُمۡ خَیۡرࣱ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:27'),
(4701, 7, 1040, 86, 'وَلَا تَقۡعُدُوا۟ بِكُلِّ صِرَ ٰ⁠طࣲ تُوعِدُونَ وَتَصُدُّونَ عَن سَبِیلِ ٱللَّهِ مَنۡ ءَامَنَ بِهِۦ وَتَبۡغُونَهَا عِوَجࣰاۚ وَٱذۡكُرُوۤا۟ إِذۡ كُنتُمۡ قَلِیلࣰا فَكَثَّرَكُمۡۖ وَٱنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:27'),
(4702, 7, 1041, 87, 'وَإِن كَانَ طَاۤىِٕفَةࣱ مِّنكُمۡ ءَامَنُوا۟ بِٱلَّذِیۤ أُرۡسِلۡتُ بِهِۦ وَطَاۤىِٕفَةࣱ لَّمۡ یُؤۡمِنُوا۟ فَٱصۡبِرُوا۟ حَتَّىٰ یَحۡكُمَ ٱللَّهُ بَیۡنَنَاۚ وَهُوَ خَیۡرُ ٱلۡحَـٰكِمِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4703, 7, 1042, 88, '۞ قَالَ ٱلۡمَلَأُ ٱلَّذِینَ ٱسۡتَكۡبَرُوا۟ مِن قَوۡمِهِۦ لَنُخۡرِجَنَّكَ یَـٰشُعَیۡبُ وَٱلَّذِینَ ءَامَنُوا۟ مَعَكَ مِن قَرۡیَتِنَاۤ أَوۡ لَتَعُودُنَّ فِی مِلَّتِنَاۚ قَالَ أَوَلَوۡ كُنَّا كَـٰرِهِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4704, 7, 1043, 89, 'قَدِ ٱفۡتَرَیۡنَا عَلَى ٱللَّهِ كَذِبًا إِنۡ عُدۡنَا فِی مِلَّتِكُم بَعۡدَ إِذۡ نَجَّىٰنَا ٱللَّهُ مِنۡهَاۚ وَمَا یَكُونُ لَنَاۤ أَن نَّعُودَ فِیهَاۤ إِلَّاۤ أَن یَشَاۤءَ ٱللَّهُ رَبُّنَاۚ وَسِعَ رَبُّنَا كُلَّ شَیۡءٍ عِلۡمًاۚ عَلَى ٱللَّهِ تَوَكَّلۡنَاۚ رَبَّنَا ٱفۡتَحۡ بَیۡنَنَا وَبَیۡنَ قَوۡمِنَا بِٱلۡحَقِّ وَأَنتَ خَیۡرُ ٱلۡفَـٰتِحِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4705, 7, 1044, 90, 'وَقَالَ ٱلۡمَلَأُ ٱلَّذِینَ كَفَرُوا۟ مِن قَوۡمِهِۦ لَىِٕنِ ٱتَّبَعۡتُمۡ شُعَیۡبًا إِنَّكُمۡ إِذࣰا لَّخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4706, 7, 1045, 91, 'فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُوا۟ فِی دَارِهِمۡ جَـٰثِمِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4707, 7, 1046, 92, 'ٱلَّذِینَ كَذَّبُوا۟ شُعَیۡبࣰا كَأَن لَّمۡ یَغۡنَوۡا۟ فِیهَاۚ ٱلَّذِینَ كَذَّبُوا۟ شُعَیۡبࣰا كَانُوا۟ هُمُ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4708, 7, 1047, 93, 'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ یَـٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَـٰلَـٰتِ رَبِّی وَنَصَحۡتُ لَكُمۡۖ فَكَیۡفَ ءَاسَىٰ عَلَىٰ قَوۡمࣲ كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4709, 7, 1048, 94, 'وَمَاۤ أَرۡسَلۡنَا فِی قَرۡیَةࣲ مِّن نَّبِیٍّ إِلَّاۤ أَخَذۡنَاۤ أَهۡلَهَا بِٱلۡبَأۡسَاۤءِ وَٱلضَّرَّاۤءِ لَعَلَّهُمۡ یَضَّرَّعُونَ\n', NULL, 0, '2023-05-26 17:32:52', '2023-05-27 11:18:28'),
(4710, 7, 1049, 95, 'ثُمَّ بَدَّلۡنَا مَكَانَ ٱلسَّیِّئَةِ ٱلۡحَسَنَةَ حَتَّىٰ عَفَوا۟ وَّقَالُوا۟ قَدۡ مَسَّ ءَابَاۤءَنَا ٱلضَّرَّاۤءُ وَٱلسَّرَّاۤءُ فَأَخَذۡنَـٰهُم بَغۡتَةࣰ وَهُمۡ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:28'),
(4711, 7, 1050, 96, 'وَلَوۡ أَنَّ أَهۡلَ ٱلۡقُرَىٰۤ ءَامَنُوا۟ وَٱتَّقَوۡا۟ لَفَتَحۡنَا عَلَیۡهِم بَرَكَـٰتࣲ مِّنَ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِ وَلَـٰكِن كَذَّبُوا۟ فَأَخَذۡنَـٰهُم بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:28'),
(4712, 7, 1051, 97, 'أَفَأَمِنَ أَهۡلُ ٱلۡقُرَىٰۤ أَن یَأۡتِیَهُم بَأۡسُنَا بَیَـٰتࣰا وَهُمۡ نَاۤىِٕمُونَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:28'),
(4713, 7, 1052, 98, 'أَوَأَمِنَ أَهۡلُ ٱلۡقُرَىٰۤ أَن یَأۡتِیَهُم بَأۡسُنَا ضُحࣰى وَهُمۡ یَلۡعَبُونَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:29'),
(4714, 7, 1053, 99, 'أَفَأَمِنُوا۟ مَكۡرَ ٱللَّهِۚ فَلَا یَأۡمَنُ مَكۡرَ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:29'),
(4715, 7, 1054, 100, 'أَوَلَمۡ یَهۡدِ لِلَّذِینَ یَرِثُونَ ٱلۡأَرۡضَ مِنۢ بَعۡدِ أَهۡلِهَاۤ أَن لَّوۡ نَشَاۤءُ أَصَبۡنَـٰهُم بِذُنُوبِهِمۡۚ وَنَطۡبَعُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا یَسۡمَعُونَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:29'),
(4716, 7, 1055, 101, 'تِلۡكَ ٱلۡقُرَىٰ نَقُصُّ عَلَیۡكَ مِنۡ أَنۢبَاۤىِٕهَاۚ وَلَقَدۡ جَاۤءَتۡهُمۡ رُسُلُهُم بِٱلۡبَیِّنَـٰتِ فَمَا كَانُوا۟ لِیُؤۡمِنُوا۟ بِمَا كَذَّبُوا۟ مِن قَبۡلُۚ كَذَ ٰ⁠لِكَ یَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:29'),
(4717, 7, 1056, 102, 'وَمَا وَجَدۡنَا لِأَكۡثَرِهِم مِّنۡ عَهۡدࣲۖ وَإِن وَجَدۡنَاۤ أَكۡثَرَهُمۡ لَفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:29'),
(4718, 7, 1057, 103, 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ بِـَٔایَـٰتِنَاۤ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِی۟هِۦ فَظَلَمُوا۟ بِهَاۖ فَٱنظُرۡ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:32:53', '2023-05-27 11:18:29'),
(4719, 7, 1058, 104, 'وَقَالَ مُوسَىٰ یَـٰفِرۡعَوۡنُ إِنِّی رَسُولࣱ مِّن رَّبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4720, 7, 1059, 105, 'حَقِیقٌ عَلَىٰۤ أَن لَّاۤ أَقُولَ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ قَدۡ جِئۡتُكُم بِبَیِّنَةࣲ مِّن رَّبِّكُمۡ فَأَرۡسِلۡ مَعِیَ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4721, 7, 1060, 106, 'قَالَ إِن كُنتَ جِئۡتَ بِـَٔایَةࣲ فَأۡتِ بِهَاۤ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4722, 7, 1061, 107, 'فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِیَ ثُعۡبَانࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4723, 7, 1062, 108, 'وَنَزَعَ یَدَهُۥ فَإِذَا هِیَ بَیۡضَاۤءُ لِلنَّـٰظِرِینَ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4724, 7, 1063, 109, 'قَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ إِنَّ هَـٰذَا لَسَـٰحِرٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4725, 7, 1064, 110, 'یُرِیدُ أَن یُخۡرِجَكُم مِّنۡ أَرۡضِكُمۡۖ فَمَاذَا تَأۡمُرُونَ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4726, 7, 1065, 111, 'قَالُوۤا۟ أَرۡجِهۡ وَأَخَاهُ وَأَرۡسِلۡ فِی ٱلۡمَدَاۤىِٕنِ حَـٰشِرِینَ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:29'),
(4727, 7, 1066, 112, 'یَأۡتُوكَ بِكُلِّ سَـٰحِرٍ عَلِیمࣲ\n', NULL, 0, '2023-05-26 17:32:54', '2023-05-27 11:18:30'),
(4728, 7, 1067, 113, 'وَجَاۤءَ ٱلسَّحَرَةُ فِرۡعَوۡنَ قَالُوۤا۟ إِنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَـٰلِبِینَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4729, 7, 1068, 114, 'قَالَ نَعَمۡ وَإِنَّكُمۡ لَمِنَ ٱلۡمُقَرَّبِینَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4730, 7, 1069, 115, 'قَالُوا۟ یَـٰمُوسَىٰۤ إِمَّاۤ أَن تُلۡقِیَ وَإِمَّاۤ أَن نَّكُونَ نَحۡنُ ٱلۡمُلۡقِینَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4731, 7, 1070, 116, 'قَالَ أَلۡقُوا۟ۖ فَلَمَّاۤ أَلۡقَوۡا۟ سَحَرُوۤا۟ أَعۡیُنَ ٱلنَّاسِ وَٱسۡتَرۡهَبُوهُمۡ وَجَاۤءُو بِسِحۡرٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4732, 7, 1071, 117, '۞ وَأَوۡحَیۡنَاۤ إِلَىٰ مُوسَىٰۤ أَنۡ أَلۡقِ عَصَاكَۖ فَإِذَا هِیَ تَلۡقَفُ مَا یَأۡفِكُونَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4733, 7, 1072, 118, 'فَوَقَعَ ٱلۡحَقُّ وَبَطَلَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4734, 7, 1073, 119, 'فَغُلِبُوا۟ هُنَالِكَ وَٱنقَلَبُوا۟ صَـٰغِرِینَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4735, 7, 1074, 120, 'وَأُلۡقِیَ ٱلسَّحَرَةُ سَـٰجِدِینَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4736, 7, 1075, 121, 'قَالُوۤا۟ ءَامَنَّا بِرَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4737, 7, 1076, 122, 'رَبِّ مُوسَىٰ وَهَـٰرُونَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4738, 7, 1077, 123, 'قَالَ فِرۡعَوۡنُ ءَامَنتُم بِهِۦ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّ هَـٰذَا لَمَكۡرࣱ مَّكَرۡتُمُوهُ فِی ٱلۡمَدِینَةِ لِتُخۡرِجُوا۟ مِنۡهَاۤ أَهۡلَهَاۖ فَسَوۡفَ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4739, 7, 1078, 124, 'لَأُقَطِّعَنَّ أَیۡدِیَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَـٰفࣲ ثُمَّ لَأُصَلِّبَنَّكُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:32:55', '2023-05-27 11:18:30'),
(4740, 7, 1079, 125, 'قَالُوۤا۟ إِنَّاۤ إِلَىٰ رَبِّنَا مُنقَلِبُونَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:30'),
(4741, 7, 1080, 126, 'وَمَا تَنقِمُ مِنَّاۤ إِلَّاۤ أَنۡ ءَامَنَّا بِـَٔایَـٰتِ رَبِّنَا لَمَّا جَاۤءَتۡنَاۚ رَبَّنَاۤ أَفۡرِغۡ عَلَیۡنَا صَبۡرࣰا وَتَوَفَّنَا مُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:30'),
(4742, 7, 1081, 127, 'وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ أَتَذَرُ مُوسَىٰ وَقَوۡمَهُۥ لِیُفۡسِدُوا۟ فِی ٱلۡأَرۡضِ وَیَذَرَكَ وَءَالِهَتَكَۚ قَالَ سَنُقَتِّلُ أَبۡنَاۤءَهُمۡ وَنَسۡتَحۡیِۦ نِسَاۤءَهُمۡ وَإِنَّا فَوۡقَهُمۡ قَـٰهِرُونَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:30'),
(4743, 7, 1082, 128, 'قَالَ مُوسَىٰ لِقَوۡمِهِ ٱسۡتَعِینُوا۟ بِٱللَّهِ وَٱصۡبِرُوۤا۟ۖ إِنَّ ٱلۡأَرۡضَ لِلَّهِ یُورِثُهَا مَن یَشَاۤءُ مِنۡ عِبَادِهِۦۖ وَٱلۡعَـٰقِبَةُ لِلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:30'),
(4744, 7, 1083, 129, 'قَالُوۤا۟ أُوذِینَا مِن قَبۡلِ أَن تَأۡتِیَنَا وَمِنۢ بَعۡدِ مَا جِئۡتَنَاۚ قَالَ عَسَىٰ رَبُّكُمۡ أَن یُهۡلِكَ عَدُوَّكُمۡ وَیَسۡتَخۡلِفَكُمۡ فِی ٱلۡأَرۡضِ فَیَنظُرَ كَیۡفَ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:31'),
(4745, 7, 1084, 130, 'وَلَقَدۡ أَخَذۡنَاۤ ءَالَ فِرۡعَوۡنَ بِٱلسِّنِینَ وَنَقۡصࣲ مِّنَ ٱلثَّمَرَ ٰ⁠تِ لَعَلَّهُمۡ یَذَّكَّرُونَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:31'),
(4746, 7, 1085, 131, 'فَإِذَا جَاۤءَتۡهُمُ ٱلۡحَسَنَةُ قَالُوا۟ لَنَا هَـٰذِهِۦۖ وَإِن تُصِبۡهُمۡ سَیِّئَةࣱ یَطَّیَّرُوا۟ بِمُوسَىٰ وَمَن مَّعَهُۥۤۗ أَلَاۤ إِنَّمَا طَـٰۤىِٕرُهُمۡ عِندَ ٱللَّهِ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:31'),
(4747, 7, 1086, 132, 'وَقَالُوا۟ مَهۡمَا تَأۡتِنَا بِهِۦ مِنۡ ءَایَةࣲ لِّتَسۡحَرَنَا بِهَا فَمَا نَحۡنُ لَكَ بِمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:31'),
(4748, 7, 1087, 133, 'فَأَرۡسَلۡنَا عَلَیۡهِمُ ٱلطُّوفَانَ وَٱلۡجَرَادَ وَٱلۡقُمَّلَ وَٱلضَّفَادِعَ وَٱلدَّمَ ءَایَـٰتࣲ مُّفَصَّلَـٰتࣲ فَٱسۡتَكۡبَرُوا۟ وَكَانُوا۟ قَوۡمࣰا مُّجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:32:56', '2023-05-27 11:18:31'),
(4749, 7, 1088, 134, 'وَلَمَّا وَقَعَ عَلَیۡهِمُ ٱلرِّجۡزُ قَالُوا۟ یَـٰمُوسَى ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَۖ لَىِٕن كَشَفۡتَ عَنَّا ٱلرِّجۡزَ لَنُؤۡمِنَنَّ لَكَ وَلَنُرۡسِلَنَّ مَعَكَ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4750, 7, 1089, 135, 'فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلرِّجۡزَ إِلَىٰۤ أَجَلٍ هُم بَـٰلِغُوهُ إِذَا هُمۡ یَنكُثُونَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4751, 7, 1090, 136, 'فَٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَـٰهُمۡ فِی ٱلۡیَمِّ بِأَنَّهُمۡ كَذَّبُوا۟ بِـَٔایَـٰتِنَا وَكَانُوا۟ عَنۡهَا غَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4752, 7, 1091, 137, 'وَأَوۡرَثۡنَا ٱلۡقَوۡمَ ٱلَّذِینَ كَانُوا۟ یُسۡتَضۡعَفُونَ مَشَـٰرِقَ ٱلۡأَرۡضِ وَمَغَـٰرِبَهَا ٱلَّتِی بَـٰرَكۡنَا فِیهَاۖ وَتَمَّتۡ كَلِمَتُ رَبِّكَ ٱلۡحُسۡنَىٰ عَلَىٰ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ بِمَا صَبَرُوا۟ۖ وَدَمَّرۡنَا مَا كَانَ یَصۡنَعُ فِرۡعَوۡنُ وَقَوۡمُهُۥ وَمَا كَانُوا۟ یَعۡرِشُونَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4753, 7, 1092, 138, 'وَجَـٰوَزۡنَا بِبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱلۡبَحۡرَ فَأَتَوۡا۟ عَلَىٰ قَوۡمࣲ یَعۡكُفُونَ عَلَىٰۤ أَصۡنَامࣲ لَّهُمۡۚ قَالُوا۟ یَـٰمُوسَى ٱجۡعَل لَّنَاۤ إِلَـٰهࣰا كَمَا لَهُمۡ ءَالِهَةࣱۚ قَالَ إِنَّكُمۡ قَوۡمࣱ تَجۡهَلُونَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4754, 7, 1093, 139, 'إِنَّ هَـٰۤؤُلَاۤءِ مُتَبَّرࣱ مَّا هُمۡ فِیهِ وَبَـٰطِلࣱ مَّا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4755, 7, 1094, 140, 'قَالَ أَغَیۡرَ ٱللَّهِ أَبۡغِیكُمۡ إِلَـٰهࣰا وَهُوَ فَضَّلَكُمۡ عَلَى ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4756, 7, 1095, 141, 'وَإِذۡ أَنجَیۡنَـٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ یَسُومُونَكُمۡ سُوۤءَ ٱلۡعَذَابِ یُقَتِّلُونَ أَبۡنَاۤءَكُمۡ وَیَسۡتَحۡیُونَ نِسَاۤءَكُمۡۚ وَفِی ذَ ٰ⁠لِكُم بَلَاۤءࣱ مِّن رَّبِّكُمۡ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4757, 7, 1096, 142, '۞ وَوَ ٰ⁠عَدۡنَا مُوسَىٰ ثَلَـٰثِینَ لَیۡلَةࣰ وَأَتۡمَمۡنَـٰهَا بِعَشۡرࣲ فَتَمَّ مِیقَـٰتُ رَبِّهِۦۤ أَرۡبَعِینَ لَیۡلَةࣰۚ وَقَالَ مُوسَىٰ لِأَخِیهِ هَـٰرُونَ ٱخۡلُفۡنِی فِی قَوۡمِی وَأَصۡلِحۡ وَلَا تَتَّبِعۡ سَبِیلَ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4758, 7, 1097, 143, 'وَلَمَّا جَاۤءَ مُوسَىٰ لِمِیقَـٰتِنَا وَكَلَّمَهُۥ رَبُّهُۥ قَالَ رَبِّ أَرِنِیۤ أَنظُرۡ إِلَیۡكَۚ قَالَ لَن تَرَىٰنِی وَلَـٰكِنِ ٱنظُرۡ إِلَى ٱلۡجَبَلِ فَإِنِ ٱسۡتَقَرَّ مَكَانَهُۥ فَسَوۡفَ تَرَىٰنِیۚ فَلَمَّا تَجَلَّىٰ رَبُّهُۥ لِلۡجَبَلِ جَعَلَهُۥ دَكࣰّا وَخَرَّ مُوسَىٰ صَعِقࣰاۚ فَلَمَّاۤ أَفَاقَ قَالَ سُبۡحَـٰنَكَ تُبۡتُ إِلَیۡكَ وَأَنَا۠ أَوَّلُ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4759, 7, 1098, 144, 'قَالَ یَـٰمُوسَىٰۤ إِنِّی ٱصۡطَفَیۡتُكَ عَلَى ٱلنَّاسِ بِرِسَـٰلَـٰتِی وَبِكَلَـٰمِی فَخُذۡ مَاۤ ءَاتَیۡتُكَ وَكُن مِّنَ ٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:32:57', '2023-05-27 11:18:31'),
(4760, 7, 1099, 145, 'وَكَتَبۡنَا لَهُۥ فِی ٱلۡأَلۡوَاحِ مِن كُلِّ شَیۡءࣲ مَّوۡعِظَةࣰ وَتَفۡصِیلࣰا لِّكُلِّ شَیۡءࣲ فَخُذۡهَا بِقُوَّةࣲ وَأۡمُرۡ قَوۡمَكَ یَأۡخُذُوا۟ بِأَحۡسَنِهَاۚ سَأُو۟رِیكُمۡ دَارَ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4761, 7, 1100, 146, 'سَأَصۡرِفُ عَنۡ ءَایَـٰتِیَ ٱلَّذِینَ یَتَكَبَّرُونَ فِی ٱلۡأَرۡضِ بِغَیۡرِ ٱلۡحَقِّ وَإِن یَرَوۡا۟ كُلَّ ءَایَةࣲ لَّا یُؤۡمِنُوا۟ بِهَا وَإِن یَرَوۡا۟ سَبِیلَ ٱلرُّشۡدِ لَا یَتَّخِذُوهُ سَبِیلࣰا وَإِن یَرَوۡا۟ سَبِیلَ ٱلۡغَیِّ یَتَّخِذُوهُ سَبِیلࣰاۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ كَذَّبُوا۟ بِـَٔایَـٰتِنَا وَكَانُوا۟ عَنۡهَا غَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4762, 7, 1101, 147, 'وَٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا وَلِقَاۤءِ ٱلۡـَٔاخِرَةِ حَبِطَتۡ أَعۡمَـٰلُهُمۡۚ هَلۡ یُجۡزَوۡنَ إِلَّا مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4763, 7, 1102, 148, 'وَٱتَّخَذَ قَوۡمُ مُوسَىٰ مِنۢ بَعۡدِهِۦ مِنۡ حُلِیِّهِمۡ عِجۡلࣰا جَسَدࣰا لَّهُۥ خُوَارٌۚ أَلَمۡ یَرَوۡا۟ أَنَّهُۥ لَا یُكَلِّمُهُمۡ وَلَا یَهۡدِیهِمۡ سَبِیلًاۘ ٱتَّخَذُوهُ وَكَانُوا۟ ظَـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4764, 7, 1103, 149, 'وَلَمَّا سُقِطَ فِیۤ أَیۡدِیهِمۡ وَرَأَوۡا۟ أَنَّهُمۡ قَدۡ ضَلُّوا۟ قَالُوا۟ لَىِٕن لَّمۡ یَرۡحَمۡنَا رَبُّنَا وَیَغۡفِرۡ لَنَا لَنَكُونَنَّ مِنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4765, 7, 1104, 150, 'وَلَمَّا رَجَعَ مُوسَىٰۤ إِلَىٰ قَوۡمِهِۦ غَضۡبَـٰنَ أَسِفࣰا قَالَ بِئۡسَمَا خَلَفۡتُمُونِی مِنۢ بَعۡدِیۤۖ أَعَجِلۡتُمۡ أَمۡرَ رَبِّكُمۡۖ وَأَلۡقَى ٱلۡأَلۡوَاحَ وَأَخَذَ بِرَأۡسِ أَخِیهِ یَجُرُّهُۥۤ إِلَیۡهِۚ قَالَ ٱبۡنَ أُمَّ إِنَّ ٱلۡقَوۡمَ ٱسۡتَضۡعَفُونِی وَكَادُوا۟ یَقۡتُلُونَنِی فَلَا تُشۡمِتۡ بِیَ ٱلۡأَعۡدَاۤءَ وَلَا تَجۡعَلۡنِی مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4766, 7, 1105, 151, 'قَالَ رَبِّ ٱغۡفِرۡ لِی وَلِأَخِی وَأَدۡخِلۡنَا فِی رَحۡمَتِكَۖ وَأَنتَ أَرۡحَمُ ٱلرَّ ٰ⁠حِمِینَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4767, 7, 1106, 152, 'إِنَّ ٱلَّذِینَ ٱتَّخَذُوا۟ ٱلۡعِجۡلَ سَیَنَالُهُمۡ غَضَبࣱ مِّن رَّبِّهِمۡ وَذِلَّةࣱ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۚ وَكَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡمُفۡتَرِینَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4768, 7, 1107, 153, 'وَٱلَّذِینَ عَمِلُوا۟ ٱلسَّیِّـَٔاتِ ثُمَّ تَابُوا۟ مِنۢ بَعۡدِهَا وَءَامَنُوۤا۟ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4769, 7, 1108, 154, 'وَلَمَّا سَكَتَ عَن مُّوسَى ٱلۡغَضَبُ أَخَذَ ٱلۡأَلۡوَاحَۖ وَفِی نُسۡخَتِهَا هُدࣰى وَرَحۡمَةࣱ لِّلَّذِینَ هُمۡ لِرَبِّهِمۡ یَرۡهَبُونَ\n', NULL, 0, '2023-05-26 17:32:58', '2023-05-27 11:18:32'),
(4770, 7, 1109, 155, 'وَٱخۡتَارَ مُوسَىٰ قَوۡمَهُۥ سَبۡعِینَ رَجُلࣰا لِّمِیقَـٰتِنَاۖ فَلَمَّاۤ أَخَذَتۡهُمُ ٱلرَّجۡفَةُ قَالَ رَبِّ لَوۡ شِئۡتَ أَهۡلَكۡتَهُم مِّن قَبۡلُ وَإِیَّـٰیَۖ أَتُهۡلِكُنَا بِمَا فَعَلَ ٱلسُّفَهَاۤءُ مِنَّاۤۖ إِنۡ هِیَ إِلَّا فِتۡنَتُكَ تُضِلُّ بِهَا مَن تَشَاۤءُ وَتَهۡدِی مَن تَشَاۤءُۖ أَنتَ وَلِیُّنَا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَاۖ وَأَنتَ خَیۡرُ ٱلۡغَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:32'),
(4771, 7, 1110, 156, '۞ وَٱكۡتُبۡ لَنَا فِی هَـٰذِهِ ٱلدُّنۡیَا حَسَنَةࣰ وَفِی ٱلۡـَٔاخِرَةِ إِنَّا هُدۡنَاۤ إِلَیۡكَۚ قَالَ عَذَابِیۤ أُصِیبُ بِهِۦ مَنۡ أَشَاۤءُۖ وَرَحۡمَتِی وَسِعَتۡ كُلَّ شَیۡءࣲۚ فَسَأَكۡتُبُهَا لِلَّذِینَ یَتَّقُونَ وَیُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلَّذِینَ هُم بِـَٔایَـٰتِنَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:32'),
(4772, 7, 1111, 157, 'ٱلَّذِینَ یَتَّبِعُونَ ٱلرَّسُولَ ٱلنَّبِیَّ ٱلۡأُمِّیَّ ٱلَّذِی یَجِدُونَهُۥ مَكۡتُوبًا عِندَهُمۡ فِی ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِیلِ یَأۡمُرُهُم بِٱلۡمَعۡرُوفِ وَیَنۡهَىٰهُمۡ عَنِ ٱلۡمُنكَرِ وَیُحِلُّ لَهُمُ ٱلطَّیِّبَـٰتِ وَیُحَرِّمُ عَلَیۡهِمُ ٱلۡخَبَـٰۤىِٕثَ وَیَضَعُ عَنۡهُمۡ إِصۡرَهُمۡ وَٱلۡأَغۡلَـٰلَ ٱلَّتِی كَانَتۡ عَلَیۡهِمۡۚ فَٱلَّذِینَ ءَامَنُوا۟ بِهِۦ وَعَزَّرُوهُ وَنَصَرُوهُ وَٱتَّبَعُوا۟ ٱلنُّورَ ٱلَّذِیۤ أُنزِلَ مَعَهُۥۤ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:32');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(4773, 7, 1112, 158, 'قُلۡ یَـٰۤأَیُّهَا ٱلنَّاسُ إِنِّی رَسُولُ ٱللَّهِ إِلَیۡكُمۡ جَمِیعًا ٱلَّذِی لَهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ لَاۤ إِلَـٰهَ إِلَّا هُوَ یُحۡیِۦ وَیُمِیتُۖ فَـَٔامِنُوا۟ بِٱللَّهِ وَرَسُولِهِ ٱلنَّبِیِّ ٱلۡأُمِّیِّ ٱلَّذِی یُؤۡمِنُ بِٱللَّهِ وَكَلِمَـٰتِهِۦ وَٱتَّبِعُوهُ لَعَلَّكُمۡ تَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:32'),
(4774, 7, 1113, 159, 'وَمِن قَوۡمِ مُوسَىٰۤ أُمَّةࣱ یَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ یَعۡدِلُونَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:32'),
(4775, 7, 1114, 160, 'وَقَطَّعۡنَـٰهُمُ ٱثۡنَتَیۡ عَشۡرَةَ أَسۡبَاطًا أُمَمࣰاۚ وَأَوۡحَیۡنَاۤ إِلَىٰ مُوسَىٰۤ إِذِ ٱسۡتَسۡقَىٰهُ قَوۡمُهُۥۤ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنۢبَجَسَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَیۡنࣰاۖ قَدۡ عَلِمَ كُلُّ أُنَاسࣲ مَّشۡرَبَهُمۡۚ وَظَلَّلۡنَا عَلَیۡهِمُ ٱلۡغَمَـٰمَ وَأَنزَلۡنَا عَلَیۡهِمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُوا۟ مِن طَیِّبَـٰتِ مَا رَزَقۡنَـٰكُمۡۚ وَمَا ظَلَمُونَا وَلَـٰكِن كَانُوۤا۟ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:32'),
(4776, 7, 1115, 161, 'وَإِذۡ قِیلَ لَهُمُ ٱسۡكُنُوا۟ هَـٰذِهِ ٱلۡقَرۡیَةَ وَكُلُوا۟ مِنۡهَا حَیۡثُ شِئۡتُمۡ وَقُولُوا۟ حِطَّةࣱ وَٱدۡخُلُوا۟ ٱلۡبَابَ سُجَّدࣰا نَّغۡفِرۡ لَكُمۡ خَطِیۤـَٔـٰتِكُمۡۚ سَنَزِیدُ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:32'),
(4777, 7, 1116, 162, 'فَبَدَّلَ ٱلَّذِینَ ظَلَمُوا۟ مِنۡهُمۡ قَوۡلًا غَیۡرَ ٱلَّذِی قِیلَ لَهُمۡ فَأَرۡسَلۡنَا عَلَیۡهِمۡ رِجۡزࣰا مِّنَ ٱلسَّمَاۤءِ بِمَا كَانُوا۟ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:32:59', '2023-05-27 11:18:33'),
(4778, 7, 1117, 163, 'وَسۡـَٔلۡهُمۡ عَنِ ٱلۡقَرۡیَةِ ٱلَّتِی كَانَتۡ حَاضِرَةَ ٱلۡبَحۡرِ إِذۡ یَعۡدُونَ فِی ٱلسَّبۡتِ إِذۡ تَأۡتِیهِمۡ حِیتَانُهُمۡ یَوۡمَ سَبۡتِهِمۡ شُرَّعࣰا وَیَوۡمَ لَا یَسۡبِتُونَ لَا تَأۡتِیهِمۡۚ كَذَ ٰ⁠لِكَ نَبۡلُوهُم بِمَا كَانُوا۟ یَفۡسُقُونَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4779, 7, 1118, 164, 'وَإِذۡ قَالَتۡ أُمَّةࣱ مِّنۡهُمۡ لِمَ تَعِظُونَ قَوۡمًا ٱللَّهُ مُهۡلِكُهُمۡ أَوۡ مُعَذِّبُهُمۡ عَذَابࣰا شَدِیدࣰاۖ قَالُوا۟ مَعۡذِرَةً إِلَىٰ رَبِّكُمۡ وَلَعَلَّهُمۡ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4780, 7, 1119, 165, 'فَلَمَّا نَسُوا۟ مَا ذُكِّرُوا۟ بِهِۦۤ أَنجَیۡنَا ٱلَّذِینَ یَنۡهَوۡنَ عَنِ ٱلسُّوۤءِ وَأَخَذۡنَا ٱلَّذِینَ ظَلَمُوا۟ بِعَذَابِۭ بَـِٔیسِۭ بِمَا كَانُوا۟ یَفۡسُقُونَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4781, 7, 1120, 166, 'فَلَمَّا عَتَوۡا۟ عَن مَّا نُهُوا۟ عَنۡهُ قُلۡنَا لَهُمۡ كُونُوا۟ قِرَدَةً خَـٰسِـِٔینَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4782, 7, 1121, 167, 'وَإِذۡ تَأَذَّنَ رَبُّكَ لَیَبۡعَثَنَّ عَلَیۡهِمۡ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ مَن یَسُومُهُمۡ سُوۤءَ ٱلۡعَذَابِۗ إِنَّ رَبَّكَ لَسَرِیعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4783, 7, 1122, 168, 'وَقَطَّعۡنَـٰهُمۡ فِی ٱلۡأَرۡضِ أُمَمࣰاۖ مِّنۡهُمُ ٱلصَّـٰلِحُونَ وَمِنۡهُمۡ دُونَ ذَ ٰ⁠لِكَۖ وَبَلَوۡنَـٰهُم بِٱلۡحَسَنَـٰتِ وَٱلسَّیِّـَٔاتِ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4784, 7, 1123, 169, 'فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفࣱ وَرِثُوا۟ ٱلۡكِتَـٰبَ یَأۡخُذُونَ عَرَضَ هَـٰذَا ٱلۡأَدۡنَىٰ وَیَقُولُونَ سَیُغۡفَرُ لَنَا وَإِن یَأۡتِهِمۡ عَرَضࣱ مِّثۡلُهُۥ یَأۡخُذُوهُۚ أَلَمۡ یُؤۡخَذۡ عَلَیۡهِم مِّیثَـٰقُ ٱلۡكِتَـٰبِ أَن لَّا یَقُولُوا۟ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّ وَدَرَسُوا۟ مَا فِیهِۗ وَٱلدَّارُ ٱلۡـَٔاخِرَةُ خَیۡرࣱ لِّلَّذِینَ یَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4785, 7, 1124, 170, 'وَٱلَّذِینَ یُمَسِّكُونَ بِٱلۡكِتَـٰبِ وَأَقَامُوا۟ ٱلصَّلَوٰةَ إِنَّا لَا نُضِیعُ أَجۡرَ ٱلۡمُصۡلِحِینَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4786, 7, 1125, 171, '۞ وَإِذۡ نَتَقۡنَا ٱلۡجَبَلَ فَوۡقَهُمۡ كَأَنَّهُۥ ظُلَّةࣱ وَظَنُّوۤا۟ أَنَّهُۥ وَاقِعُۢ بِهِمۡ خُذُوا۟ مَاۤ ءَاتَیۡنَـٰكُم بِقُوَّةࣲ وَٱذۡكُرُوا۟ مَا فِیهِ لَعَلَّكُمۡ تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4787, 7, 1126, 172, 'وَإِذۡ أَخَذَ رَبُّكَ مِنۢ بَنِیۤ ءَادَمَ مِن ظُهُورِهِمۡ ذُرِّیَّتَهُمۡ وَأَشۡهَدَهُمۡ عَلَىٰۤ أَنفُسِهِمۡ أَلَسۡتُ بِرَبِّكُمۡۖ قَالُوا۟ بَلَىٰ شَهِدۡنَاۤۚ أَن تَقُولُوا۟ یَوۡمَ ٱلۡقِیَـٰمَةِ إِنَّا كُنَّا عَنۡ هَـٰذَا غَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:33:00', '2023-05-27 11:18:33'),
(4788, 7, 1127, 173, 'أَوۡ تَقُولُوۤا۟ إِنَّمَاۤ أَشۡرَكَ ءَابَاۤؤُنَا مِن قَبۡلُ وَكُنَّا ذُرِّیَّةࣰ مِّنۢ بَعۡدِهِمۡۖ أَفَتُهۡلِكُنَا بِمَا فَعَلَ ٱلۡمُبۡطِلُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:33'),
(4789, 7, 1128, 174, 'وَكَذَ ٰ⁠لِكَ نُفَصِّلُ ٱلۡـَٔایَـٰتِ وَلَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:33'),
(4790, 7, 1129, 175, 'وَٱتۡلُ عَلَیۡهِمۡ نَبَأَ ٱلَّذِیۤ ءَاتَیۡنَـٰهُ ءَایَـٰتِنَا فَٱنسَلَخَ مِنۡهَا فَأَتۡبَعَهُ ٱلشَّیۡطَـٰنُ فَكَانَ مِنَ ٱلۡغَاوِینَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:33'),
(4791, 7, 1130, 176, 'وَلَوۡ شِئۡنَا لَرَفَعۡنَـٰهُ بِهَا وَلَـٰكِنَّهُۥۤ أَخۡلَدَ إِلَى ٱلۡأَرۡضِ وَٱتَّبَعَ هَوَىٰهُۚ فَمَثَلُهُۥ كَمَثَلِ ٱلۡكَلۡبِ إِن تَحۡمِلۡ عَلَیۡهِ یَلۡهَثۡ أَوۡ تَتۡرُكۡهُ یَلۡهَثۚ ذَّ ٰ⁠لِكَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَاۚ فَٱقۡصُصِ ٱلۡقَصَصَ لَعَلَّهُمۡ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:33'),
(4792, 7, 1131, 177, 'سَاۤءَ مَثَلًا ٱلۡقَوۡمُ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا وَأَنفُسَهُمۡ كَانُوا۟ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:33'),
(4793, 7, 1132, 178, 'مَن یَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِیۖ وَمَن یُضۡلِلۡ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:33'),
(4794, 7, 1133, 179, 'وَلَقَدۡ ذَرَأۡنَا لِجَهَنَّمَ كَثِیرࣰا مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ لَهُمۡ قُلُوبࣱ لَّا یَفۡقَهُونَ بِهَا وَلَهُمۡ أَعۡیُنࣱ لَّا یُبۡصِرُونَ بِهَا وَلَهُمۡ ءَاذَانࣱ لَّا یَسۡمَعُونَ بِهَاۤۚ أُو۟لَـٰۤىِٕكَ كَٱلۡأَنۡعَـٰمِ بَلۡ هُمۡ أَضَلُّۚ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡغَـٰفِلُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:34'),
(4795, 7, 1134, 180, 'وَلِلَّهِ ٱلۡأَسۡمَاۤءُ ٱلۡحُسۡنَىٰ فَٱدۡعُوهُ بِهَاۖ وَذَرُوا۟ ٱلَّذِینَ یُلۡحِدُونَ فِیۤ أَسۡمَـٰۤىِٕهِۦۚ سَیُجۡزَوۡنَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:34'),
(4796, 7, 1135, 181, 'وَمِمَّنۡ خَلَقۡنَاۤ أُمَّةࣱ یَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ یَعۡدِلُونَ\n', NULL, 0, '2023-05-26 17:33:01', '2023-05-27 11:18:34'),
(4797, 7, 1136, 182, 'وَٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا سَنَسۡتَدۡرِجُهُم مِّنۡ حَیۡثُ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4798, 7, 1137, 183, 'وَأُمۡلِی لَهُمۡۚ إِنَّ كَیۡدِی مَتِینٌ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4799, 7, 1138, 184, 'أَوَلَمۡ یَتَفَكَّرُوا۟ۗ مَا بِصَاحِبِهِم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِیرࣱ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4800, 7, 1139, 185, 'أَوَلَمۡ یَنظُرُوا۟ فِی مَلَكُوتِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا خَلَقَ ٱللَّهُ مِن شَیۡءࣲ وَأَنۡ عَسَىٰۤ أَن یَكُونَ قَدِ ٱقۡتَرَبَ أَجَلُهُمۡۖ فَبِأَیِّ حَدِیثِۭ بَعۡدَهُۥ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4801, 7, 1140, 186, 'مَن یُضۡلِلِ ٱللَّهُ فَلَا هَادِیَ لَهُۥۚ وَیَذَرُهُمۡ فِی طُغۡیَـٰنِهِمۡ یَعۡمَهُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4802, 7, 1141, 187, 'یَسۡـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَیَّانَ مُرۡسَىٰهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ رَبِّیۖ لَا یُجَلِّیهَا لِوَقۡتِهَاۤ إِلَّا هُوَۚ ثَقُلَتۡ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ لَا تَأۡتِیكُمۡ إِلَّا بَغۡتَةࣰۗ یَسۡـَٔلُونَكَ كَأَنَّكَ حَفِیٌّ عَنۡهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4803, 7, 1142, 188, 'قُل لَّاۤ أَمۡلِكُ لِنَفۡسِی نَفۡعࣰا وَلَا ضَرًّا إِلَّا مَا شَاۤءَ ٱللَّهُۚ وَلَوۡ كُنتُ أَعۡلَمُ ٱلۡغَیۡبَ لَٱسۡتَكۡثَرۡتُ مِنَ ٱلۡخَیۡرِ وَمَا مَسَّنِیَ ٱلسُّوۤءُۚ إِنۡ أَنَا۠ إِلَّا نَذِیرࣱ وَبَشِیرࣱ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4804, 7, 1143, 189, '۞ هُوَ ٱلَّذِی خَلَقَكُم مِّن نَّفۡسࣲ وَ ٰ⁠حِدَةࣲ وَجَعَلَ مِنۡهَا زَوۡجَهَا لِیَسۡكُنَ إِلَیۡهَاۖ فَلَمَّا تَغَشَّىٰهَا حَمَلَتۡ حَمۡلًا خَفِیفࣰا فَمَرَّتۡ بِهِۦۖ فَلَمَّاۤ أَثۡقَلَت دَّعَوَا ٱللَّهَ رَبَّهُمَا لَىِٕنۡ ءَاتَیۡتَنَا صَـٰلِحࣰا لَّنَكُونَنَّ مِنَ ٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4805, 7, 1144, 190, 'فَلَمَّاۤ ءَاتَىٰهُمَا صَـٰلِحࣰا جَعَلَا لَهُۥ شُرَكَاۤءَ فِیمَاۤ ءَاتَىٰهُمَاۚ فَتَعَـٰلَى ٱللَّهُ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4806, 7, 1145, 191, 'أَیُشۡرِكُونَ مَا لَا یَخۡلُقُ شَیۡـࣰٔا وَهُمۡ یُخۡلَقُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4807, 7, 1146, 192, 'وَلَا یَسۡتَطِیعُونَ لَهُمۡ نَصۡرࣰا وَلَاۤ أَنفُسَهُمۡ یَنصُرُونَ\n', NULL, 0, '2023-05-26 17:33:02', '2023-05-27 11:18:34'),
(4808, 7, 1147, 193, 'وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا یَتَّبِعُوكُمۡۚ سَوَاۤءٌ عَلَیۡكُمۡ أَدَعَوۡتُمُوهُمۡ أَمۡ أَنتُمۡ صَـٰمِتُونَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:34'),
(4809, 7, 1148, 194, 'إِنَّ ٱلَّذِینَ تَدۡعُونَ مِن دُونِ ٱللَّهِ عِبَادٌ أَمۡثَالُكُمۡۖ فَٱدۡعُوهُمۡ فَلۡیَسۡتَجِیبُوا۟ لَكُمۡ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:34'),
(4810, 7, 1149, 195, 'أَلَهُمۡ أَرۡجُلࣱ یَمۡشُونَ بِهَاۤۖ أَمۡ لَهُمۡ أَیۡدࣲ یَبۡطِشُونَ بِهَاۤۖ أَمۡ لَهُمۡ أَعۡیُنࣱ یُبۡصِرُونَ بِهَاۤۖ أَمۡ لَهُمۡ ءَاذَانࣱ یَسۡمَعُونَ بِهَاۗ قُلِ ٱدۡعُوا۟ شُرَكَاۤءَكُمۡ ثُمَّ كِیدُونِ فَلَا تُنظِرُونِ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:34'),
(4811, 7, 1150, 196, 'إِنَّ وَلِـِّۧیَ ٱللَّهُ ٱلَّذِی نَزَّلَ ٱلۡكِتَـٰبَۖ وَهُوَ یَتَوَلَّى ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:34'),
(4812, 7, 1151, 197, 'وَٱلَّذِینَ تَدۡعُونَ مِن دُونِهِۦ لَا یَسۡتَطِیعُونَ نَصۡرَكُمۡ وَلَاۤ أَنفُسَهُمۡ یَنصُرُونَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:35'),
(4813, 7, 1152, 198, 'وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا یَسۡمَعُوا۟ۖ وَتَرَىٰهُمۡ یَنظُرُونَ إِلَیۡكَ وَهُمۡ لَا یُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:35'),
(4814, 7, 1153, 199, 'خُذِ ٱلۡعَفۡوَ وَأۡمُرۡ بِٱلۡعُرۡفِ وَأَعۡرِضۡ عَنِ ٱلۡجَـٰهِلِینَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:35'),
(4815, 7, 1154, 200, 'وَإِمَّا یَنزَغَنَّكَ مِنَ ٱلشَّیۡطَـٰنِ نَزۡغࣱ فَٱسۡتَعِذۡ بِٱللَّهِۚ إِنَّهُۥ سَمِیعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:35'),
(4816, 7, 1155, 201, 'إِنَّ ٱلَّذِینَ ٱتَّقَوۡا۟ إِذَا مَسَّهُمۡ طَـٰۤىِٕفࣱ مِّنَ ٱلشَّیۡطَـٰنِ تَذَكَّرُوا۟ فَإِذَا هُم مُّبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:35'),
(4817, 7, 1156, 202, 'وَإِخۡوَ ٰ⁠نُهُمۡ یَمُدُّونَهُمۡ فِی ٱلۡغَیِّ ثُمَّ لَا یُقۡصِرُونَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:35'),
(4818, 7, 1157, 203, 'وَإِذَا لَمۡ تَأۡتِهِم بِـَٔایَةࣲ قَالُوا۟ لَوۡلَا ٱجۡتَبَیۡتَهَاۚ قُلۡ إِنَّمَاۤ أَتَّبِعُ مَا یُوحَىٰۤ إِلَیَّ مِن رَّبِّیۚ هَـٰذَا بَصَاۤىِٕرُ مِن رَّبِّكُمۡ وَهُدࣰى وَرَحۡمَةࣱ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:03', '2023-05-27 11:18:35'),
(4819, 7, 1158, 204, 'وَإِذَا قُرِئَ ٱلۡقُرۡءَانُ فَٱسۡتَمِعُوا۟ لَهُۥ وَأَنصِتُوا۟ لَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:33:04', '2023-05-27 11:18:35'),
(4820, 7, 1159, 205, 'وَٱذۡكُر رَّبَّكَ فِی نَفۡسِكَ تَضَرُّعࣰا وَخِیفَةࣰ وَدُونَ ٱلۡجَهۡرِ مِنَ ٱلۡقَوۡلِ بِٱلۡغُدُوِّ وَٱلۡـَٔاصَالِ وَلَا تَكُن مِّنَ ٱلۡغَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:33:04', '2023-05-27 11:18:35'),
(4821, 7, 1160, 206, 'إِنَّ ٱلَّذِینَ عِندَ رَبِّكَ لَا یَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَیُسَبِّحُونَهُۥ وَلَهُۥ یَسۡجُدُونَ ۩\n', NULL, 1, '2023-05-26 17:33:04', '2023-05-27 11:18:35'),
(4822, 6, 790, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَجَعَلَ ٱلظُّلُمَـٰتِ وَٱلنُّورَۖ ثُمَّ ٱلَّذِینَ كَفَرُوا۟ بِرَبِّهِمۡ یَعۡدِلُونَ\n', NULL, 0, '2023-05-26 17:33:18', '2023-05-27 11:18:38'),
(4823, 6, 791, 2, 'هُوَ ٱلَّذِی خَلَقَكُم مِّن طِینࣲ ثُمَّ قَضَىٰۤ أَجَلࣰاۖ وَأَجَلࣱ مُّسَمًّى عِندَهُۥۖ ثُمَّ أَنتُمۡ تَمۡتَرُونَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4824, 6, 792, 3, 'وَهُوَ ٱللَّهُ فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَفِی ٱلۡأَرۡضِ یَعۡلَمُ سِرَّكُمۡ وَجَهۡرَكُمۡ وَیَعۡلَمُ مَا تَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4825, 6, 793, 4, 'وَمَا تَأۡتِیهِم مِّنۡ ءَایَةࣲ مِّنۡ ءَایَـٰتِ رَبِّهِمۡ إِلَّا كَانُوا۟ عَنۡهَا مُعۡرِضِینَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4826, 6, 794, 5, 'فَقَدۡ كَذَّبُوا۟ بِٱلۡحَقِّ لَمَّا جَاۤءَهُمۡ فَسَوۡفَ یَأۡتِیهِمۡ أَنۢبَـٰۤؤُا۟ مَا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4827, 6, 795, 6, 'أَلَمۡ یَرَوۡا۟ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنࣲ مَّكَّنَّـٰهُمۡ فِی ٱلۡأَرۡضِ مَا لَمۡ نُمَكِّن لَّكُمۡ وَأَرۡسَلۡنَا ٱلسَّمَاۤءَ عَلَیۡهِم مِّدۡرَارࣰا وَجَعَلۡنَا ٱلۡأَنۡهَـٰرَ تَجۡرِی مِن تَحۡتِهِمۡ فَأَهۡلَكۡنَـٰهُم بِذُنُوبِهِمۡ وَأَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِینَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4828, 6, 796, 7, 'وَلَوۡ نَزَّلۡنَا عَلَیۡكَ كِتَـٰبࣰا فِی قِرۡطَاسࣲ فَلَمَسُوهُ بِأَیۡدِیهِمۡ لَقَالَ ٱلَّذِینَ كَفَرُوۤا۟ إِنۡ هَـٰذَاۤ إِلَّا سِحۡرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4829, 6, 797, 8, 'وَقَالُوا۟ لَوۡلَاۤ أُنزِلَ عَلَیۡهِ مَلَكࣱۖ وَلَوۡ أَنزَلۡنَا مَلَكࣰا لَّقُضِیَ ٱلۡأَمۡرُ ثُمَّ لَا یُنظَرُونَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4830, 6, 798, 9, 'وَلَوۡ جَعَلۡنَـٰهُ مَلَكࣰا لَّجَعَلۡنَـٰهُ رَجُلࣰا وَلَلَبَسۡنَا عَلَیۡهِم مَّا یَلۡبِسُونَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4831, 6, 799, 10, 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلࣲ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِینَ سَخِرُوا۟ مِنۡهُم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4832, 6, 800, 11, 'قُلۡ سِیرُوا۟ فِی ٱلۡأَرۡضِ ثُمَّ ٱنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:33:19', '2023-05-27 11:18:38'),
(4833, 6, 801, 12, 'قُل لِّمَن مَّا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ قُل لِّلَّهِۚ كَتَبَ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَۚ لَیَجۡمَعَنَّكُمۡ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ لَا رَیۡبَ فِیهِۚ ٱلَّذِینَ خَسِرُوۤا۟ أَنفُسَهُمۡ فَهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:38'),
(4834, 6, 802, 13, '۞ وَلَهُۥ مَا سَكَنَ فِی ٱلَّیۡلِ وَٱلنَّهَارِۚ وَهُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:38'),
(4835, 6, 803, 14, 'قُلۡ أَغَیۡرَ ٱللَّهِ أَتَّخِذُ وَلِیࣰّا فَاطِرِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَهُوَ یُطۡعِمُ وَلَا یُطۡعَمُۗ قُلۡ إِنِّیۤ أُمِرۡتُ أَنۡ أَكُونَ أَوَّلَ مَنۡ أَسۡلَمَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4836, 6, 804, 15, 'قُلۡ إِنِّیۤ أَخَافُ إِنۡ عَصَیۡتُ رَبِّی عَذَابَ یَوۡمٍ عَظِیمࣲ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4837, 6, 805, 16, 'مَّن یُصۡرَفۡ عَنۡهُ یَوۡمَىِٕذࣲ فَقَدۡ رَحِمَهُۥۚ وَذَ ٰ⁠لِكَ ٱلۡفَوۡزُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4838, 6, 806, 17, 'وَإِن یَمۡسَسۡكَ ٱللَّهُ بِضُرࣲّ فَلَا كَاشِفَ لَهُۥۤ إِلَّا هُوَۖ وَإِن یَمۡسَسۡكَ بِخَیۡرࣲ فَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4839, 6, 807, 18, 'وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۚ وَهُوَ ٱلۡحَكِیمُ ٱلۡخَبِیرُ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4840, 6, 808, 19, 'قُلۡ أَیُّ شَیۡءٍ أَكۡبَرُ شَهَـٰدَةࣰۖ قُلِ ٱللَّهُۖ شَهِیدُۢ بَیۡنِی وَبَیۡنَكُمۡۚ وَأُوحِیَ إِلَیَّ هَـٰذَا ٱلۡقُرۡءَانُ لِأُنذِرَكُم بِهِۦ وَمَنۢ بَلَغَۚ أَىِٕنَّكُمۡ لَتَشۡهَدُونَ أَنَّ مَعَ ٱللَّهِ ءَالِهَةً أُخۡرَىٰۚ قُل لَّاۤ أَشۡهَدُۚ قُلۡ إِنَّمَا هُوَ إِلَـٰهࣱ وَ ٰ⁠حِدࣱ وَإِنَّنِی بَرِیۤءࣱ مِّمَّا تُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4841, 6, 809, 20, 'ٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ یَعۡرِفُونَهُۥ كَمَا یَعۡرِفُونَ أَبۡنَاۤءَهُمُۘ ٱلَّذِینَ خَسِرُوۤا۟ أَنفُسَهُمۡ فَهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4842, 6, 810, 21, 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔایَـٰتِهِۦۤۚ إِنَّهُۥ لَا یُفۡلِحُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:33:20', '2023-05-27 11:18:39'),
(4843, 6, 811, 22, 'وَیَوۡمَ نَحۡشُرُهُمۡ جَمِیعࣰا ثُمَّ نَقُولُ لِلَّذِینَ أَشۡرَكُوۤا۟ أَیۡنَ شُرَكَاۤؤُكُمُ ٱلَّذِینَ كُنتُمۡ تَزۡعُمُونَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:39'),
(4844, 6, 812, 23, 'ثُمَّ لَمۡ تَكُن فِتۡنَتُهُمۡ إِلَّاۤ أَن قَالُوا۟ وَٱللَّهِ رَبِّنَا مَا كُنَّا مُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:39'),
(4845, 6, 813, 24, 'ٱنظُرۡ كَیۡفَ كَذَبُوا۟ عَلَىٰۤ أَنفُسِهِمۡۚ وَضَلَّ عَنۡهُم مَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:39'),
(4846, 6, 814, 25, 'وَمِنۡهُم مَّن یَسۡتَمِعُ إِلَیۡكَۖ وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن یَفۡقَهُوهُ وَفِیۤ ءَاذَانِهِمۡ وَقۡرࣰاۚ وَإِن یَرَوۡا۟ كُلَّ ءَایَةࣲ لَّا یُؤۡمِنُوا۟ بِهَاۖ حَتَّىٰۤ إِذَا جَاۤءُوكَ یُجَـٰدِلُونَكَ یَقُولُ ٱلَّذِینَ كَفَرُوۤا۟ إِنۡ هَـٰذَاۤ إِلَّاۤ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:39'),
(4847, 6, 815, 26, 'وَهُمۡ یَنۡهَوۡنَ عَنۡهُ وَیَنۡـَٔوۡنَ عَنۡهُۖ وَإِن یُهۡلِكُونَ إِلَّاۤ أَنفُسَهُمۡ وَمَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:39'),
(4848, 6, 816, 27, 'وَلَوۡ تَرَىٰۤ إِذۡ وُقِفُوا۟ عَلَى ٱلنَّارِ فَقَالُوا۟ یَـٰلَیۡتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِـَٔایَـٰتِ رَبِّنَا وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:39'),
(4849, 6, 817, 28, 'بَلۡ بَدَا لَهُم مَّا كَانُوا۟ یُخۡفُونَ مِن قَبۡلُۖ وَلَوۡ رُدُّوا۟ لَعَادُوا۟ لِمَا نُهُوا۟ عَنۡهُ وَإِنَّهُمۡ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:39'),
(4850, 6, 818, 29, 'وَقَالُوۤا۟ إِنۡ هِیَ إِلَّا حَیَاتُنَا ٱلدُّنۡیَا وَمَا نَحۡنُ بِمَبۡعُوثِینَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:40'),
(4851, 6, 819, 30, 'وَلَوۡ تَرَىٰۤ إِذۡ وُقِفُوا۟ عَلَىٰ رَبِّهِمۡۚ قَالَ أَلَیۡسَ هَـٰذَا بِٱلۡحَقِّۚ قَالُوا۟ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُوا۟ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:33:21', '2023-05-27 11:18:40'),
(4852, 6, 820, 31, 'قَدۡ خَسِرَ ٱلَّذِینَ كَذَّبُوا۟ بِلِقَاۤءِ ٱللَّهِۖ حَتَّىٰۤ إِذَا جَاۤءَتۡهُمُ ٱلسَّاعَةُ بَغۡتَةࣰ قَالُوا۟ یَـٰحَسۡرَتَنَا عَلَىٰ مَا فَرَّطۡنَا فِیهَا وَهُمۡ یَحۡمِلُونَ أَوۡزَارَهُمۡ عَلَىٰ ظُهُورِهِمۡۚ أَلَا سَاۤءَ مَا یَزِرُونَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4853, 6, 821, 32, 'وَمَا ٱلۡحَیَوٰةُ ٱلدُّنۡیَاۤ إِلَّا لَعِبࣱ وَلَهۡوࣱۖ وَلَلدَّارُ ٱلۡـَٔاخِرَةُ خَیۡرࣱ لِّلَّذِینَ یَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4854, 6, 822, 33, 'قَدۡ نَعۡلَمُ إِنَّهُۥ لَیَحۡزُنُكَ ٱلَّذِی یَقُولُونَۖ فَإِنَّهُمۡ لَا یُكَذِّبُونَكَ وَلَـٰكِنَّ ٱلظَّـٰلِمِینَ بِـَٔایَـٰتِ ٱللَّهِ یَجۡحَدُونَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4855, 6, 823, 34, 'وَلَقَدۡ كُذِّبَتۡ رُسُلࣱ مِّن قَبۡلِكَ فَصَبَرُوا۟ عَلَىٰ مَا كُذِّبُوا۟ وَأُوذُوا۟ حَتَّىٰۤ أَتَىٰهُمۡ نَصۡرُنَاۚ وَلَا مُبَدِّلَ لِكَلِمَـٰتِ ٱللَّهِۚ وَلَقَدۡ جَاۤءَكَ مِن نَّبَإِی۟ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4856, 6, 824, 35, 'وَإِن كَانَ كَبُرَ عَلَیۡكَ إِعۡرَاضُهُمۡ فَإِنِ ٱسۡتَطَعۡتَ أَن تَبۡتَغِیَ نَفَقࣰا فِی ٱلۡأَرۡضِ أَوۡ سُلَّمࣰا فِی ٱلسَّمَاۤءِ فَتَأۡتِیَهُم بِـَٔایَةࣲۚ وَلَوۡ شَاۤءَ ٱللَّهُ لَجَمَعَهُمۡ عَلَى ٱلۡهُدَىٰۚ فَلَا تَكُونَنَّ مِنَ ٱلۡجَـٰهِلِینَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4857, 6, 825, 36, '۞ إِنَّمَا یَسۡتَجِیبُ ٱلَّذِینَ یَسۡمَعُونَۘ وَٱلۡمَوۡتَىٰ یَبۡعَثُهُمُ ٱللَّهُ ثُمَّ إِلَیۡهِ یُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4858, 6, 826, 37, 'وَقَالُوا۟ لَوۡلَا نُزِّلَ عَلَیۡهِ ءَایَةࣱ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ قَادِرٌ عَلَىٰۤ أَن یُنَزِّلَ ءَایَةࣰ وَلَـٰكِنَّ أَكۡثَرَهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4859, 6, 827, 38, 'وَمَا مِن دَاۤبَّةࣲ فِی ٱلۡأَرۡضِ وَلَا طَـٰۤىِٕرࣲ یَطِیرُ بِجَنَاحَیۡهِ إِلَّاۤ أُمَمٌ أَمۡثَالُكُمۚ مَّا فَرَّطۡنَا فِی ٱلۡكِتَـٰبِ مِن شَیۡءࣲۚ ثُمَّ إِلَىٰ رَبِّهِمۡ یُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4860, 6, 828, 39, 'وَٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا صُمࣱّ وَبُكۡمࣱ فِی ٱلظُّلُمَـٰتِۗ مَن یَشَإِ ٱللَّهُ یُضۡلِلۡهُ وَمَن یَشَأۡ یَجۡعَلۡهُ عَلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4861, 6, 829, 40, 'قُلۡ أَرَءَیۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ أَوۡ أَتَتۡكُمُ ٱلسَّاعَةُ أَغَیۡرَ ٱللَّهِ تَدۡعُونَ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:33:22', '2023-05-27 11:18:40'),
(4862, 6, 830, 41, 'بَلۡ إِیَّاهُ تَدۡعُونَ فَیَكۡشِفُ مَا تَدۡعُونَ إِلَیۡهِ إِن شَاۤءَ وَتَنسَوۡنَ مَا تُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:40'),
(4863, 6, 831, 42, 'وَلَقَدۡ أَرۡسَلۡنَاۤ إِلَىٰۤ أُمَمࣲ مِّن قَبۡلِكَ فَأَخَذۡنَـٰهُم بِٱلۡبَأۡسَاۤءِ وَٱلضَّرَّاۤءِ لَعَلَّهُمۡ یَتَضَرَّعُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:40'),
(4864, 6, 832, 43, 'فَلَوۡلَاۤ إِذۡ جَاۤءَهُم بَأۡسُنَا تَضَرَّعُوا۟ وَلَـٰكِن قَسَتۡ قُلُوبُهُمۡ وَزَیَّنَ لَهُمُ ٱلشَّیۡطَـٰنُ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:40'),
(4865, 6, 833, 44, 'فَلَمَّا نَسُوا۟ مَا ذُكِّرُوا۟ بِهِۦ فَتَحۡنَا عَلَیۡهِمۡ أَبۡوَ ٰ⁠بَ كُلِّ شَیۡءٍ حَتَّىٰۤ إِذَا فَرِحُوا۟ بِمَاۤ أُوتُوۤا۟ أَخَذۡنَـٰهُم بَغۡتَةࣰ فَإِذَا هُم مُّبۡلِسُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:40'),
(4866, 6, 834, 45, 'فَقُطِعَ دَابِرُ ٱلۡقَوۡمِ ٱلَّذِینَ ظَلَمُوا۟ۚ وَٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:40'),
(4867, 6, 835, 46, 'قُلۡ أَرَءَیۡتُمۡ إِنۡ أَخَذَ ٱللَّهُ سَمۡعَكُمۡ وَأَبۡصَـٰرَكُمۡ وَخَتَمَ عَلَىٰ قُلُوبِكُم مَّنۡ إِلَـٰهٌ غَیۡرُ ٱللَّهِ یَأۡتِیكُم بِهِۗ ٱنظُرۡ كَیۡفَ نُصَرِّفُ ٱلۡـَٔایَـٰتِ ثُمَّ هُمۡ یَصۡدِفُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:41'),
(4868, 6, 836, 47, 'قُلۡ أَرَءَیۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ بَغۡتَةً أَوۡ جَهۡرَةً هَلۡ یُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:41'),
(4869, 6, 837, 48, 'وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِینَ إِلَّا مُبَشِّرِینَ وَمُنذِرِینَۖ فَمَنۡ ءَامَنَ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:41'),
(4870, 6, 838, 49, 'وَٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا یَمَسُّهُمُ ٱلۡعَذَابُ بِمَا كَانُوا۟ یَفۡسُقُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:41'),
(4871, 6, 839, 50, 'قُل لَّاۤ أَقُولُ لَكُمۡ عِندِی خَزَاۤىِٕنُ ٱللَّهِ وَلَاۤ أَعۡلَمُ ٱلۡغَیۡبَ وَلَاۤ أَقُولُ لَكُمۡ إِنِّی مَلَكٌۖ إِنۡ أَتَّبِعُ إِلَّا مَا یُوحَىٰۤ إِلَیَّۚ قُلۡ هَلۡ یَسۡتَوِی ٱلۡأَعۡمَىٰ وَٱلۡبَصِیرُۚ أَفَلَا تَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:33:23', '2023-05-27 11:18:41'),
(4872, 6, 840, 51, 'وَأَنذِرۡ بِهِ ٱلَّذِینَ یَخَافُونَ أَن یُحۡشَرُوۤا۟ إِلَىٰ رَبِّهِمۡ لَیۡسَ لَهُم مِّن دُونِهِۦ وَلِیࣱّ وَلَا شَفِیعࣱ لَّعَلَّهُمۡ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4873, 6, 841, 52, 'وَلَا تَطۡرُدِ ٱلَّذِینَ یَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِیِّ یُرِیدُونَ وَجۡهَهُۥۖ مَا عَلَیۡكَ مِنۡ حِسَابِهِم مِّن شَیۡءࣲ وَمَا مِنۡ حِسَابِكَ عَلَیۡهِم مِّن شَیۡءࣲ فَتَطۡرُدَهُمۡ فَتَكُونَ مِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4874, 6, 842, 53, 'وَكَذَ ٰ⁠لِكَ فَتَنَّا بَعۡضَهُم بِبَعۡضࣲ لِّیَقُولُوۤا۟ أَهَـٰۤؤُلَاۤءِ مَنَّ ٱللَّهُ عَلَیۡهِم مِّنۢ بَیۡنِنَاۤۗ أَلَیۡسَ ٱللَّهُ بِأَعۡلَمَ بِٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4875, 6, 843, 54, 'وَإِذَا جَاۤءَكَ ٱلَّذِینَ یُؤۡمِنُونَ بِـَٔایَـٰتِنَا فَقُلۡ سَلَـٰمٌ عَلَیۡكُمۡۖ كَتَبَ رَبُّكُمۡ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَ أَنَّهُۥ مَنۡ عَمِلَ مِنكُمۡ سُوۤءَۢا بِجَهَـٰلَةࣲ ثُمَّ تَابَ مِنۢ بَعۡدِهِۦ وَأَصۡلَحَ فَأَنَّهُۥ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4876, 6, 844, 55, 'وَكَذَ ٰ⁠لِكَ نُفَصِّلُ ٱلۡـَٔایَـٰتِ وَلِتَسۡتَبِینَ سَبِیلُ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4877, 6, 845, 56, 'قُلۡ إِنِّی نُهِیتُ أَنۡ أَعۡبُدَ ٱلَّذِینَ تَدۡعُونَ مِن دُونِ ٱللَّهِۚ قُل لَّاۤ أَتَّبِعُ أَهۡوَاۤءَكُمۡ قَدۡ ضَلَلۡتُ إِذࣰا وَمَاۤ أَنَا۠ مِنَ ٱلۡمُهۡتَدِینَ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4878, 6, 846, 57, 'قُلۡ إِنِّی عَلَىٰ بَیِّنَةࣲ مِّن رَّبِّی وَكَذَّبۡتُم بِهِۦۚ مَا عِندِی مَا تَسۡتَعۡجِلُونَ بِهِۦۤۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ یَقُصُّ ٱلۡحَقَّۖ وَهُوَ خَیۡرُ ٱلۡفَـٰصِلِینَ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4879, 6, 847, 58, 'قُل لَّوۡ أَنَّ عِندِی مَا تَسۡتَعۡجِلُونَ بِهِۦ لَقُضِیَ ٱلۡأَمۡرُ بَیۡنِی وَبَیۡنَكُمۡۗ وَٱللَّهُ أَعۡلَمُ بِٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:33:24', '2023-05-27 11:18:41'),
(4880, 6, 848, 59, '۞ وَعِندَهُۥ مَفَاتِحُ ٱلۡغَیۡبِ لَا یَعۡلَمُهَاۤ إِلَّا هُوَۚ وَیَعۡلَمُ مَا فِی ٱلۡبَرِّ وَٱلۡبَحۡرِۚ وَمَا تَسۡقُطُ مِن وَرَقَةٍ إِلَّا یَعۡلَمُهَا وَلَا حَبَّةࣲ فِی ظُلُمَـٰتِ ٱلۡأَرۡضِ وَلَا رَطۡبࣲ وَلَا یَابِسٍ إِلَّا فِی كِتَـٰبࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:41'),
(4881, 6, 849, 60, 'وَهُوَ ٱلَّذِی یَتَوَفَّىٰكُم بِٱلَّیۡلِ وَیَعۡلَمُ مَا جَرَحۡتُم بِٱلنَّهَارِ ثُمَّ یَبۡعَثُكُمۡ فِیهِ لِیُقۡضَىٰۤ أَجَلࣱ مُّسَمࣰّىۖ ثُمَّ إِلَیۡهِ مَرۡجِعُكُمۡ ثُمَّ یُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:41'),
(4882, 6, 850, 61, 'وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۖ وَیُرۡسِلُ عَلَیۡكُمۡ حَفَظَةً حَتَّىٰۤ إِذَا جَاۤءَ أَحَدَكُمُ ٱلۡمَوۡتُ تَوَفَّتۡهُ رُسُلُنَا وَهُمۡ لَا یُفَرِّطُونَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:41'),
(4883, 6, 851, 62, 'ثُمَّ رُدُّوۤا۟ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۚ أَلَا لَهُ ٱلۡحُكۡمُ وَهُوَ أَسۡرَعُ ٱلۡحَـٰسِبِینَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:41'),
(4884, 6, 852, 63, 'قُلۡ مَن یُنَجِّیكُم مِّن ظُلُمَـٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ تَدۡعُونَهُۥ تَضَرُّعࣰا وَخُفۡیَةࣰ لَّىِٕنۡ أَنجَىٰنَا مِنۡ هَـٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:42'),
(4885, 6, 853, 64, 'قُلِ ٱللَّهُ یُنَجِّیكُم مِّنۡهَا وَمِن كُلِّ كَرۡبࣲ ثُمَّ أَنتُمۡ تُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:42'),
(4886, 6, 854, 65, 'قُلۡ هُوَ ٱلۡقَادِرُ عَلَىٰۤ أَن یَبۡعَثَ عَلَیۡكُمۡ عَذَابࣰا مِّن فَوۡقِكُمۡ أَوۡ مِن تَحۡتِ أَرۡجُلِكُمۡ أَوۡ یَلۡبِسَكُمۡ شِیَعࣰا وَیُذِیقَ بَعۡضَكُم بَأۡسَ بَعۡضٍۗ ٱنظُرۡ كَیۡفَ نُصَرِّفُ ٱلۡـَٔایَـٰتِ لَعَلَّهُمۡ یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:42'),
(4887, 6, 855, 66, 'وَكَذَّبَ بِهِۦ قَوۡمُكَ وَهُوَ ٱلۡحَقُّۚ قُل لَّسۡتُ عَلَیۡكُم بِوَكِیلࣲ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:42'),
(4888, 6, 856, 67, 'لِّكُلِّ نَبَإࣲ مُّسۡتَقَرࣱّۚ وَسَوۡفَ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:42'),
(4889, 6, 857, 68, 'وَإِذَا رَأَیۡتَ ٱلَّذِینَ یَخُوضُونَ فِیۤ ءَایَـٰتِنَا فَأَعۡرِضۡ عَنۡهُمۡ حَتَّىٰ یَخُوضُوا۟ فِی حَدِیثٍ غَیۡرِهِۦۚ وَإِمَّا یُنسِیَنَّكَ ٱلشَّیۡطَـٰنُ فَلَا تَقۡعُدۡ بَعۡدَ ٱلذِّكۡرَىٰ مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:33:25', '2023-05-27 11:18:42'),
(4890, 6, 858, 69, 'وَمَا عَلَى ٱلَّذِینَ یَتَّقُونَ مِنۡ حِسَابِهِم مِّن شَیۡءࣲ وَلَـٰكِن ذِكۡرَىٰ لَعَلَّهُمۡ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4891, 6, 859, 70, 'وَذَرِ ٱلَّذِینَ ٱتَّخَذُوا۟ دِینَهُمۡ لَعِبࣰا وَلَهۡوࣰا وَغَرَّتۡهُمُ ٱلۡحَیَوٰةُ ٱلدُّنۡیَاۚ وَذَكِّرۡ بِهِۦۤ أَن تُبۡسَلَ نَفۡسُۢ بِمَا كَسَبَتۡ لَیۡسَ لَهَا مِن دُونِ ٱللَّهِ وَلِیࣱّ وَلَا شَفِیعࣱ وَإِن تَعۡدِلۡ كُلَّ عَدۡلࣲ لَّا یُؤۡخَذۡ مِنۡهَاۤۗ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ أُبۡسِلُوا۟ بِمَا كَسَبُوا۟ۖ لَهُمۡ شَرَابࣱ مِّنۡ حَمِیمࣲ وَعَذَابٌ أَلِیمُۢ بِمَا كَانُوا۟ یَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4892, 6, 860, 71, 'قُلۡ أَنَدۡعُوا۟ مِن دُونِ ٱللَّهِ مَا لَا یَنفَعُنَا وَلَا یَضُرُّنَا وَنُرَدُّ عَلَىٰۤ أَعۡقَابِنَا بَعۡدَ إِذۡ هَدَىٰنَا ٱللَّهُ كَٱلَّذِی ٱسۡتَهۡوَتۡهُ ٱلشَّیَـٰطِینُ فِی ٱلۡأَرۡضِ حَیۡرَانَ لَهُۥۤ أَصۡحَـٰبࣱ یَدۡعُونَهُۥۤ إِلَى ٱلۡهُدَى ٱئۡتِنَاۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۖ وَأُمِرۡنَا لِنُسۡلِمَ لِرَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4893, 6, 861, 72, 'وَأَنۡ أَقِیمُوا۟ ٱلصَّلَوٰةَ وَٱتَّقُوهُۚ وَهُوَ ٱلَّذِیۤ إِلَیۡهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4894, 6, 862, 73, 'وَهُوَ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ وَیَوۡمَ یَقُولُ كُن فَیَكُونُۚ قَوۡلُهُ ٱلۡحَقُّۚ وَلَهُ ٱلۡمُلۡكُ یَوۡمَ یُنفَخُ فِی ٱلصُّورِۚ عَـٰلِمُ ٱلۡغَیۡبِ وَٱلشَّهَـٰدَةِۚ وَهُوَ ٱلۡحَكِیمُ ٱلۡخَبِیرُ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4895, 6, 863, 74, '۞ وَإِذۡ قَالَ إِبۡرَ ٰ⁠هِیمُ لِأَبِیهِ ءَازَرَ أَتَتَّخِذُ أَصۡنَامًا ءَالِهَةً إِنِّیۤ أَرَىٰكَ وَقَوۡمَكَ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4896, 6, 864, 75, 'وَكَذَ ٰ⁠لِكَ نُرِیۤ إِبۡرَ ٰ⁠هِیمَ مَلَكُوتَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَلِیَكُونَ مِنَ ٱلۡمُوقِنِینَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4897, 6, 865, 76, 'فَلَمَّا جَنَّ عَلَیۡهِ ٱلَّیۡلُ رَءَا كَوۡكَبࣰاۖ قَالَ هَـٰذَا رَبِّیۖ فَلَمَّاۤ أَفَلَ قَالَ لَاۤ أُحِبُّ ٱلۡـَٔافِلِینَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4898, 6, 866, 77, 'فَلَمَّا رَءَا ٱلۡقَمَرَ بَازِغࣰا قَالَ هَـٰذَا رَبِّیۖ فَلَمَّاۤ أَفَلَ قَالَ لَىِٕن لَّمۡ یَهۡدِنِی رَبِّی لَأَكُونَنَّ مِنَ ٱلۡقَوۡمِ ٱلضَّاۤلِّینَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:42'),
(4899, 6, 867, 78, 'فَلَمَّا رَءَا ٱلشَّمۡسَ بَازِغَةࣰ قَالَ هَـٰذَا رَبِّی هَـٰذَاۤ أَكۡبَرُۖ فَلَمَّاۤ أَفَلَتۡ قَالَ یَـٰقَوۡمِ إِنِّی بَرِیۤءࣱ مِّمَّا تُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:33:26', '2023-05-27 11:18:43'),
(4900, 6, 868, 79, 'إِنِّی وَجَّهۡتُ وَجۡهِیَ لِلَّذِی فَطَرَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ حَنِیفࣰاۖ وَمَاۤ أَنَا۠ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4901, 6, 869, 80, 'وَحَاۤجَّهُۥ قَوۡمُهُۥۚ قَالَ أَتُحَـٰۤجُّوۤنِّی فِی ٱللَّهِ وَقَدۡ هَدَىٰنِۚ وَلَاۤ أَخَافُ مَا تُشۡرِكُونَ بِهِۦۤ إِلَّاۤ أَن یَشَاۤءَ رَبِّی شَیۡـࣰٔاۚ وَسِعَ رَبِّی كُلَّ شَیۡءٍ عِلۡمًاۚ أَفَلَا تَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4902, 6, 870, 81, 'وَكَیۡفَ أَخَافُ مَاۤ أَشۡرَكۡتُمۡ وَلَا تَخَافُونَ أَنَّكُمۡ أَشۡرَكۡتُم بِٱللَّهِ مَا لَمۡ یُنَزِّلۡ بِهِۦ عَلَیۡكُمۡ سُلۡطَـٰنࣰاۚ فَأَیُّ ٱلۡفَرِیقَیۡنِ أَحَقُّ بِٱلۡأَمۡنِۖ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4903, 6, 871, 82, 'ٱلَّذِینَ ءَامَنُوا۟ وَلَمۡ یَلۡبِسُوۤا۟ إِیمَـٰنَهُم بِظُلۡمٍ أُو۟لَـٰۤىِٕكَ لَهُمُ ٱلۡأَمۡنُ وَهُم مُّهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4904, 6, 872, 83, 'وَتِلۡكَ حُجَّتُنَاۤ ءَاتَیۡنَـٰهَاۤ إِبۡرَ ٰ⁠هِیمَ عَلَىٰ قَوۡمِهِۦۚ نَرۡفَعُ دَرَجَـٰتࣲ مَّن نَّشَاۤءُۗ إِنَّ رَبَّكَ حَكِیمٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4905, 6, 873, 84, 'وَوَهَبۡنَا لَهُۥۤ إِسۡحَـٰقَ وَیَعۡقُوبَۚ كُلًّا هَدَیۡنَاۚ وَنُوحًا هَدَیۡنَا مِن قَبۡلُۖ وَمِن ذُرِّیَّتِهِۦ دَاوُۥدَ وَسُلَیۡمَـٰنَ وَأَیُّوبَ وَیُوسُفَ وَمُوسَىٰ وَهَـٰرُونَۚ وَكَذَ ٰ⁠لِكَ نَجۡزِی ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4906, 6, 874, 85, 'وَزَكَرِیَّا وَیَحۡیَىٰ وَعِیسَىٰ وَإِلۡیَاسَۖ كُلࣱّ مِّنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4907, 6, 875, 86, 'وَإِسۡمَـٰعِیلَ وَٱلۡیَسَعَ وَیُونُسَ وَلُوطࣰاۚ وَكُلࣰّا فَضَّلۡنَا عَلَى ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4908, 6, 876, 87, 'وَمِنۡ ءَابَاۤىِٕهِمۡ وَذُرِّیَّـٰتِهِمۡ وَإِخۡوَ ٰ⁠نِهِمۡۖ وَٱجۡتَبَیۡنَـٰهُمۡ وَهَدَیۡنَـٰهُمۡ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:33:27', '2023-05-27 11:18:43'),
(4909, 6, 877, 88, 'ذَ ٰ⁠لِكَ هُدَى ٱللَّهِ یَهۡدِی بِهِۦ مَن یَشَاۤءُ مِنۡ عِبَادِهِۦۚ وَلَوۡ أَشۡرَكُوا۟ لَحَبِطَ عَنۡهُم مَّا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:43'),
(4910, 6, 878, 89, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَۚ فَإِن یَكۡفُرۡ بِهَا هَـٰۤؤُلَاۤءِ فَقَدۡ وَكَّلۡنَا بِهَا قَوۡمࣰا لَّیۡسُوا۟ بِهَا بِكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:43'),
(4911, 6, 879, 90, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ هَدَى ٱللَّهُۖ فَبِهُدَىٰهُمُ ٱقۡتَدِهۡۗ قُل لَّاۤ أَسۡـَٔلُكُمۡ عَلَیۡهِ أَجۡرًاۖ إِنۡ هُوَ إِلَّا ذِكۡرَىٰ لِلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:43'),
(4912, 6, 880, 91, 'وَمَا قَدَرُوا۟ ٱللَّهَ حَقَّ قَدۡرِهِۦۤ إِذۡ قَالُوا۟ مَاۤ أَنزَلَ ٱللَّهُ عَلَىٰ بَشَرࣲ مِّن شَیۡءࣲۗ قُلۡ مَنۡ أَنزَلَ ٱلۡكِتَـٰبَ ٱلَّذِی جَاۤءَ بِهِۦ مُوسَىٰ نُورࣰا وَهُدࣰى لِّلنَّاسِۖ تَجۡعَلُونَهُۥ قَرَاطِیسَ تُبۡدُونَهَا وَتُخۡفُونَ كَثِیرࣰاۖ وَعُلِّمۡتُم مَّا لَمۡ تَعۡلَمُوۤا۟ أَنتُمۡ وَلَاۤ ءَابَاۤؤُكُمۡۖ قُلِ ٱللَّهُۖ ثُمَّ ذَرۡهُمۡ فِی خَوۡضِهِمۡ یَلۡعَبُونَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:43'),
(4913, 6, 881, 92, 'وَهَـٰذَا كِتَـٰبٌ أَنزَلۡنَـٰهُ مُبَارَكࣱ مُّصَدِّقُ ٱلَّذِی بَیۡنَ یَدَیۡهِ وَلِتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَاۚ وَٱلَّذِینَ یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ یُؤۡمِنُونَ بِهِۦۖ وَهُمۡ عَلَىٰ صَلَاتِهِمۡ یُحَافِظُونَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:43'),
(4914, 6, 882, 93, 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ قَالَ أُوحِیَ إِلَیَّ وَلَمۡ یُوحَ إِلَیۡهِ شَیۡءࣱ وَمَن قَالَ سَأُنزِلُ مِثۡلَ مَاۤ أَنزَلَ ٱللَّهُۗ وَلَوۡ تَرَىٰۤ إِذِ ٱلظَّـٰلِمُونَ فِی غَمَرَ ٰ⁠تِ ٱلۡمَوۡتِ وَٱلۡمَلَـٰۤىِٕكَةُ بَاسِطُوۤا۟ أَیۡدِیهِمۡ أَخۡرِجُوۤا۟ أَنفُسَكُمُۖ ٱلۡیَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَقُولُونَ عَلَى ٱللَّهِ غَیۡرَ ٱلۡحَقِّ وَكُنتُمۡ عَنۡ ءَایَـٰتِهِۦ تَسۡتَكۡبِرُونَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:43'),
(4915, 6, 883, 94, 'وَلَقَدۡ جِئۡتُمُونَا فُرَ ٰ⁠دَىٰ كَمَا خَلَقۡنَـٰكُمۡ أَوَّلَ مَرَّةࣲ وَتَرَكۡتُم مَّا خَوَّلۡنَـٰكُمۡ وَرَاۤءَ ظُهُورِكُمۡۖ وَمَا نَرَىٰ مَعَكُمۡ شُفَعَاۤءَكُمُ ٱلَّذِینَ زَعَمۡتُمۡ أَنَّهُمۡ فِیكُمۡ شُرَكَـٰۤؤُا۟ۚ لَقَد تَّقَطَّعَ بَیۡنَكُمۡ وَضَلَّ عَنكُم مَّا كُنتُمۡ تَزۡعُمُونَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:44'),
(4916, 6, 884, 95, '۞ إِنَّ ٱللَّهَ فَالِقُ ٱلۡحَبِّ وَٱلنَّوَىٰۖ یُخۡرِجُ ٱلۡحَیَّ مِنَ ٱلۡمَیِّتِ وَمُخۡرِجُ ٱلۡمَیِّتِ مِنَ ٱلۡحَیِّۚ ذَ ٰ⁠لِكُمُ ٱللَّهُۖ فَأَنَّىٰ تُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:44'),
(4917, 6, 885, 96, 'فَالِقُ ٱلۡإِصۡبَاحِ وَجَعَلَ ٱلَّیۡلَ سَكَنࣰا وَٱلشَّمۡسَ وَٱلۡقَمَرَ حُسۡبَانࣰاۚ ذَ ٰ⁠لِكَ تَقۡدِیرُ ٱلۡعَزِیزِ ٱلۡعَلِیمِ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:44'),
(4918, 6, 886, 97, 'وَهُوَ ٱلَّذِی جَعَلَ لَكُمُ ٱلنُّجُومَ لِتَهۡتَدُوا۟ بِهَا فِی ظُلُمَـٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِۗ قَدۡ فَصَّلۡنَا ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:28', '2023-05-27 11:18:44'),
(4919, 6, 887, 98, 'وَهُوَ ٱلَّذِیۤ أَنشَأَكُم مِّن نَّفۡسࣲ وَ ٰ⁠حِدَةࣲ فَمُسۡتَقَرࣱّ وَمُسۡتَوۡدَعࣱۗ قَدۡ فَصَّلۡنَا ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَفۡقَهُونَ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4920, 6, 888, 99, 'وَهُوَ ٱلَّذِیۤ أَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَخۡرَجۡنَا بِهِۦ نَبَاتَ كُلِّ شَیۡءࣲ فَأَخۡرَجۡنَا مِنۡهُ خَضِرࣰا نُّخۡرِجُ مِنۡهُ حَبࣰّا مُّتَرَاكِبࣰا وَمِنَ ٱلنَّخۡلِ مِن طَلۡعِهَا قِنۡوَانࣱ دَانِیَةࣱ وَجَنَّـٰتࣲ مِّنۡ أَعۡنَابࣲ وَٱلزَّیۡتُونَ وَٱلرُّمَّانَ مُشۡتَبِهࣰا وَغَیۡرَ مُتَشَـٰبِهٍۗ ٱنظُرُوۤا۟ إِلَىٰ ثَمَرِهِۦۤ إِذَاۤ أَثۡمَرَ وَیَنۡعِهِۦۤۚ إِنَّ فِی ذَ ٰ⁠لِكُمۡ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4921, 6, 889, 100, 'وَجَعَلُوا۟ لِلَّهِ شُرَكَاۤءَ ٱلۡجِنَّ وَخَلَقَهُمۡۖ وَخَرَقُوا۟ لَهُۥ بَنِینَ وَبَنَـٰتِۭ بِغَیۡرِ عِلۡمࣲۚ سُبۡحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا یَصِفُونَ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4922, 6, 890, 101, 'بَدِیعُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ أَنَّىٰ یَكُونُ لَهُۥ وَلَدࣱ وَلَمۡ تَكُن لَّهُۥ صَـٰحِبَةࣱۖ وَخَلَقَ كُلَّ شَیۡءࣲۖ وَهُوَ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4923, 6, 891, 102, 'ذَ ٰ⁠لِكُمُ ٱللَّهُ رَبُّكُمۡۖ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ خَـٰلِقُ كُلِّ شَیۡءࣲ فَٱعۡبُدُوهُۚ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ وَكِیلࣱ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4924, 6, 892, 103, 'لَّا تُدۡرِكُهُ ٱلۡأَبۡصَـٰرُ وَهُوَ یُدۡرِكُ ٱلۡأَبۡصَـٰرَۖ وَهُوَ ٱللَّطِیفُ ٱلۡخَبِیرُ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4925, 6, 893, 104, 'قَدۡ جَاۤءَكُم بَصَاۤىِٕرُ مِن رَّبِّكُمۡۖ فَمَنۡ أَبۡصَرَ فَلِنَفۡسِهِۦۖ وَمَنۡ عَمِیَ فَعَلَیۡهَاۚ وَمَاۤ أَنَا۠ عَلَیۡكُم بِحَفِیظࣲ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4926, 6, 894, 105, 'وَكَذَ ٰ⁠لِكَ نُصَرِّفُ ٱلۡـَٔایَـٰتِ وَلِیَقُولُوا۟ دَرَسۡتَ وَلِنُبَیِّنَهُۥ لِقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4927, 6, 895, 106, 'ٱتَّبِعۡ مَاۤ أُوحِیَ إِلَیۡكَ مِن رَّبِّكَۖ لَاۤ إِلَـٰهَ إِلَّا هُوَۖ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4928, 6, 896, 107, 'وَلَوۡ شَاۤءَ ٱللَّهُ مَاۤ أَشۡرَكُوا۟ۗ وَمَا جَعَلۡنَـٰكَ عَلَیۡهِمۡ حَفِیظࣰاۖ وَمَاۤ أَنتَ عَلَیۡهِم بِوَكِیلࣲ\n', NULL, 0, '2023-05-26 17:33:29', '2023-05-27 11:18:44'),
(4929, 6, 897, 108, 'وَلَا تَسُبُّوا۟ ٱلَّذِینَ یَدۡعُونَ مِن دُونِ ٱللَّهِ فَیَسُبُّوا۟ ٱللَّهَ عَدۡوَۢا بِغَیۡرِ عِلۡمࣲۗ كَذَ ٰ⁠لِكَ زَیَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمۡ ثُمَّ إِلَىٰ رَبِّهِم مَّرۡجِعُهُمۡ فَیُنَبِّئُهُم بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:30', '2023-05-27 11:18:44'),
(4930, 6, 898, 109, 'وَأَقۡسَمُوا۟ بِٱللَّهِ جَهۡدَ أَیۡمَـٰنِهِمۡ لَىِٕن جَاۤءَتۡهُمۡ ءَایَةࣱ لَّیُؤۡمِنُنَّ بِهَاۚ قُلۡ إِنَّمَا ٱلۡـَٔایَـٰتُ عِندَ ٱللَّهِۖ وَمَا یُشۡعِرُكُمۡ أَنَّهَاۤ إِذَا جَاۤءَتۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:30', '2023-05-27 11:18:44'),
(4931, 6, 899, 110, 'وَنُقَلِّبُ أَفۡـِٔدَتَهُمۡ وَأَبۡصَـٰرَهُمۡ كَمَا لَمۡ یُؤۡمِنُوا۟ بِهِۦۤ أَوَّلَ مَرَّةࣲ وَنَذَرُهُمۡ فِی طُغۡیَـٰنِهِمۡ یَعۡمَهُونَ\n', NULL, 0, '2023-05-26 17:33:30', '2023-05-27 11:18:44'),
(4932, 6, 900, 111, '۞ وَلَوۡ أَنَّنَا نَزَّلۡنَاۤ إِلَیۡهِمُ ٱلۡمَلَـٰۤىِٕكَةَ وَكَلَّمَهُمُ ٱلۡمَوۡتَىٰ وَحَشَرۡنَا عَلَیۡهِمۡ كُلَّ شَیۡءࣲ قُبُلࣰا مَّا كَانُوا۟ لِیُؤۡمِنُوۤا۟ إِلَّاۤ أَن یَشَاۤءَ ٱللَّهُ وَلَـٰكِنَّ أَكۡثَرَهُمۡ یَجۡهَلُونَ\n', NULL, 0, '2023-05-26 17:33:30', '2023-05-27 11:18:45'),
(4933, 6, 901, 112, 'وَكَذَ ٰ⁠لِكَ جَعَلۡنَا لِكُلِّ نَبِیٍّ عَدُوࣰّا شَیَـٰطِینَ ٱلۡإِنسِ وَٱلۡجِنِّ یُوحِی بَعۡضُهُمۡ إِلَىٰ بَعۡضࣲ زُخۡرُفَ ٱلۡقَوۡلِ غُرُورࣰاۚ وَلَوۡ شَاۤءَ رَبُّكَ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:33:30', '2023-05-27 11:18:45'),
(4934, 6, 902, 113, 'وَلِتَصۡغَىٰۤ إِلَیۡهِ أَفۡـِٔدَةُ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ وَلِیَرۡضَوۡهُ وَلِیَقۡتَرِفُوا۟ مَا هُم مُّقۡتَرِفُونَ\n', NULL, 0, '2023-05-26 17:33:30', '2023-05-27 11:18:45'),
(4935, 6, 903, 114, 'أَفَغَیۡرَ ٱللَّهِ أَبۡتَغِی حَكَمࣰا وَهُوَ ٱلَّذِیۤ أَنزَلَ إِلَیۡكُمُ ٱلۡكِتَـٰبَ مُفَصَّلࣰاۚ وَٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ یَعۡلَمُونَ أَنَّهُۥ مُنَزَّلࣱ مِّن رَّبِّكَ بِٱلۡحَقِّۖ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِینَ\n', NULL, 0, '2023-05-26 17:33:30', '2023-05-27 11:18:45'),
(4936, 6, 904, 115, 'وَتَمَّتۡ كَلِمَتُ رَبِّكَ صِدۡقࣰا وَعَدۡلࣰاۚ لَّا مُبَدِّلَ لِكَلِمَـٰتِهِۦۚ وَهُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:33:31', '2023-05-27 11:18:45'),
(4937, 6, 905, 116, 'وَإِن تُطِعۡ أَكۡثَرَ مَن فِی ٱلۡأَرۡضِ یُضِلُّوكَ عَن سَبِیلِ ٱللَّهِۚ إِن یَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا یَخۡرُصُونَ\n', NULL, 0, '2023-05-26 17:33:31', '2023-05-27 11:18:45'),
(4938, 6, 906, 117, 'إِنَّ رَبَّكَ هُوَ أَعۡلَمُ مَن یَضِلُّ عَن سَبِیلِهِۦۖ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِینَ\n', NULL, 0, '2023-05-26 17:33:31', '2023-05-27 11:18:45'),
(4939, 6, 907, 118, 'فَكُلُوا۟ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَیۡهِ إِن كُنتُم بِـَٔایَـٰتِهِۦ مُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:33:31', '2023-05-27 11:18:45'),
(4940, 6, 908, 119, 'وَمَا لَكُمۡ أَلَّا تَأۡكُلُوا۟ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَیۡهِ وَقَدۡ فَصَّلَ لَكُم مَّا حَرَّمَ عَلَیۡكُمۡ إِلَّا مَا ٱضۡطُرِرۡتُمۡ إِلَیۡهِۗ وَإِنَّ كَثِیرࣰا لَّیُضِلُّونَ بِأَهۡوَاۤىِٕهِم بِغَیۡرِ عِلۡمٍۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِٱلۡمُعۡتَدِینَ\n', NULL, 0, '2023-05-26 17:33:31', '2023-05-27 11:18:45'),
(4941, 6, 909, 120, 'وَذَرُوا۟ ظَـٰهِرَ ٱلۡإِثۡمِ وَبَاطِنَهُۥۤۚ إِنَّ ٱلَّذِینَ یَكۡسِبُونَ ٱلۡإِثۡمَ سَیُجۡزَوۡنَ بِمَا كَانُوا۟ یَقۡتَرِفُونَ\n', NULL, 0, '2023-05-26 17:33:31', '2023-05-27 11:18:45'),
(4942, 6, 910, 121, 'وَلَا تَأۡكُلُوا۟ مِمَّا لَمۡ یُذۡكَرِ ٱسۡمُ ٱللَّهِ عَلَیۡهِ وَإِنَّهُۥ لَفِسۡقࣱۗ وَإِنَّ ٱلشَّیَـٰطِینَ لَیُوحُونَ إِلَىٰۤ أَوۡلِیَاۤىِٕهِمۡ لِیُجَـٰدِلُوكُمۡۖ وَإِنۡ أَطَعۡتُمُوهُمۡ إِنَّكُمۡ لَمُشۡرِكُونَ\n', NULL, 0, '2023-05-26 17:33:32', '2023-05-27 11:18:45'),
(4943, 6, 911, 122, 'أَوَمَن كَانَ مَیۡتࣰا فَأَحۡیَیۡنَـٰهُ وَجَعَلۡنَا لَهُۥ نُورࣰا یَمۡشِی بِهِۦ فِی ٱلنَّاسِ كَمَن مَّثَلُهُۥ فِی ٱلظُّلُمَـٰتِ لَیۡسَ بِخَارِجࣲ مِّنۡهَاۚ كَذَ ٰ⁠لِكَ زُیِّنَ لِلۡكَـٰفِرِینَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:32', '2023-05-27 11:18:45'),
(4944, 6, 912, 123, 'وَكَذَ ٰ⁠لِكَ جَعَلۡنَا فِی كُلِّ قَرۡیَةٍ أَكَـٰبِرَ مُجۡرِمِیهَا لِیَمۡكُرُوا۟ فِیهَاۖ وَمَا یَمۡكُرُونَ إِلَّا بِأَنفُسِهِمۡ وَمَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:33:32', '2023-05-27 11:18:45'),
(4945, 6, 913, 124, 'وَإِذَا جَاۤءَتۡهُمۡ ءَایَةࣱ قَالُوا۟ لَن نُّؤۡمِنَ حَتَّىٰ نُؤۡتَىٰ مِثۡلَ مَاۤ أُوتِیَ رُسُلُ ٱللَّهِۘ ٱللَّهُ أَعۡلَمُ حَیۡثُ یَجۡعَلُ رِسَالَتَهُۥۗ سَیُصِیبُ ٱلَّذِینَ أَجۡرَمُوا۟ صَغَارٌ عِندَ ٱللَّهِ وَعَذَابࣱ شَدِیدُۢ بِمَا كَانُوا۟ یَمۡكُرُونَ\n', NULL, 0, '2023-05-26 17:33:32', '2023-05-27 11:18:45'),
(4946, 6, 914, 125, 'فَمَن یُرِدِ ٱللَّهُ أَن یَهۡدِیَهُۥ یَشۡرَحۡ صَدۡرَهُۥ لِلۡإِسۡلَـٰمِۖ وَمَن یُرِدۡ أَن یُضِلَّهُۥ یَجۡعَلۡ صَدۡرَهُۥ ضَیِّقًا حَرَجࣰا كَأَنَّمَا یَصَّعَّدُ فِی ٱلسَّمَاۤءِۚ كَذَ ٰ⁠لِكَ یَجۡعَلُ ٱللَّهُ ٱلرِّجۡسَ عَلَى ٱلَّذِینَ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:32', '2023-05-27 11:18:45'),
(4947, 6, 915, 126, 'وَهَـٰذَا صِرَ ٰ⁠طُ رَبِّكَ مُسۡتَقِیمࣰاۗ قَدۡ فَصَّلۡنَا ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یَذَّكَّرُونَ\n', NULL, 0, '2023-05-26 17:33:32', '2023-05-27 11:18:45'),
(4948, 6, 916, 127, '۞ لَهُمۡ دَارُ ٱلسَّلَـٰمِ عِندَ رَبِّهِمۡۖ وَهُوَ وَلِیُّهُم بِمَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:32', '2023-05-27 11:18:45'),
(4949, 6, 917, 128, 'وَیَوۡمَ یَحۡشُرُهُمۡ جَمِیعࣰا یَـٰمَعۡشَرَ ٱلۡجِنِّ قَدِ ٱسۡتَكۡثَرۡتُم مِّنَ ٱلۡإِنسِۖ وَقَالَ أَوۡلِیَاۤؤُهُم مِّنَ ٱلۡإِنسِ رَبَّنَا ٱسۡتَمۡتَعَ بَعۡضُنَا بِبَعۡضࣲ وَبَلَغۡنَاۤ أَجَلَنَا ٱلَّذِیۤ أَجَّلۡتَ لَنَاۚ قَالَ ٱلنَّارُ مَثۡوَىٰكُمۡ خَـٰلِدِینَ فِیهَاۤ إِلَّا مَا شَاۤءَ ٱللَّهُۗ إِنَّ رَبَّكَ حَكِیمٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4950, 6, 918, 129, 'وَكَذَ ٰ⁠لِكَ نُوَلِّی بَعۡضَ ٱلظَّـٰلِمِینَ بَعۡضَۢا بِمَا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4951, 6, 919, 130, 'یَـٰمَعۡشَرَ ٱلۡجِنِّ وَٱلۡإِنسِ أَلَمۡ یَأۡتِكُمۡ رُسُلࣱ مِّنكُمۡ یَقُصُّونَ عَلَیۡكُمۡ ءَایَـٰتِی وَیُنذِرُونَكُمۡ لِقَاۤءَ یَوۡمِكُمۡ هَـٰذَاۚ قَالُوا۟ شَهِدۡنَا عَلَىٰۤ أَنفُسِنَاۖ وَغَرَّتۡهُمُ ٱلۡحَیَوٰةُ ٱلدُّنۡیَا وَشَهِدُوا۟ عَلَىٰۤ أَنفُسِهِمۡ أَنَّهُمۡ كَانُوا۟ كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4952, 6, 920, 131, 'ذَ ٰ⁠لِكَ أَن لَّمۡ یَكُن رَّبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمࣲ وَأَهۡلُهَا غَـٰفِلُونَ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4953, 6, 921, 132, 'وَلِكُلࣲّ دَرَجَـٰتࣱ مِّمَّا عَمِلُوا۟ۚ وَمَا رَبُّكَ بِغَـٰفِلٍ عَمَّا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4954, 6, 922, 133, 'وَرَبُّكَ ٱلۡغَنِیُّ ذُو ٱلرَّحۡمَةِۚ إِن یَشَأۡ یُذۡهِبۡكُمۡ وَیَسۡتَخۡلِفۡ مِنۢ بَعۡدِكُم مَّا یَشَاۤءُ كَمَاۤ أَنشَأَكُم مِّن ذُرِّیَّةِ قَوۡمٍ ءَاخَرِینَ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4955, 6, 923, 134, 'إِنَّ مَا تُوعَدُونَ لَـَٔاتࣲۖ وَمَاۤ أَنتُم بِمُعۡجِزِینَ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4956, 6, 924, 135, 'قُلۡ یَـٰقَوۡمِ ٱعۡمَلُوا۟ عَلَىٰ مَكَانَتِكُمۡ إِنِّی عَامِلࣱۖ فَسَوۡفَ تَعۡلَمُونَ مَن تَكُونُ لَهُۥ عَـٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا یُفۡلِحُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:33:33', '2023-05-27 11:18:46'),
(4957, 6, 925, 136, 'وَجَعَلُوا۟ لِلَّهِ مِمَّا ذَرَأَ مِنَ ٱلۡحَرۡثِ وَٱلۡأَنۡعَـٰمِ نَصِیبࣰا فَقَالُوا۟ هَـٰذَا لِلَّهِ بِزَعۡمِهِمۡ وَهَـٰذَا لِشُرَكَاۤىِٕنَاۖ فَمَا كَانَ لِشُرَكَاۤىِٕهِمۡ فَلَا یَصِلُ إِلَى ٱللَّهِۖ وَمَا كَانَ لِلَّهِ فَهُوَ یَصِلُ إِلَىٰ شُرَكَاۤىِٕهِمۡۗ سَاۤءَ مَا یَحۡكُمُونَ\n', NULL, 0, '2023-05-26 17:33:34', '2023-05-27 11:18:46'),
(4958, 6, 926, 137, 'وَكَذَ ٰ⁠لِكَ زَیَّنَ لِكَثِیرࣲ مِّنَ ٱلۡمُشۡرِكِینَ قَتۡلَ أَوۡلَـٰدِهِمۡ شُرَكَاۤؤُهُمۡ لِیُرۡدُوهُمۡ وَلِیَلۡبِسُوا۟ عَلَیۡهِمۡ دِینَهُمۡۖ وَلَوۡ شَاۤءَ ٱللَّهُ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:33:34', '2023-05-27 11:18:46'),
(4959, 6, 927, 138, 'وَقَالُوا۟ هَـٰذِهِۦۤ أَنۡعَـٰمࣱ وَحَرۡثٌ حِجۡرࣱ لَّا یَطۡعَمُهَاۤ إِلَّا مَن نَّشَاۤءُ بِزَعۡمِهِمۡ وَأَنۡعَـٰمٌ حُرِّمَتۡ ظُهُورُهَا وَأَنۡعَـٰمࣱ لَّا یَذۡكُرُونَ ٱسۡمَ ٱللَّهِ عَلَیۡهَا ٱفۡتِرَاۤءً عَلَیۡهِۚ سَیَجۡزِیهِم بِمَا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:33:34', '2023-05-27 11:18:46'),
(4960, 6, 928, 139, 'وَقَالُوا۟ مَا فِی بُطُونِ هَـٰذِهِ ٱلۡأَنۡعَـٰمِ خَالِصَةࣱ لِّذُكُورِنَا وَمُحَرَّمٌ عَلَىٰۤ أَزۡوَ ٰ⁠جِنَاۖ وَإِن یَكُن مَّیۡتَةࣰ فَهُمۡ فِیهِ شُرَكَاۤءُۚ سَیَجۡزِیهِمۡ وَصۡفَهُمۡۚ إِنَّهُۥ حَكِیمٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:33:34', '2023-05-27 11:18:46'),
(4961, 6, 929, 140, 'قَدۡ خَسِرَ ٱلَّذِینَ قَتَلُوۤا۟ أَوۡلَـٰدَهُمۡ سَفَهَۢا بِغَیۡرِ عِلۡمࣲ وَحَرَّمُوا۟ مَا رَزَقَهُمُ ٱللَّهُ ٱفۡتِرَاۤءً عَلَى ٱللَّهِۚ قَدۡ ضَلُّوا۟ وَمَا كَانُوا۟ مُهۡتَدِینَ\n', NULL, 0, '2023-05-26 17:33:34', '2023-05-27 11:18:46'),
(4962, 6, 930, 141, '۞ وَهُوَ ٱلَّذِیۤ أَنشَأَ جَنَّـٰتࣲ مَّعۡرُوشَـٰتࣲ وَغَیۡرَ مَعۡرُوشَـٰتࣲ وَٱلنَّخۡلَ وَٱلزَّرۡعَ مُخۡتَلِفًا أُكُلُهُۥ وَٱلزَّیۡتُونَ وَٱلرُّمَّانَ مُتَشَـٰبِهࣰا وَغَیۡرَ مُتَشَـٰبِهࣲۚ كُلُوا۟ مِن ثَمَرِهِۦۤ إِذَاۤ أَثۡمَرَ وَءَاتُوا۟ حَقَّهُۥ یَوۡمَ حَصَادِهِۦۖ وَلَا تُسۡرِفُوۤا۟ۚ إِنَّهُۥ لَا یُحِبُّ ٱلۡمُسۡرِفِینَ\n', NULL, 0, '2023-05-26 17:33:34', '2023-05-27 11:18:46'),
(4963, 6, 931, 142, 'وَمِنَ ٱلۡأَنۡعَـٰمِ حَمُولَةࣰ وَفَرۡشࣰاۚ كُلُوا۟ مِمَّا رَزَقَكُمُ ٱللَّهُ وَلَا تَتَّبِعُوا۟ خُطُوَ ٰ⁠تِ ٱلشَّیۡطَـٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوࣱّ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:33:34', '2023-05-27 11:18:46'),
(4964, 6, 932, 143, 'ثَمَـٰنِیَةَ أَزۡوَ ٰ⁠جࣲۖ مِّنَ ٱلضَّأۡنِ ٱثۡنَیۡنِ وَمِنَ ٱلۡمَعۡزِ ٱثۡنَیۡنِۗ قُلۡ ءَاۤلذَّكَرَیۡنِ حَرَّمَ أَمِ ٱلۡأُنثَیَیۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَیۡهِ أَرۡحَامُ ٱلۡأُنثَیَیۡنِۖ نَبِّـُٔونِی بِعِلۡمٍ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:46'),
(4965, 6, 933, 144, 'وَمِنَ ٱلۡإِبِلِ ٱثۡنَیۡنِ وَمِنَ ٱلۡبَقَرِ ٱثۡنَیۡنِۗ قُلۡ ءَاۤلذَّكَرَیۡنِ حَرَّمَ أَمِ ٱلۡأُنثَیَیۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَیۡهِ أَرۡحَامُ ٱلۡأُنثَیَیۡنِۖ أَمۡ كُنتُمۡ شُهَدَاۤءَ إِذۡ وَصَّىٰكُمُ ٱللَّهُ بِهَـٰذَاۚ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبࣰا لِّیُضِلَّ ٱلنَّاسَ بِغَیۡرِ عِلۡمٍۚ إِنَّ ٱللَّهَ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:46'),
(4966, 6, 934, 145, 'قُل لَّاۤ أَجِدُ فِی مَاۤ أُوحِیَ إِلَیَّ مُحَرَّمًا عَلَىٰ طَاعِمࣲ یَطۡعَمُهُۥۤ إِلَّاۤ أَن یَكُونَ مَیۡتَةً أَوۡ دَمࣰا مَّسۡفُوحًا أَوۡ لَحۡمَ خِنزِیرࣲ فَإِنَّهُۥ رِجۡسٌ أَوۡ فِسۡقًا أُهِلَّ لِغَیۡرِ ٱللَّهِ بِهِۦۚ فَمَنِ ٱضۡطُرَّ غَیۡرَ بَاغࣲ وَلَا عَادࣲ فَإِنَّ رَبَّكَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4967, 6, 935, 146, 'وَعَلَى ٱلَّذِینَ هَادُوا۟ حَرَّمۡنَا كُلَّ ذِی ظُفُرࣲۖ وَمِنَ ٱلۡبَقَرِ وَٱلۡغَنَمِ حَرَّمۡنَا عَلَیۡهِمۡ شُحُومَهُمَاۤ إِلَّا مَا حَمَلَتۡ ظُهُورُهُمَاۤ أَوِ ٱلۡحَوَایَاۤ أَوۡ مَا ٱخۡتَلَطَ بِعَظۡمࣲۚ ذَ ٰ⁠لِكَ جَزَیۡنَـٰهُم بِبَغۡیِهِمۡۖ وَإِنَّا لَصَـٰدِقُونَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4968, 6, 936, 147, 'فَإِن كَذَّبُوكَ فَقُل رَّبُّكُمۡ ذُو رَحۡمَةࣲ وَ ٰ⁠سِعَةࣲ وَلَا یُرَدُّ بَأۡسُهُۥ عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4969, 6, 937, 148, 'سَیَقُولُ ٱلَّذِینَ أَشۡرَكُوا۟ لَوۡ شَاۤءَ ٱللَّهُ مَاۤ أَشۡرَكۡنَا وَلَاۤ ءَابَاۤؤُنَا وَلَا حَرَّمۡنَا مِن شَیۡءࣲۚ كَذَ ٰ⁠لِكَ كَذَّبَ ٱلَّذِینَ مِن قَبۡلِهِمۡ حَتَّىٰ ذَاقُوا۟ بَأۡسَنَاۗ قُلۡ هَلۡ عِندَكُم مِّنۡ عِلۡمࣲ فَتُخۡرِجُوهُ لَنَاۤۖ إِن تَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ أَنتُمۡ إِلَّا تَخۡرُصُونَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4970, 6, 938, 149, 'قُلۡ فَلِلَّهِ ٱلۡحُجَّةُ ٱلۡبَـٰلِغَةُۖ فَلَوۡ شَاۤءَ لَهَدَىٰكُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4971, 6, 939, 150, 'قُلۡ هَلُمَّ شُهَدَاۤءَكُمُ ٱلَّذِینَ یَشۡهَدُونَ أَنَّ ٱللَّهَ حَرَّمَ هَـٰذَاۖ فَإِن شَهِدُوا۟ فَلَا تَشۡهَدۡ مَعَهُمۡۚ وَلَا تَتَّبِعۡ أَهۡوَاۤءَ ٱلَّذِینَ كَذَّبُوا۟ بِـَٔایَـٰتِنَا وَٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ وَهُم بِرَبِّهِمۡ یَعۡدِلُونَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4972, 6, 940, 151, '۞ قُلۡ تَعَالَوۡا۟ أَتۡلُ مَا حَرَّمَ رَبُّكُمۡ عَلَیۡكُمۡۖ أَلَّا تُشۡرِكُوا۟ بِهِۦ شَیۡـࣰٔاۖ وَبِٱلۡوَ ٰ⁠لِدَیۡنِ إِحۡسَـٰنࣰاۖ وَلَا تَقۡتُلُوۤا۟ أَوۡلَـٰدَكُم مِّنۡ إِمۡلَـٰقࣲ نَّحۡنُ نَرۡزُقُكُمۡ وَإِیَّاهُمۡۖ وَلَا تَقۡرَبُوا۟ ٱلۡفَوَ ٰ⁠حِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَۖ وَلَا تَقۡتُلُوا۟ ٱلنَّفۡسَ ٱلَّتِی حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۚ ذَ ٰ⁠لِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4973, 6, 941, 152, 'وَلَا تَقۡرَبُوا۟ مَالَ ٱلۡیَتِیمِ إِلَّا بِٱلَّتِی هِیَ أَحۡسَنُ حَتَّىٰ یَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُوا۟ ٱلۡكَیۡلَ وَٱلۡمِیزَانَ بِٱلۡقِسۡطِۖ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۖ وَإِذَا قُلۡتُمۡ فَٱعۡدِلُوا۟ وَلَوۡ كَانَ ذَا قُرۡبَىٰۖ وَبِعَهۡدِ ٱللَّهِ أَوۡفُوا۟ۚ ذَ ٰ⁠لِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4974, 6, 942, 153, 'وَأَنَّ هَـٰذَا صِرَ ٰ⁠طِی مُسۡتَقِیمࣰا فَٱتَّبِعُوهُۖ وَلَا تَتَّبِعُوا۟ ٱلسُّبُلَ فَتَفَرَّقَ بِكُمۡ عَن سَبِیلِهِۦۚ ذَ ٰ⁠لِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:33:35', '2023-05-27 11:18:47'),
(4975, 6, 943, 154, 'ثُمَّ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ تَمَامًا عَلَى ٱلَّذِیۤ أَحۡسَنَ وَتَفۡصِیلࣰا لِّكُلِّ شَیۡءࣲ وَهُدࣰى وَرَحۡمَةࣰ لَّعَلَّهُم بِلِقَاۤءِ رَبِّهِمۡ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:47'),
(4976, 6, 944, 155, 'وَهَـٰذَا كِتَـٰبٌ أَنزَلۡنَـٰهُ مُبَارَكࣱ فَٱتَّبِعُوهُ وَٱتَّقُوا۟ لَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:47');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(4977, 6, 945, 156, 'أَن تَقُولُوۤا۟ إِنَّمَاۤ أُنزِلَ ٱلۡكِتَـٰبُ عَلَىٰ طَاۤىِٕفَتَیۡنِ مِن قَبۡلِنَا وَإِن كُنَّا عَن دِرَاسَتِهِمۡ لَغَـٰفِلِینَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:47'),
(4978, 6, 946, 157, 'أَوۡ تَقُولُوا۟ لَوۡ أَنَّاۤ أُنزِلَ عَلَیۡنَا ٱلۡكِتَـٰبُ لَكُنَّاۤ أَهۡدَىٰ مِنۡهُمۡۚ فَقَدۡ جَاۤءَكُم بَیِّنَةࣱ مِّن رَّبِّكُمۡ وَهُدࣰى وَرَحۡمَةࣱۚ فَمَنۡ أَظۡلَمُ مِمَّن كَذَّبَ بِـَٔایَـٰتِ ٱللَّهِ وَصَدَفَ عَنۡهَاۗ سَنَجۡزِی ٱلَّذِینَ یَصۡدِفُونَ عَنۡ ءَایَـٰتِنَا سُوۤءَ ٱلۡعَذَابِ بِمَا كَانُوا۟ یَصۡدِفُونَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:47'),
(4979, 6, 947, 158, 'هَلۡ یَنظُرُونَ إِلَّاۤ أَن تَأۡتِیَهُمُ ٱلۡمَلَـٰۤىِٕكَةُ أَوۡ یَأۡتِیَ رَبُّكَ أَوۡ یَأۡتِیَ بَعۡضُ ءَایَـٰتِ رَبِّكَۗ یَوۡمَ یَأۡتِی بَعۡضُ ءَایَـٰتِ رَبِّكَ لَا یَنفَعُ نَفۡسًا إِیمَـٰنُهَا لَمۡ تَكُنۡ ءَامَنَتۡ مِن قَبۡلُ أَوۡ كَسَبَتۡ فِیۤ إِیمَـٰنِهَا خَیۡرࣰاۗ قُلِ ٱنتَظِرُوۤا۟ إِنَّا مُنتَظِرُونَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:47'),
(4980, 6, 948, 159, 'إِنَّ ٱلَّذِینَ فَرَّقُوا۟ دِینَهُمۡ وَكَانُوا۟ شِیَعࣰا لَّسۡتَ مِنۡهُمۡ فِی شَیۡءٍۚ إِنَّمَاۤ أَمۡرُهُمۡ إِلَى ٱللَّهِ ثُمَّ یُنَبِّئُهُم بِمَا كَانُوا۟ یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:48'),
(4981, 6, 949, 160, 'مَن جَاۤءَ بِٱلۡحَسَنَةِ فَلَهُۥ عَشۡرُ أَمۡثَالِهَاۖ وَمَن جَاۤءَ بِٱلسَّیِّئَةِ فَلَا یُجۡزَىٰۤ إِلَّا مِثۡلَهَا وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:48'),
(4982, 6, 950, 161, 'قُلۡ إِنَّنِی هَدَىٰنِی رَبِّیۤ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ دِینࣰا قِیَمࣰا مِّلَّةَ إِبۡرَ ٰ⁠هِیمَ حَنِیفࣰاۚ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:48'),
(4983, 6, 951, 162, 'قُلۡ إِنَّ صَلَاتِی وَنُسُكِی وَمَحۡیَایَ وَمَمَاتِی لِلَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:48'),
(4984, 6, 952, 163, 'لَا شَرِیكَ لَهُۥۖ وَبِذَ ٰ⁠لِكَ أُمِرۡتُ وَأَنَا۠ أَوَّلُ ٱلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:48'),
(4985, 6, 953, 164, 'قُلۡ أَغَیۡرَ ٱللَّهِ أَبۡغِی رَبࣰّا وَهُوَ رَبُّ كُلِّ شَیۡءࣲۚ وَلَا تَكۡسِبُ كُلُّ نَفۡسٍ إِلَّا عَلَیۡهَاۚ وَلَا تَزِرُ وَازِرَةࣱ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَیُنَبِّئُكُم بِمَا كُنتُمۡ فِیهِ تَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:33:36', '2023-05-27 11:18:48'),
(4986, 6, 954, 165, 'وَهُوَ ٱلَّذِی جَعَلَكُمۡ خَلَـٰۤىِٕفَ ٱلۡأَرۡضِ وَرَفَعَ بَعۡضَكُمۡ فَوۡقَ بَعۡضࣲ دَرَجَـٰتࣲ لِّیَبۡلُوَكُمۡ فِی مَاۤ ءَاتَىٰكُمۡۗ إِنَّ رَبَّكَ سَرِیعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورࣱ رَّحِیمُۢ\n', NULL, 0, '2023-05-26 17:33:37', '2023-05-27 11:18:48'),
(4987, 5, 670, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ أَوۡفُوا۟ بِٱلۡعُقُودِۚ أُحِلَّتۡ لَكُم بَهِیمَةُ ٱلۡأَنۡعَـٰمِ إِلَّا مَا یُتۡلَىٰ عَلَیۡكُمۡ غَیۡرَ مُحِلِّی ٱلصَّیۡدِ وَأَنتُمۡ حُرُمٌۗ إِنَّ ٱللَّهَ یَحۡكُمُ مَا یُرِیدُ\n', NULL, 0, '2023-05-26 17:33:46', '2023-05-27 11:18:50'),
(4988, 5, 671, 2, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تُحِلُّوا۟ شَعَـٰۤىِٕرَ ٱللَّهِ وَلَا ٱلشَّهۡرَ ٱلۡحَرَامَ وَلَا ٱلۡهَدۡیَ وَلَا ٱلۡقَلَـٰۤىِٕدَ وَلَاۤ ءَاۤمِّینَ ٱلۡبَیۡتَ ٱلۡحَرَامَ یَبۡتَغُونَ فَضۡلࣰا مِّن رَّبِّهِمۡ وَرِضۡوَ ٰ⁠نࣰاۚ وَإِذَا حَلَلۡتُمۡ فَٱصۡطَادُوا۟ۚ وَلَا یَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ أَن صَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ أَن تَعۡتَدُوا۟ۘ وَتَعَاوَنُوا۟ عَلَى ٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَلَا تَعَاوَنُوا۟ عَلَى ٱلۡإِثۡمِ وَٱلۡعُدۡوَ ٰ⁠نِۚ وَٱتَّقُوا۟ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:33:46', '2023-05-27 11:18:50'),
(4989, 5, 672, 3, 'حُرِّمَتۡ عَلَیۡكُمُ ٱلۡمَیۡتَةُ وَٱلدَّمُ وَلَحۡمُ ٱلۡخِنزِیرِ وَمَاۤ أُهِلَّ لِغَیۡرِ ٱللَّهِ بِهِۦ وَٱلۡمُنۡخَنِقَةُ وَٱلۡمَوۡقُوذَةُ وَٱلۡمُتَرَدِّیَةُ وَٱلنَّطِیحَةُ وَمَاۤ أَكَلَ ٱلسَّبُعُ إِلَّا مَا ذَكَّیۡتُمۡ وَمَا ذُبِحَ عَلَى ٱلنُّصُبِ وَأَن تَسۡتَقۡسِمُوا۟ بِٱلۡأَزۡلَـٰمِۚ ذَ ٰ⁠لِكُمۡ فِسۡقٌۗ ٱلۡیَوۡمَ یَىِٕسَ ٱلَّذِینَ كَفَرُوا۟ مِن دِینِكُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِۚ ٱلۡیَوۡمَ أَكۡمَلۡتُ لَكُمۡ دِینَكُمۡ وَأَتۡمَمۡتُ عَلَیۡكُمۡ نِعۡمَتِی وَرَضِیتُ لَكُمُ ٱلۡإِسۡلَـٰمَ دِینࣰاۚ فَمَنِ ٱضۡطُرَّ فِی مَخۡمَصَةٍ غَیۡرَ مُتَجَانِفࣲ لِّإِثۡمࣲ فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:33:46', '2023-05-27 11:18:50'),
(4990, 5, 673, 4, 'یَسۡـَٔلُونَكَ مَاذَاۤ أُحِلَّ لَهُمۡۖ قُلۡ أُحِلَّ لَكُمُ ٱلطَّیِّبَـٰتُ وَمَا عَلَّمۡتُم مِّنَ ٱلۡجَوَارِحِ مُكَلِّبِینَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ ٱللَّهُۖ فَكُلُوا۟ مِمَّاۤ أَمۡسَكۡنَ عَلَیۡكُمۡ وَٱذۡكُرُوا۟ ٱسۡمَ ٱللَّهِ عَلَیۡهِۖ وَٱتَّقُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:33:46', '2023-05-27 11:18:50'),
(4991, 5, 674, 5, 'ٱلۡیَوۡمَ أُحِلَّ لَكُمُ ٱلطَّیِّبَـٰتُۖ وَطَعَامُ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ حِلࣱّ لَّكُمۡ وَطَعَامُكُمۡ حِلࣱّ لَّهُمۡۖ وَٱلۡمُحۡصَنَـٰتُ مِنَ ٱلۡمُؤۡمِنَـٰتِ وَٱلۡمُحۡصَنَـٰتُ مِنَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ مِن قَبۡلِكُمۡ إِذَاۤ ءَاتَیۡتُمُوهُنَّ أُجُورَهُنَّ مُحۡصِنِینَ غَیۡرَ مُسَـٰفِحِینَ وَلَا مُتَّخِذِیۤ أَخۡدَانࣲۗ وَمَن یَكۡفُرۡ بِٱلۡإِیمَـٰنِ فَقَدۡ حَبِطَ عَمَلُهُۥ وَهُوَ فِی ٱلۡـَٔاخِرَةِ مِنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:50'),
(4992, 5, 675, 6, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا قُمۡتُمۡ إِلَى ٱلصَّلَوٰةِ فَٱغۡسِلُوا۟ وُجُوهَكُمۡ وَأَیۡدِیَكُمۡ إِلَى ٱلۡمَرَافِقِ وَٱمۡسَحُوا۟ بِرُءُوسِكُمۡ وَأَرۡجُلَكُمۡ إِلَى ٱلۡكَعۡبَیۡنِۚ وَإِن كُنتُمۡ جُنُبࣰا فَٱطَّهَّرُوا۟ۚ وَإِن كُنتُم مَّرۡضَىٰۤ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَاۤءَ أَحَدࣱ مِّنكُم مِّنَ ٱلۡغَاۤىِٕطِ أَوۡ لَـٰمَسۡتُمُ ٱلنِّسَاۤءَ فَلَمۡ تَجِدُوا۟ مَاۤءࣰ فَتَیَمَّمُوا۟ صَعِیدࣰا طَیِّبࣰا فَٱمۡسَحُوا۟ بِوُجُوهِكُمۡ وَأَیۡدِیكُم مِّنۡهُۚ مَا یُرِیدُ ٱللَّهُ لِیَجۡعَلَ عَلَیۡكُم مِّنۡ حَرَجࣲ وَلَـٰكِن یُرِیدُ لِیُطَهِّرَكُمۡ وَلِیُتِمَّ نِعۡمَتَهُۥ عَلَیۡكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:50'),
(4993, 5, 676, 7, 'وَٱذۡكُرُوا۟ نِعۡمَةَ ٱللَّهِ عَلَیۡكُمۡ وَمِیثَـٰقَهُ ٱلَّذِی وَاثَقَكُم بِهِۦۤ إِذۡ قُلۡتُمۡ سَمِعۡنَا وَأَطَعۡنَاۖ وَٱتَّقُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:50'),
(4994, 5, 677, 8, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ كُونُوا۟ قَوَّ ٰ⁠مِینَ لِلَّهِ شُهَدَاۤءَ بِٱلۡقِسۡطِۖ وَلَا یَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ عَلَىٰۤ أَلَّا تَعۡدِلُوا۟ۚ ٱعۡدِلُوا۟ هُوَ أَقۡرَبُ لِلتَّقۡوَىٰۖ وَٱتَّقُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:50'),
(4995, 5, 678, 9, 'وَعَدَ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُم مَّغۡفِرَةࣱ وَأَجۡرٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:51'),
(4996, 5, 679, 10, 'وَٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔایَـٰتِنَاۤ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:51'),
(4997, 5, 680, 11, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱذۡكُرُوا۟ نِعۡمَتَ ٱللَّهِ عَلَیۡكُمۡ إِذۡ هَمَّ قَوۡمٌ أَن یَبۡسُطُوۤا۟ إِلَیۡكُمۡ أَیۡدِیَهُمۡ فَكَفَّ أَیۡدِیَهُمۡ عَنكُمۡۖ وَٱتَّقُوا۟ ٱللَّهَۚ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:51'),
(4998, 5, 681, 12, '۞ وَلَقَدۡ أَخَذَ ٱللَّهُ مِیثَـٰقَ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ وَبَعَثۡنَا مِنۡهُمُ ٱثۡنَیۡ عَشَرَ نَقِیبࣰاۖ وَقَالَ ٱللَّهُ إِنِّی مَعَكُمۡۖ لَىِٕنۡ أَقَمۡتُمُ ٱلصَّلَوٰةَ وَءَاتَیۡتُمُ ٱلزَّكَوٰةَ وَءَامَنتُم بِرُسُلِی وَعَزَّرۡتُمُوهُمۡ وَأَقۡرَضۡتُمُ ٱللَّهَ قَرۡضًا حَسَنࣰا لَّأُكَفِّرَنَّ عَنكُمۡ سَیِّـَٔاتِكُمۡ وَلَأُدۡخِلَنَّكُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۚ فَمَن كَفَرَ بَعۡدَ ذَ ٰ⁠لِكَ مِنكُمۡ فَقَدۡ ضَلَّ سَوَاۤءَ ٱلسَّبِیلِ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:51'),
(4999, 5, 682, 13, 'فَبِمَا نَقۡضِهِم مِّیثَـٰقَهُمۡ لَعَنَّـٰهُمۡ وَجَعَلۡنَا قُلُوبَهُمۡ قَـٰسِیَةࣰۖ یُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَنَسُوا۟ حَظࣰّا مِّمَّا ذُكِّرُوا۟ بِهِۦۚ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَاۤىِٕنَةࣲ مِّنۡهُمۡ إِلَّا قَلِیلࣰا مِّنۡهُمۡۖ فَٱعۡفُ عَنۡهُمۡ وَٱصۡفَحۡۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:51'),
(5000, 5, 683, 14, 'وَمِنَ ٱلَّذِینَ قَالُوۤا۟ إِنَّا نَصَـٰرَىٰۤ أَخَذۡنَا مِیثَـٰقَهُمۡ فَنَسُوا۟ حَظࣰّا مِّمَّا ذُكِّرُوا۟ بِهِۦ فَأَغۡرَیۡنَا بَیۡنَهُمُ ٱلۡعَدَاوَةَ وَٱلۡبَغۡضَاۤءَ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِۚ وَسَوۡفَ یُنَبِّئُهُمُ ٱللَّهُ بِمَا كَانُوا۟ یَصۡنَعُونَ\n', NULL, 0, '2023-05-26 17:33:47', '2023-05-27 11:18:51'),
(5001, 5, 684, 15, 'یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ قَدۡ جَاۤءَكُمۡ رَسُولُنَا یُبَیِّنُ لَكُمۡ كَثِیرࣰا مِّمَّا كُنتُمۡ تُخۡفُونَ مِنَ ٱلۡكِتَـٰبِ وَیَعۡفُوا۟ عَن كَثِیرࣲۚ قَدۡ جَاۤءَكُم مِّنَ ٱللَّهِ نُورࣱ وَكِتَـٰبࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5002, 5, 685, 16, 'یَهۡدِی بِهِ ٱللَّهُ مَنِ ٱتَّبَعَ رِضۡوَ ٰ⁠نَهُۥ سُبُلَ ٱلسَّلَـٰمِ وَیُخۡرِجُهُم مِّنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِ بِإِذۡنِهِۦ وَیَهۡدِیهِمۡ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5003, 5, 686, 17, 'لَّقَدۡ كَفَرَ ٱلَّذِینَ قَالُوۤا۟ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِیحُ ٱبۡنُ مَرۡیَمَۚ قُلۡ فَمَن یَمۡلِكُ مِنَ ٱللَّهِ شَیۡـًٔا إِنۡ أَرَادَ أَن یُهۡلِكَ ٱلۡمَسِیحَ ٱبۡنَ مَرۡیَمَ وَأُمَّهُۥ وَمَن فِی ٱلۡأَرۡضِ جَمِیعࣰاۗ وَلِلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَاۚ یَخۡلُقُ مَا یَشَاۤءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5004, 5, 687, 18, 'وَقَالَتِ ٱلۡیَهُودُ وَٱلنَّصَـٰرَىٰ نَحۡنُ أَبۡنَـٰۤؤُا۟ ٱللَّهِ وَأَحِبَّـٰۤؤُهُۥۚ قُلۡ فَلِمَ یُعَذِّبُكُم بِذُنُوبِكُمۖ بَلۡ أَنتُم بَشَرࣱ مِّمَّنۡ خَلَقَۚ یَغۡفِرُ لِمَن یَشَاۤءُ وَیُعَذِّبُ مَن یَشَاۤءُۚ وَلِلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَاۖ وَإِلَیۡهِ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5005, 5, 688, 19, 'یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ قَدۡ جَاۤءَكُمۡ رَسُولُنَا یُبَیِّنُ لَكُمۡ عَلَىٰ فَتۡرَةࣲ مِّنَ ٱلرُّسُلِ أَن تَقُولُوا۟ مَا جَاۤءَنَا مِنۢ بَشِیرࣲ وَلَا نَذِیرࣲۖ فَقَدۡ جَاۤءَكُم بَشِیرࣱ وَنَذِیرࣱۗ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5006, 5, 689, 20, 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ یَـٰقَوۡمِ ٱذۡكُرُوا۟ نِعۡمَةَ ٱللَّهِ عَلَیۡكُمۡ إِذۡ جَعَلَ فِیكُمۡ أَنۢبِیَاۤءَ وَجَعَلَكُم مُّلُوكࣰا وَءَاتَىٰكُم مَّا لَمۡ یُؤۡتِ أَحَدࣰا مِّنَ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5007, 5, 690, 21, 'یَـٰقَوۡمِ ٱدۡخُلُوا۟ ٱلۡأَرۡضَ ٱلۡمُقَدَّسَةَ ٱلَّتِی كَتَبَ ٱللَّهُ لَكُمۡ وَلَا تَرۡتَدُّوا۟ عَلَىٰۤ أَدۡبَارِكُمۡ فَتَنقَلِبُوا۟ خَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5008, 5, 691, 22, 'قَالُوا۟ یَـٰمُوسَىٰۤ إِنَّ فِیهَا قَوۡمࣰا جَبَّارِینَ وَإِنَّا لَن نَّدۡخُلَهَا حَتَّىٰ یَخۡرُجُوا۟ مِنۡهَا فَإِن یَخۡرُجُوا۟ مِنۡهَا فَإِنَّا دَ ٰ⁠خِلُونَ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5009, 5, 692, 23, 'قَالَ رَجُلَانِ مِنَ ٱلَّذِینَ یَخَافُونَ أَنۡعَمَ ٱللَّهُ عَلَیۡهِمَا ٱدۡخُلُوا۟ عَلَیۡهِمُ ٱلۡبَابَ فَإِذَا دَخَلۡتُمُوهُ فَإِنَّكُمۡ غَـٰلِبُونَۚ وَعَلَى ٱللَّهِ فَتَوَكَّلُوۤا۟ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5010, 5, 693, 24, 'قَالُوا۟ یَـٰمُوسَىٰۤ إِنَّا لَن نَّدۡخُلَهَاۤ أَبَدࣰا مَّا دَامُوا۟ فِیهَا فَٱذۡهَبۡ أَنتَ وَرَبُّكَ فَقَـٰتِلَاۤ إِنَّا هَـٰهُنَا قَـٰعِدُونَ\n', NULL, 0, '2023-05-26 17:33:48', '2023-05-27 11:18:51'),
(5011, 5, 694, 25, 'قَالَ رَبِّ إِنِّی لَاۤ أَمۡلِكُ إِلَّا نَفۡسِی وَأَخِیۖ فَٱفۡرُقۡ بَیۡنَنَا وَبَیۡنَ ٱلۡقَوۡمِ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5012, 5, 695, 26, 'قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَیۡهِمۡۛ أَرۡبَعِینَ سَنَةࣰۛ یَتِیهُونَ فِی ٱلۡأَرۡضِۚ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5013, 5, 696, 27, '۞ وَٱتۡلُ عَلَیۡهِمۡ نَبَأَ ٱبۡنَیۡ ءَادَمَ بِٱلۡحَقِّ إِذۡ قَرَّبَا قُرۡبَانࣰا فَتُقُبِّلَ مِنۡ أَحَدِهِمَا وَلَمۡ یُتَقَبَّلۡ مِنَ ٱلۡـَٔاخَرِ قَالَ لَأَقۡتُلَنَّكَۖ قَالَ إِنَّمَا یَتَقَبَّلُ ٱللَّهُ مِنَ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5014, 5, 697, 28, 'لَىِٕنۢ بَسَطتَ إِلَیَّ یَدَكَ لِتَقۡتُلَنِی مَاۤ أَنَا۠ بِبَاسِطࣲ یَدِیَ إِلَیۡكَ لِأَقۡتُلَكَۖ إِنِّیۤ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5015, 5, 698, 29, 'إِنِّیۤ أُرِیدُ أَن تَبُوۤأَ بِإِثۡمِی وَإِثۡمِكَ فَتَكُونَ مِنۡ أَصۡحَـٰبِ ٱلنَّارِۚ وَذَ ٰ⁠لِكَ جَزَ ٰ⁠ۤؤُا۟ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5016, 5, 699, 30, 'فَطَوَّعَتۡ لَهُۥ نَفۡسُهُۥ قَتۡلَ أَخِیهِ فَقَتَلَهُۥ فَأَصۡبَحَ مِنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5017, 5, 700, 31, 'فَبَعَثَ ٱللَّهُ غُرَابࣰا یَبۡحَثُ فِی ٱلۡأَرۡضِ لِیُرِیَهُۥ كَیۡفَ یُوَ ٰ⁠رِی سَوۡءَةَ أَخِیهِۚ قَالَ یَـٰوَیۡلَتَىٰۤ أَعَجَزۡتُ أَنۡ أَكُونَ مِثۡلَ هَـٰذَا ٱلۡغُرَابِ فَأُوَ ٰ⁠رِیَ سَوۡءَةَ أَخِیۖ فَأَصۡبَحَ مِنَ ٱلنَّـٰدِمِینَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5018, 5, 701, 32, 'مِنۡ أَجۡلِ ذَ ٰ⁠لِكَ كَتَبۡنَا عَلَىٰ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ أَنَّهُۥ مَن قَتَلَ نَفۡسَۢا بِغَیۡرِ نَفۡسٍ أَوۡ فَسَادࣲ فِی ٱلۡأَرۡضِ فَكَأَنَّمَا قَتَلَ ٱلنَّاسَ جَمِیعࣰا وَمَنۡ أَحۡیَاهَا فَكَأَنَّمَاۤ أَحۡیَا ٱلنَّاسَ جَمِیعࣰاۚ وَلَقَدۡ جَاۤءَتۡهُمۡ رُسُلُنَا بِٱلۡبَیِّنَـٰتِ ثُمَّ إِنَّ كَثِیرࣰا مِّنۡهُم بَعۡدَ ذَ ٰ⁠لِكَ فِی ٱلۡأَرۡضِ لَمُسۡرِفُونَ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5019, 5, 702, 33, 'إِنَّمَا جَزَ ٰ⁠ۤؤُا۟ ٱلَّذِینَ یُحَارِبُونَ ٱللَّهَ وَرَسُولَهُۥ وَیَسۡعَوۡنَ فِی ٱلۡأَرۡضِ فَسَادًا أَن یُقَتَّلُوۤا۟ أَوۡ یُصَلَّبُوۤا۟ أَوۡ تُقَطَّعَ أَیۡدِیهِمۡ وَأَرۡجُلُهُم مِّنۡ خِلَـٰفٍ أَوۡ یُنفَوۡا۟ مِنَ ٱلۡأَرۡضِۚ ذَ ٰ⁠لِكَ لَهُمۡ خِزۡیࣱ فِی ٱلدُّنۡیَاۖ وَلَهُمۡ فِی ٱلۡـَٔاخِرَةِ عَذَابٌ عَظِیمٌ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5020, 5, 703, 34, 'إِلَّا ٱلَّذِینَ تَابُوا۟ مِن قَبۡلِ أَن تَقۡدِرُوا۟ عَلَیۡهِمۡۖ فَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:33:49', '2023-05-27 11:18:52'),
(5021, 5, 704, 35, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَٱبۡتَغُوۤا۟ إِلَیۡهِ ٱلۡوَسِیلَةَ وَجَـٰهِدُوا۟ فِی سَبِیلِهِۦ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:52'),
(5022, 5, 705, 36, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ لَوۡ أَنَّ لَهُم مَّا فِی ٱلۡأَرۡضِ جَمِیعࣰا وَمِثۡلَهُۥ مَعَهُۥ لِیَفۡتَدُوا۟ بِهِۦ مِنۡ عَذَابِ یَوۡمِ ٱلۡقِیَـٰمَةِ مَا تُقُبِّلَ مِنۡهُمۡۖ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:52'),
(5023, 5, 706, 37, 'یُرِیدُونَ أَن یَخۡرُجُوا۟ مِنَ ٱلنَّارِ وَمَا هُم بِخَـٰرِجِینَ مِنۡهَاۖ وَلَهُمۡ عَذَابࣱ مُّقِیمࣱ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:52'),
(5024, 5, 707, 38, 'وَٱلسَّارِقُ وَٱلسَّارِقَةُ فَٱقۡطَعُوۤا۟ أَیۡدِیَهُمَا جَزَاۤءَۢ بِمَا كَسَبَا نَكَـٰلࣰا مِّنَ ٱللَّهِۗ وَٱللَّهُ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:52'),
(5025, 5, 708, 39, 'فَمَن تَابَ مِنۢ بَعۡدِ ظُلۡمِهِۦ وَأَصۡلَحَ فَإِنَّ ٱللَّهَ یَتُوبُ عَلَیۡهِۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:52'),
(5026, 5, 709, 40, 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ یُعَذِّبُ مَن یَشَاۤءُ وَیَغۡفِرُ لِمَن یَشَاۤءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:52'),
(5027, 5, 710, 41, '۞ یَـٰۤأَیُّهَا ٱلرَّسُولُ لَا یَحۡزُنكَ ٱلَّذِینَ یُسَـٰرِعُونَ فِی ٱلۡكُفۡرِ مِنَ ٱلَّذِینَ قَالُوۤا۟ ءَامَنَّا بِأَفۡوَ ٰ⁠هِهِمۡ وَلَمۡ تُؤۡمِن قُلُوبُهُمۡۛ وَمِنَ ٱلَّذِینَ هَادُوا۟ۛ سَمَّـٰعُونَ لِلۡكَذِبِ سَمَّـٰعُونَ لِقَوۡمٍ ءَاخَرِینَ لَمۡ یَأۡتُوكَۖ یُحَرِّفُونَ ٱلۡكَلِمَ مِنۢ بَعۡدِ مَوَاضِعِهِۦۖ یَقُولُونَ إِنۡ أُوتِیتُمۡ هَـٰذَا فَخُذُوهُ وَإِن لَّمۡ تُؤۡتَوۡهُ فَٱحۡذَرُوا۟ۚ وَمَن یُرِدِ ٱللَّهُ فِتۡنَتَهُۥ فَلَن تَمۡلِكَ لَهُۥ مِنَ ٱللَّهِ شَیۡـًٔاۚ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ لَمۡ یُرِدِ ٱللَّهُ أَن یُطَهِّرَ قُلُوبَهُمۡۚ لَهُمۡ فِی ٱلدُّنۡیَا خِزۡیࣱۖ وَلَهُمۡ فِی ٱلۡـَٔاخِرَةِ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:53'),
(5028, 5, 711, 42, 'سَمَّـٰعُونَ لِلۡكَذِبِ أَكَّـٰلُونَ لِلسُّحۡتِۚ فَإِن جَاۤءُوكَ فَٱحۡكُم بَیۡنَهُمۡ أَوۡ أَعۡرِضۡ عَنۡهُمۡۖ وَإِن تُعۡرِضۡ عَنۡهُمۡ فَلَن یَضُرُّوكَ شَیۡـࣰٔاۖ وَإِنۡ حَكَمۡتَ فَٱحۡكُم بَیۡنَهُم بِٱلۡقِسۡطِۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُقۡسِطِینَ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:53'),
(5029, 5, 712, 43, 'وَكَیۡفَ یُحَكِّمُونَكَ وَعِندَهُمُ ٱلتَّوۡرَىٰةُ فِیهَا حُكۡمُ ٱللَّهِ ثُمَّ یَتَوَلَّوۡنَ مِنۢ بَعۡدِ ذَ ٰ⁠لِكَۚ وَمَاۤ أُو۟لَـٰۤىِٕكَ بِٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:53'),
(5030, 5, 713, 44, 'إِنَّاۤ أَنزَلۡنَا ٱلتَّوۡرَىٰةَ فِیهَا هُدࣰى وَنُورࣱۚ یَحۡكُمُ بِهَا ٱلنَّبِیُّونَ ٱلَّذِینَ أَسۡلَمُوا۟ لِلَّذِینَ هَادُوا۟ وَٱلرَّبَّـٰنِیُّونَ وَٱلۡأَحۡبَارُ بِمَا ٱسۡتُحۡفِظُوا۟ مِن كِتَـٰبِ ٱللَّهِ وَكَانُوا۟ عَلَیۡهِ شُهَدَاۤءَۚ فَلَا تَخۡشَوُا۟ ٱلنَّاسَ وَٱخۡشَوۡنِ وَلَا تَشۡتَرُوا۟ بِـَٔایَـٰتِی ثَمَنࣰا قَلِیلࣰاۚ وَمَن لَّمۡ یَحۡكُم بِمَاۤ أَنزَلَ ٱللَّهُ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-26 17:33:50', '2023-05-27 11:18:53'),
(5031, 5, 714, 45, 'وَكَتَبۡنَا عَلَیۡهِمۡ فِیهَاۤ أَنَّ ٱلنَّفۡسَ بِٱلنَّفۡسِ وَٱلۡعَیۡنَ بِٱلۡعَیۡنِ وَٱلۡأَنفَ بِٱلۡأَنفِ وَٱلۡأُذُنَ بِٱلۡأُذُنِ وَٱلسِّنَّ بِٱلسِّنِّ وَٱلۡجُرُوحَ قِصَاصࣱۚ فَمَن تَصَدَّقَ بِهِۦ فَهُوَ كَفَّارَةࣱ لَّهُۥۚ وَمَن لَّمۡ یَحۡكُم بِمَاۤ أَنزَلَ ٱللَّهُ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5032, 5, 715, 46, 'وَقَفَّیۡنَا عَلَىٰۤ ءَاثَـٰرِهِم بِعِیسَى ٱبۡنِ مَرۡیَمَ مُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیۡهِ مِنَ ٱلتَّوۡرَىٰةِۖ وَءَاتَیۡنَـٰهُ ٱلۡإِنجِیلَ فِیهِ هُدࣰى وَنُورࣱ وَمُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیۡهِ مِنَ ٱلتَّوۡرَىٰةِ وَهُدࣰى وَمَوۡعِظَةࣰ لِّلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5033, 5, 716, 47, 'وَلۡیَحۡكُمۡ أَهۡلُ ٱلۡإِنجِیلِ بِمَاۤ أَنزَلَ ٱللَّهُ فِیهِۚ وَمَن لَّمۡ یَحۡكُم بِمَاۤ أَنزَلَ ٱللَّهُ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5034, 5, 717, 48, 'وَأَنزَلۡنَاۤ إِلَیۡكَ ٱلۡكِتَـٰبَ بِٱلۡحَقِّ مُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیۡهِ مِنَ ٱلۡكِتَـٰبِ وَمُهَیۡمِنًا عَلَیۡهِۖ فَٱحۡكُم بَیۡنَهُم بِمَاۤ أَنزَلَ ٱللَّهُۖ وَلَا تَتَّبِعۡ أَهۡوَاۤءَهُمۡ عَمَّا جَاۤءَكَ مِنَ ٱلۡحَقِّۚ لِكُلࣲّ جَعَلۡنَا مِنكُمۡ شِرۡعَةࣰ وَمِنۡهَاجࣰاۚ وَلَوۡ شَاۤءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةࣰ وَ ٰ⁠حِدَةࣰ وَلَـٰكِن لِّیَبۡلُوَكُمۡ فِی مَاۤ ءَاتَىٰكُمۡۖ فَٱسۡتَبِقُوا۟ ٱلۡخَیۡرَ ٰ⁠تِۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِیعࣰا فَیُنَبِّئُكُم بِمَا كُنتُمۡ فِیهِ تَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5035, 5, 718, 49, 'وَأَنِ ٱحۡكُم بَیۡنَهُم بِمَاۤ أَنزَلَ ٱللَّهُ وَلَا تَتَّبِعۡ أَهۡوَاۤءَهُمۡ وَٱحۡذَرۡهُمۡ أَن یَفۡتِنُوكَ عَنۢ بَعۡضِ مَاۤ أَنزَلَ ٱللَّهُ إِلَیۡكَۖ فَإِن تَوَلَّوۡا۟ فَٱعۡلَمۡ أَنَّمَا یُرِیدُ ٱللَّهُ أَن یُصِیبَهُم بِبَعۡضِ ذُنُوبِهِمۡۗ وَإِنَّ كَثِیرࣰا مِّنَ ٱلنَّاسِ لَفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5036, 5, 719, 50, 'أَفَحُكۡمَ ٱلۡجَـٰهِلِیَّةِ یَبۡغُونَۚ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ حُكۡمࣰا لِّقَوۡمࣲ یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5037, 5, 720, 51, '۞ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَّخِذُوا۟ ٱلۡیَهُودَ وَٱلنَّصَـٰرَىٰۤ أَوۡلِیَاۤءَۘ بَعۡضُهُمۡ أَوۡلِیَاۤءُ بَعۡضࣲۚ وَمَن یَتَوَلَّهُم مِّنكُمۡ فَإِنَّهُۥ مِنۡهُمۡۗ إِنَّ ٱللَّهَ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5038, 5, 721, 52, 'فَتَرَى ٱلَّذِینَ فِی قُلُوبِهِم مَّرَضࣱ یُسَـٰرِعُونَ فِیهِمۡ یَقُولُونَ نَخۡشَىٰۤ أَن تُصِیبَنَا دَاۤىِٕرَةࣱۚ فَعَسَى ٱللَّهُ أَن یَأۡتِیَ بِٱلۡفَتۡحِ أَوۡ أَمۡرࣲ مِّنۡ عِندِهِۦ فَیُصۡبِحُوا۟ عَلَىٰ مَاۤ أَسَرُّوا۟ فِیۤ أَنفُسِهِمۡ نَـٰدِمِینَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5039, 5, 722, 53, 'وَیَقُولُ ٱلَّذِینَ ءَامَنُوۤا۟ أَهَـٰۤؤُلَاۤءِ ٱلَّذِینَ أَقۡسَمُوا۟ بِٱللَّهِ جَهۡدَ أَیۡمَـٰنِهِمۡ إِنَّهُمۡ لَمَعَكُمۡۚ حَبِطَتۡ أَعۡمَـٰلُهُمۡ فَأَصۡبَحُوا۟ خَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:33:51', '2023-05-27 11:18:53'),
(5040, 5, 723, 54, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ مَن یَرۡتَدَّ مِنكُمۡ عَن دِینِهِۦ فَسَوۡفَ یَأۡتِی ٱللَّهُ بِقَوۡمࣲ یُحِبُّهُمۡ وَیُحِبُّونَهُۥۤ أَذِلَّةٍ عَلَى ٱلۡمُؤۡمِنِینَ أَعِزَّةٍ عَلَى ٱلۡكَـٰفِرِینَ یُجَـٰهِدُونَ فِی سَبِیلِ ٱللَّهِ وَلَا یَخَافُونَ لَوۡمَةَ لَاۤىِٕمࣲۚ ذَ ٰ⁠لِكَ فَضۡلُ ٱللَّهِ یُؤۡتِیهِ مَن یَشَاۤءُۚ وَٱللَّهُ وَ ٰ⁠سِعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:53'),
(5041, 5, 724, 55, 'إِنَّمَا وَلِیُّكُمُ ٱللَّهُ وَرَسُولُهُۥ وَٱلَّذِینَ ءَامَنُوا۟ ٱلَّذِینَ یُقِیمُونَ ٱلصَّلَوٰةَ وَیُؤۡتُونَ ٱلزَّكَوٰةَ وَهُمۡ رَ ٰ⁠كِعُونَ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:53'),
(5042, 5, 725, 56, 'وَمَن یَتَوَلَّ ٱللَّهَ وَرَسُولَهُۥ وَٱلَّذِینَ ءَامَنُوا۟ فَإِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡغَـٰلِبُونَ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:54'),
(5043, 5, 726, 57, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَّخِذُوا۟ ٱلَّذِینَ ٱتَّخَذُوا۟ دِینَكُمۡ هُزُوࣰا وَلَعِبࣰا مِّنَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ مِن قَبۡلِكُمۡ وَٱلۡكُفَّارَ أَوۡلِیَاۤءَۚ وَٱتَّقُوا۟ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:54'),
(5044, 5, 727, 58, 'وَإِذَا نَادَیۡتُمۡ إِلَى ٱلصَّلَوٰةِ ٱتَّخَذُوهَا هُزُوࣰا وَلَعِبࣰاۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَوۡمࣱ لَّا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:54'),
(5045, 5, 728, 59, 'قُلۡ یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ هَلۡ تَنقِمُونَ مِنَّاۤ إِلَّاۤ أَنۡ ءَامَنَّا بِٱللَّهِ وَمَاۤ أُنزِلَ إِلَیۡنَا وَمَاۤ أُنزِلَ مِن قَبۡلُ وَأَنَّ أَكۡثَرَكُمۡ فَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:54'),
(5046, 5, 729, 60, 'قُلۡ هَلۡ أُنَبِّئُكُم بِشَرࣲّ مِّن ذَ ٰ⁠لِكَ مَثُوبَةً عِندَ ٱللَّهِۚ مَن لَّعَنَهُ ٱللَّهُ وَغَضِبَ عَلَیۡهِ وَجَعَلَ مِنۡهُمُ ٱلۡقِرَدَةَ وَٱلۡخَنَازِیرَ وَعَبَدَ ٱلطَّـٰغُوتَۚ أُو۟لَـٰۤىِٕكَ شَرࣱّ مَّكَانࣰا وَأَضَلُّ عَن سَوَاۤءِ ٱلسَّبِیلِ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:54'),
(5047, 5, 730, 61, 'وَإِذَا جَاۤءُوكُمۡ قَالُوۤا۟ ءَامَنَّا وَقَد دَّخَلُوا۟ بِٱلۡكُفۡرِ وَهُمۡ قَدۡ خَرَجُوا۟ بِهِۦۚ وَٱللَّهُ أَعۡلَمُ بِمَا كَانُوا۟ یَكۡتُمُونَ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:54'),
(5048, 5, 731, 62, 'وَتَرَىٰ كَثِیرࣰا مِّنۡهُمۡ یُسَـٰرِعُونَ فِی ٱلۡإِثۡمِ وَٱلۡعُدۡوَ ٰ⁠نِ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:52', '2023-05-27 11:18:54'),
(5049, 5, 732, 63, 'لَوۡلَا یَنۡهَىٰهُمُ ٱلرَّبَّـٰنِیُّونَ وَٱلۡأَحۡبَارُ عَن قَوۡلِهِمُ ٱلۡإِثۡمَ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُوا۟ یَصۡنَعُونَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5050, 5, 733, 64, 'وَقَالَتِ ٱلۡیَهُودُ یَدُ ٱللَّهِ مَغۡلُولَةٌۚ غُلَّتۡ أَیۡدِیهِمۡ وَلُعِنُوا۟ بِمَا قَالُوا۟ۘ بَلۡ یَدَاهُ مَبۡسُوطَتَانِ یُنفِقُ كَیۡفَ یَشَاۤءُۚ وَلَیَزِیدَنَّ كَثِیرࣰا مِّنۡهُم مَّاۤ أُنزِلَ إِلَیۡكَ مِن رَّبِّكَ طُغۡیَـٰنࣰا وَكُفۡرࣰاۚ وَأَلۡقَیۡنَا بَیۡنَهُمُ ٱلۡعَدَ ٰ⁠وَةَ وَٱلۡبَغۡضَاۤءَ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِۚ كُلَّمَاۤ أَوۡقَدُوا۟ نَارࣰا لِّلۡحَرۡبِ أَطۡفَأَهَا ٱللَّهُۚ وَیَسۡعَوۡنَ فِی ٱلۡأَرۡضِ فَسَادࣰاۚ وَٱللَّهُ لَا یُحِبُّ ٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5051, 5, 734, 65, 'وَلَوۡ أَنَّ أَهۡلَ ٱلۡكِتَـٰبِ ءَامَنُوا۟ وَٱتَّقَوۡا۟ لَكَفَّرۡنَا عَنۡهُمۡ سَیِّـَٔاتِهِمۡ وَلَأَدۡخَلۡنَـٰهُمۡ جَنَّـٰتِ ٱلنَّعِیمِ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5052, 5, 735, 66, 'وَلَوۡ أَنَّهُمۡ أَقَامُوا۟ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِیلَ وَمَاۤ أُنزِلَ إِلَیۡهِم مِّن رَّبِّهِمۡ لَأَكَلُوا۟ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۚ مِّنۡهُمۡ أُمَّةࣱ مُّقۡتَصِدَةࣱۖ وَكَثِیرࣱ مِّنۡهُمۡ سَاۤءَ مَا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5053, 5, 736, 67, '۞ یَـٰۤأَیُّهَا ٱلرَّسُولُ بَلِّغۡ مَاۤ أُنزِلَ إِلَیۡكَ مِن رَّبِّكَۖ وَإِن لَّمۡ تَفۡعَلۡ فَمَا بَلَّغۡتَ رِسَالَتَهُۥۚ وَٱللَّهُ یَعۡصِمُكَ مِنَ ٱلنَّاسِۗ إِنَّ ٱللَّهَ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5054, 5, 737, 68, 'قُلۡ یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لَسۡتُمۡ عَلَىٰ شَیۡءٍ حَتَّىٰ تُقِیمُوا۟ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِیلَ وَمَاۤ أُنزِلَ إِلَیۡكُم مِّن رَّبِّكُمۡۗ وَلَیَزِیدَنَّ كَثِیرࣰا مِّنۡهُم مَّاۤ أُنزِلَ إِلَیۡكَ مِن رَّبِّكَ طُغۡیَـٰنࣰا وَكُفۡرࣰاۖ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5055, 5, 738, 69, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَٱلَّذِینَ هَادُوا۟ وَٱلصَّـٰبِـُٔونَ وَٱلنَّصَـٰرَىٰ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَعَمِلَ صَـٰلِحࣰا فَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5056, 5, 739, 70, 'لَقَدۡ أَخَذۡنَا مِیثَـٰقَ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ وَأَرۡسَلۡنَاۤ إِلَیۡهِمۡ رُسُلࣰاۖ كُلَّمَا جَاۤءَهُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰۤ أَنفُسُهُمۡ فَرِیقࣰا كَذَّبُوا۟ وَفَرِیقࣰا یَقۡتُلُونَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5057, 5, 740, 71, 'وَحَسِبُوۤا۟ أَلَّا تَكُونَ فِتۡنَةࣱ فَعَمُوا۟ وَصَمُّوا۟ ثُمَّ تَابَ ٱللَّهُ عَلَیۡهِمۡ ثُمَّ عَمُوا۟ وَصَمُّوا۟ كَثِیرࣱ مِّنۡهُمۡۚ وَٱللَّهُ بَصِیرُۢ بِمَا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5058, 5, 741, 72, 'لَقَدۡ كَفَرَ ٱلَّذِینَ قَالُوۤا۟ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِیحُ ٱبۡنُ مَرۡیَمَۖ وَقَالَ ٱلۡمَسِیحُ یَـٰبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱعۡبُدُوا۟ ٱللَّهَ رَبِّی وَرَبَّكُمۡۖ إِنَّهُۥ مَن یُشۡرِكۡ بِٱللَّهِ فَقَدۡ حَرَّمَ ٱللَّهُ عَلَیۡهِ ٱلۡجَنَّةَ وَمَأۡوَىٰهُ ٱلنَّارُۖ وَمَا لِلظَّـٰلِمِینَ مِنۡ أَنصَارࣲ\n', NULL, 0, '2023-05-26 17:33:53', '2023-05-27 11:18:54'),
(5059, 5, 742, 73, 'لَّقَدۡ كَفَرَ ٱلَّذِینَ قَالُوۤا۟ إِنَّ ٱللَّهَ ثَالِثُ ثَلَـٰثَةࣲۘ وَمَا مِنۡ إِلَـٰهٍ إِلَّاۤ إِلَـٰهࣱ وَ ٰ⁠حِدࣱۚ وَإِن لَّمۡ یَنتَهُوا۟ عَمَّا یَقُولُونَ لَیَمَسَّنَّ ٱلَّذِینَ كَفَرُوا۟ مِنۡهُمۡ عَذَابٌ أَلِیمٌ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5060, 5, 743, 74, 'أَفَلَا یَتُوبُونَ إِلَى ٱللَّهِ وَیَسۡتَغۡفِرُونَهُۥۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5061, 5, 744, 75, 'مَّا ٱلۡمَسِیحُ ٱبۡنُ مَرۡیَمَ إِلَّا رَسُولࣱ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُ وَأُمُّهُۥ صِدِّیقَةࣱۖ كَانَا یَأۡكُلَانِ ٱلطَّعَامَۗ ٱنظُرۡ كَیۡفَ نُبَیِّنُ لَهُمُ ٱلۡـَٔایَـٰتِ ثُمَّ ٱنظُرۡ أَنَّىٰ یُؤۡفَكُونَ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5062, 5, 745, 76, 'قُلۡ أَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا یَمۡلِكُ لَكُمۡ ضَرࣰّا وَلَا نَفۡعࣰاۚ وَٱللَّهُ هُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5063, 5, 746, 77, 'قُلۡ یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لَا تَغۡلُوا۟ فِی دِینِكُمۡ غَیۡرَ ٱلۡحَقِّ وَلَا تَتَّبِعُوۤا۟ أَهۡوَاۤءَ قَوۡمࣲ قَدۡ ضَلُّوا۟ مِن قَبۡلُ وَأَضَلُّوا۟ كَثِیرࣰا وَضَلُّوا۟ عَن سَوَاۤءِ ٱلسَّبِیلِ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5064, 5, 747, 78, 'لُعِنَ ٱلَّذِینَ كَفَرُوا۟ مِنۢ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ عَلَىٰ لِسَانِ دَاوُۥدَ وَعِیسَى ٱبۡنِ مَرۡیَمَۚ ذَ ٰ⁠لِكَ بِمَا عَصَوا۟ وَّكَانُوا۟ یَعۡتَدُونَ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5065, 5, 748, 79, 'كَانُوا۟ لَا یَتَنَاهَوۡنَ عَن مُّنكَرࣲ فَعَلُوهُۚ لَبِئۡسَ مَا كَانُوا۟ یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5066, 5, 749, 80, 'تَرَىٰ كَثِیرࣰا مِّنۡهُمۡ یَتَوَلَّوۡنَ ٱلَّذِینَ كَفَرُوا۟ۚ لَبِئۡسَ مَا قَدَّمَتۡ لَهُمۡ أَنفُسُهُمۡ أَن سَخِطَ ٱللَّهُ عَلَیۡهِمۡ وَفِی ٱلۡعَذَابِ هُمۡ خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5067, 5, 750, 81, 'وَلَوۡ كَانُوا۟ یُؤۡمِنُونَ بِٱللَّهِ وَٱلنَّبِیِّ وَمَاۤ أُنزِلَ إِلَیۡهِ مَا ٱتَّخَذُوهُمۡ أَوۡلِیَاۤءَ وَلَـٰكِنَّ كَثِیرࣰا مِّنۡهُمۡ فَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:33:54', '2023-05-27 11:18:55'),
(5068, 5, 751, 82, '۞ لَتَجِدَنَّ أَشَدَّ ٱلنَّاسِ عَدَ ٰ⁠وَةࣰ لِّلَّذِینَ ءَامَنُوا۟ ٱلۡیَهُودَ وَٱلَّذِینَ أَشۡرَكُوا۟ۖ وَلَتَجِدَنَّ أَقۡرَبَهُم مَّوَدَّةࣰ لِّلَّذِینَ ءَامَنُوا۟ ٱلَّذِینَ قَالُوۤا۟ إِنَّا نَصَـٰرَىٰۚ ذَ ٰ⁠لِكَ بِأَنَّ مِنۡهُمۡ قِسِّیسِینَ وَرُهۡبَانࣰا وَأَنَّهُمۡ لَا یَسۡتَكۡبِرُونَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5069, 5, 752, 83, 'وَإِذَا سَمِعُوا۟ مَاۤ أُنزِلَ إِلَى ٱلرَّسُولِ تَرَىٰۤ أَعۡیُنَهُمۡ تَفِیضُ مِنَ ٱلدَّمۡعِ مِمَّا عَرَفُوا۟ مِنَ ٱلۡحَقِّۖ یَقُولُونَ رَبَّنَاۤ ءَامَنَّا فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِینَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5070, 5, 753, 84, 'وَمَا لَنَا لَا نُؤۡمِنُ بِٱللَّهِ وَمَا جَاۤءَنَا مِنَ ٱلۡحَقِّ وَنَطۡمَعُ أَن یُدۡخِلَنَا رَبُّنَا مَعَ ٱلۡقَوۡمِ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5071, 5, 754, 85, 'فَأَثَـٰبَهُمُ ٱللَّهُ بِمَا قَالُوا۟ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ وَذَ ٰ⁠لِكَ جَزَاۤءُ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5072, 5, 755, 86, 'وَٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔایَـٰتِنَاۤ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5073, 5, 756, 87, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تُحَرِّمُوا۟ طَیِّبَـٰتِ مَاۤ أَحَلَّ ٱللَّهُ لَكُمۡ وَلَا تَعۡتَدُوۤا۟ۚ إِنَّ ٱللَّهَ لَا یُحِبُّ ٱلۡمُعۡتَدِینَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5074, 5, 757, 88, 'وَكُلُوا۟ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَـٰلࣰا طَیِّبࣰاۚ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِیۤ أَنتُم بِهِۦ مُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5075, 5, 758, 89, 'لَا یُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِیۤ أَیۡمَـٰنِكُمۡ وَلَـٰكِن یُؤَاخِذُكُم بِمَا عَقَّدتُّمُ ٱلۡأَیۡمَـٰنَۖ فَكَفَّـٰرَتُهُۥۤ إِطۡعَامُ عَشَرَةِ مَسَـٰكِینَ مِنۡ أَوۡسَطِ مَا تُطۡعِمُونَ أَهۡلِیكُمۡ أَوۡ كِسۡوَتُهُمۡ أَوۡ تَحۡرِیرُ رَقَبَةࣲۖ فَمَن لَّمۡ یَجِدۡ فَصِیَامُ ثَلَـٰثَةِ أَیَّامࣲۚ ذَ ٰ⁠لِكَ كَفَّـٰرَةُ أَیۡمَـٰنِكُمۡ إِذَا حَلَفۡتُمۡۚ وَٱحۡفَظُوۤا۟ أَیۡمَـٰنَكُمۡۚ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمۡ ءَایَـٰتِهِۦ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:55'),
(5076, 5, 759, 90, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِنَّمَا ٱلۡخَمۡرُ وَٱلۡمَیۡسِرُ وَٱلۡأَنصَابُ وَٱلۡأَزۡلَـٰمُ رِجۡسࣱ مِّنۡ عَمَلِ ٱلشَّیۡطَـٰنِ فَٱجۡتَنِبُوهُ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:33:55', '2023-05-27 11:18:56'),
(5077, 5, 760, 91, 'إِنَّمَا یُرِیدُ ٱلشَّیۡطَـٰنُ أَن یُوقِعَ بَیۡنَكُمُ ٱلۡعَدَ ٰ⁠وَةَ وَٱلۡبَغۡضَاۤءَ فِی ٱلۡخَمۡرِ وَٱلۡمَیۡسِرِ وَیَصُدَّكُمۡ عَن ذِكۡرِ ٱللَّهِ وَعَنِ ٱلصَّلَوٰةِۖ فَهَلۡ أَنتُم مُّنتَهُونَ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5078, 5, 761, 92, 'وَأَطِیعُوا۟ ٱللَّهَ وَأَطِیعُوا۟ ٱلرَّسُولَ وَٱحۡذَرُوا۟ۚ فَإِن تَوَلَّیۡتُمۡ فَٱعۡلَمُوۤا۟ أَنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَـٰغُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5079, 5, 762, 93, 'لَیۡسَ عَلَى ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ جُنَاحࣱ فِیمَا طَعِمُوۤا۟ إِذَا مَا ٱتَّقَوا۟ وَّءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ ثُمَّ ٱتَّقَوا۟ وَّءَامَنُوا۟ ثُمَّ ٱتَّقَوا۟ وَّأَحۡسَنُوا۟ۚ وَٱللَّهُ یُحِبُّ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5080, 5, 763, 94, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَیَبۡلُوَنَّكُمُ ٱللَّهُ بِشَیۡءࣲ مِّنَ ٱلصَّیۡدِ تَنَالُهُۥۤ أَیۡدِیكُمۡ وَرِمَاحُكُمۡ لِیَعۡلَمَ ٱللَّهُ مَن یَخَافُهُۥ بِٱلۡغَیۡبِۚ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَ ٰ⁠لِكَ فَلَهُۥ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5081, 5, 764, 95, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَقۡتُلُوا۟ ٱلصَّیۡدَ وَأَنتُمۡ حُرُمࣱۚ وَمَن قَتَلَهُۥ مِنكُم مُّتَعَمِّدࣰا فَجَزَاۤءࣱ مِّثۡلُ مَا قَتَلَ مِنَ ٱلنَّعَمِ یَحۡكُمُ بِهِۦ ذَوَا عَدۡلࣲ مِّنكُمۡ هَدۡیَۢا بَـٰلِغَ ٱلۡكَعۡبَةِ أَوۡ كَفَّـٰرَةࣱ طَعَامُ مَسَـٰكِینَ أَوۡ عَدۡلُ ذَ ٰ⁠لِكَ صِیَامࣰا لِّیَذُوقَ وَبَالَ أَمۡرِهِۦۗ عَفَا ٱللَّهُ عَمَّا سَلَفَۚ وَمَنۡ عَادَ فَیَنتَقِمُ ٱللَّهُ مِنۡهُۚ وَٱللَّهُ عَزِیزࣱ ذُو ٱنتِقَامٍ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5082, 5, 765, 96, 'أُحِلَّ لَكُمۡ صَیۡدُ ٱلۡبَحۡرِ وَطَعَامُهُۥ مَتَـٰعࣰا لَّكُمۡ وَلِلسَّیَّارَةِۖ وَحُرِّمَ عَلَیۡكُمۡ صَیۡدُ ٱلۡبَرِّ مَا دُمۡتُمۡ حُرُمࣰاۗ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِیۤ إِلَیۡهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5083, 5, 766, 97, '۞ جَعَلَ ٱللَّهُ ٱلۡكَعۡبَةَ ٱلۡبَیۡتَ ٱلۡحَرَامَ قِیَـٰمࣰا لِّلنَّاسِ وَٱلشَّهۡرَ ٱلۡحَرَامَ وَٱلۡهَدۡیَ وَٱلۡقَلَـٰۤىِٕدَۚ ذَ ٰ⁠لِكَ لِتَعۡلَمُوۤا۟ أَنَّ ٱللَّهَ یَعۡلَمُ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِ وَأَنَّ ٱللَّهَ بِكُلِّ شَیۡءٍ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5084, 5, 767, 98, 'ٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ وَأَنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5085, 5, 768, 99, 'مَّا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَـٰغُۗ وَٱللَّهُ یَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5086, 5, 769, 100, 'قُل لَّا یَسۡتَوِی ٱلۡخَبِیثُ وَٱلطَّیِّبُ وَلَوۡ أَعۡجَبَكَ كَثۡرَةُ ٱلۡخَبِیثِۚ فَٱتَّقُوا۟ ٱللَّهَ یَـٰۤأُو۟لِی ٱلۡأَلۡبَـٰبِ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:33:56', '2023-05-27 11:18:56'),
(5087, 5, 770, 101, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَسۡـَٔلُوا۟ عَنۡ أَشۡیَاۤءَ إِن تُبۡدَ لَكُمۡ تَسُؤۡكُمۡ وَإِن تَسۡـَٔلُوا۟ عَنۡهَا حِینَ یُنَزَّلُ ٱلۡقُرۡءَانُ تُبۡدَ لَكُمۡ عَفَا ٱللَّهُ عَنۡهَاۗ وَٱللَّهُ غَفُورٌ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:56'),
(5088, 5, 771, 102, 'قَدۡ سَأَلَهَا قَوۡمࣱ مِّن قَبۡلِكُمۡ ثُمَّ أَصۡبَحُوا۟ بِهَا كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:56'),
(5089, 5, 772, 103, 'مَا جَعَلَ ٱللَّهُ مِنۢ بَحِیرَةࣲ وَلَا سَاۤىِٕبَةࣲ وَلَا وَصِیلَةࣲ وَلَا حَامࣲ وَلَـٰكِنَّ ٱلَّذِینَ كَفَرُوا۟ یَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَأَكۡثَرُهُمۡ لَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:56'),
(5090, 5, 773, 104, 'وَإِذَا قِیلَ لَهُمۡ تَعَالَوۡا۟ إِلَىٰ مَاۤ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ قَالُوا۟ حَسۡبُنَا مَا وَجَدۡنَا عَلَیۡهِ ءَابَاۤءَنَاۤۚ أَوَلَوۡ كَانَ ءَابَاۤؤُهُمۡ لَا یَعۡلَمُونَ شَیۡـࣰٔا وَلَا یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:56'),
(5091, 5, 774, 105, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ عَلَیۡكُمۡ أَنفُسَكُمۡۖ لَا یَضُرُّكُم مَّن ضَلَّ إِذَا ٱهۡتَدَیۡتُمۡۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِیعࣰا فَیُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:57'),
(5092, 5, 775, 106, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ شَهَـٰدَةُ بَیۡنِكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ حِینَ ٱلۡوَصِیَّةِ ٱثۡنَانِ ذَوَا عَدۡلࣲ مِّنكُمۡ أَوۡ ءَاخَرَانِ مِنۡ غَیۡرِكُمۡ إِنۡ أَنتُمۡ ضَرَبۡتُمۡ فِی ٱلۡأَرۡضِ فَأَصَـٰبَتۡكُم مُّصِیبَةُ ٱلۡمَوۡتِۚ تَحۡبِسُونَهُمَا مِنۢ بَعۡدِ ٱلصَّلَوٰةِ فَیُقۡسِمَانِ بِٱللَّهِ إِنِ ٱرۡتَبۡتُمۡ لَا نَشۡتَرِی بِهِۦ ثَمَنࣰا وَلَوۡ كَانَ ذَا قُرۡبَىٰ وَلَا نَكۡتُمُ شَهَـٰدَةَ ٱللَّهِ إِنَّاۤ إِذࣰا لَّمِنَ ٱلۡـَٔاثِمِینَ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:57'),
(5093, 5, 776, 107, 'فَإِنۡ عُثِرَ عَلَىٰۤ أَنَّهُمَا ٱسۡتَحَقَّاۤ إِثۡمࣰا فَـَٔاخَرَانِ یَقُومَانِ مَقَامَهُمَا مِنَ ٱلَّذِینَ ٱسۡتَحَقَّ عَلَیۡهِمُ ٱلۡأَوۡلَیَـٰنِ فَیُقۡسِمَانِ بِٱللَّهِ لَشَهَـٰدَتُنَاۤ أَحَقُّ مِن شَهَـٰدَتِهِمَا وَمَا ٱعۡتَدَیۡنَاۤ إِنَّاۤ إِذࣰا لَّمِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:57'),
(5094, 5, 777, 108, 'ذَ ٰ⁠لِكَ أَدۡنَىٰۤ أَن یَأۡتُوا۟ بِٱلشَّهَـٰدَةِ عَلَىٰ وَجۡهِهَاۤ أَوۡ یَخَافُوۤا۟ أَن تُرَدَّ أَیۡمَـٰنُۢ بَعۡدَ أَیۡمَـٰنِهِمۡۗ وَٱتَّقُوا۟ ٱللَّهَ وَٱسۡمَعُوا۟ۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:57'),
(5095, 5, 778, 109, '۞ یَوۡمَ یَجۡمَعُ ٱللَّهُ ٱلرُّسُلَ فَیَقُولُ مَاذَاۤ أُجِبۡتُمۡۖ قَالُوا۟ لَا عِلۡمَ لَنَاۤۖ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُیُوبِ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:57'),
(5096, 5, 779, 110, 'إِذۡ قَالَ ٱللَّهُ یَـٰعِیسَى ٱبۡنَ مَرۡیَمَ ٱذۡكُرۡ نِعۡمَتِی عَلَیۡكَ وَعَلَىٰ وَ ٰ⁠لِدَتِكَ إِذۡ أَیَّدتُّكَ بِرُوحِ ٱلۡقُدُسِ تُكَلِّمُ ٱلنَّاسَ فِی ٱلۡمَهۡدِ وَكَهۡلࣰاۖ وَإِذۡ عَلَّمۡتُكَ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِیلَۖ وَإِذۡ تَخۡلُقُ مِنَ ٱلطِّینِ كَهَیۡـَٔةِ ٱلطَّیۡرِ بِإِذۡنِی فَتَنفُخُ فِیهَا فَتَكُونُ طَیۡرَۢا بِإِذۡنِیۖ وَتُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ بِإِذۡنِیۖ وَإِذۡ تُخۡرِجُ ٱلۡمَوۡتَىٰ بِإِذۡنِیۖ وَإِذۡ كَفَفۡتُ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ عَنكَ إِذۡ جِئۡتَهُم بِٱلۡبَیِّنَـٰتِ فَقَالَ ٱلَّذِینَ كَفَرُوا۟ مِنۡهُمۡ إِنۡ هَـٰذَاۤ إِلَّا سِحۡرࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:33:57', '2023-05-27 11:18:57'),
(5097, 5, 780, 111, 'وَإِذۡ أَوۡحَیۡتُ إِلَى ٱلۡحَوَارِیِّـۧنَ أَنۡ ءَامِنُوا۟ بِی وَبِرَسُولِی قَالُوۤا۟ ءَامَنَّا وَٱشۡهَدۡ بِأَنَّنَا مُسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5098, 5, 781, 112, 'إِذۡ قَالَ ٱلۡحَوَارِیُّونَ یَـٰعِیسَى ٱبۡنَ مَرۡیَمَ هَلۡ یَسۡتَطِیعُ رَبُّكَ أَن یُنَزِّلَ عَلَیۡنَا مَاۤىِٕدَةࣰ مِّنَ ٱلسَّمَاۤءِۖ قَالَ ٱتَّقُوا۟ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5099, 5, 782, 113, 'قَالُوا۟ نُرِیدُ أَن نَّأۡكُلَ مِنۡهَا وَتَطۡمَىِٕنَّ قُلُوبُنَا وَنَعۡلَمَ أَن قَدۡ صَدَقۡتَنَا وَنَكُونَ عَلَیۡهَا مِنَ ٱلشَّـٰهِدِینَ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5100, 5, 783, 114, 'قَالَ عِیسَى ٱبۡنُ مَرۡیَمَ ٱللَّهُمَّ رَبَّنَاۤ أَنزِلۡ عَلَیۡنَا مَاۤىِٕدَةࣰ مِّنَ ٱلسَّمَاۤءِ تَكُونُ لَنَا عِیدࣰا لِّأَوَّلِنَا وَءَاخِرِنَا وَءَایَةࣰ مِّنكَۖ وَٱرۡزُقۡنَا وَأَنتَ خَیۡرُ ٱلرَّ ٰ⁠زِقِینَ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5101, 5, 784, 115, 'قَالَ ٱللَّهُ إِنِّی مُنَزِّلُهَا عَلَیۡكُمۡۖ فَمَن یَكۡفُرۡ بَعۡدُ مِنكُمۡ فَإِنِّیۤ أُعَذِّبُهُۥ عَذَابࣰا لَّاۤ أُعَذِّبُهُۥۤ أَحَدࣰا مِّنَ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5102, 5, 785, 116, 'وَإِذۡ قَالَ ٱللَّهُ یَـٰعِیسَى ٱبۡنَ مَرۡیَمَ ءَأَنتَ قُلۡتَ لِلنَّاسِ ٱتَّخِذُونِی وَأُمِّیَ إِلَـٰهَیۡنِ مِن دُونِ ٱللَّهِۖ قَالَ سُبۡحَـٰنَكَ مَا یَكُونُ لِیۤ أَنۡ أَقُولَ مَا لَیۡسَ لِی بِحَقٍّۚ إِن كُنتُ قُلۡتُهُۥ فَقَدۡ عَلِمۡتَهُۥۚ تَعۡلَمُ مَا فِی نَفۡسِی وَلَاۤ أَعۡلَمُ مَا فِی نَفۡسِكَۚ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُیُوبِ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5103, 5, 786, 117, 'مَا قُلۡتُ لَهُمۡ إِلَّا مَاۤ أَمَرۡتَنِی بِهِۦۤ أَنِ ٱعۡبُدُوا۟ ٱللَّهَ رَبِّی وَرَبَّكُمۡۚ وَكُنتُ عَلَیۡهِمۡ شَهِیدࣰا مَّا دُمۡتُ فِیهِمۡۖ فَلَمَّا تَوَفَّیۡتَنِی كُنتَ أَنتَ ٱلرَّقِیبَ عَلَیۡهِمۡۚ وَأَنتَ عَلَىٰ كُلِّ شَیۡءࣲ شَهِیدٌ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5104, 5, 787, 118, 'إِن تُعَذِّبۡهُمۡ فَإِنَّهُمۡ عِبَادُكَۖ وَإِن تَغۡفِرۡ لَهُمۡ فَإِنَّكَ أَنتَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:57'),
(5105, 5, 788, 119, 'قَالَ ٱللَّهُ هَـٰذَا یَوۡمُ یَنفَعُ ٱلصَّـٰدِقِینَ صِدۡقُهُمۡۚ لَهُمۡ جَنَّـٰتࣱ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۖ رَّضِیَ ٱللَّهُ عَنۡهُمۡ وَرَضُوا۟ عَنۡهُۚ ذَ ٰ⁠لِكَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:58'),
(5106, 5, 789, 120, 'لِلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا فِیهِنَّۚ وَهُوَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرُۢ\n', NULL, 0, '2023-05-26 17:33:58', '2023-05-27 11:18:58'),
(5107, 4, 494, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ یَـٰۤأَیُّهَا ٱلنَّاسُ ٱتَّقُوا۟ رَبَّكُمُ ٱلَّذِی خَلَقَكُم مِّن نَّفۡسࣲ وَ ٰ⁠حِدَةࣲ وَخَلَقَ مِنۡهَا زَوۡجَهَا وَبَثَّ مِنۡهُمَا رِجَالࣰا كَثِیرࣰا وَنِسَاۤءࣰۚ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِی تَسَاۤءَلُونَ بِهِۦ وَٱلۡأَرۡحَامَۚ إِنَّ ٱللَّهَ كَانَ عَلَیۡكُمۡ رَقِیبࣰا\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:06'),
(5108, 4, 495, 2, 'وَءَاتُوا۟ ٱلۡیَتَـٰمَىٰۤ أَمۡوَ ٰ⁠لَهُمۡۖ وَلَا تَتَبَدَّلُوا۟ ٱلۡخَبِیثَ بِٱلطَّیِّبِۖ وَلَا تَأۡكُلُوۤا۟ أَمۡوَ ٰ⁠لَهُمۡ إِلَىٰۤ أَمۡوَ ٰ⁠لِكُمۡۚ إِنَّهُۥ كَانَ حُوبࣰا كَبِیرࣰا\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:07'),
(5109, 4, 496, 3, 'وَإِنۡ خِفۡتُمۡ أَلَّا تُقۡسِطُوا۟ فِی ٱلۡیَتَـٰمَىٰ فَٱنكِحُوا۟ مَا طَابَ لَكُم مِّنَ ٱلنِّسَاۤءِ مَثۡنَىٰ وَثُلَـٰثَ وَرُبَـٰعَۖ فَإِنۡ خِفۡتُمۡ أَلَّا تَعۡدِلُوا۟ فَوَ ٰ⁠حِدَةً أَوۡ مَا مَلَكَتۡ أَیۡمَـٰنُكُمۡۚ ذَ ٰ⁠لِكَ أَدۡنَىٰۤ أَلَّا تَعُولُوا۟\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:07'),
(5110, 4, 497, 4, 'وَءَاتُوا۟ ٱلنِّسَاۤءَ صَدُقَـٰتِهِنَّ نِحۡلَةࣰۚ فَإِن طِبۡنَ لَكُمۡ عَن شَیۡءࣲ مِّنۡهُ نَفۡسࣰا فَكُلُوهُ هَنِیۤـࣰٔا مَّرِیۤـࣰٔا\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:07'),
(5111, 4, 498, 5, 'وَلَا تُؤۡتُوا۟ ٱلسُّفَهَاۤءَ أَمۡوَ ٰ⁠لَكُمُ ٱلَّتِی جَعَلَ ٱللَّهُ لَكُمۡ قِیَـٰمࣰا وَٱرۡزُقُوهُمۡ فِیهَا وَٱكۡسُوهُمۡ وَقُولُوا۟ لَهُمۡ قَوۡلࣰا مَّعۡرُوفࣰا\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:07'),
(5112, 4, 499, 6, 'وَٱبۡتَلُوا۟ ٱلۡیَتَـٰمَىٰ حَتَّىٰۤ إِذَا بَلَغُوا۟ ٱلنِّكَاحَ فَإِنۡ ءَانَسۡتُم مِّنۡهُمۡ رُشۡدࣰا فَٱدۡفَعُوۤا۟ إِلَیۡهِمۡ أَمۡوَ ٰ⁠لَهُمۡۖ وَلَا تَأۡكُلُوهَاۤ إِسۡرَافࣰا وَبِدَارًا أَن یَكۡبَرُوا۟ۚ وَمَن كَانَ غَنِیࣰّا فَلۡیَسۡتَعۡفِفۡۖ وَمَن كَانَ فَقِیرࣰا فَلۡیَأۡكُلۡ بِٱلۡمَعۡرُوفِۚ فَإِذَا دَفَعۡتُمۡ إِلَیۡهِمۡ أَمۡوَ ٰ⁠لَهُمۡ فَأَشۡهِدُوا۟ عَلَیۡهِمۡۚ وَكَفَىٰ بِٱللَّهِ حَسِیبࣰا\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:07'),
(5113, 4, 500, 7, 'لِّلرِّجَالِ نَصِیبࣱ مِّمَّا تَرَكَ ٱلۡوَ ٰ⁠لِدَانِ وَٱلۡأَقۡرَبُونَ وَلِلنِّسَاۤءِ نَصِیبࣱ مِّمَّا تَرَكَ ٱلۡوَ ٰ⁠لِدَانِ وَٱلۡأَقۡرَبُونَ مِمَّا قَلَّ مِنۡهُ أَوۡ كَثُرَۚ نَصِیبࣰا مَّفۡرُوضࣰا\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:07'),
(5114, 4, 501, 8, 'وَإِذَا حَضَرَ ٱلۡقِسۡمَةَ أُو۟لُوا۟ ٱلۡقُرۡبَىٰ وَٱلۡیَتَـٰمَىٰ وَٱلۡمَسَـٰكِینُ فَٱرۡزُقُوهُم مِّنۡهُ وَقُولُوا۟ لَهُمۡ قَوۡلࣰا مَّعۡرُوفࣰا\n', NULL, 0, '2023-05-26 17:34:25', '2023-05-27 11:19:07'),
(5115, 4, 502, 9, 'وَلۡیَخۡشَ ٱلَّذِینَ لَوۡ تَرَكُوا۟ مِنۡ خَلۡفِهِمۡ ذُرِّیَّةࣰ ضِعَـٰفًا خَافُوا۟ عَلَیۡهِمۡ فَلۡیَتَّقُوا۟ ٱللَّهَ وَلۡیَقُولُوا۟ قَوۡلࣰا سَدِیدًا\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5116, 4, 503, 10, 'إِنَّ ٱلَّذِینَ یَأۡكُلُونَ أَمۡوَ ٰ⁠لَ ٱلۡیَتَـٰمَىٰ ظُلۡمًا إِنَّمَا یَأۡكُلُونَ فِی بُطُونِهِمۡ نَارࣰاۖ وَسَیَصۡلَوۡنَ سَعِیرࣰا\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5117, 4, 504, 11, 'یُوصِیكُمُ ٱللَّهُ فِیۤ أَوۡلَـٰدِكُمۡۖ لِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَیَیۡنِۚ فَإِن كُنَّ نِسَاۤءࣰ فَوۡقَ ٱثۡنَتَیۡنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۖ وَإِن كَانَتۡ وَ ٰ⁠حِدَةࣰ فَلَهَا ٱلنِّصۡفُۚ وَلِأَبَوَیۡهِ لِكُلِّ وَ ٰ⁠حِدࣲ مِّنۡهُمَا ٱلسُّدُسُ مِمَّا تَرَكَ إِن كَانَ لَهُۥ وَلَدࣱۚ فَإِن لَّمۡ یَكُن لَّهُۥ وَلَدࣱ وَوَرِثَهُۥۤ أَبَوَاهُ فَلِأُمِّهِ ٱلثُّلُثُۚ فَإِن كَانَ لَهُۥۤ إِخۡوَةࣱ فَلِأُمِّهِ ٱلسُّدُسُۚ مِنۢ بَعۡدِ وَصِیَّةࣲ یُوصِی بِهَاۤ أَوۡ دَیۡنٍۗ ءَابَاۤؤُكُمۡ وَأَبۡنَاۤؤُكُمۡ لَا تَدۡرُونَ أَیُّهُمۡ أَقۡرَبُ لَكُمۡ نَفۡعࣰاۚ فَرِیضَةࣰ مِّنَ ٱللَّهِۗ إِنَّ ٱللَّهَ كَانَ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5118, 4, 505, 12, '۞ وَلَكُمۡ نِصۡفُ مَا تَرَكَ أَزۡوَ ٰ⁠جُكُمۡ إِن لَّمۡ یَكُن لَّهُنَّ وَلَدࣱۚ فَإِن كَانَ لَهُنَّ وَلَدࣱ فَلَكُمُ ٱلرُّبُعُ مِمَّا تَرَكۡنَۚ مِنۢ بَعۡدِ وَصِیَّةࣲ یُوصِینَ بِهَاۤ أَوۡ دَیۡنࣲۚ وَلَهُنَّ ٱلرُّبُعُ مِمَّا تَرَكۡتُمۡ إِن لَّمۡ یَكُن لَّكُمۡ وَلَدࣱۚ فَإِن كَانَ لَكُمۡ وَلَدࣱ فَلَهُنَّ ٱلثُّمُنُ مِمَّا تَرَكۡتُمۚ مِّنۢ بَعۡدِ وَصِیَّةࣲ تُوصُونَ بِهَاۤ أَوۡ دَیۡنࣲۗ وَإِن كَانَ رَجُلࣱ یُورَثُ كَلَـٰلَةً أَوِ ٱمۡرَأَةࣱ وَلَهُۥۤ أَخٌ أَوۡ أُخۡتࣱ فَلِكُلِّ وَ ٰ⁠حِدࣲ مِّنۡهُمَا ٱلسُّدُسُۚ فَإِن كَانُوۤا۟ أَكۡثَرَ مِن ذَ ٰ⁠لِكَ فَهُمۡ شُرَكَاۤءُ فِی ٱلثُّلُثِۚ مِنۢ بَعۡدِ وَصِیَّةࣲ یُوصَىٰ بِهَاۤ أَوۡ دَیۡنٍ غَیۡرَ مُضَاۤرࣲّۚ وَصِیَّةࣰ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِیمٌ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5119, 4, 506, 13, 'تِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن یُطِعِ ٱللَّهَ وَرَسُولَهُۥ یُدۡخِلۡهُ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ وَذَ ٰ⁠لِكَ ٱلۡفَوۡزُ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5120, 4, 507, 14, 'وَمَن یَعۡصِ ٱللَّهَ وَرَسُولَهُۥ وَیَتَعَدَّ حُدُودَهُۥ یُدۡخِلۡهُ نَارًا خَـٰلِدࣰا فِیهَا وَلَهُۥ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5121, 4, 508, 15, 'وَٱلَّـٰتِی یَأۡتِینَ ٱلۡفَـٰحِشَةَ مِن نِّسَاۤىِٕكُمۡ فَٱسۡتَشۡهِدُوا۟ عَلَیۡهِنَّ أَرۡبَعَةࣰ مِّنكُمۡۖ فَإِن شَهِدُوا۟ فَأَمۡسِكُوهُنَّ فِی ٱلۡبُیُوتِ حَتَّىٰ یَتَوَفَّىٰهُنَّ ٱلۡمَوۡتُ أَوۡ یَجۡعَلَ ٱللَّهُ لَهُنَّ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5122, 4, 509, 16, 'وَٱلَّذَانِ یَأۡتِیَـٰنِهَا مِنكُمۡ فَـَٔاذُوهُمَاۖ فَإِن تَابَا وَأَصۡلَحَا فَأَعۡرِضُوا۟ عَنۡهُمَاۤۗ إِنَّ ٱللَّهَ كَانَ تَوَّابࣰا رَّحِیمًا\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5123, 4, 510, 17, 'إِنَّمَا ٱلتَّوۡبَةُ عَلَى ٱللَّهِ لِلَّذِینَ یَعۡمَلُونَ ٱلسُّوۤءَ بِجَهَـٰلَةࣲ ثُمَّ یَتُوبُونَ مِن قَرِیبࣲ فَأُو۟لَـٰۤىِٕكَ یَتُوبُ ٱللَّهُ عَلَیۡهِمۡۗ وَكَانَ ٱللَّهُ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:26', '2023-05-27 11:19:07'),
(5124, 4, 511, 18, 'وَلَیۡسَتِ ٱلتَّوۡبَةُ لِلَّذِینَ یَعۡمَلُونَ ٱلسَّیِّـَٔاتِ حَتَّىٰۤ إِذَا حَضَرَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ إِنِّی تُبۡتُ ٱلۡـَٔـٰنَ وَلَا ٱلَّذِینَ یَمُوتُونَ وَهُمۡ كُفَّارٌۚ أُو۟لَـٰۤىِٕكَ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:07'),
(5125, 4, 512, 19, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا یَحِلُّ لَكُمۡ أَن تَرِثُوا۟ ٱلنِّسَاۤءَ كَرۡهࣰاۖ وَلَا تَعۡضُلُوهُنَّ لِتَذۡهَبُوا۟ بِبَعۡضِ مَاۤ ءَاتَیۡتُمُوهُنَّ إِلَّاۤ أَن یَأۡتِینَ بِفَـٰحِشَةࣲ مُّبَیِّنَةࣲۚ وَعَاشِرُوهُنَّ بِٱلۡمَعۡرُوفِۚ فَإِن كَرِهۡتُمُوهُنَّ فَعَسَىٰۤ أَن تَكۡرَهُوا۟ شَیۡـࣰٔا وَیَجۡعَلَ ٱللَّهُ فِیهِ خَیۡرࣰا كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5126, 4, 513, 20, 'وَإِنۡ أَرَدتُّمُ ٱسۡتِبۡدَالَ زَوۡجࣲ مَّكَانَ زَوۡجࣲ وَءَاتَیۡتُمۡ إِحۡدَىٰهُنَّ قِنطَارࣰا فَلَا تَأۡخُذُوا۟ مِنۡهُ شَیۡـًٔاۚ أَتَأۡخُذُونَهُۥ بُهۡتَـٰنࣰا وَإِثۡمࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5127, 4, 514, 21, 'وَكَیۡفَ تَأۡخُذُونَهُۥ وَقَدۡ أَفۡضَىٰ بَعۡضُكُمۡ إِلَىٰ بَعۡضࣲ وَأَخَذۡنَ مِنكُم مِّیثَـٰقًا غَلِیظࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5128, 4, 515, 22, 'وَلَا تَنكِحُوا۟ مَا نَكَحَ ءَابَاۤؤُكُم مِّنَ ٱلنِّسَاۤءِ إِلَّا مَا قَدۡ سَلَفَۚ إِنَّهُۥ كَانَ فَـٰحِشَةࣰ وَمَقۡتࣰا وَسَاۤءَ سَبِیلًا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5129, 4, 516, 23, 'حُرِّمَتۡ عَلَیۡكُمۡ أُمَّهَـٰتُكُمۡ وَبَنَاتُكُمۡ وَأَخَوَ ٰ⁠تُكُمۡ وَعَمَّـٰتُكُمۡ وَخَـٰلَـٰتُكُمۡ وَبَنَاتُ ٱلۡأَخِ وَبَنَاتُ ٱلۡأُخۡتِ وَأُمَّهَـٰتُكُمُ ٱلَّـٰتِیۤ أَرۡضَعۡنَكُمۡ وَأَخَوَ ٰ⁠تُكُم مِّنَ ٱلرَّضَـٰعَةِ وَأُمَّهَـٰتُ نِسَاۤىِٕكُمۡ وَرَبَـٰۤىِٕبُكُمُ ٱلَّـٰتِی فِی حُجُورِكُم مِّن نِّسَاۤىِٕكُمُ ٱلَّـٰتِی دَخَلۡتُم بِهِنَّ فَإِن لَّمۡ تَكُونُوا۟ دَخَلۡتُم بِهِنَّ فَلَا جُنَاحَ عَلَیۡكُمۡ وَحَلَـٰۤىِٕلُ أَبۡنَاۤىِٕكُمُ ٱلَّذِینَ مِنۡ أَصۡلَـٰبِكُمۡ وَأَن تَجۡمَعُوا۟ بَیۡنَ ٱلۡأُخۡتَیۡنِ إِلَّا مَا قَدۡ سَلَفَۗ إِنَّ ٱللَّهَ كَانَ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5130, 4, 517, 24, '۞ وَٱلۡمُحۡصَنَـٰتُ مِنَ ٱلنِّسَاۤءِ إِلَّا مَا مَلَكَتۡ أَیۡمَـٰنُكُمۡۖ كِتَـٰبَ ٱللَّهِ عَلَیۡكُمۡۚ وَأُحِلَّ لَكُم مَّا وَرَاۤءَ ذَ ٰ⁠لِكُمۡ أَن تَبۡتَغُوا۟ بِأَمۡوَ ٰ⁠لِكُم مُّحۡصِنِینَ غَیۡرَ مُسَـٰفِحِینَۚ فَمَا ٱسۡتَمۡتَعۡتُم بِهِۦ مِنۡهُنَّ فَـَٔاتُوهُنَّ أُجُورَهُنَّ فَرِیضَةࣰۚ وَلَا جُنَاحَ عَلَیۡكُمۡ فِیمَا تَرَ ٰ⁠ضَیۡتُم بِهِۦ مِنۢ بَعۡدِ ٱلۡفَرِیضَةِۚ إِنَّ ٱللَّهَ كَانَ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5131, 4, 518, 25, 'وَمَن لَّمۡ یَسۡتَطِعۡ مِنكُمۡ طَوۡلًا أَن یَنكِحَ ٱلۡمُحۡصَنَـٰتِ ٱلۡمُؤۡمِنَـٰتِ فَمِن مَّا مَلَكَتۡ أَیۡمَـٰنُكُم مِّن فَتَیَـٰتِكُمُ ٱلۡمُؤۡمِنَـٰتِۚ وَٱللَّهُ أَعۡلَمُ بِإِیمَـٰنِكُمۚ بَعۡضُكُم مِّنۢ بَعۡضࣲۚ فَٱنكِحُوهُنَّ بِإِذۡنِ أَهۡلِهِنَّ وَءَاتُوهُنَّ أُجُورَهُنَّ بِٱلۡمَعۡرُوفِ مُحۡصَنَـٰتٍ غَیۡرَ مُسَـٰفِحَـٰتࣲ وَلَا مُتَّخِذَ ٰ⁠تِ أَخۡدَانࣲۚ فَإِذَاۤ أُحۡصِنَّ فَإِنۡ أَتَیۡنَ بِفَـٰحِشَةࣲ فَعَلَیۡهِنَّ نِصۡفُ مَا عَلَى ٱلۡمُحۡصَنَـٰتِ مِنَ ٱلۡعَذَابِۚ ذَ ٰ⁠لِكَ لِمَنۡ خَشِیَ ٱلۡعَنَتَ مِنكُمۡۚ وَأَن تَصۡبِرُوا۟ خَیۡرࣱ لَّكُمۡۗ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5132, 4, 519, 26, 'یُرِیدُ ٱللَّهُ لِیُبَیِّنَ لَكُمۡ وَیَهۡدِیَكُمۡ سُنَنَ ٱلَّذِینَ مِن قَبۡلِكُمۡ وَیَتُوبَ عَلَیۡكُمۡۗ وَٱللَّهُ عَلِیمٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5133, 4, 520, 27, 'وَٱللَّهُ یُرِیدُ أَن یَتُوبَ عَلَیۡكُمۡ وَیُرِیدُ ٱلَّذِینَ یَتَّبِعُونَ ٱلشَّهَوَ ٰ⁠تِ أَن تَمِیلُوا۟ مَیۡلًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5134, 4, 521, 28, 'یُرِیدُ ٱللَّهُ أَن یُخَفِّفَ عَنكُمۡۚ وَخُلِقَ ٱلۡإِنسَـٰنُ ضَعِیفࣰا\n', NULL, 0, '2023-05-26 17:34:27', '2023-05-27 11:19:08'),
(5135, 4, 522, 29, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَأۡكُلُوۤا۟ أَمۡوَ ٰ⁠لَكُم بَیۡنَكُم بِٱلۡبَـٰطِلِ إِلَّاۤ أَن تَكُونَ تِجَـٰرَةً عَن تَرَاضࣲ مِّنكُمۡۚ وَلَا تَقۡتُلُوۤا۟ أَنفُسَكُمۡۚ إِنَّ ٱللَّهَ كَانَ بِكُمۡ رَحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:28', '2023-05-27 11:19:08'),
(5136, 4, 523, 30, 'وَمَن یَفۡعَلۡ ذَ ٰ⁠لِكَ عُدۡوَ ٰ⁠نࣰا وَظُلۡمࣰا فَسَوۡفَ نُصۡلِیهِ نَارࣰاۚ وَكَانَ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرًا\n', NULL, 0, '2023-05-26 17:34:28', '2023-05-27 11:19:08'),
(5137, 4, 524, 31, 'إِن تَجۡتَنِبُوا۟ كَبَاۤىِٕرَ مَا تُنۡهَوۡنَ عَنۡهُ نُكَفِّرۡ عَنكُمۡ سَیِّـَٔاتِكُمۡ وَنُدۡخِلۡكُم مُّدۡخَلࣰا كَرِیمࣰا\n', NULL, 0, '2023-05-26 17:34:28', '2023-05-27 11:19:08'),
(5138, 4, 525, 32, 'وَلَا تَتَمَنَّوۡا۟ مَا فَضَّلَ ٱللَّهُ بِهِۦ بَعۡضَكُمۡ عَلَىٰ بَعۡضࣲۚ لِّلرِّجَالِ نَصِیبࣱ مِّمَّا ٱكۡتَسَبُوا۟ۖ وَلِلنِّسَاۤءِ نَصِیبࣱ مِّمَّا ٱكۡتَسَبۡنَۚ وَسۡـَٔلُوا۟ ٱللَّهَ مِن فَضۡلِهِۦۤۚ إِنَّ ٱللَّهَ كَانَ بِكُلِّ شَیۡءٍ عَلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:28', '2023-05-27 11:19:08'),
(5139, 4, 526, 33, 'وَلِكُلࣲّ جَعَلۡنَا مَوَ ٰ⁠لِیَ مِمَّا تَرَكَ ٱلۡوَ ٰ⁠لِدَانِ وَٱلۡأَقۡرَبُونَۚ وَٱلَّذِینَ عَقَدَتۡ أَیۡمَـٰنُكُمۡ فَـَٔاتُوهُمۡ نَصِیبَهُمۡۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَیۡءࣲ شَهِیدًا\n', NULL, 0, '2023-05-26 17:34:28', '2023-05-27 11:19:08'),
(5140, 4, 527, 34, 'ٱلرِّجَالُ قَوَّ ٰ⁠مُونَ عَلَى ٱلنِّسَاۤءِ بِمَا فَضَّلَ ٱللَّهُ بَعۡضَهُمۡ عَلَىٰ بَعۡضࣲ وَبِمَاۤ أَنفَقُوا۟ مِنۡ أَمۡوَ ٰ⁠لِهِمۡۚ فَٱلصَّـٰلِحَـٰتُ قَـٰنِتَـٰتٌ حَـٰفِظَـٰتࣱ لِّلۡغَیۡبِ بِمَا حَفِظَ ٱللَّهُۚ وَٱلَّـٰتِی تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَٱهۡجُرُوهُنَّ فِی ٱلۡمَضَاجِعِ وَٱضۡرِبُوهُنَّۖ فَإِنۡ أَطَعۡنَكُمۡ فَلَا تَبۡغُوا۟ عَلَیۡهِنَّ سَبِیلًاۗ إِنَّ ٱللَّهَ كَانَ عَلِیࣰّا كَبِیرࣰا\n', NULL, 0, '2023-05-26 17:34:28', '2023-05-27 11:19:08'),
(5141, 4, 528, 35, 'وَإِنۡ خِفۡتُمۡ شِقَاقَ بَیۡنِهِمَا فَٱبۡعَثُوا۟ حَكَمࣰا مِّنۡ أَهۡلِهِۦ وَحَكَمࣰا مِّنۡ أَهۡلِهَاۤ إِن یُرِیدَاۤ إِصۡلَـٰحࣰا یُوَفِّقِ ٱللَّهُ بَیۡنَهُمَاۤۗ إِنَّ ٱللَّهَ كَانَ عَلِیمًا خَبِیرࣰا\n', NULL, 0, '2023-05-26 17:34:28', '2023-05-27 11:19:08');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(5142, 4, 529, 36, '۞ وَٱعۡبُدُوا۟ ٱللَّهَ وَلَا تُشۡرِكُوا۟ بِهِۦ شَیۡـࣰٔاۖ وَبِٱلۡوَ ٰ⁠لِدَیۡنِ إِحۡسَـٰنࣰا وَبِذِی ٱلۡقُرۡبَىٰ وَٱلۡیَتَـٰمَىٰ وَٱلۡمَسَـٰكِینِ وَٱلۡجَارِ ذِی ٱلۡقُرۡبَىٰ وَٱلۡجَارِ ٱلۡجُنُبِ وَٱلصَّاحِبِ بِٱلۡجَنۢبِ وَٱبۡنِ ٱلسَّبِیلِ وَمَا مَلَكَتۡ أَیۡمَـٰنُكُمۡۗ إِنَّ ٱللَّهَ لَا یُحِبُّ مَن كَانَ مُخۡتَالࣰا فَخُورًا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:08'),
(5143, 4, 530, 37, 'ٱلَّذِینَ یَبۡخَلُونَ وَیَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِ وَیَكۡتُمُونَ مَاۤ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَأَعۡتَدۡنَا لِلۡكَـٰفِرِینَ عَذَابࣰا مُّهِینࣰا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5144, 4, 531, 38, 'وَٱلَّذِینَ یُنفِقُونَ أَمۡوَ ٰ⁠لَهُمۡ رِئَاۤءَ ٱلنَّاسِ وَلَا یُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡیَوۡمِ ٱلۡـَٔاخِرِۗ وَمَن یَكُنِ ٱلشَّیۡطَـٰنُ لَهُۥ قَرِینࣰا فَسَاۤءَ قَرِینࣰا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5145, 4, 532, 39, 'وَمَاذَا عَلَیۡهِمۡ لَوۡ ءَامَنُوا۟ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَأَنفَقُوا۟ مِمَّا رَزَقَهُمُ ٱللَّهُۚ وَكَانَ ٱللَّهُ بِهِمۡ عَلِیمًا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5146, 4, 533, 40, 'إِنَّ ٱللَّهَ لَا یَظۡلِمُ مِثۡقَالَ ذَرَّةࣲۖ وَإِن تَكُ حَسَنَةࣰ یُضَـٰعِفۡهَا وَیُؤۡتِ مِن لَّدُنۡهُ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5147, 4, 534, 41, 'فَكَیۡفَ إِذَا جِئۡنَا مِن كُلِّ أُمَّةِۭ بِشَهِیدࣲ وَجِئۡنَا بِكَ عَلَىٰ هَـٰۤؤُلَاۤءِ شَهِیدࣰا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5148, 4, 535, 42, 'یَوۡمَىِٕذࣲ یَوَدُّ ٱلَّذِینَ كَفَرُوا۟ وَعَصَوُا۟ ٱلرَّسُولَ لَوۡ تُسَوَّىٰ بِهِمُ ٱلۡأَرۡضُ وَلَا یَكۡتُمُونَ ٱللَّهَ حَدِیثࣰا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5149, 4, 536, 43, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَقۡرَبُوا۟ ٱلصَّلَوٰةَ وَأَنتُمۡ سُكَـٰرَىٰ حَتَّىٰ تَعۡلَمُوا۟ مَا تَقُولُونَ وَلَا جُنُبًا إِلَّا عَابِرِی سَبِیلٍ حَتَّىٰ تَغۡتَسِلُوا۟ۚ وَإِن كُنتُم مَّرۡضَىٰۤ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَاۤءَ أَحَدࣱ مِّنكُم مِّنَ ٱلۡغَاۤىِٕطِ أَوۡ لَـٰمَسۡتُمُ ٱلنِّسَاۤءَ فَلَمۡ تَجِدُوا۟ مَاۤءࣰ فَتَیَمَّمُوا۟ صَعِیدࣰا طَیِّبࣰا فَٱمۡسَحُوا۟ بِوُجُوهِكُمۡ وَأَیۡدِیكُمۡۗ إِنَّ ٱللَّهَ كَانَ عَفُوًّا غَفُورًا\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5150, 4, 537, 44, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ أُوتُوا۟ نَصِیبࣰا مِّنَ ٱلۡكِتَـٰبِ یَشۡتَرُونَ ٱلضَّلَـٰلَةَ وَیُرِیدُونَ أَن تَضِلُّوا۟ ٱلسَّبِیلَ\n', NULL, 0, '2023-05-26 17:34:29', '2023-05-27 11:19:09'),
(5151, 4, 538, 45, 'وَٱللَّهُ أَعۡلَمُ بِأَعۡدَاۤىِٕكُمۡۚ وَكَفَىٰ بِٱللَّهِ وَلِیࣰّا وَكَفَىٰ بِٱللَّهِ نَصِیرࣰا\n', NULL, 0, '2023-05-26 17:34:30', '2023-05-27 11:19:09'),
(5152, 4, 539, 46, 'مِّنَ ٱلَّذِینَ هَادُوا۟ یُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَیَقُولُونَ سَمِعۡنَا وَعَصَیۡنَا وَٱسۡمَعۡ غَیۡرَ مُسۡمَعࣲ وَرَ ٰ⁠عِنَا لَیَّۢا بِأَلۡسِنَتِهِمۡ وَطَعۡنࣰا فِی ٱلدِّینِۚ وَلَوۡ أَنَّهُمۡ قَالُوا۟ سَمِعۡنَا وَأَطَعۡنَا وَٱسۡمَعۡ وَٱنظُرۡنَا لَكَانَ خَیۡرࣰا لَّهُمۡ وَأَقۡوَمَ وَلَـٰكِن لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَلَا یُؤۡمِنُونَ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:34:30', '2023-05-27 11:19:09'),
(5153, 4, 540, 47, 'یَـٰۤأَیُّهَا ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ ءَامِنُوا۟ بِمَا نَزَّلۡنَا مُصَدِّقࣰا لِّمَا مَعَكُم مِّن قَبۡلِ أَن نَّطۡمِسَ وُجُوهࣰا فَنَرُدَّهَا عَلَىٰۤ أَدۡبَارِهَاۤ أَوۡ نَلۡعَنَهُمۡ كَمَا لَعَنَّاۤ أَصۡحَـٰبَ ٱلسَّبۡتِۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولًا\n', NULL, 0, '2023-05-26 17:34:30', '2023-05-27 11:19:09'),
(5154, 4, 541, 48, 'إِنَّ ٱللَّهَ لَا یَغۡفِرُ أَن یُشۡرَكَ بِهِۦ وَیَغۡفِرُ مَا دُونَ ذَ ٰ⁠لِكَ لِمَن یَشَاۤءُۚ وَمَن یُشۡرِكۡ بِٱللَّهِ فَقَدِ ٱفۡتَرَىٰۤ إِثۡمًا عَظِیمًا\n', NULL, 0, '2023-05-26 17:34:30', '2023-05-27 11:19:09'),
(5155, 4, 542, 49, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ یُزَكُّونَ أَنفُسَهُمۚ بَلِ ٱللَّهُ یُزَكِّی مَن یَشَاۤءُ وَلَا یُظۡلَمُونَ فَتِیلًا\n', NULL, 0, '2023-05-26 17:34:30', '2023-05-27 11:19:09'),
(5156, 4, 543, 50, 'ٱنظُرۡ كَیۡفَ یَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَكَفَىٰ بِهِۦۤ إِثۡمࣰا مُّبِینًا\n', NULL, 0, '2023-05-26 17:34:31', '2023-05-27 11:19:09'),
(5157, 4, 544, 51, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ أُوتُوا۟ نَصِیبࣰا مِّنَ ٱلۡكِتَـٰبِ یُؤۡمِنُونَ بِٱلۡجِبۡتِ وَٱلطَّـٰغُوتِ وَیَقُولُونَ لِلَّذِینَ كَفَرُوا۟ هَـٰۤؤُلَاۤءِ أَهۡدَىٰ مِنَ ٱلَّذِینَ ءَامَنُوا۟ سَبِیلًا\n', NULL, 0, '2023-05-26 17:34:31', '2023-05-27 11:19:09'),
(5158, 4, 545, 52, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ لَعَنَهُمُ ٱللَّهُۖ وَمَن یَلۡعَنِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ نَصِیرًا\n', NULL, 0, '2023-05-26 17:34:31', '2023-05-27 11:19:10'),
(5159, 4, 546, 53, 'أَمۡ لَهُمۡ نَصِیبࣱ مِّنَ ٱلۡمُلۡكِ فَإِذࣰا لَّا یُؤۡتُونَ ٱلنَّاسَ نَقِیرًا\n', NULL, 0, '2023-05-26 17:34:31', '2023-05-27 11:19:10'),
(5160, 4, 547, 54, 'أَمۡ یَحۡسُدُونَ ٱلنَّاسَ عَلَىٰ مَاۤ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۖ فَقَدۡ ءَاتَیۡنَاۤ ءَالَ إِبۡرَ ٰ⁠هِیمَ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَءَاتَیۡنَـٰهُم مُّلۡكًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:31', '2023-05-27 11:19:10'),
(5161, 4, 548, 55, 'فَمِنۡهُم مَّنۡ ءَامَنَ بِهِۦ وَمِنۡهُم مَّن صَدَّ عَنۡهُۚ وَكَفَىٰ بِجَهَنَّمَ سَعِیرًا\n', NULL, 0, '2023-05-26 17:34:31', '2023-05-27 11:19:10'),
(5162, 4, 549, 56, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ بِـَٔایَـٰتِنَا سَوۡفَ نُصۡلِیهِمۡ نَارࣰا كُلَّمَا نَضِجَتۡ جُلُودُهُم بَدَّلۡنَـٰهُمۡ جُلُودًا غَیۡرَهَا لِیَذُوقُوا۟ ٱلۡعَذَابَۗ إِنَّ ٱللَّهَ كَانَ عَزِیزًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:32', '2023-05-27 11:19:10'),
(5163, 4, 550, 57, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۖ لَّهُمۡ فِیهَاۤ أَزۡوَ ٰ⁠جࣱ مُّطَهَّرَةࣱۖ وَنُدۡخِلُهُمۡ ظِلࣰّا ظَلِیلًا\n', NULL, 0, '2023-05-26 17:34:32', '2023-05-27 11:19:10'),
(5164, 4, 551, 58, '۞ إِنَّ ٱللَّهَ یَأۡمُرُكُمۡ أَن تُؤَدُّوا۟ ٱلۡأَمَـٰنَـٰتِ إِلَىٰۤ أَهۡلِهَا وَإِذَا حَكَمۡتُم بَیۡنَ ٱلنَّاسِ أَن تَحۡكُمُوا۟ بِٱلۡعَدۡلِۚ إِنَّ ٱللَّهَ نِعِمَّا یَعِظُكُم بِهِۦۤۗ إِنَّ ٱللَّهَ كَانَ سَمِیعَۢا بَصِیرࣰا\n', NULL, 0, '2023-05-26 17:34:32', '2023-05-27 11:19:10'),
(5165, 4, 552, 59, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ أَطِیعُوا۟ ٱللَّهَ وَأَطِیعُوا۟ ٱلرَّسُولَ وَأُو۟لِی ٱلۡأَمۡرِ مِنكُمۡۖ فَإِن تَنَـٰزَعۡتُمۡ فِی شَیۡءࣲ فَرُدُّوهُ إِلَى ٱللَّهِ وَٱلرَّسُولِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِۚ ذَ ٰ⁠لِكَ خَیۡرࣱ وَأَحۡسَنُ تَأۡوِیلًا\n', NULL, 0, '2023-05-26 17:34:32', '2023-05-27 11:19:10'),
(5166, 4, 553, 60, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ یَزۡعُمُونَ أَنَّهُمۡ ءَامَنُوا۟ بِمَاۤ أُنزِلَ إِلَیۡكَ وَمَاۤ أُنزِلَ مِن قَبۡلِكَ یُرِیدُونَ أَن یَتَحَاكَمُوۤا۟ إِلَى ٱلطَّـٰغُوتِ وَقَدۡ أُمِرُوۤا۟ أَن یَكۡفُرُوا۟ بِهِۦۖ وَیُرِیدُ ٱلشَّیۡطَـٰنُ أَن یُضِلَّهُمۡ ضَلَـٰلَۢا بَعِیدࣰا\n', NULL, 0, '2023-05-26 17:34:32', '2023-05-27 11:19:10'),
(5167, 4, 554, 61, 'وَإِذَا قِیلَ لَهُمۡ تَعَالَوۡا۟ إِلَىٰ مَاۤ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ رَأَیۡتَ ٱلۡمُنَـٰفِقِینَ یَصُدُّونَ عَنكَ صُدُودࣰا\n', NULL, 0, '2023-05-26 17:34:32', '2023-05-27 11:19:10'),
(5168, 4, 555, 62, 'فَكَیۡفَ إِذَاۤ أَصَـٰبَتۡهُم مُّصِیبَةُۢ بِمَا قَدَّمَتۡ أَیۡدِیهِمۡ ثُمَّ جَاۤءُوكَ یَحۡلِفُونَ بِٱللَّهِ إِنۡ أَرَدۡنَاۤ إِلَّاۤ إِحۡسَـٰنࣰا وَتَوۡفِیقًا\n', NULL, 0, '2023-05-26 17:34:32', '2023-05-27 11:19:10'),
(5169, 4, 556, 63, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ یَعۡلَمُ ٱللَّهُ مَا فِی قُلُوبِهِمۡ فَأَعۡرِضۡ عَنۡهُمۡ وَعِظۡهُمۡ وَقُل لَّهُمۡ فِیۤ أَنفُسِهِمۡ قَوۡلَۢا بَلِیغࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:10'),
(5170, 4, 557, 64, 'وَمَاۤ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا لِیُطَاعَ بِإِذۡنِ ٱللَّهِۚ وَلَوۡ أَنَّهُمۡ إِذ ظَّلَمُوۤا۟ أَنفُسَهُمۡ جَاۤءُوكَ فَٱسۡتَغۡفَرُوا۟ ٱللَّهَ وَٱسۡتَغۡفَرَ لَهُمُ ٱلرَّسُولُ لَوَجَدُوا۟ ٱللَّهَ تَوَّابࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:10'),
(5171, 4, 558, 65, 'فَلَا وَرَبِّكَ لَا یُؤۡمِنُونَ حَتَّىٰ یُحَكِّمُوكَ فِیمَا شَجَرَ بَیۡنَهُمۡ ثُمَّ لَا یَجِدُوا۟ فِیۤ أَنفُسِهِمۡ حَرَجࣰا مِّمَّا قَضَیۡتَ وَیُسَلِّمُوا۟ تَسۡلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:10'),
(5172, 4, 559, 66, 'وَلَوۡ أَنَّا كَتَبۡنَا عَلَیۡهِمۡ أَنِ ٱقۡتُلُوۤا۟ أَنفُسَكُمۡ أَوِ ٱخۡرُجُوا۟ مِن دِیَـٰرِكُم مَّا فَعَلُوهُ إِلَّا قَلِیلࣱ مِّنۡهُمۡۖ وَلَوۡ أَنَّهُمۡ فَعَلُوا۟ مَا یُوعَظُونَ بِهِۦ لَكَانَ خَیۡرࣰا لَّهُمۡ وَأَشَدَّ تَثۡبِیتࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:11'),
(5173, 4, 560, 67, 'وَإِذࣰا لَّـَٔاتَیۡنَـٰهُم مِّن لَّدُنَّاۤ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:11'),
(5174, 4, 561, 68, 'وَلَهَدَیۡنَـٰهُمۡ صِرَ ٰ⁠طࣰا مُّسۡتَقِیمࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:11'),
(5175, 4, 562, 69, 'وَمَن یُطِعِ ٱللَّهَ وَٱلرَّسُولَ فَأُو۟لَـٰۤىِٕكَ مَعَ ٱلَّذِینَ أَنۡعَمَ ٱللَّهُ عَلَیۡهِم مِّنَ ٱلنَّبِیِّـۧنَ وَٱلصِّدِّیقِینَ وَٱلشُّهَدَاۤءِ وَٱلصَّـٰلِحِینَۚ وَحَسُنَ أُو۟لَـٰۤىِٕكَ رَفِیقࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:11'),
(5176, 4, 563, 70, 'ذَ ٰ⁠لِكَ ٱلۡفَضۡلُ مِنَ ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ عَلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:33', '2023-05-27 11:19:11'),
(5177, 4, 564, 71, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ خُذُوا۟ حِذۡرَكُمۡ فَٱنفِرُوا۟ ثُبَاتٍ أَوِ ٱنفِرُوا۟ جَمِیعࣰا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5178, 4, 565, 72, 'وَإِنَّ مِنكُمۡ لَمَن لَّیُبَطِّئَنَّ فَإِنۡ أَصَـٰبَتۡكُم مُّصِیبَةࣱ قَالَ قَدۡ أَنۡعَمَ ٱللَّهُ عَلَیَّ إِذۡ لَمۡ أَكُن مَّعَهُمۡ شَهِیدࣰا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5179, 4, 566, 73, 'وَلَىِٕنۡ أَصَـٰبَكُمۡ فَضۡلࣱ مِّنَ ٱللَّهِ لَیَقُولَنَّ كَأَن لَّمۡ تَكُنۢ بَیۡنَكُمۡ وَبَیۡنَهُۥ مَوَدَّةࣱ یَـٰلَیۡتَنِی كُنتُ مَعَهُمۡ فَأَفُوزَ فَوۡزًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5180, 4, 567, 74, '۞ فَلۡیُقَـٰتِلۡ فِی سَبِیلِ ٱللَّهِ ٱلَّذِینَ یَشۡرُونَ ٱلۡحَیَوٰةَ ٱلدُّنۡیَا بِٱلۡـَٔاخِرَةِۚ وَمَن یُقَـٰتِلۡ فِی سَبِیلِ ٱللَّهِ فَیُقۡتَلۡ أَوۡ یَغۡلِبۡ فَسَوۡفَ نُؤۡتِیهِ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5181, 4, 568, 75, 'وَمَا لَكُمۡ لَا تُقَـٰتِلُونَ فِی سَبِیلِ ٱللَّهِ وَٱلۡمُسۡتَضۡعَفِینَ مِنَ ٱلرِّجَالِ وَٱلنِّسَاۤءِ وَٱلۡوِلۡدَ ٰ⁠نِ ٱلَّذِینَ یَقُولُونَ رَبَّنَاۤ أَخۡرِجۡنَا مِنۡ هَـٰذِهِ ٱلۡقَرۡیَةِ ٱلظَّالِمِ أَهۡلُهَا وَٱجۡعَل لَّنَا مِن لَّدُنكَ وَلِیࣰّا وَٱجۡعَل لَّنَا مِن لَّدُنكَ نَصِیرًا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5182, 4, 569, 76, 'ٱلَّذِینَ ءَامَنُوا۟ یُقَـٰتِلُونَ فِی سَبِیلِ ٱللَّهِۖ وَٱلَّذِینَ كَفَرُوا۟ یُقَـٰتِلُونَ فِی سَبِیلِ ٱلطَّـٰغُوتِ فَقَـٰتِلُوۤا۟ أَوۡلِیَاۤءَ ٱلشَّیۡطَـٰنِۖ إِنَّ كَیۡدَ ٱلشَّیۡطَـٰنِ كَانَ ضَعِیفًا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5183, 4, 570, 77, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ قِیلَ لَهُمۡ كُفُّوۤا۟ أَیۡدِیَكُمۡ وَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ فَلَمَّا كُتِبَ عَلَیۡهِمُ ٱلۡقِتَالُ إِذَا فَرِیقࣱ مِّنۡهُمۡ یَخۡشَوۡنَ ٱلنَّاسَ كَخَشۡیَةِ ٱللَّهِ أَوۡ أَشَدَّ خَشۡیَةࣰۚ وَقَالُوا۟ رَبَّنَا لِمَ كَتَبۡتَ عَلَیۡنَا ٱلۡقِتَالَ لَوۡلَاۤ أَخَّرۡتَنَاۤ إِلَىٰۤ أَجَلࣲ قَرِیبࣲۗ قُلۡ مَتَـٰعُ ٱلدُّنۡیَا قَلِیلࣱ وَٱلۡـَٔاخِرَةُ خَیۡرࣱ لِّمَنِ ٱتَّقَىٰ وَلَا تُظۡلَمُونَ فَتِیلًا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5184, 4, 571, 78, 'أَیۡنَمَا تَكُونُوا۟ یُدۡرِككُّمُ ٱلۡمَوۡتُ وَلَوۡ كُنتُمۡ فِی بُرُوجࣲ مُّشَیَّدَةࣲۗ وَإِن تُصِبۡهُمۡ حَسَنَةࣱ یَقُولُوا۟ هَـٰذِهِۦ مِنۡ عِندِ ٱللَّهِۖ وَإِن تُصِبۡهُمۡ سَیِّئَةࣱ یَقُولُوا۟ هَـٰذِهِۦ مِنۡ عِندِكَۚ قُلۡ كُلࣱّ مِّنۡ عِندِ ٱللَّهِۖ فَمَالِ هَـٰۤؤُلَاۤءِ ٱلۡقَوۡمِ لَا یَكَادُونَ یَفۡقَهُونَ حَدِیثࣰا\n', NULL, 0, '2023-05-26 17:34:34', '2023-05-27 11:19:11'),
(5185, 4, 572, 79, 'مَّاۤ أَصَابَكَ مِنۡ حَسَنَةࣲ فَمِنَ ٱللَّهِۖ وَمَاۤ أَصَابَكَ مِن سَیِّئَةࣲ فَمِن نَّفۡسِكَۚ وَأَرۡسَلۡنَـٰكَ لِلنَّاسِ رَسُولࣰاۚ وَكَفَىٰ بِٱللَّهِ شَهِیدࣰا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:11'),
(5186, 4, 573, 80, 'مَّن یُطِعِ ٱلرَّسُولَ فَقَدۡ أَطَاعَ ٱللَّهَۖ وَمَن تَوَلَّىٰ فَمَاۤ أَرۡسَلۡنَـٰكَ عَلَیۡهِمۡ حَفِیظࣰا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:11'),
(5187, 4, 574, 81, 'وَیَقُولُونَ طَاعَةࣱ فَإِذَا بَرَزُوا۟ مِنۡ عِندِكَ بَیَّتَ طَاۤىِٕفَةࣱ مِّنۡهُمۡ غَیۡرَ ٱلَّذِی تَقُولُۖ وَٱللَّهُ یَكۡتُبُ مَا یُبَیِّتُونَۖ فَأَعۡرِضۡ عَنۡهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِیلًا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:11'),
(5188, 4, 575, 82, 'أَفَلَا یَتَدَبَّرُونَ ٱلۡقُرۡءَانَۚ وَلَوۡ كَانَ مِنۡ عِندِ غَیۡرِ ٱللَّهِ لَوَجَدُوا۟ فِیهِ ٱخۡتِلَـٰفࣰا كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:11'),
(5189, 4, 576, 83, 'وَإِذَا جَاۤءَهُمۡ أَمۡرࣱ مِّنَ ٱلۡأَمۡنِ أَوِ ٱلۡخَوۡفِ أَذَاعُوا۟ بِهِۦۖ وَلَوۡ رَدُّوهُ إِلَى ٱلرَّسُولِ وَإِلَىٰۤ أُو۟لِی ٱلۡأَمۡرِ مِنۡهُمۡ لَعَلِمَهُ ٱلَّذِینَ یَسۡتَنۢبِطُونَهُۥ مِنۡهُمۡۗ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَیۡكُمۡ وَرَحۡمَتُهُۥ لَٱتَّبَعۡتُمُ ٱلشَّیۡطَـٰنَ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:12'),
(5190, 4, 577, 84, 'فَقَـٰتِلۡ فِی سَبِیلِ ٱللَّهِ لَا تُكَلَّفُ إِلَّا نَفۡسَكَۚ وَحَرِّضِ ٱلۡمُؤۡمِنِینَۖ عَسَى ٱللَّهُ أَن یَكُفَّ بَأۡسَ ٱلَّذِینَ كَفَرُوا۟ۚ وَٱللَّهُ أَشَدُّ بَأۡسࣰا وَأَشَدُّ تَنكِیلࣰا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:12'),
(5191, 4, 578, 85, 'مَّن یَشۡفَعۡ شَفَـٰعَةً حَسَنَةࣰ یَكُن لَّهُۥ نَصِیبࣱ مِّنۡهَاۖ وَمَن یَشۡفَعۡ شَفَـٰعَةࣰ سَیِّئَةࣰ یَكُن لَّهُۥ كِفۡلࣱ مِّنۡهَاۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ مُّقِیتࣰا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:12'),
(5192, 4, 579, 86, 'وَإِذَا حُیِّیتُم بِتَحِیَّةࣲ فَحَیُّوا۟ بِأَحۡسَنَ مِنۡهَاۤ أَوۡ رُدُّوهَاۤۗ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَیۡءٍ حَسِیبًا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:12'),
(5193, 4, 580, 87, 'ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَۚ لَیَجۡمَعَنَّكُمۡ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ لَا رَیۡبَ فِیهِۗ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ حَدِیثࣰا\n', NULL, 0, '2023-05-26 17:34:35', '2023-05-27 11:19:12'),
(5194, 4, 581, 88, '۞ فَمَا لَكُمۡ فِی ٱلۡمُنَـٰفِقِینَ فِئَتَیۡنِ وَٱللَّهُ أَرۡكَسَهُم بِمَا كَسَبُوۤا۟ۚ أَتُرِیدُونَ أَن تَهۡدُوا۟ مَنۡ أَضَلَّ ٱللَّهُۖ وَمَن یُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5195, 4, 582, 89, 'وَدُّوا۟ لَوۡ تَكۡفُرُونَ كَمَا كَفَرُوا۟ فَتَكُونُونَ سَوَاۤءࣰۖ فَلَا تَتَّخِذُوا۟ مِنۡهُمۡ أَوۡلِیَاۤءَ حَتَّىٰ یُهَاجِرُوا۟ فِی سَبِیلِ ٱللَّهِۚ فَإِن تَوَلَّوۡا۟ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَیۡثُ وَجَدتُّمُوهُمۡۖ وَلَا تَتَّخِذُوا۟ مِنۡهُمۡ وَلِیࣰّا وَلَا نَصِیرًا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5196, 4, 583, 90, 'إِلَّا ٱلَّذِینَ یَصِلُونَ إِلَىٰ قَوۡمِۭ بَیۡنَكُمۡ وَبَیۡنَهُم مِّیثَـٰقٌ أَوۡ جَاۤءُوكُمۡ حَصِرَتۡ صُدُورُهُمۡ أَن یُقَـٰتِلُوكُمۡ أَوۡ یُقَـٰتِلُوا۟ قَوۡمَهُمۡۚ وَلَوۡ شَاۤءَ ٱللَّهُ لَسَلَّطَهُمۡ عَلَیۡكُمۡ فَلَقَـٰتَلُوكُمۡۚ فَإِنِ ٱعۡتَزَلُوكُمۡ فَلَمۡ یُقَـٰتِلُوكُمۡ وَأَلۡقَوۡا۟ إِلَیۡكُمُ ٱلسَّلَمَ فَمَا جَعَلَ ٱللَّهُ لَكُمۡ عَلَیۡهِمۡ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5197, 4, 584, 91, 'سَتَجِدُونَ ءَاخَرِینَ یُرِیدُونَ أَن یَأۡمَنُوكُمۡ وَیَأۡمَنُوا۟ قَوۡمَهُمۡ كُلَّ مَا رُدُّوۤا۟ إِلَى ٱلۡفِتۡنَةِ أُرۡكِسُوا۟ فِیهَاۚ فَإِن لَّمۡ یَعۡتَزِلُوكُمۡ وَیُلۡقُوۤا۟ إِلَیۡكُمُ ٱلسَّلَمَ وَیَكُفُّوۤا۟ أَیۡدِیَهُمۡ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَیۡثُ ثَقِفۡتُمُوهُمۡۚ وَأُو۟لَـٰۤىِٕكُمۡ جَعَلۡنَا لَكُمۡ عَلَیۡهِمۡ سُلۡطَـٰنࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5198, 4, 585, 92, 'وَمَا كَانَ لِمُؤۡمِنٍ أَن یَقۡتُلَ مُؤۡمِنًا إِلَّا خَطَـࣰٔاۚ وَمَن قَتَلَ مُؤۡمِنًا خَطَـࣰٔا فَتَحۡرِیرُ رَقَبَةࣲ مُّؤۡمِنَةࣲ وَدِیَةࣱ مُّسَلَّمَةٌ إِلَىٰۤ أَهۡلِهِۦۤ إِلَّاۤ أَن یَصَّدَّقُوا۟ۚ فَإِن كَانَ مِن قَوۡمٍ عَدُوࣲّ لَّكُمۡ وَهُوَ مُؤۡمِنࣱ فَتَحۡرِیرُ رَقَبَةࣲ مُّؤۡمِنَةࣲۖ وَإِن كَانَ مِن قَوۡمِۭ بَیۡنَكُمۡ وَبَیۡنَهُم مِّیثَـٰقࣱ فَدِیَةࣱ مُّسَلَّمَةٌ إِلَىٰۤ أَهۡلِهِۦ وَتَحۡرِیرُ رَقَبَةࣲ مُّؤۡمِنَةࣲۖ فَمَن لَّمۡ یَجِدۡ فَصِیَامُ شَهۡرَیۡنِ مُتَتَابِعَیۡنِ تَوۡبَةࣰ مِّنَ ٱللَّهِۗ وَكَانَ ٱللَّهُ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5199, 4, 586, 93, 'وَمَن یَقۡتُلۡ مُؤۡمِنࣰا مُّتَعَمِّدࣰا فَجَزَاۤؤُهُۥ جَهَنَّمُ خَـٰلِدࣰا فِیهَا وَغَضِبَ ٱللَّهُ عَلَیۡهِ وَلَعَنَهُۥ وَأَعَدَّ لَهُۥ عَذَابًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5200, 4, 587, 94, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا ضَرَبۡتُمۡ فِی سَبِیلِ ٱللَّهِ فَتَبَیَّنُوا۟ وَلَا تَقُولُوا۟ لِمَنۡ أَلۡقَىٰۤ إِلَیۡكُمُ ٱلسَّلَـٰمَ لَسۡتَ مُؤۡمِنࣰا تَبۡتَغُونَ عَرَضَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا فَعِندَ ٱللَّهِ مَغَانِمُ كَثِیرَةࣱۚ كَذَ ٰ⁠لِكَ كُنتُم مِّن قَبۡلُ فَمَنَّ ٱللَّهُ عَلَیۡكُمۡ فَتَبَیَّنُوۤا۟ۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِیرࣰا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5201, 4, 588, 95, 'لَّا یَسۡتَوِی ٱلۡقَـٰعِدُونَ مِنَ ٱلۡمُؤۡمِنِینَ غَیۡرُ أُو۟لِی ٱلضَّرَرِ وَٱلۡمُجَـٰهِدُونَ فِی سَبِیلِ ٱللَّهِ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡۚ فَضَّلَ ٱللَّهُ ٱلۡمُجَـٰهِدِینَ بِأَمۡوَ ٰ⁠لِهِمۡ وَأَنفُسِهِمۡ عَلَى ٱلۡقَـٰعِدِینَ دَرَجَةࣰۚ وَكُلࣰّا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَفَضَّلَ ٱللَّهُ ٱلۡمُجَـٰهِدِینَ عَلَى ٱلۡقَـٰعِدِینَ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:36', '2023-05-27 11:19:12'),
(5202, 4, 589, 96, 'دَرَجَـٰتࣲ مِّنۡهُ وَمَغۡفِرَةࣰ وَرَحۡمَةࣰۚ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمًا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:12'),
(5203, 4, 590, 97, 'إِنَّ ٱلَّذِینَ تَوَفَّىٰهُمُ ٱلۡمَلَـٰۤىِٕكَةُ ظَالِمِیۤ أَنفُسِهِمۡ قَالُوا۟ فِیمَ كُنتُمۡۖ قَالُوا۟ كُنَّا مُسۡتَضۡعَفِینَ فِی ٱلۡأَرۡضِۚ قَالُوۤا۟ أَلَمۡ تَكُنۡ أَرۡضُ ٱللَّهِ وَ ٰ⁠سِعَةࣰ فَتُهَاجِرُوا۟ فِیهَاۚ فَأُو۟لَـٰۤىِٕكَ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَسَاۤءَتۡ مَصِیرًا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:12'),
(5204, 4, 591, 98, 'إِلَّا ٱلۡمُسۡتَضۡعَفِینَ مِنَ ٱلرِّجَالِ وَٱلنِّسَاۤءِ وَٱلۡوِلۡدَ ٰ⁠نِ لَا یَسۡتَطِیعُونَ حِیلَةࣰ وَلَا یَهۡتَدُونَ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:12'),
(5205, 4, 592, 99, 'فَأُو۟لَـٰۤىِٕكَ عَسَى ٱللَّهُ أَن یَعۡفُوَ عَنۡهُمۡۚ وَكَانَ ٱللَّهُ عَفُوًّا غَفُورࣰا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:13'),
(5206, 4, 593, 100, '۞ وَمَن یُهَاجِرۡ فِی سَبِیلِ ٱللَّهِ یَجِدۡ فِی ٱلۡأَرۡضِ مُرَ ٰ⁠غَمࣰا كَثِیرࣰا وَسَعَةࣰۚ وَمَن یَخۡرُجۡ مِنۢ بَیۡتِهِۦ مُهَاجِرًا إِلَى ٱللَّهِ وَرَسُولِهِۦ ثُمَّ یُدۡرِكۡهُ ٱلۡمَوۡتُ فَقَدۡ وَقَعَ أَجۡرُهُۥ عَلَى ٱللَّهِۗ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:13'),
(5207, 4, 594, 101, 'وَإِذَا ضَرَبۡتُمۡ فِی ٱلۡأَرۡضِ فَلَیۡسَ عَلَیۡكُمۡ جُنَاحٌ أَن تَقۡصُرُوا۟ مِنَ ٱلصَّلَوٰةِ إِنۡ خِفۡتُمۡ أَن یَفۡتِنَكُمُ ٱلَّذِینَ كَفَرُوۤا۟ۚ إِنَّ ٱلۡكَـٰفِرِینَ كَانُوا۟ لَكُمۡ عَدُوࣰّا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:13'),
(5208, 4, 595, 102, 'وَإِذَا كُنتَ فِیهِمۡ فَأَقَمۡتَ لَهُمُ ٱلصَّلَوٰةَ فَلۡتَقُمۡ طَاۤىِٕفَةࣱ مِّنۡهُم مَّعَكَ وَلۡیَأۡخُذُوۤا۟ أَسۡلِحَتَهُمۡۖ فَإِذَا سَجَدُوا۟ فَلۡیَكُونُوا۟ مِن وَرَاۤىِٕكُمۡ وَلۡتَأۡتِ طَاۤىِٕفَةٌ أُخۡرَىٰ لَمۡ یُصَلُّوا۟ فَلۡیُصَلُّوا۟ مَعَكَ وَلۡیَأۡخُذُوا۟ حِذۡرَهُمۡ وَأَسۡلِحَتَهُمۡۗ وَدَّ ٱلَّذِینَ كَفَرُوا۟ لَوۡ تَغۡفُلُونَ عَنۡ أَسۡلِحَتِكُمۡ وَأَمۡتِعَتِكُمۡ فَیَمِیلُونَ عَلَیۡكُم مَّیۡلَةࣰ وَ ٰ⁠حِدَةࣰۚ وَلَا جُنَاحَ عَلَیۡكُمۡ إِن كَانَ بِكُمۡ أَذࣰى مِّن مَّطَرٍ أَوۡ كُنتُم مَّرۡضَىٰۤ أَن تَضَعُوۤا۟ أَسۡلِحَتَكُمۡۖ وَخُذُوا۟ حِذۡرَكُمۡۗ إِنَّ ٱللَّهَ أَعَدَّ لِلۡكَـٰفِرِینَ عَذَابࣰا مُّهِینࣰا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:13'),
(5209, 4, 596, 103, 'فَإِذَا قَضَیۡتُمُ ٱلصَّلَوٰةَ فَٱذۡكُرُوا۟ ٱللَّهَ قِیَـٰمࣰا وَقُعُودࣰا وَعَلَىٰ جُنُوبِكُمۡۚ فَإِذَا ٱطۡمَأۡنَنتُمۡ فَأَقِیمُوا۟ ٱلصَّلَوٰةَۚ إِنَّ ٱلصَّلَوٰةَ كَانَتۡ عَلَى ٱلۡمُؤۡمِنِینَ كِتَـٰبࣰا مَّوۡقُوتࣰا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:13'),
(5210, 4, 597, 104, 'وَلَا تَهِنُوا۟ فِی ٱبۡتِغَاۤءِ ٱلۡقَوۡمِۖ إِن تَكُونُوا۟ تَأۡلَمُونَ فَإِنَّهُمۡ یَأۡلَمُونَ كَمَا تَأۡلَمُونَۖ وَتَرۡجُونَ مِنَ ٱللَّهِ مَا لَا یَرۡجُونَۗ وَكَانَ ٱللَّهُ عَلِیمًا حَكِیمًا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:13'),
(5211, 4, 598, 105, 'إِنَّاۤ أَنزَلۡنَاۤ إِلَیۡكَ ٱلۡكِتَـٰبَ بِٱلۡحَقِّ لِتَحۡكُمَ بَیۡنَ ٱلنَّاسِ بِمَاۤ أَرَىٰكَ ٱللَّهُۚ وَلَا تَكُن لِّلۡخَاۤىِٕنِینَ خَصِیمࣰا\n', NULL, 0, '2023-05-26 17:34:37', '2023-05-27 11:19:13'),
(5212, 4, 599, 106, 'وَٱسۡتَغۡفِرِ ٱللَّهَۖ إِنَّ ٱللَّهَ كَانَ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5213, 4, 600, 107, 'وَلَا تُجَـٰدِلۡ عَنِ ٱلَّذِینَ یَخۡتَانُونَ أَنفُسَهُمۡۚ إِنَّ ٱللَّهَ لَا یُحِبُّ مَن كَانَ خَوَّانًا أَثِیمࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5214, 4, 601, 108, 'یَسۡتَخۡفُونَ مِنَ ٱلنَّاسِ وَلَا یَسۡتَخۡفُونَ مِنَ ٱللَّهِ وَهُوَ مَعَهُمۡ إِذۡ یُبَیِّتُونَ مَا لَا یَرۡضَىٰ مِنَ ٱلۡقَوۡلِۚ وَكَانَ ٱللَّهُ بِمَا یَعۡمَلُونَ مُحِیطًا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5215, 4, 602, 109, 'هَـٰۤأَنتُمۡ هَـٰۤؤُلَاۤءِ جَـٰدَلۡتُمۡ عَنۡهُمۡ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا فَمَن یُجَـٰدِلُ ٱللَّهَ عَنۡهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ أَم مَّن یَكُونُ عَلَیۡهِمۡ وَكِیلࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5216, 4, 603, 110, 'وَمَن یَعۡمَلۡ سُوۤءًا أَوۡ یَظۡلِمۡ نَفۡسَهُۥ ثُمَّ یَسۡتَغۡفِرِ ٱللَّهَ یَجِدِ ٱللَّهَ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5217, 4, 604, 111, 'وَمَن یَكۡسِبۡ إِثۡمࣰا فَإِنَّمَا یَكۡسِبُهُۥ عَلَىٰ نَفۡسِهِۦۚ وَكَانَ ٱللَّهُ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5218, 4, 605, 112, 'وَمَن یَكۡسِبۡ خَطِیۤـَٔةً أَوۡ إِثۡمࣰا ثُمَّ یَرۡمِ بِهِۦ بَرِیۤـࣰٔا فَقَدِ ٱحۡتَمَلَ بُهۡتَـٰنࣰا وَإِثۡمࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5219, 4, 606, 113, 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَیۡكَ وَرَحۡمَتُهُۥ لَهَمَّت طَّاۤىِٕفَةࣱ مِّنۡهُمۡ أَن یُضِلُّوكَ وَمَا یُضِلُّونَ إِلَّاۤ أَنفُسَهُمۡۖ وَمَا یَضُرُّونَكَ مِن شَیۡءࣲۚ وَأَنزَلَ ٱللَّهُ عَلَیۡكَ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَعَلَّمَكَ مَا لَمۡ تَكُن تَعۡلَمُۚ وَكَانَ فَضۡلُ ٱللَّهِ عَلَیۡكَ عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5220, 4, 607, 114, '۞ لَّا خَیۡرَ فِی كَثِیرࣲ مِّن نَّجۡوَىٰهُمۡ إِلَّا مَنۡ أَمَرَ بِصَدَقَةٍ أَوۡ مَعۡرُوفٍ أَوۡ إِصۡلَـٰحِۭ بَیۡنَ ٱلنَّاسِۚ وَمَن یَفۡعَلۡ ذَ ٰ⁠لِكَ ٱبۡتِغَاۤءَ مَرۡضَاتِ ٱللَّهِ فَسَوۡفَ نُؤۡتِیهِ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:13'),
(5221, 4, 608, 115, 'وَمَن یُشَاقِقِ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَیَّنَ لَهُ ٱلۡهُدَىٰ وَیَتَّبِعۡ غَیۡرَ سَبِیلِ ٱلۡمُؤۡمِنِینَ نُوَلِّهِۦ مَا تَوَلَّىٰ وَنُصۡلِهِۦ جَهَنَّمَۖ وَسَاۤءَتۡ مَصِیرًا\n', NULL, 0, '2023-05-26 17:34:38', '2023-05-27 11:19:14'),
(5222, 4, 609, 116, 'إِنَّ ٱللَّهَ لَا یَغۡفِرُ أَن یُشۡرَكَ بِهِۦ وَیَغۡفِرُ مَا دُونَ ذَ ٰ⁠لِكَ لِمَن یَشَاۤءُۚ وَمَن یُشۡرِكۡ بِٱللَّهِ فَقَدۡ ضَلَّ ضَلَـٰلَۢا بَعِیدًا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5223, 4, 610, 117, 'إِن یَدۡعُونَ مِن دُونِهِۦۤ إِلَّاۤ إِنَـٰثࣰا وَإِن یَدۡعُونَ إِلَّا شَیۡطَـٰنࣰا مَّرِیدࣰا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5224, 4, 611, 118, 'لَّعَنَهُ ٱللَّهُۘ وَقَالَ لَأَتَّخِذَنَّ مِنۡ عِبَادِكَ نَصِیبࣰا مَّفۡرُوضࣰا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5225, 4, 612, 119, 'وَلَأُضِلَّنَّهُمۡ وَلَأُمَنِّیَنَّهُمۡ وَلَـَٔامُرَنَّهُمۡ فَلَیُبَتِّكُنَّ ءَاذَانَ ٱلۡأَنۡعَـٰمِ وَلَـَٔامُرَنَّهُمۡ فَلَیُغَیِّرُنَّ خَلۡقَ ٱللَّهِۚ وَمَن یَتَّخِذِ ٱلشَّیۡطَـٰنَ وَلِیࣰّا مِّن دُونِ ٱللَّهِ فَقَدۡ خَسِرَ خُسۡرَانࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5226, 4, 613, 120, 'یَعِدُهُمۡ وَیُمَنِّیهِمۡۖ وَمَا یَعِدُهُمُ ٱلشَّیۡطَـٰنُ إِلَّا غُرُورًا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5227, 4, 614, 121, 'أُو۟لَـٰۤىِٕكَ مَأۡوَىٰهُمۡ جَهَنَّمُ وَلَا یَجِدُونَ عَنۡهَا مَحِیصࣰا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5228, 4, 615, 122, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۖ وَعۡدَ ٱللَّهِ حَقࣰّاۚ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ قِیلࣰا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5229, 4, 616, 123, 'لَّیۡسَ بِأَمَانِیِّكُمۡ وَلَاۤ أَمَانِیِّ أَهۡلِ ٱلۡكِتَـٰبِۗ مَن یَعۡمَلۡ سُوۤءࣰا یُجۡزَ بِهِۦ وَلَا یَجِدۡ لَهُۥ مِن دُونِ ٱللَّهِ وَلِیࣰّا وَلَا نَصِیرࣰا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5230, 4, 617, 124, 'وَمَن یَعۡمَلۡ مِنَ ٱلصَّـٰلِحَـٰتِ مِن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنࣱ فَأُو۟لَـٰۤىِٕكَ یَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا یُظۡلَمُونَ نَقِیرࣰا\n', NULL, 0, '2023-05-26 17:34:39', '2023-05-27 11:19:14'),
(5231, 4, 618, 125, 'وَمَنۡ أَحۡسَنُ دِینࣰا مِّمَّنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنࣱ وَٱتَّبَعَ مِلَّةَ إِبۡرَ ٰ⁠هِیمَ حَنِیفࣰاۗ وَٱتَّخَذَ ٱللَّهُ إِبۡرَ ٰ⁠هِیمَ خَلِیلࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:14'),
(5232, 4, 619, 126, 'وَلِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ بِكُلِّ شَیۡءࣲ مُّحِیطࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:14'),
(5233, 4, 620, 127, 'وَیَسۡتَفۡتُونَكَ فِی ٱلنِّسَاۤءِۖ قُلِ ٱللَّهُ یُفۡتِیكُمۡ فِیهِنَّ وَمَا یُتۡلَىٰ عَلَیۡكُمۡ فِی ٱلۡكِتَـٰبِ فِی یَتَـٰمَى ٱلنِّسَاۤءِ ٱلَّـٰتِی لَا تُؤۡتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرۡغَبُونَ أَن تَنكِحُوهُنَّ وَٱلۡمُسۡتَضۡعَفِینَ مِنَ ٱلۡوِلۡدَ ٰ⁠نِ وَأَن تَقُومُوا۟ لِلۡیَتَـٰمَىٰ بِٱلۡقِسۡطِۚ وَمَا تَفۡعَلُوا۟ مِنۡ خَیۡرࣲ فَإِنَّ ٱللَّهَ كَانَ بِهِۦ عَلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:14'),
(5234, 4, 621, 128, 'وَإِنِ ٱمۡرَأَةٌ خَافَتۡ مِنۢ بَعۡلِهَا نُشُوزًا أَوۡ إِعۡرَاضࣰا فَلَا جُنَاحَ عَلَیۡهِمَاۤ أَن یُصۡلِحَا بَیۡنَهُمَا صُلۡحࣰاۚ وَٱلصُّلۡحُ خَیۡرࣱۗ وَأُحۡضِرَتِ ٱلۡأَنفُسُ ٱلشُّحَّۚ وَإِن تُحۡسِنُوا۟ وَتَتَّقُوا۟ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِیرࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:14'),
(5235, 4, 622, 129, 'وَلَن تَسۡتَطِیعُوۤا۟ أَن تَعۡدِلُوا۟ بَیۡنَ ٱلنِّسَاۤءِ وَلَوۡ حَرَصۡتُمۡۖ فَلَا تَمِیلُوا۟ كُلَّ ٱلۡمَیۡلِ فَتَذَرُوهَا كَٱلۡمُعَلَّقَةِۚ وَإِن تُصۡلِحُوا۟ وَتَتَّقُوا۟ فَإِنَّ ٱللَّهَ كَانَ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:14'),
(5236, 4, 623, 130, 'وَإِن یَتَفَرَّقَا یُغۡنِ ٱللَّهُ كُلࣰّا مِّن سَعَتِهِۦۚ وَكَانَ ٱللَّهُ وَ ٰ⁠سِعًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:15'),
(5237, 4, 624, 131, 'وَلِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۗ وَلَقَدۡ وَصَّیۡنَا ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ مِن قَبۡلِكُمۡ وَإِیَّاكُمۡ أَنِ ٱتَّقُوا۟ ٱللَّهَۚ وَإِن تَكۡفُرُوا۟ فَإِنَّ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ غَنِیًّا حَمِیدࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:15'),
(5238, 4, 625, 132, 'وَلِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ وَكَفَىٰ بِٱللَّهِ وَكِیلًا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:15'),
(5239, 4, 626, 133, 'إِن یَشَأۡ یُذۡهِبۡكُمۡ أَیُّهَا ٱلنَّاسُ وَیَأۡتِ بِـَٔاخَرِینَۚ وَكَانَ ٱللَّهُ عَلَىٰ ذَ ٰ⁠لِكَ قَدِیرࣰا\n', NULL, 0, '2023-05-26 17:34:40', '2023-05-27 11:19:15'),
(5240, 4, 627, 134, 'مَّن كَانَ یُرِیدُ ثَوَابَ ٱلدُّنۡیَا فَعِندَ ٱللَّهِ ثَوَابُ ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِۚ وَكَانَ ٱللَّهُ سَمِیعَۢا بَصِیرࣰا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5241, 4, 628, 135, '۞ یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ كُونُوا۟ قَوَّ ٰ⁠مِینَ بِٱلۡقِسۡطِ شُهَدَاۤءَ لِلَّهِ وَلَوۡ عَلَىٰۤ أَنفُسِكُمۡ أَوِ ٱلۡوَ ٰ⁠لِدَیۡنِ وَٱلۡأَقۡرَبِینَۚ إِن یَكُنۡ غَنِیًّا أَوۡ فَقِیرࣰا فَٱللَّهُ أَوۡلَىٰ بِهِمَاۖ فَلَا تَتَّبِعُوا۟ ٱلۡهَوَىٰۤ أَن تَعۡدِلُوا۟ۚ وَإِن تَلۡوُۥۤا۟ أَوۡ تُعۡرِضُوا۟ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِیرࣰا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5242, 4, 629, 136, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ ءَامِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَٱلۡكِتَـٰبِ ٱلَّذِی نَزَّلَ عَلَىٰ رَسُولِهِۦ وَٱلۡكِتَـٰبِ ٱلَّذِیۤ أَنزَلَ مِن قَبۡلُۚ وَمَن یَكۡفُرۡ بِٱللَّهِ وَمَلَـٰۤىِٕكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ فَقَدۡ ضَلَّ ضَلَـٰلَۢا بَعِیدًا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5243, 4, 630, 137, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ ثُمَّ كَفَرُوا۟ ثُمَّ ءَامَنُوا۟ ثُمَّ كَفَرُوا۟ ثُمَّ ٱزۡدَادُوا۟ كُفۡرࣰا لَّمۡ یَكُنِ ٱللَّهُ لِیَغۡفِرَ لَهُمۡ وَلَا لِیَهۡدِیَهُمۡ سَبِیلَۢا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5244, 4, 631, 138, 'بَشِّرِ ٱلۡمُنَـٰفِقِینَ بِأَنَّ لَهُمۡ عَذَابًا أَلِیمًا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5245, 4, 632, 139, 'ٱلَّذِینَ یَتَّخِذُونَ ٱلۡكَـٰفِرِینَ أَوۡلِیَاۤءَ مِن دُونِ ٱلۡمُؤۡمِنِینَۚ أَیَبۡتَغُونَ عِندَهُمُ ٱلۡعِزَّةَ فَإِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِیعࣰا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5246, 4, 633, 140, 'وَقَدۡ نَزَّلَ عَلَیۡكُمۡ فِی ٱلۡكِتَـٰبِ أَنۡ إِذَا سَمِعۡتُمۡ ءَایَـٰتِ ٱللَّهِ یُكۡفَرُ بِهَا وَیُسۡتَهۡزَأُ بِهَا فَلَا تَقۡعُدُوا۟ مَعَهُمۡ حَتَّىٰ یَخُوضُوا۟ فِی حَدِیثٍ غَیۡرِهِۦۤ إِنَّكُمۡ إِذࣰا مِّثۡلُهُمۡۗ إِنَّ ٱللَّهَ جَامِعُ ٱلۡمُنَـٰفِقِینَ وَٱلۡكَـٰفِرِینَ فِی جَهَنَّمَ جَمِیعًا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5247, 4, 634, 141, 'ٱلَّذِینَ یَتَرَبَّصُونَ بِكُمۡ فَإِن كَانَ لَكُمۡ فَتۡحࣱ مِّنَ ٱللَّهِ قَالُوۤا۟ أَلَمۡ نَكُن مَّعَكُمۡ وَإِن كَانَ لِلۡكَـٰفِرِینَ نَصِیبࣱ قَالُوۤا۟ أَلَمۡ نَسۡتَحۡوِذۡ عَلَیۡكُمۡ وَنَمۡنَعۡكُم مِّنَ ٱلۡمُؤۡمِنِینَۚ فَٱللَّهُ یَحۡكُمُ بَیۡنَكُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِۗ وَلَن یَجۡعَلَ ٱللَّهُ لِلۡكَـٰفِرِینَ عَلَى ٱلۡمُؤۡمِنِینَ سَبِیلًا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5248, 4, 635, 142, 'إِنَّ ٱلۡمُنَـٰفِقِینَ یُخَـٰدِعُونَ ٱللَّهَ وَهُوَ خَـٰدِعُهُمۡ وَإِذَا قَامُوۤا۟ إِلَى ٱلصَّلَوٰةِ قَامُوا۟ كُسَالَىٰ یُرَاۤءُونَ ٱلنَّاسَ وَلَا یَذۡكُرُونَ ٱللَّهَ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5249, 4, 636, 143, 'مُّذَبۡذَبِینَ بَیۡنَ ذَ ٰ⁠لِكَ لَاۤ إِلَىٰ هَـٰۤؤُلَاۤءِ وَلَاۤ إِلَىٰ هَـٰۤؤُلَاۤءِۚ وَمَن یُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِیلࣰا\n', NULL, 0, '2023-05-26 17:34:41', '2023-05-27 11:19:15'),
(5250, 4, 637, 144, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَّخِذُوا۟ ٱلۡكَـٰفِرِینَ أَوۡلِیَاۤءَ مِن دُونِ ٱلۡمُؤۡمِنِینَۚ أَتُرِیدُونَ أَن تَجۡعَلُوا۟ لِلَّهِ عَلَیۡكُمۡ سُلۡطَـٰنࣰا مُّبِینًا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:15'),
(5251, 4, 638, 145, 'إِنَّ ٱلۡمُنَـٰفِقِینَ فِی ٱلدَّرۡكِ ٱلۡأَسۡفَلِ مِنَ ٱلنَّارِ وَلَن تَجِدَ لَهُمۡ نَصِیرًا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:16'),
(5252, 4, 639, 146, 'إِلَّا ٱلَّذِینَ تَابُوا۟ وَأَصۡلَحُوا۟ وَٱعۡتَصَمُوا۟ بِٱللَّهِ وَأَخۡلَصُوا۟ دِینَهُمۡ لِلَّهِ فَأُو۟لَـٰۤىِٕكَ مَعَ ٱلۡمُؤۡمِنِینَۖ وَسَوۡفَ یُؤۡتِ ٱللَّهُ ٱلۡمُؤۡمِنِینَ أَجۡرًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:16'),
(5253, 4, 640, 147, 'مَّا یَفۡعَلُ ٱللَّهُ بِعَذَابِكُمۡ إِن شَكَرۡتُمۡ وَءَامَنتُمۡۚ وَكَانَ ٱللَّهُ شَاكِرًا عَلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:16'),
(5254, 4, 641, 148, '۞ لَّا یُحِبُّ ٱللَّهُ ٱلۡجَهۡرَ بِٱلسُّوۤءِ مِنَ ٱلۡقَوۡلِ إِلَّا مَن ظُلِمَۚ وَكَانَ ٱللَّهُ سَمِیعًا عَلِیمًا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:16'),
(5255, 4, 642, 149, 'إِن تُبۡدُوا۟ خَیۡرًا أَوۡ تُخۡفُوهُ أَوۡ تَعۡفُوا۟ عَن سُوۤءࣲ فَإِنَّ ٱللَّهَ كَانَ عَفُوࣰّا قَدِیرًا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:16'),
(5256, 4, 643, 150, 'إِنَّ ٱلَّذِینَ یَكۡفُرُونَ بِٱللَّهِ وَرُسُلِهِۦ وَیُرِیدُونَ أَن یُفَرِّقُوا۟ بَیۡنَ ٱللَّهِ وَرُسُلِهِۦ وَیَقُولُونَ نُؤۡمِنُ بِبَعۡضࣲ وَنَكۡفُرُ بِبَعۡضࣲ وَیُرِیدُونَ أَن یَتَّخِذُوا۟ بَیۡنَ ذَ ٰ⁠لِكَ سَبِیلًا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:16'),
(5257, 4, 644, 151, 'أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡكَـٰفِرُونَ حَقࣰّاۚ وَأَعۡتَدۡنَا لِلۡكَـٰفِرِینَ عَذَابࣰا مُّهِینࣰا\n', NULL, 0, '2023-05-26 17:34:42', '2023-05-27 11:19:16'),
(5258, 4, 645, 152, 'وَٱلَّذِینَ ءَامَنُوا۟ بِٱللَّهِ وَرُسُلِهِۦ وَلَمۡ یُفَرِّقُوا۟ بَیۡنَ أَحَدࣲ مِّنۡهُمۡ أُو۟لَـٰۤىِٕكَ سَوۡفَ یُؤۡتِیهِمۡ أُجُورَهُمۡۚ وَكَانَ ٱللَّهُ غَفُورࣰا رَّحِیمࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5259, 4, 646, 153, 'یَسۡـَٔلُكَ أَهۡلُ ٱلۡكِتَـٰبِ أَن تُنَزِّلَ عَلَیۡهِمۡ كِتَـٰبࣰا مِّنَ ٱلسَّمَاۤءِۚ فَقَدۡ سَأَلُوا۟ مُوسَىٰۤ أَكۡبَرَ مِن ذَ ٰ⁠لِكَ فَقَالُوۤا۟ أَرِنَا ٱللَّهَ جَهۡرَةࣰ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ بِظُلۡمِهِمۡۚ ثُمَّ ٱتَّخَذُوا۟ ٱلۡعِجۡلَ مِنۢ بَعۡدِ مَا جَاۤءَتۡهُمُ ٱلۡبَیِّنَـٰتُ فَعَفَوۡنَا عَن ذَ ٰ⁠لِكَۚ وَءَاتَیۡنَا مُوسَىٰ سُلۡطَـٰنࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5260, 4, 647, 154, 'وَرَفَعۡنَا فَوۡقَهُمُ ٱلطُّورَ بِمِیثَـٰقِهِمۡ وَقُلۡنَا لَهُمُ ٱدۡخُلُوا۟ ٱلۡبَابَ سُجَّدࣰا وَقُلۡنَا لَهُمۡ لَا تَعۡدُوا۟ فِی ٱلسَّبۡتِ وَأَخَذۡنَا مِنۡهُم مِّیثَـٰقًا غَلِیظࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5261, 4, 648, 155, 'فَبِمَا نَقۡضِهِم مِّیثَـٰقَهُمۡ وَكُفۡرِهِم بِـَٔایَـٰتِ ٱللَّهِ وَقَتۡلِهِمُ ٱلۡأَنۢبِیَاۤءَ بِغَیۡرِ حَقࣲّ وَقَوۡلِهِمۡ قُلُوبُنَا غُلۡفُۢۚ بَلۡ طَبَعَ ٱللَّهُ عَلَیۡهَا بِكُفۡرِهِمۡ فَلَا یُؤۡمِنُونَ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5262, 4, 649, 156, 'وَبِكُفۡرِهِمۡ وَقَوۡلِهِمۡ عَلَىٰ مَرۡیَمَ بُهۡتَـٰنًا عَظِیمࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5263, 4, 650, 157, 'وَقَوۡلِهِمۡ إِنَّا قَتَلۡنَا ٱلۡمَسِیحَ عِیسَى ٱبۡنَ مَرۡیَمَ رَسُولَ ٱللَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَـٰكِن شُبِّهَ لَهُمۡۚ وَإِنَّ ٱلَّذِینَ ٱخۡتَلَفُوا۟ فِیهِ لَفِی شَكࣲّ مِّنۡهُۚ مَا لَهُم بِهِۦ مِنۡ عِلۡمٍ إِلَّا ٱتِّبَاعَ ٱلظَّنِّۚ وَمَا قَتَلُوهُ یَقِینَۢا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5264, 4, 651, 158, 'بَل رَّفَعَهُ ٱللَّهُ إِلَیۡهِۚ وَكَانَ ٱللَّهُ عَزِیزًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5265, 4, 652, 159, 'وَإِن مِّنۡ أَهۡلِ ٱلۡكِتَـٰبِ إِلَّا لَیُؤۡمِنَنَّ بِهِۦ قَبۡلَ مَوۡتِهِۦۖ وَیَوۡمَ ٱلۡقِیَـٰمَةِ یَكُونُ عَلَیۡهِمۡ شَهِیدࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5266, 4, 653, 160, 'فَبِظُلۡمࣲ مِّنَ ٱلَّذِینَ هَادُوا۟ حَرَّمۡنَا عَلَیۡهِمۡ طَیِّبَـٰتٍ أُحِلَّتۡ لَهُمۡ وَبِصَدِّهِمۡ عَن سَبِیلِ ٱللَّهِ كَثِیرࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5267, 4, 654, 161, 'وَأَخۡذِهِمُ ٱلرِّبَوٰا۟ وَقَدۡ نُهُوا۟ عَنۡهُ وَأَكۡلِهِمۡ أَمۡوَ ٰ⁠لَ ٱلنَّاسِ بِٱلۡبَـٰطِلِۚ وَأَعۡتَدۡنَا لِلۡكَـٰفِرِینَ مِنۡهُمۡ عَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:43', '2023-05-27 11:19:16'),
(5268, 4, 655, 162, 'لَّـٰكِنِ ٱلرَّ ٰ⁠سِخُونَ فِی ٱلۡعِلۡمِ مِنۡهُمۡ وَٱلۡمُؤۡمِنُونَ یُؤۡمِنُونَ بِمَاۤ أُنزِلَ إِلَیۡكَ وَمَاۤ أُنزِلَ مِن قَبۡلِكَۚ وَٱلۡمُقِیمِینَ ٱلصَّلَوٰةَۚ وَٱلۡمُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلۡمُؤۡمِنُونَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ أُو۟لَـٰۤىِٕكَ سَنُؤۡتِیهِمۡ أَجۡرًا عَظِیمًا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5269, 4, 656, 163, '۞ إِنَّاۤ أَوۡحَیۡنَاۤ إِلَیۡكَ كَمَاۤ أَوۡحَیۡنَاۤ إِلَىٰ نُوحࣲ وَٱلنَّبِیِّـۧنَ مِنۢ بَعۡدِهِۦۚ وَأَوۡحَیۡنَاۤ إِلَىٰۤ إِبۡرَ ٰ⁠هِیمَ وَإِسۡمَـٰعِیلَ وَإِسۡحَـٰقَ وَیَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَعِیسَىٰ وَأَیُّوبَ وَیُونُسَ وَهَـٰرُونَ وَسُلَیۡمَـٰنَۚ وَءَاتَیۡنَا دَاوُۥدَ زَبُورࣰا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5270, 4, 657, 164, 'وَرُسُلࣰا قَدۡ قَصَصۡنَـٰهُمۡ عَلَیۡكَ مِن قَبۡلُ وَرُسُلࣰا لَّمۡ نَقۡصُصۡهُمۡ عَلَیۡكَۚ وَكَلَّمَ ٱللَّهُ مُوسَىٰ تَكۡلِیمࣰا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5271, 4, 658, 165, 'رُّسُلࣰا مُّبَشِّرِینَ وَمُنذِرِینَ لِئَلَّا یَكُونَ لِلنَّاسِ عَلَى ٱللَّهِ حُجَّةُۢ بَعۡدَ ٱلرُّسُلِۚ وَكَانَ ٱللَّهُ عَزِیزًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5272, 4, 659, 166, 'لَّـٰكِنِ ٱللَّهُ یَشۡهَدُ بِمَاۤ أَنزَلَ إِلَیۡكَۖ أَنزَلَهُۥ بِعِلۡمِهِۦۖ وَٱلۡمَلَـٰۤىِٕكَةُ یَشۡهَدُونَۚ وَكَفَىٰ بِٱللَّهِ شَهِیدًا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5273, 4, 660, 167, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِیلِ ٱللَّهِ قَدۡ ضَلُّوا۟ ضَلَـٰلَۢا بَعِیدًا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5274, 4, 661, 168, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ وَظَلَمُوا۟ لَمۡ یَكُنِ ٱللَّهُ لِیَغۡفِرَ لَهُمۡ وَلَا لِیَهۡدِیَهُمۡ طَرِیقًا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5275, 4, 662, 169, 'إِلَّا طَرِیقَ جَهَنَّمَ خَـٰلِدِینَ فِیهَاۤ أَبَدࣰاۚ وَكَانَ ذَ ٰ⁠لِكَ عَلَى ٱللَّهِ یَسِیرࣰا\n', NULL, 0, '2023-05-26 17:34:44', '2023-05-27 11:19:17'),
(5276, 4, 663, 170, 'یَـٰۤأَیُّهَا ٱلنَّاسُ قَدۡ جَاۤءَكُمُ ٱلرَّسُولُ بِٱلۡحَقِّ مِن رَّبِّكُمۡ فَـَٔامِنُوا۟ خَیۡرࣰا لَّكُمۡۚ وَإِن تَكۡفُرُوا۟ فَإِنَّ لِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِیمًا حَكِیمࣰا\n', NULL, 0, '2023-05-26 17:34:45', '2023-05-27 11:19:17'),
(5277, 4, 664, 171, 'یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لَا تَغۡلُوا۟ فِی دِینِكُمۡ وَلَا تَقُولُوا۟ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ إِنَّمَا ٱلۡمَسِیحُ عِیسَى ٱبۡنُ مَرۡیَمَ رَسُولُ ٱللَّهِ وَكَلِمَتُهُۥۤ أَلۡقَىٰهَاۤ إِلَىٰ مَرۡیَمَ وَرُوحࣱ مِّنۡهُۖ فَـَٔامِنُوا۟ بِٱللَّهِ وَرُسُلِهِۦۖ وَلَا تَقُولُوا۟ ثَلَـٰثَةٌۚ ٱنتَهُوا۟ خَیۡرࣰا لَّكُمۡۚ إِنَّمَا ٱللَّهُ إِلَـٰهࣱ وَ ٰ⁠حِدࣱۖ سُبۡحَـٰنَهُۥۤ أَن یَكُونَ لَهُۥ وَلَدࣱۘ لَّهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۗ وَكَفَىٰ بِٱللَّهِ وَكِیلࣰا\n', NULL, 0, '2023-05-26 17:34:45', '2023-05-27 11:19:17'),
(5278, 4, 665, 172, 'لَّن یَسۡتَنكِفَ ٱلۡمَسِیحُ أَن یَكُونَ عَبۡدࣰا لِّلَّهِ وَلَا ٱلۡمَلَـٰۤىِٕكَةُ ٱلۡمُقَرَّبُونَۚ وَمَن یَسۡتَنكِفۡ عَنۡ عِبَادَتِهِۦ وَیَسۡتَكۡبِرۡ فَسَیَحۡشُرُهُمۡ إِلَیۡهِ جَمِیعࣰا\n', NULL, 0, '2023-05-26 17:34:45', '2023-05-27 11:19:17'),
(5279, 4, 666, 173, 'فَأَمَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَیُوَفِّیهِمۡ أُجُورَهُمۡ وَیَزِیدُهُم مِّن فَضۡلِهِۦۖ وَأَمَّا ٱلَّذِینَ ٱسۡتَنكَفُوا۟ وَٱسۡتَكۡبَرُوا۟ فَیُعَذِّبُهُمۡ عَذَابًا أَلِیمࣰا وَلَا یَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِیࣰّا وَلَا نَصِیرࣰا\n', NULL, 0, '2023-05-26 17:34:45', '2023-05-27 11:19:17'),
(5280, 4, 667, 174, 'یَـٰۤأَیُّهَا ٱلنَّاسُ قَدۡ جَاۤءَكُم بُرۡهَـٰنࣱ مِّن رَّبِّكُمۡ وَأَنزَلۡنَاۤ إِلَیۡكُمۡ نُورࣰا مُّبِینࣰا\n', NULL, 0, '2023-05-26 17:34:45', '2023-05-27 11:19:17'),
(5281, 4, 668, 175, 'فَأَمَّا ٱلَّذِینَ ءَامَنُوا۟ بِٱللَّهِ وَٱعۡتَصَمُوا۟ بِهِۦ فَسَیُدۡخِلُهُمۡ فِی رَحۡمَةࣲ مِّنۡهُ وَفَضۡلࣲ وَیَهۡدِیهِمۡ إِلَیۡهِ صِرَ ٰ⁠طࣰا مُّسۡتَقِیمࣰا\n', NULL, 0, '2023-05-26 17:34:45', '2023-05-27 11:19:17'),
(5282, 4, 669, 176, 'یَسۡتَفۡتُونَكَ قُلِ ٱللَّهُ یُفۡتِیكُمۡ فِی ٱلۡكَلَـٰلَةِۚ إِنِ ٱمۡرُؤٌا۟ هَلَكَ لَیۡسَ لَهُۥ وَلَدࣱ وَلَهُۥۤ أُخۡتࣱ فَلَهَا نِصۡفُ مَا تَرَكَۚ وَهُوَ یَرِثُهَاۤ إِن لَّمۡ یَكُن لَّهَا وَلَدࣱۚ فَإِن كَانَتَا ٱثۡنَتَیۡنِ فَلَهُمَا ٱلثُّلُثَانِ مِمَّا تَرَكَۚ وَإِن كَانُوۤا۟ إِخۡوَةࣰ رِّجَالࣰا وَنِسَاۤءࣰ فَلِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَیَیۡنِۗ یُبَیِّنُ ٱللَّهُ لَكُمۡ أَن تَضِلُّوا۟ۗ وَٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمُۢ\n', NULL, 0, '2023-05-26 17:34:45', '2023-05-27 11:19:17'),
(5283, 3, 294, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤ\n', NULL, 0, '2023-05-26 17:35:04', '2023-05-27 11:19:21'),
(5284, 3, 295, 2, 'ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَ ٱلۡحَیُّ ٱلۡقَیُّومُ\n', NULL, 0, '2023-05-26 17:35:04', '2023-05-27 11:19:21'),
(5285, 3, 296, 3, 'نَزَّلَ عَلَیۡكَ ٱلۡكِتَـٰبَ بِٱلۡحَقِّ مُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیۡهِ وَأَنزَلَ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِیلَ\n', NULL, 0, '2023-05-26 17:35:04', '2023-05-27 11:19:21'),
(5286, 3, 297, 4, 'مِن قَبۡلُ هُدࣰى لِّلنَّاسِ وَأَنزَلَ ٱلۡفُرۡقَانَۗ إِنَّ ٱلَّذِینَ كَفَرُوا۟ بِـَٔایَـٰتِ ٱللَّهِ لَهُمۡ عَذَابࣱ شَدِیدࣱۗ وَٱللَّهُ عَزِیزࣱ ذُو ٱنتِقَامٍ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5287, 3, 298, 5, 'إِنَّ ٱللَّهَ لَا یَخۡفَىٰ عَلَیۡهِ شَیۡءࣱ فِی ٱلۡأَرۡضِ وَلَا فِی ٱلسَّمَاۤءِ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5288, 3, 299, 6, 'هُوَ ٱلَّذِی یُصَوِّرُكُمۡ فِی ٱلۡأَرۡحَامِ كَیۡفَ یَشَاۤءُۚ لَاۤ إِلَـٰهَ إِلَّا هُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5289, 3, 300, 7, 'هُوَ ٱلَّذِیۤ أَنزَلَ عَلَیۡكَ ٱلۡكِتَـٰبَ مِنۡهُ ءَایَـٰتࣱ مُّحۡكَمَـٰتٌ هُنَّ أُمُّ ٱلۡكِتَـٰبِ وَأُخَرُ مُتَشَـٰبِهَـٰتࣱۖ فَأَمَّا ٱلَّذِینَ فِی قُلُوبِهِمۡ زَیۡغࣱ فَیَتَّبِعُونَ مَا تَشَـٰبَهَ مِنۡهُ ٱبۡتِغَاۤءَ ٱلۡفِتۡنَةِ وَٱبۡتِغَاۤءَ تَأۡوِیلِهِۦۖ وَمَا یَعۡلَمُ تَأۡوِیلَهُۥۤ إِلَّا ٱللَّهُۗ وَٱلرَّ ٰ⁠سِخُونَ فِی ٱلۡعِلۡمِ یَقُولُونَ ءَامَنَّا بِهِۦ كُلࣱّ مِّنۡ عِندِ رَبِّنَاۗ وَمَا یَذَّكَّرُ إِلَّاۤ أُو۟لُوا۟ ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5290, 3, 301, 8, 'رَبَّنَا لَا تُزِغۡ قُلُوبَنَا بَعۡدَ إِذۡ هَدَیۡتَنَا وَهَبۡ لَنَا مِن لَّدُنكَ رَحۡمَةًۚ إِنَّكَ أَنتَ ٱلۡوَهَّابُ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5291, 3, 302, 9, 'رَبَّنَاۤ إِنَّكَ جَامِعُ ٱلنَّاسِ لِیَوۡمࣲ لَّا رَیۡبَ فِیهِۚ إِنَّ ٱللَّهَ لَا یُخۡلِفُ ٱلۡمِیعَادَ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5292, 3, 303, 10, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ لَن تُغۡنِیَ عَنۡهُمۡ أَمۡوَ ٰ⁠لُهُمۡ وَلَاۤ أَوۡلَـٰدُهُم مِّنَ ٱللَّهِ شَیۡـࣰٔاۖ وَأُو۟لَـٰۤىِٕكَ هُمۡ وَقُودُ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5293, 3, 304, 11, 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِینَ مِن قَبۡلِهِمۡۚ كَذَّبُوا۟ بِـَٔایَـٰتِنَا فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۗ وَٱللَّهُ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5294, 3, 305, 12, 'قُل لِّلَّذِینَ كَفَرُوا۟ سَتُغۡلَبُونَ وَتُحۡشَرُونَ إِلَىٰ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمِهَادُ\n', NULL, 0, '2023-05-26 17:35:05', '2023-05-27 11:19:21'),
(5295, 3, 306, 13, 'قَدۡ كَانَ لَكُمۡ ءَایَةࣱ فِی فِئَتَیۡنِ ٱلۡتَقَتَاۖ فِئَةࣱ تُقَـٰتِلُ فِی سَبِیلِ ٱللَّهِ وَأُخۡرَىٰ كَافِرَةࣱ یَرَوۡنَهُم مِّثۡلَیۡهِمۡ رَأۡیَ ٱلۡعَیۡنِۚ وَٱللَّهُ یُؤَیِّدُ بِنَصۡرِهِۦ مَن یَشَاۤءُۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَعِبۡرَةࣰ لِّأُو۟لِی ٱلۡأَبۡصَـٰرِ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:21'),
(5296, 3, 307, 14, 'زُیِّنَ لِلنَّاسِ حُبُّ ٱلشَّهَوَ ٰ⁠تِ مِنَ ٱلنِّسَاۤءِ وَٱلۡبَنِینَ وَٱلۡقَنَـٰطِیرِ ٱلۡمُقَنطَرَةِ مِنَ ٱلذَّهَبِ وَٱلۡفِضَّةِ وَٱلۡخَیۡلِ ٱلۡمُسَوَّمَةِ وَٱلۡأَنۡعَـٰمِ وَٱلۡحَرۡثِۗ ذَ ٰ⁠لِكَ مَتَـٰعُ ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلۡمَـَٔابِ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:21'),
(5297, 3, 308, 15, '۞ قُلۡ أَؤُنَبِّئُكُم بِخَیۡرࣲ مِّن ذَ ٰ⁠لِكُمۡۖ لِلَّذِینَ ٱتَّقَوۡا۟ عِندَ رَبِّهِمۡ جَنَّـٰتࣱ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَا وَأَزۡوَ ٰ⁠جࣱ مُّطَهَّرَةࣱ وَرِضۡوَ ٰ⁠نࣱ مِّنَ ٱللَّهِۗ وَٱللَّهُ بَصِیرُۢ بِٱلۡعِبَادِ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:21'),
(5298, 3, 309, 16, 'ٱلَّذِینَ یَقُولُونَ رَبَّنَاۤ إِنَّنَاۤ ءَامَنَّا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:21'),
(5299, 3, 310, 17, 'ٱلصَّـٰبِرِینَ وَٱلصَّـٰدِقِینَ وَٱلۡقَـٰنِتِینَ وَٱلۡمُنفِقِینَ وَٱلۡمُسۡتَغۡفِرِینَ بِٱلۡأَسۡحَارِ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:22'),
(5300, 3, 311, 18, 'شَهِدَ ٱللَّهُ أَنَّهُۥ لَاۤ إِلَـٰهَ إِلَّا هُوَ وَٱلۡمَلَـٰۤىِٕكَةُ وَأُو۟لُوا۟ ٱلۡعِلۡمِ قَاۤىِٕمَۢا بِٱلۡقِسۡطِۚ لَاۤ إِلَـٰهَ إِلَّا هُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:22'),
(5301, 3, 312, 19, 'إِنَّ ٱلدِّینَ عِندَ ٱللَّهِ ٱلۡإِسۡلَـٰمُۗ وَمَا ٱخۡتَلَفَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَاۤءَهُمُ ٱلۡعِلۡمُ بَغۡیَۢا بَیۡنَهُمۡۗ وَمَن یَكۡفُرۡ بِـَٔایَـٰتِ ٱللَّهِ فَإِنَّ ٱللَّهَ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:22'),
(5302, 3, 313, 20, 'فَإِنۡ حَاۤجُّوكَ فَقُلۡ أَسۡلَمۡتُ وَجۡهِیَ لِلَّهِ وَمَنِ ٱتَّبَعَنِۗ وَقُل لِّلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ وَٱلۡأُمِّیِّـۧنَ ءَأَسۡلَمۡتُمۡۚ فَإِنۡ أَسۡلَمُوا۟ فَقَدِ ٱهۡتَدَوا۟ۖ وَّإِن تَوَلَّوۡا۟ فَإِنَّمَا عَلَیۡكَ ٱلۡبَلَـٰغُۗ وَٱللَّهُ بَصِیرُۢ بِٱلۡعِبَادِ\n', NULL, 0, '2023-05-26 17:35:06', '2023-05-27 11:19:22'),
(5303, 3, 314, 21, 'إِنَّ ٱلَّذِینَ یَكۡفُرُونَ بِـَٔایَـٰتِ ٱللَّهِ وَیَقۡتُلُونَ ٱلنَّبِیِّـۧنَ بِغَیۡرِ حَقࣲّ وَیَقۡتُلُونَ ٱلَّذِینَ یَأۡمُرُونَ بِٱلۡقِسۡطِ مِنَ ٱلنَّاسِ فَبَشِّرۡهُم بِعَذَابٍ أَلِیمٍ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5304, 3, 315, 22, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ حَبِطَتۡ أَعۡمَـٰلُهُمۡ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِینَ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5305, 3, 316, 23, 'أَلَمۡ تَرَ إِلَى ٱلَّذِینَ أُوتُوا۟ نَصِیبࣰا مِّنَ ٱلۡكِتَـٰبِ یُدۡعَوۡنَ إِلَىٰ كِتَـٰبِ ٱللَّهِ لِیَحۡكُمَ بَیۡنَهُمۡ ثُمَّ یَتَوَلَّىٰ فَرِیقࣱ مِّنۡهُمۡ وَهُم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5306, 3, 317, 24, 'ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَالُوا۟ لَن تَمَسَّنَا ٱلنَّارُ إِلَّاۤ أَیَّامࣰا مَّعۡدُودَ ٰ⁠تࣲۖ وَغَرَّهُمۡ فِی دِینِهِم مَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5307, 3, 318, 25, 'فَكَیۡفَ إِذَا جَمَعۡنَـٰهُمۡ لِیَوۡمࣲ لَّا رَیۡبَ فِیهِ وَوُفِّیَتۡ كُلُّ نَفۡسࣲ مَّا كَسَبَتۡ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5308, 3, 319, 26, 'قُلِ ٱللَّهُمَّ مَـٰلِكَ ٱلۡمُلۡكِ تُؤۡتِی ٱلۡمُلۡكَ مَن تَشَاۤءُ وَتَنزِعُ ٱلۡمُلۡكَ مِمَّن تَشَاۤءُ وَتُعِزُّ مَن تَشَاۤءُ وَتُذِلُّ مَن تَشَاۤءُۖ بِیَدِكَ ٱلۡخَیۡرُۖ إِنَّكَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5309, 3, 320, 27, 'تُولِجُ ٱلَّیۡلَ فِی ٱلنَّهَارِ وَتُولِجُ ٱلنَّهَارَ فِی ٱلَّیۡلِۖ وَتُخۡرِجُ ٱلۡحَیَّ مِنَ ٱلۡمَیِّتِ وَتُخۡرِجُ ٱلۡمَیِّتَ مِنَ ٱلۡحَیِّۖ وَتَرۡزُقُ مَن تَشَاۤءُ بِغَیۡرِ حِسَابࣲ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5310, 3, 321, 28, 'لَّا یَتَّخِذِ ٱلۡمُؤۡمِنُونَ ٱلۡكَـٰفِرِینَ أَوۡلِیَاۤءَ مِن دُونِ ٱلۡمُؤۡمِنِینَۖ وَمَن یَفۡعَلۡ ذَ ٰ⁠لِكَ فَلَیۡسَ مِنَ ٱللَّهِ فِی شَیۡءٍ إِلَّاۤ أَن تَتَّقُوا۟ مِنۡهُمۡ تُقَىٰةࣰۗ وَیُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَإِلَى ٱللَّهِ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5311, 3, 322, 29, 'قُلۡ إِن تُخۡفُوا۟ مَا فِی صُدُورِكُمۡ أَوۡ تُبۡدُوهُ یَعۡلَمۡهُ ٱللَّهُۗ وَیَعۡلَمُ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5312, 3, 323, 30, 'یَوۡمَ تَجِدُ كُلُّ نَفۡسࣲ مَّا عَمِلَتۡ مِنۡ خَیۡرࣲ مُّحۡضَرࣰا وَمَا عَمِلَتۡ مِن سُوۤءࣲ تَوَدُّ لَوۡ أَنَّ بَیۡنَهَا وَبَیۡنَهُۥۤ أَمَدَۢا بَعِیدࣰاۗ وَیُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ\n', NULL, 0, '2023-05-26 17:35:07', '2023-05-27 11:19:22'),
(5313, 3, 324, 31, 'قُلۡ إِن كُنتُمۡ تُحِبُّونَ ٱللَّهَ فَٱتَّبِعُونِی یُحۡبِبۡكُمُ ٱللَّهُ وَیَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:22'),
(5314, 3, 325, 32, 'قُلۡ أَطِیعُوا۟ ٱللَّهَ وَٱلرَّسُولَۖ فَإِن تَوَلَّوۡا۟ فَإِنَّ ٱللَّهَ لَا یُحِبُّ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:22'),
(5315, 3, 326, 33, '۞ إِنَّ ٱللَّهَ ٱصۡطَفَىٰۤ ءَادَمَ وَنُوحࣰا وَءَالَ إِبۡرَ ٰ⁠هِیمَ وَءَالَ عِمۡرَ ٰ⁠نَ عَلَى ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:22'),
(5316, 3, 327, 34, 'ذُرِّیَّةَۢ بَعۡضُهَا مِنۢ بَعۡضࣲۗ وَٱللَّهُ سَمِیعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5317, 3, 328, 35, 'إِذۡ قَالَتِ ٱمۡرَأَتُ عِمۡرَ ٰ⁠نَ رَبِّ إِنِّی نَذَرۡتُ لَكَ مَا فِی بَطۡنِی مُحَرَّرࣰا فَتَقَبَّلۡ مِنِّیۤۖ إِنَّكَ أَنتَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5318, 3, 329, 36, 'فَلَمَّا وَضَعَتۡهَا قَالَتۡ رَبِّ إِنِّی وَضَعۡتُهَاۤ أُنثَىٰ وَٱللَّهُ أَعۡلَمُ بِمَا وَضَعَتۡ وَلَیۡسَ ٱلذَّكَرُ كَٱلۡأُنثَىٰۖ وَإِنِّی سَمَّیۡتُهَا مَرۡیَمَ وَإِنِّیۤ أُعِیذُهَا بِكَ وَذُرِّیَّتَهَا مِنَ ٱلشَّیۡطَـٰنِ ٱلرَّجِیمِ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5319, 3, 330, 37, 'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنࣲ وَأَنۢبَتَهَا نَبَاتًا حَسَنࣰا وَكَفَّلَهَا زَكَرِیَّاۖ كُلَّمَا دَخَلَ عَلَیۡهَا زَكَرِیَّا ٱلۡمِحۡرَابَ وَجَدَ عِندَهَا رِزۡقࣰاۖ قَالَ یَـٰمَرۡیَمُ أَنَّىٰ لَكِ هَـٰذَاۖ قَالَتۡ هُوَ مِنۡ عِندِ ٱللَّهِۖ إِنَّ ٱللَّهَ یَرۡزُقُ مَن یَشَاۤءُ بِغَیۡرِ حِسَابٍ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5320, 3, 331, 38, 'هُنَالِكَ دَعَا زَكَرِیَّا رَبَّهُۥۖ قَالَ رَبِّ هَبۡ لِی مِن لَّدُنكَ ذُرِّیَّةࣰ طَیِّبَةًۖ إِنَّكَ سَمِیعُ ٱلدُّعَاۤءِ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5321, 3, 332, 39, 'فَنَادَتۡهُ ٱلۡمَلَـٰۤىِٕكَةُ وَهُوَ قَاۤىِٕمࣱ یُصَلِّی فِی ٱلۡمِحۡرَابِ أَنَّ ٱللَّهَ یُبَشِّرُكَ بِیَحۡیَىٰ مُصَدِّقَۢا بِكَلِمَةࣲ مِّنَ ٱللَّهِ وَسَیِّدࣰا وَحَصُورࣰا وَنَبِیࣰّا مِّنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5322, 3, 333, 40, 'قَالَ رَبِّ أَنَّىٰ یَكُونُ لِی غُلَـٰمࣱ وَقَدۡ بَلَغَنِیَ ٱلۡكِبَرُ وَٱمۡرَأَتِی عَاقِرࣱۖ قَالَ كَذَ ٰ⁠لِكَ ٱللَّهُ یَفۡعَلُ مَا یَشَاۤءُ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5323, 3, 334, 41, 'قَالَ رَبِّ ٱجۡعَل لِّیۤ ءَایَةࣰۖ قَالَ ءَایَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَـٰثَةَ أَیَّامٍ إِلَّا رَمۡزࣰاۗ وَٱذۡكُر رَّبَّكَ كَثِیرࣰا وَسَبِّحۡ بِٱلۡعَشِیِّ وَٱلۡإِبۡكَـٰرِ\n', NULL, 0, '2023-05-26 17:35:08', '2023-05-27 11:19:23'),
(5324, 3, 335, 42, 'وَإِذۡ قَالَتِ ٱلۡمَلَـٰۤىِٕكَةُ یَـٰمَرۡیَمُ إِنَّ ٱللَّهَ ٱصۡطَفَىٰكِ وَطَهَّرَكِ وَٱصۡطَفَىٰكِ عَلَىٰ نِسَاۤءِ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:35:09', '2023-05-27 11:19:23'),
(5325, 3, 336, 43, 'یَـٰمَرۡیَمُ ٱقۡنُتِی لِرَبِّكِ وَٱسۡجُدِی وَٱرۡكَعِی مَعَ ٱلرَّ ٰ⁠كِعِینَ\n', NULL, 0, '2023-05-26 17:35:09', '2023-05-27 11:19:23'),
(5326, 3, 337, 44, 'ذَ ٰ⁠لِكَ مِنۡ أَنۢبَاۤءِ ٱلۡغَیۡبِ نُوحِیهِ إِلَیۡكَۚ وَمَا كُنتَ لَدَیۡهِمۡ إِذۡ یُلۡقُونَ أَقۡلَـٰمَهُمۡ أَیُّهُمۡ یَكۡفُلُ مَرۡیَمَ وَمَا كُنتَ لَدَیۡهِمۡ إِذۡ یَخۡتَصِمُونَ\n', NULL, 0, '2023-05-26 17:35:09', '2023-05-27 11:19:23'),
(5327, 3, 338, 45, 'إِذۡ قَالَتِ ٱلۡمَلَـٰۤىِٕكَةُ یَـٰمَرۡیَمُ إِنَّ ٱللَّهَ یُبَشِّرُكِ بِكَلِمَةࣲ مِّنۡهُ ٱسۡمُهُ ٱلۡمَسِیحُ عِیسَى ٱبۡنُ مَرۡیَمَ وَجِیهࣰا فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِ وَمِنَ ٱلۡمُقَرَّبِینَ\n', NULL, 0, '2023-05-26 17:35:09', '2023-05-27 11:19:23'),
(5328, 3, 339, 46, 'وَیُكَلِّمُ ٱلنَّاسَ فِی ٱلۡمَهۡدِ وَكَهۡلࣰا وَمِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:35:09', '2023-05-27 11:19:23'),
(5329, 3, 340, 47, 'قَالَتۡ رَبِّ أَنَّىٰ یَكُونُ لِی وَلَدࣱ وَلَمۡ یَمۡسَسۡنِی بَشَرࣱۖ قَالَ كَذَ ٰ⁠لِكِ ٱللَّهُ یَخۡلُقُ مَا یَشَاۤءُۚ إِذَا قَضَىٰۤ أَمۡرࣰا فَإِنَّمَا یَقُولُ لَهُۥ كُن فَیَكُونُ\n', NULL, 0, '2023-05-26 17:35:09', '2023-05-27 11:19:23'),
(5330, 3, 341, 48, 'وَیُعَلِّمُهُ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِیلَ\n', NULL, 0, '2023-05-26 17:35:09', '2023-05-27 11:19:23'),
(5331, 3, 342, 49, 'وَرَسُولًا إِلَىٰ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ أَنِّی قَدۡ جِئۡتُكُم بِـَٔایَةࣲ مِّن رَّبِّكُمۡ أَنِّیۤ أَخۡلُقُ لَكُم مِّنَ ٱلطِّینِ كَهَیۡـَٔةِ ٱلطَّیۡرِ فَأَنفُخُ فِیهِ فَیَكُونُ طَیۡرَۢا بِإِذۡنِ ٱللَّهِۖ وَأُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ وَأُحۡیِ ٱلۡمَوۡتَىٰ بِإِذۡنِ ٱللَّهِۖ وَأُنَبِّئُكُم بِمَا تَأۡكُلُونَ وَمَا تَدَّخِرُونَ فِی بُیُوتِكُمۡۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:23'),
(5332, 3, 343, 50, 'وَمُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیَّ مِنَ ٱلتَّوۡرَىٰةِ وَلِأُحِلَّ لَكُم بَعۡضَ ٱلَّذِی حُرِّمَ عَلَیۡكُمۡۚ وَجِئۡتُكُم بِـَٔایَةࣲ مِّن رَّبِّكُمۡ فَٱتَّقُوا۟ ٱللَّهَ وَأَطِیعُونِ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5333, 3, 344, 51, 'إِنَّ ٱللَّهَ رَبِّی وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَـٰذَا صِرَ ٰ⁠طࣱ مُّسۡتَقِیمࣱ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(5334, 3, 345, 52, '۞ فَلَمَّاۤ أَحَسَّ عِیسَىٰ مِنۡهُمُ ٱلۡكُفۡرَ قَالَ مَنۡ أَنصَارِیۤ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِیُّونَ نَحۡنُ أَنصَارُ ٱللَّهِ ءَامَنَّا بِٱللَّهِ وَٱشۡهَدۡ بِأَنَّا مُسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5335, 3, 346, 53, 'رَبَّنَاۤ ءَامَنَّا بِمَاۤ أَنزَلۡتَ وَٱتَّبَعۡنَا ٱلرَّسُولَ فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِینَ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5336, 3, 347, 54, 'وَمَكَرُوا۟ وَمَكَرَ ٱللَّهُۖ وَٱللَّهُ خَیۡرُ ٱلۡمَـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5337, 3, 348, 55, 'إِذۡ قَالَ ٱللَّهُ یَـٰعِیسَىٰۤ إِنِّی مُتَوَفِّیكَ وَرَافِعُكَ إِلَیَّ وَمُطَهِّرُكَ مِنَ ٱلَّذِینَ كَفَرُوا۟ وَجَاعِلُ ٱلَّذِینَ ٱتَّبَعُوكَ فَوۡقَ ٱلَّذِینَ كَفَرُوۤا۟ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِۖ ثُمَّ إِلَیَّ مَرۡجِعُكُمۡ فَأَحۡكُمُ بَیۡنَكُمۡ فِیمَا كُنتُمۡ فِیهِ تَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5338, 3, 349, 56, 'فَأَمَّا ٱلَّذِینَ كَفَرُوا۟ فَأُعَذِّبُهُمۡ عَذَابࣰا شَدِیدࣰا فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِینَ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5339, 3, 350, 57, 'وَأَمَّا ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَیُوَفِّیهِمۡ أُجُورَهُمۡۗ وَٱللَّهُ لَا یُحِبُّ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5340, 3, 351, 58, 'ذَ ٰ⁠لِكَ نَتۡلُوهُ عَلَیۡكَ مِنَ ٱلۡـَٔایَـٰتِ وَٱلذِّكۡرِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:35:10', '2023-05-27 11:19:24'),
(5341, 3, 352, 59, 'إِنَّ مَثَلَ عِیسَىٰ عِندَ ٱللَّهِ كَمَثَلِ ءَادَمَۖ خَلَقَهُۥ مِن تُرَابࣲ ثُمَّ قَالَ لَهُۥ كُن فَیَكُونُ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5342, 3, 353, 60, 'ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُن مِّنَ ٱلۡمُمۡتَرِینَ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5343, 3, 354, 61, 'فَمَنۡ حَاۤجَّكَ فِیهِ مِنۢ بَعۡدِ مَا جَاۤءَكَ مِنَ ٱلۡعِلۡمِ فَقُلۡ تَعَالَوۡا۟ نَدۡعُ أَبۡنَاۤءَنَا وَأَبۡنَاۤءَكُمۡ وَنِسَاۤءَنَا وَنِسَاۤءَكُمۡ وَأَنفُسَنَا وَأَنفُسَكُمۡ ثُمَّ نَبۡتَهِلۡ فَنَجۡعَل لَّعۡنَتَ ٱللَّهِ عَلَى ٱلۡكَـٰذِبِینَ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5344, 3, 355, 62, 'إِنَّ هَـٰذَا لَهُوَ ٱلۡقَصَصُ ٱلۡحَقُّۚ وَمَا مِنۡ إِلَـٰهٍ إِلَّا ٱللَّهُۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5345, 3, 356, 63, 'فَإِن تَوَلَّوۡا۟ فَإِنَّ ٱللَّهَ عَلِیمُۢ بِٱلۡمُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5346, 3, 357, 64, 'قُلۡ یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ تَعَالَوۡا۟ إِلَىٰ كَلِمَةࣲ سَوَاۤءِۭ بَیۡنَنَا وَبَیۡنَكُمۡ أَلَّا نَعۡبُدَ إِلَّا ٱللَّهَ وَلَا نُشۡرِكَ بِهِۦ شَیۡـࣰٔا وَلَا یَتَّخِذَ بَعۡضُنَا بَعۡضًا أَرۡبَابࣰا مِّن دُونِ ٱللَّهِۚ فَإِن تَوَلَّوۡا۟ فَقُولُوا۟ ٱشۡهَدُوا۟ بِأَنَّا مُسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5347, 3, 358, 65, 'یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لِمَ تُحَاۤجُّونَ فِیۤ إِبۡرَ ٰ⁠هِیمَ وَمَاۤ أُنزِلَتِ ٱلتَّوۡرَىٰةُ وَٱلۡإِنجِیلُ إِلَّا مِنۢ بَعۡدِهِۦۤۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5348, 3, 359, 66, 'هَـٰۤأَنتُمۡ هَـٰۤؤُلَاۤءِ حَـٰجَجۡتُمۡ فِیمَا لَكُم بِهِۦ عِلۡمࣱ فَلِمَ تُحَاۤجُّونَ فِیمَا لَیۡسَ لَكُم بِهِۦ عِلۡمࣱۚ وَٱللَّهُ یَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5349, 3, 360, 67, 'مَا كَانَ إِبۡرَ ٰ⁠هِیمُ یَهُودِیࣰّا وَلَا نَصۡرَانِیࣰّا وَلَـٰكِن كَانَ حَنِیفࣰا مُّسۡلِمࣰا وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:35:11', '2023-05-27 11:19:24'),
(5350, 3, 361, 68, 'إِنَّ أَوۡلَى ٱلنَّاسِ بِإِبۡرَ ٰ⁠هِیمَ لَلَّذِینَ ٱتَّبَعُوهُ وَهَـٰذَا ٱلنَّبِیُّ وَٱلَّذِینَ ءَامَنُوا۟ۗ وَٱللَّهُ وَلِیُّ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5351, 3, 362, 69, 'وَدَّت طَّاۤىِٕفَةࣱ مِّنۡ أَهۡلِ ٱلۡكِتَـٰبِ لَوۡ یُضِلُّونَكُمۡ وَمَا یُضِلُّونَ إِلَّاۤ أَنفُسَهُمۡ وَمَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5352, 3, 363, 70, 'یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لِمَ تَكۡفُرُونَ بِـَٔایَـٰتِ ٱللَّهِ وَأَنتُمۡ تَشۡهَدُونَ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5353, 3, 364, 71, 'یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لِمَ تَلۡبِسُونَ ٱلۡحَقَّ بِٱلۡبَـٰطِلِ وَتَكۡتُمُونَ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5354, 3, 365, 72, 'وَقَالَت طَّاۤىِٕفَةࣱ مِّنۡ أَهۡلِ ٱلۡكِتَـٰبِ ءَامِنُوا۟ بِٱلَّذِیۤ أُنزِلَ عَلَى ٱلَّذِینَ ءَامَنُوا۟ وَجۡهَ ٱلنَّهَارِ وَٱكۡفُرُوۤا۟ ءَاخِرَهُۥ لَعَلَّهُمۡ یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5355, 3, 366, 73, 'وَلَا تُؤۡمِنُوۤا۟ إِلَّا لِمَن تَبِعَ دِینَكُمۡ قُلۡ إِنَّ ٱلۡهُدَىٰ هُدَى ٱللَّهِ أَن یُؤۡتَىٰۤ أَحَدࣱ مِّثۡلَ مَاۤ أُوتِیتُمۡ أَوۡ یُحَاۤجُّوكُمۡ عِندَ رَبِّكُمۡۗ قُلۡ إِنَّ ٱلۡفَضۡلَ بِیَدِ ٱللَّهِ یُؤۡتِیهِ مَن یَشَاۤءُۗ وَٱللَّهُ وَ ٰ⁠سِعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5356, 3, 367, 74, 'یَخۡتَصُّ بِرَحۡمَتِهِۦ مَن یَشَاۤءُۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5357, 3, 368, 75, '۞ وَمِنۡ أَهۡلِ ٱلۡكِتَـٰبِ مَنۡ إِن تَأۡمَنۡهُ بِقِنطَارࣲ یُؤَدِّهِۦۤ إِلَیۡكَ وَمِنۡهُم مَّنۡ إِن تَأۡمَنۡهُ بِدِینَارࣲ لَّا یُؤَدِّهِۦۤ إِلَیۡكَ إِلَّا مَا دُمۡتَ عَلَیۡهِ قَاۤىِٕمࣰاۗ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَالُوا۟ لَیۡسَ عَلَیۡنَا فِی ٱلۡأُمِّیِّـۧنَ سَبِیلࣱ وَیَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5358, 3, 369, 76, 'بَلَىٰۚ مَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ وَٱتَّقَىٰ فَإِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:35:12', '2023-05-27 11:19:25'),
(5359, 3, 370, 77, 'إِنَّ ٱلَّذِینَ یَشۡتَرُونَ بِعَهۡدِ ٱللَّهِ وَأَیۡمَـٰنِهِمۡ ثَمَنࣰا قَلِیلًا أُو۟لَـٰۤىِٕكَ لَا خَلَـٰقَ لَهُمۡ فِی ٱلۡـَٔاخِرَةِ وَلَا یُكَلِّمُهُمُ ٱللَّهُ وَلَا یَنظُرُ إِلَیۡهِمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ وَلَا یُزَكِّیهِمۡ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:25'),
(5360, 3, 371, 78, 'وَإِنَّ مِنۡهُمۡ لَفَرِیقࣰا یَلۡوُۥنَ أَلۡسِنَتَهُم بِٱلۡكِتَـٰبِ لِتَحۡسَبُوهُ مِنَ ٱلۡكِتَـٰبِ وَمَا هُوَ مِنَ ٱلۡكِتَـٰبِ وَیَقُولُونَ هُوَ مِنۡ عِندِ ٱللَّهِ وَمَا هُوَ مِنۡ عِندِ ٱللَّهِ وَیَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:25'),
(5361, 3, 372, 79, 'مَا كَانَ لِبَشَرٍ أَن یُؤۡتِیَهُ ٱللَّهُ ٱلۡكِتَـٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ ثُمَّ یَقُولَ لِلنَّاسِ كُونُوا۟ عِبَادࣰا لِّی مِن دُونِ ٱللَّهِ وَلَـٰكِن كُونُوا۟ رَبَّـٰنِیِّـۧنَ بِمَا كُنتُمۡ تُعَلِّمُونَ ٱلۡكِتَـٰبَ وَبِمَا كُنتُمۡ تَدۡرُسُونَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:25'),
(5362, 3, 373, 80, 'وَلَا یَأۡمُرَكُمۡ أَن تَتَّخِذُوا۟ ٱلۡمَلَـٰۤىِٕكَةَ وَٱلنَّبِیِّـۧنَ أَرۡبَابًاۗ أَیَأۡمُرُكُم بِٱلۡكُفۡرِ بَعۡدَ إِذۡ أَنتُم مُّسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:26'),
(5363, 3, 374, 81, 'وَإِذۡ أَخَذَ ٱللَّهُ مِیثَـٰقَ ٱلنَّبِیِّـۧنَ لَمَاۤ ءَاتَیۡتُكُم مِّن كِتَـٰبࣲ وَحِكۡمَةࣲ ثُمَّ جَاۤءَكُمۡ رَسُولࣱ مُّصَدِّقࣱ لِّمَا مَعَكُمۡ لَتُؤۡمِنُنَّ بِهِۦ وَلَتَنصُرُنَّهُۥۚ قَالَ ءَأَقۡرَرۡتُمۡ وَأَخَذۡتُمۡ عَلَىٰ ذَ ٰ⁠لِكُمۡ إِصۡرِیۖ قَالُوۤا۟ أَقۡرَرۡنَاۚ قَالَ فَٱشۡهَدُوا۟ وَأَنَا۠ مَعَكُم مِّنَ ٱلشَّـٰهِدِینَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:26'),
(5364, 3, 375, 82, 'فَمَن تَوَلَّىٰ بَعۡدَ ذَ ٰ⁠لِكَ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:26'),
(5365, 3, 376, 83, 'أَفَغَیۡرَ دِینِ ٱللَّهِ یَبۡغُونَ وَلَهُۥۤ أَسۡلَمَ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ طَوۡعࣰا وَكَرۡهࣰا وَإِلَیۡهِ یُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:26'),
(5366, 3, 377, 84, 'قُلۡ ءَامَنَّا بِٱللَّهِ وَمَاۤ أُنزِلَ عَلَیۡنَا وَمَاۤ أُنزِلَ عَلَىٰۤ إِبۡرَ ٰ⁠هِیمَ وَإِسۡمَـٰعِیلَ وَإِسۡحَـٰقَ وَیَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَاۤ أُوتِیَ مُوسَىٰ وَعِیسَىٰ وَٱلنَّبِیُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَیۡنَ أَحَدࣲ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:26'),
(5367, 3, 378, 85, 'وَمَن یَبۡتَغِ غَیۡرَ ٱلۡإِسۡلَـٰمِ دِینࣰا فَلَن یُقۡبَلَ مِنۡهُ وَهُوَ فِی ٱلۡـَٔاخِرَةِ مِنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:26'),
(5368, 3, 379, 86, 'كَیۡفَ یَهۡدِی ٱللَّهُ قَوۡمࣰا كَفَرُوا۟ بَعۡدَ إِیمَـٰنِهِمۡ وَشَهِدُوۤا۟ أَنَّ ٱلرَّسُولَ حَقࣱّ وَجَاۤءَهُمُ ٱلۡبَیِّنَـٰتُۚ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:35:13', '2023-05-27 11:19:26'),
(5369, 3, 380, 87, 'أُو۟لَـٰۤىِٕكَ جَزَاۤؤُهُمۡ أَنَّ عَلَیۡهِمۡ لَعۡنَةَ ٱللَّهِ وَٱلۡمَلَـٰۤىِٕكَةِ وَٱلنَّاسِ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5370, 3, 381, 88, 'خَـٰلِدِینَ فِیهَا لَا یُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ یُنظَرُونَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5371, 3, 382, 89, 'إِلَّا ٱلَّذِینَ تَابُوا۟ مِنۢ بَعۡدِ ذَ ٰ⁠لِكَ وَأَصۡلَحُوا۟ فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5372, 3, 383, 90, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ بَعۡدَ إِیمَـٰنِهِمۡ ثُمَّ ٱزۡدَادُوا۟ كُفۡرࣰا لَّن تُقۡبَلَ تَوۡبَتُهُمۡ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلضَّاۤلُّونَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5373, 3, 384, 91, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ وَمَاتُوا۟ وَهُمۡ كُفَّارࣱ فَلَن یُقۡبَلَ مِنۡ أَحَدِهِم مِّلۡءُ ٱلۡأَرۡضِ ذَهَبࣰا وَلَوِ ٱفۡتَدَىٰ بِهِۦۤۗ أُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابٌ أَلِیمࣱ وَمَا لَهُم مِّن نَّـٰصِرِینَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5374, 3, 385, 92, 'لَن تَنَالُوا۟ ٱلۡبِرَّ حَتَّىٰ تُنفِقُوا۟ مِمَّا تُحِبُّونَۚ وَمَا تُنفِقُوا۟ مِن شَیۡءࣲ فَإِنَّ ٱللَّهَ بِهِۦ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5375, 3, 386, 93, '۞ كُلُّ ٱلطَّعَامِ كَانَ حِلࣰّا لِّبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ إِلَّا مَا حَرَّمَ إِسۡرَ ٰ⁠ۤءِیلُ عَلَىٰ نَفۡسِهِۦ مِن قَبۡلِ أَن تُنَزَّلَ ٱلتَّوۡرَىٰةُۚ قُلۡ فَأۡتُوا۟ بِٱلتَّوۡرَىٰةِ فَٱتۡلُوهَاۤ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5376, 3, 387, 94, 'فَمَنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ مِنۢ بَعۡدِ ذَ ٰ⁠لِكَ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:26'),
(5377, 3, 388, 95, 'قُلۡ صَدَقَ ٱللَّهُۗ فَٱتَّبِعُوا۟ مِلَّةَ إِبۡرَ ٰ⁠هِیمَ حَنِیفࣰاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:27'),
(5378, 3, 389, 96, 'إِنَّ أَوَّلَ بَیۡتࣲ وُضِعَ لِلنَّاسِ لَلَّذِی بِبَكَّةَ مُبَارَكࣰا وَهُدࣰى لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:35:14', '2023-05-27 11:19:27'),
(5379, 3, 390, 97, 'فِیهِ ءَایَـٰتُۢ بَیِّنَـٰتࣱ مَّقَامُ إِبۡرَ ٰ⁠هِیمَۖ وَمَن دَخَلَهُۥ كَانَ ءَامِنࣰاۗ وَلِلَّهِ عَلَى ٱلنَّاسِ حِجُّ ٱلۡبَیۡتِ مَنِ ٱسۡتَطَاعَ إِلَیۡهِ سَبِیلࣰاۚ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِیٌّ عَنِ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5380, 3, 391, 98, 'قُلۡ یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لِمَ تَكۡفُرُونَ بِـَٔایَـٰتِ ٱللَّهِ وَٱللَّهُ شَهِیدٌ عَلَىٰ مَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5381, 3, 392, 99, 'قُلۡ یَـٰۤأَهۡلَ ٱلۡكِتَـٰبِ لِمَ تَصُدُّونَ عَن سَبِیلِ ٱللَّهِ مَنۡ ءَامَنَ تَبۡغُونَهَا عِوَجࣰا وَأَنتُمۡ شُهَدَاۤءُۗ وَمَا ٱللَّهُ بِغَـٰفِلٍ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5382, 3, 393, 100, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِن تُطِیعُوا۟ فَرِیقࣰا مِّنَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ یَرُدُّوكُم بَعۡدَ إِیمَـٰنِكُمۡ كَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5383, 3, 394, 101, 'وَكَیۡفَ تَكۡفُرُونَ وَأَنتُمۡ تُتۡلَىٰ عَلَیۡكُمۡ ءَایَـٰتُ ٱللَّهِ وَفِیكُمۡ رَسُولُهُۥۗ وَمَن یَعۡتَصِم بِٱللَّهِ فَقَدۡ هُدِیَ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5384, 3, 395, 102, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ حَقَّ تُقَاتِهِۦ وَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5385, 3, 396, 103, 'وَٱعۡتَصِمُوا۟ بِحَبۡلِ ٱللَّهِ جَمِیعࣰا وَلَا تَفَرَّقُوا۟ۚ وَٱذۡكُرُوا۟ نِعۡمَتَ ٱللَّهِ عَلَیۡكُمۡ إِذۡ كُنتُمۡ أَعۡدَاۤءࣰ فَأَلَّفَ بَیۡنَ قُلُوبِكُمۡ فَأَصۡبَحۡتُم بِنِعۡمَتِهِۦۤ إِخۡوَ ٰ⁠نࣰا وَكُنتُمۡ عَلَىٰ شَفَا حُفۡرَةࣲ مِّنَ ٱلنَّارِ فَأَنقَذَكُم مِّنۡهَاۗ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمۡ ءَایَـٰتِهِۦ لَعَلَّكُمۡ تَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5386, 3, 397, 104, 'وَلۡتَكُن مِّنكُمۡ أُمَّةࣱ یَدۡعُونَ إِلَى ٱلۡخَیۡرِ وَیَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَیَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِۚ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5387, 3, 398, 105, 'وَلَا تَكُونُوا۟ كَٱلَّذِینَ تَفَرَّقُوا۟ وَٱخۡتَلَفُوا۟ مِنۢ بَعۡدِ مَا جَاۤءَهُمُ ٱلۡبَیِّنَـٰتُۚ وَأُو۟لَـٰۤىِٕكَ لَهُمۡ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:35:15', '2023-05-27 11:19:27'),
(5388, 3, 399, 106, 'یَوۡمَ تَبۡیَضُّ وُجُوهࣱ وَتَسۡوَدُّ وُجُوهࣱۚ فَأَمَّا ٱلَّذِینَ ٱسۡوَدَّتۡ وُجُوهُهُمۡ أَكَفَرۡتُم بَعۡدَ إِیمَـٰنِكُمۡ فَذُوقُوا۟ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5389, 3, 400, 107, 'وَأَمَّا ٱلَّذِینَ ٱبۡیَضَّتۡ وُجُوهُهُمۡ فَفِی رَحۡمَةِ ٱللَّهِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5390, 3, 401, 108, 'تِلۡكَ ءَایَـٰتُ ٱللَّهِ نَتۡلُوهَا عَلَیۡكَ بِٱلۡحَقِّۗ وَمَا ٱللَّهُ یُرِیدُ ظُلۡمࣰا لِّلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5391, 3, 402, 109, 'وَلِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5392, 3, 403, 110, 'كُنتُمۡ خَیۡرَ أُمَّةٍ أُخۡرِجَتۡ لِلنَّاسِ تَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَتَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَتُؤۡمِنُونَ بِٱللَّهِۗ وَلَوۡ ءَامَنَ أَهۡلُ ٱلۡكِتَـٰبِ لَكَانَ خَیۡرࣰا لَّهُمۚ مِّنۡهُمُ ٱلۡمُؤۡمِنُونَ وَأَكۡثَرُهُمُ ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5393, 3, 404, 111, 'لَن یَضُرُّوكُمۡ إِلَّاۤ أَذࣰىۖ وَإِن یُقَـٰتِلُوكُمۡ یُوَلُّوكُمُ ٱلۡأَدۡبَارَ ثُمَّ لَا یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5394, 3, 405, 112, 'ضُرِبَتۡ عَلَیۡهِمُ ٱلذِّلَّةُ أَیۡنَ مَا ثُقِفُوۤا۟ إِلَّا بِحَبۡلࣲ مِّنَ ٱللَّهِ وَحَبۡلࣲ مِّنَ ٱلنَّاسِ وَبَاۤءُو بِغَضَبࣲ مِّنَ ٱللَّهِ وَضُرِبَتۡ عَلَیۡهِمُ ٱلۡمَسۡكَنَةُۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ كَانُوا۟ یَكۡفُرُونَ بِـَٔایَـٰتِ ٱللَّهِ وَیَقۡتُلُونَ ٱلۡأَنۢبِیَاۤءَ بِغَیۡرِ حَقࣲّۚ ذَ ٰ⁠لِكَ بِمَا عَصَوا۟ وَّكَانُوا۟ یَعۡتَدُونَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5395, 3, 406, 113, '۞ لَیۡسُوا۟ سَوَاۤءࣰۗ مِّنۡ أَهۡلِ ٱلۡكِتَـٰبِ أُمَّةࣱ قَاۤىِٕمَةࣱ یَتۡلُونَ ءَایَـٰتِ ٱللَّهِ ءَانَاۤءَ ٱلَّیۡلِ وَهُمۡ یَسۡجُدُونَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:27'),
(5396, 3, 407, 114, 'یُؤۡمِنُونَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَیَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَیَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَیُسَـٰرِعُونَ فِی ٱلۡخَیۡرَ ٰ⁠تِۖ وَأُو۟لَـٰۤىِٕكَ مِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:28'),
(5397, 3, 408, 115, 'وَمَا یَفۡعَلُوا۟ مِنۡ خَیۡرࣲ فَلَن یُكۡفَرُوهُۗ وَٱللَّهُ عَلِیمُۢ بِٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:35:16', '2023-05-27 11:19:28'),
(5398, 3, 409, 116, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ لَن تُغۡنِیَ عَنۡهُمۡ أَمۡوَ ٰ⁠لُهُمۡ وَلَاۤ أَوۡلَـٰدُهُم مِّنَ ٱللَّهِ شَیۡـࣰٔاۖ وَأُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5399, 3, 410, 117, 'مَثَلُ مَا یُنفِقُونَ فِی هَـٰذِهِ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا كَمَثَلِ رِیحࣲ فِیهَا صِرٌّ أَصَابَتۡ حَرۡثَ قَوۡمࣲ ظَلَمُوۤا۟ أَنفُسَهُمۡ فَأَهۡلَكَتۡهُۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَـٰكِنۡ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5400, 3, 411, 118, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَتَّخِذُوا۟ بِطَانَةࣰ مِّن دُونِكُمۡ لَا یَأۡلُونَكُمۡ خَبَالࣰا وَدُّوا۟ مَا عَنِتُّمۡ قَدۡ بَدَتِ ٱلۡبَغۡضَاۤءُ مِنۡ أَفۡوَ ٰ⁠هِهِمۡ وَمَا تُخۡفِی صُدُورُهُمۡ أَكۡبَرُۚ قَدۡ بَیَّنَّا لَكُمُ ٱلۡـَٔایَـٰتِۖ إِن كُنتُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5401, 3, 412, 119, 'هَـٰۤأَنتُمۡ أُو۟لَاۤءِ تُحِبُّونَهُمۡ وَلَا یُحِبُّونَكُمۡ وَتُؤۡمِنُونَ بِٱلۡكِتَـٰبِ كُلِّهِۦ وَإِذَا لَقُوكُمۡ قَالُوۤا۟ ءَامَنَّا وَإِذَا خَلَوۡا۟ عَضُّوا۟ عَلَیۡكُمُ ٱلۡأَنَامِلَ مِنَ ٱلۡغَیۡظِۚ قُلۡ مُوتُوا۟ بِغَیۡظِكُمۡۗ إِنَّ ٱللَّهَ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5402, 3, 413, 120, 'إِن تَمۡسَسۡكُمۡ حَسَنَةࣱ تَسُؤۡهُمۡ وَإِن تُصِبۡكُمۡ سَیِّئَةࣱ یَفۡرَحُوا۟ بِهَاۖ وَإِن تَصۡبِرُوا۟ وَتَتَّقُوا۟ لَا یَضُرُّكُمۡ كَیۡدُهُمۡ شَیۡـًٔاۗ إِنَّ ٱللَّهَ بِمَا یَعۡمَلُونَ مُحِیطࣱ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5403, 3, 414, 121, 'وَإِذۡ غَدَوۡتَ مِنۡ أَهۡلِكَ تُبَوِّئُ ٱلۡمُؤۡمِنِینَ مَقَـٰعِدَ لِلۡقِتَالِۗ وَٱللَّهُ سَمِیعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5404, 3, 415, 122, 'إِذۡ هَمَّت طَّاۤىِٕفَتَانِ مِنكُمۡ أَن تَفۡشَلَا وَٱللَّهُ وَلِیُّهُمَاۗ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5405, 3, 416, 123, 'وَلَقَدۡ نَصَرَكُمُ ٱللَّهُ بِبَدۡرࣲ وَأَنتُمۡ أَذِلَّةࣱۖ فَٱتَّقُوا۟ ٱللَّهَ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:35:17', '2023-05-27 11:19:28'),
(5406, 3, 417, 124, 'إِذۡ تَقُولُ لِلۡمُؤۡمِنِینَ أَلَن یَكۡفِیَكُمۡ أَن یُمِدَّكُمۡ رَبُّكُم بِثَلَـٰثَةِ ءَالَـٰفࣲ مِّنَ ٱلۡمَلَـٰۤىِٕكَةِ مُنزَلِینَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:28'),
(5407, 3, 418, 125, 'بَلَىٰۤۚ إِن تَصۡبِرُوا۟ وَتَتَّقُوا۟ وَیَأۡتُوكُم مِّن فَوۡرِهِمۡ هَـٰذَا یُمۡدِدۡكُمۡ رَبُّكُم بِخَمۡسَةِ ءَالَـٰفࣲ مِّنَ ٱلۡمَلَـٰۤىِٕكَةِ مُسَوِّمِینَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:28'),
(5408, 3, 419, 126, 'وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ لَكُمۡ وَلِتَطۡمَىِٕنَّ قُلُوبُكُم بِهِۦۗ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِ ٱلۡعَزِیزِ ٱلۡحَكِیمِ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:28'),
(5409, 3, 420, 127, 'لِیَقۡطَعَ طَرَفࣰا مِّنَ ٱلَّذِینَ كَفَرُوۤا۟ أَوۡ یَكۡبِتَهُمۡ فَیَنقَلِبُوا۟ خَاۤىِٕبِینَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5410, 3, 421, 128, 'لَیۡسَ لَكَ مِنَ ٱلۡأَمۡرِ شَیۡءٌ أَوۡ یَتُوبَ عَلَیۡهِمۡ أَوۡ یُعَذِّبَهُمۡ فَإِنَّهُمۡ ظَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5411, 3, 422, 129, 'وَلِلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۚ یَغۡفِرُ لِمَن یَشَاۤءُ وَیُعَذِّبُ مَن یَشَاۤءُۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5412, 3, 423, 130, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَأۡكُلُوا۟ ٱلرِّبَوٰۤا۟ أَضۡعَـٰفࣰا مُّضَـٰعَفَةࣰۖ وَٱتَّقُوا۟ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5413, 3, 424, 131, 'وَٱتَّقُوا۟ ٱلنَّارَ ٱلَّتِیۤ أُعِدَّتۡ لِلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5414, 3, 425, 132, 'وَأَطِیعُوا۟ ٱللَّهَ وَٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5415, 3, 426, 133, '۞ وَسَارِعُوۤا۟ إِلَىٰ مَغۡفِرَةࣲ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا ٱلسَّمَـٰوَ ٰ⁠تُ وَٱلۡأَرۡضُ أُعِدَّتۡ لِلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5416, 3, 427, 134, 'ٱلَّذِینَ یُنفِقُونَ فِی ٱلسَّرَّاۤءِ وَٱلضَّرَّاۤءِ وَٱلۡكَـٰظِمِینَ ٱلۡغَیۡظَ وَٱلۡعَافِینَ عَنِ ٱلنَّاسِۗ وَٱللَّهُ یُحِبُّ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:35:18', '2023-05-27 11:19:29'),
(5417, 3, 428, 135, 'وَٱلَّذِینَ إِذَا فَعَلُوا۟ فَـٰحِشَةً أَوۡ ظَلَمُوۤا۟ أَنفُسَهُمۡ ذَكَرُوا۟ ٱللَّهَ فَٱسۡتَغۡفَرُوا۟ لِذُنُوبِهِمۡ وَمَن یَغۡفِرُ ٱلذُّنُوبَ إِلَّا ٱللَّهُ وَلَمۡ یُصِرُّوا۟ عَلَىٰ مَا فَعَلُوا۟ وَهُمۡ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5418, 3, 429, 136, 'أُو۟لَـٰۤىِٕكَ جَزَاۤؤُهُم مَّغۡفِرَةࣱ مِّن رَّبِّهِمۡ وَجَنَّـٰتࣱ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَاۚ وَنِعۡمَ أَجۡرُ ٱلۡعَـٰمِلِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5419, 3, 430, 137, 'قَدۡ خَلَتۡ مِن قَبۡلِكُمۡ سُنَنࣱ فَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَٱنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5420, 3, 431, 138, 'هَـٰذَا بَیَانࣱ لِّلنَّاسِ وَهُدࣰى وَمَوۡعِظَةࣱ لِّلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5421, 3, 432, 139, 'وَلَا تَهِنُوا۟ وَلَا تَحۡزَنُوا۟ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5422, 3, 433, 140, 'إِن یَمۡسَسۡكُمۡ قَرۡحࣱ فَقَدۡ مَسَّ ٱلۡقَوۡمَ قَرۡحࣱ مِّثۡلُهُۥۚ وَتِلۡكَ ٱلۡأَیَّامُ نُدَاوِلُهَا بَیۡنَ ٱلنَّاسِ وَلِیَعۡلَمَ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ وَیَتَّخِذَ مِنكُمۡ شُهَدَاۤءَۗ وَٱللَّهُ لَا یُحِبُّ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5423, 3, 434, 141, 'وَلِیُمَحِّصَ ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ وَیَمۡحَقَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5424, 3, 435, 142, 'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُوا۟ ٱلۡجَنَّةَ وَلَمَّا یَعۡلَمِ ٱللَّهُ ٱلَّذِینَ جَـٰهَدُوا۟ مِنكُمۡ وَیَعۡلَمَ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5425, 3, 436, 143, 'وَلَقَدۡ كُنتُمۡ تَمَنَّوۡنَ ٱلۡمَوۡتَ مِن قَبۡلِ أَن تَلۡقَوۡهُ فَقَدۡ رَأَیۡتُمُوهُ وَأَنتُمۡ تَنظُرُونَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:29'),
(5426, 3, 437, 144, 'وَمَا مُحَمَّدٌ إِلَّا رَسُولࣱ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُۚ أَفَإِی۟ن مَّاتَ أَوۡ قُتِلَ ٱنقَلَبۡتُمۡ عَلَىٰۤ أَعۡقَـٰبِكُمۡۚ وَمَن یَنقَلِبۡ عَلَىٰ عَقِبَیۡهِ فَلَن یَضُرَّ ٱللَّهَ شَیۡـࣰٔاۗ وَسَیَجۡزِی ٱللَّهُ ٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:30'),
(5427, 3, 438, 145, 'وَمَا كَانَ لِنَفۡسٍ أَن تَمُوتَ إِلَّا بِإِذۡنِ ٱللَّهِ كِتَـٰبࣰا مُّؤَجَّلࣰاۗ وَمَن یُرِدۡ ثَوَابَ ٱلدُّنۡیَا نُؤۡتِهِۦ مِنۡهَا وَمَن یُرِدۡ ثَوَابَ ٱلۡـَٔاخِرَةِ نُؤۡتِهِۦ مِنۡهَاۚ وَسَنَجۡزِی ٱلشَّـٰكِرِینَ\n', NULL, 0, '2023-05-26 17:35:19', '2023-05-27 11:19:30'),
(5428, 3, 439, 146, 'وَكَأَیِّن مِّن نَّبِیࣲّ قَـٰتَلَ مَعَهُۥ رِبِّیُّونَ كَثِیرࣱ فَمَا وَهَنُوا۟ لِمَاۤ أَصَابَهُمۡ فِی سَبِیلِ ٱللَّهِ وَمَا ضَعُفُوا۟ وَمَا ٱسۡتَكَانُوا۟ۗ وَٱللَّهُ یُحِبُّ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5429, 3, 440, 147, 'وَمَا كَانَ قَوۡلَهُمۡ إِلَّاۤ أَن قَالُوا۟ رَبَّنَا ٱغۡفِرۡ لَنَا ذُنُوبَنَا وَإِسۡرَافَنَا فِیۤ أَمۡرِنَا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5430, 3, 441, 148, 'فَـَٔاتَىٰهُمُ ٱللَّهُ ثَوَابَ ٱلدُّنۡیَا وَحُسۡنَ ثَوَابِ ٱلۡـَٔاخِرَةِۗ وَٱللَّهُ یُحِبُّ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5431, 3, 442, 149, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِن تُطِیعُوا۟ ٱلَّذِینَ كَفَرُوا۟ یَرُدُّوكُمۡ عَلَىٰۤ أَعۡقَـٰبِكُمۡ فَتَنقَلِبُوا۟ خَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5432, 3, 443, 150, 'بَلِ ٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ خَیۡرُ ٱلنَّـٰصِرِینَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5433, 3, 444, 151, 'سَنُلۡقِی فِی قُلُوبِ ٱلَّذِینَ كَفَرُوا۟ ٱلرُّعۡبَ بِمَاۤ أَشۡرَكُوا۟ بِٱللَّهِ مَا لَمۡ یُنَزِّلۡ بِهِۦ سُلۡطَـٰنࣰاۖ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَبِئۡسَ مَثۡوَى ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5434, 3, 445, 152, 'وَلَقَدۡ صَدَقَكُمُ ٱللَّهُ وَعۡدَهُۥۤ إِذۡ تَحُسُّونَهُم بِإِذۡنِهِۦۖ حَتَّىٰۤ إِذَا فَشِلۡتُمۡ وَتَنَـٰزَعۡتُمۡ فِی ٱلۡأَمۡرِ وَعَصَیۡتُم مِّنۢ بَعۡدِ مَاۤ أَرَىٰكُم مَّا تُحِبُّونَۚ مِنكُم مَّن یُرِیدُ ٱلدُّنۡیَا وَمِنكُم مَّن یُرِیدُ ٱلۡـَٔاخِرَةَۚ ثُمَّ صَرَفَكُمۡ عَنۡهُمۡ لِیَبۡتَلِیَكُمۡۖ وَلَقَدۡ عَفَا عَنكُمۡۗ وَٱللَّهُ ذُو فَضۡلٍ عَلَى ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5435, 3, 446, 153, '۞ إِذۡ تُصۡعِدُونَ وَلَا تَلۡوُۥنَ عَلَىٰۤ أَحَدࣲ وَٱلرَّسُولُ یَدۡعُوكُمۡ فِیۤ أُخۡرَىٰكُمۡ فَأَثَـٰبَكُمۡ غَمَّۢا بِغَمࣲّ لِّكَیۡلَا تَحۡزَنُوا۟ عَلَىٰ مَا فَاتَكُمۡ وَلَا مَاۤ أَصَـٰبَكُمۡۗ وَٱللَّهُ خَبِیرُۢ بِمَا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5436, 3, 447, 154, 'ثُمَّ أَنزَلَ عَلَیۡكُم مِّنۢ بَعۡدِ ٱلۡغَمِّ أَمَنَةࣰ نُّعَاسࣰا یَغۡشَىٰ طَاۤىِٕفَةࣰ مِّنكُمۡۖ وَطَاۤىِٕفَةࣱ قَدۡ أَهَمَّتۡهُمۡ أَنفُسُهُمۡ یَظُنُّونَ بِٱللَّهِ غَیۡرَ ٱلۡحَقِّ ظَنَّ ٱلۡجَـٰهِلِیَّةِۖ یَقُولُونَ هَل لَّنَا مِنَ ٱلۡأَمۡرِ مِن شَیۡءࣲۗ قُلۡ إِنَّ ٱلۡأَمۡرَ كُلَّهُۥ لِلَّهِۗ یُخۡفُونَ فِیۤ أَنفُسِهِم مَّا لَا یُبۡدُونَ لَكَۖ یَقُولُونَ لَوۡ كَانَ لَنَا مِنَ ٱلۡأَمۡرِ شَیۡءࣱ مَّا قُتِلۡنَا هَـٰهُنَاۗ قُل لَّوۡ كُنتُمۡ فِی بُیُوتِكُمۡ لَبَرَزَ ٱلَّذِینَ كُتِبَ عَلَیۡهِمُ ٱلۡقَتۡلُ إِلَىٰ مَضَاجِعِهِمۡۖ وَلِیَبۡتَلِیَ ٱللَّهُ مَا فِی صُدُورِكُمۡ وَلِیُمَحِّصَ مَا فِی قُلُوبِكُمۡۚ وَٱللَّهُ عَلِیمُۢ بِذَاتِ ٱلصُّدُورِ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5437, 3, 448, 155, 'إِنَّ ٱلَّذِینَ تَوَلَّوۡا۟ مِنكُمۡ یَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ إِنَّمَا ٱسۡتَزَلَّهُمُ ٱلشَّیۡطَـٰنُ بِبَعۡضِ مَا كَسَبُوا۟ۖ وَلَقَدۡ عَفَا ٱللَّهُ عَنۡهُمۡۗ إِنَّ ٱللَّهَ غَفُورٌ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:35:20', '2023-05-27 11:19:30'),
(5438, 3, 449, 156, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَكُونُوا۟ كَٱلَّذِینَ كَفَرُوا۟ وَقَالُوا۟ لِإِخۡوَ ٰ⁠نِهِمۡ إِذَا ضَرَبُوا۟ فِی ٱلۡأَرۡضِ أَوۡ كَانُوا۟ غُزࣰّى لَّوۡ كَانُوا۟ عِندَنَا مَا مَاتُوا۟ وَمَا قُتِلُوا۟ لِیَجۡعَلَ ٱللَّهُ ذَ ٰ⁠لِكَ حَسۡرَةࣰ فِی قُلُوبِهِمۡۗ وَٱللَّهُ یُحۡیِۦ وَیُمِیتُۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:30'),
(5439, 3, 450, 157, 'وَلَىِٕن قُتِلۡتُمۡ فِی سَبِیلِ ٱللَّهِ أَوۡ مُتُّمۡ لَمَغۡفِرَةࣱ مِّنَ ٱللَّهِ وَرَحۡمَةٌ خَیۡرࣱ مِّمَّا یَجۡمَعُونَ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:30'),
(5440, 3, 451, 158, 'وَلَىِٕن مُّتُّمۡ أَوۡ قُتِلۡتُمۡ لَإِلَى ٱللَّهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:30'),
(5441, 3, 452, 159, 'فَبِمَا رَحۡمَةࣲ مِّنَ ٱللَّهِ لِنتَ لَهُمۡۖ وَلَوۡ كُنتَ فَظًّا غَلِیظَ ٱلۡقَلۡبِ لَٱنفَضُّوا۟ مِنۡ حَوۡلِكَۖ فَٱعۡفُ عَنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمۡ وَشَاوِرۡهُمۡ فِی ٱلۡأَمۡرِۖ فَإِذَا عَزَمۡتَ فَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُتَوَكِّلِینَ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:31'),
(5442, 3, 453, 160, 'إِن یَنصُرۡكُمُ ٱللَّهُ فَلَا غَالِبَ لَكُمۡۖ وَإِن یَخۡذُلۡكُمۡ فَمَن ذَا ٱلَّذِی یَنصُرُكُم مِّنۢ بَعۡدِهِۦۗ وَعَلَى ٱللَّهِ فَلۡیَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:31'),
(5443, 3, 454, 161, 'وَمَا كَانَ لِنَبِیٍّ أَن یَغُلَّۚ وَمَن یَغۡلُلۡ یَأۡتِ بِمَا غَلَّ یَوۡمَ ٱلۡقِیَـٰمَةِۚ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسࣲ مَّا كَسَبَتۡ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:31'),
(5444, 3, 455, 162, 'أَفَمَنِ ٱتَّبَعَ رِضۡوَ ٰ⁠نَ ٱللَّهِ كَمَنۢ بَاۤءَ بِسَخَطࣲ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:31'),
(5445, 3, 456, 163, 'هُمۡ دَرَجَـٰتٌ عِندَ ٱللَّهِۗ وَٱللَّهُ بَصِیرُۢ بِمَا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:35:21', '2023-05-27 11:19:31'),
(5446, 3, 457, 164, 'لَقَدۡ مَنَّ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِینَ إِذۡ بَعَثَ فِیهِمۡ رَسُولࣰا مِّنۡ أَنفُسِهِمۡ یَتۡلُوا۟ عَلَیۡهِمۡ ءَایَـٰتِهِۦ وَیُزَكِّیهِمۡ وَیُعَلِّمُهُمُ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُوا۟ مِن قَبۡلُ لَفِی ضَلَـٰلࣲ مُّبِینٍ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5447, 3, 458, 165, 'أَوَلَمَّاۤ أَصَـٰبَتۡكُم مُّصِیبَةࣱ قَدۡ أَصَبۡتُم مِّثۡلَیۡهَا قُلۡتُمۡ أَنَّىٰ هَـٰذَاۖ قُلۡ هُوَ مِنۡ عِندِ أَنفُسِكُمۡۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5448, 3, 459, 166, 'وَمَاۤ أَصَـٰبَكُمۡ یَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ فَبِإِذۡنِ ٱللَّهِ وَلِیَعۡلَمَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5449, 3, 460, 167, 'وَلِیَعۡلَمَ ٱلَّذِینَ نَافَقُوا۟ۚ وَقِیلَ لَهُمۡ تَعَالَوۡا۟ قَـٰتِلُوا۟ فِی سَبِیلِ ٱللَّهِ أَوِ ٱدۡفَعُوا۟ۖ قَالُوا۟ لَوۡ نَعۡلَمُ قِتَالࣰا لَّٱتَّبَعۡنَـٰكُمۡۗ هُمۡ لِلۡكُفۡرِ یَوۡمَىِٕذٍ أَقۡرَبُ مِنۡهُمۡ لِلۡإِیمَـٰنِۚ یَقُولُونَ بِأَفۡوَ ٰ⁠هِهِم مَّا لَیۡسَ فِی قُلُوبِهِمۡۚ وَٱللَّهُ أَعۡلَمُ بِمَا یَكۡتُمُونَ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5450, 3, 461, 168, 'ٱلَّذِینَ قَالُوا۟ لِإِخۡوَ ٰ⁠نِهِمۡ وَقَعَدُوا۟ لَوۡ أَطَاعُونَا مَا قُتِلُوا۟ۗ قُلۡ فَٱدۡرَءُوا۟ عَنۡ أَنفُسِكُمُ ٱلۡمَوۡتَ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5451, 3, 462, 169, 'وَلَا تَحۡسَبَنَّ ٱلَّذِینَ قُتِلُوا۟ فِی سَبِیلِ ٱللَّهِ أَمۡوَ ٰ⁠تَۢاۚ بَلۡ أَحۡیَاۤءٌ عِندَ رَبِّهِمۡ یُرۡزَقُونَ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5452, 3, 463, 170, 'فَرِحِینَ بِمَاۤ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ وَیَسۡتَبۡشِرُونَ بِٱلَّذِینَ لَمۡ یَلۡحَقُوا۟ بِهِم مِّنۡ خَلۡفِهِمۡ أَلَّا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5453, 3, 464, 171, '۞ یَسۡتَبۡشِرُونَ بِنِعۡمَةࣲ مِّنَ ٱللَّهِ وَفَضۡلࣲ وَأَنَّ ٱللَّهَ لَا یُضِیعُ أَجۡرَ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5454, 3, 465, 172, 'ٱلَّذِینَ ٱسۡتَجَابُوا۟ لِلَّهِ وَٱلرَّسُولِ مِنۢ بَعۡدِ مَاۤ أَصَابَهُمُ ٱلۡقَرۡحُۚ لِلَّذِینَ أَحۡسَنُوا۟ مِنۡهُمۡ وَٱتَّقَوۡا۟ أَجۡرٌ عَظِیمٌ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5455, 3, 466, 173, 'ٱلَّذِینَ قَالَ لَهُمُ ٱلنَّاسُ إِنَّ ٱلنَّاسَ قَدۡ جَمَعُوا۟ لَكُمۡ فَٱخۡشَوۡهُمۡ فَزَادَهُمۡ إِیمَـٰنࣰا وَقَالُوا۟ حَسۡبُنَا ٱللَّهُ وَنِعۡمَ ٱلۡوَكِیلُ\n', NULL, 0, '2023-05-26 17:35:22', '2023-05-27 11:19:31'),
(5456, 3, 467, 174, 'فَٱنقَلَبُوا۟ بِنِعۡمَةࣲ مِّنَ ٱللَّهِ وَفَضۡلࣲ لَّمۡ یَمۡسَسۡهُمۡ سُوۤءࣱ وَٱتَّبَعُوا۟ رِضۡوَ ٰ⁠نَ ٱللَّهِۗ وَٱللَّهُ ذُو فَضۡلٍ عَظِیمٍ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:31'),
(5457, 3, 468, 175, 'إِنَّمَا ذَ ٰ⁠لِكُمُ ٱلشَّیۡطَـٰنُ یُخَوِّفُ أَوۡلِیَاۤءَهُۥ فَلَا تَخَافُوهُمۡ وَخَافُونِ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5458, 3, 469, 176, 'وَلَا یَحۡزُنكَ ٱلَّذِینَ یُسَـٰرِعُونَ فِی ٱلۡكُفۡرِۚ إِنَّهُمۡ لَن یَضُرُّوا۟ ٱللَّهَ شَیۡـࣰٔاۗ یُرِیدُ ٱللَّهُ أَلَّا یَجۡعَلَ لَهُمۡ حَظࣰّا فِی ٱلۡـَٔاخِرَةِۖ وَلَهُمۡ عَذَابٌ عَظِیمٌ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5459, 3, 470, 177, 'إِنَّ ٱلَّذِینَ ٱشۡتَرَوُا۟ ٱلۡكُفۡرَ بِٱلۡإِیمَـٰنِ لَن یَضُرُّوا۟ ٱللَّهَ شَیۡـࣰٔاۖ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5460, 3, 471, 178, 'وَلَا یَحۡسَبَنَّ ٱلَّذِینَ كَفَرُوۤا۟ أَنَّمَا نُمۡلِی لَهُمۡ خَیۡرࣱ لِّأَنفُسِهِمۡۚ إِنَّمَا نُمۡلِی لَهُمۡ لِیَزۡدَادُوۤا۟ إِثۡمࣰاۖ وَلَهُمۡ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5461, 3, 472, 179, 'مَّا كَانَ ٱللَّهُ لِیَذَرَ ٱلۡمُؤۡمِنِینَ عَلَىٰ مَاۤ أَنتُمۡ عَلَیۡهِ حَتَّىٰ یَمِیزَ ٱلۡخَبِیثَ مِنَ ٱلطَّیِّبِۗ وَمَا كَانَ ٱللَّهُ لِیُطۡلِعَكُمۡ عَلَى ٱلۡغَیۡبِ وَلَـٰكِنَّ ٱللَّهَ یَجۡتَبِی مِن رُّسُلِهِۦ مَن یَشَاۤءُۖ فَـَٔامِنُوا۟ بِٱللَّهِ وَرُسُلِهِۦۚ وَإِن تُؤۡمِنُوا۟ وَتَتَّقُوا۟ فَلَكُمۡ أَجۡرٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5462, 3, 473, 180, 'وَلَا یَحۡسَبَنَّ ٱلَّذِینَ یَبۡخَلُونَ بِمَاۤ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ هُوَ خَیۡرࣰا لَّهُمۖ بَلۡ هُوَ شَرࣱّ لَّهُمۡۖ سَیُطَوَّقُونَ مَا بَخِلُوا۟ بِهِۦ یَوۡمَ ٱلۡقِیَـٰمَةِۗ وَلِلَّهِ مِیرَ ٰ⁠ثُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5463, 3, 474, 181, 'لَّقَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّذِینَ قَالُوۤا۟ إِنَّ ٱللَّهَ فَقِیرࣱ وَنَحۡنُ أَغۡنِیَاۤءُۘ سَنَكۡتُبُ مَا قَالُوا۟ وَقَتۡلَهُمُ ٱلۡأَنۢبِیَاۤءَ بِغَیۡرِ حَقࣲّ وَنَقُولُ ذُوقُوا۟ عَذَابَ ٱلۡحَرِیقِ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5464, 3, 475, 182, 'ذَ ٰ⁠لِكَ بِمَا قَدَّمَتۡ أَیۡدِیكُمۡ وَأَنَّ ٱللَّهَ لَیۡسَ بِظَلَّامࣲ لِّلۡعَبِیدِ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5465, 3, 476, 183, 'ٱلَّذِینَ قَالُوۤا۟ إِنَّ ٱللَّهَ عَهِدَ إِلَیۡنَاۤ أَلَّا نُؤۡمِنَ لِرَسُولٍ حَتَّىٰ یَأۡتِیَنَا بِقُرۡبَانࣲ تَأۡكُلُهُ ٱلنَّارُۗ قُلۡ قَدۡ جَاۤءَكُمۡ رُسُلࣱ مِّن قَبۡلِی بِٱلۡبَیِّنَـٰتِ وَبِٱلَّذِی قُلۡتُمۡ فَلِمَ قَتَلۡتُمُوهُمۡ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:35:23', '2023-05-27 11:19:32'),
(5466, 3, 477, 184, 'فَإِن كَذَّبُوكَ فَقَدۡ كُذِّبَ رُسُلࣱ مِّن قَبۡلِكَ جَاۤءُو بِٱلۡبَیِّنَـٰتِ وَٱلزُّبُرِ وَٱلۡكِتَـٰبِ ٱلۡمُنِیرِ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:32'),
(5467, 3, 478, 185, 'كُلُّ نَفۡسࣲ ذَاۤىِٕقَةُ ٱلۡمَوۡتِۗ وَإِنَّمَا تُوَفَّوۡنَ أُجُورَكُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِۖ فَمَن زُحۡزِحَ عَنِ ٱلنَّارِ وَأُدۡخِلَ ٱلۡجَنَّةَ فَقَدۡ فَازَۗ وَمَا ٱلۡحَیَوٰةُ ٱلدُّنۡیَاۤ إِلَّا مَتَـٰعُ ٱلۡغُرُورِ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:32'),
(5468, 3, 479, 186, '۞ لَتُبۡلَوُنَّ فِیۤ أَمۡوَ ٰ⁠لِكُمۡ وَأَنفُسِكُمۡ وَلَتَسۡمَعُنَّ مِنَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ مِن قَبۡلِكُمۡ وَمِنَ ٱلَّذِینَ أَشۡرَكُوۤا۟ أَذࣰى كَثِیرࣰاۚ وَإِن تَصۡبِرُوا۟ وَتَتَّقُوا۟ فَإِنَّ ذَ ٰ⁠لِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:32'),
(5469, 3, 480, 187, 'وَإِذۡ أَخَذَ ٱللَّهُ مِیثَـٰقَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ لَتُبَیِّنُنَّهُۥ لِلنَّاسِ وَلَا تَكۡتُمُونَهُۥ فَنَبَذُوهُ وَرَاۤءَ ظُهُورِهِمۡ وَٱشۡتَرَوۡا۟ بِهِۦ ثَمَنࣰا قَلِیلࣰاۖ فَبِئۡسَ مَا یَشۡتَرُونَ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:32'),
(5470, 3, 481, 188, 'لَا تَحۡسَبَنَّ ٱلَّذِینَ یَفۡرَحُونَ بِمَاۤ أَتَوا۟ وَّیُحِبُّونَ أَن یُحۡمَدُوا۟ بِمَا لَمۡ یَفۡعَلُوا۟ فَلَا تَحۡسَبَنَّهُم بِمَفَازَةࣲ مِّنَ ٱلۡعَذَابِۖ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:32'),
(5471, 3, 482, 189, 'وَلِلَّهِ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:32'),
(5472, 3, 483, 190, 'إِنَّ فِی خَلۡقِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَٱخۡتِلَـٰفِ ٱلَّیۡلِ وَٱلنَّهَارِ لَـَٔایَـٰتࣲ لِّأُو۟لِی ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:33'),
(5473, 3, 484, 191, 'ٱلَّذِینَ یَذۡكُرُونَ ٱللَّهَ قِیَـٰمࣰا وَقُعُودࣰا وَعَلَىٰ جُنُوبِهِمۡ وَیَتَفَكَّرُونَ فِی خَلۡقِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ رَبَّنَا مَا خَلَقۡتَ هَـٰذَا بَـٰطِلࣰا سُبۡحَـٰنَكَ فَقِنَا عَذَابَ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:35:24', '2023-05-27 11:19:33'),
(5474, 3, 485, 192, 'رَبَّنَاۤ إِنَّكَ مَن تُدۡخِلِ ٱلنَّارَ فَقَدۡ أَخۡزَیۡتَهُۥۖ وَمَا لِلظَّـٰلِمِینَ مِنۡ أَنصَارࣲ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5475, 3, 486, 193, 'رَّبَّنَاۤ إِنَّنَا سَمِعۡنَا مُنَادِیࣰا یُنَادِی لِلۡإِیمَـٰنِ أَنۡ ءَامِنُوا۟ بِرَبِّكُمۡ فَـَٔامَنَّاۚ رَبَّنَا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَكَفِّرۡ عَنَّا سَیِّـَٔاتِنَا وَتَوَفَّنَا مَعَ ٱلۡأَبۡرَارِ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5476, 3, 487, 194, 'رَبَّنَا وَءَاتِنَا مَا وَعَدتَّنَا عَلَىٰ رُسُلِكَ وَلَا تُخۡزِنَا یَوۡمَ ٱلۡقِیَـٰمَةِۖ إِنَّكَ لَا تُخۡلِفُ ٱلۡمِیعَادَ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5477, 3, 488, 195, 'فَٱسۡتَجَابَ لَهُمۡ رَبُّهُمۡ أَنِّی لَاۤ أُضِیعُ عَمَلَ عَـٰمِلࣲ مِّنكُم مِّن ذَكَرٍ أَوۡ أُنثَىٰۖ بَعۡضُكُم مِّنۢ بَعۡضࣲۖ فَٱلَّذِینَ هَاجَرُوا۟ وَأُخۡرِجُوا۟ مِن دِیَـٰرِهِمۡ وَأُوذُوا۟ فِی سَبِیلِی وَقَـٰتَلُوا۟ وَقُتِلُوا۟ لَأُكَفِّرَنَّ عَنۡهُمۡ سَیِّـَٔاتِهِمۡ وَلَأُدۡخِلَنَّهُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ ثَوَابࣰا مِّنۡ عِندِ ٱللَّهِۚ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلثَّوَابِ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5478, 3, 489, 196, 'لَا یَغُرَّنَّكَ تَقَلُّبُ ٱلَّذِینَ كَفَرُوا۟ فِی ٱلۡبِلَـٰدِ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5479, 3, 490, 197, 'مَتَـٰعࣱ قَلِیلࣱ ثُمَّ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5480, 3, 491, 198, 'لَـٰكِنِ ٱلَّذِینَ ٱتَّقَوۡا۟ رَبَّهُمۡ لَهُمۡ جَنَّـٰتࣱ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ خَـٰلِدِینَ فِیهَا نُزُلࣰا مِّنۡ عِندِ ٱللَّهِۗ وَمَا عِندَ ٱللَّهِ خَیۡرࣱ لِّلۡأَبۡرَارِ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5481, 3, 492, 199, 'وَإِنَّ مِنۡ أَهۡلِ ٱلۡكِتَـٰبِ لَمَن یُؤۡمِنُ بِٱللَّهِ وَمَاۤ أُنزِلَ إِلَیۡكُمۡ وَمَاۤ أُنزِلَ إِلَیۡهِمۡ خَـٰشِعِینَ لِلَّهِ لَا یَشۡتَرُونَ بِـَٔایَـٰتِ ٱللَّهِ ثَمَنࣰا قَلِیلًاۚ أُو۟لَـٰۤىِٕكَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡۗ إِنَّ ٱللَّهَ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5482, 3, 493, 200, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱصۡبِرُوا۟ وَصَابِرُوا۟ وَرَابِطُوا۟ وَٱتَّقُوا۟ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:35:25', '2023-05-27 11:19:33'),
(5483, 2, 8, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤمۤ\n', NULL, 0, '2023-05-26 17:35:53', '2023-05-27 11:19:41'),
(5484, 2, 9, 2, 'ذَ ٰ⁠لِكَ ٱلۡكِتَـٰبُ لَا رَیۡبَۛ فِیهِۛ هُدࣰى لِّلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:35:53', '2023-05-27 11:19:41'),
(5485, 2, 10, 3, 'ٱلَّذِینَ یُؤۡمِنُونَ بِٱلۡغَیۡبِ وَیُقِیمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَـٰهُمۡ یُنفِقُونَ\n', NULL, 0, '2023-05-26 17:35:53', '2023-05-27 11:19:41'),
(5486, 2, 11, 4, 'وَٱلَّذِینَ یُؤۡمِنُونَ بِمَاۤ أُنزِلَ إِلَیۡكَ وَمَاۤ أُنزِلَ مِن قَبۡلِكَ وَبِٱلۡـَٔاخِرَةِ هُمۡ یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:35:53', '2023-05-27 11:19:41'),
(5487, 2, 12, 5, 'أُو۟لَـٰۤىِٕكَ عَلَىٰ هُدࣰى مِّن رَّبِّهِمۡۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5488, 2, 13, 6, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ سَوَاۤءٌ عَلَیۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5489, 2, 14, 7, 'خَتَمَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَعَلَىٰ سَمۡعِهِمۡۖ وَعَلَىٰۤ أَبۡصَـٰرِهِمۡ غِشَـٰوَةࣱۖ وَلَهُمۡ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5490, 2, 15, 8, 'وَمِنَ ٱلنَّاسِ مَن یَقُولُ ءَامَنَّا بِٱللَّهِ وَبِٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَمَا هُم بِمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5491, 2, 16, 9, 'یُخَـٰدِعُونَ ٱللَّهَ وَٱلَّذِینَ ءَامَنُوا۟ وَمَا یَخۡدَعُونَ إِلَّاۤ أَنفُسَهُمۡ وَمَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5492, 2, 17, 10, 'فِی قُلُوبِهِم مَّرَضࣱ فَزَادَهُمُ ٱللَّهُ مَرَضࣰاۖ وَلَهُمۡ عَذَابٌ أَلِیمُۢ بِمَا كَانُوا۟ یَكۡذِبُونَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5493, 2, 18, 11, 'وَإِذَا قِیلَ لَهُمۡ لَا تُفۡسِدُوا۟ فِی ٱلۡأَرۡضِ قَالُوۤا۟ إِنَّمَا نَحۡنُ مُصۡلِحُونَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5494, 2, 19, 12, 'أَلَاۤ إِنَّهُمۡ هُمُ ٱلۡمُفۡسِدُونَ وَلَـٰكِن لَّا یَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5495, 2, 20, 13, 'وَإِذَا قِیلَ لَهُمۡ ءَامِنُوا۟ كَمَاۤ ءَامَنَ ٱلنَّاسُ قَالُوۤا۟ أَنُؤۡمِنُ كَمَاۤ ءَامَنَ ٱلسُّفَهَاۤءُۗ أَلَاۤ إِنَّهُمۡ هُمُ ٱلسُّفَهَاۤءُ وَلَـٰكِن لَّا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:54', '2023-05-27 11:19:41'),
(5496, 2, 21, 14, 'وَإِذَا لَقُوا۟ ٱلَّذِینَ ءَامَنُوا۟ قَالُوۤا۟ ءَامَنَّا وَإِذَا خَلَوۡا۟ إِلَىٰ شَیَـٰطِینِهِمۡ قَالُوۤا۟ إِنَّا مَعَكُمۡ إِنَّمَا نَحۡنُ مُسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5497, 2, 22, 15, 'ٱللَّهُ یَسۡتَهۡزِئُ بِهِمۡ وَیَمُدُّهُمۡ فِی طُغۡیَـٰنِهِمۡ یَعۡمَهُونَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5498, 2, 23, 16, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ ٱشۡتَرَوُا۟ ٱلضَّلَـٰلَةَ بِٱلۡهُدَىٰ فَمَا رَبِحَت تِّجَـٰرَتُهُمۡ وَمَا كَانُوا۟ مُهۡتَدِینَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5499, 2, 24, 17, 'مَثَلُهُمۡ كَمَثَلِ ٱلَّذِی ٱسۡتَوۡقَدَ نَارࣰا فَلَمَّاۤ أَضَاۤءَتۡ مَا حَوۡلَهُۥ ذَهَبَ ٱللَّهُ بِنُورِهِمۡ وَتَرَكَهُمۡ فِی ظُلُمَـٰتࣲ لَّا یُبۡصِرُونَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5500, 2, 25, 18, 'صُمُّۢ بُكۡمٌ عُمۡیࣱ فَهُمۡ لَا یَرۡجِعُونَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5501, 2, 26, 19, 'أَوۡ كَصَیِّبࣲ مِّنَ ٱلسَّمَاۤءِ فِیهِ ظُلُمَـٰتࣱ وَرَعۡدࣱ وَبَرۡقࣱ یَجۡعَلُونَ أَصَـٰبِعَهُمۡ فِیۤ ءَاذَانِهِم مِّنَ ٱلصَّوَ ٰ⁠عِقِ حَذَرَ ٱلۡمَوۡتِۚ وَٱللَّهُ مُحِیطُۢ بِٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5502, 2, 27, 20, 'یَكَادُ ٱلۡبَرۡقُ یَخۡطَفُ أَبۡصَـٰرَهُمۡۖ كُلَّمَاۤ أَضَاۤءَ لَهُم مَّشَوۡا۟ فِیهِ وَإِذَاۤ أَظۡلَمَ عَلَیۡهِمۡ قَامُوا۟ۚ وَلَوۡ شَاۤءَ ٱللَّهُ لَذَهَبَ بِسَمۡعِهِمۡ وَأَبۡصَـٰرِهِمۡۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5503, 2, 28, 21, 'یَـٰۤأَیُّهَا ٱلنَّاسُ ٱعۡبُدُوا۟ رَبَّكُمُ ٱلَّذِی خَلَقَكُمۡ وَٱلَّذِینَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5504, 2, 29, 22, 'ٱلَّذِی جَعَلَ لَكُمُ ٱلۡأَرۡضَ فِرَ ٰ⁠شࣰا وَٱلسَّمَاۤءَ بِنَاۤءࣰ وَأَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَ ٰ⁠تِ رِزۡقࣰا لَّكُمۡۖ فَلَا تَجۡعَلُوا۟ لِلَّهِ أَندَادࣰا وَأَنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5505, 2, 30, 23, 'وَإِن كُنتُمۡ فِی رَیۡبࣲ مِّمَّا نَزَّلۡنَا عَلَىٰ عَبۡدِنَا فَأۡتُوا۟ بِسُورَةࣲ مِّن مِّثۡلِهِۦ وَٱدۡعُوا۟ شُهَدَاۤءَكُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:35:55', '2023-05-27 11:19:42'),
(5506, 2, 31, 24, 'فَإِن لَّمۡ تَفۡعَلُوا۟ وَلَن تَفۡعَلُوا۟ فَٱتَّقُوا۟ ٱلنَّارَ ٱلَّتِی وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُۖ أُعِدَّتۡ لِلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:42'),
(5507, 2, 32, 25, 'وَبَشِّرِ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ أَنَّ لَهُمۡ جَنَّـٰتࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۖ كُلَّمَا رُزِقُوا۟ مِنۡهَا مِن ثَمَرَةࣲ رِّزۡقࣰا قَالُوا۟ هَـٰذَا ٱلَّذِی رُزِقۡنَا مِن قَبۡلُۖ وَأُتُوا۟ بِهِۦ مُتَشَـٰبِهࣰاۖ وَلَهُمۡ فِیهَاۤ أَزۡوَ ٰ⁠جࣱ مُّطَهَّرَةࣱۖ وَهُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:42'),
(5508, 2, 33, 26, '۞ إِنَّ ٱللَّهَ لَا یَسۡتَحۡیِۦۤ أَن یَضۡرِبَ مَثَلࣰا مَّا بَعُوضَةࣰ فَمَا فَوۡقَهَاۚ فَأَمَّا ٱلَّذِینَ ءَامَنُوا۟ فَیَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۖ وَأَمَّا ٱلَّذِینَ كَفَرُوا۟ فَیَقُولُونَ مَاذَاۤ أَرَادَ ٱللَّهُ بِهَـٰذَا مَثَلࣰاۘ یُضِلُّ بِهِۦ كَثِیرࣰا وَیَهۡدِی بِهِۦ كَثِیرࣰاۚ وَمَا یُضِلُّ بِهِۦۤ إِلَّا ٱلۡفَـٰسِقِینَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:42'),
(5509, 2, 34, 27, 'ٱلَّذِینَ یَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِیثَـٰقِهِۦ وَیَقۡطَعُونَ مَاۤ أَمَرَ ٱللَّهُ بِهِۦۤ أَن یُوصَلَ وَیُفۡسِدُونَ فِی ٱلۡأَرۡضِۚ أُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:42'),
(5510, 2, 35, 28, 'كَیۡفَ تَكۡفُرُونَ بِٱللَّهِ وَكُنتُمۡ أَمۡوَ ٰ⁠تࣰا فَأَحۡیَـٰكُمۡۖ ثُمَّ یُمِیتُكُمۡ ثُمَّ یُحۡیِیكُمۡ ثُمَّ إِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:42'),
(5511, 2, 36, 29, 'هُوَ ٱلَّذِی خَلَقَ لَكُم مَّا فِی ٱلۡأَرۡضِ جَمِیعࣰا ثُمَّ ٱسۡتَوَىٰۤ إِلَى ٱلسَّمَاۤءِ فَسَوَّىٰهُنَّ سَبۡعَ سَمَـٰوَ ٰ⁠تࣲۚ وَهُوَ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:42'),
(5512, 2, 37, 30, 'وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰۤىِٕكَةِ إِنِّی جَاعِلࣱ فِی ٱلۡأَرۡضِ خَلِیفَةࣰۖ قَالُوۤا۟ أَتَجۡعَلُ فِیهَا مَن یُفۡسِدُ فِیهَا وَیَسۡفِكُ ٱلدِّمَاۤءَ وَنَحۡنُ نُسَبِّحُ بِحَمۡدِكَ وَنُقَدِّسُ لَكَۖ قَالَ إِنِّیۤ أَعۡلَمُ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:43'),
(5513, 2, 38, 31, 'وَعَلَّمَ ءَادَمَ ٱلۡأَسۡمَاۤءَ كُلَّهَا ثُمَّ عَرَضَهُمۡ عَلَى ٱلۡمَلَـٰۤىِٕكَةِ فَقَالَ أَنۢبِـُٔونِی بِأَسۡمَاۤءِ هَـٰۤؤُلَاۤءِ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:43'),
(5514, 2, 39, 32, 'قَالُوا۟ سُبۡحَـٰنَكَ لَا عِلۡمَ لَنَاۤ إِلَّا مَا عَلَّمۡتَنَاۤۖ إِنَّكَ أَنتَ ٱلۡعَلِیمُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:43'),
(5515, 2, 40, 33, 'قَالَ یَـٰۤـَٔادَمُ أَنۢبِئۡهُم بِأَسۡمَاۤىِٕهِمۡۖ فَلَمَّاۤ أَنۢبَأَهُم بِأَسۡمَاۤىِٕهِمۡ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّیۤ أَعۡلَمُ غَیۡبَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَأَعۡلَمُ مَا تُبۡدُونَ وَمَا كُنتُمۡ تَكۡتُمُونَ\n', NULL, 0, '2023-05-26 17:35:56', '2023-05-27 11:19:43'),
(5516, 2, 41, 34, 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰۤىِٕكَةِ ٱسۡجُدُوا۟ لِـَٔادَمَ فَسَجَدُوۤا۟ إِلَّاۤ إِبۡلِیسَ أَبَىٰ وَٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:35:57', '2023-05-27 11:19:43'),
(5517, 2, 42, 35, 'وَقُلۡنَا یَـٰۤـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ وَكُلَا مِنۡهَا رَغَدًا حَیۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَـٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:35:57', '2023-05-27 11:19:43'),
(5518, 2, 43, 36, 'فَأَزَلَّهُمَا ٱلشَّیۡطَـٰنُ عَنۡهَا فَأَخۡرَجَهُمَا مِمَّا كَانَا فِیهِۖ وَقُلۡنَا ٱهۡبِطُوا۟ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوࣱّۖ وَلَكُمۡ فِی ٱلۡأَرۡضِ مُسۡتَقَرࣱّ وَمَتَـٰعٌ إِلَىٰ حِینࣲ\n', NULL, 0, '2023-05-26 17:35:57', '2023-05-27 11:19:43'),
(5519, 2, 44, 37, 'فَتَلَقَّىٰۤ ءَادَمُ مِن رَّبِّهِۦ كَلِمَـٰتࣲ فَتَابَ عَلَیۡهِۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:35:57', '2023-05-27 11:19:43'),
(5520, 2, 45, 38, 'قُلۡنَا ٱهۡبِطُوا۟ مِنۡهَا جَمِیعࣰاۖ فَإِمَّا یَأۡتِیَنَّكُم مِّنِّی هُدࣰى فَمَن تَبِعَ هُدَایَ فَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:35:57', '2023-05-27 11:19:43'),
(5521, 2, 46, 39, 'وَٱلَّذِینَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔایَـٰتِنَاۤ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:35:57', '2023-05-27 11:19:43'),
(5522, 2, 47, 40, 'یَـٰبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱذۡكُرُوا۟ نِعۡمَتِیَ ٱلَّتِیۤ أَنۡعَمۡتُ عَلَیۡكُمۡ وَأَوۡفُوا۟ بِعَهۡدِیۤ أُوفِ بِعَهۡدِكُمۡ وَإِیَّـٰیَ فَٱرۡهَبُونِ\n', NULL, 0, '2023-05-26 17:35:57', '2023-05-27 11:19:43'),
(5523, 2, 48, 41, 'وَءَامِنُوا۟ بِمَاۤ أَنزَلۡتُ مُصَدِّقࣰا لِّمَا مَعَكُمۡ وَلَا تَكُونُوۤا۟ أَوَّلَ كَافِرِۭ بِهِۦۖ وَلَا تَشۡتَرُوا۟ بِـَٔایَـٰتِی ثَمَنࣰا قَلِیلࣰا وَإِیَّـٰیَ فَٱتَّقُونِ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:43'),
(5524, 2, 49, 42, 'وَلَا تَلۡبِسُوا۟ ٱلۡحَقَّ بِٱلۡبَـٰطِلِ وَتَكۡتُمُوا۟ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:43'),
(5525, 2, 50, 43, 'وَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَٱرۡكَعُوا۟ مَعَ ٱلرَّ ٰ⁠كِعِینَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:43'),
(5526, 2, 51, 44, '۞ أَتَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبِرِّ وَتَنسَوۡنَ أَنفُسَكُمۡ وَأَنتُمۡ تَتۡلُونَ ٱلۡكِتَـٰبَۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:43'),
(5527, 2, 52, 45, 'وَٱسۡتَعِینُوا۟ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ وَإِنَّهَا لَكَبِیرَةٌ إِلَّا عَلَى ٱلۡخَـٰشِعِینَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:43'),
(5528, 2, 53, 46, 'ٱلَّذِینَ یَظُنُّونَ أَنَّهُم مُّلَـٰقُوا۟ رَبِّهِمۡ وَأَنَّهُمۡ إِلَیۡهِ رَ ٰ⁠جِعُونَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:44'),
(5529, 2, 54, 47, 'یَـٰبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱذۡكُرُوا۟ نِعۡمَتِیَ ٱلَّتِیۤ أَنۡعَمۡتُ عَلَیۡكُمۡ وَأَنِّی فَضَّلۡتُكُمۡ عَلَى ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:44'),
(5530, 2, 55, 48, 'وَٱتَّقُوا۟ یَوۡمࣰا لَّا تَجۡزِی نَفۡسٌ عَن نَّفۡسࣲ شَیۡـࣰٔا وَلَا یُقۡبَلُ مِنۡهَا شَفَـٰعَةࣱ وَلَا یُؤۡخَذُ مِنۡهَا عَدۡلࣱ وَلَا هُمۡ یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:44'),
(5531, 2, 56, 49, 'وَإِذۡ نَجَّیۡنَـٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ یَسُومُونَكُمۡ سُوۤءَ ٱلۡعَذَابِ یُذَبِّحُونَ أَبۡنَاۤءَكُمۡ وَیَسۡتَحۡیُونَ نِسَاۤءَكُمۡۚ وَفِی ذَ ٰ⁠لِكُم بَلَاۤءࣱ مِّن رَّبِّكُمۡ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:44'),
(5532, 2, 57, 50, 'وَإِذۡ فَرَقۡنَا بِكُمُ ٱلۡبَحۡرَ فَأَنجَیۡنَـٰكُمۡ وَأَغۡرَقۡنَاۤ ءَالَ فِرۡعَوۡنَ وَأَنتُمۡ تَنظُرُونَ\n', NULL, 0, '2023-05-26 17:35:58', '2023-05-27 11:19:44'),
(5533, 2, 58, 51, 'وَإِذۡ وَ ٰ⁠عَدۡنَا مُوسَىٰۤ أَرۡبَعِینَ لَیۡلَةࣰ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5534, 2, 59, 52, 'ثُمَّ عَفَوۡنَا عَنكُم مِّنۢ بَعۡدِ ذَ ٰ⁠لِكَ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5535, 2, 60, 53, 'وَإِذۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ وَٱلۡفُرۡقَانَ لَعَلَّكُمۡ تَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5536, 2, 61, 54, 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ یَـٰقَوۡمِ إِنَّكُمۡ ظَلَمۡتُمۡ أَنفُسَكُم بِٱتِّخَاذِكُمُ ٱلۡعِجۡلَ فَتُوبُوۤا۟ إِلَىٰ بَارِىِٕكُمۡ فَٱقۡتُلُوۤا۟ أَنفُسَكُمۡ ذَ ٰ⁠لِكُمۡ خَیۡرࣱ لَّكُمۡ عِندَ بَارِىِٕكُمۡ فَتَابَ عَلَیۡكُمۡۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5537, 2, 62, 55, 'وَإِذۡ قُلۡتُمۡ یَـٰمُوسَىٰ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ نَرَى ٱللَّهَ جَهۡرَةࣰ فَأَخَذَتۡكُمُ ٱلصَّـٰعِقَةُ وَأَنتُمۡ تَنظُرُونَ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5538, 2, 63, 56, 'ثُمَّ بَعَثۡنَـٰكُم مِّنۢ بَعۡدِ مَوۡتِكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5539, 2, 64, 57, 'وَظَلَّلۡنَا عَلَیۡكُمُ ٱلۡغَمَامَ وَأَنزَلۡنَا عَلَیۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُوا۟ مِن طَیِّبَـٰتِ مَا رَزَقۡنَـٰكُمۡۚ وَمَا ظَلَمُونَا وَلَـٰكِن كَانُوۤا۟ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5540, 2, 65, 58, 'وَإِذۡ قُلۡنَا ٱدۡخُلُوا۟ هَـٰذِهِ ٱلۡقَرۡیَةَ فَكُلُوا۟ مِنۡهَا حَیۡثُ شِئۡتُمۡ رَغَدࣰا وَٱدۡخُلُوا۟ ٱلۡبَابَ سُجَّدࣰا وَقُولُوا۟ حِطَّةࣱ نَّغۡفِرۡ لَكُمۡ خَطَـٰیَـٰكُمۡۚ وَسَنَزِیدُ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:35:59', '2023-05-27 11:19:44'),
(5541, 2, 66, 59, 'فَبَدَّلَ ٱلَّذِینَ ظَلَمُوا۟ قَوۡلًا غَیۡرَ ٱلَّذِی قِیلَ لَهُمۡ فَأَنزَلۡنَا عَلَى ٱلَّذِینَ ظَلَمُوا۟ رِجۡزࣰا مِّنَ ٱلسَّمَاۤءِ بِمَا كَانُوا۟ یَفۡسُقُونَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:44'),
(5542, 2, 67, 60, '۞ وَإِذِ ٱسۡتَسۡقَىٰ مُوسَىٰ لِقَوۡمِهِۦ فَقُلۡنَا ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنفَجَرَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَیۡنࣰاۖ قَدۡ عَلِمَ كُلُّ أُنَاسࣲ مَّشۡرَبَهُمۡۖ كُلُوا۟ وَٱشۡرَبُوا۟ مِن رِّزۡقِ ٱللَّهِ وَلَا تَعۡثَوۡا۟ فِی ٱلۡأَرۡضِ مُفۡسِدِینَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5543, 2, 68, 61, 'وَإِذۡ قُلۡتُمۡ یَـٰمُوسَىٰ لَن نَّصۡبِرَ عَلَىٰ طَعَامࣲ وَ ٰ⁠حِدࣲ فَٱدۡعُ لَنَا رَبَّكَ یُخۡرِجۡ لَنَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ مِنۢ بَقۡلِهَا وَقِثَّاۤىِٕهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۖ قَالَ أَتَسۡتَبۡدِلُونَ ٱلَّذِی هُوَ أَدۡنَىٰ بِٱلَّذِی هُوَ خَیۡرٌۚ ٱهۡبِطُوا۟ مِصۡرࣰا فَإِنَّ لَكُم مَّا سَأَلۡتُمۡۗ وَضُرِبَتۡ عَلَیۡهِمُ ٱلذِّلَّةُ وَٱلۡمَسۡكَنَةُ وَبَاۤءُو بِغَضَبࣲ مِّنَ ٱللَّهِۗ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ كَانُوا۟ یَكۡفُرُونَ بِـَٔایَـٰتِ ٱللَّهِ وَیَقۡتُلُونَ ٱلنَّبِیِّـۧنَ بِغَیۡرِ ٱلۡحَقِّۗ ذَ ٰ⁠لِكَ بِمَا عَصَوا۟ وَّكَانُوا۟ یَعۡتَدُونَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5544, 2, 69, 62, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَٱلَّذِینَ هَادُوا۟ وَٱلنَّصَـٰرَىٰ وَٱلصَّـٰبِـِٔینَ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَعَمِلَ صَـٰلِحࣰا فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(5545, 2, 70, 63, 'وَإِذۡ أَخَذۡنَا مِیثَـٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُوا۟ مَاۤ ءَاتَیۡنَـٰكُم بِقُوَّةࣲ وَٱذۡكُرُوا۟ مَا فِیهِ لَعَلَّكُمۡ تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5546, 2, 71, 64, 'ثُمَّ تَوَلَّیۡتُم مِّنۢ بَعۡدِ ذَ ٰ⁠لِكَۖ فَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَیۡكُمۡ وَرَحۡمَتُهُۥ لَكُنتُم مِّنَ ٱلۡخَـٰسِرِینَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5547, 2, 72, 65, 'وَلَقَدۡ عَلِمۡتُمُ ٱلَّذِینَ ٱعۡتَدَوۡا۟ مِنكُمۡ فِی ٱلسَّبۡتِ فَقُلۡنَا لَهُمۡ كُونُوا۟ قِرَدَةً خَـٰسِـِٔینَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5548, 2, 73, 66, 'فَجَعَلۡنَـٰهَا نَكَـٰلࣰا لِّمَا بَیۡنَ یَدَیۡهَا وَمَا خَلۡفَهَا وَمَوۡعِظَةࣰ لِّلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5549, 2, 74, 67, 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦۤ إِنَّ ٱللَّهَ یَأۡمُرُكُمۡ أَن تَذۡبَحُوا۟ بَقَرَةࣰۖ قَالُوۤا۟ أَتَتَّخِذُنَا هُزُوࣰاۖ قَالَ أَعُوذُ بِٱللَّهِ أَنۡ أَكُونَ مِنَ ٱلۡجَـٰهِلِینَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5550, 2, 75, 68, 'قَالُوا۟ ٱدۡعُ لَنَا رَبَّكَ یُبَیِّن لَّنَا مَا هِیَۚ قَالَ إِنَّهُۥ یَقُولُ إِنَّهَا بَقَرَةࣱ لَّا فَارِضࣱ وَلَا بِكۡرٌ عَوَانُۢ بَیۡنَ ذَ ٰ⁠لِكَۖ فَٱفۡعَلُوا۟ مَا تُؤۡمَرُونَ\n', NULL, 0, '2023-05-26 17:36:00', '2023-05-27 11:19:45'),
(5551, 2, 76, 69, 'قَالُوا۟ ٱدۡعُ لَنَا رَبَّكَ یُبَیِّن لَّنَا مَا لَوۡنُهَاۚ قَالَ إِنَّهُۥ یَقُولُ إِنَّهَا بَقَرَةࣱ صَفۡرَاۤءُ فَاقِعࣱ لَّوۡنُهَا تَسُرُّ ٱلنَّـٰظِرِینَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:45'),
(5552, 2, 77, 70, 'قَالُوا۟ ٱدۡعُ لَنَا رَبَّكَ یُبَیِّن لَّنَا مَا هِیَ إِنَّ ٱلۡبَقَرَ تَشَـٰبَهَ عَلَیۡنَا وَإِنَّاۤ إِن شَاۤءَ ٱللَّهُ لَمُهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:45'),
(5553, 2, 78, 71, 'قَالَ إِنَّهُۥ یَقُولُ إِنَّهَا بَقَرَةࣱ لَّا ذَلُولࣱ تُثِیرُ ٱلۡأَرۡضَ وَلَا تَسۡقِی ٱلۡحَرۡثَ مُسَلَّمَةࣱ لَّا شِیَةَ فِیهَاۚ قَالُوا۟ ٱلۡـَٔـٰنَ جِئۡتَ بِٱلۡحَقِّۚ فَذَبَحُوهَا وَمَا كَادُوا۟ یَفۡعَلُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:45'),
(5554, 2, 79, 72, 'وَإِذۡ قَتَلۡتُمۡ نَفۡسࣰا فَٱدَّ ٰ⁠رَ ٰٔۡ ⁠تُمۡ فِیهَاۖ وَٱللَّهُ مُخۡرِجࣱ مَّا كُنتُمۡ تَكۡتُمُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:45'),
(5555, 2, 80, 73, 'فَقُلۡنَا ٱضۡرِبُوهُ بِبَعۡضِهَاۚ كَذَ ٰ⁠لِكَ یُحۡیِ ٱللَّهُ ٱلۡمَوۡتَىٰ وَیُرِیكُمۡ ءَایَـٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:45'),
(5556, 2, 81, 74, 'ثُمَّ قَسَتۡ قُلُوبُكُم مِّنۢ بَعۡدِ ذَ ٰ⁠لِكَ فَهِیَ كَٱلۡحِجَارَةِ أَوۡ أَشَدُّ قَسۡوَةࣰۚ وَإِنَّ مِنَ ٱلۡحِجَارَةِ لَمَا یَتَفَجَّرُ مِنۡهُ ٱلۡأَنۡهَـٰرُۚ وَإِنَّ مِنۡهَا لَمَا یَشَّقَّقُ فَیَخۡرُجُ مِنۡهُ ٱلۡمَاۤءُۚ وَإِنَّ مِنۡهَا لَمَا یَهۡبِطُ مِنۡ خَشۡیَةِ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَـٰفِلٍ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:45'),
(5557, 2, 82, 75, '۞ أَفَتَطۡمَعُونَ أَن یُؤۡمِنُوا۟ لَكُمۡ وَقَدۡ كَانَ فَرِیقࣱ مِّنۡهُمۡ یَسۡمَعُونَ كَلَـٰمَ ٱللَّهِ ثُمَّ یُحَرِّفُونَهُۥ مِنۢ بَعۡدِ مَا عَقَلُوهُ وَهُمۡ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:46'),
(5558, 2, 83, 76, 'وَإِذَا لَقُوا۟ ٱلَّذِینَ ءَامَنُوا۟ قَالُوۤا۟ ءَامَنَّا وَإِذَا خَلَا بَعۡضُهُمۡ إِلَىٰ بَعۡضࣲ قَالُوۤا۟ أَتُحَدِّثُونَهُم بِمَا فَتَحَ ٱللَّهُ عَلَیۡكُمۡ لِیُحَاۤجُّوكُم بِهِۦ عِندَ رَبِّكُمۡۚ أَفَلَا تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:46'),
(5559, 2, 84, 77, 'أَوَلَا یَعۡلَمُونَ أَنَّ ٱللَّهَ یَعۡلَمُ مَا یُسِرُّونَ وَمَا یُعۡلِنُونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:46'),
(5560, 2, 85, 78, 'وَمِنۡهُمۡ أُمِّیُّونَ لَا یَعۡلَمُونَ ٱلۡكِتَـٰبَ إِلَّاۤ أَمَانِیَّ وَإِنۡ هُمۡ إِلَّا یَظُنُّونَ\n', NULL, 0, '2023-05-26 17:36:01', '2023-05-27 11:19:46'),
(5561, 2, 86, 79, 'فَوَیۡلࣱ لِّلَّذِینَ یَكۡتُبُونَ ٱلۡكِتَـٰبَ بِأَیۡدِیهِمۡ ثُمَّ یَقُولُونَ هَـٰذَا مِنۡ عِندِ ٱللَّهِ لِیَشۡتَرُوا۟ بِهِۦ ثَمَنࣰا قَلِیلࣰاۖ فَوَیۡلࣱ لَّهُم مِّمَّا كَتَبَتۡ أَیۡدِیهِمۡ وَوَیۡلࣱ لَّهُم مِّمَّا یَكۡسِبُونَ\n', NULL, 0, '2023-05-26 17:36:02', '2023-05-27 11:19:46'),
(5562, 2, 87, 80, 'وَقَالُوا۟ لَن تَمَسَّنَا ٱلنَّارُ إِلَّاۤ أَیَّامࣰا مَّعۡدُودَةࣰۚ قُلۡ أَتَّخَذۡتُمۡ عِندَ ٱللَّهِ عَهۡدࣰا فَلَن یُخۡلِفَ ٱللَّهُ عَهۡدَهُۥۤۖ أَمۡ تَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:02', '2023-05-27 11:19:46'),
(5563, 2, 88, 81, 'بَلَىٰۚ مَن كَسَبَ سَیِّئَةࣰ وَأَحَـٰطَتۡ بِهِۦ خَطِیۤـَٔتُهُۥ فَأُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:36:02', '2023-05-27 11:19:46'),
(5564, 2, 89, 82, 'وَٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلۡجَنَّةِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:36:02', '2023-05-27 11:19:46'),
(5565, 2, 90, 83, 'وَإِذۡ أَخَذۡنَا مِیثَـٰقَ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ لَا تَعۡبُدُونَ إِلَّا ٱللَّهَ وَبِٱلۡوَ ٰ⁠لِدَیۡنِ إِحۡسَانࣰا وَذِی ٱلۡقُرۡبَىٰ وَٱلۡیَتَـٰمَىٰ وَٱلۡمَسَـٰكِینِ وَقُولُوا۟ لِلنَّاسِ حُسۡنࣰا وَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ ثُمَّ تَوَلَّیۡتُمۡ إِلَّا قَلِیلࣰا مِّنكُمۡ وَأَنتُم مُّعۡرِضُونَ\n', NULL, 0, '2023-05-26 17:36:02', '2023-05-27 11:19:46'),
(5566, 2, 91, 84, 'وَإِذۡ أَخَذۡنَا مِیثَـٰقَكُمۡ لَا تَسۡفِكُونَ دِمَاۤءَكُمۡ وَلَا تُخۡرِجُونَ أَنفُسَكُم مِّن دِیَـٰرِكُمۡ ثُمَّ أَقۡرَرۡتُمۡ وَأَنتُمۡ تَشۡهَدُونَ\n', NULL, 0, '2023-05-26 17:36:02', '2023-05-27 11:19:46'),
(5567, 2, 92, 85, 'ثُمَّ أَنتُمۡ هَـٰۤؤُلَاۤءِ تَقۡتُلُونَ أَنفُسَكُمۡ وَتُخۡرِجُونَ فَرِیقࣰا مِّنكُم مِّن دِیَـٰرِهِمۡ تَظَـٰهَرُونَ عَلَیۡهِم بِٱلۡإِثۡمِ وَٱلۡعُدۡوَ ٰ⁠نِ وَإِن یَأۡتُوكُمۡ أُسَـٰرَىٰ تُفَـٰدُوهُمۡ وَهُوَ مُحَرَّمٌ عَلَیۡكُمۡ إِخۡرَاجُهُمۡۚ أَفَتُؤۡمِنُونَ بِبَعۡضِ ٱلۡكِتَـٰبِ وَتَكۡفُرُونَ بِبَعۡضࣲۚ فَمَا جَزَاۤءُ مَن یَفۡعَلُ ذَ ٰ⁠لِكَ مِنكُمۡ إِلَّا خِزۡیࣱ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ وَیَوۡمَ ٱلۡقِیَـٰمَةِ یُرَدُّونَ إِلَىٰۤ أَشَدِّ ٱلۡعَذَابِۗ وَمَا ٱللَّهُ بِغَـٰفِلٍ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:02', '2023-05-27 11:19:46'),
(5568, 2, 93, 86, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ ٱشۡتَرَوُا۟ ٱلۡحَیَوٰةَ ٱلدُّنۡیَا بِٱلۡـَٔاخِرَةِۖ فَلَا یُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:46'),
(5569, 2, 94, 87, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ وَقَفَّیۡنَا مِنۢ بَعۡدِهِۦ بِٱلرُّسُلِۖ وَءَاتَیۡنَا عِیسَى ٱبۡنَ مَرۡیَمَ ٱلۡبَیِّنَـٰتِ وَأَیَّدۡنَـٰهُ بِرُوحِ ٱلۡقُدُسِۗ أَفَكُلَّمَا جَاۤءَكُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰۤ أَنفُسُكُمُ ٱسۡتَكۡبَرۡتُمۡ فَفَرِیقࣰا كَذَّبۡتُمۡ وَفَرِیقࣰا تَقۡتُلُونَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:46'),
(5570, 2, 95, 88, 'وَقَالُوا۟ قُلُوبُنَا غُلۡفُۢۚ بَل لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَقَلِیلࣰا مَّا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:46'),
(5571, 2, 96, 89, 'وَلَمَّا جَاۤءَهُمۡ كِتَـٰبࣱ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقࣱ لِّمَا مَعَهُمۡ وَكَانُوا۟ مِن قَبۡلُ یَسۡتَفۡتِحُونَ عَلَى ٱلَّذِینَ كَفَرُوا۟ فَلَمَّا جَاۤءَهُم مَّا عَرَفُوا۟ كَفَرُوا۟ بِهِۦۚ فَلَعۡنَةُ ٱللَّهِ عَلَى ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:46'),
(5572, 2, 97, 90, 'بِئۡسَمَا ٱشۡتَرَوۡا۟ بِهِۦۤ أَنفُسَهُمۡ أَن یَكۡفُرُوا۟ بِمَاۤ أَنزَلَ ٱللَّهُ بَغۡیًا أَن یُنَزِّلَ ٱللَّهُ مِن فَضۡلِهِۦ عَلَىٰ مَن یَشَاۤءُ مِنۡ عِبَادِهِۦۖ فَبَاۤءُو بِغَضَبٍ عَلَىٰ غَضَبࣲۚ وَلِلۡكَـٰفِرِینَ عَذَابࣱ مُّهِینࣱ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:46'),
(5573, 2, 98, 91, 'وَإِذَا قِیلَ لَهُمۡ ءَامِنُوا۟ بِمَاۤ أَنزَلَ ٱللَّهُ قَالُوا۟ نُؤۡمِنُ بِمَاۤ أُنزِلَ عَلَیۡنَا وَیَكۡفُرُونَ بِمَا وَرَاۤءَهُۥ وَهُوَ ٱلۡحَقُّ مُصَدِّقࣰا لِّمَا مَعَهُمۡۗ قُلۡ فَلِمَ تَقۡتُلُونَ أَنۢبِیَاۤءَ ٱللَّهِ مِن قَبۡلُ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:46'),
(5574, 2, 99, 92, '۞ وَلَقَدۡ جَاۤءَكُم مُّوسَىٰ بِٱلۡبَیِّنَـٰتِ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:47'),
(5575, 2, 100, 93, 'وَإِذۡ أَخَذۡنَا مِیثَـٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُوا۟ مَاۤ ءَاتَیۡنَـٰكُم بِقُوَّةࣲ وَٱسۡمَعُوا۟ۖ قَالُوا۟ سَمِعۡنَا وَعَصَیۡنَا وَأُشۡرِبُوا۟ فِی قُلُوبِهِمُ ٱلۡعِجۡلَ بِكُفۡرِهِمۡۚ قُلۡ بِئۡسَمَا یَأۡمُرُكُم بِهِۦۤ إِیمَـٰنُكُمۡ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:47'),
(5576, 2, 101, 94, 'قُلۡ إِن كَانَتۡ لَكُمُ ٱلدَّارُ ٱلۡـَٔاخِرَةُ عِندَ ٱللَّهِ خَالِصَةࣰ مِّن دُونِ ٱلنَّاسِ فَتَمَنَّوُا۟ ٱلۡمَوۡتَ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:47'),
(5577, 2, 102, 95, 'وَلَن یَتَمَنَّوۡهُ أَبَدَۢا بِمَا قَدَّمَتۡ أَیۡدِیهِمۡۚ وَٱللَّهُ عَلِیمُۢ بِٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:36:03', '2023-05-27 11:19:47'),
(5578, 2, 103, 96, 'وَلَتَجِدَنَّهُمۡ أَحۡرَصَ ٱلنَّاسِ عَلَىٰ حَیَوٰةࣲ وَمِنَ ٱلَّذِینَ أَشۡرَكُوا۟ۚ یَوَدُّ أَحَدُهُمۡ لَوۡ یُعَمَّرُ أَلۡفَ سَنَةࣲ وَمَا هُوَ بِمُزَحۡزِحِهِۦ مِنَ ٱلۡعَذَابِ أَن یُعَمَّرَۗ وَٱللَّهُ بَصِیرُۢ بِمَا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5579, 2, 104, 97, 'قُلۡ مَن كَانَ عَدُوࣰّا لِّجِبۡرِیلَ فَإِنَّهُۥ نَزَّلَهُۥ عَلَىٰ قَلۡبِكَ بِإِذۡنِ ٱللَّهِ مُصَدِّقࣰا لِّمَا بَیۡنَ یَدَیۡهِ وَهُدࣰى وَبُشۡرَىٰ لِلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5580, 2, 105, 98, 'مَن كَانَ عَدُوࣰّا لِّلَّهِ وَمَلَـٰۤىِٕكَتِهِۦ وَرُسُلِهِۦ وَجِبۡرِیلَ وَمِیكَىٰلَ فَإِنَّ ٱللَّهَ عَدُوࣱّ لِّلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5581, 2, 106, 99, 'وَلَقَدۡ أَنزَلۡنَاۤ إِلَیۡكَ ءَایَـٰتِۭ بَیِّنَـٰتࣲۖ وَمَا یَكۡفُرُ بِهَاۤ إِلَّا ٱلۡفَـٰسِقُونَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5582, 2, 107, 100, 'أَوَكُلَّمَا عَـٰهَدُوا۟ عَهۡدࣰا نَّبَذَهُۥ فَرِیقࣱ مِّنۡهُمۚ بَلۡ أَكۡثَرُهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5583, 2, 108, 101, 'وَلَمَّا جَاۤءَهُمۡ رَسُولࣱ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقࣱ لِّمَا مَعَهُمۡ نَبَذَ فَرِیقࣱ مِّنَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ كِتَـٰبَ ٱللَّهِ وَرَاۤءَ ظُهُورِهِمۡ كَأَنَّهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5584, 2, 109, 102, 'وَٱتَّبَعُوا۟ مَا تَتۡلُوا۟ ٱلشَّیَـٰطِینُ عَلَىٰ مُلۡكِ سُلَیۡمَـٰنَۖ وَمَا كَفَرَ سُلَیۡمَـٰنُ وَلَـٰكِنَّ ٱلشَّیَـٰطِینَ كَفَرُوا۟ یُعَلِّمُونَ ٱلنَّاسَ ٱلسِّحۡرَ وَمَاۤ أُنزِلَ عَلَى ٱلۡمَلَكَیۡنِ بِبَابِلَ هَـٰرُوتَ وَمَـٰرُوتَۚ وَمَا یُعَلِّمَانِ مِنۡ أَحَدٍ حَتَّىٰ یَقُولَاۤ إِنَّمَا نَحۡنُ فِتۡنَةࣱ فَلَا تَكۡفُرۡۖ فَیَتَعَلَّمُونَ مِنۡهُمَا مَا یُفَرِّقُونَ بِهِۦ بَیۡنَ ٱلۡمَرۡءِ وَزَوۡجِهِۦۚ وَمَا هُم بِضَاۤرِّینَ بِهِۦ مِنۡ أَحَدٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَیَتَعَلَّمُونَ مَا یَضُرُّهُمۡ وَلَا یَنفَعُهُمۡۚ وَلَقَدۡ عَلِمُوا۟ لَمَنِ ٱشۡتَرَىٰهُ مَا لَهُۥ فِی ٱلۡـَٔاخِرَةِ مِنۡ خَلَـٰقࣲۚ وَلَبِئۡسَ مَا شَرَوۡا۟ بِهِۦۤ أَنفُسَهُمۡۚ لَوۡ كَانُوا۟ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5585, 2, 110, 103, 'وَلَوۡ أَنَّهُمۡ ءَامَنُوا۟ وَٱتَّقَوۡا۟ لَمَثُوبَةࣱ مِّنۡ عِندِ ٱللَّهِ خَیۡرࣱۚ لَّوۡ كَانُوا۟ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5586, 2, 111, 104, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تَقُولُوا۟ رَ ٰ⁠عِنَا وَقُولُوا۟ ٱنظُرۡنَا وَٱسۡمَعُوا۟ۗ وَلِلۡكَـٰفِرِینَ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5587, 2, 112, 105, 'مَّا یَوَدُّ ٱلَّذِینَ كَفَرُوا۟ مِنۡ أَهۡلِ ٱلۡكِتَـٰبِ وَلَا ٱلۡمُشۡرِكِینَ أَن یُنَزَّلَ عَلَیۡكُم مِّنۡ خَیۡرࣲ مِّن رَّبِّكُمۡۚ وَٱللَّهُ یَخۡتَصُّ بِرَحۡمَتِهِۦ مَن یَشَاۤءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِیمِ\n', NULL, 0, '2023-05-26 17:36:04', '2023-05-27 11:19:47'),
(5588, 2, 113, 106, '۞ مَا نَنسَخۡ مِنۡ ءَایَةٍ أَوۡ نُنسِهَا نَأۡتِ بِخَیۡرࣲ مِّنۡهَاۤ أَوۡ مِثۡلِهَاۤۗ أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:47'),
(5589, 2, 114, 107, 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۗ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِیࣲّ وَلَا نَصِیرٍ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:47'),
(5590, 2, 115, 108, 'أَمۡ تُرِیدُونَ أَن تَسۡـَٔلُوا۟ رَسُولَكُمۡ كَمَا سُىِٕلَ مُوسَىٰ مِن قَبۡلُۗ وَمَن یَتَبَدَّلِ ٱلۡكُفۡرَ بِٱلۡإِیمَـٰنِ فَقَدۡ ضَلَّ سَوَاۤءَ ٱلسَّبِیلِ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:48'),
(5591, 2, 116, 109, 'وَدَّ كَثِیرࣱ مِّنۡ أَهۡلِ ٱلۡكِتَـٰبِ لَوۡ یَرُدُّونَكُم مِّنۢ بَعۡدِ إِیمَـٰنِكُمۡ كُفَّارًا حَسَدࣰا مِّنۡ عِندِ أَنفُسِهِم مِّنۢ بَعۡدِ مَا تَبَیَّنَ لَهُمُ ٱلۡحَقُّۖ فَٱعۡفُوا۟ وَٱصۡفَحُوا۟ حَتَّىٰ یَأۡتِیَ ٱللَّهُ بِأَمۡرِهِۦۤۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:48'),
(5592, 2, 117, 110, 'وَأَقِیمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَۚ وَمَا تُقَدِّمُوا۟ لِأَنفُسِكُم مِّنۡ خَیۡرࣲ تَجِدُوهُ عِندَ ٱللَّهِۗ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:48'),
(5593, 2, 118, 111, 'وَقَالُوا۟ لَن یَدۡخُلَ ٱلۡجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوۡ نَصَـٰرَىٰۗ تِلۡكَ أَمَانِیُّهُمۡۗ قُلۡ هَاتُوا۟ بُرۡهَـٰنَكُمۡ إِن كُنتُمۡ صَـٰدِقِینَ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:48'),
(5594, 2, 119, 112, 'بَلَىٰۚ مَنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنࣱ فَلَهُۥۤ أَجۡرُهُۥ عِندَ رَبِّهِۦ وَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:48'),
(5595, 2, 120, 113, 'وَقَالَتِ ٱلۡیَهُودُ لَیۡسَتِ ٱلنَّصَـٰرَىٰ عَلَىٰ شَیۡءࣲ وَقَالَتِ ٱلنَّصَـٰرَىٰ لَیۡسَتِ ٱلۡیَهُودُ عَلَىٰ شَیۡءࣲ وَهُمۡ یَتۡلُونَ ٱلۡكِتَـٰبَۗ كَذَ ٰ⁠لِكَ قَالَ ٱلَّذِینَ لَا یَعۡلَمُونَ مِثۡلَ قَوۡلِهِمۡۚ فَٱللَّهُ یَحۡكُمُ بَیۡنَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ فِیمَا كَانُوا۟ فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:48'),
(5596, 2, 121, 114, 'وَمَنۡ أَظۡلَمُ مِمَّن مَّنَعَ مَسَـٰجِدَ ٱللَّهِ أَن یُذۡكَرَ فِیهَا ٱسۡمُهُۥ وَسَعَىٰ فِی خَرَابِهَاۤۚ أُو۟لَـٰۤىِٕكَ مَا كَانَ لَهُمۡ أَن یَدۡخُلُوهَاۤ إِلَّا خَاۤىِٕفِینَۚ لَهُمۡ فِی ٱلدُّنۡیَا خِزۡیࣱ وَلَهُمۡ فِی ٱلۡـَٔاخِرَةِ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-26 17:36:05', '2023-05-27 11:19:48'),
(5597, 2, 122, 115, 'وَلِلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ فَأَیۡنَمَا تُوَلُّوا۟ فَثَمَّ وَجۡهُ ٱللَّهِۚ إِنَّ ٱللَّهَ وَ ٰ⁠سِعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:06', '2023-05-27 11:19:48'),
(5598, 2, 123, 116, 'وَقَالُوا۟ ٱتَّخَذَ ٱللَّهُ وَلَدࣰاۗ سُبۡحَـٰنَهُۥۖ بَل لَّهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ كُلࣱّ لَّهُۥ قَـٰنِتُونَ\n', NULL, 0, '2023-05-26 17:36:06', '2023-05-27 11:19:48'),
(5599, 2, 124, 117, 'بَدِیعُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ وَإِذَا قَضَىٰۤ أَمۡرࣰا فَإِنَّمَا یَقُولُ لَهُۥ كُن فَیَكُونُ\n', NULL, 0, '2023-05-26 17:36:06', '2023-05-27 11:19:48'),
(5600, 2, 125, 118, 'وَقَالَ ٱلَّذِینَ لَا یَعۡلَمُونَ لَوۡلَا یُكَلِّمُنَا ٱللَّهُ أَوۡ تَأۡتِینَاۤ ءَایَةࣱۗ كَذَ ٰ⁠لِكَ قَالَ ٱلَّذِینَ مِن قَبۡلِهِم مِّثۡلَ قَوۡلِهِمۡۘ تَشَـٰبَهَتۡ قُلُوبُهُمۡۗ قَدۡ بَیَّنَّا ٱلۡـَٔایَـٰتِ لِقَوۡمࣲ یُوقِنُونَ\n', NULL, 0, '2023-05-26 17:36:06', '2023-05-27 11:19:48'),
(5601, 2, 126, 119, 'إِنَّاۤ أَرۡسَلۡنَـٰكَ بِٱلۡحَقِّ بَشِیرࣰا وَنَذِیرࣰاۖ وَلَا تُسۡـَٔلُ عَنۡ أَصۡحَـٰبِ ٱلۡجَحِیمِ\n', NULL, 0, '2023-05-26 17:36:06', '2023-05-27 11:19:48'),
(5602, 2, 127, 120, 'وَلَن تَرۡضَىٰ عَنكَ ٱلۡیَهُودُ وَلَا ٱلنَّصَـٰرَىٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمۡۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۗ وَلَىِٕنِ ٱتَّبَعۡتَ أَهۡوَاۤءَهُم بَعۡدَ ٱلَّذِی جَاۤءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِیࣲّ وَلَا نَصِیرٍ\n', NULL, 0, '2023-05-26 17:36:06', '2023-05-27 11:19:48'),
(5603, 2, 128, 121, 'ٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ یَتۡلُونَهُۥ حَقَّ تِلَاوَتِهِۦۤ أُو۟لَـٰۤىِٕكَ یُؤۡمِنُونَ بِهِۦۗ وَمَن یَكۡفُرۡ بِهِۦ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-26 17:36:06', '2023-05-27 11:19:48'),
(5604, 2, 129, 122, 'یَـٰبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱذۡكُرُوا۟ نِعۡمَتِیَ ٱلَّتِیۤ أَنۡعَمۡتُ عَلَیۡكُمۡ وَأَنِّی فَضَّلۡتُكُمۡ عَلَى ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:36:07', '2023-05-27 11:19:48'),
(5605, 2, 130, 123, 'وَٱتَّقُوا۟ یَوۡمࣰا لَّا تَجۡزِی نَفۡسٌ عَن نَّفۡسࣲ شَیۡـࣰٔا وَلَا یُقۡبَلُ مِنۡهَا عَدۡلࣱ وَلَا تَنفَعُهَا شَفَـٰعَةࣱ وَلَا هُمۡ یُنصَرُونَ\n', NULL, 0, '2023-05-26 17:36:07', '2023-05-27 11:19:48'),
(5606, 2, 131, 124, '۞ وَإِذِ ٱبۡتَلَىٰۤ إِبۡرَ ٰ⁠هِـۧمَ رَبُّهُۥ بِكَلِمَـٰتࣲ فَأَتَمَّهُنَّۖ قَالَ إِنِّی جَاعِلُكَ لِلنَّاسِ إِمَامࣰاۖ قَالَ وَمِن ذُرِّیَّتِیۖ قَالَ لَا یَنَالُ عَهۡدِی ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:36:07', '2023-05-27 11:19:48'),
(5607, 2, 132, 125, 'وَإِذۡ جَعَلۡنَا ٱلۡبَیۡتَ مَثَابَةࣰ لِّلنَّاسِ وَأَمۡنࣰا وَٱتَّخِذُوا۟ مِن مَّقَامِ إِبۡرَ ٰ⁠هِـۧمَ مُصَلࣰّىۖ وَعَهِدۡنَاۤ إِلَىٰۤ إِبۡرَ ٰ⁠هِـۧمَ وَإِسۡمَـٰعِیلَ أَن طَهِّرَا بَیۡتِیَ لِلطَّاۤىِٕفِینَ وَٱلۡعَـٰكِفِینَ وَٱلرُّكَّعِ ٱلسُّجُودِ\n', NULL, 0, '2023-05-26 17:36:07', '2023-05-27 11:19:48'),
(5608, 2, 133, 126, 'وَإِذۡ قَالَ إِبۡرَ ٰ⁠هِـۧمُ رَبِّ ٱجۡعَلۡ هَـٰذَا بَلَدًا ءَامِنࣰا وَٱرۡزُقۡ أَهۡلَهُۥ مِنَ ٱلثَّمَرَ ٰ⁠تِ مَنۡ ءَامَنَ مِنۡهُم بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِۚ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُۥ قَلِیلࣰا ثُمَّ أَضۡطَرُّهُۥۤ إِلَىٰ عَذَابِ ٱلنَّارِۖ وَبِئۡسَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:36:07', '2023-05-27 11:19:49'),
(5609, 2, 134, 127, 'وَإِذۡ یَرۡفَعُ إِبۡرَ ٰ⁠هِـۧمُ ٱلۡقَوَاعِدَ مِنَ ٱلۡبَیۡتِ وَإِسۡمَـٰعِیلُ رَبَّنَا تَقَبَّلۡ مِنَّاۤۖ إِنَّكَ أَنتَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:36:07', '2023-05-27 11:19:49'),
(5610, 2, 135, 128, 'رَبَّنَا وَٱجۡعَلۡنَا مُسۡلِمَیۡنِ لَكَ وَمِن ذُرِّیَّتِنَاۤ أُمَّةࣰ مُّسۡلِمَةࣰ لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبۡ عَلَیۡنَاۤۖ إِنَّكَ أَنتَ ٱلتَّوَّابُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:36:08', '2023-05-27 11:19:49'),
(5611, 2, 136, 129, 'رَبَّنَا وَٱبۡعَثۡ فِیهِمۡ رَسُولࣰا مِّنۡهُمۡ یَتۡلُوا۟ عَلَیۡهِمۡ ءَایَـٰتِكَ وَیُعَلِّمُهُمُ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَیُزَكِّیهِمۡۖ إِنَّكَ أَنتَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-26 17:36:08', '2023-05-27 11:19:49'),
(5612, 2, 137, 130, 'وَمَن یَرۡغَبُ عَن مِّلَّةِ إِبۡرَ ٰ⁠هِـۧمَ إِلَّا مَن سَفِهَ نَفۡسَهُۥۚ وَلَقَدِ ٱصۡطَفَیۡنَـٰهُ فِی ٱلدُّنۡیَاۖ وَإِنَّهُۥ فِی ٱلۡـَٔاخِرَةِ لَمِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-26 17:36:08', '2023-05-27 11:19:49'),
(5613, 2, 138, 131, 'إِذۡ قَالَ لَهُۥ رَبُّهُۥۤ أَسۡلِمۡۖ قَالَ أَسۡلَمۡتُ لِرَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:36:08', '2023-05-27 11:19:49'),
(5614, 2, 139, 132, 'وَوَصَّىٰ بِهَاۤ إِبۡرَ ٰ⁠هِـۧمُ بَنِیهِ وَیَعۡقُوبُ یَـٰبَنِیَّ إِنَّ ٱللَّهَ ٱصۡطَفَىٰ لَكُمُ ٱلدِّینَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:36:08', '2023-05-27 11:19:49'),
(5615, 2, 140, 133, 'أَمۡ كُنتُمۡ شُهَدَاۤءَ إِذۡ حَضَرَ یَعۡقُوبَ ٱلۡمَوۡتُ إِذۡ قَالَ لِبَنِیهِ مَا تَعۡبُدُونَ مِنۢ بَعۡدِیۖ قَالُوا۟ نَعۡبُدُ إِلَـٰهَكَ وَإِلَـٰهَ ءَابَاۤىِٕكَ إِبۡرَ ٰ⁠هِـۧمَ وَإِسۡمَـٰعِیلَ وَإِسۡحَـٰقَ إِلَـٰهࣰا وَ ٰ⁠حِدࣰا وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:36:08', '2023-05-27 11:19:49'),
(5616, 2, 141, 134, 'تِلۡكَ أُمَّةࣱ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:09', '2023-05-27 11:19:49'),
(5617, 2, 142, 135, 'وَقَالُوا۟ كُونُوا۟ هُودًا أَوۡ نَصَـٰرَىٰ تَهۡتَدُوا۟ۗ قُلۡ بَلۡ مِلَّةَ إِبۡرَ ٰ⁠هِـۧمَ حَنِیفࣰاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-26 17:36:09', '2023-05-27 11:19:49'),
(5618, 2, 143, 136, 'قُولُوۤا۟ ءَامَنَّا بِٱللَّهِ وَمَاۤ أُنزِلَ إِلَیۡنَا وَمَاۤ أُنزِلَ إِلَىٰۤ إِبۡرَ ٰ⁠هِـۧمَ وَإِسۡمَـٰعِیلَ وَإِسۡحَـٰقَ وَیَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَاۤ أُوتِیَ مُوسَىٰ وَعِیسَىٰ وَمَاۤ أُوتِیَ ٱلنَّبِیُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَیۡنَ أَحَدࣲ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\n', NULL, 0, '2023-05-26 17:36:09', '2023-05-27 11:19:49'),
(5619, 2, 144, 137, 'فَإِنۡ ءَامَنُوا۟ بِمِثۡلِ مَاۤ ءَامَنتُم بِهِۦ فَقَدِ ٱهۡتَدَوا۟ۖ وَّإِن تَوَلَّوۡا۟ فَإِنَّمَا هُمۡ فِی شِقَاقࣲۖ فَسَیَكۡفِیكَهُمُ ٱللَّهُۚ وَهُوَ ٱلسَّمِیعُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-26 17:36:09', '2023-05-27 11:19:49'),
(5620, 2, 145, 138, 'صِبۡغَةَ ٱللَّهِ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ صِبۡغَةࣰۖ وَنَحۡنُ لَهُۥ عَـٰبِدُونَ\n', NULL, 0, '2023-05-26 17:36:09', '2023-05-27 11:19:49'),
(5621, 2, 146, 139, 'قُلۡ أَتُحَاۤجُّونَنَا فِی ٱللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمۡ وَلَنَاۤ أَعۡمَـٰلُنَا وَلَكُمۡ أَعۡمَـٰلُكُمۡ وَنَحۡنُ لَهُۥ مُخۡلِصُونَ\n', NULL, 0, '2023-05-26 17:36:09', '2023-05-27 11:19:49'),
(5622, 2, 147, 140, 'أَمۡ تَقُولُونَ إِنَّ إِبۡرَ ٰ⁠هِـۧمَ وَإِسۡمَـٰعِیلَ وَإِسۡحَـٰقَ وَیَعۡقُوبَ وَٱلۡأَسۡبَاطَ كَانُوا۟ هُودًا أَوۡ نَصَـٰرَىٰۗ قُلۡ ءَأَنتُمۡ أَعۡلَمُ أَمِ ٱللَّهُۗ وَمَنۡ أَظۡلَمُ مِمَّن كَتَمَ شَهَـٰدَةً عِندَهُۥ مِنَ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَـٰفِلٍ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:49'),
(5623, 2, 148, 141, 'تِلۡكَ أُمَّةࣱ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:49'),
(5624, 2, 149, 142, '۞ سَیَقُولُ ٱلسُّفَهَاۤءُ مِنَ ٱلنَّاسِ مَا وَلَّىٰهُمۡ عَن قِبۡلَتِهِمُ ٱلَّتِی كَانُوا۟ عَلَیۡهَاۚ قُل لِّلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ یَهۡدِی مَن یَشَاۤءُ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:50'),
(5625, 2, 150, 143, 'وَكَذَ ٰ⁠لِكَ جَعَلۡنَـٰكُمۡ أُمَّةࣰ وَسَطࣰا لِّتَكُونُوا۟ شُهَدَاۤءَ عَلَى ٱلنَّاسِ وَیَكُونَ ٱلرَّسُولُ عَلَیۡكُمۡ شَهِیدࣰاۗ وَمَا جَعَلۡنَا ٱلۡقِبۡلَةَ ٱلَّتِی كُنتَ عَلَیۡهَاۤ إِلَّا لِنَعۡلَمَ مَن یَتَّبِعُ ٱلرَّسُولَ مِمَّن یَنقَلِبُ عَلَىٰ عَقِبَیۡهِۚ وَإِن كَانَتۡ لَكَبِیرَةً إِلَّا عَلَى ٱلَّذِینَ هَدَى ٱللَّهُۗ وَمَا كَانَ ٱللَّهُ لِیُضِیعَ إِیمَـٰنَكُمۡۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:50'),
(5626, 2, 151, 144, 'قَدۡ نَرَىٰ تَقَلُّبَ وَجۡهِكَ فِی ٱلسَّمَاۤءِۖ فَلَنُوَلِّیَنَّكَ قِبۡلَةࣰ تَرۡضَىٰهَاۚ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَیۡثُ مَا كُنتُمۡ فَوَلُّوا۟ وُجُوهَكُمۡ شَطۡرَهُۥۗ وَإِنَّ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ لَیَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۗ وَمَا ٱللَّهُ بِغَـٰفِلٍ عَمَّا یَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:50'),
(5627, 2, 152, 145, 'وَلَىِٕنۡ أَتَیۡتَ ٱلَّذِینَ أُوتُوا۟ ٱلۡكِتَـٰبَ بِكُلِّ ءَایَةࣲ مَّا تَبِعُوا۟ قِبۡلَتَكَۚ وَمَاۤ أَنتَ بِتَابِعࣲ قِبۡلَتَهُمۡۚ وَمَا بَعۡضُهُم بِتَابِعࣲ قِبۡلَةَ بَعۡضࣲۚ وَلَىِٕنِ ٱتَّبَعۡتَ أَهۡوَاۤءَهُم مِّنۢ بَعۡدِ مَا جَاۤءَكَ مِنَ ٱلۡعِلۡمِ إِنَّكَ إِذࣰا لَّمِنَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:50'),
(5628, 2, 153, 146, 'ٱلَّذِینَ ءَاتَیۡنَـٰهُمُ ٱلۡكِتَـٰبَ یَعۡرِفُونَهُۥ كَمَا یَعۡرِفُونَ أَبۡنَاۤءَهُمۡۖ وَإِنَّ فَرِیقࣰا مِّنۡهُمۡ لَیَكۡتُمُونَ ٱلۡحَقَّ وَهُمۡ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:50'),
(5629, 2, 154, 147, 'ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِینَ\n', NULL, 0, '2023-05-26 17:36:10', '2023-05-27 11:19:50'),
(5630, 2, 155, 148, 'وَلِكُلࣲّ وِجۡهَةٌ هُوَ مُوَلِّیهَاۖ فَٱسۡتَبِقُوا۟ ٱلۡخَیۡرَ ٰ⁠تِۚ أَیۡنَ مَا تَكُونُوا۟ یَأۡتِ بِكُمُ ٱللَّهُ جَمِیعًاۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:36:11', '2023-05-27 11:19:50'),
(5631, 2, 156, 149, 'وَمِنۡ حَیۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ وَإِنَّهُۥ لَلۡحَقُّ مِن رَّبِّكَۗ وَمَا ٱللَّهُ بِغَـٰفِلٍ عَمَّا تَعۡمَلُونَ\n', NULL, 0, '2023-05-26 17:36:11', '2023-05-27 11:19:50'),
(5632, 2, 157, 150, 'وَمِنۡ حَیۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَیۡثُ مَا كُنتُمۡ فَوَلُّوا۟ وُجُوهَكُمۡ شَطۡرَهُۥ لِئَلَّا یَكُونَ لِلنَّاسِ عَلَیۡكُمۡ حُجَّةٌ إِلَّا ٱلَّذِینَ ظَلَمُوا۟ مِنۡهُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِی وَلِأُتِمَّ نِعۡمَتِی عَلَیۡكُمۡ وَلَعَلَّكُمۡ تَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:36:11', '2023-05-27 11:19:50'),
(5633, 2, 158, 151, 'كَمَاۤ أَرۡسَلۡنَا فِیكُمۡ رَسُولࣰا مِّنكُمۡ یَتۡلُوا۟ عَلَیۡكُمۡ ءَایَـٰتِنَا وَیُزَكِّیكُمۡ وَیُعَلِّمُكُمُ ٱلۡكِتَـٰبَ وَٱلۡحِكۡمَةَ وَیُعَلِّمُكُم مَّا لَمۡ تَكُونُوا۟ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:11', '2023-05-27 11:19:50'),
(5634, 2, 159, 152, 'فَٱذۡكُرُونِیۤ أَذۡكُرۡكُمۡ وَٱشۡكُرُوا۟ لِی وَلَا تَكۡفُرُونِ\n', NULL, 0, '2023-05-26 17:36:11', '2023-05-27 11:19:50'),
(5635, 2, 160, 153, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱسۡتَعِینُوا۟ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:36:11', '2023-05-27 11:19:50'),
(5636, 2, 161, 154, 'وَلَا تَقُولُوا۟ لِمَن یُقۡتَلُ فِی سَبِیلِ ٱللَّهِ أَمۡوَ ٰ⁠تُۢۚ بَلۡ أَحۡیَاۤءࣱ وَلَـٰكِن لَّا تَشۡعُرُونَ\n', NULL, 0, '2023-05-26 17:36:11', '2023-05-27 11:19:50'),
(5637, 2, 162, 155, 'وَلَنَبۡلُوَنَّكُم بِشَیۡءࣲ مِّنَ ٱلۡخَوۡفِ وَٱلۡجُوعِ وَنَقۡصࣲ مِّنَ ٱلۡأَمۡوَ ٰ⁠لِ وَٱلۡأَنفُسِ وَٱلثَّمَرَ ٰ⁠تِۗ وَبَشِّرِ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:50'),
(5638, 2, 163, 156, 'ٱلَّذِینَ إِذَاۤ أَصَـٰبَتۡهُم مُّصِیبَةࣱ قَالُوۤا۟ إِنَّا لِلَّهِ وَإِنَّاۤ إِلَیۡهِ رَ ٰ⁠جِعُونَ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:50'),
(5639, 2, 164, 157, 'أُو۟لَـٰۤىِٕكَ عَلَیۡهِمۡ صَلَوَ ٰ⁠تࣱ مِّن رَّبِّهِمۡ وَرَحۡمَةࣱۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5640, 2, 165, 158, '۞ إِنَّ ٱلصَّفَا وَٱلۡمَرۡوَةَ مِن شَعَاۤىِٕرِ ٱللَّهِۖ فَمَنۡ حَجَّ ٱلۡبَیۡتَ أَوِ ٱعۡتَمَرَ فَلَا جُنَاحَ عَلَیۡهِ أَن یَطَّوَّفَ بِهِمَاۚ وَمَن تَطَوَّعَ خَیۡرࣰا فَإِنَّ ٱللَّهَ شَاكِرٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5641, 2, 166, 159, 'إِنَّ ٱلَّذِینَ یَكۡتُمُونَ مَاۤ أَنزَلۡنَا مِنَ ٱلۡبَیِّنَـٰتِ وَٱلۡهُدَىٰ مِنۢ بَعۡدِ مَا بَیَّنَّـٰهُ لِلنَّاسِ فِی ٱلۡكِتَـٰبِ أُو۟لَـٰۤىِٕكَ یَلۡعَنُهُمُ ٱللَّهُ وَیَلۡعَنُهُمُ ٱللَّـٰعِنُونَ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5642, 2, 167, 160, 'إِلَّا ٱلَّذِینَ تَابُوا۟ وَأَصۡلَحُوا۟ وَبَیَّنُوا۟ فَأُو۟لَـٰۤىِٕكَ أَتُوبُ عَلَیۡهِمۡ وَأَنَا ٱلتَّوَّابُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5643, 2, 168, 161, 'إِنَّ ٱلَّذِینَ كَفَرُوا۟ وَمَاتُوا۟ وَهُمۡ كُفَّارٌ أُو۟لَـٰۤىِٕكَ عَلَیۡهِمۡ لَعۡنَةُ ٱللَّهِ وَٱلۡمَلَـٰۤىِٕكَةِ وَٱلنَّاسِ أَجۡمَعِینَ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5644, 2, 169, 162, 'خَـٰلِدِینَ فِیهَا لَا یُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ یُنظَرُونَ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5645, 2, 170, 163, 'وَإِلَـٰهُكُمۡ إِلَـٰهࣱ وَ ٰ⁠حِدࣱۖ لَّاۤ إِلَـٰهَ إِلَّا هُوَ ٱلرَّحۡمَـٰنُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5646, 2, 171, 164, 'إِنَّ فِی خَلۡقِ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَٱخۡتِلَـٰفِ ٱلَّیۡلِ وَٱلنَّهَارِ وَٱلۡفُلۡكِ ٱلَّتِی تَجۡرِی فِی ٱلۡبَحۡرِ بِمَا یَنفَعُ ٱلنَّاسَ وَمَاۤ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَاۤءِ مِن مَّاۤءࣲ فَأَحۡیَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَبَثَّ فِیهَا مِن كُلِّ دَاۤبَّةࣲ وَتَصۡرِیفِ ٱلرِّیَـٰحِ وَٱلسَّحَابِ ٱلۡمُسَخَّرِ بَیۡنَ ٱلسَّمَاۤءِ وَٱلۡأَرۡضِ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5647, 2, 172, 165, 'وَمِنَ ٱلنَّاسِ مَن یَتَّخِذُ مِن دُونِ ٱللَّهِ أَندَادࣰا یُحِبُّونَهُمۡ كَحُبِّ ٱللَّهِۖ وَٱلَّذِینَ ءَامَنُوۤا۟ أَشَدُّ حُبࣰّا لِّلَّهِۗ وَلَوۡ یَرَى ٱلَّذِینَ ظَلَمُوۤا۟ إِذۡ یَرَوۡنَ ٱلۡعَذَابَ أَنَّ ٱلۡقُوَّةَ لِلَّهِ جَمِیعࣰا وَأَنَّ ٱللَّهَ شَدِیدُ ٱلۡعَذَابِ\n', NULL, 0, '2023-05-26 17:36:12', '2023-05-27 11:19:51'),
(5648, 2, 173, 166, 'إِذۡ تَبَرَّأَ ٱلَّذِینَ ٱتُّبِعُوا۟ مِنَ ٱلَّذِینَ ٱتَّبَعُوا۟ وَرَأَوُا۟ ٱلۡعَذَابَ وَتَقَطَّعَتۡ بِهِمُ ٱلۡأَسۡبَابُ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:51'),
(5649, 2, 174, 167, 'وَقَالَ ٱلَّذِینَ ٱتَّبَعُوا۟ لَوۡ أَنَّ لَنَا كَرَّةࣰ فَنَتَبَرَّأَ مِنۡهُمۡ كَمَا تَبَرَّءُوا۟ مِنَّاۗ كَذَ ٰ⁠لِكَ یُرِیهِمُ ٱللَّهُ أَعۡمَـٰلَهُمۡ حَسَرَ ٰ⁠تٍ عَلَیۡهِمۡۖ وَمَا هُم بِخَـٰرِجِینَ مِنَ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:51'),
(5650, 2, 175, 168, 'یَـٰۤأَیُّهَا ٱلنَّاسُ كُلُوا۟ مِمَّا فِی ٱلۡأَرۡضِ حَلَـٰلࣰا طَیِّبࣰا وَلَا تَتَّبِعُوا۟ خُطُوَ ٰ⁠تِ ٱلشَّیۡطَـٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوࣱّ مُّبِینٌ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:51'),
(5651, 2, 176, 169, 'إِنَّمَا یَأۡمُرُكُم بِٱلسُّوۤءِ وَٱلۡفَحۡشَاۤءِ وَأَن تَقُولُوا۟ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:51'),
(5652, 2, 177, 170, 'وَإِذَا قِیلَ لَهُمُ ٱتَّبِعُوا۟ مَاۤ أَنزَلَ ٱللَّهُ قَالُوا۟ بَلۡ نَتَّبِعُ مَاۤ أَلۡفَیۡنَا عَلَیۡهِ ءَابَاۤءَنَاۤۚ أَوَلَوۡ كَانَ ءَابَاۤؤُهُمۡ لَا یَعۡقِلُونَ شَیۡـࣰٔا وَلَا یَهۡتَدُونَ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:51'),
(5653, 2, 178, 171, 'وَمَثَلُ ٱلَّذِینَ كَفَرُوا۟ كَمَثَلِ ٱلَّذِی یَنۡعِقُ بِمَا لَا یَسۡمَعُ إِلَّا دُعَاۤءࣰ وَنِدَاۤءࣰۚ صُمُّۢ بُكۡمٌ عُمۡیࣱ فَهُمۡ لَا یَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:51'),
(5654, 2, 179, 172, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ كُلُوا۟ مِن طَیِّبَـٰتِ مَا رَزَقۡنَـٰكُمۡ وَٱشۡكُرُوا۟ لِلَّهِ إِن كُنتُمۡ إِیَّاهُ تَعۡبُدُونَ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:52'),
(5655, 2, 180, 173, 'إِنَّمَا حَرَّمَ عَلَیۡكُمُ ٱلۡمَیۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِیرِ وَمَاۤ أُهِلَّ بِهِۦ لِغَیۡرِ ٱللَّهِۖ فَمَنِ ٱضۡطُرَّ غَیۡرَ بَاغࣲ وَلَا عَادࣲ فَلَاۤ إِثۡمَ عَلَیۡهِۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:52'),
(5656, 2, 181, 174, 'إِنَّ ٱلَّذِینَ یَكۡتُمُونَ مَاۤ أَنزَلَ ٱللَّهُ مِنَ ٱلۡكِتَـٰبِ وَیَشۡتَرُونَ بِهِۦ ثَمَنࣰا قَلِیلًا أُو۟لَـٰۤىِٕكَ مَا یَأۡكُلُونَ فِی بُطُونِهِمۡ إِلَّا ٱلنَّارَ وَلَا یُكَلِّمُهُمُ ٱللَّهُ یَوۡمَ ٱلۡقِیَـٰمَةِ وَلَا یُزَكِّیهِمۡ وَلَهُمۡ عَذَابٌ أَلِیمٌ\n', NULL, 0, '2023-05-26 17:36:13', '2023-05-27 11:19:52'),
(5657, 2, 182, 175, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ ٱشۡتَرَوُا۟ ٱلضَّلَـٰلَةَ بِٱلۡهُدَىٰ وَٱلۡعَذَابَ بِٱلۡمَغۡفِرَةِۚ فَمَاۤ أَصۡبَرَهُمۡ عَلَى ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5658, 2, 183, 176, 'ذَ ٰ⁠لِكَ بِأَنَّ ٱللَّهَ نَزَّلَ ٱلۡكِتَـٰبَ بِٱلۡحَقِّۗ وَإِنَّ ٱلَّذِینَ ٱخۡتَلَفُوا۟ فِی ٱلۡكِتَـٰبِ لَفِی شِقَاقِۭ بَعِیدࣲ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5659, 2, 184, 177, '۞ لَّیۡسَ ٱلۡبِرَّ أَن تُوَلُّوا۟ وُجُوهَكُمۡ قِبَلَ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَلَـٰكِنَّ ٱلۡبِرَّ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِ وَٱلۡمَلَـٰۤىِٕكَةِ وَٱلۡكِتَـٰبِ وَٱلنَّبِیِّـۧنَ وَءَاتَى ٱلۡمَالَ عَلَىٰ حُبِّهِۦ ذَوِی ٱلۡقُرۡبَىٰ وَٱلۡیَتَـٰمَىٰ وَٱلۡمَسَـٰكِینَ وَٱبۡنَ ٱلسَّبِیلِ وَٱلسَّاۤىِٕلِینَ وَفِی ٱلرِّقَابِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَٱلۡمُوفُونَ بِعَهۡدِهِمۡ إِذَا عَـٰهَدُوا۟ۖ وَٱلصَّـٰبِرِینَ فِی ٱلۡبَأۡسَاۤءِ وَٱلضَّرَّاۤءِ وَحِینَ ٱلۡبَأۡسِۗ أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ صَدَقُوا۟ۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡمُتَّقُونَ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5660, 2, 185, 178, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ كُتِبَ عَلَیۡكُمُ ٱلۡقِصَاصُ فِی ٱلۡقَتۡلَىۖ ٱلۡحُرُّ بِٱلۡحُرِّ وَٱلۡعَبۡدُ بِٱلۡعَبۡدِ وَٱلۡأُنثَىٰ بِٱلۡأُنثَىٰۚ فَمَنۡ عُفِیَ لَهُۥ مِنۡ أَخِیهِ شَیۡءࣱ فَٱتِّبَاعُۢ بِٱلۡمَعۡرُوفِ وَأَدَاۤءٌ إِلَیۡهِ بِإِحۡسَـٰنࣲۗ ذَ ٰ⁠لِكَ تَخۡفِیفࣱ مِّن رَّبِّكُمۡ وَرَحۡمَةࣱۗ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَ ٰ⁠لِكَ فَلَهُۥ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5661, 2, 186, 179, 'وَلَكُمۡ فِی ٱلۡقِصَاصِ حَیَوٰةࣱ یَـٰۤأُو۟لِی ٱلۡأَلۡبَـٰبِ لَعَلَّكُمۡ تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5662, 2, 187, 180, 'كُتِبَ عَلَیۡكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ إِن تَرَكَ خَیۡرًا ٱلۡوَصِیَّةُ لِلۡوَ ٰ⁠لِدَیۡنِ وَٱلۡأَقۡرَبِینَ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5663, 2, 188, 181, 'فَمَنۢ بَدَّلَهُۥ بَعۡدَ مَا سَمِعَهُۥ فَإِنَّمَاۤ إِثۡمُهُۥ عَلَى ٱلَّذِینَ یُبَدِّلُونَهُۥۤۚ إِنَّ ٱللَّهَ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5664, 2, 189, 182, 'فَمَنۡ خَافَ مِن مُّوصࣲ جَنَفًا أَوۡ إِثۡمࣰا فَأَصۡلَحَ بَیۡنَهُمۡ فَلَاۤ إِثۡمَ عَلَیۡهِۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:36:14', '2023-05-27 11:19:52'),
(5665, 2, 190, 183, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ كُتِبَ عَلَیۡكُمُ ٱلصِّیَامُ كَمَا كُتِبَ عَلَى ٱلَّذِینَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:52'),
(5666, 2, 191, 184, 'أَیَّامࣰا مَّعۡدُودَ ٰ⁠تࣲۚ فَمَن كَانَ مِنكُم مَّرِیضًا أَوۡ عَلَىٰ سَفَرࣲ فَعِدَّةࣱ مِّنۡ أَیَّامٍ أُخَرَۚ وَعَلَى ٱلَّذِینَ یُطِیقُونَهُۥ فِدۡیَةࣱ طَعَامُ مِسۡكِینࣲۖ فَمَن تَطَوَّعَ خَیۡرࣰا فَهُوَ خَیۡرࣱ لَّهُۥۚ وَأَن تَصُومُوا۟ خَیۡرࣱ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:52'),
(5667, 2, 192, 185, 'شَهۡرُ رَمَضَانَ ٱلَّذِیۤ أُنزِلَ فِیهِ ٱلۡقُرۡءَانُ هُدࣰى لِّلنَّاسِ وَبَیِّنَـٰتࣲ مِّنَ ٱلۡهُدَىٰ وَٱلۡفُرۡقَانِۚ فَمَن شَهِدَ مِنكُمُ ٱلشَّهۡرَ فَلۡیَصُمۡهُۖ وَمَن كَانَ مَرِیضًا أَوۡ عَلَىٰ سَفَرࣲ فَعِدَّةࣱ مِّنۡ أَیَّامٍ أُخَرَۗ یُرِیدُ ٱللَّهُ بِكُمُ ٱلۡیُسۡرَ وَلَا یُرِیدُ بِكُمُ ٱلۡعُسۡرَ وَلِتُكۡمِلُوا۟ ٱلۡعِدَّةَ وَلِتُكَبِّرُوا۟ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:52'),
(5668, 2, 193, 186, 'وَإِذَا سَأَلَكَ عِبَادِی عَنِّی فَإِنِّی قَرِیبٌۖ أُجِیبُ دَعۡوَةَ ٱلدَّاعِ إِذَا دَعَانِۖ فَلۡیَسۡتَجِیبُوا۟ لِی وَلۡیُؤۡمِنُوا۟ بِی لَعَلَّهُمۡ یَرۡشُدُونَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:52'),
(5669, 2, 194, 187, 'أُحِلَّ لَكُمۡ لَیۡلَةَ ٱلصِّیَامِ ٱلرَّفَثُ إِلَىٰ نِسَاۤىِٕكُمۡۚ هُنَّ لِبَاسࣱ لَّكُمۡ وَأَنتُمۡ لِبَاسࣱ لَّهُنَّۗ عَلِمَ ٱللَّهُ أَنَّكُمۡ كُنتُمۡ تَخۡتَانُونَ أَنفُسَكُمۡ فَتَابَ عَلَیۡكُمۡ وَعَفَا عَنكُمۡۖ فَٱلۡـَٔـٰنَ بَـٰشِرُوهُنَّ وَٱبۡتَغُوا۟ مَا كَتَبَ ٱللَّهُ لَكُمۡۚ وَكُلُوا۟ وَٱشۡرَبُوا۟ حَتَّىٰ یَتَبَیَّنَ لَكُمُ ٱلۡخَیۡطُ ٱلۡأَبۡیَضُ مِنَ ٱلۡخَیۡطِ ٱلۡأَسۡوَدِ مِنَ ٱلۡفَجۡرِۖ ثُمَّ أَتِمُّوا۟ ٱلصِّیَامَ إِلَى ٱلَّیۡلِۚ وَلَا تُبَـٰشِرُوهُنَّ وَأَنتُمۡ عَـٰكِفُونَ فِی ٱلۡمَسَـٰجِدِۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَقۡرَبُوهَاۗ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ ءَایَـٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ یَتَّقُونَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:53'),
(5670, 2, 195, 188, 'وَلَا تَأۡكُلُوۤا۟ أَمۡوَ ٰ⁠لَكُم بَیۡنَكُم بِٱلۡبَـٰطِلِ وَتُدۡلُوا۟ بِهَاۤ إِلَى ٱلۡحُكَّامِ لِتَأۡكُلُوا۟ فَرِیقࣰا مِّنۡ أَمۡوَ ٰ⁠لِ ٱلنَّاسِ بِٱلۡإِثۡمِ وَأَنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:53'),
(5671, 2, 196, 189, '۞ یَسۡـَٔلُونَكَ عَنِ ٱلۡأَهِلَّةِۖ قُلۡ هِیَ مَوَ ٰ⁠قِیتُ لِلنَّاسِ وَٱلۡحَجِّۗ وَلَیۡسَ ٱلۡبِرُّ بِأَن تَأۡتُوا۟ ٱلۡبُیُوتَ مِن ظُهُورِهَا وَلَـٰكِنَّ ٱلۡبِرَّ مَنِ ٱتَّقَىٰۗ وَأۡتُوا۟ ٱلۡبُیُوتَ مِنۡ أَبۡوَ ٰ⁠بِهَاۚ وَٱتَّقُوا۟ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:53'),
(5672, 2, 197, 190, 'وَقَـٰتِلُوا۟ فِی سَبِیلِ ٱللَّهِ ٱلَّذِینَ یُقَـٰتِلُونَكُمۡ وَلَا تَعۡتَدُوۤا۟ۚ إِنَّ ٱللَّهَ لَا یُحِبُّ ٱلۡمُعۡتَدِینَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:53'),
(5673, 2, 198, 191, 'وَٱقۡتُلُوهُمۡ حَیۡثُ ثَقِفۡتُمُوهُمۡ وَأَخۡرِجُوهُم مِّنۡ حَیۡثُ أَخۡرَجُوكُمۡۚ وَٱلۡفِتۡنَةُ أَشَدُّ مِنَ ٱلۡقَتۡلِۚ وَلَا تُقَـٰتِلُوهُمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ حَتَّىٰ یُقَـٰتِلُوكُمۡ فِیهِۖ فَإِن قَـٰتَلُوكُمۡ فَٱقۡتُلُوهُمۡۗ كَذَ ٰ⁠لِكَ جَزَاۤءُ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:53'),
(5674, 2, 199, 192, 'فَإِنِ ٱنتَهَوۡا۟ فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:36:15', '2023-05-27 11:19:53'),
(5675, 2, 200, 193, 'وَقَـٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةࣱ وَیَكُونَ ٱلدِّینُ لِلَّهِۖ فَإِنِ ٱنتَهَوۡا۟ فَلَا عُدۡوَ ٰ⁠نَ إِلَّا عَلَى ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5676, 2, 201, 194, 'ٱلشَّهۡرُ ٱلۡحَرَامُ بِٱلشَّهۡرِ ٱلۡحَرَامِ وَٱلۡحُرُمَـٰتُ قِصَاصࣱۚ فَمَنِ ٱعۡتَدَىٰ عَلَیۡكُمۡ فَٱعۡتَدُوا۟ عَلَیۡهِ بِمِثۡلِ مَا ٱعۡتَدَىٰ عَلَیۡكُمۡۚ وَٱتَّقُوا۟ ٱللَّهَ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5677, 2, 202, 195, 'وَأَنفِقُوا۟ فِی سَبِیلِ ٱللَّهِ وَلَا تُلۡقُوا۟ بِأَیۡدِیكُمۡ إِلَى ٱلتَّهۡلُكَةِ وَأَحۡسِنُوۤا۟ۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5678, 2, 203, 196, 'وَأَتِمُّوا۟ ٱلۡحَجَّ وَٱلۡعُمۡرَةَ لِلَّهِۚ فَإِنۡ أُحۡصِرۡتُمۡ فَمَا ٱسۡتَیۡسَرَ مِنَ ٱلۡهَدۡیِۖ وَلَا تَحۡلِقُوا۟ رُءُوسَكُمۡ حَتَّىٰ یَبۡلُغَ ٱلۡهَدۡیُ مَحِلَّهُۥۚ فَمَن كَانَ مِنكُم مَّرِیضًا أَوۡ بِهِۦۤ أَذࣰى مِّن رَّأۡسِهِۦ فَفِدۡیَةࣱ مِّن صِیَامٍ أَوۡ صَدَقَةٍ أَوۡ نُسُكࣲۚ فَإِذَاۤ أَمِنتُمۡ فَمَن تَمَتَّعَ بِٱلۡعُمۡرَةِ إِلَى ٱلۡحَجِّ فَمَا ٱسۡتَیۡسَرَ مِنَ ٱلۡهَدۡیِۚ فَمَن لَّمۡ یَجِدۡ فَصِیَامُ ثَلَـٰثَةِ أَیَّامࣲ فِی ٱلۡحَجِّ وَسَبۡعَةٍ إِذَا رَجَعۡتُمۡۗ تِلۡكَ عَشَرَةࣱ كَامِلَةࣱۗ ذَ ٰ⁠لِكَ لِمَن لَّمۡ یَكُنۡ أَهۡلُهُۥ حَاضِرِی ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَٱتَّقُوا۟ ٱللَّهَ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5679, 2, 204, 197, 'ٱلۡحَجُّ أَشۡهُرࣱ مَّعۡلُومَـٰتࣱۚ فَمَن فَرَضَ فِیهِنَّ ٱلۡحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِی ٱلۡحَجِّۗ وَمَا تَفۡعَلُوا۟ مِنۡ خَیۡرࣲ یَعۡلَمۡهُ ٱللَّهُۗ وَتَزَوَّدُوا۟ فَإِنَّ خَیۡرَ ٱلزَّادِ ٱلتَّقۡوَىٰۖ وَٱتَّقُونِ یَـٰۤأُو۟لِی ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5680, 2, 205, 198, 'لَیۡسَ عَلَیۡكُمۡ جُنَاحٌ أَن تَبۡتَغُوا۟ فَضۡلࣰا مِّن رَّبِّكُمۡۚ فَإِذَاۤ أَفَضۡتُم مِّنۡ عَرَفَـٰتࣲ فَٱذۡكُرُوا۟ ٱللَّهَ عِندَ ٱلۡمَشۡعَرِ ٱلۡحَرَامِۖ وَٱذۡكُرُوهُ كَمَا هَدَىٰكُمۡ وَإِن كُنتُم مِّن قَبۡلِهِۦ لَمِنَ ٱلضَّاۤلِّینَ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5681, 2, 206, 199, 'ثُمَّ أَفِیضُوا۟ مِنۡ حَیۡثُ أَفَاضَ ٱلنَّاسُ وَٱسۡتَغۡفِرُوا۟ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5682, 2, 207, 200, 'فَإِذَا قَضَیۡتُم مَّنَـٰسِكَكُمۡ فَٱذۡكُرُوا۟ ٱللَّهَ كَذِكۡرِكُمۡ ءَابَاۤءَكُمۡ أَوۡ أَشَدَّ ذِكۡرࣰاۗ فَمِنَ ٱلنَّاسِ مَن یَقُولُ رَبَّنَاۤ ءَاتِنَا فِی ٱلدُّنۡیَا وَمَا لَهُۥ فِی ٱلۡـَٔاخِرَةِ مِنۡ خَلَـٰقࣲ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5683, 2, 208, 201, 'وَمِنۡهُم مَّن یَقُولُ رَبَّنَاۤ ءَاتِنَا فِی ٱلدُّنۡیَا حَسَنَةࣰ وَفِی ٱلۡـَٔاخِرَةِ حَسَنَةࣰ وَقِنَا عَذَابَ ٱلنَّارِ\n', NULL, 0, '2023-05-26 17:36:16', '2023-05-27 11:19:53'),
(5684, 2, 209, 202, 'أُو۟لَـٰۤىِٕكَ لَهُمۡ نَصِیبࣱ مِّمَّا كَسَبُوا۟ۚ وَٱللَّهُ سَرِیعُ ٱلۡحِسَابِ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:53'),
(5685, 2, 210, 203, '۞ وَٱذۡكُرُوا۟ ٱللَّهَ فِیۤ أَیَّامࣲ مَّعۡدُودَ ٰ⁠تࣲۚ فَمَن تَعَجَّلَ فِی یَوۡمَیۡنِ فَلَاۤ إِثۡمَ عَلَیۡهِ وَمَن تَأَخَّرَ فَلَاۤ إِثۡمَ عَلَیۡهِۖ لِمَنِ ٱتَّقَىٰۗ وَٱتَّقُوا۟ ٱللَّهَ وَٱعۡلَمُوۤا۟ أَنَّكُمۡ إِلَیۡهِ تُحۡشَرُونَ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5686, 2, 211, 204, 'وَمِنَ ٱلنَّاسِ مَن یُعۡجِبُكَ قَوۡلُهُۥ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَیُشۡهِدُ ٱللَّهَ عَلَىٰ مَا فِی قَلۡبِهِۦ وَهُوَ أَلَدُّ ٱلۡخِصَامِ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5687, 2, 212, 205, 'وَإِذَا تَوَلَّىٰ سَعَىٰ فِی ٱلۡأَرۡضِ لِیُفۡسِدَ فِیهَا وَیُهۡلِكَ ٱلۡحَرۡثَ وَٱلنَّسۡلَۚ وَٱللَّهُ لَا یُحِبُّ ٱلۡفَسَادَ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5688, 2, 213, 206, 'وَإِذَا قِیلَ لَهُ ٱتَّقِ ٱللَّهَ أَخَذَتۡهُ ٱلۡعِزَّةُ بِٱلۡإِثۡمِۚ فَحَسۡبُهُۥ جَهَنَّمُۖ وَلَبِئۡسَ ٱلۡمِهَادُ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5689, 2, 214, 207, 'وَمِنَ ٱلنَّاسِ مَن یَشۡرِی نَفۡسَهُ ٱبۡتِغَاۤءَ مَرۡضَاتِ ٱللَّهِۚ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5690, 2, 215, 208, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱدۡخُلُوا۟ فِی ٱلسِّلۡمِ كَاۤفَّةࣰ وَلَا تَتَّبِعُوا۟ خُطُوَ ٰ⁠تِ ٱلشَّیۡطَـٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوࣱّ مُّبِینࣱ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5691, 2, 216, 209, 'فَإِن زَلَلۡتُم مِّنۢ بَعۡدِ مَا جَاۤءَتۡكُمُ ٱلۡبَیِّنَـٰتُ فَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ عَزِیزٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5692, 2, 217, 210, 'هَلۡ یَنظُرُونَ إِلَّاۤ أَن یَأۡتِیَهُمُ ٱللَّهُ فِی ظُلَلࣲ مِّنَ ٱلۡغَمَامِ وَٱلۡمَلَـٰۤىِٕكَةُ وَقُضِیَ ٱلۡأَمۡرُۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n', NULL, 0, '2023-05-26 17:36:17', '2023-05-27 11:19:54'),
(5693, 2, 218, 211, 'سَلۡ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ كَمۡ ءَاتَیۡنَـٰهُم مِّنۡ ءَایَةِۭ بَیِّنَةࣲۗ وَمَن یُبَدِّلۡ نِعۡمَةَ ٱللَّهِ مِنۢ بَعۡدِ مَا جَاۤءَتۡهُ فَإِنَّ ٱللَّهَ شَدِیدُ ٱلۡعِقَابِ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:54'),
(5694, 2, 219, 212, 'زُیِّنَ لِلَّذِینَ كَفَرُوا۟ ٱلۡحَیَوٰةُ ٱلدُّنۡیَا وَیَسۡخَرُونَ مِنَ ٱلَّذِینَ ءَامَنُوا۟ۘ وَٱلَّذِینَ ٱتَّقَوۡا۟ فَوۡقَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِۗ وَٱللَّهُ یَرۡزُقُ مَن یَشَاۤءُ بِغَیۡرِ حِسَابࣲ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:54'),
(5695, 2, 220, 213, 'كَانَ ٱلنَّاسُ أُمَّةࣰ وَ ٰ⁠حِدَةࣰ فَبَعَثَ ٱللَّهُ ٱلنَّبِیِّـۧنَ مُبَشِّرِینَ وَمُنذِرِینَ وَأَنزَلَ مَعَهُمُ ٱلۡكِتَـٰبَ بِٱلۡحَقِّ لِیَحۡكُمَ بَیۡنَ ٱلنَّاسِ فِیمَا ٱخۡتَلَفُوا۟ فِیهِۚ وَمَا ٱخۡتَلَفَ فِیهِ إِلَّا ٱلَّذِینَ أُوتُوهُ مِنۢ بَعۡدِ مَا جَاۤءَتۡهُمُ ٱلۡبَیِّنَـٰتُ بَغۡیَۢا بَیۡنَهُمۡۖ فَهَدَى ٱللَّهُ ٱلَّذِینَ ءَامَنُوا۟ لِمَا ٱخۡتَلَفُوا۟ فِیهِ مِنَ ٱلۡحَقِّ بِإِذۡنِهِۦۗ وَٱللَّهُ یَهۡدِی مَن یَشَاۤءُ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمٍ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:54'),
(5696, 2, 221, 214, 'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُوا۟ ٱلۡجَنَّةَ وَلَمَّا یَأۡتِكُم مَّثَلُ ٱلَّذِینَ خَلَوۡا۟ مِن قَبۡلِكُمۖ مَّسَّتۡهُمُ ٱلۡبَأۡسَاۤءُ وَٱلضَّرَّاۤءُ وَزُلۡزِلُوا۟ حَتَّىٰ یَقُولَ ٱلرَّسُولُ وَٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥ مَتَىٰ نَصۡرُ ٱللَّهِۗ أَلَاۤ إِنَّ نَصۡرَ ٱللَّهِ قَرِیبࣱ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:54'),
(5697, 2, 222, 215, 'یَسۡـَٔلُونَكَ مَاذَا یُنفِقُونَۖ قُلۡ مَاۤ أَنفَقۡتُم مِّنۡ خَیۡرࣲ فَلِلۡوَ ٰ⁠لِدَیۡنِ وَٱلۡأَقۡرَبِینَ وَٱلۡیَتَـٰمَىٰ وَٱلۡمَسَـٰكِینِ وَٱبۡنِ ٱلسَّبِیلِۗ وَمَا تَفۡعَلُوا۟ مِنۡ خَیۡرࣲ فَإِنَّ ٱللَّهَ بِهِۦ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:54'),
(5698, 2, 223, 216, 'كُتِبَ عَلَیۡكُمُ ٱلۡقِتَالُ وَهُوَ كُرۡهࣱ لَّكُمۡۖ وَعَسَىٰۤ أَن تَكۡرَهُوا۟ شَیۡـࣰٔا وَهُوَ خَیۡرࣱ لَّكُمۡۖ وَعَسَىٰۤ أَن تُحِبُّوا۟ شَیۡـࣰٔا وَهُوَ شَرࣱّ لَّكُمۡۚ وَٱللَّهُ یَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:54'),
(5699, 2, 224, 217, 'یَسۡـَٔلُونَكَ عَنِ ٱلشَّهۡرِ ٱلۡحَرَامِ قِتَالࣲ فِیهِۖ قُلۡ قِتَالࣱ فِیهِ كَبِیرࣱۚ وَصَدٌّ عَن سَبِیلِ ٱللَّهِ وَكُفۡرُۢ بِهِۦ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَإِخۡرَاجُ أَهۡلِهِۦ مِنۡهُ أَكۡبَرُ عِندَ ٱللَّهِۚ وَٱلۡفِتۡنَةُ أَكۡبَرُ مِنَ ٱلۡقَتۡلِۗ وَلَا یَزَالُونَ یُقَـٰتِلُونَكُمۡ حَتَّىٰ یَرُدُّوكُمۡ عَن دِینِكُمۡ إِنِ ٱسۡتَطَـٰعُوا۟ۚ وَمَن یَرۡتَدِدۡ مِنكُمۡ عَن دِینِهِۦ فَیَمُتۡ وَهُوَ كَافِرࣱ فَأُو۟لَـٰۤىِٕكَ حَبِطَتۡ أَعۡمَـٰلُهُمۡ فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِۖ وَأُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:54'),
(5700, 2, 225, 218, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَٱلَّذِینَ هَاجَرُوا۟ وَجَـٰهَدُوا۟ فِی سَبِیلِ ٱللَّهِ أُو۟لَـٰۤىِٕكَ یَرۡجُونَ رَحۡمَتَ ٱللَّهِۚ وَٱللَّهُ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:36:18', '2023-05-27 11:19:55'),
(5701, 2, 226, 219, '۞ یَسۡـَٔلُونَكَ عَنِ ٱلۡخَمۡرِ وَٱلۡمَیۡسِرِۖ قُلۡ فِیهِمَاۤ إِثۡمࣱ كَبِیرࣱ وَمَنَـٰفِعُ لِلنَّاسِ وَإِثۡمُهُمَاۤ أَكۡبَرُ مِن نَّفۡعِهِمَاۗ وَیَسۡـَٔلُونَكَ مَاذَا یُنفِقُونَۖ قُلِ ٱلۡعَفۡوَۗ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمُ ٱلۡـَٔایَـٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5702, 2, 227, 220, 'فِی ٱلدُّنۡیَا وَٱلۡـَٔاخِرَةِۗ وَیَسۡـَٔلُونَكَ عَنِ ٱلۡیَتَـٰمَىٰۖ قُلۡ إِصۡلَاحࣱ لَّهُمۡ خَیۡرࣱۖ وَإِن تُخَالِطُوهُمۡ فَإِخۡوَ ٰ⁠نُكُمۡۚ وَٱللَّهُ یَعۡلَمُ ٱلۡمُفۡسِدَ مِنَ ٱلۡمُصۡلِحِۚ وَلَوۡ شَاۤءَ ٱللَّهُ لَأَعۡنَتَكُمۡۚ إِنَّ ٱللَّهَ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5703, 2, 228, 221, 'وَلَا تَنكِحُوا۟ ٱلۡمُشۡرِكَـٰتِ حَتَّىٰ یُؤۡمِنَّۚ وَلَأَمَةࣱ مُّؤۡمِنَةٌ خَیۡرࣱ مِّن مُّشۡرِكَةࣲ وَلَوۡ أَعۡجَبَتۡكُمۡۗ وَلَا تُنكِحُوا۟ ٱلۡمُشۡرِكِینَ حَتَّىٰ یُؤۡمِنُوا۟ۚ وَلَعَبۡدࣱ مُّؤۡمِنٌ خَیۡرࣱ مِّن مُّشۡرِكࣲ وَلَوۡ أَعۡجَبَكُمۡۗ أُو۟لَـٰۤىِٕكَ یَدۡعُونَ إِلَى ٱلنَّارِۖ وَٱللَّهُ یَدۡعُوۤا۟ إِلَى ٱلۡجَنَّةِ وَٱلۡمَغۡفِرَةِ بِإِذۡنِهِۦۖ وَیُبَیِّنُ ءَایَـٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ یَتَذَكَّرُونَ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5704, 2, 229, 222, 'وَیَسۡـَٔلُونَكَ عَنِ ٱلۡمَحِیضِۖ قُلۡ هُوَ أَذࣰى فَٱعۡتَزِلُوا۟ ٱلنِّسَاۤءَ فِی ٱلۡمَحِیضِ وَلَا تَقۡرَبُوهُنَّ حَتَّىٰ یَطۡهُرۡنَۖ فَإِذَا تَطَهَّرۡنَ فَأۡتُوهُنَّ مِنۡ حَیۡثُ أَمَرَكُمُ ٱللَّهُۚ إِنَّ ٱللَّهَ یُحِبُّ ٱلتَّوَّ ٰ⁠بِینَ وَیُحِبُّ ٱلۡمُتَطَهِّرِینَ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5705, 2, 230, 223, 'نِسَاۤؤُكُمۡ حَرۡثࣱ لَّكُمۡ فَأۡتُوا۟ حَرۡثَكُمۡ أَنَّىٰ شِئۡتُمۡۖ وَقَدِّمُوا۟ لِأَنفُسِكُمۡۚ وَٱتَّقُوا۟ ٱللَّهَ وَٱعۡلَمُوۤا۟ أَنَّكُم مُّلَـٰقُوهُۗ وَبَشِّرِ ٱلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5706, 2, 231, 224, 'وَلَا تَجۡعَلُوا۟ ٱللَّهَ عُرۡضَةࣰ لِّأَیۡمَـٰنِكُمۡ أَن تَبَرُّوا۟ وَتَتَّقُوا۟ وَتُصۡلِحُوا۟ بَیۡنَ ٱلنَّاسِۚ وَٱللَّهُ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5707, 2, 232, 225, 'لَّا یُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِیۤ أَیۡمَـٰنِكُمۡ وَلَـٰكِن یُؤَاخِذُكُم بِمَا كَسَبَتۡ قُلُوبُكُمۡۗ وَٱللَّهُ غَفُورٌ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5708, 2, 233, 226, 'لِّلَّذِینَ یُؤۡلُونَ مِن نِّسَاۤىِٕهِمۡ تَرَبُّصُ أَرۡبَعَةِ أَشۡهُرࣲۖ فَإِن فَاۤءُو فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5709, 2, 234, 227, 'وَإِنۡ عَزَمُوا۟ ٱلطَّلَـٰقَ فَإِنَّ ٱللَّهَ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5710, 2, 235, 228, 'وَٱلۡمُطَلَّقَـٰتُ یَتَرَبَّصۡنَ بِأَنفُسِهِنَّ ثَلَـٰثَةَ قُرُوۤءࣲۚ وَلَا یَحِلُّ لَهُنَّ أَن یَكۡتُمۡنَ مَا خَلَقَ ٱللَّهُ فِیۤ أَرۡحَامِهِنَّ إِن كُنَّ یُؤۡمِنَّ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِی ذَ ٰ⁠لِكَ إِنۡ أَرَادُوۤا۟ إِصۡلَـٰحࣰاۚ وَلَهُنَّ مِثۡلُ ٱلَّذِی عَلَیۡهِنَّ بِٱلۡمَعۡرُوفِۚ وَلِلرِّجَالِ عَلَیۡهِنَّ دَرَجَةࣱۗ وَٱللَّهُ عَزِیزٌ حَكِیمٌ\n', NULL, 0, '2023-05-26 17:36:19', '2023-05-27 11:19:55'),
(5711, 2, 236, 229, 'ٱلطَّلَـٰقُ مَرَّتَانِۖ فَإِمۡسَاكُۢ بِمَعۡرُوفٍ أَوۡ تَسۡرِیحُۢ بِإِحۡسَـٰنࣲۗ وَلَا یَحِلُّ لَكُمۡ أَن تَأۡخُذُوا۟ مِمَّاۤ ءَاتَیۡتُمُوهُنَّ شَیۡـًٔا إِلَّاۤ أَن یَخَافَاۤ أَلَّا یُقِیمَا حُدُودَ ٱللَّهِۖ فَإِنۡ خِفۡتُمۡ أَلَّا یُقِیمَا حُدُودَ ٱللَّهِ فَلَا جُنَاحَ عَلَیۡهِمَا فِیمَا ٱفۡتَدَتۡ بِهِۦۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَعۡتَدُوهَاۚ وَمَن یَتَعَدَّ حُدُودَ ٱللَّهِ فَأُو۟لَـٰۤىِٕكَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:55'),
(5712, 2, 237, 230, 'فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُۥ مِنۢ بَعۡدُ حَتَّىٰ تَنكِحَ زَوۡجًا غَیۡرَهُۥۗ فَإِن طَلَّقَهَا فَلَا جُنَاحَ عَلَیۡهِمَاۤ أَن یَتَرَاجَعَاۤ إِن ظَنَّاۤ أَن یُقِیمَا حُدُودَ ٱللَّهِۗ وَتِلۡكَ حُدُودُ ٱللَّهِ یُبَیِّنُهَا لِقَوۡمࣲ یَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:55'),
(5713, 2, 238, 231, 'وَإِذَا طَلَّقۡتُمُ ٱلنِّسَاۤءَ فَبَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ سَرِّحُوهُنَّ بِمَعۡرُوفࣲۚ وَلَا تُمۡسِكُوهُنَّ ضِرَارࣰا لِّتَعۡتَدُوا۟ۚ وَمَن یَفۡعَلۡ ذَ ٰ⁠لِكَ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ وَلَا تَتَّخِذُوۤا۟ ءَایَـٰتِ ٱللَّهِ هُزُوࣰاۚ وَٱذۡكُرُوا۟ نِعۡمَتَ ٱللَّهِ عَلَیۡكُمۡ وَمَاۤ أَنزَلَ عَلَیۡكُم مِّنَ ٱلۡكِتَـٰبِ وَٱلۡحِكۡمَةِ یَعِظُكُم بِهِۦۚ وَٱتَّقُوا۟ ٱللَّهَ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:55'),
(5714, 2, 239, 232, 'وَإِذَا طَلَّقۡتُمُ ٱلنِّسَاۤءَ فَبَلَغۡنَ أَجَلَهُنَّ فَلَا تَعۡضُلُوهُنَّ أَن یَنكِحۡنَ أَزۡوَ ٰ⁠جَهُنَّ إِذَا تَرَ ٰ⁠ضَوۡا۟ بَیۡنَهُم بِٱلۡمَعۡرُوفِۗ ذَ ٰ⁠لِكَ یُوعَظُ بِهِۦ مَن كَانَ مِنكُمۡ یُؤۡمِنُ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِۗ ذَ ٰ⁠لِكُمۡ أَزۡكَىٰ لَكُمۡ وَأَطۡهَرُۚ وَٱللَّهُ یَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:55'),
(5715, 2, 240, 233, '۞ وَٱلۡوَ ٰ⁠لِدَ ٰ⁠تُ یُرۡضِعۡنَ أَوۡلَـٰدَهُنَّ حَوۡلَیۡنِ كَامِلَیۡنِۖ لِمَنۡ أَرَادَ أَن یُتِمَّ ٱلرَّضَاعَةَۚ وَعَلَى ٱلۡمَوۡلُودِ لَهُۥ رِزۡقُهُنَّ وَكِسۡوَتُهُنَّ بِٱلۡمَعۡرُوفِۚ لَا تُكَلَّفُ نَفۡسٌ إِلَّا وُسۡعَهَاۚ لَا تُضَاۤرَّ وَ ٰ⁠لِدَةُۢ بِوَلَدِهَا وَلَا مَوۡلُودࣱ لَّهُۥ بِوَلَدِهِۦۚ وَعَلَى ٱلۡوَارِثِ مِثۡلُ ذَ ٰ⁠لِكَۗ فَإِنۡ أَرَادَا فِصَالًا عَن تَرَاضࣲ مِّنۡهُمَا وَتَشَاوُرࣲ فَلَا جُنَاحَ عَلَیۡهِمَاۗ وَإِنۡ أَرَدتُّمۡ أَن تَسۡتَرۡضِعُوۤا۟ أَوۡلَـٰدَكُمۡ فَلَا جُنَاحَ عَلَیۡكُمۡ إِذَا سَلَّمۡتُم مَّاۤ ءَاتَیۡتُم بِٱلۡمَعۡرُوفِۗ وَٱتَّقُوا۟ ٱللَّهَ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِیرࣱ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:55'),
(5716, 2, 241, 234, 'وَٱلَّذِینَ یُتَوَفَّوۡنَ مِنكُمۡ وَیَذَرُونَ أَزۡوَ ٰ⁠جࣰا یَتَرَبَّصۡنَ بِأَنفُسِهِنَّ أَرۡبَعَةَ أَشۡهُرࣲ وَعَشۡرࣰاۖ فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَیۡكُمۡ فِیمَا فَعَلۡنَ فِیۤ أَنفُسِهِنَّ بِٱلۡمَعۡرُوفِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:55'),
(5717, 2, 242, 235, 'وَلَا جُنَاحَ عَلَیۡكُمۡ فِیمَا عَرَّضۡتُم بِهِۦ مِنۡ خِطۡبَةِ ٱلنِّسَاۤءِ أَوۡ أَكۡنَنتُمۡ فِیۤ أَنفُسِكُمۡۚ عَلِمَ ٱللَّهُ أَنَّكُمۡ سَتَذۡكُرُونَهُنَّ وَلَـٰكِن لَّا تُوَاعِدُوهُنَّ سِرًّا إِلَّاۤ أَن تَقُولُوا۟ قَوۡلࣰا مَّعۡرُوفࣰاۚ وَلَا تَعۡزِمُوا۟ عُقۡدَةَ ٱلنِّكَاحِ حَتَّىٰ یَبۡلُغَ ٱلۡكِتَـٰبُ أَجَلَهُۥۚ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ یَعۡلَمُ مَا فِیۤ أَنفُسِكُمۡ فَٱحۡذَرُوهُۚ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ غَفُورٌ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:56'),
(5718, 2, 243, 236, 'لَّا جُنَاحَ عَلَیۡكُمۡ إِن طَلَّقۡتُمُ ٱلنِّسَاۤءَ مَا لَمۡ تَمَسُّوهُنَّ أَوۡ تَفۡرِضُوا۟ لَهُنَّ فَرِیضَةࣰۚ وَمَتِّعُوهُنَّ عَلَى ٱلۡمُوسِعِ قَدَرُهُۥ وَعَلَى ٱلۡمُقۡتِرِ قَدَرُهُۥ مَتَـٰعَۢا بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُحۡسِنِینَ\n', NULL, 0, '2023-05-26 17:36:20', '2023-05-27 11:19:56'),
(5719, 2, 244, 237, 'وَإِن طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ وَقَدۡ فَرَضۡتُمۡ لَهُنَّ فَرِیضَةࣰ فَنِصۡفُ مَا فَرَضۡتُمۡ إِلَّاۤ أَن یَعۡفُونَ أَوۡ یَعۡفُوَا۟ ٱلَّذِی بِیَدِهِۦ عُقۡدَةُ ٱلنِّكَاحِۚ وَأَن تَعۡفُوۤا۟ أَقۡرَبُ لِلتَّقۡوَىٰۚ وَلَا تَنسَوُا۟ ٱلۡفَضۡلَ بَیۡنَكُمۡۚ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِیرٌ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5720, 2, 245, 238, 'حَـٰفِظُوا۟ عَلَى ٱلصَّلَوَ ٰ⁠تِ وَٱلصَّلَوٰةِ ٱلۡوُسۡطَىٰ وَقُومُوا۟ لِلَّهِ قَـٰنِتِینَ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5721, 2, 246, 239, 'فَإِنۡ خِفۡتُمۡ فَرِجَالًا أَوۡ رُكۡبَانࣰاۖ فَإِذَاۤ أَمِنتُمۡ فَٱذۡكُرُوا۟ ٱللَّهَ كَمَا عَلَّمَكُم مَّا لَمۡ تَكُونُوا۟ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5722, 2, 247, 240, 'وَٱلَّذِینَ یُتَوَفَّوۡنَ مِنكُمۡ وَیَذَرُونَ أَزۡوَ ٰ⁠جࣰا وَصِیَّةࣰ لِّأَزۡوَ ٰ⁠جِهِم مَّتَـٰعًا إِلَى ٱلۡحَوۡلِ غَیۡرَ إِخۡرَاجࣲۚ فَإِنۡ خَرَجۡنَ فَلَا جُنَاحَ عَلَیۡكُمۡ فِی مَا فَعَلۡنَ فِیۤ أَنفُسِهِنَّ مِن مَّعۡرُوفࣲۗ وَٱللَّهُ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5723, 2, 248, 241, 'وَلِلۡمُطَلَّقَـٰتِ مَتَـٰعُۢ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5724, 2, 249, 242, 'كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمۡ ءَایَـٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(5725, 2, 250, 243, '۞ أَلَمۡ تَرَ إِلَى ٱلَّذِینَ خَرَجُوا۟ مِن دِیَـٰرِهِمۡ وَهُمۡ أُلُوفٌ حَذَرَ ٱلۡمَوۡتِ فَقَالَ لَهُمُ ٱللَّهُ مُوتُوا۟ ثُمَّ أَحۡیَـٰهُمۡۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَشۡكُرُونَ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5726, 2, 251, 244, 'وَقَـٰتِلُوا۟ فِی سَبِیلِ ٱللَّهِ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ سَمِیعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5727, 2, 252, 245, 'مَّن ذَا ٱلَّذِی یُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنࣰا فَیُضَـٰعِفَهُۥ لَهُۥۤ أَضۡعَافࣰا كَثِیرَةࣰۚ وَٱللَّهُ یَقۡبِضُ وَیَبۡصُۜطُ وَإِلَیۡهِ تُرۡجَعُونَ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5728, 2, 253, 246, 'أَلَمۡ تَرَ إِلَى ٱلۡمَلَإِ مِنۢ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ مِنۢ بَعۡدِ مُوسَىٰۤ إِذۡ قَالُوا۟ لِنَبِیࣲّ لَّهُمُ ٱبۡعَثۡ لَنَا مَلِكࣰا نُّقَـٰتِلۡ فِی سَبِیلِ ٱللَّهِۖ قَالَ هَلۡ عَسَیۡتُمۡ إِن كُتِبَ عَلَیۡكُمُ ٱلۡقِتَالُ أَلَّا تُقَـٰتِلُوا۟ۖ قَالُوا۟ وَمَا لَنَاۤ أَلَّا نُقَـٰتِلَ فِی سَبِیلِ ٱللَّهِ وَقَدۡ أُخۡرِجۡنَا مِن دِیَـٰرِنَا وَأَبۡنَاۤىِٕنَاۖ فَلَمَّا كُتِبَ عَلَیۡهِمُ ٱلۡقِتَالُ تَوَلَّوۡا۟ إِلَّا قَلِیلࣰا مِّنۡهُمۡۚ وَٱللَّهُ عَلِیمُۢ بِٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:36:21', '2023-05-27 11:19:56'),
(5729, 2, 254, 247, 'وَقَالَ لَهُمۡ نَبِیُّهُمۡ إِنَّ ٱللَّهَ قَدۡ بَعَثَ لَكُمۡ طَالُوتَ مَلِكࣰاۚ قَالُوۤا۟ أَنَّىٰ یَكُونُ لَهُ ٱلۡمُلۡكُ عَلَیۡنَا وَنَحۡنُ أَحَقُّ بِٱلۡمُلۡكِ مِنۡهُ وَلَمۡ یُؤۡتَ سَعَةࣰ مِّنَ ٱلۡمَالِۚ قَالَ إِنَّ ٱللَّهَ ٱصۡطَفَىٰهُ عَلَیۡكُمۡ وَزَادَهُۥ بَسۡطَةࣰ فِی ٱلۡعِلۡمِ وَٱلۡجِسۡمِۖ وَٱللَّهُ یُؤۡتِی مُلۡكَهُۥ مَن یَشَاۤءُۚ وَٱللَّهُ وَ ٰ⁠سِعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:56'),
(5730, 2, 255, 248, 'وَقَالَ لَهُمۡ نَبِیُّهُمۡ إِنَّ ءَایَةَ مُلۡكِهِۦۤ أَن یَأۡتِیَكُمُ ٱلتَّابُوتُ فِیهِ سَكِینَةࣱ مِّن رَّبِّكُمۡ وَبَقِیَّةࣱ مِّمَّا تَرَكَ ءَالُ مُوسَىٰ وَءَالُ هَـٰرُونَ تَحۡمِلُهُ ٱلۡمَلَـٰۤىِٕكَةُۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:56'),
(5731, 2, 256, 249, 'فَلَمَّا فَصَلَ طَالُوتُ بِٱلۡجُنُودِ قَالَ إِنَّ ٱللَّهَ مُبۡتَلِیكُم بِنَهَرࣲ فَمَن شَرِبَ مِنۡهُ فَلَیۡسَ مِنِّی وَمَن لَّمۡ یَطۡعَمۡهُ فَإِنَّهُۥ مِنِّیۤ إِلَّا مَنِ ٱغۡتَرَفَ غُرۡفَةَۢ بِیَدِهِۦۚ فَشَرِبُوا۟ مِنۡهُ إِلَّا قَلِیلࣰا مِّنۡهُمۡۚ فَلَمَّا جَاوَزَهُۥ هُوَ وَٱلَّذِینَ ءَامَنُوا۟ مَعَهُۥ قَالُوا۟ لَا طَاقَةَ لَنَا ٱلۡیَوۡمَ بِجَالُوتَ وَجُنُودِهِۦۚ قَالَ ٱلَّذِینَ یَظُنُّونَ أَنَّهُم مُّلَـٰقُوا۟ ٱللَّهِ كَم مِّن فِئَةࣲ قَلِیلَةٍ غَلَبَتۡ فِئَةࣰ كَثِیرَةَۢ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:56'),
(5732, 2, 257, 250, 'وَلَمَّا بَرَزُوا۟ لِجَالُوتَ وَجُنُودِهِۦ قَالُوا۟ رَبَّنَاۤ أَفۡرِغۡ عَلَیۡنَا صَبۡرࣰا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:57'),
(5733, 2, 258, 251, 'فَهَزَمُوهُم بِإِذۡنِ ٱللَّهِ وَقَتَلَ دَاوُۥدُ جَالُوتَ وَءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ وَٱلۡحِكۡمَةَ وَعَلَّمَهُۥ مِمَّا یَشَاۤءُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضࣲ لَّفَسَدَتِ ٱلۡأَرۡضُ وَلَـٰكِنَّ ٱللَّهَ ذُو فَضۡلٍ عَلَى ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:57'),
(5734, 2, 259, 252, 'تِلۡكَ ءَایَـٰتُ ٱللَّهِ نَتۡلُوهَا عَلَیۡكَ بِٱلۡحَقِّۚ وَإِنَّكَ لَمِنَ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:57'),
(5735, 2, 260, 253, '۞ تِلۡكَ ٱلرُّسُلُ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضࣲۘ مِّنۡهُم مَّن كَلَّمَ ٱللَّهُۖ وَرَفَعَ بَعۡضَهُمۡ دَرَجَـٰتࣲۚ وَءَاتَیۡنَا عِیسَى ٱبۡنَ مَرۡیَمَ ٱلۡبَیِّنَـٰتِ وَأَیَّدۡنَـٰهُ بِرُوحِ ٱلۡقُدُسِۗ وَلَوۡ شَاۤءَ ٱللَّهُ مَا ٱقۡتَتَلَ ٱلَّذِینَ مِنۢ بَعۡدِهِم مِّنۢ بَعۡدِ مَا جَاۤءَتۡهُمُ ٱلۡبَیِّنَـٰتُ وَلَـٰكِنِ ٱخۡتَلَفُوا۟ فَمِنۡهُم مَّنۡ ءَامَنَ وَمِنۡهُم مَّن كَفَرَۚ وَلَوۡ شَاۤءَ ٱللَّهُ مَا ٱقۡتَتَلُوا۟ وَلَـٰكِنَّ ٱللَّهَ یَفۡعَلُ مَا یُرِیدُ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:57'),
(5736, 2, 261, 254, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ أَنفِقُوا۟ مِمَّا رَزَقۡنَـٰكُم مِّن قَبۡلِ أَن یَأۡتِیَ یَوۡمࣱ لَّا بَیۡعࣱ فِیهِ وَلَا خُلَّةࣱ وَلَا شَفَـٰعَةࣱۗ وَٱلۡكَـٰفِرُونَ هُمُ ٱلظَّـٰلِمُونَ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:57'),
(5737, 2, 262, 255, 'ٱللَّهُ لَاۤ إِلَـٰهَ إِلَّا هُوَ ٱلۡحَیُّ ٱلۡقَیُّومُۚ لَا تَأۡخُذُهُۥ سِنَةࣱ وَلَا نَوۡمࣱۚ لَّهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۗ مَن ذَا ٱلَّذِی یَشۡفَعُ عِندَهُۥۤ إِلَّا بِإِذۡنِهِۦۚ یَعۡلَمُ مَا بَیۡنَ أَیۡدِیهِمۡ وَمَا خَلۡفَهُمۡۖ وَلَا یُحِیطُونَ بِشَیۡءࣲ مِّنۡ عِلۡمِهِۦۤ إِلَّا بِمَا شَاۤءَۚ وَسِعَ كُرۡسِیُّهُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَۖ وَلَا یَـُٔودُهُۥ حِفۡظُهُمَاۚ وَهُوَ ٱلۡعَلِیُّ ٱلۡعَظِیمُ\n', NULL, 0, '2023-05-26 17:36:22', '2023-05-27 11:19:57'),
(5738, 2, 263, 256, 'لَاۤ إِكۡرَاهَ فِی ٱلدِّینِۖ قَد تَّبَیَّنَ ٱلرُّشۡدُ مِنَ ٱلۡغَیِّۚ فَمَن یَكۡفُرۡ بِٱلطَّـٰغُوتِ وَیُؤۡمِنۢ بِٱللَّهِ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰ لَا ٱنفِصَامَ لَهَاۗ وَٱللَّهُ سَمِیعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:57'),
(5739, 2, 264, 257, 'ٱللَّهُ وَلِیُّ ٱلَّذِینَ ءَامَنُوا۟ یُخۡرِجُهُم مِّنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِۖ وَٱلَّذِینَ كَفَرُوۤا۟ أَوۡلِیَاۤؤُهُمُ ٱلطَّـٰغُوتُ یُخۡرِجُونَهُم مِّنَ ٱلنُّورِ إِلَى ٱلظُّلُمَـٰتِۗ أُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:57'),
(5740, 2, 265, 258, 'أَلَمۡ تَرَ إِلَى ٱلَّذِی حَاۤجَّ إِبۡرَ ٰ⁠هِـۧمَ فِی رَبِّهِۦۤ أَنۡ ءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ إِذۡ قَالَ إِبۡرَ ٰ⁠هِـۧمُ رَبِّیَ ٱلَّذِی یُحۡیِۦ وَیُمِیتُ قَالَ أَنَا۠ أُحۡیِۦ وَأُمِیتُۖ قَالَ إِبۡرَ ٰ⁠هِـۧمُ فَإِنَّ ٱللَّهَ یَأۡتِی بِٱلشَّمۡسِ مِنَ ٱلۡمَشۡرِقِ فَأۡتِ بِهَا مِنَ ٱلۡمَغۡرِبِ فَبُهِتَ ٱلَّذِی كَفَرَۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلظَّـٰلِمِینَ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:57'),
(5741, 2, 266, 259, 'أَوۡ كَٱلَّذِی مَرَّ عَلَىٰ قَرۡیَةࣲ وَهِیَ خَاوِیَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنَّىٰ یُحۡیِۦ هَـٰذِهِ ٱللَّهُ بَعۡدَ مَوۡتِهَاۖ فَأَمَاتَهُ ٱللَّهُ مِا۟ئَةَ عَامࣲ ثُمَّ بَعَثَهُۥۖ قَالَ كَمۡ لَبِثۡتَۖ قَالَ لَبِثۡتُ یَوۡمًا أَوۡ بَعۡضَ یَوۡمࣲۖ قَالَ بَل لَّبِثۡتَ مِا۟ئَةَ عَامࣲ فَٱنظُرۡ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمۡ یَتَسَنَّهۡۖ وَٱنظُرۡ إِلَىٰ حِمَارِكَ وَلِنَجۡعَلَكَ ءَایَةࣰ لِّلنَّاسِۖ وَٱنظُرۡ إِلَى ٱلۡعِظَامِ كَیۡفَ نُنشِزُهَا ثُمَّ نَكۡسُوهَا لَحۡمࣰاۚ فَلَمَّا تَبَیَّنَ لَهُۥ قَالَ أَعۡلَمُ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:57'),
(5742, 2, 267, 260, 'وَإِذۡ قَالَ إِبۡرَ ٰ⁠هِـۧمُ رَبِّ أَرِنِی كَیۡفَ تُحۡیِ ٱلۡمَوۡتَىٰۖ قَالَ أَوَلَمۡ تُؤۡمِنۖ قَالَ بَلَىٰ وَلَـٰكِن لِّیَطۡمَىِٕنَّ قَلۡبِیۖ قَالَ فَخُذۡ أَرۡبَعَةࣰ مِّنَ ٱلطَّیۡرِ فَصُرۡهُنَّ إِلَیۡكَ ثُمَّ ٱجۡعَلۡ عَلَىٰ كُلِّ جَبَلࣲ مِّنۡهُنَّ جُزۡءࣰا ثُمَّ ٱدۡعُهُنَّ یَأۡتِینَكَ سَعۡیࣰاۚ وَٱعۡلَمۡ أَنَّ ٱللَّهَ عَزِیزٌ حَكِیمࣱ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:57'),
(5743, 2, 268, 261, 'مَّثَلُ ٱلَّذِینَ یُنفِقُونَ أَمۡوَ ٰ⁠لَهُمۡ فِی سَبِیلِ ٱللَّهِ كَمَثَلِ حَبَّةٍ أَنۢبَتَتۡ سَبۡعَ سَنَابِلَ فِی كُلِّ سُنۢبُلَةࣲ مِّا۟ئَةُ حَبَّةࣲۗ وَٱللَّهُ یُضَـٰعِفُ لِمَن یَشَاۤءُۚ وَٱللَّهُ وَ ٰ⁠سِعٌ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:57'),
(5744, 2, 269, 262, 'ٱلَّذِینَ یُنفِقُونَ أَمۡوَ ٰ⁠لَهُمۡ فِی سَبِیلِ ٱللَّهِ ثُمَّ لَا یُتۡبِعُونَ مَاۤ أَنفَقُوا۟ مَنࣰّا وَلَاۤ أَذࣰى لَّهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:57'),
(5745, 2, 270, 263, '۞ قَوۡلࣱ مَّعۡرُوفࣱ وَمَغۡفِرَةٌ خَیۡرࣱ مِّن صَدَقَةࣲ یَتۡبَعُهَاۤ أَذࣰىۗ وَٱللَّهُ غَنِیٌّ حَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:58'),
(5746, 2, 271, 264, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ لَا تُبۡطِلُوا۟ صَدَقَـٰتِكُم بِٱلۡمَنِّ وَٱلۡأَذَىٰ كَٱلَّذِی یُنفِقُ مَالَهُۥ رِئَاۤءَ ٱلنَّاسِ وَلَا یُؤۡمِنُ بِٱللَّهِ وَٱلۡیَوۡمِ ٱلۡـَٔاخِرِۖ فَمَثَلُهُۥ كَمَثَلِ صَفۡوَانٍ عَلَیۡهِ تُرَابࣱ فَأَصَابَهُۥ وَابِلࣱ فَتَرَكَهُۥ صَلۡدࣰاۖ لَّا یَقۡدِرُونَ عَلَىٰ شَیۡءࣲ مِّمَّا كَسَبُوا۟ۗ وَٱللَّهُ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:36:23', '2023-05-27 11:19:58'),
(5747, 2, 272, 265, 'وَمَثَلُ ٱلَّذِینَ یُنفِقُونَ أَمۡوَ ٰ⁠لَهُمُ ٱبۡتِغَاۤءَ مَرۡضَاتِ ٱللَّهِ وَتَثۡبِیتࣰا مِّنۡ أَنفُسِهِمۡ كَمَثَلِ جَنَّةِۭ بِرَبۡوَةٍ أَصَابَهَا وَابِلࣱ فَـَٔاتَتۡ أُكُلَهَا ضِعۡفَیۡنِ فَإِن لَّمۡ یُصِبۡهَا وَابِلࣱ فَطَلࣱّۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِیرٌ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5748, 2, 273, 266, 'أَیَوَدُّ أَحَدُكُمۡ أَن تَكُونَ لَهُۥ جَنَّةࣱ مِّن نَّخِیلࣲ وَأَعۡنَابࣲ تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُ لَهُۥ فِیهَا مِن كُلِّ ٱلثَّمَرَ ٰ⁠تِ وَأَصَابَهُ ٱلۡكِبَرُ وَلَهُۥ ذُرِّیَّةࣱ ضُعَفَاۤءُ فَأَصَابَهَاۤ إِعۡصَارࣱ فِیهِ نَارࣱ فَٱحۡتَرَقَتۡۗ كَذَ ٰ⁠لِكَ یُبَیِّنُ ٱللَّهُ لَكُمُ ٱلۡـَٔایَـٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5749, 2, 274, 267, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ أَنفِقُوا۟ مِن طَیِّبَـٰتِ مَا كَسَبۡتُمۡ وَمِمَّاۤ أَخۡرَجۡنَا لَكُم مِّنَ ٱلۡأَرۡضِۖ وَلَا تَیَمَّمُوا۟ ٱلۡخَبِیثَ مِنۡهُ تُنفِقُونَ وَلَسۡتُم بِـَٔاخِذِیهِ إِلَّاۤ أَن تُغۡمِضُوا۟ فِیهِۚ وَٱعۡلَمُوۤا۟ أَنَّ ٱللَّهَ غَنِیٌّ حَمِیدٌ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5750, 2, 275, 268, 'ٱلشَّیۡطَـٰنُ یَعِدُكُمُ ٱلۡفَقۡرَ وَیَأۡمُرُكُم بِٱلۡفَحۡشَاۤءِۖ وَٱللَّهُ یَعِدُكُم مَّغۡفِرَةࣰ مِّنۡهُ وَفَضۡلࣰاۗ وَٱللَّهُ وَ ٰ⁠سِعٌ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5751, 2, 276, 269, 'یُؤۡتِی ٱلۡحِكۡمَةَ مَن یَشَاۤءُۚ وَمَن یُؤۡتَ ٱلۡحِكۡمَةَ فَقَدۡ أُوتِیَ خَیۡرࣰا كَثِیرࣰاۗ وَمَا یَذَّكَّرُ إِلَّاۤ أُو۟لُوا۟ ٱلۡأَلۡبَـٰبِ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5752, 2, 277, 270, 'وَمَاۤ أَنفَقۡتُم مِّن نَّفَقَةٍ أَوۡ نَذَرۡتُم مِّن نَّذۡرࣲ فَإِنَّ ٱللَّهَ یَعۡلَمُهُۥۗ وَمَا لِلظَّـٰلِمِینَ مِنۡ أَنصَارٍ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5753, 2, 278, 271, 'إِن تُبۡدُوا۟ ٱلصَّدَقَـٰتِ فَنِعِمَّا هِیَۖ وَإِن تُخۡفُوهَا وَتُؤۡتُوهَا ٱلۡفُقَرَاۤءَ فَهُوَ خَیۡرࣱ لَّكُمۡۚ وَیُكَفِّرُ عَنكُم مِّن سَیِّـَٔاتِكُمۡۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِیرࣱ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5754, 2, 279, 272, '۞ لَّیۡسَ عَلَیۡكَ هُدَىٰهُمۡ وَلَـٰكِنَّ ٱللَّهَ یَهۡدِی مَن یَشَاۤءُۗ وَمَا تُنفِقُوا۟ مِنۡ خَیۡرࣲ فَلِأَنفُسِكُمۡۚ وَمَا تُنفِقُونَ إِلَّا ٱبۡتِغَاۤءَ وَجۡهِ ٱللَّهِۚ وَمَا تُنفِقُوا۟ مِنۡ خَیۡرࣲ یُوَفَّ إِلَیۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5755, 2, 280, 273, 'لِلۡفُقَرَاۤءِ ٱلَّذِینَ أُحۡصِرُوا۟ فِی سَبِیلِ ٱللَّهِ لَا یَسۡتَطِیعُونَ ضَرۡبࣰا فِی ٱلۡأَرۡضِ یَحۡسَبُهُمُ ٱلۡجَاهِلُ أَغۡنِیَاۤءَ مِنَ ٱلتَّعَفُّفِ تَعۡرِفُهُم بِسِیمَـٰهُمۡ لَا یَسۡـَٔلُونَ ٱلنَّاسَ إِلۡحَافࣰاۗ وَمَا تُنفِقُوا۟ مِنۡ خَیۡرࣲ فَإِنَّ ٱللَّهَ بِهِۦ عَلِیمٌ\n', NULL, 0, '2023-05-26 17:36:24', '2023-05-27 11:19:58'),
(5756, 2, 281, 274, 'ٱلَّذِینَ یُنفِقُونَ أَمۡوَ ٰ⁠لَهُم بِٱلَّیۡلِ وَٱلنَّهَارِ سِرࣰّا وَعَلَانِیَةࣰ فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:58'),
(5757, 2, 282, 275, 'ٱلَّذِینَ یَأۡكُلُونَ ٱلرِّبَوٰا۟ لَا یَقُومُونَ إِلَّا كَمَا یَقُومُ ٱلَّذِی یَتَخَبَّطُهُ ٱلشَّیۡطَـٰنُ مِنَ ٱلۡمَسِّۚ ذَ ٰ⁠لِكَ بِأَنَّهُمۡ قَالُوۤا۟ إِنَّمَا ٱلۡبَیۡعُ مِثۡلُ ٱلرِّبَوٰا۟ۗ وَأَحَلَّ ٱللَّهُ ٱلۡبَیۡعَ وَحَرَّمَ ٱلرِّبَوٰا۟ۚ فَمَن جَاۤءَهُۥ مَوۡعِظَةࣱ مِّن رَّبِّهِۦ فَٱنتَهَىٰ فَلَهُۥ مَا سَلَفَ وَأَمۡرُهُۥۤ إِلَى ٱللَّهِۖ وَمَنۡ عَادَ فَأُو۟لَـٰۤىِٕكَ أَصۡحَـٰبُ ٱلنَّارِۖ هُمۡ فِیهَا خَـٰلِدُونَ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:58'),
(5758, 2, 283, 276, 'یَمۡحَقُ ٱللَّهُ ٱلرِّبَوٰا۟ وَیُرۡبِی ٱلصَّدَقَـٰتِۗ وَٱللَّهُ لَا یُحِبُّ كُلَّ كَفَّارٍ أَثِیمٍ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:58'),
(5759, 2, 284, 277, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَءَاتَوُا۟ ٱلزَّكَوٰةَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَیۡهِمۡ وَلَا هُمۡ یَحۡزَنُونَ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:59'),
(5760, 2, 285, 278, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَذَرُوا۟ مَا بَقِیَ مِنَ ٱلرِّبَوٰۤا۟ إِن كُنتُم مُّؤۡمِنِینَ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:59'),
(5761, 2, 286, 279, 'فَإِن لَّمۡ تَفۡعَلُوا۟ فَأۡذَنُوا۟ بِحَرۡبࣲ مِّنَ ٱللَّهِ وَرَسُولِهِۦۖ وَإِن تُبۡتُمۡ فَلَكُمۡ رُءُوسُ أَمۡوَ ٰ⁠لِكُمۡ لَا تَظۡلِمُونَ وَلَا تُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:59'),
(5762, 2, 287, 280, 'وَإِن كَانَ ذُو عُسۡرَةࣲ فَنَظِرَةٌ إِلَىٰ مَیۡسَرَةࣲۚ وَأَن تَصَدَّقُوا۟ خَیۡرࣱ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:59'),
(5763, 2, 288, 281, 'وَٱتَّقُوا۟ یَوۡمࣰا تُرۡجَعُونَ فِیهِ إِلَى ٱللَّهِۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسࣲ مَّا كَسَبَتۡ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:59'),
(5764, 2, 289, 282, 'یَـٰۤأَیُّهَا ٱلَّذِینَ ءَامَنُوۤا۟ إِذَا تَدَایَنتُم بِدَیۡنٍ إِلَىٰۤ أَجَلࣲ مُّسَمࣰّى فَٱكۡتُبُوهُۚ وَلۡیَكۡتُب بَّیۡنَكُمۡ كَاتِبُۢ بِٱلۡعَدۡلِۚ وَلَا یَأۡبَ كَاتِبٌ أَن یَكۡتُبَ كَمَا عَلَّمَهُ ٱللَّهُۚ فَلۡیَكۡتُبۡ وَلۡیُمۡلِلِ ٱلَّذِی عَلَیۡهِ ٱلۡحَقُّ وَلۡیَتَّقِ ٱللَّهَ رَبَّهُۥ وَلَا یَبۡخَسۡ مِنۡهُ شَیۡـࣰٔاۚ فَإِن كَانَ ٱلَّذِی عَلَیۡهِ ٱلۡحَقُّ سَفِیهًا أَوۡ ضَعِیفًا أَوۡ لَا یَسۡتَطِیعُ أَن یُمِلَّ هُوَ فَلۡیُمۡلِلۡ وَلِیُّهُۥ بِٱلۡعَدۡلِۚ وَٱسۡتَشۡهِدُوا۟ شَهِیدَیۡنِ مِن رِّجَالِكُمۡۖ فَإِن لَّمۡ یَكُونَا رَجُلَیۡنِ فَرَجُلࣱ وَٱمۡرَأَتَانِ مِمَّن تَرۡضَوۡنَ مِنَ ٱلشُّهَدَاۤءِ أَن تَضِلَّ إِحۡدَىٰهُمَا فَتُذَكِّرَ إِحۡدَىٰهُمَا ٱلۡأُخۡرَىٰۚ وَلَا یَأۡبَ ٱلشُّهَدَاۤءُ إِذَا مَا دُعُوا۟ۚ وَلَا تَسۡـَٔمُوۤا۟ أَن تَكۡتُبُوهُ صَغِیرًا أَوۡ كَبِیرًا إِلَىٰۤ أَجَلِهِۦۚ ذَ ٰ⁠لِكُمۡ أَقۡسَطُ عِندَ ٱللَّهِ وَأَقۡوَمُ لِلشَّهَـٰدَةِ وَأَدۡنَىٰۤ أَلَّا تَرۡتَابُوۤا۟ إِلَّاۤ أَن تَكُونَ تِجَـٰرَةً حَاضِرَةࣰ تُدِیرُونَهَا بَیۡنَكُمۡ فَلَیۡسَ عَلَیۡكُمۡ جُنَاحٌ أَلَّا تَكۡتُبُوهَاۗ وَأَشۡهِدُوۤا۟ إِذَا تَبَایَعۡتُمۡۚ وَلَا یُضَاۤرَّ كَاتِبࣱ وَلَا شَهِیدࣱۚ وَإِن تَفۡعَلُوا۟ فَإِنَّهُۥ فُسُوقُۢ بِكُمۡۗ وَٱتَّقُوا۟ ٱللَّهَۖ وَیُعَلِّمُكُمُ ٱللَّهُۗ وَٱللَّهُ بِكُلِّ شَیۡءٍ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:25', '2023-05-27 11:19:59'),
(5765, 2, 290, 283, '۞ وَإِن كُنتُمۡ عَلَىٰ سَفَرࣲ وَلَمۡ تَجِدُوا۟ كَاتِبࣰا فَرِهَـٰنࣱ مَّقۡبُوضَةࣱۖ فَإِنۡ أَمِنَ بَعۡضُكُم بَعۡضࣰا فَلۡیُؤَدِّ ٱلَّذِی ٱؤۡتُمِنَ أَمَـٰنَتَهُۥ وَلۡیَتَّقِ ٱللَّهَ رَبَّهُۥۗ وَلَا تَكۡتُمُوا۟ ٱلشَّهَـٰدَةَۚ وَمَن یَكۡتُمۡهَا فَإِنَّهُۥۤ ءَاثِمࣱ قَلۡبُهُۥۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِیمࣱ\n', NULL, 0, '2023-05-26 17:36:26', '2023-05-27 11:19:59'),
(5766, 2, 291, 284, 'لِّلَّهِ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِۗ وَإِن تُبۡدُوا۟ مَا فِیۤ أَنفُسِكُمۡ أَوۡ تُخۡفُوهُ یُحَاسِبۡكُم بِهِ ٱللَّهُۖ فَیَغۡفِرُ لِمَن یَشَاۤءُ وَیُعَذِّبُ مَن یَشَاۤءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرٌ\n', NULL, 0, '2023-05-26 17:36:26', '2023-05-27 11:19:59'),
(5767, 2, 292, 285, 'ءَامَنَ ٱلرَّسُولُ بِمَاۤ أُنزِلَ إِلَیۡهِ مِن رَّبِّهِۦ وَٱلۡمُؤۡمِنُونَۚ كُلٌّ ءَامَنَ بِٱللَّهِ وَمَلَـٰۤىِٕكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ لَا نُفَرِّقُ بَیۡنَ أَحَدࣲ مِّن رُّسُلِهِۦۚ وَقَالُوا۟ سَمِعۡنَا وَأَطَعۡنَاۖ غُفۡرَانَكَ رَبَّنَا وَإِلَیۡكَ ٱلۡمَصِیرُ\n', NULL, 0, '2023-05-26 17:36:26', '2023-05-27 11:19:59'),
(5768, 2, 293, 286, 'لَا یُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا وُسۡعَهَاۚ لَهَا مَا كَسَبَتۡ وَعَلَیۡهَا مَا ٱكۡتَسَبَتۡۗ رَبَّنَا لَا تُؤَاخِذۡنَاۤ إِن نَّسِینَاۤ أَوۡ أَخۡطَأۡنَاۚ رَبَّنَا وَلَا تَحۡمِلۡ عَلَیۡنَاۤ إِصۡرࣰا كَمَا حَمَلۡتَهُۥ عَلَى ٱلَّذِینَ مِن قَبۡلِنَاۚ رَبَّنَا وَلَا تُحَمِّلۡنَا مَا لَا طَاقَةَ لَنَا بِهِۦۖ وَٱعۡفُ عَنَّا وَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَاۤۚ أَنتَ مَوۡلَىٰنَا فَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-26 17:36:26', '2023-05-27 11:19:59'),
(5769, 1, 1, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ\n', NULL, 0, '2023-05-26 17:36:28', '2023-05-27 11:20:00'),
(5770, 1, 2, 2, 'ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-26 17:36:28', '2023-05-27 11:20:00'),
(5771, 1, 3, 3, 'ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ\n', NULL, 0, '2023-05-26 17:36:28', '2023-05-27 11:20:00'),
(5772, 1, 4, 4, 'مَـٰلِكِ یَوۡمِ ٱلدِّینِ\n', NULL, 0, '2023-05-26 17:36:29', '2023-05-27 11:20:00'),
(5773, 1, 5, 5, 'إِیَّاكَ نَعۡبُدُ وَإِیَّاكَ نَسۡتَعِینُ\n', NULL, 0, '2023-05-26 17:36:29', '2023-05-27 11:20:00'),
(5774, 1, 6, 6, 'ٱهۡدِنَا ٱلصِّرَ ٰ⁠طَ ٱلۡمُسۡتَقِیمَ\n', NULL, 0, '2023-05-26 17:36:29', '2023-05-27 11:20:00'),
(5775, 1, 7, 7, 'صِرَ ٰ⁠طَ ٱلَّذِینَ أَنۡعَمۡتَ عَلَیۡهِمۡ غَیۡرِ ٱلۡمَغۡضُوبِ عَلَیۡهِمۡ وَلَا ٱلضَّاۤلِّینَ\n', NULL, 0, '2023-05-26 17:36:29', '2023-05-27 11:20:00'),
(5776, 19, 2251, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ كۤهیعۤصۤ\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:58'),
(5777, 19, 2252, 2, 'ذِكۡرُ رَحۡمَتِ رَبِّكَ عَبۡدَهُۥ زَكَرِیَّاۤ\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:58'),
(5778, 19, 2253, 3, 'إِذۡ نَادَىٰ رَبَّهُۥ نِدَاۤءً خَفِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:58'),
(5779, 19, 2254, 4, 'قَالَ رَبِّ إِنِّی وَهَنَ ٱلۡعَظۡمُ مِنِّی وَٱشۡتَعَلَ ٱلرَّأۡسُ شَیۡبࣰا وَلَمۡ أَكُنۢ بِدُعَاۤىِٕكَ رَبِّ شَقِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5780, 19, 2255, 5, 'وَإِنِّی خِفۡتُ ٱلۡمَوَ ٰ⁠لِیَ مِن وَرَاۤءِی وَكَانَتِ ٱمۡرَأَتِی عَاقِرࣰا فَهَبۡ لِی مِن لَّدُنكَ وَلِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5781, 19, 2256, 6, 'یَرِثُنِی وَیَرِثُ مِنۡ ءَالِ یَعۡقُوبَۖ وَٱجۡعَلۡهُ رَبِّ رَضِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5782, 19, 2257, 7, 'یَـٰزَكَرِیَّاۤ إِنَّا نُبَشِّرُكَ بِغُلَـٰمٍ ٱسۡمُهُۥ یَحۡیَىٰ لَمۡ نَجۡعَل لَّهُۥ مِن قَبۡلُ سَمِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5783, 19, 2258, 8, 'قَالَ رَبِّ أَنَّىٰ یَكُونُ لِی غُلَـٰمࣱ وَكَانَتِ ٱمۡرَأَتِی عَاقِرࣰا وَقَدۡ بَلَغۡتُ مِنَ ٱلۡكِبَرِ عِتِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5784, 19, 2259, 9, 'قَالَ كَذَ ٰ⁠لِكَ قَالَ رَبُّكَ هُوَ عَلَیَّ هَیِّنࣱ وَقَدۡ خَلَقۡتُكَ مِن قَبۡلُ وَلَمۡ تَكُ شَیۡـࣰٔا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5785, 19, 2260, 10, 'قَالَ رَبِّ ٱجۡعَل لِّیۤ ءَایَةࣰۖ قَالَ ءَایَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَـٰثَ لَیَالࣲ سَوِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5786, 19, 2261, 11, 'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ مِنَ ٱلۡمِحۡرَابِ فَأَوۡحَىٰۤ إِلَیۡهِمۡ أَن سَبِّحُوا۟ بُكۡرَةࣰ وَعَشِیࣰّا\n', NULL, 0, '2023-05-27 10:26:11', '2023-05-27 11:13:59'),
(5787, 19, 2262, 12, 'یَـٰیَحۡیَىٰ خُذِ ٱلۡكِتَـٰبَ بِقُوَّةࣲۖ وَءَاتَیۡنَـٰهُ ٱلۡحُكۡمَ صَبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5788, 19, 2263, 13, 'وَحَنَانࣰا مِّن لَّدُنَّا وَزَكَوٰةࣰۖ وَكَانَ تَقِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5789, 19, 2264, 14, 'وَبَرَّۢا بِوَ ٰ⁠لِدَیۡهِ وَلَمۡ یَكُن جَبَّارًا عَصِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5790, 19, 2265, 15, 'وَسَلَـٰمٌ عَلَیۡهِ یَوۡمَ وُلِدَ وَیَوۡمَ یَمُوتُ وَیَوۡمَ یُبۡعَثُ حَیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5791, 19, 2266, 16, 'وَٱذۡكُرۡ فِی ٱلۡكِتَـٰبِ مَرۡیَمَ إِذِ ٱنتَبَذَتۡ مِنۡ أَهۡلِهَا مَكَانࣰا شَرۡقِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5792, 19, 2267, 17, 'فَٱتَّخَذَتۡ مِن دُونِهِمۡ حِجَابࣰا فَأَرۡسَلۡنَاۤ إِلَیۡهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَرࣰا سَوِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5793, 19, 2268, 18, 'قَالَتۡ إِنِّیۤ أَعُوذُ بِٱلرَّحۡمَـٰنِ مِنكَ إِن كُنتَ تَقِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5794, 19, 2269, 19, 'قَالَ إِنَّمَاۤ أَنَا۠ رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلَـٰمࣰا زَكِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5795, 19, 2270, 20, 'قَالَتۡ أَنَّىٰ یَكُونُ لِی غُلَـٰمࣱ وَلَمۡ یَمۡسَسۡنِی بَشَرࣱ وَلَمۡ أَكُ بَغِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5796, 19, 2271, 21, 'قَالَ كَذَ ٰ⁠لِكِ قَالَ رَبُّكِ هُوَ عَلَیَّ هَیِّنࣱۖ وَلِنَجۡعَلَهُۥۤ ءَایَةࣰ لِّلنَّاسِ وَرَحۡمَةࣰ مِّنَّاۚ وَكَانَ أَمۡرࣰا مَّقۡضِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5797, 19, 2272, 22, '۞ فَحَمَلَتۡهُ فَٱنتَبَذَتۡ بِهِۦ مَكَانࣰا قَصِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:13:59'),
(5798, 19, 2273, 23, 'فَأَجَاۤءَهَا ٱلۡمَخَاضُ إِلَىٰ جِذۡعِ ٱلنَّخۡلَةِ قَالَتۡ یَـٰلَیۡتَنِی مِتُّ قَبۡلَ هَـٰذَا وَكُنتُ نَسۡیࣰا مَّنسِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:14:00'),
(5799, 19, 2274, 24, 'فَنَادَىٰهَا مِن تَحۡتِهَاۤ أَلَّا تَحۡزَنِی قَدۡ جَعَلَ رَبُّكِ تَحۡتَكِ سَرِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:14:00'),
(5800, 19, 2275, 25, 'وَهُزِّیۤ إِلَیۡكِ بِجِذۡعِ ٱلنَّخۡلَةِ تُسَـٰقِطۡ عَلَیۡكِ رُطَبࣰا جَنِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:14:00'),
(5801, 19, 2276, 26, 'فَكُلِی وَٱشۡرَبِی وَقَرِّی عَیۡنࣰاۖ فَإِمَّا تَرَیِنَّ مِنَ ٱلۡبَشَرِ أَحَدࣰا فَقُولِیۤ إِنِّی نَذَرۡتُ لِلرَّحۡمَـٰنِ صَوۡمࣰا فَلَنۡ أُكَلِّمَ ٱلۡیَوۡمَ إِنسِیࣰّا\n', NULL, 0, '2023-05-27 10:26:12', '2023-05-27 11:14:00'),
(5802, 19, 2277, 27, 'فَأَتَتۡ بِهِۦ قَوۡمَهَا تَحۡمِلُهُۥۖ قَالُوا۟ یَـٰمَرۡیَمُ لَقَدۡ جِئۡتِ شَیۡـࣰٔا فَرِیࣰّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5803, 19, 2278, 28, 'یَـٰۤأُخۡتَ هَـٰرُونَ مَا كَانَ أَبُوكِ ٱمۡرَأَ سَوۡءࣲ وَمَا كَانَتۡ أُمُّكِ بَغِیࣰّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5804, 19, 2279, 29, 'فَأَشَارَتۡ إِلَیۡهِۖ قَالُوا۟ كَیۡفَ نُكَلِّمُ مَن كَانَ فِی ٱلۡمَهۡدِ صَبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5805, 19, 2280, 30, 'قَالَ إِنِّی عَبۡدُ ٱللَّهِ ءَاتَىٰنِیَ ٱلۡكِتَـٰبَ وَجَعَلَنِی نَبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5806, 19, 2281, 31, 'وَجَعَلَنِی مُبَارَكًا أَیۡنَ مَا كُنتُ وَأَوۡصَـٰنِی بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ مَا دُمۡتُ حَیࣰّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5807, 19, 2282, 32, 'وَبَرَّۢا بِوَ ٰ⁠لِدَتِی وَلَمۡ یَجۡعَلۡنِی جَبَّارࣰا شَقِیࣰّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5808, 19, 2283, 33, 'وَٱلسَّلَـٰمُ عَلَیَّ یَوۡمَ وُلِدتُّ وَیَوۡمَ أَمُوتُ وَیَوۡمَ أُبۡعَثُ حَیࣰّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5809, 19, 2284, 34, 'ذَ ٰ⁠لِكَ عِیسَى ٱبۡنُ مَرۡیَمَۖ قَوۡلَ ٱلۡحَقِّ ٱلَّذِی فِیهِ یَمۡتَرُونَ\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5810, 19, 2285, 35, 'مَا كَانَ لِلَّهِ أَن یَتَّخِذَ مِن وَلَدࣲۖ سُبۡحَـٰنَهُۥۤۚ إِذَا قَضَىٰۤ أَمۡرࣰا فَإِنَّمَا یَقُولُ لَهُۥ كُن فَیَكُونُ\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5811, 19, 2286, 36, 'وَإِنَّ ٱللَّهَ رَبِّی وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَـٰذَا صِرَ ٰ⁠طࣱ مُّسۡتَقِیمࣱ\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:00'),
(5812, 19, 2287, 37, 'فَٱخۡتَلَفَ ٱلۡأَحۡزَابُ مِنۢ بَیۡنِهِمۡۖ فَوَیۡلࣱ لِّلَّذِینَ كَفَرُوا۟ مِن مَّشۡهَدِ یَوۡمٍ عَظِیمٍ\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:01'),
(5813, 19, 2288, 38, 'أَسۡمِعۡ بِهِمۡ وَأَبۡصِرۡ یَوۡمَ یَأۡتُونَنَاۖ لَـٰكِنِ ٱلظَّـٰلِمُونَ ٱلۡیَوۡمَ فِی ضَلَـٰلࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:01'),
(5814, 19, 2289, 39, 'وَأَنذِرۡهُمۡ یَوۡمَ ٱلۡحَسۡرَةِ إِذۡ قُضِیَ ٱلۡأَمۡرُ وَهُمۡ فِی غَفۡلَةࣲ وَهُمۡ لَا یُؤۡمِنُونَ\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:01'),
(5815, 19, 2290, 40, 'إِنَّا نَحۡنُ نَرِثُ ٱلۡأَرۡضَ وَمَنۡ عَلَیۡهَا وَإِلَیۡنَا یُرۡجَعُونَ\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:01'),
(5816, 19, 2291, 41, 'وَٱذۡكُرۡ فِی ٱلۡكِتَـٰبِ إِبۡرَ ٰ⁠هِیمَۚ إِنَّهُۥ كَانَ صِدِّیقࣰا نَّبِیًّا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:01'),
(5817, 19, 2292, 42, 'إِذۡ قَالَ لِأَبِیهِ یَـٰۤأَبَتِ لِمَ تَعۡبُدُ مَا لَا یَسۡمَعُ وَلَا یُبۡصِرُ وَلَا یُغۡنِی عَنكَ شَیۡـࣰٔا\n', NULL, 0, '2023-05-27 10:26:13', '2023-05-27 11:14:01'),
(5818, 19, 2293, 43, 'یَـٰۤأَبَتِ إِنِّی قَدۡ جَاۤءَنِی مِنَ ٱلۡعِلۡمِ مَا لَمۡ یَأۡتِكَ فَٱتَّبِعۡنِیۤ أَهۡدِكَ صِرَ ٰ⁠طࣰا سَوِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5819, 19, 2294, 44, 'یَـٰۤأَبَتِ لَا تَعۡبُدِ ٱلشَّیۡطَـٰنَۖ إِنَّ ٱلشَّیۡطَـٰنَ كَانَ لِلرَّحۡمَـٰنِ عَصِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5820, 19, 2295, 45, 'یَـٰۤأَبَتِ إِنِّیۤ أَخَافُ أَن یَمَسَّكَ عَذَابࣱ مِّنَ ٱلرَّحۡمَـٰنِ فَتَكُونَ لِلشَّیۡطَـٰنِ وَلِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5821, 19, 2296, 46, 'قَالَ أَرَاغِبٌ أَنتَ عَنۡ ءَالِهَتِی یَـٰۤإِبۡرَ ٰ⁠هِیمُۖ لَىِٕن لَّمۡ تَنتَهِ لَأَرۡجُمَنَّكَۖ وَٱهۡجُرۡنِی مَلِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5822, 19, 2297, 47, 'قَالَ سَلَـٰمٌ عَلَیۡكَۖ سَأَسۡتَغۡفِرُ لَكَ رَبِّیۤۖ إِنَّهُۥ كَانَ بِی حَفِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5823, 19, 2298, 48, 'وَأَعۡتَزِلُكُمۡ وَمَا تَدۡعُونَ مِن دُونِ ٱللَّهِ وَأَدۡعُوا۟ رَبِّی عَسَىٰۤ أَلَّاۤ أَكُونَ بِدُعَاۤءِ رَبِّی شَقِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5824, 19, 2299, 49, 'فَلَمَّا ٱعۡتَزَلَهُمۡ وَمَا یَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَهَبۡنَا لَهُۥۤ إِسۡحَـٰقَ وَیَعۡقُوبَۖ وَكُلࣰّا جَعَلۡنَا نَبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5825, 19, 2300, 50, 'وَوَهَبۡنَا لَهُم مِّن رَّحۡمَتِنَا وَجَعَلۡنَا لَهُمۡ لِسَانَ صِدۡقٍ عَلِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5826, 19, 2301, 51, 'وَٱذۡكُرۡ فِی ٱلۡكِتَـٰبِ مُوسَىٰۤۚ إِنَّهُۥ كَانَ مُخۡلَصࣰا وَكَانَ رَسُولࣰا نَّبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:01'),
(5827, 19, 2302, 52, 'وَنَـٰدَیۡنَـٰهُ مِن جَانِبِ ٱلطُّورِ ٱلۡأَیۡمَنِ وَقَرَّبۡنَـٰهُ نَجِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:02'),
(5828, 19, 2303, 53, 'وَوَهَبۡنَا لَهُۥ مِن رَّحۡمَتِنَاۤ أَخَاهُ هَـٰرُونَ نَبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:02'),
(5829, 19, 2304, 54, 'وَٱذۡكُرۡ فِی ٱلۡكِتَـٰبِ إِسۡمَـٰعِیلَۚ إِنَّهُۥ كَانَ صَادِقَ ٱلۡوَعۡدِ وَكَانَ رَسُولࣰا نَّبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:02'),
(5830, 19, 2305, 55, 'وَكَانَ یَأۡمُرُ أَهۡلَهُۥ بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ وَكَانَ عِندَ رَبِّهِۦ مَرۡضِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:02'),
(5831, 19, 2306, 56, 'وَٱذۡكُرۡ فِی ٱلۡكِتَـٰبِ إِدۡرِیسَۚ إِنَّهُۥ كَانَ صِدِّیقࣰا نَّبِیࣰّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:02'),
(5832, 19, 2307, 57, 'وَرَفَعۡنَـٰهُ مَكَانًا عَلِیًّا\n', NULL, 0, '2023-05-27 10:26:14', '2023-05-27 11:14:02'),
(5833, 19, 2308, 58, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ أَنۡعَمَ ٱللَّهُ عَلَیۡهِم مِّنَ ٱلنَّبِیِّـۧنَ مِن ذُرِّیَّةِ ءَادَمَ وَمِمَّنۡ حَمَلۡنَا مَعَ نُوحࣲ وَمِن ذُرِّیَّةِ إِبۡرَ ٰ⁠هِیمَ وَإِسۡرَ ٰ⁠ۤءِیلَ وَمِمَّنۡ هَدَیۡنَا وَٱجۡتَبَیۡنَاۤۚ إِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَـٰتُ ٱلرَّحۡمَـٰنِ خَرُّوا۟ سُجَّدࣰا وَبُكِیࣰّا ۩\n', NULL, 1, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5834, 19, 2309, 59, '۞ فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٌ أَضَاعُوا۟ ٱلصَّلَوٰةَ وَٱتَّبَعُوا۟ ٱلشَّهَوَ ٰ⁠تِۖ فَسَوۡفَ یَلۡقَوۡنَ غَیًّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5835, 19, 2310, 60, 'إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَـٰلِحࣰا فَأُو۟لَـٰۤىِٕكَ یَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا یُظۡلَمُونَ شَیۡـࣰٔا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5836, 19, 2311, 61, 'جَنَّـٰتِ عَدۡنٍ ٱلَّتِی وَعَدَ ٱلرَّحۡمَـٰنُ عِبَادَهُۥ بِٱلۡغَیۡبِۚ إِنَّهُۥ كَانَ وَعۡدُهُۥ مَأۡتِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5837, 19, 2312, 62, 'لَّا یَسۡمَعُونَ فِیهَا لَغۡوًا إِلَّا سَلَـٰمࣰاۖ وَلَهُمۡ رِزۡقُهُمۡ فِیهَا بُكۡرَةࣰ وَعَشِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5838, 19, 2313, 63, 'تِلۡكَ ٱلۡجَنَّةُ ٱلَّتِی نُورِثُ مِنۡ عِبَادِنَا مَن كَانَ تَقِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5839, 19, 2314, 64, 'وَمَا نَتَنَزَّلُ إِلَّا بِأَمۡرِ رَبِّكَۖ لَهُۥ مَا بَیۡنَ أَیۡدِینَا وَمَا خَلۡفَنَا وَمَا بَیۡنَ ذَ ٰ⁠لِكَۚ وَمَا كَانَ رَبُّكَ نَسِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5840, 19, 2315, 65, 'رَّبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَمَا بَیۡنَهُمَا فَٱعۡبُدۡهُ وَٱصۡطَبِرۡ لِعِبَـٰدَتِهِۦۚ هَلۡ تَعۡلَمُ لَهُۥ سَمِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5841, 19, 2316, 66, 'وَیَقُولُ ٱلۡإِنسَـٰنُ أَءِذَا مَا مِتُّ لَسَوۡفَ أُخۡرَجُ حَیًّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:02'),
(5842, 19, 2317, 67, 'أَوَلَا یَذۡكُرُ ٱلۡإِنسَـٰنُ أَنَّا خَلَقۡنَـٰهُ مِن قَبۡلُ وَلَمۡ یَكُ شَیۡـࣰٔا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:03'),
(5843, 19, 2318, 68, 'فَوَرَبِّكَ لَنَحۡشُرَنَّهُمۡ وَٱلشَّیَـٰطِینَ ثُمَّ لَنُحۡضِرَنَّهُمۡ حَوۡلَ جَهَنَّمَ جِثِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:03'),
(5844, 19, 2319, 69, 'ثُمَّ لَنَنزِعَنَّ مِن كُلِّ شِیعَةٍ أَیُّهُمۡ أَشَدُّ عَلَى ٱلرَّحۡمَـٰنِ عِتِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:03'),
(5845, 19, 2320, 70, 'ثُمَّ لَنَحۡنُ أَعۡلَمُ بِٱلَّذِینَ هُمۡ أَوۡلَىٰ بِهَا صِلِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:03'),
(5846, 19, 2321, 71, 'وَإِن مِّنكُمۡ إِلَّا وَارِدُهَاۚ كَانَ عَلَىٰ رَبِّكَ حَتۡمࣰا مَّقۡضِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:03'),
(5847, 19, 2322, 72, 'ثُمَّ نُنَجِّی ٱلَّذِینَ ٱتَّقَوا۟ وَّنَذَرُ ٱلظَّـٰلِمِینَ فِیهَا جِثِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:03'),
(5848, 19, 2323, 73, 'وَإِذَا تُتۡلَىٰ عَلَیۡهِمۡ ءَایَـٰتُنَا بَیِّنَـٰتࣲ قَالَ ٱلَّذِینَ كَفَرُوا۟ لِلَّذِینَ ءَامَنُوۤا۟ أَیُّ ٱلۡفَرِیقَیۡنِ خَیۡرࣱ مَّقَامࣰا وَأَحۡسَنُ نَدِیࣰّا\n', NULL, 0, '2023-05-27 10:26:15', '2023-05-27 11:14:03'),
(5849, 19, 2324, 74, 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَحۡسَنُ أَثَـٰثࣰا وَرِءۡیࣰا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:03'),
(5850, 19, 2325, 75, 'قُلۡ مَن كَانَ فِی ٱلضَّلَـٰلَةِ فَلۡیَمۡدُدۡ لَهُ ٱلرَّحۡمَـٰنُ مَدًّاۚ حَتَّىٰۤ إِذَا رَأَوۡا۟ مَا یُوعَدُونَ إِمَّا ٱلۡعَذَابَ وَإِمَّا ٱلسَّاعَةَ فَسَیَعۡلَمُونَ مَنۡ هُوَ شَرࣱّ مَّكَانࣰا وَأَضۡعَفُ جُندࣰا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:03'),
(5851, 19, 2326, 76, 'وَیَزِیدُ ٱللَّهُ ٱلَّذِینَ ٱهۡتَدَوۡا۟ هُدࣰىۗ وَٱلۡبَـٰقِیَـٰتُ ٱلصَّـٰلِحَـٰتُ خَیۡرٌ عِندَ رَبِّكَ ثَوَابࣰا وَخَیۡرࣱ مَّرَدًّا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:03'),
(5852, 19, 2327, 77, 'أَفَرَءَیۡتَ ٱلَّذِی كَفَرَ بِـَٔایَـٰتِنَا وَقَالَ لَأُوتَیَنَّ مَالࣰا وَوَلَدًا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:03'),
(5853, 19, 2328, 78, 'أَطَّلَعَ ٱلۡغَیۡبَ أَمِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَـٰنِ عَهۡدࣰا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:03'),
(5854, 19, 2329, 79, 'كَلَّاۚ سَنَكۡتُبُ مَا یَقُولُ وَنَمُدُّ لَهُۥ مِنَ ٱلۡعَذَابِ مَدࣰّا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:03'),
(5855, 19, 2330, 80, 'وَنَرِثُهُۥ مَا یَقُولُ وَیَأۡتِینَا فَرۡدࣰا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:03'),
(5856, 19, 2331, 81, 'وَٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ ءَالِهَةࣰ لِّیَكُونُوا۟ لَهُمۡ عِزࣰّا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:04'),
(5857, 19, 2332, 82, 'كَلَّاۚ سَیَكۡفُرُونَ بِعِبَادَتِهِمۡ وَیَكُونُونَ عَلَیۡهِمۡ ضِدًّا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:04'),
(5858, 19, 2333, 83, 'أَلَمۡ تَرَ أَنَّاۤ أَرۡسَلۡنَا ٱلشَّیَـٰطِینَ عَلَى ٱلۡكَـٰفِرِینَ تَؤُزُّهُمۡ أَزࣰّا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:04'),
(5859, 19, 2334, 84, 'فَلَا تَعۡجَلۡ عَلَیۡهِمۡۖ إِنَّمَا نَعُدُّ لَهُمۡ عَدࣰّا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:04'),
(5860, 19, 2335, 85, 'یَوۡمَ نَحۡشُرُ ٱلۡمُتَّقِینَ إِلَى ٱلرَّحۡمَـٰنِ وَفۡدࣰا\n', NULL, 0, '2023-05-27 10:26:16', '2023-05-27 11:14:04'),
(5861, 19, 2336, 86, 'وَنَسُوقُ ٱلۡمُجۡرِمِینَ إِلَىٰ جَهَنَّمَ وِرۡدࣰا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5862, 19, 2337, 87, 'لَّا یَمۡلِكُونَ ٱلشَّفَـٰعَةَ إِلَّا مَنِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَـٰنِ عَهۡدࣰا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5863, 19, 2338, 88, 'وَقَالُوا۟ ٱتَّخَذَ ٱلرَّحۡمَـٰنُ وَلَدࣰا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5864, 19, 2339, 89, 'لَّقَدۡ جِئۡتُمۡ شَیۡـًٔا إِدࣰّا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5865, 19, 2340, 90, 'تَكَادُ ٱلسَّمَـٰوَ ٰ⁠تُ یَتَفَطَّرۡنَ مِنۡهُ وَتَنشَقُّ ٱلۡأَرۡضُ وَتَخِرُّ ٱلۡجِبَالُ هَدًّا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5866, 19, 2341, 91, 'أَن دَعَوۡا۟ لِلرَّحۡمَـٰنِ وَلَدࣰا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5867, 19, 2342, 92, 'وَمَا یَنۢبَغِی لِلرَّحۡمَـٰنِ أَن یَتَّخِذَ وَلَدًا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5868, 19, 2343, 93, 'إِن كُلُّ مَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ إِلَّاۤ ءَاتِی ٱلرَّحۡمَـٰنِ عَبۡدࣰا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5869, 19, 2344, 94, 'لَّقَدۡ أَحۡصَىٰهُمۡ وَعَدَّهُمۡ عَدࣰّا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5870, 19, 2345, 95, 'وَكُلُّهُمۡ ءَاتِیهِ یَوۡمَ ٱلۡقِیَـٰمَةِ فَرۡدًا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:04'),
(5871, 19, 2346, 96, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ سَیَجۡعَلُ لَهُمُ ٱلرَّحۡمَـٰنُ وُدࣰّا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:05'),
(5872, 19, 2347, 97, 'فَإِنَّمَا یَسَّرۡنَـٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ ٱلۡمُتَّقِینَ وَتُنذِرَ بِهِۦ قَوۡمࣰا لُّدࣰّا\n', NULL, 0, '2023-05-27 10:26:17', '2023-05-27 11:14:05'),
(5873, 19, 2348, 98, 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هَلۡ تُحِسُّ مِنۡهُم مِّنۡ أَحَدٍ أَوۡ تَسۡمَعُ لَهُمۡ رِكۡزَۢا\n', NULL, 0, '2023-05-27 10:26:18', '2023-05-27 11:14:05'),
(5874, 18, 2141, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِیۤ أَنزَلَ عَلَىٰ عَبۡدِهِ ٱلۡكِتَـٰبَ وَلَمۡ یَجۡعَل لَّهُۥ عِوَجَاۜ\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:13'),
(5875, 18, 2142, 2, 'قَیِّمࣰا لِّیُنذِرَ بَأۡسࣰا شَدِیدࣰا مِّن لَّدُنۡهُ وَیُبَشِّرَ ٱلۡمُؤۡمِنِینَ ٱلَّذِینَ یَعۡمَلُونَ ٱلصَّـٰلِحَـٰتِ أَنَّ لَهُمۡ أَجۡرًا حَسَنࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:13'),
(5876, 18, 2143, 3, 'مَّـٰكِثِینَ فِیهِ أَبَدࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:13'),
(5877, 18, 2144, 4, 'وَیُنذِرَ ٱلَّذِینَ قَالُوا۟ ٱتَّخَذَ ٱللَّهُ وَلَدࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:13'),
(5878, 18, 2145, 5, 'مَّا لَهُم بِهِۦ مِنۡ عِلۡمࣲ وَلَا لِـَٔابَاۤىِٕهِمۡۚ كَبُرَتۡ كَلِمَةࣰ تَخۡرُجُ مِنۡ أَفۡوَ ٰ⁠هِهِمۡۚ إِن یَقُولُونَ إِلَّا كَذِبࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:13'),
(5879, 18, 2146, 6, 'فَلَعَلَّكَ بَـٰخِعࣱ نَّفۡسَكَ عَلَىٰۤ ءَاثَـٰرِهِمۡ إِن لَّمۡ یُؤۡمِنُوا۟ بِهَـٰذَا ٱلۡحَدِیثِ أَسَفًا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:13'),
(5880, 18, 2147, 7, 'إِنَّا جَعَلۡنَا مَا عَلَى ٱلۡأَرۡضِ زِینَةࣰ لَّهَا لِنَبۡلُوَهُمۡ أَیُّهُمۡ أَحۡسَنُ عَمَلࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5881, 18, 2148, 8, 'وَإِنَّا لَجَـٰعِلُونَ مَا عَلَیۡهَا صَعِیدࣰا جُرُزًا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5882, 18, 2149, 9, 'أَمۡ حَسِبۡتَ أَنَّ أَصۡحَـٰبَ ٱلۡكَهۡفِ وَٱلرَّقِیمِ كَانُوا۟ مِنۡ ءَایَـٰتِنَا عَجَبًا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5883, 18, 2150, 10, 'إِذۡ أَوَى ٱلۡفِتۡیَةُ إِلَى ٱلۡكَهۡفِ فَقَالُوا۟ رَبَّنَاۤ ءَاتِنَا مِن لَّدُنكَ رَحۡمَةࣰ وَهَیِّئۡ لَنَا مِنۡ أَمۡرِنَا رَشَدࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5884, 18, 2151, 11, 'فَضَرَبۡنَا عَلَىٰۤ ءَاذَانِهِمۡ فِی ٱلۡكَهۡفِ سِنِینَ عَدَدࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5885, 18, 2152, 12, 'ثُمَّ بَعَثۡنَـٰهُمۡ لِنَعۡلَمَ أَیُّ ٱلۡحِزۡبَیۡنِ أَحۡصَىٰ لِمَا لَبِثُوۤا۟ أَمَدࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5886, 18, 2153, 13, 'نَّحۡنُ نَقُصُّ عَلَیۡكَ نَبَأَهُم بِٱلۡحَقِّۚ إِنَّهُمۡ فِتۡیَةٌ ءَامَنُوا۟ بِرَبِّهِمۡ وَزِدۡنَـٰهُمۡ هُدࣰى\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5887, 18, 2154, 14, 'وَرَبَطۡنَا عَلَىٰ قُلُوبِهِمۡ إِذۡ قَامُوا۟ فَقَالُوا۟ رَبُّنَا رَبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ لَن نَّدۡعُوَا۟ مِن دُونِهِۦۤ إِلَـٰهࣰاۖ لَّقَدۡ قُلۡنَاۤ إِذࣰا شَطَطًا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5888, 18, 2155, 15, 'هَـٰۤؤُلَاۤءِ قَوۡمُنَا ٱتَّخَذُوا۟ مِن دُونِهِۦۤ ءَالِهَةࣰۖ لَّوۡلَا یَأۡتُونَ عَلَیۡهِم بِسُلۡطَـٰنِۭ بَیِّنࣲۖ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبࣰا\n', NULL, 0, '2023-05-27 10:26:21', '2023-05-27 11:14:14'),
(5889, 18, 2156, 16, 'وَإِذِ ٱعۡتَزَلۡتُمُوهُمۡ وَمَا یَعۡبُدُونَ إِلَّا ٱللَّهَ فَأۡوُۥۤا۟ إِلَى ٱلۡكَهۡفِ یَنشُرۡ لَكُمۡ رَبُّكُم مِّن رَّحۡمَتِهِۦ وَیُهَیِّئۡ لَكُم مِّنۡ أَمۡرِكُم مِّرۡفَقࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:14'),
(5890, 18, 2157, 17, '۞ وَتَرَى ٱلشَّمۡسَ إِذَا طَلَعَت تَّزَ ٰ⁠وَرُ عَن كَهۡفِهِمۡ ذَاتَ ٱلۡیَمِینِ وَإِذَا غَرَبَت تَّقۡرِضُهُمۡ ذَاتَ ٱلشِّمَالِ وَهُمۡ فِی فَجۡوَةࣲ مِّنۡهُۚ ذَ ٰ⁠لِكَ مِنۡ ءَایَـٰتِ ٱللَّهِۗ مَن یَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن یُضۡلِلۡ فَلَن تَجِدَ لَهُۥ وَلِیࣰّا مُّرۡشِدࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:14'),
(5891, 18, 2158, 18, 'وَتَحۡسَبُهُمۡ أَیۡقَاظࣰا وَهُمۡ رُقُودࣱۚ وَنُقَلِّبُهُمۡ ذَاتَ ٱلۡیَمِینِ وَذَاتَ ٱلشِّمَالِۖ وَكَلۡبُهُم بَـٰسِطࣱ ذِرَاعَیۡهِ بِٱلۡوَصِیدِۚ لَوِ ٱطَّلَعۡتَ عَلَیۡهِمۡ لَوَلَّیۡتَ مِنۡهُمۡ فِرَارࣰا وَلَمُلِئۡتَ مِنۡهُمۡ رُعۡبࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:14'),
(5892, 18, 2159, 19, 'وَكَذَ ٰ⁠لِكَ بَعَثۡنَـٰهُمۡ لِیَتَسَاۤءَلُوا۟ بَیۡنَهُمۡۚ قَالَ قَاۤىِٕلࣱ مِّنۡهُمۡ كَمۡ لَبِثۡتُمۡۖ قَالُوا۟ لَبِثۡنَا یَوۡمًا أَوۡ بَعۡضَ یَوۡمࣲۚ قَالُوا۟ رَبُّكُمۡ أَعۡلَمُ بِمَا لَبِثۡتُمۡ فَٱبۡعَثُوۤا۟ أَحَدَكُم بِوَرِقِكُمۡ هَـٰذِهِۦۤ إِلَى ٱلۡمَدِینَةِ فَلۡیَنظُرۡ أَیُّهَاۤ أَزۡكَىٰ طَعَامࣰا فَلۡیَأۡتِكُم بِرِزۡقࣲ مِّنۡهُ وَلۡیَتَلَطَّفۡ وَلَا یُشۡعِرَنَّ بِكُمۡ أَحَدًا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:14'),
(5893, 18, 2160, 20, 'إِنَّهُمۡ إِن یَظۡهَرُوا۟ عَلَیۡكُمۡ یَرۡجُمُوكُمۡ أَوۡ یُعِیدُوكُمۡ فِی مِلَّتِهِمۡ وَلَن تُفۡلِحُوۤا۟ إِذًا أَبَدࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:14'),
(5894, 18, 2161, 21, 'وَكَذَ ٰ⁠لِكَ أَعۡثَرۡنَا عَلَیۡهِمۡ لِیَعۡلَمُوۤا۟ أَنَّ وَعۡدَ ٱللَّهِ حَقࣱّ وَأَنَّ ٱلسَّاعَةَ لَا رَیۡبَ فِیهَاۤ إِذۡ یَتَنَـٰزَعُونَ بَیۡنَهُمۡ أَمۡرَهُمۡۖ فَقَالُوا۟ ٱبۡنُوا۟ عَلَیۡهِم بُنۡیَـٰنࣰاۖ رَّبُّهُمۡ أَعۡلَمُ بِهِمۡۚ قَالَ ٱلَّذِینَ غَلَبُوا۟ عَلَىٰۤ أَمۡرِهِمۡ لَنَتَّخِذَنَّ عَلَیۡهِم مَّسۡجِدࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:14'),
(5895, 18, 2162, 22, 'سَیَقُولُونَ ثَلَـٰثَةࣱ رَّابِعُهُمۡ كَلۡبُهُمۡ وَیَقُولُونَ خَمۡسَةࣱ سَادِسُهُمۡ كَلۡبُهُمۡ رَجۡمَۢا بِٱلۡغَیۡبِۖ وَیَقُولُونَ سَبۡعَةࣱ وَثَامِنُهُمۡ كَلۡبُهُمۡۚ قُل رَّبِّیۤ أَعۡلَمُ بِعِدَّتِهِم مَّا یَعۡلَمُهُمۡ إِلَّا قَلِیلࣱۗ فَلَا تُمَارِ فِیهِمۡ إِلَّا مِرَاۤءࣰ ظَـٰهِرࣰا وَلَا تَسۡتَفۡتِ فِیهِم مِّنۡهُمۡ أَحَدࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5896, 18, 2163, 23, 'وَلَا تَقُولَنَّ لِشَا۟یۡءٍ إِنِّی فَاعِلࣱ ذَ ٰ⁠لِكَ غَدًا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5897, 18, 2164, 24, 'إِلَّاۤ أَن یَشَاۤءَ ٱللَّهُۚ وَٱذۡكُر رَّبَّكَ إِذَا نَسِیتَ وَقُلۡ عَسَىٰۤ أَن یَهۡدِیَنِ رَبِّی لِأَقۡرَبَ مِنۡ هَـٰذَا رَشَدࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5898, 18, 2165, 25, 'وَلَبِثُوا۟ فِی كَهۡفِهِمۡ ثَلَـٰثَ مِا۟ئَةࣲ سِنِینَ وَٱزۡدَادُوا۟ تِسۡعࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5899, 18, 2166, 26, 'قُلِ ٱللَّهُ أَعۡلَمُ بِمَا لَبِثُوا۟ۖ لَهُۥ غَیۡبُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۖ أَبۡصِرۡ بِهِۦ وَأَسۡمِعۡۚ مَا لَهُم مِّن دُونِهِۦ مِن وَلِیࣲّ وَلَا یُشۡرِكُ فِی حُكۡمِهِۦۤ أَحَدࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5900, 18, 2167, 27, 'وَٱتۡلُ مَاۤ أُوحِیَ إِلَیۡكَ مِن كِتَابِ رَبِّكَۖ لَا مُبَدِّلَ لِكَلِمَـٰتِهِۦ وَلَن تَجِدَ مِن دُونِهِۦ مُلۡتَحَدࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5901, 18, 2168, 28, 'وَٱصۡبِرۡ نَفۡسَكَ مَعَ ٱلَّذِینَ یَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِیِّ یُرِیدُونَ وَجۡهَهُۥۖ وَلَا تَعۡدُ عَیۡنَاكَ عَنۡهُمۡ تُرِیدُ زِینَةَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ وَلَا تُطِعۡ مَنۡ أَغۡفَلۡنَا قَلۡبَهُۥ عَن ذِكۡرِنَا وَٱتَّبَعَ هَوَىٰهُ وَكَانَ أَمۡرُهُۥ فُرُطࣰا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5902, 18, 2169, 29, 'وَقُلِ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَن شَاۤءَ فَلۡیُؤۡمِن وَمَن شَاۤءَ فَلۡیَكۡفُرۡۚ إِنَّاۤ أَعۡتَدۡنَا لِلظَّـٰلِمِینَ نَارًا أَحَاطَ بِهِمۡ سُرَادِقُهَاۚ وَإِن یَسۡتَغِیثُوا۟ یُغَاثُوا۟ بِمَاۤءࣲ كَٱلۡمُهۡلِ یَشۡوِی ٱلۡوُجُوهَۚ بِئۡسَ ٱلشَّرَابُ وَسَاۤءَتۡ مُرۡتَفَقًا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5903, 18, 2170, 30, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ إِنَّا لَا نُضِیعُ أَجۡرَ مَنۡ أَحۡسَنَ عَمَلًا\n', NULL, 0, '2023-05-27 10:26:22', '2023-05-27 11:14:15'),
(5904, 18, 2171, 31, 'أُو۟لَـٰۤىِٕكَ لَهُمۡ جَنَّـٰتُ عَدۡنࣲ تَجۡرِی مِن تَحۡتِهِمُ ٱلۡأَنۡهَـٰرُ یُحَلَّوۡنَ فِیهَا مِنۡ أَسَاوِرَ مِن ذَهَبࣲ وَیَلۡبَسُونَ ثِیَابًا خُضۡرࣰا مِّن سُندُسࣲ وَإِسۡتَبۡرَقࣲ مُّتَّكِـِٔینَ فِیهَا عَلَى ٱلۡأَرَاۤىِٕكِۚ نِعۡمَ ٱلثَّوَابُ وَحَسُنَتۡ مُرۡتَفَقࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:15'),
(5905, 18, 2172, 32, '۞ وَٱضۡرِبۡ لَهُم مَّثَلࣰا رَّجُلَیۡنِ جَعَلۡنَا لِأَحَدِهِمَا جَنَّتَیۡنِ مِنۡ أَعۡنَـٰبࣲ وَحَفَفۡنَـٰهُمَا بِنَخۡلࣲ وَجَعَلۡنَا بَیۡنَهُمَا زَرۡعࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:15'),
(5906, 18, 2173, 33, 'كِلۡتَا ٱلۡجَنَّتَیۡنِ ءَاتَتۡ أُكُلَهَا وَلَمۡ تَظۡلِم مِّنۡهُ شَیۡـࣰٔاۚ وَفَجَّرۡنَا خِلَـٰلَهُمَا نَهَرࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:15'),
(5907, 18, 2174, 34, 'وَكَانَ لَهُۥ ثَمَرࣱ فَقَالَ لِصَـٰحِبِهِۦ وَهُوَ یُحَاوِرُهُۥۤ أَنَا۠ أَكۡثَرُ مِنكَ مَالࣰا وَأَعَزُّ نَفَرࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:15'),
(5908, 18, 2175, 35, 'وَدَخَلَ جَنَّتَهُۥ وَهُوَ ظَالِمࣱ لِّنَفۡسِهِۦ قَالَ مَاۤ أَظُنُّ أَن تَبِیدَ هَـٰذِهِۦۤ أَبَدࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:15'),
(5909, 18, 2176, 36, 'وَمَاۤ أَظُنُّ ٱلسَّاعَةَ قَاۤىِٕمَةࣰ وَلَىِٕن رُّدِدتُّ إِلَىٰ رَبِّی لَأَجِدَنَّ خَیۡرࣰا مِّنۡهَا مُنقَلَبࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:15'),
(5910, 18, 2177, 37, 'قَالَ لَهُۥ صَاحِبُهُۥ وَهُوَ یُحَاوِرُهُۥۤ أَكَفَرۡتَ بِٱلَّذِی خَلَقَكَ مِن تُرَابࣲ ثُمَّ مِن نُّطۡفَةࣲ ثُمَّ سَوَّىٰكَ رَجُلࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:15'),
(5911, 18, 2178, 38, 'لَّـٰكِنَّا۠ هُوَ ٱللَّهُ رَبِّی وَلَاۤ أُشۡرِكُ بِرَبِّیۤ أَحَدࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5912, 18, 2179, 39, 'وَلَوۡلَاۤ إِذۡ دَخَلۡتَ جَنَّتَكَ قُلۡتَ مَا شَاۤءَ ٱللَّهُ لَا قُوَّةَ إِلَّا بِٱللَّهِۚ إِن تَرَنِ أَنَا۠ أَقَلَّ مِنكَ مَالࣰا وَوَلَدࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5913, 18, 2180, 40, 'فَعَسَىٰ رَبِّیۤ أَن یُؤۡتِیَنِ خَیۡرࣰا مِّن جَنَّتِكَ وَیُرۡسِلَ عَلَیۡهَا حُسۡبَانࣰا مِّنَ ٱلسَّمَاۤءِ فَتُصۡبِحَ صَعِیدࣰا زَلَقًا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5914, 18, 2181, 41, 'أَوۡ یُصۡبِحَ مَاۤؤُهَا غَوۡرࣰا فَلَن تَسۡتَطِیعَ لَهُۥ طَلَبࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5915, 18, 2182, 42, 'وَأُحِیطَ بِثَمَرِهِۦ فَأَصۡبَحَ یُقَلِّبُ كَفَّیۡهِ عَلَىٰ مَاۤ أَنفَقَ فِیهَا وَهِیَ خَاوِیَةٌ عَلَىٰ عُرُوشِهَا وَیَقُولُ یَـٰلَیۡتَنِی لَمۡ أُشۡرِكۡ بِرَبِّیۤ أَحَدࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5916, 18, 2183, 43, 'وَلَمۡ تَكُن لَّهُۥ فِئَةࣱ یَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مُنتَصِرًا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5917, 18, 2184, 44, 'هُنَالِكَ ٱلۡوَلَـٰیَةُ لِلَّهِ ٱلۡحَقِّۚ هُوَ خَیۡرࣱ ثَوَابࣰا وَخَیۡرٌ عُقۡبࣰا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5918, 18, 2185, 45, 'وَٱضۡرِبۡ لَهُم مَّثَلَ ٱلۡحَیَوٰةِ ٱلدُّنۡیَا كَمَاۤءٍ أَنزَلۡنَـٰهُ مِنَ ٱلسَّمَاۤءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ فَأَصۡبَحَ هَشِیمࣰا تَذۡرُوهُ ٱلرِّیَـٰحُۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَیۡءࣲ مُّقۡتَدِرًا\n', NULL, 0, '2023-05-27 10:26:23', '2023-05-27 11:14:16'),
(5919, 18, 2186, 46, 'ٱلۡمَالُ وَٱلۡبَنُونَ زِینَةُ ٱلۡحَیَوٰةِ ٱلدُّنۡیَاۖ وَٱلۡبَـٰقِیَـٰتُ ٱلصَّـٰلِحَـٰتُ خَیۡرٌ عِندَ رَبِّكَ ثَوَابࣰا وَخَیۡرٌ أَمَلࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:16'),
(5920, 18, 2187, 47, 'وَیَوۡمَ نُسَیِّرُ ٱلۡجِبَالَ وَتَرَى ٱلۡأَرۡضَ بَارِزَةࣰ وَحَشَرۡنَـٰهُمۡ فَلَمۡ نُغَادِرۡ مِنۡهُمۡ أَحَدࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:16'),
(5921, 18, 2188, 48, 'وَعُرِضُوا۟ عَلَىٰ رَبِّكَ صَفࣰّا لَّقَدۡ جِئۡتُمُونَا كَمَا خَلَقۡنَـٰكُمۡ أَوَّلَ مَرَّةِۭۚ بَلۡ زَعَمۡتُمۡ أَلَّن نَّجۡعَلَ لَكُم مَّوۡعِدࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:16'),
(5922, 18, 2189, 49, 'وَوُضِعَ ٱلۡكِتَـٰبُ فَتَرَى ٱلۡمُجۡرِمِینَ مُشۡفِقِینَ مِمَّا فِیهِ وَیَقُولُونَ یَـٰوَیۡلَتَنَا مَالِ هَـٰذَا ٱلۡكِتَـٰبِ لَا یُغَادِرُ صَغِیرَةࣰ وَلَا كَبِیرَةً إِلَّاۤ أَحۡصَىٰهَاۚ وَوَجَدُوا۟ مَا عَمِلُوا۟ حَاضِرࣰاۗ وَلَا یَظۡلِمُ رَبُّكَ أَحَدࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:16'),
(5923, 18, 2190, 50, 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰۤىِٕكَةِ ٱسۡجُدُوا۟ لِـَٔادَمَ فَسَجَدُوۤا۟ إِلَّاۤ إِبۡلِیسَ كَانَ مِنَ ٱلۡجِنِّ فَفَسَقَ عَنۡ أَمۡرِ رَبِّهِۦۤۗ أَفَتَتَّخِذُونَهُۥ وَذُرِّیَّتَهُۥۤ أَوۡلِیَاۤءَ مِن دُونِی وَهُمۡ لَكُمۡ عَدُوُّۢۚ بِئۡسَ لِلظَّـٰلِمِینَ بَدَلࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:16'),
(5924, 18, 2191, 51, '۞ مَّاۤ أَشۡهَدتُّهُمۡ خَلۡقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَلَا خَلۡقَ أَنفُسِهِمۡ وَمَا كُنتُ مُتَّخِذَ ٱلۡمُضِلِّینَ عَضُدࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:16'),
(5925, 18, 2192, 52, 'وَیَوۡمَ یَقُولُ نَادُوا۟ شُرَكَاۤءِیَ ٱلَّذِینَ زَعَمۡتُمۡ فَدَعَوۡهُمۡ فَلَمۡ یَسۡتَجِیبُوا۟ لَهُمۡ وَجَعَلۡنَا بَیۡنَهُم مَّوۡبِقࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5926, 18, 2193, 53, 'وَرَءَا ٱلۡمُجۡرِمُونَ ٱلنَّارَ فَظَنُّوۤا۟ أَنَّهُم مُّوَاقِعُوهَا وَلَمۡ یَجِدُوا۟ عَنۡهَا مَصۡرِفࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5927, 18, 2194, 54, 'وَلَقَدۡ صَرَّفۡنَا فِی هَـٰذَا ٱلۡقُرۡءَانِ لِلنَّاسِ مِن كُلِّ مَثَلࣲۚ وَكَانَ ٱلۡإِنسَـٰنُ أَكۡثَرَ شَیۡءࣲ جَدَلࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5928, 18, 2195, 55, 'وَمَا مَنَعَ ٱلنَّاسَ أَن یُؤۡمِنُوۤا۟ إِذۡ جَاۤءَهُمُ ٱلۡهُدَىٰ وَیَسۡتَغۡفِرُوا۟ رَبَّهُمۡ إِلَّاۤ أَن تَأۡتِیَهُمۡ سُنَّةُ ٱلۡأَوَّلِینَ أَوۡ یَأۡتِیَهُمُ ٱلۡعَذَابُ قُبُلࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5929, 18, 2196, 56, 'وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِینَ إِلَّا مُبَشِّرِینَ وَمُنذِرِینَۚ وَیُجَـٰدِلُ ٱلَّذِینَ كَفَرُوا۟ بِٱلۡبَـٰطِلِ لِیُدۡحِضُوا۟ بِهِ ٱلۡحَقَّۖ وَٱتَّخَذُوۤا۟ ءَایَـٰتِی وَمَاۤ أُنذِرُوا۟ هُزُوࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5930, 18, 2197, 57, 'وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔایَـٰتِ رَبِّهِۦ فَأَعۡرَضَ عَنۡهَا وَنَسِیَ مَا قَدَّمَتۡ یَدَاهُۚ إِنَّا جَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن یَفۡقَهُوهُ وَفِیۤ ءَاذَانِهِمۡ وَقۡرࣰاۖ وَإِن تَدۡعُهُمۡ إِلَى ٱلۡهُدَىٰ فَلَن یَهۡتَدُوۤا۟ إِذًا أَبَدࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5931, 18, 2198, 58, 'وَرَبُّكَ ٱلۡغَفُورُ ذُو ٱلرَّحۡمَةِۖ لَوۡ یُؤَاخِذُهُم بِمَا كَسَبُوا۟ لَعَجَّلَ لَهُمُ ٱلۡعَذَابَۚ بَل لَّهُم مَّوۡعِدࣱ لَّن یَجِدُوا۟ مِن دُونِهِۦ مَوۡىِٕلࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5932, 18, 2199, 59, 'وَتِلۡكَ ٱلۡقُرَىٰۤ أَهۡلَكۡنَـٰهُمۡ لَمَّا ظَلَمُوا۟ وَجَعَلۡنَا لِمَهۡلِكِهِم مَّوۡعِدࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5933, 18, 2200, 60, 'وَإِذۡ قَالَ مُوسَىٰ لِفَتَىٰهُ لَاۤ أَبۡرَحُ حَتَّىٰۤ أَبۡلُغَ مَجۡمَعَ ٱلۡبَحۡرَیۡنِ أَوۡ أَمۡضِیَ حُقُبࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5934, 18, 2201, 61, 'فَلَمَّا بَلَغَا مَجۡمَعَ بَیۡنِهِمَا نَسِیَا حُوتَهُمَا فَٱتَّخَذَ سَبِیلَهُۥ فِی ٱلۡبَحۡرِ سَرَبࣰا\n', NULL, 0, '2023-05-27 10:26:24', '2023-05-27 11:14:17'),
(5935, 18, 2202, 62, 'فَلَمَّا جَاوَزَا قَالَ لِفَتَىٰهُ ءَاتِنَا غَدَاۤءَنَا لَقَدۡ لَقِینَا مِن سَفَرِنَا هَـٰذَا نَصَبࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:17'),
(5936, 18, 2203, 63, 'قَالَ أَرَءَیۡتَ إِذۡ أَوَیۡنَاۤ إِلَى ٱلصَّخۡرَةِ فَإِنِّی نَسِیتُ ٱلۡحُوتَ وَمَاۤ أَنسَىٰنِیهُ إِلَّا ٱلشَّیۡطَـٰنُ أَنۡ أَذۡكُرَهُۥۚ وَٱتَّخَذَ سَبِیلَهُۥ فِی ٱلۡبَحۡرِ عَجَبࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:17'),
(5937, 18, 2204, 64, 'قَالَ ذَ ٰ⁠لِكَ مَا كُنَّا نَبۡغِۚ فَٱرۡتَدَّا عَلَىٰۤ ءَاثَارِهِمَا قَصَصࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:17'),
(5938, 18, 2205, 65, 'فَوَجَدَا عَبۡدࣰا مِّنۡ عِبَادِنَاۤ ءَاتَیۡنَـٰهُ رَحۡمَةࣰ مِّنۡ عِندِنَا وَعَلَّمۡنَـٰهُ مِن لَّدُنَّا عِلۡمࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:17'),
(5939, 18, 2206, 66, 'قَالَ لَهُۥ مُوسَىٰ هَلۡ أَتَّبِعُكَ عَلَىٰۤ أَن تُعَلِّمَنِ مِمَّا عُلِّمۡتَ رُشۡدࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:17'),
(5940, 18, 2207, 67, 'قَالَ إِنَّكَ لَن تَسۡتَطِیعَ مَعِیَ صَبۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5941, 18, 2208, 68, 'وَكَیۡفَ تَصۡبِرُ عَلَىٰ مَا لَمۡ تُحِطۡ بِهِۦ خُبۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5942, 18, 2209, 69, 'قَالَ سَتَجِدُنِیۤ إِن شَاۤءَ ٱللَّهُ صَابِرࣰا وَلَاۤ أَعۡصِی لَكَ أَمۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5943, 18, 2210, 70, 'قَالَ فَإِنِ ٱتَّبَعۡتَنِی فَلَا تَسۡـَٔلۡنِی عَن شَیۡءٍ حَتَّىٰۤ أُحۡدِثَ لَكَ مِنۡهُ ذِكۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5944, 18, 2211, 71, 'فَٱنطَلَقَا حَتَّىٰۤ إِذَا رَكِبَا فِی ٱلسَّفِینَةِ خَرَقَهَاۖ قَالَ أَخَرَقۡتَهَا لِتُغۡرِقَ أَهۡلَهَا لَقَدۡ جِئۡتَ شَیۡـًٔا إِمۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5945, 18, 2212, 72, 'قَالَ أَلَمۡ أَقُلۡ إِنَّكَ لَن تَسۡتَطِیعَ مَعِیَ صَبۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5946, 18, 2213, 73, 'قَالَ لَا تُؤَاخِذۡنِی بِمَا نَسِیتُ وَلَا تُرۡهِقۡنِی مِنۡ أَمۡرِی عُسۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5947, 18, 2214, 74, 'فَٱنطَلَقَا حَتَّىٰۤ إِذَا لَقِیَا غُلَـٰمࣰا فَقَتَلَهُۥ قَالَ أَقَتَلۡتَ نَفۡسࣰا زَكِیَّةَۢ بِغَیۡرِ نَفۡسࣲ لَّقَدۡ جِئۡتَ شَیۡـࣰٔا نُّكۡرࣰا\n', NULL, 0, '2023-05-27 10:26:25', '2023-05-27 11:14:18'),
(5948, 18, 2215, 75, '۞ قَالَ أَلَمۡ أَقُل لَّكَ إِنَّكَ لَن تَسۡتَطِیعَ مَعِیَ صَبۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18'),
(5949, 18, 2216, 76, 'قَالَ إِن سَأَلۡتُكَ عَن شَیۡءِۭ بَعۡدَهَا فَلَا تُصَـٰحِبۡنِیۖ قَدۡ بَلَغۡتَ مِن لَّدُنِّی عُذۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18'),
(5950, 18, 2217, 77, 'فَٱنطَلَقَا حَتَّىٰۤ إِذَاۤ أَتَیَاۤ أَهۡلَ قَرۡیَةٍ ٱسۡتَطۡعَمَاۤ أَهۡلَهَا فَأَبَوۡا۟ أَن یُضَیِّفُوهُمَا فَوَجَدَا فِیهَا جِدَارࣰا یُرِیدُ أَن یَنقَضَّ فَأَقَامَهُۥۖ قَالَ لَوۡ شِئۡتَ لَتَّخَذۡتَ عَلَیۡهِ أَجۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18'),
(5951, 18, 2218, 78, 'قَالَ هَـٰذَا فِرَاقُ بَیۡنِی وَبَیۡنِكَۚ سَأُنَبِّئُكَ بِتَأۡوِیلِ مَا لَمۡ تَسۡتَطِع عَّلَیۡهِ صَبۡرًا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(5952, 18, 2219, 79, 'أَمَّا ٱلسَّفِینَةُ فَكَانَتۡ لِمَسَـٰكِینَ یَعۡمَلُونَ فِی ٱلۡبَحۡرِ فَأَرَدتُّ أَنۡ أَعِیبَهَا وَكَانَ وَرَاۤءَهُم مَّلِكࣱ یَأۡخُذُ كُلَّ سَفِینَةٍ غَصۡبࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18'),
(5953, 18, 2220, 80, 'وَأَمَّا ٱلۡغُلَـٰمُ فَكَانَ أَبَوَاهُ مُؤۡمِنَیۡنِ فَخَشِینَاۤ أَن یُرۡهِقَهُمَا طُغۡیَـٰنࣰا وَكُفۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18'),
(5954, 18, 2221, 81, 'فَأَرَدۡنَاۤ أَن یُبۡدِلَهُمَا رَبُّهُمَا خَیۡرࣰا مِّنۡهُ زَكَوٰةࣰ وَأَقۡرَبَ رُحۡمࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18'),
(5955, 18, 2222, 82, 'وَأَمَّا ٱلۡجِدَارُ فَكَانَ لِغُلَـٰمَیۡنِ یَتِیمَیۡنِ فِی ٱلۡمَدِینَةِ وَكَانَ تَحۡتَهُۥ كَنزࣱ لَّهُمَا وَكَانَ أَبُوهُمَا صَـٰلِحࣰا فَأَرَادَ رَبُّكَ أَن یَبۡلُغَاۤ أَشُدَّهُمَا وَیَسۡتَخۡرِجَا كَنزَهُمَا رَحۡمَةࣰ مِّن رَّبِّكَۚ وَمَا فَعَلۡتُهُۥ عَنۡ أَمۡرِیۚ ذَ ٰ⁠لِكَ تَأۡوِیلُ مَا لَمۡ تَسۡطِع عَّلَیۡهِ صَبۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:18'),
(5956, 18, 2223, 83, 'وَیَسۡـَٔلُونَكَ عَن ذِی ٱلۡقَرۡنَیۡنِۖ قُلۡ سَأَتۡلُوا۟ عَلَیۡكُم مِّنۡهُ ذِكۡرًا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5957, 18, 2224, 84, 'إِنَّا مَكَّنَّا لَهُۥ فِی ٱلۡأَرۡضِ وَءَاتَیۡنَـٰهُ مِن كُلِّ شَیۡءࣲ سَبَبࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5958, 18, 2225, 85, 'فَأَتۡبَعَ سَبَبًا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5959, 18, 2226, 86, 'حَتَّىٰۤ إِذَا بَلَغَ مَغۡرِبَ ٱلشَّمۡسِ وَجَدَهَا تَغۡرُبُ فِی عَیۡنٍ حَمِئَةࣲ وَوَجَدَ عِندَهَا قَوۡمࣰاۖ قُلۡنَا یَـٰذَا ٱلۡقَرۡنَیۡنِ إِمَّاۤ أَن تُعَذِّبَ وَإِمَّاۤ أَن تَتَّخِذَ فِیهِمۡ حُسۡنࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5960, 18, 2227, 87, 'قَالَ أَمَّا مَن ظَلَمَ فَسَوۡفَ نُعَذِّبُهُۥ ثُمَّ یُرَدُّ إِلَىٰ رَبِّهِۦ فَیُعَذِّبُهُۥ عَذَابࣰا نُّكۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5961, 18, 2228, 88, 'وَأَمَّا مَنۡ ءَامَنَ وَعَمِلَ صَـٰلِحࣰا فَلَهُۥ جَزَاۤءً ٱلۡحُسۡنَىٰۖ وَسَنَقُولُ لَهُۥ مِنۡ أَمۡرِنَا یُسۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5962, 18, 2229, 89, 'ثُمَّ أَتۡبَعَ سَبَبًا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5963, 18, 2230, 90, 'حَتَّىٰۤ إِذَا بَلَغَ مَطۡلِعَ ٱلشَّمۡسِ وَجَدَهَا تَطۡلُعُ عَلَىٰ قَوۡمࣲ لَّمۡ نَجۡعَل لَّهُم مِّن دُونِهَا سِتۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5964, 18, 2231, 91, 'كَذَ ٰ⁠لِكَۖ وَقَدۡ أَحَطۡنَا بِمَا لَدَیۡهِ خُبۡرࣰا\n', NULL, 0, '2023-05-27 10:26:26', '2023-05-27 11:14:19'),
(5965, 18, 2232, 92, 'ثُمَّ أَتۡبَعَ سَبَبًا\n', NULL, 0, '2023-05-27 10:26:27', '2023-05-27 11:14:19'),
(5966, 18, 2233, 93, 'حَتَّىٰۤ إِذَا بَلَغَ بَیۡنَ ٱلسَّدَّیۡنِ وَجَدَ مِن دُونِهِمَا قَوۡمࣰا لَّا یَكَادُونَ یَفۡقَهُونَ قَوۡلࣰا\n', NULL, 0, '2023-05-27 10:26:27', '2023-05-27 11:14:19'),
(5967, 18, 2234, 94, 'قَالُوا۟ یَـٰذَا ٱلۡقَرۡنَیۡنِ إِنَّ یَأۡجُوجَ وَمَأۡجُوجَ مُفۡسِدُونَ فِی ٱلۡأَرۡضِ فَهَلۡ نَجۡعَلُ لَكَ خَرۡجًا عَلَىٰۤ أَن تَجۡعَلَ بَیۡنَنَا وَبَیۡنَهُمۡ سَدࣰّا\n', NULL, 0, '2023-05-27 10:26:27', '2023-05-27 11:14:19'),
(5968, 18, 2235, 95, 'قَالَ مَا مَكَّنِّی فِیهِ رَبِّی خَیۡرࣱ فَأَعِینُونِی بِقُوَّةٍ أَجۡعَلۡ بَیۡنَكُمۡ وَبَیۡنَهُمۡ رَدۡمًا\n', NULL, 0, '2023-05-27 10:26:27', '2023-05-27 11:14:20'),
(5969, 18, 2236, 96, 'ءَاتُونِی زُبَرَ ٱلۡحَدِیدِۖ حَتَّىٰۤ إِذَا سَاوَىٰ بَیۡنَ ٱلصَّدَفَیۡنِ قَالَ ٱنفُخُوا۟ۖ حَتَّىٰۤ إِذَا جَعَلَهُۥ نَارࣰا قَالَ ءَاتُونِیۤ أُفۡرِغۡ عَلَیۡهِ قِطۡرࣰا\n', NULL, 0, '2023-05-27 10:26:27', '2023-05-27 11:14:20'),
(5970, 18, 2237, 97, 'فَمَا ٱسۡطَـٰعُوۤا۟ أَن یَظۡهَرُوهُ وَمَا ٱسۡتَطَـٰعُوا۟ لَهُۥ نَقۡبࣰا\n', NULL, 0, '2023-05-27 10:26:27', '2023-05-27 11:14:20'),
(5971, 18, 2238, 98, 'قَالَ هَـٰذَا رَحۡمَةࣱ مِّن رَّبِّیۖ فَإِذَا جَاۤءَ وَعۡدُ رَبِّی جَعَلَهُۥ دَكَّاۤءَۖ وَكَانَ وَعۡدُ رَبِّی حَقࣰّا\n', NULL, 0, '2023-05-27 10:26:27', '2023-05-27 11:14:20'),
(5972, 18, 2239, 99, '۞ وَتَرَكۡنَا بَعۡضَهُمۡ یَوۡمَىِٕذࣲ یَمُوجُ فِی بَعۡضࣲۖ وَنُفِخَ فِی ٱلصُّورِ فَجَمَعۡنَـٰهُمۡ جَمۡعࣰا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5973, 18, 2240, 100, 'وَعَرَضۡنَا جَهَنَّمَ یَوۡمَىِٕذࣲ لِّلۡكَـٰفِرِینَ عَرۡضًا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5974, 18, 2241, 101, 'ٱلَّذِینَ كَانَتۡ أَعۡیُنُهُمۡ فِی غِطَاۤءٍ عَن ذِكۡرِی وَكَانُوا۟ لَا یَسۡتَطِیعُونَ سَمۡعًا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5975, 18, 2242, 102, 'أَفَحَسِبَ ٱلَّذِینَ كَفَرُوۤا۟ أَن یَتَّخِذُوا۟ عِبَادِی مِن دُونِیۤ أَوۡلِیَاۤءَۚ إِنَّاۤ أَعۡتَدۡنَا جَهَنَّمَ لِلۡكَـٰفِرِینَ نُزُلࣰا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5976, 18, 2243, 103, 'قُلۡ هَلۡ نُنَبِّئُكُم بِٱلۡأَخۡسَرِینَ أَعۡمَـٰلًا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5977, 18, 2244, 104, 'ٱلَّذِینَ ضَلَّ سَعۡیُهُمۡ فِی ٱلۡحَیَوٰةِ ٱلدُّنۡیَا وَهُمۡ یَحۡسَبُونَ أَنَّهُمۡ یُحۡسِنُونَ صُنۡعًا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5978, 18, 2245, 105, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ كَفَرُوا۟ بِـَٔایَـٰتِ رَبِّهِمۡ وَلِقَاۤىِٕهِۦ فَحَبِطَتۡ أَعۡمَـٰلُهُمۡ فَلَا نُقِیمُ لَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ وَزۡنࣰا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5979, 18, 2246, 106, 'ذَ ٰ⁠لِكَ جَزَاۤؤُهُمۡ جَهَنَّمُ بِمَا كَفَرُوا۟ وَٱتَّخَذُوۤا۟ ءَایَـٰتِی وَرُسُلِی هُزُوًا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5980, 18, 2247, 107, 'إِنَّ ٱلَّذِینَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ كَانَتۡ لَهُمۡ جَنَّـٰتُ ٱلۡفِرۡدَوۡسِ نُزُلًا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5981, 18, 2248, 108, 'خَـٰلِدِینَ فِیهَا لَا یَبۡغُونَ عَنۡهَا حِوَلࣰا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5982, 18, 2249, 109, 'قُل لَّوۡ كَانَ ٱلۡبَحۡرُ مِدَادࣰا لِّكَلِمَـٰتِ رَبِّی لَنَفِدَ ٱلۡبَحۡرُ قَبۡلَ أَن تَنفَدَ كَلِمَـٰتُ رَبِّی وَلَوۡ جِئۡنَا بِمِثۡلِهِۦ مَدَدࣰا\n', NULL, 0, '2023-05-27 10:26:28', '2023-05-27 11:14:20'),
(5983, 18, 2250, 110, 'قُلۡ إِنَّمَاۤ أَنَا۠ بَشَرࣱ مِّثۡلُكُمۡ یُوحَىٰۤ إِلَیَّ أَنَّمَاۤ إِلَـٰهُكُمۡ إِلَـٰهࣱ وَ ٰ⁠حِدࣱۖ فَمَن كَانَ یَرۡجُوا۟ لِقَاۤءَ رَبِّهِۦ فَلۡیَعۡمَلۡ عَمَلࣰا صَـٰلِحࣰا وَلَا یُشۡرِكۡ بِعِبَادَةِ رَبِّهِۦۤ أَحَدَۢا\n', NULL, 0, '2023-05-27 10:26:29', '2023-05-27 11:14:21'),
(5984, 17, 2030, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ سُبۡحَـٰنَ ٱلَّذِیۤ أَسۡرَىٰ بِعَبۡدِهِۦ لَیۡلࣰا مِّنَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ إِلَى ٱلۡمَسۡجِدِ ٱلۡأَقۡصَا ٱلَّذِی بَـٰرَكۡنَا حَوۡلَهُۥ لِنُرِیَهُۥ مِنۡ ءَایَـٰتِنَاۤۚ إِنَّهُۥ هُوَ ٱلسَّمِیعُ ٱلۡبَصِیرُ\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5985, 17, 2031, 2, 'وَءَاتَیۡنَا مُوسَى ٱلۡكِتَـٰبَ وَجَعَلۡنَـٰهُ هُدࣰى لِّبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ أَلَّا تَتَّخِذُوا۟ مِن دُونِی وَكِیلࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5986, 17, 2032, 3, 'ذُرِّیَّةَ مَنۡ حَمَلۡنَا مَعَ نُوحٍۚ إِنَّهُۥ كَانَ عَبۡدࣰا شَكُورࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5987, 17, 2033, 4, 'وَقَضَیۡنَاۤ إِلَىٰ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ فِی ٱلۡكِتَـٰبِ لَتُفۡسِدُنَّ فِی ٱلۡأَرۡضِ مَرَّتَیۡنِ وَلَتَعۡلُنَّ عُلُوࣰّا كَبِیرࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5988, 17, 2034, 5, 'فَإِذَا جَاۤءَ وَعۡدُ أُولَىٰهُمَا بَعَثۡنَا عَلَیۡكُمۡ عِبَادࣰا لَّنَاۤ أُو۟لِی بَأۡسࣲ شَدِیدࣲ فَجَاسُوا۟ خِلَـٰلَ ٱلدِّیَارِۚ وَكَانَ وَعۡدࣰا مَّفۡعُولࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5989, 17, 2035, 6, 'ثُمَّ رَدَدۡنَا لَكُمُ ٱلۡكَرَّةَ عَلَیۡهِمۡ وَأَمۡدَدۡنَـٰكُم بِأَمۡوَ ٰ⁠لࣲ وَبَنِینَ وَجَعَلۡنَـٰكُمۡ أَكۡثَرَ نَفِیرًا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5990, 17, 2036, 7, 'إِنۡ أَحۡسَنتُمۡ أَحۡسَنتُمۡ لِأَنفُسِكُمۡۖ وَإِنۡ أَسَأۡتُمۡ فَلَهَاۚ فَإِذَا جَاۤءَ وَعۡدُ ٱلۡـَٔاخِرَةِ لِیَسُـࣳۤـُٔوا۟ وُجُوهَكُمۡ وَلِیَدۡخُلُوا۟ ٱلۡمَسۡجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةࣲ وَلِیُتَبِّرُوا۟ مَا عَلَوۡا۟ تَتۡبِیرًا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5991, 17, 2037, 8, 'عَسَىٰ رَبُّكُمۡ أَن یَرۡحَمَكُمۡۚ وَإِنۡ عُدتُّمۡ عُدۡنَاۚ وَجَعَلۡنَا جَهَنَّمَ لِلۡكَـٰفِرِینَ حَصِیرًا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5992, 17, 2038, 9, 'إِنَّ هَـٰذَا ٱلۡقُرۡءَانَ یَهۡدِی لِلَّتِی هِیَ أَقۡوَمُ وَیُبَشِّرُ ٱلۡمُؤۡمِنِینَ ٱلَّذِینَ یَعۡمَلُونَ ٱلصَّـٰلِحَـٰتِ أَنَّ لَهُمۡ أَجۡرࣰا كَبِیرࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5993, 17, 2039, 10, 'وَأَنَّ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِیمࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5994, 17, 2040, 11, 'وَیَدۡعُ ٱلۡإِنسَـٰنُ بِٱلشَّرِّ دُعَاۤءَهُۥ بِٱلۡخَیۡرِۖ وَكَانَ ٱلۡإِنسَـٰنُ عَجُولࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:24'),
(5995, 17, 2041, 12, 'وَجَعَلۡنَا ٱلَّیۡلَ وَٱلنَّهَارَ ءَایَتَیۡنِۖ فَمَحَوۡنَاۤ ءَایَةَ ٱلَّیۡلِ وَجَعَلۡنَاۤ ءَایَةَ ٱلنَّهَارِ مُبۡصِرَةࣰ لِّتَبۡتَغُوا۟ فَضۡلࣰا مِّن رَّبِّكُمۡ وَلِتَعۡلَمُوا۟ عَدَدَ ٱلسِّنِینَ وَٱلۡحِسَابَۚ وَكُلَّ شَیۡءࣲ فَصَّلۡنَـٰهُ تَفۡصِیلࣰا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:25'),
(5996, 17, 2042, 13, 'وَكُلَّ إِنسَـٰنٍ أَلۡزَمۡنَـٰهُ طَـٰۤىِٕرَهُۥ فِی عُنُقِهِۦۖ وَنُخۡرِجُ لَهُۥ یَوۡمَ ٱلۡقِیَـٰمَةِ كِتَـٰبࣰا یَلۡقَىٰهُ مَنشُورًا\n', NULL, 0, '2023-05-27 10:26:33', '2023-05-27 11:14:25'),
(5997, 17, 2043, 14, 'ٱقۡرَأۡ كِتَـٰبَكَ كَفَىٰ بِنَفۡسِكَ ٱلۡیَوۡمَ عَلَیۡكَ حَسِیبࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(5998, 17, 2044, 15, 'مَّنِ ٱهۡتَدَىٰ فَإِنَّمَا یَهۡتَدِی لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا یَضِلُّ عَلَیۡهَاۚ وَلَا تَزِرُ وَازِرَةࣱ وِزۡرَ أُخۡرَىٰۗ وَمَا كُنَّا مُعَذِّبِینَ حَتَّىٰ نَبۡعَثَ رَسُولࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(5999, 17, 2045, 16, 'وَإِذَاۤ أَرَدۡنَاۤ أَن نُّهۡلِكَ قَرۡیَةً أَمَرۡنَا مُتۡرَفِیهَا فَفَسَقُوا۟ فِیهَا فَحَقَّ عَلَیۡهَا ٱلۡقَوۡلُ فَدَمَّرۡنَـٰهَا تَدۡمِیرࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6000, 17, 2046, 17, 'وَكَمۡ أَهۡلَكۡنَا مِنَ ٱلۡقُرُونِ مِنۢ بَعۡدِ نُوحࣲۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِۦ خَبِیرَۢا بَصِیرࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6001, 17, 2047, 18, 'مَّن كَانَ یُرِیدُ ٱلۡعَاجِلَةَ عَجَّلۡنَا لَهُۥ فِیهَا مَا نَشَاۤءُ لِمَن نُّرِیدُ ثُمَّ جَعَلۡنَا لَهُۥ جَهَنَّمَ یَصۡلَىٰهَا مَذۡمُومࣰا مَّدۡحُورࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6002, 17, 2048, 19, 'وَمَنۡ أَرَادَ ٱلۡـَٔاخِرَةَ وَسَعَىٰ لَهَا سَعۡیَهَا وَهُوَ مُؤۡمِنࣱ فَأُو۟لَـٰۤىِٕكَ كَانَ سَعۡیُهُم مَّشۡكُورࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6003, 17, 2049, 20, 'كُلࣰّا نُّمِدُّ هَـٰۤؤُلَاۤءِ وَهَـٰۤؤُلَاۤءِ مِنۡ عَطَاۤءِ رَبِّكَۚ وَمَا كَانَ عَطَاۤءُ رَبِّكَ مَحۡظُورًا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6004, 17, 2050, 21, 'ٱنظُرۡ كَیۡفَ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضࣲۚ وَلَلۡـَٔاخِرَةُ أَكۡبَرُ دَرَجَـٰتࣲ وَأَكۡبَرُ تَفۡضِیلࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6005, 17, 2051, 22, 'لَّا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ فَتَقۡعُدَ مَذۡمُومࣰا مَّخۡذُولࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6006, 17, 2052, 23, '۞ وَقَضَىٰ رَبُّكَ أَلَّا تَعۡبُدُوۤا۟ إِلَّاۤ إِیَّاهُ وَبِٱلۡوَ ٰ⁠لِدَیۡنِ إِحۡسَـٰنًاۚ إِمَّا یَبۡلُغَنَّ عِندَكَ ٱلۡكِبَرَ أَحَدُهُمَاۤ أَوۡ كِلَاهُمَا فَلَا تَقُل لَّهُمَاۤ أُفࣲّ وَلَا تَنۡهَرۡهُمَا وَقُل لَّهُمَا قَوۡلࣰا كَرِیمࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6007, 17, 2053, 24, 'وَٱخۡفِضۡ لَهُمَا جَنَاحَ ٱلذُّلِّ مِنَ ٱلرَّحۡمَةِ وَقُل رَّبِّ ٱرۡحَمۡهُمَا كَمَا رَبَّیَانِی صَغِیرࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6008, 17, 2054, 25, 'رَّبُّكُمۡ أَعۡلَمُ بِمَا فِی نُفُوسِكُمۡۚ إِن تَكُونُوا۟ صَـٰلِحِینَ فَإِنَّهُۥ كَانَ لِلۡأَوَّ ٰ⁠بِینَ غَفُورࣰا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:25'),
(6009, 17, 2055, 26, 'وَءَاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِینَ وَٱبۡنَ ٱلسَّبِیلِ وَلَا تُبَذِّرۡ تَبۡذِیرًا\n', NULL, 0, '2023-05-27 10:26:34', '2023-05-27 11:14:26'),
(6010, 17, 2056, 27, 'إِنَّ ٱلۡمُبَذِّرِینَ كَانُوۤا۟ إِخۡوَ ٰ⁠نَ ٱلشَّیَـٰطِینِۖ وَكَانَ ٱلشَّیۡطَـٰنُ لِرَبِّهِۦ كَفُورࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6011, 17, 2057, 28, 'وَإِمَّا تُعۡرِضَنَّ عَنۡهُمُ ٱبۡتِغَاۤءَ رَحۡمَةࣲ مِّن رَّبِّكَ تَرۡجُوهَا فَقُل لَّهُمۡ قَوۡلࣰا مَّیۡسُورࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6012, 17, 2058, 29, 'وَلَا تَجۡعَلۡ یَدَكَ مَغۡلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبۡسُطۡهَا كُلَّ ٱلۡبَسۡطِ فَتَقۡعُدَ مَلُومࣰا مَّحۡسُورًا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6013, 17, 2059, 30, 'إِنَّ رَبَّكَ یَبۡسُطُ ٱلرِّزۡقَ لِمَن یَشَاۤءُ وَیَقۡدِرُۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِیرَۢا بَصِیرࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6014, 17, 2060, 31, 'وَلَا تَقۡتُلُوۤا۟ أَوۡلَـٰدَكُمۡ خَشۡیَةَ إِمۡلَـٰقࣲۖ نَّحۡنُ نَرۡزُقُهُمۡ وَإِیَّاكُمۡۚ إِنَّ قَتۡلَهُمۡ كَانَ خِطۡـࣰٔا كَبِیرࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6015, 17, 2061, 32, 'وَلَا تَقۡرَبُوا۟ ٱلزِّنَىٰۤۖ إِنَّهُۥ كَانَ فَـٰحِشَةࣰ وَسَاۤءَ سَبِیلࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6016, 17, 2062, 33, 'وَلَا تَقۡتُلُوا۟ ٱلنَّفۡسَ ٱلَّتِی حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۗ وَمَن قُتِلَ مَظۡلُومࣰا فَقَدۡ جَعَلۡنَا لِوَلِیِّهِۦ سُلۡطَـٰنࣰا فَلَا یُسۡرِف فِّی ٱلۡقَتۡلِۖ إِنَّهُۥ كَانَ مَنصُورࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6017, 17, 2063, 34, 'وَلَا تَقۡرَبُوا۟ مَالَ ٱلۡیَتِیمِ إِلَّا بِٱلَّتِی هِیَ أَحۡسَنُ حَتَّىٰ یَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُوا۟ بِٱلۡعَهۡدِۖ إِنَّ ٱلۡعَهۡدَ كَانَ مَسۡـُٔولࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6018, 17, 2064, 35, 'وَأَوۡفُوا۟ ٱلۡكَیۡلَ إِذَا كِلۡتُمۡ وَزِنُوا۟ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِیمِۚ ذَ ٰ⁠لِكَ خَیۡرࣱ وَأَحۡسَنُ تَأۡوِیلࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6019, 17, 2065, 36, 'وَلَا تَقۡفُ مَا لَیۡسَ لَكَ بِهِۦ عِلۡمٌۚ إِنَّ ٱلسَّمۡعَ وَٱلۡبَصَرَ وَٱلۡفُؤَادَ كُلُّ أُو۟لَـٰۤىِٕكَ كَانَ عَنۡهُ مَسۡـُٔولࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6020, 17, 2066, 37, 'وَلَا تَمۡشِ فِی ٱلۡأَرۡضِ مَرَحًاۖ إِنَّكَ لَن تَخۡرِقَ ٱلۡأَرۡضَ وَلَن تَبۡلُغَ ٱلۡجِبَالَ طُولࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6021, 17, 2067, 38, 'كُلُّ ذَ ٰ⁠لِكَ كَانَ سَیِّئُهُۥ عِندَ رَبِّكَ مَكۡرُوهࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6022, 17, 2068, 39, 'ذَ ٰ⁠لِكَ مِمَّاۤ أَوۡحَىٰۤ إِلَیۡكَ رَبُّكَ مِنَ ٱلۡحِكۡمَةِۗ وَلَا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ فَتُلۡقَىٰ فِی جَهَنَّمَ مَلُومࣰا مَّدۡحُورًا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6023, 17, 2069, 40, 'أَفَأَصۡفَىٰكُمۡ رَبُّكُم بِٱلۡبَنِینَ وَٱتَّخَذَ مِنَ ٱلۡمَلَـٰۤىِٕكَةِ إِنَـٰثًاۚ إِنَّكُمۡ لَتَقُولُونَ قَوۡلًا عَظِیمࣰا\n', NULL, 0, '2023-05-27 10:26:35', '2023-05-27 11:14:26'),
(6024, 17, 2070, 41, 'وَلَقَدۡ صَرَّفۡنَا فِی هَـٰذَا ٱلۡقُرۡءَانِ لِیَذَّكَّرُوا۟ وَمَا یَزِیدُهُمۡ إِلَّا نُفُورࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6025, 17, 2071, 42, 'قُل لَّوۡ كَانَ مَعَهُۥۤ ءَالِهَةࣱ كَمَا یَقُولُونَ إِذࣰا لَّٱبۡتَغَوۡا۟ إِلَىٰ ذِی ٱلۡعَرۡشِ سَبِیلࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6026, 17, 2072, 43, 'سُبۡحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا یَقُولُونَ عُلُوࣰّا كَبِیرࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6027, 17, 2073, 44, 'تُسَبِّحُ لَهُ ٱلسَّمَـٰوَ ٰ⁠تُ ٱلسَّبۡعُ وَٱلۡأَرۡضُ وَمَن فِیهِنَّۚ وَإِن مِّن شَیۡءٍ إِلَّا یُسَبِّحُ بِحَمۡدِهِۦ وَلَـٰكِن لَّا تَفۡقَهُونَ تَسۡبِیحَهُمۡۚ إِنَّهُۥ كَانَ حَلِیمًا غَفُورࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6028, 17, 2074, 45, 'وَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ جَعَلۡنَا بَیۡنَكَ وَبَیۡنَ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ حِجَابࣰا مَّسۡتُورࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6029, 17, 2075, 46, 'وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن یَفۡقَهُوهُ وَفِیۤ ءَاذَانِهِمۡ وَقۡرࣰاۚ وَإِذَا ذَكَرۡتَ رَبَّكَ فِی ٱلۡقُرۡءَانِ وَحۡدَهُۥ وَلَّوۡا۟ عَلَىٰۤ أَدۡبَـٰرِهِمۡ نُفُورࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6030, 17, 2076, 47, 'نَّحۡنُ أَعۡلَمُ بِمَا یَسۡتَمِعُونَ بِهِۦۤ إِذۡ یَسۡتَمِعُونَ إِلَیۡكَ وَإِذۡ هُمۡ نَجۡوَىٰۤ إِذۡ یَقُولُ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلࣰا مَّسۡحُورًا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6031, 17, 2077, 48, 'ٱنظُرۡ كَیۡفَ ضَرَبُوا۟ لَكَ ٱلۡأَمۡثَالَ فَضَلُّوا۟ فَلَا یَسۡتَطِیعُونَ سَبِیلࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6032, 17, 2078, 49, 'وَقَالُوۤا۟ أَءِذَا كُنَّا عِظَـٰمࣰا وَرُفَـٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقࣰا جَدِیدࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6033, 17, 2079, 50, '۞ قُلۡ كُونُوا۟ حِجَارَةً أَوۡ حَدِیدًا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6034, 17, 2080, 51, 'أَوۡ خَلۡقࣰا مِّمَّا یَكۡبُرُ فِی صُدُورِكُمۡۚ فَسَیَقُولُونَ مَن یُعِیدُنَاۖ قُلِ ٱلَّذِی فَطَرَكُمۡ أَوَّلَ مَرَّةࣲۚ فَسَیُنۡغِضُونَ إِلَیۡكَ رُءُوسَهُمۡ وَیَقُولُونَ مَتَىٰ هُوَۖ قُلۡ عَسَىٰۤ أَن یَكُونَ قَرِیبࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6035, 17, 2081, 52, 'یَوۡمَ یَدۡعُوكُمۡ فَتَسۡتَجِیبُونَ بِحَمۡدِهِۦ وَتَظُنُّونَ إِن لَّبِثۡتُمۡ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6036, 17, 2082, 53, 'وَقُل لِّعِبَادِی یَقُولُوا۟ ٱلَّتِی هِیَ أَحۡسَنُۚ إِنَّ ٱلشَّیۡطَـٰنَ یَنزَغُ بَیۡنَهُمۡۚ إِنَّ ٱلشَّیۡطَـٰنَ كَانَ لِلۡإِنسَـٰنِ عَدُوࣰّا مُّبِینࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6037, 17, 2083, 54, 'رَّبُّكُمۡ أَعۡلَمُ بِكُمۡۖ إِن یَشَأۡ یَرۡحَمۡكُمۡ أَوۡ إِن یَشَأۡ یُعَذِّبۡكُمۡۚ وَمَاۤ أَرۡسَلۡنَـٰكَ عَلَیۡهِمۡ وَكِیلࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:27'),
(6038, 17, 2084, 55, 'وَرَبُّكَ أَعۡلَمُ بِمَن فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۗ وَلَقَدۡ فَضَّلۡنَا بَعۡضَ ٱلنَّبِیِّـۧنَ عَلَىٰ بَعۡضࣲۖ وَءَاتَیۡنَا دَاوُۥدَ زَبُورࣰا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:28'),
(6039, 17, 2085, 56, 'قُلِ ٱدۡعُوا۟ ٱلَّذِینَ زَعَمۡتُم مِّن دُونِهِۦ فَلَا یَمۡلِكُونَ كَشۡفَ ٱلضُّرِّ عَنكُمۡ وَلَا تَحۡوِیلًا\n', NULL, 0, '2023-05-27 10:26:36', '2023-05-27 11:14:28'),
(6040, 17, 2086, 57, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ یَدۡعُونَ یَبۡتَغُونَ إِلَىٰ رَبِّهِمُ ٱلۡوَسِیلَةَ أَیُّهُمۡ أَقۡرَبُ وَیَرۡجُونَ رَحۡمَتَهُۥ وَیَخَافُونَ عَذَابَهُۥۤۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحۡذُورࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6041, 17, 2087, 58, 'وَإِن مِّن قَرۡیَةٍ إِلَّا نَحۡنُ مُهۡلِكُوهَا قَبۡلَ یَوۡمِ ٱلۡقِیَـٰمَةِ أَوۡ مُعَذِّبُوهَا عَذَابࣰا شَدِیدࣰاۚ كَانَ ذَ ٰ⁠لِكَ فِی ٱلۡكِتَـٰبِ مَسۡطُورࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6042, 17, 2088, 59, 'وَمَا مَنَعَنَاۤ أَن نُّرۡسِلَ بِٱلۡـَٔایَـٰتِ إِلَّاۤ أَن كَذَّبَ بِهَا ٱلۡأَوَّلُونَۚ وَءَاتَیۡنَا ثَمُودَ ٱلنَّاقَةَ مُبۡصِرَةࣰ فَظَلَمُوا۟ بِهَاۚ وَمَا نُرۡسِلُ بِٱلۡـَٔایَـٰتِ إِلَّا تَخۡوِیفࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6043, 17, 2089, 60, 'وَإِذۡ قُلۡنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِٱلنَّاسِۚ وَمَا جَعَلۡنَا ٱلرُّءۡیَا ٱلَّتِیۤ أَرَیۡنَـٰكَ إِلَّا فِتۡنَةࣰ لِّلنَّاسِ وَٱلشَّجَرَةَ ٱلۡمَلۡعُونَةَ فِی ٱلۡقُرۡءَانِۚ وَنُخَوِّفُهُمۡ فَمَا یَزِیدُهُمۡ إِلَّا طُغۡیَـٰنࣰا كَبِیرࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6044, 17, 2090, 61, 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰۤىِٕكَةِ ٱسۡجُدُوا۟ لِـَٔادَمَ فَسَجَدُوۤا۟ إِلَّاۤ إِبۡلِیسَ قَالَ ءَأَسۡجُدُ لِمَنۡ خَلَقۡتَ طِینࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6045, 17, 2091, 62, 'قَالَ أَرَءَیۡتَكَ هَـٰذَا ٱلَّذِی كَرَّمۡتَ عَلَیَّ لَىِٕنۡ أَخَّرۡتَنِ إِلَىٰ یَوۡمِ ٱلۡقِیَـٰمَةِ لَأَحۡتَنِكَنَّ ذُرِّیَّتَهُۥۤ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6046, 17, 2092, 63, 'قَالَ ٱذۡهَبۡ فَمَن تَبِعَكَ مِنۡهُمۡ فَإِنَّ جَهَنَّمَ جَزَاۤؤُكُمۡ جَزَاۤءࣰ مَّوۡفُورࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6047, 17, 2093, 64, 'وَٱسۡتَفۡزِزۡ مَنِ ٱسۡتَطَعۡتَ مِنۡهُم بِصَوۡتِكَ وَأَجۡلِبۡ عَلَیۡهِم بِخَیۡلِكَ وَرَجِلِكَ وَشَارِكۡهُمۡ فِی ٱلۡأَمۡوَ ٰ⁠لِ وَٱلۡأَوۡلَـٰدِ وَعِدۡهُمۡۚ وَمَا یَعِدُهُمُ ٱلشَّیۡطَـٰنُ إِلَّا غُرُورًا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6048, 17, 2094, 65, 'إِنَّ عِبَادِی لَیۡسَ لَكَ عَلَیۡهِمۡ سُلۡطَـٰنࣱۚ وَكَفَىٰ بِرَبِّكَ وَكِیلࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6049, 17, 2095, 66, 'رَّبُّكُمُ ٱلَّذِی یُزۡجِی لَكُمُ ٱلۡفُلۡكَ فِی ٱلۡبَحۡرِ لِتَبۡتَغُوا۟ مِن فَضۡلِهِۦۤۚ إِنَّهُۥ كَانَ بِكُمۡ رَحِیمࣰا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6050, 17, 2096, 67, 'وَإِذَا مَسَّكُمُ ٱلضُّرُّ فِی ٱلۡبَحۡرِ ضَلَّ مَن تَدۡعُونَ إِلَّاۤ إِیَّاهُۖ فَلَمَّا نَجَّىٰكُمۡ إِلَى ٱلۡبَرِّ أَعۡرَضۡتُمۡۚ وَكَانَ ٱلۡإِنسَـٰنُ كَفُورًا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6051, 17, 2097, 68, 'أَفَأَمِنتُمۡ أَن یَخۡسِفَ بِكُمۡ جَانِبَ ٱلۡبَرِّ أَوۡ یُرۡسِلَ عَلَیۡكُمۡ حَاصِبࣰا ثُمَّ لَا تَجِدُوا۟ لَكُمۡ وَكِیلًا\n', NULL, 0, '2023-05-27 10:26:37', '2023-05-27 11:14:28'),
(6052, 17, 2098, 69, 'أَمۡ أَمِنتُمۡ أَن یُعِیدَكُمۡ فِیهِ تَارَةً أُخۡرَىٰ فَیُرۡسِلَ عَلَیۡكُمۡ قَاصِفࣰا مِّنَ ٱلرِّیحِ فَیُغۡرِقَكُم بِمَا كَفَرۡتُمۡ ثُمَّ لَا تَجِدُوا۟ لَكُمۡ عَلَیۡنَا بِهِۦ تَبِیعࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:28'),
(6053, 17, 2099, 70, '۞ وَلَقَدۡ كَرَّمۡنَا بَنِیۤ ءَادَمَ وَحَمَلۡنَـٰهُمۡ فِی ٱلۡبَرِّ وَٱلۡبَحۡرِ وَرَزَقۡنَـٰهُم مِّنَ ٱلطَّیِّبَـٰتِ وَفَضَّلۡنَـٰهُمۡ عَلَىٰ كَثِیرࣲ مِّمَّنۡ خَلَقۡنَا تَفۡضِیلࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6054, 17, 2100, 71, 'یَوۡمَ نَدۡعُوا۟ كُلَّ أُنَاسِۭ بِإِمَـٰمِهِمۡۖ فَمَنۡ أُوتِیَ كِتَـٰبَهُۥ بِیَمِینِهِۦ فَأُو۟لَـٰۤىِٕكَ یَقۡرَءُونَ كِتَـٰبَهُمۡ وَلَا یُظۡلَمُونَ فَتِیلࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6055, 17, 2101, 72, 'وَمَن كَانَ فِی هَـٰذِهِۦۤ أَعۡمَىٰ فَهُوَ فِی ٱلۡـَٔاخِرَةِ أَعۡمَىٰ وَأَضَلُّ سَبِیلࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6056, 17, 2102, 73, 'وَإِن كَادُوا۟ لَیَفۡتِنُونَكَ عَنِ ٱلَّذِیۤ أَوۡحَیۡنَاۤ إِلَیۡكَ لِتَفۡتَرِیَ عَلَیۡنَا غَیۡرَهُۥۖ وَإِذࣰا لَّٱتَّخَذُوكَ خَلِیلࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6057, 17, 2103, 74, 'وَلَوۡلَاۤ أَن ثَبَّتۡنَـٰكَ لَقَدۡ كِدتَّ تَرۡكَنُ إِلَیۡهِمۡ شَیۡـࣰٔا قَلِیلًا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6058, 17, 2104, 75, 'إِذࣰا لَّأَذَقۡنَـٰكَ ضِعۡفَ ٱلۡحَیَوٰةِ وَضِعۡفَ ٱلۡمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَیۡنَا نَصِیرࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6059, 17, 2105, 76, 'وَإِن كَادُوا۟ لَیَسۡتَفِزُّونَكَ مِنَ ٱلۡأَرۡضِ لِیُخۡرِجُوكَ مِنۡهَاۖ وَإِذࣰا لَّا یَلۡبَثُونَ خِلَـٰفَكَ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6060, 17, 2106, 77, 'سُنَّةَ مَن قَدۡ أَرۡسَلۡنَا قَبۡلَكَ مِن رُّسُلِنَاۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحۡوِیلًا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6061, 17, 2107, 78, 'أَقِمِ ٱلصَّلَوٰةَ لِدُلُوكِ ٱلشَّمۡسِ إِلَىٰ غَسَقِ ٱلَّیۡلِ وَقُرۡءَانَ ٱلۡفَجۡرِۖ إِنَّ قُرۡءَانَ ٱلۡفَجۡرِ كَانَ مَشۡهُودࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6062, 17, 2108, 79, 'وَمِنَ ٱلَّیۡلِ فَتَهَجَّدۡ بِهِۦ نَافِلَةࣰ لَّكَ عَسَىٰۤ أَن یَبۡعَثَكَ رَبُّكَ مَقَامࣰا مَّحۡمُودࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6063, 17, 2109, 80, 'وَقُل رَّبِّ أَدۡخِلۡنِی مُدۡخَلَ صِدۡقࣲ وَأَخۡرِجۡنِی مُخۡرَجَ صِدۡقࣲ وَٱجۡعَل لِّی مِن لَّدُنكَ سُلۡطَـٰنࣰا نَّصِیرࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6064, 17, 2110, 81, 'وَقُلۡ جَاۤءَ ٱلۡحَقُّ وَزَهَقَ ٱلۡبَـٰطِلُۚ إِنَّ ٱلۡبَـٰطِلَ كَانَ زَهُوقࣰا\n', NULL, 0, '2023-05-27 10:26:38', '2023-05-27 11:14:29'),
(6065, 17, 2111, 82, 'وَنُنَزِّلُ مِنَ ٱلۡقُرۡءَانِ مَا هُوَ شِفَاۤءࣱ وَرَحۡمَةࣱ لِّلۡمُؤۡمِنِینَ وَلَا یَزِیدُ ٱلظَّـٰلِمِینَ إِلَّا خَسَارࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:29'),
(6066, 17, 2112, 83, 'وَإِذَاۤ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَـٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ كَانَ یَـُٔوسࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:29'),
(6067, 17, 2113, 84, 'قُلۡ كُلࣱّ یَعۡمَلُ عَلَىٰ شَاكِلَتِهِۦ فَرَبُّكُمۡ أَعۡلَمُ بِمَنۡ هُوَ أَهۡدَىٰ سَبِیلࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:29'),
(6068, 17, 2114, 85, 'وَیَسۡـَٔلُونَكَ عَنِ ٱلرُّوحِۖ قُلِ ٱلرُّوحُ مِنۡ أَمۡرِ رَبِّی وَمَاۤ أُوتِیتُم مِّنَ ٱلۡعِلۡمِ إِلَّا قَلِیلࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:29'),
(6069, 17, 2115, 86, 'وَلَىِٕن شِئۡنَا لَنَذۡهَبَنَّ بِٱلَّذِیۤ أَوۡحَیۡنَاۤ إِلَیۡكَ ثُمَّ لَا تَجِدُ لَكَ بِهِۦ عَلَیۡنَا وَكِیلًا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6070, 17, 2116, 87, 'إِلَّا رَحۡمَةࣰ مِّن رَّبِّكَۚ إِنَّ فَضۡلَهُۥ كَانَ عَلَیۡكَ كَبِیرࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6071, 17, 2117, 88, 'قُل لَّىِٕنِ ٱجۡتَمَعَتِ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَىٰۤ أَن یَأۡتُوا۟ بِمِثۡلِ هَـٰذَا ٱلۡقُرۡءَانِ لَا یَأۡتُونَ بِمِثۡلِهِۦ وَلَوۡ كَانَ بَعۡضُهُمۡ لِبَعۡضࣲ ظَهِیرࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6072, 17, 2118, 89, 'وَلَقَدۡ صَرَّفۡنَا لِلنَّاسِ فِی هَـٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلࣲ فَأَبَىٰۤ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6073, 17, 2119, 90, 'وَقَالُوا۟ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ تَفۡجُرَ لَنَا مِنَ ٱلۡأَرۡضِ یَنۢبُوعًا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6074, 17, 2120, 91, 'أَوۡ تَكُونَ لَكَ جَنَّةࣱ مِّن نَّخِیلࣲ وَعِنَبࣲ فَتُفَجِّرَ ٱلۡأَنۡهَـٰرَ خِلَـٰلَهَا تَفۡجِیرًا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6075, 17, 2121, 92, 'أَوۡ تُسۡقِطَ ٱلسَّمَاۤءَ كَمَا زَعَمۡتَ عَلَیۡنَا كِسَفًا أَوۡ تَأۡتِیَ بِٱللَّهِ وَٱلۡمَلَـٰۤىِٕكَةِ قَبِیلًا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6076, 17, 2122, 93, 'أَوۡ یَكُونَ لَكَ بَیۡتࣱ مِّن زُخۡرُفٍ أَوۡ تَرۡقَىٰ فِی ٱلسَّمَاۤءِ وَلَن نُّؤۡمِنَ لِرُقِیِّكَ حَتَّىٰ تُنَزِّلَ عَلَیۡنَا كِتَـٰبࣰا نَّقۡرَؤُهُۥۗ قُلۡ سُبۡحَانَ رَبِّی هَلۡ كُنتُ إِلَّا بَشَرࣰا رَّسُولࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6077, 17, 2123, 94, 'وَمَا مَنَعَ ٱلنَّاسَ أَن یُؤۡمِنُوۤا۟ إِذۡ جَاۤءَهُمُ ٱلۡهُدَىٰۤ إِلَّاۤ أَن قَالُوۤا۟ أَبَعَثَ ٱللَّهُ بَشَرࣰا رَّسُولࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6078, 17, 2124, 95, 'قُل لَّوۡ كَانَ فِی ٱلۡأَرۡضِ مَلَـٰۤىِٕكَةࣱ یَمۡشُونَ مُطۡمَىِٕنِّینَ لَنَزَّلۡنَا عَلَیۡهِم مِّنَ ٱلسَّمَاۤءِ مَلَكࣰا رَّسُولࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6079, 17, 2125, 96, 'قُلۡ كَفَىٰ بِٱللَّهِ شَهِیدَۢا بَیۡنِی وَبَیۡنَكُمۡۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِیرَۢا بَصِیرࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6080, 17, 2126, 97, 'وَمَن یَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن یُضۡلِلۡ فَلَن تَجِدَ لَهُمۡ أَوۡلِیَاۤءَ مِن دُونِهِۦۖ وَنَحۡشُرُهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ عَلَىٰ وُجُوهِهِمۡ عُمۡیࣰا وَبُكۡمࣰا وَصُمࣰّاۖ مَّأۡوَىٰهُمۡ جَهَنَّمُۖ كُلَّمَا خَبَتۡ زِدۡنَـٰهُمۡ سَعِیرࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6081, 17, 2127, 98, 'ذَ ٰ⁠لِكَ جَزَاۤؤُهُم بِأَنَّهُمۡ كَفَرُوا۟ بِـَٔایَـٰتِنَا وَقَالُوۤا۟ أَءِذَا كُنَّا عِظَـٰمࣰا وَرُفَـٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقࣰا جَدِیدًا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6082, 17, 2128, 99, '۞ أَوَلَمۡ یَرَوۡا۟ أَنَّ ٱللَّهَ ٱلَّذِی خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ قَادِرٌ عَلَىٰۤ أَن یَخۡلُقَ مِثۡلَهُمۡ وَجَعَلَ لَهُمۡ أَجَلࣰا لَّا رَیۡبَ فِیهِ فَأَبَى ٱلظَّـٰلِمُونَ إِلَّا كُفُورࣰا\n', NULL, 0, '2023-05-27 10:26:39', '2023-05-27 11:14:30'),
(6083, 17, 2129, 100, 'قُل لَّوۡ أَنتُمۡ تَمۡلِكُونَ خَزَاۤىِٕنَ رَحۡمَةِ رَبِّیۤ إِذࣰا لَّأَمۡسَكۡتُمۡ خَشۡیَةَ ٱلۡإِنفَاقِۚ وَكَانَ ٱلۡإِنسَـٰنُ قَتُورࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:30'),
(6084, 17, 2130, 101, 'وَلَقَدۡ ءَاتَیۡنَا مُوسَىٰ تِسۡعَ ءَایَـٰتِۭ بَیِّنَـٰتࣲۖ فَسۡـَٔلۡ بَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ إِذۡ جَاۤءَهُمۡ فَقَالَ لَهُۥ فِرۡعَوۡنُ إِنِّی لَأَظُنُّكَ یَـٰمُوسَىٰ مَسۡحُورࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6085, 17, 2131, 102, 'قَالَ لَقَدۡ عَلِمۡتَ مَاۤ أَنزَلَ هَـٰۤؤُلَاۤءِ إِلَّا رَبُّ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ بَصَاۤىِٕرَ وَإِنِّی لَأَظُنُّكَ یَـٰفِرۡعَوۡنُ مَثۡبُورࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6086, 17, 2132, 103, 'فَأَرَادَ أَن یَسۡتَفِزَّهُم مِّنَ ٱلۡأَرۡضِ فَأَغۡرَقۡنَـٰهُ وَمَن مَّعَهُۥ جَمِیعࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6087, 17, 2133, 104, 'وَقُلۡنَا مِنۢ بَعۡدِهِۦ لِبَنِیۤ إِسۡرَ ٰ⁠ۤءِیلَ ٱسۡكُنُوا۟ ٱلۡأَرۡضَ فَإِذَا جَاۤءَ وَعۡدُ ٱلۡـَٔاخِرَةِ جِئۡنَا بِكُمۡ لَفِیفࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6088, 17, 2134, 105, 'وَبِٱلۡحَقِّ أَنزَلۡنَـٰهُ وَبِٱلۡحَقِّ نَزَلَۗ وَمَاۤ أَرۡسَلۡنَـٰكَ إِلَّا مُبَشِّرࣰا وَنَذِیرࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6089, 17, 2135, 106, 'وَقُرۡءَانࣰا فَرَقۡنَـٰهُ لِتَقۡرَأَهُۥ عَلَى ٱلنَّاسِ عَلَىٰ مُكۡثࣲ وَنَزَّلۡنَـٰهُ تَنزِیلࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6090, 17, 2136, 107, 'قُلۡ ءَامِنُوا۟ بِهِۦۤ أَوۡ لَا تُؤۡمِنُوۤا۟ۚ إِنَّ ٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ مِن قَبۡلِهِۦۤ إِذَا یُتۡلَىٰ عَلَیۡهِمۡ یَخِرُّونَ لِلۡأَذۡقَانِ سُجَّدࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6091, 17, 2137, 108, 'وَیَقُولُونَ سُبۡحَـٰنَ رَبِّنَاۤ إِن كَانَ وَعۡدُ رَبِّنَا لَمَفۡعُولࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6092, 17, 2138, 109, 'وَیَخِرُّونَ لِلۡأَذۡقَانِ یَبۡكُونَ وَیَزِیدُهُمۡ خُشُوعࣰا ۩\n', NULL, 1, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6093, 17, 2139, 110, 'قُلِ ٱدۡعُوا۟ ٱللَّهَ أَوِ ٱدۡعُوا۟ ٱلرَّحۡمَـٰنَۖ أَیࣰّا مَّا تَدۡعُوا۟ فَلَهُ ٱلۡأَسۡمَاۤءُ ٱلۡحُسۡنَىٰۚ وَلَا تَجۡهَرۡ بِصَلَاتِكَ وَلَا تُخَافِتۡ بِهَا وَٱبۡتَغِ بَیۡنَ ذَ ٰ⁠لِكَ سَبِیلࣰا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6094, 17, 2140, 111, 'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِی لَمۡ یَتَّخِذۡ وَلَدࣰا وَلَمۡ یَكُن لَّهُۥ شَرِیكࣱ فِی ٱلۡمُلۡكِ وَلَمۡ یَكُن لَّهُۥ وَلِیࣱّ مِّنَ ٱلذُّلِّۖ وَكَبِّرۡهُ تَكۡبِیرَۢا\n', NULL, 0, '2023-05-27 10:26:40', '2023-05-27 11:14:31'),
(6095, 16, 1902, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ أَتَىٰۤ أَمۡرُ ٱللَّهِ فَلَا تَسۡتَعۡجِلُوهُۚ سُبۡحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-27 10:26:44', '2023-05-27 11:14:34'),
(6096, 16, 1903, 2, 'یُنَزِّلُ ٱلۡمَلَـٰۤىِٕكَةَ بِٱلرُّوحِ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن یَشَاۤءُ مِنۡ عِبَادِهِۦۤ أَنۡ أَنذِرُوۤا۟ أَنَّهُۥ لَاۤ إِلَـٰهَ إِلَّاۤ أَنَا۠ فَٱتَّقُونِ\n', NULL, 0, '2023-05-27 10:26:44', '2023-05-27 11:14:34'),
(6097, 16, 1904, 3, 'خَلَقَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ تَعَـٰلَىٰ عَمَّا یُشۡرِكُونَ\n', NULL, 0, '2023-05-27 10:26:44', '2023-05-27 11:14:35'),
(6098, 16, 1905, 4, 'خَلَقَ ٱلۡإِنسَـٰنَ مِن نُّطۡفَةࣲ فَإِذَا هُوَ خَصِیمࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-27 10:26:44', '2023-05-27 11:14:35'),
(6099, 16, 1906, 5, 'وَٱلۡأَنۡعَـٰمَ خَلَقَهَاۖ لَكُمۡ فِیهَا دِفۡءࣱ وَمَنَـٰفِعُ وَمِنۡهَا تَأۡكُلُونَ\n', NULL, 0, '2023-05-27 10:26:44', '2023-05-27 11:14:35'),
(6100, 16, 1907, 6, 'وَلَكُمۡ فِیهَا جَمَالٌ حِینَ تُرِیحُونَ وَحِینَ تَسۡرَحُونَ\n', NULL, 0, '2023-05-27 10:26:44', '2023-05-27 11:14:35'),
(6101, 16, 1908, 7, 'وَتَحۡمِلُ أَثۡقَالَكُمۡ إِلَىٰ بَلَدࣲ لَّمۡ تَكُونُوا۟ بَـٰلِغِیهِ إِلَّا بِشِقِّ ٱلۡأَنفُسِۚ إِنَّ رَبَّكُمۡ لَرَءُوفࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-27 10:26:44', '2023-05-27 11:14:35'),
(6102, 16, 1909, 8, 'وَٱلۡخَیۡلَ وَٱلۡبِغَالَ وَٱلۡحَمِیرَ لِتَرۡكَبُوهَا وَزِینَةࣰۚ وَیَخۡلُقُ مَا لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6103, 16, 1910, 9, 'وَعَلَى ٱللَّهِ قَصۡدُ ٱلسَّبِیلِ وَمِنۡهَا جَاۤىِٕرࣱۚ وَلَوۡ شَاۤءَ لَهَدَىٰكُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6104, 16, 1911, 10, 'هُوَ ٱلَّذِیۤ أَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰۖ لَّكُم مِّنۡهُ شَرَابࣱ وَمِنۡهُ شَجَرࣱ فِیهِ تُسِیمُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6105, 16, 1912, 11, 'یُنۢبِتُ لَكُم بِهِ ٱلزَّرۡعَ وَٱلزَّیۡتُونَ وَٱلنَّخِیلَ وَٱلۡأَعۡنَـٰبَ وَمِن كُلِّ ٱلثَّمَرَ ٰ⁠تِۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّقَوۡمࣲ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6106, 16, 1913, 12, 'وَسَخَّرَ لَكُمُ ٱلَّیۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ وَٱلنُّجُومُ مُسَخَّرَ ٰ⁠تُۢ بِأَمۡرِهِۦۤۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6107, 16, 1914, 13, 'وَمَا ذَرَأَ لَكُمۡ فِی ٱلۡأَرۡضِ مُخۡتَلِفًا أَلۡوَ ٰ⁠نُهُۥۤۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّقَوۡمࣲ یَذَّكَّرُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6108, 16, 1915, 14, 'وَهُوَ ٱلَّذِی سَخَّرَ ٱلۡبَحۡرَ لِتَأۡكُلُوا۟ مِنۡهُ لَحۡمࣰا طَرِیࣰّا وَتَسۡتَخۡرِجُوا۟ مِنۡهُ حِلۡیَةࣰ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ مَوَاخِرَ فِیهِ وَلِتَبۡتَغُوا۟ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6109, 16, 1916, 15, 'وَأَلۡقَىٰ فِی ٱلۡأَرۡضِ رَوَ ٰ⁠سِیَ أَن تَمِیدَ بِكُمۡ وَأَنۡهَـٰرࣰا وَسُبُلࣰا لَّعَلَّكُمۡ تَهۡتَدُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6110, 16, 1917, 16, 'وَعَلَـٰمَـٰتࣲۚ وَبِٱلنَّجۡمِ هُمۡ یَهۡتَدُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6111, 16, 1918, 17, 'أَفَمَن یَخۡلُقُ كَمَن لَّا یَخۡلُقُۚ أَفَلَا تَذَكَّرُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6112, 16, 1919, 18, 'وَإِن تَعُدُّوا۟ نِعۡمَةَ ٱللَّهِ لَا تُحۡصُوهَاۤۗ إِنَّ ٱللَّهَ لَغَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:35'),
(6113, 16, 1920, 19, 'وَٱللَّهُ یَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:36'),
(6114, 16, 1921, 20, 'وَٱلَّذِینَ یَدۡعُونَ مِن دُونِ ٱللَّهِ لَا یَخۡلُقُونَ شَیۡـࣰٔا وَهُمۡ یُخۡلَقُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:36'),
(6115, 16, 1922, 21, 'أَمۡوَ ٰ⁠تٌ غَیۡرُ أَحۡیَاۤءࣲۖ وَمَا یَشۡعُرُونَ أَیَّانَ یُبۡعَثُونَ\n', NULL, 0, '2023-05-27 10:26:45', '2023-05-27 11:14:36'),
(6116, 16, 1923, 22, 'إِلَـٰهُكُمۡ إِلَـٰهࣱ وَ ٰ⁠حِدࣱۚ فَٱلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ قُلُوبُهُم مُّنكِرَةࣱ وَهُم مُّسۡتَكۡبِرُونَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6117, 16, 1924, 23, 'لَا جَرَمَ أَنَّ ٱللَّهَ یَعۡلَمُ مَا یُسِرُّونَ وَمَا یُعۡلِنُونَۚ إِنَّهُۥ لَا یُحِبُّ ٱلۡمُسۡتَكۡبِرِینَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6118, 16, 1925, 24, 'وَإِذَا قِیلَ لَهُم مَّاذَاۤ أَنزَلَ رَبُّكُمۡ قَالُوۤا۟ أَسَـٰطِیرُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6119, 16, 1926, 25, 'لِیَحۡمِلُوۤا۟ أَوۡزَارَهُمۡ كَامِلَةࣰ یَوۡمَ ٱلۡقِیَـٰمَةِ وَمِنۡ أَوۡزَارِ ٱلَّذِینَ یُضِلُّونَهُم بِغَیۡرِ عِلۡمٍۗ أَلَا سَاۤءَ مَا یَزِرُونَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6120, 16, 1927, 26, 'قَدۡ مَكَرَ ٱلَّذِینَ مِن قَبۡلِهِمۡ فَأَتَى ٱللَّهُ بُنۡیَـٰنَهُم مِّنَ ٱلۡقَوَاعِدِ فَخَرَّ عَلَیۡهِمُ ٱلسَّقۡفُ مِن فَوۡقِهِمۡ وَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَیۡثُ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6121, 16, 1928, 27, 'ثُمَّ یَوۡمَ ٱلۡقِیَـٰمَةِ یُخۡزِیهِمۡ وَیَقُولُ أَیۡنَ شُرَكَاۤءِیَ ٱلَّذِینَ كُنتُمۡ تُشَـٰۤقُّونَ فِیهِمۡۚ قَالَ ٱلَّذِینَ أُوتُوا۟ ٱلۡعِلۡمَ إِنَّ ٱلۡخِزۡیَ ٱلۡیَوۡمَ وَٱلسُّوۤءَ عَلَى ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6122, 16, 1929, 28, 'ٱلَّذِینَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰۤىِٕكَةُ ظَالِمِیۤ أَنفُسِهِمۡۖ فَأَلۡقَوُا۟ ٱلسَّلَمَ مَا كُنَّا نَعۡمَلُ مِن سُوۤءِۭۚ بَلَىٰۤۚ إِنَّ ٱللَّهَ عَلِیمُۢ بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6123, 16, 1930, 29, 'فَٱدۡخُلُوۤا۟ أَبۡوَ ٰ⁠بَ جَهَنَّمَ خَـٰلِدِینَ فِیهَاۖ فَلَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِینَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6124, 16, 1931, 30, '۞ وَقِیلَ لِلَّذِینَ ٱتَّقَوۡا۟ مَاذَاۤ أَنزَلَ رَبُّكُمۡۚ قَالُوا۟ خَیۡرࣰاۗ لِّلَّذِینَ أَحۡسَنُوا۟ فِی هَـٰذِهِ ٱلدُّنۡیَا حَسَنَةࣱۚ وَلَدَارُ ٱلۡـَٔاخِرَةِ خَیۡرࣱۚ وَلَنِعۡمَ دَارُ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6125, 16, 1932, 31, 'جَنَّـٰتُ عَدۡنࣲ یَدۡخُلُونَهَا تَجۡرِی مِن تَحۡتِهَا ٱلۡأَنۡهَـٰرُۖ لَهُمۡ فِیهَا مَا یَشَاۤءُونَۚ كَذَ ٰ⁠لِكَ یَجۡزِی ٱللَّهُ ٱلۡمُتَّقِینَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6126, 16, 1933, 32, 'ٱلَّذِینَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰۤىِٕكَةُ طَیِّبِینَ یَقُولُونَ سَلَـٰمٌ عَلَیۡكُمُ ٱدۡخُلُوا۟ ٱلۡجَنَّةَ بِمَا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:36'),
(6127, 16, 1934, 33, 'هَلۡ یَنظُرُونَ إِلَّاۤ أَن تَأۡتِیَهُمُ ٱلۡمَلَـٰۤىِٕكَةُ أَوۡ یَأۡتِیَ أَمۡرُ رَبِّكَۚ كَذَ ٰ⁠لِكَ فَعَلَ ٱلَّذِینَ مِن قَبۡلِهِمۡۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَـٰكِن كَانُوۤا۟ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:37'),
(6128, 16, 1935, 34, 'فَأَصَابَهُمۡ سَیِّـَٔاتُ مَا عَمِلُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:37'),
(6129, 16, 1936, 35, 'وَقَالَ ٱلَّذِینَ أَشۡرَكُوا۟ لَوۡ شَاۤءَ ٱللَّهُ مَا عَبَدۡنَا مِن دُونِهِۦ مِن شَیۡءࣲ نَّحۡنُ وَلَاۤ ءَابَاۤؤُنَا وَلَا حَرَّمۡنَا مِن دُونِهِۦ مِن شَیۡءࣲۚ كَذَ ٰ⁠لِكَ فَعَلَ ٱلَّذِینَ مِن قَبۡلِهِمۡۚ فَهَلۡ عَلَى ٱلرُّسُلِ إِلَّا ٱلۡبَلَـٰغُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-27 10:26:46', '2023-05-27 11:14:37'),
(6130, 16, 1937, 36, 'وَلَقَدۡ بَعَثۡنَا فِی كُلِّ أُمَّةࣲ رَّسُولًا أَنِ ٱعۡبُدُوا۟ ٱللَّهَ وَٱجۡتَنِبُوا۟ ٱلطَّـٰغُوتَۖ فَمِنۡهُم مَّنۡ هَدَى ٱللَّهُ وَمِنۡهُم مَّنۡ حَقَّتۡ عَلَیۡهِ ٱلضَّلَـٰلَةُۚ فَسِیرُوا۟ فِی ٱلۡأَرۡضِ فَٱنظُرُوا۟ كَیۡفَ كَانَ عَـٰقِبَةُ ٱلۡمُكَذِّبِینَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6131, 16, 1938, 37, 'إِن تَحۡرِصۡ عَلَىٰ هُدَىٰهُمۡ فَإِنَّ ٱللَّهَ لَا یَهۡدِی مَن یُضِلُّۖ وَمَا لَهُم مِّن نَّـٰصِرِینَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6132, 16, 1939, 38, 'وَأَقۡسَمُوا۟ بِٱللَّهِ جَهۡدَ أَیۡمَـٰنِهِمۡ لَا یَبۡعَثُ ٱللَّهُ مَن یَمُوتُۚ بَلَىٰ وَعۡدًا عَلَیۡهِ حَقࣰّا وَلَـٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6133, 16, 1940, 39, 'لِیُبَیِّنَ لَهُمُ ٱلَّذِی یَخۡتَلِفُونَ فِیهِ وَلِیَعۡلَمَ ٱلَّذِینَ كَفَرُوۤا۟ أَنَّهُمۡ كَانُوا۟ كَـٰذِبِینَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6134, 16, 1941, 40, 'إِنَّمَا قَوۡلُنَا لِشَیۡءٍ إِذَاۤ أَرَدۡنَـٰهُ أَن نَّقُولَ لَهُۥ كُن فَیَكُونُ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6135, 16, 1942, 41, 'وَٱلَّذِینَ هَاجَرُوا۟ فِی ٱللَّهِ مِنۢ بَعۡدِ مَا ظُلِمُوا۟ لَنُبَوِّئَنَّهُمۡ فِی ٱلدُّنۡیَا حَسَنَةࣰۖ وَلَأَجۡرُ ٱلۡـَٔاخِرَةِ أَكۡبَرُۚ لَوۡ كَانُوا۟ یَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6136, 16, 1943, 42, 'ٱلَّذِینَ صَبَرُوا۟ وَعَلَىٰ رَبِّهِمۡ یَتَوَكَّلُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6137, 16, 1944, 43, 'وَمَاۤ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالࣰا نُّوحِیۤ إِلَیۡهِمۡۖ فَسۡـَٔلُوۤا۟ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6138, 16, 1945, 44, 'بِٱلۡبَیِّنَـٰتِ وَٱلزُّبُرِۗ وَأَنزَلۡنَاۤ إِلَیۡكَ ٱلذِّكۡرَ لِتُبَیِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَیۡهِمۡ وَلَعَلَّهُمۡ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6139, 16, 1946, 45, 'أَفَأَمِنَ ٱلَّذِینَ مَكَرُوا۟ ٱلسَّیِّـَٔاتِ أَن یَخۡسِفَ ٱللَّهُ بِهِمُ ٱلۡأَرۡضَ أَوۡ یَأۡتِیَهُمُ ٱلۡعَذَابُ مِنۡ حَیۡثُ لَا یَشۡعُرُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6140, 16, 1947, 46, 'أَوۡ یَأۡخُذَهُمۡ فِی تَقَلُّبِهِمۡ فَمَا هُم بِمُعۡجِزِینَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6141, 16, 1948, 47, 'أَوۡ یَأۡخُذَهُمۡ عَلَىٰ تَخَوُّفࣲ فَإِنَّ رَبَّكُمۡ لَرَءُوفࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6142, 16, 1949, 48, 'أَوَلَمۡ یَرَوۡا۟ إِلَىٰ مَا خَلَقَ ٱللَّهُ مِن شَیۡءࣲ یَتَفَیَّؤُا۟ ظِلَـٰلُهُۥ عَنِ ٱلۡیَمِینِ وَٱلشَّمَاۤىِٕلِ سُجَّدࣰا لِّلَّهِ وَهُمۡ دَ ٰ⁠خِرُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6143, 16, 1950, 49, 'وَلِلَّهِ یَسۡجُدُ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَمَا فِی ٱلۡأَرۡضِ مِن دَاۤبَّةࣲ وَٱلۡمَلَـٰۤىِٕكَةُ وَهُمۡ لَا یَسۡتَكۡبِرُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:37'),
(6144, 16, 1951, 50, 'یَخَافُونَ رَبَّهُم مِّن فَوۡقِهِمۡ وَیَفۡعَلُونَ مَا یُؤۡمَرُونَ ۩\n', NULL, 1, '2023-05-27 10:26:47', '2023-05-27 11:14:38'),
(6145, 16, 1952, 51, '۞ وَقَالَ ٱللَّهُ لَا تَتَّخِذُوۤا۟ إِلَـٰهَیۡنِ ٱثۡنَیۡنِۖ إِنَّمَا هُوَ إِلَـٰهࣱ وَ ٰ⁠حِدࣱ فَإِیَّـٰیَ فَٱرۡهَبُونِ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:38'),
(6146, 16, 1953, 52, 'وَلَهُۥ مَا فِی ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ وَلَهُ ٱلدِّینُ وَاصِبًاۚ أَفَغَیۡرَ ٱللَّهِ تَتَّقُونَ\n', NULL, 0, '2023-05-27 10:26:47', '2023-05-27 11:14:38'),
(6147, 16, 1954, 53, 'وَمَا بِكُم مِّن نِّعۡمَةࣲ فَمِنَ ٱللَّهِۖ ثُمَّ إِذَا مَسَّكُمُ ٱلضُّرُّ فَإِلَیۡهِ تَجۡـَٔرُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6148, 16, 1955, 54, 'ثُمَّ إِذَا كَشَفَ ٱلضُّرَّ عَنكُمۡ إِذَا فَرِیقࣱ مِّنكُم بِرَبِّهِمۡ یُشۡرِكُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6149, 16, 1956, 55, 'لِیَكۡفُرُوا۟ بِمَاۤ ءَاتَیۡنَـٰهُمۡۚ فَتَمَتَّعُوا۟ فَسَوۡفَ تَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6150, 16, 1957, 56, 'وَیَجۡعَلُونَ لِمَا لَا یَعۡلَمُونَ نَصِیبࣰا مِّمَّا رَزَقۡنَـٰهُمۡۗ تَٱللَّهِ لَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَفۡتَرُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6151, 16, 1958, 57, 'وَیَجۡعَلُونَ لِلَّهِ ٱلۡبَنَـٰتِ سُبۡحَـٰنَهُۥ وَلَهُم مَّا یَشۡتَهُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6152, 16, 1959, 58, 'وَإِذَا بُشِّرَ أَحَدُهُم بِٱلۡأُنثَىٰ ظَلَّ وَجۡهُهُۥ مُسۡوَدࣰّا وَهُوَ كَظِیمࣱ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6153, 16, 1960, 59, 'یَتَوَ ٰ⁠رَىٰ مِنَ ٱلۡقَوۡمِ مِن سُوۤءِ مَا بُشِّرَ بِهِۦۤۚ أَیُمۡسِكُهُۥ عَلَىٰ هُونٍ أَمۡ یَدُسُّهُۥ فِی ٱلتُّرَابِۗ أَلَا سَاۤءَ مَا یَحۡكُمُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6154, 16, 1961, 60, 'لِلَّذِینَ لَا یُؤۡمِنُونَ بِٱلۡـَٔاخِرَةِ مَثَلُ ٱلسَّوۡءِۖ وَلِلَّهِ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰۚ وَهُوَ ٱلۡعَزِیزُ ٱلۡحَكِیمُ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6155, 16, 1962, 61, 'وَلَوۡ یُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِظُلۡمِهِم مَّا تَرَكَ عَلَیۡهَا مِن دَاۤبَّةࣲ وَلَـٰكِن یُؤَخِّرُهُمۡ إِلَىٰۤ أَجَلࣲ مُّسَمࣰّىۖ فَإِذَا جَاۤءَ أَجَلُهُمۡ لَا یَسۡتَـٔۡخِرُونَ سَاعَةࣰ وَلَا یَسۡتَقۡدِمُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6156, 16, 1963, 62, 'وَیَجۡعَلُونَ لِلَّهِ مَا یَكۡرَهُونَۚ وَتَصِفُ أَلۡسِنَتُهُمُ ٱلۡكَذِبَ أَنَّ لَهُمُ ٱلۡحُسۡنَىٰۚ لَا جَرَمَ أَنَّ لَهُمُ ٱلنَّارَ وَأَنَّهُم مُّفۡرَطُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6157, 16, 1964, 63, 'تَٱللَّهِ لَقَدۡ أَرۡسَلۡنَاۤ إِلَىٰۤ أُمَمࣲ مِّن قَبۡلِكَ فَزَیَّنَ لَهُمُ ٱلشَّیۡطَـٰنُ أَعۡمَـٰلَهُمۡ فَهُوَ وَلِیُّهُمُ ٱلۡیَوۡمَ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6158, 16, 1965, 64, 'وَمَاۤ أَنزَلۡنَا عَلَیۡكَ ٱلۡكِتَـٰبَ إِلَّا لِتُبَیِّنَ لَهُمُ ٱلَّذِی ٱخۡتَلَفُوا۟ فِیهِ وَهُدࣰى وَرَحۡمَةࣰ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-27 10:26:48', '2023-05-27 11:14:38'),
(6159, 16, 1966, 65, 'وَٱللَّهُ أَنزَلَ مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَحۡیَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۤۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّقَوۡمࣲ یَسۡمَعُونَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:38'),
(6160, 16, 1967, 66, 'وَإِنَّ لَكُمۡ فِی ٱلۡأَنۡعَـٰمِ لَعِبۡرَةࣰۖ نُّسۡقِیكُم مِّمَّا فِی بُطُونِهِۦ مِنۢ بَیۡنِ فَرۡثࣲ وَدَمࣲ لَّبَنًا خَالِصࣰا سَاۤىِٕغࣰا لِّلشَّـٰرِبِینَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6161, 16, 1968, 67, 'وَمِن ثَمَرَ ٰ⁠تِ ٱلنَّخِیلِ وَٱلۡأَعۡنَـٰبِ تَتَّخِذُونَ مِنۡهُ سَكَرࣰا وَرِزۡقًا حَسَنًاۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّقَوۡمࣲ یَعۡقِلُونَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6162, 16, 1969, 68, 'وَأَوۡحَىٰ رَبُّكَ إِلَى ٱلنَّحۡلِ أَنِ ٱتَّخِذِی مِنَ ٱلۡجِبَالِ بُیُوتࣰا وَمِنَ ٱلشَّجَرِ وَمِمَّا یَعۡرِشُونَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6163, 16, 1970, 69, 'ثُمَّ كُلِی مِن كُلِّ ٱلثَّمَرَ ٰ⁠تِ فَٱسۡلُكِی سُبُلَ رَبِّكِ ذُلُلࣰاۚ یَخۡرُجُ مِنۢ بُطُونِهَا شَرَابࣱ مُّخۡتَلِفٌ أَلۡوَ ٰ⁠نُهُۥ فِیهِ شِفَاۤءࣱ لِّلنَّاسِۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّقَوۡمࣲ یَتَفَكَّرُونَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6164, 16, 1971, 70, 'وَٱللَّهُ خَلَقَكُمۡ ثُمَّ یَتَوَفَّىٰكُمۡۚ وَمِنكُم مَّن یُرَدُّ إِلَىٰۤ أَرۡذَلِ ٱلۡعُمُرِ لِكَیۡ لَا یَعۡلَمَ بَعۡدَ عِلۡمࣲ شَیۡـًٔاۚ إِنَّ ٱللَّهَ عَلِیمࣱ قَدِیرࣱ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6165, 16, 1972, 71, 'وَٱللَّهُ فَضَّلَ بَعۡضَكُمۡ عَلَىٰ بَعۡضࣲ فِی ٱلرِّزۡقِۚ فَمَا ٱلَّذِینَ فُضِّلُوا۟ بِرَاۤدِّی رِزۡقِهِمۡ عَلَىٰ مَا مَلَكَتۡ أَیۡمَـٰنُهُمۡ فَهُمۡ فِیهِ سَوَاۤءٌۚ أَفَبِنِعۡمَةِ ٱللَّهِ یَجۡحَدُونَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6166, 16, 1973, 72, 'وَٱللَّهُ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَ ٰ⁠جࣰا وَجَعَلَ لَكُم مِّنۡ أَزۡوَ ٰ⁠جِكُم بَنِینَ وَحَفَدَةࣰ وَرَزَقَكُم مِّنَ ٱلطَّیِّبَـٰتِۚ أَفَبِٱلۡبَـٰطِلِ یُؤۡمِنُونَ وَبِنِعۡمَتِ ٱللَّهِ هُمۡ یَكۡفُرُونَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6167, 16, 1974, 73, 'وَیَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا یَمۡلِكُ لَهُمۡ رِزۡقࣰا مِّنَ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِ شَیۡـࣰٔا وَلَا یَسۡتَطِیعُونَ\n', NULL, 0, '2023-05-27 10:26:49', '2023-05-27 11:14:39'),
(6168, 16, 1975, 74, 'فَلَا تَضۡرِبُوا۟ لِلَّهِ ٱلۡأَمۡثَالَۚ إِنَّ ٱللَّهَ یَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:39'),
(6169, 16, 1976, 75, '۞ ضَرَبَ ٱللَّهُ مَثَلًا عَبۡدࣰا مَّمۡلُوكࣰا لَّا یَقۡدِرُ عَلَىٰ شَیۡءࣲ وَمَن رَّزَقۡنَـٰهُ مِنَّا رِزۡقًا حَسَنࣰا فَهُوَ یُنفِقُ مِنۡهُ سِرࣰّا وَجَهۡرًاۖ هَلۡ یَسۡتَوُۥنَۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:39'),
(6170, 16, 1977, 76, 'وَضَرَبَ ٱللَّهُ مَثَلࣰا رَّجُلَیۡنِ أَحَدُهُمَاۤ أَبۡكَمُ لَا یَقۡدِرُ عَلَىٰ شَیۡءࣲ وَهُوَ كَلٌّ عَلَىٰ مَوۡلَىٰهُ أَیۡنَمَا یُوَجِّههُّ لَا یَأۡتِ بِخَیۡرٍ هَلۡ یَسۡتَوِی هُوَ وَمَن یَأۡمُرُ بِٱلۡعَدۡلِ وَهُوَ عَلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:39'),
(6171, 16, 1978, 77, 'وَلِلَّهِ غَیۡبُ ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضِۚ وَمَاۤ أَمۡرُ ٱلسَّاعَةِ إِلَّا كَلَمۡحِ ٱلۡبَصَرِ أَوۡ هُوَ أَقۡرَبُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَیۡءࣲ قَدِیرࣱ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:39'),
(6172, 16, 1979, 78, 'وَٱللَّهُ أَخۡرَجَكُم مِّنۢ بُطُونِ أُمَّهَـٰتِكُمۡ لَا تَعۡلَمُونَ شَیۡـࣰٔا وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَـٰرَ وَٱلۡأَفۡـِٔدَةَ لَعَلَّكُمۡ تَشۡكُرُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:39'),
(6173, 16, 1980, 79, 'أَلَمۡ یَرَوۡا۟ إِلَى ٱلطَّیۡرِ مُسَخَّرَ ٰ⁠تࣲ فِی جَوِّ ٱلسَّمَاۤءِ مَا یُمۡسِكُهُنَّ إِلَّا ٱللَّهُۚ إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّقَوۡمࣲ یُؤۡمِنُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:39'),
(6174, 16, 1981, 80, 'وَٱللَّهُ جَعَلَ لَكُم مِّنۢ بُیُوتِكُمۡ سَكَنࣰا وَجَعَلَ لَكُم مِّن جُلُودِ ٱلۡأَنۡعَـٰمِ بُیُوتࣰا تَسۡتَخِفُّونَهَا یَوۡمَ ظَعۡنِكُمۡ وَیَوۡمَ إِقَامَتِكُمۡ وَمِنۡ أَصۡوَافِهَا وَأَوۡبَارِهَا وَأَشۡعَارِهَاۤ أَثَـٰثࣰا وَمَتَـٰعًا إِلَىٰ حِینࣲ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6175, 16, 1982, 81, 'وَٱللَّهُ جَعَلَ لَكُم مِّمَّا خَلَقَ ظِلَـٰلࣰا وَجَعَلَ لَكُم مِّنَ ٱلۡجِبَالِ أَكۡنَـٰنࣰا وَجَعَلَ لَكُمۡ سَرَ ٰ⁠بِیلَ تَقِیكُمُ ٱلۡحَرَّ وَسَرَ ٰ⁠بِیلَ تَقِیكُم بَأۡسَكُمۡۚ كَذَ ٰ⁠لِكَ یُتِمُّ نِعۡمَتَهُۥ عَلَیۡكُمۡ لَعَلَّكُمۡ تُسۡلِمُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6176, 16, 1983, 82, 'فَإِن تَوَلَّوۡا۟ فَإِنَّمَا عَلَیۡكَ ٱلۡبَلَـٰغُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6177, 16, 1984, 83, 'یَعۡرِفُونَ نِعۡمَتَ ٱللَّهِ ثُمَّ یُنكِرُونَهَا وَأَكۡثَرُهُمُ ٱلۡكَـٰفِرُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6178, 16, 1985, 84, 'وَیَوۡمَ نَبۡعَثُ مِن كُلِّ أُمَّةࣲ شَهِیدࣰا ثُمَّ لَا یُؤۡذَنُ لِلَّذِینَ كَفَرُوا۟ وَلَا هُمۡ یُسۡتَعۡتَبُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6179, 16, 1986, 85, 'وَإِذَا رَءَا ٱلَّذِینَ ظَلَمُوا۟ ٱلۡعَذَابَ فَلَا یُخَفَّفُ عَنۡهُمۡ وَلَا هُمۡ یُنظَرُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6180, 16, 1987, 86, 'وَإِذَا رَءَا ٱلَّذِینَ أَشۡرَكُوا۟ شُرَكَاۤءَهُمۡ قَالُوا۟ رَبَّنَا هَـٰۤؤُلَاۤءِ شُرَكَاۤؤُنَا ٱلَّذِینَ كُنَّا نَدۡعُوا۟ مِن دُونِكَۖ فَأَلۡقَوۡا۟ إِلَیۡهِمُ ٱلۡقَوۡلَ إِنَّكُمۡ لَكَـٰذِبُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6181, 16, 1988, 87, 'وَأَلۡقَوۡا۟ إِلَى ٱللَّهِ یَوۡمَىِٕذٍ ٱلسَّلَمَۖ وَضَلَّ عَنۡهُم مَّا كَانُوا۟ یَفۡتَرُونَ\n', NULL, 0, '2023-05-27 10:26:50', '2023-05-27 11:14:40'),
(6182, 16, 1989, 88, 'ٱلَّذِینَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِیلِ ٱللَّهِ زِدۡنَـٰهُمۡ عَذَابࣰا فَوۡقَ ٱلۡعَذَابِ بِمَا كَانُوا۟ یُفۡسِدُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40'),
(6183, 16, 1990, 89, 'وَیَوۡمَ نَبۡعَثُ فِی كُلِّ أُمَّةࣲ شَهِیدًا عَلَیۡهِم مِّنۡ أَنفُسِهِمۡۖ وَجِئۡنَا بِكَ شَهِیدًا عَلَىٰ هَـٰۤؤُلَاۤءِۚ وَنَزَّلۡنَا عَلَیۡكَ ٱلۡكِتَـٰبَ تِبۡیَـٰنࣰا لِّكُلِّ شَیۡءࣲ وَهُدࣰى وَرَحۡمَةࣰ وَبُشۡرَىٰ لِلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40'),
(6184, 16, 1991, 90, '۞ إِنَّ ٱللَّهَ یَأۡمُرُ بِٱلۡعَدۡلِ وَٱلۡإِحۡسَـٰنِ وَإِیتَاۤىِٕ ذِی ٱلۡقُرۡبَىٰ وَیَنۡهَىٰ عَنِ ٱلۡفَحۡشَاۤءِ وَٱلۡمُنكَرِ وَٱلۡبَغۡیِۚ یَعِظُكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40'),
(6185, 16, 1992, 91, 'وَأَوۡفُوا۟ بِعَهۡدِ ٱللَّهِ إِذَا عَـٰهَدتُّمۡ وَلَا تَنقُضُوا۟ ٱلۡأَیۡمَـٰنَ بَعۡدَ تَوۡكِیدِهَا وَقَدۡ جَعَلۡتُمُ ٱللَّهَ عَلَیۡكُمۡ كَفِیلًاۚ إِنَّ ٱللَّهَ یَعۡلَمُ مَا تَفۡعَلُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40'),
(6186, 16, 1993, 92, 'وَلَا تَكُونُوا۟ كَٱلَّتِی نَقَضَتۡ غَزۡلَهَا مِنۢ بَعۡدِ قُوَّةٍ أَنكَـٰثࣰا تَتَّخِذُونَ أَیۡمَـٰنَكُمۡ دَخَلَۢا بَیۡنَكُمۡ أَن تَكُونَ أُمَّةٌ هِیَ أَرۡبَىٰ مِنۡ أُمَّةٍۚ إِنَّمَا یَبۡلُوكُمُ ٱللَّهُ بِهِۦۚ وَلَیُبَیِّنَنَّ لَكُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ مَا كُنتُمۡ فِیهِ تَخۡتَلِفُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40'),
(6187, 16, 1994, 93, 'وَلَوۡ شَاۤءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةࣰ وَ ٰ⁠حِدَةࣰ وَلَـٰكِن یُضِلُّ مَن یَشَاۤءُ وَیَهۡدِی مَن یَشَاۤءُۚ وَلَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَعۡمَلُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40'),
(6188, 16, 1995, 94, 'وَلَا تَتَّخِذُوۤا۟ أَیۡمَـٰنَكُمۡ دَخَلَۢا بَیۡنَكُمۡ فَتَزِلَّ قَدَمُۢ بَعۡدَ ثُبُوتِهَا وَتَذُوقُوا۟ ٱلسُّوۤءَ بِمَا صَدَدتُّمۡ عَن سَبِیلِ ٱللَّهِ وَلَكُمۡ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40'),
(6189, 16, 1996, 95, 'وَلَا تَشۡتَرُوا۟ بِعَهۡدِ ٱللَّهِ ثَمَنࣰا قَلِیلًاۚ إِنَّمَا عِندَ ٱللَّهِ هُوَ خَیۡرࣱ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:40');
INSERT INTO `ayahs` (`id`, `surah_id`, `no_all`, `ayah_no`, `verse`, `en_verse`, `sajda`, `created_at`, `updated_at`) VALUES
(6190, 16, 1997, 96, 'مَا عِندَكُمۡ یَنفَدُ وَمَا عِندَ ٱللَّهِ بَاقࣲۗ وَلَنَجۡزِیَنَّ ٱلَّذِینَ صَبَرُوۤا۟ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:41'),
(6191, 16, 1998, 97, 'مَنۡ عَمِلَ صَـٰلِحࣰا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنࣱ فَلَنُحۡیِیَنَّهُۥ حَیَوٰةࣰ طَیِّبَةࣰۖ وَلَنَجۡزِیَنَّهُمۡ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:41'),
(6192, 16, 1999, 98, 'فَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ فَٱسۡتَعِذۡ بِٱللَّهِ مِنَ ٱلشَّیۡطَـٰنِ ٱلرَّجِیمِ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:41'),
(6193, 16, 2000, 99, 'إِنَّهُۥ لَیۡسَ لَهُۥ سُلۡطَـٰنٌ عَلَى ٱلَّذِینَ ءَامَنُوا۟ وَعَلَىٰ رَبِّهِمۡ یَتَوَكَّلُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:41'),
(6194, 16, 2001, 100, 'إِنَّمَا سُلۡطَـٰنُهُۥ عَلَى ٱلَّذِینَ یَتَوَلَّوۡنَهُۥ وَٱلَّذِینَ هُم بِهِۦ مُشۡرِكُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:41'),
(6195, 16, 2002, 101, 'وَإِذَا بَدَّلۡنَاۤ ءَایَةࣰ مَّكَانَ ءَایَةࣲ وَٱللَّهُ أَعۡلَمُ بِمَا یُنَزِّلُ قَالُوۤا۟ إِنَّمَاۤ أَنتَ مُفۡتَرِۭۚ بَلۡ أَكۡثَرُهُمۡ لَا یَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:51', '2023-05-27 11:14:41'),
(6196, 16, 2003, 102, 'قُلۡ نَزَّلَهُۥ رُوحُ ٱلۡقُدُسِ مِن رَّبِّكَ بِٱلۡحَقِّ لِیُثَبِّتَ ٱلَّذِینَ ءَامَنُوا۟ وَهُدࣰى وَبُشۡرَىٰ لِلۡمُسۡلِمِینَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6197, 16, 2004, 103, 'وَلَقَدۡ نَعۡلَمُ أَنَّهُمۡ یَقُولُونَ إِنَّمَا یُعَلِّمُهُۥ بَشَرࣱۗ لِّسَانُ ٱلَّذِی یُلۡحِدُونَ إِلَیۡهِ أَعۡجَمِیࣱّ وَهَـٰذَا لِسَانٌ عَرَبِیࣱّ مُّبِینٌ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6198, 16, 2005, 104, 'إِنَّ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِـَٔایَـٰتِ ٱللَّهِ لَا یَهۡدِیهِمُ ٱللَّهُ وَلَهُمۡ عَذَابٌ أَلِیمٌ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6199, 16, 2006, 105, 'إِنَّمَا یَفۡتَرِی ٱلۡكَذِبَ ٱلَّذِینَ لَا یُؤۡمِنُونَ بِـَٔایَـٰتِ ٱللَّهِۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡكَـٰذِبُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6200, 16, 2007, 106, 'مَن كَفَرَ بِٱللَّهِ مِنۢ بَعۡدِ إِیمَـٰنِهِۦۤ إِلَّا مَنۡ أُكۡرِهَ وَقَلۡبُهُۥ مُطۡمَىِٕنُّۢ بِٱلۡإِیمَـٰنِ وَلَـٰكِن مَّن شَرَحَ بِٱلۡكُفۡرِ صَدۡرࣰا فَعَلَیۡهِمۡ غَضَبࣱ مِّنَ ٱللَّهِ وَلَهُمۡ عَذَابٌ عَظِیمࣱ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6201, 16, 2008, 107, 'ذَ ٰ⁠لِكَ بِأَنَّهُمُ ٱسۡتَحَبُّوا۟ ٱلۡحَیَوٰةَ ٱلدُّنۡیَا عَلَى ٱلۡـَٔاخِرَةِ وَأَنَّ ٱللَّهَ لَا یَهۡدِی ٱلۡقَوۡمَ ٱلۡكَـٰفِرِینَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6202, 16, 2009, 108, 'أُو۟لَـٰۤىِٕكَ ٱلَّذِینَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَسَمۡعِهِمۡ وَأَبۡصَـٰرِهِمۡۖ وَأُو۟لَـٰۤىِٕكَ هُمُ ٱلۡغَـٰفِلُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6203, 16, 2010, 109, 'لَا جَرَمَ أَنَّهُمۡ فِی ٱلۡـَٔاخِرَةِ هُمُ ٱلۡخَـٰسِرُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6204, 16, 2011, 110, 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِینَ هَاجَرُوا۟ مِنۢ بَعۡدِ مَا فُتِنُوا۟ ثُمَّ جَـٰهَدُوا۟ وَصَبَرُوۤا۟ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:41'),
(6205, 16, 2012, 111, '۞ یَوۡمَ تَأۡتِی كُلُّ نَفۡسࣲ تُجَـٰدِلُ عَن نَّفۡسِهَا وَتُوَفَّىٰ كُلُّ نَفۡسࣲ مَّا عَمِلَتۡ وَهُمۡ لَا یُظۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:42'),
(6206, 16, 2013, 112, 'وَضَرَبَ ٱللَّهُ مَثَلࣰا قَرۡیَةࣰ كَانَتۡ ءَامِنَةࣰ مُّطۡمَىِٕنَّةࣰ یَأۡتِیهَا رِزۡقُهَا رَغَدࣰا مِّن كُلِّ مَكَانࣲ فَكَفَرَتۡ بِأَنۡعُمِ ٱللَّهِ فَأَذَ ٰ⁠قَهَا ٱللَّهُ لِبَاسَ ٱلۡجُوعِ وَٱلۡخَوۡفِ بِمَا كَانُوا۟ یَصۡنَعُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:42'),
(6207, 16, 2014, 113, 'وَلَقَدۡ جَاۤءَهُمۡ رَسُولࣱ مِّنۡهُمۡ فَكَذَّبُوهُ فَأَخَذَهُمُ ٱلۡعَذَابُ وَهُمۡ ظَـٰلِمُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:42'),
(6208, 16, 2015, 114, 'فَكُلُوا۟ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَـٰلࣰا طَیِّبࣰا وَٱشۡكُرُوا۟ نِعۡمَتَ ٱللَّهِ إِن كُنتُمۡ إِیَّاهُ تَعۡبُدُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:42'),
(6209, 16, 2016, 115, 'إِنَّمَا حَرَّمَ عَلَیۡكُمُ ٱلۡمَیۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِیرِ وَمَاۤ أُهِلَّ لِغَیۡرِ ٱللَّهِ بِهِۦۖ فَمَنِ ٱضۡطُرَّ غَیۡرَ بَاغࣲ وَلَا عَادࣲ فَإِنَّ ٱللَّهَ غَفُورࣱ رَّحِیمࣱ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:42'),
(6210, 16, 2017, 116, 'وَلَا تَقُولُوا۟ لِمَا تَصِفُ أَلۡسِنَتُكُمُ ٱلۡكَذِبَ هَـٰذَا حَلَـٰلࣱ وَهَـٰذَا حَرَامࣱ لِّتَفۡتَرُوا۟ عَلَى ٱللَّهِ ٱلۡكَذِبَۚ إِنَّ ٱلَّذِینَ یَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا یُفۡلِحُونَ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:42'),
(6211, 16, 2018, 117, 'مَتَـٰعࣱ قَلِیلࣱ وَلَهُمۡ عَذَابٌ أَلِیمࣱ\n', NULL, 0, '2023-05-27 10:26:52', '2023-05-27 11:14:42'),
(6212, 16, 2019, 118, 'وَعَلَى ٱلَّذِینَ هَادُوا۟ حَرَّمۡنَا مَا قَصَصۡنَا عَلَیۡكَ مِن قَبۡلُۖ وَمَا ظَلَمۡنَـٰهُمۡ وَلَـٰكِن كَانُوۤا۟ أَنفُسَهُمۡ یَظۡلِمُونَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:42'),
(6213, 16, 2020, 119, 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِینَ عَمِلُوا۟ ٱلسُّوۤءَ بِجَهَـٰلَةࣲ ثُمَّ تَابُوا۟ مِنۢ بَعۡدِ ذَ ٰ⁠لِكَ وَأَصۡلَحُوۤا۟ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورࣱ رَّحِیمٌ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:42'),
(6214, 16, 2021, 120, 'إِنَّ إِبۡرَ ٰ⁠هِیمَ كَانَ أُمَّةࣰ قَانِتࣰا لِّلَّهِ حَنِیفࣰا وَلَمۡ یَكُ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:42'),
(6215, 16, 2022, 121, 'شَاكِرࣰا لِّأَنۡعُمِهِۚ ٱجۡتَبَىٰهُ وَهَدَىٰهُ إِلَىٰ صِرَ ٰ⁠طࣲ مُّسۡتَقِیمࣲ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:42'),
(6216, 16, 2023, 122, 'وَءَاتَیۡنَـٰهُ فِی ٱلدُّنۡیَا حَسَنَةࣰۖ وَإِنَّهُۥ فِی ٱلۡـَٔاخِرَةِ لَمِنَ ٱلصَّـٰلِحِینَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:42'),
(6217, 16, 2024, 123, 'ثُمَّ أَوۡحَیۡنَاۤ إِلَیۡكَ أَنِ ٱتَّبِعۡ مِلَّةَ إِبۡرَ ٰ⁠هِیمَ حَنِیفࣰاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:42'),
(6218, 16, 2025, 124, 'إِنَّمَا جُعِلَ ٱلسَّبۡتُ عَلَى ٱلَّذِینَ ٱخۡتَلَفُوا۟ فِیهِۚ وَإِنَّ رَبَّكَ لَیَحۡكُمُ بَیۡنَهُمۡ یَوۡمَ ٱلۡقِیَـٰمَةِ فِیمَا كَانُوا۟ فِیهِ یَخۡتَلِفُونَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:43'),
(6219, 16, 2026, 125, 'ٱدۡعُ إِلَىٰ سَبِیلِ رَبِّكَ بِٱلۡحِكۡمَةِ وَٱلۡمَوۡعِظَةِ ٱلۡحَسَنَةِۖ وَجَـٰدِلۡهُم بِٱلَّتِی هِیَ أَحۡسَنُۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِیلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِینَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:43'),
(6220, 16, 2027, 126, 'وَإِنۡ عَاقَبۡتُمۡ فَعَاقِبُوا۟ بِمِثۡلِ مَا عُوقِبۡتُم بِهِۦۖ وَلَىِٕن صَبَرۡتُمۡ لَهُوَ خَیۡرࣱ لِّلصَّـٰبِرِینَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:43'),
(6221, 16, 2028, 127, 'وَٱصۡبِرۡ وَمَا صَبۡرُكَ إِلَّا بِٱللَّهِۚ وَلَا تَحۡزَنۡ عَلَیۡهِمۡ وَلَا تَكُ فِی ضَیۡقࣲ مِّمَّا یَمۡكُرُونَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:43'),
(6222, 16, 2029, 128, 'إِنَّ ٱللَّهَ مَعَ ٱلَّذِینَ ٱتَّقَوا۟ وَّٱلَّذِینَ هُم مُّحۡسِنُونَ\n', NULL, 0, '2023-05-27 10:26:53', '2023-05-27 11:14:43'),
(6223, 15, 1803, 1, 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَـٰنِ ٱلرَّحِیمِ الۤرۚ تِلۡكَ ءَایَـٰتُ ٱلۡكِتَـٰبِ وَقُرۡءَانࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-27 10:26:55', '2023-05-27 11:14:46'),
(6224, 15, 1804, 2, 'رُّبَمَا یَوَدُّ ٱلَّذِینَ كَفَرُوا۟ لَوۡ كَانُوا۟ مُسۡلِمِینَ\n', NULL, 0, '2023-05-27 10:26:55', '2023-05-27 11:14:47'),
(6225, 15, 1805, 3, 'ذَرۡهُمۡ یَأۡكُلُوا۟ وَیَتَمَتَّعُوا۟ وَیُلۡهِهِمُ ٱلۡأَمَلُۖ فَسَوۡفَ یَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:26:55', '2023-05-27 11:14:47'),
(6226, 15, 1806, 4, 'وَمَاۤ أَهۡلَكۡنَا مِن قَرۡیَةٍ إِلَّا وَلَهَا كِتَابࣱ مَّعۡلُومࣱ\n', NULL, 0, '2023-05-27 10:26:55', '2023-05-27 11:14:47'),
(6227, 15, 1807, 5, 'مَّا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا یَسۡتَـٔۡخِرُونَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6228, 15, 1808, 6, 'وَقَالُوا۟ یَـٰۤأَیُّهَا ٱلَّذِی نُزِّلَ عَلَیۡهِ ٱلذِّكۡرُ إِنَّكَ لَمَجۡنُونࣱ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6229, 15, 1809, 7, 'لَّوۡ مَا تَأۡتِینَا بِٱلۡمَلَـٰۤىِٕكَةِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِینَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6230, 15, 1810, 8, 'مَا نُنَزِّلُ ٱلۡمَلَـٰۤىِٕكَةَ إِلَّا بِٱلۡحَقِّ وَمَا كَانُوۤا۟ إِذࣰا مُّنظَرِینَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6231, 15, 1811, 9, 'إِنَّا نَحۡنُ نَزَّلۡنَا ٱلذِّكۡرَ وَإِنَّا لَهُۥ لَحَـٰفِظُونَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6232, 15, 1812, 10, 'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ فِی شِیَعِ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6233, 15, 1813, 11, 'وَمَا یَأۡتِیهِم مِّن رَّسُولٍ إِلَّا كَانُوا۟ بِهِۦ یَسۡتَهۡزِءُونَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6234, 15, 1814, 12, 'كَذَ ٰ⁠لِكَ نَسۡلُكُهُۥ فِی قُلُوبِ ٱلۡمُجۡرِمِینَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6235, 15, 1815, 13, 'لَا یُؤۡمِنُونَ بِهِۦ وَقَدۡ خَلَتۡ سُنَّةُ ٱلۡأَوَّلِینَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6236, 15, 1816, 14, 'وَلَوۡ فَتَحۡنَا عَلَیۡهِم بَابࣰا مِّنَ ٱلسَّمَاۤءِ فَظَلُّوا۟ فِیهِ یَعۡرُجُونَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6237, 15, 1817, 15, 'لَقَالُوۤا۟ إِنَّمَا سُكِّرَتۡ أَبۡصَـٰرُنَا بَلۡ نَحۡنُ قَوۡمࣱ مَّسۡحُورُونَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6238, 15, 1818, 16, 'وَلَقَدۡ جَعَلۡنَا فِی ٱلسَّمَاۤءِ بُرُوجࣰا وَزَیَّنَّـٰهَا لِلنَّـٰظِرِینَ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:47'),
(6239, 15, 1819, 17, 'وَحَفِظۡنَـٰهَا مِن كُلِّ شَیۡطَـٰنࣲ رَّجِیمٍ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:48'),
(6240, 15, 1820, 18, 'إِلَّا مَنِ ٱسۡتَرَقَ ٱلسَّمۡعَ فَأَتۡبَعَهُۥ شِهَابࣱ مُّبِینࣱ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:48'),
(6241, 15, 1821, 19, 'وَٱلۡأَرۡضَ مَدَدۡنَـٰهَا وَأَلۡقَیۡنَا فِیهَا رَوَ ٰ⁠سِیَ وَأَنۢبَتۡنَا فِیهَا مِن كُلِّ شَیۡءࣲ مَّوۡزُونࣲ\n', NULL, 0, '2023-05-27 10:26:56', '2023-05-27 11:14:48'),
(6242, 15, 1822, 20, 'وَجَعَلۡنَا لَكُمۡ فِیهَا مَعَـٰیِشَ وَمَن لَّسۡتُمۡ لَهُۥ بِرَ ٰ⁠زِقِینَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6243, 15, 1823, 21, 'وَإِن مِّن شَیۡءٍ إِلَّا عِندَنَا خَزَاۤىِٕنُهُۥ وَمَا نُنَزِّلُهُۥۤ إِلَّا بِقَدَرࣲ مَّعۡلُومࣲ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6244, 15, 1824, 22, 'وَأَرۡسَلۡنَا ٱلرِّیَـٰحَ لَوَ ٰ⁠قِحَ فَأَنزَلۡنَا مِنَ ٱلسَّمَاۤءِ مَاۤءࣰ فَأَسۡقَیۡنَـٰكُمُوهُ وَمَاۤ أَنتُمۡ لَهُۥ بِخَـٰزِنِینَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6245, 15, 1825, 23, 'وَإِنَّا لَنَحۡنُ نُحۡیِۦ وَنُمِیتُ وَنَحۡنُ ٱلۡوَ ٰ⁠رِثُونَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6246, 15, 1826, 24, 'وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَقۡدِمِینَ مِنكُمۡ وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَـٔۡخِرِینَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6247, 15, 1827, 25, 'وَإِنَّ رَبَّكَ هُوَ یَحۡشُرُهُمۡۚ إِنَّهُۥ حَكِیمٌ عَلِیمࣱ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6248, 15, 1828, 26, 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَـٰنَ مِن صَلۡصَـٰلࣲ مِّنۡ حَمَإࣲ مَّسۡنُونࣲ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6249, 15, 1829, 27, 'وَٱلۡجَاۤنَّ خَلَقۡنَـٰهُ مِن قَبۡلُ مِن نَّارِ ٱلسَّمُومِ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6250, 15, 1830, 28, 'وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰۤىِٕكَةِ إِنِّی خَـٰلِقُۢ بَشَرࣰا مِّن صَلۡصَـٰلࣲ مِّنۡ حَمَإࣲ مَّسۡنُونࣲ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6251, 15, 1831, 29, 'فَإِذَا سَوَّیۡتُهُۥ وَنَفَخۡتُ فِیهِ مِن رُّوحِی فَقَعُوا۟ لَهُۥ سَـٰجِدِینَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:48'),
(6252, 15, 1832, 30, 'فَسَجَدَ ٱلۡمَلَـٰۤىِٕكَةُ كُلُّهُمۡ أَجۡمَعُونَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:49'),
(6253, 15, 1833, 31, 'إِلَّاۤ إِبۡلِیسَ أَبَىٰۤ أَن یَكُونَ مَعَ ٱلسَّـٰجِدِینَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:49'),
(6254, 15, 1834, 32, 'قَالَ یَـٰۤإِبۡلِیسُ مَا لَكَ أَلَّا تَكُونَ مَعَ ٱلسَّـٰجِدِینَ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:49'),
(6255, 15, 1835, 33, 'قَالَ لَمۡ أَكُن لِّأَسۡجُدَ لِبَشَرٍ خَلَقۡتَهُۥ مِن صَلۡصَـٰلࣲ مِّنۡ حَمَإࣲ مَّسۡنُونࣲ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:49'),
(6256, 15, 1836, 34, 'قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِیمࣱ\n', NULL, 0, '2023-05-27 10:26:57', '2023-05-27 11:14:49'),
(6257, 15, 1837, 35, 'وَإِنَّ عَلَیۡكَ ٱللَّعۡنَةَ إِلَىٰ یَوۡمِ ٱلدِّینِ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6258, 15, 1838, 36, 'قَالَ رَبِّ فَأَنظِرۡنِیۤ إِلَىٰ یَوۡمِ یُبۡعَثُونَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6259, 15, 1839, 37, 'قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6260, 15, 1840, 38, 'إِلَىٰ یَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6261, 15, 1841, 39, 'قَالَ رَبِّ بِمَاۤ أَغۡوَیۡتَنِی لَأُزَیِّنَنَّ لَهُمۡ فِی ٱلۡأَرۡضِ وَلَأُغۡوِیَنَّهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6262, 15, 1842, 40, 'إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6263, 15, 1843, 41, 'قَالَ هَـٰذَا صِرَ ٰ⁠طٌ عَلَیَّ مُسۡتَقِیمٌ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6264, 15, 1844, 42, 'إِنَّ عِبَادِی لَیۡسَ لَكَ عَلَیۡهِمۡ سُلۡطَـٰنٌ إِلَّا مَنِ ٱتَّبَعَكَ مِنَ ٱلۡغَاوِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6265, 15, 1845, 43, 'وَإِنَّ جَهَنَّمَ لَمَوۡعِدُهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6266, 15, 1846, 44, 'لَهَا سَبۡعَةُ أَبۡوَ ٰ⁠بࣲ لِّكُلِّ بَابࣲ مِّنۡهُمۡ جُزۡءࣱ مَّقۡسُومٌ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6267, 15, 1847, 45, 'إِنَّ ٱلۡمُتَّقِینَ فِی جَنَّـٰتࣲ وَعُیُونٍ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:49'),
(6268, 15, 1848, 46, 'ٱدۡخُلُوهَا بِسَلَـٰمٍ ءَامِنِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:50'),
(6269, 15, 1849, 47, 'وَنَزَعۡنَا مَا فِی صُدُورِهِم مِّنۡ غِلٍّ إِخۡوَ ٰ⁠نًا عَلَىٰ سُرُرࣲ مُّتَقَـٰبِلِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:50'),
(6270, 15, 1850, 48, 'لَا یَمَسُّهُمۡ فِیهَا نَصَبࣱ وَمَا هُم مِّنۡهَا بِمُخۡرَجِینَ\n', NULL, 0, '2023-05-27 10:26:58', '2023-05-27 11:14:50'),
(6271, 15, 1851, 49, '۞ نَبِّئۡ عِبَادِیۤ أَنِّیۤ أَنَا ٱلۡغَفُورُ ٱلرَّحِیمُ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6272, 15, 1852, 50, 'وَأَنَّ عَذَابِی هُوَ ٱلۡعَذَابُ ٱلۡأَلِیمُ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6273, 15, 1853, 51, 'وَنَبِّئۡهُمۡ عَن ضَیۡفِ إِبۡرَ ٰ⁠هِیمَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6274, 15, 1854, 52, 'إِذۡ دَخَلُوا۟ عَلَیۡهِ فَقَالُوا۟ سَلَـٰمࣰا قَالَ إِنَّا مِنكُمۡ وَجِلُونَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6275, 15, 1855, 53, 'قَالُوا۟ لَا تَوۡجَلۡ إِنَّا نُبَشِّرُكَ بِغُلَـٰمٍ عَلِیمࣲ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6276, 15, 1856, 54, 'قَالَ أَبَشَّرۡتُمُونِی عَلَىٰۤ أَن مَّسَّنِیَ ٱلۡكِبَرُ فَبِمَ تُبَشِّرُونَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6277, 15, 1857, 55, 'قَالُوا۟ بَشَّرۡنَـٰكَ بِٱلۡحَقِّ فَلَا تَكُن مِّنَ ٱلۡقَـٰنِطِینَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6278, 15, 1858, 56, 'قَالَ وَمَن یَقۡنَطُ مِن رَّحۡمَةِ رَبِّهِۦۤ إِلَّا ٱلضَّاۤلُّونَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6279, 15, 1859, 57, 'قَالَ فَمَا خَطۡبُكُمۡ أَیُّهَا ٱلۡمُرۡسَلُونَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6280, 15, 1860, 58, 'قَالُوۤا۟ إِنَّاۤ أُرۡسِلۡنَاۤ إِلَىٰ قَوۡمࣲ مُّجۡرِمِینَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6281, 15, 1861, 59, 'إِلَّاۤ ءَالَ لُوطٍ إِنَّا لَمُنَجُّوهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6282, 15, 1862, 60, 'إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَاۤ إِنَّهَا لَمِنَ ٱلۡغَـٰبِرِینَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:50'),
(6283, 15, 1863, 61, 'فَلَمَّا جَاۤءَ ءَالَ لُوطٍ ٱلۡمُرۡسَلُونَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:51'),
(6284, 15, 1864, 62, 'قَالَ إِنَّكُمۡ قَوۡمࣱ مُّنكَرُونَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:51'),
(6285, 15, 1865, 63, 'قَالُوا۟ بَلۡ جِئۡنَـٰكَ بِمَا كَانُوا۟ فِیهِ یَمۡتَرُونَ\n', NULL, 0, '2023-05-27 10:26:59', '2023-05-27 11:14:51'),
(6286, 15, 1866, 64, 'وَأَتَیۡنَـٰكَ بِٱلۡحَقِّ وَإِنَّا لَصَـٰدِقُونَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6287, 15, 1867, 65, 'فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعࣲ مِّنَ ٱلَّیۡلِ وَٱتَّبِعۡ أَدۡبَـٰرَهُمۡ وَلَا یَلۡتَفِتۡ مِنكُمۡ أَحَدࣱ وَٱمۡضُوا۟ حَیۡثُ تُؤۡمَرُونَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6288, 15, 1868, 66, 'وَقَضَیۡنَاۤ إِلَیۡهِ ذَ ٰ⁠لِكَ ٱلۡأَمۡرَ أَنَّ دَابِرَ هَـٰۤؤُلَاۤءِ مَقۡطُوعࣱ مُّصۡبِحِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6289, 15, 1869, 67, 'وَجَاۤءَ أَهۡلُ ٱلۡمَدِینَةِ یَسۡتَبۡشِرُونَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6290, 15, 1870, 68, 'قَالَ إِنَّ هَـٰۤؤُلَاۤءِ ضَیۡفِی فَلَا تَفۡضَحُونِ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6291, 15, 1871, 69, 'وَٱتَّقُوا۟ ٱللَّهَ وَلَا تُخۡزُونِ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6292, 15, 1872, 70, 'قَالُوۤا۟ أَوَلَمۡ نَنۡهَكَ عَنِ ٱلۡعَـٰلَمِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6293, 15, 1873, 71, 'قَالَ هَـٰۤؤُلَاۤءِ بَنَاتِیۤ إِن كُنتُمۡ فَـٰعِلِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6294, 15, 1874, 72, 'لَعَمۡرُكَ إِنَّهُمۡ لَفِی سَكۡرَتِهِمۡ یَعۡمَهُونَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6295, 15, 1875, 73, 'فَأَخَذَتۡهُمُ ٱلصَّیۡحَةُ مُشۡرِقِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:51'),
(6296, 15, 1876, 74, 'فَجَعَلۡنَا عَـٰلِیَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَیۡهِمۡ حِجَارَةࣰ مِّن سِجِّیلٍ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:52'),
(6297, 15, 1877, 75, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَـٰتࣲ لِّلۡمُتَوَسِّمِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:52'),
(6298, 15, 1878, 76, 'وَإِنَّهَا لَبِسَبِیلࣲ مُّقِیمٍ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:52'),
(6299, 15, 1879, 77, 'إِنَّ فِی ذَ ٰ⁠لِكَ لَـَٔایَةࣰ لِّلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:52'),
(6300, 15, 1880, 78, 'وَإِن كَانَ أَصۡحَـٰبُ ٱلۡأَیۡكَةِ لَظَـٰلِمِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:52'),
(6301, 15, 1881, 79, 'فَٱنتَقَمۡنَا مِنۡهُمۡ وَإِنَّهُمَا لَبِإِمَامࣲ مُّبِینࣲ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:52'),
(6302, 15, 1882, 80, 'وَلَقَدۡ كَذَّبَ أَصۡحَـٰبُ ٱلۡحِجۡرِ ٱلۡمُرۡسَلِینَ\n', NULL, 0, '2023-05-27 10:27:00', '2023-05-27 11:14:52'),
(6303, 15, 1883, 81, 'وَءَاتَیۡنَـٰهُمۡ ءَایَـٰتِنَا فَكَانُوا۟ عَنۡهَا مُعۡرِضِینَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6304, 15, 1884, 82, 'وَكَانُوا۟ یَنۡحِتُونَ مِنَ ٱلۡجِبَالِ بُیُوتًا ءَامِنِینَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6305, 15, 1885, 83, 'فَأَخَذَتۡهُمُ ٱلصَّیۡحَةُ مُصۡبِحِینَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6306, 15, 1886, 84, 'فَمَاۤ أَغۡنَىٰ عَنۡهُم مَّا كَانُوا۟ یَكۡسِبُونَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6307, 15, 1887, 85, 'وَمَا خَلَقۡنَا ٱلسَّمَـٰوَ ٰ⁠تِ وَٱلۡأَرۡضَ وَمَا بَیۡنَهُمَاۤ إِلَّا بِٱلۡحَقِّۗ وَإِنَّ ٱلسَّاعَةَ لَـَٔاتِیَةࣱۖ فَٱصۡفَحِ ٱلصَّفۡحَ ٱلۡجَمِیلَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6308, 15, 1888, 86, 'إِنَّ رَبَّكَ هُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِیمُ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6309, 15, 1889, 87, 'وَلَقَدۡ ءَاتَیۡنَـٰكَ سَبۡعࣰا مِّنَ ٱلۡمَثَانِی وَٱلۡقُرۡءَانَ ٱلۡعَظِیمَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6310, 15, 1890, 88, 'لَا تَمُدَّنَّ عَیۡنَیۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦۤ أَزۡوَ ٰ⁠جࣰا مِّنۡهُمۡ وَلَا تَحۡزَنۡ عَلَیۡهِمۡ وَٱخۡفِضۡ جَنَاحَكَ لِلۡمُؤۡمِنِینَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:52'),
(6311, 15, 1891, 89, 'وَقُلۡ إِنِّیۤ أَنَا ٱلنَّذِیرُ ٱلۡمُبِینُ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:53'),
(6312, 15, 1892, 90, 'كَمَاۤ أَنزَلۡنَا عَلَى ٱلۡمُقۡتَسِمِینَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:53'),
(6313, 15, 1893, 91, 'ٱلَّذِینَ جَعَلُوا۟ ٱلۡقُرۡءَانَ عِضِینَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:53'),
(6314, 15, 1894, 92, 'فَوَرَبِّكَ لَنَسۡـَٔلَنَّهُمۡ أَجۡمَعِینَ\n', NULL, 0, '2023-05-27 10:27:01', '2023-05-27 11:14:53'),
(6315, 15, 1895, 93, 'عَمَّا كَانُوا۟ یَعۡمَلُونَ\n', NULL, 0, '2023-05-27 10:27:02', '2023-05-27 11:14:53'),
(6316, 15, 1896, 94, 'فَٱصۡدَعۡ بِمَا تُؤۡمَرُ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِینَ\n', NULL, 0, '2023-05-27 10:27:02', '2023-05-27 11:14:53'),
(6317, 15, 1897, 95, 'إِنَّا كَفَیۡنَـٰكَ ٱلۡمُسۡتَهۡزِءِینَ\n', NULL, 0, '2023-05-27 10:27:02', '2023-05-27 11:14:53'),
(6318, 15, 1898, 96, 'ٱلَّذِینَ یَجۡعَلُونَ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَۚ فَسَوۡفَ یَعۡلَمُونَ\n', NULL, 0, '2023-05-27 10:27:02', '2023-05-27 11:14:53'),
(6319, 15, 1899, 97, 'وَلَقَدۡ نَعۡلَمُ أَنَّكَ یَضِیقُ صَدۡرُكَ بِمَا یَقُولُونَ\n', NULL, 0, '2023-05-27 10:27:02', '2023-05-27 11:14:53'),
(6320, 15, 1900, 98, 'فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَكُن مِّنَ ٱلسَّـٰجِدِینَ\n', NULL, 0, '2023-05-27 10:27:02', '2023-05-27 11:14:53'),
(6321, 15, 1901, 99, 'وَٱعۡبُدۡ رَبَّكَ حَتَّىٰ یَأۡتِیَكَ ٱلۡیَقِینُ\n', NULL, 0, '2023-05-27 10:27:02', '2023-05-27 11:14:53');
